INSERT INTO [t11385] VALUES (9425,'حديث كان النبي صلى الله عليه وسلم يصلي العصر والشمس لم تخرج من حجرتها. خ في الصلاة (١٢٢: ١) وفي الخمس (٤: ٥) عن إبراهيم بن المنذر، عن أنس بن عياض به.','24403','8999','0');
INSERT INTO [t11385] VALUES (9426,'أيوب بن واقد، عن هشام بن عروة، عن أبيه، عن عائشة','24405','8999','0');
