INSERT INTO [t11385] VALUES (9439,'حديث تخيروا لنطفكم فانكحوا الأكفاء وأنكحوا إليهم. ق في النكاح (٤٦: ٢) ، عن عبد الله بن سعيد الأشج الكندي، عن الحارث بن عمران الجعفري به.','24429','8999','0');
