INSERT INTO [t11385] VALUES (9441,'حديث: نزول المحصب ليس بسنة، إنما نزله النبي صلى الله عليه وسلم ليكون أسمح لخروجه. م في الحج (٥٩: ٤) عن أبي كامل - ت فيه (الحج ٨٢: ١) عن محمد بن عبد الأعلى - كلاهما عن يزيد بن زريع، عن حبيب المعلم به. وقال ت: حسن صحيح.','24431','8999','0');
INSERT INTO [t11385] VALUES (9442,'حفص بن غياث، عن هشام بن عروة، عن أبيه، عن عائشة','24432','8999','0');
