INSERT INTO [t11385] VALUES (9602,'حديث: تزوجني النبي صلى الله عليه وسلم وأنا بنت ست، وبنى بي وأنا بنت تسع. م في النكاح (١٠: ٢) عن محمد بن عبد الله بن نمير، عن عبدة به. وزاد: وكنت ألعب بالبنات.','24756','8999','0');
INSERT INTO [t11385] VALUES (9603,'وبه في الصلاة (٥٤) : أن أم سلمة ذكرت كنيسة رأتها بأرض الحبشة. . . الحديث.','24765','8999','0');
INSERT INTO [t11385] VALUES (9604,'وبه في النكاح (٢٠) : وإن خفتم ألا تقسطوا في اليتامى - ٤: ٣ -) . . . الحديث. إلا أنه لم ينسب محمدا فيه، وكذلك ما بعده - (ح ١٧٠٧٧ و ١٧٠٧٨) .','24766','8999','0');
INSERT INTO [t11385] VALUES (9605,'وبه في الجهاد (١٨) : لما رجع النبي صلى الله عليه وسلم من الخندق ووضع السلاح فجاءه جبريل. . . الحديث.','24767','8999','0');
INSERT INTO [t11385] VALUES (9606,'وبه في الرقاق (لا، بل في الأيمان والنذور ٣: ٤) يا أمة محمد! والله لو تعلمون ما أعلم لضحكتم قليلا ولبكيتم كثيرا. . . الحديث.','24768','8999','0');
INSERT INTO [t11385] VALUES (9607,'حديث: كان النبي صلى الله عليه وسلم يصلي ركعتي الفجر إذا سمع الأذان ويخففهما. م في الصلاة (١٢٢: ٦) عن عمرو الناقد، عن عبدة به.','24769','8999','0');
INSERT INTO [t11385] VALUES (9608,'حديث: المتشبع بما لم يعط كلابس ثوبي زور. م في اللباس (٣٤: ١) عن محمد بن عبد الله بن نمير، عن عبدة به. وأخرجه أيضا بهذا الإسناد، عن هشام بن عروة، عن فاطمة بنت المنذر، عن أسماء - (ح ١٥٧٤٥) - وهو المشهور، وحديث عائشة غريب.','24770','8999','0');
INSERT INTO [t11385] VALUES (9609,'وبه في الجنائز (١٣: ٥) : كفن النبي صلى الله عليه وسلم في ثلاثة أثواب. . . الحديث.','24773','8999','0');
INSERT INTO [t11385] VALUES (9610,'وبه في اللباس (٢٥: ١٣) : قدم النبي صلى الله عليه وسلم من سفر، وقد سترت على بابي درنوكا. . . الحديث.','24774','8999','0');
INSERT INTO [t11385] VALUES (9611,'وبه في الفضائل (٥٢: ٨) : قالت عائشة: أبواك، والله! من الذين استجابوا لله والرسول من بعد ما أصابهم القرح (- ٣: ١٧٢ -)','24775','8999','0');
INSERT INTO [t11385] VALUES (9612,'وبه في آخر الكتاب (التفسير ١: ١١) في قوله تعالى: ومن كان فقيرا فليأكل بالمعروف (- ٤: ٦ -) .','24776','8999','0');
INSERT INTO [t11385] VALUES (9613,'وبه في الصوم (٤٩) : كان يوم عاشوراء يوما تصومه قريش في الجاهلية. . . الحديث، وقال: صحيح.','24778','8999','0');
INSERT INTO [t11385] VALUES (9614,'وبه في الاستئذان (١٠٧: ٢) : كان أحب العمل إلى رسول الله صلى الله عليه وسلم ما ديم عليه (وقال: حسن صحيح) .','24779','8999','0');
INSERT INTO [t11385] VALUES (9615,'وبه في الشمائل (٤٤: ١٤) : أن النبي صلى الله عليه وسلم دخل عليها وعندها امرأة فقال: من هذه؟ ، قالت: فلانة لا تنام الليل. . . الحديث.','24780','8999','0');
INSERT INTO [t11385] VALUES (9616,'حديث: إن نزول الأبطح ليس بسنة، إنما نزله النبي صلى الله عليه وسلم ليكون أسمح لخروجه. ق في الحج (٨١: ١) عن هناد بن السري، عن عبدة به.','24785','8999','0');
INSERT INTO [t11385] VALUES (9617,'عبيد بن القاسم، عن هشام بن عروة، عن أبيه، عن عائشة','24787','8999','0');
INSERT INTO [t11385] VALUES (9618,'عثام بن علي العامري، عن هشام بن عروة، عن أبيه، عن عائشة','24789','8999','0');
INSERT INTO [t11385] VALUES (9619,'عثمان بن فرقد عن هشام بن عروة، عن أبيه عن عائشة','24791','8999','0');
INSERT INTO [t11385] VALUES (9620,'عقبة بن خالد، عن هشام بن عروة، عن أبيه، عن عائشة','24794','8999','0');
INSERT INTO [t11385] VALUES (9621,'حديث: أم زرع - بطوله. س في عشرة النساء (الكبرى ٤٦: ٥) عن أبي عقبة خالد بن عقبة بن خالد السكوني، عن أبيه به - موقوف، وآخره مرفوع. قد تقدم الكلام عليه في ترجمة عباد بن منصور، عن هشام بن عروة - (ح ١٦٩٦٥) .','24796','8999','0');
INSERT INTO [t11385] VALUES (9622,'علي بن مسهر، عن هشام بن عروة، عن أبيه، عن عائشة','24797','8999','0');
INSERT INTO [t11385] VALUES (9623,'حديث: استأذنت هالة بنت خويلد - أخت خديجة بنت خويلد - على رسول الله صلى الله عليه وسلم فعرف استئذان خديجة. خ في فضل خديجة (المناقب ٨٠: ٦ تعليقا) عن إسماعيل بن الخليل - م في الفضائل (٥٨: ١٢) عن سويد بن سعيد - كلاهما عن علي بن مسهر به.','24800','8999','0');
INSERT INTO [t11385] VALUES (9624,'وبه في الحج (٩٢: ٢) : كان الناس يطوفون في البيت عراة إلا الحمس، قال هشام: فأخبرني أبي، عن عائشة أن هذه الآية نزلت في الحمس: ثم أفيضوا من حيث أفاض الناس (- ٢: ١٩٩ -) قال: كانوا يفيضون من جمع فدفعوا إلى عرفات.','24806','8999','0');
INSERT INTO [t11385] VALUES (9625,'وبه في البيوع (٥٧) : لقل يوم كان يأتي على النبي صلى الله عليه وسلم إلا يأتي فيه بيت أبي بكر، فلما أذن له في الخروج. . . الحديث.','24807','8999','0');
INSERT INTO [t11385] VALUES (9626,'وبه في النكاح (٦٢) : تزوجني النبي صلى الله عليه وسلم فأتتني أمي فأدخلتني الدار، فلم يرعني إلا رسول الله صلى الله عليه وسلم ضحى. . . الحديث. وهو مختصر من حديث قد تقدم - (ح ١٧١٠٦) .','24808','8999','0');
INSERT INTO [t11385] VALUES (9627,'وبه في النذور (والأيمان ١٥: ٥) : صرخ إبليس يوم أحد أي عباد الله! أخراكم. . . الحديث.','24809','8999','0');
INSERT INTO [t11385] VALUES (9628,'وبه في الطب (٨: ٢) : أنها كانت تأمر بالتلبينة، وتقول: هو البغيض النافع - موقوف.','24810','8999','0');
INSERT INTO [t11385] VALUES (9629,'وبه في بدء الخلق (٦: ٩) أن الحارث بن هشام سأل النبي صلى الله عليه وسلم: كيف يأتيك الوحي؟ . . . الحديث.','24811','8999','0');
INSERT INTO [t11385] VALUES (9630,'وبه في أيام الجاهلية (المناقب ٨٦: ٥) : أسلمت امرأة سوداء لبعض العرب، وكان لها حفش في المسجد. . . الحديث. وفيه: % ويوم الوشاح من تعاجيب ربنا %','24812','8999','0');
INSERT INTO [t11385] VALUES (9631,'وبه في الزكاة (١٦) : أن رجلا أتى النبي صلى الله عليه وسلم فقال: إن أمي افتلتت نفسها. . . الحديث.','24814','8999','0');
INSERT INTO [t11385] VALUES (9632,'وبه في الجنائز (١٣: ٤) : أدرج النبي صلى الله عليه وسلم في حلة يمنية كانت لعبد الله بن بكر. . . الحديث.','24815','8999','0');
INSERT INTO [t11385] VALUES (9633,'وبه في الأحكام (القضاء ٤: ١) : دخلت هند بنت عتبة - امرأة أبي سفيان - على رسول الله صلى الله عليه وسلم، فقالت: إن أبا سفيان رجل شحيح. . . الحديث.','24816','8999','0');
INSERT INTO [t11385] VALUES (9634,'حديث: أن النبي صلى الله عليه وسلم كان إذا سمع المؤذن يتشهد قال: وأنا وأنا. د في الصلاة (٣٦: ٥) عن إبراهيم بن مهدي، عن علي بن مسهر به.','24817','8999','0');
INSERT INTO [t11385] VALUES (9635,'عمر بن حبيب العدوي القاضي، عن هشام بن عروة، عن أبيه، عن عائشة','24820','8999','0');
INSERT INTO [t11385] VALUES (9636,'عمر بن علي المقدمي، عن هشام بن عروة، عن أبيه، عن عائشة','24822','8999','0');
INSERT INTO [t11385] VALUES (9637,'حديث: أن النبي صلى الله عليه وسلم قضى أن الخراج بالضمان. ت في البيوع (٥٣: ٢) عن يحيى بن خلف، عن عمر بن علي المقدمي به. وقال: حسن غريب، واستغرب محمد بن إسماعيل هذا من حديث عمر بن علي، (قلت: لمحمد تراه تدليسا؟ قال: لا) . ورواه مسلم بن خالد','24823','8999','0');
INSERT INTO [t11385] VALUES (9638,'حديث: أن النبي صلى الله عليه وسلم كان يغير الاسم القبيح. ت في الاستئذان (١٠٠: ٢) عن أبي بكر بن نافع البصري، عن عمر بن علي المقدمي به. قال: وربما قال عمر بن علي، عن هشام، عن أبيه أن النبي صلى الله عليه وسلم - ولم يذكر عائشة.','24824','8999','0');
INSERT INTO [t11385] VALUES (9639,'حديث: إذا صلى أحدكم فأحدث فليمسك على أنفه ثم لينصرف. ق في الصلاة (١٧٧: ١) عن عمر بن شبة بن عبيدة النميري، عن عمر بن علي به. و (١٧٧: ٢) عن حرملة بن يحيى، عن ابن وهب، عن عمر بن قيس، عن هشام بن عروة نحوه.','24826','8999','0');
INSERT INTO [t11385] VALUES (9640,'عمر بن قيس المكي، عن هشام بن عروة، عن أبيه، عن عائشة','24827','8999','0');
INSERT INTO [t11385] VALUES (9641,'عمرو بن الحارث، عن هشام بن عروة، عن أبيه، عن عائشة','24829','8999','0');
INSERT INTO [t11385] VALUES (9642,'حديث: أن النبي صلى الله عليه وسلم دخل مكة عام الفتح من كداء من أعلى مكة. خ في الحج (٤١: ٤) وفي المغازي (؟) عن أحمد؛ عن ابن وهب، عن عمرو بن الحارث به. رواه حاتم بن إسماعيل] خ [ووهيب بن خالد] خ [، عن هشام بن عروة، عن أبيه - مرسلا، وسيأتي -','24830','8999','0');
INSERT INTO [t11385] VALUES (9643,'عنبسة بن عبد الواحد القرشي، عن هشام بن عروة، عن أبيه، عن عائشة','24831','8999','0');
INSERT INTO [t11385] VALUES (9644,'عيسى بن يونس، عن هشام بن عروة، عن أبيه، عن عائشة','24833','8999','0');
INSERT INTO [t11385] VALUES (9645,'وبه في الحج (الكبرى ٢٨٠: ٦) : المحصب ليس بسنة، إنما هو منزل نزله النبي صلى الله عليه وسلم ليكون أسمح لخروجه.','24841','8999','0');
INSERT INTO [t11385] VALUES (9646,'وبه في التفسير (في الكبرى) في قوله تعالى: يستفتونك في النساء قل الله يفتيكم فيهن (- ٤: ١٢٧ -) .','24842','8999','0');
INSERT INTO [t11385] VALUES (9647,'الفضل بن موسى، عن هشام بن عروة، عن أبيه، عن عائشة','24843','8999','0');
INSERT INTO [t11385] VALUES (9648,'فليح بن سليمان، عن هشام بن عروة، عن أبيه، عن عائشة','24845','8999','0');
INSERT INTO [t11385] VALUES (9649,'حديث: الإفك. خ في الشهادات (١٥) عن أبي الربيع سليمان بن داود، عن فليح بن سليمان، عن هشام بن عروة، عن أبيه، عن عائشة؛ وعبد الله بن الزبير (ح ٥٢٧٨) مثله. ذكره عقيب حديث الزهري، عن سعيد بن المسيب وغيره، عن عائشة - (ح ١٦١٢٦) . ٦٨ ليث بن سعد،','24846','8999','0');
INSERT INTO [t11385] VALUES (9650,'مالك بن أنس، عن هشام بن عروة، عن أبيه، عن عائشة','24850','8999','0');
INSERT INTO [t11385] VALUES (9651,'حديث: يا أمة محمد! ما أحد أغير من الله أن يزني عبده أو تزني أمته. خ في النكاح (١٠٨: ٢) عن القعنبي - س في النعوت (بل في الصلاة ٦١٨: ٣) عن قتيبة - (والنعوت، الكبرى ٥٧: ٢) عن محمد بن سلمة، عن ابن القاسم - ثلاثتهم عن مالك به. وهو مختصر من حديث','24863','8999','0');
INSERT INTO [t11385] VALUES (9652,'حديث: كان أحب العمل إلى رسول الله صلى الله عليه وسلم الذي يدوم عليه صاحبه في الرقاق (١٨: ٢) عن قتيبة، عن مالك به.','24873','8999','0');
INSERT INTO [t11385] VALUES (9653,'حديث: إن كان رسول الله صلى الله عليه وسلم ليقبل بعض نسائه وهو صائم، ثم ضحكت. خ في الصوم (٢٤: ١) عن عبد الله بن مسلمة القعنبي، عن مالك به.','24874','8999','0');
INSERT INTO [t11385] VALUES (9654,'حديث: إن الشمس والقمر لا يخسفان لموت أحد ولا لحياته، فإذا رأيتم ذلك فادعوا الله وكبروا وتصدقوا. د في الصلاة (٢٦٦: ١) عن القعنبي، عن مالك - وهو مختصر من حديث الكسوف - (ح ١٧١٤٨) .','24877','8999','0');
INSERT INTO [t11385] VALUES (9655,'حديث: أن النبي صلى الله عليه وسلم كان يغتسل وأنا من إناء واحد، نغترف منه جميعا. س في الطهارة (٢٥٤: ١) عن قتيبة، عن مالك به.','24878','8999','0');
INSERT INTO [t11385] VALUES (9656,'مالك بن سعير، عن هشام بن عروة، عن أبيه، عن عائشة - وهو مالك بن سعير بن خمس التميمي الكوفي','24881','8999','0');
INSERT INTO [t11385] VALUES (9657,'حديث: أغار قوم على لقاح النبي صلى الله عليه وسلم، فأخذهم فقطع أيديهم وأرجلهم وسمل أعينهم. س في المحاربة (٧ - ب: ٢) عن محمد بن عبد الله الخلنجي، عن مالك بن سعير به.','24884','8999','0');
INSERT INTO [t11385] VALUES (9658,'حديث: أن رسول الله صلى الله عليه وسلم أمر بالمساجد أن تبنى في الدور وأن تطهر وتطيب. ق في الصلاة (٢٩: ٢) عن عبد الرحمن بن بشر بن الحكم وأحمد بن الأزهر، كلاهما عن مالك بن سعير به. ٧١ محاضر بن المورع، عن هشام بن عروة، عن أبيه، عن عائشة','24885','8999','0');
INSERT INTO [t11385] VALUES (9659,'(ألف خت) (حديث في قبول الهدية. في ترجمة عيسى بن يونس، عن هشام بن عروة، عن أبيه - ح ١٧١٣٣) .','24886','8999','0');
INSERT INTO [t11385] VALUES (9660,'محمد بن إسحاق، عن هشام بن عروة، عن أبيه، عن عائشة','24888','8999','0');
INSERT INTO [t11385] VALUES (9661,'محمد بن بشر، عن هشام بن عروة، عن أبيه، عن عائشة','24893','8999','0');
INSERT INTO [t11385] VALUES (9662,'حديث: قصة لعب الحبشة بحرابهم في المسجد. م في العيدين (الصلاة ١٨٨: ٧) عن محمد بن عبد الله بن نمير، عن محمد بن بشر به.','24897','8999','0');
INSERT INTO [t11385] VALUES (9663,'وبه في الزكاة (١٦: ١) وفي الوصايا (٣: ٣) : أن رجلا قال: إن أمي افتلتت نفسها. . . الحديث.','24898','8999','0');
INSERT INTO [t11385] VALUES (9664,'حديث: أن ابن أم مكتوم كان يؤذن للنبي صلى الله عليه وسلم - وهو أعمى. م في الصلاة (٥: ١) عن أبي كريب، عن خالد بن مخلد، عن محمد بن جعفر به.','24902','8999','0');
INSERT INTO [t11385] VALUES (9665,'محمد بن حازم أبو معاوية الضرير، عن هشام بن عروة، عن أبيه، عن عائشة','24903','8999','0');
INSERT INTO [t11385] VALUES (9666,'حديث: بعث رسول الله صلى الله عليه وسلم أسيد بن حضير وأناسا معه في طلب قلادة. . . فذكر حديث التيمم. د في الطهارة (١٢٣: ١) عن النفيلي - س فيه (الطهارة ٢٠٥: ١) عن إسحاق بن إبراهيم - كلاهما عنه به.','24914','8999','0');
INSERT INTO [t11385] VALUES (9667,'وبه في المغازي (٢٦) : الذين استجابوا لله والرسول (- ٣: ١٧٢ -) . . . الآية. قالت عائشة لعروة: يا ابن أختي! كان أبواك منهم: الزبير وأبو بكر. . . الحديث.','24917','8999','0');
INSERT INTO [t11385] VALUES (9668,'وبه في التعبير (٢١) : أريتك في المنام قبل أن أتزوجك مرتين، رأيت الملك يحملك في سرقة من حرير. . . الحديث.','24918','8999','0');
INSERT INTO [t11385] VALUES (9669,'(ألف خ) (وبه في النكاح - ٩٦ -: وإن امرأة خافت من بعلها نشوزا أو إعراضا ٤: ١٢٨ -) . . . الحديث) .','24919','8999','0');
INSERT INTO [t11385] VALUES (9670,'وبه فيه (الصلاة ٣١: ٣) في قوله تعالى ولا تجهر بصلاتك ولا تخافت بها (- ١٧: ١١٠ -) . . . الحديث.','24926','8999','0');
INSERT INTO [t11385] VALUES (9671,'وبه في الأدب (الألفاظ ٤: ٢) لا يقولن أحدكم خبثت نفسي. . . الحديث.','24927','8999','0');
INSERT INTO [t11385] VALUES (9672,'وبه في فضائل النبي صلى الله عليه وسلم (٢٠: ٧) : ما ضرب النبي صلى الله عليه وسلم بيده امرأة ولا خادما. . . الحديث.','24928','8999','0');
INSERT INTO [t11385] VALUES (9673,'وبه في صلاة الكسوف (الصلاة ١٩٤: ٢) : كسفت الشمس في عهد رسول الله صلى الله عليه وسلم. . . الحديث.','24930','8999','0');
INSERT INTO [t11385] VALUES (9674,'وبه في الصوم (١٧: ٣) : أن حمزة بن عمرو الأسلمي سأل النبي صلى الله عليه وسلم عن الصوم في السفر. . . الحديث.','24931','8999','0');
