INSERT INTO [t11385] VALUES (9678,'وبه في آخر الكتاب (التفسير ١: ١٧) أمروا أن يستغفروا لأصحاب محمد صلى الله عليه وسلم فسبوهم.','24935','8999','0');
INSERT INTO [t11385] VALUES (9679,'حديث: إنما مر النبي صلى الله عليه وسلم على قبر فقال: إن صاحب هذا ليعذب وإن أهله ليبكون عليه. في ترجمة عروة، عن ابن عمر - (ح ٧٣٢٤) .','24936','8999','0');
