INSERT INTO [t11385] VALUES (9710,'وبه فيه (الصلاة ١٢٤: ٦) : ما رأيت رسول الله صلى الله عليه وسلم يقرأ في شيء من صلاة الليل جالسا. . . الحديث.','25000','8999','0');
INSERT INTO [t11385] VALUES (9711,'وبه فيه (الصلاة ٣١: ٣) عن عائشة في قوله تعالى: ولا تجهر بصلاتك ولا تخافت بها (- ١٧: ١١٠ -) نزلت في الدعاء.','25001','8999','0');
