INSERT INTO [t11385] VALUES (9715,'حديث: إذا مات صاحبكم فدعوه لا تقعوا فيه. د في الأدب (٥٠: ١) عن أبي خيثمة زهير بن حرب، عن وكيع به.','25005','8999','0');
INSERT INTO [t11385] VALUES (9716,'حديث: أن النبي صلى الله عليه وسلم قال لها - وكانت حائضا -: انقضى رأسك واغتسلي ق في الطهارة (١٢٤) بإسناد الذي قبله (ح ١٧٢٨٤) .','25008','8999','0');
INSERT INTO [t11385] VALUES (9717,'حديث: إن نزول الأبطح ليس بسنة، ونزله رسول الله صلى الله عليه وسلم ليكون أسمح لخروجه. ق في الحج (٨١: ١) عن هناد بن السري وعلي بن محمد، كلاهما عن وكيع به.','25009','8999','0');
INSERT INTO [t11385] VALUES (9718,'حديث: أن النبي صلى الله عليه وسلم حك بزاقا في قبلة المسجد. ق في الصلاة (٣٠: ٤) عن علي بن محمد، عن وكيع به.','25010','8999','0');
INSERT INTO [t11385] VALUES (9719,'حديث: كان النبي صلى الله عليه وسلم يدني إلي رأسه - وأنا حائض، وهو مجاور يعني معتكفا - فأغسله وأرجله. ق في الطهارة (١٢٠: ٢) عن أبي بكر بن أبي شيبة - وفيه (الطهارة ١٢٠: ٢) وفي الصوم (٦٤) عن علي بن محمد - كلاهما عن وكيع به.','25011','8999','0');
