INSERT INTO [t11385] VALUES (9722,'وبه في تزويج عائشة (المناقب ١٠٤: ٢) : أن النبي صلى الله عليه وسلم قال لها: أريتك في المنام مرتين، أرى أنك في سرقة من حرير. . . الحديث.','25015','8999','0');
INSERT INTO [t11385] VALUES (9723,'وبه في الدعوات (٣٩) : أن النبي صلى الله عليه وسلم كان يقول: اللهم! إني أعوذ بك من الكسل والهرم والمأثم والمغرم. . . الحديث.','25016','8999','0');
INSERT INTO [t11385] VALUES (9724,'يحيى بن زكريا بن أبي زائدة، عن هشام بن عروة، عن أبيه، عن عائشة','25021','8999','0');
INSERT INTO [t11385] VALUES (9725,'حديث عن عائشة: في قوله تعالى ولا تجهر بصلاتك ولا تخافت بها (- ١٧: ١١٠ -) قالت: نزلت في الدعاء. م في الصلاة (٣١: ٢) عن يحيى بن يحيى، عن يحيى بن زكريا بن أبي زائدة به.','25022','8999','0');
INSERT INTO [t11385] VALUES (9726,'وبه في صلاة العيد (الصلاة ١٨٨: ٧) : جاء حبش يزفنون في المسجد. . . الحديث.','25023','8999','0');
INSERT INTO [t11385] VALUES (9727,'وبه في الفضائل (٨٠: ١٠) : قال حسان: يا رسول الله! ائذن لي في هجاء أبي سفيان فقال: كيف بنسبي فيهم ١؟ . . . الحديث.','25024','8999','0');
INSERT INTO [t11385] VALUES (9728,'حديث: إن نزول الأبطح ليس بسنة، ونزله رسول الله صلى الله عليه وسلم ليكون أسمح لخروجه. ق في الحج (٨١: ١) عن هناد بن السري، عن ابن أبي زائدة به.','25025','8999','0');
INSERT INTO [t11385] VALUES (9729,'يحيى بن أبي زكريا أبو مروان الغساني الواسطي، عن هشام بن عروة، عن أبيه، عن عائشة','25026','8999','0');
INSERT INTO [t11385] VALUES (9730,'وبه في الاعتصام (٢٩: ٢) : أن رسول الله صلى الله عليه وسلم خطب الناس فحمد الله وأثنى عليه، وقال: ما تشيرون علي في قوم يسبون أهلي ما علمت عليهم من سوء قط؟ . وعن عروة قال: لما أخبرت عائشة بالأمر قالت: أتأذن لي أن أنطلق إلى أهلي؟ فأذن لها، وذكر','25028','8999','0');
INSERT INTO [t11385] VALUES (9731,'وبه في الديات (١٠: ٢) : صرخ إبليس يوم أحد: أي عباد الله! أخراكم. . . الحديث.','25029','8999','0');
