INSERT INTO [t11385] VALUES (9741,'وبه فيه (الصوم ٧٧) : كان النبي صلى الله عليه وسلم يصغي إلي رأسه - وهو مجاور في المسجد - فأرجله وأنا حائض.','25051','8999','0');
INSERT INTO [t11385] VALUES (9742,'وبه في الحج (١٥٩) : خرجنا مع النبي صلى الله عليه وسلم موافين هلال ذي الحجة. . . الحديث.','25052','8999','0');
