INSERT INTO [t1153] VALUES (1,'كتاب الطهارة','33','0','0');
INSERT INTO [t1153] VALUES (2,'كتاب الطهارة','33','1','0');
INSERT INTO [t1153] VALUES (3,'باب المياه وهي ثلاثة أقسام ماء طهور','34','1','0');
INSERT INTO [t1153] VALUES (4,'مسألة: قال: وهو الباقي على أصل خلقته','35','1','0');
INSERT INTO [t1153] VALUES (5,'مسألة: وما تغير بمكثه','36','1','0');
INSERT INTO [t1153] VALUES (6,'مسألة: أو بطاهر لا يمكن صونه عنه كالطحلب وورق الشجر','36','1','0');
INSERT INTO [t1153] VALUES (7,'مسألة: أو لا يخالطه كالعود والكافور والدهن','36','1','0');
INSERT INTO [t1153] VALUES (8,'مسألة: أو ما أصله الماء كالملح البحري','37','1','0');
INSERT INTO [t1153] VALUES (9,'مسألة: أو ما تروح بريح ميتة إلى جانبه','37','1','0');
INSERT INTO [t1153] VALUES (10,'مسألة: أو بطاهر','38','1','0');
INSERT INTO [t1153] VALUES (11,'مسألة: وإن سخن بنجاسة فهل يكره استعماله على روايتين','38','1','0');
INSERT INTO [t1153] VALUES (12,'مسألة: فإن غير أحد أوصافه ـ لونه أو طعمه أو ريحه ـ ففيه روايتان','40','1','0');
INSERT INTO [t1153] VALUES (13,'مسألة: أو استعمل في رفع حدث أو طهارة مشروعة كالتجديد وغسل الجمعة','42','1','0');
INSERT INTO [t1153] VALUES (14,'مسألة: أو غمس فيه يده قائم من نوم الليل قبل غسلها ثلاثا فهل يسلب طهوريته؟ على روايتين','44','1','0');
INSERT INTO [t1153] VALUES (15,'مسألة: وإن أزيلت به النجاسة فانفصل متغيرا أو قبل زوالها فهو نجس','49','1','0');
INSERT INTO [t1153] VALUES (16,'مسألة: وإن انفصل غير متغير بعد زوالها فهو طاهر','49','1','0');
INSERT INTO [t1153] VALUES (17,'مسألة: وإن كان غير الأرض فهو طاهر في أصح الوجهين','49','1','0');
INSERT INTO [t1153] VALUES (18,'مسألة: وإن خلت بالطهارة منه امرأة فهو طهور','49','1','0');
INSERT INTO [t1153] VALUES (19,'مسألة: وإن كان كثيرا فهو طاهر ما لم تكن النجاسة بولا أو عذرة','52','1','0');
INSERT INTO [t1153] VALUES (20,'مسألة: إلا أن تكون النجاسة بولا أو عذرة مائعة ففيه روايتان إحداهما لا ينجس','54','1','0');
INSERT INTO [t1153] VALUES (21,'مسألة: إلا أن يكون مما لا يمكن نزحه لكثرته فلا ينجس','55','1','0');
INSERT INTO [t1153] VALUES (22,'مسألة: وإذا انضم إلى الماء النجس ماء طاهر كثير طهره إن لم يبق فيه تغير وإذا كان الماء النجس كثيرا فزال تغيره بنفسه أو بنزح بقي بعده كثير طهر','58','1','0');
INSERT INTO [t1153] VALUES (23,'مسألة: فإن كوثر بماء يسير أو بغير الماء كالتراب ونحوه فأزال التغير لم يطهر في أحد الوجهين','59','1','0');
INSERT INTO [t1153] VALUES (24,'مسألة: والكثير ما بلغ قلتين واليسير ما دونهما','62','1','0');
INSERT INTO [t1153] VALUES (25,'مسألة: وهما خمسمائة رطل بالعراقي','63','1','0');
INSERT INTO [t1153] VALUES (26,'مسألة: وهل ذلك تقريب أو تحديد؟ على وجهين','65','1','0');
INSERT INTO [t1153] VALUES (27,'مسألة: وإذا شك في نجاسة الماء أو كان نجسا فشك في طهارته بنى على اليقين','74','1','0');
INSERT INTO [t1153] VALUES (28,'مسألة: وإن اشتبه الماء الطاهر بالنجس لم يتحر فيهما على الصحيح من المذهب ويتيمم','77','1','0');
INSERT INTO [t1153] VALUES (29,'مسألة: وهل يشترط إراقتها أو خلطهما فيه روايتان','79','1','0');
INSERT INTO [t1153] VALUES (30,'مسألة: وإن اشتبه طهور بطاهر توضأ من كل واحد منهما وصلى صلاة واحدة','81','1','0');
INSERT INTO [t1153] VALUES (31,'مسألة: وإن اشتهت ثياب طاهرة بنجسة صلى في كل ثوب صلاة بعدد النجس وزاد صلاة','81','1','0');
INSERT INTO [t1153] VALUES (32,'باب الآنية','83','1','0');
INSERT INTO [t1153] VALUES (33,'مسألة: إلا آنية الذهب والفضة والمضبب بهما فإنه يحرم اتخاذها واستعمالها على الرجال والنساء','84','1','0');
INSERT INTO [t1153] VALUES (34,'مسألة: فإن توضأ منها أو اغتسل فهل تصح طهارته؟ على وجهين','86','1','0');
INSERT INTO [t1153] VALUES (35,'مسألة: إلا أن تكون الضبة يسيرة من الفضة كتشعيب القدح فلا بأس بها إذا لم يباشرها بالاستعمال','89','1','0');
INSERT INTO [t1153] VALUES (36,'مسألة: وثياب الكفار وأوانيهم طاهرة مباحة الاستعمال ما لم تعلم نجاستها','90','1','0');
INSERT INTO [t1153] VALUES (37,'مسألة: ولا يطهر جلد الميتة بالدباغ','93','1','0');
INSERT INTO [t1153] VALUES (38,'مسألة: وهل يجوز استعماله في اليابسات على روايتين','94','1','0');
INSERT INTO [t1153] VALUES (39,'مسألة: وعنه يطهر منها جلد ما كان طاهرا حال الحياة','94','1','0');
INSERT INTO [t1153] VALUES (40,'مسألة: ولا يطهر جلد غير المأكول بالذكاة','100','1','0');
INSERT INTO [t1153] VALUES (41,'مسألة: ولبن الميتة نجس لأنه مائع في وعاء نجس فتنجس به، وكذلك أنفحتها في ظاهر المذهب لما ذكرنا','101','1','0');
INSERT INTO [t1153] VALUES (42,'مسألة: وعظمها وقرنها وظفرها نجس','102','1','0');
INSERT INTO [t1153] VALUES (43,'مسألة: وصوفها وشعرها وريشها طاهر','104','1','0');
INSERT INTO [t1153] VALUES (44,'باب الاستنجاء','109','1','0');
INSERT INTO [t1153] VALUES (45,'مسألة: يستحب لمن أراد دخول الخلاء أن يقول بسم الله','109','1','0');
INSERT INTO [t1153] VALUES (46,'مسألة: ولا يدخله بشيء فيه ذكر الله تعالى','110','1','0');
INSERT INTO [t1153] VALUES (47,'مسألة: ويقدم رجله اليسرى في الدخول واليمنى في الخروج','110','1','0');
INSERT INTO [t1153] VALUES (48,'مسألة: ويعتمد على رجله اليسرى','111','1','0');
INSERT INTO [t1153] VALUES (49,'مسألة: ولا يتكلم','111','1','0');
INSERT INTO [t1153] VALUES (50,'مسألة: ولا يلبث فوق حاجته','112','1','0');
INSERT INTO [t1153] VALUES (51,'مسألة: فإذا خرج قال غفرانك الحمد لله الذي أذهب عني الأذى وعافاني','112','1','0');
INSERT INTO [t1153] VALUES (52,'مسألة: واستتر وارتاد مكانا رخوا','113','1','0');
INSERT INTO [t1153] VALUES (53,'مسألة: وإن كان في الفضاء أبعد','113','1','0');
INSERT INTO [t1153] VALUES (54,'مسألة: ولا يبول في شق ولا سرب ولا طريق ولا ظل نافع ولا تحت شجرة مثمرة','114','1','0');
INSERT INTO [t1153] VALUES (55,'مسألة: ولا يستقبل الشمس ولا القمر','116','1','0');
INSERT INTO [t1153] VALUES (56,'مسألة: ولا يجوز أن يستقبل القبلة في الفضاء','116','1','0');
INSERT INTO [t1153] VALUES (57,'مسألة: وفي استدبارها فيه واستقبالها في البنيان روايتان','117','1','0');
INSERT INTO [t1153] VALUES (58,'مسألة: فإذا فرغ مسح بيده اليسرى من أصل ذكره إلى رأسه ثم ينتره ثلاثا','119','1','0');
INSERT INTO [t1153] VALUES (59,'مسألة: ولا يمس ذكره بيمينه ولا يستجمر بها','119','1','0');
INSERT INTO [t1153] VALUES (60,'مسألة: ثم يتحول عن موضعه لئلا يتنجس بالخارج منه ثم يستجمر ثم يستنجي بالماء','120','1','0');
INSERT INTO [t1153] VALUES (61,'مسألة: ويجزئه أحدهما','120','1','0');
INSERT INTO [t1153] VALUES (62,'مسألة: إلا أن يعدو الخارج موضع الحاجة فلا يجزئ إلا الماء','121','1','0');
INSERT INTO [t1153] VALUES (63,'مسألة: ويجوز الاستجمار بكل طاهر ينقي كالحجر ونحوه الخشب والخرق','123','1','0');
INSERT INTO [t1153] VALUES (64,'مسألة: إلا الروث والعظام والطعام وما له حرمة وما يتصل بحيوان','124','1','0');
INSERT INTO [t1153] VALUES (65,'مسألة: ولا يجزئ أقل من ثلاث مسحات إما بحجر ذي شعب أو بثلاثة','125','1','0');
INSERT INTO [t1153] VALUES (66,'مسألة: فإن لم ينق بها زاد حتى ينقي','126','1','0');
INSERT INTO [t1153] VALUES (67,'مسألة: ويقطع على وتر','126','1','0');
INSERT INTO [t1153] VALUES (68,'مسألة: ويجب الاستنجاء من كل خارج إلا الريح','127','1','0');
INSERT INTO [t1153] VALUES (69,'مسألة: فإن توضأ قبله فهل يصح وضوءه على روايتين','128','1','0');
INSERT INTO [t1153] VALUES (70,'باب السواك وسنة الوضوء: مسألة: والسواك مسنون في جميع الأوقات','129','1','0');
INSERT INTO [t1153] VALUES (71,'مسألة: إلا الصائم بعد الزوال فلا يستحب','129','1','0');
INSERT INTO [t1153] VALUES (72,'مسألة: ويتأكد استحبابه في ثلاثة مواضع عند الصلاة للخبر','130','1','0');
INSERT INTO [t1153] VALUES (73,'مسألة: ويستاك بعود لين ينقي الفم ولا يجرحه ولا يضره ولا يتفتت فيه','131','1','0');
INSERT INTO [t1153] VALUES (74,'مسألة: فإن استاك بأصبعه أو خرقة فهل يصيب السنة على وجهين','131','1','0');
INSERT INTO [t1153] VALUES (75,'مسألة: ويستاك عرضا ويدهن غبا ويكتحل وترا','132','1','0');
INSERT INTO [t1153] VALUES (76,'مسألة: ويكره القزع وهو حلق بعض الرأس','138','1','0');
INSERT INTO [t1153] VALUES (77,'مسألة: ويجب الختان ما لم يخفه على نفسه','138','1','0');
INSERT INTO [t1153] VALUES (78,'مسألة: ويتيامن في سواكه وطهوره وانتعاله ودخوله المسجد','139','1','0');
INSERT INTO [t1153] VALUES (79,'مسألة: وسنن الوضوء عشرة السواك','139','1','0');
INSERT INTO [t1153] VALUES (80,'مسألة: وغسل الكفين إلا أن يكون قائما من نوم الليل ففي وجوبه روايتان','141','1','0');
INSERT INTO [t1153] VALUES (81,'مسألة: والبداية بالمضمضة والاستنشاق والمبالغة فيهما إلا أن يكون صائما','142','1','0');
INSERT INTO [t1153] VALUES (82,'مسألة: وتخليل اللحية وهو سنة','143','1','0');
INSERT INTO [t1153] VALUES (83,'مسألة: وتخليل الأصابع','143','1','0');
INSERT INTO [t1153] VALUES (84,'مسألة: والتيامن','144','1','0');
INSERT INTO [t1153] VALUES (85,'مسألة: وأخذ ماء جديد للأذنين','144','1','0');
INSERT INTO [t1153] VALUES (86,'مسألة: والغسلة الثانية والثالثة','144','1','0');
INSERT INTO [t1153] VALUES (87,'مسألة: ومسح الرأس','145','1','0');
INSERT INTO [t1153] VALUES (88,'باب فروض الوضوء وصفته','145','1','0');
INSERT INTO [t1153] VALUES (89,'مسألة: والفم والأنف منه','145','1','0');
INSERT INTO [t1153] VALUES (90,'مسألة: وغسل اليدين','145','1','0');
INSERT INTO [t1153] VALUES (91,'مسألة: والترتيب على ما ذكر الله تعالى','148','1','0');
INSERT INTO [t1153] VALUES (92,'مسألة: والموالاة على إحدى الروايتين','149','1','0');
INSERT INTO [t1153] VALUES (93,'مسألة: وهو أن لا يؤخر غسل عضو حتى ينشف الذي قبله','150','1','0');
INSERT INTO [t1153] VALUES (94,'مسألة: والنية شرط لطهارة الحدث كله','150','1','0');
INSERT INTO [t1153] VALUES (95,'مسألة: وهي أن يقصد رفع الحدث أو الطهارة لما لا يباح إلا بها','151','1','0');
INSERT INTO [t1153] VALUES (96,'مسألة: فإن نوى ما تسن له الطهارة أو التجديد فهل يرتفع حدثه؟ على روايتين','151','1','0');
INSERT INTO [t1153] VALUES (97,'مسألة: وإن نوى غسلا مسنونا فهل يجزئ عن الواجب؟ على وجهين','152','1','0');
INSERT INTO [t1153] VALUES (98,'مسألة: وإن اجتمعت أحداث توجب الوضوء أو الغسل فنوى بطهارته أحدها فهل يرتفع سائرها؟ على وجهين','152','1','0');
INSERT INTO [t1153] VALUES (99,'مسألة: ويجب تقديم النية على أول واجبات الطهارة','152','1','0');
INSERT INTO [t1153] VALUES (100,'مسألة: واستصحاب ذكرها في جميعها وإن استصحب حكمها أجزأه','153','1','0');
INSERT INTO [t1153] VALUES (101,'مسألة: وهما واجبان في الطهارتين وعنه أن الاستنشاق وحده واجب وعنه أنهما واجبان في الكبرى دون الصغرى','155','1','0');
INSERT INTO [t1153] VALUES (102,'مسألة: ويغسل وجهه ثلاثا وحده من منابت شعر الرأس إلى ما انحدر من اللحيين والذقن طولا مع ما استرسل من اللحية ومن الأذن إلى الأذن عرضا','157','1','0');
INSERT INTO [t1153] VALUES (103,'مسألة: فإن كان فيه شعر خفيف يصف البشرة وجب غسلها معه. وإن كان يسترها أجزأه غسل ظاهره ويستحب تخليله','160','1','0');
INSERT INTO [t1153] VALUES (104,'مسألة: ثم يغسل يديه إلى المرفقين ثلاثا ويدخل المرفقين في الغسل','162','1','0');
INSERT INTO [t1153] VALUES (105,'مسألة: ثم يمسح رأسه','164','1','0');
INSERT INTO [t1153] VALUES (106,'مسألة: يبدأ بيديه من مقدمه ثم يمرهما إلى قفاه ثم يردهما إلى مقدمه','164','1','0');
INSERT INTO [t1153] VALUES (107,'مسألة: ويجب مسح جميعه مع الأذنين وعنه يجزئ مسح أكثره','165','1','0');
INSERT INTO [t1153] VALUES (108,'مسألة: ولا يستحب تكراره وعنه يستحب','170','1','0');
INSERT INTO [t1153] VALUES (109,'مسألة: ثم يغسل رجليه إلى الكعبين ثلاثا ويدخلهما في الغسل','172','1','0');
INSERT INTO [t1153] VALUES (110,'مسألة: فإن كان أقطع غسل ما بقي من محل الفرض','173','1','0');
INSERT INTO [t1153] VALUES (111,'مسألة: فإن لم يبق شيء سقط وجوب الغسل','173','1','0');
INSERT INTO [t1153] VALUES (112,'مسألة: ثم يرفع نظره إلى السماء ويقول أشهد أن لا إله إلا الله وحده لا شريك له وأشهد أن محمدا عبده ورسوله','174','1','0');
INSERT INTO [t1153] VALUES (113,'مسألة: وتباح معونته','175','1','0');
INSERT INTO [t1153] VALUES (114,'مسألة: ويباح تنشيف أعضائه ولا يستحب','176','1','0');
INSERT INTO [t1153] VALUES (115,'باب المسح على الخفين','178','1','0');
INSERT INTO [t1153] VALUES (116,'مسألة: يجوز المسح على الخفين لما ذكرنا ويجوز على الجرموقين','179','1','0');
INSERT INTO [t1153] VALUES (117,'مسألة: والعمامة والجبائر','180','1','0');
INSERT INTO [t1153] VALUES (118,'مسألة: وفي المسح على القلانس وخمر النساء المدارة تحت حلوقهن روايتان','181','1','0');
INSERT INTO [t1153] VALUES (119,'مسألة: ومن شرطه أن يلبس الجميع بعد كمال الطهارة','182','1','0');
INSERT INTO [t1153] VALUES (120,'مسألة: إلا الجبيرة على إحدى الروايتين','185','1','0');
INSERT INTO [t1153] VALUES (121,'مسألة: ويمسح المقيم يوما وليلة والمسافر ثلاثة أيام ولياليهن','186','1','0');
INSERT INTO [t1153] VALUES (122,'مسألة: إلا الجبيرة فإنه يمسح عليها إلى حلها','188','1','0');
INSERT INTO [t1153] VALUES (123,'مسألة: وابتداء المدة من الحدث بعد اللبس وعنه من المسح بعده','188','1','0');
INSERT INTO [t1153] VALUES (124,'مسألة: ومن مسح مسافرا ثم أقام أتم مسح مقيم','189','1','0');
INSERT INTO [t1153] VALUES (125,'مسألة: وإن مسح مقيما ثم سافر أو شك في ابتدائه أتم مسح مقيم، وعنه يتم مسح مسافر','189','1','0');
INSERT INTO [t1153] VALUES (126,'مسألة: وإن أحدث ثم سافر قبل المسح أتم مسح مسافر','190','1','0');
INSERT INTO [t1153] VALUES (127,'مسألة: ولا يجوز المسح إلا على ما يستر محل الفرض ويثبت بنفسه','190','1','0');
INSERT INTO [t1153] VALUES (128,'مسألة: ويثبت بنفسه','192','1','0');
INSERT INTO [t1153] VALUES (129,'مسألة: فإن كان فيه خرق يبدو منه بعض القدم أو كان واسعا يرى منه الكعب أو الجورب خفيفا يصف القدم أو يسقط منه إذا مشى أو شد لفائف لم يجز المسح عليه','192','1','0');
INSERT INTO [t1153] VALUES (130,'مسألة: وإن لبس خفا فلم يحدث حتى لبس عليه آخر جاز المسح عليه','193','1','0');
INSERT INTO [t1153] VALUES (131,'مسألة: ويمسح أعلى الخف دون أسفله وعقبه فيضع يده على الأصابع ثم يمسح إلى ساقه','195','1','0');
INSERT INTO [t1153] VALUES (132,'مسألة: ويجوز المسح على العمامة المحنكة إذا كانت ساترة لجميع الرأس إلا ما جرت العادة بكشفه','197','1','0');
INSERT INTO [t1153] VALUES (133,'مسألة: قال: ولا يجوز على غير المحنكة إلا أن تكون ذات ذؤابة فيجوز في أحد الوجهين','198','1','0');
INSERT INTO [t1153] VALUES (134,'مسألة: ويجزئ مسح أكثرها وقيل: لا يجزئ إلا مسح جميعها','199','1','0');
INSERT INTO [t1153] VALUES (135,'مسألة: ويمسح على جميع الجبيرة إذا لم تتجاوز قدر الحاجة','200','1','0');
INSERT INTO [t1153] VALUES (136,'مسألة: ومتى ظهر قدم الماسح أو رأسه أو انقضت مدة المسح استأنف الطهارة','200','1','0');
INSERT INTO [t1153] VALUES (137,'مسألة: ولا مدخل لحائل في الطهارة الكبرى إلا الجبيرة','204','1','0');
INSERT INTO [t1153] VALUES (138,'باب نواقض الوضوء','204','1','0');
INSERT INTO [t1153] VALUES (139,'مسألة: الثاني خروج النجاسات من سائر البدن فإن كانت غائطا أو بولا نقض قليلها','208','1','0');
INSERT INTO [t1153] VALUES (140,'مسألة: وإن كان غيرهما لم ينقض إلا كثيرها وهو ما فحش في النفس وحكي عنه أن قليلها ينقض','209','1','0');
INSERT INTO [t1153] VALUES (141,'مسألة: الثالث زوال العقل إلا النوم اليسير جالسا أو قائما وعنه أن نوم الراكع والساجد لا ينقض يسيره','212','1','0');
INSERT INTO [t1153] VALUES (142,'مسألة: الرابع مس الذكر بيده ببطن الكف أو بظهره','215','1','0');
INSERT INTO [t1153] VALUES (143,'مسألة: ولا ينقض مسه بذراعه','216','1','0');
INSERT INTO [t1153] VALUES (144,'مسألة: وفي مس الذكر المقطوع وجهان','217','1','0');
INSERT INTO [t1153] VALUES (145,'مسألة: وإذا لمس قبل الخنثى المشكل وذكره انتقض وضوءه وإن مس أحدهما لم ينقض إلا أن يمس الرجل ذكره لشهوة','217','1','0');
INSERT INTO [t1153] VALUES (146,'مسألة: وفي مس الدبر ومس المرأة فرجها روايتان','218','1','0');
INSERT INTO [t1153] VALUES (147,'مسألة: وعنه لا ينقض مس الفرج بحال','218','1','0');
INSERT INTO [t1153] VALUES (148,'مسألة: الخامس أن تمس بشرته بشرة أنثى لشهوة وعنه لا ينقض وعنه ينقض لمسها بكل حال','218','1','0');
INSERT INTO [t1153] VALUES (149,'مسألة: وفي نقض وضوء الملموس روايتان','220','1','0');
INSERT INTO [t1153] VALUES (150,'مسألة: ولا ينقض لمس الشعر والسن والظفر','220','1','0');
INSERT INTO [t1153] VALUES (151,'مسألة: السادس غسل الميت','221','1','0');
INSERT INTO [t1153] VALUES (152,'مسألة: السابع أكل لحم الجزور','221','1','0');
INSERT INTO [t1153] VALUES (153,'مسألة: فإن شرب من لبنها فعلى روايتين','223','1','0');
INSERT INTO [t1153] VALUES (154,'مسألة: وإن أكل من كبدها أو طحالها فعلى وجهين','224','1','0');
INSERT INTO [t1153] VALUES (155,'مسألة: الثامن الردة عن الإسلام','224','1','0');
INSERT INTO [t1153] VALUES (156,'مسألة: ومن تيقن الطهارة وشك في الحدث أو تيقن الحدث وشك في الطهارة بنى على اليقين','226','1','0');
INSERT INTO [t1153] VALUES (157,'مسألة: فإن تيقنهما وشك في السابق منهما نظر في حالة قبلهما فإن كان متطهرا فهو محدث وإن كان محدثا فهو متطهر','226','1','0');
INSERT INTO [t1153] VALUES (158,'مسألة: ومن أحدث حرم عليه الصلاة والطواف ومس المصحف','227','1','0');
INSERT INTO [t1153] VALUES (159,'باب الغسل','229','1','0');
INSERT INTO [t1153] VALUES (160,'مسألة: فإن خرج لغير ذلك لم يوجب','229','1','0');
INSERT INTO [t1153] VALUES (161,'مسألة: فإن أحس بانتقاله فأمسك ذكره فلم يخرج فعلى روايتين','232','1','0');
INSERT INTO [t1153] VALUES (162,'مسألة: فإن خرج بعد الغسل وقلنا: لا يجب الغسل بالانتقال لزمه الغسل','232','1','0');
INSERT INTO [t1153] VALUES (163,'مسألة: الثاني: التقاء الختانين وهو تغييب الحشفة في الفرج قبلا كان أو دبرا من آدمي أو بهيمة حي أو ميت','234','1','0');
INSERT INTO [t1153] VALUES (164,'مسألة: الثالث: إسلام الكافر أصليا كان أو مرتدا وقال أبو بكر: لا غسل عليه','236','1','0');
INSERT INTO [t1153] VALUES (165,'مسألة: الرابع الموت. الخامس الحيض. السادس النفاس','238','1','0');
INSERT INTO [t1153] VALUES (166,'مسألة: وفي الولادة وجهان','238','1','0');
INSERT INTO [t1153] VALUES (167,'مسألة: ومن لزمه الغسل حرم عليه قراءة آية فصاعدا وفي بعض آية روايتان','239','1','0');
INSERT INTO [t1153] VALUES (168,'مسألة: ويجوز له العبور في المسجد ويحرم عليه اللبث فيه إلا أن يتوضأ','240','1','0');
INSERT INTO [t1153] VALUES (169,'فصل في صفة الغسل','245','1','0');
INSERT INTO [t1153] VALUES (170,'مسألة: مجزئ','246','1','0');
INSERT INTO [t1153] VALUES (171,'مسألة: والحامل لا تحيض فإن رأت دما فهو دم فساد','252','1','0');
INSERT INTO [t1153] VALUES (172,'مسألة: ويتوضأ بالمد ويغتسل بالصاع فإن سبغ بدونهما أجزأه','253','1','0');
INSERT INTO [t1153] VALUES (173,'مسألة: وإذا اغتسل ينوي الطهارتين أجزأ عنهما وعنه لا يجزئه حتى يتوضأ','256','1','0');
INSERT INTO [t1153] VALUES (174,'مسألة: ويستحب للجنب إذا أراد النوم أو الأكل أو الوطء ثانيا أن يغسل فرجه ويتوضأ','258','1','0');
INSERT INTO [t1153] VALUES (175,'فصول في الحمام','262','1','0');
INSERT INTO [t1153] VALUES (176,'باب التيمم','265','1','0');
INSERT INTO [t1153] VALUES (177,'مسألة: وهو بدل لا يجوز إلا بشرطين أحدهما دخول الوقت فلا يجوز لفرض قبل وقته ولا لنفل في وقت النهي عنه','265','1','0');
INSERT INTO [t1153] VALUES (178,'مسألة: أو لضرر في استعماله من جرح أو برد شديد أو مرض يخشى زيادته أو تطاوله','269','1','0');
INSERT INTO [t1153] VALUES (179,'مسألة: أو عطش يخافه على نفسه أو رفيقه أو بهيمته','271','1','0');
INSERT INTO [t1153] VALUES (180,'مسألة: أو خشية على ماله في طلبه','273','1','0');
INSERT INTO [t1153] VALUES (181,'مسألة: أو تعذره إلا بزيادة كثيرة على ثمن مثله أو ثمن يعجز عن آدائه','275','1','0');
INSERT INTO [t1153] VALUES (182,'مسألة: فإن كان بعض بدنه جريحا تيمم له وغسل الباقي','276','1','0');
INSERT INTO [t1153] VALUES (183,'مسألة: وإن وجد ماء يكفي بعض بدنه لزمه إستعماله وتيمم للباقي إن كان جنبا، وإن كان محدثا فهل يلزمه استعماله على وجهين','279','1','0');
INSERT INTO [t1153] VALUES (184,'مسألة: ومن عدم الماء لزمه طلبه في رحله وما قرب منه، وإن دل عليه قريباص لزمه قصده وعنه لا يجب الطلب','281','1','0');
INSERT INTO [t1153] VALUES (185,'مسألة: وإن نسي الماء بموضع يمكنه استعماله وتيمم لم يجزه','282','1','0');
INSERT INTO [t1153] VALUES (186,'مسألة: ويجوز التيمم لجميع الأحداث وللنجاسة على جرح يضره إزالتها','283','1','0');
INSERT INTO [t1153] VALUES (187,'مسألة: وإن تيمم في الحضر خوفا من البرد وصلى ففي وجوب الإعادة روايتان','285','1','0');
INSERT INTO [t1153] VALUES (188,'مسألة: فإن عدم الماء والتراب صلى على حسب حاله في الصحيح من المذهب','285','1','0');
INSERT INTO [t1153] VALUES (189,'مسألة: ولا يجوز التيمم إلا بتراب طاهر له غبار يعلق باليد','286','1','0');
INSERT INTO [t1153] VALUES (190,'مسألة: فإن خالطه ذو غبار لا يجوز التيمم به كالجص ونحوه فهو كالماء إذا خالطته الطاهرات','289','1','0');
INSERT INTO [t1153] VALUES (191,'مسألة: ويجب تعيين النية لما يتيمم له من حدث أو غيره','291','1','0');
INSERT INTO [t1153] VALUES (192,'مسألة: وإن نوى أحدهما لم يجزه عن الآخر','292','1','0');
INSERT INTO [t1153] VALUES (193,'مسألة: فإن نوى جميعها','292','1','0');
INSERT INTO [t1153] VALUES (194,'مسألة: وإن نوى نفلا أو أطلق النية للصلاة لم يصل إلا نفلا','293','1','0');
INSERT INTO [t1153] VALUES (195,'مسألة: وإن نوى فرضا فله فعله والجمع بين الصلاتين وقضاء الفوائت والتنفل إلى آخر الوقت','294','1','0');
INSERT INTO [t1153] VALUES (196,'مسألة: ويبطل التيمم بخروج الوقت ووجود الماء ومبطلات الوضوء','298','1','0');
INSERT INTO [t1153] VALUES (197,'مسألة: فإن تيمم وعليه ما يجوز المسح عليه ثم خلعه لم يبطل تيممه وقال أصحابنا يبطل','301','1','0');
INSERT INTO [t1153] VALUES (198,'مسألة: وإن وجد الماء بعد الصلاة لم تجب إعادتها','304','1','0');
INSERT INTO [t1153] VALUES (199,'مسألة: وإن وجده فيها بطلت وعنه لا تبطل','305','1','0');
INSERT INTO [t1153] VALUES (200,'مسألة: ويستحب تأخير التيمم إلى آخر الوقت لمن يرجو وجود الماء','307','1','0');
INSERT INTO [t1153] VALUES (201,'مسألة: فإن تيمم في أول الوقت وصلى أجزأه','308','1','0');
INSERT INTO [t1153] VALUES (202,'مسألة: والسنة في التيمم أن ينوي ويسمي ويضرب بيديه مفرجتي الأصابع على التراب ضربة واحد فيمسح وجهه بباطن أصابعه وكفيه براحتيه','308','1','0');
INSERT INTO [t1153] VALUES (203,'مسألة: ومن حبس في المصر صلى بالتيمم ولا إعادة عليه','311','1','0');
INSERT INTO [t1153] VALUES (204,'مسألة: ولا يجوز لواجد الماء التيمم خوفا من فوات المكتوبة ولا الجنازة وعنه يجوز للجنازة','311','1','0');
INSERT INTO [t1153] VALUES (205,'مسألة: وإن إجتمع جنب وميت ومن عليها غسل حيض فبذل ماء يكفي أحدهم لاولاهم به فهو للميت وعنه أنه للحي وأيهما يقدم؟ فيه وجهان','312','1','0');
INSERT INTO [t1153] VALUES (206,'باب إزالة النجاسة','315','1','0');
INSERT INTO [t1153] VALUES (207,'مسألة: ويجب غسل نجاسة الكلب والخنزير سبعا إحداهن بالتراب','316','1','0');
INSERT INTO [t1153] VALUES (208,'مسألة: فإن جعل مكانه أشنانا أو نحوه فعلى وجهين','318','1','0');
INSERT INTO [t1153] VALUES (209,'مسألة: وفي سائر النجاسات ثلاث روايات إحداهن يجب غسلها سبعا وهل يشترط التراب على وجهين، والثانية ثلاثا والثالثة تكاثر بالماء من غير عدد كالنجاسات كلها إذا كانت على الأرض','320','1','0');
INSERT INTO [t1153] VALUES (210,'مسألة: ولا تطهر الأرض النجسة بشمس ولا ريح','325','1','0');
INSERT INTO [t1153] VALUES (211,'مسألة: ولا يطهر شيء من النجاسات بالإستحالة إلا الخمر إذا إنقلبت بنفسها','326','1','0');
INSERT INTO [t1153] VALUES (212,'مسألة: فإن خللت لم تطهر في ظاهر المذهب','326','1','0');
INSERT INTO [t1153] VALUES (213,'مسألة: ولا تطهر الادهان النجسة بالغسل','327','1','0');
INSERT INTO [t1153] VALUES (214,'مسألة: وإذا خفيت النجاسة لزمه غسل ما يتيقن به إزالتها','328','1','0');
INSERT INTO [t1153] VALUES (215,'مسألة: ويجزيء في بول الغلام الذي لم يأكل الطعام النضح','329','1','0');
INSERT INTO [t1153] VALUES (216,'مسألة: وإذا تنجس أسفل الخف أو الحذاء وجب غسله، وعنه يجزيء دلكه بالأرض وعنه يغسل من البول والغائط ويدلك من غيرهما','331','1','0');
INSERT INTO [t1153] VALUES (217,'مسألة: ولا يعفى عن يسير شيء من النجاسات إلا الدم وما تولد منه من القيح والصديد وأثر الاستنجاء','332','1','0');
INSERT INTO [t1153] VALUES (218,'مسألة: وعنه في المذي والقيء وريق البغل والحمار وسباع البهائم والطير وعرقها وبول الخفاش والنبيذ والمني أنه كالدم، وعنه المذي أنه يجزي فيه النضح','335','1','0');
INSERT INTO [t1153] VALUES (219,'مسألة: ولا ينجس الآدمي بالموت ولا ما لا نفس له سائلة كالذباب وغيره','336','1','0');
INSERT INTO [t1153] VALUES (220,'مسألة: وبول ما يؤكل لحمه وروثه ومنيه طاهر وعنه أنه نجس','339','1','0');
INSERT INTO [t1153] VALUES (221,'مسألة: والمني طاهر وعنه أنه نجس ويجزيء فرك يابسه','340','1','0');
INSERT INTO [t1153] VALUES (222,'مسألة: وفي رطوبة فرج المرأة روايتان إحداهما','342','1','0');
INSERT INTO [t1153] VALUES (223,'مسألة: وسباع البهائم والطير والبغل والحمار الأهلي نجسة وعنه أنها طاهرة','342','1','0');
INSERT INTO [t1153] VALUES (224,'مسالة: وسؤر الهرة وما دونها في الخلقة طاهر','344','1','0');
INSERT INTO [t1153] VALUES (225,'باب الحيض: مسألة: وهو دم طبيعة وجبلة','346','1','0');
INSERT INTO [t1153] VALUES (226,'مسألة: ويمنع عشرة أحدها فعل الصلاة والثاني وجوبها','347','1','0');
INSERT INTO [t1153] VALUES (227,'مسألة: ويوجب الغسل عند إنقطاعه','348','1','0');
INSERT INTO [t1153] VALUES (228,'مسألة: والنفاس مثله لا في الاعتداد','348','1','0');
INSERT INTO [t1153] VALUES (229,'مسألة: فإذا إنقطع الدم أبيح فعل الصيام والطلاق ولم يبح غيرهما تغتسل','348','1','0');
INSERT INTO [t1153] VALUES (230,'مسألة: ويجوز الاستمتاع من الحائض بما دون الفرج','349','1','0');
INSERT INTO [t1153] VALUES (231,'مسألة: فإن وطئها في الفرج فعليه نصف دينار كفارة وعنه ليس عليه إلا التوبة','349','1','0');
INSERT INTO [t1153] VALUES (232,'مسأل، قال: وأكثره خمسون سنة وعنه ستون في نساء العرب','351','1','0');
INSERT INTO [t1153] VALUES (233,'مسألة: وأقل سن تحيض له المرأة تسع سنين','351','1','0');
INSERT INTO [t1153] VALUES (234,'مسألة: وأقل الحيض يوم وليلة وعنه يوم وأكثره خمسة عشر يوما وعنه سبعة عشر','353','1','0');
INSERT INTO [t1153] VALUES (235,'مسألة: وغالبه ست أو سبع','354','1','0');
INSERT INTO [t1153] VALUES (236,'مسألة: وأقل الطهر بين الحيضتين ثلاثة عشر يوما','355','1','0');
INSERT INTO [t1153] VALUES (237,'مسألة: والمبتدأة تجلس يوما وليلة ثم تغتسل وتصلي فإن إنقطع دمها لأكثره فما دون اغتسلت عند إنقطاعه وتفعل ذلك ثلاثا فإن كان في الثلاث على قدر واحد صار عادة وإنتقلت إليه وأعادت ما صامته من الفرض فيه وعنه يصير عادة بمرتين','355','1','0');
INSERT INTO [t1153] VALUES (238,'مسألة: فإن جاوز أكثر الحيض فهي مستحاضة','357','1','0');
INSERT INTO [t1153] VALUES (239,'مسألة: فإن كان دمها متميزا بعضه أسود ثخين منتن وبعضه رقيق فحيضها زمن الدم الأسود وما عداه استحاضة','357','1','0');
INSERT INTO [t1153] VALUES (240,'مسألة: وذكر أبو الخطاب في المبتدأة أول ما ترى الدم الروايات الأربع','361','1','0');
INSERT INTO [t1153] VALUES (241,'مسألة: وإن استحيضت المعتادة رجعت إلى عادتها وإن كانت مميزة، وعنه يقدم التمييز وهو إختيار الخرقي وإن نسيت العادة عملت بالتمييز فإن لم يكن لها تمييز جلست غالب الحيض من كل شهر، وعنه أقله وقيل: فيها الروايات الأربع','362','1','0');
INSERT INTO [t1153] VALUES (242,'مسألة: وإن نسيت العادة عملت بالتمييز','367','1','0');
INSERT INTO [t1153] VALUES (243,'مسألة: فإن لم يكن لها تمييز جلست غالب الحيض من كل شهر وعنه أقله وقيل: فيها الورايات الأربع','369','1','0');
INSERT INTO [t1153] VALUES (244,'مسألة: وإن علمت عدد أيامها ونسيت موضعها جلستها من أول كل شهر في أحد الوجهين وفي الآخر تجلسها بالتحري','372','1','0');
INSERT INTO [t1153] VALUES (245,'مسألة: وكذلك الحكم في موضع حيض من لا عادة لها ولا تمييز','373','1','0');
INSERT INTO [t1153] VALUES (246,'مسألة: وإن علمت أيامها في وقت من الشهر كنصفه الأول جلستها فيه إما من أوله أو بالتحري على إختلاف الوجهين','373','1','0');
INSERT INTO [t1153] VALUES (247,'مسألة: وإن علمت موضع حيضها ونسيت عدده جلست فيه غالب الحيض أو أقله على إختلاف الروايتين','374','1','0');
INSERT INTO [t1153] VALUES (248,'مسألة: وإن تغيرت العادة بزيادة أو تقدم أو تأخر أو إنتقال فالمذهب أنها لا تلتفت إلى ما خرج عن العادة حتى يتكرر ثلاثا أو مرتين على الإختلاف','375','1','0');
INSERT INTO [t1153] VALUES (249,'مسألة: وإن طهرت في اثناء عادتها إغتسلت وصلت فإن عاودها الدم في العادة، فهل تلتفت إليه؟ على روايتين','378','1','0');
INSERT INTO [t1153] VALUES (250,'مسألة: والصفرة والكدرة في أيام الحيض من الحيض','382','1','0');
INSERT INTO [t1153] VALUES (251,'مسألة: ومن كانت ترى يوما دما ويوما طهرا فإنها تضم الدم إلى الدم فيكون حيضا والباقي طهرا إلا أن يجاوز أكثر الحيض فتكون مستحاضة','384','1','0');
INSERT INTO [t1153] VALUES (252,'مسألة: وهل يباح وطء المستحاضة في الفرج من غير خوف العنت على روايتين إحداهما','400','1','0');
INSERT INTO [t1153] VALUES (253,'مسألة: ولا حد لأقله','402','1','0');
INSERT INTO [t1153] VALUES (254,'مسألة: أي وقت رأت الطهر فهي تغتسل وتصلي إذا كان الطهر أقل من ساعة فينبغي أن لا تلتفت إليه','403','1','0');
INSERT INTO [t1153] VALUES (255,'مسألة: ويستحب أن لا يقربها في الفرج حتى تتم الأربعين','404','1','0');
INSERT INTO [t1153] VALUES (256,'مسألة: فإن إنقطع دمها في مدة الأربعين ثم عاد فيها فهو نفاس وعنه أنه مشكوك فيه تصوم وتقضي الصوم المفروض','405','1','0');
INSERT INTO [t1153] VALUES (257,'مسألة: وإن ولدت توأمين فأول النفاس من الأول وآخره منه، وعنه أنه من الأخير والأول أصح','407','1','0');
INSERT INTO [t1153] VALUES (258,'كتاب الصلاة','409','0','0');
INSERT INTO [t1153] VALUES (259,'كتاب الصلاة','409','258','0');
INSERT INTO [t1153] VALUES (260,'مسألة: وتجب على النائم ومن زال عقله بسكر أو إغماء أو شرب دواء','410','258','0');
INSERT INTO [t1153] VALUES (261,'مسألة. ولا تجب على كافر ولا مجنون ولا تصح منهما','411','258','0');
INSERT INTO [t1153] VALUES (262,'مسألة: وإذا صلى الكافر حكم بإسلامه','412','258','0');
INSERT INTO [t1153] VALUES (263,'مسألة: ويؤمر بها لسبع ويضرب على تركها لعشر','413','258','0');
INSERT INTO [t1153] VALUES (264,'مسألة: ولا تجب على صبي وعنه أنها تجب على من بلغ عشرا','413','258','0');
INSERT INTO [t1153] VALUES (265,'مسألة: فإن بلغ في أثنائها أو بعدها في وقتها لزمه إعادتها','414','258','0');
INSERT INTO [t1153] VALUES (266,'مسألة: ولا يحل لمن وجبت عليه الصلاة تأخيرها عن وقتها إلا لمن ينوي الجمع أو لمشتغل بشرطها','414','258','0');
INSERT INTO [t1153] VALUES (267,'مسألة: فمن جحد وجوبها كفر','415','258','0');
INSERT INTO [t1153] VALUES (268,'مسألة: وإن تكرها تهاونا ولا جحودا دعي إلى فعلها، فإن أبى حتى تضايق التي بعدها وجب قتله وعنه لا يجب حتى يترك ثلاثا ويضيق وقت الرابعة','415','258','0');
INSERT INTO [t1153] VALUES (269,'مسألة: ولا يقتل حتى يستتاب ثلاثا فإن تاب وإلا قتل بالسيف','417','258','0');
INSERT INTO [t1153] VALUES (270,'ومسألة: وهل يقتل حدا أو لكفره؟ على روايتين','417','258','0');
INSERT INTO [t1153] VALUES (271,'باب الأذان والإقامة','420','258','0');
INSERT INTO [t1153] VALUES (272,'مسألة: وهما مشروعان للصلوات الخمس دون غيرها للرجال دون النساء','421','258','0');
INSERT INTO [t1153] VALUES (273,'مسألة: وهما فرض على الكفاية إن اتفق أهل اتفق أهل بلد على تركهما قاتلهم الإمام','423','258','0');
INSERT INTO [t1153] VALUES (274,'مسألة: ولا يجوز أخذ الأجرة عليهما في أظهر الروايتين','426','258','0');
INSERT INTO [t1153] VALUES (275,'مسالة: فإن لم يوجد متطوع بهما رزق الإمام من بيت المال من يقوم بهما','427','258','0');
INSERT INTO [t1153] VALUES (276,'مسألة: وينبغي أن يكون المؤذن صيتا أمينا عالما بالأوقات','427','258','0');
INSERT INTO [t1153] VALUES (277,'مسألة: فإن تشاح فيه نفسان قدم أفضلهما في ذلك، ثم أفضلهما في دينه وعقله','428','258','0');
INSERT INTO [t1153] VALUES (278,'مسألة: والأذان خمسة عشر كلمة لا ترجيع فيه','429','258','0');
INSERT INTO [t1153] VALUES (279,'مسألة ك والإقامة إحدى عشرة كلمة فإن رجع في الأذان أو ثنى في الإقامة فلا بأس','430','258','0');
INSERT INTO [t1153] VALUES (280,'مسألة: ويقول في آذان الصبح الصلاة خير من النوم مرتين','432','258','0');
INSERT INTO [t1153] VALUES (281,'مسألة: ويستحب أن يترسل في الأذان ويحدر الإقامة','433','258','0');
INSERT INTO [t1153] VALUES (282,'مسألة: ويؤذن قائما متطهرا على موضع عال مستقبل القبلة','434','258','0');
INSERT INTO [t1153] VALUES (283,'مسألة: فإذا أبلغ الحيعلة ألتفت يمنيا وشمالا ولم يستدر','436','258','0');
INSERT INTO [t1153] VALUES (284,'مسألة: ويجعل أصبعيه','437','258','0');
INSERT INTO [t1153] VALUES (285,'مسألة: ويتولاهما معا','437','258','0');
INSERT INTO [t1153] VALUES (286,'مسألة: ويستحب للمؤذن أن يقيم في موضع أذانه إلا أن يشق عليه','438','258','0');
INSERT INTO [t1153] VALUES (287,'مسألة: ولا يصح الأذان إلا مرتبا متواليا فإن نكسه أو فرق بينه بسكوت طويل أو كلام كثير أو محرم لم يعتد به','439','258','0');
INSERT INTO [t1153] VALUES (288,'مسألة: ولا يصح إلا بعد دخول الوقت إلا الفجر فإنه يؤذن لها بعد نصف الليل','440','258','0');
INSERT INTO [t1153] VALUES (289,'مسألة: ويستحب أن يجلس بعد أذان المغرب جلسة خفيفة ثم يقيم','443','258','0');
INSERT INTO [t1153] VALUES (290,'مسألة: ومن جمع بين صلاتين أو قضى فوائت أذن وأقام للأولى ثم أقام لكل صلاة بعدها','444','258','0');
INSERT INTO [t1153] VALUES (291,'مسألة: وهل يجزيء أذان المميز للبالغين؟ على روايتين','446','258','0');
INSERT INTO [t1153] VALUES (292,'مسألة: وهل يصح أذان الفاسق، والأذان الملحن؟ على وجهين','448','258','0');
INSERT INTO [t1153] VALUES (293,'مسألة: ويستحب لمن سمع المؤذن أن يقول كما يقول إلا في الحيعلة فإنه يقول: لا حول ولا قوة إلا بالله','449','258','0');
INSERT INTO [t1153] VALUES (294,'مسألة: ثم يقول عند فراغه اللهم رب هذه الدعوة التامة والصلاة القائمة آت محمدا الوسيلة والفضيلة وابعثه المقام المحمود الذي وعدته إنك لا تخلف الميعاد','450','258','0');
INSERT INTO [t1153] VALUES (295,'فصول في المساجد: فصل: في فضل المساجد وبنائها وغير ذلك','453','258','0');
INSERT INTO [t1153] VALUES (296,'فصل فيما يكره في المسجد','457','258','0');
INSERT INTO [t1153] VALUES (297,'باب شروط الصلاة: مسألة: وهي ما يجب لها قبلها وهي ست أولها دخول الوقت والثاني الطهارة من الحدث','458','258','0');
INSERT INTO [t1153] VALUES (298,'مسألة: والصلوات المفروضات خمس','459','258','0');
INSERT INTO [t1153] VALUES (299,'مسألة: الظهر وهي ألأولى ووقتها من زوال الشمس إلى أن يصير ظل كل شيء مثله بعد الذي زالت عليه الشمس','460','258','0');
INSERT INTO [t1153] VALUES (300,'مسألة: وتعجيلها أفضل إلا في شدة الحر والغيم لمن يصلي الجماعة','464','258','0');
INSERT INTO [t1153] VALUES (301,'مسألة: ثم العصر وهي الوسطى ووقتها من خروج وقت الظهر إلى إصفرار الشمس وعنه إلى أن يصير ظل كل شيء مثليه ثم يذهب وقت الاختيار ويبقى وقت الضرورة إلى غروب الشمس','467','258','0');
INSERT INTO [t1153] VALUES (302,'مسألة: وتعجيلها أفضل بكل حال','470','258','0');
INSERT INTO [t1153] VALUES (303,'مسألة: ثم المغرب وهي الوتر ووقتها من مغيب الشمس إلى مغيب الشفق الأحمر','471','258','0');
INSERT INTO [t1153] VALUES (304,'مسألة ك وتعجيلها أفضل إلا ليلة جمع لمن قصدها','472','258','0');
INSERT INTO [t1153] VALUES (305,'مسألة: ثم العشاء ووقتها من مغيب الشفق الأحمر إلى ثلث الليل الأول وعنه إلى نصفه','473','258','0');
INSERT INTO [t1153] VALUES (306,'مسألة: ثم يذهب وقت الإختيار ويبقى وقت الضرورة إلى طلوع الفجر الثاني وهو البياض المعترض في المشرق ولا ظلمة بعده وتأخيرها أفضل ما لم يشق','473','258','0');
INSERT INTO [t1153] VALUES (307,'مسألة: ثم الفجر ووقتها من طلوع الفجر الثاني إلى طلوع الشمس','475','258','0');
INSERT INTO [t1153] VALUES (308,'مسألة: وتعجيلها أفضل وعنه أن أسفر المأمومون فالأفضل الأسفار','475','258','0');
INSERT INTO [t1153] VALUES (309,'مسألة: ومن أدرك تكبيرة الإحرام من صلاة في وقتها فقد أدركها','477','258','0');
INSERT INTO [t1153] VALUES (310,'مسألة: ومن شك في الوقت لم يصل حتى يغلب على ظنه دخوله','478','258','0');
INSERT INTO [t1153] VALUES (311,'مسألة: فإن أخبره بذلك مخبر عن يقين قبل قوله وإن كان عن ظن لم يقبله','478','258','0');
INSERT INTO [t1153] VALUES (312,'مسألة: ومتى اجتهد وصلى فبان أنه وافق الوقت أو ما بعده أجزأه','479','258','0');
INSERT INTO [t1153] VALUES (313,'مسألة: ومن أدرك من الوقت قدر تكبيرة ثم جن أو حاضت المراة لزمها القضاء','480','258','0');
INSERT INTO [t1153] VALUES (314,'مسألة: وإن بلغ صبي أو أسلم كافر أو أفاق مجنون أو طهرت حائض قبل طلوع الشمس بقدر تكبيرة لزمهم الصبح، وإن كان قبل غروب الشمس لزمهم الظهر والعصر، وإن كان قبل طلوع الفجر لزمهم المغرب والعشاء','480','258','0');
INSERT INTO [t1153] VALUES (315,'مسألة: ومن فاتته صلاة لزمه قضاؤها على الفور مرتبا قلت أو كثرت','482','258','0');
INSERT INTO [t1153] VALUES (316,'مسألة: فإن خشي فوات الحاضرة أو نسي الترتيب سقط وجوبه','484','258','0');
INSERT INTO [t1153] VALUES (317,'مسالة: وإن نسي الترتيب سقط وجوبه','488','258','0');
INSERT INTO [t1153] VALUES (318,'باب ستر العورة','489','258','0');
INSERT INTO [t1153] VALUES (319,'مسألة: وسترها عن النظر بما لا يصف البشرة واجب','489','258','0');
INSERT INTO [t1153] VALUES (320,'مسألة: وعورة الرجل والأمة ما بين السرة والركعة وعنه أنها الفرجان','489','258','0');
INSERT INTO [t1153] VALUES (321,'مسألة: والحرة كلها عورة إلا الوجه وفي الكفين روايتان','491','258','0');
INSERT INTO [t1153] VALUES (322,'مسالة: وأم الولد والمعتق بعضها كالأمة وعنه كالحرة','492','258','0');
INSERT INTO [t1153] VALUES (323,'مسألة: ويستحب للرجل أن يصلي في ثوبين','493','258','0');
INSERT INTO [t1153] VALUES (324,'مسألة: فإن إقتصر على ستر العورة أجزأه إذا كان على عاتقه شيء من اللباس','493','258','0');
INSERT INTO [t1153] VALUES (325,'مسألة: ويستحب للمرأة أن تصلي في درع وخمار وملحفة فإن إقتصرت على ستر العورة أجزأها','495','258','0');
INSERT INTO [t1153] VALUES (326,'مسألة: وإذا إنكشف من العورة يسير لم يفحش في النظر لم تبطل صلاته','496','258','0');
INSERT INTO [t1153] VALUES (327,'مسألة: وإن فحش بطلت','497','258','0');
INSERT INTO [t1153] VALUES (328,'مسألة: ومن صلى في ثوب حرير أو مغصوب لم تصح صلاته وعنه تصح مع التحريم','497','258','0');
INSERT INTO [t1153] VALUES (329,'مسألة: ومن لم يجد إلا ثوبا نجسا صلى فيه','498','258','0');
INSERT INTO [t1153] VALUES (330,'مسألة: وتلزمه الإعادة على المنصوص','498','258','0');
INSERT INTO [t1153] VALUES (331,'مسألة: فإن لم يكف جميعها ستر الفرجين','499','258','0');
INSERT INTO [t1153] VALUES (332,'مسألة: فإن عدم بكل حال صلى جالسا يوميء إيماء وإن صلى قائما جاز، وعنه أنه يصلي قائما ويسجد بالأرض','499','258','0');
INSERT INTO [t1153] VALUES (333,'مسألة: فإن لم يكفهما جميعا ستر أيهما شاء','499','258','0');
INSERT INTO [t1153] VALUES (334,'مسألة: فإن لم يجد إلا ما يستر عورته سترها','499','258','0');
INSERT INTO [t1153] VALUES (335,'مسألة: وإن بذلت له سترة لزمه قبولها إذا كانت عارية','499','258','0');
INSERT INTO [t1153] VALUES (336,'مسألة: وإن وجد السترة قريبة منه في أثناء الصلاة ستر وبنى وإن كانت بعيدة ستر وابتدأ','501','258','0');
INSERT INTO [t1153] VALUES (337,'مسألة: ويصلي العراة جماعة وأمامهم في وسطهم','501','258','0');
INSERT INTO [t1153] VALUES (338,'مسألة: وإن كانوا رجالا ونساء صلى كل نوع لأنفسهم','502','258','0');
INSERT INTO [t1153] VALUES (339,'مسألة: ويكره في الصلاة السدل','503','258','0');
INSERT INTO [t1153] VALUES (340,'مسألة: ويكره إشتمال الصماء وهو أن يضطبع بثوب ليس عليه غيره','503','258','0');
INSERT INTO [t1153] VALUES (341,'مسألة: ويكره إسبال شيء من ثيابه خيلاء','504','258','0');
INSERT INTO [t1153] VALUES (342,'مسألة: ويكره شد الوسط بما يشد الزنار','504','258','0');
INSERT INTO [t1153] VALUES (343,'مسألة: ويكره لف الكم','504','258','0');
INSERT INTO [t1153] VALUES (344,'مسألة: ويكره تغطية الوجه والتلثم على الفم والأنف','504','258','0');
INSERT INTO [t1153] VALUES (345,'مسألة: ولا يجوز للرجل لبس ثياب الحرير ولا ما غالبه الحرير ولا إفتراشه إلا من ضرروة','504','258','0');
INSERT INTO [t1153] VALUES (346,'مسألة: فإن أستوى هو وما نسج معه فعلى وجهين','505','258','0');
INSERT INTO [t1153] VALUES (347,'مسألة: ويحرم لبس المنسوج بالذهب والمموه به','505','258','0');
INSERT INTO [t1153] VALUES (348,'مسألة: وإن لبسه الحرير لمرض أو حكة أو في الحرب أو ألبسه الصبي فعل روايتين','505','258','0');
INSERT INTO [t1153] VALUES (349,'مسألة: ويباج حشو الجباب والفرش به ويحتمل أن يحرم','506','258','0');
INSERT INTO [t1153] VALUES (350,'مسألة: ويباح العلم الحرير في الثوب إذا كان أربع أصابع فما دون','506','258','0');
INSERT INTO [t1153] VALUES (351,'مسألة: ويكره للرجل لبس المزعفر والمعصفر','507','258','0');
INSERT INTO [t1153] VALUES (352,'باب إجتناب النجاسات: وهو الشرط الرابع فمتى لاقى بثوبه أو بدنه نجاسة غير معفو عنها أو حملها لم تصح صلاته','508','258','0');
INSERT INTO [t1153] VALUES (353,'مسألة: وإن صلى على مكان طاهر من بساط طرفه نجس صحت إلا أني يكون متعلقا به بحيث ينجر معه إذا مشى فلا يصح','509','258','0');
INSERT INTO [t1153] VALUES (354,'مسألة: ومتى وجد عليها نجاسة ولا يعلم هل كانت في الصلاة أو لا فصلاته صحيح لأن لأصل عدمها في الصلاة وإن علم أنها كانت في الصلاة لكنه جهلها أو نسيها فعلى روايتين','509','258','0');
INSERT INTO [t1153] VALUES (355,'مسألة: وإذا جبر ساقه بعظم نجس فجبر لم يلزمه قلعه إذا خاف الضرر','510','258','0');
INSERT INTO [t1153] VALUES (356,'مسألة: ولا صتح الصلاة في المقبرة والحمام والحش وأعطان الإبل وي التي تقيم فيها وتأوي إليها والمواضع المغصوب، وعنه تصح مع التحريم','510','258','0');
INSERT INTO [t1153] VALUES (357,'مسألة: وقال بعض أصحابنا حكم المجزرة والمزبلة وقارعة الطريق وأسطحتها كذلك','512','258','0');
INSERT INTO [t1153] VALUES (358,'مسألة: وتصح الصلاة إليها إلا المقبرة والحش في قول ابن حامد','513','258','0');
INSERT INTO [t1153] VALUES (359,'مسألة: ولا تصح الفر يضة في الكعبة ولا على ظهرها','514','258','0');
INSERT INTO [t1153] VALUES (360,'مسألة: وتصح النافلة إذا كان بين يديه شيء منها','514','258','0');
INSERT INTO [t1153] VALUES (361,'باب استقبال القبلة','515','258','0');
INSERT INTO [t1153] VALUES (362,'مسألة: إلا في حال العجز عنه والنافلة على الراحلة في السفر الطويل والقصير','515','258','0');
INSERT INTO [t1153] VALUES (363,'مسألة: وهل يجوز ذلك للماشي؟','516','258','0');
INSERT INTO [t1153] VALUES (364,'مسألة: فإن أمكنه إفتتاح الصلاة إلى القبلة فهل يلزمه ذلك؟ على روايتين','516','258','0');
INSERT INTO [t1153] VALUES (365,'مسألة: والفرض في القبلة أصابة العين لمن قرب منها وأصابة الجهة لمن بعد عنها','517','258','0');
INSERT INTO [t1153] VALUES (366,'مسألة: فإن أمكنه ذلك بخبر ثقة عن يقين أو استدلال بمحاريب المسلمين لزمه العمل به وإن وجد محاريب لا يعلم هل هي للمسلمين أولا لم يلتفت إليها','518','258','0');
INSERT INTO [t1153] VALUES (367,'مسألة: وإن إشتبهت عليه في السفر إجتهد في طلبها بالدلائل وأثبتها بالقطب إذا جعله وراء ظهره كان مستقبلا الكعبة','519','258','0');
INSERT INTO [t1153] VALUES (368,'مسألة: والرياح الجنوب تهب مستقبلة لبطن كتف المصلي اليسرى مارة إلى يمينه من الزاوية التي بين القبلة والمشرق والشمال مقابلتها تهب إلى مهب الجنوب','520','258','0');
INSERT INTO [t1153] VALUES (369,'مسألة: وإذا إختلف إجتهاد رجلين لم يتبع أحدهما صاحبه ويتبع الجاهل والأعمى أوثقهما في نفسه','521','258','0');
INSERT INTO [t1153] VALUES (370,'مسألة: وإذا صلى البصير في حضر فأخطأ أو صلى الأعمى بلا دليل أعاد','523','258','0');
INSERT INTO [t1153] VALUES (371,'مسألة: فإن لم يجد الأعمى من يقلده صلى وفي الإعادة روايتان','523','258','0');
INSERT INTO [t1153] VALUES (372,'مسألة: ومن صلى بالإجتهاد إلى جهة ثم علم أنه أخطأ القبلة فلا إعادة عليه','524','258','0');
INSERT INTO [t1153] VALUES (373,'مسألة: فإن أراد صلاة أخرى اجتهد لها فإن تغير إجتهاده عمل بالثاني ولم يعد ما صلى بالأول','525','258','0');
INSERT INTO [t1153] VALUES (374,'مسألة: وهل تشترط نية القضاء في الفائتة ونية الفرضية في الفرض؟ على وجهين','526','258','0');
INSERT INTO [t1153] VALUES (375,'باب النية','526','258','0');
INSERT INTO [t1153] VALUES (376,'مسألة: ويجب أن ينوي الصلاة بعينها إن كانت معينة وإلا أجزأته نية الصلاة','526','258','0');
INSERT INTO [t1153] VALUES (377,'مسألة: ويأتي بالنية عند تكبيرة الإحرام','527','258','0');
INSERT INTO [t1153] VALUES (378,'مسألة: فإن تقدمت قبل ذلك بزمن يسير جاز','527','258','0');
INSERT INTO [t1153] VALUES (379,'مسألة: ويجب أن يستصحب حكمها إلى آخر الصلاة','527','258','0');
INSERT INTO [t1153] VALUES (380,'مسألة: فإن قطعها في أثنائها بطلت الصلاة وإن تردد في قطعها فعلى وجهين','527','258','0');
INSERT INTO [t1153] VALUES (381,'مسألة: وإن أحرم بفرض فبان قبل وقته إنقلب نفلا','528','258','0');
INSERT INTO [t1153] VALUES (382,'مسألة: وإن أحرم به في وقته ثم قلبه نفلا جاز، ويحتمل أن لا يجوز إلا لعذر مثل أن يحرم منفردا يريد الصلاة في جماعة','528','258','0');
INSERT INTO [t1153] VALUES (383,'مسألة: وإن إنتقل من فرض إلى بطلت الصلاتان','529','258','0');
INSERT INTO [t1153] VALUES (384,'مسألة: وإن نوى الإمامة صح في النفل ولم يصح في الفرض ويحتمل أن يصح وهو أصح عندي','529','258','0');
INSERT INTO [t1153] VALUES (385,'مسألة: فإن أحرم منفردا ثم نوى الإئتمام لم يصح في أصح الروايتين','529','258','0');
INSERT INTO [t1153] VALUES (386,'مسألة: ومن شرط الجماعة أن ينوي الإمام والمأموم حالهما','529','258','0');
INSERT INTO [t1153] VALUES (387,'مسألة: وإن أحرم مأموما ثم نوى الإنفراد لعذر جاز','530','258','0');
INSERT INTO [t1153] VALUES (388,'مسألة: وإن كان لغير عذر لم يجز في إحدى الروايتين','530','258','0');
INSERT INTO [t1153] VALUES (389,'مسألة: وإن نوى الإمامة لإستخلاف الإمام له إذا سبقه الحدث صح في ظاهر المذهب','530','258','0');
INSERT INTO [t1153] VALUES (390,'مسألة: وإن سبق إثنان ببعض الصلاة فأتم أحدهما بصاحبه في قضاء ما فاتهما فعلى وجهين أحدهما','532','258','0');
INSERT INTO [t1153] VALUES (391,'مسألة: وإن كان لغير عذر لم يصح','532','258','0');
INSERT INTO [t1153] VALUES (392,'مسألة: وإن أحرم إماما لغيية إمام الحي ثم حضر إمام الحي في اثناء الصلاة فأحرم بهم وبني على صلاة خليفته وصار الإمام مأموما فهل يصح على وجهين','532','258','0');
INSERT INTO [t1153] VALUES (393,'فصول في أدب المشي إلى الصلاة','533','258','0');
INSERT INTO [t1153] VALUES (394,'باب صفة الصلاة','535','258','0');
INSERT INTO [t1153] VALUES (395,'مسألة: يستحب أن يقوم إلى الصلاة إذا قال المؤذن قد قامت الصلاة','536','258','0');
INSERT INTO [t1153] VALUES (396,'مسألة: ثم يسوي الإمام الصفوف','537','258','0');
INSERT INTO [t1153] VALUES (397,'مسألة: ويقول الله أكبر لا يجزئه غيرها','538','258','0');
INSERT INTO [t1153] VALUES (398,'مسألة: فإن لم يحسنها لزمه تعلمها فإن خشي فوات الوقت كبر بلغته','540','258','0');
INSERT INTO [t1153] VALUES (399,'مسألة: ويجهر الإمام بالتكبير كله ليسمع المأمومون','541','258','0');
INSERT INTO [t1153] VALUES (400,'مسألة: ويسر غيره به وبالقراءة بقدر ما يسمع نفسه','541','258','0');
INSERT INTO [t1153] VALUES (401,'مسألة: ثم يرفع يديه مع ابتداء التكبير ممدودة الأصابع مضموما بعضها إلى بعض إلى حذو منكبيه أو إلى فروع أذنيه','544','258','0');
INSERT INTO [t1153] VALUES (402,'مسألة: ثم يضع كف يده اليمنى على كوع اليسرى ويجعلهما تحت سرته','547','258','0');
INSERT INTO [t1153] VALUES (403,'مسألة: ثم يقول سبحانك اللهم وبحمدك وتبارك اسمك وتعالى جدك ولا إله غيرك','548','258','0');
INSERT INTO [t1153] VALUES (404,'مسألة: وينظر إلى موضع سجوده','548','258','0');
INSERT INTO [t1153] VALUES (405,'مسألة: ثم يقول أعوذ بالله من الشيطان الرجيم','549','258','0');
INSERT INTO [t1153] VALUES (406,'مسألة: ثم يقرأ بسم الله الرحمن الرحيم','550','258','0');
INSERT INTO [t1153] VALUES (407,'مسألة: وليست الفاتحة وعنه أنها منها ولا يجهر بشيء من ذلك','551','258','0');
INSERT INTO [t1153] VALUES (408,'مسألة: ثم يقرأ الفاتحة وفيها إحدى عشرة تشديدة','554','258','0');
INSERT INTO [t1153] VALUES (409,'مسألة: فإن ترك ترتيبها أو تشديدة منها أو قطعها بذكر كثير أو سكوت طويل لزمه استئنافها','560','258','0');
INSERT INTO [t1153] VALUES (410,'مسألة: فإذا قال ولا الضالين قال آمين','562','258','0');
INSERT INTO [t1153] VALUES (411,'مسألة: يجهر بها الإمام والمأموم في صلاة الجهر','563','258','0');
INSERT INTO [t1153] VALUES (412,'مسألة: فإن لم يحسن الفاتحة وضاق الوقت عن تعلمها قرأ قدرها في عدد الحروف وقيل في عدد الآيات من غيرها فإن لم يحسن إلا آية كررها بقدرها','564','258','0');
INSERT INTO [t1153] VALUES (413,'مسألة: فإن لم يحسن إلا بعض ذلك كرره بقدرها','565','258','0');
INSERT INTO [t1153] VALUES (414,'مسألة: فإن لم يحسن شيئا من القرآن لم يجز أن يترجم عنه بلغة أخرى ولزمه أن يقول: سبحان الله والحمد لله ولا إله إلا الله والله أكبر ولا حول ولا قوة إلا بالله','565','258','0');
INSERT INTO [t1153] VALUES (415,'مسألة: فإن لم يحسن شيئا من الذكر وقف بقدر القراءة','566','258','0');
INSERT INTO [t1153] VALUES (416,'مسألة: ثم يقرأ بعد الفاتحة سورة تكون في الصبح من طوال المفصل، وفي المغرب من قصاره وفي الباقي من أوساطه','566','258','0');
INSERT INTO [t1153] VALUES (417,'مسألة: ويجهر الإمام بالقراءة في الصبح والأوليين من المغرب والعشاء','567','258','0');
INSERT INTO [t1153] VALUES (418,'مسألة: وإن قرأ بقراءة تخرج عن مصحف عثمان لا تصح صلاته وعنه تصح','568','258','0');
INSERT INTO [t1153] VALUES (419,'مسألة: ثم يرفع يديه ويركع مكبرا فيضع يديه على ركبتيه ويمد ظهره مستويا ويجعل رأسه حيال ظهره لا يرفعه ولا يخفضه','572','258','0');
INSERT INTO [t1153] VALUES (420,'مسألة: وقد الإجزاء الانحناء بحيث يمكنه مس ركبتيه بيديه لأنه لا يخرج عن حد القيام إلى الركوع إلا به..','574','258','0');
INSERT INTO [t1153] VALUES (421,'مسألة: ثم يقول سبحان ربي العظيم ثلاثا وهو أدنى الكمال','576','258','0');
INSERT INTO [t1153] VALUES (422,'مسألة: ثم يرفع رأسه قائلا سمع الله لمن حمده ويرفع يديه','579','258','0');
INSERT INTO [t1153] VALUES (423,'مسألة: فإذا اعتدل قائما قال ربنا ولك الحمد ملء السماوات وملء الأرض وملء شئت من شيء بعد','581','258','0');
INSERT INTO [t1153] VALUES (424,'مسألة: فإن كان مأموما لم يزد على ربنا ولك الحمد','583','258','0');
INSERT INTO [t1153] VALUES (425,'مسألة: ثم يكبر ويخر ساجدا ولا يرفع يديه','587','258','0');
INSERT INTO [t1153] VALUES (426,'مسألة: فيضع ركبتيه ثم يديه ثم جبهته وأنفه ويكون على أطراف أصابعه','588','258','0');
INSERT INTO [t1153] VALUES (427,'مسألة: والسجود على هذه الأعضاء واجب إلا الأنف على إحدى الروايتين','589','258','0');
INSERT INTO [t1153] VALUES (428,'مسألة: ولا تجب عليه مباشرة المصلي بشيء منها إلا الجبهة على إحدى الروايتين','591','258','0');
INSERT INTO [t1153] VALUES (429,'مسألة: ويجافي عضديه عن جنبيه، وبطنه عن فخذيه، ويضع يديه حذو منكبيه ويفرق بين ركبتيه','592','258','0');
INSERT INTO [t1153] VALUES (430,'مسألة: ويقول سبحان ربي الأعلى ثلاثا','594','258','0');
INSERT INTO [t1153] VALUES (431,'مسألة: ثم يرفع رأسه مكبرا','596','258','0');
INSERT INTO [t1153] VALUES (432,'مسألة: ويجلس مفترشا يفرش رجله اليسرى ويجلس عليها وينصب اليمنى ويقول رب اغفر لي ثلاثا','597','258','0');
INSERT INTO [t1153] VALUES (433,'مسألة: ثم يسجد الثانية كالأولى','599','258','0');
INSERT INTO [t1153] VALUES (434,'مسألة: ثم يرفع رأسه مكبرا ويقوم على صدور قدميه معتمدا على ركبتيه','601','258','0');
INSERT INTO [t1153] VALUES (435,'مسألة: إلا أن يشق عليه فيعتمد بالأرض','602','258','0');
INSERT INTO [t1153] VALUES (436,'مسألة: ثم ينهض ثم يصلي الثانية كذلك إلا في تكبيرة الإحرام والاستفتاح وفي الاستعاذة روايتان','603','258','0');
INSERT INTO [t1153] VALUES (437,'مسألة: وعنه أنه يجلس جلسة الاستراحة على قدميه واليتيه','603','258','0');
INSERT INTO [t1153] VALUES (438,'مسألة: ثم يجلس مفترشا ويضع يده اليمنى على فخذه اليمنى يقبض منها الخنصر والبنصر ويحلق بالإبهام مع الوسطى ويشير بالسبابة في تشهده مرارا ويبسط اليسرى على فخذه اليسرى','605','258','0');
INSERT INTO [t1153] VALUES (439,'مسألة: ثم يتشهد فيقول: التحيات لله والصلوات والطيبات السلام عليك أيها النبي ورحمة الله وبركاته السلام علينا وعلى عباد الله الصالحين أشهد أن لا إله إلا الله وأشهد أن محمدا عبده ورسوله','607','258','0');
INSERT INTO [t1153] VALUES (440,'مسألة: هذا التشهد الأول فلا يستحب الزيادة على ما ذكرنا ولا تطويله','609','258','0');
INSERT INTO [t1153] VALUES (441,'مسألة: ثم يقول اللهم صل على محمد وعلى آل محمد كما صليت على آل إبراهيم إنك حميد مجيد وبارك على محمد وعلى آل محمد كما باركت على آل إبراهيم إنك حميد مجيد وإن شاء قال: كما صليت على إبراهيم وآل إبراهيم وكما باركت على إبراهيم وعلى آل إبراهيم','611','258','0');
INSERT INTO [t1153] VALUES (442,'مسألة: ويستحب أن يتعوذ فيقول أعوذ بالله من عذاب جهنم ومن عذاب القبر ومن فتنة المحيا والممات، ومن فتنة المسيح الدجال','616','258','0');
INSERT INTO [t1153] VALUES (443,'مسألة: وإن دعا بما ورد في الأخبار فلا بأس','617','258','0');
INSERT INTO [t1153] VALUES (444,'مسألة: ثم يسلم عن يمينه السلام عليكم ورحمة الله وعن يساره كذلك','621','258','0');
INSERT INTO [t1153] VALUES (445,'مسألة: وينوي بسلامه الخروج من الصلاة فإن لم ينو جاز وقال ابن حامد تبطل صلاته','627','258','0');
INSERT INTO [t1153] VALUES (446,'مسألة: وإن كانت الصلاة مغربا أو رباعية نهض مكبرا إذا فرغ من التشهد الأول','630','258','0');
INSERT INTO [t1153] VALUES (447,'مسألة: ثم يجلس في التشهد الثاني متوركا يفرش رجله اليسرى وينصب اليمنى ويخرجهما عن يمينه ويجعل اليتيه على الأرض','631','258','0');
INSERT INTO [t1153] VALUES (448,'مسألة: والمرأة كالرجل في ذلك كله إلا أنها تجمع نفسها في الركوع والسجود وتجلس متربعة أو تسدل رجليها فتجعلهما في جانب يمينها وهل يسن لها رفع اليدين على روايتين؟','633','258','0');
INSERT INTO [t1153] VALUES (449,'مسألة: ويكره رفع بصره إلى السماء','635','258','0');
INSERT INTO [t1153] VALUES (450,'مسألة: وافتراش الذراعين في السجود','636','258','0');
INSERT INTO [t1153] VALUES (451,'مسألة: ويكره الإقعاء في الجلوس وهو أن يفرش قدميه على عقبيه وعنه أنه سنة','636','258','0');
INSERT INTO [t1153] VALUES (452,'مسألة: ويكره أن يصلي وهو حاقن','637','258','0');
INSERT INTO [t1153] VALUES (453,'مسألة: أو بحضرة طعام تتوق إليه نفسه','637','258','0');
INSERT INTO [t1153] VALUES (454,'مسألة: ويكره العبث في الصلاة','638','258','0');
INSERT INTO [t1153] VALUES (455,'مسألة: والتروح وفرقعة الأصابع وتشبيكها','638','258','0');
INSERT INTO [t1153] VALUES (456,'مسألة: وله رد المار بين يديه','641','258','0');
INSERT INTO [t1153] VALUES (457,'مسألة: وله عد الآي والتسبيح','643','258','0');
INSERT INTO [t1153] VALUES (458,'مسألة: وله قتل الحية والعقرب والقملة ولبس الثوب والعمامة ما لم يطل','643','258','0');
INSERT INTO [t1153] VALUES (459,'مسألة: وإن طال الفعل في الصلاة أبطلها عمده وسهوه إلا أن يفعله متفرقا','645','258','0');
INSERT INTO [t1153] VALUES (460,'مسألة: ويكره تكرار الفاتحة','646','258','0');
INSERT INTO [t1153] VALUES (461,'مسألة: ويكره الجمع بين سور في الفرض ولا يكره في النفل','646','258','0');
INSERT INTO [t1153] VALUES (462,'مسألة: ولا يكره قراءة أواخر السور وأوساطها وعنه يكره','648','258','0');
INSERT INTO [t1153] VALUES (463,'مسألة: وله أن يفتح على الإمام إذا ارتج عليه في الصلاة وأن يرد عليه إذا غلط','649','258','0');
INSERT INTO [t1153] VALUES (464,'مسألة: وإذا نابه شيء مثل سهو إمامه أو استئذان إنسان عليه سبح إن كان رجلا وإن كانت امرأة صفحت ببطن كفها على الأخرى','651','258','0');
INSERT INTO [t1153] VALUES (465,'مسألة: ويستحب أن يصلي إلى سترة مثل آخرة الرحل','656','258','0');
INSERT INTO [t1153] VALUES (466,'مسألة: وإن بدره البصاق بصق في ثوبه، وإن كان في غير المسجد جاز أن يبصق عن يساره أو تحت قدمه','656','258','0');
INSERT INTO [t1153] VALUES (467,'مسألة: فإن لم يجد خط خطا وصلى إليه وقام ذلك مقام السترة','659','258','0');
INSERT INTO [t1153] VALUES (468,'مسألة: فإن مر من ورائها شيء لم يكره حتى لو صلى إلى سترة فمر من ورائها ما يقطع الصلاة لم تنقطع وإن مر غير ذلك لم يكره','664','258','0');
INSERT INTO [t1153] VALUES (469,'مسألة: وإن لم يكن سترة فمر بين يديه الكلب الأسود البهيم بطلت صلاته وفي المرأة والحمار روايتان','664','258','0');
INSERT INTO [t1153] VALUES (470,'مسألة: ويجوز له النظر في المصحف','673','258','0');
INSERT INTO [t1153] VALUES (471,'مسألة: وإذا مرت به آية رحمة أن يسألها أو آية عذاب أن يستعيذ منها، وعنه يكره ذلك في الفرض','674','258','0');
INSERT INTO [t1153] VALUES (472,'مسألة: وواجباتها تسعة: التكبير غير تكبيرة الإحرام والتسبيح في الركوع والسجود مرة مرة والتسميع والتحميد في الرفع من الركوع وسؤال المغفرة بين السجدتين مرة، والتشهد الأول والجلوس له والصلاة على النبي صلى الله عليه وسلم في التشهد الأخير في رواية','679','258','0');
INSERT INTO [t1153] VALUES (473,'مسألة: ومن ترك منها شيئا عمدا بطلت صلاته ومن تركه سهوا ترك للسهو وعنه أن هذه سنن لا تبطل الصلاة بتركها','683','258','0');
INSERT INTO [t1153] VALUES (474,'مسألة: وسنن الأقوال اثنا عشر الاستفتاح والتعوذ وقراءة بسم الله الرحمن الرحيم وقول آمين وقراءة السورة والجهر والإخفات وقول ملء السماوات بعد التحميد وما زاد على التسبيحة الواحدة في الركوع والسجود وعلى المرة في سؤال المغفرة والتعوذ في التشهد الأخير والقن','685','258','0');
INSERT INTO [t1153] VALUES (475,'مسألة: وما سوى هذا من سنن الأفعال لا تبطل الصلاة بتركها ولا يشرع السجود لها','689','258','0');
INSERT INTO [t1153] VALUES (476,'مسألة: ولا يشرع في العمد وهو قول أبي حنيفة','698','258','0');
INSERT INTO [t1153] VALUES (477,'مسألة: ويشرع السهو في زيادة ونقص وشك لأن الشرع إنما ورد به في ذلك','698','258','0');
INSERT INTO [t1153] VALUES (478,'مسألة: للنافلة والفرض','698','258','0');
INSERT INTO [t1153] VALUES (479,'باب سجود السهو','698','258','0');
INSERT INTO [t1153] VALUES (480,'مسألة: فمتى زاد فعلا من جنس الصلاة قياما أو قعودا أو ركوعا أو سجودا عمدا بطلت الصلاة وإن كان سهوا سجد له','699','258','0');
INSERT INTO [t1153] VALUES (481,'مسألة: فإن زاد ركعة فلم يعلم حتى فرغ منها سجد لها','699','258','0');
INSERT INTO [t1153] VALUES (482,'مسألة: وإن علم فيها جلس في الحال فتشهد إن لم يكن تشهد وسجد وسلم','700','258','0');
INSERT INTO [t1153] VALUES (483,'مسألة: وإن سبح به اثنان لزمه الرجوع','701','258','0');
INSERT INTO [t1153] VALUES (484,'مسألة: فإن لم يرجع بطلت صلاته وصلاة من اتبعه عالما، وإن فارقه أو كان جاهلا لم تبطل','702','258','0');
INSERT INTO [t1153] VALUES (485,'مسألة: والعمل المستكثر في العادة من غير جنس الصلاة يبطلها وسهوه ولا تبطل باليسير ولا يشرع له سجود','703','258','0');
INSERT INTO [t1153] VALUES (486,'مسألة: وإن أكل أو شرب عمدا بطلت صلاته قل أو كثر وإن كان سهوا لم تبطل إذا كان يسيرا','704','258','0');
INSERT INTO [t1153] VALUES (487,'مسألة: وإن أتى بقول مشروع في غير موضعه كالقراءة في السجود والقعود والتشهد في القيام وقراءة السورة في الأخريين لم تبطل صلاته بعمده','705','258','0');
INSERT INTO [t1153] VALUES (488,'مسألة: وإن سلم قبل إتمام صلاته عمدا أبطلها لأنه تكلم فيها عمدا، وإن كان سهوا ثم ذكر قريبا أتمها وسجد، وإن طال الفصل أو تكلم لغير مصلحة بطلت','705','258','0');
INSERT INTO [t1153] VALUES (489,'مسألة: وإن تكلم لمصلحتها ففيه ثلاث روايات إحداها لا تبطل والثانية تبطل والثالثة تبطل صلاة المأموم دون الإمام اختارها الخرقي','708','258','0');
INSERT INTO [t1153] VALUES (490,'مسألة: وإن تكلم في صلب الصلاة بطلت وعنه لا تبطل إذا كان ساهيا أو جاهلا ويسجد له','709','258','0');
INSERT INTO [t1153] VALUES (491,'مسألة: وإن قهقه أو نفخ أو انتحب فبان حرفان فهو كالكلام إلا ما كان من خشية الله تعالى وقال أصحابنا في النحنحة مثل ذلك، وقد روي عن أبي عبد الله أنه كان يتنحنح في الصلاة ولا يراها مبطلة للصلاة','714','258','0');
INSERT INTO [t1153] VALUES (492,'مسألة: وإن نسي أربع سجدات من أربع ركعات وذكر وهو في التشهد سجد سجدة فصحت له ركعة ويأتي بثلاث وعنه تبطل صلاته','720','258','0');
INSERT INTO [t1153] VALUES (493,'مسألة: وإن نسي التشهد الأول ونهض لزمه الرجوع ما لم ينتصب قائما فإن استتم قائما لم يرجع وإن رجع جاز وإن شرع في القراءة لم يجز له الرجوع وعليه السجود لذلك كله','722','258','0');
INSERT INTO [t1153] VALUES (494,'مسألة: فإن استوى الأمران عنده بنى على اليقين','727','258','0');
INSERT INTO [t1153] VALUES (495,'مسألة: ومن شك في ترك ركن فهو كتركه','727','258','0');
INSERT INTO [t1153] VALUES (496,'مسألة: وليس على المأموم سجود سهو إلا أن يسهو إمامه فيسجد','728','258','0');
INSERT INTO [t1153] VALUES (497,'مسألة: فإن لم يسجد الإمام فهل يسجد المأموم؟ على روايتين','729','258','0');
INSERT INTO [t1153] VALUES (498,'مسألة: ومحله قبل السلام لا في السلام قبل إتمام صلاته وفيما إذا بنى الإمام على غالب ظنه، وعنه أن الجميع قبل السلام وعنه ما كان من زيادة فهو بعد السلام وما كان من نقص كان قبله','731','258','0');
INSERT INTO [t1153] VALUES (499,'مسألة: وإن نسيه قبل السلام قضاه ما لم يطل الفصل أو يخرج من المسجد وعنه أنه يسجد وإن بعد','733','258','0');
INSERT INTO [t1153] VALUES (500,'مسألة: ويكفي لجميع السهو سجدتان إلا أن يختلف محلهما ففيه وجهان','734','258','0');
INSERT INTO [t1153] VALUES (501,'مسألة: ومتى سجد بعد السلام جلس فتشهد ثم سلم','737','258','0');
INSERT INTO [t1153] VALUES (502,'مسألة: وإن ترك السجود الواجب قبل السلام عمدا بطلت صلاته','738','258','0');
INSERT INTO [t1153] VALUES (503,'مسألة: وآكدها صلاة الكسوف والاستسقاء','739','258','0');
INSERT INTO [t1153] VALUES (504,'باب صلاة التطوع: مسألة: وهي أفضل تطوع البدن','739','258','0');
INSERT INTO [t1153] VALUES (505,'مسألة: ثم الوتر وليس بواجب ووقته ما بين صلاة العشاء وطلوع الفجر وأقله ركعة وأكثره إحدى عشرة ركعة يسلم من كل ركعتين ويوتر بركعة','740','258','0');
INSERT INTO [t1153] VALUES (506,'مسألة: وإن أوتر بتسع ثمانيا وجلس ولم يسلم ثم صلى التاسعة وتشهد وسلم وكذلك السبع وإن أوتر بخمس لم يجلس إلا في آخرهن','748','258','0');
INSERT INTO [t1153] VALUES (507,'مسألة: وأدنى الكمال ثلاث ركعات بتسليمتين','750','258','0');
INSERT INTO [t1153] VALUES (508,'مسألة: يقرأ في الأولى بسبح وفي الثانية بقل يا أيها الكافرون وفي الثالثة بقل هو الله أحد','752','258','0');
INSERT INTO [t1153] VALUES (509,'مسألة: ثم يقنت فيها بعد الركوع','753','258','0');
INSERT INTO [t1153] VALUES (510,'مسألة: وهل يمسح وجهه بيديه؟ على روايتين','758','258','0');
INSERT INTO [t1153] VALUES (511,'مسألة: ولا يقنت في غير الوتر','758','258','0');
INSERT INTO [t1153] VALUES (512,'مسألة: إلا أن ينزل بالمسلمين نازلة فللإمام خاصة القنوت في صلاة الفجر','760','258','0');
INSERT INTO [t1153] VALUES (513,'مسألة: ثم السنن الراتبة وهي عشرة ركعات ركعتان قبل الظهر وركعتان بعدها وركعتان بعد المغرب وركعتان بعد العشاء وركعتان قبل الفجر وهما آكد قال أبو الخطاب وأربع قبل العصر','763','258','0');
INSERT INTO [t1153] VALUES (514,'مسألة: ومن فاته شيء من هذه السنن سن له قضاؤه','768','258','0');
INSERT INTO [t1153] VALUES (515,'مسألة: ثم التروايح وهي عشرون ركعة يقوم بها في رمضان في جماعة ويوتر بعدها في الجماعة','779','258','0');
INSERT INTO [t1153] VALUES (516,'مسألة: فإن أحب متابعة الإمام فأوتر معه قام إذا سلم الإمام فيشفعها بأخرى','785','258','0');
INSERT INTO [t1153] VALUES (517,'مسألة: ويكره التطوع بين التراويح وفي التعقيب روايتان وهو أن يتطوع بعد التراويح والوتر في جماعة','788','258','0');
INSERT INTO [t1153] VALUES (518,'مسألة: وصلاة الليل أفضل من صلاة النهار','795','258','0');
INSERT INTO [t1153] VALUES (519,'مسألة: وصلاة الليل مثنى مثنى فإن تطوع في النهار بأربع فلا بأس والأفضل مثنى','802','258','0');
INSERT INTO [t1153] VALUES (520,'باب صلاة الجماعة، مسألة: وهي واجبة للصلوات الخمس على الرجال لا شرطا','807','258','0');
INSERT INTO [t1153] VALUES (521,'مسألة: وله فعلها في بيته في أصح الروايتين','809','258','0');
INSERT INTO [t1153] VALUES (522,'مسألة: والافضل لغيرهم الصلاة في المسجد الذي لا تقام فيه الجماعة إلا بحضوره','809','258','0');
INSERT INTO [t1153] VALUES (523,'مسألة: فان لم يعلم عذره انتظر وروسل','810','258','0');
INSERT INTO [t1153] VALUES (524,'مسألة: ولا يؤم في مسجد قبل إمامه الراتب إلا بإذنه','810','258','0');
INSERT INTO [t1153] VALUES (525,'مسألة: وهل الأولى قصد الأبعد أو الأقرب','810','258','0');
INSERT INTO [t1153] VALUES (526,'مسألة: ثم ما كان أكثر جماعة ثم في المسجد العتيق','810','258','0');
INSERT INTO [t1153] VALUES (527,'مسألة: فان صلى ثم أقيمت الصلاة وهو في المسجد استحب له إعادتها إلا المغرب فإنه يعيدها ويشفعها برابعة','810','258','0');
INSERT INTO [t1153] VALUES (528,'مسألة: ولا تكره إعادة الجماعة في غير المساجد الثلاثة','812','258','0');
INSERT INTO [t1153] VALUES (529,'مسألة: واذا أقيمت الصلاة فلا صلاة إلا المكتوبة','813','258','0');
INSERT INTO [t1153] VALUES (530,'مسألة: وإن أقيمت وهو في نافلة أتمها خفيفة','813','258','0');
INSERT INTO [t1153] VALUES (531,'مسألة: قال: ومن أدرك الركوع فقد أدرك الركعة','813','258','0');
INSERT INTO [t1153] VALUES (532,'مسألة: وأجزأته تكبيرة واحدة والأفضل اثنتان','814','258','0');
INSERT INTO [t1153] VALUES (533,'مسألة: وما أدرك مع الامام فهو آخر صلاته وما يقضيه فهو أولها يستفتح له ويتعوذ ويقرأ السورة','815','258','0');
INSERT INTO [t1153] VALUES (534,'مسألة: ولا تجب القراءة على المأموم','816','258','0');
INSERT INTO [t1153] VALUES (535,'مسألة: ويستحب أن يقرأ في سكتات الامام ومالا يجهر فيه أو لا يسمعه لبعده فان لم يسمعه لطرش فعلى وجهين','817','258','0');
INSERT INTO [t1153] VALUES (536,'مسألة: ومن ركع أو سجد قبل إمامه فعليه أ، يرفع ليأتي به بعده فإن لم يفعل عمدا بطلت صلاته عند أصحابنا إلا القاضي','818','258','0');
INSERT INTO [t1153] VALUES (537,'مسألة: وهل يستفتح ويستعيذ فيما يجهر فيه الامام؟ على روايتين','818','258','0');
INSERT INTO [t1153] VALUES (538,'مسألة: فإن ركع ورفع قبل ركوع إمامه عالما عمدا فهل تبطل صلاته؟ على وجهين','819','258','0');
INSERT INTO [t1153] VALUES (539,'مسألة: ويستحب للامام تخفيف الصلاة مع إتمامها','820','258','0');
INSERT INTO [t1153] VALUES (540,'مسألأة: ويستحب تطويل الركعة الاولى أطول من الثانية','820','258','0');
INSERT INTO [t1153] VALUES (541,'مسألة: ولا يستحب انتظار داخل وهو في الركوع في إحدى الروايتين','821','258','0');
INSERT INTO [t1153] VALUES (542,'مسألة: وإذا استأذنت المرأة إلى المسجد كره منعها وبيتها خير لها','821','258','0');
INSERT INTO [t1153] VALUES (543,'مسألة: ثم أفقههم ثم أسنهم ثم أقدمهم هجرة ثم أشرفهم ثم أتقاهم ثم من تقع له القرعة متى استووا في القراءة','823','258','0');
INSERT INTO [t1153] VALUES (544,'مسألة: وصاحب البيت وإمام المسجد أحق بالإمامة الا أن يكون بعضهم ذا سلطان','825','258','0');
INSERT INTO [t1153] VALUES (545,'مسألة: والحر أولى من العبد والحاضر أ, لى من المسافر والبصير أولى من الأعمى في أحد الوجهين','827','258','0');
INSERT INTO [t1153] VALUES (546,'مسألة: وهل تصح إماة الفاسق والاقلف؟ على روايتين','828','258','0');
INSERT INTO [t1153] VALUES (547,'مسألة: وفي إمامة أقطع اليدين وجهان','835','258','0');
INSERT INTO [t1153] VALUES (548,'مسألة: لا تصح الصلاة خلف كافر بحال','837','258','0');
INSERT INTO [t1153] VALUES (549,'مسألة: ولا تصح إمامة من به سلس البول ولا عاجز عن الركوع والسجود والقعود','844','258','0');
INSERT INTO [t1153] VALUES (550,'مسألة: ولا تصح خلف عاجز عن القيام إلا إمام الحي المرجو زوال علته','848','258','0');
INSERT INTO [t1153] VALUES (551,'مسألة: فإن صلوا قياما صحت صلاتهم في أحد الوجهين','854','258','0');
INSERT INTO [t1153] VALUES (552,'مسألة: فان ابتدأ بهم الصلاة قائما ثم اعتل فجلس أتموا خلفه قياما','855','258','0');
INSERT INTO [t1153] VALUES (553,'مسألة: ولا تصح إمامة المرأة والخنثى للرجال وللخناثى','856','258','0');
INSERT INTO [t1153] VALUES (554,'مسألة: ولا إمامة الصبي لبالغ إلا في نفل على إحدى الروايتين','858','258','0');
INSERT INTO [t1153] VALUES (555,'مسألة: ولا تصح إمامة محدث ولا نجس يعلم ذلك','859','258','0');
INSERT INTO [t1153] VALUES (556,'مسألة: ولا تصح إمامة الأمي وهو من لا يحسن الفاتحة أو يدغم حرفا أو يلحن لحنا يحيل المعنى إلا بمثله','861','258','0');
INSERT INTO [t1153] VALUES (557,'مسألة: ويكره أن يؤم نساء أجانب لا رجل معهن','862','258','0');
INSERT INTO [t1153] VALUES (558,'مسألة: ويكره أن يؤم قوما أكثرهم له كارهون','862','258','0');
INSERT INTO [t1153] VALUES (559,'مسألة: وتكره إمامة اللحان والفأفاء الذي يكرر الفاء والتمتام الذي يكرر القاف ومن لا يفصح ببعض الحروف','862','258','0');
INSERT INTO [t1153] VALUES (560,'مسألة: ولا بأس بإمامة ولد الزنا والجندي اذا سلم دينهما','863','258','0');
INSERT INTO [t1153] VALUES (561,'مسألة: ويصح ائتمام مؤدي الصلاة بمن يقضيها','863','258','0');
INSERT INTO [t1153] VALUES (562,'مسألة: فإن وقفوا قدامه لم يصح','867','258','0');
INSERT INTO [t1153] VALUES (563,'مسألة: وإن كان واحد وقف عن يمينه رجلا كان أو غلاما','868','258','0');
INSERT INTO [t1153] VALUES (564,'مسألة: وإن وقف خلفه أو عن يساره لم تصح','868','258','0');
INSERT INTO [t1153] VALUES (565,'مسألة: وإن أم امرأة وقفت خلفه','870','258','0');
INSERT INTO [t1153] VALUES (566,'مسألة: وإن اجتمع رجال وصبيان وخناثى ونساء تقدم الرجال ثم الصبيان ثم الخناثى ثم النساء','871','258','0');
INSERT INTO [t1153] VALUES (567,'مسألة: ومن لم يقف معه إلا كافر أو امرأة أو محدث يعلم حدثه فهو فذ، وكذلك الصبي إلا في النافلة','871','258','0');
INSERT INTO [t1153] VALUES (568,'مسألة: ومن جاء فوجد فرجة وقف فيها فان لم يجد وقف عن يمين الامام ولم يجذب رجلا ليقوم معه فإن لم يمكنه ذلك نبه رجلا ليقوم معه فخرج فوقف معه','875','258','0');
INSERT INTO [t1153] VALUES (569,'مسألة: فان صلى فذا ركعة لم تصح','875','258','0');
INSERT INTO [t1153] VALUES (570,'مسألة: وإن ركع فذا ثم دخل في الصف أو وقف معه آخر قبل رفع الامام صحت صلاته وإن رفع ولم يسجد صحت، وقيل إن علم النهي لم تصح وإن فعله لغير عذر لم تصح','875','258','0');
INSERT INTO [t1153] VALUES (571,'مسألة: واذا كان المأموم يرى من وراء الامام صحت صلاته اذا اتصلت الصفوف، وان لم ير من وراءه لم تصح وعنه تصح اذا كان في المسجد','879','258','0');
INSERT INTO [t1153] VALUES (572,'مسألة: ولا يكون الامام أعلى من المأموم، فإن فعل وكان كثيرا فهل تصح صلاته؟ وجهين','882','258','0');
INSERT INTO [t1153] VALUES (573,'مسألة: ويكره للإمام إطالة القعود بعد الصلاة مستقبل القبلة','884','258','0');
INSERT INTO [t1153] VALUES (574,'مسألة: ويكره للإمام أن يصلي في طاق القبلة','884','258','0');
INSERT INTO [t1153] VALUES (575,'مسألة: ويكره للإمام أن يتطوع في موضع المكتوبة','884','258','0');
INSERT INTO [t1153] VALUES (576,'مسألة: ويكره للمأمومين الوقوف بين السواري إذا قطعت صفوفهم','884','258','0');
INSERT INTO [t1153] VALUES (577,'مسألة: فإن كان معه نساء لبث قليلا لينصرف النساء','885','258','0');
INSERT INTO [t1153] VALUES (578,'مسألة: فان أمت امرأة بنساء قامت وسطهن في الصف','886','258','0');
INSERT INTO [t1153] VALUES (579,'مسألة: ومن يدافع أحد الاخبثين أو بحضرة طعام وهو محتاج اليه','887','258','0');
INSERT INTO [t1153] VALUES (580,'مسألة: والخائف من ضياع ماله، أو فواته، أو ضرر فيه على نفسه من ضرر أو سلطان أو ملازمة غريم ولا شيء معه','888','258','0');
INSERT INTO [t1153] VALUES (581,'مسألة: أو فوات رفقة، أو غلبة النعاس، أو خشية التأذي بالمطر، والوحل، والريح الشديدة في الليلة المظلمة الباردة','889','258','0');
INSERT INTO [t1153] VALUES (582,'باب صلاة أهل الاعذار','891','258','0');
INSERT INTO [t1153] VALUES (583,'مسألة: فإن صلى على ظهره ورجلاه إلى القبلة صحت صلاته','892','258','0');
INSERT INTO [t1153] VALUES (584,'مسألة: ويومىء بالركوع والسجود ويجعل سجوده أخفض من ركوعه','892','258','0');
INSERT INTO [t1153] VALUES (585,'مسألة: وان قدر على القيام وعجز عن الركوع والسجود أومأ بالركوع قائما وبالسجود قاعدا','893','258','0');
INSERT INTO [t1153] VALUES (586,'مسألة: فان قدر على القيام أو القعود في أثنائها انتقل اليه وأتمها','893','258','0');
INSERT INTO [t1153] VALUES (587,'مسألة: فإن عجز عنه أومأ بطرفه ولا تسقط الصلاة','893','258','0');
INSERT INTO [t1153] VALUES (588,'مسألة: ولا تصح الصلاة في السفينة قاعدا لقادر على القيام','894','258','0');
INSERT INTO [t1153] VALUES (589,'مسألة: وإذا قال ثقات من العلماء بالطب للمريض إن صليت مستلقيا أمكن مداواتك فله ذلك','894','258','0');
INSERT INTO [t1153] VALUES (590,'مسألة: وهل يجوز ذلك لأجل المرض على روايتين','895','258','0');
INSERT INTO [t1153] VALUES (591,'مسألة: ومن سافر سفرا مباحا يبلغ ستة عشر فرسخا فله قصر الصلاة الرباعية خاصة إلى ركعتين','896','258','0');
INSERT INTO [t1153] VALUES (592,'مسألة: إذا جاوز بيوت قريته أو خيام قومه','901','258','0');
INSERT INTO [t1153] VALUES (593,'مسألة: وهو أفضل من الاتمام وإن أتم جاز','904','258','0');
INSERT INTO [t1153] VALUES (594,'مسألة: وإن أحرم في الحضر ثم سافر أوفى السفر ثم أقام أو ذكر صلاة حضر في سفر أو صلاة سفر في حضر أو ائتم بمقيم أو بمن يشك فيه أو أحرم بصلاة يلزمه اتمامها ففسدت وأعادها أو لم ينو القصر لزمه أن يتم، وقال أبو بكر: لا يحتاج الجمع والقصر الى نية','906','258','0');
INSERT INTO [t1153] VALUES (595,'مسألة: ومن له طريقان بعيد وقريب فسلك البعيد أو ذكر صلاة سفر في آخر فله القصر','911','258','0');
INSERT INTO [t1153] VALUES (596,'مسألة: واذا نوى الاقامة ببلد أكثر من إحدى وعشرين صلاة أتم وإلا قصر','912','258','0');
INSERT INTO [t1153] VALUES (597,'مسألة: وإن أقام لقضاء حاجة أو حبس ولم ينو الاقامة قصر أبدا','916','258','0');
INSERT INTO [t1153] VALUES (598,'مسألة: والملاح الذي معه أهله وليس له نية الاقامة ببلد ليس له الترخص','919','258','0');
INSERT INTO [t1153] VALUES (599,'فصل في الجمع','920','258','0');
INSERT INTO [t1153] VALUES (600,'مسألة: والمرض الذي يلحقه بترك الجمع فيه مشقة وضعف','921','258','0');
INSERT INTO [t1153] VALUES (601,'مسألة: والمطر الذي يبل الثياب','922','258','0');
INSERT INTO [t1153] VALUES (602,'مسألة: وهل يجوز ذلك لأجل الوحل والريح الشديدة الباردة أو لمن يصلي في بيته أو في مسجد طريقه تحت ساباط على وجهين','923','258','0');
INSERT INTO [t1153] VALUES (603,'مسألة: ويفعل الأرفق به من تأخير الاولى إلى وقت الثانية أو تقديم الثانية إليها','924','258','0');
INSERT INTO [t1153] VALUES (604,'مسألة: وللجمع في وقت الأولى ثلاثة شروط','927','258','0');
INSERT INTO [t1153] VALUES (605,'مسألة: وإن جمع في وقت الثانية كفاه نية الجمع في وقت الأولى ما لم يضق عن فعليها واستمرار العذر الى دخول وقت الثانية منهما','929','258','0');
INSERT INTO [t1153] VALUES (606,'مسألة: قال الإمام أبو عبد الله: صح عن النبي صلى الله عليه وسلم صلاة الخوف من خمسة أوجه أو ستة، وقال ستة أو سبعة كل ذلك جائز لمن فعله','932','258','0');
INSERT INTO [t1153] VALUES (607,'مسألة: وإن كانت الصلاة مغربا صلى بالطائفة الاولى ركعتين وبالثانية ركعة','937','258','0');
INSERT INTO [t1153] VALUES (608,'مسألة: وإن كانت رباعية غير مقصورة صلى بكل طائفة ركعتين وأتمت الاولى بالحمد لله في كل ركعة والأخرى تتم بالحمد لله وسورة','938','258','0');
INSERT INTO [t1153] VALUES (609,'مسألة: وهل تفارقه الاولى في التشهد الاول وفي الثالثة؟ على وجهين','939','258','0');
INSERT INTO [t1153] VALUES (610,'مسألة: وإن فرقهم أربعا فصلى بكل طائفة ركعة صحت صلاة الاوليين وبطلت صلاة الامام والاخريين ان علمنا بطلان صلاته','939','258','0');
INSERT INTO [t1153] VALUES (611,'مسألة: ويستحب أن يحمل معه في الصلاة من الصلاةح ما يدفع به عن نفسه ولا يثقله كالسيف والسكين ويحتمل أن يجب ذلك','942','258','0');
INSERT INTO [t1153] VALUES (612,'مسألة: ومن هرب من عدو هربا مباحا أو سيل أو سبع أو نحوه فله الصلاة كذلك سواء خاف على نفسه أو ماله أو أهله','944','258','0');
INSERT INTO [t1153] VALUES (613,'مسألة: وهل لطالب العدو الخائف فواته الصلاة كذلك','945','258','0');
INSERT INTO [t1153] VALUES (614,'مسألة: ومن أمن في الصلاة أتم صلاة آمن، وان ابتدأها آمنا ثم خاف أتم صلاة خائف','946','258','0');
INSERT INTO [t1153] VALUES (615,'باب صلاة الجمعة','947','258','0');
INSERT INTO [t1153] VALUES (616,'مسألة: وهي واجبة على كل مسلم مكلف ذكر حر مستوطن ببناء ليس بينه وبين موضع الجمعة أكثر من فرسخ اذا لم يكن له عذر','948','258','0');
INSERT INTO [t1153] VALUES (617,'مسألة: ولا تجب على مسافر ولا عبد ولا امرأة ولا خنثى','955','258','0');
INSERT INTO [t1153] VALUES (618,'مسألة: ومن حضرها منهم أجزأته ولم تنعقد به ولم يجز له أن يؤم فيها وعنه في العبد أنها تجب عليه','958','258','0');
INSERT INTO [t1153] VALUES (619,'مسألة: ومن سقطت عنه لعذر إذا حضرها وجبت عليه وانعقدت به','960','258','0');
INSERT INTO [t1153] VALUES (620,'مسألة: ومن صلى الظهر ممن عليه حضور الجمعة قبل صلاة الامام لم تصح صلاته والأفضل لمن لا تجب عليه أن لا يصلي الظهر حتى يصلي الإمام','960','258','0');
INSERT INTO [t1153] VALUES (621,'مسألة: ولا يجوز لمن تلزمه الجمعة السفر في يومها بعد الزوال','965','258','0');
INSERT INTO [t1153] VALUES (622,'مسألة: ويجوز قبله وعنه لا يجوز، وعنه يجوز للجهاد خاصة السفر بعد الزوال فيجوز للجهاد خاصة','966','258','0');
INSERT INTO [t1153] VALUES (623,'مسألة: فإن خرج وقتها قبل فعلها صلوا ظهرا لفوات الشرط لا نعلم في ذلك خلافا','971','258','0');
INSERT INTO [t1153] VALUES (624,'مسألة: وإن خرج وقد صلوا ركعة أتموها جمعة، وإن خرج قبل ركعة فهل يتمونها ظهرا أو يستأنفونها على وجهين','971','258','0');
INSERT INTO [t1153] VALUES (625,'مسألة: الثاني أن تكون بقرية يستوطنها أربعون من أهل وجوبها فلا تجوز اقامتها في غير ذلك','974','258','0');
INSERT INTO [t1153] VALUES (626,'مسألة: ويجوز إقامتها في الأبنية المتفرقة إذا شملها اسم واحد','975','258','0');
INSERT INTO [t1153] VALUES (627,'مسألة: الثالث: حضور أربعين من أهل القرية في ظهر المذهب وعنه تنعقد بثلاثة','978','258','0');
INSERT INTO [t1153] VALUES (628,'مسألة: فإن نقصوا قبل إتمامها استأنفوا ظهرا ويحتمل أنهم إن نقصوا بعد ركعة أتموها جمعة وإن نقصوا قبل ركعة أتموها ظهرا','980','258','0');
INSERT INTO [t1153] VALUES (629,'مسألة: ومن أدرك مع الإمام منها ركعة أتمها جمعة','981','258','0');
INSERT INTO [t1153] VALUES (630,'مسألة: ومن أدرك أقل من ركعة أتمها ظهرا إذا كان قد نوى الظهر في قول الخرقي. وقال أبو اسحق بن شاقلا: ينوي جمعة ويتمها ظهرا','981','258','0');
INSERT INTO [t1153] VALUES (631,'مسألة: فإن لم يمكنه سجد اذا زال الزحام إلا أن يخاف فوات الثانية فيتابع الامام فيها وتصير أولاه ويتمها جمع','983','258','0');
INSERT INTO [t1153] VALUES (632,'مسألة: فان لم يتابع الامام عالما بتحريم ذلك بطلت صلاته، وإن جهل تحريمه فسجد ثم أدرك الامام في التشهد أتى بركعة أخرى بعد سلام الامام وصحت جمعته وعنه يتمها ظهرا','984','258','0');
INSERT INTO [t1153] VALUES (633,'مسألأة: الرابع أن يتقدمها خطبتان من شرط صحتهما حمد الله والصلاة على رسوله صلى الله عليه وسلم وقراءة أية والوصية بتقوى الله تعالى','985','258','0');
INSERT INTO [t1153] VALUES (634,'مسألة: وهل يشترط لهما الطهارة وأن يتولاهما من يتولى الصلاة على روايتين','988','258','0');
INSERT INTO [t1153] VALUES (635,'مسألة: ومن سننهما أن يخطب على منبر أو موضع عال لأن النبي صلى الله عليه وسلم كان يخطب الناس على منبر','988','258','0');
INSERT INTO [t1153] VALUES (636,'مسألة: ويسلم على المأمومين إذا أقبل عليهم','989','258','0');
INSERT INTO [t1153] VALUES (637,'مسألة: ثم يجلس إلى فراغ الاذان ويجلس بين الخطبتين','989','258','0');
INSERT INTO [t1153] VALUES (638,'مسألة: ويخطب قائما','989','258','0');
INSERT INTO [t1153] VALUES (639,'مسألة: ويعتمد على سيف، أو قوس، أو عصا','990','258','0');
INSERT INTO [t1153] VALUES (640,'مسألة: ويقصد تلقاء وجهه','990','258','0');
INSERT INTO [t1153] VALUES (641,'مسألة: ويستحب تقصير الخطبة','991','258','0');
INSERT INTO [t1153] VALUES (642,'مسألة: ويستحب أن يدعو لنفسه والمسلمين والمسلمات والحاضرين، وإن دعا لسلطان المسلمين بالصلاح فحسن','991','258','0');
INSERT INTO [t1153] VALUES (643,'مسألة: ولا يشترط إذن الإمام وعنه يشترط','992','258','0');
INSERT INTO [t1153] VALUES (644,'مسألة: ويستحب أن يقرأ في الاولى بسورة الجمعة وفي الثانية بالمنافقين','993','258','0');
INSERT INTO [t1153] VALUES (645,'مسألة: وتجوز إقامة الجمعة في موضعين من البلد للحاجة ولا يجوز مع عدمها','994','258','0');
INSERT INTO [t1153] VALUES (646,'مسألة: فإن استويا فالثانية باطلة وإن لم يكن لاحداهما مزية على الأخرى لكونهما جميعا مأذونا فيهما أو غير مأذون','995','258','0');
INSERT INTO [t1153] VALUES (647,'مسألة: فإن فعلوا فجمعة الامام هي الصحيحة','995','258','0');
INSERT INTO [t1153] VALUES (648,'مسألة: فإن وقعتا معا أو جهلت الأولى بطلتا معا','996','258','0');
INSERT INTO [t1153] VALUES (649,'مسألة: وإذا وقع العيد يوم الجمعة فاجتزىء بالعيد عن الجمعة وصلوا ظهرا جاز إلا للإمام','997','258','0');
INSERT INTO [t1153] VALUES (650,'مسألة: وأقل السنة بعد الجمعة ركعتان وأكثرها ست ركعات','999','258','0');
INSERT INTO [t1153] VALUES (651,'مسألة: ويتنظف ويتطيب ويلبس أحسن ثيابه','1006','258','0');
INSERT INTO [t1153] VALUES (652,'مسألة: ويبكر إليها ماشيا ويدنو من الامام','1007','258','0');
INSERT INTO [t1153] VALUES (653,'مسألة: ويشتغل بالصلاة والذكر ويقرأ سورة الكهف في يومها ويكثر الدعاء والصلاة على النبي صلى الله عليه وسلم','1010','258','0');
INSERT INTO [t1153] VALUES (654,'مسألة: ولا يتخطى رقاب الناس إلا أن يكون إماما أو يرى فرجة فيتخطى إليها وعنه يكره','1014','258','0');
INSERT INTO [t1153] VALUES (655,'مسألة: ولا يقيم غيره فيجلس في مكانه إلا من قدم صاحبا له فجلس في موضع يحفظه له','1015','258','0');
INSERT INTO [t1153] VALUES (656,'مسألة: ومن قام من موضعه لعارض لحقه ثم عاد إليه فهو أحق به','1016','258','0');
INSERT INTO [t1153] VALUES (657,'مسألة: وان وجد مصلى مفروشة فهل له رفعها؟ على روايتين','1016','258','0');
INSERT INTO [t1153] VALUES (658,'مسألة: ومن دخل والإمام يخطب لم يجلس حتى يركع ركعتين يوجز فيهما','1018','258','0');
INSERT INTO [t1153] VALUES (659,'مسألة: ولا يجوز الكلام والإمام يخطب إلا له أو لمن كلمه','1019','258','0');
INSERT INTO [t1153] VALUES (660,'مسألة: ويجوز الكلام قبل الخطبة وبعدها وعنه يجوز فيها','1024','258','0');
INSERT INTO [t1153] VALUES (661,'باب صلاة العيدين','1027','258','0');
INSERT INTO [t1153] VALUES (662,'مسألة: وأول وقتها إذا ارتفعت الشمس وآخره إذا زالت','1028','258','0');
INSERT INTO [t1153] VALUES (663,'مسألة: فإن لم يعلم بالعيد إلا بعد الزوال خرج من الغد فصلى بهم','1029','258','0');
INSERT INTO [t1153] VALUES (664,'مسألة: ويسن تقديم الأضحى وتأخير الفطر والأكل في الفطر قبل الصلاة والامساك في الأضحى حتى يصلي','1030','258','0');
INSERT INTO [t1153] VALUES (665,'مسألة: ويستحب الغسل والتبكير إليها بعد الصبح ماشيا على أحسن هيئة إلا المعتكف يخرج في ثياب اعتكافه أو إماما يتأخر إلى وقت الصلاة','1031','258','0');
INSERT INTO [t1153] VALUES (666,'مسألة: وغدا غدا من طريق رجع في أخرى','1039','258','0');
INSERT INTO [t1153] VALUES (667,'مسألة: وهل من شرطها الاستيطان وإذن الامام والعدد المشروط للجمعة؟ على روايتين','1040','258','0');
INSERT INTO [t1153] VALUES (668,'مسألة: وتسن في الصحراء وتكره في الجامع إلا من عذر','1043','258','0');
INSERT INTO [t1153] VALUES (669,'مسألة: ويبدأ بالصلاة فيصلي ركعتين','1046','258','0');
INSERT INTO [t1153] VALUES (670,'مسألة: يكبر فيالأولى بعد الاستفتاح وقبل التعوذ وفي الثانية بعد القيام من السجود خمسا','1048','258','0');
INSERT INTO [t1153] VALUES (671,'مسألة: ويرفع يديه مع كل تكبيرة','1053','258','0');
INSERT INTO [t1153] VALUES (672,'مسألة: ويقول الله أكبر كبيرا، والحمد لله كثيرا، وسبحان الله بكرة وأصيلا، وصلى الله على محمد النبي وآله وصحبه وسلم تسليما كثيرا، وإن أحب قال غير ذلك','1054','258','0');
INSERT INTO [t1153] VALUES (673,'مسألة: ثم يقرأ بعد الفاتحة في الأولى بسبح وفي الثانية بالغاشية ويجهر بالقراءة','1056','258','0');
INSERT INTO [t1153] VALUES (674,'مسألة: وتكون بعد التكبير في الركعتين وعنه يوالي بين القراءتين','1057','258','0');
INSERT INTO [t1153] VALUES (675,'مسألة: فإذا سلم خطب خطبتين يجلس بينهما يفتتح الأولى بتسع تكبيرات، والثانية بسبع يحثهم في خطبة الفطر على الصدقة ويبين لهم ما يخرجون، ويرغبهم في الاضحية في الأضحى ويبين لهم حكم الأضحية','1058','258','0');
INSERT INTO [t1153] VALUES (676,'مسألة: والتكبيرات الزوائد والذكر بينهما والخطبتان سنة','1060','258','0');
INSERT INTO [t1153] VALUES (677,'مسألة: ولا يتنفل قبل صلاة العيد ولا بعدها في موضع الصلاة','1062','258','0');
INSERT INTO [t1153] VALUES (678,'مسألة: ومن كبر قبل سلام الامام صلى ما فاته على صفته','1064','258','0');
INSERT INTO [t1153] VALUES (679,'مسألأة: وإن فاتته الصلاة استحب أن يقضيها على صفتها وعنه يقضيها أربعا وعنه أنه مخير بين ركعتين وأربع','1064','258','0');
INSERT INTO [t1153] VALUES (680,'مسألة: ويستحب التكبير في ليلتي العيدين','1066','258','0');
INSERT INTO [t1153] VALUES (681,'مسألة: وفي الأضحى يكبر عقيب كل فريضة في جماعة وعنه يكبر، وإن كان وحده من صلاة الفجر يوم عرفة الى العصر من آخر أيام التشريق','1068','258','0');
INSERT INTO [t1153] VALUES (682,'مسألة: وإن نسي التكبير قضاه ما لم يحدث أو يخرج من المسجد لأنه مختص بالصلاة','1072','258','0');
INSERT INTO [t1153] VALUES (683,'مسألة: وفي التكبير عقيب العيد وجهان','1073','258','0');
INSERT INTO [t1153] VALUES (684,'باب صلاة الكسوف','1074','258','0');
INSERT INTO [t1153] VALUES (685,'مسألة: ثم يصلي ركعتين يقرأ في الأولى بعد الفاتحة سورة طويلة','1076','258','0');
INSERT INTO [t1153] VALUES (686,'مسألة: فإن تجلى الكسوف فها أتمها خفيفة، وان تجلى قبلها أو غابت الشمس كاسفة أو طلعت والقمر خاسف لم تصل','1080','258','0');
INSERT INTO [t1153] VALUES (687,'مسألة: وإن أتى في كل ركعة بثلاث ركوعات أو أربع فلا بأس','1082','258','0');
INSERT INTO [t1153] VALUES (688,'مسألة: ولا يصلي لشيء من سائر الآيات إلا الزلزلة الدائمة','1083','258','0');
INSERT INTO [t1153] VALUES (689,'باب صلاة الاستسقاء','1084','258','0');
INSERT INTO [t1153] VALUES (690,'مسألة: وصفتها في موضعها وأحكامها صفة صلاة العيد','1085','258','0');
INSERT INTO [t1153] VALUES (691,'مسألة: وإذا أراد الإمام الخروج إليها وعظ الناس','1087','258','0');
INSERT INTO [t1153] VALUES (692,'مسألة: وبعدهم يوما يخرجون فيه','1087','258','0');
INSERT INTO [t1153] VALUES (693,'مسألة: ويتنظف لها بالغسل والسواك وازالة الرائحة قياسا على صلاة العيد','1087','258','0');
INSERT INTO [t1153] VALUES (694,'مسألة: ويخرج متواضعا متخشعا متذللا متضرعا','1087','258','0');
INSERT INTO [t1153] VALUES (695,'مسألة: ويخرج معه أهل الدين والصلاح والشيوخ لأنه أسرع للإجابة','1087','258','0');
INSERT INTO [t1153] VALUES (696,'مسألة: ويجوز خروج الصبيان كغيرهم من الناس','1088','258','0');
INSERT INTO [t1153] VALUES (697,'مسألة: وإن خرج معهم أهل الذمة لم يمنعو ولم يختلطوا بالمسلمين','1088','258','0');
INSERT INTO [t1153] VALUES (698,'مسألة: فيصلي بهم ثم يخطب خطبة واحدة يفتتحها بالتكبير كخطبة العيد','1088','258','0');
INSERT INTO [t1153] VALUES (699,'مسألة: ويكثر فيها الاستغفاء وقراءة الآيات التي فيها الأمر به','1090','258','0');
INSERT INTO [t1153] VALUES (700,'مسألة: ويرفع يديه فيدعو بدعاء النبي صلى الله عليه وسلم','1091','258','0');
INSERT INTO [t1153] VALUES (701,'مسألة: ويستقبل القبلة في أثناء الخطبة ويحول رداءه ويجعل الأيسر على الأيمن والأيمن على الأيسر، ويفعل الناس كذلك ويتركونه حتى ينزعوه مع ثيابهم','1094','258','0');
INSERT INTO [t1153] VALUES (702,'مسألة: ويدعو سرا حال استقبال القبلة','1096','258','0');
INSERT INTO [t1153] VALUES (703,'مسألة: فإن سقوا وإلا عادوا ثانيا وثالثا، وإن سقوا قبل خرجهم شكروا الله تعالى وسألوه المزيد من فضله','1097','258','0');
INSERT INTO [t1153] VALUES (704,'مسألة: وينادى لها الصلاة جامعة','1098','258','0');
INSERT INTO [t1153] VALUES (705,'مسألة: وهل من شرطها اذن الامام على روايتين','1098','258','0');
INSERT INTO [t1153] VALUES (706,'مسألة: ويستحب أن يقف في أول المطر ويخرج رحله وثيابه ليصيبها','1099','258','0');
INSERT INTO [t1153] VALUES (707,'مسألة: وإذا زادت المياه فخيف منها','1102','258','0');
INSERT INTO [t1153] VALUES (708,'كتاب الجنائز','1103','0','0');
INSERT INTO [t1153] VALUES (709,'كتاب الجنائز','1103','708','0');
INSERT INTO [t1153] VALUES (710,'مسألة: وإذا نزل به تعاهد بل حلقه بماء أو شراب ويندي شفتيه بقطنة','1104','708','0');
INSERT INTO [t1153] VALUES (711,'مسألة: ويستحب أن يلقنه قول لا إله إلا الله مرة','1105','708','0');
INSERT INTO [t1153] VALUES (712,'مسألة: ولا يزيد على ثلاث لئلا يضجره إلا أن يتكلم بعده بشيء فيعيد تلقينه بلطف ومداراة ليكون آخر كلامه لا إله إلا الله نص عليه أحمد','1105','708','0');
INSERT INTO [t1153] VALUES (713,'مسألة: ويقرأ عنده سورة يس','1105','708','0');
INSERT INTO [t1153] VALUES (714,'مسألة: ويوجهه إلى القبلة','1105','708','0');
INSERT INTO [t1153] VALUES (715,'مسألة: فإذا مات أغمض عينيه وشد لحييه ولين مفاصله وخلع ثيابه وسجاه بثوب يستره وجعل على بطنه مرآة أو نحوها ووضعه على سرير غسله متوجها منحدرا نحو رجليه','1106','708','0');
INSERT INTO [t1153] VALUES (716,'مسألة: ويسارع في قضاء دينه','1107','708','0');
INSERT INTO [t1153] VALUES (717,'مسألة: ويسارع في تفريق وصيته','1108','708','0');
INSERT INTO [t1153] VALUES (718,'مسألة: ويستحب المسارعة في تجهيزه إذا تيقن موته لأنه أصون له وأحفظ له من التغيير','1108','708','0');
INSERT INTO [t1153] VALUES (719,'فصل في غسل الميت، مسألة: غسل الميت ودفنه وتكفينه والصلاة عليه فرض كفاية','1109','708','0');
INSERT INTO [t1153] VALUES (720,'مسألة: وأحق الناس به وصيه ثم أبوه ثم جده ثم الأقرب فالأقرب من عصابة ثم ذوو أرحامه إلأا الصلاة عليه فان الأمير أحق بها بعد وصيه','1109','708','0');
INSERT INTO [t1153] VALUES (721,'مسألة: والأمير أحق بالصلاة عليه بعد الوصي','1110','708','0');
INSERT INTO [t1153] VALUES (722,'مسألة: وأحق الناس بغسل المرأة وصيها ثم الأقرب فالأقرب من نسائها أمها ثم بنتها ثم بناتها ثم أخواتها كما ذكرنا في حق الرجل','1112','708','0');
INSERT INTO [t1153] VALUES (723,'مسألة: ولكل واحد من الزوجين غسل صاحبه في إحدى الروايتين، كذلك السيد مع سريته','1112','708','0');
INSERT INTO [t1153] VALUES (724,'مسألة: وللرجل والمرأة غسل من له دون سبع سنين وفي ابن السبع وجهان','1114','708','0');
INSERT INTO [t1153] VALUES (725,'مسألة: وإذا مات رجل بين نسوة أو امرأو بين رجال أو خنثى مشكل يمم في أصح الروايتين وفي الأخرى يصب عليه الماء من فوق قميص ولا يمس','1115','708','0');
INSERT INTO [t1153] VALUES (726,'مسألة: وإذا أخذ في غسله ستر عورته وجرده، وقال القاضي: يغسل في قميص واسع الكمين','1116','708','0');
INSERT INTO [t1153] VALUES (727,'مسألة: ولا يغسل مسلم كافرا ولا يدفنه إلا أن يجد من يواريه غيره','1116','708','0');
INSERT INTO [t1153] VALUES (728,'مسألة: ويستر الميت عن العيون، ولا يحضره إلا من يعين في غسله','1118','708','0');
INSERT INTO [t1153] VALUES (729,'مسألة: ثم يرفع رأسه برفق إلى قريب من الجلوس وسعصر بطنه عصرا رفيقا ويكثر صب الماس حينئذ','1119','708','0');
INSERT INTO [t1153] VALUES (730,'مسألة: ثم يلف على يده خرقة فينجيه ولا يحل مس عورته، ويستحب أن لا يمس سائر بدنه الا بخرقة','1120','708','0');
INSERT INTO [t1153] VALUES (731,'مسألة: ثم ينوي غسلهما ويسمي','1120','708','0');
INSERT INTO [t1153] VALUES (732,'مسألة: قال: ويدخل أصبعيه مبلولتين بالماء بين شفتيه فيمسح أسنانه وفي منخريه فينظفها ويوضيه ولا يدخل الماء في فيه ولا أنفه','1121','708','0');
INSERT INTO [t1153] VALUES (733,'مسألة: ثم يضرب السدر فيغسل برغوته رأسه ولحيته وسائر بدنه، ثم يغسل شقه الأيمن ثم الأيسر يفعل ذلك ثلاثا','1121','708','0');
INSERT INTO [t1153] VALUES (734,'مسألة: فإن لم ينق بالثلاث وخرج منه شيء غسله إلى خمس فإن زاد فإلى سبع','1123','708','0');
INSERT INTO [t1153] VALUES (735,'مسألة: ويقص شاربه ويقلم أظافره ولا يسرح شعره ولا لحيته','1125','708','0');
INSERT INTO [t1153] VALUES (736,'مسألة: ويجعل في الغسلة الأخيرة كافورا','1125','708','0');
INSERT INTO [t1153] VALUES (737,'مسألة: والماء الحار والخلال والاشنان يستعمل إن احتيج إليه','1125','708','0');
INSERT INTO [t1153] VALUES (738,'مسألة: فإن خرج منه شيء بعد السبع حشاه بالقطن فان لم يستمسك فبالطين الحر','1127','708','0');
INSERT INTO [t1153] VALUES (739,'مسألة: ويضفر شعر المرأة ثلاثة قرون ويسدل من ورائها','1127','708','0');
INSERT INTO [t1153] VALUES (740,'مسألة: ثم ينشفه بثوب','1127','708','0');
INSERT INTO [t1153] VALUES (741,'مسألة: ثم يغسل المحل ويوضأ','1128','708','0');
INSERT INTO [t1153] VALUES (742,'مسألة: فإن خرج منه شيء بعد وضعه في أكفانه لم يعد إلى الغسل','1128','708','0');
INSERT INTO [t1153] VALUES (743,'مسألة: ويغسل المحرم بماء وسدر ولا يلبس المخيط ولا يخمر رأسه ولا يقرب طيبا','1128','708','0');
INSERT INTO [t1153] VALUES (744,'مسألة: والشهيد لا يغسل إلا أن يكون جنبا','1129','708','0');
INSERT INTO [t1153] VALUES (745,'مسألة: وينزع عنه السلاح والجلود ويزمل في ثيابه وإن أحب فيكفنه في غيرها','1130','708','0');
INSERT INTO [t1153] VALUES (746,'مسألة: وإن سقط من دابته ووجد ميتا لا أثر به أو حمل فأكل أو طال بقاؤه غسل وصلي عليه','1131','708','0');
INSERT INTO [t1153] VALUES (747,'مسألة: ولا يصلى عليه في أصح الروايتين','1131','708','0');
INSERT INTO [t1153] VALUES (748,'مسألة: ومن قتل مظلوما فهل يلحق بالشهيد على روايتين','1133','708','0');
INSERT INTO [t1153] VALUES (749,'مسألة: وإذا ولد السقط لأكثر من أربعة أشهر غسل وصلي عليه','1134','708','0');
INSERT INTO [t1153] VALUES (750,'مسألة: ومن تعذر غسله يمم','1135','708','0');
INSERT INTO [t1153] VALUES (751,'مسألة: وعلى الغاسل ستر ما رآه إن لم يكن حسنا','1135','708','0');
INSERT INTO [t1153] VALUES (752,'فصل في الكفن، مسألة: ويجب كفن الميت في ماله مقدما على الدين وغيره','1136','708','0');
INSERT INTO [t1153] VALUES (753,'مسألة: فإن لم يكن له مال فعلى من تلزمه نفقته إلا الزوج لا يلزمه كفن امرأته','1136','708','0');
INSERT INTO [t1153] VALUES (754,'مسألة: ويستحب تكفين الرجل في ثلاث لفائف بيض يبسط بعضها فوق بعض بعد تجميرها','1137','708','0');
INSERT INTO [t1153] VALUES (755,'مسألة: ثم يوضع عليها مستلقيا ويجعل الحنوط فيما بينها','1138','708','0');
INSERT INTO [t1153] VALUES (756,'مسألة: وإن كفن في قميص ومئزر ولفافة جاز','1139','708','0');
INSERT INTO [t1153] VALUES (757,'مسألة: ثم يرد طرف اللفافة العليا على شقه الأيمن ثم يرد طرفه الآخر على شقه الأيسر','1139','708','0');
INSERT INTO [t1153] VALUES (758,'مسألة: وتكفن المرأة في خمسة أثواب إزار وخمار وقميص ولفافتين','1140','708','0');
INSERT INTO [t1153] VALUES (759,'مسألة: والواجب من ذلك ثوب يستر','1141','708','0');
INSERT INTO [t1153] VALUES (760,'فصل في الصلاة على الميت','1142','708','0');
INSERT INTO [t1153] VALUES (761,'مسألة: السنة أن يقوم الامام عند رأس الرجل ووسط المرأة','1142','708','0');
INSERT INTO [t1153] VALUES (762,'مسألة: ويقدم إلى الإمام أفضلهم ويجعل وسط المرأة حذاء رأس الرجل، وقال القاضي: يسوى بين رؤوسهم','1142','708','0');
INSERT INTO [t1153] VALUES (763,'مسألة: ثم يكبر أربع تكبيرات يقرأ في الأولى الفاتحة ويصلي على النبي صلى الله عليه وسلم في الثانية','1144','708','0');
INSERT INTO [t1153] VALUES (764,'مسألة: ويدعو في التكبيرة الثالثة','1145','708','0');
INSERT INTO [t1153] VALUES (765,'مسألة: وإن كان صبيا جعل مكان الاستغفار له اللهم اجعله ذخرا لوالديه','1147','708','0');
INSERT INTO [t1153] VALUES (766,'مسألة: ثم يقف بعد الرابعة قليلا ويسلم تسليمة واحدة عن يمينه','1147','708','0');
INSERT INTO [t1153] VALUES (767,'مسألة: ويرفع يديه مع كل تكبيرة','1148','708','0');
INSERT INTO [t1153] VALUES (768,'مسألة: وإن كبر الإمام خمسا كبر بتكبيره، وعنه لا يتابع في زيادة على أربع، وعنه يتابع الى سبع','1150','708','0');
INSERT INTO [t1153] VALUES (769,'مسألة: ومن فاته شيء من التكبير قضاه على صفته وقال الخرقي: يقضيه متتابعا','1152','708','0');
INSERT INTO [t1153] VALUES (770,'مسألة: فإن سلم ولم يقضه فعلى روايتين','1153','708','0');
INSERT INTO [t1153] VALUES (771,'مسألة: ومن فاتته الصلاة على الجنازة صلى على القبر إلى شهر','1153','708','0');
INSERT INTO [t1153] VALUES (772,'مسألة: ويصلى على الغائب بالنية فإن كان في أحد جانبي البلد لم تصح عليه بالنية في أصح الوجهين','1155','708','0');
INSERT INTO [t1153] VALUES (773,'مسألة: ولا يصلى الإمام على الغال ولا من قتل نفسه','1156','708','0');
INSERT INTO [t1153] VALUES (774,'مسألة: وإن وجد بعض الميت غسل وصلي عليه وعنه لا يصلى على الجوارح','1158','708','0');
INSERT INTO [t1153] VALUES (775,'مسألة: وإن اختلط من يصلى عليه بمن لا يصلى عليه صلي على الجميع ينوي من يصلى عليه','1159','708','0');
INSERT INTO [t1153] VALUES (776,'مسألة: وإن لم يحضره إلا النساء صلين عليه','1160','708','0');
INSERT INTO [t1153] VALUES (777,'مسألة: ولا بأس بالصلاة على الميت في المسجد إذا لم يخف تلويثه','1160','708','0');
INSERT INTO [t1153] VALUES (778,'فصل في حمل الميت ودفنه، مسألة: يستحب التربيع في حمله','1161','708','0');
INSERT INTO [t1153] VALUES (779,'مسألة: قال: وهو أن يضع قائمة السرير اليسرى المقدمة على كتفه اليمنى','1162','708','0');
INSERT INTO [t1153] VALUES (780,'مسألة: وإن حمل بين العمودين فحسن','1163','708','0');
INSERT INTO [t1153] VALUES (781,'مسألة: ويستحب الإسراع بها','1163','708','0');
INSERT INTO [t1153] VALUES (782,'مسألة: ويستحب أن يكون المشاة أمامها والركبان خلفها','1167','708','0');
INSERT INTO [t1153] VALUES (783,'مسألة: ولا يجلس من تبعها حتى توضع','1175','708','0');
INSERT INTO [t1153] VALUES (784,'مسألة: وإن جاءت وهو جالس لم يقم لها لما ذكرنا من حديث علي وقد فسره اسحق بما حكينا','1177','708','0');
INSERT INTO [t1153] VALUES (785,'مسألة: ويدخل قبره من عند رجل القبر ان كان أسهل عليهم','1178','708','0');
INSERT INTO [t1153] VALUES (786,'مسألة: قال: ولا يسجى القبر الا أن يكون لامرأة','1181','708','0');
INSERT INTO [t1153] VALUES (787,'مسألة: ويلحد له لحدا وينصب عليه اللبن نصبا','1182','708','0');
INSERT INTO [t1153] VALUES (788,'مسألة: ويقول الذي يدخله بسم الله وعلى ملة رسول الله صلى الله عليه وسلم','1183','708','0');
INSERT INTO [t1153] VALUES (789,'مسألة: ولا يدخل القبر خشبا ولا شيئا مسته النار','1183','708','0');
INSERT INTO [t1153] VALUES (790,'مسألة: ويضعه في لحده وعلى جنبه الأيمن مستقبل القبلة بوجهه','1186','708','0');
INSERT INTO [t1153] VALUES (791,'مسألة: ويحثو التراب في القبر ثلاث حثيات ويهال عليه التراب','1187','708','0');
INSERT INTO [t1153] VALUES (792,'مسألة: ويرش عليه الماء ليتلبد وترابه','1191','708','0');
INSERT INTO [t1153] VALUES (793,'مسألة: ولا بأس بتطيينه','1193','708','0');
INSERT INTO [t1153] VALUES (794,'مسألة: ويكره تجصيصه والبناء عليه والجلوس والوطء عليه والاتكاء اليه والكتابة عليه','1194','708','0');
INSERT INTO [t1153] VALUES (795,'مسألة: ولا يدفن فيه إثنان إلا لضرورة ويقدم الأفضل إلى القبلة ويجعل بين كل إثنين حاجز من التراب','1204','708','0');
INSERT INTO [t1153] VALUES (796,'مسألة: وإن وقع في القبر ما له قيمة نبش وأخذ','1206','708','0');
INSERT INTO [t1153] VALUES (797,'مسألة: وإن كفن بثوب غصب أو بلغ مال غيره غرم ذلك من تركته، وقيل ينبش ويؤخذ الكفن ويشق جوفه فيخرج','1207','708','0');
INSERT INTO [t1153] VALUES (798,'مسألة: وإن ماتت حامل لم يشق بطنها وتسطو عليه القوابل فيخرجنه','1210','708','0');
INSERT INTO [t1153] VALUES (799,'مسألة: ولا تكره القراءة على القبر في أصح الروايتين','1213','708','0');
INSERT INTO [t1153] VALUES (800,'مسألة: وأي قربة فعلها وجعل ثوابها للميت المسلم نفعه ذلك','1215','708','0');
INSERT INTO [t1153] VALUES (801,'مسألة: ويستحب أن يصلح لأهل الميت طعاما يبعث إليهم ولا يصلحون هم طعاما للناس','1219','708','0');
INSERT INTO [t1153] VALUES (802,'مسألة: ويقول إذا زارها أو مر بها','1221','708','0');
INSERT INTO [t1153] VALUES (803,'مسألة: ويستحب تعزية أهل الميت','1221','708','0');
INSERT INTO [t1153] VALUES (804,'مسألة: ويقول في تعزية الكافر بالمسلم أحسن الله عزاك وغفر لميتك','1223','708','0');
INSERT INTO [t1153] VALUES (805,'مسألة: ويجوز البكاء على الميت وان يجعل المصاب على رأسه ثوبا ليعرف به ليعزى','1224','708','0');
INSERT INTO [t1153] VALUES (806,'مسألة: ولا يجوز الندب ولا النياحة ولا شق الثياب ولطم الخدود وما أشبه ذلك','1225','708','0');
INSERT INTO [t1153] VALUES (807,'كتاب الزكاة','1229','0','0');
INSERT INTO [t1153] VALUES (808,'كتاب الزكاة','1229','807','0');
INSERT INTO [t1153] VALUES (809,'مسألة: وتجب الزكاة في أربعة أصناف من المال','1230','807','0');
INSERT INTO [t1153] VALUES (810,'مسألة: وقال أصحابنا تجب في المتولد بين الوحشي والأهلي','1231','807','0');
INSERT INTO [t1153] VALUES (811,'مسألة: وفي بقر الوحش روايتان','1232','807','0');
INSERT INTO [t1153] VALUES (812,'مسألة: ولا تجب إلا بشروط خمسة: الاسلام والحرية فلا تجب على كافر ولا عبد ولا مكاتب','1233','807','0');
INSERT INTO [t1153] VALUES (813,'مسألة: فإن ملك السيد عبده مالا وقلنا إنه يملكه فلا زكاة فيه، وإن قلنا لا يملكه فزكاته على سيده','1235','807','0');
INSERT INTO [t1153] VALUES (814,'مسألة: الثالث ملك نصاب، فإن نقص عنه فلا زكاة فيه إلا أن يكون نقصا يسيرا كالحبة والحبتين','1236','807','0');
INSERT INTO [t1153] VALUES (815,'مسألة: وتجب فيما زاد على النصاب بالحساب إلا في السائمة','1236','807','0');
INSERT INTO [t1153] VALUES (816,'مسألة: والشرط الرابع تمام الملك','1237','807','0');
INSERT INTO [t1153] VALUES (817,'مسألة: ولا تجب في السائمة الموقوفة ولا في حصة المضارب من الربح قبل القسمة على أحد الوجهين فيهما','1238','807','0');
INSERT INTO [t1153] VALUES (818,'مسألة: ومن كان له دين على ملي من صداق أو غيره زكاه اذا قبضه لما مضى','1240','807','0');
INSERT INTO [t1153] VALUES (819,'مسألة: وفي الدين على غير الملي والمؤجل والمجحود والمغصوب والضائع روايتان','1241','807','0');
INSERT INTO [t1153] VALUES (820,'مسألة: ولا زكاة في مال من عليه دين ينقص النصاب إلا في المواشي والحبوب في إحدى الروايتين','1249','807','0');
INSERT INTO [t1153] VALUES (821,'مسألة: قال الخرقي: واللقطة إذا جاء ربها زكاها للحول الذي كان الملتقط ممنوعا منها','1249','807','0');
INSERT INTO [t1153] VALUES (822,'مسألة: والكفارة كالدين في أحد الوجهين','1254','807','0');
INSERT INTO [t1153] VALUES (823,'مسألة: الشرط الخامس مضي الحول شرط إلا في الخارج من الارض','1255','807','0');
INSERT INTO [t1153] VALUES (824,'مسألة: فإذا استفاد مالا فلا زكاة حتى يتم عليه الحول','1256','807','0');
INSERT INTO [t1153] VALUES (825,'مسألة: وإن ملك نصابا صغارا انعقد عليه الحول من حين ملكه وعنه ولا ينعقد حتى يبلغ سنا يجزي مثله في الزكاة','1259','807','0');
INSERT INTO [t1153] VALUES (826,'مسألة: ومتى نقص النصاب في بعض الحول أو باعه أو أبدله بغير جنسه انقطع الحول','1260','807','0');
INSERT INTO [t1153] VALUES (827,'مسألة: إلا أن يقصد بذلك الفرار من الزكاة عند قرب وجوبها فلا تسقط','1261','807','0');
INSERT INTO [t1153] VALUES (828,'مسألة: وان أبدله بنصاب من جنسه بني على حوله','1264','807','0');
INSERT INTO [t1153] VALUES (829,'مسألة: وإذا تم الحول وجبت الزكاة في عين المال وعنه تجب في الذمة','1265','807','0');
INSERT INTO [t1153] VALUES (830,'مسألة: ولا يعتبر في وجوبها مكان الاداء','1266','807','0');
INSERT INTO [t1153] VALUES (831,'مسألة: ولا تسقط بتلف المال وعنه أنها تسقط إذا لم يفرط','1266','807','0');
INSERT INTO [t1153] VALUES (832,'مسألة: وإذا مضى حولان على نصاب لم يؤد زكاتهما','1268','807','0');
INSERT INTO [t1153] VALUES (833,'مسألة: وإن كان أكثر من نصاب فعليه زكاة جميعه لكل حول','1269','807','0');
INSERT INTO [t1153] VALUES (834,'مسألة: وإذا مات من عليه الزكاة أخذت من تركته، فإن كان عليه دين اقتسموا بالحصص','1270','807','0');
INSERT INTO [t1153] VALUES (835,'باب زكاة بهيمة الأنعام، مسألة: ولا تجب إلا في السائمة منها','1271','807','0');
INSERT INTO [t1153] VALUES (836,'مسألة: وهي التي ترعى في أكثر الحول','1272','807','0');
INSERT INTO [t1153] VALUES (837,'مسألة: وهي ثلاثة أنواع: أحدها الابل فلا زكاة فيها حتى تبلغ خمسا فتجب فيها شاة','1273','807','0');
INSERT INTO [t1153] VALUES (838,'مسألة: فإن أخرج بعيرا لم يجزئه','1277','807','0');
INSERT INTO [t1153] VALUES (839,'مسألة: وفي العشر شاتان، وفي خمسة عشرة ثلاث شياه، وفي العشرين أربع شياه','1278','807','0');
INSERT INTO [t1153] VALUES (840,'مسألة: فإذا بلغت خمسا وعشرين ففيها بنت مخاض وهي التي لها سنة','1278','807','0');
INSERT INTO [t1153] VALUES (841,'مسألة: فإن عدمها أجزاه ابن لبون وهو الذي له سنتان، فإن عدمه لزمه بنت مخاض','1279','807','0');
INSERT INTO [t1153] VALUES (842,'مسألة: وفي ست وثلاثين بنت لبون وفي ست وأربعين حقة','1281','807','0');
INSERT INTO [t1153] VALUES (843,'مسألة: فإذا زادت على عشرين ومائة واحدة ففيها ثلاث بنات لبون ثم في كل أربعين بنت لبون وفي كل خمسين حقة','1282','807','0');
INSERT INTO [t1153] VALUES (844,'مسألة: فإذا بلغت مائتين انفق الفرضان، فإن شاء أخرج أربع حقاق، وإن شاء خمس بنات لبون، والمنصوص انه يخرج الحقاق','1285','807','0');
INSERT INTO [t1153] VALUES (845,'مسألة: وليس فيما بين الفريضتين شيء','1289','807','0');
INSERT INTO [t1153] VALUES (846,'مسألة: ومن وجبت عليه سن فعدمها أخرج سنا أسفل منها ومعها شاتان أو عشرون درهما وإن شاء أخرج أعلى منها وأخذ مثل ذلك من الساعي','1290','807','0');
INSERT INTO [t1153] VALUES (847,'مسألة: فإن عد السن التي تليها انتقل إلى الأخرى وجبرها بأربع شياه أو أربعين درهما وقال أبو الخطاب لا ينتقل إلا الى سن تلي الواجب','1292','807','0');
INSERT INTO [t1153] VALUES (848,'مسألة: ولا مدخل للجبران في غير الإبل','1294','807','0');
INSERT INTO [t1153] VALUES (849,'مسألة: ولا يجزىء الذكر في الزكاة في غير هذا الا ابن لبون مكان بنت مخاض','1299','807','0');
INSERT INTO [t1153] VALUES (850,'مسألة: ويؤخذ من الصغار صغيرة ومن المراض مريض، وقال أبو بكر لا يؤخذ إلا كبيرة صحيحة على قدر المال','1302','807','0');
INSERT INTO [t1153] VALUES (851,'مسألة: وإن كان نوعين كالبخاتي والعراب والبقر والجواميس والضأن والمعز','1308','807','0');
INSERT INTO [t1153] VALUES (852,'النوع الثالث في الغنم، مسألة: ولا شيء فيها حتى تبلغ أربعين فتجب فيها شاة','1310','807','0');
INSERT INTO [t1153] VALUES (853,'مسألة: ويؤخذ من المعز الثني ومن الضأن الجذع','1312','807','0');
INSERT INTO [t1153] VALUES (854,'مسألة: ولا يؤخذ في الصدقة تيس ولا هرمة ولا ذات عوار وهي المعيبة','1313','807','0');
INSERT INTO [t1153] VALUES (855,'مسألة: ولا الربي وهي التي تربي ولدها ولا الماخض ولا كرائم المال إلا أن يشاء ربه','1315','807','0');
INSERT INTO [t1153] VALUES (856,'مسألة: ولا يجوز إخراج القيمة وعنه يجوز','1317','807','0');
INSERT INTO [t1153] VALUES (857,'مسألة: وإن أخرج سنا أعلى من الفرض من جنسه جاز','1320','807','0');
INSERT INTO [t1153] VALUES (858,'فصل في الخلطة، مسألة: وإذا اختلط نفسان أو أكثر من أهل الزكاة في نصاب من الماشية حولا','1322','807','0');
INSERT INTO [t1153] VALUES (859,'مسألة: فإن اختل شرط منها أو ثبت لهما حكم الانفراد في بعض الحول زكيا زكاة المنفردين فيه','1328','807','0');
INSERT INTO [t1153] VALUES (860,'مسألة: وإن ثبت لاحدهما حكم الانفراد وحده فعليه زكاة المنفرد وعلى الثاني زكاة الخلطة','1329','807','0');
INSERT INTO [t1153] VALUES (861,'مسألة: ولو ملك رجل نصابا شهرا ثم باع نصفه مشاعا أو أعلم على بعضه وباعه مختلطا','1332','807','0');
INSERT INTO [t1153] VALUES (862,'مسألة: فإن أخرجها من المال حول المشتري لنقصان النصاب','1333','807','0');
INSERT INTO [t1153] VALUES (863,'مسألة: وإن اخرجها من غيره وقلنا الزكاة في العين فكذلك، وإن قلنا في الذمة فعليه عند تمام حوله زكاة حصته','1334','807','0');
INSERT INTO [t1153] VALUES (864,'مسألة: وإن أفرد بعضه وباعه ثم اختلطا انقطع الحول','1335','807','0');
INSERT INTO [t1153] VALUES (865,'مسألة: وإن ملك نصابين شهرا ثم باع أحدهما مشاعا فعلى قياس قول أبي بكر','1335','807','0');
INSERT INTO [t1153] VALUES (866,'مسألة: ولو ملك رجل نصابا شهرا ثم ملك آخر لا يتغير به الفرض','1335','807','0');
INSERT INTO [t1153] VALUES (867,'مسألة: وإن كان الثاني يتغير به الفرض، مثل أن يملك مائة شاة فعليه شاة فعليه زكاته إذا تم حوله وجها واحدا','1336','807','0');
INSERT INTO [t1153] VALUES (868,'مسألة: وإذا كان الثاني يتغير به الفرض ولا يبلغ نصابا','1337','807','0');
INSERT INTO [t1153] VALUES (869,'مسألة: وإذا كانت ماشية الرجل متفرقة في بلدين لا تقصر بينهما الصلاة فهي كالمجتمعة يضم بعضها إلى بعض ويزكيها كالمختلطة','1338','807','0');
INSERT INTO [t1153] VALUES (870,'مسألة: وإذا كان لرجل ستون شاة كل عشرين فيها مختلطة بعشرين لآخر فعلى الجميع شاة نصفها على صاحب الستين ونصفها على الخلطاء على كل واحد سدس شاة','1338','807','0');
INSERT INTO [t1153] VALUES (871,'مسألة: وإن ملك ما لا يغير الفرض كخمس فلا شيء فيها على الوجه الأول','1338','807','0');
INSERT INTO [t1153] VALUES (872,'مسألة: ولا تؤثر الخلطة في غير السائمة وعنه أنها تؤثر','1340','807','0');
INSERT INTO [t1153] VALUES (873,'مسألة: ويجوز للساعي أخذ الفرض من مال أي الخليطين شاء','1341','807','0');
INSERT INTO [t1153] VALUES (874,'مسألة: ويرجع المأخوذ منه على خليطه بحصته من القيمة لما ذكرنا من النص والمعنى','1342','807','0');
INSERT INTO [t1153] VALUES (875,'مسألة: فإن اختلفا في القيمة فالقول قول المرجوع عليه','1342','807','0');
INSERT INTO [t1153] VALUES (876,'مسألة: وإذا أخذ الساعي أكثر من الفرض ظلما لم يرجع بالزيادة على خليطه','1342','807','0');
INSERT INTO [t1153] VALUES (877,'مسألة: وإن أخذه بقول بعض العلماء رجع عليه','1342','807','0');
INSERT INTO [t1153] VALUES (878,'باب زكاة الخارج من الأرض','1343','807','0');
INSERT INTO [t1153] VALUES (879,'مسألة: تجب الزكاة في الحبوب كلها وفي كل ثمر يكال ويدخر كالتمر والزبيب واللوز والفستق والبندق ولا يجب في سائر الثمر ولا في الخضر والزهر والبقول','1344','807','0');
INSERT INTO [t1153] VALUES (880,'مسألة: وقال ابن حامد لا زكاة في حب البقول','1349','807','0');
INSERT INTO [t1153] VALUES (881,'مسألة: ويعتبر لوجوبها شرطان أحدهما أن يبلغ نصاب قدره بعد التصفية في الحبوب والجفاف في الثمار خمسة أوسق','1350','807','0');
INSERT INTO [t1153] VALUES (882,'مسألة: والوسق ستون صاعا، والصاع خمسة أرطال وثلث بالعراقي، فيكون ذلك الفا وستمائة رطل','1351','807','0');
INSERT INTO [t1153] VALUES (883,'مسألة: قال إلا الأرز والعلس - نوع من الحنطة يدخر في قشره -، فإن نصاب كل واحد منهما مع قشره عشرة أوسق','1352','807','0');
INSERT INTO [t1153] VALUES (884,'مسألة: وعنه أنه يعتبر نصاب ثمرة النخل والكرم رطبا ويؤخذ عشره يابسا','1353','807','0');
INSERT INTO [t1153] VALUES (885,'مسألة: وتضم ثمرة العام الواحد بعضها إلى بعض في تكميل النصاب','1354','807','0');
INSERT INTO [t1153] VALUES (886,'مسألة: فإن كان له نخل يحمل في السنة حملين ضم أحدهما إلى الآخر. وقال القاضي: لا يضم','1354','807','0');
INSERT INTO [t1153] VALUES (887,'مسألة: ولا يضم جنس إلى آخر في تكميل النصاب وعنه أن الحبوب يضم بعضها إلى بعض وعنه تضم الحنطة إلى الشعير والقطنيات بعضها إلى بعض','1354','807','0');
INSERT INTO [t1153] VALUES (888,'مسألة: فإن سقى نصف السنة بهذا ونصفها بهذا ففيه ثلاثة أرباع العشر','1359','807','0');
INSERT INTO [t1153] VALUES (889,'مسألة: وإذا اشتد الحب وبدا الصلاح في الثمر وجبت الزكاة','1360','807','0');
INSERT INTO [t1153] VALUES (890,'مسألة: ولا يستقر الوجوب إلا بجعلها في الجرين ويجعل الزرع في البيدر فإن تلفت قبله بغير تعد منه سقطت الزكاة سواء كانت خرصت أو لم تخرص','1360','807','0');
INSERT INTO [t1153] VALUES (891,'مسألة: ومتى ادعى رب المال تلفها من غير تفريطه قبل قوله من غير يمين سواء كان ذلك قبل الخرص أو بعده','1361','807','0');
INSERT INTO [t1153] VALUES (892,'مسألة: ويجب إخراج زكاة الحب مصفى والثمر يابسا','1362','807','0');
INSERT INTO [t1153] VALUES (893,'مسألة: فإن احتيج إلى قطع الثمرة قبل كمالها وبعد بدو الصلاح للخوف من العطش أو لضعف الاصل جاز قطعها لأن حق الفقراء انما يجب على طريق المواساة فلا يكلف الإنسان ما يهلك أصل ماله','1363','807','0');
INSERT INTO [t1153] VALUES (894,'مسألة: وينبغي أن يبعث الامام ساعيا إذا بدا الصلاح في الثمر فيخرصه عليهم ليتصرفوا فيه فيعرف بذلك قدر الزكاة ويعرف المالك ذلك','1364','807','0');
INSERT INTO [t1153] VALUES (895,'مسألة: فإن كان أنواعا خرص كل نوع وحده','1365','807','0');
INSERT INTO [t1153] VALUES (896,'مسألة: وإن كان نوعا واحدا فله خرص كل شجرة وحدها','1365','807','0');
INSERT INTO [t1153] VALUES (897,'مسألة: ويجب أن يترك في الخرص لرب المال الثلث أو الربع','1367','807','0');
INSERT INTO [t1153] VALUES (898,'مسألة: فإن لم يفعل فلرب المال الأكل بقدر ذلك','1368','807','0');
INSERT INTO [t1153] VALUES (899,'مسألة: ويخرج العشر من كل نوع على حدته فإن شق ذلك أخذ من الوسط','1369','807','0');
INSERT INTO [t1153] VALUES (900,'مسألة: ويجب العشر على المستأجر دون المالك','1371','807','0');
INSERT INTO [t1153] VALUES (901,'مسألة: ويجتمع العشر والخراج في كل أرض فتحت عنوة','1372','807','0');
INSERT INTO [t1153] VALUES (902,'مسألة: ويجوز لأهل الذمة شراء الارض العشرية ولا عشر عليهم، وعنه عليهم عشران يسقط أحدهما بالاسلام','1374','807','0');
INSERT INTO [t1153] VALUES (903,'فصل في المعدن، مسألة: ومن استخرج من معدن نصابا من الاثمان أو ما قيمته','1378','807','0');
INSERT INTO [t1153] VALUES (904,'مسألة: ولا يجوز اخراجها إذا كانت اثمانا إلا بعد السبك والتصفية','1382','807','0');
INSERT INTO [t1153] VALUES (905,'مسألة: ولا زكاة فيما يخرج من البحر واللؤلؤ والمرجان ونحوه في أحد الوجهين','1382','807','0');
INSERT INTO [t1153] VALUES (906,'مسألة: قال: إن وجده في موات أو أرض لا يعلم مالكها وإن علم مالكها أو كانت منتقلة إليه','1387','807','0');
INSERT INTO [t1153] VALUES (907,'مسألة: والركاز ما وجد من دفن الجاهلية عليه علامتهم، فإن كان عليه علامة المسلمين أو لم تكن عليه علامة فهو لقطة','1390','807','0');
INSERT INTO [t1153] VALUES (908,'باب زكاة الاثمان','1392','807','0');
INSERT INTO [t1153] VALUES (909,'مسألة: ولا شيء في الذهب حتى يبلغ عشرين مثقالا فيه نصف مثقال','1393','807','0');
INSERT INTO [t1153] VALUES (910,'مسألة: قال: ولا في الفضة حتى تبلغ مائتي درهم فيجب فيها خمسة دراهم','1394','807','0');
INSERT INTO [t1153] VALUES (911,'مسألة: ولا زكاة في مغشوشهما حتى يبلغ قدر ما فيه نصابا','1396','807','0');
INSERT INTO [t1153] VALUES (912,'مسألة: فإن شك فيه خير بين سبكه وبين الاخراج','1396','807','0');
INSERT INTO [t1153] VALUES (913,'مسألة: ويخرج عن الجيد الصحيح من جنسه','1398','807','0');
INSERT INTO [t1153] VALUES (914,'مسألة: فإن أخرج مكسرا أو بهرجا وزاد قدر ما بينهما من الفضل جاز نص عليه','1400','807','0');
INSERT INTO [t1153] VALUES (915,'مسألة: وهل يضم الذهب إلى الفضة في تكميل النصاب أو يخرج أحدهما عن الآخر؟ على روايتين','1400','807','0');
INSERT INTO [t1153] VALUES (916,'مسألة: ويكون الضم بالاجزاء وقيل بالقيمة فيما فيه الحظ للمساكين','1404','807','0');
INSERT INTO [t1153] VALUES (917,'مسألة: وتضم قيمة العروض إلى كل واحد منهما','1406','807','0');
INSERT INTO [t1153] VALUES (918,'مسألة: فأما الحلي المحرم والآنية وما أعد للكرى والنفقة ففيه الزكاة اذا بلغ نصابا','1410','807','0');
INSERT INTO [t1153] VALUES (919,'مسألة: والاعتبار بوزنه إلا ما كان مباح الصناعة فإن الاعتبار في النصاب بوزنه وفي الاخراج بقيمته','1413','807','0');
INSERT INTO [t1153] VALUES (920,'مسألة: ويباح للرجال من الفضة الخاتم وقبيعة السيف، وفي حلية المنطقة روايتان وعلى قياسها الجوشن والخوذة والخف والران والحمائل','1415','807','0');
INSERT INTO [t1153] VALUES (921,'مسألة: ومن الذهب قبيعة السيف وما دعت إليه الضرورة كالأنف وما ربط به أسنانه وقال أبو بكر يباح يسير الذهب','1416','807','0');
INSERT INTO [t1153] VALUES (922,'مسألة: ويباح للنساء من الذهب والفضة كل ما جرت عادتهن بلبسه قل أو كثر وقال ابن حامد إن بلغ الف مثقال حرم وفيه الزكاة','1417','807','0');
INSERT INTO [t1153] VALUES (923,'باب زكاة العروض','1419','807','0');
INSERT INTO [t1153] VALUES (924,'مسألة: ويؤخذ منها لا من العروض','1424','807','0');
INSERT INTO [t1153] VALUES (925,'مسألة: ولا تصير للتجارة إلا أن يملكها بفعله بنية التجارة بها','1425','807','0');
INSERT INTO [t1153] VALUES (926,'مسألة: فإن ملكها بإرث أو ملكها بفعله بغير نية التجارة ثم نوى التجارة بها لم تصر للتجارة','1426','807','0');
INSERT INTO [t1153] VALUES (927,'مسألة: وإن كان عنده عرض للتجارة فنواه للقنية ثم نواه للتجارة لم يصر للتجارة، وعنه أن العروض تصير للتجارة بمجرد النية','1426','807','0');
INSERT INTO [t1153] VALUES (928,'مسألة: وتقوم العروض عند الحول بما هو أحظ للمساكين من عين أو ورق ولا يعتبر ما اشتريت به','1429','807','0');
INSERT INTO [t1153] VALUES (929,'مسألة: وإن اشترى عرضا بنصاب من الاثمان أو من العروض بنى على حوله','1431','807','0');
INSERT INTO [t1153] VALUES (930,'مسألة: وإن اشتراه بنصاب من السائمة لم يبن على حوله','1432','807','0');
INSERT INTO [t1153] VALUES (931,'مسألة: وإن ملك نصابا من السائمة للتجارة فعليه زكاة التجارة دون السوم فإن لم تبلغ قيمته نصاب التجارة فعليه زكاة السوم','1433','807','0');
INSERT INTO [t1153] VALUES (932,'مسألة: وإن اشترى أرضا أو نخلا للتجارة فأثمرت النخل أو زرعت الأرض فعليه فيهما العشر ويزكي الاصل للتجارة','1436','807','0');
INSERT INTO [t1153] VALUES (933,'مسألة: وإذا أذن كل واحد من الشريكين لصاحبه في إخراج زكاته أو أذن رجلان غير الشريكين كل واحد منهما للآخر في إخراج زكاته','1440','807','0');
INSERT INTO [t1153] VALUES (934,'مسألة: فإن أخرجها أحدهما قبل الآخر ضمن الثاني نصيب الاول علم أو لم يعلم','1441','807','0');
INSERT INTO [t1153] VALUES (935,'باب زكاة الفطر','1442','807','0');
INSERT INTO [t1153] VALUES (936,'مسألة: وهي واجبة على كل مسلم تلزمه مؤنة نفسه إذا فضل عنده عن قوته وقوت عياله يوم العيد وليلته صاع وان كان مكاتبا','1442','807','0');
INSERT INTO [t1153] VALUES (937,'مسألة: وإن فضل بعض صاع فهل يلزمه إخراجه؟ على روايتين','1445','807','0');
INSERT INTO [t1153] VALUES (938,'مسألة: ويلزمه فطرة من يمونه من المسلمين','1445','807','0');
INSERT INTO [t1153] VALUES (939,'مسألة: فإن لم يجد ما يؤدي عن جميعهم بدأ بنفسه ثم بامرأته ثم برقيقه ثم بولده ثم بأمه ثم بأبيه ثم بالاقرب فالاقرب في الميراث','1447','807','0');
INSERT INTO [t1153] VALUES (940,'مسألة: ويستحب الإخراج عن الجنين ولا يجب','1448','807','0');
INSERT INTO [t1153] VALUES (941,'مسألة: ومن تكفل بمؤنة شخص في شهر رمضان لم تلزمه فطرته عند أبي الخطاب والمنصوص انها تلزمه','1448','807','0');
INSERT INTO [t1153] VALUES (942,'مسألة: وإذا كان العبد بين شركاء فعليهم صاع وعنه على كل واحد صاع وكذلك الحكم فيمن بعضه حر','1449','807','0');
INSERT INTO [t1153] VALUES (943,'مسألة: وإن عجز زوج المرأة عن فطرتها فعليها أو على سيدها إن كانت أمة فطرتها ويحتمل أن لا تجب','1450','807','0');
INSERT INTO [t1153] VALUES (944,'مسألة: ومن كان له غائب أو آبق فعليه فطرته إلا أن يشك في حياته فتسقط','1450','807','0');
INSERT INTO [t1153] VALUES (945,'مسألة: وإن علم حياته بعد ذلك أخرج لما مضى','1451','807','0');
INSERT INTO [t1153] VALUES (946,'مسألة: ولا يلزم الزوج فطرة الناشز وقال أبو الخطاب تلزمه','1451','807','0');
INSERT INTO [t1153] VALUES (947,'مسألة: ومن لزم غيره فطرته فأخرج عن نفسه بغير إذنه فهل يجزئه على وجهين','1451','807','0');
INSERT INTO [t1153] VALUES (948,'مسألة: ولا يمنع الدين وجوب الفطرة إلا أن يكون مطالبا به','1452','807','0');
INSERT INTO [t1153] VALUES (949,'مسألة: وتجب بغروب الشمس من ليلة الفطر، فمن أسلم بعد ذلك أو ملك عبدا أو زوجة أو ولد له ولد لم تلزمه فطرته، وإن وجد ذلك قبل الغروب وجبت','1453','807','0');
INSERT INTO [t1153] VALUES (950,'مسألة: والأفضل إخراجها يوم العيد قبل الصلاة','1454','807','0');
INSERT INTO [t1153] VALUES (951,'مسألة: ويجوز إخراجها قبل العيد بيومين','1454','807','0');
INSERT INTO [t1153] VALUES (952,'مسألة: ويجوز في سائر اليوم لحصول الاغناء في اليوم إلأا أنه يكون قد ترك الافضل على ما ذكرنا','1455','807','0');
INSERT INTO [t1153] VALUES (953,'مسألة: ولا يجزىء غير ذلك إلا أن يعدمه فيخرج مما يقتات','1458','807','0');
INSERT INTO [t1153] VALUES (954,'مسألة: ولا يخرج حبا معيبا ولا خبزا','1459','807','0');
INSERT INTO [t1153] VALUES (955,'مسألة: ويجزىء إخراج صاع من أجناس','1459','807','0');
INSERT INTO [t1153] VALUES (956,'مسألة: وأفضل المخرج التمر ثم ما هو أنفع للفقراء بعده','1459','807','0');
INSERT INTO [t1153] VALUES (957,'مسألة: ويجوز أن يعطي الجماعة ما يلزم الواحد والواحد ما يلزم الجماعة','1460','807','0');
INSERT INTO [t1153] VALUES (958,'باب إخراج الزكاة','1462','807','0');
INSERT INTO [t1153] VALUES (959,'مسألة: فإن جحد وجوبها جهلا به عرف ذلك فإن أصر كفر وأخذت منه واستتيب ثلاثا فإن لم يتب قتل','1463','807','0');
INSERT INTO [t1153] VALUES (960,'مسألة: وإن منعها بخلا بها أخذت منه وعزر، فإن غيب ماله أو كتمه أو قاتل دونها وأمكن أخذها أخذت من غير زيادة، وقال أبو بكر: يأخذها وشطر ماله','1464','807','0');
INSERT INTO [t1153] VALUES (961,'مسألة: فإن لم يمكن أخذها استتيب ثلاثا فإن تاب وإلا قتل وأخذت من تركته، وقال بعض أصحابنا ان قاتل عليها كفر','1464','807','0');
INSERT INTO [t1153] VALUES (962,'مسألة: وإن ادعى ما يمنع وجوب الزكا من نقصان الحول، أو النصاب، أو انتقاله عنه في بعض الحول قبل قوله بغير يمين','1466','807','0');
INSERT INTO [t1153] VALUES (963,'مسألة: والصبي والمجنون يخرج عنهما وليهما','1466','807','0');
INSERT INTO [t1153] VALUES (964,'مسألة: ويستحب للإنسان تفرقة زكاته بنفسه ويجوز دفعها إلى الساعي، وعنه يستحب أن يدفع اليه العشر ويتولى تفريق الباقي','1467','807','0');
INSERT INTO [t1153] VALUES (965,'مسألة: وعند أبي الخطاب دفعها إلى الامامخ العادل أفضل','1467','807','0');
INSERT INTO [t1153] VALUES (966,'مسألة: ولا يجزىء اخراجها إلا بنية إلا أن يأخذها الامام منه قهرا. وقال أبو الخطاب لا تجزئه أيضا بغير نية','1469','807','0');
INSERT INTO [t1153] VALUES (967,'مسألة: وإن دفعها إلى وكيله اعتبرت النية في الموكل دون الوكيل','1471','807','0');
INSERT INTO [t1153] VALUES (968,'مسألة: ويستحب أن يقول عند الدفع اللهم اجعلها مغنما ولا تجعلها مغرما','1471','807','0');
INSERT INTO [t1153] VALUES (969,'مسألة: ويقول الآخذ آجرك الله فيما أعطيت وبارك لك فيما أبقيت وجعله لك طهورا','1471','807','0');
INSERT INTO [t1153] VALUES (970,'مسألة: ولا يجوز نقلها إلى بلد تقصر إليه الصلاة فإن فعل فهل تجزئه؟ على روايتين','1472','807','0');
INSERT INTO [t1153] VALUES (971,'مسألة: فإن كان في بلد وماله في آخر أخرج زكاة المال في بلده وفطرته في البلد الذي هو فيه','1473','807','0');
INSERT INTO [t1153] VALUES (972,'مسألة: وإذا حصل عند الامام ماشية استحب له وسم الابل في أفخاذها والغنم في آذانها فان كانت زكاة كتب لله أو زكاة، وإن كانت جزية كتب صغارا أو جزية','1474','807','0');
INSERT INTO [t1153] VALUES (973,'مسألة: وفي تعجيلها لأكثر من حول روايتان','1475','807','0');
INSERT INTO [t1153] VALUES (974,'مسألة: وإن عجلها عن النصاب وما يستفيده أجزأ عن النصاب دون الزيادة','1476','807','0');
INSERT INTO [t1153] VALUES (975,'مسألة: وإن عجل عشر الثمرة قبل طلوع الطلع والحصرم لم يجزه','1477','807','0');
INSERT INTO [t1153] VALUES (976,'مسألة: وإن عجل زكاة النصاب فتم الحول وهو ناقص قدر ما عجله جاز','1478','807','0');
INSERT INTO [t1153] VALUES (977,'مسألة: وإن عجل زكاة المائتين فنتجت عند الحول سخلة لزمته شاة ثالثة','1478','807','0');
INSERT INTO [t1153] VALUES (978,'مسألة: وإن عجلها ثم تلف المال لم يرجع على الآخذ. وقال ابن حامد: إن كان الدافع الساعي أو أعمله أنها زكاة معجلة رجع عليه','1479','807','0');
INSERT INTO [t1153] VALUES (979,'مسألة: وإن عجلها فدفعها إلى مستحقها فمات أو ارتد أو استغنى أجزأت عنه','1479','807','0');
INSERT INTO [t1153] VALUES (980,'مسألة: وإن دفعها إلى غني فافتقر عند الوجوب لم يجزه','1479','807','0');
INSERT INTO [t1153] VALUES (981,'باب ذكر أهل الزكاة','1481','807','0');
INSERT INTO [t1153] VALUES (982,'مسألة: الفقراء وهم الذين لا يجدون ما يقع موقعا من كفايتهم، الثاني المساكين وهم الذين يجدون معظم كفايتهم','1481','807','0');
INSERT INTO [t1153] VALUES (983,'مسألة: ومن ملك من غير الاثمان ما لا يقوم بكفايته فليس يغني وإن كثرت قيمته','1483','807','0');
INSERT INTO [t1153] VALUES (984,'مسألة: وإن كان من الاثمان فكذلك في إحدى الروايتين والاخرى إن ملك خمسين درهما أو قيمتها من الذهب فهو غني','1484','807','0');
INSERT INTO [t1153] VALUES (985,'مسألة: الثالث: العاملون عليها وهم الجباة لها والحافظون لها','1486','807','0');
INSERT INTO [t1153] VALUES (986,'مسألة: ويشترط أن يكون العامل مسلما أمينا من غير ذوي القربى ولا يشترط حريته ولا فقره. وقال القاضي: لا يشترط إسلامه ولا كونه من غير دوي القربى','1486','807','0');
INSERT INTO [t1153] VALUES (987,'مسألة: فإن تلفت الصدقة في يده من غير تفريط أعطي أجرته من بيت المال','1488','807','0');
INSERT INTO [t1153] VALUES (988,'مسألة: الرابع المؤلفة قلوبهم وهم السادة المضاعون في عشائرهم ممن يرجى إسلامه أو يخشى شره أو يرجى بعطيته قوة ايمانه أو اسلام نظيره أو جباية الزكاة ممن لا يعطيها أو الدفع عن المسلمين وعنه ان حكمهم انقطع','1488','807','0');
INSERT INTO [t1153] VALUES (989,'مسألة: ويجوز أن يشتري بها أسيرا مسلما نص عليه','1490','807','0');
INSERT INTO [t1153] VALUES (990,'مسألة: وهل يجوز أن يشتري بها رقبة يعتقها؟ على روايتين','1491','807','0');
INSERT INTO [t1153] VALUES (991,'مسألة: السادس الغارمون وهم المدينون وهم ضربان: ضرب غرم لإصلاح ذات البين، وضرب غرم لاصلاح نفسه في مباح','1492','807','0');
INSERT INTO [t1153] VALUES (992,'مسألة: السابع في سبيل الله وهم الغزاة الذين لا ديوان لهم','1493','807','0');
INSERT INTO [t1153] VALUES (993,'مسألة: ولا يعطى منها في الحج وعنه يعطي الفقير قدر ما يحج به الفرض أن يستعين به فيه','1494','807','0');
INSERT INTO [t1153] VALUES (994,'مسألة: الثامن ابن السبيل وهو المسافر المنقطع به دون المنشىء للسفر من بلده','1495','807','0');
INSERT INTO [t1153] VALUES (995,'مسألة: ويعطى الفقير والمسكين ما يغنيهما','1496','807','0');
INSERT INTO [t1153] VALUES (996,'مسألة: ويعطى العامل قدر أجرته','1496','807','0');
INSERT INTO [t1153] VALUES (997,'مسألة: والغارم والمكاتب ما يقضيان به دينهما','1496','807','0');
INSERT INTO [t1153] VALUES (998,'مسألة: والغازي ما يحتاج إليه لغزوه وإن كثر','1496','807','0');
INSERT INTO [t1153] VALUES (999,'مسألة: ولا يزاد أحد منهم على ذلك لما ذكرنا','1496','807','0');
INSERT INTO [t1153] VALUES (1000,'مسألة: ومن كان ذا عيال أخذ ما يكفيهم لما ذكرنا','1497','807','0');
INSERT INTO [t1153] VALUES (1001,'مسألة: ولا يعطى أحد منهم مع الغني إلأا أربعة: العامل والمؤلف والغارم لاصلاح ذات البين والغازي','1497','807','0');
INSERT INTO [t1153] VALUES (1002,'مسألة: وإن فضل مع المكاتب والغارم والغازي وابن السبيل شيء بعد حاجتهم لزمهم رده','1498','807','0');
INSERT INTO [t1153] VALUES (1003,'مسألة: وإن ادعى الفقر من عرف بالغنى لم يقبل قوله إلا ببينة','1499','807','0');
INSERT INTO [t1153] VALUES (1004,'مسألة: وإن ادعى أنه مكاتب، أو غارم، أو ابن سبيل لم يقبل قوله إلا ببينة','1499','807','0');
INSERT INTO [t1153] VALUES (1005,'مسألة: فإن صدق المكاتب سيده أو الغارم غريمه فعلى وجهين','1499','807','0');
INSERT INTO [t1153] VALUES (1006,'مسألة: وإن ادعى الفقر من لم يعرف بالغنى قبل قوله لان الاصل عدم الغنى','1499','807','0');
INSERT INTO [t1153] VALUES (1007,'مسألة: وإن ادعى ان له عيالا قلد وأعطى','1500','807','0');
INSERT INTO [t1153] VALUES (1008,'مسألة: ومن سافر أو غرم في معصية لم يدفع إليه شيء فإن تاب فعلى وجهين','1500','807','0');
INSERT INTO [t1153] VALUES (1009,'مسألة: ويستحب صرفها في الاصناف كلها فان اقتصر على انسان واحد أجزأه، وعنه لا يجزئه إلا ثلاثة من كل صنف إلا العامل فانه يجوز أن يكون واحدا','1501','807','0');
INSERT INTO [t1153] VALUES (1010,'مسألة: ويستحب صرفها إلى أقاربه الذين لا تلزمه مؤنتهم وتفريقها فيهم على قدر حاجتهم','1503','807','0');
INSERT INTO [t1153] VALUES (1011,'مسألة: ويجوز للسيد دفع زكاته إلى مكاتبه وإلى غريمه','1503','807','0');
INSERT INTO [t1153] VALUES (1012,'مسألة: ولا إلى الوالدين وإن علوا، ولا إلى الولد وإن سفل','1504','807','0');
INSERT INTO [t1153] VALUES (1013,'مسألة: قال: ولا إلى الزوجة','1505','807','0');
INSERT INTO [t1153] VALUES (1014,'مسألة: ولا بني هاشم ولا مواليهم','1505','807','0');
INSERT INTO [t1153] VALUES (1015,'مسألة: ويجوز لبني هاشم الأخذ من صدقة التطوع، ووصايا الفقراء، والنذور وفي الكفارة وجهان','1506','807','0');
INSERT INTO [t1153] VALUES (1016,'مسألة: وهل يجوز دفعها إلى سائر من تلزمه مؤنته من أقاربه أو إلى الزوج أو بني المطلب على روايتين','1507','807','0');
INSERT INTO [t1153] VALUES (1017,'مسألة: وإن دفعها إلى من لا يستحقها وهو لا يعلم ثم علم لم يجزه إلا الغني اذا ظنه فقيرا في احدى الروايتين','1510','807','0');
INSERT INTO [t1153] VALUES (1018,'مسألة: وأفضل ما تكون في شهر رمضان وأوقات الحاجات','1512','807','0');
INSERT INTO [t1153] VALUES (1019,'مسألة: وتستحب الصدقة بالفاضل عن كفايته وكفاية من يمونه على الدوام','1512','807','0');
INSERT INTO [t1153] VALUES (1020,'مسألة: ومن أراد الصدقة لماله كله وهو يعلم من نفسه حسن التوكل والصبر عن المسألة فله ذلك وإن لم يثق من نفسه بذلك كره له','1513','807','0');
INSERT INTO [t1153] VALUES (1021,'كتاب الصيام','1515','0','0');
INSERT INTO [t1153] VALUES (1022,'بسم الله الرحمن الرحيم كتاب الصيام','1515','1021','0');
INSERT INTO [t1153] VALUES (1023,'ويجب صوم رمضان برؤية الهلال فإن لم ير مع الصحو أكملوا عدة شعبان ثلاثين يوما ثم صاموا، فإن حال دون منظرة غيم أو قتر ليلة الثلاثين وجب صيام بنية رمضان في ظاهر المذهب وعنه لا يجب وعنه الناس تبع للإمام فإن صام صاموا','1516','1021','0');
INSERT INTO [t1153] VALUES (1024,'إذا رأى الهلال نهارا قبل الزوال أو بعده فهو لليلة المقبلة','1519','1021','0');
INSERT INTO [t1153] VALUES (1025,'وإذا رأى الهلال أهل بلد لزم الناس كلهم الصوم','1519','1021','0');
INSERT INTO [t1153] VALUES (1026,'ويقبل في هلال رمضان قول عدل واحد ولا يقبل في سائر الشهور إلا عدلان','1520','1021','0');
INSERT INTO [t1153] VALUES (1027,'وإذا صاموا بشهادة اثنين يوما فلم يروا الهلال أفطروا','1522','1021','0');
INSERT INTO [t1153] VALUES (1028,'وإن صاموا بشهادة واحد فلم يروا الهلال فعلى وجهين','1522','1021','0');
INSERT INTO [t1153] VALUES (1029,'ومن رأى هلال رمضان وحده وردت شهادته لزمه الصوم','1523','1021','0');
INSERT INTO [t1153] VALUES (1030,'وإن رأى الهلال شوال وحده لم يفطر','1523','1021','0');
INSERT INTO [t1153] VALUES (1031,'فإن صاموا لأجل الغيم لم يفطروا','1523','1021','0');
INSERT INTO [t1153] VALUES (1032,'وإن اشتبهت الأشهر على الأسير تحرى وصام فإن وافق الشهر أو ما بعده أجزأه وإن وافق قبله لم يجره','1524','1021','0');
INSERT INTO [t1153] VALUES (1033,'ولا يجب الصوم إلا على المسلم البالغ العاقل القادر على الصوم ولا يجب على كافر ولا مجنون ولا صبي','1526','1021','0');
INSERT INTO [t1153] VALUES (1034,'ويؤمر به إذا أطاقه ويضرب عليه ليعتاده','1526','1021','0');
INSERT INTO [t1153] VALUES (1035,'وإذا قامت البينة بالرؤية في أثناء النهار لزمهم الإمساك والقضاء','1527','1021','0');
INSERT INTO [t1153] VALUES (1036,'وإن بلغ صبي أو أسلم كافر أو أفاق مجنون فكذلك وعنه لا يلزمهم شيء','1527','1021','0');
INSERT INTO [t1153] VALUES (1037,'وإن بلغ الصبي صائما أتم ولا قضاء عليه عند القاضي وعن أبي الخطاب عليه القضاء','1528','1021','0');
INSERT INTO [t1153] VALUES (1038,'وإن طهرت حائض أو نفساء أو قدم المسافر مفطرا فعليهم القضاء وفي الإمساك روايتان','1529','1021','0');
INSERT INTO [t1153] VALUES (1039,'ومن عجز عن الصوم لكبر أو مرض لا يرجى برؤه أفطر وأطعم عن كل يوم مسكينا','1529','1021','0');
INSERT INTO [t1153] VALUES (1040,'والمريض إذا خاف الضرر والمسافر استحب لهما الفطر، فإن صاما أجزاهما','1530','1021','0');
INSERT INTO [t1153] VALUES (1041,'ولا يجوز أن يصوما في رمضان عن غيره','1533','1021','0');
INSERT INTO [t1153] VALUES (1042,'وإن نوى الحاضر صوم يوم ثم سافر في أثنائه فله الفطر وعنه لا يباح','1534','1021','0');
INSERT INTO [t1153] VALUES (1043,'والحامل والمرضع إذا خافتا الضرر على أنفسهما أفطرتا وقضتا وإن خافتا على ولديهما أفطرتا وقضتا وأطعمتا عن كل يوم مسكينا','1535','1021','0');
INSERT INTO [t1153] VALUES (1044,'ومن نوى قبل الفجر ثم جن أو أغمي عليه جميع النهار لم يصح صومه وإن أفاق جزاءا منه صح صومه','1537','1021','0');
INSERT INTO [t1153] VALUES (1045,'وإن نام جميع النهار صح صومه','1538','1021','0');
INSERT INTO [t1153] VALUES (1046,'ويلزم المغمى عليه القضاء دون المجنون','1538','1021','0');
INSERT INTO [t1153] VALUES (1047,'ولا يحتاج إلى نية الفرضية وقال ابن حامد يجب ذلك','1542','1021','0');
INSERT INTO [t1153] VALUES (1048,'ولو نوى إن كان غدا من رمضان فهو فرضي','1542','1021','0');
INSERT INTO [t1153] VALUES (1049,'ومن نوى الإفطار أفطر','1543','1021','0');
INSERT INTO [t1153] VALUES (1050,'ويصح صوم النفل بنية من النهار قبل الزوال وبعده وقال القاضي: لا يجزي بعد الزوال','1545','1021','0');
INSERT INTO [t1153] VALUES (1051,'باب ما يفسد الصوم ويوجب الكفارة','1550','1021','0');
INSERT INTO [t1153] VALUES (1052,'أو استقاء أو استمنى','1553','1021','0');
INSERT INTO [t1153] VALUES (1053,'قال أو قبل أو لمس فأمنى أو مذي','1554','1021','0');
INSERT INTO [t1153] VALUES (1054,'أو كرر النظر فانزل','1555','1021','0');
INSERT INTO [t1153] VALUES (1055,'قال أو حجم أو احتجم','1556','1021','0');
INSERT INTO [t1153] VALUES (1056,'فإن فكر فأنزل لم يفسد صومه','1559','1021','0');
INSERT INTO [t1153] VALUES (1057,'إن طار إلى حلقه ذباب، أو غبار، أو قطر أو احليله','1560','1021','0');
INSERT INTO [t1153] VALUES (1058,'قال أو أصبح وفي فيه طعام فلفظه','1561','1021','0');
INSERT INTO [t1153] VALUES (1059,'أو اغتسل أو تمضمض أو استنشق فدخل الماء حلقه لم يفسد صومه','1562','1021','0');
INSERT INTO [t1153] VALUES (1060,'وإن أكل شاكا في طلوع الفجر فلا قضاء عليه','1564','1021','0');
INSERT INTO [t1153] VALUES (1061,'وإن أكل شاكا في غروب الشمس فعليه القضاء','1565','1021','0');
INSERT INTO [t1153] VALUES (1062,'ومن أكل معتقدا أنه ليل فبان نهارا فعليه القضاء','1565','1021','0');
INSERT INTO [t1153] VALUES (1063,'ولا يلزم المرأة كفارة مع العذر وهل يلزمها مع عدمه على روايتين','1571','1021','0');
INSERT INTO [t1153] VALUES (1064,'قال وكل أمر غلب عليه الصائم فليس عليه قضاء ولا كفارة','1572','1021','0');
INSERT INTO [t1153] VALUES (1065,'وإن جامع فيما دون الفرج فأنزل أو وطئ بهيمة في الفرج أفطر وفي الكفارة وجهان','1574','1021','0');
INSERT INTO [t1153] VALUES (1066,'وإن جامع في يوم رأى الهلال في ليلته وردت شهادته فعليه القضاء والكفارة وهو قول الشافعي، وقال أبو حنيفة لا تجب لأنها عقوبة فلم تجب بفعل مختلف فيه كالحد','1575','1021','0');
INSERT INTO [t1153] VALUES (1067,'وإن جامع في يومين ولم يكفر فهل يلزمه كفارة أو كفارتان على وجهين','1576','1021','0');
INSERT INTO [t1153] VALUES (1068,'وإن جامع ثم كفر ثم جامع في يومه فعليه كفارة ثانية نص عليه، وكذلك كل من لزمه الإمساك إذا جامع','1576','1021','0');
INSERT INTO [t1153] VALUES (1069,'وإن جامع وهو صحيح ثم مرض، أو جن، أو سافر لم تسقط عنه','1578','1021','0');
INSERT INTO [t1153] VALUES (1070,'ولا تجب الكفارة بغير الجماع في نهار رمضان','1580','1021','0');
INSERT INTO [t1153] VALUES (1071,'وإن نوى الصوم في سفره ثم جامع فلا كفارة عليه وعنه عليه الكفارة','1580','1021','0');
INSERT INTO [t1153] VALUES (1072,'والكفارة عتق رقبة، فإن لم يجد فصيام شهرين متتابعين، فإن لم يستطع فإطعام ستين مسكينا','1581','1021','0');
INSERT INTO [t1153] VALUES (1073,'فإن لم يجد سقطت عنه، وعنه لا تسقط وعنه أن الكفارة على التخيير فبأيها كفر أجزأه','1584','1021','0');
INSERT INTO [t1153] VALUES (1074,'باب ما يكره وما يستحب وحكم القضاء','1585','1021','0');
INSERT INTO [t1153] VALUES (1075,'ويكره ذوق الطعام وإن وجد طعمه في حلقه أفطر','1587','1021','0');
INSERT INTO [t1153] VALUES (1076,'ويكره مضغ العلك الذي يتحلل منه أجزاء ولا يجوز مضغ ما يتحلل منه أجزاء إلا أن لا يبلع ريقه وإن وجد طعمه في حلقه أفطر','1588','1021','0');
INSERT INTO [t1153] VALUES (1077,'وتكره القبلة إلا أن يكون ممن لا تحرك شهوته في إحدى الروايتين','1590','1021','0');
INSERT INTO [t1153] VALUES (1078,'ويجب عليه اجتناب الكذب والغيبة والشتم فإن شتم استحب أن يقول إني صائم','1592','1021','0');
INSERT INTO [t1153] VALUES (1079,'فصل','1593','1021','0');
INSERT INTO [t1153] VALUES (1080,'ويستحب تأخير السحور','1594','1021','0');
INSERT INTO [t1153] VALUES (1081,'يستحب التتابع في قضاء رمضان ولا يجب','1597','1021','0');
INSERT INTO [t1153] VALUES (1082,'فإن فعل فعليه القضاء وإطعام مسكين لكل يوم','1598','1021','0');
INSERT INTO [t1153] VALUES (1083,'وإن أخر لعذر فلا شيء عليه وإن مات','1599','1021','0');
INSERT INTO [t1153] VALUES (1084,'وإن أخره لغير عذر فمات قبل أن أدركه رمضان آخر','1600','1021','0');
INSERT INTO [t1153] VALUES (1085,'ومن مات وعليه صوم منذور أو حج أو اعتكاف فعله عنه وليه وإن كانت صلاة منذورة فعلى روايتين','1605','1021','0');
INSERT INTO [t1153] VALUES (1086,'باب صوم التطوع','1607','1021','0');
INSERT INTO [t1153] VALUES (1087,'ويستحب صيام أيام البيض من كل شهر وصوم الاثنين والخميس','1607','1021','0');
INSERT INTO [t1153] VALUES (1088,'ومن صام رمضان وأتبعه بست من شوال فكأنما صام الدهر','1609','1021','0');
INSERT INTO [t1153] VALUES (1089,'وصيام يوم عاشوراء كفارة سنة يوم عرفة كفارة سنيتين','1611','1021','0');
INSERT INTO [t1153] VALUES (1090,'ويستحب صيام عشر ذي الحجة','1614','1021','0');
INSERT INTO [t1153] VALUES (1091,'وأفضل الصيام بعد شهر رمضان شهر الله المحرم','1615','1021','0');
INSERT INTO [t1153] VALUES (1092,'ويكره إفراد يوم الجمعة ويوم السبت ويوم الشك ويوم النيروز والمهرجان إلا أن يوافق عادة','1615','1021','0');
INSERT INTO [t1153] VALUES (1093,'ويكره أفراد رجب بالصوم','1615','1021','0');
INSERT INTO [t1153] VALUES (1094,'ولا يجوز صوم العيدين','1622','1021','0');
INSERT INTO [t1153] VALUES (1095,'ولا يجوز صيام أيام التشريق تطوعا وفي صيامها عن الفرض روايتان','1623','1021','0');
INSERT INTO [t1153] VALUES (1096,'ومن شرع في صوم أو صلاة تطوعا استحب له إتمامه ولا يلزمه، فإن أفسده فلا قضاء عليه','1624','1021','0');
INSERT INTO [t1153] VALUES (1097,'وتطلب ليلة القدر في العشر الأواخر من رمضان وليالي الوتر آكدها','1628','1021','0');
INSERT INTO [t1153] VALUES (1098,'وأرجاها ليلة سبع وعشرين','1630','1021','0');
INSERT INTO [t1153] VALUES (1099,'ويستحب أن يجتهد فيها في الدعاء','1633','1021','0');
INSERT INTO [t1153] VALUES (1100,'كتاب الاعتكاف','1634','0','0');
INSERT INTO [t1153] VALUES (1101,'كتاب الاعتكاف','1634','1100','0');
INSERT INTO [t1153] VALUES (1102,'وهو سنة إلا أن ينذره فيجب','1634','1100','0');
INSERT INTO [t1153] VALUES (1103,'ويصح بغير صوم وعنه لا يصح فعلى هذا لا يصح في ليلة مفردة ولا بعض يوم','1637','1100','0');
INSERT INTO [t1153] VALUES (1104,'وليس للمرأة الاعتكاف إلا بإذن زوجها ولا للعبد إلا بإذن سيده','1638','1100','0');
INSERT INTO [t1153] VALUES (1105,'فإن شرعا فيه بغير إذن فلهما تحليهما وإن كان بإذن فلهما تحليهما إن كان تطوعا وإلا فلا','1639','1100','0');
INSERT INTO [t1153] VALUES (1106,'وللمكاتب أن يحج ويعتكف بغير إذن سيده','1640','1100','0');
INSERT INTO [t1153] VALUES (1107,'ومن بعضه حر إن كان بينهما مهايأة فله أن يعتكف في نوبته بغير إذن سيده','1640','1100','0');
INSERT INTO [t1153] VALUES (1108,'ولا يجوز الاعتكاف إلا في مسجد يجمع فيه إلا المرأة لها الاعتكاف في كل مسجد إلا مسجد بيتها','1641','1100','0');
INSERT INTO [t1153] VALUES (1109,'والأفضل الاعتكاف في الجامع إذا كانت الجمعة تتخلله','1645','1100','0');
INSERT INTO [t1153] VALUES (1110,'وإذا نذر الاعتكاف أو الصلاة في مسجد فله فعله في غيره، ولا كفارة عليه إلا المساجد الثلاثة','1645','1100','0');
INSERT INTO [t1153] VALUES (1111,'وافضلها المسجد الحرام ثم مسجد المدينة ثم المسجد الأقصى','1646','1100','0');
INSERT INTO [t1153] VALUES (1112,'فإن نذره في الأفضل لم يكن له فعله في غيره وإن نذره في غيره فله فعليه فيه','1647','1100','0');
INSERT INTO [t1153] VALUES (1113,'وإن نذر اعتكاف شهر بعينه لزمه الشروع فيه قبل دخول ليلته إلى انقضائه','1648','1100','0');
INSERT INTO [t1153] VALUES (1114,'وإن نذر شهرا مطلقا لزمه شهر متتابع','1650','1100','0');
INSERT INTO [t1153] VALUES (1115,'وإن نذر أياما معدودة فله تفريقها إلا عند القاضي','1651','1100','0');
INSERT INTO [t1153] VALUES (1116,'وإن نذر أياما أو ليالي متتابعة لزمه ما يتخللها من ليل أو نهار','1651','1100','0');
INSERT INTO [t1153] VALUES (1117,'ولا يعود مريضا ولا يشهد جنازة إلا أن يشترطه فيجوز وعنه له ذلك من غير شرط','1660','1100','0');
INSERT INTO [t1153] VALUES (1118,'وله السؤال عن المريض في طريقه ما لم يعرج لأن النبي صلى الله عليه وسلم كان يفعله','1663','1100','0');
INSERT INTO [t1153] VALUES (1119,'فإن خرج لما لا بد منه خروجا معتادا لحاجة الإنسان فلا شيء عليه لأنه لا بد له منه','1663','1100','0');
INSERT INTO [t1153] VALUES (1120,'وإن خرج لغير المعتاد في المتتابع وتطاول خير بين استئنافه وإتمامه مع كفارة يمين وإن فعله في معين قضى وفي الكفارة وجهان','1663','1100','0');
INSERT INTO [t1153] VALUES (1121,'وإن خرج لما له منه بد في المتتابع لزمه استئنافه وإن فعله في معين فعليه الكفارة وفي الاستئناف وجهان','1665','1100','0');
INSERT INTO [t1153] VALUES (1122,'وإن وطئ المعتكف في الفرج فسد اعتكافه ولا كفارة عليه إلا لترك نذره، وقال أبو بكر عليه كفارة يمين وقال القاضي عليه كفارة الظهار','1667','1100','0');
INSERT INTO [t1153] VALUES (1123,'وإن باشر فيما دون الفرج فأنزل فسد اعتكافه وإلا فلا','1669','1100','0');
INSERT INTO [t1153] VALUES (1124,'ويستحب للمعتكف التشاغل بفعل القربة واجتناب ما لا يعنيه','1670','1100','0');
INSERT INTO [t1153] VALUES (1125,'ولا يستحب له اقراء القرآن والعلم والمناظرة فيه إلا عند الخطاب إذا قصد به الطاعة','1673','1100','0');
INSERT INTO [t1153] VALUES (1126,'كتاب المناسك','1676','0','0');
INSERT INTO [t1153] VALUES (1127,'كتاب المناسك','1676','1126','0');
INSERT INTO [t1153] VALUES (1128,'وإنما يجب الحج والعمرة بخمسة شروط: الإسلام والعقل والبلوغ والحرية والاستطاعة','1678','1126','0');
INSERT INTO [t1153] VALUES (1129,'إلا أن يبلغ ويعتق في الحج قبل الخروج من عرفة وفي طوافها فيجزئهما','1680','1126','0');
INSERT INTO [t1153] VALUES (1130,'ويحرم الصبي المميز بإذن وليه وغير المميز يحرم عنه وليه ويفعل ما يعجز عنه من عمله','1681','1126','0');
INSERT INTO [t1153] VALUES (1131,'ونفقة الحج وكفاراته في مال وليه وعنه في مال الصبي','1684','1126','0');
INSERT INTO [t1153] VALUES (1132,'وليس للعبد الإحرام إلا بإذن سيده ولا للمرأة الإحرام نفلا إلا بإذن زوجها','1685','1126','0');
INSERT INTO [t1153] VALUES (1133,'وليس للرجل منع امرأته من حج الفرض ولا تحليلها إن أحرمت به بغير خلاف','1688','1126','0');
INSERT INTO [t1153] VALUES (1134,'ولا يصير مستطيعا ببذل غيره بحال','1693','1126','0');
INSERT INTO [t1153] VALUES (1135,'فمن كملت له هذه الشروط وجب عليه الحج على الفور','1694','1126','0');
INSERT INTO [t1153] VALUES (1136,'فإن عجز عنه لكبر أو مرض لا يرجى برؤه لزمه أن يقيم من يحج عنه ويعتمر من بلده وقد أجزأ عنه وإن عوفي','1695','1126','0');
INSERT INTO [t1153] VALUES (1137,'فصول في مخالفة النائب','1702','1126','0');
INSERT INTO [t1153] VALUES (1138,'ومن قدر على السعي لزمه ذلك إذا كان في وقت المسير ووجد طريقا آمنا لا خفارة فيه وجد فيه الماء والعلف على المعتاد','1705','1126','0');
INSERT INTO [t1153] VALUES (1139,'ومن وجب عليه الحج فتوفي قبله أخرج عنه من جميع ماله حجة وعمرة','1708','1126','0');
INSERT INTO [t1153] VALUES (1140,'فإن ضاق ماله عن ذلك أو كان عليه دين أخذ للحج بحصته وحج به','1710','1126','0');
INSERT INTO [t1153] VALUES (1141,'فإن وصى بحج تطوع ولم يف ثلثه بالحج من بلده حج به من حيث يبلغ','1711','1126','0');
INSERT INTO [t1153] VALUES (1142,'فإن مات المحرم في الطريق مضت في حجها ولم تصر محصرة','1718','1126','0');
INSERT INTO [t1153] VALUES (1143,'ولا يجوز لمن لم يحج عن نفسه أن يحج عن غيره ولا نذره ولا نافلة فإن فعل انصرف إلى حجة الإسلام، وعنه يقع ما نواه','1718','1126','0');
INSERT INTO [t1153] VALUES (1144,'وهل يجوز لمن يقدر على الحج بنفسه أن يستنيب في حج التطوع على روايتين','1722','1126','0');
INSERT INTO [t1153] VALUES (1145,'باب المواقيت','1725','1126','0');
INSERT INTO [t1153] VALUES (1146,'ومن منزله دون الميقات فميقاته من موضعه يعني إذا كان مسكنه أقرب إلى مكة من الميقات كان ميقاته مسكنه','1728','1126','0');
INSERT INTO [t1153] VALUES (1147,'وأهل مكة إذا أرادوا العمرة فمن الحل، وإن أرادوا الحج فمن مكة','1729','1126','0');
INSERT INTO [t1153] VALUES (1148,'ومن لم يكن طريقه على ميقات فإذا حاذى أقرب المواقيت إليه أحرم','1732','1126','0');
INSERT INTO [t1153] VALUES (1149,'ولا يجوز لمن أراد دخول مكة تجاوز الميقات بغير إحرام إلا لقتال مباح أو حاجة متكررة كالحطاب ونحوه ثم إن بدا له النسك أحرم من موضعه','1733','1126','0');
INSERT INTO [t1153] VALUES (1150,'ومن جاوزه مريدا للنسك غير محرم رجع من الميقات فأحرم منه','1736','1126','0');
INSERT INTO [t1153] VALUES (1151,'والاختيار أن لا يحرم قبل ميقاته ولا يحرم بالحج قبل أشهره فإن فعل فهو محرم','1738','1126','0');
INSERT INTO [t1153] VALUES (1152,'وأشهر الحج شوال وذو القعدة وعشر من ذي الحجة وهو ميقات الزمان للحج','1741','1126','0');
INSERT INTO [t1153] VALUES (1153,'باب الإحرام','1743','1126','0');
INSERT INTO [t1153] VALUES (1154,'ويصلي ركعتين ويحرم عقيبهما','1747','1126','0');
INSERT INTO [t1153] VALUES (1155,'وينوي الإحرام بنسك معين ولا ينعقد إلا بالنية','1748','1126','0');
INSERT INTO [t1153] VALUES (1156,'ويشترط فيقول اللهم ني أريد النسك الفلاني فيسره لي وتقبله مني وإن حبسني حابس فمحلي حيث حبستني','1749','1126','0');
INSERT INTO [t1153] VALUES (1157,'وهو مخير بين التمتع والإفراد والقران','1751','1126','0');
INSERT INTO [t1153] VALUES (1158,'وأفضلها التمتع ثم الإفراد ثم القران، وعنه إن ساق الهدي فالقران أفضل ثم التمتع','1751','1126','0');
INSERT INTO [t1153] VALUES (1159,'وصفة التمتع أن يحرم بالعمرة في أشهر الحج ويفرغ منها ويحرم بالحج من مكة أو من قريب منها في عامة','1756','1126','0');
INSERT INTO [t1153] VALUES (1160,'ويجب على المتمتع والقارن دم نسك إذا لم يكونا من حاضري المسجد الحرام وهم أهل مكة ومن كان منها دون مسافة القصر','1757','1126','0');
INSERT INTO [t1153] VALUES (1161,'والدم الواجب شاة أو سبع بدنة أو بدنة فإن نحر بدنة أو ذبح بقرة فقد زاد خيرا','1758','1126','0');
INSERT INTO [t1153] VALUES (1162,'ومن كان مفردا أو قارنا أحببنا له أن يفسخ إذا طاف وسعى ويجعلها عمره لأمر رسول الله صلى الله عليه وسلم أصحابه بذلك إلا أن يكون معه هدي فيكون على إحرامه','1765','1126','0');
INSERT INTO [t1153] VALUES (1163,'لو ساق المتمتع الهدي لم يكن له أن يحل','1768','1126','0');
INSERT INTO [t1153] VALUES (1164,'والمرأة إذا دخلت متمتعة فحاضت فخشيت فوات الحج أحرمت بالحج وصارت قارنة','1769','1126','0');
INSERT INTO [t1153] VALUES (1165,'ومن أحرم مطلقا وله صرفه إلى ما شاء','1772','1126','0');
INSERT INTO [t1153] VALUES (1166,'وإن أحرم بمثل ما أحرم به فلان انعقد إحرامه بمثله','1772','1126','0');
INSERT INTO [t1153] VALUES (1167,'وإن أحرم بحجتين أو عمرتين انعقد إحرامه بإحداهما','1773','1126','0');
INSERT INTO [t1153] VALUES (1168,'وإن أحرم بنسك ونسيه جعله عمرة وقال القاضي يصرفه إلى ما شاء','1773','1126','0');
INSERT INTO [t1153] VALUES (1169,'وإن أحرم عن رجلين وقع عن نفسه','1774','1126','0');
INSERT INTO [t1153] VALUES (1170,'وإن أحرم عن أحدهما لا بعينه وقع عن نفسه','1775','1126','0');
INSERT INTO [t1153] VALUES (1171,'وإذا استوى على راحلته لبى تلبية رسول الله صلى الله عليه وسلم: لبيك لا شريك لك لبيك إن الحمد والنعمة لك والملك','1775','1126','0');
INSERT INTO [t1153] VALUES (1172,'والتلبية سنة ويستحب رفع الصوت بها والإكثار منها والدعاء بعدها','1776','1126','0');
INSERT INTO [t1153] VALUES (1173,'ويلبي إذا علا نشزا أو هبط واديا وفي دبر الصلوات المكتوبات. وإقبال الليل والنهار وإذا التقت الرفاق','1779','1126','0');
INSERT INTO [t1153] VALUES (1174,'ولا ترفع المرأة صوتها بالتلبية إلا بقدر ما تسمع نفسها','1780','1126','0');
INSERT INTO [t1153] VALUES (1175,'باب محظورات الإحرام وهي تسعة','1781','1126','0');
INSERT INTO [t1153] VALUES (1176,'تقليم الأظفار','1781','1126','0');
INSERT INTO [t1153] VALUES (1177,'فمن حلق أو قلم ثلاثة فعليه دم وعنه لا يجب إلا في أربع فصاعدا','1781','1126','0');
INSERT INTO [t1153] VALUES (1178,'وفيما دون ذلك في كل واحد مد من طعام وعنه قبضة وعنه درهم','1783','1126','0');
INSERT INTO [t1153] VALUES (1179,'وإن حلق رأسه بإذنه فالفدية عليه، وإن كان مكرها أو نائما فالفدية على الحالق','1785','1126','0');
INSERT INTO [t1153] VALUES (1180,'وإن حلق محرم رأس حلال فلا فدية عليه','1786','1126','0');
INSERT INTO [t1153] VALUES (1181,'وقطع الشعر ونتفه كحلقة وشعر الرأس والبدن واحد وعنه لكل واحد حكم مفرد','1786','1126','0');
INSERT INTO [t1153] VALUES (1182,'وإن خرج في عينيه شعر فقلعه أو نزل شعرة فغطى عينيه فقصه أو انكسر ظفره فقصه أو قلع جلدا عليه شعر فلا فديه عليه','1787','1126','0');
INSERT INTO [t1153] VALUES (1183,'وإن استظل بالمحمل','1789','1126','0');
INSERT INTO [t1153] VALUES (1184,'وإن حمل على رأسه شيئا، أو نصيب حياله ثوبا، أو استظل بخيمة، أو شجرة، أو بيت فلا شيء عليه','1790','1126','0');
INSERT INTO [t1153] VALUES (1185,'وفي تغطية الوجه روايتان','1791','1126','0');
INSERT INTO [t1153] VALUES (1186,'لبس المخيط والخفين','1792','1126','0');
INSERT INTO [t1153] VALUES (1187,'إلا أن لا يجد إزارا فيلبس سراويل أو لا يجد نعلين فيلبس خفين ولا يقطعهما ولا فدية عليه','1793','1126','0');
INSERT INTO [t1153] VALUES (1188,'ولا يعقد عليه منطقة ولا رداء ولا غيره إلا إزاره وهميانه الذي فيه نفقته إذا لم يثبت إلا بالعقد','1797','1126','0');
INSERT INTO [t1153] VALUES (1189,'وإن طرح على كتفيه قباء عليه الفدية','1799','1126','0');
INSERT INTO [t1153] VALUES (1190,'ويتقلد بالسيف عند الضرورة','1799','1126','0');
INSERT INTO [t1153] VALUES (1191,'وشم المسك والكافور والعنبر والزعفران والورس والتبخر بالعود وأكل ما فيه الطيب يظهر طعمه','1801','1126','0');
INSERT INTO [t1153] VALUES (1192,'وإن مس من الطيب ما لا يعلق بيده فلا فديه عليه','1802','1126','0');
INSERT INTO [t1153] VALUES (1193,'وله شم العود والفواكه والشيح والخزامى','1802','1126','0');
INSERT INTO [t1153] VALUES (1194,'وفي شم الريحان والنرجس والورد والبنفسج والبرم ونحوها والادهان بدهن غير مطيب في رأسه روايتان','1803','1126','0');
INSERT INTO [t1153] VALUES (1195,'وإن جلس عند العطار أو في موضع ليشم الطيب فشمه فعليه الفدية وإلا فلا','1805','1126','0');
INSERT INTO [t1153] VALUES (1196,'فمن أتلفه أو تلف في يده أو أتلف جزءا منه فعليه جزاؤه','1807','1126','0');
INSERT INTO [t1153] VALUES (1197,'ويضمن ما دل عليه أو أشار إليه أو أعان على ذبحه أو كان له أثر في ذبحه مثل أن يعيره سكينا لا أن يكون القاتل محرما فيكون جزاؤه بنيهما','1808','1126','0');
INSERT INTO [t1153] VALUES (1198,'ويحرم عليه الأكل من ذلك كله وأكل ما صيد لأجله ولا يحرم عليه الأكل من غير ذلك','1811','1126','0');
INSERT INTO [t1153] VALUES (1199,'وإن أتلف بيض صيد أو نقله إلى موضع آخر ففسد فعليه ضمانه بقيمته','1815','1126','0');
INSERT INTO [t1153] VALUES (1200,'ولا يملك الصيد بغير الإرث وقيل لا يملكه به أيضا','1817','1126','0');
INSERT INTO [t1153] VALUES (1201,'وإن أمسك صيدا حتى تحلل ثم تلف أو ذبحه ضمنه وكان ميتة وقال أبو الخطاب له أكله','1817','1126','0');
INSERT INTO [t1153] VALUES (1202,'وإن أحرم وفي يده صيد أو دخل الحرم بصيد لزمه إزالة يده المشاهدة دون الحكمية عنه','1818','1126','0');
INSERT INTO [t1153] VALUES (1203,'وإن قتل صيدا صائلا عليه دفعا عن نفسه أو تخليصه من سبع أو شبكة ليطلقه فتلف لم يضمنه وقيل يضمنه فيهما','1820','1126','0');
INSERT INTO [t1153] VALUES (1204,'ولا تأثير للحرم ولا للإحرام في تحريم حيوان انسي ولا محرم الأكل إلا القمل على المحرم في رواية وأي شيء تصدق به كان خيرا منه','1821','1126','0');
INSERT INTO [t1153] VALUES (1205,'ولا يحرم صيد البحر على المحرم وفي إباحته في الحرم رايتان','1826','1126','0');
INSERT INTO [t1153] VALUES (1206,'ويضمن الجراد بقيمته فإن انفرش في طريقه فقتله بالمشي عله ففي الجزاء وجهان وعنه لا ضمان في الجراد','1828','1126','0');
INSERT INTO [t1153] VALUES (1207,'ومن اضطر إلى أكل الصيد واحتاج إلى شيء من هذه المحظورات فله فعله وعليه الفداء','1829','1126','0');
INSERT INTO [t1153] VALUES (1208,'وعليهما المضي في فاسده والقضاء على الفور من حيث أحرما أولا ونفقة المرأة في القضاء عليها إن طاوعت وإن أكرهت فعلى الزوج','1835','1126','0');
INSERT INTO [t1153] VALUES (1209,'ويتفرقان في القضاء من الموضع الذي أصابها فيه إلى أن يحلا وهو واجب أو مستحب على وجهين','1836','1126','0');
INSERT INTO [t1153] VALUES (1210,'وإن جامع بعد التحلل الأول لم يفسد نسكه ويمضي إلى التنعيم فيحرم ليطوف وهو محرم وهل يلزمه بدنة أو شاة على الروايتين','1838','1126','0');
INSERT INTO [t1153] VALUES (1211,'ولا تلبس القفازين ولا الخلخال ولا تكتحل بالأثمد','1843','1126','0');
INSERT INTO [t1153] VALUES (1212,'ويجوز لبس المعصفر والكحلي والخضاب بالحناء النظر في المرآة لهما جميعا','1845','1126','0');
INSERT INTO [t1153] VALUES (1213,'باب الفدية','1849','1126','0');
INSERT INTO [t1153] VALUES (1214,'النوع الثاني جزاء الصيد يتخير فيه بين المثل وتقويمه بدراهم يشتري بها طعاما فيطعم لكل مسكين مدا','1850','1126','0');
INSERT INTO [t1153] VALUES (1215,'فإن لم يصم قبل يوم النحر صام أيام منى وعنه لا يصومها ويصوم بعد ذلك عشرة أيام وعليه دم','1855','1126','0');
INSERT INTO [t1153] VALUES (1216,'إن أخر الصوم أو الهدي لعذر لم يلزمه إلا قضاؤه وإن أخر الهدي لغير عذر فهل يلزمه دم آخر','1856','1126','0');
INSERT INTO [t1153] VALUES (1217,'ومتى وجب عليه الصوم فشرع فيه ثم قدر على الهدي لم يلزمه الانتقال إليه إلا أن يشاء','1857','1126','0');
INSERT INTO [t1153] VALUES (1218,'وإن وجب ولم يشرع فهل يلزمه الانتقال','1857','1126','0');
INSERT INTO [t1153] VALUES (1219,'النوع الثاني المحصر يلزمه الهدي فإن لم يجد صام عشرة أيام ثم حل','1858','1126','0');
INSERT INTO [t1153] VALUES (1220,'النوع الثالث فدية الوطء','1858','1126','0');
INSERT INTO [t1153] VALUES (1221,'ويجب بالوطء في الفرج بدنة إن كان في الحج وشاة إن كان في العمرة','1858','1126','0');
INSERT INTO [t1153] VALUES (1222,'ويجب على المرأة مثل ذلك إن كانت مطاوعة وإن كانت مكرهة فلا فدية عليها','1859','1126','0');
INSERT INTO [t1153] VALUES (1223,'ومتى أنزل بالمباشرة دون الفرج فعليه بدنة وإن لم ينزل فعليه شاة وعنه بدنة','1860','1126','0');
INSERT INTO [t1153] VALUES (1224,'وإن كرر النظر فأنزل أو استمنى فعليه دم هل هو شاة أو بدنة؟ على روايتين وإن مذى بذلك فعليه شاة','1861','1126','0');
INSERT INTO [t1153] VALUES (1225,'فإن فكر فأنزل فلا شيء عليه وحكى أبو حفص البرمكي وابن عقيل أن حكمه حكم تكرار النظر إذا اقترن به الإنزال في إفساد الصوم','1862','1126','0');
INSERT INTO [t1153] VALUES (1226,'وإن قتل صيدا بعد صيد فعليه جزاؤهما وعنه عليه جزاء واحد','1863','1126','0');
INSERT INTO [t1153] VALUES (1227,'وإن فعل محظورا من أجناس فعليه لكل واحد فداء وعنه عليه فدية واحدة','1863','1126','0');
INSERT INTO [t1153] VALUES (1228,'وإن حلق أو قلم أو وطئ أو قتل صيدا عامدا أو مخطئا فعليه الكفارة وعنه في الصيد لا كفارة عليه إلا في العمد ويتخرج في الحلق مثله','1864','1126','0');
INSERT INTO [t1153] VALUES (1229,'وإن لبس أو تطيب أو غطى رأسه ناسيا فلا كفارة فيه وعنه عليه الكفارة','1865','1126','0');
INSERT INTO [t1153] VALUES (1230,'ومن رفض إحرامه ثم فعل محظورا فعليه فداؤه','1867','1126','0');
INSERT INTO [t1153] VALUES (1231,'ومن تطيب قبل إحرامه في بدنة فله استدامة ذلك في إحرامه وليس له لبس ثوب مطيب','1867','1126','0');
INSERT INTO [t1153] VALUES (1232,'وإن لبس ثوبا فانقطع ريح الطيب منه وكان بحيث إذا رش فيه الماء فاح ريحه فعليه الفدية','1868','1126','0');
INSERT INTO [t1153] VALUES (1233,'وإن أحرم وعليه قميص خلعه ولم يشقه فإن استدام لبسه فعليه الفدية','1868','1126','0');
INSERT INTO [t1153] VALUES (1234,'وأما الصيام فيجزئه بكل مكان','1870','1126','0');
INSERT INTO [t1153] VALUES (1235,'وكل دم ذكرنا يجزئ فيه شاة أو سبع بدنة ومن وجبت عليه بدنة أجزأته بقرة','1871','1126','0');
INSERT INTO [t1153] VALUES (1236,'باب جزاء الصيد','1873','1126','0');
INSERT INTO [t1153] VALUES (1237,'ما لم تقض فيه الصحابة فيرجع فيه إلى قول عدلين من أهل الخبرة، ويجوز أن يكون القاتل أحدهما','1875','1126','0');
INSERT INTO [t1153] VALUES (1238,'ويجب في كل واحد من الصغير والكبير والصحيح والمعيب مثله إلا الماخض تفدى بقيمة مثلها، وقال أبو الخطاب يجب فيها مثلها','1876','1126','0');
INSERT INTO [t1153] VALUES (1239,'ويجوز فداء أعور من عين بأعور من أخرى، وفداء الذكر والأنثى وفي فدائها به وجهان','1877','1126','0');
INSERT INTO [t1153] VALUES (1240,'ما لا مثل له وهو سائر الطير فيجب فيه قيمته إلا ما كان أكبر من الحمام فهل يجب فيه قيمته أو شاة','1877','1126','0');
INSERT INTO [t1153] VALUES (1241,'ومن أتلف جزءا من صيد فعليه ما نقص من قيمته أو قيمة مثله إن كان مثليا','1878','1126','0');
INSERT INTO [t1153] VALUES (1242,'وإن نفر صيدا فتلف بشيء ضمنه','1878','1126','0');
INSERT INTO [t1153] VALUES (1243,'وإن جرحه فغاب ولم يعلم خبره فعليه ما نقصه وكذلك إن وجد ميتا ولم يعلم موته بجنايته وإن اندمل غير ممتنع فعليه جزاء جميعه','1879','1126','0');
INSERT INTO [t1153] VALUES (1244,'وإن نتف ريشه فعاد فلا شيء عليه وقيل عليه قيمة الريش','1880','1126','0');
INSERT INTO [t1153] VALUES (1245,'وكلما قتل صيدا حكم عليه','1880','1126','0');
INSERT INTO [t1153] VALUES (1246,'وإن اشترك جماعة في قتل صيد فعليهم جزاء واحد، وعنه على كل واحد جزاء','1881','1126','0');
INSERT INTO [t1153] VALUES (1247,'باب صيد الحرم ونباته','1883','1126','0');
INSERT INTO [t1153] VALUES (1248,'وإن رمى الحلال من الحل صيدا في الحرم','1885','1126','0');
INSERT INTO [t1153] VALUES (1249,'وإن قتل من الحرم صيدا في الحل بسهمه أو كلبه','1885','1126','0');
INSERT INTO [t1153] VALUES (1250,'وإن أرسل كلبه من الحل على صيد في الحل فقتل صيدا في الحرم فعلى وجهين، وإن فعل ذلك بسهمه ضمنه','1887','1126','0');
INSERT INTO [t1153] VALUES (1251,'ومن قطعه ضمن الشجرة الكبيرة ببقرة والصغيرة بشاة والحشيش بقيمته الغصن بما نقصه فإن استخلف سقط الضمان في أحد الوجهين','1891','1126','0');
INSERT INTO [t1153] VALUES (1252,'وإن قطع غصنا في الحل أصله في الحرم ضمنه وإن قطع غصنا في الحرم أصله في الحل لم يضمنه في أحد الوجهين','1894','1126','0');
INSERT INTO [t1153] VALUES (1253,'ولا جزاء في صيد المدينة وعنه جزاءه سلب القاتل لمن أخذه','1896','1126','0');
INSERT INTO [t1153] VALUES (1254,'وحد حرمها بين ثور إلى عير وجعل النبي صلى الله عليه وسلم حول المدينة اثني عشر ميلا حمى','1897','1126','0');
INSERT INTO [t1153] VALUES (1255,'باب ذكر دخول مكة','1899','1126','0');
INSERT INTO [t1153] VALUES (1256,'ويستحب أن يدخل مكة من أعلاها من ثنية كداء ثم يدخل المسجد من باب بني شيبة','1899','1126','0');
INSERT INTO [t1153] VALUES (1257,'فإذا رأى البيت رفع يديه وكبر','1900','1126','0');
INSERT INTO [t1153] VALUES (1258,'ثم يبتدئ بطواف العمرة إن كان معتمرا وبطواف القدوم إن كان مفردا أو قارنا','1902','1126','0');
INSERT INTO [t1153] VALUES (1259,'ويضطبع بردائه فيجعل وسطه تحت عاتقه الأيمن وطرفيه على عاتقه الأيسر','1903','1126','0');
INSERT INTO [t1153] VALUES (1260,'يبتدئ من الحجر الأسود','1904','1126','0');
INSERT INTO [t1153] VALUES (1261,'ثم يأخذ على يمينه ويجعل البيت على يساره','1906','1126','0');
INSERT INTO [t1153] VALUES (1262,'فإذا أتى على الركن اليماني استلمه وقبل يده','1906','1126','0');
INSERT INTO [t1153] VALUES (1263,'ويطوف سبعا يرمل في الثلاثة الأول منها وهو إسراع المشي مع تقارب الخطى ولا يثب وثبا ويمشي أربعا','1908','1126','0');
INSERT INTO [t1153] VALUES (1264,'وكلما حاذى الحجر والركن اليماني استلمهما أو أشار إليهما ويقول كلما حاذى الحجر لا إله إلا الله والله أكبر','1911','1126','0');
INSERT INTO [t1153] VALUES (1265,'ويقول في سائر طوافه: اللهم اجعله حجا مبرورا وسعيا مشكورا وذنبا مغفورا رب اغفر وارحم وتجاوز عما تعلم الأعز الأكرم','1912','1126','0');
INSERT INTO [t1153] VALUES (1266,'ويقول بين الركنين: ربنا آتنا في الدنيا حسنة وفي الآخرة حسنه وقنا عذاب النار','1912','1126','0');
INSERT INTO [t1153] VALUES (1267,'وليس على النساء ولا أهل مكة رمل اضطباع وليس في غير هذا الطواف رمل ولا اضطباع','1914','1126','0');
INSERT INTO [t1153] VALUES (1268,'ومن طاف راكبا أو محمولا أجزأه وعنه لا يجزئه إلا لعذر ولا يجزئ عن الحامل','1915','1126','0');
INSERT INTO [t1153] VALUES (1269,'وإن طاف منكسا أو على جدار الحجر أو شاذروان الكعبة، أو ترك شيئا من طوافه وإن قل أو لم ينوه لم يجزه','1919','1126','0');
INSERT INTO [t1153] VALUES (1270,'وإن طاف محدثا أو نجسا أو عريانا لم يجزه وعنه يجزئه ويجبره بدم','1921','1126','0');
INSERT INTO [t1153] VALUES (1271,'وإن أحدث في بعض طوافه أو قطعه بفصل طويل ابتدأه','1923','1126','0');
INSERT INTO [t1153] VALUES (1272,'ولو كان يسيرا أو أقيمت الصلاة أو حضرت جنازة صلى وبنى','1924','1126','0');
INSERT INTO [t1153] VALUES (1273,'ثم يصلي ركعتين','1926','1126','0');
INSERT INTO [t1153] VALUES (1274,'ثم يعود إلى الركن فيستلمه','1928','1126','0');
INSERT INTO [t1153] VALUES (1275,'ثم يخرج إلى الصفا من بابه ويسعى سبعا يبدأ بالصفا','1929','1126','0');
INSERT INTO [t1153] VALUES (1276,'ثم ينزل فيمشي حتى يأتي العلم فيسعى سعيا شديدا إلى العلم الآخر','1931','1126','0');
INSERT INTO [t1153] VALUES (1277,'ويستحب أن يسعى طاهرا مستترا متواليا، وعنه أن ذلك من شرائطه','1932','1126','0');
INSERT INTO [t1153] VALUES (1278,'والمرأة لا ترمل ولا ترقى','1934','1126','0');
INSERT INTO [t1153] VALUES (1279,'فإن فرغ من السعي فإذا كان معتمرا قصر من شعره وتحلل إلا أن يكون قد ساق معه هديا فلا يحل حتى يحج','1935','1126','0');
INSERT INTO [t1153] VALUES (1280,'ومن كان متمتعا قطع التلبية إذا وصل إلى البيت','1937','1126','0');
INSERT INTO [t1153] VALUES (1281,'باب صفة الحج','1939','1126','0');
INSERT INTO [t1153] VALUES (1282,'يستحب للمتمتع الذي حل وغيره من المحلين بمكة الإحرام بالحج يوم التروية','1941','1126','0');
INSERT INTO [t1153] VALUES (1283,'ثم يخرج إلى منى فيبيت فيها','1943','1126','0');
INSERT INTO [t1153] VALUES (1284,'فإذا طلعت الشمس سار إلى عرفة فأقام بنمرة حتى تزول الشمس','1944','1126','0');
INSERT INTO [t1153] VALUES (1285,'ثم يخطب الإمام خطبة يعلمهم فيها الوقوف ووقته والدفع منه والمبيت بمزدلفة ثم ينزل فيصلي بهم الظهر والعصر يجمع بينهما بأذان واقامتين','1945','1126','0');
INSERT INTO [t1153] VALUES (1286,'ثم يروح إلى الموقف وعرفة كلها موقف إلا بطن عرنة، وهي من الجبل المشرف على عرفة إلى الجبال المقابلة له إلى ما يلي حوائط بني عامر','1947','1126','0');
INSERT INTO [t1153] VALUES (1287,'ويستحب أن يقف عند الصخرات وجبل الرحمة راكبا وقيل الراجل أفضل','1948','1126','0');
INSERT INTO [t1153] VALUES (1288,'ويكثر من الدعاء ومن قول لا إله إلا الله وحده لا شريك له','1949','1126','0');
INSERT INTO [t1153] VALUES (1289,'ومن وقف بها نهارا ودفع قبل غروب الشمس فعليه دم','1955','1126','0');
INSERT INTO [t1153] VALUES (1290,'فمن وافاها فوقف بها فلا دم علي','1956','1126','0');
INSERT INTO [t1153] VALUES (1291,'ثم يدفع بعد غروب الشمس إلى مزدلفة وعليه السكينة والوقار','1957','1126','0');
INSERT INTO [t1153] VALUES (1292,'فإذا وصل مزدلفة صلى المغرب والعشاء قبل حط الرحال','1958','1126','0');
INSERT INTO [t1153] VALUES (1293,'وإن صلى المغرب في الطريق ترك السنة وأجزأه','1959','1126','0');
INSERT INTO [t1153] VALUES (1294,'ومن فاتته الصلاة مع الإمام بعرفة أو بمزدلفة جمع وحده','1960','1126','0');
INSERT INTO [t1153] VALUES (1295,'ثم يبيت بها فإن دفع قبل نصف الليل فعليه دم','1960','1126','0');
INSERT INTO [t1153] VALUES (1296,'فإذا أصبح بها صلى الصبح، ثم يأتي المشعر الحرام فيرقى عليه أو يقف عنده ويحمد الله تعالى ويكبر ويدعو','1963','1126','0');
INSERT INTO [t1153] VALUES (1297,'ثم يدفع قبل طلوع الشمس','1964','1126','0');
