INSERT INTO [t1153] VALUES (1356,'ويتعين الهدي بقوله هذا هدي أو تقليده أو اشعاره مع النية، والأضحية بقوله هذه أضحية ولو نوى حال الشراء لم تتعين بذلك','2071','1126','0');
INSERT INTO [t1153] VALUES (1357,'وإذا تعيبت لم يجز بيعها ولا هبتها إلا أن يبدلها بخير منها، وقال أبو الخطاب لا يجوز أيضا','2073','1126','0');
