INSERT INTO [t1153] VALUES (1373,'فإن عين بنذره أجزأه ما عينه صغيرا كان أو كبيرا من الحيوان وغيره وعليه إيصاله إلى فقراء الحرم إلا أن يعينه بموضع سواه','2093','1126','0');
INSERT INTO [t1153] VALUES (1374,'وإذا نذر مطلقا فأقل ما يجزئه شاة أو سبع بدنة أو بقرة لأن المطلق في النذور يحمل على المعهود الشرعي','2093','1126','0');
INSERT INTO [t1153] VALUES (1375,'ومن نذر أجزأته بقرة','2093','1126','0');
INSERT INTO [t1153] VALUES (1376,'ويستحب أن يأكل من هديه ولا يأكل من واجب إلا من دم المتعة والقران','2094','1126','0');
INSERT INTO [t1153] VALUES (1377,'ولا يأكل من واجب إلا دم المتعة والقرآن دون ما سواهما','2095','1126','0');
INSERT INTO [t1153] VALUES (1378,'وذبحها أفضل من الصدقة بثمنها نص عليه','2098','1126','0');
