INSERT INTO [t1153] VALUES (1386,'المعاطاة','2107','1382','0');
INSERT INTO [t1153] VALUES (1387,'ويجوز بيع الهر والفيل وسباع البهائم التي تصلح للصيد في إحدى الروايتين إلا الكلب اختارها الخرقي والأخرى لا اختارها أبو بكر','2112','1382','0');
INSERT INTO [t1153] VALUES (1388,'ويجوز بيع العبد المرتد والمريض وفي بيع الجاني والقاتل في المحاربة ولبن الآدميات وجهان','2114','1382','0');
INSERT INTO [t1153] VALUES (1389,'وفي جواز المصحف وكرهة شرائه وإبداله روايتان','2116','1382','0');
INSERT INTO [t1153] VALUES (1390,'ولا يجوز بيع الحشرات ولا الميتة ولا شيء منها ولا سباع البهائم التي لا تصلح للصيد','2117','1382','0');
INSERT INTO [t1153] VALUES (1391,'ولا يجوز بيع الكلب أي كلب كان لا نعلم فيه خلافا في المذهب','2117','1382','0');
INSERT INTO [t1153] VALUES (1392,'ولا يجوز بيع السرجين النجس','2118','1382','0');
INSERT INTO [t1153] VALUES (1393,'ولا يجوز بيع الادهان النجسة','2119','1382','0');
INSERT INTO [t1153] VALUES (1394,'وإن اشترى له في ذمته بغير إذنه صح فإن أجازه من اشترى له ملكه وإلا لزم من اشتراه','2120','1382','0');
INSERT INTO [t1153] VALUES (1395,'وفي جواز الاستصباح بها روايتان ويخرج على ذلك جواز بيعها','2120','1382','0');
INSERT INTO [t1153] VALUES (1396,'ولا يجوز بيع ما فتح عنوة ولم يقسم كأرض الشام والعراق ومصر ونحوها','2121','1382','0');
INSERT INTO [t1153] VALUES (1397,'ولا يجوز بيع ما لا يملكه ليمضي ويشتريه ويسلمه رواية واحدة','2121','1382','0');
INSERT INTO [t1153] VALUES (1398,'وتجوز إجارتها','2124','1382','0');
INSERT INTO [t1153] VALUES (1399,'ولا يجوز بيع رباع مكة ولا إجارتها، وعنه يجوز ذلك','2124','1382','0');
INSERT INTO [t1153] VALUES (1400,'ولا يجوز بيع كل ماء عد كمياه العيون ونقع البئر ولا ما في المعادن الجارية من القار والملح والنفط ولا ما ينبت في أرضه من الكلأ والشوك، ومن أخذ منه شيئا ملكه','2126','1382','0');
INSERT INTO [t1153] VALUES (1401,'إلا أنه لا يجوز له الدخول إلى ملك غيره بغير إذنه','2126','1382','0');
INSERT INTO [t1153] VALUES (1402,'وإن ذكر له من صفته ما يكفي في السلم أو رآه ثم عقدا بعد ذلك بزمن لا يتغير فيه ظاهرا','2131','1382','0');
INSERT INTO [t1153] VALUES (1403,'ولا يجوز بيع الحمل في البطن، واللبن في الضرع، والمسك في الفأرة، والنوى في التمر','2133','1382','0');
INSERT INTO [t1153] VALUES (1404,'ولا يجوز بيع الملامسة','2134','1382','0');
INSERT INTO [t1153] VALUES (1405,'فأما بيع الأعمى وشراؤه فإن أمكنه معرفة المبيع بالذوق إن كان مطعوما أو بالشم إن كان مشموما صح بيعه وشراؤه، وإن لم يمكن جاز بيعه بالصفة كالبصير وله خيار الخلف في الصفة','2134','1382','0');
INSERT INTO [t1153] VALUES (1406,'ولا يجوز أن يبيع عبدا غير معين، ولا عبدا من عبيد، ولا شاة من قطيع..','2135','1382','0');
INSERT INTO [t1153] VALUES (1407,'وإن باعه الصبرة إلا قفيزا أو ثمرة الشجرة إلا صاعا لم يصح وعنه يصح','2136','1382','0');
INSERT INTO [t1153] VALUES (1408,'وإن باع قفيزا من هذه الصبرة صح','2136','1382','0');
INSERT INTO [t1153] VALUES (1409,'وإن باعه أرضا إلا جريبا أو جريبا من أرض يعلمان جربانها صح وكان مشاعا فيها وإلا لم يصح','2137','1382','0');
INSERT INTO [t1153] VALUES (1410,'وإن باعه حيوانا مأكولا إلا رأسه أو جلده أو أطرافه صح وإن استثنى حمله أو شحمه لم يصح','2138','1382','0');
INSERT INTO [t1153] VALUES (1411,'ويجوز بيع ما مأكوله في جوفه وبيع الباقلا والجوز واللوز في قشريه والحب المشتد في سنبله','2139','1382','0');
INSERT INTO [t1153] VALUES (1412,'وإن قال بعتك بعشرة صحاح أو إحدى عشرة مكسرة أو بعشرة نقدا أو عشرين نسيئة لم يصح','2140','1382','0');
INSERT INTO [t1153] VALUES (1413,'وإن باعه الصبرة كل قفيز بدرهم والثوب كل ذراع بدرهم والقطيع كل شاة بدرهم صح','2141','1382','0');
INSERT INTO [t1153] VALUES (1414,'وإن باعه من الصبرة كل قفيز بدرهم لم يصح لأن من للتبعيض وكل للعدد فيكون ذلك العدد منها مجهولا','2141','1382','0');
INSERT INTO [t1153] VALUES (1415,'وإن باع عبده وعبد غيره بإذنه بثمن واحد فهل يصح؟ على وجهين','2146','1382','0');
INSERT INTO [t1153] VALUES (1416,'وإن جمع بين بيع وإجارة، أو بيع وصرف صح فيهما ويقسط العوض عليهما في أحد الوجهين','2146','1382','0');
INSERT INTO [t1153] VALUES (1417,'وإن جمع بين كتابه وبيع فكاتب عبده وباعه شيئا صفقة واحدة مثل أن يقول بعتك عبدي هذا وكاتبتك بمائة كل شهر عشرة بطل البيع وجها واحدا','2147','1382','0');
INSERT INTO [t1153] VALUES (1418,'ويصح النكاح وسائر العقود في أصح الوجهين كالإجارة والصلح ونحوهما،','2147','1382','0');
INSERT INTO [t1153] VALUES (1419,'ولا يصح بيع العصير لمن يتخذه خمرا، ولا بيع السلاح في الفتنة، ولا لأهل الحرب ويحتمل أن يصح مع التحريم','2147','1382','0');
INSERT INTO [t1153] VALUES (1420,'ولا يصح بيع العبد المسلم لكافر إلا أن يكون ممن يعتق عليه فيصح في إحدى الروايتين','2149','1382','0');
INSERT INTO [t1153] VALUES (1421,'وإن أسلم عبد الذمي أجبر على إزالة ملكه عنه','2149','1382','0');
INSERT INTO [t1153] VALUES (1422,'ولا يجوز بيع الرجل على بيع أخيه وهو أن يقول لمن اشترى سلعة بعشرة أنا أعطيك مثلها بتسعة','2150','1382','0');
INSERT INTO [t1153] VALUES (1423,'وفي بيع الحاضر للبادي روايتان إحداهما يصح والأخرى لا يصح بخمسة شروط','2151','1382','0');
INSERT INTO [t1153] VALUES (1424,'فأما شراؤه له فيصح رواية واحدة','2152','1382','0');
INSERT INTO [t1153] VALUES (1425,'من باع سلعة بنسيئة لم يجز أن يشتريها بأقل مما باعها إلا أن تكون قد تغيرت صفتها، وإن اشتراها أبوه أو ابنه جاز','2153','1382','0');
INSERT INTO [t1153] VALUES (1426,'وإن باع ما يجري فيه الربا بنسيئة ثم اشترى منه بثمنه قبل قبضه من جنسه أو ما لا يجوز بيعه به بنسيئة لم يجز','2154','1382','0');
INSERT INTO [t1153] VALUES (1427,'بسم الله الرحمن الرحيم، باب الشروط في البيع','2156','1382','0');
INSERT INTO [t1153] VALUES (1428,'فإن شرطها ثيبا، كافرة فبانت بكرا مسلمة فلا فسخ له لأنه زاده خيرا وليس ذلك يزيد في الثمن ما لو شرطه غير صانع فبان صانعا','2157','1382','0');
INSERT INTO [t1153] VALUES (1429,'وإن اشترط الطائر مصوتا أو أنه يجيء من مسافة معلومة صح وقال القاضي: لا يصح','2157','1382','0');
INSERT INTO [t1153] VALUES (1430,'وذكر الخرقي في جز الرطبة إن شرطه على البائع لم يصح فيخرج ههنا مثله','2161','1382','0');
INSERT INTO [t1153] VALUES (1431,'وإن جمع بين شرطين لم يصح','2161','1382','0');
INSERT INTO [t1153] VALUES (1432,'إلا إذا شرط العتق ففي صحته روايتان','2164','1382','0');
INSERT INTO [t1153] VALUES (1433,'وعنه فيمن باع جارية وشرط على المشتري أنه إن باعها فهو أحق بها بالثمن أن البيع جائز','2165','1382','0');
INSERT INTO [t1153] VALUES (1434,'وإن شرط رهنا فاسدا كالخمر ونحوه فهل يبطل البيع على وجهين؟','2166','1382','0');
INSERT INTO [t1153] VALUES (1435,'وكذلك إذا قال المرتهن إن جئتك بحقك في محله وإلا فالرهن لك، فلا يصح البيع، إلا بيع العربون وهو أن يشتري شيئا ويعطي البائع درهما ويقول أن أخذته وإلا فالدرهم لك فقال أحمد يصح لأن عمر فعله، وعند أبي الخطاب أنه لا يصح','2168','1382','0');
INSERT INTO [t1153] VALUES (1436,'وإن باعه وشرط البراءة من كل عيب لم يبرأ إلا أن يكون البائع علم العيب فكتمه','2169','1382','0');
INSERT INTO [t1153] VALUES (1437,'وإن قال بعتك على أن تنقذني الثمن إلى ثلاث أو مدة معلومة وإلا فلا بيع بيننا فالبيع صحيح نص عليه','2169','1382','0');
INSERT INTO [t1153] VALUES (1438,'باب الخيار في المبيع','2171','1382','0');
INSERT INTO [t1153] VALUES (1439,'ولكل واحد من المتبايعين الخيار ما لم يتفرقا بأبدانهما لما ذكرناه','2173','1382','0');
INSERT INTO [t1153] VALUES (1440,'إلا أن يتبايعا على أن لا خيار بنيهما أو يسقطا الخيار بعده فيسقط في إحدى الروايتين وإن سقطه أحدهما بقي خيار صاحبه','2174','1382','0');
INSERT INTO [t1153] VALUES (1441,'ولا يجوز مجهولا في ظاهر المذهب، وعنه يجوز وهما على خيارهما ما لم يقطعاه أو تنتهي مدته','2176','1382','0');
INSERT INTO [t1153] VALUES (1442,'ولا يثبت إلا في البيع والصلح بمعناه والإجارة في الذمة أو على مدة لا تلي العقد','2177','1382','0');
INSERT INTO [t1153] VALUES (1443,'وإن شرطاه إلى الغد لم يدخل في المدة','2177','1382','0');
INSERT INTO [t1153] VALUES (1444,'وإن شرط الخيار لغيره جاز وكان توكيلا له فيه','2178','1382','0');
INSERT INTO [t1153] VALUES (1445,'وإن شرطاه مدة فابتداؤها من حين العقد، ويحتمل أن يكون من حين التفرق','2178','1382','0');
INSERT INTO [t1153] VALUES (1446,'وإن شرطا الخيار لأحدهما دون صاحبه صح','2179','1382','0');
INSERT INTO [t1153] VALUES (1447,'ولمن له الخيار الفسخ بغير حضور صاحبه ولا رضاه','2179','1382','0');
INSERT INTO [t1153] VALUES (1448,'فإن مضت المدة ولم يفسخا بطل خيارهما','2180','1382','0');
INSERT INTO [t1153] VALUES (1449,'وينتقل الملك إلى المشتري بنفس العقد في أظهر الروايتين','2181','1382','0');
INSERT INTO [t1153] VALUES (1450,'فما حصل من كسب أو نماء منفصل فهو له أمضيا العقد أو فسخاه','2182','1382','0');
INSERT INTO [t1153] VALUES (1451,'وليس لواحد من المتبايعين التصرف في المبيع في مدة الخيار إلا بما يحصل به تجربة المبيع','2182','1382','0');
INSERT INTO [t1153] VALUES (1452,'فإن تصرفا فيه ببيع أو هبة أو نحوهما لم ينفذ تصرفهما','2183','1382','0');
INSERT INTO [t1153] VALUES (1453,'ويكون تصرف البائع فسخا للبيع وتصرف المشتري إسقاطا لخياره في أحد الوجهين وفي الآخر البيع والخيار بحالهما','2184','1382','0');
INSERT INTO [t1153] VALUES (1454,'وإن أعتقه المشتري نفذ عتقه وبطل خيارهما، وكذلك إن تلف المبيع، وعنه لا يبطل خيار البائع وله الفسخ والرجوع بالقيمة','2185','1382','0');
INSERT INTO [t1153] VALUES (1455,'وإن وطئها البائع وقلنا البيع ينفسخ بوطئه فكذلك، وإن قلنا لا نفسخ فعليه المهر وولده رقيق إلا إذا قلنا الملك له، ولا حد فيه على كل حال','2187','1382','0');
INSERT INTO [t1153] VALUES (1456,'وإن وطئ المشتري الجارية فأحبلها صارت أم ولد له وولده حر ثابت النسب','2187','1382','0');
INSERT INTO [t1153] VALUES (1457,'وحكم الوقف حكم البيع في أحد الوجهين وفيه وجه آخر أنه كالعتق لأنه تصرف يبطل الشفعة فأشبه العتق','2187','1382','0');
INSERT INTO [t1153] VALUES (1458,'ومن مات منهم بطل خياره ولم يورث','2188','1382','0');
INSERT INTO [t1153] VALUES (1459,'الثانية: النجش وهو أن يزيد في السلعة من يريد شراءها ليغر المشتري فله الخيار إذا غبن','2190','1382','0');
INSERT INTO [t1153] VALUES (1460,'الثالثة المسترسل إذا غبن الغبن المذكور','2190','1382','0');
INSERT INTO [t1153] VALUES (1461,'ويرد مع المصراة عوض اللبن صاعا من تمر فإن لم يجد التمر فقيمته في موضعه سواء كانت ناقة أو بقرة أو شاة','2193','1382','0');
INSERT INTO [t1153] VALUES (1462,'فإن كان اللبن بحاله لم يتغير رده وأجزأه ويحتمل أن لا يجزئه إلا التمر','2194','1382','0');
INSERT INTO [t1153] VALUES (1463,'ومتى علم التصرية فله الرد، وقال القاضي: ليس له ردها إلا بعد ثلاث','2195','1382','0');
INSERT INTO [t1153] VALUES (1464,'ولا يحل للبائع تدليس سلعته ولا كتمان عيبها','2196','1382','0');
INSERT INTO [t1153] VALUES (1465,'وإن صار لبنها عادة لم يكن له الرد في قياس قوله: إذا اشترى أمة مزوجة فطلقها الزوج لم يملك الرد','2196','1382','0');
INSERT INTO [t1153] VALUES (1466,'فإن كانت التصرية في غير بهيمة الأنعام كالأمة والأتان والفرس ثبت له الخيار في أحد الوجهين','2196','1382','0');
INSERT INTO [t1153] VALUES (1467,'فمن اشترى معيبا لا يعلم عيبه فله الخيار بين الرد والإمساك مع الأرش وهو قسط ما بين قيمة الصحيح والمعيب من الثمن','2198','1382','0');
INSERT INTO [t1153] VALUES (1468,'وما كسب فهو للمشتري، وكذلك نماؤه المنفصل، وعنه لا يرده إلا مع نمائه','2199','1382','0');
INSERT INTO [t1153] VALUES (1469,'ووطء الثيب لا يمنع الرد وعنه يمنع','2200','1382','0');
INSERT INTO [t1153] VALUES (1470,'وإن وطئ البكر أو تعيبت عنده فله الأرش، وعنه أنه مخير بين الأرش وبين الرد وأرش العيب الحادث عنده ويأخذ الثمن','2201','1382','0');
INSERT INTO [t1153] VALUES (1471,'قال الخرقي: إلا أن يكون البائع دلس العيب فيلزمه رد الثمن كاملا','2203','1382','0');
INSERT INTO [t1153] VALUES (1472,'وإن أعتق العبد أو تلف المبيع يرجع بأرشه، وكذلك إن باعه غير عالم بعينه، وكذلك إن وهبه، وإن فعله عالما فلا شيء له','2203','1382','0');
INSERT INTO [t1153] VALUES (1473,'وإن باع بعضه فله أرش الباقي وفي أرش المبيع الروايتان وقال الخرقي له رد ملكه منه بقسطه من الثمن أو أرش العيب بقدر ملكه فيه','2206','1382','0');
INSERT INTO [t1153] VALUES (1474,'وإن صبغه أو نسجه فله الأرش ولا رد له في أظهر الروايتين','2206','1382','0');
INSERT INTO [t1153] VALUES (1475,'وإن اشترى ما مأكوله في جوفه فكسره فوجده فاسدا فإن لم يكن له مكسورا قيمة كبيض الدجاج رجع الثمن كله، وإن كان له مكسورا قيمة كبيض النعام وجوز الهند فهو مخير بين أخذ أرشه وبين رده، وعنه ليس له رد ولا أش في ذلك','2207','1382','0');
INSERT INTO [t1153] VALUES (1476,'ومن علم العيب وأخر الرد لم يبطل خياره إلا أن يوجد منه ما يدل على الرضا من التصرف ونحوه','2208','1382','0');
INSERT INTO [t1153] VALUES (1477,'فإن اشترى اثنان شيئا وشرطا الخيار أو وجداه معيبا فرضي أحدهما فللآخر الفسخ في نصيبه، وعنه ليس له ذلك','2208','1382','0');
INSERT INTO [t1153] VALUES (1478,'ولا يفتقر الرد إلى رضا ولا قضاء ولا حضور صاحبه قبل القبض ولا بعده','2208','1382','0');
INSERT INTO [t1153] VALUES (1479,'وإن اشترى واحد معيبين صفقة واحدة ليس له إلا درهما أو إمساكهما والمطالبة بالأرش','2209','1382','0');
INSERT INTO [t1153] VALUES (1480,'وإن كان أحدهما معيبا فله رده بقسطه من الثمن، وعنه ليس له إلا ردهما أو إمساكهما','2209','1382','0');
INSERT INTO [t1153] VALUES (1481,'فإن كان المبيع مما ينقصه التفريق كمصراعي باب أو زوجي خف أو من لا يجوز التفريق بينهما كجارية وولدها فليس له رد أحدهما','2209','1382','0');
INSERT INTO [t1153] VALUES (1482,'وإن اختلفا في العيب هل كان عند البائع أو حدث عند المشتري ففي أيهما يقبل قوله؟ روايتان إلا أن لا يحتمل إلا قول أحدهما فالقول قوله بغير يمين','2210','1382','0');
INSERT INTO [t1153] VALUES (1483,'ومن باع عبدا يلزمه عقوبة من قصاص أو غيره ويعلم المشتري ذلك فلا شيء له','2211','1382','0');
INSERT INTO [t1153] VALUES (1484,'فإن لم يعلم حتى قتل فله الأرش لتعذر الرد','2211','1382','0');
INSERT INTO [t1153] VALUES (1485,'وإن كانت الجناية موجبة للمال والسيد معسر قدم حق المجني عليه، وللمشتري الخيار إذا لم يكن عالما، فإن كان السيد موسرا تعلق الأرش بذمته والبيع لازم','2211','1382','0');
INSERT INTO [t1153] VALUES (1486,'ومعنى التولية البيع برأس المال فيقول وليتكه أو بعتكه برأس ماله أو بما اشتريته أو برقمه','2212','1382','0');
INSERT INTO [t1153] VALUES (1487,'والشركة بيع بعضه بقسطه من الثمن ويصح بقوله شركتك في نصفه أو ثلثه','2212','1382','0');
INSERT INTO [t1153] VALUES (1488,'والمرابحة أن يبيعه بربح فيقول رأس مالي فيه مائة بعتكه بها وربح عشرة','2213','1382','0');
INSERT INTO [t1153] VALUES (1489,'والمواضعة أن يقول بعتكه بها ووضيعة درهم من كل عشرة فيلزم المشتري تسعون درهما','2214','1382','0');
INSERT INTO [t1153] VALUES (1490,'ومتى اشتراه بثمن مؤجل أو ممن لا تقبل شهادته له أو بأكثر من ثمنه حيلة، أو باع بعض الصفقة بقسطها من الثمن فلم يبين ذلك للمشتري في تخييره بالثمن فللمشتري الخيار بين الإمساك والرد','2215','1382','0');
INSERT INTO [t1153] VALUES (1491,'وما يزاد في الثمن أو يحط منه في مدة الخيار أو يؤخذ أرشا للعيب أو جناية عليه يلحق برأس المال ويخبر به','2217','1382','0');
INSERT INTO [t1153] VALUES (1492,'وإن جنى ففداه المشتري أو زيد في الثمن أو حط منه بعد لزومه لم يلحق به','2217','1382','0');
INSERT INTO [t1153] VALUES (1493,'وإن اشترى ثوبا بعشرة وقصره بعشرة أخبر بذلك على وجهه، فإن قال تحصل علي بعشرين فهل يجوز ذلك؟ على وجهين، وإن عمل فيه بنفسه عملا يساوي عشرة لم يجز ذلك وجها واحدا','2218','1382','0');
INSERT INTO [t1153] VALUES (1494,'وإن اشتراه بعشرة ثم باعه بخمسة عشر ثم اشتراه بعشرة أخبر بذلك على وجهه، وإن قال اشتريته بعشرة جاز، وقال أصحابنا يحط الربح من الثمن الثاني ويخبر أنه اشتراه بخمسة','2218','1382','0');
INSERT INTO [t1153] VALUES (1495,'وإن كانت السلعة تالفة رجعا إلى قيمة مثلها فإن اختلفا في صفتها فالقول قول المشتري، وعنه لا يتحالفان إذا كانت تالفة والقول قول المشتري مع يمينه','2221','1382','0');
INSERT INTO [t1153] VALUES (1496,'فإن نكل أحدهما لزمه ما قال صاحبه','2221','1382','0');
INSERT INTO [t1153] VALUES (1497,'فإن تخالفا فرضي أحدهما بقول صاحبه أقر العقد وإلا فلكل واحد منهما الفسخ','2221','1382','0');
INSERT INTO [t1153] VALUES (1498,'ومتى فسخ المظلوم منهما انفسخ العقد ظاهرا وباطنا، وإن فسخ الظالم لم ينفسخ في حقه باطنا وعليه إثم الغاصب','2222','1382','0');
INSERT INTO [t1153] VALUES (1499,'وإن ماتا فورثتهما في منزلتهما','2222','1382','0');
INSERT INTO [t1153] VALUES (1500,'وإن اختلفا في صفة الثمن تحالفا إلا أن يكون للبلد نقد معلوم فيرجع إليه','2223','1382','0');
INSERT INTO [t1153] VALUES (1501,'وإن اختلفا في أجل أو شرط فالقول قول من ينفيه، وعنه يتحالفان إلا أن يكون شرطا فاسدا فالقول قول من يثبته','2223','1382','0');
INSERT INTO [t1153] VALUES (1502,'وإن قال بعني هذين قال بل أحدهما فالقول قول البائع','2224','1382','0');
INSERT INTO [t1153] VALUES (1503,'وإن قال بعتني هذا قال بل هذا حلف كل واحد منهما على ما أنكره ولم يثبت بيع واحد منهما','2224','1382','0');
INSERT INTO [t1153] VALUES (1504,'وإن قال البائع لا أسلم المبيع حتى أقبض الثمن، وقال المشتري لا أسلمه حتى أقبض المبيع وكان الثمن عينا أو عرضا جعل بينهما عدل يقبض منهما ويسلم إليهما','2224','1382','0');
INSERT INTO [t1153] VALUES (1505,'ويثبت الخيار للتخلف في الصفة وتغير ما تقدمت رؤيته وقد ذكرناه','2226','1382','0');
INSERT INTO [t1153] VALUES (1506,'وما عدا المكيل والموزون يجوز التصرف فيه قبل قبضه، وإن تلف فهو من مال المشتري','2229','1382','0');
INSERT INTO [t1153] VALUES (1507,'وعنه في الصبرة المتعينة أنه يجوز بيعها قبل قبضها، وإن تلفت فهي من ضمان المشتري','2229','1382','0');
INSERT INTO [t1153] VALUES (1508,'وفيما يتناول بالتناول وفيما عدا ذلك بالتخلية','2233','1382','0');
INSERT INTO [t1153] VALUES (1509,'ويحصل القبض فيما بيع بالكيل والوزن بكيله ووزنه','2233','1382','0');
INSERT INTO [t1153] VALUES (1510,'وفي الصبرة وما ينقل بالنقل','2233','1382','0');
INSERT INTO [t1153] VALUES (1511,'والإقالة فسخ يجوز في المبيع قبضه ولا يستحق بها شفعة ولا يجوز إلا بمثل الثمن، وعنه أنها بيع فلا يثبت فيها ذلك إلا بمثل الثمن في أحد الوجهين','2234','1382','0');
INSERT INTO [t1153] VALUES (1512,'باب الربا والصرف','2235','1382','0');
INSERT INTO [t1153] VALUES (1513,'وهو نوعان ربا الفضل وربا النسيئة','2236','1382','0');
INSERT INTO [t1153] VALUES (1514,'فأما ربا الفضل فيحرم في الجنس الواحد من كل مكيل أو موزون','2237','1382','0');
INSERT INTO [t1153] VALUES (1515,'ولا يباع ما أصله الكيل بشيء من جنسه وزنا ولا ما أصله الوزن كيلا','2246','1382','0');
INSERT INTO [t1153] VALUES (1516,'فإن اختلف الجنس جاز بيع بعضه ببعض كيلا ووزنا وجزافا','2248','1382','0');
INSERT INTO [t1153] VALUES (1517,'والجنس ما له اسم خاص يشتمل أنواعا كالذهب والفضة والبر والشعير والتمر والملح','2250','1382','0');
INSERT INTO [t1153] VALUES (1518,'وفروع الأجناس أجناس كالأدقة والأخباز والأدهان','2254','1382','0');
INSERT INTO [t1153] VALUES (1519,'واللحم والشحم والكبد أجناس','2259','1382','0');
INSERT INTO [t1153] VALUES (1520,'ولا يجوز بيع لحم بحيوان من جنسه وفي بيعه بغير جنسه وجهان','2261','1382','0');
INSERT INTO [t1153] VALUES (1521,'ولا يجوز بيع حب بدقيقة ولا بسويقه في أصح الروايتين','2261','1382','0');
INSERT INTO [t1153] VALUES (1522,'ولا يجوز بيع أصله بعصيره ولا خالصه بمشوبه ولا رطبه بيابسه ولا نيئه بمطبوخه','2262','1382','0');
INSERT INTO [t1153] VALUES (1523,'يجوز بيع دقيقه بدقيقه إذا استويا في النعومة ومطبوخه بمطبوخه وخبزه بخبزه إذا استويا في النشاف وعصيره بعصيره ورطبه برطبه','2264','1382','0');
INSERT INTO [t1153] VALUES (1524,'ولا يجوز المحاقلة وهو بيع الحب في سنبله وفي بيعه بغير جنسه وجهان','2267','1382','0');
INSERT INTO [t1153] VALUES (1525,'ولا يجوز بيع المزابنة وهو بيع الرطب في رؤوس النخل بالتمر إلا في العرايا وهي بيع الرطب في رؤوس النخل خرصا بمثله من التمر كيلا فيما دون خمسة أوسق لمن به حاجة إلى أكل الرطب ولا ثمن معه','2267','1382','0');
INSERT INTO [t1153] VALUES (1526,'فيعطيه من التمر مثل ما يؤول إليه مما في النخل عند الجفاف وعنه يعطيه مثل رطبه وقد ذكرناه','2270','1382','0');
INSERT INTO [t1153] VALUES (1527,'ولا يجوز في سائر الثمار في أحد الوجهين','2271','1382','0');
INSERT INTO [t1153] VALUES (1528,'ولا يجوز بيع جنس فيه الربا بعضه ببعض ومع أحدهما أو معهما من غير جنسهما','2271','1382','0');
INSERT INTO [t1153] VALUES (1529,'وإن تلفت بجائحة من السماء رجع على البائع، وعنه إن أتلفت الثلث فصاعدا ضمنه البائع وإلا فلا','2273','1382','0');
INSERT INTO [t1153] VALUES (1530,'ولا يجوز بيع تمر منزوع النوى بما نواه فيه لاشتمال أحدهما على ما ليس من جنسه دون الآخر','2274','1382','0');
INSERT INTO [t1153] VALUES (1531,'وإن باع نوعي جنس بنوع واحد منه كدينار قراضة وصحيح بصحيحين','2274','1382','0');
INSERT INTO [t1153] VALUES (1532,'وفي بيع النوى بتمر فيه النوى واللبن بشاة ذات لبن، والصوف بنعجة عليها صوف روايتان','2275','1382','0');
INSERT INTO [t1153] VALUES (1533,'والمرجع في الكيل والوزن إلى عرف أهل الحجاز في زمن النبي صلى الله عليه وسلم أما ما لا عرف لهم فيه ففيه وجهان','2277','1382','0');
INSERT INTO [t1153] VALUES (1534,'وإن باع مكيلا بموزون كاللحم بالبر جاز التفرق قبل القبض وفي التساوي روايتان','2280','1382','0');
INSERT INTO [t1153] VALUES (1535,'وما لا يدخله ربا الفضل كالثياب والحيوان يجوز النساء فيهما، وعنه لا يجوز، وعنه لا يجوز في الجنس الواحد كالحيوان ويجوز في الجنسين كالثياب بالحيوان','2281','1382','0');
INSERT INTO [t1153] VALUES (1536,'ولا يجوز بيع الكالئ بالكالئ وهو بيع الدين بالدين','2281','1382','0');
INSERT INTO [t1153] VALUES (1537,'وإن تقابضا ثم افترقا فوجد أحدهما ما قبضه رديئا فرده بطل العقد في إحدى الروايتين','2282','1382','0');
INSERT INTO [t1153] VALUES (1538,'وإن قبض البعض ثم افترقا بطل في الجميع في أحد الوجهين، وفي الآخر ببطل فيما لم يقبض بناء على تفريق الصفقة','2282','1382','0');
INSERT INTO [t1153] VALUES (1539,'والدراهم والدنانير تتعين بالتعيين في العقد في أظهر الروايتين فلا يجوز إبدالها، وإن خرج مغصوبة بطل العقد','2292','1382','0');
INSERT INTO [t1153] VALUES (1540,'ويحرم الربا بين المسلم والحربي','2300','1382','0');
INSERT INTO [t1153] VALUES (1541,'باب بيع الأصول والثمار','2304','1382','0');
INSERT INTO [t1153] VALUES (1542,'ولا يدخل ما هو مودع فيها من الكنز والأحجار المدفونة','2304','1382','0');
INSERT INTO [t1153] VALUES (1543,'فأما ما كان من مصالحها لكنه منفصل عنها كالمفتاح وحجر الرحى الفوقاني إذا كان السفلاني منصوبا ففيه وجهان','2304','1382','0');
INSERT INTO [t1153] VALUES (1544,'وما كان في الأرض من الحجارة المخلوقة فيها أو مبني فيها كأساسات الحيطان المهدمة فهو للمشتري لأنه من أجزائها فهو كترابها','2304','1382','0');
INSERT INTO [t1153] VALUES (1545,'وإن باع أرضا بحقوقها دخل غراسها وبناؤها في البيع، وإن لم يقل بحقوقها فعلى وجهين','2305','1382','0');
INSERT INTO [t1153] VALUES (1546,'وإن كان فيها زرع يجز مرة بعد أخرى كالرطبة والبقول أو تكرر ثمرته كالقثاء والباذنجان فالأصول للمشتري والجزة الظاهرة للبائع','2306','1382','0');
INSERT INTO [t1153] VALUES (1547,'وإن كان فيها زرع لا يحصد إلا مرة كالبر والشعير فهو للبائع مبقى إلى الحصاد إلا أن يشترط المبتاع','2307','1382','0');
INSERT INTO [t1153] VALUES (1548,'وكذلك الشجر إذا كان فيه ثمر باد كالتوت والرمان والجوز','2312','1382','0');
INSERT INTO [t1153] VALUES (1549,'الورق للمشتري بكل حال','2313','1382','0');
INSERT INTO [t1153] VALUES (1550,'وإن ظهر بعض الثمرة فهو للبائع، وما لم يظهر فهو للمشتري، وقال أبو حامد: الكل للبائع','2314','1382','0');
INSERT INTO [t1153] VALUES (1551,'وإن احتاج الزرع أو الثمرة إلى سقي لم يلزم المشتري ولم يملك منع البائع منه','2314','1382','0');
INSERT INTO [t1153] VALUES (1552,'ولا يجوز بيع الرطبة والبقول إلا بشرط جزه ولا القثاء ونحوه إلا لقطة لقطة إلا أن يبيع أصله','2319','1382','0');
INSERT INTO [t1153] VALUES (1553,'والحصاد واللقاط على المشتري','2331','1382','0');
INSERT INTO [t1153] VALUES (1554,'فإن باعه مطلقا أو بشرط التبقية لم يصح','2333','1382','0');
INSERT INTO [t1153] VALUES (1555,'فإن باعها بشرط القطع ثم تركه المشتري حتى بدا الصلاح واشتد الحب وطالت الجزة وحدثت ثمرة أخرى فلم تتميز','2344','1382','0');
INSERT INTO [t1153] VALUES (1556,'وإذا اشتد الحب وبدا الصلاح في الثمر جاز بيعه مطلقا وبشرط التبقية، وللمشتري تبقيته إلى الحصاد والجزاز','2365','1382','0');
INSERT INTO [t1153] VALUES (1557,'ويلزم البائع سقيه إن احتاج إلى ذلك لأنه يجب عليه تسلم الثمرة كاملة','2370','1382','0');
INSERT INTO [t1153] VALUES (1558,'وصلاح بعض ثمرة الشجرة صلاح لجميعها','2391','1382','0');
INSERT INTO [t1153] VALUES (1559,'وبدو الصلاح في ثمر النخل أن يحمر أو يصفر، وفي العنب أن يتموه، وفي سائر الثمار أن يبدو فيه النضج ويطيب أكله','2403','1382','0');
INSERT INTO [t1153] VALUES (1560,'ومن باع عبدا وله مال فماله للبائع إلا أن يشترطه المبتاع','2414','1382','0');
INSERT INTO [t1153] VALUES (1561,'فإن كان قصده المال اشترط علمه وسائر شروط المبيع، وإن لم يكن قصده المال لم يشترط علمه','2417','1382','0');
INSERT INTO [t1153] VALUES (1562,'وإن كان عليه ثياب فقال أحمد: ما كان للجمال فهو للبائع، وما كان للبس المعتاد فهو للمشتري','2430','1382','0');
INSERT INTO [t1153] VALUES (1563,'باب السلم','2440','1382','0');
INSERT INTO [t1153] VALUES (1564,'فأما المعدود المختلف كالحيوان والفواكه والبقول والجلود والرؤوس ونحوها ففيه روايتان','2441','1382','0');
INSERT INTO [t1153] VALUES (1565,'وفي الأواني المختلفة الرؤوس والأوساط كالقماقم والأسطال الضيقة الرؤوس وما يجمع أخلاطا مميزة كالثياب المنسوجة من نوعين وجهان','2444','1382','0');
INSERT INTO [t1153] VALUES (1566,'ولا يصح فيما لا ينضبط كالجواهر كلها والحوامل من الحيوان والمغشوش من الأثمان وغيرها وما يجمع اخلاطا غير مميزة كالغالية','2445','1382','0');
INSERT INTO [t1153] VALUES (1567,'وإن جاءه بدون ما وصف له أو نوع آخر فله أخذه','2453','1382','0');
INSERT INTO [t1153] VALUES (1568,'وإن جاءه بالأجود فقال خذه وزدني درهما لم يصح','2453','1382','0');
INSERT INTO [t1153] VALUES (1569,'وإن جاءه بجنس آخر لم يجز له آخذه','2453','1382','0');
INSERT INTO [t1153] VALUES (1570,'فإن شرط الأجود لم يصح','2453','1382','0');
INSERT INTO [t1153] VALUES (1571,'وإن جاءه بأجود منه من نوعه لزمه قبوله','2453','1382','0');
INSERT INTO [t1153] VALUES (1572,'ولا بد أن يكون المكيال معلوما فإن شرط مكيالا بعينه أو صنجة بعينها غير معلومة لم يصح','2454','1382','0');
INSERT INTO [t1153] VALUES (1573,'وفي المعدود المختلف غير الحيوان روايتان','2455','1382','0');
INSERT INTO [t1153] VALUES (1574,'إلا أن يسلم في شيء يأخذ منه كل يوم أجزاء معلومة فيصح','2457','1382','0');
INSERT INTO [t1153] VALUES (1575,'فإن أسلم في جنس إلى أجلين أو في جنسين إلى أجل صح','2458','1382','0');
INSERT INTO [t1153] VALUES (1576,'ولا بد أن يكون الأجل مقدرا بزمن معلوم للخبر','2458','1382','0');
INSERT INTO [t1153] VALUES (1577,'وإن أسلم إلى الحصاد أو شرط الخيار إليه فعلى روايتين','2460','1382','0');
INSERT INTO [t1153] VALUES (1578,'وإذا جاءه بالسلم قبل محله ولا ضرر في قبضه لزمه قبضه وإلا فلا','2461','1382','0');
INSERT INTO [t1153] VALUES (1579,'وإن أسلم في ثمرة بستان بعينه أو قرية صغيرة لم يصح لأنه لا يؤمن تلفه وانقطاعه','2462','1382','0');
INSERT INTO [t1153] VALUES (1580,'وإن أسلم إلى محل يوجد فيه عاما فانقطع خير بين الصبر والفسخ والرجوع برأس ماله أو عوضه إن كان معدوما في أحد الوجهين وفي الآخر ينفسخ بنفس التعذر','2463','1382','0');
INSERT INTO [t1153] VALUES (1581,'وهل يشترط كونه معلوم القدر والصفة كالمسلم فيه على وجهين','2467','1382','0');
INSERT INTO [t1153] VALUES (1582,'وإن أسلم ثمنا واحدا من جنسين لم يجز حتى يبين ثمن كل جنس','2469','1382','0');
INSERT INTO [t1153] VALUES (1583,'ولا يشترط ذكر مكان الإيفاء','2471','1382','0');
INSERT INTO [t1153] VALUES (1584,'إلا أن يكون موضع العقد لا يمكن الوفاء فيه','2471','1382','0');
INSERT INTO [t1153] VALUES (1585,'ويكون الوفاء في الموضع العقد','2471','1382','0');
INSERT INTO [t1153] VALUES (1586,'فإن شرط الوفاء فيه كان تأكيدا','2471','1382','0');
INSERT INTO [t1153] VALUES (1587,'وإن شرطه في غيره صح','2471','1382','0');
INSERT INTO [t1153] VALUES (1588,'ولا يجوز بيع المسلم فيه قبل قبضه ولا هبته ولا أخذ غيره مكانه ولا الحوالة به','2472','1382','0');
INSERT INTO [t1153] VALUES (1589,'ويجوز بيع الدين المستقر لمن هو في ذمته بشرط أن يقبض عوضه في المجلس ولا يجوز لغيره','2473','1382','0');
INSERT INTO [t1153] VALUES (1590,'وتجوز الإقالة في السلم وتجوز في بعضه في أحد الوجهين إذا قبض رأس مال السلم أو عوضه في مجلس الإقالة','2474','1382','0');
INSERT INTO [t1153] VALUES (1591,'وإن انفسخ العقد بإقالة أو غيرها لم يجز أن يأخذ من الثمن عوضا من غير جنسه','2475','1382','0');
INSERT INTO [t1153] VALUES (1592,'وإذا كان لرجل سلم وعليه سلم من جنسه فقال لغريمه اقبض سلمي لنفسك لم يصح قبضه لنفسه','2476','1382','0');
INSERT INTO [t1153] VALUES (1593,'وإن قال اقبضه لي ثم أقبضه لنفسك صح','2476','1382','0');
INSERT INTO [t1153] VALUES (1594,'وإن قال أنا أقبضه لنفسي وحده بالكيل','2477','1382','0');
INSERT INTO [t1153] VALUES (1595,'وإن اكتاله وتركه في المكيال وسلمه إلى غريمه فقبضه صح القبض','2477','1382','0');
INSERT INTO [t1153] VALUES (1596,'وإن قبض المسلم فيه جزافا فالقول قوله في قدره','2478','1382','0');
INSERT INTO [t1153] VALUES (1597,'وهل يجوز الرهن والكفيل بالمسلم فيه؟ على روايتين','2479','1382','0');
INSERT INTO [t1153] VALUES (1598,'وإن قبضه كيلا أو وزنا ثم ادعى غلطا لم يقبل قوله في أحد الوجهين','2479','1382','0');
INSERT INTO [t1153] VALUES (1599,'باب القرض','2484','1382','0');
INSERT INTO [t1153] VALUES (1600,'وهو من المرافق المندوب إليها في حق المقرض','2485','1382','0');
INSERT INTO [t1153] VALUES (1601,'ويصح في كل عين يجوز بيعها إلا بني آدم والجواهر ونحوهما مما لا يصح السلم فيه أحد الوجهين فيهما','2487','1382','0');
INSERT INTO [t1153] VALUES (1602,'ويثبت الملك فيه بالقبض','2489','1382','0');
INSERT INTO [t1153] VALUES (1603,'ولا يملك المقرض استرجاعه','2489','1382','0');
INSERT INTO [t1153] VALUES (1604,'وله طلب بدله في الحال','2489','1382','0');
INSERT INTO [t1153] VALUES (1605,'فإن رده المقترض عليه لزمه قبوله ما لم يتعيب أو يكن فلوسا أو مكسرة فيحرمها السلطان فيكون له القيمة وقت القرض','2490','1382','0');
INSERT INTO [t1153] VALUES (1606,'ويجب رد المثل في المكيل والموزون والقيمة في الجواهر ونحوها وفيما سوى ذلك وجهان','2490','1382','0');
INSERT INTO [t1153] VALUES (1607,'ويجوز شرط الرهن والضمين به','2491','1382','0');
INSERT INTO [t1153] VALUES (1608,'ويثبت العوض في الذمة حالا وإن أجله','2491','1382','0');
INSERT INTO [t1153] VALUES (1609,'ولا يجوز شرط ما يجر نفعا نحو أن يسكنه داره أو يقضيه خيرا منه أو في بلد آخر ويحتمل جواز هذا الشرط','2492','1382','0');
INSERT INTO [t1153] VALUES (1610,'وإن فعل ذلك من غير شرط أو قضاه خيرا أو أهدى له هدية بعد الوفاء جاز','2494','1382','0');
INSERT INTO [t1153] VALUES (1611,'وإن فعله قبل الوفاء لم يجز إلا أن تكون العادة جارية بينهما بذلك قبل القرض إلا أن يكافئه أو يحسبه من دينه','2495','1382','0');
INSERT INTO [t1153] VALUES (1612,'وإذا أقرضه أثمانا فطالبه بها ببلد آخر لزمته، وإن أقرضه غيرها فطالبه بها لم تلزمه فإن طالبه بالقيمة لزمه اداؤها','2498','1382','0');
INSERT INTO [t1153] VALUES (1613,'باب الرهن','2499','1382','0');
INSERT INTO [t1153] VALUES (1614,'وهو وثيقة بالحق','2499','1382','0');
INSERT INTO [t1153] VALUES (1615,'وهو لازم في حق الراهن جائز في حق المرتهن','2500','1382','0');
INSERT INTO [t1153] VALUES (1616,'يجوز عقده مع الحق وبعده ولا يجوز قبله إلا عند أبي الخطاب','2500','1382','0');
INSERT INTO [t1153] VALUES (1617,'إلا المكاتب إذا قلنا: استدامة القبض شرط لم يجز رهنه','2502','1382','0');
INSERT INTO [t1153] VALUES (1618,'ويصح في كل عين يجوز بيعها','2502','1382','0');
INSERT INTO [t1153] VALUES (1619,'ويجوز رهن ما يسرع إليه الفساد بدين مؤجل ويباع ويجعل ثمنه رهنا','2505','1382','0');
INSERT INTO [t1153] VALUES (1620,'ويجوز رهن المشاع','2507','1382','0');
INSERT INTO [t1153] VALUES (1621,'ويجوز رهن المبيع غير المكيل والموزون قبل قبضه إلى على ثمنه في أحد الوجهين','2511','1382','0');
INSERT INTO [t1153] VALUES (1622,'وما لا يجوز بيعه لا يجوز رهنه إلا الثمرة قبل بدو صلاحها من غير شرط القطع في أحد الوجهين','2512','1382','0');
INSERT INTO [t1153] VALUES (1623,'ولا يصح رهن العبد المسلم لكافر','2515','1382','0');
INSERT INTO [t1153] VALUES (1624,'ولا يلزم الرهن إلا بالقبض واستدامته شرط في اللزوم','2521','1382','0');
INSERT INTO [t1153] VALUES (1625,'فإن اخرجه المرتهن إلى الراهن باختياره زال لزوم الرهن','2522','1382','0');
INSERT INTO [t1153] VALUES (1626,'ولو رهنه عصيرا فتخمر زال لزومه، فإن تخلل عاد لزومه بحكم العقد السابق','2523','1382','0');
INSERT INTO [t1153] VALUES (1627,'وتصرف الراهن في الرهن لا يصح إلا العتق فإنه يصح ويؤخذ منه قيمته فيجعل رهنا مكانه وعنه لا ينفذ عتق المعسر','2532','1382','0');
INSERT INTO [t1153] VALUES (1628,'وليس عليه تزويج الأمة المرهونة فإن فعل لم يصح','2537','1382','0');
INSERT INTO [t1153] VALUES (1629,'وإن وطئ الجارية فأولدها خرجت من الرهن وأخذت منه قيمتها فجعلت رهنا','2538','1382','0');
INSERT INTO [t1153] VALUES (1630,'وإن أذن المرتهن في بيع أو هبة أو نحو ذلك صح وبطل الرهن إلا أن يأذن له في بيعه بشرط أن يجعل ثمنه رهنا أو يجعل دينه من ثمنه','2541','1382','0');
INSERT INTO [t1153] VALUES (1631,'ونماء الرهن وكسبه وأرش الجناية عليه من الرهن','2542','1382','0');
INSERT INTO [t1153] VALUES (1632,'ومؤنته على الراهن وكفنه إن مات وأجرة مخزنه إن كان مخزونا','2543','1382','0');
INSERT INTO [t1153] VALUES (1633,'وهو أمانة في يد المرتهن إن تلف بغير تعد منه فلا شيء عليه ولا يسقط بهلاكه شيء من دينه','2546','1382','0');
INSERT INTO [t1153] VALUES (1634,'وإن تلف بعضه فباقيه رهن بجميع الدين','2547','1382','0');
INSERT INTO [t1153] VALUES (1635,'ولا ينفك شيء من الرهن حتى يقضي جميع الدين','2548','1382','0');
INSERT INTO [t1153] VALUES (1636,'وإذا حل الدين وامتنع من وفائه فإن كان الراهن أذن للمرتهن أو العدل في بيع الرهن باعه ووفى الدين وإلا رفع الأمر إلى الحاكم فيجبره على وفاء الدين أو بيع الرهن فإن لم يفعل باعه الحاكم وقضى دينه','2549','1382','0');
INSERT INTO [t1153] VALUES (1637,'وإن رهنه عند رجلين فوفى أحدهما انفك في نصيبه','2549','1382','0');
INSERT INTO [t1153] VALUES (1638,'وإن رهنه رجلان شيئا فوفاه أحدهما انفك في نصيبه','2549','1382','0');
INSERT INTO [t1153] VALUES (1639,'فإن شرط جعله في يد اثنين فليس لأحدهما الانفراد بحفظه','2551','1382','0');
INSERT INTO [t1153] VALUES (1640,'وليس للراهن ولا للمرتهن إذا لم يتفقا ولا للحاكم نقله عن يد العدل إلا يتغير حاله','2551','1382','0');
INSERT INTO [t1153] VALUES (1641,'وله رده إليهما ولا يملك رده إلى أحدهما فإن فعل فعليه رده إلى يده فإن لم يفعل ضمن حق الآخر','2552','1382','0');
INSERT INTO [t1153] VALUES (1642,'فإن أذنا له في البيع لم يبع إلا بنقد البلد فإن كان فيه نقود باع بجنس الدين','2552','1382','0');
INSERT INTO [t1153] VALUES (1643,'وإن قبض الثمن فتلف في يده فهو من ضمان الراهن','2554','1382','0');
INSERT INTO [t1153] VALUES (1644,'وإن استحق المبيع رجع المشتري على الراهن','2554','1382','0');
INSERT INTO [t1153] VALUES (1645,'وإن ادعى دفع الثمن إلى المرتهن فأنكر ولم يكن قضاه ببينة ضمن، وعنه لا يضمن إلا أن يكون أمر بالإشهاد فلم يفعل وهكذا الحكم في الوكيل','2555','1382','0');
INSERT INTO [t1153] VALUES (1646,'وإن شرط أن يبيعه المرتهن أو العدل صح فإن عزلهما صح عزله','2556','1382','0');
INSERT INTO [t1153] VALUES (1647,'فإن شرط أن لا يبيعه عند الحلول أو إن جاءه بحقه في محله وإلا فالرهن له لم يصح الشرط وفي صحة الرهن روايتان','2557','1382','0');
INSERT INTO [t1153] VALUES (1648,'وإن عفا السيد على مال أو كانت موجبة للمال فاقتص منه جعل مكانه','2570','1382','0');
INSERT INTO [t1153] VALUES (1649,'وإن أقر الراهن أنه أعتق العبد قبل رهنه فالحكم في ذلك كما لو أعتقه بعد رهنه','2574','1382','0');
INSERT INTO [t1153] VALUES (1650,'وإن أقر أنه كان جنى أو أنه باعه أو غصبه قبل على نفسه ولم يقبل على المرتهن إلا أن يصدقه','2575','1382','0');
INSERT INTO [t1153] VALUES (1651,'وإن عجز عن استئذانه ولم يستأذن الحاكم فعلى روايتين','2579','1382','0');
INSERT INTO [t1153] VALUES (1652,'وإن أنفق على الرهن بغير إذن الراهن مع إمكانه فهو متبرع','2579','1382','0');
INSERT INTO [t1153] VALUES (1653,'وكذلك الحكم في الوديعة وفي نفقة الجمال إذا هرب الجمال وتركها في يد المكتري','2580','1382','0');
INSERT INTO [t1153] VALUES (1654,'وإن انهدمت الدار فعمرها المرتهن بغير إذن الراهن لم يجرع به رواية واحدة','2580','1382','0');
INSERT INTO [t1153] VALUES (1655,'فإن فهو رهن بحاله وإن سلمه بطل الرهن','2581','1382','0');
INSERT INTO [t1153] VALUES (1656,'فإن لم يستغرق الأرش قيمته بيع منه بقدره وباقيه رهن، وقيل يباع جميعه ويكون باقي ثمنه رهنا','2582','1382','0');
INSERT INTO [t1153] VALUES (1657,'فإن اختار المرتهن فداءه ففداه بإذن الراهن رجع به، وإن فداه بغير إذنه فهل يرجع به؟ على روايتين','2582','1382','0');
INSERT INTO [t1153] VALUES (1658,'وإن جني عليه جناية موجبة للقصاص فللسيد القصاص فإن اقتص فعليه قيمة أقلهما قيمة تجعل مكانه','2587','1382','0');
INSERT INTO [t1153] VALUES (1659,'وكذلك إن جنى على سيده فاقتص منه هو أو ورثته','2588','1382','0');
INSERT INTO [t1153] VALUES (1660,'وإن عفا السيد عن المال صح في حقه ولم يصح في حق المرتهن، فإذا انفك الرهن رد إلى الجاني، وقال أبو الخطاب يصح وعليه قيمته','2589','1382','0');
INSERT INTO [t1153] VALUES (1661,'وإن وطئ المرتهن الجارية بغير إذن الراهن فعليه الحد والمهر وولده رقيق','2591','1382','0');
INSERT INTO [t1153] VALUES (1662,'وإن وطئها بإذن الراهن وادعى الجهالة وكان مثله يجهل ذلك فلا حد عليه ولا مهر وولده حر لا تلزمه قيمته','2592','1382','0');
INSERT INTO [t1153] VALUES (1663,'باب الحجر','2594','1382','0');
INSERT INTO [t1153] VALUES (1664,'وهو على ضربين حجر على الإنسان لحظ نفسه وحجر لحق غيره','2594','1382','0');
INSERT INTO [t1153] VALUES (1665,'ومن لزمه دين مؤجل لم يطالب به قبل أجله','2596','1382','0');
INSERT INTO [t1153] VALUES (1666,'فإن أراد سفرا يحل الدين قبل مدته فلغريمه منعه إلا أن يوثقه برهن أو كفيل','2596','1382','0');
INSERT INTO [t1153] VALUES (1667,'فإن كان لا يحل الدين قبله ففي منعه روايتان','2596','1382','0');
INSERT INTO [t1153] VALUES (1668,'وإذا كان حالا وله ما يفي به لم يحجر عليه','2597','1382','0');
INSERT INTO [t1153] VALUES (1669,'فإن أصر باعه الحاكم وقضى دينه','2597','1382','0');
INSERT INTO [t1153] VALUES (1670,'وإن ادعى الاعسار وكان دينه عن عوض كالبيع والقرض أو عرف له مال سابق حبس إلا أن يقيم البينة على نفاد ماله أو إعساره، وهل يحلف معها؟ على وجهين، وإن لم يكن كذلك حلف وخلي سبيله','2598','1382','0');
INSERT INTO [t1153] VALUES (1671,'وإن كان له مال لا يفي به فسأل غرماؤه الحاكم عليه لزمته إجابتهم','2602','1382','0');
INSERT INTO [t1153] VALUES (1672,'ويستحب إظهار الحجر عليه والإشهاد عليه','2603','1382','0');
INSERT INTO [t1153] VALUES (1673,'فإن تصرف في ذمته بشراء أو ضمان أو اقرار صح','2604','1382','0');
INSERT INTO [t1153] VALUES (1674,'وإن جنى شارك المجني عليه الغرماء وإن جنى عبده قدم المجني عليه بثمنه','2604','1382','0');
INSERT INTO [t1153] VALUES (1675,'ولم يتعلق بها حق من شفعة أو جناية أو رهن','2615','1382','0');
INSERT INTO [t1153] VALUES (1676,'ولم تزد زيادة متصلة كالسمن وتعلم صنعة','2618','1382','0');
INSERT INTO [t1153] VALUES (1677,'فأما الزيادة المنفصلة والنقص بهزال أو نسيان صنعة فلا يمنع الرجوع والزيادة للمفلس وعنه للبائع','2619','1382','0');
INSERT INTO [t1153] VALUES (1678,'وإن صبغ الثوب أو قصره لم يمنع الرجوع والزيادة للمفلس','2622','1382','0');
INSERT INTO [t1153] VALUES (1679,'وإن غرس الأرض أو بنى فيها فله الرجوع ودفع قيمة الغراس والبناء فيملكه إلا أن يختار المفلس والغرماء القلع ومشاركته بالنقص','2631','1382','0');
INSERT INTO [t1153] VALUES (1680,'فإن أبوا القلع وأبى دفع القيمة سقط الرجوع','2632','1382','0');
INSERT INTO [t1153] VALUES (1681,'ويترك له من ماله ما تدعو إليه حاجته من مسكين وخادم','2638','1382','0');
INSERT INTO [t1153] VALUES (1682,'وينفق عليه بالمعروف إلى أن يفرغ من قسمته بين غرمائه','2639','1382','0');
INSERT INTO [t1153] VALUES (1683,'بسم الله الرحمن الرحيم. وبه نستعين. باب الصلح','2641','1382','0');
INSERT INTO [t1153] VALUES (1684,'مسألة: والصلح في الأموال قسمان أحدهما: صلح على الإقرار وهو نوعان أحدهما: صلح على جنس الحق مثل أن يقر له بدين فيضع عنه بعضه أو بعين فيهب له بعضها ويأخذ الباقي فيصح إن لم يكن بشرط مثل أن يقول على أن تعطيني الباقي أو يمنعه حقه بدونه','2641','1382','0');
INSERT INTO [t1153] VALUES (1685,'مسألة: وإن صالح عن الحق بأكثر منه من جنسه مثل أن يصالح عن دية الخطأ أو قيمة متلف بأكثر منها من جنسها لم يصح','2643','1382','0');
INSERT INTO [t1153] VALUES (1686,'مسألة: ولا يصح ذلك ممن لا يملك التبرع كالمكاتب، والمأذون له، وولي اليتيم، إلا في حال الإنكار وعدم البينة','2643','1382','0');
INSERT INTO [t1153] VALUES (1687,'مسألة: وإن صالح عن المؤجل ببعضه حالا لم يصح','2643','1382','0');
INSERT INTO [t1153] VALUES (1688,'مسألة: وإن وضع بعض الحال وأجل باقيه صح الإسقاط دون التأجيل','2643','1382','0');
INSERT INTO [t1153] VALUES (1689,'مسألة: وإن صالح إنسانا ليقر له بالعبودية أو امرأة لتقر له بالزوجية لم يصح','2644','1382','0');
INSERT INTO [t1153] VALUES (1690,'مسألة: ولو قال أقر لي بديني وأعطيك منه مائة ففعل صح الإقرار ولم يصح الصلح','2644','1382','0');
INSERT INTO [t1153] VALUES (1691,'مسألة: وإن صالحه بعرض قيمته أكثر منها جاز','2644','1382','0');
INSERT INTO [t1153] VALUES (1692,'مسألة: وإن صالحه عن بيت على أن يسكنه سنة أو يبني له فوقه غرفة لم يصح','2644','1382','0');
INSERT INTO [t1153] VALUES (1693,'مسألة: وإن دفع المدعي عليه العبودية إلى المدعي مالا صلحا عن دعواه صح','2645','1382','0');
INSERT INTO [t1153] VALUES (1694,'مسألة: وإن صالحت المرأة بتزويج نفسها صح، فإن كان الصلح عن عيب في مبيعها فتبين أنه ليس بعيب رجعت بأرشه لا بمهر مثلها','2645','1382','0');
INSERT INTO [t1153] VALUES (1695,'مسألة: وإن صالح عما في الذمة بشيء في الذمة لم يجز التفرق قبل القبض لأنه بيع دين بدين','2646','1382','0');
INSERT INTO [t1153] VALUES (1696,'مسألة: ويصح الصلح عن المجهول بمعلوم إذا كان مما لا يمكن معرفته للحاجة','2647','1382','0');
INSERT INTO [t1153] VALUES (1697,'مسألة: وإن كان شقصا مشفوعا ثبتت فيه الشفعة، ويكون إبراء في حق الآخر فلا يرد ما صولح عنه بعيب ولا يؤخذ بشفعة','2650','1382','0');
INSERT INTO [t1153] VALUES (1698,'مسألة: فإن صالح عن المنكر أجنبي بغير إذنه صح ولم يرجع عليه في أصح الوجهين','2651','1382','0');
INSERT INTO [t1153] VALUES (1699,'مسألة: فإن كان أحدهما عالما بكذب نفسه فالصلح باطل في حقه وما أخذه حرام عليه','2651','1382','0');
INSERT INTO [t1153] VALUES (1700,'مسألة: وإن صالح الأجنبي لنفسه لتكون المطالبة له غير معترف بصحة الدعوى أو معترفا بها عالما بعجزه عن استنقاذها لم يصح وإن ظن القدرة عليه صح فإن عجز عنه فهو مخير بين فسخ الصلح وإمضائه','2652','1382','0');
INSERT INTO [t1153] VALUES (1701,'مسألة: ولو صالح سارقا ليطلعه، أو شاهدا ليكتم شهادته، أو شفيعا عن شفعته، أو مقذوفا عن حده لم يصح الصلح وتسقط الشفعة وفي الشفعة وفي ثلاثة وجوه','2656','1382','0');
INSERT INTO [t1153] VALUES (1702,'مسألة: وإن صالحه أن يجري على أرضه أو سطحه ماء معلوما صح','2657','1382','0');
INSERT INTO [t1153] VALUES (1703,'مسألة: ويجوز أن يشتري ممرا في دار وموضعا في حائطه يفتحه بابا، وبقعة يحفرها بئرا','2661','1382','0');
INSERT INTO [t1153] VALUES (1704,'مسألة: وإن يشتري علو بيت يبني عليه بنيانا موصوفا','2661','1382','0');
INSERT INTO [t1153] VALUES (1705,'مسألة: وإن حصل في هوائه أغصان شجرة غيره فطالبه بإزالتها فله ذلك فإن أبى فله قطعها','2662','1382','0');
INSERT INTO [t1153] VALUES (1706,'مسألة: فإن صالحه عن ذلك بعوض لم يجز','2662','1382','0');
INSERT INTO [t1153] VALUES (1707,'مسألة: وإن اتفقا على أن الثمرة له أو بينهما جاز ولم يلزم','2663','1382','0');
INSERT INTO [t1153] VALUES (1708,'مسألة: ولا يجوز أن يشرع إلى طريق نافذ جناحا ولا ساباطا ولا دكاكا','2665','1382','0');
INSERT INTO [t1153] VALUES (1709,'مسألة: ولا يجوز أن يفعل ذلك في ملك إنسان أو درب غير نافذ إلا بإذن أهله','2668','1382','0');
INSERT INTO [t1153] VALUES (1710,'مسألة: مسألة: وإن صالح عن ذلك بعوض جاز في أحد الوجهين','2669','1382','0');
INSERT INTO [t1153] VALUES (1711,'مسألة: وإذا كان ظهر داره في درب غير نافذ ففتح فيه بابا لغير الاستطراق جاز','2670','1382','0');
INSERT INTO [t1153] VALUES (1712,'مسألة: وإن فتحه للإستطراق لم يجز بغير إذنهم لأنه ليس لهم حق في الدرب الذي هو ملك غيره وفيه وجه آخر أنه يجوز لأن له رفع جميعه','2671','1382','0');
INSERT INTO [t1153] VALUES (1713,'مسألة: ولو كان بابه في آخر الدرب ملك نقله إلى أوله ولم يملك نقله إلى داخل منه في أحد الوجهين','2671','1382','0');
INSERT INTO [t1153] VALUES (1714,'مسألة: وليس له أن يفتح في حائط جاره ولا الحائط المشترك روزنة ولا طاقا بغير إذن صاحبه','2674','1382','0');
INSERT INTO [t1153] VALUES (1715,'مسألة: وليس له وضع خشبة عليه إلا عند الضرورة بأن لا يمكنه التسقيف إلا به','2674','1382','0');
INSERT INTO [t1153] VALUES (1716,'مسألة: وعنه ليس له وضع خشبة على جدار المسجد وهذا تنبيه على أنه لا يضعه على جدار جاره','2676','1382','0');
INSERT INTO [t1153] VALUES (1717,'مسألة: وإن كان بينهما حائط فانهدم فطالب أحدهما صاحبه ببنائه أجبر عليه وعنه لا يجبر','2681','1382','0');
INSERT INTO [t1153] VALUES (1718,'مسألة: فإن بناه بآلته فهو بينهما وإن بناه بآلة من عنده فهو له وليس للآخر الانتفاع به وإن طلب الانتفاع به خير الثاني بين أخذ نصف قيمته وبين أخذ آلته','2683','1382','0');
INSERT INTO [t1153] VALUES (1719,'مسألة: وليس له منعه من بنائه على الرواية التي تقول لا يجبر الممتنع إذا أراد شريكه البناء فليس له منعه لأن له حقا في الحمل ورسما فلا يجوز منعه منه','2683','1382','0');
INSERT INTO [t1153] VALUES (1720,'مسألة: وإن كان بينهما نهر أو بئر أو دولاب أو ناعورة أو قناة فاحتاج إلى عمارة ففي إجبار الممتنع وجهان','2687','1382','0');
INSERT INTO [t1153] VALUES (1721,'مسألة: وليس لأحدهما منع صاحبه من عمارته','2688','1382','0');
INSERT INTO [t1153] VALUES (1722,'باب الحوالة','2692','1382','0');
INSERT INTO [t1153] VALUES (1723,'مسألة: والحوالة تنقل الحق من ذمة المحيل إلى ذمة المحال عليه فلا يملك المحتال الرجوع عليه بحال إذا صحت الحوالة برئت ذمة المحيل وانتقل الحق إلى ذمة المحال عليه','2692','1382','0');
INSERT INTO [t1153] VALUES (1724,'مسألة: ولا تصح إلا بشروط ثلاثة أحدها: أن يحيل على دين مستقر فإن أحال على مال الكتابة أو السلم قبل قبضه أو الصداق قبل الدخول لم يصح وإن أحال المكاتب سيده أو الزوج امرأته صح،','2694','1382','0');
INSERT INTO [t1153] VALUES (1725,'مسألة ولا يعتبر رضى المحال عليه ولا رضى المحتال إن كان المحال عليه مليئا','2699','1382','0');
INSERT INTO [t1153] VALUES (1726,'مسألة وإن ظنه مليئا فبان مفلسا ولم يكن رضي بالحوالة رجع عليه وإلا فلا يحتمل أن يرجع','2700','1382','0');
INSERT INTO [t1153] VALUES (1727,'مسألة وإذا أحال المشتري البائع بالثمن أو أحال البائع عليه به فبيان البيع باطلا فالحوالة باطلة','2701','1382','0');
INSERT INTO [t1153] VALUES (1728,'مسألة فإن فسخ العقد بعيب أو إقالة لم تبطل الحوالة','2702','1382','0');
INSERT INTO [t1153] VALUES (1729,'مسألة وللبائع أن يحيل المشتري على من أحاله المشتري عليه في الصورة الأولى وللمشتري أن يحيل المحتال عليه','2703','1382','0');
INSERT INTO [t1153] VALUES (1730,'مسألة وإذا قال: أحلتك قال: بل وكلتني أو قال: وكلتك قال: بل احلتني فالقول قول مدعي الوكالة','2703','1382','0');
INSERT INTO [t1153] VALUES (1731,'مسألة وإذا اتفقا على أنه قال: أحلتك بالمال الذي قبل زيد ثم اختلفا فقال: المحيل إنما وكلتك في القبض لي','2704','1382','0');
INSERT INTO [t1153] VALUES (1732,'مسألة وإن قال أحلتك بدينك فالقول قول مدعي الحوالة وجها واحدا','2705','1382','0');
INSERT INTO [t1153] VALUES (1733,'باب الضمان','2708','1382','0');
INSERT INTO [t1153] VALUES (1734,'مسألة فإن برئت ذمة المضمون عنه برئ الضامن','2711','1382','0');
INSERT INTO [t1153] VALUES (1735,'مسألة وإن برىء الضامن أو أقر ببراءته لم يبرأ المضمون عنه لأنه أصل فلا يبرأ بإبراء التبع ولأنه وثيقة انحلت من غير استيفاء الدين منها','2711','1382','0');
INSERT INTO [t1153] VALUES (1736,'مسألة ولو ضمن ذمي لذمي عن ذمي خمرا فأسلم المضمون له والمضمون عنه بريء هو والضامن معا','2712','1382','0');
INSERT INTO [t1153] VALUES (1737,'مسألة ولا يصح إلا من جائز التصرف','2712','1382','0');
INSERT INTO [t1153] VALUES (1738,'مسألة ولا يصح من صبي ولا مجنون ولا سفيه ولا من عبد بغير إذن سيده وعنه يصح ويتبع به بعد العتق وإن','2713','1382','0');
INSERT INTO [t1153] VALUES (1739,'مسألة ولا يصح إلا برضى الضامن ولا يعتبر رضى المضمون له وإلا المضمون عنه ولا معرفة الضامن لها','2716','1382','0');
INSERT INTO [t1153] VALUES (1740,'مسألة ولا يعتبر كون الحق معلوما ولا واجبا إذا كان مآله الى الوجوب فلو قال: ضمنت لك ما على فلان أو','2718','1382','0');
INSERT INTO [t1153] VALUES (1741,'مسألة ويصح ضمان دين الضامن','2720','1382','0');
INSERT INTO [t1153] VALUES (1742,'مسألة ويصح ضمان دين الميت المفلس وغيره ولا تبرأ ذمته قبل القضاء في أصح الروايتين','2721','1382','0');
INSERT INTO [t1153] VALUES (1743,'مسألة ويصح ضمان عهدة المبيع عن البائع للمشتري وعن المشتري للبائع','2722','1382','0');
INSERT INTO [t1153] VALUES (1744,'مسألة ولا يصح ضمان دين الكتابة في أصح الروايتين','2724','1382','0');
INSERT INTO [t1153] VALUES (1745,'مسألة ولا يصح ضمان الأمانات كالوديعة ونحوها إلا أن يضمن التعدي فيها','2724','1382','0');
INSERT INTO [t1153] VALUES (1746,'مسألة فأما الأعيان المضمونة كالغصوب والعواري والمقبوض على وجه السوم فيصح ضمانها','2725','1382','0');
INSERT INTO [t1153] VALUES (1747,'مسألة وإن قضى الضامن الدين متبرعا لم يرجع بشيء لأنه تطوع بذلك أشبه الصدقة وسواء ضمن بإذنه أو بغير','2726','1382','0');
INSERT INTO [t1153] VALUES (1748,'مسألة وإن نوى الرجوع وكان الضمان والقضاء بغير إذن المضمون عنه فهل يرجع؟ على روايتين','2726','1382','0');
INSERT INTO [t1153] VALUES (1749,'مسألة وإن أنكر المضمون له القضاء وحلف لم يرجع الضامن على المضمون عنه سواء صدقه أو كذبه','2729','1382','0');
INSERT INTO [t1153] VALUES (1750,'مسألة وإن اعترف المضمون له بالقضاء وأنكر المضمون عنه لم يسمع إنكاره','2731','1382','0');
INSERT INTO [t1153] VALUES (1751,'مسألة وإن قضى المؤجل قبل أجله لم يرجع حتى يحل','2731','1382','0');
INSERT INTO [t1153] VALUES (1752,'مسألة وإن مات الضامن أو المضمون عنه فهل يحل الدين؟ على روايتين وأيهما حل عليه لم يحل على الآخر','2732','1382','0');
INSERT INTO [t1153] VALUES (1753,'مسألة ويصح ضمان الحال مؤجلا وإن ضمن المؤجل حالا لم يلزمه قبل أجله في اصح الوجهين','2732','1382','0');
INSERT INTO [t1153] VALUES (1754,'مسألة وتصح ببدن من عليه دين وبالأعيان المضمونة','2736','1382','0');
INSERT INTO [t1153] VALUES (1755,'مسألة ولا يصح ببدن من عليه حد ولا قصاص سواء كان حقا لله تعالى كحد الزنا والسرقة أو لآدمي كحد','2737','1382','0');
INSERT INTO [t1153] VALUES (1756,'مسألة ولا يصح بغير معين كأحد هذين','2738','1382','0');
INSERT INTO [t1153] VALUES (1757,'مسألة وإن كفل بجزء شائع من إنسان أو عضو أوكفل بإنسان على أنه إن جاء به وإلا فهو كفيل بآخر أو ضامن','2738','1382','0');
INSERT INTO [t1153] VALUES (1758,'مسألة ومتى أحضره وسلمه بريء إلا أن يحضره قبل الأجل وفي قبضه ضرر','2740','1382','0');
INSERT INTO [t1153] VALUES (1759,'مسألة إلا برضا الكفيل وفي رضا المكفول به وجهان','2740','1382','0');
INSERT INTO [t1153] VALUES (1760,'مسألة وإن مات المكفول به أو تلفت العين بفعل الله تعالى او سلم نفسه برىء الكفيل','2742','1382','0');
INSERT INTO [t1153] VALUES (1761,'مسألة وإن تعذر إحضاره مع بقائه لزم الكفيل الدين أو عوض العين','2743','1382','0');
INSERT INTO [t1153] VALUES (1762,'مسألة وإن غاب أمهل الكفيل بقدر ما يمضي فيحضره فإن تعذر إحضاره ضمن ما عليه','2743','1382','0');
INSERT INTO [t1153] VALUES (1763,'مسألة وإذا طالب الكفيل المكفول به بالحضور معه لزمه ذلك إن كانت الكفالة بإذنه أو طالبه صاحب الحق','2744','1382','0');
INSERT INTO [t1153] VALUES (1764,'مسألة ولو تكفل واحد غريما لأثنين فأبرأه أحدهما أو أحضره عند أحدهما لم يبرأ من الآخر','2746','1382','0');
INSERT INTO [t1153] VALUES (1765,'باب الشركة','2747','1382','0');
INSERT INTO [t1153] VALUES (1766,'مسألة ولا تصح إلا بشرطين أحدهما أن يكون رأس المال دراهم أو دنانير','2749','1382','0');
INSERT INTO [t1153] VALUES (1767,'مسألة وهل تصح بالمغشوش والفلوس؟ على وجهين','2751','1382','0');
INSERT INTO [t1153] VALUES (1768,'مسألة الشرط الثاني أن يشرطا لكل واحد منهما جزءا من الربح مشاعا معلوما كالنصف والثلث والربع','2752','1382','0');
INSERT INTO [t1153] VALUES (1769,'مسألة وإن قالا الربح بيننا فهو بينهما نصفين','2753','1382','0');
INSERT INTO [t1153] VALUES (1770,'مسألة فإن لم يذكر الربح لم يصح كالمضاربة','2753','1382','0');
INSERT INTO [t1153] VALUES (1771,'مسألة وإن شرطا لأحدهما جزءا مجهولا لم يصح','2753','1382','0');
INSERT INTO [t1153] VALUES (1772,'مسألة وإن شرطا لأحدهما في الشركة والمضاربة دراهم معلومة أو ربح أحد الثوبين لم يصح','2754','1382','0');
INSERT INTO [t1153] VALUES (1773,'مسألة وكذلك الحكم في المساقاة والمزارعة قياسا على الشركة','2755','1382','0');
INSERT INTO [t1153] VALUES (1774,'مسألة ولا يشترط أن يخلطا المالين ولا أن يكونا من جنس واحد','2755','1382','0');
INSERT INTO [t1153] VALUES (1775,'مسألة وما يشتريه كل واحد منهما بعد عقد الشركة فهو بينهما','2757','1382','0');
INSERT INTO [t1153] VALUES (1776,'مسألة وإن تلف أحد المالين فهو من ضمانهما إذا خلطا المال وإن لم يخلط فكذلك','2757','1382','0');
INSERT INTO [t1153] VALUES (1777,'مسألة والوضيعة على قدر المال','2757','1382','0');
INSERT INTO [t1153] VALUES (1778,'مسألة وهل له أن يودع أو يبيع نساء او يبضع أو يوكل فيما يتولى مثله بنفسه أو يرهن أو يرتهن على وجهين','2759','1382','0');
INSERT INTO [t1153] VALUES (1779,'مسألة وليس له أن يكاتب الرقيق ولا يزوجه ولا يعتقه على مال ولا غيره لأن الشركة انعقدت على التجارة','2759','1382','0');
INSERT INTO [t1153] VALUES (1780,'مسألة ولا يضارب بالمال ولا يأخذ به سفتجة ولا يعطيها إلا بإذن شريكه','2759','1382','0');
INSERT INTO [t1153] VALUES (1781,'مسألة ولس له أن يستدين على مال الشركة فإن فعل فهو عليه وربحه له إلا أن يأذن شريكه','2761','1382','0');
INSERT INTO [t1153] VALUES (1782,'مسألة وإن أخر حقه من الدين جاز','2762','1382','0');
INSERT INTO [t1153] VALUES (1783,'مسألة وإن تقاسما الدين في الذمة لم يصح','2762','1382','0');
INSERT INTO [t1153] VALUES (1784,'مسألة وإن أبرأ من الدين لزم في حقه دون صاحبه','2762','1382','0');
INSERT INTO [t1153] VALUES (1785,'مسألة وكذلك إن أقر بمال سواء أقر بعين أو دين','2762','1382','0');
INSERT INTO [t1153] VALUES (1786,'مسألة وعلى كل واحد مهما أن يتولى ما جرت العادة أن يتولاه من نشر الثوب وطيه وختم الكيس وإحرازه','2763','1382','0');
INSERT INTO [t1153] VALUES (1787,'مسألة فإن فعله ليأخذ أجرته فهل له ذلك على وجهين','2763','1382','0');
INSERT INTO [t1153] VALUES (1788,'مسألة وفاسد مثل أن يشترط ما يعود بجهالة الربح أو ضمان المال أو أن عليه من الوضيعة أكثر من قدر ماله','2764','1382','0');
INSERT INTO [t1153] VALUES (1789,'مسألة: وإذا فسد العقد قسم الربح على قدر المالين','2766','1382','0');
INSERT INTO [t1153] VALUES (1790,'مسألة فإن قال: خذه فاتجر به والربح كله لي فهو إبضاع','2769','1382','0');
INSERT INTO [t1153] VALUES (1791,'مسألة وإن قال والربح كله لك فهو قرض','2769','1382','0');
INSERT INTO [t1153] VALUES (1792,'مسألة ولو قال: لك ثلث الربح صح والباقي لرب المال','2770','1382','0');
INSERT INTO [t1153] VALUES (1793,'مسألة وإن قال ولي ثلث الربح ولم يذكر نصيب العامل ففيه وجهان','2770','1382','0');
INSERT INTO [t1153] VALUES (1794,'مسألة وإن قال: خذه مضاربة والربح كله لك أو لي لم يصح','2770','1382','0');
INSERT INTO [t1153] VALUES (1795,'مسألة وإن اختلفا في الجزء المشروط فهو للعامل قليلا كان أو كثيرا','2771','1382','0');
INSERT INTO [t1153] VALUES (1796,'مسألة وإذا فسدت فالربح لرب المال وللعامل الإجرة وعنه له الأقل من الإجرة أو ما شرط له من الربح','2774','1382','0');
INSERT INTO [t1153] VALUES (1797,'مسألة وإن شرطا تأقيت المضاربة فهل تفسد على ضربين','2776','1382','0');
INSERT INTO [t1153] VALUES (1798,'مسألة وإن قال بع هذا العرض وضارب بثمنه أو اقبض وديعتي وضارب بها او إذا قدم الحاج فضارب بهذا صح','2777','1382','0');
INSERT INTO [t1153] VALUES (1799,'مسألة وإن قال ضارب بالدين الذي عليك لم يصح','2778','1382','0');
INSERT INTO [t1153] VALUES (1800,'مسألة وإن اخرج مالا ليعمل فيه هو وآخر والربح بينهما صح','2779','1382','0');
INSERT INTO [t1153] VALUES (1801,'مسألة وإن اشترى امرأته صح وانفسخ نكاحهما','2787','1382','0');
INSERT INTO [t1153] VALUES (1802,'مسألة وإن اشترى المضارب من يعتق عليه صح الشراء','2788','1382','0');
INSERT INTO [t1153] VALUES (1803,'مسألة وليس للمضارب أن يضارب لآخر إذا كان فيه ضرر على الأول فإن فعل رد نصيبه من الربح في الشركة','2793','1382','0');
INSERT INTO [t1153] VALUES (1804,'مسألة وليس لرب المال أن يشتري من مال المضاربة شيئا لنفسه وعنه يجوز','2799','1382','0');
INSERT INTO [t1153] VALUES (1805,'مسألة وكذلك شراء السيد من عبده المأذون','2799','1382','0');
INSERT INTO [t1153] VALUES (1806,'مسألة وان اشترى أحد الشريكين نصيب شريكه صح','2800','1382','0');
INSERT INTO [t1153] VALUES (1807,'مسألة وان اشترى الجميع بطل في نصيبه لأنه ملكه','2801','1382','0');
INSERT INTO [t1153] VALUES (1808,'مسألة وليس للمضارب نفقة إلا بشرط سواء مانت تجارته في الحضر أو السفر','2801','1382','0');
INSERT INTO [t1153] VALUES (1809,'مسألة فأذن له في التسري فاشتري جارية ملكها وصار ثمنها قرضا نص عليه أحمد لأن البضع لا يباح إلا بملك','2803','1382','0');
INSERT INTO [t1153] VALUES (1810,'مسألة فإن اختلفا في قدر النفقة فقال أبو الخطاب يرجع في القوت الى الإطعام في الكفارة وفي الكسوة إلى أقل ملبوس','2803','1382','0');
INSERT INTO [t1153] VALUES (1811,'مسألة وليس للمضارب ربح حتى يستوفى رأس المال يعني أنه لا يستحق أخذ شيء من الربح حتى يسلم رأس','2804','1382','0');
INSERT INTO [t1153] VALUES (1812,'مسألة فإن اشترى سلعتين فربح في إحداهما وخسر في الأخرى أو تلفت جبرت الوضيعة من الربح','2805','1382','0');
INSERT INTO [t1153] VALUES (1813,'مسألة وإن تلف بعض رأس المال قبل التصرف فيه انفسخت فيه المضاربة','2806','1382','0');
INSERT INTO [t1153] VALUES (1814,'مسألة وإن تلف المال ثم اشترى سلعة للمضاربة به فهي له وثمنها عليه سواء علم بتلف المال قبل نقد الثمن أو','2807','1382','0');
INSERT INTO [t1153] VALUES (1815,'مسألة فإن تلف بعد الشراء فالمضاربة بحالها والثمن على رب المال','2807','1382','0');
INSERT INTO [t1153] VALUES (1816,'مسألة وإذا ظهر الربح لم يكن للعامل أخذ شيء إلا بإذن رب المال','2808','1382','0');
INSERT INTO [t1153] VALUES (1817,'مسألة وإذا طلب العامل البيع فأبى رب المال أجبر إن كان فيه ربح وإلا فلا','2808','1382','0');
INSERT INTO [t1153] VALUES (1818,'مسألة وإن انفسخ القراض والمال عرض فرضي رب المال أن يأخذ بماله عرضا أو طلب البيع فله ذلك','2809','1382','0');
INSERT INTO [t1153] VALUES (1819,'مسألة وإن كان دينا لزم العامل تقاضيه','2810','1382','0');
INSERT INTO [t1153] VALUES (1820,'مسألة وإن قارض في المرض فالربح من راس المال وإن زاد على أجر المثل','2811','1382','0');
INSERT INTO [t1153] VALUES (1821,'مسألة وإن مات المضارب ولم يعرف مال المضاربة فهو دين في تركته','2812','1382','0');
INSERT INTO [t1153] VALUES (1822,'مسألة ويقدم به على سائر الغرماء إذا مات رب المال','2812','1382','0');
INSERT INTO [t1153] VALUES (1823,'مسألة والقول قول رب المال في رده إليه مع يمينه','2813','1382','0');
INSERT INTO [t1153] VALUES (1824,'مسألة وفي الجزء المشروط للعامل','2813','1382','0');
INSERT INTO [t1153] VALUES (1825,'مسألة وإن قال أذنت لي في البيع نسأ وفي الشراء بخمسة فأنكره رب المال وقال: إنما أذنت لك في البيع نقدا','2814','1382','0');
INSERT INTO [t1153] VALUES (1826,'مسألة وإن قال ربحت ألفا ثم خسرتها أو تلفت قبل قوله','2814','1382','0');
INSERT INTO [t1153] VALUES (1827,'مسألة وإن قال: غلطت أو نسيت لم يقبل قوله','2814','1382','0');
INSERT INTO [t1153] VALUES (1828,'مسألة والملك بينهما على ما شرطاه','2822','1382','0');
INSERT INTO [t1153] VALUES (1829,'مسألة وهما في التصرف كشريكي العنان','2823','1382','0');
INSERT INTO [t1153] VALUES (1830,'مسألة وتصح مع اتفاق الصنائع رواية واحدة فأما مع اختلافهما ففيه وجهان','2825','1382','0');
INSERT INTO [t1153] VALUES (1831,'مسألة وإن مرض أحدهما فالكسب بينهما فإن طالبه الصحيح أن يقيم مقامه لزم ذلك','2826','1382','0');
INSERT INTO [t1153] VALUES (1832,'مسألة وإن اشتركا ليحملا على دابتيهما والأجرة بينهما صح','2827','1382','0');
INSERT INTO [t1153] VALUES (1833,'مسألة فإذا تقبلا حمل شيء فحملاه عليهما أو على غير الدابتين صحت الشركة','2828','1382','0');
INSERT INTO [t1153] VALUES (1834,'مسألة وإن أجراهما بأعيانهما فلكل واحد منهما أجرة دابته','2828','1382','0');
INSERT INTO [t1153] VALUES (1835,'مسألة وإن جمعا بين شركة العنان والأبدان والوجوه والمضاربة صح','2835','1382','0');
INSERT INTO [t1153] VALUES (1836,'باب الوكالة','2839','1382','0');
INSERT INTO [t1153] VALUES (1837,'مسألة ويجوز القبول على الفور والتراخي نحو أن يوكله في بيع شيء فيبيعه بعد سنة أو يبلغه أنه وكله منذ شهر','2840','1382','0');
INSERT INTO [t1153] VALUES (1838,'مسألة ولا يصح التوكيل والتوكل في شيء إلا ممن يصح تصرفه فيه','2841','1382','0');
INSERT INTO [t1153] VALUES (1839,'مسألة ويجوز التوكيل في كل حق آدمي من العقود والفسوخ والعتق والطلاق والرجعة وتملك المباحات','2842','1382','0');
INSERT INTO [t1153] VALUES (1840,'مسألة ويجوز أن يوكل من يقبل له النكاح ومن يزوج موليته إذا كان الوكيل ممن يصح له ذلك لنفسه وموليته','2844','1382','0');
INSERT INTO [t1153] VALUES (1841,'مسألة ويجوز في كل حق لله تعالى تدخله النيابة من العبادات والحدود في إثباتها واستيفائها كحد الزنا والسرقة','2845','1382','0');
INSERT INTO [t1153] VALUES (1842,'مسألة ويجوز الاستيفاء في حضرة الموكل وغيبته نص عليه أحمد وهو قول مالك إلا القصاص وحد القذف','2846','1382','0');
INSERT INTO [t1153] VALUES (1843,'مسألة ولا يجوز للوكيل أن يوكل فيما يتولى مثله بنفسه إلا بإذن الموكل وعنه يجوز وكذلك الوصي والحاكم','2847','1382','0');
INSERT INTO [t1153] VALUES (1844,'مسألة ويجوز توكيل عبد غيره بإذن سيده','2849','1382','0');
INSERT INTO [t1153] VALUES (1845,'مسألة فإن وكله بإذنه في شراء نفسه من سيده فعلى روايتين','2849','1382','0');
INSERT INTO [t1153] VALUES (1846,'مسألة والوكالة عقد جائز من الطرفين','2851','1382','0');
INSERT INTO [t1153] VALUES (1847,'مسألة وتبطل بالموت والجنون والحجر للسفه وكذلك كل عقد جائز كالشركة والمضاربة','2851','1382','0');
INSERT INTO [t1153] VALUES (1848,'مسألة وهل تبطل بالردة وحرية عبده؟ على وجهين','2852','1382','0');
INSERT INTO [t1153] VALUES (1849,'مسألة وهل ينعزل الوكيل بالموت والعزل قبل علمه؟ على روايتين','2855','1382','0');
INSERT INTO [t1153] VALUES (1850,'مسألة وإذا وكل اثنين لم يكن لأحدهما الإنفراد بالتصرف إلا أن يجعل ذلك إليه','2857','1382','0');
INSERT INTO [t1153] VALUES (1851,'مسألة ولا يجوز للوكيل في البيع أن يبيع لنفسه وعنه يجوز إذا زاد على مبلغ ثمنه في النداء أو وكل من يبيع','2859','1382','0');
INSERT INTO [t1153] VALUES (1852,'مسألة وهل يجوز أن يبيع لولده أو والده أو مكاتبه؟ على وجهين','2861','1382','0');
INSERT INTO [t1153] VALUES (1853,'مسألة ولا يجوز أن يبيع نساء ولا بغير نقد البلد ويحتمل أن يجوز كالمضارب','2862','1382','0');
INSERT INTO [t1153] VALUES (1854,'مسألة وإن باع بدون ثمن مما قدره له صح وضمن النقص','2863','1382','0');
INSERT INTO [t1153] VALUES (1855,'مسألة وإن باع بأكثر من ثمن المثل صح سواء كانت الزيادة من جنس الثمن الذي أمر به أو لم تكن هي','2865','1382','0');
INSERT INTO [t1153] VALUES (1856,'مسألة وإن قال بعه بدرهم فباعه بدينار صح في أحد الوجهين','2865','1382','0');
INSERT INTO [t1153] VALUES (1857,'مسألة فإن قال بعه بألف نساء فباعه بألف حالة صح إن كان لا يستضر بحفظ الثمن في الحال','2866','1382','0');
INSERT INTO [t1153] VALUES (1858,'مسألة وإن وكله في الشراء فاشترى بأكثر من ثمن المثل أو بأكثر مما قدره له أما إذا وكله في الشراء فاشترى','2867','1382','0');
INSERT INTO [t1153] VALUES (1859,'مسألة وإن وكل في شراء شيء نقدا بثمن معين فاشتراه به مؤجلا صح','2868','1382','0');
INSERT INTO [t1153] VALUES (1860,'مسألة وإن قال اشتر لي شاة بدينار فاشترى له شاتين تساوي إحداهما دينارا أو اشترى له شاة تساوي دينارا','2869','1382','0');
INSERT INTO [t1153] VALUES (1861,'مسألة وليس له شراء معيب فإن وجد بما اشتراه عيبا فله رده','2871','1382','0');
INSERT INTO [t1153] VALUES (1862,'مسألة فإن رده فصدق الموكل البائع في الرضى بالعيب فهل يصح الرد على وجهين','2872','1382','0');
INSERT INTO [t1153] VALUES (1863,'مسألة وإن قال البائع موكلك قد رضي بالعيب فالقول قول الوكيل بيمينه أنه لا يعلم ذلك','2872','1382','0');
INSERT INTO [t1153] VALUES (1864,'مسألة وإن وكله في شراء معين فاشتراه ووجده معيبا فهل له رده قبل إعلام الموكل على وجهين','2873','1382','0');
INSERT INTO [t1153] VALUES (1865,'مسألة فإن قال اشتر لي بعين هذا الثمن فاشترى له في ذمته لم يلزم الموكل','2873','1382','0');
INSERT INTO [t1153] VALUES (1866,'مسألة فإن قال اشتر لي في ذمتك وانقد الثمن فاشترى بعينه صح','2874','1382','0');
INSERT INTO [t1153] VALUES (1867,'مسألة وإن أمره ببيعه في سوق بثمن فباعه في آخر صح وإن قال بعه من زيد فباعه من غيره لم يصح','2874','1382','0');
INSERT INTO [t1153] VALUES (1868,'مسألة وإن وكله في بيع شيء ملك تسليمه ولم يملك قبض ثمنه إلا بقرينة فإن تعذر قبضه لم يلزم الوكيل','2877','1382','0');
INSERT INTO [t1153] VALUES (1869,'مسألة وإن وكله في بيع فاسد لم يصح ولم يملكه','2878','1382','0');
INSERT INTO [t1153] VALUES (1870,'مسألة: وإن وكله في بيع ماله كله صح','2879','1382','0');
INSERT INTO [t1153] VALUES (1871,'مسألة: وإن وكله في كل قليل وكثير لم يصح','2879','1382','0');
INSERT INTO [t1153] VALUES (1872,'مسألة: وإن قال: إشتر لي ما شئت أو عبدا بما شئت لم يصح حتى يذكر النوع وقدر الثمن وعنه ما يدل على أنه يصح','2880','1382','0');
INSERT INTO [t1153] VALUES (1873,'مسألة: وإن وكله في الخصومة لم يكن وكيلا في القبض','2881','1382','0');
INSERT INTO [t1153] VALUES (1874,'مسألة: وإن وكله في القبض كان وكيلا في الخصومة في أحد الوجهين','2882','1382','0');
INSERT INTO [t1153] VALUES (1875,'مسألة: وإن وكل في قبض الحق من لإنسان لم يكن له القبض من وارثه وإن قال: أقبض حقي الذي قبله فله القبض من وارثه','2882','1382','0');
INSERT INTO [t1153] VALUES (1876,'مسألة: وإن وكله في قبضه اليوم لم يكن له قبضه غدا','2883','1382','0');
INSERT INTO [t1153] VALUES (1877,'مسألة: وإن وكله في الإيداع فأودع ولم يشهد لم يضمن إذا أنكر المودع','2883','1382','0');
INSERT INTO [t1153] VALUES (1878,'مسألة: وإن وكله في قضاء الدين فقضاه ولم يشهد وأنكر الغريم ضمن إلا أن يقضيه بحضرة الموكل','2884','1382','0');
INSERT INTO [t1153] VALUES (1879,'مسألة: وإن قال: بعت الثوب وقبضت الثمن فتلف فالقول قوله','2886','1382','0');
INSERT INTO [t1153] VALUES (1880,'مسألة: وإن اختلفا في رده إلى الموكل فالقول قوله إن كان متطوعا وإن كان بجعل فعلى وجهين','2887','1382','0');
INSERT INTO [t1153] VALUES (1881,'مسألة: وكذلك يخرج في الأجير والمرتهن','2888','1382','0');
INSERT INTO [t1153] VALUES (1882,'مسألة: فإن قال: أذنت لي في البيع نسيئة وفي الشراء بخمسه فأنكره فعلى وجهين','2888','1382','0');
INSERT INTO [t1153] VALUES (1883,'مسألة: وإن قال أذنت لي أن أتزوج لك فلانة ففعلت وصدقته المرأة فأنكر فالقول قول المنكر بغير يمين وهل يلزم الوكيل نصف الصداق؟ على وجهين','2892','1382','0');
INSERT INTO [t1153] VALUES (1884,'مسألة: ويجوز التوكيل بجعل وبغيره','2895','1382','0');
INSERT INTO [t1153] VALUES (1885,'مسألة: وإن كذبه لم يستحلف','2899','1382','0');
INSERT INTO [t1153] VALUES (1886,'مسألة: فإن دفعه إليه فأنكر صاحب الحق الوكالة وحلف رجع على الدافع وحده','2899','1382','0');
INSERT INTO [t1153] VALUES (1887,'مسألة: وإن كان المدفوع وديعة فوجدها أخذها وإن تلف فله تضمين أيهما شاء ولا يرجع من ضمنه على الآخر بشيء','2900','1382','0');
INSERT INTO [t1153] VALUES (1888,'مسألة: فإن كان ادعى أن صاحب الحق أحاله ففي وجوب الدفع إليه مع التصديق واليمين مع الإنكار وجهان','2901','1382','0');
INSERT INTO [t1153] VALUES (1889,'مسألة: وإن ادعى أنه مات وأنا وارثه فصدقه أنه وارث الحق لا وارث له سواه لزمه الدفع إليه','2901','1382','0');
INSERT INTO [t1153] VALUES (1890,'كتاب الإقرار','2909','0','0');
INSERT INTO [t1153] VALUES (1891,'كتاب الإقرار','2909','1890','0');
INSERT INTO [t1153] VALUES (1892,'مسألة: ويصح الإقرار من كل مكلف مختار غير محجور عليه. أما الطفل والمجنون فلا يصح إقرارهما وكذلك المبرسم والنائم والمغمى عليه','2910','1890','0');
INSERT INTO [t1153] VALUES (1893,'مسألة: وكذلك العبد المأذون له في التجارة','2910','1890','0');
INSERT INTO [t1153] VALUES (1894,'مسألة: فإن أقر مراهق غير مأذون له ثم اختلف هو المقر له في بلوغه فالقول قول المقر','2911','1890','0');
INSERT INTO [t1153] VALUES (1895,'مسألة: ولا يصح إقرار السكران وتتخرج صحته بناء على طلاقه','2911','1890','0');
INSERT INTO [t1153] VALUES (1896,'مسألة: ولا يصح إقرار المكره إلا أن يقر بغير ما أكره عليه مثل أن يكره على الإقرار لإنسان فيقر لغيره أو على الإقرار بطلاق امرأة فيقر بطلاق غيرها أو على الإقرار بدنانير فيقر بدراهم فيصح','2911','1890','0');
INSERT INTO [t1153] VALUES (1897,'مسألة: وإن أكره على وزن ثمن فباع داره في ذلك صح بيعه','2912','1890','0');
INSERT INTO [t1153] VALUES (1898,'مسألة: وأما المريض مرض الموت المخوف فيصح إقراره بغير المال','2912','1890','0');
INSERT INTO [t1153] VALUES (1899,'مسألة: وإن أقر بمال لمن لا يرثه صح في أصح الروايتين','2912','1890','0');
INSERT INTO [t1153] VALUES (1900,'مسألة: ولا يحاص المقر له غرماء الصحة','2913','1890','0');
INSERT INTO [t1153] VALUES (1901,'مسألة: وإن أقر لوارث لم يقبل إلا ببينة','2913','1890','0');
INSERT INTO [t1153] VALUES (1902,'مسألة: إلا أن يقر لزوجته بمهر مثلها فما دونه فيصح','2914','1890','0');
INSERT INTO [t1153] VALUES (1903,'مسألة: وإن أقر لوارث فصار عند الموت غير وارث لم يصح وإن أقر لغير وارث صح وإن صار وارثا نص عليه وقال: إن الاعتبار بحال الموت فيصح في الأولى ولا يصح في الثانية كالوصية','2915','1890','0');
INSERT INTO [t1153] VALUES (1904,'مسألة: وإن أقر لوارث وأجنبي بطل في حق الوارث وصح في حق الأجنبي','2915','1890','0');
INSERT INTO [t1153] VALUES (1905,'مسألة: وإن أقر لامرأته بدين ثم أبانها ثم تزوجها لم يصح إقراره لها','2916','1890','0');
INSERT INTO [t1153] VALUES (1906,'مسألة: وإن أقر المريض بوارث صحيح صح وعنه لا يصح','2916','1890','0');
INSERT INTO [t1153] VALUES (1907,'مسألة: وإن أقر بطلاق امرأته في صحته لم يسقط ميراثها','2917','1890','0');
INSERT INTO [t1153] VALUES (1908,'مسألة: وإن أقر العبد غير المأذون له بمال لم يقبل في الحال ويتبع به بعد العتق','2918','1890','0');
INSERT INTO [t1153] VALUES (1909,'مسألة: وإن أقر السيد عليه بمال أو ما يوجبه كجناية الخطأ قبل','2918','1890','0');
INSERT INTO [t1153] VALUES (1910,'مسألة: وإن أقر العبد بسرقة مال في يده وكذبه السيد قبل إقراره في القطع دون المال','2919','1890','0');
INSERT INTO [t1153] VALUES (1911,'مسألة: وإن أقر إنه باع عبده من نفسه بألف وأقر العبد به ثبت ويكون كالكتابة، وإن أنكر عتق ولم يلزمه الألف','2920','1890','0');
INSERT INTO [t1153] VALUES (1912,'مسألة: وإن تزوج مجهولة النسب فأقرت بالرق لم يقبل إقرارها','2920','1890','0');
INSERT INTO [t1153] VALUES (1913,'مسألة: وإن أقر لعبد غيره بمال صح وكان لمالكه','2920','1890','0');
INSERT INTO [t1153] VALUES (1914,'مسألة: وإن أقر السيد لعبده بمال لم يصح','2920','1890','0');
INSERT INTO [t1153] VALUES (1915,'مسألة: وإن أقر لبهيمة لم يصح','2920','1890','0');
INSERT INTO [t1153] VALUES (1916,'مسألة: فإن كان الصغير المقر بنسبه ميتا ورثه لأنه ثبت نسبه وبهذا قال الشافعي، ويحتمل أن يثبت نسبه دون ميراثه لأنه متهم في قصد أخذ ميراثه، وقال أبو حنيفة لا يثبت نسبه ولا إرثه لذلك','2922','1890','0');
INSERT INTO [t1153] VALUES (1917,'مسألة: ومن ثبت نسبه فجاءت أمه بعد موت المقر فادعت الزوجية لم يثبت بذلك','2923','1890','0');
INSERT INTO [t1153] VALUES (1918,'مسألة: وإن أقر بنسب أخ أو عم في حياة أبيه وحده لم يقبل وإن كان بعد موتهما وهو الوارث وحده قبل إقراره وثبت النسب، وإن كان معه غيره لم يثبت النسب وللمقر له من الميراث ما فضل في يد المقر','2926','1890','0');
INSERT INTO [t1153] VALUES (1919,'مسألة: وإن أقر أن فلانة امرأته أو أقرت أن فلانا زوجها فلم يصدق المقر له المقر إلا بعد موت المقر صح','2928','1890','0');
INSERT INTO [t1153] VALUES (1920,'مسألة: وإن أقر من عليه الولاء بنسب وارث لم يقبل إقراره إلا أن يصدقه مولاه','2928','1890','0');
INSERT INTO [t1153] VALUES (1921,'مسألة: وإن أقرت امرأة بنكاح على نفسها فهل يقبل؟ على روايتين','2928','1890','0');
INSERT INTO [t1153] VALUES (1922,'مسألة: فإن أقر المولى عليها به قبل إن كانت مجبرة','2928','1890','0');
INSERT INTO [t1153] VALUES (1923,'مسألة: وإن أقر الورثة على موروثهم بدين لزمهم قضاؤه من التركة فإن أقر بعضهم لزمه بقدر ميراثه فإن لم تكن تركة لم يلزمهم شيء','2929','1890','0');
INSERT INTO [t1153] VALUES (1924,'مسألة: وإن أقر لكبير عاقل فلم يصدقه بطل إقراره في أحد الوجهين','2931','1890','0');
INSERT INTO [t1153] VALUES (1925,'باب ما يحصل به الإقرار','2932','1890','0');
INSERT INTO [t1153] VALUES (1926,'مسألة: وإن قال أنا أقر أو لا أنكر أو يجوز أن يكون محقا أو عسى أو لعل أو أحسب أو أظن أو أقدر أوخذ أو اتزن أو افتح كمك لم يكن مقرا','2932','1890','0');
INSERT INTO [t1153] VALUES (1927,'مسألة: وإن قال: أنا مقر أو خذها أو اتزنها أو اقبضها أو احرزها أو هي صحاح فهل يكون مقرا؟ يحتمل وجهين','2933','1890','0');
INSERT INTO [t1153] VALUES (1928,'مسألة: وإن قال: له علي ألف إن شاء الله أو في علمي أو فيما أعلم أو قال: اقضني ديني عليك ألفا أو سلم إلي ثوبي هذا أو فرسي هذه فقال: نعم فقد أقر بها','2933','1890','0');
INSERT INTO [t1153] VALUES (1929,'مسألة: وإن قال: له علي ألف في علمي أو فيما أعلم كان مقرا به','2935','1890','0');
INSERT INTO [t1153] VALUES (1930,'مسألة: وإن قال: إن قدم فلان فله علي ألف درهم لم يكن مقرا','2935','1890','0');
INSERT INTO [t1153] VALUES (1931,'مسألة: وإن قال: له علي ألف إذا جاء رأس الشهر كان إقرارا وإن قال: إذا جاء رأس الشهر فله علي ألف فعلى وجهين','2935','1890','0');
INSERT INTO [t1153] VALUES (1932,'مسألة: وإن قال: له علي ألف إن شهد به فلان أو إن شهد به فلان صدقته لم يكن مقرا','2936','1890','0');
INSERT INTO [t1153] VALUES (1933,'باب الحكم فيما إذا وصل بإقراره ما يغيره','2936','1890','0');
INSERT INTO [t1153] VALUES (1934,'مسألة: وإن قال: إن شهد به فلان فهو صادق احتمل وجهين','2936','1890','0');
INSERT INTO [t1153] VALUES (1935,'مسألة: وإن أقر العربي بالعجمية أو العجمي بالعربية وقال لم أرد ما قلت فالقول قوله مع يمينه لأنه يحتمل أن يكون صادقا فلا يكون مقرا','2936','1890','0');
INSERT INTO [t1153] VALUES (1936,'مسألة: وإن قال: له علي ألف إلا ألفا لم يصح','2937','1890','0');
INSERT INTO [t1153] VALUES (1937,'مسألة: وإن قال: كان له عندي ألف وقضيته أو قضيت منه خمسمائة درهم فقال الخرقي: ليس بإقرار والقول قوله مع يمينه','2937','1890','0');
INSERT INTO [t1153] VALUES (1938,'مسألة: فإذا قال: له على هؤلاء العبيد العشرة إلا واحدا لزمه تسليم تسعة','2943','1890','0');
INSERT INTO [t1153] VALUES (1939,'مسألة: فإن ماتوا إلا واحدا فقال: هو المستثنى فهل يقبل؟ على وجهين','2943','1890','0');
INSERT INTO [t1153] VALUES (1940,'مسألة: وإن قال: له في هذه الدار إلا هذا البيت لي قبل منه لأن الأول استثناء فلا يدخل البيت في إقراره والثاني في معنى الاستثناء لكونه أخرج بعض ما يتناوله اللفظ بكلام متصل وسواء كان البيت أكثر من نصف الدار أو أقل','2944','1890','0');
INSERT INTO [t1153] VALUES (1941,'مسألة: وإن قال: له علي درهمان وثلاثة إلا درهمين فهل يصح؟ على وجهين','2944','1890','0');
INSERT INTO [t1153] VALUES (1942,'مسألة: وإن قال: له علي درهم ودرهم إلا درهما أو ثلاثة ودرهمان إلا درهمين أو ثلاثة ونصف إلا نصفا أو درهما أو خمسة وتسعون إلا خمسة لم يصح الاستثناء','2945','1890','0');
INSERT INTO [t1153] VALUES (1943,'مسألة: وإن قال: له علي عشرة إلا خمسة إلا ثلاثة إلا درهمين إلا درهما لزمه عشرة','2946','1890','0');
INSERT INTO [t1153] VALUES (1944,'مسألة: ويصح الاستثناء من الاستثناء','2946','1890','0');
INSERT INTO [t1153] VALUES (1945,'مسألة: وإن قال: له علي خمسة إلا درهمين ودرهما لزمته الخمسة في أحد الوجهين','2946','1890','0');
INSERT INTO [t1153] VALUES (1946,'مسألة: ولا يصح الاستثناء من غير الجنس نص عليه','2948','1890','0');
INSERT INTO [t1153] VALUES (1947,'مسألة: إلا يستثني عينا من ورق أو ورقا من عين فيصح','2951','1890','0');
INSERT INTO [t1153] VALUES (1948,'مسألة: إلا أن يكون في بلد أوزانهم ناقصة أو مغشوشة فهل يلزمه من دراهم البلد أو من غيرها؟ على وجهين','2952','1890','0');
INSERT INTO [t1153] VALUES (1949,'مسألة: وإن قال له علي ألف إلى شهر لزمه مؤجلا ويحتمل أن يلزمه حالا','2953','1890','0');
INSERT INTO [t1153] VALUES (1950,'مسألة: وإن قال له علي ألف زيوف وفسره بما لا فضة فيه لم يقبل وإن فسره بمغشوشة قبل. وكذلك إن فسرها بمعيبة عيبا ينقصها قبل لأنه صادق وإن فسرها برصاص أو نحاس أو ما لا قيمة له لم يقبل لأن تلك ليست دراهم على الحقيقة فيكون تفسيره بها رجوعا عما أقر به فلم','2953','1890','0');
INSERT INTO [t1153] VALUES (1951,'مسألة: وإن قال له علي دراهم ناقصة لزمته ناقصة','2953','1890','0');
INSERT INTO [t1153] VALUES (1952,'مسألة: وإن قال له عندي رهن وقال المالك: بل وديعة فالقول قول المالك','2955','1890','0');
INSERT INTO [t1153] VALUES (1953,'مسألة: وإن قال له علي ألف من ثمن مبيع لم أقبضه وقال المقر له بل هو دين في ذمتك فعلى وجهين','2956','1890','0');
INSERT INTO [t1153] VALUES (1954,'مسألة: وإن قال له عندي ألف وفسره بدين أو وديعة قبل منه','2956','1890','0');
INSERT INTO [t1153] VALUES (1955,'مسألة: وإن قال له علي ألف وفسره بوديعة لم يقبل قوله وإن ادعى بعد هذا تلفه لم يقبل قوله','2956','1890','0');
INSERT INTO [t1153] VALUES (1956,'مسألة: وإن قال له في هذا المال ألف لزمه تسليمه إليه','2960','1890','0');
INSERT INTO [t1153] VALUES (1957,'مسألة: وإن قال: له من مالي أو في مالي أو في ميراثي من أبي نصف ألف أو نصف داري هذه وفسره بالهبة وقال: بدا لي من تقبيضه قبل','2960','1890','0');
INSERT INTO [t1153] VALUES (1958,'مسألة: وإن قال: له في ميراث أبي ألف فهو دين على التركة','2961','1890','0');
INSERT INTO [t1153] VALUES (1959,'مسألة: وإن قال: له هذه الدار عارية ثبت لها حكم العارية','2961','1890','0');
INSERT INTO [t1153] VALUES (1960,'مسألة: وإن أقر أنه وهب أو رهن أو قبض أو أقر بقبض ثمن أو غيره ثم أنكر وقال: ما قضت ولا أقبضت وسأل أحلاف خصمه فهل تلزمه اليمين؟ على وجهين','2961','1890','0');
INSERT INTO [t1153] VALUES (1961,'مسألة: وإن باع شيئا ثم أقر أن المبيع لغيره لم يقبل قوله على المشتري','2962','1890','0');
INSERT INTO [t1153] VALUES (1962,'مسألة: وإن قال: لم يكن ملكي ثم ملكته بعد لم يقبل قوله','2962','1890','0');
INSERT INTO [t1153] VALUES (1963,'مسألة: وإن قال: ملكه لعمرو وغصبته من زيد فهي كالمسألة التي قبلها','2967','1890','0');
INSERT INTO [t1153] VALUES (1964,'مسألة: وإن قال: غصبته من أحدهما أو هو لأحدهما صح الإقرار','2967','1890','0');
INSERT INTO [t1153] VALUES (1965,'مسألة: وإن أقر بألف في وقتين لزمه ألف واحد','2968','1890','0');
INSERT INTO [t1153] VALUES (1966,'مسألة: وإن أقر بألف من ثمن عبد ثم أقر بألف من ثمن فرس أو قرض لزمه ألفان','2969','1890','0');
INSERT INTO [t1153] VALUES (1967,'مسألة: وإن ادعى رجلان دارا في يد غيرهما شركة بينهما بالسوية فأقر لأحدهما بنصفها فالمقر به بينهما نصفان','2969','1890','0');
INSERT INTO [t1153] VALUES (1968,'مسألة: وإن قال في مرض موته: هذا الألف لقطة فتصدقوا به لزم الورثة الصدقة بثلثه','2970','1890','0');
INSERT INTO [t1153] VALUES (1969,'مسألة: وإن أقر بها لهما معا فهي بينهما','2971','1890','0');
INSERT INTO [t1153] VALUES (1970,'مسألة: وإن أقر لأحدهما وحده فهي له ويحلف للآخر أنه لا يعلم أنها له وإن نكل قضي عليه بالنكول','2971','1890','0');
INSERT INTO [t1153] VALUES (1971,'مسألة: وإن ادعى رجل على الميت مائة دينا فأقر له ثم ادعى آخر مثل ذلك فأقر له فإن كان في مجلس واحد فهي بينهما وإن كان في مجلسين فهي للأول ولا شيء للثاني','2971','1890','0');
INSERT INTO [t1153] VALUES (1972,'مسألة: إلا أن يكون عدلا فيحلف الغريم مع شهادته ويأخذ مائة وتكون المائة الباقية بين الابنين','2972','1890','0');
INSERT INTO [t1153] VALUES (1973,'مسألة: وإن خلف ابنين ومائتين فادعى رجل مائة دينا على الميت فصدقه أحد الاثنين وأنكر الآخر لزم المقر نصفها','2972','1890','0');
INSERT INTO [t1153] VALUES (1974,'مسألة: وإن خلف ابنين وعبدين متساويي القيمة لا يملك غيرهما فقال أحد الابنين أبي أعتق هذا وقال الآخر بل أعتق هذا','2973','1890','0');
INSERT INTO [t1153] VALUES (1975,'باب الإقرار بالمجمل','2974','1890','0');
INSERT INTO [t1153] VALUES (1976,'مسألة: وإن فسره بحق شفعة أو مال قبل وإن قل وإن فسره بمال كقشرة جوزة أو ميتة أو خمر لم يقبل وإن فسره بكلب أو حد قذف فعلى وجهين','2976','1890','0');
INSERT INTO [t1153] VALUES (1977,'مسألة: وإن قال غصبت منه شيئا ثم فسره بنفسه أو ولده لم يقبل','2976','1890','0');
INSERT INTO [t1153] VALUES (1978,'مسألة: وإن قال له علي مال عظيم أو خطير أو كثير أو جليل قبل تفسيره بالكثير والقليل','2977','1890','0');
INSERT INTO [t1153] VALUES (1979,'وإن غصب شيئا فعجز عن رده فأدى قيمته فعليه أجرته إلى وقت أداء القيمة','2977','1890','0');
INSERT INTO [t1153] VALUES (1980,'مسألة: وإن قال: له علي دراهم كثيرة قبل تفسيره بثلاثة فصاعدا','2979','1890','0');
INSERT INTO [t1153] VALUES (1981,'مسألة: وإن قال: له علي كذا درهم أو كذا وكذا درهم أو كذا كذا درهم','2979','1890','0');
INSERT INTO [t1153] VALUES (1982,'مسألة: وإن قال: كذا درهما بالنصب لزمه درهم','2979','1890','0');
INSERT INTO [t1153] VALUES (1983,'مسألة: وإن قال: كذا وكذا درهما بالنصب فقال ابن حامد والقاضي: يلزمه درهم','2980','1890','0');
INSERT INTO [t1153] VALUES (1984,'المسألة الثانية: إذا قال: كذا كذا بغير عطف فالحكم فيها كالحكم في كذا بغير تكرير سواء','2981','1890','0');
INSERT INTO [t1153] VALUES (1985,'المسألة الثالثة: إذا عطف فقال: كذا وكذا درهم بالرفع لزمه درهم واحد','2981','1890','0');
INSERT INTO [t1153] VALUES (1986,'مسألة: وإن قال: له علي ألف ودرهم أو ألف ودينار أو ألف وثوب أو فرس أو درهم وألف أو دينار وألف فقال ابن حامد والقاضي: الألف من جنس ما عطف عليه','2982','1890','0');
INSERT INTO [t1153] VALUES (1987,'مسألة: وإن قال: له علي ألف رجع في تفسيره إليه فإن فسره بأجناس قبل منه','2982','1890','0');
INSERT INTO [t1153] VALUES (1988,'مسألة: وإن قال: له علي ألف وخمسون درهما أو خمسون وألف درهم فالجميع دراهم','2983','1890','0');
INSERT INTO [t1153] VALUES (1989,'مسألة: وهذا اختيار ابن حامد والقاضي، وقال أبو الحسن التميمي وأبو الخطاب: يكون الألف مبهما يرجع في تفسيره إليه وهو قول مالك والشافعي','2984','1890','0');
INSERT INTO [t1153] VALUES (1990,'مسألة: وإن قال: له في هذا العبد شركة أو هو شريكي فيه أو هو شركة بينهما رجع إلى تفسير نصيب الشريك إليه','2985','1890','0');
INSERT INTO [t1153] VALUES (1991,'مسألة: وإن قال له علي أكثر من مال فلان قيل له فسر فإن فسره بأكثر منه قدرا قبل وإن قال: أردت أكثر بقاء ونفعا لأن الحلال أنفع من الحرام قبل قوله مع يمينه','2985','1890','0');
INSERT INTO [t1153] VALUES (1992,'مسألة: ولو ادعى عليه دينا فقال لفلان علي أكثر مما لك، وقال: أردت التهزئ لهما لزمه ويرجع في تفسيره إليه في أحد الوجهين','2986','1890','0');
INSERT INTO [t1153] VALUES (1993,'مسألة: وإن قال: له علي درهم فوق درهم أو تحت درهم أو فوقه أو تحته أو قبله أو بعده أو معه درهم أو درهم ودرهم أو درهم بل درهمان أو درهمان بل درهم لزمه درهمان','2987','1890','0');
INSERT INTO [t1153] VALUES (1994,'مسألة: وإن قال: له علي درهم ودرهم أو درهم فدرهم أو درهم ثم درهم لزمه درهمان','2988','1890','0');
INSERT INTO [t1153] VALUES (1995,'مسألة: فإن قال: له علي درهم بل درهمان أو درهم لكن درهمان لزمه درهمان وبه قال الشافعي، وقال زفر وداود تلزمه ثلاثة لأن بل للإضراب فلما أقر بدرهم وأضرب عنه لزمه لأنه لا يقبل رجوعه عما أقر به ولزمه الدرهمان اللذان أقر بهما','2989','1890','0');
INSERT INTO [t1153] VALUES (1996,'مسألة: وإن قال: له علي درهمان بل درهم أو درهم لكن درهم فهل يلزمه درهم أو درهمان؟ على وجهين','2989','1890','0');
INSERT INTO [t1153] VALUES (1997,'مسألة: وإن قال: درهم في دينار لزمه درهم وإن قال: له علي درهم في عشرة لزمه درهم إلا أن يريد الحساب فيلزمه عشرة، أما إذا قال: له عندي درهم في دينار','2990','1890','0');
INSERT INTO [t1153] VALUES (1998,'مسألة: وإن قال: له عندي تمر في جراب أو سكين في قراب أو ثوب في منديل أو عبد عليه عمامة أو دابة عليها سرج فهل يكون مقرا بالمظروف دون الظرف؟ وجهان','2990','1890','0');
INSERT INTO [t1153] VALUES (1999,'مسألة: ولو قال: له علي هذا الدرهم بل هذان الدرهمان لزمته الثلاثة','2990','1890','0');
INSERT INTO [t1153] VALUES (2000,'مسألة: وإن قال: له عندي خاتم فيه فص فهو مقر بهما','2991','1890','0');
INSERT INTO [t1153] VALUES (2001,'مسألة: وإن قال: له علي درهم أو دينار لزمه أحدهما يرجع في تفسيره إليه','2991','1890','0');
INSERT INTO [t1153] VALUES (2002,'كتاب العارية','2992','0','0');
INSERT INTO [t1153] VALUES (2003,'كتاب العارية','2992','2002','0');
INSERT INTO [t1153] VALUES (2004,'مسألة: وهي هبة منفعه تجوز في كل المنافع إلا منافع البضع','2993','2002','0');
INSERT INTO [t1153] VALUES (2005,'مسألة: ولا تجوز إعارة العبد المسلم لكافر','2994','2002','0');
INSERT INTO [t1153] VALUES (2006,'مسألة: ويكره إعارة الأمة الشابة لرجل غير محرمها','2994','2002','0');
INSERT INTO [t1153] VALUES (2007,'مسألة: وللمعير الرجوع فيها متى شاء ما لم يأذن في شغلها بشيء يستضر المستعير برجوعه','2995','2002','0');
INSERT INTO [t1153] VALUES (2008,'مسألة: فإن أذن له في شغله بشيء يستضر المستعير برجوعه فيه لم يجز له الرجوع','2995','2002','0');
INSERT INTO [t1153] VALUES (2009,'مسألة: واستعارة والديه للخدمة','2995','2002','0');
INSERT INTO [t1153] VALUES (2010,'مسألة: وإن أعاره أرضا للدفن لم يرجع حتى يبلى الميت','2996','2002','0');
INSERT INTO [t1153] VALUES (2011,'مسألة: وإن أعاره حائطا ليضع عليه أطراف خشبه لم يرجع ما دام عليه','2996','2002','0');
INSERT INTO [t1153] VALUES (2012,'مسألة: وإن سقط عنه لهدم أو غيره لم يملك رده','2996','2002','0');
INSERT INTO [t1153] VALUES (2013,'مسألة: وإن أعاره أرضا للزرع لم يرجع إلى الحصاد إلا أن يكون مما يحصد قصيلا فيحصده','2997','2002','0');
INSERT INTO [t1153] VALUES (2014,'مسألة: وإن أعارها للغراس والبناء وشرط عليه القلع في وقت أو عند رجوعه ثم رجع لزمه القلع','2997','2002','0');
INSERT INTO [t1153] VALUES (2015,'مسألة: وإن لم يشترط لم يلزمه إلا أن يضمن له المعير النقص','2998','2002','0');
INSERT INTO [t1153] VALUES (2016,'مسألة: فإن أبى القلع في الحال التي لا يجبر عليه فيها فبذل له المعير قيمة الغراس والبناء ليملكه أجبر المستعير عليه','2998','2002','0');
INSERT INTO [t1153] VALUES (2017,'مسألة: وإن أبيا البيع ترك بحاله','2999','2002','0');
INSERT INTO [t1153] VALUES (2018,'مسألة: وللمعير التصرف في أرضه على وجه لا يضر بالشجر','2999','2002','0');
INSERT INTO [t1153] VALUES (2019,'مسألة: ولم يذكر أصحابنا عليه أجرة من حين الرجوع في هذه المسائل','2999','2002','0');
INSERT INTO [t1153] VALUES (2020,'مسألة: فإن امتنع المعير من دفع القيمة وارش النقص وامتنع المستعير من القلع ودفع الأجر لم يقلع','2999','2002','0');
INSERT INTO [t1153] VALUES (2021,'مسألة: وإن غرس أو بنى بعد الرجوع أو بعد الوقت فهو غاصب يأتي حكمه','3000','2002','0');
INSERT INTO [t1153] VALUES (2022,'مسألة: وإن حمل السيل بذرا إلى أرض فنبت فيها فهو لصاحبه','3001','2002','0');
INSERT INTO [t1153] VALUES (2023,'مسألة: وإن حمل السيل نوى غرس رجل فنبت في أرض غيره كالزيتون ونحوه فهو لمالك النوى','3002','2002','0');
INSERT INTO [t1153] VALUES (2024,'مسألة: والعارية مضمونة بقيمتها يوم التلف وإن شرط نفي ضمانها سواء تعدى المستعير فيها أو لم يتعد','3003','2002','0');
INSERT INTO [t1153] VALUES (2025,'مسألة: وكل ما كان أمانة لا يصير مضمونا بشرطه لأن مقتضى العقد كونه أمانة فإذا شرط ضمانه فقد التزم ضمان ما لم يوجد سبب ضمانه فلم يلزمه كما لو اشترط ضمان الوديعة','3005','2002','0');
INSERT INTO [t1153] VALUES (2026,'مسألة: وإن تلفت أجزاؤها بالإستعمال كخمل المنشفة فعلى وجهين','3005','2002','0');
INSERT INTO [t1153] VALUES (2027,'مسألة: وليس للمستعير أن يعير','3006','2002','0');
INSERT INTO [t1153] VALUES (2028,'مسألة: وإن تلفت عند الثاني فللمالك تضمين أيهما شاء','3007','2002','0');
INSERT INTO [t1153] VALUES (2029,'مسألة: وعلى المستعير مؤنة رد العارية','3008','2002','0');
INSERT INTO [t1153] VALUES (2030,'مسألة: إن رد الدابة إلى إصطبل المالك أو غلامه لم يبرأ إلا بردها إلى من جرت عادته بجريان ذلك على يده كالسائس ونحوه','3008','2002','0');
INSERT INTO [t1153] VALUES (2031,'مسألة: وإن كان بعد مضي مدة لها أجرة فالقول قول المالك فيما مضى من المدة دون ما بقي منها','3009','2002','0');
INSERT INTO [t1153] VALUES (2032,'مسألة: وهل يستحق أجر المثل أو المدعي إذا زاد عليها؟ على وجهين','3010','2002','0');
INSERT INTO [t1153] VALUES (2033,'مسألة: وإن اختلفا بعد تلف الدابة فقال المالك: أعرتك وقال الراكب: بل أجرتني فالقول قول المالك إذا كان قبل مضي مدة لها أجرة','3011','2002','0');
INSERT INTO [t1153] VALUES (2034,'مسألة: وإن قال: أجرتني أو أعرتني قال: بل غصبتني فالقول قول المالك وقيل: قول الغاصب','3011','2002','0');
INSERT INTO [t1153] VALUES (2035,'كتاب الغصب','3012','0','0');
INSERT INTO [t1153] VALUES (2036,'كتاب الغصب','3012','2035','0');
INSERT INTO [t1153] VALUES (2037,'مسألة: ويضمن العقار بالغصب ويتصور غصب الأراضي والدور ويجب ضمانه على غاصبه','3013','2035','0');
INSERT INTO [t1153] VALUES (2038,'مسألة: وتضمن أم الولد والعقار بالغصب','3013','2035','0');
INSERT INTO [t1153] VALUES (2039,'مسألة: وإن غصب كلبا فيه نفع أو خمر ذمي لزمه ردهما','3014','2035','0');
INSERT INTO [t1153] VALUES (2040,'مسألة: وإن غصب جلد ميتة فهل يجب رده؟ على وجهين','3016','2035','0');
INSERT INTO [t1153] VALUES (2041,'مسألة: وإن دبغه وقلنا بطهارته يلزمه رده كالخمر إذا تخللت ويحتمل أن لا يجب رده','3016','2035','0');
INSERT INTO [t1153] VALUES (2042,'مسألة: وإن استولى على حر لم يضمنه بذلك','3016','2035','0');
INSERT INTO [t1153] VALUES (2043,'مسألة: وإن استعمل الحر كرها فعليه أجرته','3017','2035','0');
INSERT INTO [t1153] VALUES (2044,'مسألة: وإن خلطه بما يتميز منه لزمه تخليصه ورده','3018','2035','0');
INSERT INTO [t1153] VALUES (2045,'مسألة: وإن بنى عليه لزمه رده إلا أن يكون قد بلي','3019','2035','0');
INSERT INTO [t1153] VALUES (2046,'مسألة: وإن سمر بالمسامير بابا لزمه قلعها وردها','3019','2035','0');
INSERT INTO [t1153] VALUES (2047,'مسألة: وإن أدركها ربها والزرع قائم خير بين تركه إلى الحصاد بأجرة مثله وبين أخذه بعوضه وهل ذلك قيمته أو نفقته؟ على وجهين','3022','2035','0');
INSERT INTO [t1153] VALUES (2048,'مسألة: إن زرع الأرض وردها بعد أخذ الزرع فعليه أجرتها','3022','2035','0');
INSERT INTO [t1153] VALUES (2049,'مسألة: وإن غرس أو بنى أخذ بقلع غرسه وبنائه وتسوية الأرض وأرش نقصها وأجرتها','3025','2035','0');
INSERT INTO [t1153] VALUES (2050,'مسألة: وإن غصب لوحا فرقع به سفينة لم يقلع حتى ترسي إذا كانت السفينة يخاف غرقها بقلع اللوح لم يقلع حتى تخرج إلى الساحل','3030','2035','0');
INSERT INTO [t1153] VALUES (2051,'مسألة: وإن غصب خيطا فخاط به جرح حيوان وخيف عليه من قلعه فعليه قيمته إلا أن يكون الحيوان مأكولا للغاصب فهل يلزمه رده ويذبح الحيوان؟ على وجهين','3030','2035','0');
INSERT INTO [t1153] VALUES (2052,'مسألة: فإن مات الحيوان لزمه رده إلا أن يكون آدميا معصوما لأن غير الآدمي لا حرمة له بعد الموت','3031','2035','0');
INSERT INTO [t1153] VALUES (2053,'مسألة: وإن غصب جارحا فصاد أو شبكة أو شركا فأمسك شيئا أو فرسا فصاد عليه أو غنم فهو لمالكه','3032','2035','0');
INSERT INTO [t1153] VALUES (2054,'مسألة: وإن غصب ثوبا فقصره أو غزلا فنسجه أو فضة أو حديدا فضربه أو خشبا فنجره أو شاة فذبحها وشواها رد ذلك بزيادته وأرش نقصه','3032','2035','0');
INSERT INTO [t1153] VALUES (2055,'مسألة: وإن غصب أرضا فحفر فيها بئرا ووضع ترابها في أرض مالكها لم يملك طمها إذا أبرأها المالك من ضمان ما يتلف بها في أحد الوجهين','3035','2035','0');
INSERT INTO [t1153] VALUES (2056,'مسألة: وإن غصب حبا فزرعه أو نوى فصار غرسا أو بيضا فصار فراخا رده ولا شيء للغاصب','3036','2035','0');
INSERT INTO [t1153] VALUES (2057,'مسألة: وإن غضبه وجني عليه ضمنه بأكثر الأمرين','3037','2035','0');
INSERT INTO [t1153] VALUES (2058,'مسألة: وإن جنى عليه غير الغاصب فله تضمين الغاصب أكثر الأمرين ويرجع الغاصب على الجاني بأرش الجناية وله تضمين الجاني أرش الجناية وتضمين الغاصب ما بقي من النقص','3038','2035','0');
INSERT INTO [t1153] VALUES (2059,'مسألة: وإن غصب عبدا فخصاه لزمه رده ورد قيمته','3039','2035','0');
INSERT INTO [t1153] VALUES (2060,'مسألة: وإن نقصت قيمة العين لتغير الأسعار لم يضمن نص عليه','3040','2035','0');
INSERT INTO [t1153] VALUES (2061,'مسألة: وإن نقصت القيمة لمرض أو غيره ثم عادت ببرئه لم يلزمه شيء إلا رده','3041','2035','0');
INSERT INTO [t1153] VALUES (2062,'مسألة: وإن زادت القيمة لسمن أو غيره ثم نقصت ضمن الزيادة','3041','2035','0');
INSERT INTO [t1153] VALUES (2063,'مسألة: فإن عاد مثل الزيادة الأولى من جنسها مثل إن كانت قيمتها مائة فسمنت فبلغت ألفا ثم هزلت فعادت إلى مائة ثم سمنت فعادت إلى ألف ففيه وجهان','3043','2035','0');
INSERT INTO [t1153] VALUES (2064,'مسألة: وإن كانت من غير جنس الأولى لم يسقط ضمانها','3044','2035','0');
INSERT INTO [t1153] VALUES (2065,'مسألة: وإن غصب عبدا مفرطا في السمن فهزل فزادت قيمته أو لم ينقص رده ولا شيء عليه','3044','2035','0');
INSERT INTO [t1153] VALUES (2066,'مسألة: وإن نقص المغصوب نقصا غير مستقر كحنطة ابتلت وعفنت وخشي فسادها فعليه ضمان ما نقصه','3045','2035','0');
INSERT INTO [t1153] VALUES (2067,'مسألة: وإن جنى المغصوب فعليه أرش جنايته سواء جنى على سيده أو غيره','3046','2035','0');
INSERT INTO [t1153] VALUES (2068,'مسألة: وجنايته على الغاصب وعلى ماله هدر','3047','2035','0');
INSERT INTO [t1153] VALUES (2069,'مسألة: ويضمن زوائد الغصب كالولد والثمرة إذا تلفت أو نقصت كالأصل سواء كان منفردا أو مع أصله مثل ثمرة الشجرة وولد الحيوان','3047','2035','0');
INSERT INTO [t1153] VALUES (2070,'مسألة: فإن خلط المغصوب بماله على وجه لا يتميز منه مثل أن خلط حنطة أو زيتا بمثله لزمه مثله منه في أحد الوجهين وفي الآخر يلزمه مثله من حيث شاء','3048','2035','0');
INSERT INTO [t1153] VALUES (2071,'مسألة: وإن خلطه بدونه أو خير منه أو بغير جنسه فله مثله في قياس التي قبلها','3048','2035','0');
INSERT INTO [t1153] VALUES (2072,'مسألة: وإن غصب ثوبا فصبغه أو سويقا فلته بزيت وكان الصبغ والزيت من مال الغاصب فإن نقصت قيمتهما أو قيمة أحدهما ضمن الغاصب النقص','3050','2035','0');
INSERT INTO [t1153] VALUES (2073,'مسألة: وإن لم تنقص ولم تزد مثل أن كانت قيمة كل واحد منهما خمسة فصارت قيمتهما عشرة فهما شريكان','3050','2035','0');
INSERT INTO [t1153] VALUES (2074,'مسألة: وإن زادت قيمتهما مثل إن كانت قيمة كل واحد منهما خمسة فصارت قيمتهما عشرين فإن كان ذلك لزيادة الثياب في السوق كانت الزيادة لصاحب الثوب وإن كانت لزيادة الصبغ فهي لصاحب الصبغ وإن كانت لزيادتهما معا فهي بينهما على قدر زيادة كل واحد منهما','3050','2035','0');
INSERT INTO [t1153] VALUES (2075,'مسألة: وإن أراد أحدهما قلع الصبغ لم يجبر الآخر عليه ويحتمل أن يجبر إذا ضمن له الغاصب النقص','3051','2035','0');
INSERT INTO [t1153] VALUES (2076,'مسألة: وإن وهب الغاصب الصبغ للمالك أو وهبه تزويق الدار ونحوها فهل يلزمه قبوله؟ على وجهين','3052','2035','0');
INSERT INTO [t1153] VALUES (2077,'مسألة: وإن غصب صبغا فصبغ به ثوبا أو زيتا فلت به سويقا احتمل أن يكون كذلك','3052','2035','0');
INSERT INTO [t1153] VALUES (2078,'مسألة: وإن غصب ثوبا وصبغا فصبغه به رده وأرش نقصه ولا شيء في زيادته','3053','2035','0');
INSERT INTO [t1153] VALUES (2079,'مسألة: وإن ولدت فالولد رقيق للسيد لأنه من نمائها وأجزائها ولا يلحق نسبه بالواطئ لأنه من زنا وضعته حيا وجب رده معها كزوائد الغصب وإن أسقطته ميتا لم يضمن لأننا لا نعلم حياته قبل هذا','3054','2035','0');
INSERT INTO [t1153] VALUES (2080,'مسألة: ويضمن نقص الولادة ولا ينجز بزيادتها بالولد','3055','2035','0');
INSERT INTO [t1153] VALUES (2081,'مسألة: وإن باعها أو وهبها لعالم بالغصب فوطئها فللمالك تضمين أيهما شاء نقصها ومهرها وأجرتها وقيمة ولدها إن تلف فإن ضمن الغاصب رجع على الآخر ولا يرجع الآخر عليه','3056','2035','0');
INSERT INTO [t1153] VALUES (2082,'مسألة: وإن لم يعلما بالغصب فضمنهما رجعا على الغاصب','3057','2035','0');
INSERT INTO [t1153] VALUES (2083,'مسألة: وإن ولدت منه فالولد حر','3057','2035','0');
INSERT INTO [t1153] VALUES (2084,'مسألة: ويفديه بمثله في صفاته تقريبا','3058','2035','0');
INSERT INTO [t1153] VALUES (2085,'مسألة: ويرجع بذلك على الغاصب','3058','2035','0');
INSERT INTO [t1153] VALUES (2086,'وإن تلفت فعليه قيمتها لمالكها كما يلزمه نقصها ولا يرجع بها على الغاصب إن كان مشتريا','3059','2035','0');
INSERT INTO [t1153] VALUES (2087,'وعنه أن ما حصلت له منفعة كالأجرة والمهر وأرش البكارة لا يرجع به','3059','2035','0');
INSERT INTO [t1153] VALUES (2088,'فإن ضمن الغاصب رجع على المشتري بما لا يرجع به عليه كما لو رجع به على المشتري لا يرجع به المشتري على الغاصب','3060','2035','0');
INSERT INTO [t1153] VALUES (2089,'فإن ولدت من زوج فمات الولد ضمنه بقيمته','3060','2035','0');
INSERT INTO [t1153] VALUES (2090,'وإن أغارها فتلفت عند المستعير استقر ضمان قيمتها عليه وضمان الأجر على الغاصب','3061','2035','0');
INSERT INTO [t1153] VALUES (2091,'وإن اشترى أرضا فغرسها أو بنى فيها فخرجت مستحقة وقلع غرسه وبناءه رجع المشتري على البائع بما غرمه','3061','2035','0');
INSERT INTO [t1153] VALUES (2092,'وإن أطعمه لمالكه ولم يعلم لم يبرأ نص عليه','3062','2035','0');
INSERT INTO [t1153] VALUES (2093,'وإن أطعم المغصوب لعالم بالغصب استقر الضمان عليه','3062','2035','0');
INSERT INTO [t1153] VALUES (2094,'وإن لم يعلم وقال الغاصب كله فإنه طعامي استقر الضمان على الغاصب','3062','2035','0');
INSERT INTO [t1153] VALUES (2095,'وإن لم يقل ففي أيهما يستقر عليه الضمان؟ وجهان أحدهما','3062','2035','0');
INSERT INTO [t1153] VALUES (2096,'وإن رهنه عند مالكه أو أودعه إياه أو أجره أو استأجره على قصارته أو خياطته ولم يعلم لم يبرأ من الضمان','3063','2035','0');
INSERT INTO [t1153] VALUES (2097,'وإن اشترى عبدا فأعتقه فادعى رجل أن البائع غصبه فصدقه أحدهما لم يقبل على الآخر وإن صدقاه مع العبد لم يبطل العتق ويستقر الضمان على المشتري ويحتمل أن يبطل العتق إذا صدقوه كلهم','3064','2035','0');
INSERT INTO [t1153] VALUES (2098,'وإن أعاره إياه بريء علم أو يعلم','3064','2035','0');
INSERT INTO [t1153] VALUES (2099,'وإن أعوز المثل فعليه قيمة مثله يوم إعوازه','3066','2035','0');
INSERT INTO [t1153] VALUES (2100,'وإن لم يكن مثليا ضمنه قول باقي الجماعة','3067','2035','0');
INSERT INTO [t1153] VALUES (2101,'فإن كان مصوغا أو تبرا تخالف قيمته وزنه قومه بغير جنسه','3070','2035','0');
INSERT INTO [t1153] VALUES (2102,'فإن كان محلى بالنقدين معا قومه بما شاء منهما','3071','2035','0');
INSERT INTO [t1153] VALUES (2103,'وإن تلف بعض المغصوب فنقصت قيمة باقيه كزوجي خف تلف أحدهما فعليه رد الباقي وقيمة التالف وأرش النقص وقيل لا يلزمه أرش النقص','3072','2035','0');
INSERT INTO [t1153] VALUES (2104,'وإن غصب عبدا فأبق أو فرسا فشرد أو شيئا تعذر رده مع بقائه ضمن قيمته فإن قدر عليه بعد رده وأخذ القيمة','3074','2035','0');
INSERT INTO [t1153] VALUES (2105,'وإن غصب عصيرا فتخمر فعليه مثله','3075','2035','0');
INSERT INTO [t1153] VALUES (2106,'وإن تلف المغصوب فعليه أجرته إلى وقت تلفه','3077','2035','0');
INSERT INTO [t1153] VALUES (2107,'وإن اتجر بالدراهم فالربح لمالكها','3078','2035','0');
INSERT INTO [t1153] VALUES (2108,'وإن إشترى في ذمته ثم نقدها احتمل أن يكون الربح للغاصب','3079','2035','0');
INSERT INTO [t1153] VALUES (2109,'وإن اختلفا في قيمة المغصوب أو قدره أو صناعة فيه فالقول قول الغاصب','3080','2035','0');
INSERT INTO [t1153] VALUES (2110,'وإن اختلفا في رده أو عيب فيه فالقول قول المالك','3080','2035','0');
INSERT INTO [t1153] VALUES (2111,'وإن بقيت في يده غصوب لا يعلم أربابها تصدق بها عنهم بشرط الضمان كاللقطة','3081','2035','0');
INSERT INTO [t1153] VALUES (2112,'وإن فتح قفصا عن طائر فطار أو حل قيد عبده أو رباط فرسه فذهبت ضمنه','3082','2035','0');
INSERT INTO [t1153] VALUES (2113,'وإن ربط دابة في طريق فأتلفت أو اقتنى كلبا عقورا فعقر أو خرق ثوبا ضمن','3083','2035','0');
INSERT INTO [t1153] VALUES (2114,'وإن حل وكاء زق مائع أو جامد فأذابته الشمس أو بقي بعد حله فألقته الريح فاندفق ضمنه','3083','2035','0');
INSERT INTO [t1153] VALUES (2115,'وإن أجج نارا في ملكه أو سقى فتعدى إلى ملك غيره فأتلفه ضمن إذا كان قد أسرف فيه أو فرط وإلا فلا','3084','2035','0');
INSERT INTO [t1153] VALUES (2116,'إلا أن يكون دخل منزله بغير إذنه لأنه متعد بالدخول فقد تسبب إلى إتلاف نفسه بجنايته','3084','2035','0');
INSERT INTO [t1153] VALUES (2117,'وقيل في الكلب روايتان في الجملة إحداهما','3084','2035','0');
INSERT INTO [t1153] VALUES (2118,'وإن حفر في فنائه بئرا لنفسه ضمن ما تلف به والفناء ما كان خارج الدار قريبا منها','3085','2035','0');
INSERT INTO [t1153] VALUES (2119,'وإن حفرها في سابلة لنفع المسلمين لم يضمن في أصح الروايتين','3086','2035','0');
INSERT INTO [t1153] VALUES (2120,'وإن بسط في مسجد حصيرا أو علق فيه قنديلا أو سقفه أو نصب عليه بابا أو جعل فيه رفا لينتفع به الناس فتلف به شيء فلا ضمان عليه','3087','2035','0');
INSERT INTO [t1153] VALUES (2121,'وإن جلس في مسجد أو طريق واسع فعثر به حيوان فتلف لم يضمن في أحد الوجهين','3087','2035','0');
INSERT INTO [t1153] VALUES (2122,'وإن أخرج جناحا أو ميزابا إلى الطريق فسقط على شي فأتلفه ضمن','3087','2035','0');
INSERT INTO [t1153] VALUES (2123,'وإن مال حائطه فلم يهدمه حتى أتلف شيئا لم يضمنه نص عليه وأومأ في موضع أنه إن تقدم إليه لنقضه وأشهد عليه فلم يفعل ضمن','3088','2035','0');
INSERT INTO [t1153] VALUES (2124,'وما أتلفت البهيمة فلا ضمان على صاحبها إلا أن تكون في يد إنسان كالراكب والسائق والقائد قد يضمن ما جنت يدها أو فمها دون ما جنت رجلها','3091','2035','0');
INSERT INTO [t1153] VALUES (2125,'ويضمن ما أفسدت من الزرع والشجر ليلا ولا يضمن ما أفسدت من ذلك نهارا','3092','2035','0');
INSERT INTO [t1153] VALUES (2126,'ومن صال عليه آدمي أو غيره فقتله دفعا عن نفسه لم يضمنه لأنه قتله بالدفع الجائز فلم يجب ضمانه فإن كان الصائل بهيمة فلم يمكنه دفعها إلا بقلتها جاز له قتلها إجماعا ولا يضمنها إذا كانت لغيره','3093','2035','0');
INSERT INTO [t1153] VALUES (2127,'وإن اصطدمت سفينتان فغرقتا ضمن كل واحد منهم سفينة الآخر وما فيها','3094','2035','0');
INSERT INTO [t1153] VALUES (2128,'وإن كانت إحداهما منحدرة فعلى صاحبها ضمان المصعدة إلا أن يكون عليه ريح فلم يقدر على ضبطها','3094','2035','0');
INSERT INTO [t1153] VALUES (2129,'وإن كسر مزمارا أو طنبورا أو صليبا لم يضمنه','3095','2035','0');
INSERT INTO [t1153] VALUES (2130,'وإن كسر آنية ذهب أو فضة لم يضمنها','3096','2035','0');
INSERT INTO [t1153] VALUES (2131,'وإن كسر إناء خمر لم يضمنه في أصح الروايتين','3096','2035','0');
INSERT INTO [t1153] VALUES (2132,'باب الشفعة','3097','2035','0');
INSERT INTO [t1153] VALUES (2133,'ولا يحل الإحتيال على إسقاطها فإن فعل لم يسقط','3098','2035','0');
INSERT INTO [t1153] VALUES (2134,'ولا تثبت إلا بشروط خمسة أحدها أن يكون مبيعا فلا شفعة فيما انتقل بغير عوض بحال','3101','2035','0');
INSERT INTO [t1153] VALUES (2135,'ولا تجب فيما عوضه غير المال كالصداق وعوض الخلع والصلح عن دم العمد في أحد الوجهين','3102','2035','0');
INSERT INTO [t1153] VALUES (2136,'الثاني أن يكون شقصا مشاعا من عقار فيقسم فأما المقسوم المحدود فلا شفعة لمجاوره فيه','3104','2035','0');
INSERT INTO [t1153] VALUES (2137,'ولا تجب فيما ليس بعقار كالشجر والحيوان والبناء المفرد في إحدى الروايتين إلا أن الغراس والبناء تبعا للأرض ولا يؤخذ الزرع والثمرة تبعا في أحد الوجهين','3108','2035','0');
INSERT INTO [t1153] VALUES (2138,'إلا أن يعلم وهو غائب فيشهد على الطلب ثم إن أخر الطلب بعد الإشهاد مع إمكانه أو ترك الإشهاد أو لم يشهد لكن سار في طلبها فعلى وجهين','3113','2035','0');
INSERT INTO [t1153] VALUES (2139,'فإن ترك الطلب والإشهاد لعجزه عنهما كالمريض والمحبوس ومن لا يجد من يشهده لم تبطل شفعته','3115','2035','0');
INSERT INTO [t1153] VALUES (2140,'أو لإظهارهم زيادة في الثمن أو نقصا في المبيع أو أن المشتري غيره أو أخبره من لا يقبل خبره فلم يصدقه أو قال للمشتري بعني ما اشتريت أو صالحني سقطت شفعته','3117','2035','0');
INSERT INTO [t1153] VALUES (2141,'وإن قال الشفيع للمشتري بعني ما اشتريت أو قاسمني بطلت شفعته','3119','2035','0');
INSERT INTO [t1153] VALUES (2142,'وإن دل في البيع لم تبطل شفعته','3120','2035','0');
INSERT INTO [t1153] VALUES (2143,'وإن توكل الشفيع في البيع لم تسقط شفعته بذلك سواء توكل للبائع أو للمشتري','3120','2035','0');
INSERT INTO [t1153] VALUES (2144,'وإن جعل له الخيار فاختار إمضاء البيع فهو على شفعته','3121','2035','0');
INSERT INTO [t1153] VALUES (2145,'وإن أسقط شفعته فقبل البيع لم تسقط ويحتمل أن تسقط','3122','2035','0');
INSERT INTO [t1153] VALUES (2146,'وإن كانا شفيعين فالشفعة بينهما على قدر ملكهما وعنه على عدد الرؤوس','3128','2035','0');
INSERT INTO [t1153] VALUES (2147,'فإن ترك أحدهما شفعته لم يكن للآخر إلا أن يأخذ الكل أو يترك','3130','2035','0');
INSERT INTO [t1153] VALUES (2148,'وإن كان المشتري شريكا فالشفعة بينه وبين الآخر','3133','2035','0');
INSERT INTO [t1153] VALUES (2149,'وإن ترك المشتري شفعته ليوجب الكل على شريكه لم يكن له ذلك','3133','2035','0');
INSERT INTO [t1153] VALUES (2150,'وإذا كانت دار بين اثنين فباع أحدهما نصيبه لأجنبي صفقتين ثم علم الشريك فله أن يأخذ بالبيعين وله أن يأخذ بأحدهما فإن أخذ بالثاني شاركه المشتري في شفعته في أحد الوجهين وإن أخذ بالأول لم يشاركه وإن أخذ بهما جميعا لم يشاركه في شفعة الأول وهل يشاركه في شف','3134','2035','0');
INSERT INTO [t1153] VALUES (2151,'وإن اشترى إثنان حق واحد فللشفيع أخذ حق أحدهما','3136','2035','0');
INSERT INTO [t1153] VALUES (2152,'وإن اشترى واحد حق إثنين أو اشترى شقصين من دارين صفقة واحدة فللشفيع أخذ أحدهما على أصح الوجهين','3138','2035','0');
INSERT INTO [t1153] VALUES (2153,'وإن تلف بعض المبيع فله أخذ الباقي بحصته من الثمن وقال ابن حامد إن كان تلفه بفعل الله تعالى فليس له أخذه إلا بجميع الثمن','3140','2035','0');
INSERT INTO [t1153] VALUES (2154,'وإن باع شقصا وسيفا فللشفيع أخذ الشقص بحصته من الثمن ويحتمل أن لا يجوز','3140','2035','0');
INSERT INTO [t1153] VALUES (2155,'ولا شفعة بشركة الوقف في أحد الوجهين','3142','2035','0');
INSERT INTO [t1153] VALUES (2156,'فإن اشترى إثنان دارا صفقة واحدة فلا شفعة لأحدهما على صاحبه','3142','2035','0');
INSERT INTO [t1153] VALUES (2157,'فإن إدعى كل واحد منهما السبق فتحالفا أو تعارضت بينتاهما فلا شفعة لهما','3142','2035','0');
INSERT INTO [t1153] VALUES (2158,'وإن باع فله الأخذ بأي البيعين شاء فإن أخذ بالأول رجع الثاني على الأول','3144','2035','0');
INSERT INTO [t1153] VALUES (2159,'وإن فسخ العقد بعيب أو إقالة أو تحالف فللشفيع أخذه ويأخذه في التحالف بما حلف عليه البائع','3145','2035','0');
INSERT INTO [t1153] VALUES (2160,'وإن أجره المشتري أخذه الشفيع وله الأجرة من يوم أخذه','3149','2035','0');
INSERT INTO [t1153] VALUES (2161,'وإن استغله المشتري فالغلة له','3149','2035','0');
INSERT INTO [t1153] VALUES (2162,'وإن أخذه الشفيع وفيه زرع أو ثمرة ظاهرة فهي للمشتري مبقاة إلى الحصاد والجذاذ','3149','2035','0');
INSERT INTO [t1153] VALUES (2163,'وإن قاسم المشتري وكيل الشفيع أو قاسم الشفيع لكونه أظهر له زيادة في الثمن أو نحوه وغرس أو بنى فللشفيع أن يدفع إليه قيمة الغراس والبناء ويمكله أو يقلعه ويضمن النقص فإن اختار الشفيع أخذه واختار المشتري قلعه فله ذلك إذا لم يكن فيه ضرر','3150','2035','0');
INSERT INTO [t1153] VALUES (2164,'فإن باع الشفيع ملكه قبل العلم لم تسقط شفعته في أحد الوجهين وللمشتري الشفعة فيما باعه في أصح الوجهين','3152','2035','0');
INSERT INTO [t1153] VALUES (2165,'وإن ماتت بطلت شفعته إلا أن يموت بعد طلبها فتكون لوارثه','3154','2035','0');
INSERT INTO [t1153] VALUES (2166,'وما يزاد في ثمن أو يحط منه في مدة الخيار يلحق به وما بعد ذلك لا يحلق به','3160','2035','0');
INSERT INTO [t1153] VALUES (2167,'وإن كان مؤجلا أخذ الشفيع بالأجل إن كان مليئا وإلا أقام كفيلا مليئا وأخذه به','3161','2035','0');
INSERT INTO [t1153] VALUES (2168,'وإن كان الثمن عرضا أعطاه مثله إن كان ذا مثل وإلا أعطاه قيمته','3161','2035','0');
INSERT INTO [t1153] VALUES (2169,'وإن اختلفا في قدر الثمن فالقول قول المشتري إلا أن تكون للشفيع بينة','3162','2035','0');
INSERT INTO [t1153] VALUES (2170,'وإن قال المشتري اشتريته بألف وأقام البائع بينة أنه باعه فللشفيع بألفين أخذه بألف فإن قال المشتري غلطت فهل يقبل قوله مع يمينه على وجهين','3164','2035','0');
INSERT INTO [t1153] VALUES (2171,'وإن ادعى أنك اشتريته بألف قال بل اتهبته أو ورثته فالقول قوله مع يمينه فإن نكل أو قامت للشفيع بينه فله أخذه ويقال للمشتري إما أن تقبل الثمن أو تبرئ منه','3166','2035','0');
INSERT INTO [t1153] VALUES (2172,'وإن كان عوضا في الخلع والصداق والصلح عن دم العمد وقلنا: بوجوب الشفعة فيه فقال القاضي: يأخذه بقيمته','3171','2035','0');
INSERT INTO [t1153] VALUES (2173,'وإن أقر البائع بالبيع وأنكر المشتري فهل تجب الشفعة؟ على وجهين أحدهما','3176','2035','0');
INSERT INTO [t1153] VALUES (2174,'وعهدة الشفيع على المشتري وعهدة المشتري على البائع','3178','2035','0');
INSERT INTO [t1153] VALUES (2175,'وإن أبى المشتري قبض المبيع أجبره الحاكم عليه ثم يأخذه الشفيع منه قاله القاضي','3180','2035','0');
INSERT INTO [t1153] VALUES (2176,'ولا شفعة لكافر على مسلم','3181','2035','0');
INSERT INTO [t1153] VALUES (2177,'ولو ورث إثنان شقصا عن أبيهما فباع أحدهما نصيبه فالشفعة بين أخيه وشريك أبيه','3181','2035','0');
INSERT INTO [t1153] VALUES (2178,'وهل تجب الشفعة للمضارب على رب المال ولرب المال على المضارب فيما يشتريه من مال المضاربة؟ على وجهين','3184','2035','0');
INSERT INTO [t1153] VALUES (2179,'باب المساقاة','3192','2035','0');
INSERT INTO [t1153] VALUES (2180,'تجوز المساقاة في ثمر النخل وفي كل شجر له ثمر مأكول ببعض ثمرته','3192','2035','0');
INSERT INTO [t1153] VALUES (2181,'وتصح بلفظ المساقاة','3195','2035','0');
INSERT INTO [t1153] VALUES (2182,'وتصح بلفظ الإجارة في أحد الوجهين','3195','2035','0');
INSERT INTO [t1153] VALUES (2183,'وقد نص أحمد في رواية جماعة فيمن قال أجرتك هذه الأرض بثلث ما يخرج منها أنه يصح وهذه مزارعة بلفظ الإجارة ذكره أبو الخطاب','3195','2035','0');
INSERT INTO [t1153] VALUES (2184,'وهل تصح على ثمرة موجودة','3196','2035','0');
INSERT INTO [t1153] VALUES (2185,'وإن ساقاه على شجرة بغرسه ويعمل عليه حتى يثمر بجزء من الثمرة صح','3197','2035','0');
INSERT INTO [t1153] VALUES (2186,'والمساقاة عقد جائز في ظاهر كلامه','3201','2035','0');
INSERT INTO [t1153] VALUES (2187,'فإن قلنا هي عقد لازم تصح إلا على مدة معلومة','3203','2035','0');
INSERT INTO [t1153] VALUES (2188,'فإن شرطا مدة لا تكمل فيها لم يصح','3204','2035','0');
INSERT INTO [t1153] VALUES (2189,'وإن شرطا مدة قد تكمل فيها الثمرة وقد لا تكمل ففي صحة المساقاة وجهان','3205','2035','0');
INSERT INTO [t1153] VALUES (2190,'وإن مات العامل تمم الوارث فإن لم يكن له وارث أقام الحاكم مقامه من تركته','3206','2035','0');
INSERT INTO [t1153] VALUES (2191,'فإن فسخ بعد ظهور الثمرة فهي بينهما فإن فسخ قبل ظهورها فهل للعامل أجرة؟ على وجهين','3207','2035','0');
INSERT INTO [t1153] VALUES (2192,'وكذلك إن هرب العامل ولم يوجد له ما ينفق عليها فهو كما لو مات','3208','2035','0');
INSERT INTO [t1153] VALUES (2193,'فإن عمل فيها رب المال بإذن حاكم أو إشهاد رجع به وإلا فلا','3208','2035','0');
INSERT INTO [t1153] VALUES (2194,'وعلى رب المال ما فيه حفظ الأصل من شد الحيطان وأجر الأنهار وحفر البئر والدولاب وما يديره','3209','2035','0');
INSERT INTO [t1153] VALUES (2195,'وحكم العامل حكم المضارب فيما يقبل قوله فيه وفيما يرد','3212','2035','0');
INSERT INTO [t1153] VALUES (2196,'وإن شرط إن سقى سيحا فله الربع وإن سقى بكلفة فله النصف أو إن زرعها شعيرا فله الربع وإن زرعها حنطة فله النصف لم يصح في أحد الوجهين','3216','2035','0');
INSERT INTO [t1153] VALUES (2197,'ولو قال ساقيتك هذا البستان بالنصف على أن أساقيك الآخر بالربع لم يصح وجها واحدا','3217','2035','0');
INSERT INTO [t1153] VALUES (2198,'وإن قال ما زرعت من شعير فلي ربعه وما زرعت من حنطة فلي نصفه لم يصح','3217','2035','0');
INSERT INTO [t1153] VALUES (2199,'فصل في المزارعة','3219','2035','0');
INSERT INTO [t1153] VALUES (2200,'فإن كان في الأرض شجر فزارعه الأرض وساقاه على الشجر صح','3224','2035','0');
INSERT INTO [t1153] VALUES (2201,'ولا يشترط كون البذر من رب الأرض وظاهر المذهب اشتراطه','3225','2035','0');
INSERT INTO [t1153] VALUES (2202,'فإن شرط أن يأخذ رب الأرض مثل بذره ويقتسما الباقي لم يصح','3228','2035','0');
INSERT INTO [t1153] VALUES (2203,'وكذلك لو شرطا لأحدهما دراهم معلومة أو زرع ناحية معينة أو شرط لأحدهما ما على الجداول إما منفردا أو مع نصيبه فهو فاسد بإجماع العلماء','3228','2035','0');
INSERT INTO [t1153] VALUES (2204,'ومتى فسد فالزرع لصاحب البذر','3228','2035','0');
INSERT INTO [t1153] VALUES (2205,'وحكم المزارعة حكم المساقاة','3229','2035','0');
INSERT INTO [t1153] VALUES (2206,'والحصاد على العامل نص عليه وكذلك الجذاذ وعنه أن الجذاذ عليهما','3229','2035','0');
INSERT INTO [t1153] VALUES (2207,'وإن قال أنا أزرع الأرض بذري وعواملي وتسقيها بمائك والزرع بيننا ففيها روايتان','3230','2035','0');
INSERT INTO [t1153] VALUES (2208,'وإن زارع شريكه في نصيبه صح','3232','2035','0');
INSERT INTO [t1153] VALUES (2209,'فصل في إجارة الأرض','3233','2035','0');
INSERT INTO [t1153] VALUES (2210,'بسم الله الرحمن الرحيم. باب الإجارة','3242','2035','0');
INSERT INTO [t1153] VALUES (2211,'مسألة: وهي عقد على المنافع تنعقد بلفظ الإجارة والكراء وما في معناهما، وفي لفظ البيع وجهان','3243','2035','0');
INSERT INTO [t1153] VALUES (2212,'مسألة: ولا تصح إلا بشروط ثلاثة','3245','2035','0');
INSERT INTO [t1153] VALUES (2213,'مسألة: أو بناء حائط يذكر طوله وعرضه وسمكه وآلته وإجارة أرض معينة لزرع كذا أو غرس أو بناء معلوم','3245','2035','0');
INSERT INTO [t1153] VALUES (2214,'مسألة: وإن استأجر للركوب ذكر المركوب فرسا أو بعيرا أو نحوه','3248','2035','0');
INSERT INTO [t1153] VALUES (2215,'مسألة: فإن كان للحمل لم يحتج إلى ذكره','3249','2035','0');
INSERT INTO [t1153] VALUES (2216,'مسألة: يجوز أن يستأجر الأجير بطعامه وكسوته وكذلك الظئر','3251','2035','0');
INSERT INTO [t1153] VALUES (2217,'مسألة: ويستحب كان تعطى عند الفطام عبدا أو وليدة إذا كان المسترضع موسرا','3257','2035','0');
INSERT INTO [t1153] VALUES (2218,'مسألة: وإن دفع ثوبه إلى خياط أو قصار ليعملاه ولهما عادة بأجرة صح ولهما ذلك، وإن لم يعقدا عقد إجارة، وكذلك دخول الحمام والركوب في سفينة الملاح','3259','2035','0');
INSERT INTO [t1153] VALUES (2219,'مسألة: ويجوز إجارة دار بسكنى دار وخدمة عبد وتزويج امرأة','3260','2035','0');
INSERT INTO [t1153] VALUES (2220,'مسألة: وتجوز إجارة الحلي بأجرة من جنسه وقيل لا يصح','3261','2035','0');
INSERT INTO [t1153] VALUES (2221,'مسألة: وإن قال إن خطت الثوب اليوم فلك درهم وإن خطته غدا فلك نصف درهم فهل يصح؟ على روايتين','3263','2035','0');
INSERT INTO [t1153] VALUES (2222,'مسألة: وإن قال إن خطته روميا فلك درهم، وإن خطته فارسيا فلك نصف درهم فهل يصح؟ على وجهين','3264','2035','0');
INSERT INTO [t1153] VALUES (2223,'مسألة: وإن أكراه دابة وقال إن رددتها اليوم فكراؤها خمسة، وإن رددتها غدا فكراؤها عشرة، فقال أحمد: لا بأس به','3265','2035','0');
INSERT INTO [t1153] VALUES (2224,'مسألة: وإن أكراه دابة عشرة أيام بعشرة دراهم فما زاد فله بكل يوم درهم، فقال أحمد: في رواية أبي الحارث هو جائز','3266','2035','0');
INSERT INTO [t1153] VALUES (2225,'مسألة: ونص أحمد على أنه لا يجوز أن يكتري لمدة غزاته','3267','2035','0');
INSERT INTO [t1153] VALUES (2226,'مسألة: وإن أكراه كل شهر بدرهم أو كل دلو بتمرة فالمنصوص عن أحمد أنه يصح، وكلما دخل شهر لزمهما حكم الإجارة ولكل واحد منهما الفسخ عند انقضاء كل شهر، وقال أبو بكر وابن حامد: لا يصح','3267','2035','0');
INSERT INTO [t1153] VALUES (2227,'مسألة: وإن سمى لكل يوم شيئا معلوما فجائز','3267','2035','0');
INSERT INTO [t1153] VALUES (2228,'مسألة: ولا يجوز الاستئجار على حمل الميتة والخمر وعنه يصح للحر أكل أجرته','3273','2035','0');
INSERT INTO [t1153] VALUES (2229,'مسألة: ويجوز له استئجار حائط ليضع عليه أطراف خشبه','3275','2035','0');
INSERT INTO [t1153] VALUES (2230,'مسألة: ويجوز استئجار حيوان ليصيد به إلا الكلب','3275','2035','0');
INSERT INTO [t1153] VALUES (2231,'مسألة: ويجوز استئجار كتاب ليقرأ فيه إلا المصحف في أحد الوجهين','3275','2035','0');
INSERT INTO [t1153] VALUES (2232,'مسألة: ويجوز استئجار النقد للتحلي والوزن لا غير','3277','2035','0');
INSERT INTO [t1153] VALUES (2233,'مسألة: فإن أطلق الإجارة لم يصح في أحد الوجهين ويصح في الآخر وينتفع بها في ذلك','3277','2035','0');
INSERT INTO [t1153] VALUES (2234,'مسألة: ويجوز استئجار ولده لخدمته وامرأته لرضاع ولده وحضانته','3279','2035','0');
INSERT INTO [t1153] VALUES (2235,'مسألة: ولا تصح الإجارة إلا بشروط خمسة أحدها أن يعقد على نفع العين دون أجزائها','3280','2035','0');
INSERT INTO [t1153] VALUES (2236,'مسألة: ولا يجوز استئجار حيوان ليأخذ لبنه','3280','2035','0');
INSERT INTO [t1153] VALUES (2237,'مسألة: إلا في الظئر ونفع البئر يدخل تبعا','3281','2035','0');
INSERT INTO [t1153] VALUES (2238,'مسألة: الثاني معرفة العين برؤية أو صفة في أحد الوجهين ويصح في الآخر بدونه، وللمستأجر خيار الرؤية','3282','2035','0');
INSERT INTO [t1153] VALUES (2239,'مسألة: الثالث القدرة على التسليم، فلا تصح إجارة الآبق، والشارد، والمغصوب، من غير غاصبه إذا لم يقدر على أخذه منه','3283','2035','0');
INSERT INTO [t1153] VALUES (2240,'مسألة: ولا تجوز إجارة المشاع مفردا لغير شريكه، وعنه ما يدل على الجواز','3283','2035','0');
INSERT INTO [t1153] VALUES (2241,'مسألة: الشرط الرابع اشتمال العين على المنفعة','3285','2035','0');
INSERT INTO [t1153] VALUES (2242,'مسألة: الخامس كون المنفعة مملوكة للمؤجر، أو مأذونا له فيها','3285','2035','0');
INSERT INTO [t1153] VALUES (2243,'مسألة: يجوز للمستأجر إجارة العين لمن يقوم مقامه من المؤجر وغيره','3286','2035','0');
INSERT INTO [t1153] VALUES (2244,'مسألة: وتجوز إجارتها بمثل الأجرة وزيادة، وعنه لا تجوز بزيادة، وعنه إن جدد فيها عمارة جازت الزيادة وإلا فلا','3287','2035','0');
INSERT INTO [t1153] VALUES (2245,'مسألة: وللمستعير إجارتها إذا أذن له المعير مدة بعينها','3289','2035','0');
INSERT INTO [t1153] VALUES (2246,'مسألة: فإن مات المؤجر فانتقل إلى من بعده لم تفسخ الإجارة في أحد الوجهين','3289','2035','0');
INSERT INTO [t1153] VALUES (2247,'مسألة: وتجوز إجارة الوقف','3289','2035','0');
INSERT INTO [t1153] VALUES (2248,'مسألة: وإن أجر الولي اليتيم أو ماله مدة فبلغ في أثنائها فليس له فسخ الإجارة. ذكره أبو الخطاب','3290','2035','0');
INSERT INTO [t1153] VALUES (2249,'مسألة: فإن أجر السيد عبده مدة ثم أعتقه في أثنائها صح العتق','3292','2035','0');
INSERT INTO [t1153] VALUES (2250,'مسألة: ويشترط أن تكون المدة معلومة يغلب على الظن بقاء العين فيها وإن طالت','3293','2035','0');
INSERT INTO [t1153] VALUES (2251,'مسألة: ولا يشترط أن تلي العقد، فلو أجره سنة خمس في سنة أربع صح','3295','2035','0');
INSERT INTO [t1153] VALUES (2252,'مسألة: وإذا أجره في أثناء شهر سنة استوفى شهرا بالعدد وسائرها بالأهلة','3297','2035','0');
INSERT INTO [t1153] VALUES (2253,'مسألة: يجوز استئجار رجل ليدله على طريق','3303','2035','0');
INSERT INTO [t1153] VALUES (2254,'مسألة: ويصح استئجار رحى لطحن قفزان معلومة','3303','2035','0');
INSERT INTO [t1153] VALUES (2255,'مسألة: ولا يجوز الجمع بين تقدير المدة والعمل كقوله استأجرتك لتخيط لي هذا الثوب في يوم، وعنه يجوز','3309','2035','0');
INSERT INTO [t1153] VALUES (2256,'مسألة: ولا تجوز الإجارة على عمل يختص فاعله أن يكون من أهل القربة كالحج والأذان، وعنه تجوز','3311','2035','0');
INSERT INTO [t1153] VALUES (2257,'مسألة: وإن استأجر من يحجمه صح، ويكره للحر أكل أجرته ويطعمه الرقيق والبهائم، وقال القاضي: لا يصح','3316','2035','0');
INSERT INTO [t1153] VALUES (2258,'مسألة: وله استيفاء المنفعة وما دونها في الضرر من جنسها','3323','2035','0');
INSERT INTO [t1153] VALUES (2259,'مسألة: فإذا استأجر أرضا لزرع الحنطة فله زرع الشعير ونحوه وليس له زرع الدخن ونحوه ولا يملك الغرس ولا البناء ولو اكتراها لأحدهما لم يملك الآخر','3323','2035','0');
INSERT INTO [t1153] VALUES (2260,'مسألة: وإن اكترى دابة للركوب أو الحمل لم يملك الآخر، وإن اكتراها لحمل الحديد أو القطن لم يملك الآخر','3330','2035','0');
INSERT INTO [t1153] VALUES (2261,'مسألة: وإن فعل ما ليس له فعله فعليه أجر المثل','3332','2035','0');
INSERT INTO [t1153] VALUES (2262,'مسألة: وإن اكتراها لحمولة شيء فزاده أو إلى موضع فجاوزه فعليه الأجرة المذكورة وأجرة المثل للزائد، وقال أبو بكر: عليه أجرة المثل للجميع','3332','2035','0');
INSERT INTO [t1153] VALUES (2263,'مسألة: وإن تلفت ضمنها إلا أن تكون في يد صاحبها فيضمن نصف قيمتها في أحد الوجهين','3337','2035','0');
INSERT INTO [t1153] VALUES (2264,'مسألة: فأما تفريغ البالوعة والكنيف فيلزم المستأجر إذا سلمها فارغة','3342','2035','0');
INSERT INTO [t1153] VALUES (2265,'مسألة: وإن بدا له قبل تقضي المدة فعليه الأجرة','3350','2035','0');
INSERT INTO [t1153] VALUES (2266,'مسألة: وإن حوله المالك قبل تقضيها فليس له أجر لما سكن نص عليه ويحتمل أن له من الأجرة بقسطه','3352','2035','0');
INSERT INTO [t1153] VALUES (2267,'مسألة: وإن هرب الأجير حتى انقضت المدة انفسخت الإجارة، وإن كانت على عمل خير المستأجر بين الصبر والفسخ','3352','2035','0');
INSERT INTO [t1153] VALUES (2268,'مسألة: فإن هرب الجمال أو مات وترك الجمال أنفق عليها الحاكم من مال الجمال أو أذن للمستأجر في الإنفاق فإذا قدم باعها ووفى المنفق وحفظ باقي ثمنها لصاحبه','3353','2035','0');
INSERT INTO [t1153] VALUES (2269,'مسألة: وتفسخ الإجارة بتلف العين المعقود عليها','3356','2035','0');
INSERT INTO [t1153] VALUES (2270,'مسألة: وموت الصبي المرتضع','3357','2035','0');
INSERT INTO [t1153] VALUES (2271,'مسألة: وموت الراكب إذا لم يكن له من يقوم مقامه في استيفاء المنفعة','3358','2035','0');
INSERT INTO [t1153] VALUES (2272,'مسألة: وانقلاع الضرس الذي اكترى لقلعه أو برؤه','3359','2035','0');
INSERT INTO [t1153] VALUES (2273,'مسألة: فإن اكترى دارا فانهدمت أو أرضا للزرع فانقطع ماؤها انفسخت الإجارة فيما بقي من المدة في أحد الوجهين وفي الآخر يثبت للمستأجر خيار الفسخ','3359','2035','0');
INSERT INTO [t1153] VALUES (2274,'مسألة: ولا تنفسخ بموت المكتري أو المكري','3361','2035','0');
INSERT INTO [t1153] VALUES (2275,'مسألة: ولا تنفسخ بعذر لأحدهما مثل أن يكتري للحج فتضيع نفقته أو دكانا فيحترق متاعه','3362','2035','0');
INSERT INTO [t1153] VALUES (2276,'مسألة: وإن غصبت العين خير المستأجر بين الفسخ والإمضاء ومطالبة الغاصب بأجرة المثل','3363','2035','0');
INSERT INTO [t1153] VALUES (2277,'مسألة: ومن استؤجر لعمل شيء فمرض أقيم مقامه من يعمله والأجرة عليه','3364','2035','0');
INSERT INTO [t1153] VALUES (2278,'مسألة: وإن وجد العين معيبة فله الفسخ','3365','2035','0');
INSERT INTO [t1153] VALUES (2279,'مسألة: وإن قال: أذنت لي في تفصيله قباء قال: بل قميصا فالقول قول الخياط نص عليه','3366','2035','0');
INSERT INTO [t1153] VALUES (2280,'مسألة: ويجوز بيع العين المستأجرة ولا تنفسخ الإجارة إلا أن يشتريها المستأجر فتنفسخ في إحدى الروايتين','3366','2035','0');
INSERT INTO [t1153] VALUES (2281,'مسألة: ويضمن الأجير المشترك ما جنت يده من تخريق الثوب وغلطه في تفصيله','3372','2035','0');
INSERT INTO [t1153] VALUES (2282,'مسألة: ولا ضمان عليه فيما تلف من حرزه أو بغير فعله ولا أجرة له فيما عمل فيه وعنه يضمن','3375','2035','0');
INSERT INTO [t1153] VALUES (2283,'مسألة: ولا ضمان على حجام ولا ختان ولا نزاع ولا طبيب إذا علم منهم حذق ولم تجن أيديهم','3376','2035','0');
INSERT INTO [t1153] VALUES (2284,'مسألة: ولا ضمان على الراعي إذا لم يتعد','3378','2035','0');
INSERT INTO [t1153] VALUES (2285,'مسألة: وإذا حبس الصانع الثوب على أجرته فتلف ضمنه','3380','2035','0');
INSERT INTO [t1153] VALUES (2286,'مسألة: فإن أتلف الثوب بعد عمله خير المالك بين تضمينه إياه غير معمول ولا أجرة له وبين تضمينه إياه معمولا ويدفع إليه الأجرة','3380','2035','0');
INSERT INTO [t1153] VALUES (2287,'مسألة: وكذلك المعلم إذا ضرب الصبي للتأديب','3383','2035','0');
INSERT INTO [t1153] VALUES (2288,'مسألة: إذا ضرب المستأجر الدابة بقدر العادة أو كبحها أو الرائض الدابة لم يضمن','3383','2035','0');
INSERT INTO [t1153] VALUES (2289,'مسألة: ولا يجب تسليم أجرة العمل في الذمة حتى يتسلمه','3393','2035','0');
INSERT INTO [t1153] VALUES (2290,'مسألة: وإذا انقضت الإجارة وفي الأرض غراس أو بناء لم يشترط قلعه عند انقضاء الأجل فللمالك أخذه بالقيمة وتركه بالأجرة أو قلعه وضمان نقصه، وإن اشترط القلع لزمه ذلك ولا يلزمه تسوية الأرض إلا بشرط','3395','2035','0');
INSERT INTO [t1153] VALUES (2291,'مسألة: وإن كان فيها زرع بقاؤه بتفريط المستأجر فللمالك أخذه بالقيمة وتركه بالأجرة وإن كان بغير تفريط لزم تركه بالأجرة','3398','2035','0');
INSERT INTO [t1153] VALUES (2292,'مسألة: إذا تسلم العين بالإجارة الفاسدة فعليه أجرة سكن أو لم يسكن','3400','2035','0');
INSERT INTO [t1153] VALUES (2293,'مسألة: إذا اكترى بدراهم وأعطاه عنها دنانير ثم انفسخ العقد رجع المستأجر بالدراهم','3400','2035','0');
INSERT INTO [t1153] VALUES (2294,'باب إحياء الموات','3401','2035','0');
INSERT INTO [t1153] VALUES (2295,'مسألة: فإن كان فيها آثار الملك ولا يعلم لها مالك ففيه روايتان','3401','2035','0');
INSERT INTO [t1153] VALUES (2296,'مسألة: ومن أحيا أرضا ميتة فهي له للأخبار التي رويناها مسلما كان أو كافرا في دار الإسلام وغيرها.','3404','2035','0');
INSERT INTO [t1153] VALUES (2297,'مسألة: ويملكه بإذن الإمام وغير إذنه','3405','2035','0');
INSERT INTO [t1153] VALUES (2298,'مسألة: إلا ما أحياه مسلم من أرض الكافر التي صولحوا عليها','3406','2035','0');
INSERT INTO [t1153] VALUES (2299,'مسألة: وما قرب من العامر وتعلق بمصالحه لا يملك بالإحياء فإن لم يتعلق بمصالحه فعلى روايتين','3407','2035','0');
INSERT INTO [t1153] VALUES (2300,'مسألة: ولا تملك المعادن الظاهرة كالملح والقار، والكحل والجص، والنفط بالإحياء وليس للإمام اقطاعه','3409','2035','0');
INSERT INTO [t1153] VALUES (2301,'مسألة: فإن كان بقرب الساحل موضع','3411','2035','0');
INSERT INTO [t1153] VALUES (2302,'مسألة: وإذا ملك المحيي ملك ما فيه من المعادن الباطنة كمعادن الذهب والفضة','3411','2035','0');
INSERT INTO [t1153] VALUES (2303,'مسألة: وإن ظهر فيه عين ماء أو معدن جار أو كلأ أو شجر فهو أحق به لأنه في ملكه','3412','2035','0');
INSERT INTO [t1153] VALUES (2304,'مسألة: ويلزمه بذل ما فضل من مائه لبهائهم غيره','3412','2035','0');
INSERT INTO [t1153] VALUES (2305,'مسألة: وإن حفر بئرا عادية ملك حريمها خمسين ذراعا وإن لم تكن عادية فحريمها خمسة وعشرون','3418','2035','0');
INSERT INTO [t1153] VALUES (2306,'مسألة: وقيل: حريمها قدر مد رشائها من كل جانب','3419','2035','0');
INSERT INTO [t1153] VALUES (2307,'مسألة: وقيل: إحياء الأرض ما عد إحياء وهو عمارتها بما تتهيأ به لما يراد منها','3420','2035','0');
INSERT INTO [t1153] VALUES (2308,'مسألة: ومن تحجر مواتا لم يملكه وهو أحق به وورثته من بعده ومن ينقله إليه وليس له بيعه وقيل: له ذلك','3422','2035','0');
INSERT INTO [t1153] VALUES (2309,'مسألة: فإن لم يتم إحياءه قيل له: إما أن تحييه وإما أن تتركه','3423','2035','0');
INSERT INTO [t1153] VALUES (2310,'مسألة: فإن طلب الإمهال أمهل مدة قريبة','3424','2035','0');
INSERT INTO [t1153] VALUES (2311,'مسألة: وله إقطاع الجلوس في الطرق الواسعة ورحاب المساجد ما لم يضيق على الناس','3427','2035','0');
INSERT INTO [t1153] VALUES (2312,'مسألة: فإن لم يقطعها فلمن يسبق إليها الجلوس فيها ويكون أحق بها ما لم ينقل قماشه عنها','3427','2035','0');
INSERT INTO [t1153] VALUES (2313,'مسألة: فإن طال مقامه منع في أحد الوجهين','3428','2035','0');
INSERT INTO [t1153] VALUES (2314,'مسألة: وإن سبق اثنان إليه احتمل أن يقرع بينهما واحتمل أن يقدم الإمام من يرى منهما فإن كان الجالس يضيق على المارة لم يحل له الجلوس فيه وليس للإمام تمكينه بعوض ولا غيره','3428','2035','0');
INSERT INTO [t1153] VALUES (2315,'مسألة: وهل يمنع إذا طال مقامه للأخذ؟ على وجهين','3429','2035','0');
INSERT INTO [t1153] VALUES (2316,'مسألة: ومن سبق إلى مباح كصيد أو عنبر وحطب وثمر ولقطة ولقيط وما ينبذه الناس رغبة عنه أو يضيع منهم مما لا تتبعه النفس وما يسقط من البلح وسائر المباحات فهو أحق به بإذن الإمام وغير إذنه لقول رسول اله صلى الله عليه وسلم: "من سبق إلى ما لم يسبق إليه مسلم ف','3430','2035','0');
INSERT INTO [t1153] VALUES (2317,'مسألة: وإذا كان الماء في نهر غير مملوك كمياه الأمطار فلمن في أعلاه أن يسقي ويحبس الماء حتى يصل إلى الكعب ثم يرسل إلى من يليه','3430','2035','0');
INSERT INTO [t1153] VALUES (2318,'مسألة: فإن أراد إنسان إحياء أرض ليسقيها من ماء النهر جاز ما لم يضر بأهل الأرض الشاربة منه','3432','2035','0');
INSERT INTO [t1153] VALUES (2319,'مسألة: وللإمام أن يحمي أرضا من الموات ترعى فيها دواب المسلمين التي يقوم بحفظها ما لم يضيق على الناس، ولا يجوز ذلك لغيره','3439','2035','0');
INSERT INTO [t1153] VALUES (2320,'مسألة: وما حماه النبي صلى الله عليه وسلم فليس لأحد نقضه ولا تغييره مع بقاء الحاجة إليه','3442','2035','0');
INSERT INTO [t1153] VALUES (2321,'كتاب الوقف','3443','0','0');
INSERT INTO [t1153] VALUES (2322,'كتاب الوقف','3443','2321','0');
INSERT INTO [t1153] VALUES (2323,'مسألة: وفيه روايتان إحداهما أنه يحصل بالقول والفعل الدال عليه مثل أن يبني مسجدا ويأذن للناس في الصلاة فيه أو يجعل أرضه مقبرة ويأذن لهم في الدفن فيها أو سقاية ويشرعها لهم','3444','2321','0');
INSERT INTO [t1153] VALUES (2324,'مسألة: وصريحه وقفت وسبلت وحبست فمتى أتى بواحدة منها صار وقفا من غير انضمام أمر زائد','3445','2321','0');
INSERT INTO [t1153] VALUES (2325,'مسألة: ولا يصح الوقف إلا بشروط أربعة: أحدها: أن يكون في عين يجوز بيعها ويمكن الانتفاع بها دائما مع بقاء عينها كالحيوان والعقار والأثاث والسلاح','3446','2321','0');
INSERT INTO [t1153] VALUES (2326,'مسألة: ويصح وقف المشاع وبهذا قال مالك والشافعي وأبو يوسف، وقال محمد بن الحسن لا يصح وبناه على أصله في أن القبض شرط وهو لا يصح في المشاع','3447','2321','0');
INSERT INTO [t1153] VALUES (2327,'مسألة: ويصح وقف الحلي على اللبس والعارية','3448','2321','0');
INSERT INTO [t1153] VALUES (2328,'مسألة: ولا يصح الوقف في الذمة كعبد ودار وسلاح غير معين','3449','2321','0');
INSERT INTO [t1153] VALUES (2329,'مسألة: ولا يصح في غير معين كأحد هذين العبدين','3449','2321','0');
INSERT INTO [t1153] VALUES (2330,'مسألة: ولا يصح وقف ما لا يجوز بيعه كأم الولد والكلب والمرهون، وكذلك الخنزير وسائر سباع البهائم التي لا تصلح للصيد وجوارح الطير التي لا يصاد بها','3449','2321','0');
INSERT INTO [t1153] VALUES (2331,'مسألة: ولا يصح على الكنائس وبيوت النار والبيع وكتب التوراة والإنجيل','3450','2321','0');
INSERT INTO [t1153] VALUES (2332,'مسألة: ولا يصح الوقف على حربي ولا مرتد','3452','2321','0');
INSERT INTO [t1153] VALUES (2333,'مسألة: ولا يصح على نفسه في إحدى الروايتين، فإن وقف على غيره واستثنى الأكل منه مدة حياته صح','3452','2321','0');
INSERT INTO [t1153] VALUES (2334,'مسألة: وإن وقف على غيره واستثنى الأكل منه مدة حياته صح','3453','2321','0');
INSERT INTO [t1153] VALUES (2335,'مسألة: ولا يصح على حيوان لا يملك كالعبد القن وأم الولد والمدبر والميت والحمل والملك والبهيمة والجن','3456','2321','0');
INSERT INTO [t1153] VALUES (2336,'مسألة: الثالث أن يقفه على معين يملك ولا يصح على مجهول كرجل ومسجد','3456','2321','0');
INSERT INTO [t1153] VALUES (2337,'مسألة: الرابع أن يقف ناجزا فإذا علقه على شرط لم يصح إلا أن يقول هو وقف بعد موتي فيصح في قول الخرقي وعند أبي الخطاب لا يصح','3457','2321','0');
INSERT INTO [t1153] VALUES (2338,'مسألة: وإن وقف على جهة تنقطع ولم يذكر له مآلا أو وقف على من يجوز ثم على من لا يجوز أو قال: وقفت وسكت انصرف بعد انقراض من يجوز الوقف عليه إلى ورثة الواقف وقفا عليهم في إحدى الروايتين، والأخرى إلى أقرب عصبته وهل يختص به فقراءهم؟ على وجهين، وقال القاض','3461','2321','0');
INSERT INTO [t1153] VALUES (2339,'مسألة: وإن قال وقفت داري سنة أو إلى يوم يقدم الحاج لم يصح في أحد الوجهين','3465','2321','0');
INSERT INTO [t1153] VALUES (2340,'مسألة: ولا يشترط إخراج الوقف عن يده في إحدى الروايتين','3465','2321','0');
INSERT INTO [t1153] VALUES (2341,'مسألة: ويملك صوفه ولبنه وثمرته ونفعه','3467','2321','0');
INSERT INTO [t1153] VALUES (2342,'مسألة: وليس له وطء الجارية فإن فعل فلا حد عليه ولا مهر','3467','2321','0');
INSERT INTO [t1153] VALUES (2343,'مسألة: وإن ولدت فالولد حر','3467','2321','0');
INSERT INTO [t1153] VALUES (2344,'مسألة: فإن أعتقها لم ينفذ عتقه','3468','2321','0');
INSERT INTO [t1153] VALUES (2345,'مسألة: وإن وطئها أجنبي بشبهة فالولد حر','3468','2321','0');
INSERT INTO [t1153] VALUES (2346,'مسألة: وله تزويج الأمة وأخذ مهرها وولدها وقف معها ويحتمل أن يملكه','3469','2321','0');
INSERT INTO [t1153] VALUES (2347,'مسألة: وإن تلفت فعليه قيمتها يشتري بها مثلها','3469','2321','0');
INSERT INTO [t1153] VALUES (2348,'مسألة: وإن جنى الوقف خطأ فالأرش على الموقوف عليه ويحتمل أن يكون في كسبه','3470','2321','0');
INSERT INTO [t1153] VALUES (2349,'مسألة: وإذا وقف على ثلاثة ثم على المساكين فمن مات منهم رجع نصيبه إلى الآخرين فإذا ماتا رجع إلى المساكين','3471','2321','0');
INSERT INTO [t1153] VALUES (2350,'مسألة: فإن لم يشرط ناظرا فالنظر للموقوف عليه وقيل للحاكم ينفق عليه من غلته','3473','2321','0');
INSERT INTO [t1153] VALUES (2351,'مسألة: وإن وقف على ولده ثم على المساكين فهو لولده الذكور والإناث والحبالى بالسوية','3474','2321','0');
INSERT INTO [t1153] VALUES (2352,'مسألة: ولا يدخل فيه ولد البنات وهل يدخل فيه ولد البنين؟ على روايتين','3475','2321','0');
INSERT INTO [t1153] VALUES (2353,'مسألة: وإن وقف على عقبه أو ولد ولده أو ذريته أو نسله دخل فيه ولد البنين بغير خلاف علمناه','3483','2321','0');
INSERT INTO [t1153] VALUES (2354,'مسألة: فإن قال على ولد ولدي لصلبي أو المنتسبين إلي لم يدخل ولد البنات','3484','2321','0');
INSERT INTO [t1153] VALUES (2355,'مسألة: وإن وقف على بنيه أو بني فلان فهو للذكور خاصة دون الأناثي والخناثى عند الجمهور','3487','2321','0');
INSERT INTO [t1153] VALUES (2356,'مسألة: إلا أن يكونوا قبيلة فيدخل فيه النساء دون أولادهن من غيرهم','3487','2321','0');
INSERT INTO [t1153] VALUES (2357,'مسألة: وإن وقف على قرابته أو قرابة فلان فهو للذكر والأنثى من أولاده وأولاد أبيه وجده وجد أبيه لأن النبي صلى الله عليه وسلم لم يجاوز بني هاشم بسهم ذوي القربى','3488','2321','0');
INSERT INTO [t1153] VALUES (2358,'مسألة: وأهل بيته بمنزلة قرابته وقال الخرقي: يعطى من قبل أبيه وأمه','3490','2321','0');
INSERT INTO [t1153] VALUES (2359,'مسألة: وقومه ونسباؤه كقرابته لأن قوم الرجل قبيلته وهم نسباؤه','3491','2321','0');
INSERT INTO [t1153] VALUES (2360,'مسألة: والعترة هم العشيرة الأدنون في عرف الناس وولده الذكور والإناث وإن سفلوا','3492','2321','0');
INSERT INTO [t1153] VALUES (2361,'مسألة: وذوو رحمه كل قرابة له من جهة الآباء والأمهات','3492','2321','0');
INSERT INTO [t1153] VALUES (2362,'مسألة: والأيامى والعزاب من لا زوج له من الرجال والنساء','3492','2321','0');
INSERT INTO [t1153] VALUES (2363,'مسألة: فأما الأرامل فهن النساء اللاتي فارقهن أزواجهن بموت أو غيره','3493','2321','0');
INSERT INTO [t1153] VALUES (2364,'مسألة: وإن وقف على أهل قريته أو قرابته لم يدخل فيهم من يخالف دينه وفيه وجه آخر أن المسلم يدخل فيه وإن كان الواقف كافرا','3495','2321','0');
INSERT INTO [t1153] VALUES (2365,'مسألة: وإن وقف على مواليه وله موال من فوق وموال من أسفل تناول جميعهم، وقال ابن حامد: يختص الموالي من فوق','3497','2321','0');
INSERT INTO [t1153] VALUES (2366,'مسألة: فإن لم يمكن حصرهم كالمساكين والقبيلة الكثيرة كبني هاشم وبني تميم صح الوقف عليهم','3499','2321','0');
INSERT INTO [t1153] VALUES (2367,'مسألة: وإن وقف على جماعة يمكن حصرهم واستيعابهم وجب تعميمهم والتسوية بينهم','3499','2321','0');
INSERT INTO [t1153] VALUES (2368,'مسألة: ولا يعطى كل واحد أكثر من القدر الذي يعطى من الزكاة يعني إذا كان الوقف على صنف من أصناف الزكاة','3501','2321','0');
INSERT INTO [t1153] VALUES (2369,'مسألة: والوصية كالوقف في هذا التفصيل لأن مبناها على لفظ الموصي أشبهت الوقف','3502','2321','0');
INSERT INTO [t1153] VALUES (2370,'مسألة: ولا يجوز بيعه إلا أن تتعطل منافعه فيباع ويصرف ثمنه في مثله، وكذلك الفرس الحبيس إذا لم يصلح للغزو بيع واشتري بثمنه ما يصلح للجهاد، وكذلك المسجد إذا لم ينتفع به في موضعه، وعنه لا تباع المساجد لكن تنقل آلتها إلى مسجد آخر','3503','2321','0');
INSERT INTO [t1153] VALUES (2371,'مسألة: ويجوز بيع بعض آلته وصرفها في عمارته','3505','2321','0');
INSERT INTO [t1153] VALUES (2372,'مسألة: وما فضل من حصره وزيته عن حاجته جاز صرفه إلى مسجد آخر والصدقة به على فقراء المسلمين','3507','2321','0');
INSERT INTO [t1153] VALUES (2373,'مسألة: ولا يجوز غرس شجرة في المسجد','3508','2321','0');
INSERT INTO [t1153] VALUES (2374,'مسألة: فإن كانت مغروسة جاز الأكل منها','3508','2321','0');
INSERT INTO [t1153] VALUES (2375,'باب الهبة والعطية','3510','2321','0');
INSERT INTO [t1153] VALUES (2376,'مسألة: فإن شرط فيها عوضا معلوما صارت بيعا وعنه يغلب فيها حكم الهبة','3510','2321','0');
INSERT INTO [t1153] VALUES (2377,'مسألة: وإن شرط ثوابا مجهولا لم تصح الهبة','3511','2321','0');
INSERT INTO [t1153] VALUES (2378,'مسألة: وتحصل الهبة بما يتعارفه الناس هبة من الإيجاب والقبول والمعاطاة المقترنة بما يدل عليها','3512','2321','0');
INSERT INTO [t1153] VALUES (2379,'مسألة: وتلزم بالقبض وعنه تلزم في غير المكيل والموزون بمجرد الهبة','3513','2321','0');
INSERT INTO [t1153] VALUES (2380,'مسألة: ولا يصح القبض إلا بإذن الواهب إلا ما كان في يد المتهب فيكفي مضي زمن يتأتى قبضه فيه وعنه لا يصح حتى يأذن في القبض','3515','2321','0');
INSERT INTO [t1153] VALUES (2381,'مسألة: فإن مات الواهب قام وارثه مقامه في الإذن والرجوع','3516','2321','0');
INSERT INTO [t1153] VALUES (2382,'مسألة: وإن أبرأ الغريم غريمه من دينه أو وهبه له أو أحله منه برئ، وإن رد ذلك ولم يقبله','3517','2321','0');
INSERT INTO [t1153] VALUES (2383,'مسألة: وتصح هبة المشاع وبه قال مالك والشافعي وسواء في ذلك ما أمكن قسمته أو لم يمكن','3521','2321','0');
INSERT INTO [t1153] VALUES (2384,'مسألة: وتصح هبة ما يجوز بيعه لأنه تمليك في الحياة فصح كالبيع وتصح هبة الكلب وما يباح الانتفاع به من النجاسات','3522','2321','0');
INSERT INTO [t1153] VALUES (2385,'مسألة: ولا تصح هبة المجهول كالحمل في البطن واللبن في الضرع','3523','2321','0');
INSERT INTO [t1153] VALUES (2386,'مسألة: ولا يجوز تعليقها على شرط ولا شرط ما ينافي مقتضاها نحو أن لا يبيعها ولا يهبها','3523','2321','0');
INSERT INTO [t1153] VALUES (2387,'مسألة: ولا توقيتها كقوله وهبتك هذا سنة','3524','2321','0');
INSERT INTO [t1153] VALUES (2388,'مسألة: إلا في العمرى والرقبى وهو أن يقول أعمرتك هذه الدار أو أرقبتكها أو جعلتها لك عمرك أو حياتك فإنه يصح وتكون للمعمر ولورثته من بعده','3524','2321','0');
INSERT INTO [t1153] VALUES (2389,'مسألة: وإن شرط رجوعها إلى المعمر عند موته أو قال هي لآخرنا موتا صح الشرط وعنه لا يصح وتكون للمعمر ولورثته من بعده','3526','2321','0');
INSERT INTO [t1153] VALUES (2390,'مسألة: فإن خص بعضهم أو فضله فعليه التسوية بالرجوع أو إعطاء الآخر حتى يستووا','3531','2321','0');
INSERT INTO [t1153] VALUES (2391,'مسألة: وإن مات قبل ذلك ثبت للمعطى وعنه لا يثبت وللباقين الرجوع اختاره أبو عبد الله بن بطة','3533','2321','0');
INSERT INTO [t1153] VALUES (2392,'مسألة: فإن سوى بينهم في الوقف أو وقف ثلثه في مرضه على بعضهم جاز نص عليه وقياس المذهب أن لا يجوز','3535','2321','0');
INSERT INTO [t1153] VALUES (2393,'مسألة: ولا يجوز لواهب أن يرجع في الهبة','3538','2321','0');
INSERT INTO [t1153] VALUES (2394,'مسألة: وإن نقصت العين أو زادت زيادة مفصلة لم تمنع الرجوع والزيادة للابن ويحتمل أنها للأب وهل تمنع المتصلة الرجوع؟ على روايتين','3542','2321','0');
INSERT INTO [t1153] VALUES (2395,'مسألة: وإن باعه المتهب ثم رجع إليه بفسخ أو إقالة فهل له الرجوع؟ على وجهين','3544','2321','0');
INSERT INTO [t1153] VALUES (2396,'مسألة: وإن كاتبه أو رهنه لم يملك أبوه الرجوع إلا أن ينفك الرهن وينفسخ','3545','2321','0');
INSERT INTO [t1153] VALUES (2397,'مسألة: وإن وهبه المتهب لابنه لم يملك أبوه الرجوع إلا أن يرجع هو','3545','2321','0');
INSERT INTO [t1153] VALUES (2398,'مسألة: وعن أحمد في المرأة تهب زوجها مهرها إن كان سألها ذلك رده إليها رضيت أو كرهت لأنها لا تهبه له إلا مخافة غضبه أو إضرار بها بأن يتزوج عليها','3546','2321','0');
INSERT INTO [t1153] VALUES (2399,'مسألة: فإن تصرف فيه قبل تملكه ببيع أو عتق أو إبراء من دين لم يصح تصرفه فيه','3548','2321','0');
INSERT INTO [t1153] VALUES (2400,'مسألة: وإن وطئ جارية ابنه فأحبلها صارت أم ولد له وولده حر لا تلزمه قيمته ولا حد عليه ولا مهر وفي التعزير وجهان','3549','2321','0');
INSERT INTO [t1153] VALUES (2401,'مسألة: وليس للابن مطالبة أبيه بدين ولا قيمة متلف ولا أرش جناية ولا غير ذلك','3550','2321','0');
INSERT INTO [t1153] VALUES (2402,'مسألة: والهدية والصدقة نوعان من الهبة','3551','2321','0');
INSERT INTO [t1153] VALUES (2403,'مسألة: وإن كان مرض الموت المخوف كالبرسام وهو بخار يرتقي إلى الرأس','3551','2321','0');
INSERT INTO [t1153] VALUES (2404,'مسألة: فعطاياه كالوصية في أنها لا تصح لوارث ولا لأجنبي بزيادة على الثلث إلا بإجازة الورثة كالهبة والعتق والكتابة والمحاباة','3552','2321','0');
INSERT INTO [t1153] VALUES (2405,'مسألة: فأما الأمراض الممتدة كالجذام وحمى الربع والسل في ابتدائه والفالج في دوامه فإن صار صاحبها صاحب فراش فهي مخوفة وإلا فلا','3553','2321','0');
INSERT INTO [t1153] VALUES (2406,'مسألة: ومن كان بين الصفين عند التحام الحرب أو في لجة البحر عند هيجانه أو وقع الطاعون ببلده أو قدم ليقتص منه والحامل عند المخاض فهو كالمريض','3554','2321','0');
INSERT INTO [t1153] VALUES (2407,'مسألة: وإن لم يف الثلث بالتبرعات المنجزة بدئ بالأول فالأول سواء كان الأول عتقا أو غيره','3557','2321','0');
INSERT INTO [t1153] VALUES (2408,'مسألة: وإن تساوت قسم بين الجميع بالحصص وعنه يقدم العتق','3558','2321','0');
INSERT INTO [t1153] VALUES (2409,'مسألة: وأما معاوضة المريض بثمن المثل فتصح من رأس المال وإن كانت مع وارث','3560','2321','0');
INSERT INTO [t1153] VALUES (2410,'مسألة: وإن حابى وارثه فقال القاضي: يبطل في قدر ما حاباه ويصح فيما عداه','3560','2321','0');
INSERT INTO [t1153] VALUES (2411,'مسألة: فإن كان له شفيع فله أخذه فإن أخذه فلا خيار للمشتري','3561','2321','0');
INSERT INTO [t1153] VALUES (2412,'مسألة: وإن باع المريض أجنبيا وحاباه وكان شفيعه وارثا فله الأخذ بالشفعة','3562','2321','0');
INSERT INTO [t1153] VALUES (2413,'مسألة: ويعتبر الثلث عند الموت','3562','2321','0');
INSERT INTO [t1153] VALUES (2414,'مسألة: فلو أعتق في مرضه عبدا أو وهبه لإنسان ثم كسب في حياة سيده شيئا ثم مات سيده فخرج من الثلث كان كسبه له إن كان معتقا وللموهوب إن كان موهوبا، وإن خرج بعضه فلهما من كسبه بقدر ذلك','3564','2321','0');
INSERT INTO [t1153] VALUES (2415,'مسألة: وإن كان موهوبا لإنسان فللموهوب له من العبد بقدر ما عتق منه وله من كسبه بقدر ذلك','3565','2321','0');
INSERT INTO [t1153] VALUES (2416,'مسألة: وإن أعتق جارية لا مال له غيرها ثم وطئها ومهر مثلها نصف قيمتها فهو كما لو كسبت نصف قيمتها','3567','2321','0');
INSERT INTO [t1153] VALUES (2417,'فصول في هبة المريض','3568','2321','0');
INSERT INTO [t1153] VALUES (2418,'مسألة: وإن باع مريض قفيزا لا يملك غيره يساوي ثلاثين بقفيز يساوي عشرة','3572','2321','0');
INSERT INTO [t1153] VALUES (2419,'مسألة: وإن أصدق امرأة عشرة في مرضه لا مال له غيرها','3572','2321','0');
INSERT INTO [t1153] VALUES (2420,'مسألة: وإن مات قبلها ورثته وسقطت المحاباة','3573','2321','0');
INSERT INTO [t1153] VALUES (2421,'مسألة: ولو أعتق أمته وتزوجها في مرضه فنقل المروذي عن أحمد أنها ترث اختاره القاضي، وقال الشافعي لا ترث لأن توريثها يفضي إلى إبطال عتقها، لأنه وصية وإبطال عتقها يبطل توريثها','3579','2321','0');
INSERT INTO [t1153] VALUES (2422,'مسألة: ولو أعتقها وقيمتها مائة ثم تزوجها وأصدقها مائتين لا مال له سواهما وهما مهر مثلها ثم مات صح العتق ولم تستحق الصداق لئلا يفضي إلى بطلان عتقها ثم يبطل صداقها وقال القاضي تستحق المائتين وتعتق','3579','2321','0');
INSERT INTO [t1153] VALUES (2423,'مسألة: وإن تبرع بالثلث ثم اشترى أباه من الثلثين وله ابن','3580','2321','0');
INSERT INTO [t1153] VALUES (2424,'فصول في تصريف المريض','3580','2321','0');
INSERT INTO [t1153] VALUES (2425,'باب اللقطة','3583','2321','0');
INSERT INTO [t1153] VALUES (2426,'مسألة: وتنقسم ثلاثة أقسام أحدها: ما لا تتبعه الهمة كالسوط والشسع والرغيف فيملكه بأخذه بلا تعريف','3584','2321','0');
INSERT INTO [t1153] VALUES (2427,'مسألة: فمن لا يأمن نفسه عليها ليس له أخذها فإن أخذها لزمه ضمانها ولا يملكها وإن عرفها','3596','2321','0');
INSERT INTO [t1153] VALUES (2428,'مسألة: ومن أمن نفسه عليها وقوي على تعريفها فله أخذها','3597','2321','0');
INSERT INTO [t1153] VALUES (2429,'مسألة: ومتى أخذها ثم ردها إلى موضعها ضمنها','3597','2321','0');
INSERT INTO [t1153] VALUES (2430,'مسألة: وهي على ثلاثة أضرب','3604','2321','0');
INSERT INTO [t1153] VALUES (2431,'مسألة: الثاني: ما يخشى فساده','3608','2321','0');
INSERT INTO [t1153] VALUES (2432,'مسألة: وغرامة التجفيف منه وله بيع بعضه في ذلك','3609','2321','0');
INSERT INTO [t1153] VALUES (2433,'مسألة: الثالث: سائر المال فيلزمه حفظه','3610','2321','0');
INSERT INTO [t1153] VALUES (2434,'مسألة: فإن لم تعرف دخلت في ملكه بعد الحول','3615','2321','0');
INSERT INTO [t1153] VALUES (2435,'مسألة: وعن أحمد لا يملك إلا الأثمان وهو ظاهر المذهب وهل له الصدقة بغيرها؟ على روايتين','3618','2321','0');
INSERT INTO [t1153] VALUES (2436,'مسألة: وعن أحمد أن لقطة الحرم لا تملك بحال','3622','2321','0');
INSERT INTO [t1153] VALUES (2437,'مسألة: فمتى جاء صاحبها فوصفها لزم دفعها إليه بنمائها المتصل وزيادتها المنفصلة لمالكها قبل الحول ولو أخذها بعده في أصح الوجهين','3625','2321','0');
INSERT INTO [t1153] VALUES (2438,'مسألة: وإن تلفت أو نقصت قبل الحول لم يضمنها وبعده يضمنها','3627','2321','0');
INSERT INTO [t1153] VALUES (2439,'مسألة: وإن وصفها اثنان قسمت بينهما في أحد الوجهين ذكره أبو الخطاب','3629','2321','0');
INSERT INTO [t1153] VALUES (2440,'مسألة: إلا أن يدفعها بحكم الحاكم فلا يملك صاحبها مطالبته','3630','2321','0');
INSERT INTO [t1153] VALUES (2441,'مسألة: فإن أقام آخر بينة أنها له قدم لأن البينة أقوى من الوصف','3630','2321','0');
INSERT INTO [t1153] VALUES (2442,'مسألة: وإن وجدها صبي أو سفيه قام وليه بتعريفها فإذا عرفها فهي لواجدها','3635','2321','0');
INSERT INTO [t1153] VALUES (2443,'مسألة: وإن التقطها عبد فلسيده أخذها منه وتركها معه يتولى تعريفها إن كان عدلا فإن لم يأمن العبد سيده عليها لزمه سترها عنه فإن أتلفها قبل الحول فهي في رقبته وإن أتلفها بعده فهي في ذمته','3637','2321','0');
INSERT INTO [t1153] VALUES (2444,'مسألة: والمكاتب كالحر في اللقطة','3639','2321','0');
INSERT INTO [t1153] VALUES (2445,'باب اللقيط','3640','2321','0');
INSERT INTO [t1153] VALUES (2446,'مسألة: وهو حر','3640','2321','0');
INSERT INTO [t1153] VALUES (2447,'مسألة: ينفق عليه من بيت المال إن ليم يوجد معه ما ينفق عليه','3641','2321','0');
INSERT INTO [t1153] VALUES (2448,'مسألة: ويحكم بإسلامه إلا أن يوجد في بلد الكفار ولا مسلم فيه فيكون كافرا فإن كان فيه مسلم فعلى وجهين','3642','2321','0');
INSERT INTO [t1153] VALUES (2449,'مسألة: وما وجد معه من فراش تحته أو ثياب أو مال في جيبه أو تحت فراشه أو حيوان مشدود بثيابه فهو له، وإن كان مدفونا تحته أو مطروحا قريبا منه فعلى وجهين','3643','2321','0');
INSERT INTO [t1153] VALUES (2450,'مسألة: وأولى الناس بحضانته واجده إن كان أمينا','3644','2321','0');
INSERT INTO [t1153] VALUES (2451,'مسألة: وله الإنفاق عليه مما وجد معه بغير إذن حاكم، وعنه ما يدل على أنه لا ينفق عليه إلا بإذنه','3645','2321','0');
INSERT INTO [t1153] VALUES (2452,'مسألة: فإن كان الملتقط فاسقا لم تقر في يده وهو قول الشافعي','3646','2321','0');
INSERT INTO [t1153] VALUES (2453,'مسألة: فإن كان الملتقط رقيقا لم يقر في يده','3648','2321','0');
INSERT INTO [t1153] VALUES (2454,'مسألة: أو كافرا واللقيط مسلم','3649','2321','0');
INSERT INTO [t1153] VALUES (2455,'مسألة: أو بدويا ينتقل في المواضع ففيه وجهان','3649','2321','0');
INSERT INTO [t1153] VALUES (2456,'مسألة: وإن وجده في الحضر وأراد نقله إلى البادية لم يقر في يده لوجهين','3649','2321','0');
INSERT INTO [t1153] VALUES (2457,'مسألة: وإن التقطه في البادية مقيم في حلة أقر في يده لأنه ينقله من أرض البؤس والشقاء إلى الرفاهية والدعة والدين','3649','2321','0');
INSERT INTO [t1153] VALUES (2458,'مسألة: وإن التقطه في الحضر من يريد نقله إلى بلد آخر للإقامة فيه لم يجز في أحد الوجهين','3650','2321','0');
INSERT INTO [t1153] VALUES (2459,'مسألة: وإن التقطه اثنان قدم الموسر على المعسر والحاضر على المسافر فإن استويا وتشاحا أقرع بينهما','3650','2321','0');
INSERT INTO [t1153] VALUES (2460,'مسألة: فإن اختلفا في الملتقط منهما قدم من له بينة','3652','2321','0');
INSERT INTO [t1153] VALUES (2461,'مسألة: فإن لم يكن لهما بينة قدم صاحب اليد فيكون القول قوله مع يمينه أنه التقطه','3653','2321','0');
INSERT INTO [t1153] VALUES (2462,'مسألة: فإن كان في أيديهما أقرع بينهما فيسلم إلى من تقع له القرعة مع يمينه','3653','2321','0');
INSERT INTO [t1153] VALUES (2463,'مسألة: فإن لم يكن لهما يد فوصفه أحدهما قدم','3653','2321','0');
INSERT INTO [t1153] VALUES (2464,'مسألة: وإن قتل عمدا فوليه الإمام إن شاء اقتص وإن شاء أخذ الدية','3655','2321','0');
INSERT INTO [t1153] VALUES (2465,'مسألة: وإن قطع طرفه عمدا انتظر بلوغه إلا أن يكون فقيرا مجنونا فللإمام العفو على مال ينفقه عليه','3655','2321','0');
INSERT INTO [t1153] VALUES (2466,'مسألة: وإن ادعى الجاني عليه أو قاذفه رقه وكذبه اللقيط بعد بلوغه فالقول قول اللقيط','3656','2321','0');
INSERT INTO [t1153] VALUES (2467,'مسألة: وإذا ادعى إنسان أنه مملوكه لم يقبل إلا ببينة تشهد أن أمته ولدته في ملكه ويحتمل أن لا يعتبر قولها في ملكه','3657','2321','0');
INSERT INTO [t1153] VALUES (2468,'مسألة: وإن أقر بالرق بعد بلوغه لم يقبل وعنه يقبل وقال القاضي: يقبل فيما عليه رواية واحدة وهل يقبل في غيره؟ على روايتين','3658','2321','0');
INSERT INTO [t1153] VALUES (2469,'مسألة: وإن قال إني كافر لم يقبل قوله وحكمه حكم المرتد، وقيل يقبل إلا أن يكون قد نطق بالإسلام وهو لا يعقله','3663','2321','0');
INSERT INTO [t1153] VALUES (2470,'مسألة: ولا يتبع الكافر في دينه إلا أن يقيم بينة أنه ولد على فراشه','3665','2321','0');
INSERT INTO [t1153] VALUES (2471,'مسألة: فإن ادعاه اثنان أو أكثر لأحدهما بينة قدم بها فإن استووا في بينة أو عدمها عرض معهما على القافة أو مع أقاربهما إن ماتا','3667','2321','0');
INSERT INTO [t1153] VALUES (2472,'مسألة: فإن ألحقته بأحدهما لحق به ليرجح جانبه وإن ألحقته بهما لحق بهما وكان ابنه يرثهما ميراث ابن ويرثانه جميعا ميراث أب واحد','3672','2321','0');
INSERT INTO [t1153] VALUES (2473,'مسألة: ولا يلحق بأكثر من أم واحدة','3673','2321','0');
INSERT INTO [t1153] VALUES (2474,'مسألة: فإن ادعاه أكثر من اثنين فألحقته بهم لحق وإن كثروا','3675','2321','0');
INSERT INTO [t1153] VALUES (2475,'مسألة: فإن نفته القافة عنهم أو أشكل عليهم أو لم يوجد قافة ضاع نسبه في أحد الوجهين وفي الآخر يترك حتى يبلغ فينتسب إلى من شاء أومأ إليه أحمد','3676','2321','0');
INSERT INTO [t1153] VALUES (2476,'مسألة: وكذلك الحكم إن وطئ امرأة اثنان بشبهة','3678','2321','0');
INSERT INTO [t1153] VALUES (2477,'مسألة: ولا يقبل قول القائف إلا أن يكون ذكرا عدلا مجربا في الإصابة','3679','2321','0');
INSERT INTO [t1153] VALUES (2478,'كتاب الوصايا','3681','0','0');
INSERT INTO [t1153] VALUES (2479,'كتاب الوصايا','3681','2478','0');
INSERT INTO [t1153] VALUES (2480,'مسألة: وتصح من السفيه في أصح الوجهين','3684','2478','0');
INSERT INTO [t1153] VALUES (2481,'مسألة: وتصح من البالغ الرشيد','3684','2478','0');
INSERT INTO [t1153] VALUES (2482,'مسألة: ولا تصح من غير عاقل كالطفل والمجنون والمبرسم، وفي السكران وجهان','3686','2478','0');
INSERT INTO [t1153] VALUES (2483,'مسألة: وتصح وصية الأخرس بالإشارة ولا تصح ممن اعتقل لسانه بها ويحتمل أن تصح','3687','2478','0');
INSERT INTO [t1153] VALUES (2484,'مسألة: وإن وجدت وصيته بخطه صحت وعنه لا تصح حتى يشهد عليها','3689','2478','0');
INSERT INTO [t1153] VALUES (2485,'مسألة: فأما من لا وارث له فتجوز وصيته بجميع ماله وعنه لا يجوز إلا الثلث','3697','2478','0');
INSERT INTO [t1153] VALUES (2486,'مسألة: ولا يجوز لمن له وارث بزيادة على الثلث لأجنبي ولا لوارثه بشيء إلا بإجازة الورثة','3699','2478','0');
INSERT INTO [t1153] VALUES (2487,'مسألة: وإن لم يف الثلث بالوصايا تحاصوا فيه وادخل النقص على كل واحد بقدر وصيته وعنه يقدم العتق','3702','2478','0');
INSERT INTO [t1153] VALUES (2488,'مسألة: فإن وصى لكل وارث بمعين قدر نصيبه كرجل خلف ابنا وبنتا وعبدا قيمته مائة وأمة قيمتها خمسون فوصى للابن بالعبد وللبنت بالأمة صحت الوصية في أحد الوجهين','3702','2478','0');
INSERT INTO [t1153] VALUES (2489,'مسألة: وإن أجاز الورثة الوصية جازت','3704','2478','0');
INSERT INTO [t1153] VALUES (2490,'مسألة: ومن أوصى له وهو في الظاهر وارث فصار عند الموت غير وارث صحت الوصية، وإن أوصى له وهو غير وارث فصار عند الموت وارثا بطلت لأن اعتبار الوصية بالموت','3706','2478','0');
INSERT INTO [t1153] VALUES (2491,'مسألة: ولا تصح إجازتهم وردهم إلا بعد موت الموصي وما قبل ذلك لا عبرة به','3707','2478','0');
INSERT INTO [t1153] VALUES (2492,'مسألة: ومن أجاز الوصية ثم قال إنما أجزت لأني ظننت المال قليلا فالقول قوله مع يمينه وله الرجوع بما زاد على ما ظنه في أظهر الوجهين إلا أن تقوم به بينة','3708','2478','0');
INSERT INTO [t1153] VALUES (2493,'مسألة: وإن كان المجاز عينا أو فرسا يزيد على الثلث فأجاز الوصية بها ثم قال ظننت باقي المال كثيرا تخرج الوصية من ثلثه فبان قليلا أو ظهر عليه دين لم أعلمه تبطل الوصية','3709','2478','0');
INSERT INTO [t1153] VALUES (2494,'مسألة: ولا يثبت الملك للموصى له إلا بالقبول بعد الموت فأما رده وقبوله قبل ذلك فلا عبرة به','3709','2478','0');
INSERT INTO [t1153] VALUES (2495,'مسألة: وإن مات الموصى له قبل موت الموصي بطلت الوصية','3710','2478','0');
INSERT INTO [t1153] VALUES (2496,'مسألة: وإن ردها بعد موته بطلت أيضا','3711','2478','0');
INSERT INTO [t1153] VALUES (2497,'مسألة: وإن مات بعده وقبل الرد والقبول قام وارثه مقامه ذكره الخرقي، وقال القاضي يبطل على قياس قوله','3713','2478','0');
INSERT INTO [t1153] VALUES (2498,'مسألة: وإن قبلها بعد الموت ثبت الملك حين القبول في الصحيح من المذهب','3715','2478','0');
INSERT INTO [t1153] VALUES (2499,'مسألة: فما حصل من كسب أو نماء منفصل في الموصى به بعد موت الموصي وقبل القبول كالولد والثمرة والكسب فهو للورثة على الوجه الأول','3717','2478','0');
INSERT INTO [t1153] VALUES (2500,'مسألة: وإن كانت الوصية بأمة فوطئها الوارث قبل القبول فأولدها صارت أم ولد له وولدها حر لأنه وطئها في ملكه','3717','2478','0');
INSERT INTO [t1153] VALUES (2501,'مسألة: وإن وصى له بزوجته فأولدها بعد موت الموصي وقبل القبول فولده رقيق للوارث','3718','2478','0');
INSERT INTO [t1153] VALUES (2502,'مسألة: وإن وصى له بأبيه فمات قبل القبول فقبل ابنه عتق الموصى به ولم يرث شيئا','3718','2478','0');
INSERT INTO [t1153] VALUES (2503,'مسألة: فإذا قال قد رجعت في وصيتي أو أبطلتها أو نحو ذلك كقوله غيرتها بطلت','3721','2478','0');
INSERT INTO [t1153] VALUES (2504,'مسألة: وإن قال ما أوصيت به لفلان فهو لفلان كان رجوعا','3721','2478','0');
INSERT INTO [t1153] VALUES (2505,'مسألة: وإن وصى به لآخر ولم يقل ذلك فهو بينهما','3722','2478','0');
INSERT INTO [t1153] VALUES (2506,'مسألة: وإن باعه أو وهبه أو رهنه كان رجوعا','3724','2478','0');
INSERT INTO [t1153] VALUES (2507,'مسألة: وإن كاتبه أو دبره أو جحد الوصية فعلى وجهين','3724','2478','0');
INSERT INTO [t1153] VALUES (2508,'مسألة: وإن خلطه بغيره على وجه لا يتميز منه كان رجوعا','3725','2478','0');
INSERT INTO [t1153] VALUES (2509,'مسألة: وإن زاد في الدار عمارة أو انهدم بعضها فهل يستحقه الموصى له؟ على وجهين','3726','2478','0');
INSERT INTO [t1153] VALUES (2510,'مسألة: وإن وصى له بقفيز من صبرة ثم خلط الصبرة بأخرى لم يكن رجوعا سواء خلطها بمثلها أو خير منها أو دونها لأنه كان مشاعا وبقي مشاعا','3726','2478','0');
INSERT INTO [t1153] VALUES (2511,'مسألة: وإن وصى لرجل بشيء ثم قال إن قدم فلان فهو له، فقدم في حياة الموصي فهو له','3727','2478','0');
INSERT INTO [t1153] VALUES (2512,'مسألة: وإن قال أخرجوا الواجب من ثلثي أخرج من الثلث وتمم من رأس المال','3731','2478','0');
INSERT INTO [t1153] VALUES (2513,'باب الموصى له','3733','2478','0');
INSERT INTO [t1153] VALUES (2514,'مسألة: وتصح للمرتد كما تصح الهبة له','3735','2478','0');
INSERT INTO [t1153] VALUES (2515,'مسألة: وتصح لمكاتبه ومدبره وأم ولده','3735','2478','0');
INSERT INTO [t1153] VALUES (2516,'مسألة: وتصح الوصية لمدبره','3737','2478','0');
INSERT INTO [t1153] VALUES (2517,'مسألة: وتصح الوصية لأم ولده لأنها حرة حين لزوم الوصية','3737','2478','0');
INSERT INTO [t1153] VALUES (2518,'مسألة: وتصح لعبد غيره','3738','2478','0');
INSERT INTO [t1153] VALUES (2519,'مسألة: وتصح لعبده بمشاع كثلثه فإن خرج العبد من الوصية عتق واستحق باقيه، وإن لم يخرج من الثلث عتق منه بقدر الثلث','3739','2478','0');
INSERT INTO [t1153] VALUES (2520,'مسألة: وإن وصى له بمعين كثوب أو دار أو مائة لم تصح الوصية في قول الأكثرين منهم','3740','2478','0');
INSERT INTO [t1153] VALUES (2521,'مسألة: وتصح الوصية للحمل إذا علم أنه كان موجودا حين الوصية بأن تضعه لأقل من ستة أشهر إن كانت ذات زوج أو سيد يطؤها أو لأقل من أربع سنين إن لم يكن كذلك في أحد الوجهين','3741','2478','0');
INSERT INTO [t1153] VALUES (2522,'مسألة: وإن وصى لما تحمل هذه المرأة لم يصح','3743','2478','0');
INSERT INTO [t1153] VALUES (2523,'مسألة: وإن قتل الوصي الموصي بطلت الوصية، وإن جرحه ثم أوصى له فمات من الجرح لم تبطل في ظاهر كلامه وقال أصحابنا في الوصية للقاتل روايتان','3745','2478','0');
INSERT INTO [t1153] VALUES (2524,'مسألة: وإن وصى لصنف من أصناف الزكاة أو لجميع الأصناف صح','3746','2478','0');
INSERT INTO [t1153] VALUES (2525,'مسألة: وإن وصى لكتب القرآن أو العلم أو لمسجد أو لفرس حبيس ينفق عليه صح لأن ذلك قربة يصح بذل المال فيه','3747','2478','0');
INSERT INTO [t1153] VALUES (2526,'مسألة: وإن وصى في أبواب البر فقال شيخنا يصرف في القرب كلها','3751','2478','0');
INSERT INTO [t1153] VALUES (2527,'مسألة: وإن وصى أن يحج عنه بألف صرف في حجة بعد أخرى حتى ينفذ','3752','2478','0');
INSERT INTO [t1153] VALUES (2528,'مسألة: وإن وصى أن يحج عنه بألف دفع الكل إلى من يحج','3754','2478','0');
INSERT INTO [t1153] VALUES (2529,'مسألة: فإن عينه في الوصية فقال يحج عني فلان بألف صرف ذلك إليه، وإن لم يعين فللموصى إليه صرفه إلى من شاء','3755','2478','0');
INSERT INTO [t1153] VALUES (2530,'مسألة: فإن أبى الحج وقال اصرفوا لي الفضل لم يعطه وبطلت الوصية في حقه','3755','2478','0');
INSERT INTO [t1153] VALUES (2531,'مسألة: وإن وصى لأهل سكته فهو لأهل دربه','3757','2478','0');
INSERT INTO [t1153] VALUES (2532,'مسألة: وإن وصى لجيرانه تناول أربعين دارا من كل جانب','3758','2478','0');
INSERT INTO [t1153] VALUES (2533,'مسألة: وإن وصى لأقرب قرابته أو لأقرب الناس إليه أو أقربهم به رحما لم يدفع بنفسه من غير واسطة والأخ والجد سواء','3759','2478','0');
INSERT INTO [t1153] VALUES (2534,'مسألة: والأخ من الأب والأخ من الأم سواء والأخ من الأبوين أحق منهما','3760','2478','0');
INSERT INTO [t1153] VALUES (2535,'مسألة: وإن وصى لكتب التوراة والإنجيل لم تصح','3762','2478','0');
INSERT INTO [t1153] VALUES (2536,'مسألة: ولا تصح لملك ولا لبهيمة ولا لجني','3763','2478','0');
INSERT INTO [t1153] VALUES (2537,'مسألة: وإن وصى لحي وميت يعلم موته فالكل للحي، ويحتمل أن لا يكون له إلا النصف وإن لم يعلم فللحي نصف الموصى به','3763','2478','0');
INSERT INTO [t1153] VALUES (2538,'مسألة: وإن وصى لهما بثلثي ماله وأجاز الورثة لهما جازت وإن عينوا نصيب الوارث بالرد وحده فللأجنبي الثلث كاملا','3765','2478','0');
INSERT INTO [t1153] VALUES (2539,'مسألة: فإن وصى لوارثه وأجنبي بثلثه فأجاز سائر الورثة وصية الوارث فالثلث بينهما نصفين','3765','2478','0');
INSERT INTO [t1153] VALUES (2540,'مسألة: ولو وصى بماله لابنيه وأجنبي فردوا وصية الوارث فهو على ما قال، وإن أجازوا للوارث فالثلث بينهما','3767','2478','0');
INSERT INTO [t1153] VALUES (2541,'مسألة: وإن وصى لزيد والفقراء والمساكين بثلثه فلزيد التسع','3768','2478','0');
INSERT INTO [t1153] VALUES (2542,'باب الموصى به','3769','2478','0');
INSERT INTO [t1153] VALUES (2543,'مسألة: وتصح بالمعدوم','3769','2478','0');
INSERT INTO [t1153] VALUES (2544,'مسألة: وتصح بما فيه نفع مباح من غير المال كالكلب والزيت النجس','3770','2478','0');
INSERT INTO [t1153] VALUES (2545,'مسألة: وتصح الوصية بالمجهول كعبد وشاة لأن الوصية تصح بالمعدوم فالمجهول بطريق الأولى،','3772','2478','0');
INSERT INTO [t1153] VALUES (2546,'مسألة: فإن اختلف الاسم بالحقيقة والعرف كالشاة في العرف اسم للأنثى والبعير والثور اسم للذكر غلب العرف','3772','2478','0');
INSERT INTO [t1153] VALUES (2547,'مسألة: والدابة اسم للذكر والأنثى من الخيل والبغال والحمير','3774','2478','0');
INSERT INTO [t1153] VALUES (2548,'مسألة: وإن وصى له بغير معين كعبد من عبيده صح ويعطيه الورثة ما شاؤوا','3774','2478','0');
INSERT INTO [t1153] VALUES (2549,'مسألة: وإن لم يكن له عبيد لم تصح الوصية في أحد الوجهين','3775','2478','0');
INSERT INTO [t1153] VALUES (2550,'مسألة: وإن وصى له بقوس للرمى والبندق والندف فله قوس النشاب لأنه أظهرها إلا أن يقترن به قرينة تصرفه إلى غيره وعند أبي الخطاب له أحدهم بالقرعة كالوصية بعبد من عبيده','3776','2478','0');
INSERT INTO [t1153] VALUES (2551,'مسألة: فإن كان له عبيد فماتوا إلا واحدا تعينت الوصية فيه، وكذلك إن لم يكن له إلا عبد واحد لتعذر تسليم الباقي،','3776','2478','0');
INSERT INTO [t1153] VALUES (2552,'مسألة: وإن وصى له بطبل حرب صحت الوصية به لأن فيه منفعة مباحة،','3777','2478','0');
INSERT INTO [t1153] VALUES (2553,'مسألة: وتنفذ الوصية فيما علم من ماله أو لم يعلم','3778','2478','0');
INSERT INTO [t1153] VALUES (2554,'مسألة: وإن وصى بثلثه فاستحدث مالا دخل ثلثه في الوصية','3779','2478','0');
INSERT INTO [t1153] VALUES (2555,'مسألة: وإن قتل وأخذت ديته فهل تدخل الدية في الوصية؟ على روايتين','3779','2478','0');
INSERT INTO [t1153] VALUES (2556,'مسألة: فإن وصى بمعين بقدر نصف الدية فهل الدية على الورثة من الثلثين؟ على وجهين','3780','2478','0');
INSERT INTO [t1153] VALUES (2557,'مسألة: إذا أوصى بمنافع عبده أو أمته أبدا أو مدة بعينها فللورثة عتقها لأنها مملوكة لهم ومنفعتها باقية للموصى له ولا يرجع على المعتق بشيء،','3782','2478','0');
INSERT INTO [t1153] VALUES (2558,'مسألة: ومهرها ههنا وفي كل موضع واجب للورثة','3783','2478','0');
INSERT INTO [t1153] VALUES (2559,'مسألة: وإن وطئت بشبهة فالولد حر لأن وطء الشبهة يكون الولد حرا','3783','2478','0');
INSERT INTO [t1153] VALUES (2560,'مسألة: ولهم ولاية تزويجها لأنهم يملكون رقبتها','3783','2478','0');
INSERT INTO [t1153] VALUES (2561,'مسألة: وإن قتلت فللورثة قيمتها في أحد الوجهين','3784','2478','0');
INSERT INTO [t1153] VALUES (2562,'مسألة: وللوصي استخدامها وإجارتها وإعارتها لأن الوصية له بنفعها وهذا منه','3785','2478','0');
INSERT INTO [t1153] VALUES (2563,'مسألة: وليس لواحد منهما وطؤها','3785','2478','0');
INSERT INTO [t1153] VALUES (2564,'مسألة: وإن ولدت من زوج أو زنا فحكمه حكمها','3786','2478','0');
INSERT INTO [t1153] VALUES (2565,'مسألة: وفي نفقتها ثلاثة أوجه','3786','2478','0');
INSERT INTO [t1153] VALUES (2566,'مسألة: وفي اعتبارها من الثلث وجهان','3787','2478','0');
INSERT INTO [t1153] VALUES (2567,'مسألة: وإن وصى لرجل برقبتها ولآخر بمنفعتها صح','3787','2478','0');
INSERT INTO [t1153] VALUES (2568,'مسألة: تصح الوصية بالمكاتب إذا قلنا يصح بيعه','3789','2478','0');
INSERT INTO [t1153] VALUES (2569,'مسألة: وإن وصى له بمال الكتابة أو ينجم منها صح','3790','2478','0');
INSERT INTO [t1153] VALUES (2570,'مسألة: وإن وصى لرجل برقبته ولآخر بما عليه صح فإن أدى إلى صاحب المال أو أبرأه منه عتق وبطلت وصيته إلى صاحب الرقبة','3791','2478','0');
INSERT INTO [t1153] VALUES (2571,'مسألة: ولو تلف المال كله غيره بعد موت الموصي فهو للموصى له','3793','2478','0');
INSERT INTO [t1153] VALUES (2572,'مسألة: وإن لم يأخذه زمانا قوم وقت الموت لا وقت الأخذ','3794','2478','0');
INSERT INTO [t1153] VALUES (2573,'مسألة: فإن لم يكن له سوى المعين إلا مال غائب أو دين في ذمة موسر أو معسر فللموصى له ثلث الموصى به وكلما اقتضى من الدين شيء أو حضر من الغائب ملك من الموصى به قدر ثلثه حتى يكمله كله','3795','2478','0');
INSERT INTO [t1153] VALUES (2574,'مسألة: وكذلك الحكم في المدبر','3797','2478','0');
INSERT INTO [t1153] VALUES (2575,'مسألة: وإن وصى له بثلث عبد فاستحق ثلثاه فله الثلث الباقي وإن وصى له بثلث ثلاثة أعبد فاستحق اثنان منهم أو ماتا لفه ثلث الباقي','3800','2478','0');
INSERT INTO [t1153] VALUES (2576,'مسألة: وإن وصى له بعبد لا يملكه غيره قيمته مائة ولآخر بثلث ماله وملكه غير العبد مائتان فأجاز الورثة فللموصى له بالثلث ثلث المائتين وربع العبد وللموصى له بالعبد ثلاثة أرباعه، وإن ردوا، فقال الخرقي للموصى له بالثلث سدس المائتين وسدس العبد وللموصى له با','3801','2478','0');
INSERT INTO [t1153] VALUES (2577,'مسألة: وإن كانت الوصية بالنصف مكان الثلث فله في حال الإجازة مائة وثلث العبد ولصاحب العبد ثلثاه','3803','2478','0');
INSERT INTO [t1153] VALUES (2578,'مسألة: وإن وصى لرجل بثلث ماله ولآخر بمائة ولثالث بتمام الثلث على المائة فلم يزد الثلث على المائة وذلك إذا كان المال ثلاثمائة بطلت وصية صاحب التمام','3805','2478','0');
INSERT INTO [t1153] VALUES (2579,'باب الوصية بالانصباء والأجزاء','3807','2478','0');
INSERT INTO [t1153] VALUES (2580,'مسألة: فإذا وصى له بمثل نصيب ابنه، وله ابنان، فله الثلث وإن كانوا ثلاثة الربع وإن كان معهم بنت فله التسعان لأن المسألة من سبعة لكل ابن سهمان ويزاد عليها مثل نصيب ابن، سهمان، فتصير تسعة فالاثنان منها تسعاها','3808','2478','0');
INSERT INTO [t1153] VALUES (2581,'مسألة: وإن وصى بنصيب ابنه فكذلك في أحد الوجهين','3808','2478','0');
INSERT INTO [t1153] VALUES (2582,'مسألة: وإن وصى بضعف نصيب ابنه أو ضعفيه فله مثله مرتين وإن وصى بثلاثة أضعافه فله ثلاثة أمثاله','3809','2478','0');
INSERT INTO [t1153] VALUES (2583,'مسألة: وإذا وصى له بمثل نصيب أحد ورثته ولم يسمه كان له مثل ما لأقلهم نصيبا','3811','2478','0');
INSERT INTO [t1153] VALUES (2584,'مسألة: فإن خلف أربعة بنين فأوصى بمثل نصيب خامس لو كان إلا مثل نصيب سادس لو كان فقد أوصى له بالخمس إلا السدس بعد الوصية فله سهم يزاد على ثلاثين وتصح من اثنين وستين له سهمان ولكل ابن خمسة عشر','3811','2478','0');
INSERT INTO [t1153] VALUES (2585,'مسألة: ولو وصى له بمثل نصيب وارث لو كان فله مثل ماله لو كانت الوصية وهو موجود فقدر الوارث موجودا وانظر ما للموصى له مع وجوده فهو له مع عدمه','3813','2478','0');
INSERT INTO [t1153] VALUES (2586,'مسألة: وإن وصى له بسهم ففيه ثلاثة روايات','3817','2478','0');
INSERT INTO [t1153] VALUES (2587,'مسألة: وإن وصى بجزء معلوم كثلث أو ربع أخذته من مخرجه فدفعته إليه وقسمت الباقي على مسألة الورثة إلا أن يزيد على الثلث ولا يجيزوا له فتفرض له الثلث وتقسم الثلثين عليها','3821','2478','0');
INSERT INTO [t1153] VALUES (2588,'مسألة: وإن وصى بجزأين أو أكثر أخذتها من مخرجها وقسمت الباقي على المسألة فإن زادت على الثلث وردوا جعلت السهام الحاصلة للأوصياء ثلث المال وقسمت الثلثين على الورثة','3821','2478','0');
INSERT INTO [t1153] VALUES (2589,'مسألة: وإن وصى لرجل بجميع ماله ولآخر بنصفه وخلف ابنين فالمال بينهما على ثلاثة إن أجيز لهما والثل على ثلاثة إن رد عليهما','3828','2478','0');
INSERT INTO [t1153] VALUES (2590,'مسألة: فإن أجاز أحد الابنين لهما دون الآخر فسهمه بينهما على ثلاثة ولا شيء للمجيز وللابن الآخر الثلث والثلثان بين الوصيين على ثلاثة فإن أجاز أحدهما لصاحب المال وحده فللآخر التسع وللابن الآخر الثلث والباقي لصاحب المال في أحد الوجهين','3829','2478','0');
INSERT INTO [t1153] VALUES (2591,'مسألة: فإن أجازوا لصاحب النصف وحده فلصاحب المال التسعان ولصاحب النصف النصف في أحد الوجهين','3829','2478','0');
INSERT INTO [t1153] VALUES (2592,'مسألة: وإن كان الجزء الموصى به النصف خرج فيها وجه ثالث','3831','2478','0');
INSERT INTO [t1153] VALUES (2593,'مسألة: إذا وصى لرجل بمثل نصيب أحد ابنيه ولآخر بثلث باقي المال فعلى الوجه الأول لصاحب النصيب ثلث المال وللآخر ثلث باقي المال تسعان والباقي للابنين وتصح من تسعة','3833','2478','0');
INSERT INTO [t1153] VALUES (2594,'مسألة: وإن كانت وصية الثاني بثلث ما يبقى من النصف فعلى الوجه الأول تصح من ثمانية عشر لصاحب النصيب الثلث ستة وللآخر ثلث ما يبقى من النصف سهم يبقى أحد عشر للابنين','3834','2478','0');
INSERT INTO [t1153] VALUES (2595,'كتاب الفرائض','3836','0','0');
INSERT INTO [t1153] VALUES (2596,'كتاب الفرائض','3836','2595','0');
INSERT INTO [t1153] VALUES (2597,'مسألة: وأسباب التوارث ثلاثة: رحم، ونكاح، وولاء لا غير','3837','2595','0');
INSERT INTO [t1153] VALUES (2598,'مسألة: والمجمع على توريثهم من الذكور عشرة: الابن وابنه وان نزل، والأب وأبوه وان علا، والأخ من كل جهة، وابن الأخ من الأم، والعم وابنه كذلك، والزوج ومولى النعمة، ومن النساء سبع: البنت وبنت الابن والأم والجدة والأخت والمرأة ومولاة النعمة','3838','2595','0');
INSERT INTO [t1153] VALUES (2599,'مسألة: والوارث ثلاثة ذوو فرض وعصبات وذوو رحم. باب ميراث ذوي الفروض','3840','2595','0');
INSERT INTO [t1153] VALUES (2600,'مسألة: فإن لم يفضل عن الفرض إلا السدس فهو له، ويسقط من معه من الاخوة والأخوات كأم وابنتين وجد وأخت أو أخ','3847','2595','0');
INSERT INTO [t1153] VALUES (2601,'مسألة: فإن لم يكن في الأكدرية زوج فهي أم وأخت وجد','3851','2595','0');
INSERT INTO [t1153] VALUES (2602,'مسألة: وولد الأب كولد الأبوين في مقاسمة الجد','3852','2595','0');
INSERT INTO [t1153] VALUES (2603,'مسألة: إلا أن يكون ولد الأبوين أختا واحدة فتأخذ تمام النصف، وما فضل فهو لهم ولا يتفق هذا في مسألة فيها فرض غير السدس','3853','2595','0');
INSERT INTO [t1153] VALUES (2604,'مسألة: فإن كان معهم أم فلها السدس وللجد ثلث الباقي ولا ثلث له، فتضربها في ثلاثة تكن ثمانية عشر للأم ثلثه وللجد ثلث الباقي خمسة وللأخت للأبوين تسعة يبقى للأخ وأخته سهم، وتصح من أربعة وخمسين وتسمى مختصرة زيد','3854','2595','0');
INSERT INTO [t1153] VALUES (2605,'مسألة: وعصبته عصبة أمه وعنه أنها هي عصبته','3863','2595','0');
INSERT INTO [t1153] VALUES (2606,'مسألة: فإذا خلف أما وخالا فللأم الثلث بلا خلاف والباقي للخال','3865','2595','0');
INSERT INTO [t1153] VALUES (2607,'مسألة: وإذا مات ابن الملاعن وخلف أمه وجدته فلأمه الثلث والباقي للجدة على إحدى الروايتين، وهذه جدة ورثت مع أم أكثر منها','3869','2595','0');
INSERT INTO [t1153] VALUES (2608,'مسألة: فإن كان بعضهن أقرب من بعض فالميراث لأقربهن، وعنه أن القربى من جهة الأب لا تحجب البعدى من جهة الأم','3874','2595','0');
INSERT INTO [t1153] VALUES (2609,'مسألة: ولا يرث أكثر من ثلاث جدات أم الأم وأم الأب وأم الجد ومن كان من أمهاتهن وإن علت درجتهن','3875','2595','0');
INSERT INTO [t1153] VALUES (2610,'مسألة: والجدات المتحاذيات أم أم أم وأم أم أب وأم أبي أب، وإن كثرن فعلى ذلك','3876','2595','0');
INSERT INTO [t1153] VALUES (2611,'مسألة: وترث الجدة وابنها حي وعنه لا ترث','3877','2595','0');
INSERT INTO [t1153] VALUES (2612,'مسألة: فإن اجتمعت جدة ذات قرابتين مع أخرى، فقياس قول أحمد أن السدس بينهما أثلاثا لذات القرابتين ثلثاه وللأخرى ثلثه','3879','2595','0');
INSERT INTO [t1153] VALUES (2613,'مسألة: وبنات الابن بمنزلة البنات إذا لم يكن بنات','3881','2595','0');
INSERT INTO [t1153] VALUES (2614,'مسألة: فإن كانت بنت وبنات ابن، فللبنت النصف ولبنات الابن واحدة كانت أو أكثر من ذلك السدس تكملة الثلثين، إلا أن يكون معهن ذكر فيعصبهن فيما بقي للذكر مثل حظ الأنثيين','3882','2595','0');
INSERT INTO [t1153] VALUES (2615,'مسألة: وإن استكمل البنات الثلثين سقط بنات الابن، إلا أن يكون معهن أو أنزل منهن ذكر فيعصبهن فيما بقي','3883','2595','0');
INSERT INTO [t1153] VALUES (2616,'مسألة: والأخوات مع البنات عصبة يرثن ما فضل كالأخوة، وليست لهن معهن فريضة مسماة','3886','2595','0');
INSERT INTO [t1153] VALUES (2617,'مسألة: ويسقط ولد الأبوين بثلاثة بالابن وابنه والأب','3888','2595','0');
INSERT INTO [t1153] VALUES (2618,'مسألة: ويسقط ولد الأم بأربعة بالولد ذكرا كان أو أنثى وولد الابن والأب والجد','3889','2595','0');
INSERT INTO [t1153] VALUES (2619,'باب العصبات','3891','2595','0');
INSERT INTO [t1153] VALUES (2620,'مسألة: فإذا انقرض العصبة من النسب ورث المولى المعتق، ثم عصباته الأقرب فالأقرب','3892','2595','0');
INSERT INTO [t1153] VALUES (2621,'مسألة: وأربعة من الذكور يعصبون أخواتهم فيمنعونهن الفرض ويقتسمون ما ورثوا للذكر مثل حظ الأنثيين، وهم الابن وابنه والأخ من الأبوين والأخ من الأب ومن عداهم من العصبات','3892','2595','0');
INSERT INTO [t1153] VALUES (2622,'مسألة: وابن ابن الابن يعصب من بازائه من أخواته وبنات عمه وبنات عم أبيه على كل حال إذا لم يكن لهن فرض، ويسقط من هو أنزل منه كبناته وبنات أخيه وبنات ابن عمه، وكلما نزلت درجته زاد فيمن يعصبه قبيل الآخر','3893','2595','0');
INSERT INTO [t1153] VALUES (2623,'مسألة: ومتى كان بعض بني الأعمام زوجا أو أخا لأم أخذ فرضه وشارك الباقين في تعصيبهم','3894','2595','0');
INSERT INTO [t1153] VALUES (2624,'مسألة: وإذا اجتمع ذو فرض وعصبة بدئ بذي الفرض فأخذ فرضه وما بقي للعصبة','3898','2595','0');
INSERT INTO [t1153] VALUES (2625,'مسألة: وإن استغرقت الفروض المال فلا شيء للعصبة','3898','2595','0');
INSERT INTO [t1153] VALUES (2626,'مسألة: ولو كان مكانهم أخوات لأبوين أو لأب عالت إلى عشرة وسميت ذات الفروخ','3902','2595','0');
INSERT INTO [t1153] VALUES (2627,'باب أصول المسائل','3905','2595','0');
INSERT INTO [t1153] VALUES (2628,'مسألة: الفروض ستة ذكرها الله تعالى في كتابه، وهي نوعان: النصف والربع والثمن والثلثان والثلث والسدس','3905','2595','0');
INSERT INTO [t1153] VALUES (2629,'مسألة: قال: وإن اجتمع مع الرجل أحد الثلاثة، فهي من اثني عشر وتعول على الأفراد إلى سبعة عشر، ولا تعول إلى أكثر من ذلك','3906','2595','0');
INSERT INTO [t1153] VALUES (2630,'مسألة: وإن اجتمع مع الثمن سدس أو ثلثان فأصلها من أربعة وعشرين، وتعول إلى سبعة وعشرين ولا تعول إلى أكثر منها','3908','2595','0');
INSERT INTO [t1153] VALUES (2631,'مسألة: وإن اختلفت أجناسهم فخذ عدد سهامهم من أصل ستة فاجعله أصل مسألتهم','3911','2595','0');
INSERT INTO [t1153] VALUES (2632,'مسألة: فإن كان المردود عليه واحدا أخذ المال كله بالفرض والرد كأم أو جدة أو بنت أو أخت وإن كانوا جماعة من جنس واحد كبنات أو أخوات أو جدات اقتسموه كالعصبة من البنين والاخوة وسائر العصبات، فإن انكسر عليهم ضربت عددهم في مسألة الرد','3911','2595','0');
INSERT INTO [t1153] VALUES (2633,'مسألة: فإن انكسر على فريق منهم ضربته في عدد سهامهم لأنه أصل مسألتهم','3912','2595','0');
INSERT INTO [t1153] VALUES (2634,'مسألة: فإن كان معهم أحد الزوجين أعطيته فرضه من أصل مسألته وقسمت الباقي على مسألة الرد وهي فريضة أهل الرد، وهو ينقسم إذا كانت زوجة','3913','2595','0');
INSERT INTO [t1153] VALUES (2635,'باب تصحيح المسائل','3915','2595','0');
INSERT INTO [t1153] VALUES (2636,'مسألة: وإن انكسر على فريقين أو أكثر لم يخل من أربعة أقسام','3916','2595','0');
INSERT INTO [t1153] VALUES (2637,'باب المناسخات','3921','2595','0');
INSERT INTO [t1153] VALUES (2638,'مسألة: فإن مات ثالث جمعت سهامه مما صحت منه الأوليان، وعملت فيها عملك في مسألة الثاني مع الأول، وكذلك تصنع في الرابع ومن بعده','3925','2595','0');
INSERT INTO [t1153] VALUES (2639,'باب قسم التركات','3927','2595','0');
INSERT INTO [t1153] VALUES (2640,'مسألة: وإن شئت في مسائل المناسخات قسمت التركة على المسألة الأولى، ثم أخذت نصيب الثاني فقسمته على مسألته، وكذلك الثالث فإن كان بين التركة والمسألة موافقة رددتهما إلى وفقهما وقسمت وفق التركة على وفق المسألة واعمل على ما ذكرنا','3928','2595','0');
INSERT INTO [t1153] VALUES (2641,'مسألة: وإن أردت القسمة على قراريط الدينار، فاجعل عدد القراريط كالتركة المعلومة واعمل على ما قلنا. وقراريط الدينار في عرف بلدنا أربعة وعشرون قيراطا، فإن كانت السهام كثيرة وأردت أن تعلم سهم القيراط، فانظر ما يتركب منه العدد، فإنه لا بد أن يتركب من ضرب','3929','2595','0');
INSERT INTO [t1153] VALUES (2642,'مسألة: وإن كانت التركة سهاما، من عقار كثلث وربع ونحو ذلك','3931','2595','0');
INSERT INTO [t1153] VALUES (2643,'باب ذوي الأرحام','3934','2595','0');
INSERT INTO [t1153] VALUES (2644,'مسألة: ويورثون بالتنزيل، فيجعل كل وارث بمنزلة من أدلى به، فيجعل ولد البنات والأخوات كأمهاتهم، وبنات الاخوة والأعمام. وولد الاخوة من الأم كآبائهم، والأخوال. والخالات وأبو الأم كالأم والعمات والعم من الأم كالأب، وعنه كالعم، ثم تجعل نصيب كل وارث لمن أدل','3937','2595','0');
INSERT INTO [t1153] VALUES (2645,'مسألة: فإن أدلى جماعة منهم بواحد واستوت منازلهم، فنصيبه بينهم بالسوية ذكرهم وأنثاهم سواء، وعنه للذكر مثل حظ الأنثيين إلا ولد الأم، وقال الخرقي يسوي بينهم إلا الخال والخالة','3940','2595','0');
INSERT INTO [t1153] VALUES (2646,'مسألة: وإذا كان ابن وبنت أخت وبنت أخت أخرى، فلبنت الأخت وحدها النصف وللأخرى وأخيها النصف بينهما','3940','2595','0');
INSERT INTO [t1153] VALUES (2647,'مسألة: وإن اختلفت منازلهم من المدلى به جعلته كالميت، وقسمت نصيبه بينهم على ذلك','3940','2595','0');
INSERT INTO [t1153] VALUES (2648,'مسألة: فإن خلف ثلاثة أخوال مفترقين، فللخال من الأم السدس والباقي للخال من الأبوين','3950','2595','0');
INSERT INTO [t1153] VALUES (2649,'مسألة: فإن كان معهم أبو أم أسقطهم كما يسقط الأب الاخوة وأولادهم','3950','2595','0');
INSERT INTO [t1153] VALUES (2650,'مسألة: وإن خلف ثلاث بنات عمومة مفترقين فالمال لبنت العم من الأبوين وحدها','3954','2595','0');
INSERT INTO [t1153] VALUES (2651,'مسألة: فإن أدلى جماعة منهم بجماعة، قسمت المال بين المدلى بهم كأنهم أحياء، فما صار لكل وارث فهو لمن أدلى به','3955','2595','0');
INSERT INTO [t1153] VALUES (2652,'مسألة: والجهات أربع: الأبوة، والأمومة، والبنوة، والأخوة','3957','2595','0');
INSERT INTO [t1153] VALUES (2653,'مسألة: وإن مات على ردته فماله فيء، وعنه أنه لورثته من المسلمين، وعنه أنه لورثته من أهل الدين الذي اختاره','3960','2595','0');
INSERT INTO [t1153] VALUES (2654,'مسألة: ومن أمت بقرابتين ورث بهما بإجماع المورثين','3960','2595','0');
INSERT INTO [t1153] VALUES (2655,'مسألة: فإن اتفق معهم أحد الزوجين، أعطيته فرضه غير محجوب ولا معاول','3961','2595','0');
INSERT INTO [t1153] VALUES (2656,'مسألة: ولا يعول من مسائل ذوي الأرحام إلا مسألة واحدة وشبهها','3963','2595','0');
INSERT INTO [t1153] VALUES (2657,'باب ميراث الحمل','3964','2595','0');
INSERT INTO [t1153] VALUES (2658,'مسألة: فإذا وضع الحمل دفعت إليه نصيبه، ورددت الباقي إلى مستحقه، وإن كان يرث الموقوف كله أخذه، وإن أعوز شيئا رجع على من هو في يده','3965','2595','0');
INSERT INTO [t1153] VALUES (2659,'مسألة: وإن خرج بعضه فاستهل ثم انفصل ميتا لم يرث','3969','2595','0');
INSERT INTO [t1153] VALUES (2660,'مسألة: وإن ولدت توأمين فاستهل أحدهما وأشكل، أقرع بينهما، فمن خرج سهمه فهو المستهل','3970','2595','0');
INSERT INTO [t1153] VALUES (2661,'باب ميراث المفقود','3974','2595','0');
INSERT INTO [t1153] VALUES (2662,'مسألة: وإن مات للمفقود من يرثه قبل الحكم بوفاته، وقف للمفقود نصيبه من ميراثه ودفع إلى كل وارث اليقين','3977','2595','0');
INSERT INTO [t1153] VALUES (2663,'مسألة: ولباقي الورثة أن يصطلحوا على ما زاد عن نصيبه فيقتسموه','3977','2595','0');
INSERT INTO [t1153] VALUES (2664,'باب ميراث الخنثى','3981','2595','0');
INSERT INTO [t1153] VALUES (2665,'مسألة: ويعتبر بمباله في قول من بلغنا قوله من أهل العلم','3981','2595','0');
INSERT INTO [t1153] VALUES (2666,'مسألة: فإن مات له من يرثه، وكان يرجى انكشاف حاله وهو الصغير','3982','2595','0');
INSERT INTO [t1153] VALUES (2667,'مسألة: فإذا كان ابن وبنت وولد خنثى','3983','2595','0');
INSERT INTO [t1153] VALUES (2668,'مسألة: فإن كانا خنثيين أو أكثر نزلتهم بعدد أحوالهم في أحد الوجهين','3986','2595','0');
INSERT INTO [t1153] VALUES (2669,'باب ميراث الغرقى ومن عمي موتهم','3989','2595','0');
INSERT INTO [t1153] VALUES (2670,'مسألة: قال: فلو غرق أخوان أحدهما مولى زيد والآخر مولى عمرو، فمن لم يورث أحدهما من صاحبه جعل ميراث كل واحد لمولاه وهو أحسن','3991','2595','0');
INSERT INTO [t1153] VALUES (2671,'باب ميراث أهل الملل','3993','2595','0');
INSERT INTO [t1153] VALUES (2672,'مسألة: إلا أن يسلم قبل قسم الميراث فيرثه، وعنه لا يرث','3994','2595','0');
INSERT INTO [t1153] VALUES (2673,'مسألة: وإن عتق عبد بعد موت موروثه، وقبل القسم لم يرث وجها واحدا','3996','2595','0');
INSERT INTO [t1153] VALUES (2674,'مسألة: ويرث أهل الذمة بعضهم بعضا إن اتفقت أديانهم','3997','2595','0');
INSERT INTO [t1153] VALUES (2675,'مسألة: وهم ثلاث ملل: اليهودية، والنصرانية، ودين سائرهم','3997','2595','0');
INSERT INTO [t1153] VALUES (2676,'مسألة: وإن اختلف أديانهم لم يتوارثوا','3999','2595','0');
INSERT INTO [t1153] VALUES (2677,'مسألة: ولا يرث حربي ذميا، ولا ذمي حربيا','3999','2595','0');
INSERT INTO [t1153] VALUES (2678,'مسألة: والمرتد لا يرث أحدا إلا أن يسلم قبل قسم الميراث','4000','2595','0');
INSERT INTO [t1153] VALUES (2679,'مسألة: إذا خلف أمه وهي أخته من أبيه وعما','4006','2595','0');
INSERT INTO [t1153] VALUES (2680,'مسألة: ولا يرثون بنكاح ذوات المحارم، ولا بنكاح لا يقرون عليه، لو أسلموا','4007','2595','0');
INSERT INTO [t1153] VALUES (2681,'باب ميراث المطلقة','4013','2595','0');
INSERT INTO [t1153] VALUES (2682,'مسألة: وإن كان الطلاق رجعيا، لم يقطعه ما دامت في العدة','4013','2595','0');
INSERT INTO [t1153] VALUES (2683,'مسألة: فإن طلق الزوج المسلم امرأته الذمية أو الأمة في المرض طلاقا بائنا ثم أسلمت الذمية وعتقت الأمة ثم مات في عدتهما لم يرثاه لأنه لم يكن عند الطلاق فارا','4014','2595','0');
INSERT INTO [t1153] VALUES (2684,'مسألة: وإن قال سيد الأمة أنت حرة غدا فطلقها اليوم وهو يعلم بقول السيد ورثته','4014','2595','0');
INSERT INTO [t1153] VALUES (2685,'مسألة: وإن طلقها في مرض الموت المخوف طلاقا لا يتهم فيه بأن سألته الطلاق أو علق طلاقها على فعل لها منه بد ففعلته. أو علقه على شرط في الصحة فوجد في المرض، أو طلق من لا يرث كالأمة والذمية فعتقت وأسلمت فهو كالطلاق الصحيح في أصح الروايتين','4014','2595','0');
INSERT INTO [t1153] VALUES (2686,'مسألة: وإن كان متهما بقصد حرمانها الميراث مثل أن طلقها ابتداء أو علقه على فعل لا بد لها منه كالصلاة ونحوها ففعلته أو قال للأمة أو ذمية إذا أسلمت أو عتقت فأنت طالق أو علم أن سيد الأمة قال لها أنت حرة غدا فطلقها اليوم ورثته ما دامت في العدة ولم يرثها','4015','2595','0');
INSERT INTO [t1153] VALUES (2687,'مسألة: وإن علق طلاقها على فعل لا بد لها منه كالصلاة المكتوبة والصيام الواجب ففعلته فحكمه حكم طلاقه ابتداء','4016','2595','0');
INSERT INTO [t1153] VALUES (2688,'مسألة: وهل ترثه بعد العدة أو ترثه المطلقة قبل الدخول؟ على روايتين','4016','2595','0');
INSERT INTO [t1153] VALUES (2689,'مسألة: وإن تزوجت في عدتها لم ترثه سواء كانت في الزوجية أو بانت من الزوج الثاني','4016','2595','0');
INSERT INTO [t1153] VALUES (2690,'مسألة: وإن أكره الابن امرأة أبيه في مرض أبيه على ما يفسخ نكاحها لم يقطع ميراثها','4019','2595','0');
INSERT INTO [t1153] VALUES (2691,'مسألة: وإن فعلت المرأة في مرض موتها ما يفسخ نكاحها لم يسقط ميراث زوجها وذلك بأن ترضع امرأة زوجها الصغيرة أو زوجها الصغير أو ارتدت فإن زوجها يرثها ولا ترثه','4021','2595','0');
INSERT INTO [t1153] VALUES (2692,'مسألة: وإن خلف زوجات نكاح بعضهن فاسد أقرع بينهن فمن أصابتها القرعة فلا ميراث لها','4021','2595','0');
INSERT INTO [t1153] VALUES (2693,'مسألة: إذا طلق أربع نسوة في مرضه فانقضت عدتهن ثم تزوج أربعا سواهن فالميراث للزوجات وعنه أنه بين الثمان','4022','2595','0');
INSERT INTO [t1153] VALUES (2694,'مسألة: إذا ألحق باثنين فمات وترك أما حرة فلها الثلث والباقي لهما، وإن كان لكل واحد منهما ابن سواه أو لأحدهما ابنان فلأمه السدس، وإن مات أحد الأبوين وله ابن آخر فماله بينهما نصفين','4030','2595','0');
INSERT INTO [t1153] VALUES (2695,'باب الاقرار بمشارك في الميراث','4034','2595','0');
INSERT INTO [t1153] VALUES (2696,'مسألة: وسواء كان المقر به يحجب المقر أو لا يحجبه كأخ يقر بابن للميت أو ابن ابن يقر بابن للميت أو أخ من أب يقر بأخذ من أبوين فإنه يثبت نسبه بذلك ويرث ويسقط المقر','4036','2595','0');
INSERT INTO [t1153] VALUES (2697,'مسألة: وإن أقر بعضهم لم يثبت نسبه إلا أن يشهد منهم عدلان أنه ولد على فراشه أو أن الميت أقر به','4037','2595','0');
INSERT INTO [t1153] VALUES (2698,'مسألة: وعلى المقر أن يدفع إليه فضل ما في يده عن ميراثه','4037','2595','0');
INSERT INTO [t1153] VALUES (2699,'مسألة: فإن لم يكن في يد المقر فضل فلا شيء للمقر به','4040','2595','0');
INSERT INTO [t1153] VALUES (2700,'مسألة: وطريق العمل فيها أن تضرب مسألة الاقرار في مسألة الإنكار وتدفع إلى المقر سهمه من مسألة الإقرار مضروبا في مسألة الإنكار','4041','2595','0');
INSERT INTO [t1153] VALUES (2701,'مسألة: وإن خلف ابنا فأقر بأخوين بكلام متصل فتصادقا ثبت نسبهما فإن تجاحدا فكذلك في أقوى الوجهين لأن نسبهما ثبت بإقرار من هو كل الورثة قبلهما وفي الآخر لا يثبت','4042','2595','0');
INSERT INTO [t1153] VALUES (2702,'مسألة: فإن أقر بأحدهما بعد الآخر أعطى الأول نصف ما في يده','4043','2595','0');
INSERT INTO [t1153] VALUES (2703,'مسألة: وإن أقر بعض الورثة بامرأة الميت لزمه من إرثها بقدر حصته','4043','2595','0');
INSERT INTO [t1153] VALUES (2704,'مسألة: إذا قال مات أبي وأنت أخي فقال هو أبي ولست بأخي لم يقبل إنكاره لأنه نسب الميت إليه بأنه أبوه وأقر بمشاركة المقر له في ميراثه بطريق الاخوة فلما أنكر اخوته لم يثبت إقراره به وبقيت دعواه أنه أبوه دونه غير مقبولة كما لو ادعى ذلك قبل الاقرار. فأما إ','4048','2595','0');
INSERT INTO [t1153] VALUES (2705,'مسألة: فإن قال ماتت زوجتي وأنت أخوها فقال لست بزوجها فهل يقبل إنكاره؟ على وجهين','4048','2595','0');
INSERT INTO [t1153] VALUES (2706,'مسألة: فإن كان معهم أختان من أم فمسألة الإنكار من تسعة ومسألة الإقرار من أربعة وعشرين وهما يتفقان بالإثلاث فإذا ضربت وفق إحداهما في الأخرى تكن اثنين وسبعين للزوج من مسألة الإنكار ثلاثة في وفق مسألة الإقرار أربعة وعشرون وللأختين من الأم سهمان في ثماني','4049','2595','0');
INSERT INTO [t1153] VALUES (2707,'باب ميراث القاتل','4051','2595','0');
INSERT INTO [t1153] VALUES (2708,'مسألة: فأما ما لا يضمن بشيء من هذا القتل قصاصا أو حدا أو دفعا عن نفسه وقتل العادل الباغي والباغي العادل فلا يمنع، وعنه لا يرث العادل الباغي ولا الباغي العادل فيخرج منه أن كل قاتل لا يرث','4052','2595','0');
INSERT INTO [t1153] VALUES (2709,'باب ميراث المعتق بعضه','4055','2595','0');
INSERT INTO [t1153] VALUES (2710,'مسألة: فأما المعتق بعضه فما كسبه بجزئه الحر فهو لورثته ويرث ويحجب وقدر ما فيه من الحرية','4057','2595','0');
INSERT INTO [t1153] VALUES (2711,'مسألة: وإن كان عصبتان نصف كل واحد منهما حر كالأخوين فهل تكمل الحرية فيهما؟ يحتمل وجهين، وإن كان أحدهما يحجب الآخر كابن وابن ابن فالصحيح أنها لا تكمل','4059','2595','0');
INSERT INTO [t1153] VALUES (2712,'مسألة: كل من أعتق عبدا أو عتق عليه برحم أو كتابة أو تدبير أو استيلاد أو وصية بعتقه فله عليه الولاء وعلى أولاده من زوجة معتقه أو من أمه وعلى معتقيه ومعتقي أولاده وأولادهم ومعتقهم أبدا ما تناسلوا','4071','2595','0');
INSERT INTO [t1153] VALUES (2713,'باب الولاء','4071','2595','0');
INSERT INTO [t1153] VALUES (2714,'مسألة: أو عتق عليه برحم','4074','2595','0');
INSERT INTO [t1153] VALUES (2715,'مسألة: أو استيلاد أو وصية بعتقه','4075','2595','0');
INSERT INTO [t1153] VALUES (2716,'مسألة: ويرث به عند عدم العصبة من النسب، فمتى كان للمعتق عصبة أو ذوو فروض تستحق فروضهم المال فلا شيء للمولى','4076','2595','0');
INSERT INTO [t1153] VALUES (2717,'مسألة: ثم يرث به عصباته الأقرب فالأقرب','4077','2595','0');
INSERT INTO [t1153] VALUES (2718,'مسألة: وعنه في المكاتب إذا أدى إلى الورثة أن ولاءه لهم','4080','2595','0');
INSERT INTO [t1153] VALUES (2719,'مسألة: ومن كان أحد أبويه الحرين حر الأصل فلا ولاء عليه','4080','2595','0');
INSERT INTO [t1153] VALUES (2720,'مسألة: ومن أعتق سائبة في نذره أو زكاته أو كفارته ففيه روايتان إحداهما: له عليه الولاء والثانية لا ولاء عليه وما رجع من ميراثه رد في مثله يشتري به رقابا يعتقهم','4082','2595','0');
INSERT INTO [t1153] VALUES (2721,'مسألة: ومن أعتق عبده عن حي بلا أمره أو عن ميت فالولاء للمعتق','4084','2595','0');
INSERT INTO [t1153] VALUES (2722,'مسألة: وإن أعتقه عنه بأمره فالولاء للمعتق عنه','4085','2595','0');
INSERT INTO [t1153] VALUES (2723,'مسألة: وإذا قال اعتق عبدك عني وعلي ثمنه ففعل فالثمن عليه والولاء للمعتق عنه','4085','2595','0');
INSERT INTO [t1153] VALUES (2724,'مسألة: ولو قال أعتقه والثمن علي ففعل فالثمن عليه','4086','2595','0');
INSERT INTO [t1153] VALUES (2725,'مسألة: وإن قال الكافر لرجل أعتق عبدك المسلم عني وعلي ثمنه ففعل فهل يصح على وجهين','4086','2595','0');
INSERT INTO [t1153] VALUES (2726,'مسألة: وإن أعتق عبدا يباينه في دينه فله ولاؤه وهل يرث به؟ على روايتين','4086','2595','0');
INSERT INTO [t1153] VALUES (2727,'مسألة: فإن كان السيد عصبة على دين المعتق ورثه دون سيده وقال داود لا يرث عصبته في حياته','4087','2595','0');
INSERT INTO [t1153] VALUES (2728,'مسألة: ولا يرث من الولاء ذو فرض إلا الأب والجد يرثان السدس مع الابن','4091','2595','0');
INSERT INTO [t1153] VALUES (2729,'مسألة: والجد يرث الثلث مع الإخوة إن كان أحظ له إذا خلف المعتق أخاه وجده فالولاء بينهما نصفين','4092','2595','0');
INSERT INTO [t1153] VALUES (2730,'مسألة: والولاء لا يورث وإنما يورث به','4094','2595','0');
INSERT INTO [t1153] VALUES (2731,'مسألة: ولا يباع ولا يوهب','4095','2595','0');
INSERT INTO [t1153] VALUES (2732,'مسألة: وهو للكبر فإذا مات المعتق وخلف عتيقه وابنين فمات أحد الابنين عن ابن ثم مات المولى فالميراث لابن معتقه','4095','2595','0');
INSERT INTO [t1153] VALUES (2733,'مسألة: وإذا اشترى رجل وأخته أباهما أو أخاهما عتق عليهما بالملك ثم اشترى عبدا فأعتقه ثم مات العتيق ثم مات مولاه ورثه الرجل دون أخته','4097','2595','0');
INSERT INTO [t1153] VALUES (2734,'مسألة: وإن أعتق الجد لم يجر ولاءهم في أصح الروايتين وعنه يجره','4102','2595','0');
INSERT INTO [t1153] VALUES (2735,'مسألة: وإن اشترى الابن أباه عتق عليه وله ولاؤه وولاء إخوته ويبقى ولاؤه لمولى أمه لأنه لا يجر ولاء نفسه','4104','2595','0');
INSERT INTO [t1153] VALUES (2736,'مسألة: وإن اشترى هذا الولد عبدا فأعتقه ثم اشترى العبد أبا معتقه فأعتقه فإنه يجر ولاء سيده','4104','2595','0');
INSERT INTO [t1153] VALUES (2737,'باب الوديعة','4113','2595','0');
INSERT INTO [t1153] VALUES (2738,'مسألة: وهي أمانة لا ضمان عليه فيها إلا أن يتعدى، وإن تلفت من بين ماله لم يضمن في أصح الروايتين','4115','2595','0');
INSERT INTO [t1153] VALUES (2739,'مسألة: ويلزمه حفظها في حرز مثلها','4116','2595','0');
INSERT INTO [t1153] VALUES (2740,'مسألة: وإن عين صاحبها حرزا فجعلها في دونه ضمن','4118','2595','0');
INSERT INTO [t1153] VALUES (2741,'مسألة: وإن أحرزها في مثله أو فوقه لم يضمن','4118','2595','0');
INSERT INTO [t1153] VALUES (2742,'مسألة: وإن نهاه المالك عن إخراجها فأخرجها لغشيان شيء الغالب منه التوى لم يضمن، وإن تركها فتلفت ضمنها وإن أخرجها لغير خوف ضمن','4119','2595','0');
INSERT INTO [t1153] VALUES (2743,'مسألة: فإن قال لا تخرجها وإن خفت عليها فاخرجها عند الخوف أو تركها لم يضمن','4121','2595','0');
INSERT INTO [t1153] VALUES (2744,'مسألة: وإن أودعه بهيمة فلم يعلفها حتى ماتت ضمنها إلا أن ينهاه المالك عن علفها','4123','2595','0');
INSERT INTO [t1153] VALUES (2745,'مسألة: وإن قال اترك الوديعة في جيبك فتركها في كمه ضمن','4127','2595','0');
INSERT INTO [t1153] VALUES (2746,'مسألة: وإن أمره أن يجعلها في صندوق وقال لا تقفل عليها ولا تنم فوقها فخالفه أو قال لا تقفل عليها إلا قفلا واحدا فجعل عليها قفلين فلا ضمان عليه','4130','2595','0');
INSERT INTO [t1153] VALUES (2747,'مسألة: وإن دفع الوديعة إلى من يحفظ ماله كزوجته أو عبده لم يضمن','4132','2595','0');
INSERT INTO [t1153] VALUES (2748,'مسألة: وإن دفعها إلى أجنبي أو حاكم ضمن وليس للمالك مطالبة الأجنبي وقال القاضي له ذلك','4132','2595','0');
INSERT INTO [t1153] VALUES (2749,'مسألة: وإن أراد سفرا أو خاف عليها عند نفسه ردها إلى مالكها أو وكيله في قبضها إن قدر على ذلك','4134','2595','0');
INSERT INTO [t1153] VALUES (2750,'مسألة: فإن لم يجده حملها معه إن كان أحفظ لها','4135','2595','0');
INSERT INTO [t1153] VALUES (2751,'مسألة: فإن لم يجد صاحبها ولا وكيله فله دفعها إلى الحاكم سواء كان به ضرورة إلى السفر أو لم يكن','4136','2595','0');
INSERT INTO [t1153] VALUES (2752,'مسألة: فإن تعذر ذلك أودعها ثقة أو دفنها وأعلم بها ثقة يسكن تلك الدار فإن دفنها ولم يعلم بها أحدا أو أعلم بها من لا يسكن الدار ضمنها','4137','2595','0');
INSERT INTO [t1153] VALUES (2753,'مسألة: وإن تعدى فيها فركب الدابة لغير نفعها ولبس الثوب أو أخذ الوديعة ليستعملها أو ليخزن فيها ثم ردها إلى موضعها بنية الأمانة ضمن لتعديه ولم يزل عنه الضمان بردها','4138','2595','0');
INSERT INTO [t1153] VALUES (2754,'مسألة: فإن جحدها ثم أقر بها فتلفت ضمنها','4138','2595','0');
INSERT INTO [t1153] VALUES (2755,'مسألة: فإن كسر ختم كيسها أو كانت مشدودة فحل الشد ضمن','4138','2595','0');
INSERT INTO [t1153] VALUES (2756,'مسألة: وإن خلطها بما لا تتميز منه ضمنها','4139','2595','0');
INSERT INTO [t1153] VALUES (2757,'مسألة: وإن خلطها بمتميز أو ركب الدابة ليسقيها لم يضمن أما إذا خلطها بما تتميز منه مثل أن خلط دراهم بدنانير لم يضمن','4141','2595','0');
INSERT INTO [t1153] VALUES (2758,'مسألة: ومن أودع شيئا فأخذ بعضه ثم رده أو مثله فضاع الكل ضمنه وحده اختاره الخرقي وعنه يضمن الجميع','4142','2595','0');
INSERT INTO [t1153] VALUES (2759,'مسألة: وإن أودعه صبي وديعة ضمنها ولم يبرأ إلا بالتسليم إلى وليه','4144','2595','0');
INSERT INTO [t1153] VALUES (2760,'مسألة: وإن أودع الصبي أو المعتوه وديعة فتلفت بتفريطه لم يضمن فإن أتلفها أو أكلها ضمنها في قول القاضي','4145','2595','0');
INSERT INTO [t1153] VALUES (2761,'مسألة: وإن أودع عبدا وديعة فأتلفها خرج على الوجهين في الصغير','4146','2595','0');
INSERT INTO [t1153] VALUES (2762,'مسألة: وإن قال لم تودعني ثم أقر بها أو ثبتت ببينة ثم ادعى الرد أو التلف لم يقبل قوله وإن أقام بينة ويحتمل أن تقبل بينته','4151','2595','0');
INSERT INTO [t1153] VALUES (2763,'مسألة: وإن قال ما لك عندي شيء قبل قوله في الرد والتلف','4152','2595','0');
INSERT INTO [t1153] VALUES (2764,'مسألة: وإن مات المودع فادعى وارثه التسليم لم يقبل إلا ببينة','4154','2595','0');
INSERT INTO [t1153] VALUES (2765,'مسألة: فإن تلفت عنده قبل إمكان ردها لم يضمنها','4154','2595','0');
INSERT INTO [t1153] VALUES (2766,'مسألة: وإن ادعى الوديعة اثنان فأقر بها لأحدهما فهي له مع يمينه','4158','2595','0');
INSERT INTO [t1153] VALUES (2767,'مسألة: وإن أقر بها لهما جميعا فهي بينهما','4159','2595','0');
INSERT INTO [t1153] VALUES (2768,'مسألة: وإن أودعه اثنان مكيلا أو موزونا فطلب أحدهما نصيبه سلمه إليه','4164','2595','0');
INSERT INTO [t1153] VALUES (2769,'مسألة: وإن غصبت الوديعة فهل للمودع المطالبة بها؟ على وجهين','4164','2595','0');
INSERT INTO [t1153] VALUES (2770,'كتاب النكاح','4166','0','0');
INSERT INTO [t1153] VALUES (2771,'كتاب النكاح','4166','2770','0');
INSERT INTO [t1153] VALUES (2772,'مسألة: والنكاح سنة لما ذكرنا من أدلة الكتاب والسنة وأدناها الاستحباب','4168','2770','0');
INSERT INTO [t1153] VALUES (2773,'مسألة: والاشتغال به أفضل من التخلي لنوافل العبادة إلا أن يخاف على نفسه مواقعة المحظور بتركه فيجب','4168','2770','0');
INSERT INTO [t1153] VALUES (2774,'مسألة: وعن أحمد أن النكاح واجب على الإطلاق','4171','2770','0');
INSERT INTO [t1153] VALUES (2775,'مسألة: ويستحب تخير ذات الدين الولود البكر الحسيبة الأجنبية','4172','2770','0');
INSERT INTO [t1153] VALUES (2776,'مسألة: ويجوز لمن أراد خطبة امرأة النظر إلى وجهها في غير خلوة بها','4174','2770','0');
INSERT INTO [t1153] VALUES (2777,'مسألة: وله النظر إلى ذلك وإلى الرأس والساقين من الأمة المستامة ومن ذوات محارمه وعنه لا ينظر من ذوات محارمه إلا إلى الوجه والكفين','4176','2770','0');
INSERT INTO [t1153] VALUES (2778,'مسألة: وللعبد النظر إليهما من مولاته','4179','2770','0');
INSERT INTO [t1153] VALUES (2779,'مسألة: ولغير أولي الأربة من الرجال كالكبير والعنين ونحوهما النظر إلى ذلك وعنه لا يباح','4180','2770','0');
INSERT INTO [t1153] VALUES (2780,'مسألة: وللشاهد النظر إلى وجه المشهود عليها','4181','2770','0');
INSERT INTO [t1153] VALUES (2781,'مسألة: وللطبيب النظر إلى ما تدعو الحاجة إلى نظره إليه من بدنها من العورة وغيرها فإنه موضع حاجة','4181','2770','0');
INSERT INTO [t1153] VALUES (2782,'مسألة: فإن كان ذا شهوة فهو كذي المحرم','4182','2770','0');
INSERT INTO [t1153] VALUES (2783,'مسألة: وللصبي المميز غير ذي الشهوة النظر إلى المرأة إلى ما فوق السرة وتحت الركبة في إحدى الروايتين','4182','2770','0');
INSERT INTO [t1153] VALUES (2784,'مسألة: وللمرأة مع المرأة والرجل مع الرجل النظر إلى ما عدا ما بين السرة والركبة وعنه أن الكافرة مع المسلمة كالأجنبي','4183','2770','0');
INSERT INTO [t1153] VALUES (2785,'مسألة: ويباح للمرأة النظر من الرجل إلى غير العورة وعنه لا يباح','4184','2770','0');
INSERT INTO [t1153] VALUES (2786,'مسألة: ويجوز النظر إلى الغلام لغير شهوة','4186','2770','0');
INSERT INTO [t1153] VALUES (2787,'مسألة: ولا يجوز النظر إلى أحد ممن ذكرنا لشهوة لما ذكرنا من خوف الفتنة','4186','2770','0');
INSERT INTO [t1153] VALUES (2788,'مسألة: ولكل واحد من الزوجين النظر إلى جميع بدن الآخر ولمسه وكذلك السيد مع أمته','4186','2770','0');
INSERT INTO [t1153] VALUES (2789,'مسألة: ولا يجوز التصريح بخطبة المعتدة ولا التعريض بخطبة الرجعية','4191','2770','0');
INSERT INTO [t1153] VALUES (2790,'مسألة: فأما الرجعية فلا يجوز لأحد التعريض لخطبتها ولا التصريح، لأنها في حكم الزوجات فهي كالتي في صلب نكاحه','4192','2770','0');
INSERT INTO [t1153] VALUES (2791,'مسألة: ويجوز في عدة الوفاة وفي البائن بطلاق ثلاث','4192','2770','0');
INSERT INTO [t1153] VALUES (2792,'مسألة: وهل يجوز في عدة البائن بغير الثلاث؟ على وجهين','4192','2770','0');
INSERT INTO [t1153] VALUES (2793,'مسألة: والتعريض قوله إني في مثلك لراغب ولا تفوتيني بنفسك وما أحوجني إلى مثلك','4193','2770','0');
INSERT INTO [t1153] VALUES (2794,'مسألة: وتجيبه المرأة ما يرغب عنك وإن قضي شيء كان وما أشبهه','4193','2770','0');
INSERT INTO [t1153] VALUES (2795,'مسألة: ولا يجوز للرجل أن يخطب على خطبة أخيه إن أجيب وإن رد حل وإن لم يعلم الحال فعلى وجهين','4194','2770','0');
INSERT INTO [t1153] VALUES (2796,'مسألة: والتعويل في الإجابة والرد عليها إن لم تكن مجبرة وإن كانت مجبرة فعلى الولي','4197','2770','0');
INSERT INTO [t1153] VALUES (2797,'مسألة: ويستحب عقد النكاح مساء يوم الجمعة','4200','2770','0');
INSERT INTO [t1153] VALUES (2798,'مسألة: ويستحب أن يخطب قبل العقد بخطبة ابن مسعود','4200','2770','0');
INSERT INTO [t1153] VALUES (2799,'مسألة: يستحب أن يقال للمتزوج بارك الله لكما وعليكما وجمع بينكما في خير وعافية','4202','2770','0');
INSERT INTO [t1153] VALUES (2800,'مسألة: ويقول إذا زفت إليه اللهم إني أسألك خيرها وخير ما جبلتها عليه وأعوذ بك من شرها وشر ما جبلتها عليه','4203','2770','0');
INSERT INTO [t1153] VALUES (2801,'باب أركان النكاح وشروطه','4203','2770','0');
INSERT INTO [t1153] VALUES (2802,'مسألة: ولا يملك إجبار عبده الكبير إذا كان عاقلا','4206','2770','0');
INSERT INTO [t1153] VALUES (2803,'مسألة: والقبول أن يقول قبلت هذا النكاح أو ما يقوم مقامه في حق من لا يحسن','4206','2770','0');
INSERT INTO [t1153] VALUES (2804,'مسألة: فإن قدر على تعلمها بالعربية لم يلزمه ذلك','4206','2770','0');
INSERT INTO [t1153] VALUES (2805,'مسألة: فإن اقتصر على قوله قبلت بأن يقول الولي زوجتك ابنتي فيقول قبلك صح وانعقد النكاح','4207','2770','0');
INSERT INTO [t1153] VALUES (2806,'مسألة: فإن تقدم القبول الإيجاب لم يصح','4208','2770','0');
INSERT INTO [t1153] VALUES (2807,'مسألة: وإن تراخى القبول عن الإيجاب صح ما داما في المجلس ولم يتشاغلا عنه بغيره','4210','2770','0');
INSERT INTO [t1153] VALUES (2808,'مسألة: فإن تفرقا قبله بطل الإيجاب','4210','2770','0');
INSERT INTO [t1153] VALUES (2809,'مسألة: فإن قال زوجتك بنتي وله بنات لم يصح حتى يشير إليها أو يسميها أو يصفها بما تتميز به، وإن لم يكن له إلا ابنة واحدة صح','4212','2770','0');
INSERT INTO [t1153] VALUES (2810,'مسألة: وإن قال إن وضعت زوجتي ابنة فقد زوجتكها لم يصح','4213','2770','0');
INSERT INTO [t1153] VALUES (2811,'مسألة: إلا الأب له تزويج أولاده الصغار والمجانين وبناته الأبكار بغير إذنهم','4215','2770','0');
INSERT INTO [t1153] VALUES (2812,'مسألة: وهل تزويج الثيب الصغيرة؟ على وجهين','4222','2770','0');
INSERT INTO [t1153] VALUES (2813,'مسألة: وللسيد تزويج إمائه الثيبات الإبكار وعبيده الصغار بغير إذنهم','4224','2770','0');
INSERT INTO [t1153] VALUES (2814,'مسألة: وليس لهم تزويج صغيرة بحال','4232','2770','0');
INSERT INTO [t1153] VALUES (2815,'مسألة: وإذن الثيب الكلام وإذن البكر الصمات','4234','2770','0');
INSERT INTO [t1153] VALUES (2816,'مسألة: ولا فرق بين الثيوبة بوطء مباح أو محرم','4236','2770','0');
INSERT INTO [t1153] VALUES (2817,'مسألة: فأما زوال ابكارة بأصبع أو وثبة فلا يغير صفة الإذن','4237','2770','0');
INSERT INTO [t1153] VALUES (2818,'مسألة: وعن أحمد أن لها تزويج أمتها ومعتقتها','4243','2770','0');
INSERT INTO [t1153] VALUES (2819,'مسألة: وأحق الناس بنكاح المرأة الحرة أبوها','4244','2770','0');
INSERT INTO [t1153] VALUES (2820,'مسألة: ثم أبوه وإن علا','4245','2770','0');
INSERT INTO [t1153] VALUES (2821,'مسألة: ثم ابنها ثم ابنه وإن سفل متى عدم الأب وآباؤه','4246','2770','0');
INSERT INTO [t1153] VALUES (2822,'مسألة: ثم أخويها لأبيها','4247','2770','0');
INSERT INTO [t1153] VALUES (2823,'مسألة: وعنه تقديم الابن على الجد والتسوية بين الجد والاخوة وبين الأخ للأبوين والأخ للأب وقد ذكرناه','4248','2770','0');
INSERT INTO [t1153] VALUES (2824,'مسألة: ثم بنو الاخوة وإن سفلوا ثم العم ثم ابنه ثم الأقرب فالأقرب من العصبات على ترتيب الميراث','4248','2770','0');
INSERT INTO [t1153] VALUES (2825,'مسألة: ثم المولى المنعم ثم عصباته من بعده الأقرب فالأقرب','4249','2770','0');
INSERT INTO [t1153] VALUES (2826,'مسألة: وولي الأمة سيدها إذا كان من أهل ولاية التزويج','4253','2770','0');
INSERT INTO [t1153] VALUES (2827,'مسألة: فإن كانت لامرأة فوليها ولي سيدتها وقد ذكرنا ذلك','4253','2770','0');
INSERT INTO [t1153] VALUES (2828,'مسألة: ويشترط في الولي الحرية والذكورية واتفاق الدين والعقل','4257','2770','0');
INSERT INTO [t1153] VALUES (2829,'مسألة: فإن كان الأقرب طفلا أو كافرا أو عبدا زوج الأبعد','4260','2770','0');
INSERT INTO [t1153] VALUES (2830,'مسألة: وإن عضل الأقرب زوج الأبعد وعنه يزوج الحاكم','4260','2770','0');
INSERT INTO [t1153] VALUES (2831,'مسألة: وإن غاب غيبة منقطعة زوج الأبعد وهي ما لا تقطع إلا بكلفة ومشقة في ظاهر كلام أحمد وقال الخرقي ما لا يصل إليه الكتاب أو يصل فلا يجب عنه، وقال القاضي ما لا تقطعه القافلة في السنة إلا مرة، وقد قال أحمد إذا كان الأب بعيد السفر زوج الأبعد. قال أبو ال','4263','2770','0');
INSERT INTO [t1153] VALUES (2832,'مسألة: ولا يلي كافر نكاح مسلمة بحال إلا إذا أسلمت أم ولده في وجه','4265','2770','0');
INSERT INTO [t1153] VALUES (2833,'مسألة: ولا يلي مسلم نكاح كافرة إلا سيد الأمة وولي سيدها أو السلطان','4266','2770','0');
INSERT INTO [t1153] VALUES (2834,'مسألة: ويلي الذمي نكاح موليته الذمية من الذمي','4266','2770','0');
INSERT INTO [t1153] VALUES (2835,'مسألة: وإذا زوج الأبعد من غير عذر للأقرب أو زوج الأجنبي لم يصح، وعنه يصح ويقف على إجازة الولي','4267','2770','0');
INSERT INTO [t1153] VALUES (2836,'مسألة: ووكيل كل واحد من هؤلاء يقوم مقامه وإن كان حاضرا ووصيه في النكاح بمنزلته','4272','2770','0');
INSERT INTO [t1153] VALUES (2837,'مسألة: وإذا استوى الأولياء في الدرجة كالإخوة والأعمام وبنيهم صح التزويج من كل واحد منهم لأن سبب الولاية موجود في واحد منهم','4275','2770','0');
INSERT INTO [t1153] VALUES (2838,'مسألة: والأولى تقديم أكبرهم وأفضلهم','4275','2770','0');
INSERT INTO [t1153] VALUES (2839,'مسألة: فإن سبق غير من وقعت له القرعة فزوج صح تزويجه في أقوى الوجهين إذا زوج كفؤا بإذن المرأة','4276','2770','0');
INSERT INTO [t1153] VALUES (2840,'مسألة: وإذا زوج الوليان اثنين ولم يعلم السابق منهما فسخ النكاحان','4276','2770','0');
INSERT INTO [t1153] VALUES (2841,'مسألة: فإن جهل الأول منهما فسخ النكاحان','4278','2770','0');
INSERT INTO [t1153] VALUES (2842,'مسألة: وإذا زوج السيد عبده الصغير من أمته جاز أن يتولى طرفي العقد، لأنه ملك يحكم الملك لا بحكم الإذن في قولهم جميعا','4281','2770','0');
INSERT INTO [t1153] VALUES (2843,'مسألة: وكذلك ولي المرأة مثل ابن العم والمولى والحاكم إذا أذنت له في تزوجها، وعنه لا يجوز حتى توكل في أحد الطرفين','4282','2770','0');
INSERT INTO [t1153] VALUES (2844,'مسألة: وإن قال السيد لأمته أعتقتك وجعلت عتقك صداقك صح فإن طلقها قبل الدخول رجع عليها بنصف قيمتها وكذلك إن قال جعلت عتق أمتي صداقها','4284','2770','0');
INSERT INTO [t1153] VALUES (2845,'مسألة: وإن قال لأمته أعتقتك على أن تزوجيني نفسك ويكون عتقك صداقك أولم يقل ويكون عتقك فقبلت عتقت ولم يلزمها أن تزوجه نفسها','4287','2770','0');
INSERT INTO [t1153] VALUES (2846,'مسألة: وعند ينعقد بحضور مراهقين عاقلين','4294','2770','0');
INSERT INTO [t1153] VALUES (2847,'مسألة: ولا ينعقد نكاح المسلم بشهادة ذميين ويتخرج أن ينعقد إذا كانت المرأة ذمية','4294','2770','0');
INSERT INTO [t1153] VALUES (2848,'مسألة: ولا ينعقد بحضور أصمين ولا أخرسين','4294','2770','0');
INSERT INTO [t1153] VALUES (2849,'مسألة: وهل ينعقد بحضور عدوين أو ابني الزوجين أو أحدهما؟ على وجهين','4294','2770','0');
INSERT INTO [t1153] VALUES (2850,'مسألة: لكن إن لم ترض المرأة والأولياء جميعهم فلمن لم يرض الفسخ','4297','2770','0');
INSERT INTO [t1153] VALUES (2851,'مسألة: فلو زوج الأب بغير كفء برضاها فللاخوة الفسخ','4298','2770','0');
INSERT INTO [t1153] VALUES (2852,'مسألة: والكفاءة الدين والمنصب يعني بالمنصب النسب','4298','2770','0');
INSERT INTO [t1153] VALUES (2853,'مسألة: والعرب بعضهم لبعض أكفاء وسائر الناس بعضهم لبعض أكفاء وعنه لا تزوج قرشية لغير قرشي ولا هاشمية لغير هاشمي','4300','2770','0');
INSERT INTO [t1153] VALUES (2854,'مسألة: وعنه أن الحرية والصناعة واليسار من شروط الكفاءة فلا تزوج حرة بعبد ولا بنت بزاز بحجام ولا بنت بان بحائك ولا موسرة بمعسرة','4301','2770','0');
INSERT INTO [t1153] VALUES (2855,'باب المحرمات في النكاح','4305','2770','0');
INSERT INTO [t1153] VALUES (2856,'مسألة: فإن متن قبل الدخول فهل تحرم بناتهن على روايتين','4309','2770','0');
INSERT INTO [t1153] VALUES (2857,'مسألة: ويثبت تحريم المصاهرة بالوطء الحلال والحرام','4310','2770','0');
INSERT INTO [t1153] VALUES (2858,'مسألة: فإن كانت الموطوءة ميتة أو صغيرة لا يوطأ مثلها فعلى وجهين','4312','2770','0');
INSERT INTO [t1153] VALUES (2859,'مسألة: وإن باشر امرأة أو نظر إلى فرجها أو خلا بها لشهوة فعلى روايتين','4313','2770','0');
INSERT INTO [t1153] VALUES (2860,'مسألة: ومن يلوط بغلام حرم على كل واحد منهما أم الآخر وابنته','4315','2770','0');
INSERT INTO [t1153] VALUES (2861,'مسألة: ويحرم الجمع بين المرأة وعمتها أو خالتها','4318','2770','0');
INSERT INTO [t1153] VALUES (2862,'مسألة: فإن جمع بينهما في عقد واحد لم يصح','4320','2770','0');
INSERT INTO [t1153] VALUES (2863,'مسألة: وإن تزوجهما في عقدين أو تزوج إحداهما في عدة الأخرى سواء كانت بائنا أو رجعية فنكاح الثانية باطل','4320','2770','0');
INSERT INTO [t1153] VALUES (2864,'مسألة: وإن اشترى أخت امرأته أو عمتها أو خالتها صح','4322','2770','0');
INSERT INTO [t1153] VALUES (2865,'مسألة: وإن اشتراهن في عقد واحد صح لما ذكرنا','4323','2770','0');
INSERT INTO [t1153] VALUES (2866,'مسألة: وله وطء إحداهما لأن الأخرى لم تصر فراشا','4323','2770','0');
INSERT INTO [t1153] VALUES (2867,'مسألة: وإن وطئ إحداهما فليس له وطئ الأخرى حتى يحرم الموطوءة على نفسه بإخراج عن ملكه أو تزويج','4324','2770','0');
INSERT INTO [t1153] VALUES (2868,'مسألة: فإن عادت إلى ملكه لم يطأ واحدة منهما حتى يحرم الأخرى','4325','2770','0');
INSERT INTO [t1153] VALUES (2869,'مسألة: وإن وطئ أمته ثم تزوج أختها لم يصح عند أبي بكر','4326','2770','0');
INSERT INTO [t1153] VALUES (2870,'مسألة: ولا بأس أن يجمع بين من كانت زوجة رجل وابنته من غيرها','4328','2770','0');
INSERT INTO [t1153] VALUES (2871,'مسألة: ولا يحل للحر أن يجمع بين أكثر من أربع ولا للعبد أن يتزوج أكثر من اثنتين فإن طلق إحداهن لم يتزوج أخرى حتى تنقضي عدتها','4330','2770','0');
INSERT INTO [t1153] VALUES (2872,'مسألة: وتحرم الزانية حتى تتوب وتنقضي عدتها','4335','2770','0');
INSERT INTO [t1153] VALUES (2873,'مسألة: وتحرم المحرمة حتى تحل','4340','2770','0');
INSERT INTO [t1153] VALUES (2874,'مسألة: ولا يحل لمسلمة نكاح كافر بحال','4340','2770','0');
INSERT INTO [t1153] VALUES (2875,'مسألة: ولا يحل لمسلم نكاح كافرة بحال إلا حرائر أهل الكتاب','4340','2770','0');
INSERT INTO [t1153] VALUES (2876,'مسألة: وتحرم مطلقته ثلاثا حتى تنكح زوجا غيره','4340','2770','0');
INSERT INTO [t1153] VALUES (2877,'مسألة: فإن كان أحد أبويها غير كتابي أو كانت من نساء بني تغلب فهل تحل له؟ على روايتين','4344','2770','0');
INSERT INTO [t1153] VALUES (2878,'مسألة: وليس للمسلم وإن كان عبدا نكاح أمة كتابية وعنه يجوز','4345','2770','0');
INSERT INTO [t1153] VALUES (2879,'مسألة: ولا يحل لحر نكاح أمة مسلمة إلا أن يخاف العنت ولا يجد طولا لنكاح حرة ولا ثمن أمة','4346','2770','0');
INSERT INTO [t1153] VALUES (2880,'مسألة: فإن تزوجها وفيه الشرطان ثم أيسر أو نكح حرة فهل يبطل نكاح الأمة؟ على روايتين','4349','2770','0');
INSERT INTO [t1153] VALUES (2881,'مسألة: وإن تزوج حرة وأمة فلم تعفه ولم يجد طولا لحرة أخرى فهل له نكاح أمة أخرى؟ على روايتين','4350','2770','0');
INSERT INTO [t1153] VALUES (2882,'مسألة: وهل له أن ينكحها على حرة على روايتين؟','4351','2770','0');
INSERT INTO [t1153] VALUES (2883,'مسألة: قال الخرقي وله أن يتزوج من الإماء أربعا إذا كان الشرطان فيه قائمين','4351','2770','0');
INSERT INTO [t1153] VALUES (2884,'مسألة: وللعبد نكاح الأمة وإن فقد فيه الشرطان','4351','2770','0');
INSERT INTO [t1153] VALUES (2885,'مسألة: وإن جمع بينهما في العقد جاز','4352','2770','0');
INSERT INTO [t1153] VALUES (2886,'مسألة: وليس للعبد نكاح سيدته','4352','2770','0');
INSERT INTO [t1153] VALUES (2887,'مسألة: وليس للحر أن يتزوج أمته','4352','2770','0');
INSERT INTO [t1153] VALUES (2888,'مسألة: ولا يجوز للحر أن يتزوج أمة ابنه لأن له فيها شبهة ملك','4352','2770','0');
INSERT INTO [t1153] VALUES (2889,'مسألة: ومن جمع بين محللة ومحرمة في عقد واحد فهل يصح فيمن تحل؟ على روايتين','4354','2770','0');
INSERT INTO [t1153] VALUES (2890,'مسألة: وكل من حرم نكاحها حرم وطؤها بملك اليمين إلا إماء أهل الكتاب','4355','2770','0');
INSERT INTO [t1153] VALUES (2891,'مسألة: فإن تزوج امرأة فقال أنا امرأة انفسخ نكاحه','4358','2770','0');
INSERT INTO [t1153] VALUES (2892,'مسألة: فإن شرط أن لا يخرجها من دارها أو بلدها أو لا يتزوج عليها أو لا يتسرى فهو صحيح لازم فإن وفى به وإلا فلها الفسخ','4359','2770','0');
INSERT INTO [t1153] VALUES (2893,'باب الشروط في النكاح','4359','2770','0');
INSERT INTO [t1153] VALUES (2894,'مسألة: وإن تزوج رجلا ثم قال أنا رجل لم يقبل قوله في فسخ نكاحه','4359','2770','0');
INSERT INTO [t1153] VALUES (2895,'مسألة: وإن شرط لها طلاق ضرتها فقال أبو الخطاب هو صحيح','4361','2770','0');
INSERT INTO [t1153] VALUES (2896,'مسألة: قال رضي الله عنه: القسم الثاني فاسد وهو ثلاثة أنواع','4361','2770','0');
INSERT INTO [t1153] VALUES (2897,'مسألة: فإن نوى ذلك من غير شرط لم يصح أيضا في ظاهر المذهب وقيل يكره ويصح','4365','2770','0');
INSERT INTO [t1153] VALUES (2898,'مسألة: وكذلك إن شرط أن يطلقها في وقت بعينه','4372','2770','0');
INSERT INTO [t1153] VALUES (2899,'مسألة: وإن علق ابتداءه على شرط كقوله زوجتك إذا جاء رأس الشهر أو إن رضيت أمها فهذا كله باطل من أصله','4372','2770','0');
INSERT INTO [t1153] VALUES (2900,'مسألة: فإن شرطها كتابية فبانت مسلمة فلا خيار له','4376','2770','0');
INSERT INTO [t1153] VALUES (2901,'مسألة: وإن تزوج أمة يظنها حرة أو على أنها حرة وولدت منه فالولد حر ويفديهم بمثلهم يوم ولادتهم ويرجع بذلك على من غره، ويفرق بينهما إن لم يكن ممن يجوز له نكاح الإماء، وإن كان ممن يجوز له نكاح الإماء فله الخيار، فإن رضي المقام معها فما ولدت بعد ذلك فهو ر','4377','2770','0');
INSERT INTO [t1153] VALUES (2902,'مسألة: وإن كان المغرور عبدا فولده أحرار ويفديهم إذا أعتق ويرجع به على من غره','4385','2770','0');
INSERT INTO [t1153] VALUES (2903,'مسألة: وإن تزوجت المرأة عبدا على أنه حر أو ظنته حرا فبان عبدا فلها الخيار','4386','2770','0');
INSERT INTO [t1153] VALUES (2904,'مسألة: وإن كان عبدا فلها الخيار في فسخ النكاح','4388','2770','0');
INSERT INTO [t1153] VALUES (2905,'مسألة: ولها الفسخ بنفسها من غير حاكم','4389','2770','0');
INSERT INTO [t1153] VALUES (2906,'مسألة: فإن أعتق قبل فسخها أو أمكنته من وطئها بطل خيارها','4389','2770','0');
INSERT INTO [t1153] VALUES (2907,'مسألة: وخيار المعتقة على التراخي ما لم يوجد منها ما يدل على الرضى ولا يمنع الزوج وطأها','4391','2770','0');
INSERT INTO [t1153] VALUES (2908,'مسألة: فإن كانت صغيرة أو مجنونة فلها الخيار إذا بلغت أو عقلت','4391','2770','0');
INSERT INTO [t1153] VALUES (2909,'مسألة: وإن طلقت قبل اختيارها وقع الطلاق وبطل خيارها','4392','2770','0');
INSERT INTO [t1153] VALUES (2910,'مسألة: وإن أعتقت الأمة الرجعية فلها الخيار','4393','2770','0');
INSERT INTO [t1153] VALUES (2911,'مسألة: فإن اختارت المقام فهل يبطل خيارها؟ على وجهين','4393','2770','0');
INSERT INTO [t1153] VALUES (2912,'مسألة: وإن اختارت الفسخ قبل الدخول فلا مهر لها','4394','2770','0');
INSERT INTO [t1153] VALUES (2913,'مسألة: ومتى اختارت المعتقة الفسخ بعد الدخول فالمهر للسيد','4394','2770','0');
INSERT INTO [t1153] VALUES (2914,'مسألة: وإن أعتق أحد الشريكين وهو معسر فلا خيار لها','4395','2770','0');
INSERT INTO [t1153] VALUES (2915,'مسألة: وإن عتق الزوجان معا فلا خيار لها وعنه ينفسخ نكاحهما','4397','2770','0');
INSERT INTO [t1153] VALUES (2916,'باب حكم العيوب في النكاح','4399','2770','0');
INSERT INTO [t1153] VALUES (2917,'مسألة: فإن اختلفا في إمكان الجماع بما بقي من ذكره فالقول قول المرأة','4401','2770','0');
INSERT INTO [t1153] VALUES (2918,'مسألة: فإن ادعت ذلك أجل سنة منذ ترافعه','4403','2770','0');
INSERT INTO [t1153] VALUES (2919,'مسألة: فإن اعترف بذلك أو قامت بينة على إقراره به فأنكر فطلبت يمينه فنكل ثبت عجزه','4403','2770','0');
INSERT INTO [t1153] VALUES (2920,'مسألة: وإن اعترفت أنه وطئها مرة بطل كونه عنينا','4404','2770','0');
INSERT INTO [t1153] VALUES (2921,'مسألة: وإن وطئها في الدبر أو وطئ غيرها لم تزل العنة ويحتمل أن تزول','4405','2770','0');
INSERT INTO [t1153] VALUES (2922,'مسألة: وإن كانت ثيبا فالقول لما ذكرنا','4408','2770','0');
INSERT INTO [t1153] VALUES (2923,'مسألة: وإن علم بالعيب وقت العقد أو قال قد رضيت بها معيبة بعد العقد أو وجد منه دلالة على الرضا من وطء أو تمكين مع العلم بالعيب فلا خيار له لا نعلم فيه خلافا','4413','2770','0');
INSERT INTO [t1153] VALUES (2924,'مسألة: فإن فسخ قبل الدخول فلا مهر، وإن فسخ بعده فعليه المهر المسمى وقيل عنه مهر المثل','4414','2770','0');
INSERT INTO [t1153] VALUES (2925,'مسألة: ولا يجوز الفسخ إلا بحكم حاكم، لأنه مجتهد فيه فهو كفسخ العنة والفسخ للإعسار بالنفقة ويخالف خيار المعتقة لأنه متفق عليه','4414','2770','0');
INSERT INTO [t1153] VALUES (2926,'مسألة: ويرجع به على من غره من المرأة والولي وعنه لا يرجع','4416','2770','0');
INSERT INTO [t1153] VALUES (2927,'مسألة: وليس له تزويج كبيرة بمعيب بغير رضاها','4418','2770','0');
INSERT INTO [t1153] VALUES (2928,'مسألة: فإن اختارت الكبيرة تزويج مجبوب أو عنين لم يملك منعها','4418','2770','0');
INSERT INTO [t1153] VALUES (2929,'مسألة: فأما إن علمت العيب بعد العقد أو حدث به لم يملك إجبارها على الفسخ إذا رضيت','4419','2770','0');
INSERT INTO [t1153] VALUES (2930,'باب نكاح الكفار. وحكمه حكم نكاح المسلمين فيما يجب بدون تحريم المحرمات','4420','2770','0');
INSERT INTO [t1153] VALUES (2931,'مسألة: ويقرون على الأنكحة المحرمة ما اعتقدوا حلها ولم يرتفعوا إلينا','4421','2770','0');
INSERT INTO [t1153] VALUES (2932,'مسألة: وإن أسلموا أو ترافعوا إلينا في ابتداء العقد لم نمضه إلا على الوجه الصحيح مثل أنكحة المسلمين بالولي والشهود والإيجاب والقبول','4422','2770','0');
INSERT INTO [t1153] VALUES (2933,'مسألة: لكن إن كانت المرأة في هذه الحال ممن لا يجوز ابتداء نكاحها كأحد المحرمات بالنسب أو السبب أو المعتدة أو المرتدة أو الوثنية والمجوسية والمطلقة ثلاثا لم يقرا','4422','2770','0');
INSERT INTO [t1153] VALUES (2934,'مسألة: وإن قهر حربي حربية فوطئها أو طاوعته أو اعتقداه نكاحا ثم أسلما أقرا عليه','4423','2770','0');
INSERT INTO [t1153] VALUES (2935,'مسألة: وإذا كان المهر مسمى صحيحا أو فاسدا قبضته استقر، وإن كان فاسدا فلم تقبضه فرض لها مهر المثل','4423','2770','0');
INSERT INTO [t1153] VALUES (2936,'مسألة: فإن أسلمت الكتابية قبله وقبل الدخول تعجلت الفرقة سواء كان زوجها كتابيا أو غير كتابي إذ لا يجوز لكافر نكاح المسلمة','4426','2770','0');
INSERT INTO [t1153] VALUES (2937,'مسألة: فإن كانت هي المسلمة فلا مهر لها، وإن كان هو المسلم قبلها فلها نصف المهر وعنه لا مهر لها','4426','2770','0');
INSERT INTO [t1153] VALUES (2938,'مسألة: وإن قالت أسلمت قبلي وأنكرها فالقول قولها','4428','2770','0');
INSERT INTO [t1153] VALUES (2939,'مسألة: وإن قال الزوج أسلمنا معا فنحن على النكاح فأنكرته فعلى وجهين','4428','2770','0');
INSERT INTO [t1153] VALUES (2940,'مسألة: وإن أسلم أحدهما بعد الدخول وقف الأمر على انقضاء العدة، فإن أسلم الثاني قبل انقضائها فهما على نكاحهما وإلا تبينا أن الفرقة وقعت من حين أسلم الأول','4429','2770','0');
INSERT INTO [t1153] VALUES (2941,'مسألة: فإن اختلفا في السابق منهما فقال الزوج أسلمت قبلك فلا نفقة لك وقالت المراة بل أنا المسلمة أولا فلي النفقة فالقول قولها في أحد الوجهين','4432','2770','0');
INSERT INTO [t1153] VALUES (2942,'مسألة: فإن أسلمت قبله فلها نفقة العدة','4432','2770','0');
INSERT INTO [t1153] VALUES (2943,'مسألة: وعنه أن الفرقة تتعجل باسلام أحدهما كما قبل الدخول وقد ذكرناه','4433','2770','0');
INSERT INTO [t1153] VALUES (2944,'مسألة: فأما الصداق فواجب بكل حال','4433','2770','0');
INSERT INTO [t1153] VALUES (2945,'مسألة: وإن كانت الردة بعد الدخول فهل تتعجل الفرقة أو تقف على انقضاء العدة؟ على روايتين','4435','2770','0');
INSERT INTO [t1153] VALUES (2946,'مسألة: فإن كان هو المرتد فعليه نفقة العدة','4436','2770','0');
INSERT INTO [t1153] VALUES (2947,'مسألة: وإن انتقل أحد الكتابيين إلى دين لا يقر عليه فهو كردته','4437','2770','0');
INSERT INTO [t1153] VALUES (2948,'مسألة: وعليه نفقتهن إلى أن يختار','4441','2770','0');
INSERT INTO [t1153] VALUES (2949,'مسألة: وإن طلق إحداهن كان اختيارا لها','4441','2770','0');
INSERT INTO [t1153] VALUES (2950,'مسألة: وإن ظاهر أو آلى من إحداهن فهل يكون اختيارا لها؟ على وجهين','4442','2770','0');
INSERT INTO [t1153] VALUES (2951,'مسألة: وإن وطئ إحداهن كان اختيارا لها في قياس المذهب','4442','2770','0');
INSERT INTO [t1153] VALUES (2952,'مسألة: وإن طلق الجميع أقرع بينهن فأخرج أربعا منهن بالقرعة فكن المختارات ووقع طلاقه بهن وانفسخ نكاح البواقي وله نكاح البواقي','4442','2770','0');
INSERT INTO [t1153] VALUES (2953,'مسألة: فإن مات فعلى الجميع عدة الوفاة','4443','2770','0');
INSERT INTO [t1153] VALUES (2954,'مسألة: وإذا ماتت امرأة وخلفت ابنها وعصبتها ومولاها فولاؤها لابنها وعقله على عصبتها','4443','2770','0');
INSERT INTO [t1153] VALUES (2955,'مسألة: والميراث لأربع منهن بالقرعة في قياس المذهب','4443','2770','0');
INSERT INTO [t1153] VALUES (2956,'مسألة: وإن كانتا أما وبنتا ولم يدخل بالأم انفسخ نكاحها، وإن كان دخل بالأم فسد نكاحهما','4447','2770','0');
INSERT INTO [t1153] VALUES (2957,'مسألة: فإن أسلم وهو موسر فلم يسلمن حتى أعسر فله الاختيار منهن، لأن شرائط النكاح تعتبر في وقت الاختيار','4450','2770','0');
INSERT INTO [t1153] VALUES (2958,'مسألة: وإن أسلمت إحداهن بعده ثم عتقت ثم أسلم البواقي فله الاختيار منهن','4451','2770','0');
INSERT INTO [t1153] VALUES (2959,'مسألة: وإن أسلم وتحته حرة وإماء فأسلمت الحرة في عدتها قبلهن أو بعدهن انفسخ نكاحهن','4452','2770','0');
INSERT INTO [t1153] VALUES (2960,'مسألة: وإن أسلم عبد وتحته إماء فأسلمن معه ثم عتق فله أن يختار منهن','4454','2770','0');
INSERT INTO [t1153] VALUES (2961,'مسألة: وإن أسلم وأعتق ثم أسلمن فحكمه حكم الحر','4454','2770','0');
INSERT INTO [t1153] VALUES (2962,'كتاب الصداق','4457','0','0');
INSERT INTO [t1153] VALUES (2963,'كتاب الصداق','4457','2962','0');
INSERT INTO [t1153] VALUES (2964,'مسألة: ويستحب تخفيفه','4458','2962','0');
INSERT INTO [t1153] VALUES (2965,'مسألة: ويستحب أن لا يعرى النكاح من تسميته','4458','2962','0');
INSERT INTO [t1153] VALUES (2966,'مسألة: ويستحب أن لا يزيد على صداق أزواج رسول الله صلى الله عليه وسلم وبناته وهو خمسمائة درهم','4459','2962','0');
INSERT INTO [t1153] VALUES (2967,'مسألة: ولا يتقدر أقله ولا أكثره بل كل ما جاز أن يكون ثمنا جاز أن يكون صداقا','4459','2962','0');
INSERT INTO [t1153] VALUES (2968,'مسألة: كل ما جاز أن يكون ثمنا جاز أن يكون صداقا من قليل وكثير وعين ودين ومعجل ومؤجل ومنفعة معلومة كرعاية غنمها مدة وخياطة ثوب ورد عبدها الآبق من موضع معين','4460','2962','0');
INSERT INTO [t1153] VALUES (2969,'مسألة: وإن كانت المنفعة مجهولة كرد عبدها أين كان','4461','2962','0');
INSERT INTO [t1153] VALUES (2970,'مسألة: وكل ما يجوز أن يكون ثمنا في البيع كالمحرم والمعدوم والمجهول','4461','2962','0');
INSERT INTO [t1153] VALUES (2971,'مسألة: وإن تزوجها على منافعه مدة معلومة فعلى روايتين','4463','2962','0');
INSERT INTO [t1153] VALUES (2972,'مسألة: وكل موضع لا تصح التسمية يجب مهر المثل','4463','2962','0');
INSERT INTO [t1153] VALUES (2973,'مسألة: وإن أصدقها تعليم أبواب من الفقه أو الحديث أو قصيدة من الشعر المباح صح','4464','2962','0');
INSERT INTO [t1153] VALUES (2974,'مسألة: وإن كان لا يحفظها لم يصح ويحتمل أن يصح ويتعلمها ويعلمها','4464','2962','0');
INSERT INTO [t1153] VALUES (2975,'مسألة: فإن تعلمتها من غيره فعليه أجر تعليمها','4465','2962','0');
INSERT INTO [t1153] VALUES (2976,'مسألة: فإن طلقها قبل الدخول وقبل تعليمها ففيه وجهان','4465','2962','0');
INSERT INTO [t1153] VALUES (2977,'مسألة: وإن أصدقها تعليم شيء من القرآن معين لم يصح وعنه يصح','4466','2962','0');
INSERT INTO [t1153] VALUES (2978,'مسألة: ولا يحتاج إلى ذكر قراءة من وقال أبو الخطاب يحتاج إلى ذلك','4468','2962','0');
INSERT INTO [t1153] VALUES (2979,'مسألة: وإن تزوج نساء بمهر واحد وخالعهن بعوض واحد صح ويقسم بينهن على قدر مهورهن في أحد الوجهين وفي الآخر يقسم بينهن بالسوية','4468','2962','0');
INSERT INTO [t1153] VALUES (2980,'مسألة: وإن أصدقها عبدا من عبيده لم يصح','4471','2962','0');
INSERT INTO [t1153] VALUES (2981,'مسألة: وكذلك إن أصدقها عبدا مطلقا فجاءها بقيمته أو خالعته على ذلك فجاءته بقيمته لم يلزمهما قبولها','4474','2962','0');
INSERT INTO [t1153] VALUES (2982,'مسألة: وإن أصدقها طلاق امرأة له أخرى لم يصح، وعنه يصح فإن فات طلاقها بموتها فلها مهرها في قياس المذهب','4475','2962','0');
INSERT INTO [t1153] VALUES (2983,'مسألة: وإن تزوجها على ألف إن كان أبوها حيا وألفين إن كان ميتا لم تصح التسمية','4478','2962','0');
INSERT INTO [t1153] VALUES (2984,'مسألة: وإن تزوجها على ألف إن لم يكن له زوجة وعلى ألفين إن كانت له زوجة لم تصح التسمية','4478','2962','0');
INSERT INTO [t1153] VALUES (2985,'مسألة: وإذا قال العبد لسيدته أعتقيني على أن أتزوجك فأعتقته على ذلك عتق ولم يلزمه شيء، وكذلك إن قالت لعبدها أعتقتك على أن تتزوج بي لم يلزمه ذلك يعتق ولم يلزمه قيمة نفسه','4479','2962','0');
INSERT INTO [t1153] VALUES (2986,'مسألة: وإذا فرض الصداق مؤجلا ولم يذكر محل الأجل صح ومحله الفرقة عند أصحابنا وعند أبي الخطاب لا يصح','4480','2962','0');
INSERT INTO [t1153] VALUES (2987,'مسألة: وإن وجدت به عيبا فلها الخيار بين أخذ أرشه أو رده وأخذ قيمته','4482','2962','0');
INSERT INTO [t1153] VALUES (2988,'مسألة: فإن طلقها قبل الدخول رجع عليها بالألف الذي قبضته ولم يكن على الأب شيء مما أخذ','4484','2962','0');
INSERT INTO [t1153] VALUES (2989,'مسألة: وإن فعل ذلك غير الأب فالكل لها دونه','4485','2962','0');
INSERT INTO [t1153] VALUES (2990,'مسألة: وللأب تزويج ابنته البكر بدون صداق مثلها وإن كرهت','4486','2962','0');
INSERT INTO [t1153] VALUES (2991,'مسألة: وإن فعل ذلك غيره بأختها صح','4487','2962','0');
INSERT INTO [t1153] VALUES (2992,'مسألة: وإن زوج ابنه الصغير بأكثر من مهر المثل صح ذمة الابن','4487','2962','0');
INSERT INTO [t1153] VALUES (2993,'مسألة: فإن كان معسرا فهل يضمنه الأب؟ يحتمل وجهين','4487','2962','0');
INSERT INTO [t1153] VALUES (2994,'مسألة: وللأب قبض صداق ابنه الصغيرة بغير إذنها','4488','2962','0');
INSERT INTO [t1153] VALUES (2995,'مسألة: وإن تزوج بغير إذن سيده لم يصح النكاح','4489','2962','0');
INSERT INTO [t1153] VALUES (2996,'مسألة: فإن فارقها قبل الدخول فلا شيء عليه','4490','2962','0');
INSERT INTO [t1153] VALUES (2997,'مسألة: وإن زوج السيد عبده أمته لم يجب مهر ذكره أبو بكر والقاضي','4493','2962','0');
INSERT INTO [t1153] VALUES (2998,'مسألة: وإن زوج عبده حرة ثم باعها أياه بثمن في الذمة تحول صداقها أو نصفه إن كان قبل الدخول الى ثمنه','4493','2962','0');
INSERT INTO [t1153] VALUES (2999,'مسألة: فإن كان معينا كالعبد والدار فلها التصرف فيه ونماؤه لها وزكاته ونقصه وضمانه عليها سواء قبضته أو لم تقبضه متصلا كان النماء أو منفصلا وعليها زكاته إذا حال عليها الحول نص عليه أحمد','4495','2962','0');
INSERT INTO [t1153] VALUES (3000,'مسألة: فإن كان غير معين كقفيز من صبرة لم يدخل في ضمانها ولم تملك التصرف فيه إلا بقبضه كالبيع','4496','2962','0');
INSERT INTO [t1153] VALUES (3001,'مسألة: فإن قبضت صداقها ثم طلقها قبل الدخول رجع عليها بنصفه إن كان باقيا','4497','2962','0');
INSERT INTO [t1153] VALUES (3002,'مسألة: وإن كان ناقصا خير الزوج بين أخذ نصفه ناقصا وبين أخذ القيمة يوم العقد','4499','2962','0');
INSERT INTO [t1153] VALUES (3003,'مسألة: وإن كان تالفا أو مستحقا بدين أو شفعة فله نصف القيمة يوم العقد إلا أن يكون مثليا فيرجع بنصف مثله','4501','2962','0');
INSERT INTO [t1153] VALUES (3004,'مسألة: وإن نقص الصداق في يدها بعد الطلاق فهل تضمن بعضه. يحتمل وجهين','4503','2962','0');
INSERT INTO [t1153] VALUES (3005,'مسألة: وإن قال الزوج نقص قبل الطلاق فعليك ضمانه وقالت بعده فالقول قولها مع يمينها','4504','2962','0');
INSERT INTO [t1153] VALUES (3006,'مسألة: والزوج هو الذي بيده عقدة النكاح فإذا طلقها قبل الدخول فأيهما عفا لصاحبه عما وجب له من الأمر وهو جائر الأمر في ماله برئ منه صاحبه، وعنه أنه الأب فله أن يعفو عن نصف صداق ابنته الصغيرة إذا طلقت قبل الدخول','4512','2962','0');
INSERT INTO [t1153] VALUES (3007,'مسألة: وكل فرقة جاءت من قبل الزوج قبل الدخول كطلاقه وخلعه وإسلامه وردته أو من أجنبي كالرضاع ونحوه يتنصف بها المهر بينهما','4521','2962','0');
INSERT INTO [t1153] VALUES (3008,'مسألة: وكل فرقة جاءت من المرأة قبل الدخول كإسلامها أو ردتها أو رضاعها من ينفسخ النكاح برضاعه أو ارتضاعها وهي صغيرة أو فسخها لعنته واعساره أو فسخه لعيبها أو فسخها لعتقها تحت عبد فإنه يسقط به مهرها، ولا تجب المتعة لأنها أتلفت العوض قبل تسليمه فسقط ال','4522','2962','0');
INSERT INTO [t1153] VALUES (3009,'مسألة: وفرقة اللعان تخرج على روايتين','4522','2962','0');
INSERT INTO [t1153] VALUES (3010,'مسألة: وفي فرقة بيع من الزوج وشرائها له وجهان','4523','2962','0');
INSERT INTO [t1153] VALUES (3011,'مسألة: وفرقة الموت يستقر بها المهر كله، كالدخول إذا كان المهر مسمى','4523','2962','0');
INSERT INTO [t1153] VALUES (3012,'مسألة: فإن ادعى أقل منه وادعت أكثر منه رد إليه بلا يمين عند القاضي في الأحوال كلها','4524','2962','0');
INSERT INTO [t1153] VALUES (3013,'مسألة: وإن قال تزوجتك على هذا العبد قالت بل على هذه الأمة خرج على الروايتين','4525','2962','0');
INSERT INTO [t1153] VALUES (3014,'مسألة: وإن اختلفا في قبض الصداق فالقول قولها مع يمينها إذا لم تكن بينة','4529','2962','0');
INSERT INTO [t1153] VALUES (3015,'مسألة: وإن قال هو عقد واحد أسررته ثم أظهرته وقالت بل هو عقدان فالقول قولها مع يمينها','4531','2962','0');
INSERT INTO [t1153] VALUES (3016,'مسألة: ولها المطالبة بفرضه لأن النكاح لا يخلو من المهر فوجب لها المطالبة ببيان قدره','4538','2962','0');
INSERT INTO [t1153] VALUES (3017,'مسألة: وإن مات أحدهما قبل الإصابة وقبل الفرض ورثه صاحبه ولها مهر نسائها','4541','2962','0');
INSERT INTO [t1153] VALUES (3018,'مسألة: فإن طلقها قبل الدخول لم يكن لها عليه إلا المتعة','4543','2962','0');
INSERT INTO [t1153] VALUES (3019,'مسالة: على الموسع قدره وعلى المقتر قدره، فأعلاها خادم وأدناها كسوة يجوز لها أن تصلي فيها','4545','2962','0');
INSERT INTO [t1153] VALUES (3020,'مسألة: وعن أحمد في تقديرها إلى الحاكم','4546','2962','0');
INSERT INTO [t1153] VALUES (3021,'مسألة: فإن دخل بها استقر مهر المثل لأن الوطء في نكاح من غير مهر خالص لرسول الله صلى الله عليه وسلم فإن طلقها بعد ذلك فهل تجب المتعة؟ على روايتين أصحهما لا يجب','4547','2962','0');
INSERT INTO [t1153] VALUES (3022,'مسألة: فإن لم يوجد إلا دونها زيدت بقدر فضيلتها','4550','2962','0');
INSERT INTO [t1153] VALUES (3023,'مسألة: فإن لم يوجد إلا دونها زيدت بقدر فضيلتها','4550','2962','0');
INSERT INTO [t1153] VALUES (3024,'مسألة: فإن كانت عادة نسائها تأجيل المهر فرض مؤجلا في أحد الوجهين','4551','2962','0');
INSERT INTO [t1153] VALUES (3025,'مسألة: فإن لم يكن لها أقارب اعتبر شبهها من أهل بلدها','4551','2962','0');
INSERT INTO [t1153] VALUES (3026,'مسألة: فإن دخل بها استقر المسمى وعنه يجب مهر المثل وهي أصح','4551','2962','0');
INSERT INTO [t1153] VALUES (3027,'مسألة: ولا يستقر بالخلوة','4552','2962','0');
INSERT INTO [t1153] VALUES (3028,'مسألة: ويجب مهر المثل للموطوءة بشبهة والمكرهة على الزنا ولا يجب معه أرش البكارة ويحتمل أن يجب للمكرهة','4553','2962','0');
INSERT INTO [t1153] VALUES (3029,'مسألة: وإذا دفع أجنبية فأذهب عذرتها فعليه أرش بكارتها','4555','2962','0');
INSERT INTO [t1153] VALUES (3030,'مسألة: فإن فعل ذلك الزوج ثم طلق قبل الدخول لم يكن لها عليه إلا نصف المسمى','4556','2962','0');
INSERT INTO [t1153] VALUES (3031,'مسألة: وللمرأة منع نفسها حتى تقبض مهرها إذا كان حالا','4556','2962','0');
INSERT INTO [t1153] VALUES (3032,'مسألة: وإن أعسر بالمهر قبل الدخول فلها الفسخ إذا كان حالا','4558','2962','0');
INSERT INTO [t1153] VALUES (3033,'مسألة: ولا يجوز الفسخ في ذلك كله إلا بحكم حاكم','4558','2962','0');
INSERT INTO [t1153] VALUES (3034,'باب الوليمة','4559','2962','0');
INSERT INTO [t1153] VALUES (3035,'مسألة: وهي مستحبة','4559','2962','0');
INSERT INTO [t1153] VALUES (3036,'مسألة: والإجابة إليها واجبة إذا عينه الداعي المسلم في اليوم الأول','4560','2962','0');
INSERT INTO [t1153] VALUES (3037,'مسألة: فإن دعا الجفلى كقوله يا أيها الناس تعالوا إلى الطعام أو يقول الرسول أمرت أن أدعو كل من لقيت أو شئت لم تجب الإجابة ولم تستحب لأنه لم يعين وبالدعوة فلم تتعين عليه الإجابة','4561','2962','0');
INSERT INTO [t1153] VALUES (3038,'مسألة: أو دعاه فيما بعد اليوم الأول','4561','2962','0');
INSERT INTO [t1153] VALUES (3039,'مسألة: وسائر الدعوات والإجابة إليها مستحبة غير واجبة لما فيها من إطعام الطعام وكذلك الإجابة إليها','4562','2962','0');
INSERT INTO [t1153] VALUES (3040,'مسألة: وإذا حضر وهو صائم صوما واجبا لم يفطر وإن كان نفلا أو مفطرا استحب له الأكل وإن أحب دعا وانصرف','4563','2962','0');
INSERT INTO [t1153] VALUES (3041,'مسألة: وإن دعاه اثنان أجاب أولهما','4565','2962','0');
INSERT INTO [t1153] VALUES (3042,'مسألة: وإن علم أن في الدعوة منكرا كالزمر والخمر وأمكنه الإنكار حضر وأنكر وإلا لم يحضر','4566','2962','0');
INSERT INTO [t1153] VALUES (3043,'مسألة: وإن شاهد ستورا معلقة فيها صور الحيوان لم يجلس إلى أن تزال وإن كانت مبسوطة أو على وسائد فلا بأس','4567','2962','0');
INSERT INTO [t1153] VALUES (3044,'مسألة: وإن علم به فلم يره ولم يسمعه فله الجلوس والأكل نص عليه أحمد','4567','2962','0');
INSERT INTO [t1153] VALUES (3045,'مسألة: فإن سترت الحيطان بستور لا صور فيها أو فيها صور غير الحيوان فهل تباح؟ على روايتين','4571','2962','0');
INSERT INTO [t1153] VALUES (3046,'مسألة: والنثار والتقاطه مكروه وعنه لا يكره','4573','2962','0');
INSERT INTO [t1153] VALUES (3047,'مسألة: ولا يباح الأكل بغير إذن','4573','2962','0');
INSERT INTO [t1153] VALUES (3048,'مسألة: ومن حصل في حجره شيء فهو له غير مكروه','4575','2962','0');
INSERT INTO [t1153] VALUES (3049,'مسألة: ويستحب إعلان النكاح والضرب عليه بالدف','4575','2962','0');
INSERT INTO [t1153] VALUES (3050,'فصل في آداب الأكل','4576','2962','0');
INSERT INTO [t1153] VALUES (3051,'باب عشرة النساء','4580','2962','0');
INSERT INTO [t1153] VALUES (3052,'مسألة: وإذا تم العقد وجب تسليم المرأة في بيت الزوج إذا طلبها وكانت حرة يمكن الاستمتاع بها','4581','2962','0');
INSERT INTO [t1153] VALUES (3053,'مسألة: فإن سألت الانظار أنظرت مدة جرت العادة بإصلاحها أمرها فيها كاليومين والثلاثة','4583','2962','0');
INSERT INTO [t1153] VALUES (3054,'مسألة: وإن كانت أمة لم يجب تسليمها إلا بالليل','4583','2962','0');
INSERT INTO [t1153] VALUES (3055,'مسألة: وإنما يجب تسليمها في بيت الزوج إذا لم تشترط دارها','4583','2962','0');
INSERT INTO [t1153] VALUES (3056,'مسألة: وله الاستمتاع بها ما لم يشغلها عن الفرائض من غير إضرار بها','4584','2962','0');
INSERT INTO [t1153] VALUES (3057,'مسألة: ولا يجوز وطؤها في الحيض إجماعا','4585','2962','0');
INSERT INTO [t1153] VALUES (3058,'مسألة: ولا يعزل عن الحرة إلا بإذنها','4586','2962','0');
INSERT INTO [t1153] VALUES (3059,'مسألة: وله إجبارها على الغسل من الحيض والجنابة والنجاسة واجتناب المحرمات وأخذ الشعر الذي تعافه النفس إلا الذمية فله إجبارها على الغسل من الحيض والنفاس وفي سائر الأشياء روايتان','4589','2962','0');
INSERT INTO [t1153] VALUES (3060,'مسألة: وإن كانت أمة فمن ثمان ليال ليلة','4591','2962','0');
INSERT INTO [t1153] VALUES (3061,'مسألة: وله الانفراد بنفسه فيما بقي وقد ذكرناه لأنه قد وفاهن حقهن فلم تجب عليه زيادة كما لو وفاهن حقهن من النفقة والكسوة والسكن','4592','2962','0');
INSERT INTO [t1153] VALUES (3062,'مسألة: وعليه أن يطأ في كل أربعة أشهر مرة','4592','2962','0');
INSERT INTO [t1153] VALUES (3063,'مسألة: فإن سافر عنها أكثر من ستة أشهر فطلبت قدومه لزمه ذلك إن لم يكن له عذر','4593','2962','0');
INSERT INTO [t1153] VALUES (3064,'مسألة: فإن أتى شيئا من ذلك ولم يكن ثم عذر فطلبت الفرقة فرق بينهما','4594','2962','0');
INSERT INTO [t1153] VALUES (3065,'مسألة: ويستحب أن يقول عند الجماع بسم الله اللهم جنبني الشيطان وجنب الشياطين ما رزقتني','4595','2962','0');
INSERT INTO [t1153] VALUES (3066,'مسألة: ولا ينزع إذا فرغ قبلها حتى يفرغ','4597','2962','0');
INSERT INTO [t1153] VALUES (3067,'مسألة: ولا بأس أن يجمع بين وطء نسائه وإمائه بغسل واحد','4597','2962','0');
INSERT INTO [t1153] VALUES (3068,'مسألة: ولا يكثر الكلام حال الوطء','4597','2962','0');
INSERT INTO [t1153] VALUES (3069,'مسألة: ولا يجامع إحداهما بحيث تراه الأخرى أو غيرهما لأن فيه دناءة ولا يحدثها بما جرى بينهما ولا يحدث غيرها','4599','2962','0');
INSERT INTO [t1153] VALUES (3070,'مسألة: وله منعها من الخروج من منزلها إلى ما لها منه بد سواء أرادت والديها أو عيادتهما أو حضور جنازة أحدهما','4599','2962','0');
INSERT INTO [t1153] VALUES (3071,'مسألة: فإن مرض بعض محارمها أو مات استحب له أن يأذن لها في الخروج إليه','4600','2962','0');
INSERT INTO [t1153] VALUES (3072,'مسألة: ولا تملك المرأة إجازة نفسها للرضاع والخدمة بغير إذن زوجها','4601','2962','0');
INSERT INTO [t1153] VALUES (3073,'مسألة: وله أن يمنعها من رضاع ولدها إلا أن يضطر إليها ويخشى عليه','4602','2962','0');
INSERT INTO [t1153] VALUES (3074,'مسألة: وعماد القسم الليل إلا لمن معيشته بالليل كالحارس','4604','2962','0');
INSERT INTO [t1153] VALUES (3075,'مسألة: وليس له البداءة بإحداهن ولا السفر إلا بقرعة','4605','2962','0');
INSERT INTO [t1153] VALUES (3076,'مسألة: إذا بات عندها أو غيرها لزمه المبيت عند الثانية','4605','2962','0');
INSERT INTO [t1153] VALUES (3077,'مسألة: وليس عليه التسوية بينهن في الوطء بل يستحب','4606','2962','0');
INSERT INTO [t1153] VALUES (3078,'مسألة: ويقسم لزوجته الأمة ليلة وللحرة ليلتين وإن كانت كتابية','4607','2962','0');
INSERT INTO [t1153] VALUES (3079,'مسألة: ويقسم للحائض والنفساء والمريضة والمعيبة والمحرمة والصغيرة الممكن وطؤها وكلهن سواء في القسم','4609','2962','0');
INSERT INTO [t1153] VALUES (3080,'مسألة: فإن دخل في ليلتها الى غيرها لم يجز إلا لحاجة داعية فإن لم يلبث لم يقض وإن لبث أو جامع لزمه أن يقضي لها ذلك من حق الأخرى','4609','2962','0');
INSERT INTO [t1153] VALUES (3081,'مسألة: ومتى سافر بها بقرعة لم يقض وإن كان بغير قرعة لزمه القضاء للأخرى','4614','2962','0');
INSERT INTO [t1153] VALUES (3082,'مسألة: وإن امتنعت من السفر معه أو من المبيت عنده أو سافرت بغير إذن سقط حقها من القسم','4616','2962','0');
INSERT INTO [t1153] VALUES (3083,'مسألة: وإن أشخصها هو فهي على حقها من ذلك','4616','2962','0');
INSERT INTO [t1153] VALUES (3084,'مسألة: وإن سافرت لحاجتها بإذنه فعلى وجهين','4617','2962','0');
INSERT INTO [t1153] VALUES (3085,'مسألة: وللمرأة أن تهب حقها من القسم لبعض ضرائرها بإذنه أو له فيجعله لمن شاء منهن','4617','2962','0');
INSERT INTO [t1153] VALUES (3086,'مسألة: ولا قسم عليه في ملك اليمين وله الاستمتاع بهن كيف شاء','4619','2962','0');
INSERT INTO [t1153] VALUES (3087,'مسألة: ويستحب التسوية بينهن لئلا يضر ببعضهن وأن لا يعضلهن إن لم يرد الاستمتاع بهن','4619','2962','0');
INSERT INTO [t1153] VALUES (3088,'مسألة: فمتى رجعت في الهبة عاد حقها ولها ذلك في المستقبل لأنها هبة لم تقبض، وليس لها الرجوع فيما مضى','4619','2962','0');
INSERT INTO [t1153] VALUES (3089,'مسألة: وإن زفت إليه امرأتان قدمت السابقة منهما ثم أقام عند الأخرى ثم دار، وإن زفتا معا قدم إحداهما بالقرعة ثم أقام عند الأخرى','4621','2962','0');
INSERT INTO [t1153] VALUES (3090,'مسألة: وإن أراد السفر فخرجت القرعة لأحداهما سافر بها ودخل حق العقد في قسم السفر فإذا قدم بدأ بالأخرى فوفاها حق العقد','4621','2962','0');
INSERT INTO [t1153] VALUES (3091,'مسألة: فمتى ظهرت منها إمارات النشوز بأن لا تجيبه إلى الاستمتاع أو تجيبه متبرمة ومتكرهة وعظها فإن أصرت هجرها في المضجع ما شاء','4622','2962','0');
INSERT INTO [t1153] VALUES (3092,'مسألة: وله أن يخرج في نهار ليل القسم لمعاشه وقضاء حقوق الناس','4622','2962','0');
INSERT INTO [t1153] VALUES (3093,'فصل: في النشوز','4622','2962','0');
INSERT INTO [t1153] VALUES (3094,'مسألة: فإن ادعى كل واحد منهما ظلم صاحبه له اسكنهما الحاكم الى جانب ثقة يشرف عليهما ويلزمهما الانصاف','4625','2962','0');
INSERT INTO [t1153] VALUES (3095,'مسألة: فإن امتنعا من ذلك لم يجبرا عليه وعنه أن الزوج إن وكل في الطلاق بعوض أو وكلت المرأة في بذل العوض وإلا جعل الحاكم إليهما ذلك','4625','2962','0');
INSERT INTO [t1153] VALUES (3096,'مسألة: فإن خرجا إلى الشقاق والعداوة بعث الحاكم حكمين حرين مسلمين عدلين','4625','2962','0');
INSERT INTO [t1153] VALUES (3097,'مسألة: فإن غاب الزوجان أو أحدهما لم ينقطع نظر الحكمين على الرواية الأولى وينقطع على الثانية، وإن جنا انقطع نظرهما على الرواية الأولى ولم ينقطع على الثانية','4627','2962','0');
INSERT INTO [t1153] VALUES (3098,'كتاب الخلع','4628','0','0');
INSERT INTO [t1153] VALUES (3099,'كتاب الخلع','4628','3098','0');
INSERT INTO [t1153] VALUES (3100,'مسألة: وإن خالعته لغير ذلك كره ووقع الخلع وعنه لا يجوز','4630','3098','0');
INSERT INTO [t1153] VALUES (3101,'مسألة: فأما إن عضلها لتفدي نفسها منه ففعلت فالخلع باطل والعوض مردود والزوجية بحالها إلا أن يكون طلاقا فيكون رجعيا','4631','3098','0');
INSERT INTO [t1153] VALUES (3102,'مسألة: فإن كان محجورا عليه دفع المال إلى وليه لأن ولي المحجور عليه هو الذي يقبض حقوقه وأمواله وهذا من حقوقه','4633','3098','0');
INSERT INTO [t1153] VALUES (3103,'مسألة: وإن كان عبدا إلى سيده لأنه للسيد لكونه من اكتساب عبده واكتسابه له','4633','3098','0');
INSERT INTO [t1153] VALUES (3104,'مسألة: ويصح الخلع من كل زوج يصح طلاقه مسلما كان أو ذميا لأنه إذا ملك الطلاق وهو مجرد إسقاط من غير تحصيل شيء فلأن يملكه محصلا للعوض أولى','4633','3098','0');
INSERT INTO [t1153] VALUES (3105,'مسألة: وهل للأب خلع ابنته الصغيرة أو طلاقها؟ على روايتين','4634','3098','0');
INSERT INTO [t1153] VALUES (3106,'مسألة: وليس له خلع ابنته الصغيرة بشيء من مالها','4635','3098','0');
INSERT INTO [t1153] VALUES (3107,'مسألة: ويصح الخلع مع الزوجة','4635','3098','0');
INSERT INTO [t1153] VALUES (3108,'مسألة: ويصح بذل العوض فيه من كل جائز التصرف لأنه بذل عوض في عقد معاوضة أشبه البيع','4636','3098','0');
INSERT INTO [t1153] VALUES (3109,'مسألة: فإن خالعته الأمة على شيء معلوم بغير إذن سيدها كان في ذمتها تتبع به بعد العتق','4637','3098','0');
INSERT INTO [t1153] VALUES (3110,'مسألة: وإن خالعته المحجور عليها لم يصح الخلع ووقع طلاقه رجعيا أما المحجور عليها للفلس فيصح خلعها وبذلها للعوض','4638','3098','0');
INSERT INTO [t1153] VALUES (3111,'مسألة: والخلع طلاق بائن إلا أن يقع بالخلع أو الفسخ والمفاداة ولا ينوي به الطلاق فينكون فسخا لا ينقص به عدد الطلاق في إحدى الروايتين والأخرى هو طلاق بائن بكل حال','4639','3098','0');
INSERT INTO [t1153] VALUES (3112,'مسألة: وإن طلق إحدى نسائه في ليلتها أثم لأنه فوت حقها الواجب لها فإن عادت إليه برجعة أو نكاح قضى لها لأنه قدر على إيفاء حقها فلزمه كالمعسر إذا أيسر بالدين','4640','3098','0');
INSERT INTO [t1153] VALUES (3113,'مسألة: ولا يقع بالعدة من الخلع طلاق ولو واجهها به','4642','3098','0');
INSERT INTO [t1153] VALUES (3114,'مسألة: وإن شرط الرجعة في الخلع لم يصح الشرط في أحد الوجهين وفي الآخر يصح الشرط ويبطل العوض','4644','3098','0');
INSERT INTO [t1153] VALUES (3115,'مسألة: ولا يستحب أن يأخذ منها أكثر مما أعطاها فإن فعل كره وصح، وقال أبو بكر لا يجوز ويترك الزيادة','4647','3098','0');
INSERT INTO [t1153] VALUES (3116,'مسألة: وإن أراد النقلة من بلد إلى بلد وأخذ إحداهن معه والأخرى مع غيره لم يجز إلا بقرعة','4649','3098','0');
INSERT INTO [t1153] VALUES (3117,'مسألة: وإن خالعها على محرم كالخمر والحر فهو كالخلع بغير عوض إذا علما تحريمه','4649','3098','0');
INSERT INTO [t1153] VALUES (3118,'مسألة: وإن خالعها على عبد فبان حرا أو مستحقا فله قيمته عليها، وإن بان معيبا فله أرشه أو قيمته ويرده','4650','3098','0');
INSERT INTO [t1153] VALUES (3119,'مسألة: وإن خالعها على رضاع ولده عامين أو سكنى دار صح فإن مات الولد أو خربت الدار رجع بأجرة باقي المدة','4651','3098','0');
INSERT INTO [t1153] VALUES (3120,'مسألة: وإن خالع الحامل على نفقة عدتها صح وسقطت','4654','3098','0');
INSERT INTO [t1153] VALUES (3121,'مسألة: فإن خالعها على ما في يدها من الدراهم صح وله ما في يدها، وإن لم يكن في يدها شيء فله عليها ثلاثة دراهم','4656','3098','0');
INSERT INTO [t1153] VALUES (3122,'مسألة: وإن خالعها على ما في بيتها من المتاع فإن كان فيه متاع فهو له قليلا كان أو كثيرا لأن الخلع على المجهول جائز كالوصية به معلوما كان أو مجهولا لأن الاسم يقع عليه وإن لم يكن فيه متاع فله أقل ما يقع عليه أسم المتاع كالوصية وكالمسألة قبلها','4656','3098','0');
INSERT INTO [t1153] VALUES (3123,'مسألة: وإن خالعها على حمل أمتها أو ما تحمل شجرتها فله ذلك، فإن لم تحملا فقال أحمد ترضيه بشيء. وقال القاضي لا شيء له','4657','3098','0');
INSERT INTO [t1153] VALUES (3124,'مسألة: وإن خالعها على عبد فله أقل ما يسمى عبدا، وإن قال إن أعطيتني عبدا فأنت طالق طلقت بأي عبد أعطته طلاقا بائنا وملك العبد','4658','3098','0');
INSERT INTO [t1153] VALUES (3125,'مسألة: وإذا قال إذا أعطيتني هذا العبد فأنت طالق فأعطته إياه طلقت فإن خرج معيبا فلا شيء له','4659','3098','0');
INSERT INTO [t1153] VALUES (3126,'مسألة: وإن قال إن أعطيتني ثوبا هرويا فانت طالق فأعطته مرويا لم تطلق','4661','3098','0');
INSERT INTO [t1153] VALUES (3127,'مسألة: إذا قالت اخلعني بألف أو على ألف ففعل بانت واستحق الألف','4665','3098','0');
INSERT INTO [t1153] VALUES (3128,'مسألة: وإذا قالت طلقني واحدة بألف فطلقها ثلاثا استحق الألف','4666','3098','0');
INSERT INTO [t1153] VALUES (3129,'مسألة: وإن قالت طلقني ثلاثا بألف فطلقها واحدة لم يستحق شيئا ويحتمل أن يستحق ثلث الألف','4667','3098','0');
INSERT INTO [t1153] VALUES (3130,'مسألة: وإن لم يكن بقي من طلاقها إلا واحدة ففعل استحق الألف علمت أو لم تعلم ويحتمل أن لا يستحق إلا ثلثه إذا لم تعلم','4669','3098','0');
INSERT INTO [t1153] VALUES (3131,'مسألة: ولو لم يكن بقي من طلاقها إلا واحدة فقالت طلقني ثلاثا بألف فقال أنت طالق طلقتين الأولى بألف والثانية بغير شيء وقعت الأولى واستحق الألف ولم تقع الثانية','4670','3098','0');
INSERT INTO [t1153] VALUES (3132,'مسألة: وإن كان له امرأتان مكلفة وغير مكلفة مميزة فقال لهما أنتما طالقتان إن شئتما، فقالتا قد شئنا لزم المكلفة نصف الألف وطلقت بائنا ووقع بالأخرى رجعيا ولا شيء عليها','4672','3098','0');
INSERT INTO [t1153] VALUES (3133,'مسألة: فإن قال لامرأته أنت طالق وعليك ألف طلقت ولا شيء عليها لأنه لم يجعل له العوض في مقابلتها ولا شرطا فيها','4673','3098','0');
INSERT INTO [t1153] VALUES (3134,'مسألة: وإن قال بألف فكذلك ويحتمل أن لا تطلق حتى تختار فيلزمها الألف','4674','3098','0');
INSERT INTO [t1153] VALUES (3135,'مسألة: وإن قال أنت طالق على ألف','4674','3098','0');
INSERT INTO [t1153] VALUES (3136,'مسألة: وإن خالعها وحاباها فهو من رأس المال','4677','3098','0');
INSERT INTO [t1153] VALUES (3137,'مسألة: وإن خالعها في مرض موته وأوصى لها بأكثر من ميراثها لم تستحق أكثر من ميراثها','4677','3098','0');
INSERT INTO [t1153] VALUES (3138,'مسألة: وإذا وكل الزوج في خلع امرأته مطلقا فخالع بمهرها فما زاد صح، وإن نقص من المهر رجع على الوكيل بالنقص ويحتمل أن يتخير بين قبوله ناقصا وبين رده وله الرجعة، وإن عين له العوض فنقص منه لم يصح الخلع عند ابن حامد. وصح عند كأبي بكر ويرجع على الوكي','4680','3098','0');
INSERT INTO [t1153] VALUES (3139,'مسألة: وإن وكلت المرأة في خلعها فخالع بمهرها فما دون أو بما عينته فما دون صح، وإن زاد لم يصح ويحتمل أن يصح وتبطل الزيادة','4683','3098','0');
INSERT INTO [t1153] VALUES (3140,'مسألة: وإن تخالعا تراجعا بما بينهما من الحقوق وعنه أنها تسقط','4683','3098','0');
INSERT INTO [t1153] VALUES (3141,'مسألة: وإن اختلفا في قدر العوض أو عينه أو تأجيله فالقول قولها، وكذلك إن اختلفا في صفته','4684','3098','0');
INSERT INTO [t1153] VALUES (3142,'مسألة: وإن علق طلاقها بصفة ثم خالعها فوجدت الصفة ثم عاد فتزوجها فوجدت الصفة طلقت نص عليه ويتخرج أن لا تطلق بناء على الرواية في العتق واختاره أبو الحسن التميمي، وإن لم توجد الصفة حال البينونة عادت رواية واحدة','4685','3098','0');
INSERT INTO [t1153] VALUES (3143,'كتاب الطلاق','4688','0','0');
INSERT INTO [t1153] VALUES (3144,'كتاب الطلاق','4688','3143','0');
INSERT INTO [t1153] VALUES (3145,'مسألة: ويباح عند الحاجة ويكره من غير حاجة وعنه أنه يحرم، ويستحب إذا كان بقاء النكاح ضررا','4689','3143','0');
INSERT INTO [t1153] VALUES (3146,'مسألة: ويصح من الزوج العاقل البالغ المختار ومن الصبي العاقل وعنه لا يصح حتى يبلغ','4690','3143','0');
INSERT INTO [t1153] VALUES (3147,'مسألة: ومن زال عقله بسبب يعذر فيه كالمجنون والنائم والمغمى عليه والمبرسم لم يقع طلاقه','4692','3143','0');
INSERT INTO [t1153] VALUES (3148,'مسألة: وإن كان بسبب لا يعذر فيه كالسكران ومن شرب ما يزيل عقله لغير حاجة ففي صحة طلاقه روايتان وكذا يخرج في قتله وقذفه وسرقته وزناه وظهاره وإيلائه','4693','3143','0');
INSERT INTO [t1153] VALUES (3149,'مسألة: ومن أكره على الطلاق بغير حق لم يقع طلاقه','4696','3143','0');
INSERT INTO [t1153] VALUES (3150,'مسألة: وإن هدده بالقتل وأخذ المال ونحوه قادر يغلب على ظنه وقوع ما هدده به فهو إكراه، وعنه لا يكون مكرها حتى يناله شيء من العذاب كالضرب والخنق وعصر الساق واختاره الخرقي','4698','3143','0');
INSERT INTO [t1153] VALUES (3151,'مسألة: ويقع الطلاق في النكاح المختلف فيه كالنكاح بلا ولي عند أصحابنا','4701','3143','0');
INSERT INTO [t1153] VALUES (3152,'مسألة: وإذا وكل في الطلاق من يصح توكيله صح طلاقه','4701','3143','0');
INSERT INTO [t1153] VALUES (3153,'مسألة: وله أن يطلق متى شاء إلا أن يحد له حدا','4702','3143','0');
INSERT INTO [t1153] VALUES (3154,'مسألة: ولا يطلق أكثر من واحدة إلا أن يجعل ذلك إليه','4702','3143','0');
INSERT INTO [t1153] VALUES (3155,'مسألة: وإن قال لامرأته طلقي نفسك فلها ذلك كالوكيل، فإن نوى عددا فهو على ما نوى وإن طلق من غير نية لم يملك إلا واحدة','4703','3143','0');
INSERT INTO [t1153] VALUES (3156,'مسألة: فإن وكل اثنين صح وليس لأحدهما أن يطلق على الانفراد إلا أن يجعل ذلك إليه','4703','3143','0');
INSERT INTO [t1153] VALUES (3157,'مسألة: وإن قال اختاري من ثلاث ما شئت لم يكن لها أن تختار أكثر من اثنتين','4705','3143','0');
INSERT INTO [t1153] VALUES (3158,'باب سنة الطلاق وبدعته','4706','3143','0');
INSERT INTO [t1153] VALUES (3159,'مسألة: وإن طلق المدخول بها في حيضها أو في طهر أصابها فيه فهو طلاق بدعة محرم ويقع طلاقه في قوله عامة أهل العلم','4708','3143','0');
INSERT INTO [t1153] VALUES (3160,'مسألة: تستحب رجعتها وعنه أنها واجبة','4709','3143','0');
INSERT INTO [t1153] VALUES (3161,'مسألة: وإن طلقها ثلاثا في طهر لم يصبها فيه كره وفي تحريمه روايتان','4711','3143','0');
INSERT INTO [t1153] VALUES (3162,'مسألة: وإن قال لمن لها سنة وبدعة أنت طالق في طهر لم يصبها فيه طلقت إذا طهرت من الحيضة المستقبلة','4718','3143','0');
INSERT INTO [t1153] VALUES (3163,'مسألة: وإن قال لها أنت طالق للبدعة وهي حائض أو في طهر أصابها فيه طلقت في الحال','4720','3143','0');
INSERT INTO [t1153] VALUES (3164,'مسألة: وإن قال لها أنت طالق ثلاثا للسنة طلقت ثلاثا في طهر لم يصبها فيه في إحدى الروايتين، وفي الاخرى تطلق في الحال واحدة وتطلق الثانية والثالثة في طهرين في نكاحين ان أمكن','4720','3143','0');
INSERT INTO [t1153] VALUES (3165,'مسألة: وإن قال لها أنت طالق في كل قرء طلقة وهي من اللائي لم يحضن لم تطلق حتى تحيض فتطلق في كل حيضة طلقة','4724','3143','0');
INSERT INTO [t1153] VALUES (3166,'مسألة: وإن قال أنت طالق أحسن الطلاق أو أجمله فهو كقوله أنت طالق للسنة','4726','3143','0');
INSERT INTO [t1153] VALUES (3167,'مسألة: وإن قال أقبحه أو أسمجه أو أفحشه أو أردأه أو ألكعه حمل على طلاق البدعة فإن كانت في وقت البدعة وإلا وقف على مجيء زمان البدعة','4726','3143','0');
INSERT INTO [t1153] VALUES (3168,'مسألة: وإن قال أنت طالق طلقة حسنة قبيحة فاحشة جميلة تامة ناقصة وقع في الحال','4727','3143','0');
INSERT INTO [t1153] VALUES (3169,'مسألة: وإن قال أردت أن أحسن أحوالك أو أقبحها أن تكوني مطلقة فيقع في الحال','4727','3143','0');
INSERT INTO [t1153] VALUES (3170,'باب صريح الطلاق وكنايته','4728','3143','0');
INSERT INTO [t1153] VALUES (3171,'مسألة: وقال الخرقي صريحه ثلاثة ألفاظ الطلاق والفراق والسراح وما تصرف منهن','4729','3143','0');
INSERT INTO [t1153] VALUES (3172,'مسألة: فمتى أتى بصريح الطلاق وقع نواه أو لم ينوه','4730','3143','0');
INSERT INTO [t1153] VALUES (3173,'مسألة: فإن نوى بقوله أنت طالق من وثاق أو أراد أن يقول طاهر فسبق لسانه أو أراد أنها مطلقة من زوج كان قبله لم تطلق، فإن أدعى ذلك دين، وهل يقبل في الحكم؟ على روايتين إلا ان يكون في حال الغضب أو بعد سؤالها الطلاق فلا يقبل','4731','3143','0');
INSERT INTO [t1153] VALUES (3174,'مسالة: ولو قيل له أطلقت امرأتك فقال نعم وأراد الكذب طلقت ولو قيل له ألك امرأة قال لا واراد الكذب لم تطلق','4733','3143','0');
INSERT INTO [t1153] VALUES (3175,'مسألة: وإن لطم امرأته أو أطعمها أو أسقاها وقال هذا طلاقك طلقت. إلا أن ينوي أن هذا سبب طلاقك أو نحو ذلك، لأن هذا اللفظ كناية في الطلاق إذا نواه به وقع ولا يقع من غير نية أو دلالة حال لأنه أضاف إليها الطلاق فوقع بها كما لو قال أنت طالق','4735','3143','0');
INSERT INTO [t1153] VALUES (3176,'مسالة: وإن قال أنت طالق لا شيء أو ليس بشيء أو لا يلزمك طلقت','4736','3143','0');
INSERT INTO [t1153] VALUES (3177,'مسألة: وإن قال أنت طالق أولا؟ أو طالق واحدة أولا؟ لم تطلق','4736','3143','0');
INSERT INTO [t1153] VALUES (3178,'مسألة: وإن كتب طلاق امرأته ونوى الطلاق وقع وإن نوى تجويد خطه أو غم أهله لم يقع وهل تقبل دعواه في الحكم؟ على روايتين','4737','3143','0');
INSERT INTO [t1153] VALUES (3179,'مسالة: وإن لم ينو شيئا','4738','3143','0');
INSERT INTO [t1153] VALUES (3180,'مسألة: وإن كتبه بشيء لا يبين مثل بأصبعه على وسادة أو في الهواء فظاهر كلام أحمد أنه لا يقع','4738','3143','0');
INSERT INTO [t1153] VALUES (3181,'مسألة: وصريح الطلاق في لسان العجم بهشتم','4739','3143','0');
INSERT INTO [t1153] VALUES (3182,'مسألة: والخفية نحو اخرجي واذهبي وذوقي وتجرعي وخليتك وأنت مخلاة وأنت واحدة ولست لي بامرأة واعتدي واستبرئي وما أشبه، واختاري ووهبتك لأهلك فهذه ثلاث إن نوى ثلاثا واثنتان إن نواهما وواحدة إن نواها أو طلق','4743','3143','0');
INSERT INTO [t1153] VALUES (3183,'مسألة: واختلف في قوله الحقي بأهلك، وحبلك عالى غاربك وتزوجي من شئت وحللت للأزواج ولا سبيل لي عليك، ولا سلطان لي عليك وأنت علي حرام وأنت علي حرج هل هي ظاهرة أو خفية؟ وغطي شعرك وقد أعتقتك فهذه عن أحمد فيها روايتان','4743','3143','0');
INSERT INTO [t1153] VALUES (3184,'مسالة: ومن شرط وقوع الطلاق بها أن ينوي بها الطلاق','4746','3143','0');
INSERT INTO [t1153] VALUES (3185,'مسألة: إلا أن يأتي بها في حال الخصومة والغضب فعلى روايتين','4747','3143','0');
INSERT INTO [t1153] VALUES (3186,'مسألة: وإن جاء جوابا لسؤالها الطلاق فقال أصحابنا يقع بها الطلاق','4749','3143','0');
INSERT INTO [t1153] VALUES (3187,'مسألة: ومتى نوى بالكناية الطلاق وقع بالظاهرة ثلاث وإن نوى واحدة','4751','3143','0');
INSERT INTO [t1153] VALUES (3188,'مسألة: وأما ما لا يدل على الطلاق نحو كلي واشربي واقعدي واقربي وبارك الله عليك وأنت مليحة أو قبيحة وقومي وأطعميني واسقيني وغفر الله لك وما أحسنك وأشباه ذلك فليس بكناية ولا تطلق به وإن نوى','4752','3143','0');
INSERT INTO [t1153] VALUES (3189,'مسالة: وكذلك قوله أنا طالق لأن الزوج ليس محلا للطلاق، وإن قال: أنا منك طالق لم تطلق زوجته','4753','3143','0');
INSERT INTO [t1153] VALUES (3190,'مسألة: وإن قال أنا منك بائن أو حرام فهل هو كناية أولا؟ على وجهين','4754','3143','0');
INSERT INTO [t1153] VALUES (3191,'مسألة: وإن قالت أنت علي كظهر أمي، تنوي به الطلاق لم يقع وكان ظهارا','4754','3143','0');
INSERT INTO [t1153] VALUES (3192,'مسألة: وإن قال أنت علي حرام أو ما أحل الله علي حرام ففيه ثلاث روايات إحداهن: أنه ظهار وإن نوى الطلاق اختاره الخرقي والثانية: كناية ظاهرة والثالثة: هو يمين','4755','3143','0');
INSERT INTO [t1153] VALUES (3193,'مسألة: وإن قال ما أحل الله علي حرام أعني به الطلاق فقال أحمد تطلق امرأته ثلاثا وإن قال أعني به طلاقا طلقت واحدة','4757','3143','0');
INSERT INTO [t1153] VALUES (3194,'مسألة: وإن قال أنت علي كالميتة والدم وقع ما نواه من الطلاق والظهار واليمين، وإن لم ينو شيئا فهل يكون ظهارا أو يمينا؟ على وجهين','4759','3143','0');
INSERT INTO [t1153] VALUES (3195,'مسألة: وإن قال حلفت بالطلاق وكذب لزمه إقراره في الحكم ولا يلزمه فيما بينه وبين الله تعالى، وإن قال حلفت بالطلاق أو علي يمين بالطلاق ولم يكن حلف لم يلزمه شيء فيما بينه وبين الله تعالى ولزمه ما أقر به في الحكم','4760','3143','0');
INSERT INTO [t1153] VALUES (3196,'مسألة: وإن قال: اختاري نفسك لم يكن لها أن تطلق أكثر من واحدة إلا أن يجعل إليها أكثر من ذلك وليس لها أن تطلق إلا ما دامت في المجلس ولم يتشاغلا بما يقطعه','4763','3143','0');
INSERT INTO [t1153] VALUES (3197,'مسألة: وليس لها أن تطلق إلا ما داما في المجلس ولم يتشاغلا بما يقطعه','4765','3143','0');
INSERT INTO [t1153] VALUES (3198,'مسألة: فإن جعل لها الخيار اليوم كله أو جعل أمرها بيدها فردته أو رجع فيه أو وضئها بطل خيارها هذا المذهب','4766','3143','0');
INSERT INTO [t1153] VALUES (3199,'مسالة: ولفظة الأمر والخيار كناية في حق الزوج تفتقر إلى نيته','4769','3143','0');
INSERT INTO [t1153] VALUES (3200,'مسألة: فإن اختلفا في نيتها فقال: لم تنوي الطلاق باختيارك نفسك فقالت: قد نويت فالقول قولها','4770','3143','0');
INSERT INTO [t1153] VALUES (3201,'مسألة: وإن قال طلقي نفسك فقالت اخترت نفسي ونوت الطلاق وقع','4771','3143','0');
INSERT INTO [t1153] VALUES (3202,'مسالة: وليس لها أن تطلق أكثر من واحدة إلا أن يجعل إليها أكثر منها','4772','3143','0');
INSERT INTO [t1153] VALUES (3203,'مسألة: وإذا قال: وهبتك لأهلك فإن قبلوها فواحدة وإن ردوها فلا شيء، وعنه إن قبلوها فثلاث وإن ردوها فواحدة وكذلك إذا قال وهبتك لنفسك','4772','3143','0');
INSERT INTO [t1153] VALUES (3204,'مسألة: فإن قالت: اخترت نفسي فهي واحدة رجعية','4775','3143','0');
INSERT INTO [t1153] VALUES (3205,'باب ما يختلف به عدد الطلاق','4776','3143','0');
INSERT INTO [t1153] VALUES (3206,'مسألة: فإذا قال: أنت الطلاق أو الطلاق لي لازم ونوى الثلاث طلقت ثلاثا','4778','3143','0');
INSERT INTO [t1153] VALUES (3207,'مسألة: فإن لم ينو شيئا ففيه روايتان','4779','3143','0');
INSERT INTO [t1153] VALUES (3208,'مسألة: وإن قال: أنت طالق ونوى ثلاثا ففيه روايتان','4780','3143','0');
INSERT INTO [t1153] VALUES (3209,'مسألة: وإن قال: أنت طالق واحدة ونوى ثلاثا لم يقع إلا واحدة','4781','3143','0');
INSERT INTO [t1153] VALUES (3210,'مسألة: وإن قال: أنت طالق هكذا وأشار بأصابعه الثلاث طلقت ثلاثا','4781','3143','0');
INSERT INTO [t1153] VALUES (3211,'مسألة: وإن قال أنت طالق كل الطلاق أو أكثره أو جميعه أو منتهاه أو طالق كألف أو بعدد الحصا أو القطر أو الرمل أو الريح أو التراب طلقت ثلاثا وإن نوى واحدة','4782','3143','0');
INSERT INTO [t1153] VALUES (3212,'مسألة: وإن قال: أشد الطلاق أو أغلظه أو أطوله أو أعرضه أو ملء الدنيا ونوى الثلاث وقع الثلاث وإن لم ينو شيئا أو نوى واحدة فهي واحدة','4783','3143','0');
INSERT INTO [t1153] VALUES (3213,'مسألة: وإن قال: أنت طالق من واحدة الى ثلاث وقع طلقتان','4784','3143','0');
INSERT INTO [t1153] VALUES (3214,'مسألة: وإذا قال: أنت طالق طلقة في اثنتين ونوى طلقة مع طلقتين وقعت الثلاث','4785','3143','0');
INSERT INTO [t1153] VALUES (3215,'مسألة: وإن قال: ثلاثة أنصاف طلقة طلقت طلقتين','4788','3143','0');
INSERT INTO [t1153] VALUES (3216,'مسألة: وإن قال: ثلاثة أنصاف طلقتين طلقت ثلاثا ويحتمل أن تطلق طلقتين','4788','3143','0');
INSERT INTO [t1153] VALUES (3217,'مسألة: وإن قال: نصفي طلقتين وقعت طلقتان','4788','3143','0');
INSERT INTO [t1153] VALUES (3218,'مسألة: وإن قال: نصف طلقة ثلث طلقة سدس طلقة أو نصف وثلث وسدس طلقة طلقت طلقة','4789','3143','0');
INSERT INTO [t1153] VALUES (3219,'مسألة: وإن قال: نصف طلقة وثلث طلقة طلقت ثلاثا','4789','3143','0');
INSERT INTO [t1153] VALUES (3220,'-مسألة: وإذا قال لأربع نسوة أوقعت بينكن طلقة أو اثنين أو ثلاثا أو أربعا وقع بكل واحدة طلقة','4790','3143','0');
INSERT INTO [t1153] VALUES (3221,'مسألة: وإن قال أوقعت بينكن خمسا وقع بكل واحدة طلقتان','4791','3143','0');
INSERT INTO [t1153] VALUES (3222,'مسألة: وإن قال: شعرك أو ظفرك أو سنك طالق لم تطلق','4793','3143','0');
INSERT INTO [t1153] VALUES (3223,'مسألة: وإن أضافه إلى الريق والحمل والدمع والعرق لم تطلق','4793','3143','0');
INSERT INTO [t1153] VALUES (3224,'مسألة: وإن قال: روحك طالق طلقت','4793','3143','0');
INSERT INTO [t1153] VALUES (3225,'مسألة: إذا قال للمدخول بها أنت طالق أنت طالق طلقت طلقتين إلا أن ينوي بالثانية التأكيد أو افهامها','4794','3143','0');
INSERT INTO [t1153] VALUES (3226,'مسألة: وإن قال: أنت طالق فطالق أو ثم طالق أو طالق طلقة بل طلقتين أو بل طلقة أو طالق طلقة بعدها طلقة أو قبل طلقة طلقت طلقتين إن كانت مدخولا بها','4795','3143','0');
INSERT INTO [t1153] VALUES (3227,'مسألة: وإن قال: أنت طالق طلقة قبلها طلقة فكذلك عند القاضي','4796','3143','0');
INSERT INTO [t1153] VALUES (3228,'مسألة: وإن قال: أنت طالق طلقة معها طلقة أو مع طلقة أو طالق وطالق طلقت طلقتين','4797','3143','0');
INSERT INTO [t1153] VALUES (3229,'مسألة: والمعلق كالمنجز في حكم المدخول بها وغيرها','4800','3143','0');
INSERT INTO [t1153] VALUES (3230,'مسألة: وإن قال إن دخلت الدار فأنت طالق طلقة معها طلقة فدخلت طلقت طلقتين','4801','3143','0');
INSERT INTO [t1153] VALUES (3231,'مسألة: وإن قال لغير مدخول بها: أنت طالق ثم طالق ثم طالق إن دخلت الدار وإن دخلت الدار فانت طالق فطالق فطلق فدخلت طلقت واحدة فبانت بها ولم تطلق غيرها','4801','3143','0');
INSERT INTO [t1153] VALUES (3232,'باب الاستثناء في الطلاق','4802','3143','0');
INSERT INTO [t1153] VALUES (3233,'مسألة: وإن قال: أنت طالق ثلاثا إلا ثلاثا أو ثلاثا إلا اثنتين أو خمسا إلا ثلاثا أو ثلاثا إلا ربع طلقة طلقت ثلاثا','4804','3143','0');
INSERT INTO [t1153] VALUES (3234,'مسألة: وإن قال: أنت طالق اثنتين إلا واحدة فعلى وجهين ذكرناهما، وذلك مبني على صحة إستثناء النصف','4804','3143','0');
INSERT INTO [t1153] VALUES (3235,'مسألة: وإن قال: أنت طالق ثلاثا إلا اثنتين إلا واحدة، فهل تطلق ثلاثا أو اثنتين؟ على وجهين','4805','3143','0');
INSERT INTO [t1153] VALUES (3236,'مسألة: وإن قال: أنت طالق ثلاثا إلا ثلاثا إلا واحدة لم يصح ووع ثلاث','4805','3143','0');
INSERT INTO [t1153] VALUES (3237,'مسألة: وإن قال: أنت طالق وطالق إلا واحدة أو طلقتين وواحدة إلا واحدة أو طلقتين ونصفا إلى واحدة طلقت ثلاثا ويحتمل أن يقع طلقتان','4806','3143','0');
INSERT INTO [t1153] VALUES (3238,'مسألة: وإن قال: أنت طالق واحدة، واثنتين إلا واحدة','4807','3143','0');
INSERT INTO [t1153] VALUES (3239,'مسألة: وإن قال: أنت طالق ثلاثا واستثنى بقلبه إلا واحدة وقعت الثلاث وإن قال نسائي طوالق واستثنى واحدة بقلبه لم تطلق','4808','3143','0');
INSERT INTO [t1153] VALUES (3240,'باب طلاق في الماضي والمستقبل','4813','3143','0');
INSERT INTO [t1153] VALUES (3241,'مسألة: وإن لم ينو لم يقع في ظاهر كلامه','4813','3143','0');
INSERT INTO [t1153] VALUES (3242,'مسألة: وحكي عن أبي بكر أنه يقع إذا قال قبل أن أتزوجك ولا يقع إذا قال أنت طالق أمس','4813','3143','0');
INSERT INTO [t1153] VALUES (3243,'مسألة: وإذا قال: أردت ان زوجا قبلي طلقها أو طلقتها أنا في نكاح قبل هذا قبل منه إذا احتمل الصدق في ظاهر كلام أحمد','4814','3143','0');
INSERT INTO [t1153] VALUES (3244,'مسألة: فإن مات أو جن أو خرس قبل العلم بمراده فهل تطلق؟ على وجهين','4814','3143','0');
INSERT INTO [t1153] VALUES (3245,'مسألة: وإن قال: أنت طالق قبل قدوم زيد بشهر فقدم قبل مضي شهر لم تطلق بغير خلاف من أصحابنا','4814','3143','0');
INSERT INTO [t1153] VALUES (3246,'مسألة: وإن قدم بعد شهر وجزء يقع الطلاق فيه تبينا وقوعه فيه لأنه ايقاع للطلاق بعد عقده','4815','3143','0');
INSERT INTO [t1153] VALUES (3247,'مسألة: وإن خالعها بعد اليمين بيوم وكان الطلاق بائنا ثم قدم بعد الشهر بيومين صح الخلع وبطل الطلاق','4815','3143','0');
INSERT INTO [t1153] VALUES (3248,'مسألة: وإن قال: أنت طالق قبل موتي طلقت في الحال','4816','3143','0');
INSERT INTO [t1153] VALUES (3249,'مسألة: وإن قال: أنت طالق بعد موتي أو مع موتي لم تطلق نص عليه أحمد','4816','3143','0');
INSERT INTO [t1153] VALUES (3250,'مسألة: وإن تزوج أمة أبيه ثم قال: إذا مات أبي أو اشتريتك فأنت طالق فمات أبوه أو اشتراها لم يقع الطلاق','4817','3143','0');
INSERT INTO [t1153] VALUES (3251,'مسألة: وإن قال: أنت طالق إن شربت ماء الكوز ولا ماء فيه وإن صعدت السماء أو إن شاء الميت أو البهيمة لم تطلق في أحد الوجهين وتطلق في الأخر','4819','3143','0');
INSERT INTO [t1153] VALUES (3252,'مسألة: وإن قال أنت طالق اليوم إذا جاء غدا فعلى وجهين','4820','3143','0');
INSERT INTO [t1153] VALUES (3253,'مسألة: إذا قال أنت طالق غدا أو يوم السبت في رجب طلقت بأول ذلك','4821','3143','0');
INSERT INTO [t1153] VALUES (3254,'مسألة: ولو قال: أنت طالق اليوم أو في هذا الشهر فكذلك لما ذكرنا وإن قال أردت في آخره أو أوسطه أو يوم كذا من الشهر أو في النهار دون الليل قبل فيما بينه وبين الله تعالى، وهل يقبل في الحكم؟ يخرج على روايتين','4822','3143','0');
INSERT INTO [t1153] VALUES (3255,'مسألة: وإن قال: أنت طالق اليوم وغدا وبعد غد أو في اليوم وفي غد وفي بعده فهل تطلق ثلاثا أو واحدة؟ على وجهين','4823','3143','0');
INSERT INTO [t1153] VALUES (3256,'مسألة: وإن قال: أنت طالق اليوم إن لم أطلقك اليوم طلقت في آخر جزء منه إذا بقي من اليوم ما لا يتسع لتطليقها فيه','4823','3143','0');
INSERT INTO [t1153] VALUES (3257,'مسألة: وإن قال: أنت طالق يوم يقدم زيد فماتت غدوة وقدم بعد موتها فهل يقع بها الطلاق؟ على وجهين','4825','3143','0');
INSERT INTO [t1153] VALUES (3258,'مسألة: وإن قال: أنت طالق إلى شهر طلقت عند انقضائه','4826','3143','0');
INSERT INTO [t1153] VALUES (3259,'مسألة: وإن أراد نصف طلقة اليوم ونصفها غدا فتطلق اليوم واحدة وغدا الاخرى لأن النصف يكمل فيصير طلقة تامة وإن قال أردت نصف طلقة اليوم وباقيها غدا احتمل وجهين','4826','3143','0');
INSERT INTO [t1153] VALUES (3260,'مسألة: وإن قال: أنت طالق في غد إذا قدم زيد فماتت قبل قدومه لم تطلق','4826','3143','0');
INSERT INTO [t1153] VALUES (3261,'مسألة: وإن قال: أنت طالق اليوم غدا طلقت اليوم واحدة لأن من طلقت اليوم فهي طالق غد','4826','3143','0');
INSERT INTO [t1153] VALUES (3262,'مسالة: فإن أراد طالق اليوم وطالق غدا فتطلق طلقتين في اليومين فإن قال: أردت أنها تطلق في أحد اليومين طلقت اليوم ولم تطلق غدا لأنه جعل الزمان كله ظرفا لوقوع الطلاق فوقع في أوله','4826','3143','0');
INSERT INTO [t1153] VALUES (3263,'مسألة: وإن قال: أنت طالق في آخر الشهر أو أول أخره طلقت في أول جزء من آخر يوم منه لأنه آخره. وإن قال: في آخر أوله طلقت في آخر أول يوم منه لأنه أوله','4827','3143','0');
INSERT INTO [t1153] VALUES (3264,'مسألة: وإن قال: إذا مضت سنة فأنت طالق طلقت إذا مضى اثنا عشر شهرا بالاهلة ويكمل الشهر الذي حلف إلى تمام اثني عشر شهرا بالأهلة','4828','3143','0');
INSERT INTO [t1153] VALUES (3265,'مسألة: وإن قال: إذا مضت السنة فأنت: طالق طلقت بانسلاخ ذي الحجة','4829','3143','0');
INSERT INTO [t1153] VALUES (3266,'مسألة: وإذا قال: أنت طالق في كل سنة طلقة فهذه صفة صحيحة','4829','3143','0');
INSERT INTO [t1153] VALUES (3267,'مسألة: وإذا قال: أنت طالق يوم يقدم زيد فقدم ليلا لم تطلق إلا أن يريد باليوم الوقت فتطلق وقت قدومه لأن الوقت يسمى يوما قال الله تعالى: "ومن يولهم يومئذ دبره"','4830','3143','0');
INSERT INTO [t1153] VALUES (3268,'مسألة: وإن قال: أردت بالسنة اثني عشر شهرا قبل لأنها سنة حقيقة، وهل يقبل في الحكم؟ يخرج على روايتين','4830','3143','0');
INSERT INTO [t1153] VALUES (3269,'مسألة: وإن قدم ميتا أو مكرها لم تطلق','4831','3143','0');
INSERT INTO [t1153] VALUES (3270,'باب تعليق الطلاق بالشروط','4834','3143','0');
INSERT INTO [t1153] VALUES (3271,'مسألة: وإن قال لأجنبية: إن قمت فانت طالق فتزوجها ثم قامت لم تطلق','4835','3143','0');
INSERT INTO [t1153] VALUES (3272,'مسألة: وإن علق الزوج الطلاق بشرط لم تطلق قبل وجوده','4836','3143','0');
INSERT INTO [t1153] VALUES (3273,'مسألة: وإن قال سبق لساني بالشرط ولم أرده وقع في الحال','4836','3143','0');
INSERT INTO [t1153] VALUES (3274,'مسألة: فإن قال أنت طالق ثم قال أردت ان قمت دين','4836','3143','0');
INSERT INTO [t1153] VALUES (3275,'مسألة: وإن قال عجلت ما علقته لم يتعجل','4837','3143','0');
INSERT INTO [t1153] VALUES (3276,'مسألة: وليس فيها ما يقتضي التكرار الا كلما','4837','3143','0');
INSERT INTO [t1153] VALUES (3277,'مسألة: وكلها على التراخي إذا تجردت عن لم فإذا اتصلت بها صارت على الفور إلا إن وفي إذا وجهان','4838','3143','0');
INSERT INTO [t1153] VALUES (3278,'مسألة: وفي إذا وجهان','4838','3143','0');
INSERT INTO [t1153] VALUES (3279,'مسألة: وإن اتصلت بها أي بلم صارت على الفور إلا إن فإنها على التراخي لأنها لا تقتضي وقتا إلا ضرورة أن الفعل لا يقع إلا في وقت فهي مطلقة في الزمان كله','4838','3143','0');
INSERT INTO [t1153] VALUES (3280,'مسألة: فإذا قال إذا أكلت رمانة فأنت طالق وكلما أكلت نصف رمانة فأنت طالق فأكلت رمانة طلقت ثلاثا لوجود صفة النصف مرتين والجميع مرة','4840','3143','0');
INSERT INTO [t1153] VALUES (3281,'مسألة: ولو علق طلاقها على صفات ثلاث فاجتمعن في عين واحدة نحو أن يقول أن رأيت رجلا فأنت طالق وإن رأيت أسود فأنت طالق، وإن رأيت فقيها فأنت طالق فرأت رجلا أسود فقيها طلقت ثلاثا','4840','3143','0');
INSERT INTO [t1153] VALUES (3282,'مسالة: وإن قال إن لم أطلقك فأنت طالق لم تطلق إلا في آخر جزء من حياة أحدهما إلا أن يكون له نية','4841','3143','0');
INSERT INTO [t1153] VALUES (3283,'مسألة: وإن قال إذا لم أطلقك فأنت طالق ولم يطلقها فهل تطلق في الحال؟ على وجهين','4844','3143','0');
INSERT INTO [t1153] VALUES (3284,'مسألة: وإن قال كلما لم أطلقك فأنت طالق فمضى زمن يمكن فيه طلاقها ثلاثا ولم يطلقها طلقت ثلاثا','4844','3143','0');
INSERT INTO [t1153] VALUES (3285,'مسألة: ولو قال العامي: إن دخلت الدار فأنت طالق بفتح الهمزة فهو شرط','4844','3143','0');
INSERT INTO [t1153] VALUES (3286,'مسألة: وإن قال إن قمت وأنت طالق طلقت في الحال لأن الواو ليست جوابا للشرط فإن قال أردت بها الجزاء أو أردت أني أجعل قيامها وطلاقها شرطين لشيء ثم أمسكت دين لأن ما قاله محتمل وهل يقبل في الحكم؟ على روايتين','4845','3143','0');
INSERT INTO [t1153] VALUES (3287,'مسألة: وإن قال إن قمت فقعدت فأنت طالق أو إن قمت ثم قعدت لم تطلق حتى تقوم ثم تقعد','4848','3143','0');
INSERT INTO [t1153] VALUES (3288,'مسألة: وإن قال إن قمت وقعدت فأنت طالق طلقت بوجودهما كيفما كان','4848','3143','0');
INSERT INTO [t1153] VALUES (3289,'مسألة: وإن قال إن قمت أو قعدت فأنت طالق طلقت بوجود أحدهما','4850','3143','0');
INSERT INTO [t1153] VALUES (3290,'مسألة: وإذا قال إذا حضت نصف حيضة فأنت طالق طلقت إذا ذهب نصف الحيضة','4851','3143','0');
INSERT INTO [t1153] VALUES (3291,'مسألة: وإن قال إذا طهرت فأنت طالق وكانت طاهرا لم تطلق حتى تحيض ثم تطهر','4851','3143','0');
INSERT INTO [t1153] VALUES (3292,'مسألة: وإذا قالت قد حضت وكذبها قبل قولها في نفسها في إحدى الروايتين بغير يمين لأنها أمينة على نفسها','4852','3143','0');
INSERT INTO [t1153] VALUES (3293,'مسألة: ولو قال قد حضت فأنكرته طلقت بإقراره','4853','3143','0');
INSERT INTO [t1153] VALUES (3294,'مسألة: وإن قال كلما حاضت إحداكن فضرائرها طوالق','4854','3143','0');
INSERT INTO [t1153] VALUES (3295,'مسألة: فإن قال إن حضت فأنت وضرتك طالقتان فقالت قد حضت وكذبها طلقت وحدها','4854','3143','0');
INSERT INTO [t1153] VALUES (3296,'مسألة: وإن قال لامرأتيه إن حضتما فأنتما طالقتان فقالتا قد حضنا فصدقهما طلقتا','4854','3143','0');
INSERT INTO [t1153] VALUES (3297,'مسألة: وإن قال ذلك لأربع فقد علق طلاق كل واحدة منهن على حيض الأربع فإن قلن قد حضن فصدقهن طلقن','4854','3143','0');
INSERT INTO [t1153] VALUES (3298,'مسألة: ويحرم وطؤها قبل استبرائها في إحدى الروايتين إن كان الطلاق بائنا نص عليه أحمد','4857','3143','0');
INSERT INTO [t1153] VALUES (3299,'مسألة: وإن قال إن لم تكوني حاملا فأنت طالق فهي بالعكس','4857','3143','0');
INSERT INTO [t1153] VALUES (3300,'مسألة: وإذا قال إن كنت حاملا بذكر فأنت طالق واحدة وإن كنت حاملا بأنثى فأنت طالق اثنتين فولدت ذكرا وانثى طلقت ثلاثا لوجود الصفة','4858','3143','0');
INSERT INTO [t1153] VALUES (3301,'مسألة: فإن أشكل كيفية وضعهما وقعت واحدة بيقين ولغا ما زاد فلا تلزمه الثانية لأنه مشكوك فيه والورع أن يلتزمهما','4859','3143','0');
INSERT INTO [t1153] VALUES (3302,'مسألة: ولا فرق بين أن تلده حيا أو ميتا','4860','3143','0');
INSERT INTO [t1153] VALUES (3303,'مسألة: إذا قال إذا طلقتك فأنت طالق ثم قال إن قمت فأنت طالق فقامت طلقت بقيامها ثم طلقت بالصفة أخرى لأنه قد طلقها بعد عقد الصفة لأن الصفة تطليقة لها وتعليقه لطلاقها بقيامها إذا اتصل به القيام تطليق لها','4862','3143','0');
INSERT INTO [t1153] VALUES (3304,'مسألة: ولو قال أولا إن قمت فأنت طالق ثم قال إن طلقتك فأنت طالق فقامت طلقت بالقيام واحدة ولم تطلق بتعليق الطلاق لأنه لم يطلقها بعد ذلك','4862','3143','0');
INSERT INTO [t1153] VALUES (3305,'مسألة: ولو قال إن قمت فأنت طالق ثم قال إن وقع عليك طلاقي فأنت طالق فقامت طلقت بالقيام ثم تطلق الثانية بوقوع الطلاق عليها إن كانت مدخولا بها لأن الطلاق الواقع بها طلاقه فقد وجدت الصفة','4863','3143','0');
INSERT INTO [t1153] VALUES (3306,'مسألة: وإن قال كلما طلقتك فأنت طالق فهذا حرف يقتضيالتكرار','4863','3143','0');
INSERT INTO [t1153] VALUES (3307,'مسألة: وإن قال كلما وقع عليك طلاقي فأنت طالق ثم وقع عليها طلاقه بمباشرة أو سبب أو بصفة عقدها بعد ذلك أو قبله طلقت ثلاثا لأن الثانية طلقة واقعة عليها فتقع بها الثالثة','4864','3143','0');
INSERT INTO [t1153] VALUES (3308,'مسألة: وإن قال كلما وقع عليك طلاقي أو إن وقع عليك طلاقي فأنت طالق قبله ثلاثا ثم قال أنت طالق فلا نص فيها','4865','3143','0');
INSERT INTO [t1153] VALUES (3309,'مسألة: وإن قال كلما طلقت واحدة منكن فعبد من عبيدي حر، وكلما طلقت اثنتين فعبدان حران وكلما طلقت ثلاثا فثلاثة أحرار، وكلما طلقت أربعا فأربعة أحرار ثم طلق الأربع مجتمعات أو متفرقات عتق خمسة عشر عبدا','4871','3143','0');
INSERT INTO [t1153] VALUES (3310,'مسألة: وإن قال لنسائه الأربع أيتكن وقع عليها طلاقي فصواحبها طوالق ثم وقع على إحداهن طلاقه طلق الجميع ثلاثا','4871','3143','0');
INSERT INTO [t1153] VALUES (3311,'مسألة: إذا قال لامرأته إذا أتاك طلاقي فأنت طالق ثم كتب إليها إذا أتاك كتابي فأنت طالق فأتاها الكتاب طلقت طلقتين','4874','3143','0');
INSERT INTO [t1153] VALUES (3312,'فصل في تعليقه بالحلف','4875','3143','0');
INSERT INTO [t1153] VALUES (3313,'مسألة: فإذا قال إن حلفت بطلاقك فأنت طالق','4875','3143','0');
INSERT INTO [t1153] VALUES (3314,'مسألة: وإن قال ان حلفت فأنت طالق وأعاده مرة أخرى طلقت واحدة، ولأن إعادته حلف وإن أعاده ثلاثا طلقت ثلاثا','4876','3143','0');
INSERT INTO [t1153] VALUES (3315,'مسألة: وإن قال ان كلمتك فأنت طالق وأعاده ثلاثا طلقت ثلاثا','4876','3143','0');
INSERT INTO [t1153] VALUES (3316,'مسألة: وإن قال لامرأتيه كلما حلفت بطلاقكما فأنتما طالقتان ثم أعاد ذلك ثلاثا طلقت كل واحدة منهما ثلاثا','4876','3143','0');
INSERT INTO [t1153] VALUES (3317,'مسألة: وإن قال لمدخول بهما كلما حلفت بطلاق واحدة منكما فأنتما طالقتان وأعاد ثانيا طلقت كل واحدة طلقتين','4878','3143','0');
INSERT INTO [t1153] VALUES (3318,'مسألة: وإن قال لإحداهما إذا حلفت بطلاق ضرتك فأنت طالق ثم قال ذلك للأخرى طلقت الأولى','4878','3143','0');
INSERT INTO [t1153] VALUES (3319,'مسألة: وإن قال كلما حلفت بطلاق واحدة منكما فهي طالق، أو فضرتها طالق وأعاده طلقت كل واحدة منهما طلقة','4879','3143','0');
INSERT INTO [t1153] VALUES (3320,'مسألة: وإن قال ان بدأتك بالكلام فأنت طالق','4881','3143','0');
INSERT INTO [t1153] VALUES (3321,'مسألة: وإذا قال ان كلمت فلانا فأنت طالق فكلمته فلم يسمع لتشاغله أو غفلته أو كاتبته أو راسلته حنث','4881','3143','0');
INSERT INTO [t1153] VALUES (3322,'مسألة: وإن أشارت إليه احتمل وجهين','4882','3143','0');
INSERT INTO [t1153] VALUES (3323,'مسألة: وإن كلمته سكران أو أصم بحيث يعلم أنها تكلمه أو مجنونا يسمع كلامها حنث','4882','3143','0');
INSERT INTO [t1153] VALUES (3324,'مسألة: وإن كلمته ميتا أو غائبا أو مغمى عليه أو نائما لم يحنث','4883','3143','0');
INSERT INTO [t1153] VALUES (3325,'مسألة: فإن قال لامرأتيه ان كلمتما هذين الرجلين فأنتما طالقتان فكلمت كل واحدة منهما واحدا طلقتا ويحتمل ان لا يحنث حتى تكلما جميعا كل واحد منهما','4884','3143','0');
INSERT INTO [t1153] VALUES (3326,'مسألة: فإن قال إن أمرتك فخالفتني فأنت طالق','4885','3143','0');
INSERT INTO [t1153] VALUES (3327,'فصل في تعليقه بالاذن','4886','3143','0');
INSERT INTO [t1153] VALUES (3328,'مسألة: وإن قال إن خرجت إلى غير الحمام بغير اذني فانت طالق فخرجت إلى غير الحمام طلقت سواء عدلت إلى الحمام أو لم تعدل','4887','3143','0');
INSERT INTO [t1153] VALUES (3329,'مسألة: وإن خرجت تريد الحمام ثم عدلت إلى غيره','4887','3143','0');
INSERT INTO [t1153] VALUES (3330,'مسألة: وإن أذن لها من حيث لا تعلم فخرجت طلقت','4887','3143','0');
INSERT INTO [t1153] VALUES (3331,'مسألة: وإن حلف لعامل أن لا يخرج إلا بإذنه فعزل فهل تنحل يمينه؟ على وجهين','4888','3143','0');
INSERT INTO [t1153] VALUES (3332,'فصل في تعليقه بالمشيئة','4889','3143','0');
INSERT INTO [t1153] VALUES (3333,'مسالة: ومتى وجدت المشيئة باللسان وقع الطلاق سوء كان على الفور أو التراخي','4889','3143','0');
INSERT INTO [t1153] VALUES (3334,'مسألة: وإن قال أنت طالق ان شئت فقالت قد شئت ان شئت فقال قد شئت لم تطلق','4890','3143','0');
INSERT INTO [t1153] VALUES (3335,'مسألة: وإن قال أنت طالق إن شئت وشاء أبوك لم تطلق حتى يشاءا','4891','3143','0');
INSERT INTO [t1153] VALUES (3336,'مسألة: وإن قال أنت طالق إن شاء زيد فمات أو جن أو خرس قبل المشيئة لم تطلق','4891','3143','0');
INSERT INTO [t1153] VALUES (3337,'مسألة: وإن قال أنت طالق إلا أن يشاء زيد فمات أو جن أو خرس طلقت في الحال','4892','3143','0');
INSERT INTO [t1153] VALUES (3338,'مسألة: وإن قال أنت طالق واحدة إلا أن يشاء زيد ثلاثا فقال أبو بكر تطلق ثلاثا في أحد الوجهين','4892','3143','0');
INSERT INTO [t1153] VALUES (3339,'مسالة: وإن قال أنت طالق إن شاء الله طلقت وإن قال لأمته أنت حرة إن شاء الله عتقت وحكي عنه أنه يقع العتق دون الطلاق','4893','3143','0');
INSERT INTO [t1153] VALUES (3340,'مسألة: وإن قال أنت طالق إلا أن يشاء الله طلقت','4894','3143','0');
INSERT INTO [t1153] VALUES (3341,'مسألة: وإن قال إن لم يشأ الله أو ما لم يشأ الله فعلى وجهين','4894','3143','0');
INSERT INTO [t1153] VALUES (3342,'مسألة: وإن قال إن دخلت الدار فأنت طالق إن شاء الله فدخلت فهل تطلق؟ على روايتين','4895','3143','0');
INSERT INTO [t1153] VALUES (3343,'مسألة: وإن قال أنت طالق لرضا زيد أو مشيئته طلقت في الحال','4895','3143','0');
INSERT INTO [t1153] VALUES (3344,'مسألة: وإن قال إن كنت تحبين أن يعذبك الله في النار فأنت طالق، أو قال إن كنت تحبينه بقلبك فقالت أنا أحبه','4896','3143','0');
INSERT INTO [t1153] VALUES (3345,'فصل في مسائل متفرقة','4897','3143','0');
INSERT INTO [t1153] VALUES (3346,'مسالة: وإن قال من بشرتني بقدوم أخي فهي طالق فأخبرته امرأتاه طلق الأولى منهما إلا أن تكون الثانية هي الصادقة وحدها فتطلق وحدها','4898','3143','0');
INSERT INTO [t1153] VALUES (3347,'مسألة: وإن قال من أخبرتني بقدومه فهي طالق','4899','3143','0');
INSERT INTO [t1153] VALUES (3348,'مسألة: وإن حلف لا يفعل شيئا ففعله ناسيا حنث في الطلاق والعتاق ولم يحنث في اليمين المكفرة في ظاهر المذهب','4900','3143','0');
INSERT INTO [t1153] VALUES (3349,'مسألة: وإن حلف لا يدخل على إنسان بيتا أو لا يكلمه أو لا يسلم عليه أو لا يفارقه حتى يقضيه حقه فدخل بيتا هو فيه ولم يعلم، أو سلم على قوم هو فيهم ولم يعلم أو قضاه حقه ففارقه فخرج رديئا أو أحاله به ففارقه ظنا منه أنه قد برئ خرج على الروايتين في الن','4901','3143','0');
INSERT INTO [t1153] VALUES (3350,'مسألة: وإن حلف لا يفعل شيئا ففعل بعضه لم يحنث وعنه يحنث إلا أن ينوي جميعه','4902','3143','0');
INSERT INTO [t1153] VALUES (3351,'مسألة: وإن حلف ليفعلن شيئا أو ليدخلن الدار، لم يبرأ إلا بفعل جميعه، والدخول إلى الدار بجملته','4903','3143','0');
INSERT INTO [t1153] VALUES (3352,'مسالة: وإن حلف لا يدخل دارا فأدخلها بعض جسده أو دخل طاق الباب، أو لا يلبس ثوبا من غزلها فلبس ثوبا فيه منه أو لا يشرب ماء هذا الكوز فشرب بعضه خرج على الروايتين فيمن فعل بعض المحلوف عليه','4903','3143','0');
INSERT INTO [t1153] VALUES (3353,'مسألة: وإن حلف لا يشرب ماء هذا النهر فشرب منه حنث وجها واحدا','4903','3143','0');
INSERT INTO [t1153] VALUES (3354,'مسألة: وإن حلف لا يلبس ثوبا اشتراه زيد أو نسجه أو لا يأكل طعاما طبخه فلبس ثوبا نسجه هو وغيره أو اشتراه أو أكل طعاما طبخاه فعلى روايتين','4905','3143','0');
INSERT INTO [t1153] VALUES (3355,'مسالة: وإن حلف لا يأكل طعاما اشتراه زيد فأكل طعاما اشتراه هو وغيره حنث إلا أن يكون أراد أن لا ينفرد أحدهما بالشراء','4905','3143','0');
INSERT INTO [t1153] VALUES (3356,'باب التأويل في الحلف','4906','3143','0');
INSERT INTO [t1153] VALUES (3357,'مسألة: فإذا أكل تمرا فقال لتخبرني بعدد ما أكلت أو لتميزن نوى ما أكلت ولم تعلم فإنها تعد له عددا يعلم أنه قد أتى على عدد ذلك','4909','3143','0');
INSERT INTO [t1153] VALUES (3358,'مسألة: وإن حلف ليقعدن على بارية في بيته ولا يدخله بارية فإنه يدخل قصبا فينسجه فيه فيجلس عليها في البيت فلا يحنث','4909','3143','0');
INSERT INTO [t1153] VALUES (3359,'مسألة: فإن كان الماء واقفا حمل منه مكرها','4910','3143','0');
INSERT INTO [t1153] VALUES (3360,'مسألة: وإن حلف عاملان ههنا وعنى موضعا معينا بر في يمينه','4910','3143','0');
INSERT INTO [t1153] VALUES (3361,'مسألة: ولو سرقت منه امرأته شيئا فحلف بالطلاق لتصدقني أسرقت مني شيئا أم لا وخافت أن تصدقه فإنها تقول سرقت منك ما سرقت منك، ولو استحلفه ظلم هل رأيت فلانا أو لا وكان قد رآه فإنه يعني بما رأيته ما ضربت رئته','4910','3143','0');
INSERT INTO [t1153] VALUES (3362,'مسألة: وإن استحلفه ظالم ما لفلان عندك وديعة وكانت عنده وديعة فإنه يعني بما الذي ويبر في يمينه لأنه صادق','4910','3143','0');
INSERT INTO [t1153] VALUES (3363,'مسألة: وإن حلف لا أقمت عليه ولا نزلت منه ولا صعدت فيه فإنه ينتقل إلى سلم آخر وتنحل يمينه','4910','3143','0');
INSERT INTO [t1153] VALUES (3364,'مسألة: وإن كان على سلم فحلف لا نزلت إليك ولا صعدت إلى هذه ولا أقمت مكاني ساعة','4910','3143','0');
INSERT INTO [t1153] VALUES (3365,'مسألة: وإن حلف ليطبخن قدرا برطل ملح يأكل كل منه فلا يجد طعم الملح فإنه يسلق به بيضا ويأكل منه ولا يحنث','4910','3143','0');
INSERT INTO [t1153] VALUES (3366,'مسألة: وإن حلف لا يأكل بيضا ولا تفاحا وليأكلن مما في هذا الوعاء فوجد فيه بيضا وتفاحا فإنه يعمل من البيض ناطفا ومن التفاح شرابا ويأكل منه ولا يحنث','4910','3143','0');
INSERT INTO [t1153] VALUES (3367,'مسالة: وإن حلف لا أقمت في هذا الماء ولا خرجت منه وكان الماء جاريا لم يحنث','4910','3143','0');
INSERT INTO [t1153] VALUES (3368,'مسألة: ولو حلف على امرأته لا سرقت مني شيئا فخانته في وديعته لم يحنث','4911','3143','0');
INSERT INTO [t1153] VALUES (3369,'باب الشك في الطلاق','4911','3143','0');
INSERT INTO [t1153] VALUES (3370,'مسألة: وإن شك في عدد الطلاق بنى على اليقين لما ذكرنا وقال الخرقي إذا طلق فلم يدر واحدة طلق أم ثلاثا اعتزلها وعليه نفقتها ما دامت في العدة فإن راجعها في العدة لزمته النفقة ولم يطأها حتى يتيقن كم الطلاق؟ لأنه متيقن للتحريم شاك في التحليل','4912','3143','0');
INSERT INTO [t1153] VALUES (3371,'مسألة: وإن قال لامرأتيه إحداكما طالق ينوي واحدة بعينها طلقت وحدها فإن لم ينو خرجت المطلقة بالقرعة','4913','3143','0');
INSERT INTO [t1153] VALUES (3372,'مسألة: فإن قال لنسائه إحداكن طالق غدا طلقت واحدة منهن إذا جاء الغد وأخرجت بالقرعة','4914','3143','0');
INSERT INTO [t1153] VALUES (3373,'مسألة: وإن طلق واحدة وأنسيها فكذلك عند أصحابنا','4915','3143','0');
INSERT INTO [t1153] VALUES (3374,'مسألة: فعلى قول أصحابنا إن تبين أن المطلقة غير التي وقعت عليها القرعة بأن يذكر ذلك تبين أنها كانت محرمة عليه ويكون وقوع الطلاق من حين طلق لا من حين ذكر','4916','3143','0');
INSERT INTO [t1153] VALUES (3375,'مسألة: إلا أن تكون قد تزوجت أو يكون بحكم حاكم لأنها إذا تزوجت فقد تعلق بها حق الزوج الثاني فلا يقبل قوله في فسخ نكاحه والقرعة من جهة الحاكم بالفرقة لا يمكن للزوج رفعها فتقع الفرقة بالزوجين','4916','3143','0');
INSERT INTO [t1153] VALUES (3376,'مسألة: وإن طار طائر فقال إن كان هذا غرابا ففلانة طالق، وإن لم يكن غرابا ففلانة طالق فهي كالمنسية','4921','3143','0');
INSERT INTO [t1153] VALUES (3377,'مسالة: وإن قال إن كان الطائر غرابا ففلانه طالق وإن كان حماما ففلانه طالق لم يحكم بحنثه في واحدة منهما','4921','3143','0');
INSERT INTO [t1153] VALUES (3378,'مسألة: فإن قال أحدهما إن كان غرابا فعبدي حر وقال الآخر إن لم يكن غرابا فعبدي حر فطار ولم يعلما حاله لم يحكم بعتق واحد من العبدين','4922','3143','0');
INSERT INTO [t1153] VALUES (3379,'مسالة: إذا قال لامرأته وأجنبية إحداكما طالق، أو قال لحماته ابنتك طالق، أو قال سلمى طالق واسم امرأته سلمى طلقت امرأته','4923','3143','0');
INSERT INTO [t1153] VALUES (3380,'مسألة: فإن نادى امرأته فأجابته امرأة له أخرى فقال أنت طالق يظنها المناداة طلقت في إحدى الروايتين','4924','3143','0');
INSERT INTO [t1153] VALUES (3381,'مسالة: وإن لقي أجنبية ظنها زوجته فقال فلانة أنت طالق فإذا هي أجنبية طلقت زوجته','4924','3143','0');
INSERT INTO [t1153] VALUES (3382,'كتاب الرجعة','4925','0','0');
INSERT INTO [t1153] VALUES (3383,'كتاب الرجعة','4925','3382','0');
INSERT INTO [t1153] VALUES (3384,'مسألة: إذا طلق الحر امرأته بعد دخوله بها أقل من ثلاث، أو العبد واحدة بغير عوض والأمر يقتضي بينونتها فله رجعتها ما دامت في العدة رضيت أو كرهت لما ذكرنا','4926','3382','0');
INSERT INTO [t1153] VALUES (3385,'مسالة: وألفاظ الرجعة راجعت امرأتي ورجعتها أو ارتجعتها أو رددتها أو أمسكتها','4926','3382','0');
INSERT INTO [t1153] VALUES (3386,'مسالة: فإن قال نكحتها أو تزوجتها فليس هو بصريح فيها وهل تحصل الرجعة به؟ فيه وجهان','4927','3382','0');
INSERT INTO [t1153] VALUES (3387,'مسألة: وهل من شرطها الاشهاد؟ على روايتين','4927','3382','0');
INSERT INTO [t1153] VALUES (3388,'مسالة: ويباح لزوجها وطؤها والخلوة والسفر بها ولها أن تتزين له وتتشرف له','4929','3382','0');
INSERT INTO [t1153] VALUES (3389,'مسألة: والرجعية زوجة يلحقها الطلاق والظهار والإيلاء ولعانه ويرث أحدهما صاحبه إن مات بالإجماع وإن خالعها صح خلعه','4929','3382','0');
INSERT INTO [t1153] VALUES (3390,'مسألة: ولا تحصل بمباشرتها والنظر إلى فرجها والخلوة بها لشهوة','4930','3382','0');
INSERT INTO [t1153] VALUES (3391,'مسألة: ولا يصح تعليق الرجعة على شرط لأنه استباحة فرج مقصود فأشبه النكاح','4931','3382','0');
INSERT INTO [t1153] VALUES (3392,'مسألة: وإن طهرت من الحيضة الثالثة ولم تغتسل فهل له رجعتها؟ على روايتين','4933','3382','0');
INSERT INTO [t1153] VALUES (3393,'مسألة: وإن انقضت عدتها ولم يرتجعها بانت ولا تحل إلا بنكاح جديد لقول الله سبحانه: " وبعولتهن أحق بردهن "','4935','3382','0');
INSERT INTO [t1153] VALUES (3394,'مسألة: وتعود على ما بقي من طلاقها سواء رجعت بعد نكاح زوج غيره أو قبله، وعنه أنها إن رجعت بعد نكاح زوج غيره رجعت بطلاق ثلاث','4935','3382','0');
INSERT INTO [t1153] VALUES (3395,'مسألة: وإن ارتجعها في عدتها وأشهد على رجعتها من حيث لا تعلم فاعتدت ثم تزوجت من أصابها ردت إليه ولا يطؤها حتى تنقضي عدتها في إحدى الروايتين، والأخرى هي زوجة الثاني','4937','3382','0');
INSERT INTO [t1153] VALUES (3396,'مسألة: وإن ادعت المرأة انقضاء عدتها قبل قولها إذا كان ممكنا إلا أن تدعيه بالحيض في شهر فلا يقبل إلا ببينة','4939','3382','0');
INSERT INTO [t1153] VALUES (3397,'مسألة: فإن ادعت انقضاء عدتها بالقروء في أقل من شهر لم يقبل إلا ببينة','4941','3382','0');
INSERT INTO [t1153] VALUES (3398,'مسألة: إذا قالت انقضت عدتي فقال قد كنت راجعتك فالقول قولها','4943','3382','0');
INSERT INTO [t1153] VALUES (3399,'مسألة: وإن سبق فقال ارتجعتك فقالت قد انقضت عدتي قبل رجعتك فأنكرها فالقول قوله','4944','3382','0');
INSERT INTO [t1153] VALUES (3400,'مسألة: وإن تداعيا معا قدم قولها','4944','3382','0');
INSERT INTO [t1153] VALUES (3401,'مسألة: ولو كانت أمة فاشتراها مطلقها لم يحل له وطؤها في قول أكثر أهل العلم ويحتمل أن تحل','4950','3382','0');
INSERT INTO [t1153] VALUES (3402,'مسألة: فإن كان مجبوبا قد بقي من ذكره قدر الحشفة فأولجه أحلها وإلا فلا','4950','3382','0');
INSERT INTO [t1153] VALUES (3403,'مسألة: فإن كانت ذمية فوطئها زوجها الذمي أحلها لمطلقها المسلم','4951','3382','0');
INSERT INTO [t1153] VALUES (3404,'مسألة: وإن وطئها في الدبر أو وطئت بشبهة أو بملك يمين لم تحل','4952','3382','0');
INSERT INTO [t1153] VALUES (3405,'مسألة: وإن وطئها زوجها في حيض أو نفاس أو إحرام أحلها وقال أصحابنا لا يحلها','4953','3382','0');
INSERT INTO [t1153] VALUES (3406,'مسألة: وإن طلق العبد زوجته اثنتين لم تحل له حتى تنكح زوجا غيره سواء عتقا أو بقيا على الرق','4953','3382','0');
INSERT INTO [t1153] VALUES (3407,'مسألة: وإذا غاب عن مطلقته فأتته فذكرت أنها نكحت من أصابها، وانقضت عدتها منه وكان ذلك ممكنا فلها نكاحها إذا غلب على ظنه صدقها وإلا فلا','4955','3382','0');
INSERT INTO [t1153] VALUES (3408,'كتاب الإيلاء','4957','0','0');
INSERT INTO [t1153] VALUES (3409,'كتاب الإيلاء','4957','3408','0');
INSERT INTO [t1153] VALUES (3410,'مسألة: وهو الحلف على ترك الوطء في موضوع الشرع والأصل فيه قول الله تعالى: " للذين يؤلون من نسائهم تربص أربعة أشهر " وكان أبي بن كعب وابن عباس يقرآن يقسمون','4957','3408','0');
INSERT INTO [t1153] VALUES (3411,'مسألة: ويشترط له أربعة شروط أحدها','4957','3408','0');
INSERT INTO [t1153] VALUES (3412,'مسألة: فإن تركه مضرا بها من غير عذر فهل تضرب له مدة الإيلاء ويحكم عليه بحكمه؟ على روايتين','4958','3408','0');
INSERT INTO [t1153] VALUES (3413,'مسألة: وإن حلف على ترك الوطء في الدبر أو دون الفرج لم يكن موليا','4958','3408','0');
INSERT INTO [t1153] VALUES (3414,'مسألة: وإن حلف لا يجامعها إلا جماع سوء يريد جماعا ضعيفا لا يزيد على التقاء الختانين لم يكن موليا','4959','3408','0');
INSERT INTO [t1153] VALUES (3415,'مسألة: وإذا حلف على ترك الوطء بلفظ لا يحتمل غيره كلفظه الصريح وقوله لا أدخلت ذكري في فرجك وللبكر خاصة لا افتضضتك لم يدن فيه','4959','3408','0');
INSERT INTO [t1153] VALUES (3416,'مسألة: فإن حلف بنذر أو عتق أو طلاق لم يصر موليا في الظاهر عنه وعنه يكون موليا','4962','3408','0');
INSERT INTO [t1153] VALUES (3417,'مسألة: وإن قال إن وطئتك فأنت زانية أو فلله علي صوم هذا الشهر لم يكن موليا','4963','3408','0');
INSERT INTO [t1153] VALUES (3418,'مسألة: أو يعلقه على شرط يغلب على الظن أنه لا يوجد في أقل من أربعة أشهر','4966','3408','0');
INSERT INTO [t1153] VALUES (3419,'مسألة: وإن قال والله لا وطئتك حتى تحبلي فهو مؤل','4967','3408','0');
INSERT INTO [t1153] VALUES (3420,'مسألة: وإن حلف على ترك الوطء حتى يقدم زيد أو نحوه مما لا يغلب على الظن عدمه في أربعة أشهر أو لا وطئتك في هذه البلدة لم يكن موليا','4968','3408','0');
INSERT INTO [t1153] VALUES (3421,'مسألة: وإن قال والله لا وطئتك مدة أو ليطولن تركي لجماعك لم يكن موليا حتى ينوي أكثر من أربعة أشهر','4968','3408','0');
INSERT INTO [t1153] VALUES (3422,'مسألة: وإن قال إن وطئتك فوالله لا وطئتك وإن دخلت الدار فوالله لا وطئتك لم يكن موليا في الحال لأنه لا يلزمه بالوطء حق لكن إن وطئها أو دخلت الدار صار موليا','4970','3408','0');
INSERT INTO [t1153] VALUES (3423,'مسألة: وإن قال والله لا وطئتك في السنة إلا مرة لم يصر موليا في الحال','4970','3408','0');
INSERT INTO [t1153] VALUES (3424,'مسالة: وإن قال والله لا وطئتك في السنة إلا يوما فكذلك في أحد الوجهين','4971','3408','0');
INSERT INTO [t1153] VALUES (3425,'مسالة: فإن قال والله لا وطئتك أربعة أشهر فإذا مضت فوالله لا وطئتك أربعة أشهر أو فإذا مضت فلا وطئتك شهرين أو لا وطئتك فإذا مضت فوالله لا وطئتك أربعة أشهر','4973','3408','0');
INSERT INTO [t1153] VALUES (3426,'مسألة: وإن قال والله لا وطئتك إن شئت فشاءت صار موليا','4973','3408','0');
INSERT INTO [t1153] VALUES (3427,'مسألة: وإن قال إلا أن تشائي أو إلا باختيارك، أو إلا أن تختاري لم يصر موليا، وصار كقوله إلا برضاك أو حتى تشائي','4974','3408','0');
INSERT INTO [t1153] VALUES (3428,'مسألة: وإن قال لنسائه والله لا وطئت واحدة منكن صار موليا منهن إلا أن يريد واحدة بعينها وإن أراد واحدة مبهمة فقال أبو بكر تخرج بالقرعة','4975','3408','0');
INSERT INTO [t1153] VALUES (3429,'مسألة: وإن قال والله لا وطئت كل واحدة منكن كان موليا من جميعهن في الحال، ولا يقبل قوله نويت واحدة منهن معينة ولا مبهمة لأن لفظة كل أزالت احتمال الخصوص وتنحل يمينه بوطء واحدة كالمسألة التي قبلها','4976','3408','0');
INSERT INTO [t1153] VALUES (3430,'مسألة: وإن قال والله لا أطؤكن فهي كالتي قبلها في أحد الوجهين','4977','3408','0');
INSERT INTO [t1153] VALUES (3431,'مسألة: وإن آلى من واحدة وقال للاخرى شركتك معها لم يصر موليا من الثانية','4981','3408','0');
INSERT INTO [t1153] VALUES (3432,'مسألة: ويصح إيلاء الذمي ويلزمه ما يلزم المسلم إذا تقاضوا إلينا','4985','3408','0');
INSERT INTO [t1153] VALUES (3433,'مسألة: ولا يصح إيلاء الصبي والمجنون','4987','3408','0');
INSERT INTO [t1153] VALUES (3434,'مسألة: وفي إيلاء السكران وجهان','4987','3408','0');
INSERT INTO [t1153] VALUES (3435,'مسألة: ومدة الإيلاء في الأحرار والرقيق سواء، وعنه أنها في العبد على النصف','4988','3408','0');
INSERT INTO [t1153] VALUES (3436,'مسألة: ولا حق بسيد الامة في طلب الفيئة والعفو عنها وإنما ذلك إليها','4989','3408','0');
INSERT INTO [t1153] VALUES (3437,'مسألة: فإن فكان بالرجل عذر يمنع الوطء احتسبت عليه بمدة، وإن كان ذلك نهاية لم يحتسب عليه وإن طرأ بها استؤنفت المدة عند زواله','4992','3408','0');
INSERT INTO [t1153] VALUES (3438,'مسألة: وإن طلقها في أثناء المدة انقطعت','4993','3408','0');
INSERT INTO [t1153] VALUES (3439,'مسألة: إلا الحيض فإنه يحتسب عليه بمدته وفي النفاس وجهان','4993','3408','0');
INSERT INTO [t1153] VALUES (3440,'مسألة: وإن كان العذر به وهو مما يعجز به عن الوطء من مرض أو حبس بغير حق أو غيره لزمه أن يفيء بلسانه','4995','3408','0');
INSERT INTO [t1153] VALUES (3441,'مسألة: وإن انقضت المدة وبها عذر يمنع الوطء لم تملك طلب الفيئة','4995','3408','0');
INSERT INTO [t1153] VALUES (3442,'مسألة: وإن كان مظاهرا فقال أمهلوني حتى أطلب رقبة أعتقها عن ظهاري أمهل ثلاثة أيام','4997','3408','0');
INSERT INTO [t1153] VALUES (3443,'مسألة: وإن قال أمهلوني حتى أقضي صلاتي، أو أتغدى فإني جائع، أو حتى ينهضم الطعام أو أنام فإني ناعس أمهل بقدر ذلك','4998','3408','0');
INSERT INTO [t1153] VALUES (3444,'مسالة: فإن لم يبق له عذر طلبت الفيئة وهي الجماع','4999','3408','0');
INSERT INTO [t1153] VALUES (3445,'مسألة: فإذا جامع انحلت يمينه وعليه كفارتها','4999','3408','0');
INSERT INTO [t1153] VALUES (3446,'مسألة: وإن وطئها في الفرج وطأ محرما مثل أن يطأ في الحيض أو النفاس أو الإحرام أو صيام فرض من أحدهما أو مظاهرا فقد فاء إليها','5001','3408','0');
INSERT INTO [t1153] VALUES (3447,'مسالة: وأدنى ما يكفي من ذلك تغييب الحشفة في الفرج','5003','3408','0');
INSERT INTO [t1153] VALUES (3448,'مسالة: وإن لم تعفه أمر بالطلاق إن طلبت ذلك','5004','3408','0');
INSERT INTO [t1153] VALUES (3449,'مسألة: فإن طلق واحدة فله رجعتها وعنه أنها تكون بائنة','5004','3408','0');
INSERT INTO [t1153] VALUES (3450,'مسألة: وإن لم يفىء وأعفته المرأة سقط حقها ويحتمل أن لا يسقط ولها المطالبة بعد','5004','3408','0');
INSERT INTO [t1153] VALUES (3451,'مسألة: فإن لم يطلق حبس وضيق عليه حتى يطلق في إحدى الروايتين والأخرى يطلق الحاكم عليه','5005','3408','0');
INSERT INTO [t1153] VALUES (3452,'مسألة: فإن طلق واحدة فهو كطلاق المولي','5006','3408','0');
INSERT INTO [t1153] VALUES (3453,'مسألة: وإن ادعى أن المدة ما انقضت، وادعت مضيها فالقول قوله في أنها لم تمض مع يمينه','5006','3408','0');
INSERT INTO [t1153] VALUES (3454,'مسألة: فإن ادعى أنه وطئها فأنكرته وكانت ثيبا فالقول قوله مع يمينه','5007','3408','0');
INSERT INTO [t1153] VALUES (3455,'كتاب الظهار','5008','0','0');
INSERT INTO [t1153] VALUES (3456,'كتاب الظهار','5008','3455','0');
INSERT INTO [t1153] VALUES (3457,'مسألة: والظهار أن يشبه امرأته، أو عضوا منها بظهر من تحرم عليه على التأبيد أو بها أو بعضو منها فيقول أنت علي كظهر أمي أو كيد أختي أو كوجه حماتي، أو يقول ظهرك أو يدك علي كظهر أمي، أو كيد أختي أو خالتي من نسب أو رضاع عمتى شبه امرأته بظهر من تحرم علي','5009','3455','0');
INSERT INTO [t1153] VALUES (3458,'مسألة: وإن قال أنت علي كأمي كان مظاهرا، فإن قال أردت كأمي في الكرامة أو نحوه دين وهل يقبل في الحكم؟ يخرج على روايتين','5013','3455','0');
INSERT INTO [t1153] VALUES (3459,'مسالة: وإن قال أنت كأمي أو مثل أمي ولم يقل علي ولا عندي فإن نوى به الظهار كان ظهارا لأنه يحتمله','5015','3455','0');
INSERT INTO [t1153] VALUES (3460,'مسألة: وإن قال أنت علي كظهر أبي ففيه روايتان','5015','3455','0');
INSERT INTO [t1153] VALUES (3461,'مسألة: وإن قال أنت علي كظهر أجنبية أو أخت زوجتي أو عمتها أو خالتها فعلى روايتين','5016','3455','0');
INSERT INTO [t1153] VALUES (3462,'مسالة: وإن قال أنت علي كظهر البهيمة لم يكن مظاهرا','5016','3455','0');
INSERT INTO [t1153] VALUES (3463,'مسألة: وإن قال أنت علي حرام فهو ظهار إلا أن ينوي طلاقا أو يمينا فهل يكون ظهارا أو ما نواه؟ على روايتين','5017','3455','0');
INSERT INTO [t1153] VALUES (3464,'مسألة: ويصح من كل زوجة كبيرة كانت أو صغيرة مسلمة أو ذمية ممكن وطؤها أو غير ممكن','5021','3455','0');
INSERT INTO [t1153] VALUES (3465,'مسألة: فإن ظاهر من أمته أو أم ولده لم يصح وعليه كفارة يمين ويحتمل أن تلزمه كفارة الظهار','5021','3455','0');
INSERT INTO [t1153] VALUES (3466,'مسألة: وإن قالت المرأة لزوجها أنت علي كظهر أبي لم تكن مظاهرة','5022','3455','0');
INSERT INTO [t1153] VALUES (3467,'مسألة: وعليها تمكين زوجها من وطئها قبل التكفير','5024','3455','0');
INSERT INTO [t1153] VALUES (3468,'مسألة: وإن قال لأجنبية أنت علي كظهر أمي لم يطأها إن تزوجها حتى يكفر','5024','3455','0');
INSERT INTO [t1153] VALUES (3469,'مسألة: وإن قال لأجنبية أنت علي حرام وأراد في تلك الحال لم يكن عليه شيء لأنه صادق وإن أراد في كل حال لم يطأها إن تزوجها حتى يكفر','5026','3455','0');
INSERT INTO [t1153] VALUES (3470,'مسألة: ويصح الظهار معجلا ومعلقا بشرط ومطلقا وموقتا نحو أن يقول أنت علي كظهر أمي في شهر رمضان وإن دخلت الدار فمتى انقضى الوقت زال الظهار وإن أصابها فيه وجبت الكفارة عليه','5027','3455','0');
INSERT INTO [t1153] VALUES (3471,'مسألة: وتجب الكفارة بالعود وهو الوطء نص عليه أحمد وأنكر قول مالك أنه العزم على الوطء','5030','3455','0');
INSERT INTO [t1153] VALUES (3472,'مسالة: وهل يحرم الاستمتاع منها بما دون الفرج؟ على روايتين','5030','3455','0');
INSERT INTO [t1153] VALUES (3473,'مسالة: فإن مات أحدهما أو طلقها قبل الوطء فلا كفارة عليه فإن عاد فتزوجها لم يطأها حتى يكفر','5032','3455','0');
INSERT INTO [t1153] VALUES (3474,'مسألة: وإن وطئ قبل التكفير أثم واستقرت عليه الكفارة','5034','3455','0');
INSERT INTO [t1153] VALUES (3475,'مسألة: وتجزئه كفارة واحدة','5034','3455','0');
INSERT INTO [t1153] VALUES (3476,'مسألة: وإن ظاهر من امرأته الأمة ثم اشتراها لم تحل له حتى يكفر، وقال أبو بكر يبطل الظهار وتحل له فإن وطئها فعليه كفارة يمين','5035','3455','0');
INSERT INTO [t1153] VALUES (3477,'مسألة: وإن كرر الظهار قبل التكفير فكفارة واحدة','5035','3455','0');
INSERT INTO [t1153] VALUES (3478,'مسألة: وإن ظاهر من نسائه بكلمة واحدة فكفارة واحدة، وإن كان بكلمات فلكل واحدة كفارة','5036','3455','0');
INSERT INTO [t1153] VALUES (3479,'مسالة: وكفارة القتل مثلها','5038','3455','0');
INSERT INTO [t1153] VALUES (3480,'فصل في كفارة الظهار وما في معناها','5038','3455','0');
INSERT INTO [t1153] VALUES (3481,'مسألة: والاعتبار في الكفارة بحال الوجوب في إحدى الروايتين','5039','3455','0');
INSERT INTO [t1153] VALUES (3482,'مسألة: فإن شرع في الصوم ثم قدر على العتق لم يلزمه الانتقال إليه','5040','3455','0');
INSERT INTO [t1153] VALUES (3483,'مسالة: فإن كانت له رقبة يحتاج إلى خدمتها لكبر أو مرض أو زمن أو عظم خلق ونحوه مما يعجز عن خدمة نفسه أو يكون ممن لا يخدم نفسه في العادة ولا يجد رقبة فاضلة عن خدمته فليس عليه الإعتاق','5042','3455','0');
INSERT INTO [t1153] VALUES (3484,'مسألة: فإن كان له دار يسكنها أو عقار يحتاج إلى غلته لمؤنته أو عرض للتجارة لا يستغني عن ربحه في مؤنته لم يلزمه العتق','5043','3455','0');
INSERT INTO [t1153] VALUES (3485,'مسألة: وإن وجد رقبة بثمن مثلها لزمه شراؤها وإن كانت بزيادة تجحف بماله لم يلزمه شراؤها لأن عليه ضرار في ذلك وإن كانت الزيادة لا تجحف بماله ففيه وجهان','5043','3455','0');
INSERT INTO [t1153] VALUES (3486,'مسألة: وإن وهب له رقبة لم يلزمه قبولها','5044','3455','0');
INSERT INTO [t1153] VALUES (3487,'مسألة: وإن كان ماله غائبا وأمكنه شراؤها بنسيئة فقد ذكر شيخنا فيما إذا عدم الماء فبذل له بثمن في الذمة يقدر على ادائه في بلده وجهين','5044','3455','0');
INSERT INTO [t1153] VALUES (3488,'مسألة: ولا يجزئ في كفارة القتل إلا رقبة مؤمنة','5044','3455','0');
INSERT INTO [t1153] VALUES (3489,'مسألة: ولا يجزئ إلا رقبة سليمة من العيوب المضرة بالعمل ضررابينا','5045','3455','0');
INSERT INTO [t1153] VALUES (3490,'مسألة: لا يجزئ مقطوع اليد أو الرجل ولا أشلهما ولا مقطوع إبهام اليد أو سبابتها أو الوسطى','5046','3455','0');
INSERT INTO [t1153] VALUES (3491,'مسألة: ولا يجزئ الأخرس','5047','3455','0');
INSERT INTO [t1153] VALUES (3492,'مسألة: ولا يجزئ مجنون مطبق لأنه لا يقدر على العمل','5047','3455','0');
INSERT INTO [t1153] VALUES (3493,'مسالة: ولا يجزئ المريض المأيوس من برئه كمرض السل','5047','3455','0');
INSERT INTO [t1153] VALUES (3494,'مسألة: ولا يجزئ غائب','5047','3455','0');
INSERT INTO [t1153] VALUES (3495,'مسألة: ولا يجزئ عتق من علق عتقه بصفة عند وجودها','5048','3455','0');
INSERT INTO [t1153] VALUES (3496,'مسألة: ولا يجزئ من يعتق عليه بالقرابة','5048','3455','0');
INSERT INTO [t1153] VALUES (3497,'مسألة: ولا يجزئ من اشتراه بشرط العتق','5049','3455','0');
INSERT INTO [t1153] VALUES (3498,'مسالة: ولا أم ولده في الصحيح عنه','5050','3455','0');
INSERT INTO [t1153] VALUES (3499,'مسألة: ولا يجزئ مكاتب قد أدى من كتابته شيئا في اختيار شيوخنا وعنه يجزئ وعنه لا يجزئ مكاتب بحال','5051','3455','0');
INSERT INTO [t1153] VALUES (3500,'مسألة: ويجزئ الأعرج يسيرا','5053','3455','0');
INSERT INTO [t1153] VALUES (3501,'مسألة: ويجوز عتق ولد الزنا','5054','3455','0');
INSERT INTO [t1153] VALUES (3502,'مسألة: ويجزئ عتق المدبر','5054','3455','0');
INSERT INTO [t1153] VALUES (3503,'مسألة: ويجزئ الصغير','5055','3455','0');
INSERT INTO [t1153] VALUES (3504,'مسألة: ولو ملك نصف عبد فأعتقه عن كفارته ثم اشترى باقيه فأعتقه أجزأه','5056','3455','0');
INSERT INTO [t1153] VALUES (3505,'مسألة: فإن أعتقه عن كفارته وهو موسر فسرى إلى نصيب شريكه عتق','5056','3455','0');
INSERT INTO [t1153] VALUES (3506,'مسألة: ولو أعتق نصفي عبدين أو نصفي أمتين أو نصف عبد ونصف أمة أجزأ عنه','5057','3455','0');
INSERT INTO [t1153] VALUES (3507,'مسألة: فإن تخلل صومها صوم شهر رمضان أو فطر واجب كفطر العيد أو الفطر لحيض أو نفاس لم ينقطع التتابع وبنى على ما مضى من صيامه','5059','3455','0');
INSERT INTO [t1153] VALUES (3508,'مسألة: فإن أفطر لمرض مخوف أو جنون لم ينقطع التتابع','5060','3455','0');
INSERT INTO [t1153] VALUES (3509,'مسألة: فإن خافتا على ولديهما فأفطرتا ففيه وجهان','5061','3455','0');
INSERT INTO [t1153] VALUES (3510,'مسألة: وإن أفطر لغير عذر أو صام تطوعا أو قضاء أو نذرا أو عن كفارة أخرى لزمه الاستئناف','5061','3455','0');
INSERT INTO [t1153] VALUES (3511,'مسألة: وكذلك فطر الحامل والمرضع لخوفهما على أنفسهما','5061','3455','0');
INSERT INTO [t1153] VALUES (3512,'مسألة: وإن أفطر لعذر يبيح الفطر كالسفر والمرض غير المخوف فعلى وجهين','5062','3455','0');
INSERT INTO [t1153] VALUES (3513,'مسألة: وإن أصاب المظاهر منها ليلا أو نهارا انقطع التتابع','5064','3455','0');
INSERT INTO [t1153] VALUES (3514,'مسألة: وإن وطئ غيرها ليلا لم ينقطع التتابع','5065','3455','0');
INSERT INTO [t1153] VALUES (3515,'مسألة: ولا يجوز دفعها إلى الكافر','5068','3455','0');
INSERT INTO [t1153] VALUES (3516,'مسألة: ويجوز دفع الكفارة إلى من ظاهره الفقر فإن بان غنيا فهل يجزئه؟ فيه وجهان','5068','3455','0');
INSERT INTO [t1153] VALUES (3517,'مسألة: وإن رددها على مسكين واحد ستين يوما لم يجزئه إلا أن لا يجد غيره فيجزئه في ظاهر المذهب، وعنه لا يجزئه وعنه يجزئه وإن وجد غيره','5068','3455','0');
INSERT INTO [t1153] VALUES (3518,'مسألة: وإن دفع إلى مسكين واحد في يوم من كفارتين أجزأه','5069','3455','0');
INSERT INTO [t1153] VALUES (3519,'مسألة: والمخرج في الكفارة ما يجزئ في الفطرة وهو البر والشعير والتمر والزبيب سواء كان قوت بلده أو لم يكن','5069','3455','0');
INSERT INTO [t1153] VALUES (3520,'مسألة: فإن كان قوت بلده غير ذلك كالذرة والدخن والأرز لم يجز إخراجه','5070','3455','0');
INSERT INTO [t1153] VALUES (3521,'مسألة: وإخراج الحب أفضل عند أبي عبد الله','5070','3455','0');
INSERT INTO [t1153] VALUES (3522,'مسألة: وفي الخبز روايتان','5071','3455','0');
INSERT INTO [t1153] VALUES (3523,'مسألة: ولا يجزئ من البر أقل من مد ولا من غيره أقل من مدين','5071','3455','0');
INSERT INTO [t1153] VALUES (3524,'مسألة: ولا يجزئ من الخبز أقل من رطلين بالعراقي إلا أن يعلم أنه مد','5074','3455','0');
INSERT INTO [t1153] VALUES (3525,'مسألة: فإن أخرج القيمة أو غدى المساكين أو عشاهم لم يجزئه ويحتمل أن يجزئه','5075','3455','0');
INSERT INTO [t1153] VALUES (3526,'مسألة: وإن غدى المساكين أو عشاهم لم يجزئه وعنه يجزئه','5075','3455','0');
INSERT INTO [t1153] VALUES (3527,'مسالة: فإن كانت عليه كفارة واحدة فنوى عن كفارتي أجزأه لأن النية تعينت لها وإن كان عليه كفارات من جنس واحد لم يجب تعيين سببها','5078','3455','0');
INSERT INTO [t1153] VALUES (3528,'مسألة: وإن كانت من أجناس كظهار وقتل وجماع في رمضان ويمين فقال أبو الخطاب لا تفتقر إلى تعيين السبب. وبهذا قال الشافعي لأنها عبادة واجبة فلم تفتقر صحة أدائها إلى تعيين سببها كما لو كان من جنس واحد','5079','3455','0');
INSERT INTO [t1153] VALUES (3529,'كتاب اللعان','5084','0','0');
INSERT INTO [t1153] VALUES (3530,'كتاب اللعان','5084','3529','0');
INSERT INTO [t1153] VALUES (3531,'مسألة وصفة اللعان أن يبدأ الزوج فيقول أشهد بالله إني لمن الصادقين فيما رميت به امرأتي هذه من الزنا ويشير','5087','3529','0');
INSERT INTO [t1153] VALUES (3532,'مسألة فإن نقص أحدهما من الألفاظ الخمسة شيئا أو بدأت باللعان قبله أو تلاعنا بغير حضرة الحاكم أو نائبه','5088','3529','0');
INSERT INTO [t1153] VALUES (3533,'مسألة وإن أبدل لفظة أشهد بأقسم أو أحلف أو لفظة اللعنة بالإبعاد أو الغضب بالسخط فعلى وجهين','5089','3529','0');
INSERT INTO [t1153] VALUES (3534,'مسألة ومن قدر على اللعان بالعربية لم يصح منه إلا بها فإن عجز عنها لزمه تعلمها في أحد الوجهين وفي الآخر','5090','3529','0');
INSERT INTO [t1153] VALUES (3535,'مسألة وإذا فهمت إشارة الأخرس أو كتابته صح لعانه بها وإلا فلا','5090','3529','0');
INSERT INTO [t1153] VALUES (3536,'مسألة وهل يصح لعان من اعتقل لسانه وأيس من نطقه بالإشارة؟ على وجهين','5092','3529','0');
INSERT INTO [t1153] VALUES (3537,'مسألة فإذا بلغ كل واحد منهما الخامسة أمر الحاكم رجلا فأمسك يده على فم الرجل وامرأة تضع يدها على','5094','3529','0');
INSERT INTO [t1153] VALUES (3538,'مسألة وأن يكون ذلك بحضرة الحاكم أو نائبه','5095','3529','0');
INSERT INTO [t1153] VALUES (3539,'مسألة وإذا قذف رجل نساءه فعليه أن يفرد كل واحدة بلعان، وعنه يجزئه لعان واحد','5096','3529','0');
INSERT INTO [t1153] VALUES (3540,'مسألة وإن كانت المرأة خفرة بعث من يلاعن بينهما','5096','3529','0');
INSERT INTO [t1153] VALUES (3541,'مسألة وإن قذف أجنبية ثم تزوجها حد ولم يلاعن','5099','3529','0');
INSERT INTO [t1153] VALUES (3542,'مسألة وإن أبان زوجته ثم قذفها إضافة إلى حال الزوجية فإن كان بينهما ولد يريد نفيه فله أن ينفيه باللعان وإلا','5100','3529','0');
INSERT INTO [t1153] VALUES (3543,'مسألة وإن قذفها في نكاح فاسد فهي كالمسألة التي قبلها إن كان بينهما ولد فله لعانها ونفيه وإن لم يكن بينهما','5101','3529','0');
INSERT INTO [t1153] VALUES (3544,'مسألة وإن أبان امرأته بعد قذفها فله أن يلاعن سواء كان بينهما ولد أو لم يكن نص عليه','5102','3529','0');
INSERT INTO [t1153] VALUES (3545,'مسألة فإن قذف زوجته الصغيرة أو المجنونة عزر ولا لعان بينهما','5104','3529','0');
INSERT INTO [t1153] VALUES (3546,'مسألة فإن قال وطئت بشبهة أو مكرهة فلا لعان بينهما','5108','3529','0');
INSERT INTO [t1153] VALUES (3547,'مسألة وإن قال لم تزن ولكن ليس هذا الولد مني فهو ولده في الحكم ولا حد عليه لها','5108','3529','0');
INSERT INTO [t1153] VALUES (3548,'مسألة وإن قال ذلك بعد أن أبانها فشهدت امرأة مرضية أنه ولد على فراشه لحقه نسبه لأن شهادة المرأة','5110','3529','0');
INSERT INTO [t1153] VALUES (3549,'مسألة وإن ولدت توأمين فأقر بأحدهما ونفى الآخر لحقه نسبهما ويلاعن لنفي الحد عنه وقال القاضي يحد','5110','3529','0');
INSERT INTO [t1153] VALUES (3550,'مسألة وإن مات أحدهما قبل اللعان ورثه صاحبه ولحقه نسب الولد ولا لعان','5114','3529','0');
INSERT INTO [t1153] VALUES (3551,'مسألة وإن مات الولد فله لعانها ونفيه لأن شروط اللعان تتحقق بدون الولد فلا تنتفي بموته','5115','3529','0');
INSERT INTO [t1153] VALUES (3552,'مسألة وإن لاعن وتكلمت الزوجة عن اللعان خلي سبيلها ولحقه الولد ذكره الخرقي وعن أحمد أنها تحبس','5119','3529','0');
INSERT INTO [t1153] VALUES (3553,'مسألة ولا يعرض للزوج حتى تطالبه امرأته فإن أراد اللعان من غير طلبها فإن كان بينهما ولد يريد نفيه فله','5122','3529','0');
INSERT INTO [t1153] VALUES (3554,'مسألة الثاني الفرقة بينهما وعنه لا تحصل حتى يفرق الحاكم بينهما','5125','3529','0');
INSERT INTO [t1153] VALUES (3555,'مسألة الثالث التحريم المؤبد وعنه أنه إن أكذب نفسه حلت له','5130','3529','0');
INSERT INTO [t1153] VALUES (3556,'مسألة وإذا قلنا تحل له بإذاب نفسه فإن لم يكن وجد منه طلاق فهي باقية على النكاح','5131','3529','0');
INSERT INTO [t1153] VALUES (3557,'مسألة الرابع انتفاء الولد عنه بمجرد اللعان ذكره أبو بكر وينتفي عنه حملها وإن لم يذكره وقال الخرقي لا ينتفي','5132','3529','0');
INSERT INTO [t1153] VALUES (3558,'مسألة وإن نفى الحمل في التعانه لم ينتف حتى ينفيه عند وضعها له ويلاعن','5135','3529','0');
INSERT INTO [t1153] VALUES (3559,'مسألة وإن أخر نفيه مع إمكانه لزمه نسبه ولم يكن له نفيه بعد ذلك','5137','3529','0');
INSERT INTO [t1153] VALUES (3560,'مسألة وإن هنىء به فسكت كان إقرارا ذكره أبو بكر','5137','3529','0');
INSERT INTO [t1153] VALUES (3561,'مسألة فإن امن على الدعاء لزمه في قولهم جميعا','5137','3529','0');
INSERT INTO [t1153] VALUES (3562,'مسألة فإن قال أخرته رجاء موته لم يعذر بذلك ويبطل خيار لأنه أخر نفيه مع إمكانه لغير عذر','5138','3529','0');
INSERT INTO [t1153] VALUES (3563,'مسألة وإن قال لم أعلم به أو لم أعلم أن لي نفيه أو لم أعلم أن ذلك على الفور وأمكن صدقه قبل منه','5139','3529','0');
INSERT INTO [t1153] VALUES (3564,'مسألة وإن أخره لغيبة أو مرض أو شيء يمنعه ذلك لم يسقط نفيه','5139','3529','0');
INSERT INTO [t1153] VALUES (3565,'مسألة ومتى أكذب نفسه بعد نفيه لحقه نسبه ولزمه الحد إن كانت المرأة محصنة أو التعزير إن لم تكن محصنة','5141','3529','0');
INSERT INTO [t1153] VALUES (3566,'مسألة ويلزمه الحد إذا أكذب نفسه سواء أكذبها قبل لعانها أو بعده','5143','3529','0');
INSERT INTO [t1153] VALUES (3567,'مسألة فأما إن أتت به لدون ستة أشهر مد تزوجها','5144','3529','0');
INSERT INTO [t1153] VALUES (3568,'مسألة وإن أقرت بانقضاء القروء ثم أتت به لأكثر من ستة أشهر لم يلحق بالزوج','5144','3529','0');
INSERT INTO [t1153] VALUES (3569,'مسألة فإن طلقها وهي حامل فولدت ثم ولدت آخر قبل مضي ستة أشهر فهو من الزوج','5145','3529','0');
INSERT INTO [t1153] VALUES (3570,'مسألة أو صبي دون عشر أو مقطوع الذكر والأنثيين','5147','3529','0');
INSERT INTO [t1153] VALUES (3571,'مسألة وإن طلقها طلاقا رجعيا فولدت لأكثر من أربع سنين منذ طلقها ولأقل من أربع سنين منذ انقضت عدتها','5148','3529','0');
INSERT INTO [t1153] VALUES (3572,'مسألة وإن أعتقها أو باعها بعد اعترافه بوطئها فأتت بولد لدون ستة أشهر من حين العتق أو لبيع فهو ولده','5155','3529','0');
INSERT INTO [t1153] VALUES (3573,'مسألة وإن استبرئت ثم أتت بولد لأكثر من ستة أشهر لم يلحقه نسبه','5156','3529','0');
INSERT INTO [t1153] VALUES (3574,'مسألة وكذلك إن لم يستبرئها فأتت به لأكثر من ستة أشهر فادعى المشتري أنه منه سواء ادعاه البائع أو لم يدعه','5156','3529','0');
INSERT INTO [t1153] VALUES (3575,'مسألة فأما إن لم يكن البائع أقر بوطئها بيعها لم يلحقه الولد بحال سواء ولدته لستة أشهر أو لأقل منها','5157','3529','0');
INSERT INTO [t1153] VALUES (3576,'مسألة وإن ادعاه البائع فهو عبد للمشتري','5157','3529','0');
INSERT INTO [t1153] VALUES (3577,'مسألة وإن وطىء المجنون من لا ملك له عليها ولا شبهة ملك فأتت بولد لم يلحقه نسبه','5157','3529','0');
INSERT INTO [t1153] VALUES (3578,'كتاب العدد','5158','0','0');
INSERT INTO [t1153] VALUES (3579,'كتاب العدد','5158','3578','0');
INSERT INTO [t1153] VALUES (3580,'مسألة وإن خلا بها وهي مطاوعة فعليها العدة سواء كان بهما او بأحدهما مانع من الوطء كالإحرام والصيام','5159','3578','0');
INSERT INTO [t1153] VALUES (3581,'مسألة إلا ألا يعلم بها كالأعمى والطفل فلا عدة عليها ولا يكمل صداقها لأن المظنة لا تتحقق','5161','3578','0');
INSERT INTO [t1153] VALUES (3582,'مسألة والحمل الذي تنقضي به العدة ما تبين فيه شيء من خلق الإنسان، فإن وضعت مضغة لا يتبين فيها شيء','5163','3578','0');
INSERT INTO [t1153] VALUES (3583,'مسألة وإن أتت بولد لا يلحقه نسبه كامرأة الطفل لم تنقض به العدة وعنه تنقضي وفيه بعد','5165','3578','0');
INSERT INTO [t1153] VALUES (3584,'مسألة وأقل مدة الحمل ستة أشهر وغالبها تسعة وأكثرها أربع سنين وعنه سنتان','5168','3578','0');
INSERT INTO [t1153] VALUES (3585,'مسألة وأقل ما يتعين به الولد أحد وثمانون يوما وهو أقل ما تنقضي به العدة من الحمل وهو أن تضعه بعد ثمانين','5170','3578','0');
INSERT INTO [t1153] VALUES (3586,'مسألة وإن مات زوج الزوجية في عدتها استأنفت عدة الوفاة من حين وسقطت عدة الطلاق','5172','3578','0');
INSERT INTO [t1153] VALUES (3587,'مسألة وإن طلقها في الصحة طلاقا بائنا ثم مات في عدتها لم تنتقل من عدتها وتبني على عدة الطلاق ولا تعتد','5173','3578','0');
INSERT INTO [t1153] VALUES (3588,'مسألة وإن كان الطلاق في مرض موته اعتدت أطول الأجلين من عدة الطلاق وعدة الوفاة','5173','3578','0');
INSERT INTO [t1153] VALUES (3589,'مسألة وإن ارتابت المتوفى عنها لظهور أمارات الحمل من الحركة وانتفاخ البطن وانقطاع الحيض قبل أن تنكح لم','5175','3578','0');
INSERT INTO [t1153] VALUES (3590,'مسألة وإذا مات عن امرأة نكاحها فاسد فقال القاضي عليها عدة الوفاة نص عليه وقال ابن حامد لا عدة عليها','5176','3578','0');
INSERT INTO [t1153] VALUES (3591,'مسألة والقرء الحيض في أصح الروايتين والثانية هي الاطهار','5178','3578','0');
INSERT INTO [t1153] VALUES (3592,'مسألة ولا تعتد بالحيضة التي طلقها فيها حتى تأتي بثلاث كاملة بعدها','5181','3578','0');
INSERT INTO [t1153] VALUES (3593,'مسألة ولا تعتد بالحيضة التي طلقها فيها وإذا طهرت من الحيضة الثالثة حلت في إحدى الروايتين والأخرى لا','5182','3578','0');
INSERT INTO [t1153] VALUES (3594,'مسألة والرواية الثانية القروء الاطهار وتعتد بالطهر الذي طلقها فيه قرءا فإذا طعنت في الحيضة الثالثة حلت','5184','3578','0');
INSERT INTO [t1153] VALUES (3595,'مسألة وعدة أم الولد عدة الأمة لأنها أمة مملوكة وعدة المعتق بعضها بالحساب من عدة حرة وأمة أما إذا اعتدت','5189','3578','0');
INSERT INTO [t1153] VALUES (3596,'مسألة وحد الإياس خمسون سنة وعنه أن ذلك حده في نساء العجم وحده في نساء العرب ستون سنة','5189','3578','0');
INSERT INTO [t1153] VALUES (3597,'مسألة وإن حاضت الصغيرة في عدتها انتقلت الى القروء ويلزمها إكمالها','5191','3578','0');
INSERT INTO [t1153] VALUES (3598,'مسألة وإن يئست ذات القروء في عدتها انتقلت إلى عدة الآيسات ثلاثة أشهر','5192','3578','0');
INSERT INTO [t1153] VALUES (3599,'مسألة وان اعتقت الأمة الرجعية في عدتها بنت على عدة حرة وإن كانت بائنا بنت على عدة امة','5192','3578','0');
INSERT INTO [t1153] VALUES (3600,'مسألة وإن كانت امة اعتدت أحد عشر شهرا تسعة أشهر للحمل وشهرين للعدة','5194','3578','0');
INSERT INTO [t1153] VALUES (3601,'مسألة وعدة الجارية التي أدركت فلم تحض والمستحاضة اليائسة ثلاثة أشهر وعنه سنة','5196','3578','0');
INSERT INTO [t1153] VALUES (3602,'مسألة وهكذا حكم المتسحاضة الناسية','5197','3578','0');
INSERT INTO [t1153] VALUES (3603,'مسألة فأما التي عرفت ما رفع الحيض من مرض ورضاع ونحوه فلا تزال في عدة حتى يعود الحيض فتعتد به','5198','3578','0');
INSERT INTO [t1153] VALUES (3604,'مسألة وهل تفتقر إلى رفع الأمر إلى الحاكم ليحكم بضرب المدة وعدة الوفاة؟ على وجهين','5203','3578','0');
INSERT INTO [t1153] VALUES (3605,'مسألة وإذا حكم حاكم بالفرقة نفذ حكمه في الظاهر دون الباطن فلو طلق الأول صح طلاقه','5203','3578','0');
INSERT INTO [t1153] VALUES (3606,'مسألة فإذا فعلت ذلك يعني تربصت أربع سنين واعتدت عدة الوفاة ثم تزوجت ثم قدم زوجها الأول فإن كان','5204','3578','0');
INSERT INTO [t1153] VALUES (3607,'مسألة وإن قدم بعد دخول الثاني بها خير الأول بين أخذها فتكون امرأته بالعقد الأول وبين صداقها وتكون','5205','3578','0');
INSERT INTO [t1153] VALUES (3608,'مسألة ويأخذ منه صداقها','5205','3578','0');
INSERT INTO [t1153] VALUES (3609,'مسألة وهل يأخذ منه صداقها الذي أعطاها أو الذي أعطاها الثاني؟ على روايتين','5205','3578','0');
INSERT INTO [t1153] VALUES (3610,'مسألة ومن مات عنها زوجها أو طلقها وهو غائب فعدتها من يوم مات أو طلق وإن لم تجتنب ما تجتنبه المعتدات','5210','3578','0');
INSERT INTO [t1153] VALUES (3611,'مسألة وعدة الموطوءة بشهبة عدة المطلقة وكذلك المزني بها وعنه أنها تستبرأ بحيضة','5212','3578','0');
INSERT INTO [t1153] VALUES (3612,'فصول تتعلق بالمفقود','5213','3578','0');
INSERT INTO [t1153] VALUES (3613,'مسألة وإن تزوجت في عدتها لم تنقطع عدتها حتى تدخل بها فتنقطع حينئذ','5218','3578','0');
INSERT INTO [t1153] VALUES (3614,'مسألة ثم إذا فارقها بنت على عدة الأول ثم استأنفت العدة من الثاني','5219','3578','0');
INSERT INTO [t1153] VALUES (3615,'مسألة وكل معتدة من غير النكاح الصحيح كالزانية والموطوءة بشبهة أو في نكاح فاسد فقياس المذهب تحريم','5220','3578','0');
INSERT INTO [t1153] VALUES (3616,'مسألة وإن أتت بولد من أحدهما انقضت عدتها منه ثم اعتدت للآخر أيهما كان','5220','3578','0');
INSERT INTO [t1153] VALUES (3617,'مسألة وإن أمكن أن يكون منهما وهو أن تأتي به لستة أشهر فصاعدا من وطء الثاني ولأربع سنين فما دونها','5221','3578','0');
INSERT INTO [t1153] VALUES (3618,'مسألة وللثاني أن ينكحها بعد انقضاء العدتين وعنه أنها تحرم عليه على التأبيد','5222','3578','0');
INSERT INTO [t1153] VALUES (3619,'مسألة وإن طلقها قبل دخوله بها فهل تبني أو تستأنف على روايتين','5224','3578','0');
INSERT INTO [t1153] VALUES (3620,'مسألة وإن وطىء رجلان امرأة فعليها عدتان لهما لحديث عمر وعلى الذي ذكرناه فيما إذا تزوجت في عدتها','5224','3578','0');
INSERT INTO [t1153] VALUES (3621,'مسألة وإن راجعها ثم طلقها بعد دخوله بها استأنفت العدة من الطلاق الثاني لأنه طلاق من نكاح اتصل به المسيس','5224','3578','0');
INSERT INTO [t1153] VALUES (3622,'مسألة وإن طلقها طلاقا بائنا ثم نكحها في عدتها ثم طلقها قبل دخوله بها فعلى روايتين','5225','3578','0');
INSERT INTO [t1153] VALUES (3623,'مسألة ويستوي في وجوبه الحرة والأمة والمسلمة والذمية والكبيرة والصغيرة','5228','3578','0');
INSERT INTO [t1153] VALUES (3624,'مسألة وهل يجب على البائن على روايتين','5228','3578','0');
INSERT INTO [t1153] VALUES (3625,'مسألة ولا إحداد على الرحعية بغير خلاف نعلمه','5229','3578','0');
INSERT INTO [t1153] VALUES (3626,'مسألة والإحداد اجتناب الطيب والزينة والتحسين كلبس الحلي والملون من الثياب للتحسين','5229','3578','0');
INSERT INTO [t1153] VALUES (3627,'مسألة ولا يحرم عليها الأبيض من الثياب وإن كان حسنا','5233','3578','0');
INSERT INTO [t1153] VALUES (3628,'مسألة إلا أن تدعو ضرورة الى خروجها منه بأن يحولها مالكه أو تخشى على نفسها','5235','3578','0');
INSERT INTO [t1153] VALUES (3629,'مسألة ولا تخرج ليلا وتخرج نهارا لحوائجها سواء كانت مطلقة أو متوفى عنها','5243','3578','0');
INSERT INTO [t1153] VALUES (3630,'مسألة وإن أذن لها زوجها في النقلة إلى بلد للسكنى فيه فمات قبل مفارقة البنيان لزمها العود إلى منزلها وإن','5245','3578','0');
INSERT INTO [t1153] VALUES (3631,'مسألة وإن سافر بها فمات في الطريق وهي قريبو لزمها العود','5247','3578','0');
INSERT INTO [t1153] VALUES (3632,'مسألة وإن أذن لها في الحج فأحرمت به ثم مات فخشيت فوات الحج مضت في سفرها وإن لم تخش وهي في','5248','3578','0');
INSERT INTO [t1153] VALUES (3633,'مسألة وأما المبتوتة فلا تجب عليها عدة الوفاة في منزله وتعتد حيث شاءت نص عليه قال اصحابنا لا يتعين','5251','3578','0');
INSERT INTO [t1153] VALUES (3634,'باب في استبراء الإماء','5253','3578','0');
INSERT INTO [t1153] VALUES (3635,'مسألة وسواء ملكها من صغير أو كبير أو رجل أو امرأة أو مجبوب أو من رجل قد استبرأها لم يطأها','5256','3578','0');
INSERT INTO [t1153] VALUES (3636,'مسألة وإن أعتقها قبل استبرائها لم يحل له نكاحها حتى يستبرئها ولها نكاح غيره إن لم يكن بائعها يطؤها','5257','3578','0');
INSERT INTO [t1153] VALUES (3637,'مسألة والصغيرة التي لا يوطأ مثلها هل يجب استبراؤها؟ على وجهين','5258','3578','0');
INSERT INTO [t1153] VALUES (3638,'مسألة وإن اشترى زوجته لم يلزمه استبراؤها لأنه فرلاش له','5259','3578','0');
INSERT INTO [t1153] VALUES (3639,'مسألة أو عجزت مكاتبيته حلت لسيدها بغير استبراء','5259','3578','0');
INSERT INTO [t1153] VALUES (3640,'مسألة أو أسلمت أمته المجوسية أو المرتدة أو الوثنية التي حاضت عنده أو كان هو المرتد فأسلم فهي حلال بغير','5259','3578','0');
INSERT INTO [t1153] VALUES (3641,'مسألة أو اشترى مكاتبة ذوات رحمه فحضن عنده ثم عجز أو اشترى عبده التاجر امة فاستبرأها ثم أخذها سيده','5260','3578','0');
INSERT INTO [t1153] VALUES (3642,'مسألة وإن وجد الاستبراء في يد البائع قبل القبض أجزأ ويحتمل أن لا يجزىء','5261','3578','0');
INSERT INTO [t1153] VALUES (3643,'مسألة وإن باع أمته ثم عادت إليه بفسخ أو غيره بعد القبض وجب استبراؤها وإن كان قبله فعلى روايتين','5261','3578','0');
INSERT INTO [t1153] VALUES (3644,'مسألة وإذا اشترى أمة مزوجة فطلقها الزوج قبل الدخول وجب استبراؤها','5262','3578','0');
INSERT INTO [t1153] VALUES (3645,'مسألة وإن كان بعده لم يجب في أحد الوجهين','5262','3578','0');
INSERT INTO [t1153] VALUES (3646,'مسألة وإن مات زوجها وسيدها ولم يعلم السابق منهما وبين موتهما أقل من شهرين وخمسة أيام لزمها بعد موت','5267','3578','0');
INSERT INTO [t1153] VALUES (3647,'مسألة وإن كان بينهما أكثر من ذلك أو جهلت المدة فعليها بعد موت الآخر منهما أطول الأجلين من أربعة','5267','3578','0');
INSERT INTO [t1153] VALUES (3648,'مسألة وإن اشترك رجلان في وطء أمة لزمها استبراءان','5269','3578','0');
INSERT INTO [t1153] VALUES (3649,'مسألة أو بحيضة إن كانت ممن تحيض','5270','3578','0');
INSERT INTO [t1153] VALUES (3650,'مسألة أو يمضي شهر إن كانت آيسة أو صغيرة وعنه بثلاثة أشهر اختارها الخرقي','5271','3578','0');
INSERT INTO [t1153] VALUES (3651,'مسألة ومن ارتفع حيضها لا تدري ما رفعه اعتدت بتسعة أشهر للحمل وشهر مكان الحيض','5272','3578','0');
INSERT INTO [t1153] VALUES (3652,'كتاب الرضاع','5273','0','0');
INSERT INTO [t1153] VALUES (3653,'كتاب الرضاع','5273','3652','0');
INSERT INTO [t1153] VALUES (3654,'مسألة ولا ينتشر إلى من في درجته من إخوته وأخواته ولا من هو أعلى منه من آبائه وأمهاته وأعمامه وعماته','5275','3652','0');
INSERT INTO [t1153] VALUES (3655,'مسألة وإن أرضعت بلبن ولدها من الزنا طفلا صار ولدا لها وحرم على الزاني تحريم المصاهرة ولم تثبت حرمة','5276','3652','0');
INSERT INTO [t1153] VALUES (3656,'مسألة وإن وطىء رجلان امرأة بشبهة بولد فأرضعت بلبنه طفلا صار ابنا لمن يثبت نسب المولود منه سواء ثبت','5277','3652','0');
INSERT INTO [t1153] VALUES (3657,'مسألة وإن ثاب لامرأة لبن من غير حمل تقدم لم ينشر الحرمة نص عليه في لبن البكر وعنه ذكرها ابن أبي موسى','5278','3652','0');
INSERT INTO [t1153] VALUES (3658,'مسألة ولا ينشر الحرمة غير لبن المرأة فلو ارتضع طفلان من رجل أو بهيمة أو خنثى مشكل لم ينشر الحرمة','5278','3652','0');
INSERT INTO [t1153] VALUES (3659,'مسألة فلو ارتضع بعدهما بلحظة لم يثبت التحريم وقال أبو الخطاب إذا ارتضع بعد الحولين بساعة لم يحرم وقال','5281','3652','0');
INSERT INTO [t1153] VALUES (3660,'مسألة ومتى أخذ الثدني فامتص منه ثم تركه أوقطع عليه فهي رضعة فإن عاد فأخذه فهي رضعة أخرى بعد ما','5283','3652','0');
INSERT INTO [t1153] VALUES (3661,'مسألة والسعوط والوجور كالرضاع في إحدى الروايتين','5284','3652','0');
INSERT INTO [t1153] VALUES (3662,'مسألة ويحرم لبن الميتة واللبن المشوب ذكره الخرقي وقال أبو بكر لا يثبت التحريم بهما','5286','3652','0');
INSERT INTO [t1153] VALUES (3663,'مسألة ويحرم اللبن المشوب','5287','3652','0');
INSERT INTO [t1153] VALUES (3664,'مسألة والحقنة لا تنشر الحرمة نص عليه وقال ابن حامد ينشرها','5288','3652','0');
INSERT INTO [t1153] VALUES (3665,'مسألة وإن أرضعت اثنتين منفردتين انفسخ نكاحهما على الرواية الأولى وعلى الثانية ينفسخ نكاح الأولى ويثبت','5289','3652','0');
INSERT INTO [t1153] VALUES (3666,'مسألة وإن أرضعت الثلاث متفرقات انفسخ نكاح الأولتين وثبت نكاح الثالثة على الرواية الأولى وعلى الثانية','5290','3652','0');
INSERT INTO [t1153] VALUES (3667,'مسألة وإن أرضعت إحداهن منفردة واثنتين بعدها معا بأن تلقم كل واحدة منهما ثديا فيمتصان معا أو تحلب من','5290','3652','0');
INSERT INTO [t1153] VALUES (3668,'مسألة وله أن يتزوج من شاء من الأصاغر','5291','3652','0');
INSERT INTO [t1153] VALUES (3669,'مسألة وكل امرأة تحرم ابنتها عليه كأمه وأخته وجدته وربيبته إذا أرضعت طفلة حرمتها عليه','5291','3652','0');
INSERT INTO [t1153] VALUES (3670,'مسألة وإن أفسدت نكاحها قبل الدخول فلا مهر لها','5292','3652','0');
INSERT INTO [t1153] VALUES (3671,'مسألة وإن أفسدت نكاح نفسها بعد الدخول لم يسقط مهرها ويجب على زوجها وإن أفسده غيرها وجب','5294','3652','0');
INSERT INTO [t1153] VALUES (3672,'مسألة وإن أفسدت نكاح نفسها بعد الدخول لم يسقط مهرها','5295','3652','0');
INSERT INTO [t1153] VALUES (3673,'مسألة فإذا أرضعت امرأته الكبرى الصغرى فانفسخ نكاحها فعليه نصف مهر الصغرى يرجع به على الكبرى','5295','3652','0');
INSERT INTO [t1153] VALUES (3674,'مسألة فلو دبت الصغرى إلى الكبرى وهي نائمة فارتضعت منها خمس رضعات انفسخ نكاح الكبيرة وحرمت','5295','3652','0');
INSERT INTO [t1153] VALUES (3675,'مسألة وإذا كان لرجل خمس أمهات أولاد لهن منه لبن فأرضعن امرأة له صغرى كل واحدة منهن رضعة حرمت','5296','3652','0');
INSERT INTO [t1153] VALUES (3676,'مسألة ولو كان له ثلاث نسوة لهن لبن منه فأرضعن امرأة له صغرى كل واحدة رضعتين لم تحرم المرضعات','5298','3652','0');
INSERT INTO [t1153] VALUES (3677,'مسألة فإن كان لرجل ثلاث بنات امرأة لهن لبن فأرضعن ثلاث نسوة له صغار حرمت الكبيرة','5298','3652','0');
INSERT INTO [t1153] VALUES (3678,'مسألة وإن أرضعن واحدة كل واحدة منهن رضعتين فهل تحرم الكبرى بذلك؟ على وجهين','5299','3652','0');
INSERT INTO [t1153] VALUES (3679,'مسألة ولو تزوجت الصبي أولا ثم فسخت نكاحه لعيب ثم تزوجت كبيرا فصار لها منه لبن فأرضعت به الصبي','5300','3652','0');
INSERT INTO [t1153] VALUES (3680,'مسألة وإن شهدت به امرأة مرضية ثبت بشهادتها وعنه أنها إن كانت مرضية استحلفت فإن كانت كاذبة لم يحل','5303','3652','0');
INSERT INTO [t1153] VALUES (3681,'مسألة وإن تزوج امرأة ثم قال قبل الدخول هي أختي من الرضاع انفسخ النكاح فإن صدقته فلا مهر لها وإن','5305','3652','0');
INSERT INTO [t1153] VALUES (3682,'مسألة وإن كانت هي التي قالت هو أخي من الرضاع وأكذبها فهي زوجته في الحكم لأنه لا يقبل قولها في فسخ','5307','3652','0');
INSERT INTO [t1153] VALUES (3683,'مسألة ولو قال الزوج هي ابنتي من الرضاع وهي في سنه أو أكبر منه لم تحرم لتحققنا كذبه وقد ذكرناه','5308','3652','0');
INSERT INTO [t1153] VALUES (3684,'مسألة ولو تزوج رجل امرأة لها لبن من زوج قبله فحملت منه ولم يزد لبنها فهو للأول وإن زاد لبنها فأرضعت','5308','3652','0');
INSERT INTO [t1153] VALUES (3685,'كتاب النفقات','5311','0','0');
INSERT INTO [t1153] VALUES (3686,'كتاب النفقات','5311','3685','0');
INSERT INTO [t1153] VALUES (3687,'مسألة فإن تنازعا فيها رجع الأمر إلى الحاكم','5315','3685','0');
INSERT INTO [t1153] VALUES (3688,'مسألة ويجب عليه كسوتها بإجماع أهل العلم','5317','3685','0');
INSERT INTO [t1153] VALUES (3689,'مسألة وعليه ما يعود بنظافة المرأة من الدهن والسدر وثمن مما تغسل به رأسها وما يعود بنظافتها','5317','3685','0');
INSERT INTO [t1153] VALUES (3690,'مسألة فأما الطيب والخضاب والحناء ونحوه فلا يلزمه إلا أن يريد منها التزين به','5318','3685','0');
INSERT INTO [t1153] VALUES (3691,'مسألة وإن احتاجت إلى من يخدمها لكون مثلها لا تخدم نفسها أو لمرضها لومه ذلك','5318','3685','0');
INSERT INTO [t1153] VALUES (3692,'مسألة فإن كان لها خادم وإلا أقام لها خادما بشراء أو كراء أو عارية ولا يلزم الزوج أن يملكها خادما','5319','3685','0');
INSERT INTO [t1153] VALUES (3693,'مسألة وعليه نفقته بقدر نفقة الفقيرين إلا في النظافة','5319','3685','0');
INSERT INTO [t1153] VALUES (3694,'مسألة فإن قالت أنا أخدم نفسي وآخذ ما يلزمك لخادمي لم يكن لها ذلك ولم يلزمه','5320','3685','0');
INSERT INTO [t1153] VALUES (3695,'مسألة فإن قال الزوج أنا أخدمك بنفسي لم يلزمها','5320','3685','0');
INSERT INTO [t1153] VALUES (3696,'مسألة وأما البائن بفسخ أو طلاق فإن كانت حاملا فلها النفقة والسكنى وإلا فلا شيء لها وعنه لها السكنى','5320','3685','0');
INSERT INTO [t1153] VALUES (3697,'مسألة ولا يلزمه أكثر من نفقة خادم واحد لأن المستحق خدمتها في نفسها ويحصل ذلك بواحد','5320','3685','0');
INSERT INTO [t1153] VALUES (3698,'مسألة فإن طلق زوجته ولم ينفق عيها يظنها حائلا ثم تبين أنها كانت حاملا فعليه نفقة ما مضى','5324','3685','0');
INSERT INTO [t1153] VALUES (3699,'مسألة وإن أنفق عيها يظنها حاملا وبانت حائلا مثل من ادعت الحمل لتكون لها النفقة أنفق عليها ثلاثة أشهر ثم','5324','3685','0');
INSERT INTO [t1153] VALUES (3700,'مسألة وهل تجب النفقة للحامل لحملها أو لها من أجله على روايتين','5326','3685','0');
INSERT INTO [t1153] VALUES (3701,'مسألة وأما المتوفى عنها زوجها فإن كانت حائلا فلا سكنى لها ولا نفقة في مدة العدة لأن النكاح قد زال بالموت','5327','3685','0');
INSERT INTO [t1153] VALUES (3702,'مسألة فإن طلب أحدهما دفع القيمة لم يلزم الآخر','5331','3685','0');
INSERT INTO [t1153] VALUES (3703,'مسألة وإن انقضت السنة وهي صحيحة فعليه كسوة السنة الأخرى ويحتمل أن لا يلزمه','5331','3685','0');
INSERT INTO [t1153] VALUES (3704,'مسألة فإذا قبضتها فسرقت أو تلفت لم يلزمه عوضها','5331','3685','0');
INSERT INTO [t1153] VALUES (3705,'مسألة وعليه كسوتها في كل عام مرة','5331','3685','0');
INSERT INTO [t1153] VALUES (3706,'مسألة وإن ماتت أو طلقها قبل مضي السنة فهل يرجع عليها بقسط بقية السنة على وجهين','5332','3685','0');
INSERT INTO [t1153] VALUES (3707,'مسألة وإذا قبضت النفقة فلها التصرف فيها على وجه لا يضر بها ولا ينهك بدنها فيجوز لها بيعها وهبتها','5332','3685','0');
INSERT INTO [t1153] VALUES (3708,'مسألة وإن غاب مدة ولم ينفق فعليه نفقة ما مضى تركها لتعذر أو غير عذر في أظهر الروايتين','5333','3685','0');
INSERT INTO [t1153] VALUES (3709,'مسألة وإن سلمت نفسها وهي ممن يتعذر وطؤها لرتق أوحيض أو نفاس أو لكونها نضوة الخلق لا يمكنه وطؤها','5334','3685','0');
INSERT INTO [t1153] VALUES (3710,'مسألة وإن أسلمت نفسها وهي صغيرة وجبت عليه نفقتها إذا كانت كبيرة يمكن وطؤها','5335','3685','0');
INSERT INTO [t1153] VALUES (3711,'مسألة فإن كانت صغيرة لا يمكن وطؤها لم تجب نفقتها ولا تسليمها إليه إذا طلبها','5335','3685','0');
INSERT INTO [t1153] VALUES (3712,'مسألة وإن بذلته والزوج غائب لم يفرض لها حتى يراسله الحاكم ويمضي زمن يمكن أن يقدم في مثله','5336','3685','0');
INSERT INTO [t1153] VALUES (3713,'مسألة وإن منعت نفسها أو معها أهلها فلا نفقة لها وإن تساكنا بعد العقد فلم تبذل ولم يطلب فلا نفقة لها وإن طال مقامها على ذلك','5337','3685','0');
INSERT INTO [t1153] VALUES (3714,'مسألة إلا أن تمنع نفسها قبل الدخول حتى تقبض صداقها الحال فلها ذلك وتحب نفقتها','5337','3685','0');
INSERT INTO [t1153] VALUES (3715,'مسألة فأما الصداق المؤجل فليس لها منع نفسها حتى تقبضه كالثمن المؤجل في البيع وقد ذكرنا هذه المسائل في','5338','3685','0');
INSERT INTO [t1153] VALUES (3716,'مسألة وإن سلمت الأمة نفسها ليلا أو نهارا فهي كالحرة في وجوب النفقة','5338','3685','0');
INSERT INTO [t1153] VALUES (3717,'مسألة وإن كان بعد الدخول فكذلك في أحد الوجهين قياسا على ما قبل الدخول والثاني ليس لها ذلك','5338','3685','0');
INSERT INTO [t1153] VALUES (3718,'مسألة وإن كانت تأوي إليه ليلا وعند السيد نهارا فعلى كل واحد منهما النفقة بقدر مقامها عنده','5340','3685','0');
INSERT INTO [t1153] VALUES (3719,'مسألة وإذا نشزت المرأة أو سافرت بغير إذنه أو تطوعت بحج أو صوم أو أحرمت بحج منذور في الذمة بغير إذنه','5342','3685','0');
INSERT INTO [t1153] VALUES (3720,'مسألة وإن بعثها في حاجته فهي على نفقتها','5343','3685','0');
INSERT INTO [t1153] VALUES (3721,'مسألة وكذلك إن ادعى تسليم النفقة إليها فأنكرته فالقول قولها كذلك','5344','3685','0');
INSERT INTO [t1153] VALUES (3722,'مسألة وإن أحرمت بمنذور معين في وقته فعلى وجهين','5344','3685','0');
INSERT INTO [t1153] VALUES (3723,'مسألة وإن اختلفا في نشوزها فادعى أنها نشزت وأنكرت الزوجة فالقول قولها مع يمينها لأن الأصل عدم','5344','3685','0');
INSERT INTO [t1153] VALUES (3724,'مسألة وإن اختلفا في بذل التسليم فقالت: بذلت لك تسليم نفسي فأنكرها فالقول قوله','5345','3685','0');
INSERT INTO [t1153] VALUES (3725,'مسألة وإن رضيت بالمقام معه مع عسرته وترك المطالبة جاز','5347','3685','0');
INSERT INTO [t1153] VALUES (3726,'مسألة وإن أعسر بنفقة الخادم أو النفقة الماضية أو نفقة الموسر أو المتوسط أو الأدم فلا فسخ لها وتكون النفقة','5348','3685','0');
INSERT INTO [t1153] VALUES (3727,'مسألة ويثبت ذلك في ذمته وكذلك إن أعسر بالمسكن وقلنا لا يثبت لها الفسخ','5348','3685','0');
INSERT INTO [t1153] VALUES (3728,'مسألة وإن أعسر بالسكنى أو المهر فهل لها الفسخ يحتمل وجهين','5349','3685','0');
INSERT INTO [t1153] VALUES (3729,'مسألة وإن أعسر زوج الأمة فرضيت لم يكن لسيدها الفسخ ويحتمل أن له ذلك','5350','3685','0');
INSERT INTO [t1153] VALUES (3730,'مسألة فإن لم تقدر أجبره الحاكم وحبسه فإن صبر على الحبس ولم ينفق أخذ الحاكم النفقة من ماله فدفعها إلى','5353','3685','0');
INSERT INTO [t1153] VALUES (3731,'مسألة وإن غاب زوجها ولم يترك لها نفقة فإن قدرت له على مال أخذت بقدر حاجتها لحديث هند','5354','3685','0');
INSERT INTO [t1153] VALUES (3732,'مسألة فإن غيب ماله وصبر على الحبس فلها الفسخ إذا لم يقدر الحاكم له على مال يأخذه أو لم يقدر على النفقة','5354','3685','0');
INSERT INTO [t1153] VALUES (3733,'مسألة ولا يجوز الفسخ في ذلك كله إلا بحكم الحاكم','5356','3685','0');
INSERT INTO [t1153] VALUES (3734,'باب نفقة الأقارب والمماليك','5356','3685','0');
INSERT INTO [t1153] VALUES (3735,'مسألة ويلزمه نفقة سائر آبائه وإن علوا وأولاده وإن سفلوا','5358','3685','0');
INSERT INTO [t1153] VALUES (3736,'مسألة وتلزمه نفقة كل من يرثه بفرض أو تعصيب ممن سواهم سواء ورثه الآخر أو لا كعمته وعتيقه','5360','3685','0');
INSERT INTO [t1153] VALUES (3737,'مسألة فأما ذوو الأرحام فلا نفقة عليهم رواية واحدة ذكره القاضي وقال أبو الخطاب يخرج في وجوبها عليهم','5362','3685','0');
INSERT INTO [t1153] VALUES (3738,'مسألة وإن كان للفقير وارث فنفقته عليهم على قدر ميراثهم منه لأن الله تعالى رتب النفقة على الإرث','5363','3685','0');
INSERT INTO [t1153] VALUES (3739,'مسألة فإن كان له أم وجد فعلى الأم الثلث والباقي على الجد','5363','3685','0');
INSERT INTO [t1153] VALUES (3740,'مسألة وان اجتمع جدة وأخ فعلى الجدة السدس والباقي على الأخ لأن ميراثهما منه كذلك وعلى هذا المعنى','5364','3685','0');
INSERT INTO [t1153] VALUES (3741,'مسألة إلا أن يكون له أب فتكون النفقة عليه وحده','5365','3685','0');
INSERT INTO [t1153] VALUES (3742,'مسألة ومن له ابن فقير وأخ موسر فلا نفقة له عليهما وهكذا ذكره القاضي وأبو الخطاب','5366','3685','0');
INSERT INTO [t1153] VALUES (3743,'مسألة ومن له أم فقيرة وجدة موسرة فالنفقة عليها يعني على الجدة','5366','3685','0');
INSERT INTO [t1153] VALUES (3744,'مسألة ومن لم يفضل عنده إلا نفقة شخص واحد بدأ بالأقرب فالأقرب فإن كان له أبوان جعله بينهما','5368','3685','0');
INSERT INTO [t1153] VALUES (3745,'مسألة ومن كان صحيحا مكلفا لا حرفة له سوى الوالدين فهل تجب نفقته على روايتين','5368','3685','0');
INSERT INTO [t1153] VALUES (3746,'مسألة وليس له منع المرأة من رضاع ولدها إذا طلبت ذلك','5369','3685','0');
INSERT INTO [t1153] VALUES (3747,'مسألة فإن كان له أبوان فهو بينهما','5369','3685','0');
INSERT INTO [t1153] VALUES (3748,'مسألة وإن كان معهما ابن فقال القاضي إن كان الابن صغيرا أو مجنونا قدم','5369','3685','0');
INSERT INTO [t1153] VALUES (3749,'مسألة وإن كان له أب وجد أو ابن وابن ابن فالأب والابن أحق','5369','3685','0');
INSERT INTO [t1153] VALUES (3750,'مسألة ولا تجب النفقة مع اختلاف الدين','5370','3685','0');
INSERT INTO [t1153] VALUES (3751,'مسألة إن ترك الإنفاق الواجب مدة لم يلزمه عوضه','5371','3685','0');
INSERT INTO [t1153] VALUES (3752,'مسألة ومن لزمته نفقة رجل فهل يلزمه امرأته على روايتين','5373','3685','0');
INSERT INTO [t1153] VALUES (3753,'مسألة وإن أعسر زوج الصغيرة أو المجنونة لم يكن لوليهما الفسخ','5377','3685','0');
INSERT INTO [t1153] VALUES (3754,'مسألة وإن امتنعت من رضاعه لم تجبر إلا أن يضطر إليها ويخشى عليه','5378','3685','0');
INSERT INTO [t1153] VALUES (3755,'مسألة وإن تزوجت المرأة فلزوجها منعها من رضاع ولدها إلا أن يضطر إليها','5379','3685','0');
INSERT INTO [t1153] VALUES (3756,'مسألة ولا يجب عليه أجرة الظئر لما زاد على الحولين','5379','3685','0');
INSERT INTO [t1153] VALUES (3757,'مسألة وعليه تزويجهم إذا طلبوا ذلك','5382','3685','0');
INSERT INTO [t1153] VALUES (3758,'مسألة إلا الأمة إذا كان يستمتع بها','5383','3685','0');
INSERT INTO [t1153] VALUES (3759,'مسألة ولا يكلفهم من العمل ما لا يطيقون','5383','3685','0');
INSERT INTO [t1153] VALUES (3760,'مسألة ويريحهم وقت القيلولة والنوم وأوقات الصلوات','5383','3685','0');
INSERT INTO [t1153] VALUES (3761,'مسألة ولا يسترضع الأمة لغير ولدها إلا أن يكون فيها فضل عن ريه','5384','3685','0');
INSERT INTO [t1153] VALUES (3762,'مسألة ويداويهم إذا مرضوا','5384','3685','0');
INSERT INTO [t1153] VALUES (3763,'مسألة وإذا ولي أحدهم طعامه أطعمه معه فإن أبى أطعمه منه','5384','3685','0');
INSERT INTO [t1153] VALUES (3764,'مسألة ولا يجبر العبد على المخارجة وإن اتفقا عليها جاز','5385','3685','0');
INSERT INTO [t1153] VALUES (3765,'مسألة ومتى امتنع السيد من الواجب عليه وطلب العبد البيع لزمه بيعه','5386','3685','0');
INSERT INTO [t1153] VALUES (3766,'مسألة وله تأديب رقيقه بما يؤدب به ولده وامرأته','5386','3685','0');
INSERT INTO [t1153] VALUES (3767,'مسألة وللعبد التسري بإذن سيده ولو ملكه جارية لم يكن له التسري بها إلا بأذنه','5387','3685','0');
INSERT INTO [t1153] VALUES (3768,'مسألة ولا يحملها ما لا تطيق','5394','3685','0');
INSERT INTO [t1153] VALUES (3769,'كتاب الجنايات','5400','0','0');
INSERT INTO [t1153] VALUES (3770,'كتاب الجنايات','5400','3769','0');
INSERT INTO [t1153] VALUES (3771,'مسألة والقتل على أربعة أضرب عمد وشبه عمد وخطأ وما أجري مجرى الخطأ','5401','3769','0');
INSERT INTO [t1153] VALUES (3772,'مسألة والعمد أن يقتله بما يغلب على الظن موته به عالما بكونه آدميا معصوما وهو تسعة أقسام','5402','3769','0');
INSERT INTO [t1153] VALUES (3773,'مسألة فإن بقي من ذلك ضمناحتى مات أو كان الغرز بها في مقتل كالفؤاد والخصيتين فهو عمد محض','5403','3769','0');
INSERT INTO [t1153] VALUES (3774,'مسألة وإن قطع سلعة من أجنبي بغير إذنه فمات فعليه القود لأنه جرحه بغير إذنه جرحا لا يجوز له فكان عليه','5403','3769','0');
INSERT INTO [t1153] VALUES (3775,'مسألة وإن ادعى القاتل بالسم أنني لم أعلم أنه سم قاتل لم يقبل قوله في أحد الوجهين','5411','3769','0');
INSERT INTO [t1153] VALUES (3776,'مسألة فإن خلط السم بطعام نفسه فدخل إنسان منزله فأكله فلا ضمان عليه','5411','3769','0');
INSERT INTO [t1153] VALUES (3777,'مسألة والذي أجري مجرى الخطأ كالنائم ينقلب على إنسان فيقتله أو يقتل بالسبب مثل أن يحفر بئرا أو ينصب','5416','3769','0');
INSERT INTO [t1153] VALUES (3778,'مسألة وإن جرحه أحدهما جرحا والآخر مائة فهما سواء في القصاص والدية','5417','3769','0');
INSERT INTO [t1153] VALUES (3779,'مسألة وإن قطع أحدهما يده من الكوع والآخر من المرفق فهما قاتلان','5419','3769','0');
INSERT INTO [t1153] VALUES (3780,'مسألة وإن فعل أحدهما فعلا لا تبقى معه الحياة كقطع حشوته أو مريئه أو ودجيه ثم ضرب عنقه آخر فالقاتل','5420','3769','0');
INSERT INTO [t1153] VALUES (3781,'مسألة فإن رماه من شاهق فتلقاه آخر بسيف فقده فالقصاص على الثاني','5421','3769','0');
INSERT INTO [t1153] VALUES (3782,'مسألة وإن ألقاه في لجة فالتقمه الحوت فالقود على الرامي في أحد الوجهين','5421','3769','0');
INSERT INTO [t1153] VALUES (3783,'مسألة وإن أكره أنسانا على القتل فقتل فالقصاص عليهما','5422','3769','0');
INSERT INTO [t1153] VALUES (3784,'مسألة وإن أمر من لا يميز أو مجنونا أو عبده الذي لا يعلم أن القتل محرم فقتل القصاص على الآمر','5423','3769','0');
INSERT INTO [t1153] VALUES (3785,'مسألة وإن أمر كبيرا عاقلا عالما بتحريم القتل به فقتل فالقصاص على القاتل','5424','3769','0');
INSERT INTO [t1153] VALUES (3786,'مسألة وإن أمر السلطان بقتل إنسان بغير حق من يعلم ذلك فالقصاص على القتل وإن لم يعلم فعلى الآمر','5424','3769','0');
INSERT INTO [t1153] VALUES (3787,'مسألة وإن أمسك إنسانا لآخر فقتله قتل القاتل وحبس الممسك حتى يموت في إحدى الروايتين','5425','3769','0');
INSERT INTO [t1153] VALUES (3788,'مسألة وإن كتفه وطرحه في أرض مسبعة أو ذات حيات فقتلته فحكمه حكم الممسك','5427','3769','0');
INSERT INTO [t1153] VALUES (3789,'مسألة وفي شريك السبع وشريك نفسه وجهان','5430','3769','0');
INSERT INTO [t1153] VALUES (3790,'مسألة ولو جرحه إنسان عمدا فداوى جرحه بسم أو خاطه في اللحم أو فعل ذلك وليه أو الإمام فمات ففي','5431','3769','0');
INSERT INTO [t1153] VALUES (3791,'باب شروط القصاص','5432','3769','0');
INSERT INTO [t1153] VALUES (3792,'مسألة وإن قطع مسلم أو ذمي يد مرتد أو حربي فأسلم ثم مات فلا شيء على القاطع لأنه لم يجن على معصوم','5434','3769','0');
INSERT INTO [t1153] VALUES (3793,'مسألة ولو قطع يد مسلم فارتد ثم مات فلا شيء على القاطع في أحد الوجهين','5435','3769','0');
INSERT INTO [t1153] VALUES (3794,'مسألة وإن عاد إلى الإسلام ثم مات وجب القصاص على قاتله','5436','3769','0');
INSERT INTO [t1153] VALUES (3795,'مسألة وبقتل العبد المسلم بالعبد المسلم تساوت قيمتهما أو اختلفت','5438','3769','0');
INSERT INTO [t1153] VALUES (3796,'مسألة ويقتل الذكر بالأنثى والأنثى بالذكر','5440','3769','0');
INSERT INTO [t1153] VALUES (3797,'مسألة وعن أحمد لا يقتل العبد بالعبد إلا أن تستوي قيمتهما ولا عمل عليه وقد ذكرناه','5441','3769','0');
INSERT INTO [t1153] VALUES (3798,'مسألة ويقتل الكافر بالمسلم','5441','3769','0');
INSERT INTO [t1153] VALUES (3799,'مسألة ولا يقتل مسلم بكافر أي كافر كان','5442','3769','0');
INSERT INTO [t1153] VALUES (3800,'مسألة ولا يقتل حر بعبد','5443','3769','0');
INSERT INTO [t1153] VALUES (3801,'مسألة إلا أن يجرحه وهو مثله أو يقتله ثم يسلم القاتل أو الجارح أو يعتق فيموت المجروح فإنه يقتل به','5444','3769','0');
INSERT INTO [t1153] VALUES (3802,'مسألة وإن جرح مسلم كافرا فأسلم المجروح ثم مات مسلما بسراية الجرح لم يقتل به قاتله لعدم التكافؤ','5446','3769','0');
INSERT INTO [t1153] VALUES (3803,'مسألة وإن رمى مسلم ذميا عبدا فلم يقع السهم به حتى عتق وأسلم فلا قود وعليه دية حر مسلم إذا مات من','5447','3769','0');
INSERT INTO [t1153] VALUES (3804,'مسألة ولو قتل من يعرفه ذميا عبدا فبان أنه قد عتق وأسلم فعليه القصاص','5453','3769','0');
INSERT INTO [t1153] VALUES (3805,'مسألة وإن كان يعرفه مرتدا فكذلك عند أبي بكر','5453','3769','0');
INSERT INTO [t1153] VALUES (3806,'مسألة ومتى ورث ولده القصاص أو شيئا منه أو ورث القاتل شيئا من دمه سقط القصاص فلو قتل أحد الزوجين','5457','3769','0');
INSERT INTO [t1153] VALUES (3807,'مسألة ويقتل الولد بكل واحد منهما في أظهر الروايتين','5457','3769','0');
INSERT INTO [t1153] VALUES (3808,'مسألة ولو قتل رجل أخازوجته فورثته ثم ماتت فورثها ولده سقط عنه القصاص وسواء كان لها ولد من غيره أو لا','5458','3769','0');
INSERT INTO [t1153] VALUES (3809,'مسألة: ولو قتل أباه أو أخاه فورثه أخواه ثم قتل أحدهما صاحبه سقط القصاص عن الأول لأنه ورث بعض دم نفسه','5459','3769','0');
INSERT INTO [t1153] VALUES (3810,'مسألة وإن قتل أحد الابنين اباه والآخر أمه وهي زوجة الأب سقط القصاص عن الأول لذلك، وله أن يقتص','5459','3769','0');
INSERT INTO [t1153] VALUES (3811,'مسألة وإن قتل من لا يعرفه وادعى كفره لم يقبل','5461','3769','0');
INSERT INTO [t1153] VALUES (3812,'مسألة وإن قتل رجلا في داره وادعى أنه دخل يكابره على أهله أو ماله فقتله دفعا عن نفسه وانكر وليه فالقول','5462','3769','0');
INSERT INTO [t1153] VALUES (3813,'مسألة وإن تجارح اثنان وادعى كل واحد منهما أنه جرح صاحبه دفعا عن نفسه وأنكر الآخر وجب القصاص','5463','3769','0');
INSERT INTO [t1153] VALUES (3814,'باب استيفاء القصاص','5465','3769','0');
INSERT INTO [t1153] VALUES (3815,'مسألة فإن قتلا قاتل أبيهما أو قطعا قاطعهما قهرا احتمل أن يسقط حقهما واحتمل أن تجب دية أبيهما','5467','3769','0');
INSERT INTO [t1153] VALUES (3816,'مسألة فإن فعل فلا قصاص عليه','5468','3769','0');
INSERT INTO [t1153] VALUES (3817,'مسألة وإن اقتصا ممن لا تحمل ديته العاقلة كالعبد سقط حقهما وجها واحدا لأنه لا يمكن إيجاب ديته على العاقلة','5468','3769','0');
INSERT INTO [t1153] VALUES (3818,'مسألة وعليه لشركائه حقهم من الدية ويسقط عن الجاني في أحد الوجهين وفي الآخر لهم ذلك في تركة الجاني','5469','3769','0');
INSERT INTO [t1153] VALUES (3819,'مسألة وإن قتله الباقون عالمين بالعفو وسقوط القصاص به فعليهم القود وإلا فلا قود وعليهم ديته','5472','3769','0');
INSERT INTO [t1153] VALUES (3820,'مسألة وسواء كان الجميع حاضرين أو بعضهم غائب لما ذكرناه','5473','3769','0');
INSERT INTO [t1153] VALUES (3821,'مسألة وإن كان بعضهم صغيرا أومجنونا فليس للبالغ العاقل استيفاء حتى يصيرا مكلفين في المشهور وعنه له ذلك','5474','3769','0');
INSERT INTO [t1153] VALUES (3822,'مسألة وكل من ورث المال ورث القصاص على حسب ميراثه من المال حتى الزوجين وذوي الأرحام','5476','3769','0');
INSERT INTO [t1153] VALUES (3823,'مسألة ومن لاوارث له وليه الإمام إن شاء اقتص وإن شاء عفا فله أن يفعل من ذلك ما يرى فيه المصلحة','5476','3769','0');
INSERT INTO [t1153] VALUES (3824,'مسألة وإن اقتص من حامل وجب ضمان حنايتها على قاتلها','5478','3769','0');
INSERT INTO [t1153] VALUES (3825,'مسألة وإن ادعت الحمل ففيه وجهان','5478','3769','0');
INSERT INTO [t1153] VALUES (3826,'مسألة وحكم الحد في ذلك حكم القصاص لما ذكرنا من الحديث ولأنه في معنى القصاص','5478','3769','0');
INSERT INTO [t1153] VALUES (3827,'مسألة وعليه تفقد الآلة فإن كانت كالة منعه الاستيفاء بها لئلا يعذب المقتول','5479','3769','0');
INSERT INTO [t1153] VALUES (3828,'مسألة وينظر السلطان في الولي فإن كان يحسن الاستيفاء ويقدر عليه بالقوة والمعرفة مكنه منه','5480','3769','0');
INSERT INTO [t1153] VALUES (3829,'مسألة وإن كان الولي لا يحسن الاستيفاء أمره بالتوكيل','5480','3769','0');
INSERT INTO [t1153] VALUES (3830,'مسألة فإن احتاج الوكيل إلى أجرة فمن مال الجاني فقد قيل يؤخذ العوض من بيت المال','5481','3769','0');
INSERT INTO [t1153] VALUES (3831,'مسألة والولي مخير بين الاستيفاء بنفسه إن كان يحسن وبين التوكيل','5481','3769','0');
INSERT INTO [t1153] VALUES (3832,'مسألة وإن تشاح أولياء المقتول في الاستيفاء قدم أحدهم بالقرعة','5481','3769','0');
INSERT INTO [t1153] VALUES (3833,'مسألة فإن قتله بمحرم في نفسه كتجريع الخمر واللواط ونحوه قتل بالسيف رواية واحدة','5487','3769','0');
INSERT INTO [t1153] VALUES (3834,'مسألة ولا يجوز الزيادة على ما أتى به رواية واحدة ولا قطع شيء من أطرافه فإن فعل فلا قصاص فيه وتجب فيه','5487','3769','0');
INSERT INTO [t1153] VALUES (3835,'مسألة وإن تشاحوا فيمن يقتله منهم على الكمال اقيد للأول','5492','3769','0');
INSERT INTO [t1153] VALUES (3836,'مسألة وإن قتل وقطع طرفا قطع طرفه أولا ثم قتل لولي المقتول سواء تقدم القتل أو تأخر','5493','3769','0');
INSERT INTO [t1153] VALUES (3837,'باب العفو عن القصاص','5495','3769','0');
INSERT INTO [t1153] VALUES (3838,'مسألة وإن قطع أيدي جماعة فحكمهم حكم القتل على ما ذكرنا من التفصيل الاختلاف','5495','3769','0');
INSERT INTO [t1153] VALUES (3839,'مسألة وإن مات القاتل وجبت الدية في تركته','5499','3769','0');
INSERT INTO [t1153] VALUES (3840,'مسألة وإن قطع اصبعا عمدا فعفا عنه ثم سرت إلى الكف أو النفس وكان العفو على مال فله تمام الدية وإن','5499','3769','0');
INSERT INTO [t1153] VALUES (3841,'مسألة وإن قال الجاني عفوت مطلقا أو عفوت عنها وعن سرايتها قال بل عفوت الى مال أو عفوت عنها دون','5502','3769','0');
INSERT INTO [t1153] VALUES (3842,'مسألة وإن قتل الجاني العافي عمدا فلوليه القصاص أو الدية كاملة وقال القاضي له القصاص أو تمام الدية','5502','3769','0');
INSERT INTO [t1153] VALUES (3843,'مسألة وإذا وكل رجلا في القصاص ثم عفا ولم يعلم الوكيل حتى اقتص فلا شيء عليه وهل يضمن العافي؟','5503','3769','0');
INSERT INTO [t1153] VALUES (3844,'مسألة وإن عفا قاتله بعد الجرح صح وسواء عفا بلفظ العفو أو الوصية','5506','3769','0');
INSERT INTO [t1153] VALUES (3845,'مسألة وإن وجب لعبد قصاص في الطرف أو جرح أو تعزير قذف فله طلبه والعفو عنه','5507','3769','0');
INSERT INTO [t1153] VALUES (3846,'مسألة وإن أبرأ القاتل من الدية الواجبة على عاقلته أو العبد من الجناية المتعلق أرشها برقبته لم يصح','5507','3769','0');
INSERT INTO [t1153] VALUES (3847,'مسألة ولا يحتمل أن يصح عفوه عن المال ولا وصية به لقاتل ولا غيره إذا قلنا إنه يحدث على ملك الورثة لأنه','5507','3769','0');
INSERT INTO [t1153] VALUES (3848,'مسألة وإن أبرأه من الدية أو وصى له بها فهي وصية لقاتل هل تصح لقاتل؟ على روايتين','5507','3769','0');
INSERT INTO [t1153] VALUES (3849,'باب ما يوجب القصاص فيما دون النفس','5508','3769','0');
INSERT INTO [t1153] VALUES (3850,'مسألة وهو نوعان أحدهما الأطراف فتؤخذ العين بالعين والأنف بالأنف والأذن بالأذن والسن بالسن والجفن','5510','3769','0');
INSERT INTO [t1153] VALUES (3851,'مسألة ويشترط للقصاص في الطرف ثلاثة شروط أحدها أن يكون القطع من مفصل أو له حد ينتهي اليه كمارن','5510','3769','0');
INSERT INTO [t1153] VALUES (3852,'مسألة وتقطع العين بالعين','5513','3769','0');
INSERT INTO [t1153] VALUES (3853,'مسألة ويؤخذ السن بالسن','5516','3769','0');
INSERT INTO [t1153] VALUES (3854,'مسألة وتؤخذ الشفة بالشفة','5518','3769','0');
INSERT INTO [t1153] VALUES (3855,'مسألة وتؤخذ اليد باليد','5518','3769','0');
INSERT INTO [t1153] VALUES (3856,'مسألة ويؤخذ الجفن بالجفن','5518','3769','0');
INSERT INTO [t1153] VALUES (3857,'مسألة ويؤخذ كل واحد من الأصابع والكف والمرفق والذكر والانثيين بمثله','5521','3769','0');
INSERT INTO [t1153] VALUES (3858,'مسألة ويجري القصاص في الانثيين كما ذكرنا من النص والمعنى','5522','3769','0');
INSERT INTO [t1153] VALUES (3859,'مسألة وهل يجري القصاص في الألية؟ على وجهين','5522','3769','0');
INSERT INTO [t1153] VALUES (3860,'مسألة وفي القصاص في شفري المرأة وجهان','5522','3769','0');
INSERT INTO [t1153] VALUES (3861,'مسألة وإذا وصح إنسانا فذهب ضوء عينه أو سمعه أو شمه فإنه يوضحه فإنه جرح يمكن الاقتصاص منه من غير','5523','3769','0');
INSERT INTO [t1153] VALUES (3862,'مسألة وتؤخذ الأصبع والسن والأنملة بمثلها في الموضع والاسم ولا تؤخذ أنملة بأنملة إلا أن يتفقا في الموضع والاسم','5525','3769','0');
INSERT INTO [t1153] VALUES (3863,'مسألة فلو قطع أنملة رجل العليا وقطع الوسطى من آخر ليس له عليا فصاحب الوسطى مخير بين أخذ عقل','5525','3769','0');
INSERT INTO [t1153] VALUES (3864,'مسألة ولا تؤخذ أصلية بزائدة ولا زائدة بأصلية ولا زائدة بزائدة في غير محلها لعدم التماثل وإن تراضيا عليه','5526','3769','0');
INSERT INTO [t1153] VALUES (3865,'مسألة فلو تراضيا على إحدى اليدين بدلا عن الأخرى فقطعها المقتص سقط القود','5527','3769','0');
INSERT INTO [t1153] VALUES (3866,'مسألة وإن قال له أخرج يمينك فاخرج يساره فقطعها أجزأت على قول أبي بكر سواء قطعها عالما بها أو جاهلا','5527','3769','0');
INSERT INTO [t1153] VALUES (3867,'مسألة وإن أخرجها دهشة أو ظنا منه أنها تجزىء فعلى القاطع ديتها إن علم أنها يسار وأنها لا تجزىء ويعزر','5529','3769','0');
INSERT INTO [t1153] VALUES (3868,'مسألة وإن كان من عليه القصاص مجنونا مثل من يجن بعد وجوب القصاص عليه فعلى قاطعها للقود','5529','3769','0');
INSERT INTO [t1153] VALUES (3869,'مسألة وإن كان من له القصاص مجنونا ومن عليه القصاص عاقلا فأخرج إليه يساره أو يمينه فقطعها ذهبت هدرا','5530','3769','0');
INSERT INTO [t1153] VALUES (3870,'مسألة ولا تؤخذ عين صحيحة بقائمة ولا لسان ناطق بأخرس ولا ذكر صحيح بأشل','5533','3769','0');
INSERT INTO [t1153] VALUES (3871,'مسألة ولا يؤخذ ذكر فحل بذكر خصي ولا عنين','5533','3769','0');
INSERT INTO [t1153] VALUES (3872,'مسألة إلا مارن الأشم الصحيح فإنه يؤخذ بأنف الأخشم الذي لا يشم','5534','3769','0');
INSERT INTO [t1153] VALUES (3873,'مسألة وتؤخذ أذن السميع بأذن الأصم لما ذكرنا','5534','3769','0');
INSERT INTO [t1153] VALUES (3874,'مسألة ويؤخذ المعيب من ذلك كله بالصحيح وبمثله إذا أمن من قطع الشلاء التلف','5535','3769','0');
INSERT INTO [t1153] VALUES (3875,'مسألة وتؤخذ الناقصة بالناقصة إذا تساوتا فيه بأن يكون المقطوع من يد الجاني كالمقطوع من يد المجني عليه','5536','3769','0');
INSERT INTO [t1153] VALUES (3876,'مسألة وتؤخذ الناقصة بالكاملة لأنها دون حقه، وهل له أخذ دية الأصابع الناقصة على وجهين','5536','3769','0');
INSERT INTO [t1153] VALUES (3877,'مسألة وان اختلفا في شلل العضو وضحته فالقول قول المجني عليه في أحد الوجهين','5538','3769','0');
INSERT INTO [t1153] VALUES (3878,'مسألة وإن كسر بعض سنه برد من سن الجاني مثله إذا أمن قلعها','5539','3769','0');
INSERT INTO [t1153] VALUES (3879,'مسألة ولا يقتص من السن حتى ييأس من عودها وهي سن من قد أثغر أي سقطت رواضعه ثم نبتت فإن قلع','5541','3769','0');
INSERT INTO [t1153] VALUES (3880,'مسألة فإن عاد بدل السن على صفتها في موضعها فلا شييء على الجاني وإن مضى زمن عودها ولم تعد','5541','3769','0');
INSERT INTO [t1153] VALUES (3881,'مسألة وإن مات المجني عليه قبل الإياس من عودها فلا قصاص','5541','3769','0');
INSERT INTO [t1153] VALUES (3882,'مسألة فإن قطع سن كبير فقال القاضي يسأل أهل الخبرة','5541','3769','0');
INSERT INTO [t1153] VALUES (3883,'مسألة وإن اقتص من سن فعادت غرم سن الجاني لأنه قد تبين أن القصاص لم يكن يجب ويضمنها بالدية دون','5541','3769','0');
INSERT INTO [t1153] VALUES (3884,'مسألة وإن عادت سن المجني عليه قصيرة أو معيبة فعلى الجاني أرش نقصها بالحساب','5541','3769','0');
INSERT INTO [t1153] VALUES (3885,'مسألة ولا يجب القصاص فيما سوى ذلك من الشجاج والجروح كما دون الموضحة أو أعظم منها','5543','3769','0');
INSERT INTO [t1153] VALUES (3886,'مسألة إلا أن تكون أعظم من الموضحة كالهاشمة والمنقلة والمأمومة فله أن يقتص موضحة بغير خلاف بين أصحابنا','5544','3769','0');
INSERT INTO [t1153] VALUES (3887,'مسألة ولا شيء له مع القصاص على قول أبي بكر','5545','3769','0');
INSERT INTO [t1153] VALUES (3888,'مسألة ويعتبر قدر الجرح بالمساحة فلو أوضح إنسانا في بعض رأسه مقدار ذلك البعض جمع رأس الشاج وزيادة','5545','3769','0');
INSERT INTO [t1153] VALUES (3889,'مسألة وإن تفرقت أفعالهم أو قطع كل واحد من جانب فلا قصاص عليهم رواية واحدة','5552','3769','0');
INSERT INTO [t1153] VALUES (3890,'مسألة وسراية الجناية مضمونة بالقصاص أو الدية','5552','3769','0');
INSERT INTO [t1153] VALUES (3891,'مسألة وإن شل ففيه ديته','5554','3769','0');
INSERT INTO [t1153] VALUES (3892,'مسألة وسراية القود غير مضمونة فلو قطع اليد قصاصا فسرى إلى النفس فلا شيء على القاطع','5555','3769','0');
INSERT INTO [t1153] VALUES (3893,'مسألة ولا يقتص في الطرف إلا بعد برئه','5556','3769','0');
INSERT INTO [t1153] VALUES (3894,'مسألة فإن فعل ذلك سقط حقه من سرايته فلو سرى إلى نفسه كان هدرا ولو سرى القصاص الى نفس الجاني','5558','3769','0');
INSERT INTO [t1153] VALUES (3895,'كتاب الديات','5562','0','0');
INSERT INTO [t1153] VALUES (3896,'كتاب الديات','5562','3895','0');
INSERT INTO [t1153] VALUES (3897,'مسألة فإن كان القتل عمدا محضا فهي في مال الجاني حالة','5563','3895','0');
INSERT INTO [t1153] VALUES (3898,'مسألة وإن كان شبه عمد أو خطأ أو ما جرى مجراه فعلى عاقتله','5564','3895','0');
INSERT INTO [t1153] VALUES (3899,'مسألة وان القى انسانا على أفعى أوألقاها عليه فقتلته أوطلب انسانا بسيف مجرد فهرب فوقع في شيء تلف به','5567','3895','0');
INSERT INTO [t1153] VALUES (3900,'مسألة فإن طلب إنسانا بالسيف مشهورا فهرب منه فتلف في هربه ضمنه','5567','3895','0');
INSERT INTO [t1153] VALUES (3901,'مسألة وإن حفر في فنائه بئرا لنفسه أو في طريق لغير مصلحة المسلمين أو في ملك غيره بغير إذنه أو وضع في','5569','3895','0');
INSERT INTO [t1153] VALUES (3902,'مسألة وإن بالت فيها دابته فزلق به حيوان فمات به فقال أصحابنا على صاحب الدابة الضمان إذا كان راكبا','5569','3895','0');
INSERT INTO [t1153] VALUES (3903,'مسألة وإن حفر بئرا ووضع آخر حجرا أو نصب سكينا فعثر بالحجر فوقع في البئر أو على السكين فالضمان','5569','3895','0');
INSERT INTO [t1153] VALUES (3904,'مسألة وإن كانا راكبين فماتت الدابتان فعلى كل واحد منهما قيمة دابة الآخر','5573','3895','0');
INSERT INTO [t1153] VALUES (3905,'مسألة وان اصطدم نفسان فماتا فعلى عاقلة كل واحد منهما دية الآخر','5573','3895','0');
INSERT INTO [t1153] VALUES (3906,'مسألة وان غصب صغيرا فنهشته حية أو أصابته صاعقة ففيه الدية وإن مات بمرض فعلى وجهين','5573','3895','0');
INSERT INTO [t1153] VALUES (3907,'مسألة إلا أن يكون أحدهما يسير والآخر واقفا فعلى السائر ضمان الواقف ودابته','5574','3895','0');
INSERT INTO [t1153] VALUES (3908,'مسألة إلا أن يكون في طريق ضيق قاعدا أو واقفا فلا ضمان عليه فيه وعليه ضمان ما تلف به','5575','3895','0');
INSERT INTO [t1153] VALUES (3909,'مسألة وإن أركب صبيين لا ولاية له عليهما فاصطدما فماتا فعلى عاقلته ديتهما','5575','3895','0');
INSERT INTO [t1153] VALUES (3910,'مسألة وإن رمى ثلاثة بمنجنيق فقتل الحجر إنسانا فعلى عاقلة كل واحد منهم ثلث ديته','5575','3895','0');
INSERT INTO [t1153] VALUES (3911,'مسألة وان كانوا أكثر من ثلاثة فالدية حالة في اموالهم في الصحيح من المذهب','5577','3895','0');
INSERT INTO [t1153] VALUES (3912,'مسألة وإن جنى إنسان على نفسه أو طرفه خطأ فلا شيء له وعنه على عاقلته ديته فلورثته ودية طرفه لنفسه','5578','3895','0');
INSERT INTO [t1153] VALUES (3913,'مسألة وإذا نزل رجل في بئر فحر عليه آخر فمات الأول من سقطته فعلى عاقلته ديته','5579','3895','0');
INSERT INTO [t1153] VALUES (3914,'مسألة وإن كان الأول جذب الثاني وجذب الثاني الثالث فلا شيء على الثالث','5580','3895','0');
INSERT INTO [t1153] VALUES (3915,'مسألة وإن وقع عليهما ثالث فمات الثاني به فعلى عاقلة الثالث ديته','5580','3895','0');
INSERT INTO [t1153] VALUES (3916,'مسألة وإن خر رجل في زبية أسد فجذب آخر وجذب الثاني ثالثا وجذب الثالث رابعا فقتلهم الأسد فالقياس','5582','3895','0');
INSERT INTO [t1153] VALUES (3917,'مسألة ومن اضطر الى طعام إنسان أو شرابه به مثل ضرورته فمنعه حتى مات ضمنه نص عليه','5583','3895','0');
INSERT INTO [t1153] VALUES (3918,'مسألة وإن افزع انسانا فأحدث بغائظ فعليه ثلث ديته وعنه لا شيء عليه','5584','3895','0');
INSERT INTO [t1153] VALUES (3919,'مسألة ويتخرج وجوب الضمان على ما قاله فيما إذا أرسل السلطان الى امرأة ليحضرها فاجهضت جنينا أو','5586','3895','0');
INSERT INTO [t1153] VALUES (3920,'مسألة وإن سلم ولده الى السابح ليعلمه فغرق لم يضمنه ويحتمل أن تضمنه العاقلة','5587','3895','0');
INSERT INTO [t1153] VALUES (3921,'مسألة وإن أمر إنسانا أن ينزل بئرا أو يصعد شجرة فهلك بذلك لم يضمنه','5588','3895','0');
INSERT INTO [t1153] VALUES (3922,'مسألة وإن وضع جرة على سطحه أو حائطه أو حجرا فرمته الريح على إنسان فقتله أو شيء أتلفه لم يضمنه','5588','3895','0');
INSERT INTO [t1153] VALUES (3923,'مسألة وإن أخرج جناحا إلى الطريق أو ميزانا فسقط على إنسان فاتأفه ضمنه','5588','3895','0');
INSERT INTO [t1153] VALUES (3924,'باب مقادير ديات النفس','5589','3895','0');
INSERT INTO [t1153] VALUES (3925,'مسألة وعن أحمد رحمه الله أن الإبل هي الأصل خاصة','5590','3895','0');
INSERT INTO [t1153] VALUES (3926,'مسألة فإن كان القتل عمدا أو شبه عمد وجبت أرباعا خمس وعشرون بنت مخاض وخمس وعشرون بنت لبون','5592','3895','0');
INSERT INTO [t1153] VALUES (3927,'مسألة وإذا قلنا إن الإبل هي الأصل خاصة فعلى من عليه الدية تسليمها إلى مستحقها سليمة من العيوب وأيهما','5592','3895','0');
INSERT INTO [t1153] VALUES (3928,'مسألة وإن كان القتل خطأ وجبت أخماسا عشرون بنت مخاض وعشرون ابن مخاض وعشرون بنت لبون','5594','3895','0');
INSERT INTO [t1153] VALUES (3929,'مسألة ويؤخذ في البقر النصف مسنات والنصف أتبعة وفي الغنم النصف ثنايا والنصف أجذعة إذا كانت الغنم ضأنا','5596','3895','0');
INSERT INTO [t1153] VALUES (3930,'مسألة ولا تعتبر القيمة في شيء من ذلك إذا كان سليما من العيوب وقال أبو الخطاب تعتبر أن تكون القيمة','5596','3895','0');
INSERT INTO [t1153] VALUES (3931,'مسألة ويؤخذ في الحلل المتعارف من ذلك باليمن','5599','3895','0');
INSERT INTO [t1153] VALUES (3932,'مسألة وتساوي جراح المرأة جراح الرجل إلى ثلث الدية فإذا زادت صارت على النصف','5601','3895','0');
INSERT INTO [t1153] VALUES (3933,'مسألة ودية الخنثى المشكل نصف دية ذكر ونصف دية أنثى وذلك ثلاثة أرباع دية الذكرلأنه يحتمل الذكورية','5602','3895','0');
INSERT INTO [t1153] VALUES (3934,'مسألة ونساؤهم على النصف من دياتهم','5605','3895','0');
INSERT INTO [t1153] VALUES (3935,'مسألة ودية المجوسي والوثني ثمانمائة درهم','5605','3895','0');
INSERT INTO [t1153] VALUES (3936,'مسألة وجراحاتهم على النصف من دياتهم كجراحات المسلمين من دياتهم قياسا عليهم','5605','3895','0');
INSERT INTO [t1153] VALUES (3937,'مسألة ومن لم تبلغه الدعوة فلا ضمان فيه','5606','3895','0');
INSERT INTO [t1153] VALUES (3938,'مسألة فأما عبدة الأوثان وسائر من ليس له كتاب كالترك ومن عبد ما استحسن فلا ذمة لهم وإنما تحقن دماؤهم','5606','3895','0');
INSERT INTO [t1153] VALUES (3939,'مسألة وفي جراحه إن لم يكن مقدرا في الحر ما نقصه بعد التئام الجرح كسائر الأموال وإن كان مقدرا في الحر','5608','3895','0');
INSERT INTO [t1153] VALUES (3940,'مسألة ومن نصفه حر ففيه نصف دية حر ونصف قيمته وكذلك في جراحه','5611','3895','0');
INSERT INTO [t1153] VALUES (3941,'مسألة وإذا قطع خصيتي عبد أو أنفه أو اذنيه لزمته قيمته للسيد ولم يزل ملك السيد عنه وإن قطع ذكره ثم','5611','3895','0');
INSERT INTO [t1153] VALUES (3942,'مسألة ولا يقبل في الغرة خنثى ولا معيب ولا من له دون سبع سنين','5618','3895','0');
INSERT INTO [t1153] VALUES (3943,'مسألة وإن كان الجنين مملوكا ففيه عشر قيمة أمه ذكرا كان أو أنثى','5619','3895','0');
INSERT INTO [t1153] VALUES (3944,'مسألة وإن ضرب بطن أمه فعتقت ثم أسقطت الجنين ففيه غرة','5624','3895','0');
INSERT INTO [t1153] VALUES (3945,'مسألة وإن كان الجنين محكوما بكفره ففيه عشر دية','5626','3895','0');
INSERT INTO [t1153] VALUES (3946,'مسألة وان سقط الجنين حيا ثم مات ففيه دية حر ان كان حرا أو قيمته أن كان مملوكا','5626','3895','0');
INSERT INTO [t1153] VALUES (3947,'مسألة وان كان أحد أبويه كتابيا والآخر مجوسيا اعتبر أكثرهما دية فيجب عشر دية كتابية على كل حال','5626','3895','0');
INSERT INTO [t1153] VALUES (3948,'مسألة وان اختلفا في حياته ولا بينة لهما ففي أيهما يقدم قوله؟ وجهان','5629','3895','0');
INSERT INTO [t1153] VALUES (3949,'مسألة وظاهر كلام الخرقي أن الدية لا تغلظ لشيء من ذلك وهو قول الحسن والشعبي والنخعي وأبي حنيفة','5636','3895','0');
INSERT INTO [t1153] VALUES (3950,'مسألة إن قتل مسلم كافرا عمدا ضعفت الدية على قاتله لإزالة القود كما حكم عثمان رضي الله عنه','5638','3895','0');
INSERT INTO [t1153] VALUES (3951,'مسألة وإن كانت الجناية أكثر من قيمته ففيه روايتان','5639','3895','0');
INSERT INTO [t1153] VALUES (3952,'مسألة وإن سلمه اليه السيد فأبى ولي الجناية قبوله وقال: بعه أنت وادفع ثمنه الي فهل يلزم السيد ذلك','5640','3895','0');
INSERT INTO [t1153] VALUES (3953,'مسألة وان جنى عمدا فعفا الولي على القصاص على رقبته فهل يملكه بغير رضا السيد؟ على روايتين','5640','3895','0');
