INSERT INTO [t1153] VALUES (4058,'باب العاقلة وما تحمله','5725','3895','0');
INSERT INTO [t1153] VALUES (4059,'مسألة وخطأ الإمام والحاكم في احكامه في بيت المال وعنه على عاقلته','5729','3895','0');
INSERT INTO [t1153] VALUES (4060,'مسألة ويحمل الغائب كما يحمل الحاضر','5729','3895','0');
INSERT INTO [t1153] VALUES (4061,'مسألة وهل يتعاقل اهل الذمة على روايتين','5730','3895','0');
INSERT INTO [t1153] VALUES (4062,'مسألة ولا يعقل حربي عن ذمي ولا ذمي عن حربي','5730','3895','0');
