INSERT INTO [t1153] VALUES (4064,'مسألة ولا تحمل العاقلة عمدا ولا عبدا ولا صلحا ولا اعترافا ولا ما دون ثلث الدية ويكون ذلك في مال الجاني','5734','3895','0');
INSERT INTO [t1153] VALUES (4065,'مسألة وقال ابو بكر ولا تحمل العاقلة شبه العمد ويكون في مال القاتل في ثلاث سنين','5739','3895','0');
