INSERT INTO [t1153] VALUES (4069,'مسألة: واختلف القائلون بالتقدير بنصف دينار وربعه فقال بعضهم: يتكرر الواجب في الأحوال الثلاثة، فيكون الواجب فيها دينارا ونصفا وعلى المتوسط ثلاثة أرباع دينار','5741','3895','0');
INSERT INTO [t1153] VALUES (4070,'مسألة ويبدأ بالأقرب فالأقرب فمتى اتسعت أموال الأقربين لها لم يتجاوزهم وإلا ننقل الى من يليهم','5742','3895','0');
INSERT INTO [t1153] VALUES (4071,'مسألة وإن تساوى جماعة في القرب وزع القدر الذي يلزمهم بينهم','5744','3895','0');
INSERT INTO [t1153] VALUES (4072,'مسألة وما تحمله العاقلة يجب مؤجلا في ثلاث سنين في كل سنة ثلثه ان كان دية كاملة','5745','3895','0');
INSERT INTO [t1153] VALUES (4073,'مسألة وان كان الواجب ثلث الدية وجب في رأس الحول الأول وإن كان نصفها كدية اليد وجب في رأس','5745','3895','0');
INSERT INTO [t1153] VALUES (4074,'مسألة فإن كانت الدية ناقصة كدية المرأة والكتابي ففيها وجهان','5746','3895','0');
INSERT INTO [t1153] VALUES (4075,'مسألة وابتداء الحول في الجرح من حين الاندمال وفي القتل من حين الموت وقال القاضي إن لم يسر الجرح الى','5747','3895','0');
INSERT INTO [t1153] VALUES (4076,'مسألة ومن مات من العاقلة قبل الحول أو افتقر سقط ما عليه وإن مات بعد الحول لم يسقط ما عليه','5747','3895','0');
INSERT INTO [t1153] VALUES (4077,'مسألة وعمد الصبي والمجنون خطأ تحمله العاقلة','5748','3895','0');
INSERT INTO [t1153] VALUES (4078,'باب كفارة القتل','5748','3895','0');
INSERT INTO [t1153] VALUES (4079,'مسألة مسلما كان المقتول أو كافرا حرا أو عبدا','5749','3895','0');
INSERT INTO [t1153] VALUES (4080,'مسألة ولو ضرب بطن امرأة فألقت جنينا ميتا أو حيا ثم مات فعليه الكفارة','5749','3895','0');
INSERT INTO [t1153] VALUES (4081,'مسألة وتجب الكفارة بقتل العبد','5750','3895','0');
INSERT INTO [t1153] VALUES (4082,'مسألة وسواء كان القاتل كبيرا عاقلا أو صبيا أو مجنونا حرا او عبدا','5750','3895','0');
INSERT INTO [t1153] VALUES (4083,'مسألة ويكفر العبد بالصيام لأنه لا مال له وقد ذكرنا كفارة العبد فيما مضى','5750','3895','0');
INSERT INTO [t1153] VALUES (4084,'مسألة فأما قتل المباح كالقصاص والحد وقتل الباغي والصائل فلا كفارة فيه','5751','3895','0');
INSERT INTO [t1153] VALUES (4085,'مسألة وفي العمد وشبهه روايتان','5752','3895','0');
INSERT INTO [t1153] VALUES (4086,'باب القسامة','5755','3895','0');
INSERT INTO [t1153] VALUES (4087,'مسألة: ولا يثبت إلا بشروط أربعة: أحدها دعوى القتل ذكرا كان المقتول أو أنثى، حرا أو عبدا مسلما أو ذميا، وأما الجراح فلا قسامة فيها','5756','3895','0');
INSERT INTO [t1153] VALUES (4088,'مسألة: وسواء كان المقتول ذكرا أو أنثى حرا أو عبدا مسلما أو ذميا','5759','3895','0');
INSERT INTO [t1153] VALUES (4089,'مسألة: فأما الجراح فلا قسامة فيها','5763','3895','0');
INSERT INTO [t1153] VALUES (4090,'مسألة: فأما قول القتيل فلان قتلني فليس بلوث','5768','3895','0');
INSERT INTO [t1153] VALUES (4091,'مسألة: ومتى ادعى القتل مع عدم اللوث عمدا، فقال الخرقي: لا يحكم له بيمين ولا غيرها، وعن أحمد أنه يحلف يمينا واحدة وهي الأولى، وإن كان خطأ حلف يمينا واحدة','5769','3895','0');
INSERT INTO [t1153] VALUES (4092,'مسألة: وذكر الخرقي من شروط القسامة أن تكون الدعوى عمدا يوجب القصاص إذا ثبت القتل وأن تكون الدعوى على واحد','5778','3895','0');
INSERT INTO [t1153] VALUES (4093,'مسألة: وعن أحمد يحلف من العصبة الوارث منهم وغير الوارث خمسون رجلا كل واحد يمينا','5791','3895','0');
INSERT INTO [t1153] VALUES (4094,'مسألة: فان لم يحلف المدعون حلف المدعى عليه خمسين يمينا وبرىء','5794','3895','0');
INSERT INTO [t1153] VALUES (4095,'مسألة: فان لم يحلف المدعون ولم يرضوا بيمين المدعى عليه فداه الامام من بيت المال','5796','3895','0');
INSERT INTO [t1153] VALUES (4096,'مسألة: وإن طلبوا أيمانهم فنكلوا لم يحبسوا وهل تلزمهم الدية أو تكون في بيت المال؟ على روايتين','5796','3895','0');
INSERT INTO [t1153] VALUES (4097,'باب قتال أهل البغي','5798','3895','0');
INSERT INTO [t1153] VALUES (4098,'مسألة: وهم القوم الذين يخرجون على الامام بتأويل سائغ ولهم منعة وشوكة','5801','3895','0');
INSERT INTO [t1153] VALUES (4099,'مسألة: وعلى الامام أن يراسلهم ويسألهم ما ينقمون منه ويزيل ما يذكرونه من مظلمة ويكشف من شبهة فان فاؤوا والا قاتلهم','5803','3895','0');
INSERT INTO [t1153] VALUES (4100,'مسألة: وعلى رعيته معونته على حربهم للآية','5804','3895','0');
INSERT INTO [t1153] VALUES (4101,'مسألة: فان استنظروه مدة رجا رجوعهم فيها أنظرهم ويكشف عن حالهم ويبحث عن أمرهم، فان بان له أن قصدهم الرجوع إلى الطاعة ومعرفة الحق أمهلهم، قال ابن المنذر: أجمع على هذا كل من نحفظ عنه من أهل العلم','5804','3895','0');
INSERT INTO [t1153] VALUES (4102,'مسألة: فان ظن أنها مكيدة لم ينظرهم وقاتلهم','5804','3895','0');
INSERT INTO [t1153] VALUES (4103,'مسألة: ولا يقاتلهم بما يعم إتلافه كالمنجنيق والنار إلا لضرورة','5807','3895','0');
INSERT INTO [t1153] VALUES (4104,'مسألة: ولا يستعين في حربهم بكافر ولا بمن يرى قتلهم مدبرين','5808','3895','0');
INSERT INTO [t1153] VALUES (4105,'مسألة: وهل يجوز أن يستعين عليهم بسلاحهم وكراعهم؟ على وجهين','5808','3895','0');
INSERT INTO [t1153] VALUES (4106,'مسألة: وذكر القاضي أن أحمد أومأ الى جواز الانتفاع به حال الحرب','5808','3895','0');
INSERT INTO [t1153] VALUES (4107,'مسألة: ولا يتبع لهم مدبر ولا يجاز على جريح','5809','3895','0');
INSERT INTO [t1153] VALUES (4108,'مسألة: ومن أسر من رجالهم حبس حتى تنقضي الحرب ثم يرسل','5810','3895','0');
INSERT INTO [t1153] VALUES (4109,'مسألة: ولا يغنم لهم مال ولا يسبى لهم ذرية','5810','3895','0');
INSERT INTO [t1153] VALUES (4110,'مسألة: وان أسر صبي أو امرأة فهل يفعل به ذلك أو يخلى سبيله في الحال؟ يحتمل وجهين','5811','3895','0');
INSERT INTO [t1153] VALUES (4111,'مسألة: وإذا انقضى الحرب فمن وجد ماله في يد انسان أخذه','5811','3895','0');
INSERT INTO [t1153] VALUES (4112,'مسألة: ولا يضمن أهل العدل ما أتلفوه عليهم حال الحرب من نفس أو مال، وهل يضمن البغاة ما أتلفوه على أهل العدل في الحرب؟ على روايتين','5811','3895','0');
INSERT INTO [t1153] VALUES (4113,'مسألة: ومن أتلف في غير حال الحرب شيئا ضمنه سواء كان قبل الحرب أوبعده','5813','3895','0');
INSERT INTO [t1153] VALUES (4114,'مسألة: وما أخذوا في حال امتناعهم من زكاة او خراج أو جزية لم يعد عليهم، ولا على صاحبه','5815','3895','0');
INSERT INTO [t1153] VALUES (4115,'مسألة: ومن ادعى زكاته اليهم قبل بغير يمين','5815','3895','0');
INSERT INTO [t1153] VALUES (4116,'مسألة: وإن ادعى ذمي جزيته اليهم لم يقبل الا ببينة','5815','3895','0');
INSERT INTO [t1153] VALUES (4117,'مسألأة: وإن ادعى دفع خراجه اليهم فهل يقبل بغير بينة؟ على وجهين','5816','3895','0');
INSERT INTO [t1153] VALUES (4118,'مسألة: وتجوز شهادتهم','5816','3895','0');
INSERT INTO [t1153] VALUES (4119,'مسألة: ولا ينقض من حكم حاكمهم الا ما ينقض من حكم غيره','5816','3895','0');
INSERT INTO [t1153] VALUES (4120,'مسألة: وإن استعانوا بأهل الذمة فأعانوهم انتقض عهدهم، إلا أن يدعوا أنهم ظنوا أنه تجب عليهم معونة من استعان بهم من المسلمين ونحو ذلك فلا ينتقض عهدهم','5818','3895','0');
INSERT INTO [t1153] VALUES (4121,'مسألة: وان استعانوا بأهل الحرب وآمنوهم لم يصح امانهم وابيح قتلهم','5819','3895','0');
INSERT INTO [t1153] VALUES (4122,'مسألة: وان أظهر قوم رأي الخوارج ولم يجتمعوا لحرب لم يتعرض لهم','5819','3895','0');
INSERT INTO [t1153] VALUES (4123,'مسألة: وان سبوا الامام عزرهم وكذلك إن سبوا غيره من أهل العدل','5820','3895','0');
INSERT INTO [t1153] VALUES (4124,'مسألة: وان جنوا جناية أو أتوا حدا اقامه عليهم','5822','3895','0');
INSERT INTO [t1153] VALUES (4125,'مسألة: وان اقتتلت طائفتان لعصبية أو طلب رئاسة فهما ظالمتان وتضمن كل واحدة منهما ما اتلفت على الأخرى','5823','3895','0');
INSERT INTO [t1153] VALUES (4126,'باب حكم المرتد','5824','3895','0');
INSERT INTO [t1153] VALUES (4127,'مسألة: فمن أشرك بالله تعالى جحد ربوبيته أو وحدانيته أو صفة من صفاته أو اتخذ صاحبة أو ولدا أو جحد نبيا أو كتابا من كتب الله أو شيئا منه أو سب الله سبحانه وتعالى أو رسوله كفر','5824','3895','0');
INSERT INTO [t1153] VALUES (4128,'مسألة: فان جحد وجوب العبادات الخمس أو شيئا منها أو أحل الزنا أو الخمر أو شيئا من المحرمات الظاهرة المجمع على تحريمها لجهل عرف ذلك فان كان ممن لا يجهل ذلك كفر','5825','3895','0');
INSERT INTO [t1153] VALUES (4129,'مسألة: ومن ترك شيئا من العبادات الخمس تهاونا لم يكفر وعنه يكفر','5827','3895','0');
INSERT INTO [t1153] VALUES (4130,'مسألة: ومن ارتد عن الاسلام من الرجال والنساء وهو بالغ عاقل دعي اليه ثلاثة أيام وضيق عليه فان لم يتب قتل','5828','3895','0');
INSERT INTO [t1153] VALUES (4131,'مسألة: ويقتل بالسيف لأنه آلة القتل ولا يحرق بالنار','5832','3895','0');
INSERT INTO [t1153] VALUES (4132,'مسألة: ولا يقتله إلا الإمام أو نائبه حرا كان المرتد أو عبدا','5832','3895','0');
INSERT INTO [t1153] VALUES (4133,'مسألة: فان قتله غيره بغير إذنه أساء وعزر لاساءته وافتياته على الامام ولا ضمان عليه لأنه محل غير معصوم وسواء قتله قبل الاستتابة أو بعدها لذلك','5833','3895','0');
INSERT INTO [t1153] VALUES (4134,'مسألة: وان عقل الصبي الاسلام صح اسلامه وردته وعنه يصح اسلامه دون ردته وعنه لا يصح منهما شيء حتى يبلغ','5833','3895','0');
INSERT INTO [t1153] VALUES (4135,'مسألة: وإن أسلم ثم قال: لم أدر ما قلت لم يلتفت إلى قوله وأجبر على الاسلام','5836','3895','0');
INSERT INTO [t1153] VALUES (4136,'مسألة: ولا يقتل حتى يبلغ ويجاوز ثلاثة أ] ام من وقت بلوغه فان ثبت على كفره قتل','5837','3895','0');
INSERT INTO [t1153] VALUES (4137,'مسألة: ومن ارتد وهو سكران لم يقتل حتى يصحو ويتم له ثلاثة أيام من وقت ردته فان مات في سكره مات كافرا وعنه لا تصح ردته','5837','3895','0');
INSERT INTO [t1153] VALUES (4138,'مسألة: وهل تقبل توبة الزنديق ومن تكررت ردته أو من سب الله تعالى أو رسوله أو الساحر؟ على روايتين: إحداهما لا تقبل توبته ويقتل بكل حال، إحداهما لا تقبل توبته ويقتل بكل حال، والأخرى تقبل توبته كغيره','5839','3895','0');
INSERT INTO [t1153] VALUES (4139,'مسألة: وتوبة المرتد إسلامه وهو أن يشهد أن لا إله إلا الله محمدا عبده ورسوله، الا أن تكون ردته بانكار فرض أو احلال محرم، أو جحد نبي أو كتاب، أو إلى دين من يعتقد أن محمدا بعث الى العرب خاصة، فلا يصح إسلامه حتى يقر بما جحده ويشهد أن محمدا بعث الى الع','5843','3895','0');
INSERT INTO [t1153] VALUES (4140,'مسألة: وإذا أتى الكافر بالشهادتين ثم قال لم أرد الاسلام صار بذلك مرتدا ويجبر على الاسلام','5846','3895','0');
INSERT INTO [t1153] VALUES (4141,'مسألة: واذا مات المرتد وارثه بينة أنه صلى بعد الردة حكم باسلامه','5847','3895','0');
INSERT INTO [t1153] VALUES (4142,'مسألة: ولا يبطل إحصان المسلم بردته ولا عباداته التي فعلها في إسلامه إذا عاد الى الاسلام','5850','3895','0');
INSERT INTO [t1153] VALUES (4143,'مسألة: ويقضي ديونه واروش جناياته وينفق على من تلزمه مؤنته','5852','3895','0');
INSERT INTO [t1153] VALUES (4144,'مسألة: وما أتلف من شيء ضمنه ويتخرج في الجماعة الممتنعة ان لا يضمن ما اتلفه','5853','3895','0');
INSERT INTO [t1153] VALUES (4145,'مسألة: واذا أسلم فهل يلزمه قضاء ما ترك من العبادات؟ على روايتين','5854','3895','0');
INSERT INTO [t1153] VALUES (4146,'مسألة: وإذا ارتد الزوجان ولحقا بدار الحرب لم يجز استرقاقهما ولا استرقاق أولادهما الذين ولدوا في الاسلام، ومن لم يسلم منهم قتل ويجوز استرقاق من ولد بعد الردة وهل يقرون على كفرهم؟ على روايتين','5855','3895','0');
INSERT INTO [t1153] VALUES (4147,'مسألة: وأما الذي يعزم على الجن ويزعم أنه يجمعها فتطيعه فلا يكفر ولا يقتل','5865','3895','0');
INSERT INTO [t1153] VALUES (4148,'مسألة: فأما الذي يسحر بالأدوية والتدخين وسقي شيء يضر فلا يكفر ولا يقتل','5865','3895','0');
INSERT INTO [t1153] VALUES (4149,'كتاب الحدود','5868','0','0');
INSERT INTO [t1153] VALUES (4150,'كتاب الحدود مسألة: ولا يجب الحد الا على بالغ عاقل عالم بالتحريم','5868','4149','0');
INSERT INTO [t1153] VALUES (4151,'مسألة: ولا يجب الحد إلا على عالم بالتحريم','5869','4149','0');
INSERT INTO [t1153] VALUES (4152,'مسألة: ولا يجوز أن يقيم الحد إلا الإمام أو نائبه','5870','4149','0');
INSERT INTO [t1153] VALUES (4153,'مسألة: إلا السيد فإن له إقامة الحد بالجلد خاصة على رقيقه القن، وهل له القتل في الردة والقطع في السرقة؟ على روايتين','5870','4149','0');
INSERT INTO [t1153] VALUES (4154,'مسألة: ولا يملك إقامته على من بعضه حر ولا أمته المزوجة','5873','4149','0');
INSERT INTO [t1153] VALUES (4155,'مسألة: فان كان السيد فاسقا أو امرأة فله اقامته في ظاهر كلامه ويحتمل أن لا يملكه','5873','4149','0');
INSERT INTO [t1153] VALUES (4156,'مسألة: ولا يملكه المكاتب لأنه ليس من أهل الولاية، وفيه وجه أنه يملكه','5874','4149','0');
INSERT INTO [t1153] VALUES (4157,'مسألة: وسواء ثبت ببينة أو إقرار','5874','4149','0');
INSERT INTO [t1153] VALUES (4158,'مسألة: وإن ثبت بعلمه فله إقامته نص عليه، ويحتمل أن لا يملكه كالامام','5875','4149','0');
INSERT INTO [t1153] VALUES (4159,'مسألة: ولا يقيم الامام الحد بعلمه','5875','4149','0');
INSERT INTO [t1153] VALUES (4160,'مسألة: ويضرب الرجل قائما','5876','4149','0');
INSERT INTO [t1153] VALUES (4161,'مسألة: ولا تقام الحدود في المساجد','5876','4149','0');
INSERT INTO [t1153] VALUES (4162,'مسألة: ولا يمد ولا يربط ولا يجرد قال ابن مسعود ليس في ديننا مد ولا قيد ولا تجريد','5877','4149','0');
INSERT INTO [t1153] VALUES (4163,'مسألة: ولا يبالغ في ضربه بحيث يشق الجلد','5878','4149','0');
INSERT INTO [t1153] VALUES (4164,'مسألة: والمرأة كذلك فيما ذكرنا من صفة الجلد، إلا أنها تضرب جالسة وتشد عليها ثيابها وتمسك يداها لئلا تنكشف','5878','4149','0');
INSERT INTO [t1153] VALUES (4165,'مسألة: والجلد في الزنا أشد الجلد ثم جلد القذف ثم الشرب ثم التعزير','5878','4149','0');
INSERT INTO [t1153] VALUES (4166,'مسألة: وان رأى الامام الجلد في حد الخمر بالجريد والنعال فله ذلك','5879','4149','0');
INSERT INTO [t1153] VALUES (4167,'مسألة: قال أصحابنا ولا يؤخر الحد للمرض، فإن كان جلدا وخشي عليه من السوط أقيم بأطراف الثياب والعثكول، ويحتمل أن يؤخر للمرض المرجو زواله','5879','4149','0');
INSERT INTO [t1153] VALUES (4168,'مسألة: وإذا مات المحدود في الجلد فالحق قتله ولا يجب على أحد ضمانه جلدا كان أو غيره','5883','4149','0');
INSERT INTO [t1153] VALUES (4169,'مسألة: وان زاد على الحد سوطا أو أكثر فتلف ضمنه وهل يضمن جميع الدية أو نصفها؟ على وجهين؟','5884','4149','0');
INSERT INTO [t1153] VALUES (4170,'مسألة: وإذا كان الحد رجما لم يحفر له رجلا كان أو امرأة في أحد الوجهين','5886','4149','0');
INSERT INTO [t1153] VALUES (4171,'مسألة: واما المرأة فان كان ثبت باقرارها لم يحفر لها، وإن ثبت ببينة حفر لها الى الصدر','5886','4149','0');
INSERT INTO [t1153] VALUES (4172,'مسألة: ويستحب أن يبدأ الشهود بالرجم وإن ثبت بالاقرار استحب أن يبدأ الامام','5887','4149','0');
INSERT INTO [t1153] VALUES (4173,'مسألة: ومتى رجع المقر بالحد عن إقراره قبل منه، وان رجع في اثناء الحد لم يتمم','5888','4149','0');
INSERT INTO [t1153] VALUES (4174,'مسألة: وإن رجم ببينة فهرب لم يترك وإن كان باقرار ترك','5888','4149','0');
INSERT INTO [t1153] VALUES (4175,'مسألة: وأما حقوق الآدميين فتستوفى كلها سواء كان فيها قتل أو لم يكن','5891','4149','0');
INSERT INTO [t1153] VALUES (4176,'مسألة: فان اجتمعت مع حدود الله بدىء بها','5892','4149','0');
INSERT INTO [t1153] VALUES (4177,'مسألة: فان فعل ذلك في الحرم استوفي منه فيه','5898','4149','0');
INSERT INTO [t1153] VALUES (4178,'مسألة: وإن أتى حدا في الغزو ولم يستوف منه في أرض العدو حتى يرجع إلى دار الإسلام فيقام عليه','5899','4149','0');
INSERT INTO [t1153] VALUES (4179,'باب حد الزنا','5901','4149','0');
INSERT INTO [t1153] VALUES (4180,'مسألة: إذا زنى الحر المحصن فحده الرجم حتى يموت، وهل يجلد قبل الرجم؟ على روايتين','5903','4149','0');
INSERT INTO [t1153] VALUES (4181,'مسألة: والمحصن من وطىء امرأته في قبلها في نكاح صحيح وهما بالغان عاقلان حران فان اختل شرط منها فلا إحصان لواحد منهما','5906','4149','0');
INSERT INTO [t1153] VALUES (4182,'مسألة: ويثبت الاحصان للذميين وهل تحصن الذمية مسلما؟ على روايتين','5909','4149','0');
INSERT INTO [t1153] VALUES (4183,'مسألة: وإن كان لرجل ولد من امرأة، فقال ما وطئتها لم يثبت إحصانه ولا يرجم إذا زنى','5910','4149','0');
INSERT INTO [t1153] VALUES (4184,'مسألة: وإن زنى الحر غير المحصن جلد مائة وغرب عاما إلى مسافة القصر وان كان ثيبا','5913','4149','0');
INSERT INTO [t1153] VALUES (4185,'مسألة: وعنه أن المرأة تنفى إلى دون مسافة القصر','5915','4149','0');
INSERT INTO [t1153] VALUES (4186,'مسألة: ويخرج مع المرأة محرمها ليسكنها في موضع، ثم إن شاء رجع إذا أمن عليها، وإن شاء أقام معها حتى يكمل حولها، وإن أبى الخروج معها بذلت له الاجرة','5916','4149','0');
INSERT INTO [t1153] VALUES (4187,'مسألة: وإن كان الزاني رقيقا فحده خمسون جلدة بكل حال ولا يغرب','5918','4149','0');
INSERT INTO [t1153] VALUES (4188,'مسألة: وإن كان نصفه حرا فحده خمس وسبعون جلدة، ويغرب نصف عام ويحتمل أن لا يغرب أما الرجم فلا يجب عليه وإن كان محصنا','5921','4149','0');
INSERT INTO [t1153] VALUES (4189,'مسألة: وحد اللوطي كحد الزاني سواء وعنه حده الرجم بكل حال','5922','4149','0');
INSERT INTO [t1153] VALUES (4190,'مسألة: ومن أتى بهيمة فحده حد اللوطي عند القاضي، واختار الخرقي وأبو بكر أنه يعزر وتقتل البهيمة','5923','4149','0');
INSERT INTO [t1153] VALUES (4191,'مسألة: وكره أحمد أكل لحمها وهل يحرم؟ على وجهين','5925','4149','0');
INSERT INTO [t1153] VALUES (4192,'مسألة: وأقل ذلك تغييب الحشفة في الفرج لأن أحكام الوطء تتعلق به ولا تتعلق بما دونه','5926','4149','0');
INSERT INTO [t1153] VALUES (4193,'مسألة: وإن وطىء دون الفرج فلاحد عليه','5926','4149','0');
INSERT INTO [t1153] VALUES (4194,'مسألة: وان أتت المرأة المرأة فلا حد عليهما','5926','4149','0');
INSERT INTO [t1153] VALUES (4195,'مسألة: أو وجد امرأة نائمة على فراشه ظنها امرأته أو جاريته، أو دعا الضرير امرأته أو جاريته فأجابه غيرها فوطئها فلا حد عليه','5928','4149','0');
INSERT INTO [t1153] VALUES (4196,'مسألة: أو وطىء في نكاه مختلف في صحته، أو وطىء في دبرها أو حيضها أو نفاسها','5929','4149','0');
INSERT INTO [t1153] VALUES (4197,'مسألة: أو أكره على الزنا فلا حد عليه وقال أصحابنا إن أكره الرجل فزنى حد','5930','4149','0');
INSERT INTO [t1153] VALUES (4198,'مسألة: ولا حد على من لم يعلم بتحريم الزنا','5930','4149','0');
INSERT INTO [t1153] VALUES (4199,'مسألة: وان وطىء ميتة أو ملك أمه أو أخته من الرضاع فوطئها فهل يحد أو يعزر؟ على وجهين','5932','4149','0');
INSERT INTO [t1153] VALUES (4200,'مسألة: وإن وطىء في نكاح مجمع على بطلانه، كنكاح المزوجة والمعتدة والخامسة وذوات المحارم من النسب والرضاع فعليه الحد','5932','4149','0');
INSERT INTO [t1153] VALUES (4201,'مسألة: أو استأجر امرأة للزنا أو لغيره فزنى بها أو زنى بامرأة له عليها القصاص، أو بصغيرة أو مجنونة أو بامرأة ثم تزوجها، أو بأمة ثم اشتراها، أو أمكنت العاقلة البالغة من نفسها مجنونا أو صغيرا فوطئها فعليهم الحد','5935','4149','0');
INSERT INTO [t1153] VALUES (4202,'مسألة: ومتى رجع المقر بالحد عن اقراره، قبل منه وقد ذكرنا الخلاف فيه، والله أعلم','5944','4149','0');
INSERT INTO [t1153] VALUES (4203,'مسألة: وإن جاء بعضهم بعد أن قام الحاكم، أو شهد ثلاثة وامتنع الرابع من الشهادة، أو لم يكملها فهم قذفة وعليهم الحد','5947','4149','0');
INSERT INTO [t1153] VALUES (4204,'مسألة: وان كانوا فساقا أو عميانا أوبعضهم، فعليهم الحد، وعنه أنه لا حد عليهم','5949','4149','0');
INSERT INTO [t1153] VALUES (4205,'مسألة: وإن كان أحدهم زوجا حد الثلاثة، ولا عن الزوج إن شاء','5949','4149','0');
INSERT INTO [t1153] VALUES (4206,'مسألة: وإن شهد اثنان أنه زنى بها في بيت أو بلد، واثنان أنه زنى بها في بيت أو بلد آخر، فهم قذفة وعليهم الحد، وعنه يحد المشهود عليه، وهو بعيد','5950','4149','0');
INSERT INTO [t1153] VALUES (4207,'مسألة: وإن شهد اثنان أنه زنى بها في زاوية بيت وشهد اثنان أنه زنى بها في زاوية منه أخرى، كملت شهادتهم إن كانت الزاويتان متقاربتين، وحد المشهود عليه','5951','4149','0');
INSERT INTO [t1153] VALUES (4208,'مسألة: وإن شهد اثنان أنه زنى بها في قميص أبيض، وشهد آخران أنه زنى بها في قميص أحمر، كملت شهادتهم، ويحتمل أن لا تكمل، كما لو شهد كل اثنين أنه زنى بها في بيت غير الذي شهد به صاحباهما','5952','4149','0');
INSERT INTO [t1153] VALUES (4209,'مسألة: وإن شهدا أنه زنى بها مطاوعة، وشهد آخران أنه زنى بها مكرهة، فلا حد عليها إجماعا، لأن الشهادة لم تكمل على فعل موجب للحد، وفي الرجل وجهان','5953','4149','0');
INSERT INTO [t1153] VALUES (4210,'مسألة: وهل يحد الجميع أو شاهدا المطاوعة؟ على وجهين؟','5953','4149','0');
INSERT INTO [t1153] VALUES (4211,'مسألة: وإن شهد اربعة فرجع احدهم، فلا شيء على الراجع ويحد الثلاثة، وإن كان رجوعه بعد الحد، فلا حد على الثلاثة، ويغرم الرابع ربع ما أتلفوه','5954','4149','0');
INSERT INTO [t1153] VALUES (4212,'مسألة: وإن شهد أربعة بالزنا بامرأة، ثقات من النساء أنها عذراء، فلا حد عليها ولا الشهود نص عليه','5956','4149','0');
INSERT INTO [t1153] VALUES (4213,'مسألة: وإن شهد أربعة أنه زنى بامرأة، وشهد أربعة آخرون أنهم هم الزناة بها لم يحد المشهود عليه، وهل يحد الشهود الأولون حد الزنا؟ على روايتين','5957','4149','0');
INSERT INTO [t1153] VALUES (4214,'مسألة: وإن حملت امرأة لا زوج لها ولا سيد، لم تحد بذلك بمجرده، لكنها تسأل، فان ادعت أنها أكرهت ووطئت بشبهة، أو لم تعترف بالزنا لم تحد','5958','4149','0');
INSERT INTO [t1153] VALUES (4215,'باب القذف','5960','4149','0');
INSERT INTO [t1153] VALUES (4216,'مسألة: ومن قذف حرا محصنا فعليه جلد ثمانين جلدة إن كان القاذف حرا، وأربعين إن كان عبدا، وقذف غير المحصن يوجب التعزير','5960','4149','0');
INSERT INTO [t1153] VALUES (4217,'مسألة: والمحصن هو الحر المسلم العاقل العفيف الذي يجامع مثله، وهل يشترط البلوغ؟ على روايتين؟','5961','4149','0');
INSERT INTO [t1153] VALUES (4218,'مسألة: وقذف غير المحصن يوجب التعزير، فاذا قذف مشتركا أو عبدا، أو مسلما له دون عشر سنين، أو مسلمة لها دون تسع، أو من ليس بعفيف، فعليه التعزير','5962','4149','0');
INSERT INTO [t1153] VALUES (4219,'مسألة: وإن قال: زنيت وانت صغيرة، وفسره بصغر عن تسع لم يحد، والا خرج على روايتين','5964','4149','0');
INSERT INTO [t1153] VALUES (4220,'مسألة: وإن قال لحرة مسلمة: زنيت وانت نصرانية، أو أمة، ولم تكن كذلك، فعليه الحد','5965','4149','0');
INSERT INTO [t1153] VALUES (4221,'مسألة: ومن قذف محصنا، فزال إحصانه قبل إقامة الحد عليه، لم يسقط الحد عن القاذف','5966','4149','0');
INSERT INTO [t1153] VALUES (4222,'مسألة: وإن أتت بولد يخالف لونه لونهما، لم يبح نفيه بذلك، وقال أبو الخطاب: ظاهر كلامه اباحته','5968','4149','0');
INSERT INTO [t1153] VALUES (4223,'مسألة: وإن قال: يا لوطي، أو يا معفوج، فهو صريح في المنصوص عن أحمد، وعليه الحد','5970','4149','0');
INSERT INTO [t1153] VALUES (4224,'مسألة: فان قال: اردت بقولي: يا لوطي، انك تعمل عمل قوم لوط، فقال الخرقي، لا حد عليه وهو بعيد','5971','4149','0');
INSERT INTO [t1153] VALUES (4225,'مسألة: فان قال: اردت أنك تعمل عمل قوم لوط، غير إتيان الرجال. احتمل وجهين','5971','4149','0');
INSERT INTO [t1153] VALUES (4226,'مسألة: وإن قال: لست بولد فلان، فقد قذف أمه','5972','4149','0');
INSERT INTO [t1153] VALUES (4227,'مسألة: وإن قال: لست بولدي، فعلى وجهين','5973','4149','0');
INSERT INTO [t1153] VALUES (4228,'مسألة: وإن قال: أنت أزنى الناس، أو ازنى من فلانة، فهو قاذف له لأنه أضاف إليه الزنا بصفة المبالغة، وهذا قول أبي بكر','5973','4149','0');
INSERT INTO [t1153] VALUES (4229,'مسألة: وإن قال لرجل: يا زانية، أو لامرأة: يا زان، أو قال: زنت يداك ورجلاك، فهو صريح في القذف في قول أبي بكر، وليس بصريح عند ابن حامد','5974','4149','0');
INSERT INTO [t1153] VALUES (4230,'مسألة: وإن قال: زنأت في الجبل مهموزا، فهو صريح عند أبي بكر، وقال ابن حامد: إن كان يعرف العربية فليس بصريح','5974','4149','0');
INSERT INTO [t1153] VALUES (4231,'مسألة: والكنايات نحو قوله لامرأته: قد فضحته وغطيت أو نكست رأسه، وجعلت له قرونا، وعلقت عليه أولادا من غيره، وأفسدت فراشه، أو يقول لمن يخاصمه: يا حلال ابن الحلال، ما يعرفك الناس بالزنا يا عفيفة يا فاجرة، يا قحبة، يا خبيثة، أو يقول لعربي: يا نبطي، يا','5976','4149','0');
INSERT INTO [t1153] VALUES (4232,'مسألة: أو يسمع رجلا يقذف فيقول: صدقت، أو أخبرني فلان أنك زنيت وكذبه الآخر، فهو كناية إذا فسره بما يحتمله غير القذف، قبل في قوله في أحد الوجهين، وفي الآخر صريح','5978','4149','0');
INSERT INTO [t1153] VALUES (4233,'مسألة: وإن قذف أهل بلد، أو جماعة لا يتصور الزنا من جميعهم، عزر ولم يحد','5979','4149','0');
INSERT INTO [t1153] VALUES (4234,'مسألة: وإن قال لامرأته: يا زانية، قالت: بك زنيت، لم تكن قاذفة','5979','4149','0');
INSERT INTO [t1153] VALUES (4235,'مسألة: وإن قال لرجل: اقذفني، فقذفه، فهل يحد أو يعزر؟ على وجهين','5979','4149','0');
INSERT INTO [t1153] VALUES (4236,'مسألة: وإذا قذفت المرأة، لم يكن لولدها المطالبة إذا كانت الأم في الحياة، وإن قذفت وهي ميتة، مسلمة كانت أو كافرة، أو أمة، إذا طالب الابن، وكان حرا مسلما، ذكره الخرقي، وقال أبو بكر: لا يجب الحد بقذف ميتة','5980','4149','0');
INSERT INTO [t1153] VALUES (4237,'مسألة: وإن مات المقذوف سقط الحد عن القاذف','5981','4149','0');
INSERT INTO [t1153] VALUES (4238,'مسألة: وإن قذف أم النبي صلى الله عليه وسلم قتل، مسلما كان أو كافرا','5981','4149','0');
INSERT INTO [t1153] VALUES (4239,'مسألة: ومن قذف الجماعة بكلمة واحدة، فحد واحد إذا طالبوا أو واحد منهم، وعنه إن طالبوا متفرقين، حد لكل واحد حدا','5982','4149','0');
INSERT INTO [t1153] VALUES (4240,'مسألة: وإن قذفهم بكلمات حد لكل واحد حدا','5983','4149','0');
INSERT INTO [t1153] VALUES (4241,'مسألة: وإن حد للقذف فأعاده، لم يعد عليه الحد، أما إذا قذف رجل مرات ولم يحد، فحد واحد رواية واحدة','5984','4149','0');
INSERT INTO [t1153] VALUES (4242,'باب القطع في السرقة','5987','4149','0');
INSERT INTO [t1153] VALUES (4243,'مسألة: ولا يجب إلا بسبعة شروط: احدها السرقة، وهي أخذ المال على وجه الاختفاء، ومنه استرق السمع ومسارقة النظر، إذا كان يستخفي بذلك','5987','4149','0');
INSERT INTO [t1153] VALUES (4244,'مسألة: ولا قطع على منتهب، ولا مختلس، ولا غاصب، ولا خائن، ولا جاحد وديعة، ولا عارية، وعنه يقطع جاحد العارية','5987','4149','0');
INSERT INTO [t1153] VALUES (4245,'مسألة: ويقطع الطرار، وهو الذي يبط الجيب وغيره ويأخذ منه، وعنه ولا يقطع','5989','4149','0');
INSERT INTO [t1153] VALUES (4246,'مسألة: ويقطع بسرقة العبد الصغير في قول عامة أهل العلم','5991','4149','0');
INSERT INTO [t1153] VALUES (4247,'مسألة: ولا يقطع بسرقة حر وإن كان صغيرا، وعنه أنه يقطع بسرقة الصغير','5992','4149','0');
INSERT INTO [t1153] VALUES (4248,'مسألة: فإن كان عليه حلي أو ثياب تبلغ نصابا، لم يقطع وبه قال أبو حنيفة وأكثر أصحاب الشافعي','5993','4149','0');
INSERT INTO [t1153] VALUES (4249,'مسألة: ولا يقطع بسرقة مصحف وعند أبي الخطاب يقطع','5994','4149','0');
INSERT INTO [t1153] VALUES (4250,'مسألة: ويقطع بسرقة سائر كتب العلم','5994','4149','0');
INSERT INTO [t1153] VALUES (4251,'مسألة: ولا يقطع بسرقة آلة لهو ولا محرم كالخمر','5995','4149','0');
INSERT INTO [t1153] VALUES (4252,'مسألة: وإن سرق آنية فيها الخمر، أو صليبا أو صنم ذهب لم يقطع، وعند أبي الخطاب يقطع','5996','4149','0');
INSERT INTO [t1153] VALUES (4253,'مسألة: وإن سرق نصابا ثم نقصت قيمته، أو ملكه ببيع أو هبة، أو غيرهما لم يسقط القطع','6000','4149','0');
INSERT INTO [t1153] VALUES (4254,'مسألة: وإن ملك العين المسروقة بهبة أو بيع، أو غير ذلك من أسباب الملك، وكان ملكها قبل رفعه إلى الحاكم والمطالبة بها عنده لم يجب القطع','6001','4149','0');
INSERT INTO [t1153] VALUES (4255,'مسألة: إن دخل الحرز فذبح شاة قيمتها نصاب، فنقصت عن النصاب ثم أخرجها، لم يقطع','6001','4149','0');
INSERT INTO [t1153] VALUES (4256,'مسألة: وإن سرق فرد خف منفردا درهمان، وقيمته مع الآخر أربعة، لم يقطع','6002','4149','0');
INSERT INTO [t1153] VALUES (4257,'مسألة: وإن اشتركوا في سرقة نصاب قطعوا، سواء أخرجوه جملة، أو أخرج كل واحد جزءا','6002','4149','0');
INSERT INTO [t1153] VALUES (4258,'مسألة: وإن هتك اثنان حرزا ودخلاه، فأخرج أحدهما نصابا وحده، أو دخل أحدهما فقدمه إلى باب النقب، وأدخل الآخر يده فأخرجه، قطعا','6003','4149','0');
INSERT INTO [t1153] VALUES (4259,'مسألة: فإن نقبا حرزا، فدخل أحدهما فقرب المتاع من النقب، وأدخل الآخر يده فأخرجه، فقال أصحابنا: قياس قول أحمد أن القطع عليهما','6004','4149','0');
INSERT INTO [t1153] VALUES (4260,'مسألة: وإن رماه الداخل إلى خارج، فأخذه الآخر، فالقطع على الداخل وحده','6004','4149','0');
INSERT INTO [t1153] VALUES (4261,'مسألة: إلا أن ينقب أحدهما ويذهب، فيأتي الآخر من غير علم فيسرق، فلا قطع','6005','4149','0');
INSERT INTO [t1153] VALUES (4262,'مسألة: وإن نقب أحدهما، ودخل الآخر فأخرجه، فلا قطع عليهما، ويحتمل أن يقطعا','6005','4149','0');
INSERT INTO [t1153] VALUES (4263,'مسألة: فان سرق من غير حرز فلا قطع عليه، لقوات شرطه، مثل أن يجد حرزا مهتوكا، أو بابا مفتوحا فيأخذ منه، فلا قطع عليه لذلك','6006','4149','0');
INSERT INTO [t1153] VALUES (4264,'مسألة: فان دخل الحرز، فأتلف فيه نصابا ولم يخرجه، فلا قطع عليه','6006','4149','0');
INSERT INTO [t1153] VALUES (4265,'مسألة: وإن ابتلع جوهرا أو ذهبا فخرج به، أو نقب ودخل فترك المتاع على بهيمة فخرجت به، أو في ماء جار فأخرجه، أو قال لصغير أو معتوه: ادخل فأخرجه، ففعل، فعليه القطع','6006','4149','0');
INSERT INTO [t1153] VALUES (4266,'مسألة: والحرز ما جرت العادة بحفظ المال فيه، ويختلف باختلاف الاموال والبلدان، وعدل السلطان وجوره، وقوته وضعفه','6010','4149','0');
INSERT INTO [t1153] VALUES (4267,'مسألة: وحرز البقل والباقلا. ونحوه وقدوره وراء الشرائج، إذا كان في السوق حارس. والشرائج تكون من القصب والخشب','6011','4149','0');
INSERT INTO [t1153] VALUES (4268,'مسألة: وحرز المواشي الصير، وحرزها في المراعي بالراعي ونظره إليها','6012','4149','0');
INSERT INTO [t1153] VALUES (4269,'مسألة: وحرز حمولة الابل بتقطيرها، وقائدها وسائقها إذا كان يراها','6012','4149','0');
INSERT INTO [t1153] VALUES (4270,'مسألة: وحرز الخشب والحطب الحظائر','6012','4149','0');
INSERT INTO [t1153] VALUES (4271,'مسألة: وحرز الثياب في الحمام بالحافظ، فان سرق من الحمام ولا حافظ فيه، فلا قطع عليه في قول عامتهم','6013','4149','0');
INSERT INTO [t1153] VALUES (4272,'مسألة: وحرز الكفن في القبر على الميت، فلو نبش قببرا وأخذ الكفن قطع','6015','4149','0');
INSERT INTO [t1153] VALUES (4273,'مسألة: وحرز الباب تركيبه في موضعه','6018','4149','0');
INSERT INTO [t1153] VALUES (4274,'مسألة: فلو سرق رتاج الكعبة، أو باب مسجد، أو تأزيره، قطع','6018','4149','0');
INSERT INTO [t1153] VALUES (4275,'مسألة: وإن سرق قناديل المسجد، أو حصره، فعلى وجهين','6018','4149','0');
INSERT INTO [t1153] VALUES (4276,'مسألة: وإن نام إنسان على ردائه في المسجد، فسرقه سارق، قطع','6019','4149','0');
INSERT INTO [t1153] VALUES (4277,'مسألة: وإن سرق من السوق غزلا، وثم حافظ، قطع، لأن حرزه بحافظه، فاذا سرقه قطع، كما يقطع بسرقة الثياب من الحمام، إذا كان ثم حافظ','6019','4149','0');
INSERT INTO [t1153] VALUES (4278,'مسألة: ومن سرق من النخل أو الشجر من غير حرز، فلا قطع عليه، ويضمن عوضهما مرتين','6019','4149','0');
INSERT INTO [t1153] VALUES (4279,'مسألة: قال أبو بكر: ما كان حرزا لمال، فهو حرز لمال آخر، قياسا لأحدهما على الآخر، والصحيح خلاف ذلك. لأنا إنما رجعنا في الحرز إلى العرف والعادة، أن الجواهر والدراهم والدنانير لا تحرز في الصير والحظائر، ومن أحرزها أو نحوها في ذلك عد مفرطا، فكان العم','6021','4149','0');
INSERT INTO [t1153] VALUES (4280,'مسألة: ولا يقطع العبد بالسرقة من مال سيده في قول الجميع، ووافقهم أبو ثور فيه وحكي عن داود أنه يقطع لعموم الآية','6024','4149','0');
INSERT INTO [t1153] VALUES (4281,'مسألة: ولا يقطع مسلم بالسرقة من بيت المال','6025','4149','0');
INSERT INTO [t1153] VALUES (4282,'مسألة: ولا يقطع بالسرقة من مال له فيه شرك، أو لأحد ممن لا يقطع بالسرقة منه','6026','4149','0');
INSERT INTO [t1153] VALUES (4283,'مسألة: ومن سرق من الغنيمة ممن له حق، أو لولده، أو لسيده لم يقطع. لما ذكرنا من المسألة قبلها','6027','4149','0');
INSERT INTO [t1153] VALUES (4284,'مسألة: وهل يقطع أحد الزوجين بالسرقة من مال الآخر المحرز عنه؟ على روايتين','6027','4149','0');
INSERT INTO [t1153] VALUES (4285,'مسألة: ويقطع سائر الاقارب بالسرقة من مال أقاربهم، كالإخوة والأخوات ومن عداهم','6028','4149','0');
INSERT INTO [t1153] VALUES (4286,'مسألة: ويقطع المسلم بالسرقة من مال الذمي والمستأمن، ويقطعان بسرقة ماله','6028','4149','0');
INSERT INTO [t1153] VALUES (4287,'مسألة: ومن سرق عينا وادعى أنها ملكه لم يقطع، وعنه يقطع، وعنه لا يقطع، إلا أن يكون معروفا بالسرقة','6028','4149','0');
INSERT INTO [t1153] VALUES (4288,'مسألة: وإذا سرق المسروق منه مال السارق، أو المغصوب منه مال الغاصب، من الحرز الذي فيه العين المسروقة، أو المغصوبة، لم يقطع، وإن سرق من غير ذلك، أو سرق من مال من له عليه دين قطع؟، إلا أن يعجز عن أخذه منه، فيسرق قدر حقه فلا يقطع، وقال القاضي: يقطع','6029','4149','0');
INSERT INTO [t1153] VALUES (4289,'مسألة: ومن أجر داره أو أعارها، ثم سرق منها مال المستعير أو المستأجر قطع','6031','4149','0');
INSERT INTO [t1153] VALUES (4290,'مسألة: وإن استعار دارا، فنقبها المعير وسرق مال المستعير منها، قطع أيضا','6032','4149','0');
INSERT INTO [t1153] VALUES (4291,'مسألة: ولا ينزع عن إقراره حتى يقطع','6035','4149','0');
INSERT INTO [t1153] VALUES (4292,'مسألة: وإذا وجب القطع، قطعت يده اليمنى من مفصل الكف وحسمت، وهو أن تغمس في زيت مغلي، فان عاد، قطعت رجله اليسرى من مفصل الكف وحسمت','6039','4149','0');
INSERT INTO [t1153] VALUES (4293,'مسألة: فان عاد حبس ولم يقطع، وعنه أنه تقطع يده اليسرى في الثالثة، ورجله اليمنى في الرابعة','6042','4149','0');
INSERT INTO [t1153] VALUES (4294,'مسألة: ومن سرق وليس له يد يمنى، قطعت رجله اليسرى، وإن سرق وله يمنى فذهبت، سقط القطع، وإن ذهبت يده اليسرى، لم تقطع اليمنى على الرواية الأولى وتقطع على الأخرى','6043','4149','0');
INSERT INTO [t1153] VALUES (4295,'مسألة: وان سرق وله يمنى فذهبت، سقط القطع','6044','4149','0');
INSERT INTO [t1153] VALUES (4296,'مسألة: وان ذهبت يده اليسرى، أو كانت مقطوعة، أو شلاء، أو مقطوعة الاصابع، أو شلت قبل قطع يمناه، لم تقطع يمناه على الرواية الأولى، وتقطع على الثانية','6044','4149','0');
INSERT INTO [t1153] VALUES (4297,'مسألة: وإذا وجب قطع يمناه، فقطع القاطع يسراه بدلا عن يمينه أجزأت، ولا شيء على القاطع إلا الأدب','6045','4149','0');
INSERT INTO [t1153] VALUES (4298,'مسألة: ويجتمع القطع الضمان فترد العين المسروقة الى مالكها، وإن كانت تالفة غرم قيمتها وقطع','6046','4149','0');
INSERT INTO [t1153] VALUES (4299,'مسألة: وهل يجب الزيت الذي يحسم به من بيت المال، أو من مال السارق؟ على وجهين','6048','4149','0');
INSERT INTO [t1153] VALUES (4300,'باب حد المحاربين وهم قطاع الطريق','6049','4149','0');
INSERT INTO [t1153] VALUES (4301,'مسألة: وهم الذين يعرضون للناس بالسلاح في الصحراء فيغضبونهم المال مجاهرة، فأما من يأخذه على وجه السرقة فليس بمحارب','6050','4149','0');
INSERT INTO [t1153] VALUES (4302,'مسألة: وإن فعلوا ذلك في البنيان، لم يكونوا محاربين','6050','4149','0');
INSERT INTO [t1153] VALUES (4303,'مسألة: فاذا قدر عليهم، فمن كان منهم قد قتل من يكافئه وأخذ المال، قتل حتما وصلب حتى يشتهر، وقال أبو بكر: يصلب قدر ما يقع عليه اسم الصلب، وعن أحمد أنه يقطع مع ذلك','6052','4149','0');
INSERT INTO [t1153] VALUES (4304,'مسألة: وان قتل من لا يكافئه، فهل يقتل، على روايتين','6053','4149','0');
INSERT INTO [t1153] VALUES (4305,'مسألة: وان جنى جناية توجب القصاص فيما دون النفس، فهل يتحتم استيفاؤه؟ على روايتين','6055','4149','0');
INSERT INTO [t1153] VALUES (4306,'مسألة: وحكم الردء حكم المباشر','6055','4149','0');
INSERT INTO [t1153] VALUES (4307,'مسألة: ومن قتل ولم يأخذ المال، قتل، وهل يصلب؟ على روايتين','6058','4149','0');
INSERT INTO [t1153] VALUES (4308,'مسألة: ومن أخذ المال ولم يقتل، قطعت يده اليمنى ورجله اليسرى في مقام واحد وحسمتا','6058','4149','0');
INSERT INTO [t1153] VALUES (4309,'مسألة: ولا يقطع منهم إلا من أخذ ما يقطع السارق في مثله','6059','4149','0');
INSERT INTO [t1153] VALUES (4310,'مسألة: فان كانت يمينه مقطوعة، أو مستحقة في قصاص، أو شلاء، قطعت رجله اليسرى، وهل تقطع يسرى يديه؟ ينبني على الرويتين في قطع يسرى السارق في المرة الثالثة','6060','4149','0');
INSERT INTO [t1153] VALUES (4311,'مسألة: ومن لم يقتل ولا أخذ المال، نفي وشرد، فلا يترك يأوي إلى بلد، وعنه إن نفيه تعزيره بما يردعه','6060','4149','0');
INSERT INTO [t1153] VALUES (4312,'مسألة: ومن تاب منهم قبل القدرة عليه سقطت عنه حدود الله تعالى من الصلب والقطع والنفي وانحتام القتل، وأخذوا بحقوق الآدميين من الأنفس والجراح والأموال، إلا أن يعفى له عنها','6061','4149','0');
INSERT INTO [t1153] VALUES (4313,'مسألة: ومن وجب عليه حد سوى ذلك، فتاب قبل إقامته لم يسقط عنه، وعنه أنه يسقط بمجرد التوبة قبل إصلاح العمل','6062','4149','0');
INSERT INTO [t1153] VALUES (4314,'مسألة: ومن مات وعليه حد سقط عنه لفوات محله، كما يسقط غسل ما ذهب من أعضاء الطهارة في الوضوء والغسل','6062','4149','0');
INSERT INTO [t1153] VALUES (4315,'مسألة: فان أريدت نفسه، لم يلزمه الدفع','6064','4149','0');
INSERT INTO [t1153] VALUES (4316,'مسألة: وإن عض انسان انسانا، فانتزع يده من فيه، فسقطت ثناياه ذهبت هدرا','6067','4149','0');
INSERT INTO [t1153] VALUES (4317,'مسألة: وإن نظر في بيته من خصاص الباب، أو نحوه، فحذف عينه ففقأها، فلا شيء عليه','6069','4149','0');
INSERT INTO [t1153] VALUES (4318,'باب حد المسكر','6073','4149','0');
INSERT INTO [t1153] VALUES (4319,'كل شراب أسكر كثيره فقليله حرام، من أي شي كان ويسمى خمرا، حكمه حكم عصير العنب في تحريمه، ووجوب الحد على شاربه','6075','4149','0');
INSERT INTO [t1153] VALUES (4320,'ولا يجوز شربه للذة، ولا للتداوي، ولا لعطش، ولا غيره، إلا أن يضطر إليه لدفع لقمة غص بها، فيجوز','6077','4149','0');
INSERT INTO [t1153] VALUES (4321,'ومن شربه مختارا عالما أن كثيره يسكر، قليلا كان أو كثيرا، فعليه الحد ثمانين جلدة، وعنه أربعون','6078','4149','0');
INSERT INTO [t1153] VALUES (4322,'والرقيق على النصف من ذلك','6081','4149','0');
INSERT INTO [t1153] VALUES (4323,'والذمي لا يحد بشربه في الصحيح عنه','6082','4149','0');
INSERT INTO [t1153] VALUES (4324,'وهل يجب الحد بوجود الرائحة؟ على روايتين','6082','4149','0');
INSERT INTO [t1153] VALUES (4325,'والعصير إذا أتت عليه ثلاثة أيام حرم، إلا أن يغلى قبل ذلك فيحرم، نص عليه','6086','4149','0');
INSERT INTO [t1153] VALUES (4326,'وقال أبو الخطاب: عندي أن كلام أحمد في ذلك محمول على عصير، الغالب أنه يتخمر في ثلاثة أيام','6087','4149','0');
INSERT INTO [t1153] VALUES (4327,'ولا يكره أن يترك في الماء تمر، أو زبيب ونحوه ليأخذ ملوحته، ما لم يشتد أو يأتي عليه ثلاث','6087','4149','0');
INSERT INTO [t1153] VALUES (4328,'ولا يكره الانتباذ في الدباء والحنتم والنقير والمزفت','6088','4149','0');
INSERT INTO [t1153] VALUES (4329,'ويكره الخليطان وهو أن ينبذ شيئين كالتمر والزبيب','6089','4149','0');
INSERT INTO [t1153] VALUES (4330,'وإن قتله اثنان فسلبه غنيمة','6090','4149','0');
INSERT INTO [t1153] VALUES (4331,'ولا بأس بالفقاع، وبه قال إسحاق وابن المنذر، قال شيخنا: ولا أعلم فيه خلافا، لأنه لا يسكر وإذا ترك يفسد بخلاف الخمر، والأشياء على الإباحة، ما لم يرد بتحريمها حجة','6091','4149','0');
INSERT INTO [t1153] VALUES (4332,'ومن وطئ جارية امرأته، فعليه الحد إلا أن تكون قد احلتها له، فيجلد مائة وهل يلحقه نسب ولدها؟ على روايتين','6095','4149','0');
INSERT INTO [t1153] VALUES (4333,'باب التعزير','6095','4149','0');
INSERT INTO [t1153] VALUES (4334,'وهل يلحقه نسب ولدها إذا حملت من هذا الوطء؟ على روايتين','6098','4149','0');
INSERT INTO [t1153] VALUES (4335,'ولا يسقط الحد بالإباحة في غير هذا الوضع','6098','4149','0');
INSERT INTO [t1153] VALUES (4336,'ولا يزاد في التعزير على عشر جلدات في غير هذا الموضع','6099','4149','0');
INSERT INTO [t1153] VALUES (4337,'وإن استمنى بيده لغير حاجة عزر','6110','4149','0');
INSERT INTO [t1153] VALUES (4338,'وهو فرض كفاية، إذا قام به قوم سقط عن الباقين','6111','4149','0');
INSERT INTO [t1153] VALUES (4339,'ولا يجب إلا على ذكر حر مكلف مستطيع، وهو الصحيح الواجد لزاده، وما يحمله إذا كان بعيدا','6113','4149','0');
INSERT INTO [t1153] VALUES (4340,'وأقل ما يفعل مرة في كل عام، إلا أن تدعو الحاجة إلى تأخيره','6115','4149','0');
INSERT INTO [t1153] VALUES (4341,'وأفضل ما يتطوع به الجهاد','6116','4149','0');
INSERT INTO [t1153] VALUES (4342,'وغزو البحر أفضل من غزو البر','6117','4149','0');
INSERT INTO [t1153] VALUES (4343,'ويقاتل كل قوم من يليهم من العدو','6118','4149','0');
INSERT INTO [t1153] VALUES (4344,'وتمام الرباط أربعون يوما وهو لزوم الثغر للجهاد','6120','4149','0');
INSERT INTO [t1153] VALUES (4345,'ولا يستحب نقل أهله إليه، وقال رسول الله صلى الله عليه وسلم: رباط في سبيل الله خير من ألف يوم فيما سواه من المنازل','6124','4149','0');
INSERT INTO [t1153] VALUES (4346,'وتجب الهجرة على من يعجز عن إظهار دينه في دار الحرب، وتستحب لمن قدر عليه','6125','4149','0');
INSERT INTO [t1153] VALUES (4347,'وما أخذوا من دار الحرب من ركاز أو مباح له قيمة فهو غنيمة','6128','4149','0');
INSERT INTO [t1153] VALUES (4348,'ولا يجاهد من عليه دين لا وفاء له، ومن أحد أبويه مسلم إلا بإذن غريمه وأبيه، إلا أن يتعين عليه الجهاد، فإنه لا طاعة لهما في ترك فريضة','6128','4149','0');
INSERT INTO [t1153] VALUES (4349,'ويغزى مع كل بر وفاخر','6129','4149','0');
INSERT INTO [t1153] VALUES (4350,'ولا يجوز للمسلمين الفرار من ضعفهم، إلا متحرفين لقتال أو متحيزين إلى فئة، فإن زاد الكفار فلهم الفرار، ألا أن يغلب على ظنهم الظفر','6131','4149','0');
INSERT INTO [t1153] VALUES (4351,'فإن القي في مركبهم نار فاشتعلت فيه، فالذي يغلب على ظنهم السلامة فيه من المقام، أو إلقاء، أنفسهم في الماء، فالأولى لهم فعله وإن استوى عندهم الأمران، فقال أحمد رحمه الله: كيف شاء صنع','6135','4149','0');
INSERT INTO [t1153] VALUES (4352,'ولا يجوز إحراق نحل ولا تغرقه','6136','4149','0');
INSERT INTO [t1153] VALUES (4353,'ولا يجوز عقر دابة ولا ذبح شاة، إلا لأكل يحتاج إليه','6137','4149','0');
INSERT INTO [t1153] VALUES (4354,'وفي حرق شجرهم وزرعهم وقطعه روايتان: إحداهما','6140','4149','0');
INSERT INTO [t1153] VALUES (4355,'وإذا ظفر بهم، لم يقتل صبي ولا امرأة، ولا راهب ولا شيخ فان ولا أعمى، لا رأي لهم إلا أن يقاتلوا','6142','4149','0');
INSERT INTO [t1153] VALUES (4356,'فإن تترسوا بهم جاز رميهم ويقصد المقاتلة','6147','4149','0');
INSERT INTO [t1153] VALUES (4357,'وإن تترسوا بالمسلمين لم يجز رميهم، إلا أن يخاف على المسلمين فيرميهم، ويقصد الكفار','6147','4149','0');
INSERT INTO [t1153] VALUES (4358,'ومن أسر أسيرا، لم يجز له قتله حتى يأتي به الإمام إلا أن يمتنع من السير معه، ولا يمكنه إكراهة','6148','4149','0');
INSERT INTO [t1153] VALUES (4359,'ويخير الأمير في الأسرى بين القتل والاسترقاق، والمن، والفداء بمسلم أو بمال، وعنه لا يجوز بمال إلا غير الكتابي، ففي استرقاقه روايتان، ولا يجوز أن يختار إلا الأصلح للمسلمين','6150','4149','0');
INSERT INTO [t1153] VALUES (4360,'ومن سبي من أطفالهم منفردا، أو مع أحد أبويه فهو مسلم ومن سبي مع أبويه فهو على دينهما','6155','4149','0');
INSERT INTO [t1153] VALUES (4361,'فإن اسلموا رقوا في الحال','6155','4149','0');
INSERT INTO [t1153] VALUES (4362,'ولا ينفسخ النكاح باسترقاق الزوجين، وإن سبيت المرأة وحدها انفسخ نكاحها وحلت لسابيها','6157','4149','0');
INSERT INTO [t1153] VALUES (4363,'ولا يفرق في البيع بين ذوي رحم محرم، إلا بعد البلوغ على إحدى الروايتين','6160','4149','0');
INSERT INTO [t1153] VALUES (4364,'وهل يجوز بيع من استرق منهم للمشركين؟ على روايتين','6160','4149','0');
INSERT INTO [t1153] VALUES (4365,'وإذا حصر الإمام حصنا، لزمه مصابرته إذا رأى المصلحة فيها','6164','4149','0');
INSERT INTO [t1153] VALUES (4366,'ومن أسلم منهم، أحرز دمه وماله وأولاده الصغار','6165','4149','0');
INSERT INTO [t1153] VALUES (4367,'وإن سألوا الموادعة بمال أو غيره جاز إن كانت المصلحة فيه، وقد ذكرنا ذلك','6167','4149','0');
INSERT INTO [t1153] VALUES (4368,'وإن نزلوا على حكم حاكم، جاز إذا كان حرا مسلما بالغا عاقلا من أهل الإجتهاد','6167','4149','0');
INSERT INTO [t1153] VALUES (4369,'ولا يحكم إلا بما فيه الحظ للمسلمين من القتل والسبي والفداء فإن حكم بالمن لزم قبوله في أحد الوجهين','6168','4149','0');
INSERT INTO [t1153] VALUES (4370,'وإن حكم بقتل أو سبي فأسلموا، عصموا دماءهم وفي استرقاقهم وجهان','6169','4149','0');
INSERT INTO [t1153] VALUES (4371,'باب ما يلزم الإمام والجيش','6170','4149','0');
INSERT INTO [t1153] VALUES (4372,'ويمنع المخذل والمرجف','6170','4149','0');
INSERT INTO [t1153] VALUES (4373,'ويمنع النساء إلا طاعنة في السن، لسقي الماء ومعالجة الجرحى','6171','4149','0');
INSERT INTO [t1153] VALUES (4374,'ولا يستعين بمشرك إلا عند الحاجة إليه','6172','4149','0');
INSERT INTO [t1153] VALUES (4375,'ويرفق بهم في المسير، فيسير بهم سير أضعفهم لئلا يشق عليهم، فإن دعت الحاجة إلى الجد في السير جاز','6174','4149','0');
INSERT INTO [t1153] VALUES (4376,'ويجوز أن يبذل جعلا لمن يدله على طريق أو قلعه أو ماء، ويجب أن يكون معلوما، إلا أن يكون من مال الكفار فيجوز أن يكون مجهولا','6177','4149','0');
INSERT INTO [t1153] VALUES (4377,'فإن شرط له جارية معينة على قلعة يفتحها، نحو أن يشرط بنت فلان من أهل القلعة، لم يستحق شيئا حتى يفتح القلعة','6178','4149','0');
INSERT INTO [t1153] VALUES (4378,'وإن فتحت صلحا ولم يشترطوا الجارية فله قيمتها إن رضي بها وإن أبى إلا الجارية وأبى صاحب القلعة تسليمها، فقال القاضي: يفسخ الصلح','6179','4149','0');
INSERT INTO [t1153] VALUES (4379,'وله أن ينفل في البداءة الربع بعد الخمس، وفي الرجعة الثلث بعده، وذلك أنه إذا دخل الجيش، بعث سرية تغير، وإذا رجع بعث أخرى، فما أتت به أخرج خمسه، وأعطى السرية ما جعل لها، وقسم الباقي للجيش والسرية معا','6180','4149','0');
INSERT INTO [t1153] VALUES (4380,'ولا يجوز لأحد أن يتعلف ولا يحتطب ولا يبارز، ولا يخرج من العسكر، ولا يحدق حدثا لا بإذن الأمير','6188','4149','0');
INSERT INTO [t1153] VALUES (4381,'فإن دعا كافر إلى البراز، استحب لمن يعلم من نفسه القوة والشجاعة أن يبارزه بإذن الأمير','6190','4149','0');
INSERT INTO [t1153] VALUES (4382,'فإن شرط الكافر أن لا يقاتله غير الخارج إليه فله شرطه','6191','4149','0');
INSERT INTO [t1153] VALUES (4383,'فإن انهزم المسلم، أو أثخن بالجراح، جاز الدفع عنه','6191','4149','0');
INSERT INTO [t1153] VALUES (4384,'وإن قتله المسلم فله سلبه','6192','4149','0');
INSERT INTO [t1153] VALUES (4385,'وكل من قتل قتيلا فله سلبه غير مخموس، إذا قتله حال الحرب منهمكا على القتال غير مثخن، وغرر بنفسه في قتله، وعنه لا يستحقه إلا من شرط له','6193','4149','0');
INSERT INTO [t1153] VALUES (4386,'وإن قطع أربعته وقتله آخر، فسلبه للقاطع دون القاتل','6200','4149','0');
INSERT INTO [t1153] VALUES (4387,'وإن أسره وقتله الإمام فسلبه غنيمة','6201','4149','0');
INSERT INTO [t1153] VALUES (4388,'وإن قطع يده ورجله وقتله آخر فسلبه غنيمة، وقيل: هو للقاتل','6201','4149','0');
INSERT INTO [t1153] VALUES (4389,'والسلب ما كان عليه من ثياب وحلي وسلاح والدابة بآلتها، عنه أن الدابة ليست من السلب، ونفقته وخيمته ورحله غنيمة','6202','4149','0');
INSERT INTO [t1153] VALUES (4390,'باب الأمان','6208','4149','0');
INSERT INTO [t1153] VALUES (4391,'وإن دخل قوم لا منعه لهم دار الحرب بغير إذن الإمام فغنموا، فعن أحمد فيها ثلاث روايات','6209','4149','0');
INSERT INTO [t1153] VALUES (4392,'كتاب الجهاد','6211','0','0');
INSERT INTO [t1153] VALUES (4393,'كتاب الجهاد','6211','4392','0');
INSERT INTO [t1153] VALUES (4394,'ومن أخذ من دار الحرب طعاما أو علفا، فله أكله وعلف دابته بغير إذن، وليس له بيعه فإن باعه رد ثمنه في المغنم','6212','4392','0');
INSERT INTO [t1153] VALUES (4395,'فإن فضل معه منه شيء فأدخله البلد، رده في الغنيمة إلا أن يكون يسيرا فله أكله في إحدى الروايتين','6217','4392','0');
INSERT INTO [t1153] VALUES (4396,'ومن أخذ سلاحا، فله أن يقاتله به حتى تنقضي الحرب ثم يرده، وليس له ركوب الفرس في إحدى الروايتين','6219','4392','0');
INSERT INTO [t1153] VALUES (4397,'باب قسمة الغنائم','6220','4392','0');
INSERT INTO [t1153] VALUES (4398,'وإن أخذ منهم مال مسلم فأدركه صاحبه قبل قسمه فهو أحق به، وإن أدركه مقسوما فهو أحق به بثمنه، وعنه لا حق لهم فيه، وإن أخذه منهم أحد الرعية بثمن، فصاحبه أحق به بثمنه وإن أخذه بغير عوض، فصاحبه أحق به بغير شيء','6220','4392','0');
INSERT INTO [t1153] VALUES (4399,'ويملك الكفار أموال المسلمين بالقهر ذكره القاضي، وقال أبو الخطاب: ظاهر كلام أحمد أنهم يملكونها، روي عن أحمد في ذلك روايتان','6225','4392','0');
INSERT INTO [t1153] VALUES (4400,'وتملك الغنيمة بالاستيلاء عليها في دار الحرب ويجوز قسمها فيها','6230','4392','0');
INSERT INTO [t1153] VALUES (4401,'وهي لمن شهد الوقعة من أهل القتال، قاتل أو لم يقاتل من تجار العسكر وأجرائهم الذين يستعدون للقتال','6231','4392','0');
INSERT INTO [t1153] VALUES (4402,'فأما المريض العاجز عن القتال، والمخذل والمرجف، والفرس الضعيف العجيف فلا حق له','6232','4392','0');
INSERT INTO [t1153] VALUES (4403,'وإذا ألحق مدد وهرب أسير، فأدركوا الحرب قبل تقضيها أسهم لهم، وإن جاءوا بعد إحراز الغنيمة فلا شيء لهم','6233','4392','0');
INSERT INTO [t1153] VALUES (4404,'وإذا أراد القسمة بدأ بالأسلاب فدفعها إلى أهلها','6237','4392','0');
INSERT INTO [t1153] VALUES (4405,'ثم يخمس الباقي، فيقسم خمسه على خمسة أسهم','6237','4392','0');
INSERT INTO [t1153] VALUES (4406,'ثم يعطي النفل بعد ذلك','6246','4392','0');
INSERT INTO [t1153] VALUES (4407,'ويرضخ لمن لا سهم له، وهم العبيد والنساء والصبيان','6247','4392','0');
INSERT INTO [t1153] VALUES (4408,'وفي الكافر روايتان: إحداهما يرضخ له والأخرى يسهم له','6251','4392','0');
INSERT INTO [t1153] VALUES (4409,'ولا يبلغ بالرضخ للراجل سهم راجل، ولا للفارس سهم فارس','6252','4392','0');
INSERT INTO [t1153] VALUES (4410,'فإن تغيرت حالهم قبل تقضي الحرب أسهم لهم','6252','4392','0');
INSERT INTO [t1153] VALUES (4411,'وإن غزا العبد على فرس لسيده، قسم للفرس ورضخ العبد','6252','4392','0');
INSERT INTO [t1153] VALUES (4412,'ثم يقسم باقي الغنيمة للراجل سهم وللفارس ثلاثة أسهم، سهم له وسهمان لفرسه','6254','4392','0');
INSERT INTO [t1153] VALUES (4413,'إلا أن يكون فرسه هجينا أو برذونا، فيكون له سهم، وعنه له سهمان كالعربي','6256','4392','0');
INSERT INTO [t1153] VALUES (4414,'ولا يسهم لأكثر من فرسين','6258','4392','0');
INSERT INTO [t1153] VALUES (4415,'ولا يسهم لغير الخيل، وقال الخرقي: من غزا على بعير لا يقدر على غيره، قسم له ولبعيره سهمان','6259','4392','0');
INSERT INTO [t1153] VALUES (4416,'ومن دخل دار الحرب راجلا، ثم ملك فرسا أو استعاره أو أستأجره فشهد به الوقعة، فله سهم فارس، ومن دخل فارسا فنفق فرسه، أو شرد حتى تقضي الحرب، فله سهم راجل','6261','4392','0');
INSERT INTO [t1153] VALUES (4417,'ومن غصب فرسا فقاتل عليه، فسهم الفرس لمالكه','6261','4392','0');
INSERT INTO [t1153] VALUES (4418,'وإذا قال الإمام من أخذ شيئا فهو له، أو فضل بعض الغانمين على بعض لم يجز في إحدى الروايتين، ويجوز في الأخرى','6263','4392','0');
INSERT INTO [t1153] VALUES (4419,'ومن استؤجر للجهاد ممن لا يلزمه من العبيد والكفار فليس له إلا الأجرة','6264','4392','0');
INSERT INTO [t1153] VALUES (4420,'ومن مات بعد انقضاء الحرب فسهمه لوارثه','6267','4392','0');
INSERT INTO [t1153] VALUES (4421,'ويشارك الجيش سراياه فيما غنمت ويشاركونه فيما غنم','6268','4392','0');
INSERT INTO [t1153] VALUES (4422,'وإذا قسمت الغنيمة في أرض الحرب فتبايعوها ثم غلب عليها العدو، فهي مال المشتري في إحدى الروايتين اختارها الخلال وصاحبه، والأخرى هي من مال البائع اختارها الخرقي','6269','4392','0');
INSERT INTO [t1153] VALUES (4423,'وإن وطئ جارية من المغنم ممن له فيها حق أو لولده، أدب ولم يبلغ به الحد، وعليه مهرها، إلا أن تلد منه فيكون عليه قيمتها، وتصير أم ولد له، والولد حر ثابت النسب','6273','4392','0');
INSERT INTO [t1153] VALUES (4424,'ومن أعتق منهم عبدا، عتق عليه قد حصته، وقوم عليه باقيه إن كان موسرا وكذلك إن كان فيهم من يعتق عليه','6275','4392','0');
INSERT INTO [t1153] VALUES (4425,'والغال من الغنيمة يحرق كله، إلا السلاح والمصحف والحيوان','6276','4392','0');
INSERT INTO [t1153] VALUES (4426,'وما أخذ من الفدية أو أهداه الكفار إلى أمير الجيش أوبعض قواده فهو غنيمة','6281','4392','0');
INSERT INTO [t1153] VALUES (4427,'باب حكم الأرضين المغنومة','6281','4392','0');
INSERT INTO [t1153] VALUES (4428,'الضرب الثاني','6285','4392','0');
INSERT INTO [t1153] VALUES (4429,'ويقرون فيها بغير جزية','6286','4392','0');
INSERT INTO [t1153] VALUES (4430,'والمرجع في الخراج والجزية إلى اجتهاد الإمام، في الزيادة والنقصان على قدر الطاقة وعنه يرجع إلى ما ضربه عمر رضي الله عنه لا يزاد ولا ينقص، وعنه تجوز الزيادة دون النقص','6286','4392','0');
INSERT INTO [t1153] VALUES (4431,'ويجب الخراج على المالك دون المستأجر','6288','4392','0');
INSERT INTO [t1153] VALUES (4432,'والخراج كالدين، يحبس به الموسر وينظر المعسر','6288','4392','0');
INSERT INTO [t1153] VALUES (4433,'فإن أمكن زرعه عاما بعد عام، وجب نصف خراجه في كل عام','6288','4392','0');
INSERT INTO [t1153] VALUES (4434,'ومن عجز عن عمارة أرضه، أجبر على إجارتها أو رفع يده عنها','6288','4392','0');
INSERT INTO [t1153] VALUES (4435,'وإن رأى الإمام المصلحة في إسقاط الخراج، أو تخفيفه عن إنسان جاز لأنه فيء فكان النظر فيه إلى الإمام','6289','4392','0');
INSERT INTO [t1153] VALUES (4436,'باب الفيء','6289','4392','0');
INSERT INTO [t1153] VALUES (4437,'ولا يخمس وقال الخرقي: يخمس فيصرف خمسه إلى أهل الخمس، وباقيه في المصالح','6291','4392','0');
INSERT INTO [t1153] VALUES (4438,'فإن فضل منه فضلة قسمه بين المسلمين ويبدأ بالمهاجرين ويقدم الأقرب فالأقرب من رسول الله صلى الله عليه وسلم','6293','4392','0');
INSERT INTO [t1153] VALUES (4439,'ثم الأنصار ثم سائر المسلمين وهل يفاضل بينهم؟ على روايتين','6294','4392','0');
INSERT INTO [t1153] VALUES (4440,'ومن مات بعد حلول وقت العطاء دفع إلى ورثته حقه، لأه مات بعد الاستحقاق فانتفل حقه إلى وارثه كسائر الموروثات','6296','4392','0');
INSERT INTO [t1153] VALUES (4441,'ومن مات من أجناد المسلمين، دفع إلى امرأته وأولاده الصغار ما يكفيهم','6296','4392','0');
INSERT INTO [t1153] VALUES (4442,'فإذا بلغ ذكورهم، فاختاروا أن يكونوا في المقاتلة فرض لهم وإن لم يختاروا تركوا وسقط حقهم من عطاء المقاتلة','6297','4392','0');
INSERT INTO [t1153] VALUES (4443,'وما لا يناله الماء مما لا يمكن زرعه فلا خراج عليه','6298','4392','0');
INSERT INTO [t1153] VALUES (4444,'ويصح أمان الإمام لجميع الكفار وآحادهم','6299','4392','0');
INSERT INTO [t1153] VALUES (4445,'ومن قال لكافر: أنت آمن، أولا بأس عليك أو اجرتك أو قف أو ألق سلاحك أو مترس نفذ أمنه','6300','4392','0');
INSERT INTO [t1153] VALUES (4446,'ومن جاء بمشرك فادعى أنه أمنه فأنكره فالقول قوله وعنه القول قول الأسير، وعنه قول من يدل الحال على صدقة','6303','4392','0');
INSERT INTO [t1153] VALUES (4447,'ومن أعطي أمانا ليفتح حصنا ففتحه واشتبه علينا حرم قتلهم واسترقاقهم','6304','4392','0');
INSERT INTO [t1153] VALUES (4448,'ويجوز عقد الأمان للرسول والمستأمن، ويقيمون مدة الهدنة بغير جزية، وعند أبي الخطاب لا يقيمون سنة إلا بجزية','6305','4392','0');
INSERT INTO [t1153] VALUES (4449,'ومن دخل دار الإسلام بغير أمان وادعى أنه رسول أو تاجر معه متاع يبيعه قبل منه','6306','4392','0');
INSERT INTO [t1153] VALUES (4450,'وإذا أودع المستأمن ماله مسلما، أو أقرضه إياه، ثم عاد إلى دار الحرب، بقي الأمان في ماله يبعث إليه إن طلبه','6308','4392','0');
INSERT INTO [t1153] VALUES (4451,'وإذا أسر الكفار مسلما، فأطلقوه بشرط أن يقيم عندهم مدة لزمه الوفاء لهم ولم يكن له أن يهرب','6311','4392','0');
INSERT INTO [t1153] VALUES (4452,'فإن لم يشترطوا شيئا، أو شرطوا كونه رقيقا فله أن يقتل ويسرق ويهرب','6312','4392','0');
INSERT INTO [t1153] VALUES (4453,'وإن أطلقوه بشرط أن يبعث إليهم مالا، وإن عجز عنه عاد إليهم، لزمه الوفاء لهم إلا أن تكون امرأة فلا ترجع إليهم وقال الخرقي: لا يرجع الرجل أيضا','6312','4392','0');
INSERT INTO [t1153] VALUES (4454,'باب الهدنة','6316','4392','0');
INSERT INTO [t1153] VALUES (4455,'ولا يجوز عقد الهدنة إلا من الإمام أو نائبه','6317','4392','0');
INSERT INTO [t1153] VALUES (4456,'فمتى رأى المصلحة جاز له عقدها مدة معلومة وإن طالت، وعنه لا يجوز في زيادة على العشر، فإن زاد على عشر بطل في الزيادة وفي العشر وجهان','6319','4392','0');
INSERT INTO [t1153] VALUES (4457,'وإن هادنهم مطلقا لم يصح','6320','4392','0');
INSERT INTO [t1153] VALUES (4458,'وإن شرط فيها شرطا فاسدا، كنقضها متى شاء، أو رد النساء إليهم أو صداقهن، أو سلاحهم أو ادخالهم الحرم، لم يصح الشرط وقي العقد وجهان','6320','4392','0');
INSERT INTO [t1153] VALUES (4459,'وإن شرط رد من جاء من الرجال مسلما جاز، ولا يمنعهم أخذه ولا يجبره على ذلك، وله أن يأمرهم بقتالهم والفرار منهم','6323','4392','0');
INSERT INTO [t1153] VALUES (4460,'وعلى الإمام حماية من هادنه من المسلمين دون غيرهم وإن سباهم كفار آخرون لم يجز لنا شراؤهم','6325','4392','0');
INSERT INTO [t1153] VALUES (4461,'وإن خاف نقض العهد منهم، نبذ إليهم عهدهم لقول الله تعالى: " وإما تخافن من قوم خيانة فانبذ إليهم على سواء"','6325','4392','0');
INSERT INTO [t1153] VALUES (4462,'باب عقد الذمة','6327','4392','0');
INSERT INTO [t1153] VALUES (4463,'ولا يجوز عقدها إلا لأهل الكتاب، وهم اليهود والنصارى، ومن يوافقهم في التدين بالتوراة الإنجيل، كالسامرة والفرنج، ومن له شبهة كتاب وهم المجوس، وعنه يجوز عقدها لجميع الكفار إلا عبدة الأوثان من العرب','6328','4392','0');
INSERT INTO [t1153] VALUES (4464,'فأما الصابئ فينظر فيه، فإن انتسب إلى أحد الكتابين فهو من أهله، وإلا فلا','6332','4392','0');
INSERT INTO [t1153] VALUES (4465,'ومن تهود أو تنصر بعد بعث نبينا محمد صلى الله عليه وسلم أو ولد بين أبوين لا تقبل الجزية من حدهما، فعلى وجهين','6333','4392','0');
INSERT INTO [t1153] VALUES (4466,'ولا تؤخذ الجزية من نصارى بني تغلب، وتؤخذ الزكاة من أموالهم، مثلي ما تؤخذ من أموال المسلمين','6333','4392','0');
INSERT INTO [t1153] VALUES (4467,'ومصرفه مصرف الجزية اختاره القاضي','6335','4392','0');
INSERT INTO [t1153] VALUES (4468,'ولا يؤخذ ذلك من كتابي غيرهم، وقال القاضي: تؤخذ من نصارى العرب ويهودهم','6336','4392','0');
INSERT INTO [t1153] VALUES (4469,'ولا جزية على صبي، ولا امرأة ولا مجنون ولا زمن ولا أعمى ولا عبد، ولا فقير يعجز عنها','6338','4392','0');
INSERT INTO [t1153] VALUES (4470,'ومن بلغ أو أفاق أو استغنى، فهو من أهلها بالعقد الأول، ويؤخذ منه في آخر الحول بقدر ما أدرك','6342','4392','0');
INSERT INTO [t1153] VALUES (4471,'ومن كان يجن ويفيق لفقت إفاقته، فإذا بلغت حولا أخذت منه، ويحتمل أن يؤخذ في آخر كل حول، بقدر إفاقته منه','6343','4392','0');
INSERT INTO [t1153] VALUES (4472,'وتقسم الجزية بينهم، فيجعل على الغني ثمانية وأربعون درهما وعلى المتوسط أربعة وعشرون، وعلى الفقير اثنا عشر','6344','4392','0');
INSERT INTO [t1153] VALUES (4473,'وإذا بذلوا الواجب عليهم لزم قبوله وحرم قتالهم','6346','4392','0');
INSERT INTO [t1153] VALUES (4474,'والغني منهم من عده الناس غنيا في ظاهر المذهب','6346','4392','0');
INSERT INTO [t1153] VALUES (4475,'ومن أسلم بعد الحول سقطت عنه الجزية وإن مات أخذت من تركته، وقال القاضي: تسقط','6348','4392','0');
INSERT INTO [t1153] VALUES (4476,'وإن اجتمعت عليه جزية سنين، استوفيت كلها ولم تتداخل','6349','4392','0');
INSERT INTO [t1153] VALUES (4477,'وتؤخذ الجزية منهم في آخر الحول، ويمتهنون عند أخذها، ويطال قيامهم وتجر أيديهم','6350','4392','0');
INSERT INTO [t1153] VALUES (4478,'ويجوز أن يشترط عليهم ضيافة من يمر بهم من المسلمين ويبين أيام الضيافة، وقدر الطعام والادام والعلف، وعدد من يضاف، ولا يجب ذلك من غير شرط، وقيل: يجب','6351','4392','0');
INSERT INTO [t1153] VALUES (4479,'وإذا تولى إمام، فعرف قدر جزيتهم وما شرط عليهم، أقرهم عليه، فإن لم يعرف، رجع إلى قولهم، فإن بان كذبهم رجع عليهم. وعند أبي الخطاب أنه يستأنف العقد معهم','6353','4392','0');
INSERT INTO [t1153] VALUES (4480,'وإذا عقد الذمة معهم، كتب أسماءهم وأسماء آبائهم، وعددهم وحلاهم ودينهم','6353','4392','0');
INSERT INTO [t1153] VALUES (4481,'باب أحكام الذمة','6354','4392','0');
INSERT INTO [t1153] VALUES (4482,'ويلزمهم التميز عن المسلمين في شعورهم، بحذف مقادم رؤوسهم وترك الفرق، وكناهم فلا يتكنون بكنى المسلمين','6356','4392','0');
INSERT INTO [t1153] VALUES (4483,'ولا يجوز تصديرهم في المجالس، ولا بداءنهم بالسلام، فإن سلم أحدهم قيل له: عليكم','6358','4392','0');
INSERT INTO [t1153] VALUES (4484,'ويمنعون من تعلية البنيان على المسلمين، وفي مساواتهم وجهان','6360','4392','0');
INSERT INTO [t1153] VALUES (4485,'وإن ملكوا دارا عالية من مسلم، لم يجب نقضها، لأنهم ملكوها على هذه الصفة','6360','4392','0');
INSERT INTO [t1153] VALUES (4486,'وفي تهنئتهم وتعزيتهم وعيادتهم روايتان','6360','4392','0');
INSERT INTO [t1153] VALUES (4487,'ويمنعون من إحداث الكنائس والبيع، ولا يمنعون رم شعثها، وفي بناء ما استهدم منها روايتان','6361','4392','0');
INSERT INTO [t1153] VALUES (4488,'ويمنعون من إظهار المنكر وضرب الناقوس، والجهر بكتابهم','6363','4392','0');
INSERT INTO [t1153] VALUES (4489,'وإن صولحوا في بلادهم على إعطاء الجزية، لم يمنعوا شيئا من ذلك، ولم يؤخذوا بغيار ولا زنا، ولا تغيير شعورهم ولا مراكبهم','6363','4392','0');
INSERT INTO [t1153] VALUES (4490,'ويمنعون من دخول الحرم','6363','4392','0');
INSERT INTO [t1153] VALUES (4491,'ويمنعون من الإقامة بالحجاز، كالمدينة واليمامة، وخيبر وفدك وما والاها','6364','4392','0');
INSERT INTO [t1153] VALUES (4492,'فإن قدم رسول، لا بد له من لقاء الإمام، خرج إليه ولم يأذن له، فإن دخل عزر وهدد وأخرج، فإن مرض أو مات أخرج، وإن دفن نبش وأخرج إلا أن يكون قد بلي','6364','4392','0');
INSERT INTO [t1153] VALUES (4493,'فإن دخلوا بتجارة، لم يقيموا في موضع واحد أكثر من أربعة أيام','6365','4392','0');
INSERT INTO [t1153] VALUES (4494,'فإن مرض لم يخرج حتى يبرأ وإن مات دفن به','6366','4392','0');
INSERT INTO [t1153] VALUES (4495,'ولا يمنعون من تيماء وفيد ونحوهما','6366','4392','0');
INSERT INTO [t1153] VALUES (4496,'وهل لهم دخول المساجد بإذن مسلم؟ على روايتين','6366','4392','0');
INSERT INTO [t1153] VALUES (4497,'فإن اتجر حربي إلينا، أخذ منه العشر، ولا يؤخذ من أقل من عشرة دنانير','6368','4392','0');
INSERT INTO [t1153] VALUES (4498,'ويؤخذ منه في كل عام مرة وقال ابن حامد: يؤخذ من الحربي كلما دخل إلينا','6370','4392','0');
INSERT INTO [t1153] VALUES (4499,'وعلى الإمام حفظهم والمنع من أذاهم، واستنقاذ من أسر منهم','6371','4392','0');
INSERT INTO [t1153] VALUES (4500,'وإن تحاكموا إلى الحاكم مع مسلم، لزمه الحكم بينهم، وإن تحاكم بعضهم مع بعض، أو استعدى بعضهم على بعض، خير بين الحكم بينهم وبين تركهم','6372','4392','0');
INSERT INTO [t1153] VALUES (4501,'وإن تبايعوا بيوعا فاسدة وتقابضوا لم ينقض فعلهم، لأنه عقد تم قبل إسلامهم، على ما يجوز ابتداء العقد عليه، فأقروا عليه ولم ينقض كأنكحتهم، وإن لم يتقابضوا فسخه، سواء كان قد حكم بينهم حاكمهم أم لا','6372','4392','0');
INSERT INTO [t1153] VALUES (4502,'وإن تهود نصراني، أو تنصر يهودي، لم يقر ولم يقبل منه إلا الإسلام، أو الدين الذي كان عليه، ويحتمل أن لا يقبل منه إلا الإسلام، فإن أبى هدد ويحبس، ويحتمل أن يقبل، وعنه أنه يقر','6373','4392','0');
INSERT INTO [t1153] VALUES (4503,'وإن انتقل إلى غير دين أهل الكتاب، أو انتقل المجوسي إلى غير دين أهل الكتاب، لم يقر وأمر أن يسلم، فإن أبى قتل','6374','4392','0');
INSERT INTO [t1153] VALUES (4504,'وإن انتقل غير الكتابي إلى دين أهل الكتاب، أقر، ويحتمل أن لا يقبل منه إلا الإسلام','6374','4392','0');
INSERT INTO [t1153] VALUES (4505,'وإن تمجس الوثني فهل يقر','6375','4392','0');
INSERT INTO [t1153] VALUES (4506,'وإن تعدى على مسلم بقتل، أو قذف أو زنا، أو قطع طريق، أو تجسس أو إيواء جاسوس، أو ذكر الله تعالى أو كتباه أو رسوله بسوء، فعلى روايتين','6375','4392','0');
INSERT INTO [t1153] VALUES (4507,'وإن أظهر منكرا، أو رفع صوته بكتابه، لم ينتقض عهده','6376','4392','0');
INSERT INTO [t1153] VALUES (4508,'ولا ينتقض عهد نسائه وأولاده بنقض عهده وإذا انتقض عهده حير الإمام فيه كالأسير الحربي','6376','4392','0');
INSERT INTO [t1153] VALUES (4509,'وماله فيء عند الخرقي وقال أبو بكر: هو لورثته','6377','4392','0');
INSERT INTO [t1153] VALUES (4510,'كتاب الصيد','6390','0','0');
INSERT INTO [t1153] VALUES (4511,'كتاب الصيد','6390','4510','0');
INSERT INTO [t1153] VALUES (4512,'مسألة فإن لم يجد ما يذكيه به','6392','4510','0');
INSERT INTO [t1153] VALUES (4513,'مسألة فإن لم يفعل وتركه حتى مات لم يحل وقال القاضي يحل والأخرى يحل إلا أن يذكيه','6392','4510','0');
INSERT INTO [t1153] VALUES (4514,'مسألة وإن رمى صيدا فأثبته ثم رماه آخر فقتله لم يحل ولمن أثبته قيمته مجروحا على قاتله','6393','4510','0');
INSERT INTO [t1153] VALUES (4515,'مسألة وإن أدرك الصيد متحركا كحركة المذبوح فحكمه حكم الميت لا يحتاج إلى ذكاة','6397','4510','0');
INSERT INTO [t1153] VALUES (4516,'مسألة فإن رمى مسلم ومجوسي صيدا أو أرسلا عليه جارحا أو شارك كلب المسلم في قتله لم يحل','6398','4510','0');
INSERT INTO [t1153] VALUES (4517,'وإن رد كلب المجوسي إلى كلب المسلم فقتله حل أكله','6400','4510','0');
INSERT INTO [t1153] VALUES (4518,'مسألة وإن صاد المسلم بكلب المجوسي حل صيده','6400','4510','0');
INSERT INTO [t1153] VALUES (4519,'مسألة وإن صاد المجوسي بكلب المسلم لم يحل صيده في قول الجميع','6401','4510','0');
INSERT INTO [t1153] VALUES (4520,'مسألة وإن أرسل المسلم كلبا فزجره المجوسي حل صيده لأن الصائد هو المسلم','6401','4510','0');
INSERT INTO [t1153] VALUES (4521,'مسألة وإن أصاب بالمعراض أكل ما قتل بحده دون عرضه','6402','4510','0');
INSERT INTO [t1153] VALUES (4522,'مسألة وإن نصب مناجل أو سكاكين وسمى عند نصبها فقتلت صيدا أبيح فإن بان منه عضو فحكم حكم البائن','6403','4510','0');
INSERT INTO [t1153] VALUES (4523,'مسألة وإذا قتله بسهم مسموم لم يبح إذا غلب على الظن أن السم أعان على قتله','6404','4510','0');
INSERT INTO [t1153] VALUES (4524,'مسألة وإن رماه فوقع في ماء أو تردى من جبل أو وطىء عليه شيء فقتله لم يبح إلا أن تكون الجراح','6404','4510','0');
INSERT INTO [t1153] VALUES (4525,'مسألة فإن رماه في الهواء فوقع على الأرض فمات حل','6405','4510','0');
INSERT INTO [t1153] VALUES (4526,'مسألة وإن رمى صيدا فغاب ثم وجده ميتا لا أثر به غير سهمه حل وعنه إن كانت الجراح موحية حل','6406','4510','0');
INSERT INTO [t1153] VALUES (4527,'مسألة وإن ضربه فأبان منه عضوا وبقيت فيه حياة مستقرة لم يبح ما أبان وإن بقي معلقا بجلده حل','6408','4510','0');
INSERT INTO [t1153] VALUES (4528,'مسألة وإن أخذ قطعة من حوت وأفلت حيا أبيح ما أخذ منه','6411','4510','0');
INSERT INTO [t1153] VALUES (4529,'مسألة وأما ما ليس بمحدد كالبندق والعصي والحجر والشبكة والفخ فلا يباح ما قتل به لأنه وقيذ','6411','4510','0');
INSERT INTO [t1153] VALUES (4530,'مسألة: والجوارح نوعان ما يصيد بنابه كالكلب والفهد فتعليمه بثلاثة أشياء، وإن يسترسل إذا أرسل','6414','4510','0');
INSERT INTO [t1153] VALUES (4531,'مسألة ولا يعتبر تكرر ذلك منه','6414','4510','0');
INSERT INTO [t1153] VALUES (4532,'مسألة فإن أكل بعد تعلمه لم يحرم ما تقدم من صيده ولم يبح ما أكل منه في إحدى الروايتين والأخرى يحل','6416','4510','0');
INSERT INTO [t1153] VALUES (4533,'مسألة ولا بد أن يجرح الصيد فإن قتله بصدمته أو خنقه لم يبح','6420','4510','0');
INSERT INTO [t1153] VALUES (4534,'مسألة وما أصابه فم الكلب هل يجب غسله؟ على وجهين','6421','4510','0');
INSERT INTO [t1153] VALUES (4535,'مسألة وإن أرسل كلبه إلى هدف فقتل صيدا أو أرسله يريد الصيد ولا يرى صيدا لم يحل صيده إذا قتله','6423','4510','0');
INSERT INTO [t1153] VALUES (4536,'مسألة فإن رمى حجرا يظنه صيدا لم يحل ويحتمل أن يحل ذكره أبو الخطاب','6424','4510','0');
INSERT INTO [t1153] VALUES (4537,'مسألة وإن رمى صيدا فقتل غيره أو رمى صيدا فقتل جماعة حل','6424','4510','0');
INSERT INTO [t1153] VALUES (4538,'مسألة وإن أرسل سهمه على صيد فأعانته الريح فقتلته ولولاها ما وصل حل','6426','4510','0');
INSERT INTO [t1153] VALUES (4539,'مسألة وإن رمى صيدا فأثبته ملكه، فإن تحامل فأخذه غيره لزمه رده كما يلزمه رد الشاة','6426','4510','0');
INSERT INTO [t1153] VALUES (4540,'مسألة وإن لم يثبته فدخل خيمة إنسان فأخذه فهو لآخذه لأن الأول لم يملكه لكونه ممتنعا فمكله الثاني بأخذه','6426','4510','0');
INSERT INTO [t1153] VALUES (4541,'مسألة وإن وقع صيد في شبكة إنسان فخرقها وذهب بها فصاده آخر فهو للثاني','6426','4510','0');
INSERT INTO [t1153] VALUES (4542,'مسألة ويكره صيد السمك بالنجاسة','6428','4510','0');
INSERT INTO [t1153] VALUES (4543,'مسألة ومن كان في سفينة فوثبت سمكة فوقعت في حجره فهي له دون صاحب السفينة','6428','4510','0');
INSERT INTO [t1153] VALUES (4544,'مسألة وإن صنع بركة ليصيد بها السمك فما حصل فيها ملكه وإن لم يقصد بها ذلك لم يمكله','6428','4510','0');
INSERT INTO [t1153] VALUES (4545,'مسألة وإن قال الحاكم المعزول كنت حكمت في ولايتي لفلان على فلان بحق قبل قوله وبه قال إسحاق','6428','4510','0');
INSERT INTO [t1153] VALUES (4546,'مسألة ويكره صيد الطير بالشباش','6429','4510','0');
INSERT INTO [t1153] VALUES (4547,'مسألة وإن ارسل صيدا وقال أعتقتك لم يزل ملكه عنه ويحتمل أن يزول وهو لمن أخذه','6429','4510','0');
INSERT INTO [t1153] VALUES (4548,'باب الذكاة','6431','4510','0');
INSERT INTO [t1153] VALUES (4549,'مسألة وعنه في السرطان وسائر البحري أنه يحل بلا ذكاة','6433','4510','0');
INSERT INTO [t1153] VALUES (4550,'مسألة وعنه في الجراد لا يؤكل إلا أن يموت بسبب ككبه وتغريقه','6434','4510','0');
INSERT INTO [t1153] VALUES (4551,'مسألة ويشترط للذكاة شروط أربعة','6435','4510','0');
INSERT INTO [t1153] VALUES (4552,'مسألة ولا تباح ذكاة مجنون ولا سران ولا طفل غير مميز ولا مجوسي ولا وثني ولا مرتد','6436','4510','0');
INSERT INTO [t1153] VALUES (4553,'مسألة فإن ذبح بآلة مغصوبة حل في أصح الوجهين','6440','4510','0');
INSERT INTO [t1153] VALUES (4554,'مسألة وإن نحره أجزأ وهو أن يطعنه بمحدد في لبته ويستحب أن ينحر البعير ويذبح ما سواه','6441','4510','0');
INSERT INTO [t1153] VALUES (4555,'مسألة فإن عجز عن ذلك أي عن قطع الحلقوم والمريء مثل أن يند البعير أو يتردى في بئر فلا يقدر على ذبحه','6442','4510','0');
INSERT INTO [t1153] VALUES (4556,'مسألة وإن ذبح من قفاها وهو مخطىء فأتت السكين على موضع ذبحها وهي في الحياة أكلت وإن فعله عمدا','6443','4510','0');
INSERT INTO [t1153] VALUES (4557,'مسألة وكل ما وجد سبب الموت كالمنخنقة والموقوذة والمتدرية والنطيحة وأكيلة السبع إذا أدرك ذكاتها','6444','4510','0');
INSERT INTO [t1153] VALUES (4558,'مسألة إلا الأخرس فإنه يومىء برأسه إلى السماء','6445','4510','0');
INSERT INTO [t1153] VALUES (4559,'مسألة فإن ترك التسمية عمدا لم تبح وإن تركها ساهيا أبيحت وعنه تباح في الحالين وعنه لا تباح فيهما','6446','4510','0');
INSERT INTO [t1153] VALUES (4560,'مسألة وذكاة الجنين ذكاة أمه إذا خرج ميتا أو متحركا كحركة المذبوح وإن كانت فيه حياة مستقرة','6447','4510','0');
INSERT INTO [t1153] VALUES (4561,'مسألة ويكره أن يكسر عنق الحيوان أو يسلخه حتى يبرد أي حتى تزهق نفسه','6449','4510','0');
INSERT INTO [t1153] VALUES (4562,'مسألة فإن فعل أساء وأكلت لأن ذلك حصل بعد ذبحها وحلها','6449','4510','0');
INSERT INTO [t1153] VALUES (4563,'مسألة وإن ذبح الحيوان ثم غرق في ماء أو وطىء عليه شيء يقتله فهل يحل؟ على روايتين','6449','4510','0');
INSERT INTO [t1153] VALUES (4564,'مسألة وإذا ذبح الكتابي ما يحرم عليه كذي الظفر لم يحرم علينا','6450','4510','0');
INSERT INTO [t1153] VALUES (4565,'مسألة وإن ذبح ليعيده أو ليقرب به إلى شيء مما يعظمونه لم يحرم عليه لأنه من طعامهم فيدخل في عموم الآية','6450','4510','0');
INSERT INTO [t1153] VALUES (4566,'مسألة ومن ذبح حيوان فوجد في بطنه جرادا أو طائرا فوجد في حوصلته حبا أو وجد الحب في بعر الجمل','6451','4510','0');
INSERT INTO [t1153] VALUES (4567,'كتاب الأطعمة','6452','0','0');
INSERT INTO [t1153] VALUES (4568,'مسألة والحيوانات مباحة لعموم النص الدل على الإباحة إلا الحمر الأهلية','6452','4567','0');
INSERT INTO [t1153] VALUES (4569,'كتاب الأطعمة','6452','4567','0');
INSERT INTO [t1153] VALUES (4570,'مسألة وما له ناب يفرس به كالأسد والنمر والذئب والفهد والكلب والخنزير وابن آوى والسنور وابن عرس','6455','4567','0');
INSERT INTO [t1153] VALUES (4571,'مسألة وما له مخلب من الطير يصيد به كالبازي والصقر والشاهين والحدأة والبومة','6458','4567','0');
INSERT INTO [t1153] VALUES (4572,'مسألة وما يأكل الجيف كالنسر والرخم واللقلق وغراب البين والأبقع','6459','4567','0');
INSERT INTO [t1153] VALUES (4573,'مسألة وما يستخبث كالقنفذ والفأر والحيات والحشرات كلها','6460','4567','0');
INSERT INTO [t1153] VALUES (4574,'مسألة وما تولد من مأكول وغيره كالبغل والسمع، والسمع ولد الضبع من الذئب وقيل سمي العسبار','6463','4567','0');
INSERT INTO [t1153] VALUES (4575,'مسألة وفي الثعلب والوبر وسنور البر واليربوع روايتان','6464','4567','0');
INSERT INTO [t1153] VALUES (4576,'مسألة وما عدا هذا فمباح كبهيمة الأنعام والخيل والدجاج','6467','4567','0');
INSERT INTO [t1153] VALUES (4577,'مسألة والوحشي من البقر والظباء والحمر يباح','6468','4567','0');
INSERT INTO [t1153] VALUES (4578,'مسألة والأرانب مباحة','6470','4567','0');
INSERT INTO [t1153] VALUES (4579,'مسألة وسائر الوحش لعموم النص والضبع والضب','6471','4567','0');
INSERT INTO [t1153] VALUES (4580,'مسألة والزاغ مباح','6473','4567','0');
INSERT INTO [t1153] VALUES (4581,'مسألة وجميع حيوان البحر مباح لقول الله تعالى: "أحل لكم صيد البحر وطعامه متاعا لكم" إلا الضفدع','6475','4567','0');
INSERT INTO [t1153] VALUES (4582,'مسألة حتى تحبس وتزول الكراهة بحبسها اتفاقا','6480','4567','0');
INSERT INTO [t1153] VALUES (4583,'مسألة وما يسقى بالماء النجس من الزرع والثمار محرم وكذلك ما سمد به وقال ابن عقيل يحتمل أن يكره ذلك','6481','4567','0');
INSERT INTO [t1153] VALUES (4584,'مسألة وإن وجد طعاما لا يعرف مالكه وميتة أو صيدا وهو محرم فقال أصحابنا يأكل الميتة','6488','4567','0');
INSERT INTO [t1153] VALUES (4585,'مسألة فإن لم يجد إلا طعاما لم يبذله مالكه فإن كان صاحبه مضطرا إليه فهو أحق به وإلا لزمه بذله','6492','4567','0');
INSERT INTO [t1153] VALUES (4586,'مسألة فإن لم يجد إلا آدميا مباح الدم كالمرتد والزاني المحصن حل له قتله وأكله','6494','4567','0');
INSERT INTO [t1153] VALUES (4587,'مسألة وفي الزرع وشرب لبن الماشية روايتان','6500','4567','0');
INSERT INTO [t1153] VALUES (4588,'مسألة ويجب على المسلم ضيافة المسلم المجتاز به يوما وليلة فإن ابى فللضيف طلبه به عند الحاكم','6506','4567','0');
INSERT INTO [t1153] VALUES (4589,'مسألة وتستحب ضيافته ثلاثة أيام فما زاد فهو صدقة','6509','4567','0');
INSERT INTO [t1153] VALUES (4590,'مسألة وليس له إنزال الضيف في بيته','6510','4567','0');
INSERT INTO [t1153] VALUES (4591,'باب السبق','6515','4567','0');
INSERT INTO [t1153] VALUES (4592,'مسألة ولا تجوز بعوض إلا في الخيل والإبل والسهام','6516','4567','0');
INSERT INTO [t1153] VALUES (4593,'مسألة ولا تصح إلا بشروط خمسة','6519','4567','0');
INSERT INTO [t1153] VALUES (4594,'مسألة ولا يشترط تعيين الراكبين ولا القوسين','6519','4567','0');
INSERT INTO [t1153] VALUES (4595,'مسألة الثاني أن يكون المركوبان والقوسان من نوع واحد فلا يجوز بين عربي وهجين ولا بين قوس عربية','6520','4567','0');
INSERT INTO [t1153] VALUES (4596,'مسألة الثالث تحديد المسافة والغاية ومدى الرمي بما جرت به العادة','6521','4567','0');
INSERT INTO [t1153] VALUES (4597,'مسألة فإن كان الجعل من الإمام أو أحد غيرهما أو أحدهما على أن من سبق أخذه جاز','6522','4567','0');
INSERT INTO [t1153] VALUES (4598,'مسألة الشرط الرابع كون العوض معلوما لأنه مال في عقد فوجب العلم به كسائر العقود','6522','4567','0');
INSERT INTO [t1153] VALUES (4599,'مسألة الشرط الخامس الخروج عن شبه القمار بأن لا يخرج جميعهم','6522','4567','0');
INSERT INTO [t1153] VALUES (4600,'مسألة فإن جاءا معا فلا شيء لهما','6523','4567','0');
INSERT INTO [t1153] VALUES (4601,'مسألة وإن أخرجا معا لم يجز إلا أن يدخلا بينهما محللا يكافىء فرسه فرسيهما أو بعيره بعيريهما','6524','4567','0');
INSERT INTO [t1153] VALUES (4602,'مسألة وإن قال المخرج من سبق فله عشرة ومن صلى فله ذلك لم يجز إذا كانا اثنين وإن قال من صلى فله خمسة','6525','4567','0');
INSERT INTO [t1153] VALUES (4603,'مسألة وإن شرطا أن السابق يطعم السبق أصحابه أو غيرهم لم يصح الشرط','6528','4567','0');
INSERT INTO [t1153] VALUES (4604,'مسألة وتنفسخ بموت أحد المتعاقدين','6531','4567','0');
INSERT INTO [t1153] VALUES (4605,'مسألة والسبق في الخيل بالراس إذا تماثلت الأعناق وفي مختلفي العنق والإبل بالكتف','6531','4567','0');
INSERT INTO [t1153] VALUES (4606,'مسألة ولا يجوز أن يجنب أحدهما مع فرسه فرسا يحرضه على العدو ولا يصيح به في وقت سباقه','6533','4567','0');
INSERT INTO [t1153] VALUES (4607,'مسألة وإذا أطلقا الإصابة تناولها على أي صفة كانت','6541','4567','0');
INSERT INTO [t1153] VALUES (4608,'مسألة فإن قالا خواسق وهو ما خرق الغرض وثبت فيه أو خوازق وهو ما خزقه ووقع بين يديه أو موارق','6541','4567','0');
INSERT INTO [t1153] VALUES (4609,'مسألة وإن شرط إصابة موضع من الغرض كالدائرة فيه تقيد به','6541','4567','0');
INSERT INTO [t1153] VALUES (4610,'مسألة وإن تشاحا في المبتدىء منهما أقرع بينهما وقيل يقدم من له مزية بإخراج السبق','6542','4567','0');
INSERT INTO [t1153] VALUES (4611,'مسألة وإذا بدأ أحدهما في وجه بدأ الآخر في الثاني تعديلا بينهما فإن شرطا البداءة لأحدهما في كل الوجوه','6542','4567','0');
INSERT INTO [t1153] VALUES (4612,'مسألة والسنة أن يكون لهما غرضان يرميان أحدهما ثم يمضيان إليه فيأخذان السهام يرميان الآخر','6543','4567','0');
INSERT INTO [t1153] VALUES (4613,'مسألة وإذا أطارت الريح الغرض فوقع السهم موضعه فإن كان شرطهما خواصل احتسب له به','6544','4567','0');
INSERT INTO [t1153] VALUES (4614,'مسألة وإن كان شرطهما خواسق لم يحتسب له به ولا عليه','6544','4567','0');
INSERT INTO [t1153] VALUES (4615,'مسألة وإن عرض عارض من كسر قوس أو قطع وتر أو ريح شديدة لم يحسب عليه بالسهم','6546','4567','0');
INSERT INTO [t1153] VALUES (4616,'مسألة وإن عرض مطر أو ظلمة جاز تأخير الرمي','6547','4567','0');
INSERT INTO [t1153] VALUES (4617,'كتاب الأيمان','6548','0','0');
INSERT INTO [t1153] VALUES (4618,'كتاب الأيمان','6548','4617','0');
INSERT INTO [t1153] VALUES (4619,'مسألة واليمين التي تجب بها الكفارة هي اليمين بالله تعالى أو صفة من صفاته','6552','4617','0');
INSERT INTO [t1153] VALUES (4620,'مسألة وأسماء الله تعالى قسمان','6552','4617','0');
INSERT INTO [t1153] VALUES (4621,'مسألة فهذا إن نوى بالقسم به اسم الله تعالى أو اطلق كان يمينا لأنه باطلاقه ينصرف إليه بالنية','6553','4617','0');
INSERT INTO [t1153] VALUES (4622,'مسألة وأما ما لا يعد من اسمائه كالشيء والموجود والحي والعالم والمؤمن والكريم والشاكر','6553','4617','0');
INSERT INTO [t1153] VALUES (4623,'مسألة وإن قال وحق الله وعهد الله وايم وأمانة الله وميثاقه وقدرته وعظمته وكبريائه وجلاله وعزته ونحو ذلك','6554','4617','0');
INSERT INTO [t1153] VALUES (4624,'مسألة وإن قال وايم والله أو وايمن الله فهي يمين موجبة للكفارة','6555','4617','0');
INSERT INTO [t1153] VALUES (4625,'مسألة وإن قال وأمانة الله فقال القاضي لا يختلف المذهب في أن الحلف بأمانة الله يمين مكفرة','6555','4617','0');
INSERT INTO [t1153] VALUES (4626,'مسألة وإن قال والعهد والميثاق وسائر ذلك ولم يضفه إلى الله تعالى لم يكن يمينا إلا أن ينوي صفة الله تعالى','6557','4617','0');
INSERT INTO [t1153] VALUES (4627,'مسألة وإن قال لعمر الله كان يمينا وقال أبو بكر لا يكون يمينا إلا أن ينوي','6558','4617','0');
INSERT INTO [t1153] VALUES (4628,'مسألة وإن حلف بكلام الله أو بالمصحف أو بالقرآن فهي يمين فيها كفارة واحدة وعنه عليه بكل آية كفارة','6560','4617','0');
INSERT INTO [t1153] VALUES (4629,'مسألة وإن قال أحلف بالله أو أشهد بالله أو أقسم بالله أو أعزم بالله كان يمينا وإن لم يذكر اسم الله لم يكن يمينا','6561','4617','0');
INSERT INTO [t1153] VALUES (4630,'مسألة ويجوز القسم بغير حرف القسم فيقول الله لأفعلن بالجر والنصب فإن قال الله لأفعلن بالرفع كان يمينا','6564','4617','0');
INSERT INTO [t1153] VALUES (4631,'مسألة ويكره الحلف بغير الله تعالى ويحتمل أن يكون محرما وذلك نحو أن يحلف بأبيه أو بالكعبة أو بصحابي','6566','4617','0');
INSERT INTO [t1153] VALUES (4632,'مسألة ولا تجب به الكفارة سواء أضافه إلى الله تعالى مثل قوله ومعلوم الله وخلقه ورزقه وبيته أو لم يضفه','6567','4617','0');
INSERT INTO [t1153] VALUES (4633,'مسألة وقال أصحابنا تجب الكفارة بالحنث برسول الله صلى الله عليه وسلم خاصة','6567','4617','0');
INSERT INTO [t1153] VALUES (4634,'مسألة فأما اليمين على لاماضي فليست منعقدة وهي نوعان الغموس وهي التي يحلف بها كاذبا عالما بكذبه','6568','4617','0');
INSERT INTO [t1153] VALUES (4635,'مسألة وإن سبقت اليمين على لسانه كقوله لا والله وبلى والله في عرض حديثه فلا كفارة عليه','6571','4617','0');
INSERT INTO [t1153] VALUES (4636,'مسألة فإن حلف فقال إن شاء الله لم يحنث فعل أو ترك إذا كان متصلا بيمينه','6574','4617','0');
INSERT INTO [t1153] VALUES (4637,'مسألة فإن دعي إلى الحلف عند الحاكم وهو محق استحب له افتداء يمينه فإن حلف فلا بأس','6577','4617','0');
INSERT INTO [t1153] VALUES (4638,'مسألة وعليه كفارة إن فعل في إحدى الروايتين','6580','4617','0');
INSERT INTO [t1153] VALUES (4639,'مسألة وإن قال هو يهودي أو نصراني أو برىء من الله تعالى أو من القرآن أو الإسلام أو النبي عليه السلام','6580','4617','0');
INSERT INTO [t1153] VALUES (4640,'مسألة وإن قال أنا أستحل الزنا ونحوه فعلى وجهين','6581','4617','0');
INSERT INTO [t1153] VALUES (4641,'مسألة وإن قال عصيت الله أو أنا أعصي الله في كل أمرني به أو محوت المصحف إن فعلت كذا وحنث','6581','4617','0');
INSERT INTO [t1153] VALUES (4642,'مسألة وإن قال عبد فلان حر لأفعلن فليس بشيء وعنه عليه كفارة إن حنث','6582','4617','0');
INSERT INTO [t1153] VALUES (4643,'مسألة وإن قال أيمان البيعة تلزمني فهي يمين رتبها الحجاج تشتمل على اليمين بالله تعالى والطلاق والعتاق','6583','4617','0');
INSERT INTO [t1153] VALUES (4644,'مسألة وإن قال علي نذر أو يمين إن فعلت كذا وفعله فقال أصحابنا عليه كفارة يمين','6583','4617','0');
INSERT INTO [t1153] VALUES (4645,'مسألة وهي تجمع تخييرا وترتيبا بين ثلاثة أشياء إطعام عشرة مساكين أو كسوتهم أو تحرير رقبة','6584','4617','0');
INSERT INTO [t1153] VALUES (4646,'مسألة فإن لم يجد فصيام ثلاثة أيام متتابعة إن شاء قبل الحنث وإن شاء بعده ولا يجوز تقديمها على اليمين','6585','4617','0');
INSERT INTO [t1153] VALUES (4647,'مسألة وهو مخير في التكفير إن شاء قبل الحنث وإن شاء بعده','6586','4617','0');
INSERT INTO [t1153] VALUES (4648,'مسألة ومن كرر أيمانا قبل التكفير فكفارة واحدة وعنه لكل يمين كفارة','6588','4617','0');
INSERT INTO [t1153] VALUES (4649,'مسألة والظاهر فيما إذا تكرر الأيمان أنها إن كانت على فعل واحد فكفارة واحدة وإن كانت على أفعال فعليه','6589','4617','0');
INSERT INTO [t1153] VALUES (4650,'مسألة وإن كانت الأيمان مختلفة الكفارة كالظهار واليمين بالله تعالى فلكل يمين كفارتها مثل أن يحلف بالله تعالى','6590','4617','0');
INSERT INTO [t1153] VALUES (4651,'مسألة وكفارة العبد الصيام وليس لسيده منعه منه ومن بعضه حر فحكمه في الكفارة حكم الأحرار','6591','4617','0');
INSERT INTO [t1153] VALUES (4652,'مسألة وليس للسيد منع عبده من التكفير بالصيام سواء كان الحنث والحلف بإذنه أو بغير إذنه','6593','4617','0');
INSERT INTO [t1153] VALUES (4653,'باب جامع الأيمان','6597','4617','0');
INSERT INTO [t1153] VALUES (4654,'مسألة فإن لم تكن له نية رجع إلى سبب وما هيجها','6598','4617','0');
INSERT INTO [t1153] VALUES (4655,'مسألة وإن حلف أن لا يبيع ثوبه إلا بمائة فباعه باكثر لم يحنث وإن باعه بأقل حنث','6599','4617','0');
INSERT INTO [t1153] VALUES (4656,'مسألة ومن حلف لا يبيع ثوبه بعشر فباعه بها أو بأقل حنث وإن باعه بأكثر لم يحنث','6600','4617','0');
INSERT INTO [t1153] VALUES (4657,'مسألة وإن حلف لا يدخل دارا ونوى اليوم لم يحنث بالدخول في غيره','6600','4617','0');
INSERT INTO [t1153] VALUES (4658,'مسألة وإن حلف لا يشرب له الماء من العطش يقصد قطع منته حنث بأكل خبزه واستعارة دابته','6601','4617','0');
INSERT INTO [t1153] VALUES (4659,'مسألة وإن حلف لا يلبس ثوبا من غزلها يقصد منتها فباعه واشترى بثمنه ثوبا فلبسه حنث','6601','4617','0');
INSERT INTO [t1153] VALUES (4660,'مسألة وإن دعي إلى غذاء فحلف لا يتغذى اختصت يمينه به إذا قصده لما ذكرنا','6601','4617','0');
INSERT INTO [t1153] VALUES (4661,'مسألة فإن حلف لا يأوي معها في دار يريد جفاءها ولم يكن للدار سبب يهيج يمينه فأوى معها في غيرها حنث','6602','4617','0');
INSERT INTO [t1153] VALUES (4662,'مسألة وإن حلف لعامل لا يخرج إلا بإذنه فعزل أو على زوجته فطلقها أو على عبده فاعتقه ونحوه','6604','4617','0');
INSERT INTO [t1153] VALUES (4663,'مسألة وذكر القاضي فيمن قال لامرأته إن سرقت مني شيئا ويعينه فأنت طالق ففعلت لم تطلق','6612','4617','0');
INSERT INTO [t1153] VALUES (4664,'مسألة إذا حلف لا يصوم لم يحنث حتى يصوم يوما','6613','4617','0');
INSERT INTO [t1153] VALUES (4665,'مسألة وإن حلف لا يصلي لم يحنث حتى يفرغ مما يقع عليه اسم الصلاة','6614','4617','0');
INSERT INTO [t1153] VALUES (4666,'مسألة وإن حلف لا يهبه فتصدق عليه حنث وكذلك إن اهدى له أو أعمره','6615','4617','0');
INSERT INTO [t1153] VALUES (4667,'مسألة وإن حلف لا يتصدق عليه فوهبه لم يحنث لأن التصدق نوع من الهبة ولا يحنث الحالف على نوع آخر','6615','4617','0');
INSERT INTO [t1153] VALUES (4668,'مسألة وإن باعه وحاباه حنث في أحد الوجهين','6616','4617','0');
INSERT INTO [t1153] VALUES (4669,'مسألة وإن وصى له لم يحنث','6616','4617','0');
INSERT INTO [t1153] VALUES (4670,'مسألة وإن أعاره لم يحنث إلا عند أبي الخطاب','6616','4617','0');
INSERT INTO [t1153] VALUES (4671,'مسألة وإن وقف عليه حنث، قاله أبو الخطاب','6616','4617','0');
INSERT INTO [t1153] VALUES (4672,'مسألة وإن أكل المرق لم يحنث','6618','4617','0');
INSERT INTO [t1153] VALUES (4673,'مسألة وإن حلف لا يأكل الشحم فأكل شحم الظهر حنث','6619','4617','0');
INSERT INTO [t1153] VALUES (4674,'مسألة وإن حلف لا يأكل لبنا فأكل زبدا أو سمنا أو كشكا أو بصلا أو جبنا لم يحنث','6621','4617','0');
INSERT INTO [t1153] VALUES (4675,'مسألة وإن حلف على الفاكهة فأكل من ثمر الشجر كالجوز واللوز والتمر والرمان حنث وإن أكل البطيخ حنث','6622','4617','0');
INSERT INTO [t1153] VALUES (4676,'مسألة ولا يحنث بأكل القثاء والخيار ونحوه والقرع والباذنجان','6623','4617','0');
INSERT INTO [t1153] VALUES (4677,'مسألة وإن حلف لا يأكل رطبا فأكل مذنبا حنث','6623','4617','0');
INSERT INTO [t1153] VALUES (4678,'مسألة وإن أكل تمرا أو بسرا لم يحنث لأنه ليس برطب','6624','4617','0');
INSERT INTO [t1153] VALUES (4679,'مسألة وإن حلف لا يأكل أدما حنث بأكل البيض والشواء والجبن والملح والزيتون واللبن وسائر ما يصطبغ به','6625','4617','0');
INSERT INTO [t1153] VALUES (4680,'مسألة وإن حلف لا يلبس شيئا فلبس ثوبا أو درعا أو جوشنا أو خفا أو نعلا حنث','6627','4617','0');
INSERT INTO [t1153] VALUES (4681,'مسألة وإن حلف لا يلبس حليا فلبس حلية ذهب أو فضة أو جوهر حنث وإن لبس الدراهم والدنانير','6628','4617','0');
INSERT INTO [t1153] VALUES (4682,'مسألة وإن حلف لا يركب دابة فلان ولا يلبس ثوبه ولا يدخل داره فركب دابة عبده ولبس ثوبه ودخل داره','6630','4617','0');
INSERT INTO [t1153] VALUES (4683,'مسألة وإن حلف لا يركب دابة فلان فركب دابة استعارها لم يحنث وإن ركب دابة استأجرها حنث','6631','4617','0');
INSERT INTO [t1153] VALUES (4684,'مسألة وإن حلف لا يركب دابة عبده فركب دابة جعلت برسمه حنث','6631','4617','0');
INSERT INTO [t1153] VALUES (4685,'مسألة وإن حلف لا يدخل دارا فدخل سطحها حنث وإن دخل طاق الباب احتمل وجهين','6632','4617','0');
INSERT INTO [t1153] VALUES (4686,'مسألة وإن حلف لا يكلم إنسانا حنث كل إنسان ـ لأنه فعل المحلوف عليه ـ فإن زجره فقال تنح أو اسكت','6634','4617','0');
INSERT INTO [t1153] VALUES (4687,'مسألة وإن حلف لا يبتدئه بكلام فتكلما معا حنث','6635','4617','0');
INSERT INTO [t1153] VALUES (4688,'مسألة وإن حلف لا يكلمه حينا فذلك ستة أشهر نص عليه','6637','4617','0');
INSERT INTO [t1153] VALUES (4689,'مسألة وإن قال زمنا أو دهرا أو بعيدا أو مليا أو الزمان رجع إلى أقل ما يتناوله اللفظ','6638','4617','0');
INSERT INTO [t1153] VALUES (4690,'مسألة وإن قال عمرا احتمل أنه كذلك واحتمل أن يكون أربعين عاما','6639','4617','0');
INSERT INTO [t1153] VALUES (4691,'مسألة وإن قال الابد والدهر فذلك على الزمان كله','6639','4617','0');
INSERT INTO [t1153] VALUES (4692,'مسألة والحقب ثمانون عاما وقال مالك أربعون يوما','6640','4617','0');
INSERT INTO [t1153] VALUES (4693,'مسألة والشهور اثنا عشر عند القاضي وعند أبي الخطاب ثلاثة كالأشهر','6640','4617','0');
INSERT INTO [t1153] VALUES (4694,'مسألة والأيام ثلاثة لأنها أقل الجمع قال الله تعالى "واذكروا الله في أيام معدودات"','6640','4617','0');
INSERT INTO [t1153] VALUES (4695,'مسألة وإن حلف لا يدخل باب هذه الدار فحول ودخله حنث','6641','4617','0');
INSERT INTO [t1153] VALUES (4696,'مسألة وإن حلف لا يكلمه إلى حين الحصاد انتهت يمينه بأوله','6642','4617','0');
INSERT INTO [t1153] VALUES (4697,'مسألة وإن حلف لا مال له ولا مال غير زكوي أو دين على الناس حنث','6642','4617','0');
INSERT INTO [t1153] VALUES (4698,'مسألة وإن حلف لا يفعل شيئا فوكل من يفعله حنث إلا أن ينوي','6644','4617','0');
INSERT INTO [t1153] VALUES (4699,'مسألة وإن حلف على وطء امرأة تعلقت يمينه بجماعها','6645','4617','0');
INSERT INTO [t1153] VALUES (4700,'مسألة وإن حلف على وطء دار تعلقت يمينه بدخولها راكبا أو ماشيا أو حافيا أو منتعلا','6645','4617','0');
INSERT INTO [t1153] VALUES (4701,'مسألة وإن حلف لا يشم الريحان فشم الورد والبنفسج والياسمين او لا يشم الورد والبنفسج فشم دهنها','6645','4617','0');
INSERT INTO [t1153] VALUES (4702,'مسألة وإن حلف لا يأكل لحما فأكل سمكا حنث عند الخرقي ولم يحنث عند ابن أبي موسى','6646','4617','0');
INSERT INTO [t1153] VALUES (4703,'مسألة وإن حلف لا يأكل راسا ولا بيضا حنث بأكل رؤوس الطير والسمك والجراد عند القاضي','6647','4617','0');
INSERT INTO [t1153] VALUES (4704,'مسألة وإن حلف لا يدخل بيتا فدخل مسجدا أو حماما أو بيت شعر أو أدم أو لا يركب فركب سفينة حنث','6649','4617','0');
INSERT INTO [t1153] VALUES (4705,'مسألة وإن حلف لا يتكلم فقرأ أو سبح أو ذكر الله تعالى لم يحنث','6650','4617','0');
INSERT INTO [t1153] VALUES (4706,'مسألة وإن حلف ليضربنه مائة سوط فجمعها فضربه بها ضربة واحدة لم يبر في يمينه','6651','4617','0');
INSERT INTO [t1153] VALUES (4707,'مسألة وإن حلف لا يضرب امرأته فخنقها أو نتف شعرها أو عضها حنث','6651','4617','0');
INSERT INTO [t1153] VALUES (4708,'مسألة وإن حلف لا يأكل شحما فأكل اللحم الأحمر فقال الخرقي يحنث لأن الشحم ما يذوب بالنار','6654','4617','0');
INSERT INTO [t1153] VALUES (4709,'مسألة فإن حلف لا يطعم شيئا حنث بأكله وشربه ومصه','6656','4617','0');
INSERT INTO [t1153] VALUES (4710,'مسألة وإن ذاقه ولم يبتلعه لم يحنث','6657','4617','0');
INSERT INTO [t1153] VALUES (4711,'مسألة وإن حلف لا يأكل مائعا فأكله بالخبز حنث','6657','4617','0');
INSERT INTO [t1153] VALUES (4712,'مسألة وإن حلف لا يركب ولا يلبس فاستدام ذلك حنث','6657','4617','0');
INSERT INTO [t1153] VALUES (4713,'مسألة وإن حلف لا يدخل دارا هو داخلها فأقام فيها حنث عند القاضي ولم يحنث عند أبي الخطاب','6658','4617','0');
INSERT INTO [t1153] VALUES (4714,'مسألة وإن حلف لا يدخل على فلان بيتا فدخل فلان عليه فأقام معه فعلى الوجهين','6659','4617','0');
INSERT INTO [t1153] VALUES (4715,'مسألة وإن أقام لنقل أهله ومتاعه لم يحنث','6660','4617','0');
INSERT INTO [t1153] VALUES (4716,'مسألة وإن خرج دون أهله ومتاعه حنث','6662','4617','0');
INSERT INTO [t1153] VALUES (4717,'مسألة وإن حلف لا يساكن فلانا فبنيا بينهما حائطا وهما متساكنان حنث وإن كان في الدار حجرتان','6666','4617','0');
INSERT INTO [t1153] VALUES (4718,'مسألة وإن حلف ليخرجن من هذه البلدة فخرج دون أهله لم يحنث وإن حلف ليخرجن من هذه الدار فخرج','6671','4617','0');
INSERT INTO [t1153] VALUES (4719,'مسألة وإن حلف ليخرجن من هذه البلدة أو ليرحلن عن هذه الدار ففعل له العودة إليها على روايتين','6672','4617','0');
INSERT INTO [t1153] VALUES (4720,'مسألة وإن حلف ليشربن هذا الماء أو ليضربن عبده غدا فتلف المحلوف عليه قبل الغد حنث عند الخرقي','6678','4617','0');
INSERT INTO [t1153] VALUES (4721,'مسألة وإن قال والله لأشربن ماء هذا الكوز غدا فاندفق اليوم أو لآكلن هذا الخبز غدا فتلف','6688','4617','0');
INSERT INTO [t1153] VALUES (4722,'مسألة وإن حلف ليقضينه حقه فأبرأه فهل يحنث على وجهين','6689','4617','0');
INSERT INTO [t1153] VALUES (4723,'مسألة وإن مات المستحق فقضى ورثته لم يحنث ذكره أبو الخطاب لأن قضاء ورثته يقوم مقام قضائه','6691','4617','0');
INSERT INTO [t1153] VALUES (4724,'مسألة وإن باعه بحقه عرضا لم يحنث عند ابن حامد لأنه قد قضاه حقه','6692','4617','0');
INSERT INTO [t1153] VALUES (4725,'مسألة وإن حلف ليقضينه حقه عند رأس الهلال أو مع رأسه أو إلى رأس الهلال أو إلى استهلاله','6693','4617','0');
INSERT INTO [t1153] VALUES (4726,'مسألة وإن حلف لا فارقتك حتى استوفي حقي منك فهرب منه حنث','6695','4617','0');
INSERT INTO [t1153] VALUES (4727,'مسألة فإن حلف لا فترقنا فهرب منه حنث','6713','4617','0');
INSERT INTO [t1153] VALUES (4728,'مسألة وقدر الفرقة ما عده الناس فراقا كفرقة البيع وقد ذكرناه في البيع','6718','4617','0');
INSERT INTO [t1153] VALUES (4729,'باب النذر','6719','4617','0');
INSERT INTO [t1153] VALUES (4730,'أن يلزم نفسه لله تعالى شيئا فيقول: لله علي أن أفعل كذا','6719','4617','0');
INSERT INTO [t1153] VALUES (4731,'مسألة ولا يصح إلا بالقول فإن نواه من غير قول لم يصح','6720','4617','0');
INSERT INTO [t1153] VALUES (4732,'مسألة ولا يصح في محال ولا واجب فلو قال لله علي صوم أمس أو صوم رمضان لم ينعقد','6720','4617','0');
INSERT INTO [t1153] VALUES (4733,'مسألة والنذر المنعقد على خمسة أقسام','6721','4617','0');
INSERT INTO [t1153] VALUES (4734,'مسألة فإن نذر مكروها كالطلاق فإنه مكروه لقول النبي صلى الله عليه وسلم أبغض الحلال إلى الله الطلاق','6723','4617','0');
INSERT INTO [t1153] VALUES (4735,'مسألة إلا أن ينذر ذبح ولده ففيه روايتان إحداهما أنه كذلك والثانية يلزمه ذبح كبش','6724','4617','0');
INSERT INTO [t1153] VALUES (4736,'مسألة ويحتمل أن لا ينعقد نذر المباح ولا المعصية ولا تجب به كفارة','6727','4617','0');
INSERT INTO [t1153] VALUES (4737,'مسألة ولو نذر الصدقة بكل ماله فله الصدقة بثلثه ولا كفارة عليه','6728','4617','0');
INSERT INTO [t1153] VALUES (4738,'مسألة وإن نذر الصدقة بألف لزمه جميعه','6729','4617','0');
INSERT INTO [t1153] VALUES (4739,'مسألة وإن نذر صوم يوم الخميس فوافق عيد أو حيض افطر وقضى وكفر','6732','4617','0');
INSERT INTO [t1153] VALUES (4740,'مسألة وإن نذر صوم سنة لم يدخل في نذره رمضان ويوما العيد وفي أيام التشريق','6732','4617','0');
INSERT INTO [t1153] VALUES (4741,'مسألة ونقل عنه ما يدل على أنه أن صام يوم العيد صح صومه','6733','4617','0');
INSERT INTO [t1153] VALUES (4742,'مسألة وإن وافق أيام التشريق فهل يصومها؟ على روايتين','6733','4617','0');
INSERT INTO [t1153] VALUES (4743,'مسألة وإن نذر صوم يوم يقدم فلان فقدم ليلا فلا شيء عليه وإن قدم نهارا فعنه ما يدل على أنه لا ينعقد نذره','6733','4617','0');
INSERT INTO [t1153] VALUES (4744,'مسألة وإن وافق يوم نذره وهو مجنون فلا قضاء عليه ولا كفارة لأنه خرج عن أهلية التكليف قبل وقت النذر','6736','4617','0');
INSERT INTO [t1153] VALUES (4745,'مسألة وإن نذر صوم شهر معين فلم يصمه لغير عذر فعليه القضاء وكفارة يمين','6737','4617','0');
INSERT INTO [t1153] VALUES (4746,'مسألة وإن صام قبله لم يجزئه وكذلك إن نذر الحج في عام فحج قبله','6737','4617','0');
INSERT INTO [t1153] VALUES (4747,'مسألة وإن أفطر في أثنائه لغير عذر لزمه استئنافه ويكفر ويحتمل أن يتم باقيه ويقضي ويكفر','6737','4617','0');
INSERT INTO [t1153] VALUES (4748,'مسألة وإن نذر صوم شهر لزمه التتابع','6739','4617','0');
INSERT INTO [t1153] VALUES (4749,'مسألة وإن نذر أياما معدودة لم يلزمه التتابع إلا أن يشترطه','6740','4617','0');
INSERT INTO [t1153] VALUES (4750,'مسألة وإن نذر صياما متتابعا فافطر لمرض أو حيض قضى لا غير وإن أفطر لغير عذر لزمه الاستيفاء','6741','4617','0');
INSERT INTO [t1153] VALUES (4751,'مسألة وإن نذر صياما فعجز عنه لكبر أو مرض لا يرجى برؤه أطعم عنه لكل يوم مسكينا ويحتمل أن يكفر','6744','4617','0');
INSERT INTO [t1153] VALUES (4752,'مسألة وإن نذر المشي إلى بيت الله الحرام أو موضع من الحرم لم يجزئه إلا المشي في حج أو عمرة','6747','4617','0');
INSERT INTO [t1153] VALUES (4753,'مسألة فإن نذر الركوب فمشى فعلى الروايتين','6750','4617','0');
INSERT INTO [t1153] VALUES (4754,'مسألة فإن نذر رقبة فهي التي تجزىء عن الواجب إلا أن ينوي رقبة بعينها','6754','4617','0');
INSERT INTO [t1153] VALUES (4755,'مسألة وإن نذر أن يطوف على أربع طاف طوافين','6758','4617','0');
INSERT INTO [t1153] VALUES (4756,'كتاب القضاء','6761','0','0');
INSERT INTO [t1153] VALUES (4757,'كتاب القضاء','6761','4756','0');
INSERT INTO [t1153] VALUES (4758,'مسألة فيجب على الإمام أن ينصب في كل إقليم قاضيا ويختار لذلك أفضل من يجد وأورعهم','6762','4756','0');
INSERT INTO [t1153] VALUES (4759,'مسألة ويختار لذلك أفضل من يجد وأورعهم ويأمر بتقوى الله تعالى وإيثار طاعته في سره وتحري العدل','6763','4756','0');
INSERT INTO [t1153] VALUES (4760,'مسألة وهل يجب من يصلح له إذا طلب ولم يوجد غيره الدخول فيه وعنه أنه سئل هل يأثم القاضي إذا لم يوجد','6763','4756','0');
INSERT INTO [t1153] VALUES (4761,'مسألة وإن وجد غيره كره له طلبه بغير خلاف في المذهب','6765','4756','0');
INSERT INTO [t1153] VALUES (4762,'مسألة وإن طلب فالأفضل ألا يجيب في ظاهر كلام أحمد','6765','4756','0');
INSERT INTO [t1153] VALUES (4763,'مسألة ولا تثبت ولاية القضاء إلا بتولية الإمام أو نائبه لأنها من المصالح العامة فلم تجز إلا من جهة الإمام','6765','4756','0');
INSERT INTO [t1153] VALUES (4764,'مسألة ومن شرط صحتها معرفة المولي كون المولى على صفة تصلح للقضاء','6765','4756','0');
INSERT INTO [t1153] VALUES (4765,'مسألة ويعين ما يوليه الحكم فيه من الأعمال والبلدان ومشافهته بالولاية أو مكاتبته بها وإشهاد شاهدين','6766','4756','0');
INSERT INTO [t1153] VALUES (4766,'مسألة وهل تشترط عدالة المولي على روايتين','6767','4756','0');
INSERT INTO [t1153] VALUES (4767,'مسألة وألفاظ التولية الصريحة سبعة وليتك الحكم وقلدتك أو استنبتك واستخلفتك ورددت إليك','6767','4756','0');
INSERT INTO [t1153] VALUES (4768,'مسألة وله طلب الرزق لنفسه وأمنائه وخلفائه مع الحاجة فأما مع عدمها فعلى وجهين','6768','4756','0');
INSERT INTO [t1153] VALUES (4769,'مسألة فإن جعل إليهما عملا واحدا جاز','6770','4756','0');
INSERT INTO [t1153] VALUES (4770,'مسألة وإذا مات المولي أو عزل المولى مع صلاحيته لم تبطل ولايته في أحد الوجهين وتبطل في الآخر','6771','4756','0');
INSERT INTO [t1153] VALUES (4771,'مسألة وهل ينعزل قبل العلم بالعزل على روايتين بناء على الوكيل','6772','4756','0');
INSERT INTO [t1153] VALUES (4772,'مسالة وإذا قال المولي من نظر في الحكم في البلد الفلاني من فلان وفلان فهو خليفتي','6773','4756','0');
INSERT INTO [t1153] VALUES (4773,'مسألة وإن قال وليت فلانا وفلانا فمن نظر منهما فهو خليفتي انعقدت الولاية لمن ينظر منهما','6774','4756','0');
INSERT INTO [t1153] VALUES (4774,'مسألة وليس من شرط الحاكم أن يكون كاتبا وفيه وجه آخر أنه يشترط ذلك ليعلم ما يكتبه كاتبه','6776','4756','0');
INSERT INTO [t1153] VALUES (4775,'مسألة والمجتهد من يعرف من كتاب الله تعالى وسنة رسول الله صلى الله عليه وسلم الحقيقة والمجاز والأمر','6777','4756','0');
INSERT INTO [t1153] VALUES (4776,'مسألة ويعرف ما أجمع عليه مما اختلف فيه والقياس وحدوده وشروطه وكيفية استنباط الأحكام','6778','4756','0');
INSERT INTO [t1153] VALUES (4777,'باب أدب القاضي','6782','4756','0');
INSERT INTO [t1153] VALUES (4778,'مسألة ويجعل دخوله يوم الاثنين أو الخميس أو السبت إن أمكنه لقوله عليه الصلاة والسلام بورك لأمتي','6783','4756','0');
INSERT INTO [t1153] VALUES (4779,'مسألة فإذا اجتمع الناس أمر بعهده فقرىء عليهم ليعلموا توليته وأمر من ينادي من له حاجة فليحضر يوم كذا','6783','4756','0');
INSERT INTO [t1153] VALUES (4780,'مسألة ثم يخرج في اليوم الذي وعد الجلوس فيه على اعدل أحواله غير غضبان ولا جائع ولا شبعان ولا حاقن','6784','4756','0');
INSERT INTO [t1153] VALUES (4781,'مسألة ويستعين بالله تعالى ويتوكل عليه ويدعوه سرا أن يعصمه من الزلل ويوفقه للصواب ولما يرضيه','6785','4756','0');
INSERT INTO [t1153] VALUES (4782,'مسألة ولا يتخذ حاجبا ولا بوابا يحجب عن الوصول إليه','6786','4756','0');
INSERT INTO [t1153] VALUES (4783,'مسألة ويعرض القصص فيبدأ بالأول فالأول','6786','4756','0');
INSERT INTO [t1153] VALUES (4784,'مسألة ولا يعلمه كيف يدعي في أحد الوجهين لما ذكرنا وفي الآخر له تحرير الدعوى إذا لم يحسن تحريرها','6787','4756','0');
INSERT INTO [t1153] VALUES (4785,'مسألة ويحضر مجلسه الفقهاء من كل مذهب','6787','4756','0');
INSERT INTO [t1153] VALUES (4786,'مسألة والمشاورة ههنا لاستخراج الأدلة','6788','4756','0');
INSERT INTO [t1153] VALUES (4787,'مسألة فإن اتضح له الحكم حكم وإلا أخره ولا يقلد غيره وإن كان أعلم منه','6788','4756','0');
INSERT INTO [t1153] VALUES (4788,'مسألة ولا يقضي وهو غضبان ولا حاقن ولا في شدة الجوع والعطش والهم والوجع والنعاس والبرد المؤلم','6789','4756','0');
INSERT INTO [t1153] VALUES (4789,'مسألة ولا يحل له أن يرتشي ولا يقبل الهدية إلا ممن كان يهدي إليه قبل ولايته بشرط أن لا تكون له حكومة','6790','4756','0');
INSERT INTO [t1153] VALUES (4790,'مسألة ويكره أن يتولى البيع والشراء بنفسه ويستحب أن يوكل في ذلك من لا يعرف أنه وكيله','6792','4756','0');
INSERT INTO [t1153] VALUES (4791,'مسألة فإن ارتشى الحاكم أو قبل هدية ليس له قبولها ردها إلى اربابها','6792','4756','0');
INSERT INTO [t1153] VALUES (4792,'مسألة وتستحب له عيادة المرضى وشهود الجنائز ما لم تشغله عن الحكم وزيارة الأخوان والصالحين من الناس','6793','4756','0');
INSERT INTO [t1153] VALUES (4793,'مسألة وله حضور الولائم','6793','4756','0');
INSERT INTO [t1153] VALUES (4794,'مسألة ويوضي الوكلاء والأعوان على بابه بالرفق بالخصوم وقلة الطمع ويجتهد أن يكونوا شيوخا أو كهولا','6794','4756','0');
INSERT INTO [t1153] VALUES (4795,'مسألة ويتخذ كاتبا مسلما مكلفا عدلا حافظا عالما يجلسه حيث يشاهد ما يكتبه ويجعل القمطر مختوما بين يديه','6794','4756','0');
INSERT INTO [t1153] VALUES (4796,'مسألة ويسحتب أن لا يحكم إلا بحضرة الشهود','6795','4756','0');
INSERT INTO [t1153] VALUES (4797,'مسألة ولا يحكم لنفسه ولا لمن لا تقبل شهادته له ويحكم بينهم بعض خلفائه','6795','4756','0');
INSERT INTO [t1153] VALUES (4798,'مسألة وإن كان حبس في تهمة أو افتئات على القاضي','6798','4756','0');
INSERT INTO [t1153] VALUES (4799,'مسألة وإن لم يحضر له خصم فقال حبست ظلما ولا حق علي ولا خصم نادى بذلك ثلاثا فإن حضر له خصم','6798','4756','0');
INSERT INTO [t1153] VALUES (4800,'مسألة ثم ينظر في أمر المجانين واليتامى والوقوف','6799','4756','0');
INSERT INTO [t1153] VALUES (4801,'مسألة ثم ينظر في حال القاضي قبله فإن كان ممن يصلح للقضاء لم ينقض من أحكامه إلا ما خالف نص كتاب','6800','4756','0');
INSERT INTO [t1153] VALUES (4802,'مسألة وإن كان ممن لا يصلح نقض أحكامه وإن وافقت الصحيح ويحتمل أن لا ينقض الصواب منها','6802','4756','0');
INSERT INTO [t1153] VALUES (4803,'مسألة وإن استعداه أحد على خصم له أحضره وعنه لا يحضره حتى يعلم أن لما ادعاه أصلا','6803','4756','0');
INSERT INTO [t1153] VALUES (4804,'مسألة وإن استعداه على القاضي قبله سأله عما يدعيه فإن قال لي عليه دين من معاملة أو رشوة راسلة بذلك','6804','4756','0');
INSERT INTO [t1153] VALUES (4805,'مسألة وإن قال حكم علي بشهادة فاسقين فالقول قوله بغير يمين','6805','4756','0');
INSERT INTO [t1153] VALUES (4806,'مسألة فإن ادعى على أمراة غير برزة لم يحضرها وامرها بالتوكيل فإن وجبت عليها اليمين أرسل إليها من يحلفها','6807','4756','0');
INSERT INTO [t1153] VALUES (4807,'مسألة وإن ادعى على غائب عن البلد في موضع لا حاكم فيه كتب إلى ثقات من أهل ذلك البلد','6808','4756','0');
INSERT INTO [t1153] VALUES (4808,'باب طريق الحكم وصفته','6809','4756','0');
INSERT INTO [t1153] VALUES (4809,'مسألة وإن أقر لم يحكم له حتى يطالبه المدعي بالحكم','6810','4756','0');
INSERT INTO [t1153] VALUES (4810,'مسألة وللمدعي أن يقول لي بينة؟','6811','4756','0');
INSERT INTO [t1153] VALUES (4811,'مسألة وإن أنكر مثل أن يقول المدعي أقرضته ألفا أو بعته فيقول ما أقرضني ولا باعني','6811','4756','0');
INSERT INTO [t1153] VALUES (4812,'مسألة وإذا سمع الحاكم الشهادة وكانت صحيحة حكم بها إذا سأله المدعي','6812','4756','0');
INSERT INTO [t1153] VALUES (4813,'مسألة ولا خلاف في أنه يجوز له الحكم بالإقرار والبينة في مجلسه إذا سمعه معه شاهدان فإن لم يسمعه معه أحد أو','6812','4756','0');
INSERT INTO [t1153] VALUES (4814,'مسألة وليس له الحكم بعلمه فيما رآه أوسمعه في غير مجلسه نص عليه وهو اختيار الأصحاب وعنه ما يدل','6812','4756','0');
INSERT INTO [t1153] VALUES (4815,'مسألة وإن قال المدعي ما لي بينة فالقول قول المنكر مع يمينه فيعلمه أن له اليمين على خصمه فإن سأله إحلافه','6814','4756','0');
INSERT INTO [t1153] VALUES (4816,'مسألة وإن أحلفه أو حلف من غير سؤال المدعي لم يعتد بيمينه','6815','4756','0');
INSERT INTO [t1153] VALUES (4817,'مسألة وإن نكل قضى عليه بالنكول','6815','4756','0');
INSERT INTO [t1153] VALUES (4818,'مسألة وإن قال لا أعلم لي بينة ثم قال علمت لي بينة','6816','4756','0');
INSERT INTO [t1153] VALUES (4819,'مسألة وإن قال المدعي لي بينة بعد قوله مالي بينة لم يسمع ذكره الخرقي','6816','4756','0');
INSERT INTO [t1153] VALUES (4820,'مسألة فإن نكل أيضا صرفهما','6816','4756','0');
INSERT INTO [t1153] VALUES (4821,'مسألة وإن قال شاهدان نحن نشهد لك فقال هذان بينتي','6817','4756','0');
INSERT INTO [t1153] VALUES (4822,'مسألة وإن قال ما أريد أن تشهدا لي','6817','4756','0');
INSERT INTO [t1153] VALUES (4823,'مسألة وإن قال لي بينة وأريد يمينه فإن كانت غائبة فله إحلافه وإن كانت حاضرة فهل له ذلك على وجهين','6817','4756','0');
INSERT INTO [t1153] VALUES (4824,'مسألة فإن سكت عن جواب المدعي فلم يقر ولم ينكر حبسه الحاكم حتى يجيب ولا يجعله بذلك ناكلا','6818','4756','0');
INSERT INTO [t1153] VALUES (4825,'مسألة وإن حلف المنكر ثم أحضر المدعي بينة حكم بها ولم تكن اليمين مزيلة للحق','6818','4756','0');
INSERT INTO [t1153] VALUES (4826,'مسألة وإن قال لي حساب أريد أن أنظر فيه لم يلزم المدعي إنظاره','6820','4756','0');
INSERT INTO [t1153] VALUES (4827,'مسألة وإن قال قضيته أو ابرأني ولي بينة بالقضاء أو الإبراء وسأل الأنظار أنظر ثلاثا','6820','4756','0');
INSERT INTO [t1153] VALUES (4828,'مسألة وإن قال لي مخرج مما ادعاه لم يكن مجيبا','6820','4756','0');
INSERT INTO [t1153] VALUES (4829,'مسألة وإن ادعى عليه عينا في يده فأقر بها لغيره جعل الخصم فيها وهل يحلف المدعى عليه على وجهين','6820','4756','0');
INSERT INTO [t1153] VALUES (4830,'مسألة وإن أقر بها الغائب أو لغير مكلف معين كالصبي والمجنون صارت الدعوى عليه فإن لم تكن للمدعي بينة لم','6822','4756','0');
INSERT INTO [t1153] VALUES (4831,'مسألة وإن أقر بها لمجهول قيل له إما إن تعرفه وإما أن نجعلك ناكلا وقضينا عليك فإن أصر قضي عليه بالنكول','6823','4756','0');
INSERT INTO [t1153] VALUES (4832,'مسألة فإن كان المدعى عينا بالإشارة حاضرة عينها بالإشارة لأنها تعلم بذلك وإن كانت غائبة ذكر صفاتها','6823','4756','0');
INSERT INTO [t1153] VALUES (4833,'مسألة وإن كانت تالفة من ذوات الأمثال ذكر قدرها وجنسها وصفتها','6824','4756','0');
INSERT INTO [t1153] VALUES (4834,'مسألة وإن ادعى نكاحا فلا بد من ذكر المرأة بعينها إن حضرت وإلا ذكر اسمها ونسبها وذكر شرروط النكاح','6825','4756','0');
INSERT INTO [t1153] VALUES (4835,'مسألة وإذا ادعى بيعا أو عقدا سواه فهل يشترط ذكر شروطه يحتمل وجهين','6826','4756','0');
INSERT INTO [t1153] VALUES (4836,'مسألة وإن ادعت المرأة نكاحا على رجل وادعت معها نفقة أو مهرا سمعت دعواها وإن لم تدع سوى النكاح','6826','4756','0');
INSERT INTO [t1153] VALUES (4837,'مسألة وإن ادعى قتل موروثه ذكر القاتل وأنه انفرد به أو شاركه فيه غيره وأنه قتله عمدا أو خطأ أو شبه عمد','6827','4756','0');
INSERT INTO [t1153] VALUES (4838,'مسألة وإن ادعى سيفا محلى بذهب قومه بغير جنس حليته وإن كان محلى بذهب وفضة قومه بما شاء منهما','6828','4756','0');
INSERT INTO [t1153] VALUES (4839,'مسألة وإن ادعى الإرث ذكر سببه','6828','4756','0');
INSERT INTO [t1153] VALUES (4840,'مسألة وإن علم الحاكم عدالتهما عمل بعلمه وحكم بشهادتهما','6831','4756','0');
INSERT INTO [t1153] VALUES (4841,'مسألة إلا أن يرتاب بهما فيفرقهما ويسأل كل واحد منهما كيف تحملت الشهادة؟ ومتى؟ وفي أي موضع؟','6832','4756','0');
INSERT INTO [t1153] VALUES (4842,'مسألة وإن اتفقوا وعظهم وخوفهم كما روي عن شريح أنه كان يقول للشاهدين إذا حضرا يا هذان ألا تريان','6832','4756','0');
INSERT INTO [t1153] VALUES (4843,'مسألة وليس للحاكم أن يرتب شهودا لا يقبل غيرهم','6833','4756','0');
INSERT INTO [t1153] VALUES (4844,'مسألة فإن ثبتا حكم بشهادتهما لأن الظاهر صدقهما ولا يحكم حتى يسأله المدعي لأن الحق له وقد ذكرناه','6834','4756','0');
INSERT INTO [t1153] VALUES (4845,'مسألة ولا يسمع الجرح إلا مفسرا بما يقدح في العدالة ويعتبر فيه اللفظ فيقول أشهد أني رأيته يشرب الخمر','6835','4756','0');
INSERT INTO [t1153] VALUES (4846,'مسألة وإن جرحهما المشهود عليه كلف البينة بالجرح فإن سأل الأنظار انظر ثلاثا ليجرحهما','6835','4756','0');
INSERT INTO [t1153] VALUES (4847,'مسألة وللمدعي ملازمته إلا أن يقيم بينة بالجرح','6835','4756','0');
INSERT INTO [t1153] VALUES (4848,'مسألة وإن شهد عنده فاسق يعرف حاله قال للمدعي زدني شهودا','6837','4756','0');
INSERT INTO [t1153] VALUES (4849,'مسألة ويكفي في التزكية شاهدان يشهدان أنه عدل رضي ولا يحتاج في التزكية أن يقول علي ولي','6838','4756','0');
INSERT INTO [t1153] VALUES (4850,'مسألة وإن جهل حاله طالب المدعي بتزكيته','6838','4756','0');
INSERT INTO [t1153] VALUES (4851,'مسألة وإن عدله اثنان وجرحه اثنان فالجرح أولى','6839','4756','0');
INSERT INTO [t1153] VALUES (4852,'مسألة وإن سأل المدعي حبس المشهود عليه حتى يزكي شهوده فهل يحبس؟ على وجهين','6840','4756','0');
INSERT INTO [t1153] VALUES (4853,'مسألة وإن أقام شاهدا وسأل حبسه حتى يقيم الآخر حبسه إن كان في المال','6840','4756','0');
INSERT INTO [t1153] VALUES (4854,'مسألة وإن حاكم إليه من لا يعرف لسانه ترجم له من يعرف لسانه','6841','4756','0');
INSERT INTO [t1153] VALUES (4855,'مسألة ولا يقبل في الترجمة والجرح والتعديل والتعريف والرسالة إلا قول عدلين','6841','4756','0');
INSERT INTO [t1153] VALUES (4856,'مسألة ومن ثبتت عدالته مرة فهل يحتاج إلى تجديد البحث عن عدالته مرة أخرى؟ على وجهين','6843','4756','0');
INSERT INTO [t1153] VALUES (4857,'مسألة والميت المدعى عليه كالغائب بل أولى','6844','4756','0');
INSERT INTO [t1153] VALUES (4858,'مسألة ثم إذا ما قدم الغائب أو بلغ الصبي أو أفاق المجنون فهو على حجته','6845','4756','0');
INSERT INTO [t1153] VALUES (4859,'مسألة وهل يحلف المدعى عليه إذا لم يبرأ إليه منه ولا من شيء منه؟ على روايتين','6845','4756','0');
INSERT INTO [t1153] VALUES (4860,'مسألة وإن كان الخصم في البلد غائبا عن المجلس لم تسمع البينة حتى يحضر فإن امتنع من الحضور سمعت البينة','6846','4756','0');
INSERT INTO [t1153] VALUES (4861,'مسألة وإن ادعى أن أباه مات عنه وعن اخ له غائب وله مال في يد فلان أو دين عليه فأقر المدعى عليه أو ثبتت','6847','4756','0');
INSERT INTO [t1153] VALUES (4862,'مسألة وإن ادعى إنسان أن الحاكم حكم له بحق فصدقه قبل قول الحاكم وحده','6849','4756','0');
INSERT INTO [t1153] VALUES (4863,'مسألة وإن لم يشهد به أحد لكن وجده في قمطره في صحيفة تحت ختمه بخطه فهل ينفذه على روايتين','6850','4756','0');
INSERT INTO [t1153] VALUES (4864,'مسألة وكذلك الشاهد إذا وجد خطه بشهادة في كتاب ولم يذكرها فهل له أن يشهد بها على روايتين','6850','4756','0');
INSERT INTO [t1153] VALUES (4865,'مسألة وحكم الحاكم لا يزيل الشيء عن صفته في الباطن وذكر ابن أبي موسى عنه رواية أخرى أنه يزيل العقود','6853','4756','0');
INSERT INTO [t1153] VALUES (4866,'باب حكم كتاب القاضي إلى القاضي','6855','4756','0');
INSERT INTO [t1153] VALUES (4867,'مسألة ويجوز كتاب القاضي فيما حكم به لينفذه في المسافة القريبة ومسافة القصر ويجوز فيما ثبت عنده ليحكم','6857','4756','0');
INSERT INTO [t1153] VALUES (4868,'مسألة ويجوز أن يكتب إلى قاض معين وإلى من يصل إليه كتابي هذا من قضاة المسلمين وحكامهم من غير تعيين','6859','4756','0');
INSERT INTO [t1153] VALUES (4869,'مسألة ولا يقبل الكتاب إلا أن يشهد به شاهدان يحضرهما القاضي الكاتب فيقرؤه عليهما ثم يقول أشهد كما','6859','4756','0');
INSERT INTO [t1153] VALUES (4870,'مسألة ولو أدرج الكتاب وختمه وقال هذا خطي اشهدا علي بما فيه أو قد أشهدتكما على نفسي بما فيه لم يصح','6862','4756','0');
INSERT INTO [t1153] VALUES (4871,'مسألة وإذا وصل الكتاب فأحضر المكتوب إليه الخصم المحكوم عليه في الكتاب فقال لست فلان بن فلان','6863','4756','0');
INSERT INTO [t1153] VALUES (4872,'مسألة وكل من ثبت له عند حاكم حق أو ثبتت براءته مثل أن أنكر وحلفه الحاكم أن يكتب له محضرا بما جرى','6868','4756','0');
INSERT INTO [t1153] VALUES (4873,'مسألة وإن سأل من ثبت محضره عند الحاكم أن يسجل به فعل ذلك وجعله نسختين يدفعها اليه ونسخة يحبسها','6869','4756','0');
INSERT INTO [t1153] VALUES (4874,'فصل في صفة الكتاب إلى القاضي','6872','4756','0');
INSERT INTO [t1153] VALUES (4875,'باب القسمة','6876','4756','0');
INSERT INTO [t1153] VALUES (4876,'مسألة وهذه القسمة جارية مجرى البيع لما فيها من الرد وبهذا يصير بيعا','6877','4756','0');
INSERT INTO [t1153] VALUES (4877,'مسألة والضرر المانع من القسمة هو نقص القيمة بالقسم في ظاهر كلام أحمد ولا ينتفعان به مقسوما في ظاهر','6878','4756','0');
INSERT INTO [t1153] VALUES (4878,'مسألة وإن كان الضرر على أحدهما دون الآخر كرجلين لأحدهما الثلثان وللآخر الثلث ينتفع صاحب الثلثين','6880','4756','0');
INSERT INTO [t1153] VALUES (4879,'مسألة وإن كان بينهما عبيد أو بهائم أو ثياب ونحوها فطلب أحدهما قسمها أعيانا بالقيمة لم يجبر الآخر عليه','6881','4756','0');
INSERT INTO [t1153] VALUES (4880,'مسألة وإن كان بينهما حائط لم يجبر الممتنع من قسمته وإن استهدم لم يجبر على قسم عرضته وقال أصحابنا إن','6883','4756','0');
INSERT INTO [t1153] VALUES (4881,'مسألة وإن كان بينهما دار لها علو وسفل فطلب أحدهما قسمها لأحدهما العلو والآخر السفل أو كان بينهما','6884','4756','0');
INSERT INTO [t1153] VALUES (4882,'مسألة وإن كان بينهما أرض ذات زرع فطلب أحدهما قسمتها دون الزرع قسمت لأنه لا ضرر في قسمها','6886','4756','0');
INSERT INTO [t1153] VALUES (4883,'مسألة وإن طلب قسمتها مع الزرع لم يجبر الآخر','6886','4756','0');
INSERT INTO [t1153] VALUES (4884,'مسألة وإن تراضوا عليه والزرع قصيل أو قطن جاز','6887','4756','0');
INSERT INTO [t1153] VALUES (4885,'مسألة وإن كان بينهما نهر أو قناة أو عين ينبع ماؤها فالماء بينهما على ما اشترطا عند استخراج ذلك','6887','4756','0');
INSERT INTO [t1153] VALUES (4886,'مسألة وإن أراد قسم ذلك بنصب خشبة أو حجر في مصدم الماء فيه ثقبان على قدر حق كل واحد منهما جاز','6888','4756','0');
INSERT INTO [t1153] VALUES (4887,'مسألة وهذه القسمة إفراز حق أحدهما من الآخر وليست بيعا','6890','4756','0');
INSERT INTO [t1153] VALUES (4888,'مسألة فمتى عدلت السهام وأخرجت القرعة لزمت القسمة','6891','4756','0');
INSERT INTO [t1153] VALUES (4889,'مسألة وإذا سألوا الحاكم قسمة عقار لم يثبت عنده أنه لهم قسمه وذكر في كتاب القسمة أنه قسمة بمجرد','6892','4756','0');
INSERT INTO [t1153] VALUES (4890,'مسألة وإذا كان في القسمة تقويم لم يجز أقل من قاسمين لأنها شهادة بالقيمة فلم يقبل فيها أقل من اثنين كسائر','6892','4756','0');
INSERT INTO [t1153] VALUES (4891,'مسألة وإن تقاسموا ثم استحق من حصة أحدهم شيء معين بطلت القسمة وإن كان شائعا فيهما فعلى وجهين','6901','4756','0');
INSERT INTO [t1153] VALUES (4892,'مسألة وإن اقتسما دارين قسمة تراض فبنى أحدهما في نصيبه ثم خرجت الدار مستحقة فقلع بناؤه رجع بنصف','6901','4756','0');
INSERT INTO [t1153] VALUES (4893,'مسألة وإن خرج في نصيب أحدهما عيب','6902','4756','0');
INSERT INTO [t1153] VALUES (4894,'مسألة وإذا اقتسم الورثة العقار ثم ظهر على الميت دين فإن قلنا هو إفراز حق لم تبطل القسمة وإن قلنا هي بيع','6902','4756','0');
INSERT INTO [t1153] VALUES (4895,'مسألة وإن اقتسما فحصلت الطريق في نصيب أحدهما ولا منفذ للآخر بطلت القسمة','6905','4756','0');
INSERT INTO [t1153] VALUES (4896,'مسألة ويجوز للأب والوصي قسم مال المولى عليه مع شريكه','6905','4756','0');
INSERT INTO [t1153] VALUES (4897,'كتاب الشهادات','6907','0','0');
INSERT INTO [t1153] VALUES (4898,'بسم الله الرحمن الرحيم كتاب الشهادات','6907','4897','0');
INSERT INTO [t1153] VALUES (4899,'مسألة: قال الخرقي: ومن لزمته الشهادة فعليه أن يقوم بها على القريب والبعيد لا يسعه التخلف عن إقامتها وهو قادر على ذلك','6909','4897','0');
INSERT INTO [t1153] VALUES (4900,'مسألة: ولا يجوز لمن تعينت عليه أخذ الأجرة عليها، ولا يجوز ذلك لمن لم تتعين عليه في أصح الوجهين','6910','4897','0');
INSERT INTO [t1153] VALUES (4901,'مسألة: ومن كانت عنده شهادة في حد لله تعالى أبيح إقامتها ولم يستحب وللحاكم أن يعرض له بالوقوف في أحد الوجهين','6910','4897','0');
INSERT INTO [t1153] VALUES (4902,'مسألة: ومن كانت عنده شهادة لآدمي يعلمها لم يقمها حتى يسأله، فإن لم يعلمها استحب له إعلامه بها وله إقامتها قبل ذلك','6911','4897','0');
INSERT INTO [t1153] VALUES (4903,'مسألة: ولا يجوز أن يشهد إلا بما يعلمه برؤية أو سماع','6912','4897','0');
INSERT INTO [t1153] VALUES (4904,'مسألة: والرؤية تختص بالأفعال كالقتل والغصب والسرقة وشرب الخمر والرضاع والولادة وغيرها','6912','4897','0');
INSERT INTO [t1153] VALUES (4905,'مسألة: والسماع على ضربين من المشهود عليه نحو الإقرار والعقود والطلاق','6913','4897','0');
INSERT INTO [t1153] VALUES (4906,'مسألة: الضرب الثاني سماع من جهة الاستفاضة فيما يتعذر علمه في الغالب إلا بذلك كالنسب والموت والملك والنكاح والخلع والوقف ومصرفه والعتق والولاء والعزل وما اشبه بذلك','6915','4897','0');
INSERT INTO [t1153] VALUES (4907,'مسألة: ولا تقبل الاستفاضة إلا من عدد يقع العلم بخبرهم في ظاهر كلام أحمد والخرقي، وقال القاضي: تسمع من عدلين فصاعدا','6917','4897','0');
INSERT INTO [t1153] VALUES (4908,'مسألة: وإن سمع إنسانا يقر بنسب أب أو ابن فصدقة المقر له جاز أن يشهد له به، وإن كذبه لم يشهد، وإن سكت جاز أن يشهد، ويحتمل ألا يشهد حتى يتكرر','6917','4897','0');
INSERT INTO [t1153] VALUES (4909,'مسألة: وإن رأى شيئا في يد إنسان يتصرف فيه تصرف الملاك من النقض والبناء والإجارة والإعارة ونحوها جاز أن يشهد بالملك','6918','4897','0');
INSERT INTO [t1153] VALUES (4910,'مسألة: وإن شهد بالرضاع فلا بد من ذكر عدد الرضعات وأنه شرب من ثديها أو من لبن حلب منه','6919','4897','0');
INSERT INTO [t1153] VALUES (4911,'مسألة: وإن شهد بالقتل احتاج أن يقول ضربه بالسيف أو جرحه فقتله أو مات من ذلك فإن قال جرحه فمات لم يحكم به','6919','4897','0');
INSERT INTO [t1153] VALUES (4912,'مسألة: ومن شهد بالزنا فلا بد أن يذكر بمن زنا؟ وأين زنا وأنه رأى ذكره في فرجها','6919','4897','0');
INSERT INTO [t1153] VALUES (4913,'مسألة: وإن شهد بالقذف فلا بد من ذكر المقذوف وصفة القذف لذلك','6920','4897','0');
INSERT INTO [t1153] VALUES (4914,'مسألة: وإن شهد أن هذا العبد ابن أمة فلان لم يحكم به حتى يقولا ولدته في ملكه','6920','4897','0');
INSERT INTO [t1153] VALUES (4915,'مسألة: وإن شهدت أنه اشتراها من فلان أو وقفها عليه أو أعتقها لم يحكم بها حتى يقولا وهي في ملكه','6921','4897','0');
INSERT INTO [t1153] VALUES (4916,'مسألة: وإن شهد أن هذا الغزل من قطنه والطائر من بيضته أو الدقيق من حنطته حكم له بها','6921','4897','0');
INSERT INTO [t1153] VALUES (4917,'مسألة: وتجوز شهادة المستخفي','6923','4897','0');
INSERT INTO [t1153] VALUES (4918,'مسألة: ومن سمع رجلا يقر بحق أو يشهد شاهدا بحق، أو سمع حاكما يحكم أو يشهد على حكمه وإنفاذه جاز أن يشهد به في إحدى الروايتين ولا يجوز في الأخرى حتى يشهده على ذلك','6923','4897','0');
INSERT INTO [t1153] VALUES (4919,'مسألة: وإن شهد أحدهما أنه أقر له بألف أمس، وشهد آخر أنه أقر له بألف اليوم، أو شهد أحداهما أنه باعه داره أمس، وشهد آخر أنه باعه إياها اليوم، كملت البينة وثبت البيع والاقرار، وكذلك كل شهادة على القول','6928','4897','0');
INSERT INTO [t1153] VALUES (4920,'مسألة: وكذلك القذف إذا شهد أحدهما أنه قذفه غدوة وشهد الآخر أنه قذفه عشية، أو شهد أحدهما أنه قذفه بالعربية، وشهد الآخر أنه قذفه بالعجمية، أو اختلفا في المكان لم يثبت القذف','6929','4897','0');
INSERT INTO [t1153] VALUES (4921,'مسألة: وإن شهد شاهد أنه أقر له بألفين، وشهد آخر أنه أقر له بألف ثبت الألف، ويحلف على الآخر مع شاهده إن أحب','6931','4897','0');
INSERT INTO [t1153] VALUES (4922,'مسألة: وإن شهد أحدهما أن له عليه ألفا وشهد آخر أن له عليه ألفين فهل تكمل البينة على ألف؟ على وجهين','6932','4897','0');
INSERT INTO [t1153] VALUES (4923,'مسألة: وإن شهد أحدهما أن له عليه ألفا من قرض، وشهد آخر أن له عليه ألفا من ثمن مبيع لم تكمل البينة','6933','4897','0');
INSERT INTO [t1153] VALUES (4924,'مسألة: وإن شهد شاهدان أن له عليه ألفا، وقال أحدهما قضاه بعضه، بطلت شهادته نص عليه، وإن شهد أنه أقرضه ألفا وقال أحدهما: قضاه نصفه، صحت شهادتهما','6933','4897','0');
INSERT INTO [t1153] VALUES (4925,'مسألة: وإن كانت له بينة بألف فقال: أريد أن تشهدا لي بخمسمائة لم يجز','6934','4897','0');
INSERT INTO [t1153] VALUES (4926,'باب شروط من تقبل شهادته','6936','4897','0');
INSERT INTO [t1153] VALUES (4927,'مسألة: ويتخرج قبول شهادة أهل الذمة على قبول شهادة الفاسق من جهة الاعتقاد المتدين به إذا لم يتدين بالشهادة لموافقيه على مخالفيه كالخطابية','6945','4897','0');
INSERT INTO [t1153] VALUES (4928,'مسألة: فأما من فعل شيئا من الفروع المختلف فيها فتزوج بغير ولي، أو شرب من النبيذ ما لا يسكره، أو أخر الحج مع امكانه ونحوه متأولا، فلا ترد شهادته','6946','4897','0');
INSERT INTO [t1153] VALUES (4929,'مسألة: الثاني: استعمال المروءة، وهو فعل ما يجمله ويزينه وترك ما يدنسه ويشينه، فلا تقبل شهادة المصانع، والمتمسخر، والمغني، والرقاص، واللاعب بالشطرنج، والنرد، والحمام، والذي يتغدى في السوق ويمد رجليه في مجمع الناس، ويحدث بمباضعته أهله وأمته','6947','4897','0');
INSERT INTO [t1153] VALUES (4930,'فصل في اللعب','6949','4897','0');
INSERT INTO [t1153] VALUES (4931,'فصل في الملاهي','6953','4897','0');
INSERT INTO [t1153] VALUES (4932,'مسألة: ولا تقبل شهادة القاذف حتى يتوب','6966','4897','0');
INSERT INTO [t1153] VALUES (4933,'مسألة: وتوبته أن يكذب نفسه، وقيل: إن علم صدق فتوبته أن يقول: قد ندمت على ما قلت ولا أعود إلى مثله وأنا تائب إلى الله تعالى منه','6969','4897','0');
INSERT INTO [t1153] VALUES (4934,'مسألة: وتجوز شهادة الأعمى في المسموعات إذا تيقن الصوت وبالاستفاضة','6972','4897','0');
INSERT INTO [t1153] VALUES (4935,'مسألة: وتجوز في المرئيات التي تحملها قبل العمى إذا عرف الفاعل باسمه ونسبه ما يتميز به','6973','4897','0');
INSERT INTO [t1153] VALUES (4936,'مسألة: وشهادة ولد الزنا جائزة في الزنا وغيره','6974','4897','0');
INSERT INTO [t1153] VALUES (4937,'مسألة: فإن لم يعرف المشهود عليه باسمه ونسبه ولم يعرفه إلا بعينه، قبلت شهادته أيضا لما ذكرنا في المسألة الأولى','6974','4897','0');
INSERT INTO [t1153] VALUES (4938,'مسألة: وإن شهد عند الحاكم ثم عمي قبلت شهادته وجاز الحكم بها','6974','4897','0');
INSERT INTO [t1153] VALUES (4939,'مسألة: وتقبل شهادة البدوي على القروي والقروي على البدوي إذا اجتمعت الشروط','6975','4897','0');
INSERT INTO [t1153] VALUES (4940,'مسألة: وتقبل شهادة الإنسان على فعل نفسه كالمرضعة بالرضاع، والقاسم على القسمة، والحاكم على حكمه بعد العزل','6975','4897','0');
INSERT INTO [t1153] VALUES (4941,'باب موانع الشهادة','6976','4897','0');
INSERT INTO [t1153] VALUES (4942,'مسألة: ولا تقبل شهادة أحد الزوجين لصاحبه في إحدى الروايتين','6978','4897','0');
INSERT INTO [t1153] VALUES (4943,'مسألة: ولا تقبل شهادة السيد لعبده ولا العبد لسيده','6979','4897','0');
INSERT INTO [t1153] VALUES (4944,'مسألة: وتقبل شهادة الأخ لأخيه وسائر الأقارب، والصديق لصديقه والمولى لعتيقه','6979','4897','0');
INSERT INTO [t1153] VALUES (4945,'مسألة: ولا تقبل شهادة الموصى له للميت والوكيل لموكله بما هو وكيل فيه، والشريك لشريكه والغرماء للمفلس بالمال، وأحد الشفيعين بعفو الآخر عن شفاعته، وكذلك المضارب بمال المضاربة','6982','4897','0');
INSERT INTO [t1153] VALUES (4946,'مسألة: ولو لم يشهد بها عند الحاكم حتى صار عدلا قبلت','6986','4897','0');
INSERT INTO [t1153] VALUES (4947,'مسألة: وإن شهد لمكاتبه أو لموروثه بجرح قبل برئه فردت ثم أعادها بعد عتق المكاتب وبرء الجرح ففي ردهما وجهان','6987','4897','0');
INSERT INTO [t1153] VALUES (4948,'مسألة: ولو شهد وهو كافر أو صبي أو عبد فردت شهادتهم ثم أعادوها بعد زوال الكفر والصبا والرق قبلت','6987','4897','0');
INSERT INTO [t1153] VALUES (4949,'مسألة: وإن شهد الشريك بعفو شريكه عن الشفعة ثم عفا الشاهد عن شفعته وأعاد تلك الشهادة لم تقبل ذكره القاضي','6988','4897','0');
INSERT INTO [t1153] VALUES (4950,'باب أقسام المشهود به','6988','4897','0');
INSERT INTO [t1153] VALUES (4951,'مسألة: إذا ادعى رجل الخلع قبل فيه رجل وامرأتان أو شاهد ويمين المدعي','7004','4897','0');
INSERT INTO [t1153] VALUES (4952,'مسألة: وإذا شهد رجل وامرأتان لرجل بجارية أنها أم ولده وولدها منه قضي له بالجارية أم الولد، وهل تثبت حرية الولد ونسبه من مدعيه؟ على روايتين','7005','4897','0');
INSERT INTO [t1153] VALUES (4953,'باب الشهادة على الشهادة والرجوع عن الشهادة','7006','4897','0');
INSERT INTO [t1153] VALUES (4954,'مسألة: وتثبت شهادة شاهدي الأصل بشهادة شاهدين يشهدان عليهما سواء شهدوا على كل واحد منهما أو شهد على كل واحد منهما شاهد من شهود الفرع، وقال أبو عبد الله بن بطة: لا يثبت حتى يشهد أربعة على كل شاهد أصل شاهدا فرع','7013','4897','0');
INSERT INTO [t1153] VALUES (4955,'مسألة: ولا مدخل للنساء في شهادة الفرع','7015','4897','0');
INSERT INTO [t1153] VALUES (4956,'مسألة: قال القاضي: لا تجوز شهادة رجلين على شهادة رجل وامرأتين نص عليه أحمد. وقال أبو الخطاب: هذه الرواية سهو من ناقلها','7016','4897','0');
INSERT INTO [t1153] VALUES (4957,'مسألة: ولا يجوز للحاكم أن يحكم بشهادة شاهدين الفرع حتى تثبت عنده عدالتهما وعدالة شاهدي الأصل','7016','4897','0');
INSERT INTO [t1153] VALUES (4958,'مسألة: وإن شهدوا عنده فلم يحكم حتى حضر شهود الأصل وقف الحكم على سماع شهادتهم لأنه قدر على الأصل قبل العمل بالبذل فأشبه المتيمم إذا قدر على الماء فلا تصح صلاته حتى يتوضأ','7017','4897','0');
INSERT INTO [t1153] VALUES (4959,'مسألة: فإن حكم بشهادتهما ثم رجع شهود الفرع لزمهم الضمان، لأن الإتلاف كان بشهادتهم فلزمهم الضمان كما لو أتلفوا بأيديهم','7017','4897','0');
INSERT INTO [t1153] VALUES (4960,'مسألة: وإن حدث منهم ما يمنع قبول شهادتهم لم يجز الحكم','7017','4897','0');
INSERT INTO [t1153] VALUES (4961,'مسألة: فإن رجع شهود الأصل لم يضمنوا','7018','4897','0');
INSERT INTO [t1153] VALUES (4962,'مسألة: وإن رجع شهود العتق غرموا القيمة','7020','4897','0');
INSERT INTO [t1153] VALUES (4963,'مسألة: وإن رجع شهود الطلاق قبل الدخول، غرموا نصف المسمى، وإن كان بعده لم يغرموا شيئا','7020','4897','0');
INSERT INTO [t1153] VALUES (4964,'مسألة: وإن رجع شهود القصاص أو الحد قبل الاستيفاء لم يستوف وإن كان بعده وقالوا أخطأنا فعليهم دية ما تلف ويقسط الغرم على عددهم فإن رجع أحدهم غرم بقسطه','7021','4897','0');
INSERT INTO [t1153] VALUES (4965,'مسألة: فإذا شهد ستة بالزنا على محصن، فرجم بشهادتهم ثم رجع واحد فعليه القصاص أو سدس الدية وإن رجع اثنان فعليهما القصاص أو ثلث الدية','7025','4897','0');
INSERT INTO [t1153] VALUES (4966,'مسألة: وإن شهد أربعة بالزنا وإثنان بالإحصان، ثم رجع الجميع لزمتهم الدية أسداسا في أحد الوجهين وفي الأخرى على شهود الزنا النصف وعلى شهود الإحصان النصف فإن شهد أربعة بالزنا وشهد اثنان منهم بالإحصان صحت الشهادة فإن رجم ثم رجعوا عن الشهادة فعلى من شهد','7027','4897','0');
INSERT INTO [t1153] VALUES (4967,'مسألة: وإذا حكم الحاكم بشاهد ويمين ثم رجع الشاهد غرم المال كله ويتخرج أن يغرم النصف','7029','4897','0');
INSERT INTO [t1153] VALUES (4968,'مسألة: إن بان بعد الحكم أن الشاهدين كانا كافرين أو فاسقين، نقض الحكم ويرجع بالمال أو ببدله على المحكوم له، وإن كان المحكوم به إتلافا فالضمان على المزكين. فإن لم يكن ثم تزكية فعلى الحاكم، وعنه لا ينقض إذا كانا فاسقين','7031','4897','0');
INSERT INTO [t1153] VALUES (4969,'مسألة: وإن شهدوا عند الحاكم بحق ثم ماتوا، حكم بشهادتهم إذا ثبتت عدالتهم','7035','4897','0');
INSERT INTO [t1153] VALUES (4970,'مسألة: وإذا علم الحاكم بشاهد الزور عزره وطاف به في المواضع التي يشتهر فيها فيقال أنا وجدنا هذا شاهد زور فاجتنبوه','7035','4897','0');
INSERT INTO [t1153] VALUES (4971,'مسألة: ولا تقبل الشهادة إلا بلفظ الشهادة، فإن قال أعلم أو أحق لم يحكم به','7038','4897','0');
INSERT INTO [t1153] VALUES (4972,'باب اليمين في الدعاوى','7040','4897','0');
INSERT INTO [t1153] VALUES (4973,'مسألة: ويجوز الحكم في المال وما يقصد به المال بشاهد ويمين المدعي','7043','4897','0');
INSERT INTO [t1153] VALUES (4974,'مسألة: وهل يثبت العتق بشاهد ويمين؟ على روايتين','7044','4897','0');
INSERT INTO [t1153] VALUES (4975,'مسألة: ولا يقبل في النكاح والرجعة وسائر ما لا يستحلف فيه كالطلاق والوصية شاهد ويمين','7044','4897','0');
INSERT INTO [t1153] VALUES (4976,'مسألة: ومن حلف على فعل نفسه أو دعوى عليه في الإثبات حلف على البت','7045','4897','0');
INSERT INTO [t1153] VALUES (4977,'مسألة: ومن توجهت عليه يمين الجماعة فقال: أحلف يمينا واحدة فرضوا جاز وإن أبوا حلف لكل واحد يمينا','7046','4897','0');
INSERT INTO [t1153] VALUES (4978,'مسألة: وإن رأى الحاكم تغليظها بلفظ أو زمن أو مكان جاز','7049','4897','0');
INSERT INTO [t1153] VALUES (4979,'مسألة ولا تغلظ اليمين إلا فيما له خطر كالجنايات والعتاق والطلاق وما تجب فيه الزكاة من المال عند من يرى التغليظ','7054','4897','0');
INSERT INTO [t1153] VALUES (4980,'مسألة: وإن رأى الحاكم ترك التغليظ فتركه كان مصيبا','7055','4897','0');
INSERT INTO [t1153] VALUES (4981,'باب الدعاوى والبينات','7067','4897','0');
INSERT INTO [t1153] VALUES (4982,'مسألة: ولا تصح الدعوى والإنكار إلا من جائز التصرف','7067','4897','0');
INSERT INTO [t1153] VALUES (4983,'مسألة: وإن تداعيا عينا لم تخل من ثلاثة أقسام','7068','4897','0');
INSERT INTO [t1153] VALUES (4984,'مسألة: ولو تنازعا دابة أحدهما راكبها أو له عليها حمل والآخر أخذ بزمامها فهي للأول','7068','4897','0');
INSERT INTO [t1153] VALUES (4985,'مسالة: وإن تنازع صاحب الدار والخياط الإبرة والمقص فيها للخياط','7069','4897','0');
INSERT INTO [t1153] VALUES (4986,'مسألة: وإن تنازعا حائطا معقودا ببناء أحدهما أو وحده أو متصلا به اتصالا لا يمكن إحداثه أو له عليه أزج فهو له وإن كان محلولا من بنائهما أو معقودا بهما فهو بينهما','7070','4897','0');
INSERT INTO [t1153] VALUES (4987,'مسألة: وإن تنازع هو والقراب القرية فهي للقراب','7070','4897','0');
INSERT INTO [t1153] VALUES (4988,'مسألة: وإن تنازعا عرصة فيها شجر أو بناء لأحدهما فهي له','7070','4897','0');
INSERT INTO [t1153] VALUES (4989,'مسألة: ولا ترجح الدعوى بوضع خشب أحدهما عليه ولا بوجوه الآجر والتزويق والتجصيص ومعاقد القمط في الخص','7072','4897','0');
INSERT INTO [t1153] VALUES (4990,'مسألة: وإن تنازع صاحب العلو والسفل في السلم والدرجة فهي لصاحب العلو إلا أن يكون تحت الدرجة مسكن لصاحب السفل فيكون بينهما، وإن تنازعا في السقف الذي بينهما فهو بينهما','7074','4897','0');
INSERT INTO [t1153] VALUES (4991,'مسألة: وإن تنازع المؤجر والمستأجر في رف مقلوع أو مضراع له شكل منصوب في الدار فهو لصاحبها وإلا فهو بينهما','7076','4897','0');
INSERT INTO [t1153] VALUES (4992,'مسألة: وإن تنازعا دارا في أيديهما فادعاها أحدهما وادعى الآخر نصفها جعل بينهما نصفين واليمين على مدعي النصف','7077','4897','0');
INSERT INTO [t1153] VALUES (4993,'مسألة: وإن تنازع الزوجان أو ورثتهما في قماش البيت فما كان يصلح للرجال فهو للرجل، وما كان يصلح للنساء فهو للمرأة، وما كان يصلح لهما فهو بينهما','7082','4897','0');
INSERT INTO [t1153] VALUES (4994,'مسألة: وإن اختلف صانعان في قماش دكان لهما حكم بآلة كل صناعة لصاحبها في ظاهر كلام أحمد والخرقي','7083','4897','0');
INSERT INTO [t1153] VALUES (4995,'مسألة: وكل من قلنا هو له فهو له مع يمينه إذا لم تكن بينة لاحتمال ما ادعاه خصمه','7085','4897','0');
INSERT INTO [t1153] VALUES (4996,'مسألة: وإن كان لأحدهما بينة حكم له بها','7085','4897','0');
INSERT INTO [t1153] VALUES (4997,'مسألة: وإن كان لكل واحد منهما بينة حكم بها للمدعي في ظاهر المذهب، وعنه إن شهدت بينة المدعي عليه أنها له نتجت في ملكه أو قطعية من الإمام قدمت بينته، وإلا فهي للمدعي بينته، وقال القاضي فيهما: إذا لم يكن مع بينة الداخل ترجيح لم يحكم بها رواية واحدة، وق','7086','4897','0');
INSERT INTO [t1153] VALUES (4998,'مسألة: وإن أقام الداخل ببينة أنه اشتراها من الخارج وأقام بينة أنه اشتراها من الداخل فقال القاضي: تقدم بينة الداخل لأنه الخارج في المعنى، وقيل: تقدم بينة الخارج','7088','4897','0');
INSERT INTO [t1153] VALUES (4999,'مسألة: وإن تنازعا صبيا في يديهما فكذلك','7092','4897','0');
INSERT INTO [t1153] VALUES (5000,'مسألة: وإن تنازعا مسناة بين نهر أحدهما وأرض الآخر تحالفا وهي بينهما','7092','4897','0');
INSERT INTO [t1153] VALUES (5001,'مسألة: وإن كان لأحدهما بينة حكم له بها لأنه ترجح بالبينة، وإن كان لكل واحد منهما بينة قدم أسبقهما تاريخا، فإن وقتت إحدهما وأطلقت الأخرى فهما سواء ويحتمل تقديم المطلقة','7094','4897','0');
INSERT INTO [t1153] VALUES (5002,'مسألة: وإن كانت إحداهما متقدمة التاريخ وحكم بها له مثل أن تشهد إحداهما أنها له منذ سنة، وتشهد الأخرى أنها للآخر منذ سنتين فيقدم أسبقهما تاريخا','7095','4897','0');
INSERT INTO [t1153] VALUES (5003,'مسألة: فإن وقتت إحداهما وأطلقت الأخرى فهما سواء ذكره القاضي، ويحتمل أن يحكم به لمن يوقت قاله أبو الخطاب، وهو قول أبي يوسف ومحمد','7095','4897','0');
INSERT INTO [t1153] VALUES (5004,'مسأأأألة: وإن شهدت إحداهما بالملك والأخرى بالملك والنتاج أو سبب من أسباب الملك فهل يرجح بذلك؟ على وجهين','7095','4897','0');
INSERT INTO [t1153] VALUES (5005,'مسألة: ولا تقدم إحداهما بكثرة العدد ولا اشتهار العدالة ولا الرجلان على الرجل والمرأتين، ويقدم الشهادان على الشاهد واليمين في أحد الوجهين','7096','4897','0');
INSERT INTO [t1153] VALUES (5006,'مسألة: وإن تساوتا تعارضتا وقسمت العين بينهما بغير يمين، وعنه أنهما يتحالفان كمن لا بينة لهما، وعنه أنه يقرع بينهما فمن قرع صاحبه حلف وأخذها','7097','4897','0');
INSERT INTO [t1153] VALUES (5007,'مسألة: وإن ادعى أحدهما أنه اشتراهما من زيد لم تسمع البينة على ذلك حتى تقول: وهي ملكه، وتشهد البينة به','7098','4897','0');
INSERT INTO [t1153] VALUES (5008,'مسألة: وإن أقام أحدهما بينة أنها ملكه وأقام الآخر بينة أنه اشتراها أو أعتقه قدمت بينة الثاني','7099','4897','0');
INSERT INTO [t1153] VALUES (5009,'مسألة: وإن أقام رجل بينة أن هذه الدار لأبي خلفها تركة، وأقامت امرأته بينة أن أباه أصدقها إياها فهي للمرأة لما ذكرنا','7099','4897','0');
INSERT INTO [t1153] VALUES (5010,'مسألة: وإن كانت لكل واحد منهما بينة ففيه روايتان ذكرهما أبو الخطاب','7100','4897','0');
INSERT INTO [t1153] VALUES (5011,'مسألة: فإن كان المدعى عبدا فأقر لأحدهما لم يرجح باقراره','7100','4897','0');
INSERT INTO [t1153] VALUES (5012,'مسألة: وإن أقر صاحب اليد لأحدهما لم يرجح بإقراره إذا قلنا لا تسقط البينتان','7102','4897','0');
INSERT INTO [t1153] VALUES (5013,'مسألة: وإن ادعاها صاحب اليد لنفسه وقلنا بسقوط البينتين حلف لكل واحد منهما وهي له، وهو قول القاضي','7102','4897','0');
INSERT INTO [t1153] VALUES (5014,'مسألة: وإن كان في يد رجل عبد فادعى أنه أشتراه من زيد فادعى العبد أن زيدا أعتقه وأقام كل واحد بينة انبنى على بينة الداخل والخارج، فإن كان العبد في يد زيد فالحكم فيه كالحكم فيما إذا ادعيا عينا في يد غيرهما','7106','4897','0');
INSERT INTO [t1153] VALUES (5015,'مسألة: وإن كان في يده عبد وادعى عليه رجلان كل واحد منهما أنه اشتراه بثمن سماه فصدقهما لزمه الثمن لكل واحد منهما، وإن أنكرهما حلف لهما وبريء، وإن صدق أحدهما لزما ما ادعاه وحلف للآخر وإن كان لاحدهما بينة فله الثمن ويحلف للآخر وإن كان لكل واحد منهم بينة','7107','4897','0');
INSERT INTO [t1153] VALUES (5016,'مسألة: وإن ادعى كل واحد منهما أنه باعني إياه بألف وأقام بينة قدم أسبقهما تاريخا لما ذكرنا، فإن لم تسبق إحداهما تعارضتا','7109','4897','0');
INSERT INTO [t1153] VALUES (5017,'مسألة: وإن قال أحدهما: غصبني إياه، وقال، الآخر: ملكنيه، أو أقر لي به، فإن أقام كل واحد منهما بينة فهو للمغصوب منه ولا يغرم للآخر شيئا','7109','4897','0');
INSERT INTO [t1153] VALUES (5018,'مسألة: وإذا ادعى رجل زوجية أمرأة فأقرت بذلك قبل إقرارها','7109','4897','0');
INSERT INTO [t1153] VALUES (5019,'باب في التعارض البينتين','7110','4897','0');
INSERT INTO [t1153] VALUES (5020,'مسألة: وإن قال إن مت في المحرم فسالم حر، وإن مت في صفر فغانم حر، وأقام كل واحد منهما بينة بدعواه بموجب عتقه قدمت بينة سالم في أحد الوجوه','7110','4897','0');
INSERT INTO [t1153] VALUES (5021,'مسألة: وإن قال: إن مت من مرضي هذا فسالم حر، وإن برئت فغانم حر، فأقاما بينتين تعارضتا وبقيا على الرق، ذكره أصحابنا، والقياس أن يعتق أحدهما بالقرعة، ويحتمل أن يعتق غانم وحده لأن بينته تشهد بزيادة','7111','4897','0');
INSERT INTO [t1153] VALUES (5022,'مسألة: وإن اتلف ثوبا فشهدت بينة أن قيمته عشرون وشهدت أخرى أن قيمته ثلاثون لزمته أقل القيمتين','7112','4897','0');
INSERT INTO [t1153] VALUES (5023,'مسألة: ولو ماتت امرأة وابنها فقال زوجها ماتت فورثناها ثم مات ابني فورثته وقال أخوها: بل ما ابنها فورثته ثم ماتت فورثناها، حلف كل واحد منهما على إبطال دعوى صاحبه وكان ميراث الابن لأبيه وميراث المرأة لأخيها وزوجها نصفين وأن أقام كل واحد منهما بينة بدعو','7113','4897','0');
INSERT INTO [t1153] VALUES (5024,'مسألة: وإن شهدت بينة سالم أنه رجع عن عتق غانم عتق سالم وحده سواء كانت بينته وارثة أو لم تكن','7116','4897','0');
INSERT INTO [t1153] VALUES (5025,'مسألة: وإن كانت قيمة غانم سدس المال وبينته أجنبية قبلت','7116','4897','0');
INSERT INTO [t1153] VALUES (5026,'مسألة: وإن شهدت بينة أنه أعتق سالما في مرضه وشهدت أخرى أنه وصى بعتق غانم وكل واحد منهما ثلث المال عتق سالم وحده','7117','4897','0');
INSERT INTO [t1153] VALUES (5027,'مسألة: وإن شهدت بينة غانم أنه أعتقه في مرضه أيضا عتق أقدمهما تاريخا، فإن جهل السابق عتق أحدهما بالقرعة','7117','4897','0');
INSERT INTO [t1153] VALUES (5028,'مسألة: فإن كانت بينة أحدهما وارثة ولم تكذب الأجنبية فكذلك وإن قالت ما أعتق سالما إنما أعتق غانما عتق غانم كله وحكم سالم كحكمه لو لم يطعن في بينته في أنه يعتق ان تقدم تاريخ عتقه أو خرجت له القرعة وإلا فلا، وإن كانت الوراثة فاسقة ولم تطعن في بينة سال','7119','4897','0');
INSERT INTO [t1153] VALUES (5029,'مسألة: وإن أقام كل واحد بينة أنه مات على دينه تعارضتا وإن قاله شاهدان: نعرفه مسلما وقال شاهدان: نعرفه كافرا، فالميراث للمسلم إذا لم يؤرخ الشهود معرفتهم','7127','4897','0');
INSERT INTO [t1153] VALUES (5030,'مسألة: وإن خلف أبوين كافرين وابنين مسلمين فاختلفوا في دينه، فالقول قول الأبوين ويحتمل أن القول قول الابنين','7130','4897','0');
INSERT INTO [t1153] VALUES (5031,'مسألة: ولو مات مسلم وخلف ولدين مسلما وكافرا فأسلم الكافر وقال: أسلمت قبل موت أبي وقال أخوه: بل بعده فلا ميراث له لأن الأصل بقاء الكفر حتى يعلم زواله وعلى أخيه اليمين وتكون على نفي العلم لأنها على نفي فعل الغير وقد ذكرناه','7135','4897','0');
INSERT INTO [t1153] VALUES (5032,'مسألة: وإن قال: أسلمت في المحرم ومات أبي في صفر فلي الميراث، وقال أخوه: بل مات في ذي الحجة، فله الميراث مع أخيه، لأن الأصل بقاء الحياة فإن أقام كل واحد بينة بدعواه فهل يتعارضان أو نقدم بينة من ادعى تقديم موته؟ فيه وجهان ذكرناهما في الفصل قبل هاتين ال','7136','4897','0');
INSERT INTO [t1153] VALUES (5033,'كتاب العتق','7137','0','0');
INSERT INTO [t1153] VALUES (5034,'كتاب التعق','7137','5033','0');
INSERT INTO [t1153] VALUES (5035,'مسألة: وهو من أفضل القرب','7137','5033','0');
INSERT INTO [t1153] VALUES (5036,'مسألة: والمستحب عتق من له كسب ودين ينتفع بالعتق','7138','5033','0');
INSERT INTO [t1153] VALUES (5037,'مسألة: ويحصل العتق بالقول والملك ولا يحصل بالنية المجردة','7138','5033','0');
INSERT INTO [t1153] VALUES (5038,'مسألة: وفي قوله لأمته: أنت طالق وأنت حرام روايتان','7141','5033','0');
INSERT INTO [t1153] VALUES (5039,'مسألة: وإن قال لعبده وهو أكبر منه: أنت ابني لم يعتق ذكره القاضي ويحتمل أن يعتق','7142','5033','0');
INSERT INTO [t1153] VALUES (5040,'مسألة: وإن اعتق حاملا عتق جنينها إلا أن يستثنيه','7142','5033','0');
INSERT INTO [t1153] VALUES (5041,'مسألة: وإن أعتق ما في بطنها دونها، عتق وحده','7144','5033','0');
INSERT INTO [t1153] VALUES (5042,'مسألة: وأما الملك فمن ملك ذا رحم محرم عتق عليه وعنه لا يعتق إلا عمودا النسب','7145','5033','0');
INSERT INTO [t1153] VALUES (5043,'مسألة: وإن ملك ولده من الزنا، لم يعتق عليه في ظاهر كلام أحمد','7147','5033','0');
INSERT INTO [t1153] VALUES (5044,'مسألة: وإن ملك سهما ممن يعتق عليه بغير الميراث وهو موسر عتق عليه كله وعليه قيمة نصيب شريكه وإن كان معسرا لم يعتق عليه إلا ما ملك وإن ملكه بالميراث لم يعتق منه إلا ما ملك موسرا كان أو معسرا وعنه أنه يعتق عليه نصيب الشريك إن كان موسرا','7147','5033','0');
INSERT INTO [t1153] VALUES (5045,'مسألة: وإن مثل بعبده فجدع أنفه وأذنه أو نحو ذلك عتق نص عليه','7150','5033','0');
INSERT INTO [t1153] VALUES (5046,'مسألة: وإذا أعتق العبد فماله لسيده','7151','5033','0');
INSERT INTO [t1153] VALUES (5047,'مسألة: وإن أعتق شركا له في عبد وهو موسر بقيمة باقيه عتق كله وعليه قيمة باقيه يوم العتق لشريكه','7153','5033','0');
INSERT INTO [t1153] VALUES (5048,'مسألة: فإن أعتقه الشريك بعد ذلك وقبل أخذ القيمة لم يثبت له فيه عتق','7155','5033','0');
INSERT INTO [t1153] VALUES (5049,'مسألة: وإن كان معسرا لم يعتق إلا نصيبه وبقي حق شريكه فيه وعنه يعتق كله ويستسعى العبد في قيمة باقيه غير مشقوق عليه','7158','5033','0');
INSERT INTO [t1153] VALUES (5050,'مسألة: وإذا كان العبد لثلاثة لأحدهم نصفه ولآخر ثلثه ولثالث سدسه فإعتق صاحب النصف وصاحب السدس معا وهما موسران عتق عليهما وضمنا حق شريكهما فيه نصفين وصار ولاؤه بينهما أثلاثا ويحتمل أن يضمناه على قدر ملكهما فيه','7161','5033','0');
INSERT INTO [t1153] VALUES (5051,'مسألة: وإذا أعتق الكافر نصيبه من مسلم وهو موسر سرى إلى باقيه في أحد الوجهين','7162','5033','0');
INSERT INTO [t1153] VALUES (5052,'مسألة: وإن ادعى كل واحد من الشريكين أن شريكه أعتق نصيبه وهما موسران فقد صار العبد حرا باعتراف كل واحد منهما وصار مدعيا على شريكه قيمة حقه منه ولا ولاء عليه لواحد منهما','7163','5033','0');
INSERT INTO [t1153] VALUES (5053,'مسألة: وإن كانا معسرين لم يعتق على واحد منهما','7164','5033','0');
INSERT INTO [t1153] VALUES (5054,'مسألة: وإن اشترى أحدهما نصيب صاحبه عتق عليه ولم يسر إلى النصف الذي كان له','7164','5033','0');
INSERT INTO [t1153] VALUES (5055,'مسألة: وإن كان أحدهما موسرا والآخر معسرا عتق نصيب المعسر وحده','7166','5033','0');
INSERT INTO [t1153] VALUES (5056,'مسألة: وإذا قال أحد الشريكين: إذا اعتقت نصيبك فنصيبي حر فأعتق الأول وهو موسر عتق كله عليه','7168','5033','0');
INSERT INTO [t1153] VALUES (5057,'مسألة: وإن كان معسرا لم يعتق عليه إلا نصيبه','7168','5033','0');
INSERT INTO [t1153] VALUES (5058,'مسألة: وإن قال: إذا اعتقت نصيبك فنصيبي حر مع نصيبك فأعتق نصيبه عتق عليهما موسرا كان أو معسرا','7168','5033','0');
INSERT INTO [t1153] VALUES (5059,'مسألة: وإذا علق عتق عبده بشرط كقوله إن أديت إلي الفا فأنت حر أو إن دخلت الدار فأنت حر فهي صفة لازمة ألزمها نفسه ولا يملك إبطالها بالقول قياسا على النذر','7172','5033','0');
INSERT INTO [t1153] VALUES (5060,'مسألة: إلا أن تكون الصفة وجدت منه في حال زوال ملكه فهل تعود بعوده؟ على روايتين','7175','5033','0');
INSERT INTO [t1153] VALUES (5061,'مسألة: وتبطل الصفة بالموت','7176','5033','0');
INSERT INTO [t1153] VALUES (5062,'مسألة: وإن قال إن دخلت الدار فأنت حر بعد موتي أو أنت حر بعد موتي بشهر فهل يصح ويعتق بذلك؟ على روايتين','7176','5033','0');
INSERT INTO [t1153] VALUES (5063,'مسألة: وإن قال إن دخلت الدار فأنت حر بعد موتي فدخل في حياة السيد صار مدبرا','7178','5033','0');
INSERT INTO [t1153] VALUES (5064,'مسألة: وإن قال إن ملكت فلانا فهو حر أو كل مملوك أملكه فهو حر فهل يصح؟ على روايتين','7179','5033','0');
INSERT INTO [t1153] VALUES (5065,'مسألة: فإن قال العبد ذلك ثم عتق وملك عتق في أحد الوجهين قياسا على الحر والثاني: لا يعتق وهو الصحيح','7180','5033','0');
INSERT INTO [t1153] VALUES (5066,'مسألة: وإن قال: آخر مملوك أشتريه فهو حر، فملك عبيدا لم يعتق واحد منهم حتى يموت','7181','5033','0');
INSERT INTO [t1153] VALUES (5067,'مسألة: فإن قال لأمته: آخر ولد تلدينه فهو حر، فولدت حيا ثم ميتا، لم يعتق الأول','7183','5033','0');
INSERT INTO [t1153] VALUES (5068,'مسألة: ولا يتبع ولد المعتقة بالصفة أمه في العتق في أصح الوجهين إلا أن تكون حاملا به حال عتقها أو حال تعليق عتقها','7183','5033','0');
INSERT INTO [t1153] VALUES (5069,'مسألة: وإذا قال لعبده: أنت حر وعليك ألف أو على ألف عتق ولا شيء عليه. وعنه إن لم يقبل لم يعتق','7184','5033','0');
INSERT INTO [t1153] VALUES (5070,'مسألة: وإن قال: على أن تخدمني سنة فكذلك','7185','5033','0');
INSERT INTO [t1153] VALUES (5071,'مسألة: وإن قال: أحد عبدي حر، أقرع بينهما، فمن وقعت له القرعة، فهو حر من حين أعتقه','7186','5033','0');
INSERT INTO [t1153] VALUES (5072,'مسألة: وإن مات أحد العبدين أقر بينه وبين الحي فإن وقعت على الميت حسبناه من التركة وقومناه حين الإعتاق سواء مات في حياة سيده أو بعده قبل القرعة','7188','5033','0');
INSERT INTO [t1153] VALUES (5073,'مسألة: وإن أعتق عبدا أو نسيه أخرج بالقرعة','7191','5033','0');
INSERT INTO [t1153] VALUES (5074,'مسألة: فإن علم بعد أن المعتق غيره عتق وهل يبطل عتق الأول؟ على وجهين','7192','5033','0');
INSERT INTO [t1153] VALUES (5075,'مسألة: وإن أعتق جزءا من عبده أو دبره وهو أن يقول إذا مت فنصف عبدي حر ثم مات فإن كان النصف المدبر ثلث ماله من غير زيادة عتق ولم يسر','7193','5033','0');
INSERT INTO [t1153] VALUES (5076,'مسألة: وإن أعتق في مرضه شركا له في عبد أو دبره وثلثه يحتمل باقيه أعطي الشريك وكان جميعه حرا في إحدى الروايتين والأخرى لا يعتق إلا ما ملك منه','7194','5033','0');
INSERT INTO [t1153] VALUES (5077,'مسألة: وإن أعتق في مرضه ستة أعبد قيمتهم سواء وثلثه يحتملهم ثم ظهر عليه دين يستغرقهم بيعوا في دينه ويحتمل أن يعتق ثلثهم','7195','5033','0');
INSERT INTO [t1153] VALUES (5078,'مسألة: وإن أعتقهم فأعتقنا ثلثهم ثم ظهر له مال يخرجون من ثلثه عتق من أرق منهم','7197','5033','0');
INSERT INTO [t1153] VALUES (5079,'مسألة: وإن لم يظهر له مال، جزأنا له ثلاثة أجزاء كل اثنين جزءا وأقرعنا بينهم بسهم حرية وسهمي رق فمن خرج له سهم الحرية عتق ورق الباقون','7199','5033','0');
INSERT INTO [t1153] VALUES (5080,'مسألة: وإن كانوا ثمانية فإن شاء أقرع بينهم بسهمي حرية وخمسة رق وسهم لمن ثلثاه حرن وإن شاء جزأهم أربعة أجزاء فأقرع بينهم بسهم حرية وثلاثة رق ثم أعاد القرعة لأخراج من ثلثاه حر وإن فعل غير ذلك جاز بأن يجعل ثلاثة جزءا وثلاثة جزءا واثنين جزءا فإن خرجت','7206','5033','0');
INSERT INTO [t1153] VALUES (5081,'مسألة: وإن أعتق عبدين قيمة أحدهما مائتان والآخر ثلثمائة فأجاز الورثة عتقهما عتقا وإن لم يجز الورثة عتق ثلثهما وكمل الثلث في أحدهما فتجمع قيمتهما فتكون خمسمائة ثم يقرع بينهما فمن خرج له سهم الحرية ضربنا قيمته في ثلاثة ونسبنا قيمتهما إلى المرتفع بالضرب','7208','5033','0');
INSERT INTO [t1153] VALUES (5082,'مسألة: وأن اعتق واحدا من ثلاثة أعبد غير معين فمات أحدهم في حياة السيد، أقرع بينه وبين الحيين فإن وقعت رقعة العتق على الميت رق الآخران','7208','5033','0');
INSERT INTO [t1153] VALUES (5083,'باب التدبير','7211','5033','0');
INSERT INTO [t1153] VALUES (5084,'مسألة: ويعتبر من الثلث','7211','5033','0');
INSERT INTO [t1153] VALUES (5085,'مسألة: ويصح من كل من تصح وصيته','7212','5033','0');
INSERT INTO [t1153] VALUES (5086,'مسألة: وصريحه لفظ العتق والحرية المعلقين بالموت كقوله: أنت حر أو عتيق أو معتق أو محرر بعد موتي فيصير بذلك مدبرا بلا خلاف نعلمه','7214','5033','0');
INSERT INTO [t1153] VALUES (5087,'مسألة: ويصح مطلقا ومقيدا','7214','5033','0');
INSERT INTO [t1153] VALUES (5088,'مسألة: وإن قال: متى شئت فأنت مدبر أو أنت حر بعد موتي، أو إذا شئت أو أي وقت شئت فهو تدبير بصفة، فمتى شاء في حياة سيده صار مدبرا يعتق بموته','7215','5033','0');
INSERT INTO [t1153] VALUES (5089,'مسألة: وإن قال: إن شئت فأنت مدبر فقياس المذهب أنه على التراخي كقوله متى شئت','7216','5033','0');
INSERT INTO [t1153] VALUES (5090,'مسألة: وإذا قال: قد رجعت في تدبيري أو أبطلته، لم يبطل لأنه تعليق للعتق بصفة وعنه أنه يبطل كالوصية','7217','5033','0');
INSERT INTO [t1153] VALUES (5091,'مسألة: وله بيع المدبر وهبته وإن عاد إليه التدبير وعنه لا يباع إلا في الدين وعنه لا تباع الأمة خاصة','7220','5033','0');
INSERT INTO [t1153] VALUES (5092,'مسألة: وإن عاد إليه عاد التدبير','7223','5033','0');
INSERT INTO [t1153] VALUES (5093,'مسألة: وما ولدت المدبرة بعد تدبيرها فهو بمنزلتها ولا يتبعها ولدها من قبل التدبير','7223','5033','0');
INSERT INTO [t1153] VALUES (5094,'مسألة: وله إصابة مدبرته فإن أولدها بطل تدبيرها','7227','5033','0');
INSERT INTO [t1153] VALUES (5095,'مسألة: وإن كانت المدبر أو دبر المكاتب جاز','7228','5033','0');
INSERT INTO [t1153] VALUES (5096,'مسألة: فإن أدى عتق بالكتابة وبطل التدبير وإن مات سيده قبل الأداء عتق إن حمل الثلث ما بقي من كتابة وبطلت الكتابة وإن لم يخرج من الثلث عتق منه الثلث وسقط من الكتابة بقدر ما عتق منه وكان على الكتابة فيما بقي','7229','5033','0');
INSERT INTO [t1153] VALUES (5097,'مسألة: وإذا دبر شركا له في عبد لم يسر إلى نصيب شريكه وإن أعتق شريكه سرى إلى المدبر وغرم قيمته لسيده ويحتمل أن يسري في الأول دون الثاني','7230','5033','0');
INSERT INTO [t1153] VALUES (5098,'مسألة: وإذا أسلم مدبر الكافر لم يقر في يده وترك في يد عدل ينفق عليه من كسبه وما فضل لسيده وإن أعوز فعليه تمامه إلا أن يرجع في التدبير ونقول بصحة رجوعه فيجبر على بيعه','7231','5033','0');
INSERT INTO [t1153] VALUES (5099,'مسألة: ومن أنكر التدبير لم يحكم عليه إلا بشاهدين وهل يحكم بشاهد وامرأتين أو شاهد ويمين العبد؟ على روايتين','7232','5033','0');
INSERT INTO [t1153] VALUES (5100,'مسألة: وإذا قتل المدبر سيده بطل تدبيره لأمرين','7236','5033','0');
INSERT INTO [t1153] VALUES (5101,'باب الكتابة','7242','5033','0');
INSERT INTO [t1153] VALUES (5102,'مسألة: وهي مستحبة لمن يعلم فيه خير وهو الكسب والأمانة وعنه أنها واجبة إذا ابتغاها من سيده أجبر عليها','7243','5033','0');
INSERT INTO [t1153] VALUES (5103,'مسألة: وهل تكره كتابة من لا كسب له؟ على روايتين','7244','5033','0');
INSERT INTO [t1153] VALUES (5104,'مسألة: ولا يصح إلا من جائز التصرف فأما المجنون والطفل، فلا تصح مكاتبتهما لرقيقهما ولا مكاتبة سيدهما لهما، لأن الكتابة نقل الملك بعوض فلا تصح منهما كالبيع','7245','5033','0');
INSERT INTO [t1153] VALUES (5105,'مسألة: فإن كاتب المميز عبده بإذن وليه صح، ويحتمل أن لا يصح بناء على قولنا إنه لا يصح بيعه بإذن وليه. ولأنه عقد إعتاق فلم يصح منه كالعتق بغير مال ولا يصح بغير إذن وليه بحال','7245','5033','0');
INSERT INTO [t1153] VALUES (5106,'مسألة: وإن كاتب السيد عبده المميز صح وبهذا قال أبو حنيفة، وقال الشافعي: لا يصح فيها جميعها بحال لأنه ليس بمكلف أشبه المجنون','7245','5033','0');
INSERT INTO [t1153] VALUES (5107,'مسألة: ولا يصح إلا بالقول وينعقد بقوله كاتبتك على كذا','7251','5033','0');
INSERT INTO [t1153] VALUES (5108,'مسألة: ولا يفتقر إلى قوله وإن أديت إلي فأنت حر بل متى أدى عتق','7251','5033','0');
INSERT INTO [t1153] VALUES (5109,'مسألة: ولا يصح إلا على عوض معلوم منجم بنجمين فصاعدا','7251','5033','0');
INSERT INTO [t1153] VALUES (5110,'مسألة: ويشترط على ما يؤدي إليه في كل نجم كالثمن في البيع ولئلا يفضي إلى النزاع والاختلاف','7253','5033','0');
INSERT INTO [t1153] VALUES (5111,'مسألة: وتصح على مال وخدمة سواء تقدمت الخدمة أو تأخرت','7255','5033','0');
INSERT INTO [t1153] VALUES (5112,'مسألة: وإذا أدى ما كوتب عليه أو أبريء منه عتق، لأنه لم يبق لسيده عليه شيء ولا يعتق قبل أداء جميع الكتابة','7257','5033','0');
INSERT INTO [t1153] VALUES (5113,'مسألة: وما فضل في يده فهو له','7258','5033','0');
INSERT INTO [t1153] VALUES (5114,'مسألة: فلو مات قبل الأداء، كان ما في يده لسيده في الصحيح عنه وعلى الرواية الأخرى لسيده بقية كتابته والباقي لورثته','7260','5033','0');
INSERT INTO [t1153] VALUES (5115,'مسألة: وإذا عجلت الكتابة قبل محلها لزم السيد الأخذ وعتق','7263','5033','0');
INSERT INTO [t1153] VALUES (5116,'مسألة: ولا بأس أن يعجل المكاتب لسيده ويضع عنه بعض كتابته مثل أن يكاتبه على ألف في نجمين إلى سنة','7267','5033','0');
INSERT INTO [t1153] VALUES (5117,'مسألة: وإذا أدى وعتق فوجد السيد بالعوض عيبا فله أرشه أو قيمته ولا يرتفع العتق','7270','5033','0');
INSERT INTO [t1153] VALUES (5118,'مسألة: وإن شرط عليه أن لا يسافر ولا يأخذ الصدقة فهل يصح الشرط؟ على وجهين','7272','5033','0');
INSERT INTO [t1153] VALUES (5119,'مسألة: وله الإنفاق على نفسه وولده ورقيقه وكل ما فيه صلاح المال','7273','5033','0');
INSERT INTO [t1153] VALUES (5120,'مسألة: وليس له أن يتزوج، ولا يتسرى، ولا يتبرع، ولا يقرض، ولا يحابي، ولا يقتص من عبده الجاني على بعض رقيقه، ولا يعتق، ولا يكاتب إلا بإذن سيده','7275','5033','0');
INSERT INTO [t1153] VALUES (5121,'مسألة: وولاء من يعتقه ويكاتبه لسيده','7280','5033','0');
INSERT INTO [t1153] VALUES (5122,'مسألة: وليس له أن يبيع نسيئة وإن باع السلعة بأضعاف قيمتها وهذا مذهب الشافعي','7281','5033','0');
INSERT INTO [t1153] VALUES (5123,'مسألة: ولا يكفر بالمال وعنه له ذلك بإذن سيده','7282','5033','0');
INSERT INTO [t1153] VALUES (5124,'مسألة: وهل له أن يرهن أو يضارب؟ يحتمل وجهين','7283','5033','0');
INSERT INTO [t1153] VALUES (5125,'مسألة: وليس له شراء ذوي رحمه إلا بإذن سيده','7283','5033','0');
INSERT INTO [t1153] VALUES (5126,'مسألة: وله أن يقبلهم إذا وهبوا له أو وصي له بهم','7284','5033','0');
INSERT INTO [t1153] VALUES (5127,'مسألة: وإذا ملكهم فليس له بيعهم ولا هبتهم ولا إخراجهم عن ملكه','7284','5033','0');
INSERT INTO [t1153] VALUES (5128,'مسألة: وولد المكاتبة الذي ولدته في الكتابة يتبعها','7286','5033','0');
INSERT INTO [t1153] VALUES (5129,'مسألة: وإن أشترى زوجته صح وانفسخ نكاحها','7289','5033','0');
INSERT INTO [t1153] VALUES (5130,'مسألة: وإن استولد أمته فهل تصير أم ولد يمتنع عليه بيعها؟ على وجهين','7290','5033','0');
INSERT INTO [t1153] VALUES (5131,'مسألة: وإن جنى عليه فعليه أرش جنايته','7292','5033','0');
INSERT INTO [t1153] VALUES (5132,'مسألة: وليس له أن يطأ مكاتبته إلا أن يشترط','7293','5033','0');
INSERT INTO [t1153] VALUES (5133,'مسألة: وإن حبسه مدة فعليه أرفق الامرين به من إنظاره مثل تلك المدة أو أجرة مثله','7293','5033','0');
INSERT INTO [t1153] VALUES (5134,'مسألة: وإن وطئها من غير أن يشترط أو وطيء أمتها أدب ولم يبلغ به الحد','7295','5033','0');
INSERT INTO [t1153] VALUES (5135,'مسألة: فإن أولدها صارت أم ولد له سواء وطئها بشرط أو بغير شرط','7296','5033','0');
INSERT INTO [t1153] VALUES (5136,'مسألة: فإن أدت عتقت، وإن مات سيدها قبل أدائها عتقت وسقط ما بقي من كتابتها وما في يدها لها إلا أن يكون بعد عجزها. وقال أصحابنا: هو لورثة سيدها وكذلك الحكم فيما إذا أعتق المكاتب سيده','7298','5033','0');
INSERT INTO [t1153] VALUES (5137,'مسألة: وكذلك الحكم فيما إذا أعتق المكاتب سيده يكون كسبه له في قول القاضي ومن وافقه','7299','5033','0');
INSERT INTO [t1153] VALUES (5138,'مسألة: وإن وطئها جميعا فقد وجب لها على كل واحد منهما مهر مثلها','7303','5033','0');
INSERT INTO [t1153] VALUES (5139,'مسألة: وإن ولدت من أحدهما صارت ام ولد له','7304','5033','0');
INSERT INTO [t1153] VALUES (5140,'مسألة: وإن أتت بولد وألحق بهما، صارت أم ولد لهما يعتق نصفها بموت أحدهما وباقيها بموت الآخر كما لو كان سيدها واحدا واستولدها فإنها تعتق بموته وعند القاضي: لا يسري استيلاد أحدهما إلى نصيب شريكه','7305','5033','0');
INSERT INTO [t1153] VALUES (5141,'مسألة: ويجوز بيع المكاتب ومشتريه يقوم مقام المكاتب','7310','5033','0');
INSERT INTO [t1153] VALUES (5142,'مسألة: فإن أدى عتق وولاؤه له، وإن عجز عاد قنا له، وإن لم يعلم أنه مكاتب فله الرد والأرش','7313','5033','0');
INSERT INTO [t1153] VALUES (5143,'مسألة: وإن اشترى كل واحد من المكاتبين الآخرين صح شراء الأول وبطل شراء الثاني وسواء كانا لواحد أو لاثنين','7316','5033','0');
INSERT INTO [t1153] VALUES (5144,'مسألة: فإن لم يعلم السابق منهما فسد البيعان','7317','5033','0');
INSERT INTO [t1153] VALUES (5145,'مسألة: وإن أسر العدو المكاتب فاشتراه رجل فأحب سيده أخذه بما اشتراه وإلا فهو عند مشتريه مبقى على ما بقي من كتابته يعتق بالأداء وولاؤه له','7317','5033','0');
INSERT INTO [t1153] VALUES (5146,'مسألة: وعليه فداء نفسه ويكون الأرش في ذمته فيضمن ما كان عليه قبل العتق ويفديه بأقل الأمرين من قيمته أو أرش جنايته','7321','5033','0');
INSERT INTO [t1153] VALUES (5147,'مسألة: وإن عجز فلسيده تعجيزه ويفيده أيضا بما ذكرناه وقال أبو بكر فيه رواية أخرى أنه يفديه بأرش الجناية بالغة ما بلغت','7321','5033','0');
INSERT INTO [t1153] VALUES (5148,'مسألة: وإن لزمته ديون تعلقت بذمته بيع بها بعد العتق','7324','5033','0');
INSERT INTO [t1153] VALUES (5149,'مسألة: ويعتق بالأداء إلى سيده وقد ذكرنا ذلك وبالأداء إلى الورثة','7331','5033','0');
INSERT INTO [t1153] VALUES (5150,'مسألة: ولا تنفسخ بموت السيد لا نعلم في ذلك خلافا ولا تنفسخ بجنونه ولا الحجرعليه لأنه عقد لازم أشبه البيع','7331','5033','0');
INSERT INTO [t1153] VALUES (5151,'مسألة: وإن حل نجم فلم يؤده فللسيد الفسخ وعنه لا يعجز حتى يحل نجمان وعنه لا يعجز حتى يقول قد عجزت','7338','5033','0');
INSERT INTO [t1153] VALUES (5152,'مسألة: وليس للعبد فسخها بحال','7342','5033','0');
INSERT INTO [t1153] VALUES (5153,'مسألة: ولو زوج ابنته من مكاتبه ثم مات انفسخ النكاح','7343','5033','0');
INSERT INTO [t1153] VALUES (5154,'مسألة: ويجب على السيد أن يؤتيه ربع مال الكتابة إن شاء وضعه عنه وإن شاء قبضه ثم دفعه إليه','7344','5033','0');
INSERT INTO [t1153] VALUES (5155,'مسألة: فإن أدى ثلاثة أرباع الكتابة وعجز عن الربع عتق ولم تنفسخ الكتابة في قول القاضي وأصحابه','7347','5033','0');
INSERT INTO [t1153] VALUES (5156,'مسألة: وإن اختلفوا بعد الاداء في قدر ما أدى كل واحد منهم، فالقول قول من يدعي أداء قدر الواجب عليه','7352','5033','0');
INSERT INTO [t1153] VALUES (5157,'مسألة: وتجوز كتابة بعض عبده فإذا أدى عتق كله قاله أبو بكر','7356','5033','0');
INSERT INTO [t1153] VALUES (5158,'مسألة: وتجوز كتابة حصته من العبد المشترك بغير إذن شريكه','7356','5033','0');
INSERT INTO [t1153] VALUES (5159,'مسألة: وإذا أدى ما كوتب عليه ومثله لسيده الآخر عتق كله إن كان الذي كاتبه موسرا وعليه قيمة حصة شريكه','7358','5033','0');
INSERT INTO [t1153] VALUES (5160,'مسألة: وإن أعتق الشريك قبل أدائه عتق عليه كله إن كان موسرا وعليه قيمة نصيب المكاتب، وقال أبو بكر والقاضي: لا يسري إلى النصف المكاتب','7359','5033','0');
INSERT INTO [t1153] VALUES (5161,'مسألة: وإن كاتبا عبدهما جاز سواء كان على التساوي أو التفاضل ولا يجوز أن يؤدي إليهما إلا على التساوي','7361','5033','0');
INSERT INTO [t1153] VALUES (5162,'مسألة: وإن اختلفا في عوضها فالقول قول السيد في إحدى الروايتين','7377','5033','0');
INSERT INTO [t1153] VALUES (5163,'مسألة: وإن اختلفا في وفاء مالهما فقال العبد أديت وعتقت وأنكر السيد فالقول قول السيد مع يمينه','7379','5033','0');
INSERT INTO [t1153] VALUES (5164,'مسألة: وإن أقام العبد شاهدا وحلف معه أو شاهدا وامرأتين ثبت الأداء وعتق وهذا قول الشافعي','7381','5033','0');
INSERT INTO [t1153] VALUES (5165,'مسألة: وتنفسخ بموت السيد وجنونه والحجر للسفه','7387','5033','0');
INSERT INTO [t1153] VALUES (5166,'مسألة: وهل يتبع المكاتبة ولدها فيها؟ على وجهين','7390','5033','0');
INSERT INTO [t1153] VALUES (5167,'مسألة: وقال أبو بكر: لا تنفسخ بموت السيد ولا جنونه ولا الحجر عليه للسفه','7391','5033','0');
INSERT INTO [t1153] VALUES (5168,'باب أحكام أمهات الأولاد','7392','5033','0');
INSERT INTO [t1153] VALUES (5169,'مسألة: إذا حملت الأمة من سيدها، فوضعت ما يتبين فيه خلق الإنسان صارت له بذلك أم ولد فإذا مات عتقت وإن لم يملك غيرها','7393','5033','0');
INSERT INTO [t1153] VALUES (5170,'مسألة: وتعتق بموت سيدها من رأس المال وإن لم يملك سواها','7395','5033','0');
INSERT INTO [t1153] VALUES (5171,'مسألة: وإن وضعت جسما لا تخطيط فيه فعلى روايتين','7396','5033','0');
INSERT INTO [t1153] VALUES (5172,'مسألة: وإن أصابها في ملك غيره بنكاح أو غيره ثم ملكها حاملا، عتق الجنين ولم تصر أم ولد له وعنه تصير','7398','5033','0');
INSERT INTO [t1153] VALUES (5173,'مسألة: وأحكام أم الولد أحكام الأمة في الإجارة والاستخدام والوطء وسائر أمورها إلا فيما ينقل الملك في رقبتها كالبيع والهبة والوقف أو ما يراد له كالرهن وعنه ما يدل على جواز بيعها مع الكراهة ولا عمل عليه','7404','5033','0');
INSERT INTO [t1153] VALUES (5174,'مسألة: وإن ولدت من غير سيدها فلولدها حكمها في العتق بموت سيدها سواء عتقت أو ماتت','7408','5033','0');
INSERT INTO [t1153] VALUES (5175,'مسألة: وإن مات سيدها وهي حامل فهل يستحق النفقة لمدة حملها؟ على روايتين','7410','5033','0');
INSERT INTO [t1153] VALUES (5176,'مسألة: وإذا جنت أم الولد فداها سيدها بقيمتها أو دونها، وعنه يفديه بأرش الجناية كله','7411','5033','0');
INSERT INTO [t1153] VALUES (5177,'مسألة: فإن عادت فجنت فداها أيضا، وعنه: يتعلق ذلك بذمتها','7413','5033','0');
INSERT INTO [t1153] VALUES (5178,'مسألة: وإن قتلت سيدها عمدا فعليها القصاص وإن عفوا على مال أو كانت الجناية خطأ فعليها قيمة نفسها وتعتق في الموضعين','7415','5033','0');
INSERT INTO [t1153] VALUES (5179,'مسألة: ولا حد على قاذفها، وعنه: عليه الحد','7416','5033','0');
INSERT INTO [t1153] VALUES (5180,'مسألة: وإذا وطيء أحد الشريكين الجارية وأولدها صارت أم ولد له وولده حر وعليه قيمة نصيب شريكه فإن كان معسرا كان في ذمته','7418','5033','0');
INSERT INTO [t1153] VALUES (5181,'مسألة: وعند القاضي وأبي الخطاب إن كان الأول معسرا لم يسر','7420','5033','0');
