INSERT INTO [t11569] VALUES (1,'إذا خرج من الخلاء قال: «غفرانك» . قال الترمذي: هذا حديث حسن غريب، لا نعرفه إلا من حديث','2','0','0');
INSERT INTO [t11569] VALUES (2,'يخرج فيبول فيتمسح بالتراب، فقال: يا رسول الله، الماء منك قريب! فقال: «ما أدري لعلي لا','5','0','0');
INSERT INTO [t11569] VALUES (3,'الماء لا ينجس ". حدثنا إسحاق بن إبراهيم الدبري، عن عبد الرزاق، عن الثوري، عن سماك بن حرب،','8','0','0');
INSERT INTO [t11569] VALUES (4,'«إذا كان الماء قلتين لم يحمل الخبث» . وهكذا رواه إسحاق بن إبراهيم الحنظلي، وجماعة عن أبي','11','0','0');
INSERT INTO [t11569] VALUES (5,'الماء لا ينجسه شيء إلا ما غلب على ريحه وطعمه ولونه» . قال الحافظ عبد الغني المقدسي: ورواه','20','0','0');
INSERT INTO [t11569] VALUES (6,'السنور سبع ". ورواه البيهقي، عن أبي بكر أحمد بن الحسن القاضي، عن أبي العباس الأصم، عن','24','0','0');
INSERT INTO [t11569] VALUES (7,'فجئته بإداوة، فإذا فيها نبيذ، فتوضأ رسول الله صلى الله عليه وسلم. قال الدارقطني: الرجل','28','0','0');
INSERT INTO [t11569] VALUES (8,'واحدة واحدة، فقال: «هذا وضوء من لا يقبل الله منه صلاة إلا به» . ثم توضأ اثنتين اثنتين، فقال:','35','0','0');
INSERT INTO [t11569] VALUES (9,'يتوضأ، فغسل كفيه ثلاثا، وتمضمض ثلاثا، واستنشق ثلاثا، وغسل وجهه ثلاثا، وذراعيه ثلاثا ومسح','42','0','0');
INSERT INTO [t11569] VALUES (10,'فأمرني أن أمسح على الجبائر. وقال أبو الحسن بن سلمة: أبناه الدبري، عن عبد الرزاق نحوه. هذا','49','0','0');
INSERT INTO [t11569] VALUES (11,'توضأ، ثم أخذ حفنة من ماء، فقال بها هكذا - يعني: انتضح بها. وقال الإمام أحمد بن حنبل: ثنا','51','0','0');
INSERT INTO [t11569] VALUES (12,'إذا بال توضأ، وينتضح. كذا رواه الثوري، ومعمر، وزائدة، عن منصور. ورواه شعبة كما أخبرنا أبو','57','0','0');
INSERT INTO [t11569] VALUES (13,'فتوضأ النبي صلى الله عليه وسلم فلما فرغ أخذ النبي صلى الله عليه وسلم بيده ماء، فنضح به','59','0','0');
INSERT INTO [t11569] VALUES (14,'نزلت آية التيمم، ضرب رسول الله صلى الله عليه وسلم بيده على الأرض، فمسح بها وجهه، وضرب','66','0','0');
INSERT INTO [t11569] VALUES (15,'العين وكاء السه، فمن نام فليتوضأ ". وقال البيهقي: أخبرنا أبو عبد الله الحافظ، وأبو عبد','68','0','0');
INSERT INTO [t11569] VALUES (16,'«لا وضوء إلا من صوت أو ريح» . وهذا مختصر، وتمامه: فيما أخبرنا أبو عبد الله الحافظ، أبنا أبو','72','0','0');
INSERT INTO [t11569] VALUES (17,'يقبلني، ثم يخرج إلى الصلاة ولا يحدث وضوء. قال ابن عدي: وهذا أيضا لا أعلم رواه عن منصور','74','0','0');
INSERT INTO [t11569] VALUES (18,'يتوضأ، ثم يقبل، ثم يصلي ولا يتوضأ. وقال ابن ماجه: حدثنا أبو بكر بن أبي شيبة، ثنا محمد بن','76','0','0');
INSERT INTO [t11569] VALUES (19,'قبل بعض نسائه، ثم خرج إلى الصلاة ولم يتوضأ. قال عروة قلت لها: من هي إلا أنت؟ قال: فضحكت.','78','0','0');
INSERT INTO [t11569] VALUES (20,'رجل مس ذكره في الصلاة؟ قال: «وهل هو إلا مضغة منه - أو بضعة منه -» . وقال أبو القاسم البغوي:','81','0','0');
INSERT INTO [t11569] VALUES (21,'سال من أنفي دم، فقال: أحدث وضوءا. وقال المحاملي: أحدث لما أحدثت وضوءا. حدثنا القاسم بن','99','0','0');
INSERT INTO [t11569] VALUES (22,'أهراق الدم. فأمرها أن تغتسل عند كل صلاة وتصلي. ورواه الأوزاعي، عن يحيى، فجعل المستحاضة','99','0','0');
INSERT INTO [t11569] VALUES (23,'«عدي أيام أقرائك» . وأمرها أن تحتشي وتصلي، وتغتسل لكل طهر. قال ابن عدي: وهذا الحديث لم يحدث','101','0','0');
INSERT INTO [t11569] VALUES (24,'الذي يأتي امرأته وهي حائض: «يتصدق بدينار، أو بنصف دينار» . قال: عبد الله بن أحمد: قال أبي:','104','0','0');
INSERT INTO [t11569] VALUES (25,'هو ركضة من ركضات الشيطان، فتحيضي ستة أو سبعة أيام في علم الله عز وجل، ثم اغتسلي حتى إذا','116','0','0');
INSERT INTO [t11569] VALUES (26,'يذكر الله على كل أحيانه. رواه مسلم في «صحيحه» عن أبي كريب، ورواه أبو داود أيضا عن أبي كريب،','121','0','0');
INSERT INTO [t11569] VALUES (27,'إنها ليست بنجس، إنها من الطوافين عليكم والطوافات» . هكذا رواه مالك بن أنس في «الموطأ» ، وقد','125','0','0');
INSERT INTO [t11569] VALUES (28,'لا تستمتعوا من الميتة بإهاب ولا عصب ". وقال أيضا: حدثنا محمد بن إسماعيل مولى بني هاشم، ثنا','131','0','0');
INSERT INTO [t11569] VALUES (29,'«لا صلاة لمن لا وضوء له، ولا وضوء لمن لم يذكر اسم الله عليه» . وقال الترمذي: حدثنا نصر بن','136','0','0');
INSERT INTO [t11569] VALUES (30,'للوضوء شيطانا يقال له: ولهان، فاتقوا وسواس الماء ". اللفظ واحد. وقال عبد الله بن الإمام','141','0','0');
INSERT INTO [t11569] VALUES (31,'يتوضأ، والماء يسيل من وجهه ولحيته على صدره، فرأيته يفصل بين المضمضة والاستنشاق. قال أبو','146','0','0');
INSERT INTO [t11569] VALUES (32,'توضأ بأعلى نهر، فلما فرغ من وضوئه أفرغ فضله في النهر، وقال: «يبلغه الله قوما ينفعهم به» .','148','0','0');
INSERT INTO [t11569] VALUES (33,'«الأذنان من الرأس» . هذا الحديث غير مخرج في شيء من السنن الأربعة، والصواب وقفه على أبي موسى.','150','0','0');
INSERT INTO [t11569] VALUES (34,'توضأ عمر وبقي على بعض رجله قطعة لم يصبها الماء، فأمره رسول الله صلى الله عليه وسلم أن','152','0','0');
INSERT INTO [t11569] VALUES (35,'مسح أعلى الخف وأسفله. فقال: ليس بمحفوظ، وسائر الأحاديث عن المغيرة أصح. انتهى ما ذكره. وقد','158','0','0');
INSERT INTO [t11569] VALUES (36,'فلقيه رجل فسلم عليه، فلم يرد عليه حتى أقبل على الجدار، فمسح بوجهه ويديه، ثم رد عليه','159','0','0');
INSERT INTO [t11569] VALUES (37,'" التيمم ضربتان: ضربة للوجه، وضربة لليدين إلى المرفقين ". وقال الدارقطني: حدثنا محمد بن','170','0','0');
INSERT INTO [t11569] VALUES (38,'الاستطابة: «بثلاثة أحجار، ليس فيها رجيع» . وقال عبد الوهاب بن الحسن بن موسى الكلابي، أبنا','178','0','0');
INSERT INTO [t11569] VALUES (39,'«عليكم بالسواك» . فقال أبو زرعة: هذا خطأ، رواه الزهري، عن عبيد بن السباق، يعني: عن النبي صلى','182','0','0');
INSERT INTO [t11569] VALUES (40,'السواك من أذن النبي صلى الله عليه وسلم موضع القلم من أذن الكاتب. قال الطبراني: لم يروه عن','183','0','0');
INSERT INTO [t11569] VALUES (41,'توضأ ثلاثا ثلاثا. قال سفيان: قال أبو النضر عن أبي أنس: وعنده رجال من أصحاب رسول الله صلى','187','0','0');
INSERT INTO [t11569] VALUES (42,'فغسل يديه قبل أن يدخلهما الإناء، ثم قال: هكذا رأيت رسول الله صلى الله عليه وسلم صنع. هكذا','191','0','0');
INSERT INTO [t11569] VALUES (43,'فأخذ بيمينه الإناء فكفأ على يده اليسرى، فغسل كفيه ثلاث مرات. قال عبد خير: كل ذلك لا يدخل','193','0','0');
INSERT INTO [t11569] VALUES (44,'الوضوء مرة مرة، فقال: «هذا الذي افترض الله عليكم» . ثم توضأ مرتين مرتين، فقال: «من ضعف، ضعف','199','0','0');
INSERT INTO [t11569] VALUES (45,'خرج من الخلاء، فاتبعته بالإداوة أو القدح، فجلست له بالطريق، فكان إذا أتى حاجة أبعد. كذا','200','0','0');
INSERT INTO [t11569] VALUES (46,'«ويل للأعقاب من النار» . وحدثني حرملة بن يحيى، ثنا عبد الله بن وهب، أخبرني حيوة، أخبرني محمد','202','0','0');
INSERT INTO [t11569] VALUES (47,'أتموا الوضوء، ويل للأعقاب من النار» . هكذا رواه ابن ماجه مختصرا، منفردا به عن أصحاب السنن،','205','0','0');
INSERT INTO [t11569] VALUES (48,'«ويل للأعقاب من النار» . قد تقدم ذكر هذا الحديث. وقال ابن أبي حاتم: سئل أبو زرعة، عن حديث','207','0','0');
INSERT INTO [t11569] VALUES (49,'«ويل للأعقاب من النار» . فقال أبو زرعة: مطرح ضعيف الحديث، انتهى ما ذكره. ولم يخرج أحد من','208','0','0');
INSERT INTO [t11569] VALUES (50,'صلى رسول الله صلى الله عليه وسلم يوم الفتح خمس صلوات بوضوء، ومسح على خفيه، فقال له عمر:','210','0','0');
INSERT INTO [t11569] VALUES (51,'مخضب من صفر، قالت: فكنت أرجل رأس رسول الله صلى الله عليه وسلم. وقال الإمام أحمد بن حنبل','213','0','0');
INSERT INTO [t11569] VALUES (52,'مخضبا من صفر، فقالت: كان رسول الله صلى الله عليه وسلم يغتسل فيه. ورواه عقبة بن مكرم، عن','215','0','0');
INSERT INTO [t11569] VALUES (53,'بال ثم توضأ، ومسح على خفيه. رواه مسلم في «الصحيح» عن يحيى بن يحيى وغيره عن أبي معاوية،','216','0','0');
INSERT INTO [t11569] VALUES (54,'بال فتوضأ، ومسح على خفيه. وقال ابن أبي حاتم: سئل أبو زرعة، عن حديث رواه أبو نعيم، عن','218','0','0');
INSERT INTO [t11569] VALUES (55,'يمسح على خفيه وخماره. رواه ابن ماجه، عن أبي بكر بن أبي شيبة، عن يونس بن محمد المؤدب، عن','219','0','0');
INSERT INTO [t11569] VALUES (56,'" للوضوء شيطان يقال له: الولهان ". فقال أبو زرعة: هو عندي منكر. انتهى ما ذكره. وقد تقدم','223','0','0');
INSERT INTO [t11569] VALUES (57,'نغتسل من إناء واحد، وذلك القدح يومئذ يدعى الفرق. رواه البخاري في «الصحيح» عن آدم عن ابن أبي','224','0','0');
INSERT INTO [t11569] VALUES (58,'«معك ماء؟» قلت: نعم. فنزل عن راحلته، ثم مشى حتى توارى عني في سواد الليل، ثم جاء فأفرغت عليه','226','0','0');
INSERT INTO [t11569] VALUES (59,'اختلفت يدي ويد رسول الله صلى الله عليه وسلم في الوضوء من إناء واحد. خارجة بن الحارث بن','229','0','0');
INSERT INTO [t11569] VALUES (60,'«إذا استيقظ أحدكم من نومه فلا يغمس يده في الإناء حتى يغسلها ثلاثا، فإنه لا يدري أين باتت','233','0','0');
INSERT INTO [t11569] VALUES (61,'المرأة ترى ما يرى الرجل في المنام، فترى من نفسها ما يرى الرجل من نفسه؟ فقالت عائشة: يا أم','234','0','0');
INSERT INTO [t11569] VALUES (62,'توضأ فمسح رأسه مرة. عطاء بن أبي رباح لم يدرك عثمان رضي الله عنه، وحجاج بن أرطأة سمع من','236','0','0');
INSERT INTO [t11569] VALUES (63,'فأصب على رأسي ثلاث مرات» . قال أبو زرعة: وحدثنا عمرو بن قسيط الرقي، ثنا عبيد الله بن عمرو،','237','0','0');
INSERT INTO [t11569] VALUES (64,'قبلها ثم مضى لوجهه ولم يحدث وضوء. قال ابن أبي حاتم: سمعت أبي يقول: (أبو سلام) هذا هو خطأ،','239','0','0');
INSERT INTO [t11569] VALUES (65,'«بسم الله» . ثم يقول: «اللهم إني أعوذ بك من الخبث والخبائث» . أبو معشر: هو نجيح، وقد تكلم فيه','240','0','0');
INSERT INTO [t11569] VALUES (66,'ترك الوضوء مما مست النار. وأخبرنا أبو بكر بن الحسن القاضي، ثنا أبو العباس الأصم، ثنا محمد','241','0','0');
INSERT INTO [t11569] VALUES (67,'مسح على خفيه، ولكن أقبل «المائدة» أم بعدها؟ لا يخبرك أحد أن رسول الله صلى الله عليه وسلم','244','0','0');
INSERT INTO [t11569] VALUES (68,'«إذا استيقظ أحدكم من منامه فليغسل كفيه ثلاث مرات قبل أن يدخلها في الإناء، فإنه لا يدري أين','245','0','0');
INSERT INTO [t11569] VALUES (69,'توضأ ثلاثا ثلاثا. زاد القعنبي في حديثه: ومرتين، ومرة. لم يخرج هذا الحديث في شيء من الكتب','246','0','0');
INSERT INTO [t11569] VALUES (70,'توضأ مرة مرة، ثم قال: «هذا وضوءنا معشر الأنبياء فمن زاد على ذلك فقد أساء وأربى» . فقال أبو','247','0','0');
INSERT INTO [t11569] VALUES (71,'تبرز وتوضأ ومسح على خفيه. . . وذكر الحديث. فقال أبي: هذا خطأ، إنما هو: (إسماعيل بن محمد','248','0','0');
INSERT INTO [t11569] VALUES (72,'ترك الوضوء مما مست النار. فقال أبي: هذا حديث مضطرب المتن، إنما هو (أن النبي صلى الله عليه','251','0','0');
INSERT INTO [t11569] VALUES (73,'أكل مع النبي صلى الله عليه وسلم لحما، ثم صلى ولم يتوضأ. فسمعت محمد بن عوف يقول: هذا خطأ،','252','0','0');
INSERT INTO [t11569] VALUES (74,'توضأ وبقي على ظهر قدمه مثل ظفر إبهامه لم يمسه الماء، فقال له النبي صلى الله عليه وسلم:','254','0','0');
INSERT INTO [t11569] VALUES (75,'" إذا أخذت مضجعك فتوضأ وضوءك للصلاة، ثم اضطجع على شقك الأيمن، ثم قل: اللهم إني أسلمت وجهي','257','0','0');
INSERT INTO [t11569] VALUES (76,'«ويل للأعقاب من النار» . ورواه الأوزاعي، وحسين المعلم، عن يحيى بن أبي كثير، عن سالم الدوسي،','259','0','0');
INSERT INTO [t11569] VALUES (77,'مسح على عمامته وخفيه. لفظ حديث عبد الله بن المبارك، وفي حديث الآخرين: أنه رأى رسول الله','260','0','0');
INSERT INTO [t11569] VALUES (78,'توضأ وخلل لحيته. فقال: هذا حديث موضوع، وأبو سفيان الأنماري مجهول، انتهى ما ذكره. وهذا','263','0','0');
INSERT INTO [t11569] VALUES (79,'أنتم الغر المحجلون من آثار الطهور، فمن استطاع منكم أن يطيل غرته فليفعل ". قال أبي: إنما هو:','264','0','0');
INSERT INTO [t11569] VALUES (80,'ذهب لحاجته في غزوة تبوك، قال المغيرة: فذهبت معه بماء، فجاء رسول الله صلى الله عليه وسلم،','265','0','0');
INSERT INTO [t11569] VALUES (81,'إذا ذهب إلى الغائط أبعد. يونس بن خباب: تكلم فيه يحيى بن معين وغيره، ولم يسمع من يعلى بن','267','0','0');
INSERT INTO [t11569] VALUES (82,'«لينهكن أحدكم أصابعه قبل أن تنهكه النار» . قال ابن أبي حاتم: سمعت أبي يقول: رفعه منكر. انتهى','273','0','0');
INSERT INTO [t11569] VALUES (83,'توضأ، فأفرغ على يديه ثلاثا فغسلهما، ثم مضمض واستنثر وغسل وجهه ثلاثا، وغسل يده اليمنى إلى','275','0','0');
INSERT INTO [t11569] VALUES (84,'أشد ضفر رأسي، فأنقضه لغسل الجنابة؟ فقال: «لا، إنما يكفيك أن تحثي على رأسك ثلاث حثيات، ثم','278','0','0');
INSERT INTO [t11569] VALUES (85,'«الوضوء مما مست النار» . قال ابن شهاب: أخبرني عمر بن عبد العزيز، أن عبد الله بن إبراهيم بن','282','0','0');
INSERT INTO [t11569] VALUES (86,'«من كان يؤمن بالله واليوم الآخر فليكرم جاره، ومن كان يؤمن بالله واليوم الآخر فليكرم ضيفه،','285','0','0');
INSERT INTO [t11569] VALUES (87,'شرب رسول الله صلى الله عليه وسلم لبنا، ثم قال: «هاتوا ماء» . فمضمض، وقال: إن له دسما ". فسمعت','288','0','0');
INSERT INTO [t11569] VALUES (88,'«ويل للأعقاب من النار» . وقال ابن أبي حاتم: سألت أبي عن حديث رواه خلف بن الوليد، ثنا أيوب بن','290','0','0');
INSERT INTO [t11569] VALUES (89,'يمسح على الموقين والخمار. قال: وأخبرنا أبو علي الروذباري، أبنا أبو طاهر محمد بن الحسن','291','0','0');
