INSERT INTO [t12109] VALUES (28,'النداء الثامن والعشرون: في وجوب الوفاء بالعهود وفى المنة بحلية بهيمة الأنعام إلا ما استثنى منه','70','0','0');
INSERT INTO [t12109] VALUES (29,'النداء التاسع والعشرون: في تحريم استحلال شعائر الله إلا ما نسخ منها، وفى إباحة الصيد بعد التحلل، ووجوب التعاون على البر والتقوى، وحرمة التعاون على الإثم والعدوان.','73','0','0');
INSERT INTO [t12109] VALUES (30,'النداء الثلاثون: في وجوب الوضوء وبيان كيفيته ووجوب الغسل من الجنابة وبيان نواقض الوضوء وكيفية التيمم','76','0','0');
INSERT INTO [t12109] VALUES (31,'النداء الحادي والثلاثون: في وجوب العدل في الحكم والشهادة وحرمة ترك العدل من أجل البغض والعداء والأمر بتقوى الله عز وجل','79','0','0');
INSERT INTO [t12109] VALUES (32,'النداء الثاني والثلاثون: في الأمر بذكر النعم لشكرها وتقوى الله عز وجل، والتوكل عليه سبحانه وتعالى','82','0','0');
INSERT INTO [t12109] VALUES (33,'النداء الثالث والثلاثون: في الأمر بتقوى الله عز وجل وطلب الوسيلة إلى الله تعالى. والجهاد في سبيله عز وجل','85','0','0');
INSERT INTO [t12109] VALUES (34,'النداء الرابع والثلاثون: في حرمة اتخاذ اليهود والنصارى أولياء وعلة ذلك والتحذير من موالاتهم','87','0','0');
INSERT INTO [t12109] VALUES (35,'النداء الخامس والثلاثون: في التحذير من الردة عن الإسلام وبيان صفات المؤمنين الصادقين','89','0','0');
INSERT INTO [t12109] VALUES (36,'النداء السادس والثلاثون: في حرمة ولاية من يتخذ دين الله هزوا ولعبا من أهل الكتاب وغيرهم','92','0','0');
INSERT INTO [t12109] VALUES (37,'النداء السابع والثلاثون: في حرمة تحريم ما احل الله من الطيبات وحرمة الاعتداء في الدين','95','0','0');
INSERT INTO [t12109] VALUES (38,'النداء الثامن والثلاثون: في تحريم الخمر والميسر والأنصاب والأزلام','98','0','0');
INSERT INTO [t12109] VALUES (39,'النداء التاسع والثلاثون: في ابتلاء الله تعالى عباده المحرمين بالحج والعمرة بظهور الصيد وسهولة صيده','100','0','0');
INSERT INTO [t12109] VALUES (40,'النداء الأربعون: في حرمة الصيد حال الإحرام وبيان جزاء من قتل الصيد عامدا وهو محرم والعياذ بالله','103','0','0');
INSERT INTO [t12109] VALUES (41,'النداء الحادي والأربعون: في النهى عن السؤال عما لا فائدة فيه ولا حاجة تدعو إليه والتحذير من عواقبه','106','0','0');
INSERT INTO [t12109] VALUES (42,'النداء الثاني والأربعون: في الأمر بإصلاح المؤمن نفسه وتطهيرها بالإيمان والعمل الصالح وإعلامه بأنه لا يضره','109','0','0');
INSERT INTO [t12109] VALUES (43,'النداء الثالث والأربعون: في وجوب الإشهاد على الوصية وجواز شهادة غير المسلم على الوصية إذا تعذر وجود المسلم','112','0','0');
INSERT INTO [t12109] VALUES (44,'النداء الرابع والأربعون: في حرمة الفرار من صفوف القتال في سبيل الله وأنه من الكبائر الموجبة لغضب الله وعذابه','115','0','0');
INSERT INTO [t12109] VALUES (45,'النداء الخامس والأربعون: في وجوب طاعة الله والرسول صلى الله عليه وسلم وحرمة معصيتهما، وحرمة التشبه بالمنافقين','117','0','0');
INSERT INTO [t12109] VALUES (46,'النداء السادس والأربعون: في وجوب الاستجابة لنداء الله والرسول إذا أمرا أو نهيا أو بشرا وأنذرا. ووجوب اتقاء الفتن بما تتقى به','120','0','0');
INSERT INTO [t12109] VALUES (47,'النداء السابع والأربعون: في حرمة خيانة الله والرسول صلى الله عليه سلم، وخيانة الأمانات، والتحذير من فتنة المال والولد','123','0','0');
INSERT INTO [t12109] VALUES (48,'النداء الثامن والأربعون: في الترغيب في تقوى الله عز وجل وبيان ثمارها العاجلة والآجلة','126','0','0');
INSERT INTO [t12109] VALUES (49,'النداء التاسع والأربعون: في بيان عوامل النصر في الجهاد وهى طاعة الله ورسوله، وعدم النزاع ولزوم الصبر، والإخلاص لله','128','0','0');
INSERT INTO [t12109] VALUES (50,'النداء الخمسون: في حرمة اتخاذ الأقارب أولياء إن هم استحبوا الكفر على الإيمان','131','0','0');
