INSERT INTO [t12109] VALUES (52,'النداء الثاني والخمسون: في حرمة أكل أموال الناس بالباطل والوعيد الشديد لمن يكنز الذهب والفضة ولا يخرج زكاتهما','137','0','0');
INSERT INTO [t12109] VALUES (53,'النداء الثالث الخمسون: في وجوب الخروج إلى الجهاد إذا دعا الإمام إلى ذلك وهو ما يعرف بالتعبئة العامة، وحرمة القعود عنه','140','0','0');
INSERT INTO [t12109] VALUES (54,'النداء الرابع الخمسون: في الأمر بتقوى الله عز وجل والصدق في النية والقول والعمل','143','0','0');
