INSERT INTO [t12109] VALUES (56,'النداء السادس الخمسون: في الأمر بإقام الصلاة وإيتاء الزكاة والجهاد ولزوم الإسلام والاعتصام به','148','0','0');
INSERT INTO [t12109] VALUES (57,'النداء السابع والخمسون: في النهى عن اتباع خطوات الشيطان وبيان حال المتبع لها. وامتنان الله تعالى على المؤمنين بوقايتهم من الشيطان.','151','0','0');
INSERT INTO [t12109] VALUES (58,'النداء الثامن والخمسون: في وجوب الاستئذان على من يراد الدخول عليه في بيته، وعدم مشروعية الاستئذان على بيت غير مسكون للعبد حاجة فيه','154','0','0');
INSERT INTO [t12109] VALUES (59,'النداء التاسع والخمسون: في مشروعية استئذان الخدم والأطفال على أهل البيت ثلاثة أوقات. ووجوب استئذان الطفل إذا بلغ الحلم','157','0','0');
INSERT INTO [t12109] VALUES (60,'النداء الستون: وجوب ذكر النعم وشكرها وبيان موجب الذكر والشكر لله تعالى','160','0','0');
INSERT INTO [t12109] VALUES (61,'النداء الحادي والستون: في الأمر بذكر الله وتسبيحه عز وجل بكرة وعشيا وبيان ثواب ذلك من الله عز وجل','163','0','0');
INSERT INTO [t12109] VALUES (62,'النداء الثاني والستون: في سقوط العدة على المطلقة قبل المسيس، ووجوب المتعة لها إن لم يسم لها مهر','166','0','0');
INSERT INTO [t12109] VALUES (63,'النداء الثالث والستون: في وجوب الأدب مع رسول الله صلى الله عليه وسلم بأدنى أذى وحرمة نكاح نسائه بعده صلى الله عليه وسلم','169','0','0');
INSERT INTO [t12109] VALUES (64,'النداء الرابع والستون: في وجوب الصلاة والسلام على النبي صلى الله عليه وسلم','172','0','0');
INSERT INTO [t12109] VALUES (65,'النداء الخامس والستون: في حرمة أذية رسول الله صلى الله عليه وسلم وحرمة التشبه باليهود في أذية موسى عليه السلام','175','0','0');
INSERT INTO [t12109] VALUES (66,'النداء السادس والستون: في وجوب تقوى الله عز وجل ووجوب القول السديد','177','0','0');
INSERT INTO [t12109] VALUES (67,'النداء السابع والستون: في نصرة الله وما تثمره من نصرة لعباد الله المؤمنين وبيان خسران الكافرين وتعاستهم وضلالهم','179','0','0');
INSERT INTO [t12109] VALUES (68,'النداء الثامن والستون: في وجوب طاعة الله وطاعة رسوله صلى الله عليه وسلم والتحذير من إبطال الأعمال الصالحة','181','0','0');
INSERT INTO [t12109] VALUES (69,'النداء التاسع والستون: في حرمة تقديم الرأي عن الكتاب والسنة ووجوب تقوى الله عز وجل','184','0','0');
INSERT INTO [t12109] VALUES (70,'النداء السبعون: في وجوب الأدب مع رسول الله صلى الله عليه وسلم حتى لا يتعرض المؤمن لبطلان عمله فيهلك','185','0','0');
INSERT INTO [t12109] VALUES (71,'النداء الحادي والسبعون: وجوب التثبت في الحكم قولا أو فعلا وفى بيان أفضلية أصحاب رسول الله صلى الله عليه وسلم','188','0','0');
INSERT INTO [t12109] VALUES (72,'النداء الثاني والسبعون: في حرمة السخرية بالمؤن وحرمة التنابز بالألقاب','190','0','0');
INSERT INTO [t12109] VALUES (73,'النداء الثالث والسبعون: في وجوب اجتناب كثير من الظن وحرمة التجسس والغيبة. ووجوب تقوى الله عز وجل','193','0','0');
INSERT INTO [t12109] VALUES (74,'النداء الرابع والسبعون: في وجوب تقوى الله والإيمان برسول الله محمد صلى الله عليه وسلم وبيان الجزاء على ذلك','196','0','0');
INSERT INTO [t12109] VALUES (75,'النداء الخامس والسبعون: في حرمة التناجى بالإثم والعدوان ومعصية الرسول والإذن في التناجى بالبر والتقوى','198','0','0');
INSERT INTO [t12109] VALUES (76,'النداء السادس والسبعون: في وجوب التفسح في المجالس إذا أمر المؤمن بذلك ووجوب القيام من المجلس إذا أمر كذلك وذلك لصالح الدعوة','201','0','0');
INSERT INTO [t12109] VALUES (77,'النداء السابع والسبعون: في بيان حكم مناجاة الرسول صلى الله عليه وسلم وتقديم صدقة قبلها ونسخ ذلك تخفيفا، ووجوب إقام الصلاة إيتاء الزكاة وطاعة الله ورسوله صلى الله عليم وسلم','203','0','0');
INSERT INTO [t12109] VALUES (78,'النداء الثامن والسبعون: في وجوب تقوى الله عز وجل والتزود للآخرة ووجوب ذكر الله وحرمة نسيانه لما يفضي إليه من الخسران والحرمان','206','0','0');
INSERT INTO [t12109] VALUES (79,'النداء التاسع والسبعون: في حرمة اتخاذ الكفرة أحباء يودون وأولياء ينصرون. وإن من يفعل ذلك فقد ضل طريق السعادة والكمال','208','0','0');
INSERT INTO [t12109] VALUES (80,'النداء الثمانون: في بيان حكم المهاجرات من دار الكفر إلى دار الإيمان، وكيفية معاملتهن مع أزواجهن','211','0','0');
INSERT INTO [t12109] VALUES (81,'النداء الحادي الثمانون: في حرمة موالاة اليهود','214','0','0');
INSERT INTO [t12109] VALUES (82,'النداء الثاني الثمانون: في لوم وعتاب من يقول ولا يفعل، وأن ذلك من موجبات مقت الله تعالى للعبد. وفى بيان حب الله تعالى للمجاهدين في سبيله الثابتين في المعارك','216','0','0');
INSERT INTO [t12109] VALUES (83,'النداء الثالث الثمانون: في عرض بضاعة أغلى بضاعة إذ هي الحنة وبيان الثمن المحصل لها وهو الإيمان والجهاد','219','0','0');
INSERT INTO [t12109] VALUES (84,'النداء الرابع والثمانون: في وجوب نصرة دين الله وأهله ائتساء بمن دعوا إلى ذلك فأجابوا ففازوا بالنصر والغلبة','222','0','0');
INSERT INTO [t12109] VALUES (85,'النداء الخامس والثمانون: في وجوب حضور صلاة الجمعه إذا نودي لها وحرمة البيع والشراء وسائر الأعمال بعد النداء','225','0','0');
INSERT INTO [t12109] VALUES (86,'النداء السادس والثمانون: في حرمة الانشغال بالمال والولد عن عبادة الله تعالى ووجوب الزكاة والترغيب في الصدقات، والتحذير من فجاءة الموت قبل التوبة','228','0','0');
INSERT INTO [t12109] VALUES (87,'النداء السابع والثمانون: في التحذير من فتنة المال والزوجة والولد وبيان فضل العفو والصفح والغفران، وعلاج شح النفس','231','0','0');
INSERT INTO [t12109] VALUES (88,'النداء الثامن والثمانون: في مشروعية الطلاق السني وبيان العدة وعدم إخراج المطلقة من البيت حتى تنتهي عدتها إلا أن تؤذى ومشروعية الإشهاد على الطلاق والرجعة','234','0','0');
INSERT INTO [t12109] VALUES (89,'النداء التاسع والثمانون: في وجوب وقاية النفس والأهل من النار وذلك بالإيمان وطاعة الله ورسول (صلى الله عليه وسلم) وبيان وصف النار','237','0','0');
INSERT INTO [t12109] VALUES (90,'النداء التسعون: في وجوب التوبة من كل ذنب وعلى الفور وأن تكون التوبة نصوحا، رجاء مغفرة الذنوب ودخول الجنة','239','0','0');
INSERT INTO [t12109] VALUES (91,'الخاتمة','242','0','0');
