INSERT INTO [t121382] VALUES (35,'(١) باب ذكر الخبر الثابت عن النبي - صلى الله عليه وسلم- بأن إتمام الوضوء من الإسلام.','39','34','0');
INSERT INTO [t121382] VALUES (36,'(٢) باب ذكر فضائل الوضوء يكون بعده صلاة مكتوبة','40','34','0');
INSERT INTO [t121382] VALUES (37,'(٣) باب ذكر فضل الوضوء ثلاثا ثلاثا يكون بعده [٢ - ب] صلاة تطوع لا يحدث المصلي فيها نفسه]','40','34','0');
INSERT INTO [t121382] VALUES (38,'(٤) [باب ذكر حط الخطايا بالوضوء من غير ذكر صلاة تكون بعده]','41','34','0');
INSERT INTO [t121382] VALUES (39,'(٥) [باب ذكر حط الخطايا ورفع الدرجات في الجنة بإسباغ الوضوء على المكاره وفضل انتظار الصلاة بعد الصلاة أجر المرابط في سبيل الله]','41','34','0');
INSERT INTO [t121382] VALUES (40,'(٦) باب ذكر علامة أمة النبي - صلى الله عليه وسلم- الذين جعلهم الله خير أمة أخرجت للناس -بآثار الوضوء يوم القيامة، علامة يعرفون بها في ذلك اليوم','42','34','0');
INSERT INTO [t121382] VALUES (41,'(٧) باب استحباب تطويل التحجيل بغسل العضدين في الوضوء، إذ الحلية تبلغ مواضع الوضوء يوم القيامة بحكم النبي المصطفى- صلى الله عليه وسلم -.','43','34','0');
INSERT INTO [t121382] VALUES (42,'(٨) باب نفي قبول الصلاة بغير وضوء، بذكر خبر مجمل غير مفسر','43','34','0');
INSERT INTO [t121382] VALUES (43,'(٩) باب ذكر الخبر المفسر للفظة المجملة التي ذكرتها، والدليل على أن النبي - صلى الله عليه وسلم- إنما نفى قبول الصلاة لغير المتوضئ المحدث الذي قد أحدث حدثا يوجب الوضوء، لا كل قائم إلى الصلاة وإن كان غير محدث حدثا يوجب الوضوء','44','34','0');
INSERT INTO [t121382] VALUES (44,'(١٠) باب ذكر الدليل على أن الله -عز وجل- إنما أوجب الوضوء على بعض القائمين إلى الصلاة، لا على كل قائم إلى الصلاة في قوله: {يا أيها الذين آمنوا إذا قمتم إلى الصلاة فاغسلوا وجوهكم} [المائدة: ٦] الآية. إذ الله جل وعلا ولى نبيه -صلى الله عليه وسلم- بيان ما أنزل عليه خاصا وعاما، فبين النبي - صلى الله عليه وسلم- بسنته [٤ - أ] أن الله إنما أمر بالوضوء بعض','44','34','0');
INSERT INTO [t121382] VALUES (45,'(١١) باب الدليل على أن الوضوء لا يجب إلا من حدث','46','34','0');
INSERT INTO [t121382] VALUES (46,'(١٢) باب صفة وضوء النبي - صلى الله عليه وسلم- على طهر من غير حدث كان مما يوجب الوضوء.','47','34','0');
INSERT INTO [t121382] VALUES (47,'(١٣) باب ذكر وجوب الوضوء من الغائط والبول والنوم.','48','34','0');
INSERT INTO [t121382] VALUES (48,'(١٤) باب ذكر وجوب الوضوء من المذي','49','34','0');
INSERT INTO [t121382] VALUES (49,'(١٥) باب الأمر بغسل الفرج من المذي مع الوضوء','50','34','0');
INSERT INTO [t121382] VALUES (50,'(١٦) باب الأمر بنضح الفرج من المذي','50','34','0');
INSERT INTO [t121382] VALUES (51,'(١٧) باب ذكر الدليل على أن الأمر بغسل الفرج ونضحه من المذي أمر ندب وإرشاد لا أمر فريضة وإيجاب','51','34','0');
INSERT INTO [t121382] VALUES (52,'(١٨) باب ذكر وجوب الوضوء من الريح الذي يسمع صوتها بالأذن، أو يوجد رائحتها بالأنف','52','34','0');
INSERT INTO [t121382] VALUES (53,'(١٩) باب ذكر الدليل [على] أن الوضوء لا يجب إلا بيقين حدث. إذ الطهارة بيقين لا تزول بشك وارتياب. وإنما يزول اليقين باليقين. فإذا كانت الطهارة قد تقدمت بيقين لم تبطل الطهارة إلا بيقين حدث','52','34','0');
INSERT INTO [t121382] VALUES (54,'(٢٠) باب ذكر الدليل على أن الاسم باسم المعرفة بالألف واللام قد لا يحوي جميع المعاني التي تدخل في ذلك الاسم','52','34','0');
INSERT INTO [t121382] VALUES (55,'(٢١) باب ذكر خبر روي مختصرا عن رسول الله -صلى الله عليه وسلم- أوهم عالما ممن لم يميز بين الخبر المختصر والخبر المتقصى أن الوضوء لا يجب إلا من الحدث الذي له صوت أو رائحة','53','34','0');
INSERT INTO [t121382] VALUES (56,'(٢٢) باب ذكر الخبر المتقصي للفظة المختصرة التي ذكرتها. والدليل على أن النبي -صلى الله عليه وسلم- إنما أعلم أن لا وضوء إلا من صوت أو ريح عند مسألة سئل عنها في الرجل يخيل إليه أنه قد خرجت منه ريح','53','34','0');
INSERT INTO [t121382] VALUES (57,'(٢٣) باب ذكر الدليل [على] أن اللمس قد يكون باليد، ضد قول من زعم أن اللمس لا يكون إلا بجماع بالفرج في الفرج','55','34','0');
INSERT INTO [t121382] VALUES (58,'(٢٤) باب الأمر بالوضوء من أكل لحوم الإبل','56','34','0');
INSERT INTO [t121382] VALUES (59,'(٢٥) باب استحباب الوضوء من مس الذكر','57','34','0');
INSERT INTO [t121382] VALUES (60,'(٢٦) باب ذكر الدليل [على] أن المحدث لا يجب عليه الوضوء قبل وقت الصلاة','58','34','0');
INSERT INTO [t121382] VALUES (61,'(٢٧) باب ذكر الخبر الدال على أن خروج الدم من غير مخرج الحدث لا يوجب الوضوء','58','34','0');
INSERT INTO [t121382] VALUES (62,'(٢٨) باب ذكر الدليل على أن وطء الأنجاس لا يوجب الوضوء','60','34','0');
INSERT INTO [t121382] VALUES (63,'(٢٩) باب إسقاط إيجاب الوضوء من أكل ما مسته النار أو غيرته','60','34','0');
INSERT INTO [t121382] VALUES (64,'(٣٠) باب ذكر الدليل على أن اللحم الذي ترك النبي -صلى الله عليه وسلم- الوضوء من أكله كان لحم غنم، لا لحم إبل','61','34','0');
INSERT INTO [t121382] VALUES (65,'(٣١) باب ذكر الدليل على أن ترك النبي -صلى الله عليه وسلم- الوضوء مما مست النار أو غيرت، ناسخ لوضوئه كان مما مست النار أو غيرت','62','34','0');
INSERT INTO [t121382] VALUES (66,'(٣٢) باب الرخصة في ترك غسل اليدين، والمضمضة من أكل اللحم، إذ العرب قد تسمي غسل اليدين وضوءا','62','34','0');
INSERT INTO [t121382] VALUES (67,'(٣٣) باب ذكر الدليل على أن الكلام السيئ والفحش في المنطق لا يوجب وضوءا','63','34','0');
INSERT INTO [t121382] VALUES (68,'(٣٤) باب استحباب المضمضة من شرب اللبن','63','34','0');
INSERT INTO [t121382] VALUES (69,'(٣٥) باب ذكر الدليل على أن المضمضة من شرب اللبن استحباب لإزالة الدسم من الفم وإذهابه، لا لإيجاب المضمضة من شربه','63','34','0');
INSERT INTO [t121382] VALUES (70,'(٣٦) باب ذكر ما كان الله -عز وجل- فرق به بين نبيه -صلى الله عليه وسلم-، وبين أمته في النوم من أن عينيه إذا نامتا لم يكن قلبه ينام','64','34','0');
INSERT INTO [t121382] VALUES (71,'(٣٧) باب التباعد للغائط في الصحاري عن الناس','65','34','0');
INSERT INTO [t121382] VALUES (72,'(٣٨) باب الرخصة في ترك التباعد عن الناس عند البول','65','34','0');
INSERT INTO [t121382] VALUES (73,'(٣٩) باب استحباب الاستتار عند الغائط','65','34','0');
INSERT INTO [t121382] VALUES (74,'(٤٠) باب الرخصة للنساء في الخروج للبراز بالليل إلى الصحاري','66','34','0');
INSERT INTO [t121382] VALUES (75,'(٤١) باب التحفظ من البول كي لا يصيب البدن والثياب، والتغليظ في ترك غسله إذا أصاب البدن أو الثياب','66','34','0');
INSERT INTO [t121382] VALUES (76,'(٤٢) باب ذكر خبر روي عن النبي -صلى الله عليه وسلم- في النهي عن استقبال القبلة واستدبارها عند الغائط والبول، بلفظ عام مراده خاص','67','34','0');
INSERT INTO [t121382] VALUES (77,'(٤٣) باب ذكر خبر روي عن النبي -صلى الله عليه وسلم- في الرخصة في البول مستقبل القبلة بعد نهي النبي -صلى الله عليه وسلم- عنه مجملا غير مفسر [١١ - ب]. قد يحسب من لم يتبحر العلم أن البول مستقبل القبلة جائز لكل بائل، وفي أي موضع كان. ويتوهم من لا يفهم العلم ولا يميز بين المفسر والمجمل أن فعل النبي -صلى الله عليه وسلم- في هذا ناسخ لنهيه عن البول مستقبل القبلة','68','34','0');
INSERT INTO [t121382] VALUES (78,'(٤٤) باب ذكر الخبر المفسر للخبرين اللذين ذكرتهما في البابين المتقدمين، والدليل على أن النبي -صلى الله عليه وسلم- إنما نهى عن استقبال القبلة واستدبارها عند الغائط والبول في الصحاري والمواضع اللواتي لا سترة فيها، وأن الرخصة في ذلك في الكنف والمواضع التي [فيها] بين المتغوط والبائل وبين القبلة حائط أو سترة','68','34','0');
INSERT INTO [t121382] VALUES (79,'(٤٥) باب الرخصة في البول قائما','69','34','0');
INSERT INTO [t121382] VALUES (80,'(٤٦) باب استحباب تفريج الرجلين عند البول قائما، إذ هو أحرى أن لا ينشر البول على الفخذين والساقين','70','34','0');
INSERT INTO [t121382] VALUES (81,'(٤٧) باب كراهية تسمية البائل مهريقا للماء','70','34','0');
INSERT INTO [t121382] VALUES (82,'(٤٨) باب الرخصة في البول في الطساس','70','34','0');
INSERT INTO [t121382] VALUES (83,'(٤٩) باب النهي عن البول في الماء الراكد الذي لا يجري، وفي نهيه عن ذلك دلالة على إباحة البول في الماء الجاري','71','34','0');
INSERT INTO [t121382] VALUES (84,'(٥٠) باب النهي عن التغوط على طريق المسلمين وظلهم الذي هو مجالسهم]','71','34','0');
INSERT INTO [t121382] VALUES (85,'(٥١) باب النهي عن مس الذكر باليمين','72','34','0');
INSERT INTO [t121382] VALUES (86,'(٥٢) باب الاستعاذة من الشيطان الرجيم عند دخول المتوضأ','72','34','0');
INSERT INTO [t121382] VALUES (87,'(٥٣) باب إعداد الأحجار للاستنجاء عند إتيان الغائط','73','34','0');
INSERT INTO [t121382] VALUES (88,'(٥٤) باب النهي عن المحادثة على الغائط','73','34','0');
INSERT INTO [t121382] VALUES (89,'(٥٥) باب النهي عن نظر المسلم إلى عورة أخيه المسلم','74','34','0');
INSERT INTO [t121382] VALUES (90,'(٥٦) باب كراهية رد السلام يسلم على البائل','74','34','0');
INSERT INTO [t121382] VALUES (91,'(٥٧) باب الأمر بالاستطابة بالأحجار، والدليل على أن الاستطابة بالأحجار يجزي دون الماء','74','34','0');
INSERT INTO [t121382] VALUES (92,'(٥٨) باب الأمر بالاستطابة بالأحجار وترا لا شفعا','75','34','0');
INSERT INTO [t121382] VALUES (93,'(٥٩) باب ذكر الدليل على أن الأمر بالاستطابة وترا، هو الوتر الذي يزيد على الواحد، الثلاث فما فوقه من الوتر، إذ الواحد قد يقع عليه اسم الوتر، والاستطابة بحجر واحد غير مجزية [١٤ - أ] إذ النبي -صلى الله عليه وسلم- قد أمر أن لا يكتفى بدون ثلاثة أحجار في الاستطابة','75','34','0');
INSERT INTO [t121382] VALUES (94,'(٦٠) باب الدليل على أن الأمر بالوتر في الاستطابة أمر استحباب لا أمر إيجاب، وأن من استطاب بأكثر من ثلاثة بشفع لا بوتر غير عاص في فعله، إذ تارك الاستحباب غير الإيجاب تارك فضيلة لا فريضة','76','34','0');
INSERT INTO [t121382] VALUES (95,'(٦١) باب النهي عن الاستطابة باليمين','76','34','0');
INSERT INTO [t121382] VALUES (96,'(٦٢) باب النهي عن الاستطابة بدون ثلاثة أحجار','77','34','0');
INSERT INTO [t121382] VALUES (97,'(٦٣) باب الدليل على النهي عن الاستطابة بدون ثلاثة أحجار','77','34','0');
INSERT INTO [t121382] VALUES (98,'(٦٤) باب ذكر العلة التي من أجلها زجر عن الاستنجاء بالعظام والروث','78','34','0');
INSERT INTO [t121382] VALUES (99,'(٦٥) باب ذكر ثناء الله - عز وجل - على المتطهرين بالماء','79','34','0');
INSERT INTO [t121382] VALUES (100,'(٦٦) باب ذكر استنجاء النبي -صلى الله عليه وسلم- بالماء','79','34','0');
INSERT INTO [t121382] VALUES (101,'(٦٧) باب تسمية الاستنجاء بالماء فطرة','80','34','0');
INSERT INTO [t121382] VALUES (102,'(٦٨) باب دلك اليد بالأرض، وغسلهما بعد الفراغ من الاستنجاء بالماء','81','34','0');
INSERT INTO [t121382] VALUES (103,'(٦٩) باب القول عند الخروج من المتوضأ','81','34','0');
INSERT INTO [t121382] VALUES (104,'(٧٠) باب ذكر خبر روي عن النبي -صلى الله عليه وسلم- في نفي تنجيس الماء، بلفظ مجمل غير مفسر، بلفظ عام مراده خاص','81','34','0');
INSERT INTO [t121382] VALUES (105,'(٧١) باب ذكر الخبر المفسر للفظة المجملة التي ذكرتها، والدليل على أن النبي -صلى الله عليه وسلم- إنما أراد بقوله: "الماء لا ينجسه شيء"، بعض المياه لا كل','82','34','0');
INSERT INTO [t121382] VALUES (106,'(٧٢) باب النهي عن اغتسال الجنب في الماء الدائم، بلفظ عام مراده خاص، وفيه دليل على أن قوله -صلى الله عليه وسلم -: "الماء لا ينجسه شيء" -لفظ عام مراده خاص، على ما بينت قبل- أراد الماء الذي يكون قلتين فصاعدا','83','34','0');
INSERT INTO [t121382] VALUES (107,'(٧٣) باب النهي عن الوضوء من الماء الدائم الذي قد بيل فيه، والنهي عن الشرب منه بذكر لفظ عام مراده خاص','83','34','0');
INSERT INTO [t121382] VALUES (108,'(٧٤) باب الأمر بغسل الإناء من ولوغ الكلب، والدليل على أن النبي -صلى الله عليه وسلم- إنما أمر بغسل الإناء من ولوغ الكلب تطهيرا للإناء، لا على ما ادعى بعض أهل العلم أن الأمر بغسله أمر تعبد وأن الإناء طاهر، والوضوء والاغتسال بذلك الماء جائز، وشرب ذلك الماء طلق مباح','83','34','0');
INSERT INTO [t121382] VALUES (109,'(٧٥) باب الأمر بإهراق الماء الذي ولغ فيه الكلب، وغسل الإناء من ولوغ الكلب، وفيه دليل على نقض قول من زعم أن الماء طاهر، والأمر بغسل الإناء تعبد، إذ غير جائز أن يأمر النبي -صلى الله عليه وسلم- بهراقة ماء طاهر غير نجس','84','34','0');
INSERT INTO [t121382] VALUES (110,'(٧٦) باب النهي عن غمس المستيقظ من النوم يده في الإناء قبل غسلها','85','34','0');
INSERT INTO [t121382] VALUES (111,'(٧٧) باب ذكر الدليل على أن النبي -صلى الله عليه وسلم- إنما أراد بقوله: "فإنه لا يدري أين باتت يده منه"، أي أنه لا يدري أين أتت يده من جسده','85','34','0');
INSERT INTO [t121382] VALUES (112,'(٧٨) باب ذكر الدليل على أن الماء إذا خالطه فرث ما يؤكل لحمه لم ينجس','86','34','0');
INSERT INTO [t121382] VALUES (113,'(٧٩) باب الرخصة في الوضوء بسؤر الهرة، والدليل على أن خراطيم ما يأكل الميتة من السباع، ومما لا يجوز أكل لحمه من الدواب والطيور إذا ماس الماء الذي دون القلتين، ولا نجاسة مرئية بخراطيمها ومناقيرها إن ذلك لا ينجس الماء، إذ العلم محيط أن الهرة تأكل الفأر، وقد أباح النبي -صلى الله عليه وسلم- الوضوء بفضل سؤرها، فدلت سنته على أن خرطوم ما يأكل الميتة إذا ماس الماء الذي دون القلتين لم ينجس ذلك، ما خلا الكلب الذي قد حض النبي -صلى الله عليه وسلم- بالأمر بغسل الإناء من ولوغه سبعا، وخلا الخنزير الذي هو أنجس من الكلب أو مثله','87','34','0');
INSERT INTO [t121382] VALUES (114,'(٨٠) باب ذكر الدليل على أن سقوط الذباب في الماء لا ينجسه، وفيه ما دل على أن لا نجاسة في الأحياء، وإن كان لا يجوز أكل لحمه، إلا ما خص النبي -صلى الله عليه و','88','34','0');
INSERT INTO [t121382] VALUES (115,'(٨١) باب إباحة الوضوء بالماء المستعمل. والدليل على أن الماء إذا غسل به بعض أعضاء البدن أو جميعه لم ينجس الماء، وكان الماء طاهرا. إذا كان الموضع المغسول من البدن طاهرا لا نجاسة عليه','89','34','0');
INSERT INTO [t121382] VALUES (116,'(٨٢) باب إباحة الوضوء من فضل وضوء المتوضئ','89','34','0');
INSERT INTO [t121382] VALUES (117,'(٨٣) باب إباحة الوضوء من فضل وضوء المرأة','90','34','0');
INSERT INTO [t121382] VALUES (118,'(٨٤) باب إباحة الوضوء بفضل غسل المرأة من الجنابة','90','34','0');
INSERT INTO [t121382] VALUES (119,'(٨٥) باب الدليل على أن سؤر الحائض ليس بنجس. وإباحة الوضوء والغسل به، إذ هو طاهر غير نجس. إذ لو كان سؤر حائض نجسا لما شرب النبي -صلى الله عليه وسلم- ماء نجسا غير مضطر إلى شربه','91','34','0');
INSERT INTO [t121382] VALUES (120,'(٨٦) باب الرخصة في الغسل والوضوء من ماء البحر، إذ ماؤه طهور، ميتته حل، ضد قول من كره الوضوء والغسل من ماء البحر؛ وزعم أن تحت البحر نارا، وتحت النار بحرا، حتى عد سبعة أبحر، وسبع نيران. وكره الوضوء والغسل من مائه لهذه العلة زعم','91','34','0');
INSERT INTO [t121382] VALUES (121,'(٨٧) باب الرخصة في الوضوء والغسل من الماء الذي يكون في أواني أهل الشرك وأسقيتهم، والدليل على أن الإهاب يطهر بدباغ المشركين إياه','92','34','0');
INSERT INTO [t121382] VALUES (122,'(٨٨) باب الرخصة في الوضوء من الماء يكون في جلود الميتة إذا دبغت','93','34','0');
INSERT INTO [t121382] VALUES (123,'(٨٩) باب الدليل على أن أبوال ما يؤكل لحمه ليس بنجس، ولا ينجس الماء إذا خالطه. إذ النبي -صلى الله عليه وسلم- قد أمر بشرب أبوال الإبل مع ألبانها، ولو كان نجسا لم يأمر بشربه، وقد أعلم أن لا شفاء في المحرم، وقد أمر بالاستشفاء بأبوال الإبل، ولو كان نجسا كان محرما، كان داء لا دواء، وما كان فيه شفاء كما أعلم -صلى الله عليه وسلم- لما سئل: أيتداوى [١٩ - ب] بالخمر؟ فقال: إنما هي داء وليست بدواء"','93','34','0');
INSERT INTO [t121382] VALUES (124,'(٩٠) باب ذكر خبر روي عن النبي -صلى الله عليه وسلم- في إجازة الوضوء بالمد من الماء، أوهم بعض العلماء أن توقيت المد من الماء للوضوء توقيت لا يجوز الوضوء بأقل منه','94','34','0');
INSERT INTO [t121382] VALUES (125,'(٩١) باب ذكر الدليل على أن توقيت المد من الماء للوضوء، أن الوضوء بالمد يجزئ، لا أنه لا يسع المتوضئ أن يزيد على المد أو ينقص منه، إذ لو لم يجزئ الزيادة على ذلك ولا النقصان منه، كان على المرء إذا أراد الوضوء أن يكيل مدا من ماء فيتوضأ به، لا يبقي منه شيئا. وقد يرفق المتوضئ بالقليل من الماء فيكفي بغسل أعضاء الوضوء، ويخرق بالكثير فلا يكفي لغسل أعضاء الوضوء','94','34','0');
INSERT INTO [t121382] VALUES (126,'(٩٢) باب الرخصة في الوضوء بأقل من قدر المد من الماء','95','34','0');
INSERT INTO [t121382] VALUES (127,'(٩٣) باب ذكر الدليل على أن لا توقيت في قدر الماء الذي يتوضأ به المرء، فيضيق على المتوضئ أن يزيد عليه أو ينقص منه، إذ لو كان لقدر الماء الذي يتوضأ به المرء مقدار لا يجوز أن يزيد عليه ولا ينقص منه شيئا، لما جاز أن يجتمع اثنان ولا جماعة على إناء واحد، فيتوضئوا منه جميعا. والعلم محيط أنهم إذا اجتمعوا على إناء واحد يتوضئون منه، فإن بعضهم أكثر حملا للماء من بعض','95','34','0');
INSERT INTO [t121382] VALUES (128,'(٩٤) باب استحباب القصد في صب الماء وكراهة التعدي فيه، والأمر باتقاء وسوسة الماء','96','34','0');
INSERT INTO [t121382] VALUES (129,'(٩٥) باب إباحة الوضوء والغسل في أواني النحاس','96','34','0');
INSERT INTO [t121382] VALUES (130,'(٩٦) باب إباحة الوضوء من أواني الزجاج، ضد قول بعض المتصوفة الذي يتوهم أن اتخاذ أواني الزجاج من الإسراف. إذ الخزف أصلب وأبقى من الزجاج','97','34','0');
INSERT INTO [t121382] VALUES (131,'(٩٧) باب إباحة الوضوء من الركوة والقعب','98','34','0');
INSERT INTO [t121382] VALUES (132,'(٩٨) باب إباحة الوضوء من الجفان والقصاع','98','34','0');
INSERT INTO [t121382] VALUES (133,'(٩٩) باب الأمر بتغطية الأواني: التي يكون فيها الماء للوضوء، بلفظ مجمل غير مفسر، ولفظ عام مراده خاص','99','34','0');
INSERT INTO [t121382] VALUES (134,'(١٠٠) باب ذكر الخبر المفسر للفظة المجملة التي ذكرتها، والدليل على أن النبي -صلى الله عليه وسلم- إنما أمر بتغطية الأواني بالليل، لا بالنهار جميعا','99','34','0');
INSERT INTO [t121382] VALUES (135,'(١٠١) باب الأمر بتسمية الله -عز وجل- عند تخمير الأواني، والعلة التي من أجلها أمر النبي -صلى الله عليه وسلم- بتخمير الإناء','100','34','0');
INSERT INTO [t121382] VALUES (136,'(١٠٢) باب [٢٢ - أ] بدء النبي -صلى الله عليه وسلم- بالسواك عند دخول منزله','102','34','0');
INSERT INTO [t121382] VALUES (137,'(١٠٣) باب فضل السواك وتطهير الفم به','102','34','0');
INSERT INTO [t121382] VALUES (138,'(١٠٤) باب استحباب التسوك عند القيام من النوم للتهجد','102','34','0');
INSERT INTO [t121382] VALUES (139,'(١٠٥) باب فضل السواك وتضعيف فضل الصلاة التي يستاك لها على الصلاة التي لا يستاك لها -إن صح الخبر-','103','34','0');
INSERT INTO [t121382] VALUES (140,'(١٠٦) باب الأمر بالسواك عند كل صلاة أمر ندب وفضيلة، لا أمر وجوب وفريضة','103','34','0');
INSERT INTO [t121382] VALUES (141,'(١٠٧) باب ذكر الدليل على أن الأمر بالسواك أمر فضيلة لا أمر فريضة. إذ لو كان السواك فرضا أمر النبي -صلى الله عليه وسلم- أمته شق ذلك عليهم أو لم يشق. وقد أعلم -صلى الله عليه وسلم- أنه كان آمرا به أمته عند كل صلاة، لولا أن ذلك يشق عليهم. فدل هذا القول منه -صلى الله عليه وسلم- أن أمره بالسواك أمر فضيلة. وأنه إنما أمر به من يخف ذلك عليه، دون من يشق ذلك عليه','104','34','0');
INSERT INTO [t121382] VALUES (142,'(١٠٨) باب صفة استياك النبي - صلى الله عليه وسلم -','105','34','0');
INSERT INTO [t121382] VALUES (143,'(١٠٩) باب إيجاب إحداث النية للوضوء والغسل','105','34','0');
INSERT INTO [t121382] VALUES (144,'(١١٠) باب ذكر تسمية الله -عز وجل- عند الوضوء','106','34','0');
INSERT INTO [t121382] VALUES (145,'(١١١) باب الأمر بغسل اليدين ثلاثا، عند الاستيقاظ من النوم قبل إدخالهما الإناء','106','34','0');
INSERT INTO [t121382] VALUES (146,'(١١٢) باب كراهة معارضة خبر النبي- عليه السلام- بالقياس والرأي. والدليل على أن أمر النبي -صلى الله عليه وسلم- يجب قبوله إذا علم المرء به، وإن لم يدرك ذلك عقله ورأيه. قال الله -عز وجل-: (وما كان لمؤمن ولا مؤمنة إذا قضى الله ورسوله أمرا أن يكون لهم الخيرة من أمرهم) [الأحزاب: ٣٦]','107','34','0');
INSERT INTO [t121382] VALUES (147,'(١١٣) باب صفة غسل اليدين قبل إدخالهما الإناء. وصفة وضوء النبي -صلى الله عليه وسلم -.','107','34','0');
INSERT INTO [t121382] VALUES (148,'(١١٤) باب إباحة المضمضة والاستنشاق من غرفة واحدة، والوضوء مرة مرة','108','34','0');
INSERT INTO [t121382] VALUES (149,'(١١٥) باب الأمر بالاستنشاق عند الاستيقاظ من النوم، وذكر العلة التي من أجلها أمر به','109','34','0');
INSERT INTO [t121382] VALUES (150,'(١١٦) باب الأمر بالمبالغة في الاستنشاق إذا كان المتوضئ مفطرا غير صائم','109','34','0');
INSERT INTO [t121382] VALUES (151,'(١١٧) باب تخليل اللحية في الوضوء عند غسل الوجه','109','34','0');
INSERT INTO [t121382] VALUES (152,'(١١٨) باب استحباب صك الوجه بالماء عند غسل الوجه','110','34','0');
INSERT INTO [t121382] VALUES (153,'(١١٩) باب استحباب تجديد حمل الماء لمسح الرأس غير فضل بلل اليدين','111','34','0');
INSERT INTO [t121382] VALUES (154,'(١٢٠) باب استحباب مسح الرأس باليدين جميعا ليكون أوعب لمسح جميع الرأس. وصفة المسح، والبدء بمقدم الرأس قبل المؤخر في المسح','111','34','0');
INSERT INTO [t121382] VALUES (155,'(١٢١) باب ذكر الدليل على أن المسح على الرأس إنما يكون بما يبقى من بلل الماء على اليدين، لا بنفس الماء كما يكون الغسل بالماء','112','34','0');
INSERT INTO [t121382] VALUES (156,'(١٢٢) باب مسح جميع الرأس في الوضوء','112','34','0');
INSERT INTO [t121382] VALUES (157,'(١٢٣) باب مسح باطن الأذنين وظاهرهما','112','34','0');
INSERT INTO [t121382] VALUES (158,'(١٢٤) باب ذكر الدليل على أن الكعبين اللذين أمر المتوضئ بغسل الرجلين إليهما، العظمان الناتئان في جانبي القدم، لا العظم الصغير الناتئ على ظهر القدم، على ما يتوهمه من يتحذلق ممن لا يفهم العلم ولا لغة العرب','113','34','0');
INSERT INTO [t121382] VALUES (159,'(١٢٥) باب التغليظ في ترك غسل العقبين في الوضوء. والدليل على أن الفرض غسل القدمين، لا مسحهما، إذا كانتا باديتين غير مغطيتين بالخف أو ما يقوم مقام الخف، لا على ما [٢٥ - ب] زعمت الروافض أن الفرض مسح القدمين لا غسلهما، إذ لو كان الماسح على القدمين مؤديا للفرض، لما جاز أن يقال لتارك فضيلة: ويل له. وقال -صلى الله عليه وسلم-: "ويل للأعقاب من النار"، إذا ترك المتوضئ غسل عقبيه','114','34','0');
INSERT INTO [t121382] VALUES (160,'(١٢٦) باب التغليظ في ترك غسل بطون الأقدام في الوضوء. وفيه أيضا دلالة على أن الماسح على ظهر القدمين غير مؤد للفرض، لا كما زعمت الروافض أن الفرض مسح ظهورهما، لا غسل جميع القدمين','115','34','0');
INSERT INTO [t121382] VALUES (161,'(١٢٧) باب ذكر الدليل على أن المسح على القدمين غير جائز، لا كما زعمت الروافض، والخوارج','115','34','0');
INSERT INTO [t121382] VALUES (162,'(١٢٨) باب ذكر البيان أن الله -جل وعلا- أمر بغسل القدمين في قوله: (وأرجلكم إلى الكعبين) لا بمسحهما، على ما زعمت الروافض والخوارج. والدليل على صحة تأويل المطلبي -رحمه الله- أن معنى الآية على التقديم والتأخير، على معنى: اغسلوا وجوهكم وأيديكم وأرجلكم وامسحوا برءوسكم؛ [٢٦ - أ] فقدم ذكر المسح على ذكر الرجلين، كما قال ابن مسعود، وابن عباس، وعروة بن الزبير: وأرجلكم إلى الكعبين، قالوا: رجع الأمر إلى الغسل','116','34','0');
INSERT INTO [t121382] VALUES (163,'(١٢٩) باب التغليظ في المسح على الرجلين وترك غسلهما في الوضوء، والدليل على أن الماسح على القدمين التارك لغسلهما، مستوجب للعقاب بالنار، إلا أن يعفو الله ويصفح، نعوذ بالله من عقابه','117','34','0');
INSERT INTO [t121382] VALUES (164,'(١٣٠) باب غسل أنامل القدمين في الوضوء، وفيه ما دل على أن الفرض غسلهما لا مسحهما','117','34','0');
INSERT INTO [t121382] VALUES (165,'(١٣١) باب تخليل أصابع القدمين في الوضوء','117','34','0');
INSERT INTO [t121382] VALUES (166,'(١٣٢) باب صفة وضوء النبي -صلى الله عليه وسلم- ثلاثا ثلاثا','118','34','0');
INSERT INTO [t121382] VALUES (167,'(١٣٣) باب إباحة الوضوء مرتين مرتين','118','34','0');
INSERT INTO [t121382] VALUES (168,'(١٣٤) باب إباحة الوضوء مرة مرة. والدليل على أن غاسل أعضاء الوضوء مرة مرة مؤد لفرض الوضوء. إذ غاسل أعضاء الوضوء مرة مرة واقع عليه اسم غاسل. والله -عز وجل- أمر بغسل أعضاء الوضوء بلا ذكر توقيت. وفي وضوء النبي -صلى الله عليه وسلم- مرة مرة، ومرتين مرتين، وثلاثا ثلاثا، وغسل بعض أعضاء الوضوء شفعا، وبعضه وترا، دلالة على أن هذا كله مباح. وأن كل من فعل في الوضوء ما فعله النبي -صلى الله عليه وسلم- في بعض الأوقات مؤد لفرض الوضوء. لأن هذا من اختلاف المباح، لا من الاختلاف الذي بعضه مباح وبعضه محظور','118','34','0');
INSERT INTO [t121382] VALUES (169,'(١٣٥) باب إباحة غسل بعض أعضاء الوضوء شفعا وبعضه وترا','119','34','0');
INSERT INTO [t121382] VALUES (170,'(١٣٦) باب التغليظ في غسل أعضاء الوضوء أكثر من ثلاث، والدليل على أن فاعله مسيء ظالم، أو متعد ظالم','119','34','0');
INSERT INTO [t121382] VALUES (171,'(١٣٧) باب الأمر بإسباغ الوضوء','120','34','0');
INSERT INTO [t121382] VALUES (172,'(١٣٨) باب ذكر تكفير الخطايا، والزيادة في الحسنات بإسباغ الوضوء على المكاره','120','34','0');
INSERT INTO [t121382] VALUES (173,'(١٣٩) باب الأمر بالتيامن في الوضوء أمر استحباب لا أمر إيجاب','121','34','0');
INSERT INTO [t121382] VALUES (174,'(١٤٠) باب ذكر الدليل على أن الأمر بالبدء بالميامن في الوضوء أمر استحباب واختيار، لا أمر فرض وإيجاب','122','34','0');
INSERT INTO [t121382] VALUES (175,'(١٤١) باب الرخصة في المسح على العمامة','122','34','0');
INSERT INTO [t121382] VALUES (176,'(١٤٢) باب ذكر المسح على الخفين من غير ذكر توقيت للمسافر وللمقيم بذكر أخبار مجملة [٢٨ - أ] غير مفسرة','123','34','0');
INSERT INTO [t121382] VALUES (177,'(١٤٣) باب ذكر مسح النبي -صلى الله عليه وسلم- على الخفين في الحضر','124','34','0');
INSERT INTO [t121382] VALUES (178,'(١٤٤) باب ذكر مسح النبي -صلى الله عليه وسلم- على الخفين بعد نزول سورة المائدة، ضد قول من زعم أن النبي -صلى الله عليه وسلم- إنما مسح على الخفين قبل نزول المائدة','124','34','0');
INSERT INTO [t121382] VALUES (179,'(١٤٥) باب الرخصة في المسح على الموقين','125','34','0');
INSERT INTO [t121382] VALUES (180,'(١٤٦) باب ذكر الخبر المفسر للألفاظ المجملة التي ذكرتها. والدليل على أن الرخصة في المسح على الخفين للابسها على طهارة، دون لابسها محدثا غير متطهر','126','34','0');
INSERT INTO [t121382] VALUES (181,'(١٤٧) باب الدليل على أن لابس أحد الخفين قبل غسل كلا الرجلين، إذا لبس الخف الآخر بعد غسل الرجل الأخرى، غير جائز له المسح على الخفين إذا أحدث، إذ هو لابس أحد الخفين قبل كمال الطهارة. والنبي -صلى الله عليه وسلم- إنما رخص في المسح على الخفين إذا لبسهما على طهارة. ومن ذكرنا في هذا الباب صفته، هو لابس أحد الخفين على غير طهر، إذ هو غاسل إحدى الرجلين لا كلتيهما عند لبسه أحد الخفين','127','34','0');
INSERT INTO [t121382] VALUES (182,'(١٤٨) باب ذكر توقيت المسح على الخفين للمقيم والمسافر','128','34','0');
INSERT INTO [t121382] VALUES (183,'(١٤٩) باب ذكر الدليل على أن الأمر بالمسح على الخفين أمر إباحة، أن المسح يقوم مقام غسل القدمين، إذا كان القدم باديا غير مغطى بالخف، وإن خالع الخف وإن كان لبسه على طهارة، إذا غسل قدميه كان مؤديا للفرض، غير عاص، إلا أن يكون تاركا للمسح رغبة عن سنة النبي -صلى الله عليه وسلم -','128','34','0');
INSERT INTO [t121382] VALUES (184,'(١٥٠) باب ذكر الدليل على أن الرخصة في المسح على الخفين إنما هي من الحدث الذي يوجب الوضوء دون الجنابة التي توجب الغسل','129','34','0');
INSERT INTO [t121382] VALUES (185,'(١٥١) باب التغليظ في ترك المسح على الخفين رغبة عن السنة','129','34','0');
INSERT INTO [t121382] VALUES (186,'(١٥٢) باب الرخصة في المسح على الجوربين والنعلين','129','34','0');
INSERT INTO [t121382] VALUES (187,'(١٥٣) باب ذكر أخبار رويت عن النبي -صلى الله عليه وسلم- في المسح على النعلين مجملة، غلط في الاحتجاج بها بعض من أجاز المسح على النعلين في الوضوء الواجب من الحدث','130','34','0');
INSERT INTO [t121382] VALUES (188,'(١٥٤) باب ذكر الدليل على أن مسح النبي -صلى الله عليه وسلم- على النعلين كان في وضوء متطوع به، لا في وضوء واجب عليه من حدث يوجب الوضوء','130','34','0');
INSERT INTO [t121382] VALUES (189,'(١٥٥) باب ذكر أخبار رويت عن النبي -صلى الله عليه وسلم- في المسح على الرجلين مجملة، غلط في الاحتجاج بها بعض من لم ينعم الروية في الأخبار، وأباح للمحدث المسح على الرجلين','131','34','0');
INSERT INTO [t121382] VALUES (190,'(١٥٦) باب ذكر الدليل على أن مسح النبي -صلى الله عليه وسلم- على القدمين كان وهو طاهر لا محدث','131','34','0');
INSERT INTO [t121382] VALUES (191,'(١٥٧) باب الرخصة في استعانة المتوضئ ممن يصب عليه الماء ليتطهر، خلاف مذهب من يتوهم من المتصوفة أن هذا من الكبر','131','34','0');
INSERT INTO [t121382] VALUES (192,'(١٥٨) باب الرخصة في وضوء الجماعة من الإناء الواحد','132','34','0');
INSERT INTO [t121382] VALUES (193,'(١٥٩) باب الرخصة في وضوء الرجال والنساء من الإناء الواحد','132','34','0');
INSERT INTO [t121382] VALUES (194,'(١٦٠) باب استحباب الوضوء لذكر الله، وإن كان الذكر على غير وضوء مباحا','133','34','0');
INSERT INTO [t121382] VALUES (195,'(١٦١) باب ذكر الدليل على أن كراهية النبي -صلى الله عليه وسلم- لذكر الله على [٣١ - أ] غير طهر كانت إذ الذكر على طهارة أفضل، لا أنه غير جائز أن يذكر الله على غ','133','34','0');
INSERT INTO [t121382] VALUES (196,'(١٦٢) باب الرخصة في قراءة القرآن، وهو أفضل الذكر، على غير وضوء','134','34','0');
INSERT INTO [t121382] VALUES (197,'(١٦٣) باب استحباب الوضوء للدعاء، ومسألة الله، ليكون المرء طاهرا عند الدعاء والمسألة','135','34','0');
INSERT INTO [t121382] VALUES (198,'(١٦٤) باب استحباب وضوء الجنب إذا أراد النوم','136','34','0');
INSERT INTO [t121382] VALUES (199,'(١٦٥) باب ذكر الدليل على أن الوضوء الذي أمر به الجنب للنوم كوضوء الصلاة، إذ العرب قد تسمي غسل اليدين وضوءا','136','34','0');
INSERT INTO [t121382] VALUES (200,'(١٦٦) باب استحباب غسل الذكر مع الوضوء إذا أراد الجنب النوم','137','34','0');
INSERT INTO [t121382] VALUES (201,'(١٦٧) باب استحباب الوضوء للجنب إذا أراد الأكل','137','34','0');
INSERT INTO [t121382] VALUES (202,'(١٦٨) باب استحباب الوضوء عند النوم، وإن لم يكن المرء جنبا ليكون مبيته على طهارة','137','34','0');
INSERT INTO [t121382] VALUES (203,'(١٦٩) باب ذكر الدليل على أن الوضوء الذي أمر به الجنب للأكل كوضوء الصلاة سواء','138','34','0');
INSERT INTO [t121382] VALUES (204,'(١٧٠) باب ذكر الدليل على أن الأمر بالوضوء للجنب عند إرادة الأكل أمر ندب وإرشاد، وفضيلة وإباحة','138','34','0');
INSERT INTO [t121382] VALUES (205,'(١٧١) باب ذكر الدليل على أن جميع ما ذكرت من الأبواب من وضوء الاستحباب على ما ذكرت، أن الأمر بالوضوء من ذلك كله أمر ندب وإرشاد وفضيلة، لا أمر فرض وإيجاب','138','34','0');
INSERT INTO [t121382] VALUES (206,'(١٧٢) باب استحباب الوضوء عند معاودة الجماع بلفظ مجمل غير مفسر','138','34','0');
INSERT INTO [t121382] VALUES (207,'(١٧٣) باب ذكر الدليل على أن الوضوء لمعاودة لجماع كوضوء الصلاة','139','34','0');
INSERT INTO [t121382] VALUES (208,'(١٧٤) باب ذكر الدليل على أن الأمر بالوضوء عند إرادة الجماع [أمر ندب وإرشاد]','139','34','0');
INSERT INTO [t121382] VALUES (209,'(١٧٥) باب فضل التهليل والشهادة للنبي -صلى الله عليه وسلم- بالرسالة والعبودية، وأن لا يطرى كما أطرت النصارى عيسى ابن مريم، إذا شهد له بالعبودية مع الشهادة له بالرسالة عند الفراغ من الوضوء','140','34','0');
INSERT INTO [t121382] VALUES (210,'(١٧٦) باب ذكر أخبار رويت عن النبي -صلى الله عليه وسلم- في الرخصة في ترك الغسل في الجماع من غير إمناء قد نسخ بعض أحكامها','141','34','0');
INSERT INTO [t121382] VALUES (211,'(١٧٧) باب ذكر نسخ إسقاط الغسل في الجماع من غير إمناء','141','34','0');
INSERT INTO [t121382] VALUES (212,'(١٧٨) باب ذكر إيجاب الغسل بمماسة الختانين أو التقائهما [٣٣ - ب] وإن لم يكن أمنى','143','34','0');
INSERT INTO [t121382] VALUES (213,'(١٧٩) باب إيجاب إحداث النية للاغتسال من الجنابة. والدليل على ضد قول من زعم أن الجنب إذا دخل نهرا ناويا للسباحة فماس الماء جميع بدنه ولم ينو غسلا ولا أراده إذا فرض الغسل، ولا تقربا إلى الله -عز وجل- أو صب عليه ماء، وهو مكره، فماس الماء جميع جسده، أن فرض الغسل ساقط عنه','144','34','0');
INSERT INTO [t121382] VALUES (214,'(١٨٠) باب ذكر الدليل على أن جماع نسوة لا يوجب أكثر من غسل واحد','144','34','0');
INSERT INTO [t121382] VALUES (215,'(١٨١) باب صفة ماء الرجل الذي يوجب الغسل، وصفة ماء المرأة الذي يوجب عليها الغسل، إذا لم يكن جماع يكون فيه التقاء الختانين','145','34','0');
INSERT INTO [t121382] VALUES (216,'(١٨٢) باب إيجاب الغسل من الإمناء، وإن كان الإمناء من غير جماع، يلتقي فيه الختانان أو يتماسان، كان الإمناء من مباشرة أو جماع دون الفرج، أو من قبلة أو من احتلام. كان الإمناء في اليقظة بعد الغسل من الجنابة، قبل تبول الجنب قبل الاغتسال أو بعده، أو بعد ما يبول. ضد قول من زعم أن الإمناء إذا كان بعد الجنابة وبعد الاغتسال قبل تبول الجنب أوجب ذلك المني غسلا ثانيا، وإن كان الإمناء بعد ما تبول الجنب ثم يغتسل بعد البول ما يوجب ذلك الإمناء -زعم- غسلا','146','34','0');
INSERT INTO [t121382] VALUES (217,'(١٨٣) باب ذكر إيجاب الغسل على المرأة في الاحتلام إذا أنزلت الماء','147','34','0');
INSERT INTO [t121382] VALUES (218,'(١٨٤) باب ذكر الدليل على أن لا وقت فيما يغتسل به المرء من الماء، فيضيق الزيادة فيه أو النقصان منه','147','34','0');
INSERT INTO [t121382] VALUES (219,'(١٨٥) باب الاستتار للاغتسال من الجنابة','148','34','0');
INSERT INTO [t121382] VALUES (220,'(١٨٦) باب إباحة الاغتسال من القصاع والمراكن [٣٥ - أ] والطاس','148','34','0');
INSERT INTO [t121382] VALUES (221,'(١٨٧) باب صفة الغسل من الجنابة','149','34','0');
INSERT INTO [t121382] VALUES (222,'(١٨٨) باب تخليل أصول شعر الرأس بالماء، قبل إفراغ الماء على الرأس. وحثي الماء على الرأس بعد التخليل حثيات ثلاث','150','34','0');
INSERT INTO [t121382] VALUES (223,'(١٨٩) باب اكتفاء صاحب الجمة والشعر الكثير بإفراغ ثلاث حثيات من الماء على الرأس في غسل الجنابة','150','34','0');
INSERT INTO [t121382] VALUES (224,'(١٩٠) باب استحباب بدء المغتسل بإفاضة الماء على الميامن قبل المياسر','150','34','0');
INSERT INTO [t121382] VALUES (225,'(١٩١) باب الرخصة في ترك المرأة نقض ضفائر رأسها في الغسل من الجنابة','151','34','0');
INSERT INTO [t121382] VALUES (226,'(١٩٢) باب غسل المرأة من الجنابة، والدليل على أن غسلها كغسل الرجل سواء','152','34','0');
INSERT INTO [t121382] VALUES (227,'(١٩٣) باب الزجر عن دخول الماء بغير مئزر للغسل','153','34','0');
INSERT INTO [t121382] VALUES (228,'(١٩٤) باب اغتسال الرجل والمرأة وهما جنبان من إناء واحد','153','34','0');
INSERT INTO [t121382] VALUES (229,'(١٩٥) باب إفراغ المرأة الماء على يد زوجها ليغسل يديه قبل إدخالهما الإناء إذا أراد الاغتسال من الجنابة','153','34','0');
INSERT INTO [t121382] VALUES (230,'(١٩٦) باب الأمر بالاغتسال إذا أسلم الكافر','153','34','0');
INSERT INTO [t121382] VALUES (231,'(١٩٧) باب استحباب غسل الكافر إذا أسلم بالماء والسدر','154','34','0');
INSERT INTO [t121382] VALUES (232,'(١٩٨) باب استحباب الاغتسال من الحجامة، ومن غسل الميت','155','34','0');
INSERT INTO [t121382] VALUES (233,'(١٩٩) باب استحباب اغتسال المغمى عليه بعد الإفاقة من الإغماء','155','34','0');
INSERT INTO [t121382] VALUES (234,'(٢٠٠) باب ذكر الدليل على أن اغتسال النبي صلى الله عليه وسلم من الإغماء لم يكن اغتسال فرض ووجوب، وإنما اغتسل استراحة من الغم الذي أصابه في الإغماء ليخفف بدنه ويستريح','156','34','0');
INSERT INTO [t121382] VALUES (235,'(٢٠١) باب استحباب اغتسال الجنب للنوم','156','34','0');
INSERT INTO [t121382] VALUES (236,'(٢٠٢) باب ذكر دليل أن النبي -صلى الله عليه وسلم- قد كان يأمر بالوضوء قبل نزول سورة المائدة','157','34','0');
INSERT INTO [t121382] VALUES (237,'(٢٠٣) باب ذكر ما كان من إباحة الصلاة بلا تيمم عند عدم الماء قبل نزول آية التيمم','159','34','0');
INSERT INTO [t121382] VALUES (238,'(٢٠٤) باب الرخصة في النزول في السفر على غير ماء للحاجة تبدو من منافع الدنيا','159','34','0');
INSERT INTO [t121382] VALUES (239,'(٢٠٥) باب ذكر ما كان الله -عز وجل- فضل به رسوله -صلى الله عليه وسلم- على الأنبياء قبله، وفضل أمته على الأمم السالفة قبلهم، بإباحته لهم التيمم بالتراب عند الإعواز من الماء','159','34','0');
INSERT INTO [t121382] VALUES (240,'(٢٠٦) باب ذكر الدليل على أن ما وقع عليه اسم التراب فالتيمم به جائز عند الإعواز من الماء، وإن كان التراب على بساط أو ثوب أو حيث ما كان، وإن لم يكن على الأرض، مع الدليل على أن خبر أبي معاوية الذي ذكرناه مختصر. أراد "جعلت لنا الأرض طهورا" أي عند الإعواز من الماء، إذا كان المحدث غير مريض مرضا يخاف -إن ماس الماء- التلف أو المرض المخوف أو الألم الشديد. لا أنه جعل الأرض طهورا وإن كان المحدث صحيحا واجدا للماء، أو مريضا لا يضر إمساس البدن الماء','160','34','0');
INSERT INTO [t121382] VALUES (241,'(٢٠٧) باب إباحة التيمم بتراب [٣٩ - أ] السباخ، ضد قول من زعم من أهل عصرنا أن التيمم بالسبخة غير جائز، وقود هذه المقالة يقود إلى أن التيمم بالمدينة غير جائز، إذ أرضها سبخة. وقد خبر النبي صلى الله عليه وسلم أنها طيبة أو طابة','161','34','0');
INSERT INTO [t121382] VALUES (242,'(٢٠٨) باب ذكر الدليل على أن التيمم ضربة واحدة للوجه والكفين، لا ضربتان، مع الدليل على أن مسح الذراعين في التيمم غير واجب','162','34','0');
INSERT INTO [t121382] VALUES (243,'(٢٠٩) باب النفخ في اليدين بعد ضربهما على التراب للتيمم','162','34','0');
INSERT INTO [t121382] VALUES (244,'(٢١٠) باب نفض اليدين من التراب بعد ضربهما على الأرض قبل النفخ فيهما، وقبل مسح الوجه واليدين للتيمم','162','34','0');
INSERT INTO [t121382] VALUES (245,'(٢١١) باب ذكر الدليل على أن الجنب يجزيه التيمم عند الإعواز من الماء في السفر','164','34','0');
INSERT INTO [t121382] VALUES (246,'(٢١٢) باب الرخصة في التيمم للمجدور والمجروح، وإن كان الماء موجودا إذا خاف -إن ماس الماء البدن- التلف، أو المرض، أو الوجع المؤلم','165','34','0');
INSERT INTO [t121382] VALUES (247,'(٢١٣) باب استحباب التيمم في الحضر لرد السلام وإن كان الماء موجودا','166','34','0');
INSERT INTO [t121382] VALUES (248,'(٢١٤) باب حت دم الحيضة من الثوب، وقرصه بالماء، ورش الثوب بعد','166','34','0');
INSERT INTO [t121382] VALUES (249,'(٢١٥) باب ذكر الدليل على أن النضح المأمور به هو نضح ما لم يصب الدم من الثوب','167','34','0');
INSERT INTO [t121382] VALUES (250,'(٢١٦) باب استحباب غسل دم الحيض من الثوب بالماء والسدر، وحكه بالأضلاع، إذ هو أحرى أن يذهب أثره من الثوب إذا حك بالضلع، وغسل بالسدر مع الماء، من أن يغسل بالماء بحتا','168','34','0');
INSERT INTO [t121382] VALUES (251,'(٢١٧) باب ذكر الدليل على أن الاقتصار من غسل الثوب الملبوس في المحيض على غسل أثر الدم منه جائز، وإن لم يحك موضع الدم بضلع، ولا قرص موضعه بالأظفار، وإن لم يغسل بسدر أيضا،','168','34','0');
INSERT INTO [t121382] VALUES (252,'(٢١٨) باب الرخصة في غسل الثوب من عرق الجنب. والدليل على أن عرق الجنب طاهر غير نجس','169','34','0');
INSERT INTO [t121382] VALUES (253,'(٢١٩) باب ذكر الدليل على أن عرق الإنسان طاهر غير نجس','170','34','0');
INSERT INTO [t121382] VALUES (254,'(٢٢٠) باب غسل بول الصبية من الثوب','170','34','0');
INSERT INTO [t121382] VALUES (255,'(٢٢١) باب غسل بول الصبية وإن كانت مرضعة، والفرق بين بولها وبين بول الصبي المرضع','171','34','0');
INSERT INTO [t121382] VALUES (256,'(٢٢٢) باب نضح بول الغلام ورشه قبل أن يطعم','171','34','0');
INSERT INTO [t121382] VALUES (257,'(٢٢٣) باب استحباب غسل المني من الثوب','172','34','0');
INSERT INTO [t121382] VALUES (258,'(٢٢٤) باب ذكر الدليل على أن المني ليس بنجس، والرخصة في فركه إذا كان يابسا من الثوب','173','34','0');
INSERT INTO [t121382] VALUES (259,'(٢٢٥) باب نضح الثوب من المذي إذا خفي موضعه في الثوب','175','34','0');
INSERT INTO [t121382] VALUES (260,'(٢٢٦) باب ذكر وطء الأذى اليابس بالخف والنعل، والدليل على أن ذلك لا يوجب غسل الخف ولا النعل. وأن تطهيرهما يكون بالمشي على الأرض الطاهرة بعدها','176','34','0');
INSERT INTO [t121382] VALUES (261,'(٢٢٧) باب النهي عن البول في المساجد وتقذيرها','176','34','0');
INSERT INTO [t121382] VALUES (262,'(٢٢٨) باب سلت المني من الثوب بالإذخر إذا كان رطبا','177','34','0');
INSERT INTO [t121382] VALUES (263,'(٢٢٩) باب الزجر عن قطع البول على البائل في المسجد قبل الفراغ منه. والدليل على أن صب دلو من ماء يطهر الأرض وإن لم يحفر موضع البول، فينقل ترابه من المسجد على ما زعم بعض العراقيين. إذ الله -عز وجل- أنعم على عباده المؤمنين بأن بعث فيهم نبيه -صلى الله عليه وسلم- ميسرا لا معسرا','177','34','0');
INSERT INTO [t121382] VALUES (264,'(٢٣٠) باب استحباب نضح الأرض [٤٤ - أ] من ربض الكلاب عليها','178','34','0');
INSERT INTO [t121382] VALUES (265,'(٢٣١) باب الدليل على أن مرور الكلاب في المساجد لا يوجب نضحا ولا غسلا','179','34','0');
INSERT INTO [t121382] VALUES (266,'كتاب الصلاة','180','0','0');
INSERT INTO [t121382] VALUES (267,'(١) باب بدء فرض الصلوات الخمس','180','266','0');
INSERT INTO [t121382] VALUES (268,'(٢) باب ذكر فرض الصلوات الخمس من عدد الركعة، بلفظ خبر مجمل غير مفسر، بلفظ عام مراده خاص','183','266','0');
INSERT INTO [t121382] VALUES (269,'(٣) باب ذكر الخبر المفسر للفظة المجملة التي ذكرتها','183','266','0');
INSERT INTO [t121382] VALUES (270,'(٤) باب فرض الصلوات الخمس، والدليل على أن لا فرض من الصلاة إلا الخمس، وأن كل ما سوى الخمس من الصلاة فتطوع، ليس شيء منها فرض إلا الخمس فقط','184','266','0');
INSERT INTO [t121382] VALUES (271,'(٥) باب الدليل على أن إقام الصلاة من الإيمان','185','266','0');
INSERT INTO [t121382] VALUES (272,'(٦) باب ذكر الدليل على أن إقام الصلاة من الإسلام. إذ الإيمان والإسلام اسمان بمعنى واحد','185','266','0');
INSERT INTO [t121382] VALUES (273,'(٧) باب في فضائل الصلوات الخمس','186','266','0');
INSERT INTO [t121382] VALUES (274,'(٨) باب ذكر الدليل على أن الحد الذي أصابه هذا السائل فأعلمه - صلى الله عليه وسلم - أن الله قد عفا عنه بوضوئه وصلاته كان معصية ارتكبها دون الزنى الذي ي','187','266','0');
INSERT INTO [t121382] VALUES (275,'(٩) باب ذكر الدليل على أن الصلوات الخمس إنما تكفر صغائر الذنوب دون كبائرها','188','266','0');
INSERT INTO [t121382] VALUES (276,'(١٠) باب فضيلة السجود في الصلاة، وحط الخطايا بها مع رفع الدرجات في الجنة','189','266','0');
INSERT INTO [t121382] VALUES (277,'(١١) باب فضل صلاة الصبح، وصلاة العصر','190','266','0');
INSERT INTO [t121382] VALUES (278,'(١٢) باب ذكر اجتماع ملائكة الليل وملائكة النهار في صلاة الفجر وصلاة العصر جميعا، ودعاء الملائكة لمن شهد الصلاتين جميعا','191','266','0');
INSERT INTO [t121382] VALUES (279,'(١٣) باب ذكر مواقيت الصلوات الخمس','192','266','0');
INSERT INTO [t121382] VALUES (280,'(١٤) باب ذكر الدليل على أن فرض الصلاة كان على الأنبياء قبل محمد -صلى الله عليه وسلم- كانت خمس صلوات، كهي على النبي -صلى الله عليه وسلم- وأمته، وأن أوقات صلواتهم كانت أوقات النبي محمد -صلى الله عليه وسلم- وأمته','193','266','0');
INSERT INTO [t121382] VALUES (281,'(١٥) باب ذكر وقت الصلاة للمعذور','194','266','0');
INSERT INTO [t121382] VALUES (282,'(١٦) باب اختيار الصلاة في أول أوقاتها، بذكر خبر لفظه لفظ عام، مراده خاص','195','266','0');
INSERT INTO [t121382] VALUES (283,'(١٧) باب ذكر الدليل على أن النبي -صلى الله عليه وسلم- إنما أراد بقوله: "الصلاة في أول وقتها"، بعض الصلاة دون جميعها، وفي بعض الأوقات دون جميع الأوقات. إذ قد أمر النبي -صلى الله عليه وسلم- بتبريد الظهر في شدة الحر، وقد أعلم أن لولا ضعف الضعيف، وسقم السقيم لأخر صلاة العشاء الآخرة إلى شطر الليل','195','266','0');
INSERT INTO [t121382] VALUES (284,'(١٨) باب استحباب تعجيل صلاة العصر','196','266','0');
INSERT INTO [t121382] VALUES (285,'(١٩) باب ذكر التغليظ في تأخير صلاة العصر إلى اصفرار الشمس','197','266','0');
INSERT INTO [t121382] VALUES (286,'(٢٠) باب التغليظ في تأخير صلاة العصر من غير ضرورة','198','266','0');
INSERT INTO [t121382] VALUES (287,'(٢١) باب الأمر بتبكير صلاة العصر في يوم الغيم، والتغليظ في ترك صلاة العصر','199','266','0');
INSERT INTO [t121382] VALUES (288,'(٢٢) باب استحباب تعجيل صلاة المغرب','199','266','0');
INSERT INTO [t121382] VALUES (289,'(٢٣) باب التغليظ في تأخير صلاة المغرب، وإعلام النبي -صلى الله عليه وسلم- أمته أنهم لا يزالون بخير، ثابتين على الفطرة، ما لم يؤخروها إلى اشتباك النجوم','199','266','0');
INSERT INTO [t121382] VALUES (290,'(٢٤) باب النهي عن تسمية صلاة المغرب عشاء، إذ العامة أو كثير منهم يسمونها عشاء.','201','266','0');
INSERT INTO [t121382] VALUES (291,'(٢٥) باب استحباب تأخير صلاة العشاء إذا لم يخف المرء الرقاد قبلها، ولم يخف الإمام ضعف الضعيف، وسقم السقيم فتفوتهم الجماعة لتأخير الإمام الصلاة، أو يشق عليهم حضور الجماعة إذا أخر صلاة العشاء','201','266','0');
INSERT INTO [t121382] VALUES (292,'(٢٦) باب كراهية النوم قبل صلاة العشاء والحديث بعدها، بذكر خبر مجمل غير مفسر','203','266','0');
INSERT INTO [t121382] VALUES (293,'(٢٧) باب ذكر الخبر الدال على الرخصة في النوم قبل العشاء إذا أخرت الصلاة. وفيه ما دل على أن كراهة النبي -صلى الله عليه وسلم- النوم قبلها إذا لم تؤخر','204','266','0');
INSERT INTO [t121382] VALUES (294,'(٢٨) باب كراهة تسمية صلاة العشاء عتمة','205','266','0');
INSERT INTO [t121382] VALUES (295,'(٢٩) باب استحباب التغليس لصلاة الفجر','206','266','0');
INSERT INTO [t121382] VALUES (296,'(٣٠) باب ذكر بيان الفجر الذي يجوز صلاة الصبح بعد طلوعه. إذ الفجر هنا فجران، طلوع أحدهما بالليل. وطلوع الثاني يكون بطلوع النهار','210','266','0');
INSERT INTO [t121382] VALUES (297,'(٣١) باب فضل انتظار الصلاة والجلوس في المسجد، وذكر دعاء الملائكة لمنتظر الصلاة الجالس في المسجد','210','266','0');
INSERT INTO [t121382] VALUES (298,'(٣٢) باب ذكر الدليل على أن الشيء قد يشبه بالشيء، إذا أشبهه في بعض المعاني لا في جميعها، إذ النبي - صلى الله عليه وسلم - قد أعلم أن العبد لا يزال في صلاة م','212','266','0');
INSERT INTO [t121382] VALUES (299,'(٣٣) باب في بدء الأذان والإقامة','213','266','0');
INSERT INTO [t121382] VALUES (300,'(٣٤) باب ذكر الدليل على أن من كان أرفع صوتا وأجهر، كان أحق بالأذان ممن كان أخفض صوتا. إذ الأذان إنما ينادى به لاجتماع الناس للصلاة','214','266','0');
INSERT INTO [t121382] VALUES (301,'(٣٥) باب الأمر بالأذان للصلاة قائما لا قاعدا، إذ الأذان قائما أحرى أن يسمعه من بعد عن المؤذن من أن يؤذن وهو قاعد','215','266','0');
INSERT INTO [t121382] VALUES (302,'(٣٦) باب ذكر الدليل على أن بدء الأذان إنما كان بعد هجرة النبي -صلى الله عليه وسلم- إلى المدينة، وأن صلاته بمكة إنما كانت من غير نداء لها ولا إقامة','215','266','0');
INSERT INTO [t121382] VALUES (303,'(٣٧) باب تثنية الأذان، وإفراد الإقامة، بذكر خبر مجمل غير مفسر، بلفظ عام، مراده خاص','215','266','0');
INSERT INTO [t121382] VALUES (304,'(٣٨) باب ذكر الدليل على أن الآمر بلالا أن يشفع الأذان، ويوتر الإقامة، كان النبي -صلى الله عليه وسلم- لا بعده أبو بكر ولا عمر، كما ادعى بعض الجهلة أنه جائز أن يكون الصديق أو الفاروق أمرا بلالا بذلك','216','266','0');
INSERT INTO [t121382] VALUES (305,'(٣٩) باب ذكر الخبر المفسر للفظة المجملة التي ذكرتها، والدليل على أن النبي -صلى الله عليه وسلم- إنما أمر بأن يشفع بعض الأذان لا كلها، وأنه إنما أمر بأن يوتر بعض الإقامة لا كلها، وأن اللفظة التي في خبر أنس إنما هي','216','266','0');
INSERT INTO [t121382] VALUES (306,'(٤٠) باب تثنية "قد قامت الصلاة" في الإقامة، ضد قول بعض من لا يفهم العلم ولا يميز بين ما يكون لفظه عاما مراده خاص، وبين [ما] لفظه لفظ عام مراده عام، فتوهم بجهله أن قوله: "ويوتر الإقامة" كل الإقامة، لا بعضها من أولها إلى آخرها، يعني الحسين بن الفضل','219','266','0');
INSERT INTO [t121382] VALUES (307,'(٤١) باب الترجيع في الأذان [٥٥ - ب] مع تثنية الإقامة، وهذا من جنس اختلاف المباح، فمباح أن يؤذن المؤذن فيرجع في الأذان ويثني الإقامة، ومباح أن يثني الأذان ويفرد الإقامة، إذ قد صح كلا الأمرين من النبي -صلى الله عليه وسلم-. فأما تثنية الأذان والإقامة فلم يثبت عن النبي -صلى الله عليه وسلم- الأمر بهما','220','266','0');
INSERT INTO [t121382] VALUES (308,'(٤٢) باب التثويب في أذان الصبح','225','266','0');
INSERT INTO [t121382] VALUES (309,'(٤٣) باب الانحراف في الأذان عند قول المؤذن حي على الصلاة، حي على الفلاح، والدليل على أنه إنما ينحرف بفيه لا ببدنه كله، وإنما يمكن الانحراف بالفم بانحراف الوجه','227','266','0');
INSERT INTO [t121382] VALUES (310,'(٤٤) باب إدخال الإصبعين في الأذنين عند الأذان، إن صح الخبر، فإن هذه اللفظة لست أحفظها إلا عن حجاج بن أرطاة، ولست أفهم أسمع الحجاج هذا الخبر من عون بن أبي جحيفة أم لا؟ فأشك في صحة هذا الخبر لهذه العلة','228','266','0');
INSERT INTO [t121382] VALUES (311,'(٤٥) باب فضل الأذان، ورفع الصوت به، وشهادة من يسمعه من حجر ومدر، وشجر وجن وإنس للمؤذن','228','266','0');
INSERT INTO [t121382] VALUES (312,'(٤٦) باب الاستهام على الأذان إذا تشاجر الناس عليه','229','266','0');
INSERT INTO [t121382] VALUES (313,'(٤٧) باب ذكر تباعد الشيطان عن المؤذن عند أذانه وهربه كي لا يسمع الأذان','229','266','0');
INSERT INTO [t121382] VALUES (314,'(٤٨) باب الأمر بالأذان والإقامة في السفر للصلاة كلها، ضد قول من زعم أنه لا يؤذن في السفر للصلاة إلا للفجر خاصة','230','266','0');
INSERT INTO [t121382] VALUES (315,'(٤٩) باب الأمر بالأذان والإقامة في السفر وإن كانا اثنين لا أكثر، بذكر خبر لفظه عام مراده خاص','230','266','0');
INSERT INTO [t121382] VALUES (316,'(٥٠) باب ذكر الخبر المفسر للفظة المجملة التي ذكرت أنها لفظة عام مرادها خاص. والدليل على أن النبي -صلى الله عليه وسلم- إنما أمر أن يؤذن أحدهما لا كلاهما','231','266','0');
INSERT INTO [t121382] VALUES (317,'(٥١) باب الأذان في السفر، وإن كان المرء وحده ليس معه جماعة','232','266','0');
INSERT INTO [t121382] VALUES (318,'(٥٢) باب إباحة الأذان للصبح قبل طلوع الفجر إذا كان للمسجد مؤذنان لا مؤذن واحد، فيؤذن أحدهما قبل طلوع الفجر، والآخر بعد طلوعه بذكر خبر مجمل غير مفسر','233','266','0');
INSERT INTO [t121382] VALUES (319,'(٥٣) باب ذكر العلة التي كان لها بلال يؤذن [٥٨ - ب] بليل','234','266','0');
INSERT INTO [t121382] VALUES (320,'(٥٤) باب ذكر قدر ما كان بين أذان بلال وأذان ابن أم مكتوم','234','266','0');
INSERT INTO [t121382] VALUES (321,'(٥٥) باب ذكر خبر روي عن النبي -صلى الله عليه وسلم -[يرى] بعض أهل الجهل أنه يضاد هذا الخبر الذي ذكرنا أن النبي -صلى الله عليه وسلم- قال: "إن بلالا يؤذن بليل"','234','266','0');
INSERT INTO [t121382] VALUES (322,'(٥٦) باب الأذان للصلوات بعد ذهاب الوقت','237','266','0');
INSERT INTO [t121382] VALUES (323,'(٥٧) باب الأمر بأن يقال ما يقوله المؤذن إذا سمعه ينادي بالصلاة، بلفظ عام مراده خاص','239','266','0');
INSERT INTO [t121382] VALUES (324,'(٥٨) باب ذكر الأخبار المفسرة للفظتين اللتين ذكرتهما في خبر أبي سعيد وأم حبيبة، والدليل على أن النبي -صلى الله عليه وسلم- إنما أمر في خبر أبي سعيد أن يقال كما يقول المؤذن حتى يفرغ، وكذاك كان يقول كما يقول المؤذن حتى يسكت، خلا قوله: حي على الصلاة، حي على الفلاح','240','266','0');
INSERT INTO [t121382] VALUES (325,'(٥٩) باب ذكر فضيلة هذا القول عند سماع الأذان، إذا قاله المرء صدقا من قلبه','241','266','0');
INSERT INTO [t121382] VALUES (326,'(٦٠) باب فضل الصلاة على النبي - صلى الله عليه وسلم - بعد فراغ سماع الأذان','242','266','0');
INSERT INTO [t121382] VALUES (327,'(٦١) باب [٦٠ - ب] استحباب الدعاء عند الأذان، ورجاء إجابة الدعوة عنده','243','266','0');
INSERT INTO [t121382] VALUES (328,'(٦٢) باب صفة الدعاء عند مسألة الله -عز وجل- للنبي -صلى الله عليه وسلم- محمد الوسيلة، واستحقاق الداعي بتلك الدعوة الشفاعة يوم القيامة','243','266','0');
INSERT INTO [t121382] VALUES (329,'(٦٣) باب فضيلة الشهادة لله -عز وجل- بوحدانيته وللنبي -صلى الله عليه وسلم- برسالته وعبوديته، وبالرضا بالله ربا، وبمحمد رسولا، وبالإسلام دينا، عند سماع الأذان، وما يرجى من مغفرة الذنوب بذلك','243','266','0');
INSERT INTO [t121382] VALUES (330,'(٦٤) باب الزجر عن أخذ الأجر على الأذان','244','266','0');
INSERT INTO [t121382] VALUES (331,'(٦٥) باب الرخصة في أذان الأعمى إذا كان له من يعلمه الوقت','245','266','0');
INSERT INTO [t121382] VALUES (332,'(٦٦) باب استحباب الدعاء بين الأذان والإقامة، رجاء أن تكون الدعوة غير مردودة بينهما','245','266','0');
INSERT INTO [t121382] VALUES (333,'(٦٧) باب ذكر الصلاة كانت إلى بيت المقدس قبل هجرة النبي -صلى الله عليه وسلم- إلى المدينة، إذ القبلة في ذلك الوقت بيت المقدس لا الكعبة','246','266','0');
INSERT INTO [t121382] VALUES (334,'(٦٨) باب بدء الأمر باستقبال الكعبة للصلاة، ونسخ الأمر بالصلوات إلى بيت المقدس','247','266','0');
INSERT INTO [t121382] VALUES (335,'(٦٩) باب ذكر الدليل على أن القبلة إنما هي الكعبة لا جميع المسجد الحرام','247','266','0');
INSERT INTO [t121382] VALUES (336,'(٧٠) باب ذكر الدليل على أن الشطر في هذا الموضع القبل لا النصف. وهذا من الجنس الذي نقول إن العرب قد يوقع الاسم الواحد على الشيئين المختلفين، قد يوقع اسم الشطر على النصف وعلى القبل أي الجهة','249','266','0');
INSERT INTO [t121382] VALUES (337,'(٧١) باب النهي عن التشبيك بين الأصابع عند الخروج إلى الصلاة','250','266','0');
INSERT INTO [t121382] VALUES (338,'(٧٢) باب الدعاء عند الخروج إلى الصلاة','253','266','0');
INSERT INTO [t121382] VALUES (339,'(٧٣) باب فضل المشي إلى المساجد للصلاة','253','266','0');
INSERT INTO [t121382] VALUES (340,'(٧٤) باب السلام على النبي -صلى الله عليه وسلم-، ومسألة الله فتح أبواب الرحمة عند دخول المسجد','254','266','0');
INSERT INTO [t121382] VALUES (341,'(٧٥) باب القول عند الانتهاء إلى الصف قبل تكبيرة الافتتاح','255','266','0');
INSERT INTO [t121382] VALUES (342,'(٧٦) باب إيجاب استقبال القبلة للصلاة','255','266','0');
INSERT INTO [t121382] VALUES (343,'(٧٧) باب إحداث النية عند دخول كل صلاة يريدها المرء فينويها بعينها فريضة كانت أو نافلة، إذ الأعمال إنما تكون بالنية، وإنما يكون للمرء ما ينوي بحكم النبي المصطفى صلى الله عليه وسلم','256','266','0');
INSERT INTO [t121382] VALUES (344,'(٧٨) باب البدء برفع اليدين عند افتتاح الصلاة قبل التكبير','256','266','0');
INSERT INTO [t121382] VALUES (345,'(٧٩) باب الرخصة في رفع اليدين تحت الثياب في البرد، وترك إخراجهما من الثياب عند رفعهما','256','266','0');
INSERT INTO [t121382] VALUES (346,'(٨٠) باب نشر الأصابع عند رفع اليدين في الصلاة','256','266','0');
INSERT INTO [t121382] VALUES (347,'(٨١) باب التكبير لافتتاح الصلاة','258','266','0');
INSERT INTO [t121382] VALUES (348,'(٨٢) باب ذكر الدعاء بين تكبيرة الافتتاح وبين القراءة','258','266','0');
INSERT INTO [t121382] VALUES (349,'(٨٣) باب ذكر بيان إغفال من زعم أن الدعاء بما ليس في القرآن غير جائز في الصلاة المكتوبة، وهذا القول خلاف سنن النبي -صلى الله عليه وسلم- الثابتة. قد دعا النبي -صلى الله عليه وسلم- في أول صلاته، ووسطها، وآخرها بما ليس في القرآن','259','266','0');
INSERT INTO [t121382] VALUES (350,'(٨٤) باب إباحة الدعاء بعد التكبير، وقبل القراءة، بغير ما ذكرنا في خبر علي بن أبي طالب، والدليل على أن هذا الاختلاف في الافتتاح من جهة اختلاف المباح، جائز للمصلي أن يفتتح بكل ما ثبت عن النبي -صلى الله عليه وسلم- أنه افتتح الصلاة به بعد التكبير من حمد وثناء على الله -عز وجل-، ودعاء مما هو في القرآن، ومما ليس في القرآن من الدعاء','260','266','0');
INSERT INTO [t121382] VALUES (351,'(٨٥) باب الاستعاذة في الصلاة قبل القراءة، قال الله عز وجل: (فإذا قرأت القرآن فاستعذ بالله من الشيطان الرجيم) [النحل: ٩٨]','263','266','0');
INSERT INTO [t121382] VALUES (352,'(٨٦) باب ذكر سؤال العبد ربه -عز وجل- من فضله بين التكبير والقراءة في صلاة الفريضة ضد قول من زعم أن الدعاء بما ليس في القرآن يفسد صلاة الفريضة','264','266','0');
INSERT INTO [t121382] VALUES (353,'(٨٧) باب الأمر بالخشوع في الصلاة، إذ المصلي يناجي ربه، والمناجي ربه يجب عليه أن يفرغ قلبه لمناجاة خالقه -عز وجل-، ولا يشغل قلبه التعلق بشيء من أمور','264','266','0');
INSERT INTO [t121382] VALUES (354,'(٨٨) باب التغليظ في النظر إلى السماء في الصلاة','265','266','0');
INSERT INTO [t121382] VALUES (355,'(٨٩) باب وضع اليمين على الشمال في الصلاة قبل افتتاح القراءة','265','266','0');
INSERT INTO [t121382] VALUES (356,'(٩٠) باب وضع بطن الكف اليمنى على كف اليسرى والرسغ والساعد جميعا','266','266','0');
INSERT INTO [t121382] VALUES (357,'(٩١) باب في الخشوع في الصلاة أيضا، والزجر عن الالتفات في الصلاة، إذ الله - عز وجل - يصرف وجهه عن وجه المصلي إذا التفت في صلاته','266','266','0');
INSERT INTO [t121382] VALUES (358,'(٩٢) باب [٦٥ - ب] ذكر الدليل على أن الالتفات في الصلاة ينقص الصلاة لا أنه يفسدها فسادا يجب عليه إعادتها','267','266','0');
INSERT INTO [t121382] VALUES (359,'(٩٣) باب ذكر الدليل على أن الالتفات المنهي عنه في الصلاة التي تكون صلاة المرء به ناقصة هو أن يلوي الملتفت عنقه، لا أن يلحظ بعينه يمينا وشمالا من غير أن يلوي عنقه، إذ النبي - صلى الله عليه وسلم - قد كان يلتفت في صلاته من غير أن يلوي عنقه خلف ظهره','268','266','0');
INSERT INTO [t121382] VALUES (360,'(٩٤) باب ذكر الدليل على أن الالتفات المنهي عنه في الصلاة هو الالتفات في الصلاة في غير الوقت الذي يحتاج المصلي أن يعرف فعل المأمومين أو بعضهم ليأمرهم','268','266','0');
INSERT INTO [t121382] VALUES (361,'(٩٥) باب إيجاب القراءة في الصلاة بفاتحة الكتاب ونفي الصلاة بغير قراءتها','269','266','0');
INSERT INTO [t121382] VALUES (362,'(٩٦) باب ذكر لفظة رويت عن النبي - صلى الله عليه وسلم - في ترك قراءة فاتحة الكتاب بلفظ ادعت فرقة أنها دالة على أن ترك قراءة فاتحة الكتاب ينقص صلاة المصلي، لا تبطل صلاته ولا يجب إعادتها','270','266','0');
INSERT INTO [t121382] VALUES (363,'(٩٧) باب ذكر الدليل [على أن] الخداج الذي أعلم النبي - صلى الله عليه وسلم - في هذا الخبر هو النقص الذي لا تجزئ الصلاة معه','270','266','0');
INSERT INTO [t121382] VALUES (364,'(٩٨) باب افتتاح القراءة بـ (الحمد لله رب العالمين)','271','266','0');
INSERT INTO [t121382] VALUES (365,'(٩٩) باب ذكر الدليل على أن (بسم الله الرحمن الرحيم) آية من فاتحة الكتاب','271','266','0');
INSERT INTO [t121382] VALUES (366,'(١٠٠) باب ذكر خبر غلط في الاحتجاج به من لم يتبحر بالعلم، فتوهم أن النبي - صلى الله عليه وسلم - لم يكن يقرأ بـ (بسم الله الرحمن الرحيم) في الصلاة في فاتحة الكتاب ولا في غيرها من السور','271','266','0');
INSERT INTO [t121382] VALUES (367,'(١٠١) باب ذكر الدليل على أن أنسا إنما أراد بقوله: "لم أسمع أحدا منهم يقرأ: (بسم الله الرحمن الرحيم). أي لم أسمع أحدا منهم يقرأ جهرا (بسم الله الرحمن الرحيم)، وأنهم كانوا يسرون (بسم الله الرحمن الرحيم) في الصلاة، لا كما توهم من لم [٦٦ - ب] يشتغل بطلب العلم من مظانه، [و] طلب الرئاسة قبل تعلم العلم','272','266','0');
INSERT INTO [t121382] VALUES (368,'(١٠٢) باب ذكر الدليل على أن الجهر بـ (بسم الله الرحمن الرحيم) والمخافتة به جميعا مباح، ليس واحد منهما محظورا، وهذا من اختلاف المباح','273','266','0');
INSERT INTO [t121382] VALUES (369,'(١٠٣) باب فضل قراءة فاتحة الكتاب، مع البيان أنها السبع المثاني، وأن الله لم ينزل في التوراة ولا في الإنجيل ولا في القرآن مثلها','274','266','0');
INSERT INTO [t121382] VALUES (370,'(١٠٤) باب القراءة في الظهر والعصر في الأوليين منهما بفاتحة الكتاب وسورة، وفي الأخريين بفاتحة الكتاب','276','266','0');
INSERT INTO [t121382] VALUES (371,'(١٠٥) باب المخافتة بالقراءة في الظهر والعصر، وترك الجهر فيهما بالقراءة','277','266','0');
INSERT INTO [t121382] VALUES (372,'(١٠٦) باب إباحة الجهر ببعض الآي في صلاة الظهر والعصر','278','266','0');
INSERT INTO [t121382] VALUES (373,'(١٠٧) باب تطويل الركعتين الأوليين من الظهر والعصر، وحذف الأخريين منهما','278','266','0');
INSERT INTO [t121382] VALUES (374,'(١٠٨) باب إباحة القراءة في الأخريين من الظهر والعصر بأكثر من فاتحة الكتاب، وهذا من اختلاف المباح، لا من اختلاف الذي يكون أحدهما محظورا والآخر مباحا، فجائز أن يقرأ في الأخريين في كل ركعة بفاتحة الكتاب، فيقصر [٦٨ - أ] من القراءة عليها، ومباح أن يزاد في الأخريين على فاتحة الكتاب','279','266','0');
INSERT INTO [t121382] VALUES (375,'(١٠٩) باب ذكر قراءة القرآن في الركعتين الأوليين من الظهر والعصر','279','266','0');
INSERT INTO [t121382] VALUES (376,'(١١٠) باب ذكر الدليل على أن الصلاة بقراءة فاتحة الكتاب جائزة دون غيرها من القراءة، وأن ما زاد على فاتحة الكتاب من القراءة في الصلاة فضيلة لا فريضة، في خبر عبادة بن الصامت "لا صلاة لمن لا يقرأ بفاتحة الكتاب"، دلالة على أن من قرأ بها له صلاة. وفي خبر أبي هريرة "من صلى صلاة لم يقرأ فيها بأم القرآن فهي خداج"، دلالة على أن من قرأ بفاتحة الكتاب في الصلاة لم تكن صلاته خداجا','280','266','0');
INSERT INTO [t121382] VALUES (377,'(١١١) باب القراءة في صلاة المغرب','281','266','0');
INSERT INTO [t121382] VALUES (378,'(١١٢) باب ذكر الدليل على أن النبي - صلى الله عليه وسلم - إنما كان يقرأ بطول الطولين في الركعتين الأوليين من المغرب، لا في ركعة واحدة','282','266','0');
INSERT INTO [t121382] VALUES (379,'(١١٣) باب القراءة في صلاة العشاء الآخرة','284','266','0');
INSERT INTO [t121382] VALUES (380,'(١١٤) باب القراءة في صلاة العشاء في السفر','286','266','0');
INSERT INTO [t121382] VALUES (381,'(١١٥) باب القراءة في صلاة الصبح','286','266','0');
INSERT INTO [t121382] VALUES (382,'(١١٦) باب القراءة في الفجر يوم الجمعة','288','266','0');
INSERT INTO [t121382] VALUES (383,'(١١٧) باب قراءة المعوذتين في الصلاة، ضد قول من زعم أن المعوذتين ليستا من القرآن','288','266','0');
INSERT INTO [t121382] VALUES (384,'(١١٨) باب إباحة ترداد المصلي قراءة السورة الواحدة في كل ركعتين من المكتوبة','291','266','0');
INSERT INTO [t121382] VALUES (385,'(١١٩) باب إباحة قراءة السورتين في الركعة الواحدة','291','266','0');
INSERT INTO [t121382] VALUES (386,'(١٢٠) باب إباحة جمع السور في الركعة الواحدة من المفصل','292','266','0');
INSERT INTO [t121382] VALUES (387,'(١٢١) باب إباحة ترديد الآية الواحدة في الصلاة مرارا عند التدبر والتفكر في القرآن إن صح الخبر','293','266','0');
INSERT INTO [t121382] VALUES (388,'(١٢٢) باب إباحة قراءة السورة الواحدة في ركعتين من المكتوبة','293','266','0');
INSERT INTO [t121382] VALUES (389,'(١٢٣) باب الدعاء في الصلاة بالمسألة عند قراءة آية الرحمة، والاستعاذة عند قراءة آية العذاب، والتسبيح عند قراءة آية التنزيه','293','266','0');
INSERT INTO [t121382] VALUES (390,'(١٢٤) باب إجازة الصلاة بالتسبيح، والتكبير، والتحميد، والتهليل، لمن لا يحسن القرآن','294','266','0');
INSERT INTO [t121382] VALUES (391,'(١٢٥) باب إباحة قراءة بعض السورة في الركعة الواحدة للعلة تعرض للمصلي','296','266','0');
INSERT INTO [t121382] VALUES (392,'(١٢٦) باب الجهر بالقراءة في الصلاة، والمخافتة بها','297','266','0');
INSERT INTO [t121382] VALUES (393,'(١٢٧) باب النهي عن قراءة القرآن في الركوع والسجود','297','266','0');
INSERT INTO [t121382] VALUES (394,'(١٢٨) باب فضل السجود عند قراءة السجدة، وبكاء الشيطان ودعائه بالويل لنفسه عند سجود القارئ السجدة','297','266','0');
INSERT INTO [t121382] VALUES (395,'(١٢٩) باب السجدة في (ص)، [٧٢ - أ]','298','266','0');
INSERT INTO [t121382] VALUES (396,'(١٣٠) باب ذكر العلة التي لها سجد النبي - صلى الله عليه وسلم - في (ص)','298','266','0');
INSERT INTO [t121382] VALUES (397,'(١٣١) باب السجود في النجم','299','266','0');
INSERT INTO [t121382] VALUES (398,'(١٣٢) باب السجود في (إذا السماء انشقت)، و (اقرأ باسم ربك الذي خلق)','299','266','0');
INSERT INTO [t121382] VALUES (399,'(١٣٣) باب صفة سجود الراكب عند قراءة السجدة','300','266','0');
INSERT INTO [t121382] VALUES (400,'(١٣٤) باب استحباب سجود المستمع لقراءة القرآن عند قراءة القارئ السجدة إذا سجد','300','266','0');
INSERT INTO [t121382] VALUES (401,'(١٣٥) باب ذكر الدليل على ضد قول من زعم [٧٢ - ب] أن النبي - صلى الله عليه وسلم - لم يسجد في المفصل بعد هجرته إلى المدينة','301','266','0');
INSERT INTO [t121382] VALUES (402,'(١٣٣) باب السجود عند قراءة السجدة في الصلاة المكتوبة، ضد قول بعض أهل الجهل ممن لا يفهم العلم من أهل عصرنا ممن زعم أن السجدة عند قراءة السجدة في الصلاة المكتوبة غير جائزة','302','266','0');
INSERT INTO [t121382] VALUES (403,'(١٣٧) باب الذكر والدعاء في السجود عند قراءة السجدة','303','266','0');
INSERT INTO [t121382] VALUES (404,'(١٣٨) باب ذكر الدليل على أن السجود عند قراءة السجدة فضيلة لا فريضة، إذ النبي - صلى الله عليه وسلم - سجد وسجد المسلمون معه والمشركون جميعا،','304','266','0');
INSERT INTO [t121382] VALUES (405,'(١٣٩) باب الدليل على المنصت السامع [٧٣ - ب] قراءة السجدة لا يجب عليه السجود إذا لم يسجد القارئ، ضد قول من زعم أن السجدة على من استمع لها وأنصت','305','266','0');
INSERT INTO [t121382] VALUES (406,'(١٤٠) باب الجهر بآمين عند انقضاء فاتحة الكتاب في الصلاة التي يجهر الإمام فيها بالقراءة','306','266','0');
INSERT INTO [t121382] VALUES (407,'(١٤١) باب ذكر حسد اليهود المؤمنين على التأمين','308','266','0');
INSERT INTO [t121382] VALUES (408,'(١٤٢) باب الدليل على أن الإمام إذا جهل فلم يقل: آمين، أو نسيه، كان على المأموم -إذا سمعه يقول: ولا الضالين عند ختمه قراءة فاتحة الكتاب- أن يقول: آمين. إذ النبي - صلى الله عليه وسلم - قد أمر المأموم أن يقول: آمين، إذا قال إمامه ولا الضالين، كما أمره أن يقول: آمين، إذا قاله إمامه','309','266','0');
INSERT INTO [t121382] VALUES (409,'(١٤٣) باب ذكر خبر روي عن النبي - صلى الله عليه وسلم - في تكبيره في الصلاة في كل خفض ورفع، بلفظ عام مراده خاص','310','266','0');
INSERT INTO [t121382] VALUES (410,'(١٤٤) باب ذكر الدليل على أن هذه اللفظة التي ذكرتها لفظ عام مراده خاص، وأن النبي - صلى الله عليه وسلم - إنما كان يكبر [٧٤ - ب] في بعض الرفع، لا','310','266','0');
INSERT INTO [t121382] VALUES (411,'(١٤٥) باب رفع اليدين عند إرادة المصلي الركوع، وبعد رفع رأسه من الركوع','314','266','0');
INSERT INTO [t121382] VALUES (412,'(١٤٦) باب الدليل على أن النبي صلى الله عليه وسلم أمر برفع اليدين عند إرادة الركوع، وعند رفع الرأس من الركوع','315','266','0');
INSERT INTO [t121382] VALUES (413,'(١٤٧) باب الاعتدال في الركوع، والتجافي، ووضع اليدين على الركبتين','316','266','0');
INSERT INTO [t121382] VALUES (414,'(١٤٨) باب الأمر بإعادة الصلاة إذا لم يطمئن المصلي في الركوع، أو لم يعتدل في القيام بعد رفع الرأس من الركوع','318','266','0');
INSERT INTO [t121382] VALUES (415,'(١٤٩) باب ذكر البيان أن صلاة من لا يقيم صلبه [٧٦ - ب] في الركوع والسجود غير مجزئة، لا أنها ناقصة مجزئة كما توهم بعض من يدعي العلم','319','266','0');
INSERT INTO [t121382] VALUES (416,'(١٥٠) باب تفريج أصابع اليدين عند وضعهما على الركبتين في الركوع','320','266','0');
INSERT INTO [t121382] VALUES (417,'(١٥١) باب ذكر نسخ التطبيق في الركوع، والبيان على أن وضع اليدين على الركبتين ناسخ للتطبيق، إذ التطبيق كان مقدما، ووضع اليدين على الركبتين مؤخرا بعده، فالمقدم منسوخ، والمؤخر ناسخ','320','266','0');
INSERT INTO [t121382] VALUES (418,'(١٥٢) باب ذكر البيان أن التطبيق غير جائز بعد أمر النبي صلى الله عليه وسلم بوضع اليدين على الركبتين','321','266','0');
INSERT INTO [t121382] VALUES (419,'(١٥٣) باب وضع الراحة على الركبة في الركوع، وأصابع اليدين على أعلى الساق الذي يلي الركبتين','322','266','0');
INSERT INTO [t121382] VALUES (420,'(١٥٤) باب الأمر بتعظيم الرب عز وجل في الركوع','322','266','0');
INSERT INTO [t121382] VALUES (421,'(١٥٥) باب التسبيح في الركوع','323','266','0');
INSERT INTO [t121382] VALUES (422,'(١٥٦) باب التحميد مع التسبيح، ومسألة الله الغفران في الركوع','324','266','0');
INSERT INTO [t121382] VALUES (423,'(١٥٧) باب التقديس في الركوع','325','266','0');
INSERT INTO [t121382] VALUES (424,'(١٥٨) باب الدليل على ضد قول من زعم أن المصلي إذا دعا في صلاة المكتوبة بما ليس في القرآن أن صلاته تفسد','325','266','0');
INSERT INTO [t121382] VALUES (425,'(١٥٩) باب الاعتدال وطول القيام بعد رفع الرأس من الركوع','327','266','0');
INSERT INTO [t121382] VALUES (426,'(١٦٠) باب التسوية بين الركوع والقيام بعد رفع الرأس من الركوع','327','266','0');
INSERT INTO [t121382] VALUES (427,'(١٦١) باب قول المصلي: سمع الله لمن حمده، مع رفع الرأس من الركوع معا','328','266','0');
INSERT INTO [t121382] VALUES (428,'(١٦٢) باب التحميد والدعاء بعد رفع الرأس من الركوع','328','266','0');
INSERT INTO [t121382] VALUES (429,'(١٦٣) باب فضيلة التحميد بعد رفع الرأس من الركوع، مع الدليل على أن النبي صلى الله عليه وسلم لم يرد بقوله: إذا قال الإمام سمع الله لمن حمده، فقولوا: ربنا لك الحمد". أن الإمام لا يجوز له أن يزيد بعد رفع الرأس من الركوع على قوله: ربنا لك الحمد','329','266','0');
INSERT INTO [t121382] VALUES (430,'(١٦٤) باب القنوت بعد رفع الرأس من الركوع للأمر يحدث، فيدعو الإمام في القنوت بعد رفع الرأس من الركوع في الركعة الأخيرة من صلاة الفريضة','330','266','0');
INSERT INTO [t121382] VALUES (431,'(١٦٥) باب القنوت في صلاة المغرب','331','266','0');
INSERT INTO [t121382] VALUES (432,'(١٦٦) باب القنوت في صلاة العشاء الآخرة','331','266','0');
INSERT INTO [t121382] VALUES (433,'(١٦٧) باب القنوت في الصلوات كلها، وتأمين المأمومين عند دعاء الإمام في القنوت، ضد ما يفعله العامة في قنوت الوتر فيضجون بالدعاء مع دعاء الإمام','331','266','0');
