INSERT INTO [t121382] VALUES (856,'(٥٩٠) باب الأمر بالدعاء على ناشد الضالة في المسجد أن لا يؤديها الله عليه','634','266','0');
INSERT INTO [t121382] VALUES (857,'(٥٩١) باب النهي عن البيع والشراء في المساجد','635','266','0');
INSERT INTO [t121382] VALUES (858,'(٥٩٢) باب الأمر بالدعاء على المتبايعين في المسجد أن لا تربح تجارتهما، وفيه ما دل على أن البيع ينعقد وإن كانا عاصيين بفعلهما','635','266','0');
INSERT INTO [t121382] VALUES (859,'(٥٩٣) باب الزجر عن إنشاد الشعر في المساجد بلفظ عام مراده -علمي- خاص','636','266','0');
INSERT INTO [t121382] VALUES (860,'(٥٩٤) باب ذكر الخبر الدال [على] أن النبي صلى الله عليه وسلم إنما نهى عن تناشد بعض الأشعار في المساجد لا عن جميعها','636','266','0');
INSERT INTO [t121382] VALUES (861,'(٥٩٥) باب النهي عن البزاق في المسجد إذا لم يدفن','637','266','0');
INSERT INTO [t121382] VALUES (862,'(٥٩٦) باب الأمر بدفن البزاق في المسجد ليكون كفارة للبزق','637','266','0');
