INSERT INTO [t121382] VALUES (915,'(٦٤٩) باب تطويل السجود في صلاة الكسوف','672','266','0');
INSERT INTO [t121382] VALUES (916,'(٦٥٠) باب تقصير السجدة الثانية عن الأولى في صلاة الكسوف','673','266','0');
