INSERT INTO [t121382] VALUES (919,'(٦٥٣) باب الدعاء والرغبة إلى الله في الجلوس في آخر صلاة الكسوف حتى تنجلي الشمس إذا لم يكن قد انجلت قبل','674','266','0');
INSERT INTO [t121382] VALUES (920,'(٦٥٤) باب خطبة الإمام بعد صلاة الكسوف','675','266','0');
INSERT INTO [t121382] VALUES (921,'(٦٥٥) باب استحباب استحداث التوبة عند كسوف الشمس. لما سبق من المرء من الذنوب والخطايا','676','266','0');
INSERT INTO [t121382] VALUES (922,'(٦٥٦) باب الأمر بالصدقة عند كسوف الشمس','678','266','0');
INSERT INTO [t121382] VALUES (923,'(٦٥٧) باب الأمر بالعتاقة في كسوف الشمس','679','266','0');
