INSERT INTO [t121382] VALUES (925,'(٦٥٩) باب التواضع، والتبذل، والتخشع، والتضرع، عند الخروج إلى الاستسقاء','680','266','0');
INSERT INTO [t121382] VALUES (926,'(٦٦٠) باب الخروج إلى المصلى للاستسقاء','680','266','0');
INSERT INTO [t121382] VALUES (927,'(٦٦١) باب الخطبة قبل صلاة الاستسقاء','681','266','0');
INSERT INTO [t121382] VALUES (928,'(٦٦٢) باب ترك الكلام عند الدعاء في خطبة الاستسقاء','681','266','0');
INSERT INTO [t121382] VALUES (929,'(٦٦٣) باب ترك الأذان والإقامة لصلاة الاستسقاء، والدليل على أنه لا يؤذن ولا يقام للتطوع، وإن صليت التطوع في الجماعة','681','266','0');
INSERT INTO [t121382] VALUES (930,'(٦٦٤) باب خروج الإمام بالناس إلى الاستسقاء','682','266','0');
