INSERT INTO [t121382] VALUES (932,'(٦٦٦) باب صفة رفع اليدين في الاستسقاء','682','266','0');
INSERT INTO [t121382] VALUES (933,'(٦٦٧) باب صفة تحويل الرداء في الاستسقاء إذا كان الرداء ثقيلا','683','266','0');
INSERT INTO [t121382] VALUES (934,'(٦٦٨) باب ذكر الدليل على أن النبي صلى الله عليه وسلم إنما حول رداءه، فجعل الأيمن على الأيسر، والأيسر على الأيمن لأن الرداء ثقل عليه، فاشتد عليه أن يجعل أعلاه أسفله','683','266','0');
INSERT INTO [t121382] VALUES (935,'(٦٦٩) باب صفة الدعاء في الاستسقاء','684','266','0');
