INSERT INTO [t121382] VALUES (937,'(٦٧٠) باب عدد [ركعات] صلاة الاستسقاء','684','266','0');
INSERT INTO [t121382] VALUES (938,'(٦٧٢) باب الجهر بالقراءة في صلاة الاستسقاء، والدليل على ضد قول من زعم من التابعين أن صلاة النهار عجماء، يريد أنه لا يجهر بالقراءة في شيء من صلوات النهار','685','266','0');
INSERT INTO [t121382] VALUES (939,'(٦٧٣) باب استحباب الاستسقاء ببعض قرابة النبي صلى الله عليه وسلم بالبلدة التي يستسقي بها ببعض قرابته صلى الله عليه وسلم','685','266','0');
INSERT INTO [t121382] VALUES (940,'(٦٧٤) باب إعادة الخطبة ثانية بعد صلاة الاستسقاء','686','266','0');
INSERT INTO [t121382] VALUES (941,'(٦٧٥) باب الاستسقاء في الخطبة يوم الجمعة إذا شكي إلى الإمام بقحط المطر، ودعاء الإمام بحبس المطر عن المدن والقرى، إذا شكي إليه كثرة الأمطار، وخيف هدم البنيان، وانقطاع السبل','686','266','0');
INSERT INTO [t121382] VALUES (942,'(٦٧٦) باب ترك الإمام العود للخروج لصلاة الاستسقاء ثانيا إذا سقوا في أول مرة استسقوا','687','266','0');
INSERT INTO [t121382] VALUES (943,'(٦٧٧) باب عدد [ركعات] صلاة العيدين','687','266','0');
INSERT INTO [t121382] VALUES (944,'(٦٧٨) باب استحباب الأكل يوم الفطر قبل الخروج إلى المصلى، وترك الأكل يوم النحر إلى الرجوع من المصلى فيأكل من ذبيحته إن كان ممن يضحي','688','266','0');
INSERT INTO [t121382] VALUES (945,'(٦٧٩) باب ذكر الخبر الدال على أن ترك الأكل يوم النحر حتى يذبح المرء فضيلة، وإن كان الأكل مباحا قبل الغدو إلى المصلى، والآكل غير حارج ولا آثم','688','266','0');
INSERT INTO [t121382] VALUES (946,'(٦٨٠) باب استحباب أكل التمر يوم الفطر قبل الغدو إلى المصلى','688','266','0');
INSERT INTO [t121382] VALUES (947,'(٦٨١) باب استحباب الفطر يوم الفطر على وتر من التمر','689','266','0');
INSERT INTO [t121382] VALUES (948,'(٦٨٢) باب الخروج إلى المصلى لصلاة العيدين، والدليل على أن صلاة العيدين تصلى في المصلى لا في المساجد، إذا أمكن الخروج إلى المصلى','689','266','0');
INSERT INTO [t121382] VALUES (949,'(٦٨٣) باب التكبير والتهليل في الغدو إلى المصلى في العيدين، إن صح الخبر. فإن في القلب من هذا الخبر، وأحسب الحمل فيه على عبد الله بن عمر العمري إن لم يكن الغلط من [١٥٣ - ب] ابن أخي ابن وهب','689','266','0');
INSERT INTO [t121382] VALUES (950,'(٦٨٤) باب ترك الأذان والإقامة لصلاة العيدين، وهذا من الجنس الذي أعلمت أن لا أذان، ولا إقامة إلا لصلاة الفريضة، وإن صليت غير الفريضة جماعة','690','266','0');
INSERT INTO [t121382] VALUES (951,'(٦٨٥) باب إخراج العنزة في العيدين إلى المصلى، ليستتر بها الإمام في المصلى إذا صلى، بذكر خبر مجمل لم يبين فيه العلة التي كان النبي صلى الله عليه وسلم','690','266','0');
INSERT INTO [t121382] VALUES (952,'(٦٨٦) باب ذكر الخبر المفسر للعلة التي كان النبي صلى الله عليه وسلم يخرج العنزة إلى المصلى، والدليل على أنه إنما كان خرجها إذ لا بناء بالمصلى يومئذ يستر المصلي','691','266','0');
INSERT INTO [t121382] VALUES (953,'(٦٨٧) باب ترك الصلاة في المصلى قبل العيدين وبعدها اقتداء بالنبي واستنانا به','691','266','0');
INSERT INTO [t121382] VALUES (954,'(٦٨٨) باب البدء بصلاة العيدين قبل الخطبة','692','266','0');
INSERT INTO [t121382] VALUES (955,'(٦٨٩) باب عدد التكبير في صلاة العيدين في القيام قبل الركوع','692','266','0');
INSERT INTO [t121382] VALUES (956,'(٦٩٠) باب ذكر الدليل على ضد قول من زعم أنه يوالي بين [١٥٤ - أ] القراءتين في صلاة العيدين','692','266','0');
INSERT INTO [t121382] VALUES (957,'(٦٩١) باب القراءة في صلاة العيدين','692','266','0');
INSERT INTO [t121382] VALUES (958,'(٦٩٢) باب استقبال الإمام الناس للخطبة بعد الفراغ من الصلاة','693','266','0');
INSERT INTO [t121382] VALUES (959,'(٦٩٣) باب الخطبة يوم العيد بعد صلاة العيد','693','266','0');
INSERT INTO [t121382] VALUES (960,'(٦٩٤) باب الخطبة على المنبر في العيدين','694','266','0');
INSERT INTO [t121382] VALUES (961,'(٦٩٥) باب الخطبة قائما على الأرض إذا لم يكن بالمصلى منبر','694','266','0');
INSERT INTO [t121382] VALUES (962,'(٦٩٦) باب عدد الخطب في العيدين، والفصل بين الخطبتين بجلوس','694','266','0');
INSERT INTO [t121382] VALUES (963,'(٦٩٧) باب السكوت في الجلوس بين الخطبتين [١٥٤ - ب] وترك الكلام فيه','695','266','0');
INSERT INTO [t121382] VALUES (964,'(٦٩٨) باب قراءة القرآن في الخطبة، والاقتصاد في الخطبة والصلاة جميعا','695','266','0');
INSERT INTO [t121382] VALUES (965,'(٦٩٩) باب الأمر بالصدقة وما ينوب الإمام من أمر الرعية في خطبة العيد','695','266','0');
INSERT INTO [t121382] VALUES (966,'(٧٠٠) باب إشارة الخاطب بالسبابة على المنبر عند الدعاء في الخطبة وتحريكه إياها عند الإشارة بها','696','266','0');
INSERT INTO [t121382] VALUES (967,'(٧٠١) باب كراهة رفع اليدين على المنبر في الخطبة','696','266','0');
INSERT INTO [t121382] VALUES (968,'(٧٠٢) باب الاعتماد على القسي أو العصا على المنبر في الخطبة','697','266','0');
INSERT INTO [t121382] VALUES (969,'(٧٠٣) باب إباحة الكلام في الخطبة بالأمر والنهي، والدليل على ضد قول من زعم أن الخطبة صلاة، ولو كانت الخطبة صلاة ما تكلم النبي صلى الله عليه وسلم فيها بما لا يجوز في الصلاة','697','266','0');
INSERT INTO [t121382] VALUES (970,'(٧٠٤) باب أمر الإمام القارئ بقراءة القرآن واستماعه للقراءة وهو على المنبر، والبكاء على المنبر عند استماع القرآن','698','266','0');
INSERT INTO [t121382] VALUES (971,'(٧٠٥) باب النزول عن المنبر للسجود إذا قرأ الخاطب السجدة على المنبر، إن صح الخبر، فإن في القلب من هذا الإسناد، لأن بعض','698','266','0');
INSERT INTO [t121382] VALUES (972,'(٧٠٦) باب الرخصة للخاطب في قطع الخطبة للحاجة تبدو له','699','266','0');
INSERT INTO [t121382] VALUES (973,'(٧٠٧) باب إباحة قطع الخطبة ليعلم بعض الرعية العلم','700','266','0');
INSERT INTO [t121382] VALUES (974,'(٧٠٨) باب انتظار القوم الإمام جلوسا في العيدين بعد فراغه من الخطبة ليعظ النساء ويذكرهن','700','266','0');
INSERT INTO [t121382] VALUES (975,'(٧٠٩) باب ذكر عظة الإمام النساء وتذكيره إياهن، وأمره إياهن بالصدقة بعد خطبة العيدين','701','266','0');
INSERT INTO [t121382] VALUES (976,'(٧١٠) باب ذكر الدليل على أن النبي صلى الله عليه وسلم إنما أتى النساء بعد فراغه من الخطبة ليعظهن، إذ النساء لم يسمعن خطبته وموعظته','701','266','0');
INSERT INTO [t121382] VALUES (977,'(٧١١) باب الرخصة في ترك انتظار الرعية للخطبة يوم العيد','702','266','0');
INSERT INTO [t121382] VALUES (978,'(٧١٢) باب اجتماع العيد والجمعة في يوم واحد، وصلاة الإمام بالناس العيد ثم الجمعة، وإباحة القراءة فيهما جميعا بسورتين بأعيانهما','702','266','0');
INSERT INTO [t121382] VALUES (979,'(٧١٣) باب الرخصة لبعض الرعية في التخلف عن الجمعة إذا اجتمع العيد والجمعة في يوم واحد، إن صح الخبر، فإني لا أعرف إياس بن أبي رملة بعدالة ولا جرح','703','266','0');
INSERT INTO [t121382] VALUES (980,'(٧١٤) باب الرخصة للإمام إذا اجتمع العيدان والجمعة أن يعيد بهم ولا يجمع بهم، إن كان ابن عباس أراد بقوله: أصاب ابن الزبير السنة، سنة النبي صلى الله عليه وسلم','703','266','0');
INSERT INTO [t121382] VALUES (981,'(٧١٥) باب إباحة خروج النساء في العيدين، وإن كن أبكارا، ذوات خدور حيضا كن أو أطهارا','704','266','0');
INSERT INTO [t121382] VALUES (982,'(٧١٦) باب الأمر باعتزال الحائض إذا شهدت العيد. والدليل على أنها إنما أمرت بالخروج لمشاهدة الخير ودعوة المسلمين','705','266','0');
INSERT INTO [t121382] VALUES (983,'(٧١٧) باب استحباب الرجوع من المصلى من غير الطريق الذي أتى فيه المصلي','705','266','0');
INSERT INTO [t121382] VALUES (984,'(٧١٨) باب استحباب الصلاة في المنزل بعد الرجوع من المصلى','705','266','0');
INSERT INTO [t121382] VALUES (985,'كتاب الإمامة في الصلاة','707','0','0');
INSERT INTO [t121382] VALUES (986,'(١) باب فضل صلاة الجماعة على صلاة الفذ','707','985','0');
INSERT INTO [t121382] VALUES (987,'(٢) باب ذكر الدليل على ضد قول من زعم أن النبي صلى الله عليه وسلم لا يخاطب أمته بلفظ مجمل، موه بجهله على بعض الغباء، احتجاجا لمقالته هذه أنه إذا خاطبهم بكلام مجمل فقد خاطبهم بما لم يفدهم معنى، زعم','708','985','0');
INSERT INTO [t121382] VALUES (988,'(٣) باب فضل صلاة العشاء والفجر في الجماعة [و] البيان أن صلاة الفجر في الجماعة أفضل من صلاة العشاء في الجماعة، وأن فضلها في الجماعة ضعفي فضل العشاء في الجماعة','708','985','0');
INSERT INTO [t121382] VALUES (989,'(٤) باب ذكر اجتماع ملائكة الليل وملائكة النهار في صلاة الفجر','708','985','0');
INSERT INTO [t121382] VALUES (990,'(٥) باب ذكر الحض على شهود صلاة العشاء والصبح ولو لم يقدر المرء على شهودهما إلا حبوا على الركب','709','985','0');
INSERT INTO [t121382] VALUES (991,'(٦) باب ذكر البيان أن ما كثر من العدد في الصلاة جماعة كانت الصلاة أفضل','709','985','0');
INSERT INTO [t121382] VALUES (992,'(٧) باب أمر العميان بشهود صلاة الجماعة، وإن خاف الأعمى هوام الليل والسباع إذا شهد الجماعة','710','985','0');
INSERT INTO [t121382] VALUES (993,'(٨) باب أمر العميان بشهود صلاة الجماعة وإن كانت منازلهم نائية عن المسجد، لا يطاوعهم قائدوهم بإتيانهم إياهم المساجد، والدليل على أن شهود الجماعة فريضة لا فضيلة، إذ غير جائز أن يقال: لا رخصة للمرء في ترك الفضيلة','710','985','0');
INSERT INTO [t121382] VALUES (994,'(٩) باب في التغليظ في ترك شهود الجماعة','711','985','0');
INSERT INTO [t121382] VALUES (995,'(١٠) باب تخوف النفاق على تارك شهود الجماعة','712','985','0');
INSERT INTO [t121382] VALUES (996,'(١١) باب ذكر أثقل الصلاة على المنافقين وتخوف النفاق على تارك شهود العشاء والصبح في الجماعة','712','985','0');
INSERT INTO [t121382] VALUES (997,'(١٢) باب التغليظ في ترك صلاة الجماعة في القرى والبوادي، واستحواذ الشيطان على تاركها','713','985','0');
INSERT INTO [t121382] VALUES (998,'(١٣) باب صلاة المريض في منزله جماعة إذا لم يمكنه شهودها في المسجد لعلة حادثة','713','985','0');
INSERT INTO [t121382] VALUES (999,'(١٤) باب الرخصة للمريض في ترك شهود الجماعة','714','985','0');
INSERT INTO [t121382] VALUES (1000,'(١٥) باب فضل المشي إلى الجماعة متوضيا وما يرجى فيه من المغفرة','714','985','0');
