INSERT INTO [t121382] VALUES (1002,'(١٧) باب ذكر فرح الرب تعالى بمشي عبده إلى المسجد متوضيا','715','985','0');
INSERT INTO [t121382] VALUES (1003,'(١٨) باب ذكر كتبة الحسنات بالمشي إلى الصلاة','716','985','0');
INSERT INTO [t121382] VALUES (1004,'(١٩) باب ذكر كتبة الصدقة بالمشي إلى الصلاة','716','985','0');
INSERT INTO [t121382] VALUES (1005,'(٢٠) باب ضمان الله الغادي إلى المسجد والرائح إليه','717','985','0');
INSERT INTO [t121382] VALUES (1006,'(٢١) باب ذكر ما أعد الله من النزل في الجنة للغادي إلى المسجد والرائح إليه','717','985','0');
INSERT INTO [t121382] VALUES (1007,'(٢٢) باب ذكر كتابة أجر المصلي بالمشي إلى الصلاة','718','985','0');
