INSERT INTO [t121382] VALUES (1009,'(٢٤) باب فضل المشي إلى المساجد من المنازل المتباعدة من المساجد لكثرة الخطا','719','985','0');
INSERT INTO [t121382] VALUES (1010,'(٢٥) باب الشهادة بالإيمان لعمار المساجد بإتيانها والصلاة فيها','720','985','0');
INSERT INTO [t121382] VALUES (1011,'(٢٦) باب فضل إيطان المساجد للصلاة فيها','720','985','0');
INSERT INTO [t121382] VALUES (1012,'(٢٧) باب فضل الجلوس في المسجد انتظارا للصلاة، وذكر صلاة الملائكة عليه ودعائهم له، ما لم يؤذ فيه، أو يحدث فيه','720','985','0');
INSERT INTO [t121382] VALUES (1013,'(٢٨) باب الأمر بالسكينة في المشي إلى الصلاة، والنهي عن السعي إليها','721','985','0');
INSERT INTO [t121382] VALUES (1014,'(٢٩) باب الزجر عن الخروج من المسجد بعد الأذان وقبل الصلاة','721','985','0');
INSERT INTO [t121382] VALUES (1015,'(٣٠) باب ذكر أحق الناس بالإمامة','722','985','0');
INSERT INTO [t121382] VALUES (1016,'(٣١) باب استحقاق الإمامة بالازدياد من حفظ القرآن وإن كان غيره أسن منه وأشرف','723','985','0');
INSERT INTO [t121382] VALUES (1017,'(٣٢) باب ذكر استحقاق الإمامة بكبر السن إذا استووا في القراءة والسنة والهجرة','723','985','0');
INSERT INTO [t121382] VALUES (1018,'(٣٣) باب إمامة المولى القرشي إذا كان المولى أكثر جمعا للقرآن. خبر النبي - صلى الله عليه وسلم -: "يؤمهم أقرؤهم لكتاب الله" دلالة على أن المولى إذا كان أقرأ من القرشي فهو أحق بالإمامة','724','985','0');
INSERT INTO [t121382] VALUES (1019,'(٣٤) باب إباحة إمامة غير المدرك البالغين إذا كان غير المدرك أكثر جمعا للقرآن من البالغين','724','985','0');
INSERT INTO [t121382] VALUES (1020,'(٣٥) باب ذكر الدليل على ضد قول من كره للابن إمامة أبيه','725','985','0');
INSERT INTO [t121382] VALUES (1021,'(٣٦) باب التغليظ على الأئمة في تركهم إتمام الصلاة وتأخيرهم الصلاة، والدليل على أن صلاة الإمام قد تكون ناقصة، وصلاة المأموم تامة، ضد قول من زعم أن صلاة المأموم متصلة بصلاة إمامه، إذا فسدت صلاة الإمام، فسدت صلاة المأموم، زعم','725','985','0');
INSERT INTO [t121382] VALUES (1022,'(٣٧) باب الرخصة في ترك انتظار الإمام إذا أبطأ، وأمر المأمومين أحدهم بالإمامة','726','985','0');
INSERT INTO [t121382] VALUES (1023,'(٣٨) باب الرخصة في صلاة الإمام الأعظم خلف من أم الناس من رعيته، وإن كان الإمام من الرعية يؤم الناس بغير إذن الإمام الأعظم','727','985','0');
INSERT INTO [t121382] VALUES (1024,'(٣٩) باب إمامة المرء السلطان بأمره، واستخلاف الإمام رجلا من الرعية إذا غاب عن حضرة المسجد الذي يؤم الناس فيه، فتكون الإمامة بأمره','728','985','0');
INSERT INTO [t121382] VALUES (1025,'(٤٠) باب الزجر عن إمامة المرء من يكره إمامته','729','985','0');
INSERT INTO [t121382] VALUES (1026,'(٤١) باب المنهي عن إمامة الزائر','730','985','0');
INSERT INTO [t121382] VALUES (1027,'(٤٢) باب الرخصة في قيام الإمام على مكان أرفع من مكان المأمومين لتعليم الناس الصلاة','730','985','0');
INSERT INTO [t121382] VALUES (1028,'(٤٣) باب النهي عن قيام الإمام على مكان أرفع من المأمومين إذا لم يرد تعليم الناس','731','985','0');
INSERT INTO [t121382] VALUES (1029,'(٤٤) باب إيذان المؤذن الإمام بالصلاة','731','985','0');
INSERT INTO [t121382] VALUES (1030,'(٤٥) باب انتظار المؤذن الإمام بالإقامة','732','985','0');
INSERT INTO [t121382] VALUES (1031,'(٤٦) باب النهي عن قيام الناس إلى الصلاة قبل رؤيتهم إمامهم','732','985','0');
INSERT INTO [t121382] VALUES (1032,'(٤٧) باب الرخصة في كلام الإمام بعد الفراغ من الإقامة، والحاجة تبدو لبعض الناس','732','985','0');
INSERT INTO [t121382] VALUES (1033,'(٤٨) باب ذكر دعاء النبي - صلى الله عليه وسلم - للأئمة بالرشاد','733','985','0');
INSERT INTO [t121382] VALUES (1034,'(٤٩) باب قيام المأموم الواحد عن يمين الإمام إذا لم يكن معهما أحد','735','985','0');
INSERT INTO [t121382] VALUES (1035,'(٥٠) باب ذكر الدليل على ضد قول من زعم أن المأموم يقوم خلف الإمام ينتظر مجيء غيره، فإن فرغ الإمام من القراءة، وأراد الركوع قبل مجيء غيره، تقدم فقام عن يمين الإمام','735','985','0');
