INSERT INTO [t121382] VALUES (1060,'(٧٥) باب طرد المصطفين بين السواري عنها','747','985','0');
INSERT INTO [t121382] VALUES (1061,'(٧٦) باب النهي عن الاصطفاف بين السواري','747','985','0');
INSERT INTO [t121382] VALUES (1062,'(٧٧) باب الزجر عن صلاة المأموم خلف الصف وحده، والبيان أن صلاته خلف الصف وحده غير جائزة، يجب عليه استقبالها، [و] أن قوله: لا صلاة له، من الجنس الذي نقول: إن العرب تنفي الاسم عن الشيء لنقصه عن الكمال','747','985','0');
INSERT INTO [t121382] VALUES (1063,'(٧٨) باب الرخصة في ركوع المأموم قبل اتصاله بالصف، ودبيبه راكعا حتى يتصل بالصف، في ركوعه. [١٦٥ - ب]','749','985','0');
INSERT INTO [t121382] VALUES (1064,'(٧٩) باب ذكر البيان أن أولي الأحلام والنهى أحق بالصف الأول إذ النبي - صلى الله عليه وسلم - أمر بأن يلوه','750','985','0');
INSERT INTO [t121382] VALUES (1065,'(٨٠) باب إباحة تأخير الأحداث عن الصف الأول، إن قاموا في الصف الأول، ثم حضر بعض أولي الأحلام والنهى، وليقوم من أمر النبي صلى الله عليه وسلم بأن يليه في المقدم، ويؤخر عن الصف المقدم من ليس من أهل الأحلام والنهى','750','985','0');
INSERT INTO [t121382] VALUES (1066,'(٨١) باب الرخصة في شق أولي الأحلام والنهى للصفوف إذا كانوا قد اصطفوا عند حضورهم ليقوموا في الصف الأول','751','985','0');
INSERT INTO [t121382] VALUES (1067,'(٨٢) باب أمر المؤمنين بالاقتداء بالإمام، والنهي عن مخالفتهم إياه','751','985','0');
INSERT INTO [t121382] VALUES (1068,'(٨٣) باب الزجر عن مبادرة المأموم الإمام بالتكبير والركوع والسجود','751','985','0');
INSERT INTO [t121382] VALUES (1069,'(٨٤) باب ذكر البيان أن المأموم إنما يكبر بعد فراغ الإمام من التكبير، لا يكون مكبرا حتى يفرغ من التكبير ويتم الراء التي هي آخر التكبير، والفرق بين قوله: "إذا كبر فكبروا، وبين قوله: وإذا ركع فاركعوا، وإذا سجد فاسجدوا، إذ اسم المكبر لا يقع على الإمام ما لم يتم التكبير، واسم الراكع قد يقع عليه إذا استوى راكعا، وكذلك اسم الساجد يقع عليه إذا استوى جالسا','752','985','0');
INSERT INTO [t121382] VALUES (1070,'(٨٥) باب سكوت الإمام قبل القراءة، وبعد تكبيرة الافتتاح','752','985','0');
INSERT INTO [t121382] VALUES (1071,'(٨٦) باب ذكر البيان أن اسم الساكت قد يقع على الناطق سرا إذا','752','985','0');
INSERT INTO [t121382] VALUES (1072,'(٨٧) باب تطويل الإمام الركعة الأولى من الصلوات ليتلاحق المأمومون','753','985','0');
INSERT INTO [t121382] VALUES (1073,'(٨٨) باب القراءة خلف الإمام وإن جهر الإمام بالقراءة، والزجر عن أن يزيد المأموم على قراءة فاتحة الكتاب إذا جهر الإمام بالقراءة','753','985','0');
INSERT INTO [t121382] VALUES (1074,'(٨٩) باب تأمين المأموم عند فراغ الإمام من قراءة فاتحة الكتاب في الصلاة التي يجهر فيها الإمام بالقراءة، وإن نسي إمام وجهل فلم يؤمن','754','985','0');
INSERT INTO [t121382] VALUES (1075,'(٩٠) باب فضل تأمين المأموم إذا أمن إمامه رجاء مغفرة ما تقدم من ذنب المؤمن إذا وافق تأمينه الملائكة، مع الدليل على أن على الإمام الجهر بالتأمين إذا جهر بالقراءة ليسمع المأموم تأمينه، إذ غير جائز أن يأمر النبي - صلى الله عليه وسلم - المأموم بالتأمين إذا أمن إمامه، ولا سبيل له إلى معرفة تأمين الإمام إذا أخفى الإمام التأمين','754','985','0');
INSERT INTO [t121382] VALUES (1076,'(٩١) باب ذكر إجابة الرب - عز وجل - المؤمن عند فراغ قراءة فاتحة الكتاب','756','985','0');
INSERT INTO [t121382] VALUES (1077,'(٩٢) باب ذكر حسد اليهود المؤمنين على تأمينهم','756','985','0');
INSERT INTO [t121382] VALUES (1078,'(٩٣) باب ذكر ما كان الله - عز وجل - خص نبيه - صلى الله عليه وسلم - بالتأمين، فلم يعطه أحدا من النبيين قبله، خلا هارون حين دعا موسى، فأمن هارون، إن ثبت الخبر','757','985','0');
INSERT INTO [t121382] VALUES (1079,'(٩٤) باب السنة في جهر الإمام بالقراءة، واستحباب الجهر بالقراءة جهرا بين المخافتة وبين الجهر الرفيع','757','985','0');
INSERT INTO [t121382] VALUES (1080,'(٩٥) باب ذكر مخافتة الإمام القراءة في الظهر والعصر، وإباحة الجهر ببعض الآي أحيانا فيما يخافت بالقراءة في الصلاة','758','985','0');
INSERT INTO [t121382] VALUES (1081,'(٩٦) باب جهر الإمام بالقراءة في صلاة المغرب','759','985','0');
INSERT INTO [t121382] VALUES (1082,'(٩٧) باب جهر الإمام بالقراءة في صلاة العشاء','759','985','0');
INSERT INTO [t121382] VALUES (1083,'(٩٨) باب جهر الإمام بالقراءة في صلاة الغداة','759','985','0');
INSERT INTO [t121382] VALUES (1084,'(٩٩) باب ذكر الخبر المفسر أن النبي - صلى الله عليه وسلم - إنما كان يجهر في الأوليين من المغرب، والأوليين من العشاء، لا في جميع الركعات كلها، من المغرب والعشاء إن ثبت الخبر مسندا، ولا إخال، وإنما خرجت هذا الخبر في هذا الكتاب إذ لا خلاف بين أهل القبلة في صحة متنه، وإن لم يثبت الخبر من جهة الإسناد الذي نذكره','760','985','0');
INSERT INTO [t121382] VALUES (1085,'(١٠٠) باب الأمر بمبادرة الإمام المأموم بالركوع والسجود','761','985','0');
INSERT INTO [t121382] VALUES (1086,'(١٠١) باب النهي عن مبادرة الإمام المأموم بالركوع، والإخبار بأن الإمام ما سبق المأموم من الركوع، أدركه المأموم بعد رفع الإمام رأسه من الركوع','762','985','0');
INSERT INTO [t121382] VALUES (1087,'(١٠٢) باب ذكر الوقت الذي يكون فيه المأموم مدركا للركعة إذا ركع إمامه قبل','763','985','0');
INSERT INTO [t121382] VALUES (1088,'(١٠٣) باب رفع الإمام رأسه من الركوع قبل المأموم','763','985','0');
INSERT INTO [t121382] VALUES (1089,'(١٠٤) باب الأمر بتحميد المأموم ربه - عز وجل - عند رفع الرأس من','763','985','0');
INSERT INTO [t121382] VALUES (1090,'(١٠٥) باب مبادرة الإمام المأموم بالسجود، وثبوت المأموم قائما، وتركه الانحناء للسجود حتى يسجد إمامه','764','985','0');
INSERT INTO [t121382] VALUES (1091,'(١٠٦) باب التغليظ في مبادرة المأموم الإمام برفع الرأس من السجود','765','985','0');
INSERT INTO [t121382] VALUES (1092,'(١٠٧) باب ذكر إدراك المأموم ما فاته من سجود الإمام بعد رفع الإمام رأسه','765','985','0');
INSERT INTO [t121382] VALUES (1093,'(١٠٨) باب النهي عن مبادرة المأموم الإمام بالقيام والقعود','765','985','0');
INSERT INTO [t121382] VALUES (1094,'(١٠٩) باب افتتاح الإمام القراءة في الركعة الثانية في الصلاة التي يجهر فيها من غير سكت قبلها','765','985','0');
INSERT INTO [t121382] VALUES (1095,'(١١٠) باب تخفيف الإمام الصلاة مع الإتمام','766','985','0');
INSERT INTO [t121382] VALUES (1096,'(١١١) باب النهي عن تطويل الإمام الصلاة مخافة تنفير المأمومين وقنوتهم','766','985','0');
INSERT INTO [t121382] VALUES (1097,'(١١٢) باب قدر قراءة الإمام الذي لا يكون تطويلا','767','985','0');
INSERT INTO [t121382] VALUES (1098,'(١١٣) باب تقدير الإمام الصلاة بضعفاء المأمومين، وكبارهم، وذوي الحوائج منهم','767','985','0');
INSERT INTO [t121382] VALUES (1099,'(١١٤) باب تخفيف الإمام القراءة للحاجة تبدو لبعض المأمومين','768','985','0');
INSERT INTO [t121382] VALUES (1100,'(١١٥) باب الرخصة في تخفيف الإمام الصلاة للحاجة تبدو لبعض المأمومين بعدما قد نوى إطالتها','768','985','0');
INSERT INTO [t121382] VALUES (1101,'(١١٦) باب الرخصة في خروج المأموم من صلاة الإمام للحاجة تبدو له من أمور الدنيا إذا طول الصلاة','768','985','0');
INSERT INTO [t121382] VALUES (1102,'(١١٧) باب الأمر بائتمام أهل الصفوف الأواخر بأهل الصفوف الأول','769','985','0');
INSERT INTO [t121382] VALUES (1103,'(١١٨) باب أمر المأموم بالصلاة جالسا إذا صلى إمامه جالسا','769','985','0');
INSERT INTO [t121382] VALUES (1104,'(١١٩) باب أمر المأموم بالجلوس بعد افتتاحه الصلاة قائما إذا صلى الإمام قاعدا','770','985','0');
INSERT INTO [t121382] VALUES (1105,'(١٢٠) باب النهي عن صلاة المأموم قائما خلف الإمام قاعدا','770','985','0');
INSERT INTO [t121382] VALUES (1106,'(١٢١) باب ذكر أخبار تأولها بعض العلماء ناسخة لأمر رسول الله - صلى الله عليه وسلم - المأموم بالصلاة جالسا إذا صلى إمامه جالسا','771','985','0');
INSERT INTO [t121382] VALUES (1107,'(١٢٢) باب إدراك المأموم الإمام ساجدا، والأمر بالاقتداء به في السجود، وأن لا يعتد به، إذ المدرك للسجدة إنما يكون بإدراك الركوع قبلها','775','985','0');
INSERT INTO [t121382] VALUES (1108,'(١٢٣) باب إجازة الصلاة الواحدة بإمامين','775','985','0');
INSERT INTO [t121382] VALUES (1109,'(١٢٤) باب استخلاف الإمام الأعظم في المرض بعض رعيته ليتولى الإمامة بالناس','777','985','0');
INSERT INTO [t121382] VALUES (1110,'(١٢٥) باب ذكر استخلاف الإمام عند الغيبة عن حضرة المسجد الذي هو إمامه عند الحاجة تبدو له','778','985','0');
INSERT INTO [t121382] VALUES (1111,'(١٢٦) باب الرخصة في الاقتداء بالمصلي الذي ينوي الصلاة منفردا، ولا ينوي إمامة المقتدي به','778','985','0');
INSERT INTO [t121382] VALUES (1112,'(١٢٧) باب افتتاح غير الطاهر الصلاة ناويا الإمامة، وذكره أنه غير طاهر بعد الافتتاح، وتركه الاستخلاف عند ذلك لينتظر المأمومون رجوعه بعد الطهارة فيؤمهم','779','985','0');
INSERT INTO [t121382] VALUES (1113,'(١٢٨) باب الرخصة في خصوصية الإمام نفسه بالدعاء دون المأمومين، خلاف الخبر غير الثابت المروي عن النبي - صلى الله عليه وسلم -: أنه قد خانهم إذا خص نفسه بالدعاء دونهم','780','985','0');
INSERT INTO [t121382] VALUES (1114,'(١٢٩) باب الرخصة في الصلاة جماعة في المسجد الذي قد جمع فيه، ضد قول من زعم أنهم يصلون فرادى إذا صلى في المسجد جماعة مرة','780','985','0');
INSERT INTO [t121382] VALUES (1115,'(١٣٠) باب إباحة ائتمام المصلي فريضة بالمصلي نافلة، ضد قول من زعم من العراقيين أنه غير جائز أن يأتم المصلي فريضة بالمصلي نافلة','781','985','0');
INSERT INTO [t121382] VALUES (1116,'(١٣١) باب ذكر البيان أن معاذا كان يصلي مع النبي - صلى الله عليه وسلم - فريضة لا تطوعا كما ادعى بعض العراقيين','782','985','0');
INSERT INTO [t121382] VALUES (1117,'(١٣٢) باب الأمر بالصلاة منفردا عند تأخير الإمام الصلاة جماعة','782','985','0');
INSERT INTO [t121382] VALUES (1118,'(١٣٣) باب الأمر بالصلاة جماعة بعد أداء الفرض منفردا عند تأخير الإمام الصلاة','783','985','0');
INSERT INTO [t121382] VALUES (1119,'(١٣٤) باب الصلاة جماعة بعد صلاة الصبح منفردا، فتكون الصلاة جماعة للمأموم نافلة وصلاة المنفرد قبلها فريضة. والدليل على أن قول النبي - صلى الله عليه وسلم -: "لا صلاة بعد الصبح حتى تطلع الشمس"، نهي خاص لا نهي عام','784','985','0');
INSERT INTO [t121382] VALUES (1120,'(١٣٥) باب النهي عن ترك الصلاة جماعة نافلة بعد الصلاة منفردا فريضة','785','985','0');
INSERT INTO [t121382] VALUES (1121,'(١٣٦) باب ذكر الدليل على أن الصلاة الأولى التي يصليها المرء في وقتها تكون فريضة، والثانية التي يصليها جماعة مع الإمام تكون تطوعا','785','985','0');
INSERT INTO [t121382] VALUES (1122,'(١٣٧) باب النهي عن إعادة الصلاة على نية الفرض','786','985','0');
INSERT INTO [t121382] VALUES (1123,'(١٣٨) باب المدرك وترا من صلاة الإمام، وجلوسه في الوتر من صلاته اقتداء بالإمام','786','985','0');
INSERT INTO [t121382] VALUES (1124,'(١٣٩) باب إمامة المسافر المقيمين، وإتمام المقيمين صلاتهم بعد فراغ الإمام إن ثبت الخبر، فإن في القلب من علي بن زيد بن جدعان، وإنما خرجت هذا الخبر في هذا الكتاب لأن هذه مسألة لا يختلف العلماء فيها','787','985','0');
INSERT INTO [t121382] VALUES (1125,'(١٤٠) باب المسبوق ببعض الصلاة، والأمر باقتدائه بالإمام فيما يدرك، وإتمامه ما سبق به بعد فراغ الإمام من الصلاة','788','985','0');
INSERT INTO [t121382] VALUES (1126,'(١٤١) باب المسبوق بوتر من صلاة الإمام، والدليل على أن لا سجدتي السهو عليه، ضد قول من زعم أنه عليه سجدتا السهو، على مذهبهم في هذه المسألة تكون سجدتا العمد، لا سجدتا السهو، إذ المأموم إنما يتعمد الجلوس في الوتر من صلاته اقتداء بإمامه إذ كان','788','985','0');
INSERT INTO [t121382] VALUES (1127,'(١٤٢) باب تلقين الإمام إذا تعايا أو ترك [١٧٤ - أ] شيئا من القرآن','790','985','0');
INSERT INTO [t121382] VALUES (1128,'(١٤٣) باب وضع الإمام نعليه عن يساره','791','985','0');
INSERT INTO [t121382] VALUES (1129,'(١٤٤) باب الرخصة للمريض في ترك إتيان الجماعة','791','985','0');
INSERT INTO [t121382] VALUES (1130,'(١٤٥) باب الرخصة في ترك الجماعة عند حضور العشاء','792','985','0');
INSERT INTO [t121382] VALUES (1131,'(١٤٦) باب الرخصة في ترك الجماعة [١٧٤ - ب] إذا كان المرء حاقنا','792','985','0');
INSERT INTO [t121382] VALUES (1132,'(١٤٧) باب الرخصة في ترك العميان الجماعة في الأمطار والسيول','793','985','0');
INSERT INTO [t121382] VALUES (1133,'(١٤٨) باب إباحة ترك الجماعة في السفر، والأمر بالصلاة في الرحال في الليلة المطيرة والباردة، بذكر خبر مختصر غير متقصى لو حمل الخبر على ظاهره كان شهود الجماعة في الليلة المطيرة والباردة معصية، إذ النبي - صلى الله عليه وسلم - قد أمر بالصلاة في الرحال [١٧٥ - أ]','794','985','0');
INSERT INTO [t121382] VALUES (1134,'(١٤٩) باب إباحة ترك الجماعة في السفر في الليلة المظلمة، وإن لم تكن باردة ولا مطيرة، بمثل اللفظ الذي ذكرت في الباب قبل','795','985','0');
INSERT INTO [t121382] VALUES (1135,'(١٥٠) باب إباحة ترك الجماعة في السفر، والأمر بالصلاة في الرحال في المطر القليل غير المؤذي بمثل اللفظ الذي ذكرت قبل','796','985','0');
INSERT INTO [t121382] VALUES (1136,'(١٥١) باب إباحة الصلاة في الرحال وترك الجماعة في اليوم المطير في السفر مثل اللفظة التي ذكرت قبل، والدليل على أن حكم النهار في إباحة ترك الصلاة في الجماعة في المطر كحكم الليل سواء','796','985','0');
INSERT INTO [t121382] VALUES (1137,'(١٥٢) باب ذكر الخبر المتقصي للفظة المختصرة التي ذكرتها من أمر النبي - صلى الله عليه وسلم - بالصلاة في الرحال، والدليل على أن أمر النبي - صلى الله عليه وسلم -[١٧٥ - ب] بذلك أمر إباحة لا أمر عزم، يكون متعديه عاصيا إن شهد الصلاة جماعة في المطر','797','985','0');
INSERT INTO [t121382] VALUES (1138,'(١٥٣) باب إتيان المساجد في الليلة المطيرة المظلمة، والدليل على أن الأمر بالصلاة في الرحال في مثل تلك الليلة أمر إباحة له لا حتم','797','985','0');
INSERT INTO [t121382] VALUES (1139,'(١٥٤) باب النهي عن إتيان الجماعة لآكل الثوم','798','985','0');
INSERT INTO [t121382] VALUES (1140,'(١٥٥) باب توقيت النهي عن إتيان الجماعة لآكل الثوم','798','985','0');
INSERT INTO [t121382] VALUES (1141,'(١٥٦) باب النهي عن إتيان المساجد لآكل الثوم','799','985','0');
INSERT INTO [t121382] VALUES (1142,'(١٥٧) باب النهي عن إتيان الجماعة لآكل الكراث','799','985','0');
INSERT INTO [t121382] VALUES (1143,'(١٥٨) باب الدليل على أن النهي عن إتيان المساجد لآكلهن نيئا غير مطبوخ','799','985','0');
INSERT INTO [t121382] VALUES (1144,'(١٥٩) باب الدليل على أن النهي عن ذلك لتأذي الناس بريحه لا تحريما لأكله','799','985','0');
INSERT INTO [t121382] VALUES (1145,'(١٦٠) باب ذكر الدليل على أن النهي عن ذلك لتأذي الملائكة بريحه، إذ الناس يتأذون به','800','985','0');
INSERT INTO [t121382] VALUES (1146,'(١٦١) باب النهي عن إتيان المسجد لآكل الثوم والبصل والكراث إلى أن يذهب ريحه','801','985','0');
INSERT INTO [t121382] VALUES (1147,'(١٦٢) باب ذكر ما خص الله به نبيه - صلى الله عليه وسلم - من ترك أكل الثوم والبصل والكراث مطبوخا','801','985','0');
INSERT INTO [t121382] VALUES (1148,'(١٦٣) باب الدليل على أن النبي - صلى الله عليه وسلم - خص بترك أكلهن لمناجاة الملائكة','801','985','0');
INSERT INTO [t121382] VALUES (1149,'(١٦٤) باب الرخصة في أكله عند الضرورة والحاجة إليه','802','985','0');
INSERT INTO [t121382] VALUES (1150,'(١٦٥) باب صلاة التطوع بالنهار في الجماعة ضد مذهب من كره ذلك','802','985','0');
INSERT INTO [t121382] VALUES (1151,'(١٦٦) باب صلاة التطوع بالليل في الجماعة في غير رمضان، ضد مذهب من كره ذلك','803','985','0');
INSERT INTO [t121382] VALUES (1152,'(١٦٧) باب الوتر جماعة في غير رمضان','804','985','0');
INSERT INTO [t121382] VALUES (1153,'(١٦٨) باب إمامة المرأة النساء في الفريضة','804','985','0');
INSERT INTO [t121382] VALUES (1154,'(١٦٩) باب الإذن للنساء في إتيان المساجد','805','985','0');
INSERT INTO [t121382] VALUES (1155,'(١٧٠) باب النهي عن منع النساء الخروج إلى المساجد بالليل','805','985','0');
INSERT INTO [t121382] VALUES (1156,'(١٧١) باب الأمر بخروج النساء إلى المساجد تفلات','806','985','0');
INSERT INTO [t121382] VALUES (1157,'(١٧٢) باب الزجر عن شهود المرأة المسجد متعطرة','806','985','0');
INSERT INTO [t121382] VALUES (1158,'(١٧٣) باب التغليظ في تعطر المرأة عند الخروج ليوجد ريحها وتسمية فاعلها زانية. والدليل على أن اسم الزاني قد يقع على من يفعل فعلا لا يوجب ذلك الفعل جلدا ولا رجما، مع الدليل على أن التشبيه الذي يوجب ذلك الفعل إنما يكون إذا اشتبهت العلتان لا لاجتماع الاسم، إذ المتعطرة التي تخرج ليوجد ريحها قد سماها النبي - صلى الله عليه وسلم - زانية، وهذا الفعل لا يوجب جلدا ولا رجما، ولو كان التشبيه بكون الاسم على الاسم، لكانت الزانية بالتعطر يجب عليها ما يجب على الزانية بالفرج، ولكن لما كانت العلة الموجبة للحد في الزنا الوطء بالفرج لم يجز أن يحكم لمن يقع عليه اسم زان وزانية بغير جماع بالفرج في الفرج بجلد ولا رجم [١٧٧ - أ]','806','985','0');
INSERT INTO [t121382] VALUES (1159,'(١٧٤) باب إيجاب الغسل على المتطيبة للخروج إلى المسجد، ونفي قبول صلاتها إن صلت قبل أن تغتسل','807','985','0');
INSERT INTO [t121382] VALUES (1160,'(١٧٥) باب اختيار صلاة المرأة في بيتها على صلاتها في المسجد، إن ثبت الخبر','807','985','0');
INSERT INTO [t121382] VALUES (1161,'(١٧٦) باب اختيار صلاة المرأة في بيتها على صلاتها في حجرتها، إن كان قتادة سمع هذا الخبر من مورق','809','985','0');
INSERT INTO [t121382] VALUES (1162,'(١٧٧) باب اختيار صلاة المرأة في حجرتها على صلاتها في دارها، وصلاتها في مسجد قومها على صلاتها في مسجد النبي - صلى الله عليه وسلم -','809','985','0');
INSERT INTO [t121382] VALUES (1163,'(١٧٨) باب اختيار صلاة المرأة في مخدعها على صلاتها في بيتها','810','985','0');
INSERT INTO [t121382] VALUES (1164,'(١٧٩) باب اختيار صلاة المرأة في أشد مكان من بيتها ظلمة','811','985','0');
INSERT INTO [t121382] VALUES (1165,'(١٨٠) باب فضل صفوف النساء المؤخرة على الصفوف المقدمة، والدليل على أن صفوفهن إذا كانت متباعدة عن صفوف الرجال كان أفضل','811','985','0');
INSERT INTO [t121382] VALUES (1166,'(١٨١) باب أمر النساء بخفض أبصارهن إذا صلين مع الرجال إذا خفن رؤية عورات الرجال إذا سجد الرجال أمامهن','811','985','0');
INSERT INTO [t121382] VALUES (1167,'(١٨٢) باب الزجر عن رفع النساء رءوسهن من السجود، إذا صلين مع الرجال قبل استواء الرجال جلوسا، إذا ضاقت أزرهم، فخيف أن يرى النساء عوراتهم','812','985','0');
INSERT INTO [t121382] VALUES (1168,'(١٨٣) باب التغليظ في قيام المأموم في الصف المؤخر إذا كان خلفه نساء، إرادة النظر إليهن أو إلى بعضهن، والدليل على أن المصلي إذا نظر إلى من خلفه من النساء لم يفسد ذلك الفعل صلاته','812','985','0');
INSERT INTO [t121382] VALUES (1169,'(١٨٤) باب ذكر الدليل على أن النهي عن منع النساء المساجد كان إذ كن لا يخاف فسادهن في الخروج إلى المساجد وظن لا بيقين','813','985','0');
INSERT INTO [t121382] VALUES (1170,'(١٨٥) باب ذكر بعض أحداث نساء بني إسرائيل الذي من أجله منعن المساجد','814','985','0');
INSERT INTO [t121382] VALUES (1171,'(١٨٦) باب الرخصة في إمامة المماليك الأحرار إذا كان المماليك أقرأ من الأحرار','814','985','0');
INSERT INTO [t121382] VALUES (1172,'(١٨٧) باب الصلاة جماعة في الأسفار','815','985','0');
INSERT INTO [t121382] VALUES (1173,'(١٨٨) باب الصلاة جماعة بعد ذهاب وقتها','815','985','0');
INSERT INTO [t121382] VALUES (1174,'(١٨٩) باب الجمع بين الصلاتين في الجماعة في السفر','816','985','0');
INSERT INTO [t121382] VALUES (1175,'(١٩٠) باب الأمر بالفصل بين الفريضة والتطوع بالكلام أو الخروج','816','985','0');
INSERT INTO [t121382] VALUES (1176,'(١٩١) باب رفع الصوت بالتكبير والذكر عند قضاء الإمام الصلاة','817','985','0');
INSERT INTO [t121382] VALUES (1177,'(١٩٢) باب نية المصلي بالسلام من عن يمينه إذا سلم عن يمينه، ومن عن شماله إذا سلم عن يساره','817','985','0');
INSERT INTO [t121382] VALUES (1178,'(١٩٣) باب سلام المأموم من الصلاة عند سلام الإمام','817','985','0');
INSERT INTO [t121382] VALUES (1179,'(١٩٤) باب رد المأموم على الإمام إذا سلم الإمام عند انقضاء الصلاة','818','985','0');
INSERT INTO [t121382] VALUES (1180,'(١٩٥) باب إقبال الإمام بوجهه يمنة إذا سلم عن يمينه، ويسرة إذا سلم عن شماله، وفيه دليل أيضا أن الإمام إذا سلم عن يمينه، والمأمومين الذين عن يساره إذ','819','985','0');
INSERT INTO [t121382] VALUES (1181,'(١٩٦) باب انحراف الإمام من الصلاة التي لا يتطوع بعدها','820','985','0');
INSERT INTO [t121382] VALUES (1182,'(١٩٧) باب تخيير الإمام في الانصراف من الصلاة أن ينصرف يمنة أو ينصرف يسرة','820','985','0');
INSERT INTO [t121382] VALUES (1183,'(١٩٨) باب إباحة استقبال الإمام بوجهه بعد السلام إذا لم يكن [١٨٠ - ب] مقابله من قد فاته بعض صلاة الإمام فيكون مقابل الإمام إذا قام يقضي','821','985','0');
INSERT INTO [t121382] VALUES (1184,'(١٩٩) باب الزجر عن مبادرة الإمام بالانصراف من الصلاة','821','985','0');
INSERT INTO [t121382] VALUES (1185,'(٢٠٠) باب نهوض الإمام عند الفراغ من الصلاة التي يتطوع بعدها ساعة يسلم من غير لبث، إذا لم يكن خلفه نساء','822','985','0');
INSERT INTO [t121382] VALUES (1186,'(٢٠١) باب ذكر الدليل على أن النبي - صلى الله عليه وسلم - إنما كان يقوم ساعة يسلم إذا لم يكن خلفه نساء، واستحباب ثبوت الإمام جالسا إذا كان خلفه نساء ليرجع النساء قبل [أن] يلحقهم الرجال','822','985','0');
INSERT INTO [t121382] VALUES (1187,'(٢٠٢) باب تخفيف ثبوت الإمام بعد السلام لينصرف النساء قبل الرجال، وترك تطويله الجلوس بعد السلام','823','985','0');
INSERT INTO [t121382] VALUES (1188,'كتاب الجمعة','824','0','0');
INSERT INTO [t121382] VALUES (1189,'(١) باب ذكر فرض الجمعة','824','1188','0');
INSERT INTO [t121382] VALUES (1190,'(٢) باب الدليل على أن فرض الجمعة على البالغين دون الأطفال','825','1188','0');
INSERT INTO [t121382] VALUES (1191,'(٣) باب ذكر إسقاط فرض الجمعة عن النساء','826','1188','0');
INSERT INTO [t121382] VALUES (1192,'(٤) باب ذكر أول جمعة جمعت بمدينة النبي - صلى الله عليه وسلم -، وذكر عدد من جمع بها أولا','827','1188','0');
INSERT INTO [t121382] VALUES (1193,'(٥) باب ذكر الجمعة التي جمعت بعد الجمعة التي جمعت بالمدينة، وذكر الموضع الذي جمع به','828','1188','0');
INSERT INTO [t121382] VALUES (1194,'(٦) باب ذكر من الله - عز وجل - على أمة محمد - صلى الله عليه وسلم - خير أمة أخرجت للناس بهدايته إياهم ليوم الجمعة، فله الحمد كثيرا على ذلك، إذ قد ضل','828','1188','0');
INSERT INTO [t121382] VALUES (1195,'(٧) باب في ذكر فضل يوم الجمعة، وأنها أفضل الأيام، وفزع الخلق غير الثقلين الجن والإنس، بذكر خبر مختصر غير متقصى','829','1188','0');
INSERT INTO [t121382] VALUES (1196,'(٨) باب ذكر الخبر المتقصى للفظة المختصرة التي ذكرتها، والدليل على أن العلة التي تفزع الخلق لها من يوم الجمعة هي خوفهم من قيام الساعة فيها، إذ الساعة تقوم يوم الجمعة','830','1188','0');
INSERT INTO [t121382] VALUES (1197,'(٩) باب صفة يوم الجمعة وأهلها إذا بعثوا يوم القيامة، إن صح الخبر فإن في النفس من هذا الإسناد','832','1188','0');
INSERT INTO [t121382] VALUES (1198,'(١٠) باب ذكر الساعة التي فيها خلق الله آدم من يوم الجمعة','832','1188','0');
INSERT INTO [t121382] VALUES (1199,'(١١) باب ذكر العلة التي أحسب لها سميت الجمعة جمعة','833','1188','0');
INSERT INTO [t121382] VALUES (1200,'(١٢) باب فضل الصلاة على النبي - صلى الله عليه وسلم - يوم الجمعة','833','1188','0');
INSERT INTO [t121382] VALUES (1201,'(١٣) باب ذكر بعض ما خص به يوم الجمعة من الفضيلة بأن جعل الله فيه ساعة يستجيب فيها دعاء المصلي، بذكر خبر مجمل غير مفسر، مختصر غير متقصى','834','1188','0');
INSERT INTO [t121382] VALUES (1202,'(١٤) باب ذكر الخبر المتقصى لبعض هذه اللفظة المجملة التي ذكرتها، والدليل على أن النبي - صلى الله عليه وسلم - إنما أعلم أن هذه الساعة التي في الجمعة إنما يستجاب فيها دعاء المصلي دون غيره، وفيه اختصار أيضا، ليست هذه اللفظة التي أذكرها بمتقصاة لكلها','834','1188','0');
INSERT INTO [t121382] VALUES (1203,'(١٥) باب ذكر الخبر المتقصي للفظتين المجملتين اللتين ذكرتهما في البابين قبل. والبيان أن النبي - صلى الله عليه وسلم - إنما أعلم أن دعاء المصلي القائم يستجاب في تلك الساعة من يوم الجمعة دون دعاء غير المصلي ودون دعاء المصلي غير القائم وذكر قصر تلك الساعة التي يستجاب فيها الدعاء يوم الجمعة','835','1188','0');
INSERT INTO [t121382] VALUES (1204,'(١٦) باب ذكر البيان أن الساعة التي ذكرناها هي في كل جمعة من الجمعات لا في بعضها دون بعض','835','1188','0');
INSERT INTO [t121382] VALUES (1205,'(١٧) باب ذكر الدليل أن الدعاء بالخير مستجاب','836','1188','0');
INSERT INTO [t121382] VALUES (1206,'(١٨) باب ذكر وقت تلك الساعة التي يستجاب فيها الدعاء [١٨٣ - ب] من يوم الجمعة','836','1188','0');
INSERT INTO [t121382] VALUES (1207,'(١٩) باب ذكر الدليل أن الدعاء في تلك الساعة يستجاب في الصلاة','836','1188','0');
INSERT INTO [t121382] VALUES (1208,'(٢٠) باب ذكر إنساء النبي - صلى الله عليه وسلم - وقت تلك الساعة بعد علمه إياها','837','1188','0');
INSERT INTO [t121382] VALUES (1209,'(٢١) باب إيجاب الغسل للجمعة مثل اللفظة التي ذكرت قبل أن الأمر إذا كان لعلة فمتى كانت العلة قائمة كان الأمر واجبا','838','1188','0');
INSERT INTO [t121382] VALUES (1210,'(٢٢) باب ذكر الدليل [على] أن النبي - صلى الله عليه وسلم - إنما أراد بقوله: واجب أي واجب على البطلان لا وجوب فرض لا يجزئ غيره','839','1188','0');
INSERT INTO [t121382] VALUES (1211,'(٢٣) باب ذكر الخبر المفسر للفظة المجملة التي ذكرتها، والدليل أن النبي - صلى الله عليه وسلم - إنما أمر بغسل يوم الجمعة من أتاها دون من لم يأت الجمعة','841','1188','0');
INSERT INTO [t121382] VALUES (1212,'(٢٤) باب أمر الخاطب بالغسل يوم الجمعة في خطبة الجمعة، والدليل على أن الخطبة ليست بصلاة كما توهم بعض الناس، إذ الخطبة لو كانت صلاة ما جاز أن يتكلم فيها ما لا يجوز من الكلام في الصلاة','842','1188','0');
INSERT INTO [t121382] VALUES (1213,'(٢٥) باب أمر النساء بالغسل لشهود الجمعة، وهذه اللفظة أيضا من','842','1188','0');
INSERT INTO [t121382] VALUES (1214,'(٢٦) باب ذكر علة ابتداء الأمر بالغسل للجمعة','843','1188','0');
INSERT INTO [t121382] VALUES (1215,'(٢٧) باب ذكر دليل أن الغسل يوم الجمعة فضيلة لا فريضة','844','1188','0');
INSERT INTO [t121382] VALUES (1216,'(٢٨) باب ذكر فضيلة الغسل يوم الجمعة إذا ابتكر المغتسل إلى الجمعة فدنا وأنصت، ولم يلغ','845','1188','0');
INSERT INTO [t121382] VALUES (1217,'(٢٩) باب ذكر بعض فضائل الغسل يوم الجمعة','846','1188','0');
INSERT INTO [t121382] VALUES (1218,'(٣٠) باب الأمر بالتطيب يوم الجمعة، إذ من الحقوق على المسلم التطيب إذا كان واجدا له','847','1188','0');
INSERT INTO [t121382] VALUES (1219,'(٣١) باب فضيلة التطيب والتسوك، ولبس أحسن ما يجد المرء من الثياب بعد الاغتسال يوم الجمعة، وترك تخطي رقاب الناس، والتطوع بالصلاة بما قضى الله للمرء أن يتطوع بها قبل الجمعة، والإنصات عند خروج الإمام حتى تقضى الصلاة','847','1188','0');
INSERT INTO [t121382] VALUES (1220,'(٣٢) باب فضيلة الادهان يوم الجمعة، والجمع بين الادهان وبين التطيب يوم الجمعة','848','1188','0');
INSERT INTO [t121382] VALUES (1221,'(٣٣) باب استحباب اتخاذ المرء في الجمعة ثيابا سوى ثوبي المهنة','848','1188','0');
INSERT INTO [t121382] VALUES (1222,'(٣٤) باب استحباب لبس الحلل في الجمعة إن كان الحجاج بن أرطأة سمع هذا الخبر من أبي جعفر محمد بن علي','849','1188','0');
INSERT INTO [t121382] VALUES (1223,'(٣٥) باب فضل التبكير إلى الجمعة مغتسلا، والدنو من الإمام، والاستماع والإنصات','849','1188','0');
INSERT INTO [t121382] VALUES (1224,'(٣٦) باب تمثيل المهجرين إلى الجمعة في الفضل بالمهدين، والدليل على أن من سبق بالتهجير كان أفضل ممن أبطأ به','850','1188','0');
INSERT INTO [t121382] VALUES (1225,'(٣٧) باب ذكر جلوس الملائكة على أبواب المسجد يوم الجمعة لكتبة المهجرين إليها على منازلهم، ووقت طيهم للصحف لاستماع الخطبة','850','1188','0');
INSERT INTO [t121382] VALUES (1226,'(٣٨) باب ذكر عدد من يقعد على كل باب من أبواب المسجد يوم الجمعة من الملائكة لكتابة المهجرين إليها، والدليل على أن الاثنين قد يقع عليهما اسم جماعة، إذ النبي - صلى الله عليه وسلم - قد أوقع على الملكين اسم الملائكة','851','1188','0');
INSERT INTO [t121382] VALUES (1227,'(٣٩) باب ذكر دعاء الملائكة للمتخلفين عن الجمعة بعد طيهم الصحف','851','1188','0');
INSERT INTO [t121382] VALUES (1228,'(٤٠) باب فضل المشي إلى الجمعة، وترك الركوب، واستحباب مقاربة الخطا لتكثر الخطا فيكثر الأجر','852','1188','0');
INSERT INTO [t121382] VALUES (1229,'(٤١) باب الأمر بالسكينة في المشي إلى الجمعة، والنهي عن السعي إليها، والدليل على أن الاسم الواحد يقع على فعلين يؤمر بأحدهما ويزجر عن الآخر بالاسم الواحد، فمن لا يفهم العلم، ولا يميز بين المعنيين، قد يخطر بباله أنهما مختلفان، قد أمر الله - عز وجل - في نص كتابه بالسعي إلى الجمعة في قوله: (يا أيها الذين آمنوا إذا نودي للصلاة من يوم الجمعة فاسعوا إلى ذكر الله) [الجمعة: ٩] والنبي المصطفى قد نهى عن السعي إلى الصلاة فقال - صلى الله عليه وسلم -: "إذا أتيتم الصلاة فعليكم السكينة والوقار"، وقال - صلى الله عليه وسلم -: "فإذا أتيتم الصلاة، فلا تسعوا إليها، وامشوا وعليكم السكينة" فالله - عز وجل - أمر بالسعي إلى الجمعة، والنبي - صلى الله عليه وسلم - قد نهى عن السعي إلى الصلاة. فالسعي الذي أمر الله به إلى الجمعة هو المضي إليها، غير السعي الذي زجر النبي - صلى الله عليه وسلم - في إتيان الصلاة. لأن السعي الذي زجر النبي - صلى الله عليه وسلم - هو الخبب وشدة المشي إلى الصلاة الذي هو ضد الوقار والسكينة، فما أمر الله - عز وجل - به غير ما زجر النبي - صلى الله عليه وسلم - عنه، وإن كان الاسم الواحد قد يقع عليهما جميعا','852','1188','0');
INSERT INTO [t121382] VALUES (1230,'(٤٢) باب ذكر الأذان الذي كان على عهد رسول الله - صلى الله عليه وسلم - الذي أمر الله - جل وعلا - بالسعي إلى الجمعة إذا نودي به، والوقت الذي كان ينادى به، وذكر من أحدث النداء الأول قبل خروج الإمام','853','1188','0');
INSERT INTO [t121382] VALUES (1231,'(٤٣) باب فضل إنصات المأموم عند خروج الإمام قبل الابتداء في الخطبة، ضد قول من زعم أن كلام الإمام يقطع الكلام','854','1188','0');
INSERT INTO [t121382] VALUES (1232,'(٤٤) باب ذكر [أن] موضع قيام النبي - صلى الله عليه وسلم - في الخطبة، كان قبل اتخاذه المنبر. والدليل على أن الخطبة على الأرض جائزة من غير صعود المنبر يوم الجمعة، والعلة التي لها أمر النبي - صلى الله عليه وسلم - باتخاذ [١٨٧ - ب] المنبر إذ هو أحرى أن يسمع الناس خطبة الإمام إذا كثروا، إذا خطب على المنبر','856','1188','0');
INSERT INTO [t121382] VALUES (1233,'(٤٥) باب ذكر العلة التي لها حن الجذع عند قيام النبي - صلى الله عليه وسلم - على المنبر. وصفة منبر النبي - صلى الله عليه وسلم -، وعدد درجه، والاستناد إلى شيء إذا خطب على الأرض','856','1188','0');
INSERT INTO [t121382] VALUES (1234,'(٤٦) باب استحباب الاعتماد في الخطبة على القسي أو العصا استنانا بالنبي - صلى الله عليه وسلم -','857','1188','0');
INSERT INTO [t121382] VALUES (1235,'(٤٧) باب ذكر العود الذي منه اتخذ منبر رسول الله - صلى الله عليه وسلم -','857','1188','0');
INSERT INTO [t121382] VALUES (1236,'(٤٨) باب أمر الإمام الناس بالجلوس عند الاستواء على المنبر يوم الجمعة إن كان الوليد بن مسلم ومن دونه حفظ ابن عباس في هذا الإسناد، فإن أصحاب ابن جريج أرسلوا هذا الخبر عن عطاء، عن النبي - صلى الله عليه وسلم -','858','1188','0');
INSERT INTO [t121382] VALUES (1237,'(٤٩) باب ذكر عدد الخطبة يوم الجمعة، والجلسة بين الخطبتين، ضد قول من جهل السنة فزعم أن السنة بدعة، وقال: الجلوس بين الخطبتين بدعة','858','1188','0');
INSERT INTO [t121382] VALUES (1238,'(٥٠) باب استحباب تقصير الخطبة، وترك تطويلها','859','1188','0');
INSERT INTO [t121382] VALUES (1239,'(٥١) باب صفة خطبة النبي - صلى الله عليه وسلم -، وبدؤه فيها بحمد الله، والثناء عليه','859','1188','0');
INSERT INTO [t121382] VALUES (1240,'(٥٢) باب قراءة القرآن في الخطبة يوم الجمعة','860','1188','0');
INSERT INTO [t121382] VALUES (1241,'(٥٣) باب الرخصة في الاستسقاء في خطبة الجمعة إذا قحط الناس، وخيف من القحط هلاك الأموال، وانقطاع السبل إن لم يغث الله بمنه وطوله','861','1188','0');
INSERT INTO [t121382] VALUES (1242,'(٥٤) باب الدعاء بحبس المطر عن البيوت والمنازل إذا خيف الضرر من كثرة الأمطار وهدم المنازل، ومسألة الله - عز وجل - تحويل الأمطار إلى الجبال والأودية حيث لا يخاف الضرر في خطبة الجمعة','861','1188','0');
INSERT INTO [t121382] VALUES (1243,'(٥٥) باب الرخصة في تبسم الإمام في الخطبة','862','1188','0');
INSERT INTO [t121382] VALUES (1244,'(٥٦) باب صفة رفع اليدين في الاستسقاء في خطبة الجمعة','862','1188','0');
INSERT INTO [t121382] VALUES (1245,'(٥٧) باب الإشارة بالسبابة على المنبر في خطبة الجمعة، وكراهة رفع اليدين على المنبر في غير الاستسقاء','863','1188','0');
INSERT INTO [t121382] VALUES (1246,'(٥٨) باب تحريك السبابة عند الإشارة بها في الخطبة','864','1188','0');
INSERT INTO [t121382] VALUES (1247,'(٥٩) باب النزول عن المنبر للسجود عند قراءة السجدة في الخطبة إن صح الخبر','864','1188','0');
INSERT INTO [t121382] VALUES (1248,'(٦٠) باب الرخصة في العلم إذا سئل الإمام وقت خطبته على المنبر يوم الجمعة، ضد مذهب من توهم أن الخطبة صلاة، ولا يجوز الكلام فيها بما لا يجوز في الصلاة','864','1188','0');
INSERT INTO [t121382] VALUES (1249,'(٦١) باب الرخصة في تعليم الإمام الناس ما يجهلون في الخطبة من غير سؤال يسأل الإمام','865','1188','0');
INSERT INTO [t121382] VALUES (1250,'(٦٢) باب الرخصة في سلام الإمام في الخطبة على القادم من السفر إذا دخل المسجد','865','1188','0');
INSERT INTO [t121382] VALUES (1251,'(٦٣) باب أمر الإمام الناس في خطبة يوم الجمعة بالصدقة، إذا رأى حاجة، وفقرا','866','1188','0');
INSERT INTO [t121382] VALUES (1252,'(٦٤) باب الرخصة في قطع الإمام الخطبة لتعليم السائل العلم','867','1188','0');
INSERT INTO [t121382] VALUES (1253,'(٦٥) باب نزول الإمام عن المنبر، وقطعه الخطبة للحاجة تبدو له','867','1188','0');
INSERT INTO [t121382] VALUES (1254,'(٦٦) باب فضل الإنصات والاستماع للخطبة','868','1188','0');
INSERT INTO [t121382] VALUES (1255,'(٦٧) باب الزجر عن الكلام يوم الجمعة عند خطبة الإمام','868','1188','0');
INSERT INTO [t121382] VALUES (1256,'(٦٨) باب الزجر عن إنصات الناس بالكلام يوم الجمعة والإمام يخطب','868','1188','0');
INSERT INTO [t121382] VALUES (1257,'(٦٩) باب الزجر عن إنصات الناس بالكلام وإن لم يسمع الزاجر خطبة الإمام','869','1188','0');
INSERT INTO [t121382] VALUES (1258,'(٧٠) باب النهي عن السؤال عن العلم غير الإمام، والإمام يخطب','869','1188','0');
INSERT INTO [t121382] VALUES (1259,'(٧١) باب ذكر إبطال فضيلة الجمعة بالكلام والإمام يخطب، بلفظ مجمل غير مفسر، وزجر المتكلم عن الكلام بالتسبيح','870','1188','0');
INSERT INTO [t121382] VALUES (1260,'(٧٢) باب ذكر الخبر المفسر للفظة المجملة [١٩٠ - ب] التي ذكرتها،','870','1188','0');
INSERT INTO [t121382] VALUES (1261,'(٧٣) باب الأمر بإنصات المتكلم والإمام يخطب بالإشارة إليه بالزجر','871','1188','0');
INSERT INTO [t121382] VALUES (1262,'(٧٤) باب النهي عن تخطي الناس يوم الجمعة والإمام يخطب، وإباحة زجر الإمام عن ذلك في خطبته','871','1188','0');
INSERT INTO [t121382] VALUES (1263,'(٧٥) باب النهي عن التفريق بين الناس في الجمعة، وفضيلة اجتناب ذلك','872','1188','0');
INSERT INTO [t121382] VALUES (1264,'(٧٦) باب طبقات من يحضر الجمعة','872','1188','0');
INSERT INTO [t121382] VALUES (1265,'(٧٧) باب ذكر الخبر المفسر للأخبار المجملة التي ذكرتها في الأبواب المتقدمة، والدليل [على] أن جميع ما تقدم من الأخبار في ذكر الجمعة أنها كفارة للذنوب والخطايا إنما هي ألفاظ عام مرادها خاص، أراد النبي المصطفى - صلى الله عليه وسلم - أنها كفارة لصغائر الذنوب دون كبارها','873','1188','0');
INSERT INTO [t121382] VALUES (1266,'(٧٨) باب النهي عن الحبوة يوم الجمعة والإمام يخطب','873','1188','0');
INSERT INTO [t121382] VALUES (1267,'(٧٩) باب الزجر عن الحلق يوم الجمعة قبل الصلاة','873','1188','0');
INSERT INTO [t121382] VALUES (1268,'(٨٠) باب فضل ترك الجهل يوم الجمعة من حين يأتي المرء الجمعة إلى انقضاء الصلاة','874','1188','0');
INSERT INTO [t121382] VALUES (1269,'(٨١) باب الزجر عن مس الحصى والإمام يخطب يوم الجمعة، والإعلام بأن مس الحصى في ذلك الوقت لغو','874','1188','0');
INSERT INTO [t121382] VALUES (1270,'(٨٢) باب استحباب تحول الناعس يوم الجمعة عن موضعه إلى غيره، والدليل [على] أن النعاس ليس باستحقاق نوم، ولا يوجب وضوءا','874','1188','0');
INSERT INTO [t121382] VALUES (1271,'(٨٣) باب الزجر عن إقامة الرجل أخاه يوم الجمعة من مجلسه ليخلفه فيه','875','1188','0');
INSERT INTO [t121382] VALUES (1272,'(٨٤) باب ذكر قيام الرجل من مجلسه يوم الجمعة ثم يرجع، وقد خلفه فيه غيره، والبيان أنه أحق بمجلسه ممن خلفه فيه','875','1188','0');
INSERT INTO [t121382] VALUES (1273,'(٨٥) باب الأمر بالتوسع والتفسح إذا ضاق الموضع. قال الله - عز وجل -: (يا أيها الذين آمنوا إذا قيل لكم تفسحوا في المجالس فافسحوا يفسح الله لكم) [المجادله:١١]','876','1188','0');
INSERT INTO [t121382] VALUES (1274,'(٨٦) باب ذكر كراهة انفضاض الناس عن الإمام وقت خطبته للنظر إلى لهو أو تجارة. قال الله - عز وجل - لنبيه المصطفى - صلى الله عليه وسلم -: (وإذا رأوا تجارة أو لهوا انفضوا إليها وتركوك قائما) الآية','876','1188','0');
INSERT INTO [t121382] VALUES (1275,'(٨٧) باب الأمر بإعطاء المساجد حقها من الصلاة عند دخولها','877','1188','0');
INSERT INTO [t121382] VALUES (1276,'(٨٨) باب الأمر بالتطوع بركعتين عند دخول المسجد قبل الجلوس','877','1188','0');
INSERT INTO [t121382] VALUES (1277,'(٨٩) باب الزجر عن الجلوس عند دخول المسجد قبل [أن] يصلي ركعتين','877','1188','0');
INSERT INTO [t121382] VALUES (1278,'(٩٠) باب الأمر بالرجوع إلى المسجد ليصلي الركعتين إذا دخله فخرج منه قبل أن يصليهما','878','1188','0');
INSERT INTO [t121382] VALUES (1279,'(٩١) باب الدليل على أن الأمر بركعتين عند دخول المسجد أمر ندب وإرشاد وفضيلة','878','1188','0');
INSERT INTO [t121382] VALUES (1280,'(٩٢) باب الدليل على أن الجالس عند دخول المسجد قبل [أن] يصلي الركعتين لا يجب إعادتهما، إذ الركعتان عند دخول المسجد فضيلة لا فريضة','879','1188','0');
INSERT INTO [t121382] VALUES (1281,'(٩٣) باب الأمر بتطوع ركعتين عند دخول المسجد وإن كان الإمام يخطب خطبة الجمعة، ضد قول من زعم أنه غير جائز أن يصلي داخل المسجد والإمام يخطب','879','1188','0');
INSERT INTO [t121382] VALUES (1282,'(٩٤) باب سؤال الإمام في خطبة الجمعة داخل المسجد وقت الخطبة أصلى ركعتين أم لا؟ وأمر الإمام الداخل بأن يصلي ركعتين إن لم يكن صلاهما قبل سؤال الإمام إياه. والدليل على أن الخطبة ليست بصلاة','880','1188','0');
INSERT INTO [t121382] VALUES (1283,'(٩٥) باب أمر الإمام في خطبة الجمعة داخل المسجد بركعتين يصليهما. والدليل على أن النبي - صلى الله عليه وسلم - لم يقطع خطبته ليصلي الداخل الذي أمره أن يصلي ركعتين إلى أن يفرغ المصلي من الركعتين كما زعم بعض من لم ينعم النظر في الأخبار','881','1188','0');
INSERT INTO [t121382] VALUES (1284,'(٩٦) باب أمر الإمام في خطبته الجالس قبل [أن] يصليهما بالقيام','881','1188','0');
INSERT INTO [t121382] VALUES (1285,'(٩٧) باب إباحة ما أراد المصلي من الصلاة قبل الجمعة','882','1188','0');
INSERT INTO [t121382] VALUES (1286,'(٩٨) باب استحباب تطويل الصلاة قبل صلاة الجمعة','883','1188','0');
INSERT INTO [t121382] VALUES (1287,'(٩٩) باب وقت الإقامة لصلاة الجمعة','883','1188','0');
INSERT INTO [t121382] VALUES (1288,'(١٠٠) باب الرخصة في الكلام للمأموم والإمام بعد الخطبة، وقبل افتتاح الصلاة','883','1188','0');
INSERT INTO [t121382] VALUES (1289,'(١٠١) باب وقت صلاة الجمعة','884','1188','0');
INSERT INTO [t121382] VALUES (1290,'(١٠٢) باب استحباب التبكير بالجمعة','884','1188','0');
INSERT INTO [t121382] VALUES (1291,'(١٠٣) باب التبريد بصلاة الجمعة في شدة الحر والتبكير بها','884','1188','0');
INSERT INTO [t121382] VALUES (1292,'(١٠٤) باب ذكر عدد صلاة الجمعة','885','1188','0');
INSERT INTO [t121382] VALUES (1293,'(١٠٥) باب القراءة في صلاة الجمعة','885','1188','0');
INSERT INTO [t121382] VALUES (1294,'(١٠٦) باب إباحة قراءة غير سورة المنافقين في الركعة الثانية من صلاة الجمعة وإن قرأ في الأولى بسورة الجمعة','885','1188','0');
INSERT INTO [t121382] VALUES (1295,'(١٠٧) باب إباحة القراءة في صلاة الجمعة بـ (سبح اسم ربك الأعلى)، و (هل أتاك حديث الغاشية). وهذا الاختلاف في القراءة من اختلاف المباح','886','1188','0');
INSERT INTO [t121382] VALUES (1296,'(١٠٨) باب المدرك ركعة من صلاة الجمعة مع الإمام، والدليل [على] أن المدرك منها ركعة يكون مدركا للجمعة، يجب عليه أن يضيف إليها أخرى، لا كما قال بعض من زعم أن من فاتته الخطبة فعليه أن يصلي ظهرا أربعا، مع الدليل أن من لم يدرك منها ركعة فعليه أن يصلي ظهرا أربعا نقض ما قال بعض العراقيين أن من أدرك التشهد يوم الجمعة أجزأته ركعتان','887','1188','0');
INSERT INTO [t121382] VALUES (1297,'(١٠٩) باب الدليل على تجويز صلاة الجمعة بأقل من أربعين','888','1188','0');
INSERT INTO [t121382] VALUES (1298,'(١١٠) باب التغليظ في التخلف عن شهود الجمعة','889','1188','0');
INSERT INTO [t121382] VALUES (1299,'(١١١) باب ذكر الختم على قلوب التاركين للجمعات، وكونهم من الغافلين بالتخلف عن الجمعة','889','1188','0');
INSERT INTO [t121382] VALUES (1300,'(١١٢) باب ذكر الدليل على أن الوعيد لتارك الجمعة هو لتاركها من غير عذر','890','1188','0');
INSERT INTO [t121382] VALUES (1301,'(١١٣) باب ذكر الدليل [على] أن الطبع على القلب بترك الجمعات الثلاث إنما يكون إذا تركها تهاونا بها','890','1188','0');
INSERT INTO [t121382] VALUES (1302,'(١١٤) باب التغليظ في الغيبة عن المدن لمنافع الدنيا إذا آلت الغيبة إلى ترك شهود الجمعات','891','1188','0');
INSERT INTO [t121382] VALUES (1303,'(١١٥) باب ذكر شهود من كان خارج المدن الجمعة مع الإمام إذا جمع في المدن إن صح الخبر، فإن في القلب من سوء حفظ عبد الله بن عمر العمري رحمه الله','891','1188','0');
INSERT INTO [t121382] VALUES (1304,'(١١٦) باب الأمر بصدقة دينار إن وجده، أو بنصف دينار إن أعوزه دينار لترك جمعة من غير عذر إن صح الخبر، فإني لا أقف على سماع قتادة عن قدامة بن وبرة، ولست أعرف قدامة بعدالة ولا جرح','892','1188','0');
INSERT INTO [t121382] VALUES (1305,'(١١٧) باب الرخصة في التخلف عن الجمعة في الأمطار إذا كان المطر وابلا كبيرا','892','1188','0');
INSERT INTO [t121382] VALUES (1306,'(١١٨) باب الرخصة في التخلف عن الجمعة في المطر وإن لم يكن المطر مؤذيا، وهذا من الجنس الذي أعلمت في غير موضع من كتابنا في كتاب "معاني القرآن" وفي الكتب المصنفة من المسند أن الله جل وعلا ورسوله المصطفى قد يبيحان الشيء لعلة من غير حظر ذلك الشيء، وإن كانت تلك العلة معدومة، من ذلك قوله -جل وعلا- في المطلقة ثلاثا إذا نكحت زوجا غير الأول: (فإن طلقها فلا جناح عليهما أن يتراجعا) [البقرة: ٢٣٠] فأباح الله - جل وعلا - المطلقة ثلاثا بعد طلاق الثاني، وهي قد تحل له بموت الثاني [١٩٥ - أ] وإن لم يطلقها، وقد تحل له إذا انفسخ النكاح بينهما إما بلعان بينها وبين الزوج الثاني أو بارتداد أحدهما، ثم تنقضي عدتها قبل [أن] يرجع المرتد منها إلى الإسلام، وغير ذلك مما يفسخ النكاح بين الزوجين. ومن هذا الجنس قوله -تبارك وتعالى-: (فليس عليكم جناح أن تقصروا من الصلاة) الآية. والقصر أيضا مباح، وإن لم يخافوا من فتنة الكفار','893','1188','0');
INSERT INTO [t121382] VALUES (1307,'(١١٩) باب أمر الإمام المؤذن في أذان الجمعة بالنداء أن الصلاة في البيوت ليعلم السامع أن التخلف عن الجمعة في المطر طلق مباح','894','1188','0');
INSERT INTO [t121382] VALUES (1308,'(١٢٠) باب أمر الإمام المؤذن بحذف حي على الصلاة، والأمر بالصلاة في البيوت بدله','894','1188','0');
INSERT INTO [t121382] VALUES (1309,'(١٢١) باب الدليل على أن الأمر بالنداء يوم الجمعة بالصلاة في الرحال الذي خبر ابن عباس أنه "فعله من هو خير مني" النبي - صلى الله عليه وسلم -، إن كان عباد بن منصور حفظ هذا الخبر الذي أذكره','895','1188','0');
INSERT INTO [t121382] VALUES (1310,'(١٢٢) باب الأمر بالفصل بين صلاة الجمعة، وبين صلاة التطوع بعدها بكلام أو خروج','895','1188','0');
INSERT INTO [t121382] VALUES (1311,'(١٢٣) باب الاكتفاء من الخروج للفصل بين الجمعة والتطوع بعدها بالتقدم أمام المصلى الذي صلى فيه الجمعة','895','1188','0');
INSERT INTO [t121382] VALUES (1312,'(١٢٤) باب استحباب تطوع الإمام بعد الجمعة في منزله','896','1188','0');
INSERT INTO [t121382] VALUES (1313,'(١٢٥) باب إباحة صلاة التطوع بعد الجمعة للإمام في المسجد','896','1188','0');
INSERT INTO [t121382] VALUES (1314,'(١٢٦) باب أمر المأموم بأن يتطوع بعد الجمعة بأربع ركعات بلفظ مختصر غير متقصى','897','1188','0');
INSERT INTO [t121382] VALUES (1315,'(١٢٧) باب ذكر الخبر المتقصي للفظة المختصرة التي ذكرتها،','897','1188','0');
INSERT INTO [t121382] VALUES (1316,'(١٢٨) باب الرجوع إلى المنازل بعد قضاء الجمعة للغداء والقيلولة','898','1188','0');
INSERT INTO [t121382] VALUES (1317,'(١٢٩) باب استحباب الانتشار بعد صلاة الجمعة، والابتغاء من فضل الله قال الله - عز وجل -: (فإذا قضيت الصلاة فانتشروا في الأرض وابتغوا من فضل الله) إلا أن في القلب من هذا الخبر، فإني لا أعرف سعيد بن عنبسة القطان هذا، ولا عبد الله بن بسر الذي روى عنه سعيد هذا بعدالة ولا جرح، غير أن الله - عز وجل - قد أمر في نص تنزيله بعد قضاء صلاة الجمعة بالانتشار في الأرض، والابتغاء من فضل الله، وهذا من أمر الإباحة','899','1188','0');
INSERT INTO [t121382] VALUES (1318,'كتاب الصوم','900','0','0');
INSERT INTO [t121382] VALUES (1319,'(١) باب ذكر البيان أن صوم شهر رمضان من الإيمان','900','1318','0');
INSERT INTO [t121382] VALUES (1320,'(٢) باب ذكر البيان أن صوم شهر رمضان من الإسلام، إذ الإيمان والإسلام اسمان لمسمى واحد','901','1318','0');
INSERT INTO [t121382] VALUES (1321,'(٣) باب ذكر فتح أبواب الجنان -نسأل الله دخولها- وإغلاق أبواب','901','1318','0');
INSERT INTO [t121382] VALUES (1322,'(٤) باب ذكر البيان أن النبي - صلى الله عليه وسلم - إنما أراد بقوله: "وصفدت الشياطين" مردة الجن منهم، لا جميع الشياطين، إذ اسم الشياطين قد يقع على بعضهم، وذكر دعاء الملك في رمضان إلى الخيرات، والتقصير عن السيئات، مع الدليل على أن أبواب الجنان إذا فتحت لم يغلق منها باب، ولا يفتح باب من أبواب النيران إذا أغلقت في شهر رمضان','902','1318','0');
INSERT INTO [t121382] VALUES (1323,'(٥) باب في فضل شهر رمضان، وأنه خير الشهور للمسلمين، وذكر إعداد المؤمن القوة من النفقة للعبادة قبل دخوله','902','1318','0');
INSERT INTO [t121382] VALUES (1324,'(٦) باب ذكر تفضل الله - عز وجل - على عبادة المؤمنين في أول ليلة من شهر رمضان بمغفرته إياهم كرما وجودا إن صح الخبر فإني لا أعرف خلفا أبا الربيع هذا بعدالة ولا جرح، ولا عمرو بن حمزة القيسي الذي هو دونه','903','1318','0');
INSERT INTO [t121382] VALUES (1325,'(٧) باب ذكر تزيين الجنة لشهر رمضان، وذكر بعض ما أعد الله للصائمين في الجنة غير ممكن لآدمي صفته، إذ فيها ما لا عين رأت، ولا أذن سمعت، ولا خطر على قلب بشر، إن صح الخبر، فإن في القلب من جرير بن أيوب البجلي','904','1318','0');
INSERT INTO [t121382] VALUES (1326,'(٨) باب فضائل شهر رمضان، إن صح الخبر','905','1318','0');
INSERT INTO [t121382] VALUES (1327,'(٩) باب استحباب الاجتهاد في العبادة في رمضان لعل الرب - عز وجل - برأفته ورحمته، يغفر للمجتهد قبل [أن] ينقضي الشهر، ولا يرغم بأنف العبد بمضي رمضان','906','1318','0');
INSERT INTO [t121382] VALUES (1328,'(١٠) باب استحباب الجود بالخير والعطايا في شهر رمضان إلى انسلاخه استنانا بالنبي - صلى الله عليه وسلم -','907','1318','0');
INSERT INTO [t121382] VALUES (1329,'(١١) باب الاجتنان بالصوم من النار، إذ الله - عز وجل - جعل الصوم جنة من النار، نعوذ بالله من النار','907','1318','0');
INSERT INTO [t121382] VALUES (1330,'(١٢) باب الدليل على أن الصوم إنما يكون جنة باجتناب ما نهي الصائم عنه، وإن كان ما نهي عنه مما لا يفطره، ولكن ينقص صومه عن الكمال والتمام','908','1318','0');
INSERT INTO [t121382] VALUES (1331,'(١٣) باب فضل الصيام، وأنه لا عدل له من الأعمال','908','1318','0');
INSERT INTO [t121382] VALUES (1332,'(١٤) باب ذكر مغفرة الذنوب السالفة بصوم شهر رمضان إيمانا واحتسابا','909','1318','0');
INSERT INTO [t121382] VALUES (1333,'(١٥) باب ذكر تمثيل الصائم في طيب ريحه بطيب ريح المسك، إذ هو أطيب الطيب','909','1318','0');
INSERT INTO [t121382] VALUES (1334,'(١٦) باب ذكر طيب خلفة الصائم عند الله يوم القيامة','910','1318','0');
INSERT INTO [t121382] VALUES (1335,'(١٧) باب ذكر إعطاء الرب - عز وجل - الصائم أجره بغير حساب، إذ الصيام من الصبر، قال الله - عز وجل -: (إنما يوفى الصابرون أجرهم بغير حساب) [الزمر: ١٠]','911','1318','0');
INSERT INTO [t121382] VALUES (1336,'(١٨) باب ذكر البيان أن الصيام من الصبر على ما تأولت خبر النبي - صلى الله عليه وسلم -','911','1318','0');
INSERT INTO [t121382] VALUES (1337,'(١٩) باب ذكر فرح الصائم يوم القيامة بإعطاء الرب إياه ثواب صومه بلا حساب، جعلنا الله منهم','912','1318','0');
INSERT INTO [t121382] VALUES (1338,'(٢٠) باب ذكر استجابة الله - عز وجل - دعاء الصوام إلى فطرهم من صيامهم، جعلنا الله منهم','912','1318','0');
INSERT INTO [t121382] VALUES (1339,'(٢١) باب ذكر باب الجنة الذي يخص بدخوله الصوام دون غيرهم، ونفي الظمأ عمن يدخل الجنة، ويشرب من شرابها، جعلنا الله منهم','913','1318','0');
INSERT INTO [t121382] VALUES (1340,'(٢٢) باب صفة بدء الصوم كان في تخيير الله - عز وجل - عباده المؤمنين بين الصوم والإطعام، ونسخ ذلك بإيجاب الصوم عليهم من غير تخيير','913','1318','0');
INSERT INTO [t121382] VALUES (1341,'(٢٣) باب ذكر ما كان الصائم عنه ممنوعا بعد النوم في ليل الصوم من الأكل والشرب والجماع عند ابتداء فرض الصيام','914','1318','0');
INSERT INTO [t121382] VALUES (1342,'(٢٤) باب الأمر بالصيام لرؤية الهلال إذا لم يغم على الناس','914','1318','0');
INSERT INTO [t121382] VALUES (1343,'(٢٥) باب ذكر البيان أن الله - جل وعلا - جعل الأهلة مواقيت للناس لصومهم وفطرهم، إذ قد أمر الله على لسان نبيه عليه السلام بصوم شهر رمضان لرؤيته والفطر لرؤيته ما لم يغم، قال الله - عز وجل -: (يسألونك عن الأهلة قل هي مواقيت للناس) الآية [البقرة: ١٨٩].','915','1318','0');
INSERT INTO [t121382] VALUES (1344,'(٢٦) باب الأمر بالتقدير للشهر إذا غم على الناس','915','1318','0');
INSERT INTO [t121382] VALUES (1345,'(٢٧) باب ذكر الدليل على أن الأمر بالتقدير للشهر إذا غم أن يعد شعبان ثلاثين يوما، ثم يصام','915','1318','0');
INSERT INTO [t121382] VALUES (1346,'(٢٨) باب ذكر الدليل على ضد قول من زعم أن النبي - صلى الله عليه وسلم - إنما أمر بإكمال ثلاثين يوما لصوم شهر رمضان دون إكمال ثلاثين يوما لشعبان','916','1318','0');
INSERT INTO [t121382] VALUES (1347,'(٢٩) باب الزجر عن الصيام لرمضان قبل مضي ثلاثين يوما لشعبان إذا لم ير الهلال','917','1318','0');
INSERT INTO [t121382] VALUES (1348,'(٣٠) باب التسوية بين الزجر عن صيام رمضان قبل رؤية هلال رمضان إذا لم يغم الهلال، وبين الزجر عن إفطار رمضان قبل رؤية هلال شوال إذا لم يغم الهلال. والدليل على أن الصائم لرمضان إذا غم الهلال قبل مضي ثلاثين يوما لشعبان عاص كالمفطر قبل مضي ثلاثين يوما لرمضان إذا غم الهلال','918','1318','0');
INSERT INTO [t121382] VALUES (1349,'(٣١) باب الزجر عن صوم اليوم الذي يشك فيه، أمن رمضان أ [م] من شعبان، بلفظ مجمل غير مفسر','918','1318','0');
INSERT INTO [t121382] VALUES (1350,'(٣٢) باب ذكر الدليل على أن الهلال يكون لليلة التي يرى صغر أو كبر ما لم تمض ثلاثون يوما للشهر، ثم لا يرى الهلال لغيم أو سحاب','918','1318','0');
INSERT INTO [t121382] VALUES (1351,'(٣٣) باب الدليل على أن الواجب على أهل كل بلدة صيام رمضان لرؤيتهم لا رؤية غيرهم','919','1318','0');
INSERT INTO [t121382] VALUES (1352,'(٣٤) باب ذكر أخبار رويت عن النبي - صلى الله عليه وسلم - في أن الشهر تسع وعشرون، بلفظ عام مراده خاص','919','1318','0');
INSERT INTO [t121382] VALUES (1353,'(٣٥) باب ذكر الدليل على خلاف ما توهمه العامة والجهال أن الهلال إذا كان كبيرا مضيئا أنه لليلة الماضية لا لليلة المستقبلة','920','1318','0');
INSERT INTO [t121382] VALUES (1354,'(٣٦) باب ذكر إعلام النبي - صلى الله عليه وسلم - أمته أن الشهر تسع وعشرون بإشارة لا بنطق، مع إعلامه إياهم أنه أمي لا يكتب ولا يحسب، صلى الله عليه وسلم، مع الدليل على أن الإشارة المفهومة من الناطق تقوم مقام النطق في الحكم كهي من الأخرس','920','1318','0');
INSERT INTO [t121382] VALUES (1355,'(٣٧) باب ذكر الخبر المفسر للفظة المجملة التي ذكرتها، والدليل على أن النبي - عليه الصلاة والسلام - أراد بقوله: "الشهر تسع وعشرون" بعض الشهور لا كلها، والدليل على أن قوله: "الشهر تسع وعشرون" أراد: أي قد يكون تسعا وعشرين','921','1318','0');
INSERT INTO [t121382] VALUES (1356,'(٣٨) باب الدليل [على] أن صيام تسع وعشرين لرمضان كان على عهد النبي - صلى الله عليه وسلم - أكثر من صيام ثلاثين، خلاف ما يتوهم بعض الجهال والرعاع أن الواجب أن يصام لكل رمضان ثلاثين يوما كوامل','921','1318','0');
INSERT INTO [t121382] VALUES (1357,'(٣٩) باب إجازة شهادة الشاهد الواحد على رؤية الهلال','922','1318','0');
INSERT INTO [t121382] VALUES (1358,'(٤٠) باب ذكر البيان أن الله - عز وجل - أراد بقوله: (حتى يتبين لكم الخيط الأبيض من الخيط الأسود من الفجر) بيان بياض النهار من الليل فوقع اسما لخيط على بياض النهار وعلى سواد الليل، وهذا من الجنس الذي كنت أعلم أن العرب لم تكن تعرفها في معناها، وأن الله - عز وجل - إنما أنزل الكتاب بلغتهم لا بمعانيهم. فالخيط لغتهم، وإيقاع هذا الاسم على بياض النهار وسواد الليل لم يكن من معانيهم التي يفهمونها حتى أعلمهم - صلى الله عليه وسلم','922','1318','0');
INSERT INTO [t121382] VALUES (1359,'(٤١) باب الدليل على أن الفجر هما فجران، وأن طلوع الثاني منهما هو المحرم على الصائم الأكل والشرب والجماع لا الأول، وهذا من الجنس الذي أعلمت أن الله - عز وجل - ولى نبيه عليه السلام البيان عنه عز وجل','923','1318','0');
INSERT INTO [t121382] VALUES (1360,'(٤٢) باب صفة الفجر الذي ذكرناه وهو المعترض لا المستطيل','924','1318','0');
INSERT INTO [t121382] VALUES (1361,'(٤٣) باب الدليل على أن الفجر الثاني الذي ذكرناه هو البياض المعترض الذي لونه الحمرة إن صح الخبر، فإني لا أعرف عبد الله بن النعمان هذا بعدالة ولا جرح، ولا أعرف له عنه راويا غير ملازم بن عمرو','924','1318','0');
INSERT INTO [t121382] VALUES (1362,'(٤٤) باب الدليل على أن الأذان قبل الفجر لا يمنع الصائم طعامه، ولا شرابه، ولا جماعا ضد ما يتوهم العامة','925','1318','0');
INSERT INTO [t121382] VALUES (1363,'(٤٥) باب ذكر قدر ما كان بين أذان بلال وأذان ابن أم مكتوم','925','1318','0');
INSERT INTO [t121382] VALUES (1364,'(٤٦) باب إيجاب الإجماع على الصوم الواجب قبل طلوع الفجر بلفظ عام، مراده خاص','925','1318','0');
INSERT INTO [t121382] VALUES (1365,'(٤٧) باب إيجاب النية لصوم كل يوم قبل طلوع فجر ذلك اليوم، خلاف قول من زعم أن نية واحدة في وقت واحد لجميع الشهر جائز','926','1318','0');
INSERT INTO [t121382] VALUES (1366,'(٤٨) باب الدليل على أن النبي - صلى الله عليه وسلم -[٢٠١ - ب] أراد بقوله: "لا صيام لمن لم يجمع الصيام من الليل"، الواجب من الصيام دون التطوع منه','926','1318','0');
INSERT INTO [t121382] VALUES (1367,'(٤٩) باب الأمر بالسحور أمر ندب وإرشاد إذ السحور بركة، لا أمر فرض وإيجاب، يكون تاركه عاصيا بتركه','926','1318','0');
INSERT INTO [t121382] VALUES (1368,'(٥٠) باب ذكر الدليل أن السحور قد يقع عليه اسم الغداء','927','1318','0');
INSERT INTO [t121382] VALUES (1369,'(٥١) باب الأمر بالاستعانة على الصوم بالسحور إن جاز الاحتجاج بخبر زمعة بن صالح فإن في القلب منه لسوء حفظه','927','1318','0');
INSERT INTO [t121382] VALUES (1370,'(٥٢) باب استحباب السحور فصلا من صيام النهار، وصيام أهل الكتاب، والأمر بمخالفتهم إذ هم لا يتسحرون','928','1318','0');
INSERT INTO [t121382] VALUES (1371,'(٥٣) باب تأخير السحور','928','1318','0');
INSERT INTO [t121382] VALUES (1372,'(٥٤) باب ذكر المفطر بالجماع في نهار الصيام','929','1318','0');
INSERT INTO [t121382] VALUES (1373,'(٥٥) باب إيجاب الكفارة على المجامع في الصوم في رمضان بالعتق','929','1318','0');
INSERT INTO [t121382] VALUES (1374,'(٥٦) باب إعطاء الإمام المجامع في رمضان نهارا ما يكفر به إذا لم يكن واجدا للكفارة، مع الدليل على أن المجامع في رمضان نهارا إذا كان غير واجد للكفارة وقت الجماع، ثم استفاد ما به يكفر، كانت الكفارة واجبة عليه','930','1318','0');
INSERT INTO [t121382] VALUES (1375,'(٥٧) باب ذكر خبر روي مختصرا وهم بعض العلماء من الحجازيين أن المجامع في رمضان نهارا جائز له أن يكفر بالإطعام، وإن كان [٢٠٢ - ب] واجدا لعتق رقبة، مستطيعا لصوم شهرين متتابعين','931','1318','0');
INSERT INTO [t121382] VALUES (1376,'(٥٨) باب ذكر الدليل على أن النبي - صلى الله عليه وسلم -، إنما أمر هذا المجامع بالصدقة بعد أن أخبره أنه لا يجد عتق رقبة، ويشبه أن يكون قد أعلم أيضا أنه غير مستطيع لصوم شهرين متتابعين كأخبار أبي هريرة فاختصر الخبر','931','1318','0');
INSERT INTO [t121382] VALUES (1377,'(٥٩) باب الدليل على أن المجامع في رمضان إذا ملك ما يطعم ستين مسكينا؛ ولم يملك معه قوت نفسه وعياله. لم تجب عليه الكفارة','933','1318','0');
INSERT INTO [t121382] VALUES (1378,'(٦٠) باب الأمر بالاستغفار للمعصية التي ارتكبها المجامع في صوم رمضان إذا لم يجد الكفارة بعتق ولا بإطعام، ولا يستطيع صوم شهرين متتابعين، والأمر بإطعام التمر في كفارة الجماع في رمضان','934','1318','0');
INSERT INTO [t121382] VALUES (1379,'(٦١) باب ذكر قدر مكتل التمر لإطعام ستين مسكينا في كفارة الجماع في صوم رمضان','934','1318','0');
INSERT INTO [t121382] VALUES (1380,'(٦٢) باب الدليل على خلاف قول من زعم أن إطعام مسكين واحد طعام ستين مسكينا في ستين يوما، كل يوم طعام مسكين جائز في كفارة الجماع في صوم رمضان، فلم يميز بين إ','935','1318','0');
INSERT INTO [t121382] VALUES (1381,'(٦٣) باب الدليل على أن صيام الشهرين في كفارة الجماع لا يجوز متفرقا، إنما يجب صيام شهرين متتابعين','935','1318','0');
INSERT INTO [t121382] VALUES (1382,'(٦٤) باب الدليل على أن المجامع إذا وجب عليه صيام شهرين متتابعين ففرط في الصيام، حتى تنزل به المنية، قضي الصوم عنه، كالدين يكون عليه مع الدليل على أن دين الله أحق بالقضاء من ديون العباد','936','1318','0');
INSERT INTO [t121382] VALUES (1383,'(٦٥) باب أمر المجامع بقضاء صوم يوم مكان اليوم الذي جامع فيه إذا لم يكن واجدا للكفارة التي ذكرتها قبل إن صح الخبر، فإن في القلب من هذه اللفظة','936','1318','0');
INSERT INTO [t121382] VALUES (1384,'(٦٦) باب ذكر البيان أن الاستقاء على العمد يفطر الصائم','937','1318','0');
INSERT INTO [t121382] VALUES (1385,'(٦٧) باب ذكر إيجاب قضاء الصوم عن المستقيء عمدا، وإسقاط القضاء عمن يذرعه القيء. والدليل على أن إيجاب الكفارة على المجامع لا لعلة الفطر فقط، إذ لو كان لعلة الفطر فقط لا للجماع خاصة، كان على كل مفطر الكفارة، والمستقيء عمدا مفطر بحكم النبي - صلى الله عليه وسلم -، والكفارة غير واجبة عليه','938','1318','0');
INSERT INTO [t121382] VALUES (1386,'(٦٨) باب ذكر البيان أن الحجامة تفطر الحاجم والمحجوم جميعا','939','1318','0');
INSERT INTO [t121382] VALUES (1387,'(٦٩) باب ذكر الدليل على أن السعوط وما يصل إلى الأنواف من المنخرين يفطر الصائم','948','1318','0');
INSERT INTO [t121382] VALUES (1388,'(٧٠) باب ذكر تعليق المفطرين قبل وقت الإفطار بعراقيبهم وتعذيبهم في الآخرة بفطرهم قبل تحلة صومهم','948','1318','0');
INSERT INTO [t121382] VALUES (1389,'(٧١) باب التغليظ في إفطار يوم من رمضان متعمدا من غير رخصة إن صح الخبر، فإني لا أعرف ابن المطوس ولا أباه غير [أن] حبيب بن أبي ثابت قد ذكر أنه لقي أبا ا','949','1318','0');
INSERT INTO [t121382] VALUES (1390,'(٧٢) باب ذكر البيان أن الآكل والشارب ناسيا لصيامه غير مفطر بالأكل والشرب','950','1318','0');
INSERT INTO [t121382] VALUES (1391,'(٧٣) باب ذكر إسقاط القضاء والكفارة عن الآكل والشارب في الصيام إذا كان ناسيا لصيامه وقت الأكل والشرب','950','1318','0');
INSERT INTO [t121382] VALUES (1392,'(٧٤) باب ذكر الفطر قبل غروب الشمس إذا حسب الصائم أنها قد غربت','951','1318','0');
INSERT INTO [t121382] VALUES (1393,'(٧٥) باب النهي عن الجهل في الصيام','951','1318','0');
INSERT INTO [t121382] VALUES (1394,'(٧٦) باب الزجر عن السباب والاقتتال في الصيام، وإن سب الصائم أو قوتل، وإعلام الصائم مقاتله وسابه أنه صائم لعله ينزجر عن قتاله وسبابه إذا علم أنه لا ينتصر منه لعلة صومه','952','1318','0');
INSERT INTO [t121382] VALUES (1395,'(٧٧) باب الأمر بالجلوس إذا شتم الصائم، وهو قائم لتسكين الغضب على المشتوم فلا ينتصر بالجواب','952','1318','0');
INSERT INTO [t121382] VALUES (1396,'(٧٨) باب النهي عن قول الزور والعمل به، والجهل في الصوم، والتغليظ فيه','952','1318','0');
INSERT INTO [t121382] VALUES (1397,'(٧٩) باب النهي عن اللغو في الصيام، والدليل على أن الإمساك عن اللغو والرفث من تمام الصوم','953','1318','0');
INSERT INTO [t121382] VALUES (1398,'(٨٠) باب نفي ثواب الصوم عن الممسك عن الطعام والشراب مع ارتكابه ما زجر عنه غير الأكل والشرب','953','1318','0');
INSERT INTO [t121382] VALUES (1399,'(٨١) باب الرخصة في المباشرة التي هي دون الجماع للصائم، والدليل على أن اسم الواحد قد يقع على فعلين، أحدهما مباح، والآخر محظور، إذ اسم المباشرة قد أوقعه الله في نص كتابه على الجماع، ودل الكتاب على أن الجماع في الصوم محظور. قال المصطفى - صلى الله عليه وسلم -: "إن الجماع يفطر الصائم". والنبي المصطفى - صلى الله عليه وسلم - قد دل بفعله على أن المباشرة التي هي دون الجماع مباحة في الصوم غير مكروهة','954','1318','0');
INSERT INTO [t121382] VALUES (1400,'(٨٢) باب تمثيل النبي - صلى الله عليه وسلم - قبلة الصائم بالمضمضة منه بالماء','956','1318','0');
INSERT INTO [t121382] VALUES (1401,'(٨٣) باب الرخصة في قبلة الصائم','956','1318','0');
INSERT INTO [t121382] VALUES (1402,'(٨٤) باب الرخصة في قبلة الصائم رؤوس النساء ووجوههن خلاف مذهب من كان يكره ذلك','957','1318','0');
INSERT INTO [t121382] VALUES (1403,'(٨٥) باب الرخصة في مص الصائم لسان المرأة خلاف مذهب من كره القبلة للصائم على الفم إن جاز الاحتجاج بمصدع أبي يحيى، فإني لا أعرفه بعدالة ولا جرح','957','1318','0');
INSERT INTO [t121382] VALUES (1404,'(٨٦) باب الرخصة في قبلة الصائم المرأة الصائمة','958','1318','0');
INSERT INTO [t121382] VALUES (1405,'(٨٧) باب ذكر الدليل على أن القبلة للصائم مباحة لجميع الصوام، ولم تكن خاصة للنبي - صلى الله عليه وسلم','958','1318','0');
INSERT INTO [t121382] VALUES (1406,'(٨٨) باب الرخصة في السواك للصائم','958','1318','0');
INSERT INTO [t121382] VALUES (1407,'(٨٩) باب الرخصة في اكتحال الصائم إن صح الخبر، وإن [لم] يصح الخبر من جهة النقل فالقرآن دال على إباحته، وهو قول [الله عز وجل] (فالآن باشروهن) الآية [البقره:١٨٧]. دال على إباحة الكحل للصائم','959','1318','0');
INSERT INTO [t121382] VALUES (1408,'(٩٠) باب إباحة ترك الجنب الاغتسال من الجنابة إلى طلوع الفجر إذا كان مريدا للصوم','960','1318','0');
INSERT INTO [t121382] VALUES (1409,'(٩١) باب ذكر خبر روي في الزجر عن الصوم إذا أدرك الجنب الصبح قبل [أن] يغتسل لم يفهم معناه بعض العلماء، فأنكر الخبر، وتوهم أن أبا هريرة مع جلالته ومكانه من العلم غلط في روايته.','960','1318','0');
INSERT INTO [t121382] VALUES (1410,'(٩٢) باب الدليل على أن جنابة النبي - صلى الله عليه وسلم - التي أخر الغسل بعدها إلى طلوع الفجر فصام كان من جماع لا من احتلام','962','1318','0');
INSERT INTO [t121382] VALUES (1411,'(٩٣) باب الدليل على أن الصوم جائز لكل من أصبح جنبا واغتسل بعد طلوع الفجر، والزجر عن أن يقال: كان هذا خاصا للنبي - صلى الله عليه وسلم -، مع الدليل على أن كل ما فعله النبي - صلى الله عليه وسلم - مما لم يخبر أنه خاص له، فعلى الناس التأسي به واتباعه - صلى الله عليه وسلم -','963','1318','0');
INSERT INTO [t121382] VALUES (1412,'(٩٤) باب ذكر خبر روي عن النبي - صلى الله عليه وسلم - في الصوم في السفر بلفظة مختصرة من غير ذكر السبب الذي قال له تلك المقالة','964','1318','0');
INSERT INTO [t121382] VALUES (1413,'(٩٥) باب ذكر السبب الذي قال النبي - صلى الله عليه وسلم -: "ليس من البر الصيام في السفر"','965','1318','0');
INSERT INTO [t121382] VALUES (1414,'(٩٦) باب ذكر خبر روي عن النبي - صلى الله عليه وسلم - في تسمية الصوم في السفر عصاة من غير ذكر العلة التي أسماهم بهذا الاسم، توهم بعض العلماء أن الصوم في السفر غير جائز لهذا الخبر','966','1318','0');
INSERT INTO [t121382] VALUES (1415,'(٩٧) باب الدليل على أن النبي - صلى الله عليه وسلم - إنما سماهم عصاة إذ أمرهم بالإفطار وصاموا','966','1318','0');
INSERT INTO [t121382] VALUES (1416,'(٩٨) باب الدليل على أن النبي - صلى الله عليه وسلم - إنما أمر أصحابه بالفطر عام فتح مكة إذ الفطر أقوى لهم على الحرب، لا أن الصوم في السفر غير جائز','967','1318','0');
INSERT INTO [t121382] VALUES (1417,'(٩٩) باب التغليظ في ترك سنة النبي - صلى الله عليه وسلم - رغبة عنها، وجائز أن يسمى تارك السنة عاصيا إذا تركها رغبة عنها، لا بتركها، إذ الترك غير معصية، و','968','1318','0');
INSERT INTO [t121382] VALUES (1418,'(١٠٠) باب ذكر إسقاط فرض الصوم عن المسافر، إذ هو مباح له الفطر في السفر على أن يصوم في الحضر من أيام أخر، [لا] أن الفرض ساقط عنه، لا تجب عليه إعادته. قال الله - عز وجل -: (فمن كان منكم مريضا أو على سفر فعدة من أيام أخر) [البقرة: ١٨٤].','969','1318','0');
INSERT INTO [t121382] VALUES (1419,'(١٠١) باب ذكر البيان أن الفطر في السفر رخصة، لا أن حتما عليه أن يفطر','969','1318','0');
INSERT INTO [t121382] VALUES (1420,'(١٠٢) باب استحباب الفطر في السفر في رمضان لقبول رخصة الله التي رخص لعباده المؤمنين، إذ الله يحب قابل رخصته','969','1318','0');
INSERT INTO [t121382] VALUES (1421,'(١٠٣) باب ذكر تخيير المسافر بين الصوم والفطر، إذ الفطر رخصة والصوم جائز، مع الدليل على أن قوله: "ليس البر" و"ليس من البر الصوم في السفر" على ما تأولت، لأن الصوم في السفر ليس من البر، إذ ما ليس من البر، فمعصية، ولو كان الصوم في السفر معصية، لما جعل للمسافر الخيار بين الطاعة والمعصية، والنبي - صلى الله عليه وسلم - خير المسافر بين الصوم والإفطار','970','1318','0');
INSERT INTO [t121382] VALUES (1422,'(١٠٤) باب استحباب الصوم في السفر لمن قوي عليه، والفطر لمن ضعف عنه','970','1318','0');
INSERT INTO [t121382] VALUES (1423,'(١٠٥) باب استحباب الفطر في السفر إذا عجز عن خدمة نفسه إذا صام','971','1318','0');
INSERT INTO [t121382] VALUES (1424,'(١٠٦) باب ذكر الدليل على أن المفطر الخادم في السفر أفضل من الصائم المخدوم في السفر','972','1318','0');
INSERT INTO [t121382] VALUES (1425,'(١٠٧) باب الرخصة في صوم بعض رمضان، وفطر بعض في السفر','972','1318','0');
INSERT INTO [t121382] VALUES (1426,'(١٠٨) باب ذكر خبر توهم بعض العلماء أن الفطر في السفر ناسخ لإباحة الصوم في السفر','973','1318','0');
INSERT INTO [t121382] VALUES (1427,'(١٠٩) باب ذكر البيان على أن هذه الكلمة "وإنما يؤخذ بالآخر" ليس من قول ابن عباس','973','1318','0');
INSERT INTO [t121382] VALUES (1428,'(١١٠) باب ذكر دليل ثان على أن أمر النبي - صلى الله عليه وسلم - بالفطر عام الفتح لم يكن بناسخ لإباحته الصوم في السفر','974','1318','0');
INSERT INTO [t121382] VALUES (1429,'(١١١) باب الرخصة في الفطر في رمضان في السفر لمن قد صام بعضه في الحضر، خلاف مذهب من أوجب عليه الصوم في السفر إذا كان قد صام بعضه في الحضر، توهم أن قوله: (فمن شهد منكم الشهر فليصمه) [البقرة: ١٨٥] أن من شهد بعض الشهر وهو حاضر غير مسافر فوجب عليه صوم جميع الشهر وإن سافر في بعضه','974','1318','0');
INSERT INTO [t121382] VALUES (1430,'(١١٢) باب إباحة الفطر في رمضان في السفر يوما قد مضى بعضه، والمرء ناو للصوم فيه','975','1318','0');
INSERT INTO [t121382] VALUES (1431,'(١١٣) باب إباحة الفطر في اليوم الذي يخرج فيه المرء فيه مسافرا من بلده، إن ثبت الخبر، ضد مذهب من زعم أنه إذا دخل في الصوم مقيما، ثم سافر لم يجز له الفطر، وإباحة الفطر إذا جاوز المرء بيوت البلدة التي يخرج منها وإن كان قريبا يرى بيوتها','975','1318','0');
INSERT INTO [t121382] VALUES (1432,'(١١٤) باب الرخصة في الفطر في رمضان في مسيرة أقل من يوم وليلة، إن ثبت الخبر، فإني لا أعرف منصور بن زيد الكلبي هذا بعدالة ولا جرح','976','1318','0');
INSERT INTO [t121382] VALUES (1433,'(١١٥) باب الرخصة للحامل والمرضع في الإفطار في رمضان، والبيان أن فرض الصوم ساقط عنهما في رمضان على أن يقضيا من أيام أخر، إذ النبي - صلى الله عليه وسلم - قرنهما، أو إحديهما إلى المسافر، فجعل حكمهما أو حكم إحديهما حكم المسافر','977','1318','0');
INSERT INTO [t121382] VALUES (1434,'(١١٦) باب ذكر إسقاط فرض الصوم عن النساء أيام حيضهن','978','1318','0');
INSERT INTO [t121382] VALUES (1435,'(١١٧) باب ذكر الدليل على أن الحائض يجب عليها قضاء الصوم في أيام طهرها، والرخصة لها في تأخير قضاء الصوم الذي أسقط الفرض عنها في أيام حيضها إلى شعبان','979','1318','0');
INSERT INTO [t121382] VALUES (1436,'(١١٨) باب قضاء ولي الميت صوم رمضان عن الميت إذا مات وأمكنه القضاء ففرط في قضائه','981','1318','0');
INSERT INTO [t121382] VALUES (1437,'(١١٩) باب قضاء الصيام عن المرأة تموت وعليها صيام، والدليل [على] أن الصائم إذا قضى الحي عن الميت يكون ساقطا عن الميت، كالدين يقضى عنه بعد الموت، إذ النبي - صلى الله عليه وسلم - شبه قضاء الصوم عن الميت بقضاء الدين عنها','981','1318','0');
INSERT INTO [t121382] VALUES (1438,'(١٢٠) باب الأمر بقضاء الصوم بالنذر عن الناذرة إذا ماتت قبل الوفاء بنذرها','982','1318','0');
INSERT INTO [t121382] VALUES (1439,'(١٢١) باب ذكر البيان أن من قضى الصوم عن الناذر والناذرة من ولي أو قريب أو بعيد أو ذكر أو أنثى أو حر أو عبد أو حرة أو أمة فالقضاء جائز عن الميت','982','1318','0');
INSERT INTO [t121382] VALUES (1440,'(١٢٢) باب الإطعام عن الميت يموت وعليه صوم لكل يوم مسكينا إن صح الخبر، فإن في القلب من أشعث بن سوار رحمه الله لسوء حفظه','983','1318','0');
INSERT INTO [t121382] VALUES (1441,'(١٢٣) باب قدر مكيلة ما يطعم كل مسكين في كفارة الصوم إن ثبت الخبر، فإن في القلب من هذا الإسناد','983','1318','0');
INSERT INTO [t121382] VALUES (1442,'(١٢٤) باب ذكر خبر روي عن النبي - صلى الله عليه وسلم - في وقت الفطر بلفظ خبر معناه عندي معنى الأمر','983','1318','0');
INSERT INTO [t121382] VALUES (1443,'(١٢٥) باب ذكر دوام الناس على الخير ما عجلوا الفطر، وفيه كالدلالة على أنهم إذا أخروا الفطر وقعوا في الشر','984','1318','0');
INSERT INTO [t121382] VALUES (1444,'(١٢٦) باب ذكر ظهور الدين ما عجل الناس فطرهم، والدليل على أن اسم الدين قد يقع على بعض شعب الإسلام','985','1318','0');
INSERT INTO [t121382] VALUES (1445,'(١٢٧) باب ذكر استحسان سنة المصطفى محمد - صلى الله عليه وسلم - ما لم ينتظر بالفطر قبل طلوع النجوم','985','1318','0');
INSERT INTO [t121382] VALUES (1446,'(١٢٨) باب ذكر حب الله - عز وجل - المعجلين للإفطار، والدليل على ضد قول بعض أهل عصرنا ممن زعم أنه غير جائز أن يقال: أحب العباد إلى الله أعجلهم فطرا، إلا أن يكون الله يحب جميع عباده. وخالفنا في باب أفعل فادعى ما لا يحسنه. فقد بينت باب أفعل في غير موضع من كتبنا في كتاب "معاني القرآن" والكتب المصنفة من المسند','986','1318','0');
INSERT INTO [t121382] VALUES (1447,'(١٢٩) باب استحباب الفطر قبل صلاة المغرب','986','1318','0');
INSERT INTO [t121382] VALUES (1448,'(١٣٠) باب إعطاء مفطر الصائم مثل أجر الصائم من غير أن ينتقص الصائم من أجره شيئا','987','1318','0');
INSERT INTO [t121382] VALUES (1449,'(١٣١) باب استحباب الفطر على الرطب إذا وجد، وعلى التمر إذا لم يوجد الرطب','987','1318','0');
INSERT INTO [t121382] VALUES (1450,'(١٣٢) باب استحباب الفطر على الماء إذا أعوز الصائم الرطب والتمر جميعا','988','1318','0');
INSERT INTO [t121382] VALUES (1451,'(١٣٣) باب الدليل على أن الأمر بالفطر على التمر إذا كان موجودا أمر اختيار واستحباب طالما للبركة إذ التمر بركة، وأن الأمر بالفطر على الماء إذا أعوز التمر أمر استحباب واختيار [٢١٣ - أ] إذ الماء طهور، لا أن الأمر بذلك أمر فرض وإيجاب','988','1318','0');
INSERT INTO [t121382] VALUES (1452,'(١٣٤) باب الزجر عن الوصال في الصوم، وذكر ما خص الله به نبيه - صلى الله عليه وسلم - من إباحة الوصال إذ الله - تبارك وتعالى - فرق بينه وبين أمته في ذلك أن كان الله يطعمه ويسقيه بالليل دونهم مكرمة له - صلى الله عليه وسلم -','989','1318','0');
INSERT INTO [t121382] VALUES (1453,'(١٣٥) باب تسمية الوصال بتعمق في الدين','990','1318','0');
INSERT INTO [t121382] VALUES (1454,'(١٣٦) باب الدليل على أن الوصال منهي عنه، إذ ذلك يشق على المرء، خلاف ما يتأوله بعض المتصوفة ممن يفطر على اللقمة أو الجرعة من الماء فيعذب نفسه ليالي وأياما','990','1318','0');
INSERT INTO [t121382] VALUES (1455,'(١٣٧) باب النهي عن الوصال إلى السحر، إذ تعجيل الفطر أفضل من تأخيره، إن كان الوصال إلى السحر قد أباحه المصطفى - صلى الله عليه وسلم','991','1318','0');
INSERT INTO [t121382] VALUES (1456,'(١٣٨) باب إباحة الوصال إلى السحر وإن كان تعجيل الفطر أفضل','991','1318','0');
INSERT INTO [t121382] VALUES (1457,'(١٣٩) باب ذكر الدليل عن أن لا فرض على المسلمين من الصيام غير رمضان إلا ما يجب عليهم بأفعالهم وأقوالهم','991','1318','0');
INSERT INTO [t121382] VALUES (1458,'(١٤٠) باب الزجر عن قول المرء: "صمت رمضان كله"','992','1318','0');
INSERT INTO [t121382] VALUES (1459,'(١٤١) باب فضل الصوم في المحرم إذ هو أفضل الصيام بعد شهر رمضان','992','1318','0');
INSERT INTO [t121382] VALUES (1460,'(١٤٢) باب استحباب صوم شعبان ووصله بشهر رمضان، إذ كان أحب الشهور إلى النبي - صلى الله عليه وسلم - أن يصومه','992','1318','0');
INSERT INTO [t121382] VALUES (1461,'(١٤٣) باب إباحة وصل صوم شعبان بصوم رمضان، والدليل على أن معنى خبر أبي هريرة عن النبي - صلى الله عليه وسلم -: "إذا انتصف شعبان فلا تصوموا حتى رمضان"، أي ألا تواصلوا شعبان برمضان فتصوموا جميع شعبان، أو أن يوافق ذلك صوما كان يصومه المرء قبل ذاك فيصوم ذلك الصيام بعد النصف من شعبان، لا أنه نهى عن الصوم إذا انتصف شعبان نهيا مطلقا','993','1318','0');
INSERT INTO [t121382] VALUES (1462,'(١٤٤) باب بدء النبي - صلى الله عليه وسلم - بصيام عاشوراء وصامه','994','1318','0');
INSERT INTO [t121382] VALUES (1463,'(١٤٥) باب الدليل على أن بدء صيام عاشوراء كان قبل فرض صوم شهر رمضان','994','1318','0');
INSERT INTO [t121382] VALUES (1464,'(١٤٦) باب ذكر الدليل على أن ترك النبي - صلى الله عليه وسلم - صوم عاشوراء بعد','994','1318','0');
INSERT INTO [t121382] VALUES (1465,'(١٤٧) باب ذكر خبر غلط في معناه عالم ممن لم يفهم معنى الخبر، وتوهم أن الأمر لصوم عاشوراء جميعا منسوخ بفرض صوم رمضان','995','1318','0');
INSERT INTO [t121382] VALUES (1466,'(١٤٨) باب علة أمر النبي - صلى الله عليه وسلم - بصيام عاشوراء بعد مقدمه المدينة','996','1318','0');
INSERT INTO [t121382] VALUES (1467,'(١٤٩) باب الدليل على أن أمر النبي - صلى الله عليه وسلم - بصيام عاشوراء لم يكن بأمر فرض وإيجاب بدءا ولا عددا، وأنه كان أمر فضيلة واستحباب','997','1318','0');
INSERT INTO [t121382] VALUES (1468,'(١٥٠) باب فضيلة صيام عاشوراء وتحري النبي - صلى الله عليه وسلم - صيامه لفضله من بين الأيام خلا صيام رمضان','998','1318','0');
INSERT INTO [t121382] VALUES (1469,'(١٥١) باب ذكر تكفير الذنوب بصيام عاشوراء، والبيان أن العمل الصالح يتقدم الفعل، الشيء يكون بعده، فيكفر العمل الصالح الذنوب، تكون بعد العمل الصالح، لا كما يتوهم من خالفنا في تقديم كفارة اليمين قبل الحنث، وزعم أنه غير جائز أن يتقدم المرء عملا صالحا يكفر ذنبا يكون بعده','998','1318','0');
INSERT INTO [t121382] VALUES (1470,'(١٥٢) باب استحباب ترك الأمهات إرضاع الأطفال يوم عاشوراء تعظيما ليوم عاشوراء، إن صح الخبر. فإن في القلب من خالد بن ذكوان','999','1318','0');
INSERT INTO [t121382] VALUES (1471,'(١٥٣) باب الأمر بصيام يوم عاشوراء، وإن أصبح المرء غير ناو للصيام غير مجمع على الصيام من الليل. والدليل [على] أن النبي - صلى الله عليه وسلم - إنما أراد بقوله: "لا صيام لمن لا يجمع الصيام من الليل" صوم الواجب دون صوم التطوع','1000','1318','0');
INSERT INTO [t121382] VALUES (1472,'(١٥٤) باب الأمر بصيام بعض يوم عاشوراء إذا لم يعلم المرء بيوم عاشوراء قبل [أن] يطعم والفرق في الصوم بين عاشوراء وبين غيره، إذ صوم بعض يوم لا يكون صوما في غير يوم عاشوراء، لما خص النبي - صلى الله عليه وسلم - به يوم عاشوراء، فأمر بصوم بعض ذلك اليوم وإن كان المرء قد طعم أول النهار','1000','1318','0');
INSERT INTO [t121382] VALUES (1473,'(١٥٥) باب ذكر التخيير بين صيام عاشوراء وإفطاره، والدليل على أن الأمر بصوم يوم عاشوراء أمر ندب وإرشاد وفضيلة','1001','1318','0');
INSERT INTO [t121382] VALUES (1474,'(١٥٦) باب الأمر بأن يصام قبل عاشوراء يوما أو بعده يوما، مخالفة لفعل اليهود في صوم عاشوراء','1001','1318','0');
INSERT INTO [t121382] VALUES (1475,'(١٥٧) باب استحباب صوم يوم التاسع من المحرم اقتداء بالنبي - صلى الله عليه وسلم -','1002','1318','0');
INSERT INTO [t121382] VALUES (1476,'(١٥٨) باب فضل صوم يوم عرفة وتكفير الذنوب بلفظ خبر مجمل غير مفسر','1002','1318','0');
INSERT INTO [t121382] VALUES (1477,'(١٥٩) باب ذكر خبر روي عن النبي - صلى الله عليه وسلم - في النهي عن صوم يوم عرفة مجمل غير مفسر','1002','1318','0');
INSERT INTO [t121382] VALUES (1478,'(١٦٠) باب ذكر خبر مفسر للفظتين المجملتين اللتين ذكرتهما، والدليل على أن النبي - صلى الله عليه وسلم - إنما كره صوم عرفة بعرفات لا غيره، وفيه ما دل على أن قوله: صوم يوم عرفة يكفر السنة الماضية، والسنة المستقبلة" بغير عرفات','1003','1318','0');
INSERT INTO [t121382] VALUES (1479,'(١٦١) باب استحباب الإفطار يوم عرفة بعرفات اقتداء بالنبي -صلى الله عليه وسلم-، وتقويا بالفطر على الدعاء، إذ الدعاء يوم عرفة أفضل الدعاء أو من أفضله','1003','1318','0');
INSERT INTO [t121382] VALUES (1480,'(١٦٢) باب ذكر إفطار النبي - صلى الله عليه وسلم - في عشر ذي الحجة','1003','1318','0');
INSERT INTO [t121382] VALUES (1481,'(١٦٣) باب ذكر علة قد كان النبي - صلى الله عليه وسلم - يترك لها بعض أعمال التطوع وإن كان يحث عليها، وهي خشية أن يفرض عليهم ذلك الفعل مع استحبابه - صلى الله عليه وسلم - ما خفف على الناس من الفرائض','1004','1318','0');
INSERT INTO [t121382] VALUES (1482,'(١٦٤) باب استحباب صوم يوم وإفطار يوم، والإعلام بأنه صوم نبي الله داود - صلى الله عليه وسلم','1004','1318','0');
INSERT INTO [t121382] VALUES (1483,'(١٦٥) باب الإخبار بأن صوم يوم وفطر يوم أفضل الصيام وأحبه إلى الله وأعدله','1005','1318','0');
INSERT INTO [t121382] VALUES (1484,'(١٦٦) باب ذكر الدليل على أن النبي - صلى الله عليه وسلم - إنما خبر أن صيام داود أعدل الصيام وأفضله، وأحبه إلى الله إذ صائم يوم، ومفطر يوم يكون مؤديا لحظ ن','1006','1318','0');
INSERT INTO [t121382] VALUES (1485,'(١٦٧) باب ذكر الدليل على أن داود كان من أعبد الناس إذ كان صومه ما ذكرنا','1007','1318','0');
INSERT INTO [t121382] VALUES (1486,'(١٦٨) باب ذكر تمني النبي - صلى الله عليه وسلم - استطاعة صوم يوم وإفطار يومين','1008','1318','0');
INSERT INTO [t121382] VALUES (1487,'(١٦٩) باب فضل الصوم في سبيل الله، ومباعدة الله المرء يصوم يوما في سبيل الله عن النار سبعين خريفا بذكر خبر مجمل غير مفسر','1008','1318','0');
INSERT INTO [t121382] VALUES (1488,'(١٧٠) باب ذكر الخبر المفسر للفظة المجملة التي ذكرتها والدليل [على] أن صوم اليوم الذي ذكرناه في سبيل الله إنما يباعد الله صائمه به عن النار أنه إذا صامه','1008','1318','0');
INSERT INTO [t121382] VALUES (1489,'(١٧١) باب فضل اتباع صيام رمضان بصيام ستة أيام من شوال، فيكون كصيام السنة كلها','1009','1318','0');
INSERT INTO [t121382] VALUES (1490,'(١٧٢) باب ذكر الدليل على أن النبي - صلى الله عليه وسلم - إنما أعلم أن صيام رمضان وستة أيام من شوال يكون كصيام الدهر، إذ الله - عز وجل - جعل الحسنة بعشر أمثالها أو يزيد إن شاء الله عز وجل','1009','1318','0');
INSERT INTO [t121382] VALUES (1491,'(١٧٣) باب استحباب صوم يوم الاثنين ويوم الخميس، وتحري صومهما، اقتداء بفعل النبي - صلى الله عليه وسلم','1010','1318','0');
INSERT INTO [t121382] VALUES (1492,'(١٧٤) باب استحباب صوم يوم الاثنين [٢١٧ - أ] إذ النبي - صلى الله عليه وسلم - ولد يوم الاثنين، وفيه أوحي إليه، وفيه مات - صلى الله عليه وسلم','1010','1318','0');
INSERT INTO [t121382] VALUES (1493,'(١٧٥) باب في استحباب صوم يوم الاثنين والخميس أيضا، لأن الأعمال فيهما تعرض على الله عز وجل','1011','1318','0');
INSERT INTO [t121382] VALUES (1494,'(١٧٦) باب فضل صوم يوم واحد من كل شهر، وإعطاء الله - عز وجل - صائم','1011','1318','0');
INSERT INTO [t121382] VALUES (1495,'(١٧٧) باب الأمر بصوم ثلاثة أيام من كل شهر استحبابا لا إيجابا','1012','1318','0');
INSERT INTO [t121382] VALUES (1496,'(١٧٨) باب ذكر الدليل على أن الأمر بصوم الثلاث من كل شهر أمر ندب لا أمر فرض','1012','1318','0');
INSERT INTO [t121382] VALUES (1497,'(١٧٩) باب ذكر تفضل الله - عز وجل - على الصائم ثلاثة أيام من كل شهر بإعطائه أجر صيام الدهر بالحسنة الواحدة عشر أمثالها','1013','1318','0');
INSERT INTO [t121382] VALUES (1498,'(١٨٠) باب استحباب صيام هذه الأيام الثلاثة من كل شهر أيام البيض منها','1014','1318','0');
INSERT INTO [t121382] VALUES (1499,'(١٨١) باب إباحة صوم هذه الأيام الثلاثة من كل شهر أول الشهر، مبادرة بصومها خوف أن لا يدرك المرء صومها أيام البيض','1015','1318','0');
INSERT INTO [t121382] VALUES (1500,'(١٨٢) باب ذكر الدليل على أن صوم ثلاثة أيام من كل شهر يقوم مقام صيام الدهر، كان صوم الثلاثة أيام من أول الشهر، أو من وسطه، أو من آخره','1015','1318','0');
INSERT INTO [t121382] VALUES (1501,'(١٨٣) باب ذكر إيجاب الله - عز وجل -[الجنة] للصائم يوما واحدا إذا جمع مع صومه صدقة، وشهود جنازة، وعيادة مريض','1016','1318','0');
INSERT INTO [t121382] VALUES (1502,'(١٨٤) باب في صفة صوم النبي - صلى الله عليه وسلم - خلا ما تقدم ذكرنا له بذكر خبر مجمل غير مفسر','1016','1318','0');
INSERT INTO [t121382] VALUES (1503,'(١٨٥) باب ذكر الخبر المفسر للفظة المجملة التي ذكرتها','1017','1318','0');
INSERT INTO [t121382] VALUES (1504,'(١٨٦) باب ذكر صوم أيام متتابعة من الشهر وإفطار أيام متتابعة بعدها من الشهر','1017','1318','0');
INSERT INTO [t121382] VALUES (1505,'(١٨٧) باب ذكر ما أعد الله - جل وعلا - في الجنة من الغرف لمداوم صيام التطوع إن صح الخبر، فإن في القلب من عبد الرحمن بن إسحاق أبي شيبة الكوفي، وليس هو بعبد الرحمن بن إسحاق الملقب بعباد الذي روى عن سعيد المقبري والزهري وغيرهما هو صالح الحديث، مدني سكن واسط، ثم انتقل إلى البصرة، ولست أعرف ابن معانق ولا أبا معانق الذي روى عنه يحيى بن أبي كثير','1018','1318','0');
INSERT INTO [t121382] VALUES (1506,'(١٨٨) باب ذكر صلاة الملائكة على الصائم عند أكل المفطرين عنده','1019','1318','0');
INSERT INTO [t121382] VALUES (1507,'(١٨٩) باب الرخصة في صوم التطوع وإن لم يجمع المرء على الصوم من الليل، والدليل على أن النبي - صلى الله عليه وسلم - إنما أراد بقوله: "لا صيام لمن لم يجمع الصيام من الليل"، صوم الواجب دون صوم التطوع','1020','1318','0');
INSERT INTO [t121382] VALUES (1508,'(١٩٠) باب إباحة الفطر في صوم التطوع بعد مضي بعض النهار، والمرء ناو للصوم فيما مضى من النهار','1020','1318','0');
INSERT INTO [t121382] VALUES (1509,'(١٩١) باب ذكر الدليل على أن الفطر في صوم التطوع بعد دخوله فيه مجمعا على صوم ذلك اليوم خلاف مذهب من رأى إيجاب إعادة صوم ذلك اليوم عليه','1021','1318','0');
INSERT INTO [t121382] VALUES (1510,'(١٩٢) باب تمثيل الصوم في الشتاء بالغنيمة الباردة، والدليل [على] أن الشيء قد يشبه بما يشبهه في بعض المعاني لا في كلها','1021','1318','0');
INSERT INTO [t121382] VALUES (1511,'(١٩٣) والدليل على أن النبي - صلى الله عليه وسلم - قد ينهى عن الشيء، ويسكت عن غيره غير مبيح لما سكت عنه. إذ النبي - صلى الله عليه وسلم - قد زجر عن صوم يوم الفطر ويوم النحر في الأخبار التي رويت عنه في النهي عن صومهما، ولم يكن في نهيه عن صومهما إباحة صوم أيام التشريق إذ قد نهى أيضا عن صوم أيام التشريق في غير هذه الأخبار التي نهى فيها عن صوم يوم الفطر ويوم الأضحى','1022','1318','0');
INSERT INTO [t121382] VALUES (1512,'(١٩٤) باب النهي عن صوم أيام التشريق بدلالة بتصريح نهي','1022','1318','0');
INSERT INTO [t121382] VALUES (1513,'(١٩٥) باب الزجر عن صيام أيام التشريق بتصريح نهي','1023','1318','0');
INSERT INTO [t121382] VALUES (1514,'(١٩٦) باب ذكر النهي عن صيام الدهر من غير ذكر العلة التي لها نهي عنه','1024','1318','0');
INSERT INTO [t121382] VALUES (1515,'(١٩٧) باب ذكر العلة التي لها زجر النبي - صلى الله عليه وسلم - عن صوم الدهر','1024','1318','0');
INSERT INTO [t121382] VALUES (1516,'(١٩٨) باب الرخصة في صوم الدهر إذا أفطر المرء الأيام التي زجر عن الصيام فيهن','1025','1318','0');
INSERT INTO [t121382] VALUES (1517,'(١٩٩) باب فضل صيام الدهر إذا أفطر الأيام التي زجر عن الصيام فيها','1025','1318','0');
INSERT INTO [t121382] VALUES (1518,'(٢٠٠) باب ذكر أخبار رويت عن النبي -صلى الله عليه وسلم- في النهي عن صوم يوم الجمعة مجملة غير مفسرة','1027','1318','0');
INSERT INTO [t121382] VALUES (1519,'(٢٠١) باب ذكر الخبر المفسر في النهي عن صيام يوم الجمعة والدليل [على] أن النهي عنه إذا أفرد يوم الجمعة بالصيام، من غير أن يصام قبله أو بعده','1027','1318','0');
INSERT INTO [t121382] VALUES (1520,'(٢٠٢) باب الدليل على أن يوم الجمعة يوم عيد، وأن النهي عن صيامه إذ هو عيد، والفرق بين الجمعة وبين العيدين الفطر والأضحى، إذ جاء بنهي صومهما مفردا، ولا','1028','1318','0');
INSERT INTO [t121382] VALUES (1521,'(٢٠٣) باب أمر الصائم يوم الجمعة مفردا بالفطر بعد مضي بعض النهار','1028','1318','0');
INSERT INTO [t121382] VALUES (1522,'(٢٠٤) باب النهي عن صوم يوم السبت تطوعا إذا أفرد بالصوم، بذكر خبر مجمل غير مفسر بلفظ عام مراده خاص. وأحسب أن النهي عن صيامه، إذ اليهود تعظمه، وقد اتخذته عيدا بدل الجمعة','1029','1318','0');
INSERT INTO [t121382] VALUES (1523,'(٢٠٥) باب ذكر الدليل على أن النهي عن صوم يوم السبت تطوعا إذا أفرد بصوم، لا إذا صام صائم [٢٢١ - ب] يوما قبله، أو يوما بعده','1030','1318','0');
INSERT INTO [t121382] VALUES (1524,'(٢٠٦) باب الرخصة في يوم السبت إذا صام يوم الأحد بعده','1030','1318','0');
INSERT INTO [t121382] VALUES (1525,'(٢٠٧) باب النهي عن صوم المرأة تطوعا بغير إذن زوجها إذا كان زوجها حاضرا غير غائب عنها، بذكر خبر لفظه خاص مراده عام، من الجنس الذي نقول: إن الأمر إذا كان لعل','1031','1318','0');
INSERT INTO [t121382] VALUES (1526,'(٢٠٨) باب ذكر أبواب ليلة القدر، والتأليف بين الأخبار المأثورة عن النبي - صلى الله عليه وسلم - فيها ما يحسب كثيرا من حملة العلم ممن لا يفهم صناعة العلم أنها متهاترة متنافية، وليس كذلك هي عندنا بحمد الله ونعمته بل هي مختلفة الألفاظ متفقة المعنى على ما سأبينه إن شاء الله','1032','1318','0');
INSERT INTO [t121382] VALUES (1527,'(٢٠٩) باب ذكر دوام ليلة القدر في كل رمضان إلى قيام الساعة ونفي انقطاعها بنفي الأنباء','1032','1318','0');
INSERT INTO [t121382] VALUES (1528,'(٢١٠) باب ذكر الدليل على أن ليلة القدر هي في رمضان من غير شك ولا ارتياب في غيره، ضد قول من زعم أن الحالف آخر يوم من شعبان أن امرأته طالق، أو عبده حر، أو أمته حرة ليلة القدر أن الطلاق والعتق غير واقع إلى مضي السنة من يوم حلف، لأنه زعم، لا يدري، ليلة القدر هي في رمضان أو في غيره. لقول ابن مسعود: من يقم الحول يصبها','1033','1318','0');
INSERT INTO [t121382] VALUES (1529,'(٢١١) باب ذكر الدليل على أن ليلة القدر في العشر الأواخر من رمضان، خلاف قول من ذكرنا مقالتهم في الباب قبل هذا، والدليل على أن الحالف يوم شهر رمضان قبل غروب الشمس بطرفه بأن امرأته طالق، أو عبده حر، فهل هلال شوال كان الطلاق أو العتق أو هما لو كان الحلف بهما جميعا واقعا، إذ ليلة القدر قد مضت بعد حلفه من غير شك ولا ارتياب، إذ هي في العشر الأواخر من رمضان لا قبل ولا بعد','1034','1318','0');
INSERT INTO [t121382] VALUES (1530,'(٢١٢) باب الأمر بالتماس ليلة القدر وطلبها في العشر الأواخر من رمضان بلفظ مجمل غير مفسر','1035','1318','0');
INSERT INTO [t121382] VALUES (1531,'(٢١٣) باب ذكر الخبر المفسر للفظة المجملة التي ذكرتها، والدليل على أن النبي - صلى الله عليه وسلم - إنما أمر بطلب ليلة القدر في العشر الأواخر من رمضان في الوتر منها لا في الشفع','1036','1318','0');
INSERT INTO [t121382] VALUES (1532,'(٢١٤) باب ذكر الدليل على أن الأمر بطلب ليلة القدر في الوتر مما يبقى من العشر الأواخر، لا في الوتر مما يمضي منها','1036','1318','0');
INSERT INTO [t121382] VALUES (1533,'(٢١٥) باب ذكر الخبر المفسر للدليل الذي ذكرت في طلب ليلة القدر في الوتر مما يبقى من العشر الأواخر لا مما يمضي منها','1037','1318','0');
INSERT INTO [t121382] VALUES (1534,'(٢١٦) باب الدليل على أن الوتر مما يبقى من العشر الأواخر، قد يكون أيضا الوتر مما مضى منه، إذ الشهر قد يكون تسعا وعشرين','1038','1318','0');
INSERT INTO [t121382] VALUES (1535,'(٢١٧) باب ذكر الخبر المفسر للدليل الذي ذكرت، إذ النبي - صلى الله عليه وسلم - قد أمر بطلبها ليلة ثلاث وعشرين مما قد مضى من الشهر وكانت ليلة سابعة مما تبقى','1038','1318','0');
INSERT INTO [t121382] VALUES (1536,'(٢١٨) باب ذكر خبر روي عن النبي - صلى الله عليه وسلم - في الأمر بطلب ليلة القدر في السبع الأواخر، من غير ذكر العلة التي لها أمر بالاقتصار على طلبها في السبع','1039','1318','0');
INSERT INTO [t121382] VALUES (1537,'(٢١٩) باب ذكر الخبر الدال على صحة المعنى الثاني الذي ذكرت أنه أمر بطلبها في السبع الأواخر إذا ضعف وعجز طالبها عن طلبها في العشر كله','1040','1318','0');
INSERT INTO [t121382] VALUES (1538,'(٢٢٠) باب ذكر الدليل على أن ليلة القدر قد كانت في زمن النبي - صلى الله عليه وسلم - في بعض الشهر ليلة إحدى وعشرين في رمضان','1040','1318','0');
INSERT INTO [t121382] VALUES (1539,'(٢٢١) باب ذكر الأمر بطلب ليلة القدر ليلة ثلاث وعشرين، إذ جائز أن تكون ليلة القدر في بعض السنين ليلة إحدى وعشرين وفي بعض ليلة ثلاث وعشرين','1040','1318','0');
INSERT INTO [t121382] VALUES (1540,'(٢٢٢) باب ذكر كون ليلة القدر في بعض السنين ليلة سبع وعشرين، إذ ليلة القدر تنتقل في العشر الأواخر في الوتر على ما ذكرت','1041','1318','0');
INSERT INTO [t121382] VALUES (1541,'(٢٢٣) باب الأمر بطلب ليلة القدر آخر ليلة من رمضان، إذ جائز أن يكون في بعض السنين تلك الليلة','1042','1318','0');
INSERT INTO [t121382] VALUES (1542,'(٢٢٤) باب صفة ليلة القدر بنفي الحر والبرد فيها، وشدة ضوئها، ومنع خروج شياطينها منها حتى يضيء فجرها','1042','1318','0');
INSERT INTO [t121382] VALUES (1543,'(٢٢٥) باب صفة الشمس عند طلوعها صبيحة ليلة القدر','1043','1318','0');
INSERT INTO [t121382] VALUES (1544,'(٢٢٦) باب حمرة الشمس عند طلوعها وضعفها صبيحة ليلة القدر، والاستدلال بصفة الشمس على ليلة القدر إن صح الخبر، فإن في القلب من حفظ زمعة','1044','1318','0');
INSERT INTO [t121382] VALUES (1545,'(٢٢٧) باب الدليل على أن الشمس لا يكون لها شعاع إلى وقت ارتفاعها ذلك اليوم إلى آخر النهار','1044','1318','0');
INSERT INTO [t121382] VALUES (1546,'(٢٢٨) باب ذكر كثرة الملائكة في الأرض ليلة القدر','1044','1318','0');
INSERT INTO [t121382] VALUES (1547,'(٢٢٩) باب ذكر البيان أن المدرك لصلاة العشاء في جماعة ليلة القدر يكون مدركا لفضيلة ليلة القدر','1045','1318','0');
INSERT INTO [t121382] VALUES (1548,'(٢٣٠) باب ذكر إنساء الله - عز وجل - النبي - صلى الله عليه وسلم - ليلة القدر بعد رؤيته إياها','1045','1318','0');
INSERT INTO [t121382] VALUES (1549,'(٢٣١) باب ذكر الدليل على أن رؤية النبي - صلى الله عليه وسلم - ليلة القدر كان في نوم وفي يقظة','1045','1318','0');
INSERT INTO [t121382] VALUES (1550,'(٢٣٢) باب ذكر رجاء النبي - صلى الله عليه وسلم - وظنه أن يكون رفع علمه ليلة القدر خيرا لأمته من اطلاعهم على علمها، إذ الاجتهاد في العمل ليالي طمعا في إدراك ليلة القدر أفضل وأكبر عملا من الاجتهاد [٢٢٤ - ب] في ليلة واحدة خاصة','1046','1318','0');
INSERT INTO [t121382] VALUES (1551,'(٢٣٣) باب مغفرة ذنوب العبد بقيام ليلة القدر إيمانا واحتسابا','1046','1318','0');
INSERT INTO [t121382] VALUES (1552,'(٢٣٤) باب استحباب شهود البدوي الصلاة في مسجد المدينة ليلة ثلاث وعشرين من رمضان إذا كان سكنه قرب المدينة تحريا لإدراك ليلة القدر في مسجدها','1046','1318','0');
INSERT INTO [t121382] VALUES (1553,'(٢٣٥) باب ذكر الدليل على أن قيام شهر رمضان سنة النبي - صلى الله عليه وسلم -، خلاف زعم الروافض الذين يزعمون أن قيام شهر رمضان بدعة لا سنة','1047','1318','0');
INSERT INTO [t121382] VALUES (1554,'(٢٣٦) باب الأمر بقيام رمضان أمر ترغيب لا أمر عزم وإيجاب','1048','1318','0');
INSERT INTO [t121382] VALUES (1555,'(٢٣٧) باب ذكر مغفرة سالف ذنوب أخر بقيام رمضان إيمانا واحتسابا','1048','1318','0');
INSERT INTO [t121382] VALUES (1556,'(٢٣٨) باب الصلاة جماعة في قيام شهر رمضان، ضد قول من يتوهم أن الفاروق هو أول من أمر بالصلاة جماعة في قيام شهر رمضان','1048','1318','0');
INSERT INTO [t121382] VALUES (1557,'(٢٣٩) باب ذكر الدليل على أن النبي - صلى الله عليه وسلم - إنما خص القيام بالناس هذه الليالي الثلاث لليلة القدر فيهن','1049','1318','0');
INSERT INTO [t121382] VALUES (1558,'(٢٤٠) باب ذكر قيام الليل كله للمصلي مع الإمام في قيام رمضان حتى يفرغ','1049','1318','0');
INSERT INTO [t121382] VALUES (1559,'(٢٤١) باب الدليل على أن النبي - صلى الله عليه وسلم - إنما ترك قيام ليالي رمضان كله خشية أن يفترض قيام الليل على أمته فيعجزوا عنه','1050','1318','0');
INSERT INTO [t121382] VALUES (1560,'(٢٤٢) باب إمامة القارئ الأميين في قيام شهر رمضان، مع الدليل على أن صلاة الجماعة في قيام رمضان سنة النبي - صلى الله عليه وسلم -، لا بدعة كما زعمت الروافض','1051','1318','0');
INSERT INTO [t121382] VALUES (1561,'(٢٤٣) باب استحباب صلاة النساء جماعة مع الإمام في قيام رمضان، مع الدليل على أن قيام رمضان في جماعة أفضل من صلاة المرء منفردا في رمضان، وإن كان المأمومون قراء، يقرؤون القرآن، لا كمن اختار صلاة المنفرد على صلاة الجماعة في قيام رمضان','1051','1318','0');
INSERT INTO [t121382] VALUES (1562,'(٢٤٤) باب في فضل قيام رمضان، واستحقاق قائمه اسم الصديقين والشهداء إذا جمع مع قيامه رمضان صيام نهاره، وكان مقيما للصلوات الخمس، مؤديا للزكاة، شاهدا لله بالوحدانية، مقرا للنبي - صلى الله عليه وسلم - بالرسالة','1052','1318','0');
INSERT INTO [t121382] VALUES (1563,'(٢٤٥) باب ذكر عدد صلاة النبي - صلى الله عليه وسلم - بالليل في رمضان. والدليل على أنه لم يكن يزيد في رمضان على عدد الركعات في الصلاة بالليل ما كان يصلي من غير رمضان','1053','1318','0');
INSERT INTO [t121382] VALUES (1564,'(٢٤٦) باب استحباب إحياء ليالي العشر الأواخر من شهر رمضان، وترك مجامعة النساء فيهن، والاشتغال بالعبادة، وإيقاظ المرء أهله فيهن','1053','1318','0');
INSERT INTO [t121382] VALUES (1565,'(٢٤٧) باب استحباب الاجتهاد في العمل في العشر الأواخر من شهر رمضان','1054','1318','0');
INSERT INTO [t121382] VALUES (1566,'(٢٤٨) باب استحباب ترك المبيت على الفراش في رمضان إذ البائت على الفراش أثقل نوما، وأقل نشاطا للقيام من النائم على غير الفرش الوطيئة الممهدة في شهر رمضان','1054','1318','0');
INSERT INTO [t121382] VALUES (1567,'(٢٤٩) باب وقت الاعتكاف في العشر الأواخر من شهر رمضان','1054','1318','0');
INSERT INTO [t121382] VALUES (1568,'(٢٥٠) باب إباحة ضرب القباب في المسجد للاعتكاف فيهن','1055','1318','0');
INSERT INTO [t121382] VALUES (1569,'(٢٥١) باب في اعتكاف شهر رمضان كله','1055','1318','0');
INSERT INTO [t121382] VALUES (1570,'(٢٥٢) باب الاقتصار في الاعتكاف على العشر الأوسط، والعشر الأواخر من رمضان، إذ الاعتكاف كله فضيلة لا فريضة، والفضيلة لا تضيق على المرء أن يزيد فيها أو ينقص منها','1055','1318','0');
INSERT INTO [t121382] VALUES (1571,'(٢٥٣) باب إباحة الاقتصار من الاعتكاف على العشر الأواخر من شهر رمضان دون العشرين الأولين','1056','1318','0');
INSERT INTO [t121382] VALUES (1572,'(٢٥٤) باب الرخصة في الاقتصار على اعتكاف السبع الوسط من شهر رمضان دون ما قبله وما بعده من رمضان','1056','1318','0');
INSERT INTO [t121382] VALUES (1573,'(٢٥٥) باب المداومة على اعتكاف العشر الأواخر من شهر رمضان','1057','1318','0');
INSERT INTO [t121382] VALUES (1574,'(٢٥٦) باب الاعتكاف في شوال إذا فات الاعتكاف في رمضان لفضل دوام العمل','1057','1318','0');
INSERT INTO [t121382] VALUES (1575,'(٢٥٧) باب الاعتكاف في السنة المقبلة إذا فات ذلك لسفر أو علة تصيب المرء','1057','1318','0');
INSERT INTO [t121382] VALUES (1576,'(٢٥٨) باب الأمر بوفاء نذر الاعتكاف ينذره المرء في الشرك، ثم يسلم الناذر قبل قضاء النذر. وإباحة اعتكاف ليلة واحدة في عشر رمضان','1058','1318','0');
INSERT INTO [t121382] VALUES (1577,'(٢٥٩) باب إباحة دخول المعتكف البيت لحاجة الإنسان: الغائط والبول','1059','1318','0');
INSERT INTO [t121382] VALUES (1578,'(٢٦٠) باب ترك دخول المعتكف البيت إلا لحاجة الإنسان، وإباحة إخراج المعتكف رأسه من المسجد إلى المرأة لتغسله وترجله','1060','1318','0');
INSERT INTO [t121382] VALUES (1579,'(٢٦١) باب الرخصة في ترجيل المرأة الحائض رأس المعتكف، ومسها إياه وهي خارجة من المسجد','1060','1318','0');
INSERT INTO [t121382] VALUES (1580,'(٢٦٢) باب الرخصة في زيارة المرأة وزوجها في اعتكافه ومحادثتها إياه عند زيارتها إياه','1060','1318','0');
INSERT INTO [t121382] VALUES (1581,'(٢٦٣) باب ذكر الدليل على أن النبي - صلى الله عليه وسلم - إنما بلغ مع صفية حين أراد قلبها إلى منزلها باب المسجد، لا أنه خرج من المسجد فردها إلى منزلها','1061','1318','0');
INSERT INTO [t121382] VALUES (1582,'(٢٦٤) باب الرخصة في السمر للمعتكف مع نسائه في الاعتكاف','1061','1318','0');
INSERT INTO [t121382] VALUES (1583,'(٢٦٥) باب الافتراش في المسجد، ووضع السرر فيه للاعتكاف','1062','1318','0');
INSERT INTO [t121382] VALUES (1584,'(٢٦٦) باب الرخصة في بناء بيوت السعف في المسجد للاعتكاف فيها','1062','1318','0');
INSERT INTO [t121382] VALUES (1585,'(٢٦٧) باب الرخصة في وضع الأمتعة التي يحتاج إليها المعتكف في اعتكافه في المسجد','1062','1318','0');
INSERT INTO [t121382] VALUES (1586,'(٢٦٨) باب الخبر الدال على إجازة الاعتكاف بلا مقارنة للصوم إذ النبي - صلى الله عليه وسلم - قد أمر باعتكاف ليلة، ولا صوم في الليل','1063','1318','0');
INSERT INTO [t121382] VALUES (1587,'(٢٦٩) باب الرخصة للنساء في الاعتكاف في مسجد الجماعات مع أزواجهن إذا اعتكفوا','1063','1318','0');
INSERT INTO [t121382] VALUES (1588,'(٢٧٠) باب ذكر المعتكف ينذر في اعتكافه ما ليس لله فيه طاعة، وليس بنذر يتقرب إلى الله عز وجل','1063','1318','0');
INSERT INTO [t121382] VALUES (1589,'(٢٧١) باب وقت خروج المعتكف من معتكفه، والدليل على أن المعتكف يخرج من معتكفه مصبحا لا ممسيا','1064','1318','0');
INSERT INTO [t121382] VALUES (1590,'كتاب الزكاة','1065','0','0');
INSERT INTO [t121382] VALUES (1591,'(١) باب البيان أن إيتاء الزكاة من الإسلام بحكم الأمينين، أمين السماء جبريل وأمين الأرض محمد النبي صلى الله عليهما','1065','1590','0');
INSERT INTO [t121382] VALUES (1592,'(٢) باب البيان أن إيتاء الزكاة من الإيمان، إذ الإيمان والإسلام اسمان لمعنى واحد','1066','1590','0');
INSERT INTO [t121382] VALUES (1593,'(٣) باب الأمر بقتال مانع الزكاة اتباعا لأمر الله عز وجل بقتال المشركين حتى يتوبوا من الشرك، ويقيموا الصلاة، ويؤتوا الزكاة، وائتمارا لأمره جل وعلا بتخليتهم بعد إقام الصلاة وإيتاء الزكاة. قال الله عز وجل: (فاقتلوا المشركين حيث وجدتموهم) إلى قوله: (فإن تابوا وأقاموا الصلاة وآتوا الزكاة فخلوا سبيلهم) [التوبه:٥] وقال: (فإن تابوا وأقاموا الصلاة وآتوا الزكاة فإخوانكم في الدين) [التوبه:١١]','1067','1590','0');
INSERT INTO [t121382] VALUES (1594,'(٤) باب الدليل على أن دم المرء وماله إنما يحرمان بعد الشهادة بإقام الصلاة وإيتاء الزكاة إذا وجبت، إذ الله عز وجل جعلهم إخوان المسلمين بعد التوبة من الشرك وبعد إقام الصلاة وإيتاء الزكاة إذا وجبتا','1068','1590','0');
INSERT INTO [t121382] VALUES (1595,'(٥) باب ذكر إدخال مانع الزكاة النار مع أوائل من يدخلها، بالله نتعوذ من النار','1068','1590','0');
INSERT INTO [t121382] VALUES (1596,'(٦) باب ذكر لعن لاوي الصدقة الممتنع من أدائها','1069','1590','0');
INSERT INTO [t121382] VALUES (1597,'(٧) باب صفات ألوان عقاب مانع الزكاة يوم القيامة، قبل الفصل بين الخلق، نعوذ بالله من عذابه','1069','1590','0');
INSERT INTO [t121382] VALUES (1598,'(٨) باب ذكر بعض ألوان مانع الزكاة والدليل على ضد قول من جهل معنى قوله تعالى: (والذين يكنزون الذهب والفضة)','1070','1590','0');
INSERT INTO [t121382] VALUES (1599,'(٩) باب ذكر أخبار رويت عن النبي - صلى الله عليه وسلم - في الكنز مجملة غير مفسرة','1071','1590','0');
INSERT INTO [t121382] VALUES (1600,'(١٠) باب ذكر الخبر المفسر للكنز والدليل على أن الكنز هو المال [٢٢٩ - ب] الذي لا يؤدى زكاته، لا المال المدفون الذي يؤدى زكاته','1072','1590','0');
INSERT INTO [t121382] VALUES (1601,'(١١) باب ذكر الدليل على أن لا واجب في المال غير الزكاة وفيه ما دل على أن الوعيد بالعذاب للمكتنز ولمن لا يؤدي زكاة ماله دون من يؤديها وإن كان المال مدفونا','1073','1590','0');
INSERT INTO [t121382] VALUES (1602,'(١٢) باب ذكر دليل آخر على أن الوعيد للمكتنز هو لمانع الزكاة دون من يؤديها','1073','1590','0');
INSERT INTO [t121382] VALUES (1603,'(١٣) باب بيعة الإمام الناس على إيتاء الزكاة','1074','1590','0');
INSERT INTO [t121382] VALUES (1604,'(١٤) باب ذكر البيان أن فرض الزكاة كان قبل الهجرة إلى أرض الحبشة إذ النبي - صلى الله عليه وسلم - مقيم بمكة قبل هجرته إلى المدينة','1074','1590','0');
INSERT INTO [t121382] VALUES (1605,'(١٥) باب فرض صدقة الإبل والغنم، والدليل على أن الله - عز وجل - أراد بقوله: (خذ من أموالهم صدقة) [التوبة: ١٠٣] بعض الأموال لا كلها، إذ اسم المال قد يقع على ما دون خمس من الإبل وعلى ما دون الأربعين من الغنم','1075','1590','0');
INSERT INTO [t121382] VALUES (1606,'(١٦) باب ذكر الدليل على أن صغار الإبل والغنم وكبارهما تعد على مالكها عند أخذ الساعي الصدقة من مالكها','1078','1590','0');
INSERT INTO [t121382] VALUES (1607,'(١٧) باب الدليل على أن الصدقة لا تجب فيما دون خمس من الإبل ولا فيما دون الأربعين من الغنم، مع الدليل على أن اسم الصدقة واقع على عشر الحبوب والثمار وعلى زكاة الناض من الورق، وعلى صدقة المواشي، إذ العامة تفرق بين الزكاة والصدقة والعشر لجهلها بالعلم فتتوهم أن اسم الصدقة إنما يقع على صدقة المواشي دون عشر الحبوب والثمار ويتوهم أن الواجب في الناض إنما يقع عليه','1078','1590','0');
INSERT INTO [t121382] VALUES (1608,'(١٨) باب ذكر الدليل على أن اسم الزكاة أيضا واقع على صدقة المواشي إذ الصدقة والزكاة اسمان [٢٣١ - أ] للواجب في المال','1079','1590','0');
INSERT INTO [t121382] VALUES (1609,'(١٩) باب ذكر الدليل على أن الصدقة إنما تجب في الإبل والغنم في سوائمهما دون غيرهما، ضد قول من زعم أن في الإبل العوامل صدقة','1080','1590','0');
INSERT INTO [t121382] VALUES (1610,'(٢٠) باب صدقة البقر بذكر لفظ مجمل غير مفسر','1081','1590','0');
INSERT INTO [t121382] VALUES (1611,'(٢١) باب ذكر الخبر المفسر للفظة المجملة التي ذكرتها، والدليل على أن النبي - صلى الله عليه وسلم - إنما أوجب الصدقة في البقر في سوائمها دون عواملها','1082','1590','0');
INSERT INTO [t121382] VALUES (1612,'(٢٢) باب النهي عن أخذ اللبون في الصدقة بغير رضى صاحب الماشية','1083','1590','0');
INSERT INTO [t121382] VALUES (1613,'(٢٣) باب الزجر عن إخراج الهرمة والمعيبة والتيس في الصدقة بغير مشيئة المصدق وإباحة أخذهن إذا شاء المصدق وأراد','1084','1590','0');
INSERT INTO [t121382] VALUES (1614,'(٢٤) باب إباحة دعاء الإمام على مخرج مسن ماشيته في الصدقة بأن لا يبارك له في ماشيته، ودعائه لمخرج أفضل ماشيته في الصدقة بأن يبارك له في ماله','1084','1590','0');
INSERT INTO [t121382] VALUES (1615,'(٢٥) باب الزجر عن أخذ المصدق خيار المال [٢٣٢ - أ] بذكر خبر مجمل غير مفسر','1085','1590','0');
INSERT INTO [t121382] VALUES (1616,'(٢٦) باب ذكر الخبر المفسر للفظة المجملة التي ذكرتها، والدليل على أن النبي - صلى الله عليه وسلم - إنما زجر عن أخذ كرائم أموال من تجب عليه الصدقة في ماله إذا أخذ المصدق كرائم أموالهم بغير طيب أنفسهم، إذ النبي - صلى الله عليه وسلم - قد أباح أخذ خيار أموالهم إذا طابت أنفسهم بإعطائها، ودعا لمعطيها بالبركة في ماله وفي إبله','1086','1590','0');
INSERT INTO [t121382] VALUES (1617,'(٢٧) باب الزجر عن الجمع بين المتفرق والتفريق بين المجتمع في السوائم خيفة الصدقة، وتراجع الخليطين بينهما بالسوية فيما أخذ المصدق ماشيتهما جميعا','1087','1590','0');
INSERT INTO [t121382] VALUES (1618,'(٢٨) باب النهي عن الجلب عند أخذ الصدقة من المواشي، والأمر بأخذ صدقة المواشي في ديار مالكها من غير أن يؤمروا بجلب المواشي إلى الساعي ليأخذ صدقتهما','1088','1590','0');
INSERT INTO [t121382] VALUES (1619,'(٢٩) باب أخذ الغنم والدراهم فيما بين أسنان الإبل التي يجب في الصدقة إذا لم يوجد السن الواجبة في الإبل، والبيان ضد قول من زعم أن بين السنيين قدر قيمة ما بينهما. وهذا القول إغفال من قائله أو هو خلاف سنة النبي - صلى الله عليه وسلم -. وكل قول خلاف سنته فمردود غير مقبول','1089','1590','0');
INSERT INTO [t121382] VALUES (1620,'(٣٠) باب الأمر بسمة إبل الصدقة إذا قبضت في الصدقة ليعرف الوالي والرعية إبل الصدقة من غيرها ليقسمها على أهل سهمان الصدقة دون غيرها -إن صح الخبر-','1090','1590','0');
INSERT INTO [t121382] VALUES (1621,'(٣١) باب سمة غنم الصدقة إذا قبضت','1091','1590','0');
INSERT INTO [t121382] VALUES (1622,'(٣٢) باب إسقاط الصدقة، صدقة المال عن الخيل والرقيق، بذكر لفظ مختصر غير مستقصى في الرقيق خاصة','1091','1590','0');
INSERT INTO [t121382] VALUES (1623,'(٣٣) باب ذكر الخبر المتقصى للفظة المختصرة التي ذكرتها في صدقة [٢٣٣ - ب] الرقيق، والدليل على أن النبي - صلى الله عليه وسلم - إنما عفا عن الصدقة في الرقيق صدقة الأموال دون صدقة الفطر','1092','1590','0');
INSERT INTO [t121382] VALUES (1624,'(٣٤) باب ذكر السنة الدالة على معنى أخذ عمر بن الخطاب من الخيل والرقيق الصدقة، والدليل على أنه إنما أخذها منهم إذ جادت أنفسهم وكانت بإعطائها متطوعين بالدفع، لا أن الصدقة كانت واجبة على الخيل والرقيق. إذ الفاروق قد أعلم القوم الذين أخذ منهم صدقة الخيل والرقيق أن النبي - صلى الله عليه وسلم - والصديق قبله لم يأخذا صدقة الخيل والرقيق','1092','1590','0');
INSERT INTO [t121382] VALUES (1625,'(٣٥) باب ذكر إسقاط الصدقة عن الحمر مع الدليل على إسقاطها عن الخيل، والدليل على أن الله - عز وجل - إنما أمر نبيه - صلى الله عليه وسلم - بأخذ الصدقة من بعض أموال المسلمين لا من جميع أموالهم في قوله - عز وجل -: (خذ من أموالهم صدقة تطهرهم وتزكيهم بها) [التوبة: ١٠٣] إذ اسم المال واقع على الخيل والحمير جميعا فبين به النبي - صلى الله عليه وسلم - الذي ولاه الله بيان ما أنزل عليه، إن الله إنما أمره بأخذ الصدقة من بعض أموال المسلمين لا من جميعها','1093','1590','0');
INSERT INTO [t121382] VALUES (1626,'(٣٦) باب الرخصة في تأخير الإمام قسم الصدقة بعد أخذه إياها، وإباحة بعثة مواشي الصدقة إلى الرعي، إلى أن يرى الإمام قسمها','1094','1590','0');
INSERT INTO [t121382] VALUES (1627,'(٣٧) باب إسقاط فرض الزكاة عما دون خمس أواق من الورق','1095','1590','0');
INSERT INTO [t121382] VALUES (1628,'(٣٨) باب الدليل على [أن] الخمسة الأواق هي مائتا درهم','1095','1590','0');
INSERT INTO [t121382] VALUES (1629,'(٣٩) باب ذكر مبلغ الزكاة في الورق إذا بلغ خمس أواق','1096','1590','0');
INSERT INTO [t121382] VALUES (1630,'(٤٠) باب ذكر البيان أن الزكاة واجبة على ما زاد على المائتين من الورق ضد قول من زعم أن الزكاة غير واجبة على ما زاد على المائتي درهم حتى تبلغ الزيادة أربعين درهما','1096','1590','0');
INSERT INTO [t121382] VALUES (1631,'(٤١) باب ذكر الدليل على أن الزكاة غير واجبة على الحلي، إذ اسم الورق في لغة العرب الذين خوطبنا بلغتهم لا يقع -علمي- على الحلي الذي هو متاع ملبوس','1096','1590','0');
INSERT INTO [t121382] VALUES (1632,'(٤٢) باب ذكر إسقاط الصدقة عما دون خمسة أوسق','1097','1590','0');
INSERT INTO [t121382] VALUES (1633,'(٤٣) باب ذكر إيجاب الصدقة في البر والتمر إذا بلغ الصنف الواحد منهما خمسة أوسق','1098','1590','0');
INSERT INTO [t121382] VALUES (1634,'(٤٤) باب ذكر الدليل على أن النبي - صلى الله عليه وسلم - إنما أوجب في البر الزكاة إذا بلغ البر خمسة أوساق، وفي التمر إذا بلغ خمسة أوساق، لا إذا بلغ البر والتمر خمسة أوساق إذا ضم أحدهما إلى الآخر','1098','1590','0');
INSERT INTO [t121382] VALUES (1635,'(٤٥) باب إيجاب الصدقة في الزبيب إذا بلغ خمسة أوسق. وفي القلب من هذا الإسناد، ليس هذا الخبر مما سمعه عمرو بن دينار من جابر -علمي-','1099','1590','0');
INSERT INTO [t121382] VALUES (1636,'(٤٦) باب ذكر مبلغ الواجب من الصدقة في الحبوب والثمار، والفرق بين الواجب في الصدقة فيما سقته السماء أو الأنهار أو هما، وبين ما سقي بالرشاء والدوالي','1100','1590','0');
INSERT INTO [t121382] VALUES (1637,'(٤٧) باب ذكر مبلغ الوسق إن صح الخبر. ولا خلاف بين العلماء في مبلغه على ما روي في هذا الخبر إلا أن أبا البختري لا أحسبه سمع من أبي سعيد','1101','1590','0');
INSERT INTO [t121382] VALUES (1638,'(٤٨) باب الزجر عن إخراج الحبوب والتمور الرديئة في الصدقة، قال الله - عز وجل -: (ولا تيمموا الخبيث منه تنفقون ولستم بآخذيه إلا أن تغمضوا فيه) [البقرة: ٢٦٧]','1101','1590','0');
INSERT INTO [t121382] VALUES (1639,'(٤٩) باب وقت بعثة الإمام الخارص يخرص الثمار','1103','1590','0');
INSERT INTO [t121382] VALUES (1640,'(٥٠) باب السنة في خرص العنب لتؤخذ زكاته زبيبا كما تؤخذ زكاة النخل تمرا','1104','1590','0');
INSERT INTO [t121382] VALUES (1641,'(٥١) باب السنة في قدر ما يؤمر الخارص بتركه من الثمار فلا يخرصه على صاحب المال ليكون قدر ما يأكله رطبا ويطعمه قبل يبس التمر، غير داخل فيما يخرج منه العشر أو نصف العشر','1105','1590','0');
INSERT INTO [t121382] VALUES (1642,'(٥٢) باب فرض إخراج الصدقة في العسر واليسر، والتغليظ في منع الزكاة في العسر','1105','1590','0');
INSERT INTO [t121382] VALUES (1643,'(٥٣) باب ذكر البيان أن النبي - صلى الله عليه وسلم إنما أراد بالنجدة والرسل في هذا الموضع العسر واليسر، وأراد بقوله من نجدتها ورسلها: أي وفي نجدتها ورسلها','1106','1590','0');
INSERT INTO [t121382] VALUES (1644,'(٥٤) باب ذكر أخذ الصدقة من المعادن إن صح الخبر، فإن في القلب من اتصال هذا الإسناد','1107','1590','0');
INSERT INTO [t121382] VALUES (1645,'(٥٥) باب ذكر صدقة العسل إن صح الخبر، فإن في القلب من هذا الإسناد','1108','1590','0');
INSERT INTO [t121382] VALUES (1646,'(٥٦) باب إيجاب الخمس في الركاز','1110','1590','0');
INSERT INTO [t121382] VALUES (1647,'(٥٧) باب وجوب الخمس فيما يوجد في الخرب العادي من دفن الجاهلية. والدليل على أن الركاز ليس بدفن الجاهلية إذ النبي - صلى الله عليه وسلم -إن ثبت هذا الخبر عنه- قد فرق بين الموجود في الخرب العادي، وبين الركاز فأوجب فيهما جميعا الخمس','1111','1590','0');
INSERT INTO [t121382] VALUES (1648,'(٥٨) باب الرخصة في تقديم الصدقة قبل حلول الحول على المال، والفرق بين الفرض الذي يجب في المال وبين الفرض الواجب على البدن','1111','1590','0');
INSERT INTO [t121382] VALUES (1649,'(٥٩) باب احتساب ما قد حبس المؤمن السلاح والعبد في سبيل الله من الصدقة إذا وجبت فهذه المسألة أيضا من باب تقديم الصدقة قبل وجوبها','1114','1590','0');
INSERT INTO [t121382] VALUES (1650,'(٦٠) باب استسلاف الإمام المال لأهل سهمان الصدقة، ورده ذلك من الصدقة إذا قبضت بعد الاستسلاف','1114','1590','0');
INSERT INTO [t121382] VALUES (1651,'(٦١) باب ذكر التغليظ على السعاية بذكر خبر مجمل غير مفسر','1114','1590','0');
INSERT INTO [t121382] VALUES (1652,'(٦٢) باب ذكر الدليل على أن التغليظ في العمل على السعاية المذكور في خبر عقبة هو في الساعي إذا لم يعدل في عمله وجار وظلم. وفضل السعاية على الصدقة إذا عدل الساعي فيما يتولى منها، وتشبيهه بالغازي في سبيل الله','1115','1590','0');
INSERT INTO [t121382] VALUES (1653,'(٦٣) باب في التغليظ في الاعتداء في الصدقة، وتمثيل المعتدي فيها بمانعها','1115','1590','0');
INSERT INTO [t121382] VALUES (1654,'(٦٤) باب التغليظ في غلول الساعي من الصدقة','1116','1590','0');
INSERT INTO [t121382] VALUES (1655,'(٦٥) باب ذكر البيان أن ما كتم الساعي من قليل المال أو كثيره عن الإمام كان ما كتم غلولا. قال الله - عز وجل -: (وما كان لنبي أن يغل ومن يغلل يأت بما غل يوم القيامة) [آل عمران: ١٦١])','1117','1590','0');
INSERT INTO [t121382] VALUES (1656,'(٦٦) باب التغليظ في قبول المصدق الهدية ممن يتولى السعاية عليهم','1117','1590','0');
INSERT INTO [t121382] VALUES (1657,'(٦٧) باب صفة إتيان الساعي يوم القيامة بما غل من الصدقة، وأمر الإمام بمحاسبة الساعي إذا [٢٣٨ - ب] قدم من سعايته','1118','1590','0');
INSERT INTO [t121382] VALUES (1658,'(٦٨) باب الأمر بإرضاء المصدق وإصداره راضيا عن أصحاب الأموال','1119','1590','0');
INSERT INTO [t121382] VALUES (1659,'(٦٩) باب الزجر عن استعمال موالي النبي - صلى الله عليه وسلم - على الصدقة إذا طلبوا العمالة إذ هم ممن لا تحل لهم الصدقة المفروضة','1119','1590','0');
INSERT INTO [t121382] VALUES (1660,'(٧٠) باب الزجر عن استعمال موالي النبي - صلى الله عليه وسلم - على الصدقة إذا طلبوا العمالة على السعاية، إذ الموالي من أنفس القوم، والصدقة تحرم عليهم كتح','1122','1590','0');
INSERT INTO [t121382] VALUES (1661,'(٧١) باب صلاة الإمام على المأخوذ منه الصدقة، اتباعا لأمر الله - عز وجل - بنبيه - صلى الله عليه وسلم - في قوله: (خذ من أموالهم صدقة تطهرهم وتزكيهم بها وصل عليهم إن صلاتك سكن لهم) [التوبة: ١٠٣]','1122','1590','0');
INSERT INTO [t121382] VALUES (1662,'(٧٢) باب الأمر بقسم الصدقة في أهل البلدة التي تؤخذ منهم الصدقة','1122','1590','0');
INSERT INTO [t121382] VALUES (1663,'(٧٣) باب ذكر تحريم الصدقة المفروضة على النبي المصطفى - صلى الله عليه وسلم - والدليل على أن الله - عز وجل - إنما أراد بقوله: (إنما الصدقات للفقراء) إلى آخر الآية [التوبة: ٦٠] بعض الفقراء أو بعض المساكين وبعض العاملين، وبعض الغارمين وبعض أبناء السبيل، فولى النبي - صلى الله عليه وسلم - بيان ما أنزل عليه في الكتاب، فبين - صلى الله عليه وسلم - أن هذه الألفاظ ألفاظ عام مرادها خاص؛ إذ كل هؤلاء الأصناف الفقراء والمساكين ومن ذكر في هذه الآية موجودون في آل النبي - صلى الله عليه وسلم -، وقد أعلم - صلى الله عليه وسلم - أن الصدقة لا تحل له ولا لمواليهم','1123','1590','0');
INSERT INTO [t121382] VALUES (1664,'(٧٤) باب ذكر البيان أن على أولياء الأطفال من آل النبي - صلى الله عليه وسلم - منعهم من أكل ما حرم على البالغين','1124','1590','0');
INSERT INTO [t121382] VALUES (1665,'(٧٥) باب ذكر الدليل على أن الصدقة المحرمة على النبي - صلى الله عليه وسلم - هي','1124','1590','0');
INSERT INTO [t121382] VALUES (1666,'(٧٦) باب ذكر دلائل أخر على أن النبي - صلى الله عليه وسلم - إنما أراد بقوله: "إن الصدقة لا تحل لآل محمد، صدقة الفريضة دون صدقة التطوع','1125','1590','0');
INSERT INTO [t121382] VALUES (1667,'(٧٧) باب ذكر الدليل على أن بني عبد المطلب هم من آل النبي - صلى الله عليه وسلم - الذين حرموا الصدقة، لا كما قال من زعم أن آل النبي - صلى الله عليه وسلم - الذين حرموا الصدقة، آل علي وآل جعفر وآل العباس','1127','1590','0');
INSERT INTO [t121382] VALUES (1668,'(٧٨) باب إعطاء الفقراء من الصدقة اتباعا لأمر الله في قوله: (إنما الصدقات للفقراء) [التوبة: ٦٠] الآية','1128','1590','0');
INSERT INTO [t121382] VALUES (1669,'(٧٩) باب صدقة الفقير الذي يجوز له المسألة في الصدقة، والدليل على أن لا وقت فيما يعطي الفقير من الصدقة إلا قدر سد خلته وفاقته','1129','1590','0');
INSERT INTO [t121382] VALUES (1670,'(٨٠) باب الدليل على أن شهادة ذوي الحجا في هذا الموضع هي اليمين، إذ الله - عز وجل - قد سمى اليمين في اللعان شهادة','1130','1590','0');
INSERT INTO [t121382] VALUES (1671,'(٨١) باب الرخصة في إعطاء من له ضيعة من الصدقة إذا أصابت غلته جائحة أذهبت غلته قدر ما يسد فاقته','1130','1590','0');
INSERT INTO [t121382] VALUES (1672,'(٨٢) باب إعطاء اليتامى من الصدقة إذا كانوا فقراء -إن ثبت الخبر فإن في النفس من أشعث بن سوار- وإن لم يثبت هذا الخبر فالقرآن كاف في نقل الخبر الخاص فيه. قد أعلم الله في محكم تنزيله [٢٤١ - ب] أن للفقراء قسم في الصدقات. فالفقير كان يتيما أو غير يتيم فله في الصدقة قسم بنص الكتاب','1131','1590','0');
INSERT INTO [t121382] VALUES (1673,'(٨٣) باب ذكر صفة المسكين الذي أمر الله بإعطائه من الصدقة','1131','1590','0');
INSERT INTO [t121382] VALUES (1674,'(٨٤) باب إعطاء العامل على الصدقة منها رزقا لعمله، قال الله - عز وجل -: (إنما الصدقات للفقراء والمساكين والعاملين عليها ...) الآية [التوبة: ٦٠]','1132','1590','0');
INSERT INTO [t121382] VALUES (1675,'(٨٥) باب ذكر الدليل على أن العامل على الصدقة إن عمل عليها متطوعا بالعمل من غير إرادة ونية لأخذ عمالة على عمله فأعطاه الإمام لعمالته رزقا من غير مسألة ولا إشراف فجائز له أخذه','1133','1590','0');
INSERT INTO [t121382] VALUES (1676,'(٨٦) باب ذكر إعطاء العامل على الصدقة عمالة من الصدقة وإن كان غنيا','1135','1590','0');
INSERT INTO [t121382] VALUES (1677,'(٨٧) باب فرض الإمام للعامل على الصدقة رزقا معلوما','1135','1590','0');
INSERT INTO [t121382] VALUES (1678,'(٨٨) باب إذن الإمام للعامل بالتزويج، واتخاذ الخادم، والمسكن من الصدقة','1135','1590','0');
INSERT INTO [t121382] VALUES (1679,'(٨٩) باب ذكر إعطاء المؤلفة قلوبهم من الصدقة ليسلموا للعطية','1136','1590','0');
INSERT INTO [t121382] VALUES (1680,'(٩٠) باب إعطاء رؤساء الناس وقادتهم على الإسلام تألفا بالعطية','1136','1590','0');
INSERT INTO [t121382] VALUES (1681,'(٩١) باب إعطاء الغارمين من الصدقة وإن كانوا أغنياء، بلفظ خبر مجمل غير مفسر','1137','1590','0');
INSERT INTO [t121382] VALUES (1682,'(٩٢) باب الدليل على أن الغارم الذي يجوز إعطاؤه من الصدقة وإن كان غنيا هو الغارم في الحمالة، والدليل على أنه يعطى قدر ما يؤدي الحمالة لا أكثر','1137','1590','0');
INSERT INTO [t121382] VALUES (1683,'(٩٣) باب الرخصة في إعطاء من يحج من سهم سبيل الله إذ الحج من سبيل الله','1138','1590','0');
INSERT INTO [t121382] VALUES (1684,'(٩٤) باب إعطاء الإمام الحاج إبل الصدقة ليحجوا عليها','1138','1590','0');
INSERT INTO [t121382] VALUES (1685,'(٩٥) باب الرخصة في إعطاء الإمام المظاهر من الصدقة ما يكفر به عن ظهاره إذا لم يكن واجدا للكفارة','1139','1590','0');
INSERT INTO [t121382] VALUES (1686,'(٩٦) باب أمر الإمام المصدق بقسم الصدقة حيث يقبض إن صح الخبر فإن في القلب من أشعث بن سوار وإن لم يثبت هذا الخبر، فخبر ابن عباس في أمر النبي - صلى الله عليه وسلم - معاذا بأخذ الصدقة من أغنياء أهل اليمن وقسمها في فقرائهم - كاف من هذا الخبر','1140','1590','0');
INSERT INTO [t121382] VALUES (1687,'(٩٧) باب حمل صدقات أهل البوادي إلى الإمام ليكون هو المفرق لها','1141','1590','0');
INSERT INTO [t121382] VALUES (1688,'(٩٨) باب حمل الصدقة من المدن إلى الإمام ليتولى تفرقتها على أهل الصدقة','1141','1590','0');
INSERT INTO [t121382] VALUES (1689,'(٩٩) باب الرخصة في قسم المرء صدقته من غير دفعها إلى الوالي، قال الله - عز وجل -: (إن تبدوا الصدقات فنعما هي) .. الآية [البقرة: ٢٧١]','1142','1590','0');
INSERT INTO [t121382] VALUES (1690,'(١٠٠) باب إعطاء الإمام دية من لا يعرف قاتله من الصدقة، وهذا عندي من جنس الحمالة لشبه أن يكون المصطفى - صلى الله عليه وسلم - تحمل بهذه الدية فأعطاها من إبل الصدقة','1142','1590','0');
INSERT INTO [t121382] VALUES (1691,'(١٠١) باب استحباب إيثار المرء بصدقته قرابته دون الأباعد لانتظام الصدقة وصلة معا بتلك العطية','1143','1590','0');
INSERT INTO [t121382] VALUES (1692,'(١٠٢) باب فضل الصدقة على ذي الرحم الكاشح','1143','1590','0');
INSERT INTO [t121382] VALUES (1693,'(١٠٣) باب ذكر تحريم الصدقة على الأصحاء الأقوياء على الكسب، والأغنياء بكسبهم عن الصدقات وإن لم يكونوا أغنياء بمال يملكونه، بذكر خبر مجمل غير مفسر','1144','1590','0');
INSERT INTO [t121382] VALUES (1694,'(١٠٤) باب ذكر الدليل على أن النبي - صلى الله عليه وسلم - إنما أراد بهذه الصدقة التي أعلم أنها لا تحل للغني ولا للسوي صدقة الفريضة دون صدقة التطوع','1144','1590','0');
INSERT INTO [t121382] VALUES (1695,'(١٠٥) باب الرخصة في إعطاء الإمام من الصدقة من يذكر حاجة وفاقة لا يعلم الإمام منه خلافه من غير مسألة عن حاله أهو فقير محتاج أم لا؟','1144','1590','0');
INSERT INTO [t121382] VALUES (1696,'(١٠٦) باب استحباب الاستعفاف عن أكل الصدقة لمن يجد عنها غنى. يعني من المعاني، وإن كان من أهلها، إذ هي غسالة ذنوب الناس؟','1145','1590','0');
INSERT INTO [t121382] VALUES (1697,'(١٠٧) باب كراهة المسألة من الصدقة إذا كان سائلها واجدا غداء أو عشاء يشبعه يوما وليلة، وإن كان أخذه للصدقة من غير مسألة جائزا','1145','1590','0');
INSERT INTO [t121382] VALUES (1698,'(١٠٨) باب ذكر فرض زكاة الفطر والبيان على أن زكاة الفطر فرض على من يجب عليه زكاته، ضد قول من زعم أنها سنة غير فريضة، والمبين عن الله - عز وجل - ما أنزل عليه من وحيه أعلم أمتة أن هذه الصدقة فرض عليهم، كما أعلمهم أن في خمس من الإبل صدقة، وبين لهم جميع الفرض الذي يجب في مواشيهم وناضهم، وثمارهم، وحبوبهم، والله - جل وعلا - إنما أجمل ذكر الصدقة والزكاة في كتابه وقال لنبيه - صلى الله عليه وسلم -: (خذ من أموالهم صدقة) وقال لعباده المؤمنين: (فأقيموا الصلاة وآتوا الزكاة) فولى نبيه المصطفى - صلى الله عليه وسلم -[٢٤٤ - ب] بيان الزكاة التي هي صدقة وزكاة، إذ هما اسمان لمعنى واحد، فبين المصطفى - صلى الله عليه وسلم - أن صدقة الفطر فريضة. كما بين سائر الصدقات التي أخبرهم وأعلمهم أنها فريضة، فكيف يجوز لعالم أن يقبل بعض بيانه، ويدفع بعضه!','1146','1590','0');
INSERT INTO [t121382] VALUES (1699,'(١٠٩) باب ذكر الدليل على أن الأمر بصدقة الفطر كان قبل فرض لزكاة الأموال','1147','1590','0');
INSERT INTO [t121382] VALUES (1700,'(١١٠) باب الدليل على أن فرض صدقة الفطر على الذكر والأنثى، والحر والمملوك، مع الدليل على أن النبي - صلى الله عليه وسلم - إذا أمرنا لأمر مرة لم ينسخ أمره السكت بعد ذلك، ولا ينسخ أمره إلا أن يعلم - صلى الله عليه وسلم - أن ما كان أمرهم به ساقط عنهم','1147','1590','0');
INSERT INTO [t121382] VALUES (1701,'(١١١) باب الدليل على أن صدقة الفطر عن المملوك واجب على مالكه لا على المملوك كما توهم بعض الناس','1148','1590','0');
INSERT INTO [t121382] VALUES (1702,'(١١٢) باب ذكر دليل ثان أن صدقة الفطر عن المملوك واجب على مالكه، وأن معنى قوله - صلى الله عليه وسلم - في خبر ابن عمر: على المملوك معناه عن المملوك، لا أنها واجبة على المملوك كما زعم من قال أن المماليك يملكون','1148','1590','0');
INSERT INTO [t121382] VALUES (1703,'(١١٣) باب الدليل على أن صدقة الفطر يجب أداؤها عن المماليك المسلمين دون المشركين، خلاف قول من زعم أنها واجبة على المسلم في عبيده المشركين','1149','1590','0');
INSERT INTO [t121382] VALUES (1704,'(١١٤) باب الدليل على أن صدقة الفطر فرض على كل من استطاع أداءها خلاف قول من زعم أن فرضها ساقط عن من لا يجب عليه زكاة الفطر','1149','1590','0');
INSERT INTO [t121382] VALUES (1705,'(١١٥) باب ذكر الدليل على أن زكاة رمضان إنما تجب بصاع النبي - صلى الله عليه وسلم - لا بالصاع الذي أحدث بعد، إذ الصاع على عهد النبي - صلى الله عليه وسلم - بالمدينة كان صاعه','1150','1590','0');
INSERT INTO [t121382] VALUES (1706,'(١١٦) باب الدليل على أن فرض صدقة الفطر على من يستطيع أداءها دون من لم يستطع','1150','1590','0');
INSERT INTO [t121382] VALUES (1707,'(١١٧) باب إيجاب صدقة الفطر على الصغير، خلاف قول من زعم أنها ساقطة عن من سقط عنه فرض الصلاة','1150','1590','0');
INSERT INTO [t121382] VALUES (1708,'(١١٨) باب توقيت فرض زكاة الفطر في مبلغه من الكيل','1151','1590','0');
INSERT INTO [t121382] VALUES (1709,'(١١٩) باب الدليل على أن الأمر بصدقة نصف صاع من حنطة أحدثه الناس بعد النبي المصطفى - صلى الله عليه وسلم -','1151','1590','0');
INSERT INTO [t121382] VALUES (1710,'(١٢٠) باب الدليل على أنهم أمروا بنصف صاع حنطة إذ كان ذلك قيمة صاع تمر أو شعير، والواجب على هذا الأصل أن يتصدق بآصع من حنطة في بعض الأزمان وبعض البلدان','1152','1590','0');
INSERT INTO [t121382] VALUES (1711,'(١٢١) باب ذكر أول ما أحدث الأمر بنصف صاع حنطة، وذكر أول من أحدثه','1152','1590','0');
INSERT INTO [t121382] VALUES (1712,'(١٢٢) باب إخراج التمر والشعير في صدقة الفطر','1152','1590','0');
INSERT INTO [t121382] VALUES (1713,'(١٢٣) باب إخراج الزبيب والأقط في صدقة الفطر','1153','1590','0');
INSERT INTO [t121382] VALUES (1714,'(١٢٤) باب إخراج السلت صدقة الفطر إن كان ابن عيينة ومن دونه حفظه أو صح خبر ابن عباس، وإلا فإن في خبر موسى بن عقبة كفاية إن شاء الله','1154','1590','0');
INSERT INTO [t121382] VALUES (1715,'(١٢٥) باب إخراج جميع الأطعمة كلها في صدقة الفطر. والدليل على ضد قول من زعم أن الهليلج والفلوس جائز إخراجها في صدقة الفطر','1155','1590','0');
INSERT INTO [t121382] VALUES (1716,'(١٢٦) باب ذكر ثناء الله - عز وجل - على مؤدي صدقة الفطر','1156','1590','0');
INSERT INTO [t121382] VALUES (1717,'(١٢٧) باب الأمر بأداء صدقة الفطر قبل خروج الناس إلى صلاة العيد','1156','1590','0');
INSERT INTO [t121382] VALUES (1718,'(١٢٨) باب الدليل على أن أمر النبي - صلى الله عليه وسلم - بأدائها في يوم الفطر لا في غيره','1157','1590','0');
INSERT INTO [t121382] VALUES (1719,'(١٢٩) باب الدليل على أن الصلاة التي أمر النبي - صلى الله عليه وسلم - بأداء صدقة الفطر قبل الخروج إليها صلاة العيد لا غيرها','1157','1590','0');
INSERT INTO [t121382] VALUES (1720,'(١٣٠) باب الرخصة في تأخير الإمام قسم صدقة الفطر عن يوم الفطر إذا أديت إليه','1157','1590','0');
INSERT INTO [t121382] VALUES (1721,'(١٣١) باب في فضل الصدقة، وقبض الرب - عز وجل - إياها ليربيها لصاحبها، والبيان أنه لا يقبل إلا الطيب','1158','1590','0');
INSERT INTO [t121382] VALUES (1722,'(١٣٢) باب الأمر باتقاء النار -نعوذ بالله منها- بالصدقة، وإن قلت','1159','1590','0');
INSERT INTO [t121382] VALUES (1723,'(١٣٣) باب إظلال الصدقة صاحبها يوم القيامة إلى الفراغ من الحكم بين العباد','1160','1590','0');
INSERT INTO [t121382] VALUES (1724,'(١٣٤) باب فضل الصدقة على غيرها من الأعمال إن صح الخبر، فإني لا أعرف أبا فروة بعدالة ولا جرح','1161','1590','0');
INSERT INTO [t121382] VALUES (1725,'(١٣٥) باب الدليل على أن الصدقة بالمملوك أفضل من عتق المتصدق إياه إن صح الخبر','1162','1590','0');
INSERT INTO [t121382] VALUES (1726,'(١٣٦) باب فضل المتصدق على المتصدق عليه','1162','1590','0');
INSERT INTO [t121382] VALUES (1727,'(١٣٧) باب ذكر نماء المال بالصدقة [٢٤٧ - ب] منه، وإعطاء الرب - عز وجل - المتصدق الخلف. قال الله - عز وجل -: (وما أنفقتم من شيء فهو يخلفه) [سبأ: ٣٩]','1163','1590','0');
INSERT INTO [t121382] VALUES (1728,'(١٣٨) باب فضل الصدقة عن ظهر غنى يفضل عمن يعول المتصدق','1164','1590','0');
INSERT INTO [t121382] VALUES (1729,'(١٣٩) باب الزجر عن صدقة المرء بماله كله، والدليل على أن النبي - صلى الله عليه وسلم - أراد بقوله: "عن ظهر غنى" عما يغنيه ومن يعول، لا عن كثرة الرجل','1164','1590','0');
INSERT INTO [t121382] VALUES (1730,'(١٤٠) باب صدقة المقل إذا أبقى لنفسه قدر حاجته','1165','1590','0');
INSERT INTO [t121382] VALUES (1731,'(١٤١) باب ذكر الدليل على أن النبي - صلى الله عليه وسلم - إنما فضل صدقة المقل إذا كان فضلا عمن يعول، لا إذا تصدق على الأباعد وترك من يعول جياعا عراة. إذ النبي - صلى الله عليه وسلم - قد أمر ببدء من يعول','1166','1590','0');
INSERT INTO [t121382] VALUES (1732,'(١٤٢) باب التغليظ في مسألة الغني من الصدقة','1166','1590','0');
INSERT INTO [t121382] VALUES (1733,'(١٤٣) باب ذكر الغني الذي يكون المسألة معه إلحافا','1167','1590','0');
INSERT INTO [t121382] VALUES (1734,'(١٤٤) باب تشبيه الملحف بمن يسف المسألة','1167','1590','0');
INSERT INTO [t121382] VALUES (1735,'(١٤٥) باب الرخصة في الصدقة على من يمونه متطوعا','1167','1590','0');
INSERT INTO [t121382] VALUES (1736,'(١٤٦) باب فضل الصدقة على المماليك إذا كانوا عند مليك السوء، إن ثبت الخبر','1168','1590','0');
INSERT INTO [t121382] VALUES (1737,'(١٤٧) باب ذكر إعطاء المرء المال ناويا الصدقة. وألقاه ذلك المال موضع الصدقة من غير نطق منه بأنه صدقة','1168','1590','0');
INSERT INTO [t121382] VALUES (1738,'(١٤٨) باب ذكر الدليل على أن النبي - صلى الله عليه وسلم - إنما فضل صدقة المقل إذا كان فضلا عمن يعول، ولا إذا تصدق على الأباعد وترك من يعول جياعا','1168','1590','0');
INSERT INTO [t121382] VALUES (1739,'(١٤٩) باب الزجر عن عيب المتصدق المقل بالقليل من الصدقة ولمزه، والزجر عن رمي المتصدق بالكثير من الصدقة بالرياء والسمعة، إذ الله - عز وجل - هو العالم بإرادة المراد، ولا إرادة مما تكنه القلوب، ولم يطلع الله العباد على ما في ضمائر غيرهم من الإرادة','1169','1590','0');
INSERT INTO [t121382] VALUES (1740,'(١٥٠) باب فضل صدقة الصحيح الشحيح الخائف من الفقر، المؤمل طول العمر على صدقة المريض الخائف نزول المنية به','1169','1590','0');
INSERT INTO [t121382] VALUES (1741,'(١٥١) باب فضل صدقة المرء بأحب ماله لله، إذ الله - عز وجل - نفى إدراك البر عمن لا ينفق مما يحب. قال الله - عز وجل -: (لن تنالوا البر حتى تنفقوا مما تحبون) [آل عمران:٩٢]','1170','1590','0');
INSERT INTO [t121382] VALUES (1742,'(١٥٢) باب ذكر حب الله - عز وجل - المخفي بالصدقة إذ الله - عز وجل - قد فضلها على صدقة العلانية. قال الله: (إن تبدوا الصدقات فنعما هي وإن تخفوها وتؤتوها الفقراء فهو خير لكم). [البقره:٢٧١]','1170','1590','0');
INSERT INTO [t121382] VALUES (1743,'(١٥٣) باب ذكر مثل ضربه النبي - صلى الله عليه وسلم - للمتصدق ومنع [٢٤٩ - أ] الشياطين إياه منها بتخويف الفقير إن صح الخبر، فإني لا أقف هل سمع الأعمش من ابن بريدة أم لا. قال الله - عز وجل -: (الشيطان يعدكم الفقر ويأمركم بالفحشاء ...) الآية [البقرة: ٢٦٨]','1171','1590','0');
INSERT INTO [t121382] VALUES (1744,'(١٥٤) باب الأمر بإتيان القرابة بما يتقرب به المولى لله - عز وجل - من صدقة التطوع','1171','1590','0');
INSERT INTO [t121382] VALUES (1745,'(١٥٥) باب ذكر الدليل على أن احتمال الشهادة بصدقة العقار جائز للشهود إذا علموا العقار المتصدق به من غير تحديد، إذ العقار مشهورا بالمتصدق منسوب إليه مستغن بشهرته ونسبته إلى المتصدق به عن ذكر تحديده. والدليل على إباحة الحاكم احتمال الشهادة إذا شهد عليها','1172','1590','0');
INSERT INTO [t121382] VALUES (1746,'(١٥٦) باب استحباب إتيان المرأة زوجها وولدها بصدقة التطوع على غيرهم من الأباعد؛ إذ هم أحق بأن يتصدق عليهم من الأباعد','1173','1590','0');
INSERT INTO [t121382] VALUES (1747,'(١٥٧) باب ذكر تضعيف صدقة المرأة على زوجها وعلى ما في حجرها على الصدقة على غيرهم','1174','1590','0');
INSERT INTO [t121382] VALUES (1748,'(١٥٨) باب صدقة المرء على ولده، والدليل على أن الصدقة إذا رجعت إلى المتصدق بها إرثا عن المتصدق عليه جاز له','1175','1590','0');
INSERT INTO [t121382] VALUES (1749,'(١٥٩) باب الأمر بالصدقة من الثمار قبل الجذاذ من كل حائط بقنو يوضع في المسجد','1176','1590','0');
INSERT INTO [t121382] VALUES (1750,'(١٦٠) باب كراهية الصدقة بالحشف من الثمار، وإن كانت الصدقة تطوعا، إذ الصدقة بخير الثمار وأوساطها أفضل من الصدقة بشرارها','1176','1590','0');
INSERT INTO [t121382] VALUES (1751,'(١٦١) باب إعطاء السائل من الصدقة وإن كان زيه [٢٥٠ - أ] زي الأغنياء في المركب والملبس','1176','1590','0');
INSERT INTO [t121382] VALUES (1752,'(١٦٢) باب ذكر مبلغ الثمار الذي يستحب وضع قنو منه للمساكين في المسجد، إذا بلغ جذاذ الرجل من الثمار ذلك المبلغ','1177','1590','0');
INSERT INTO [t121382] VALUES (1753,'(١٦٣) باب ذكر الدليل على أن أمر النبي - صلى الله عليه وسلم - بوضع القنو -الذي ذكرنا- في المسجد للمساكين أمر ندب وإرشاد، لا أمر فريضة وإيجاب، خبر طلحة بن عبد الله من هذا الباب','1177','1590','0');
INSERT INTO [t121382] VALUES (1754,'(١٦٤) باب الأمر بإعطاء السائل وإن قلت العطية وصغرت قيمتها، وكراهية رد السائل من غير إعطاء إذا لم يكن للمسؤول ما يجزل العطية','1178','1590','0');
INSERT INTO [t121382] VALUES (1755,'(١٦٥) باب التغليظ في الرجوع عن صدقة التطوع وتمثيله بالكلب يقي ثم يعود في قيئه','1179','1590','0');
INSERT INTO [t121382] VALUES (1756,'(١٦٦) باب استحباب الإعلان بالصدقة ناويا لاستنان الناس بالمتصدق فيكتب لمبتدئ الصدقة مثل أجر المتصدقين استنانا به','1179','1590','0');
INSERT INTO [t121382] VALUES (1757,'(١٦٧) باب الرخصة في الخيلاء عند الصدقة','1180','1590','0');
INSERT INTO [t121382] VALUES (1758,'(١٦٨) باب كراهية منع الصدقة إذ مانعها مانع استقراض ربه','1181','1590','0');
INSERT INTO [t121382] VALUES (1759,'(١٦٩) باب ذكر البيان أن لأهل الصدقة بابا من أبواب الجنة يخصون بدخولها من ذلك الباب','1182','1590','0');
INSERT INTO [t121382] VALUES (1760,'(١٧٠) باب التغليظ في مسألة الغني الصدقة','1182','1590','0');
INSERT INTO [t121382] VALUES (1761,'(١٧١) باب التغليظ في الصدقة مراءاة وسمعة، والدليل على أن المرائي بالصدقة من أوائل من تستعر بهم النار يوم القيامة','1182','1590','0');
INSERT INTO [t121382] VALUES (1762,'(١٧٢) باب ذكر أول صدقة محبسة تصدق بها في الإسلام، وأشراط المتصدق صدقة المحرمة حبس أصول الصدقة والمنع من بيع رقابها وهبتها وتوريثها، وتسبيل منافعها وغلاتها على الفقراء، والقربى، والرقاب، وفي سبيل الله، وابن السبيل، والضعيف','1184','1590','0');
INSERT INTO [t121382] VALUES (1763,'(١٧٣) باب إباحة الحبس على من لا يحصون لكثرة العدد، والدليل على أن الحبس إذا كان على قوم لا يحصون عددا لكثرتهم جائز أن تعطى منافع تلك الصدقة بعض أهل تلك الصفة، ضد قول من زعم أن الوصية إذا أوصى بها لقوم لا يحصون لكثرة عددهم أن الوصية باطلة غير جائزة على اتفاقهم معنا أنه إذا أوصى للمساكين والفقراء بثلثه أو ببعض ثلثه أن الوصية جائزة و [لو] أعطى وصيه بعض الفقراء أو بعض المساكين أو جميع المساكين وجميع الفقراء لا يحصون كثرة','1185','1590','0');
INSERT INTO [t121382] VALUES (1764,'(١٧٤) باب إجازة الحبس على قوم موهومين غير مسمين، وفي سبيل الله، وفي الرقاب، وفي الضيف عن غير اشتراط حصة سبيل الله، وحصة الرقاب، وحصة الضيف منها، وإباحة','1186','1590','0');
INSERT INTO [t121382] VALUES (1765,'(١٧٥) باب ذكر الدليل على أن قوله: تصدق بها على الفقراء، والقربى، إنما أراد تصدق بأصلها حبسا','1186','1590','0');
INSERT INTO [t121382] VALUES (1766,'(١٧٦) باب إباحة حبس آبار المياه','1187','1590','0');
INSERT INTO [t121382] VALUES (1767,'(١٧٧) باب الوصية بالحبس من الضياع والأرضين','1187','1590','0');
INSERT INTO [t121382] VALUES (1768,'(١٧٨) باب فضائل بناء السوق لأبناء السابلة، وحفر الأنهار للشارب، مع الدليل على أن قوله في خبر العلاء، عن أبيه، عن أبي هريرة، وخبر أبي قتادة في قوله: أن صدقة قد جرت تلك اللفظة بناء المساجد وبناء البيوت للسابلة، وحفر الأنهار للشاربة، أن كل ما ينتفع به المسلمون مما يفعله المرء قد يقع عليه اسم الصدقة','1188','1590','0');
INSERT INTO [t121382] VALUES (1769,'(١٧٩) باب حبس آبار المياه على الأغنياء والفقراء وابن السبيل','1188','1590','0');
INSERT INTO [t121382] VALUES (1770,'(١٨٠) باب إباحة شرب المحبس من ماء الآبار التي حبسها','1189','1590','0');
INSERT INTO [t121382] VALUES (1771,'(١٨١) باب ذكر الدليل على أن أجر الصدقة المحبسة يكتب للمحبس بعد موته ما دامت الصدقة جارية','1190','1590','0');
INSERT INTO [t121382] VALUES (1772,'(١٨٢) باب فضل سقي الماء إن صح الخبر','1190','1590','0');
INSERT INTO [t121382] VALUES (1773,'(١٨٣) باب الصدقة عن الميت عن غير وصية من مال الميت، وتكفير ذنوب الميت بها','1191','1590','0');
INSERT INTO [t121382] VALUES (1774,'(١٨٤) باب ذكر كتابة الأجر للميت عن غير وصية بالصدقة عنه من ماله','1191','1590','0');
INSERT INTO [t121382] VALUES (1775,'(١٨٥) باب الصدقة عن الميت إذا توفي عن غير [وصية وانتفاع] الميت في الآخرة بها','1191','1590','0');
INSERT INTO [t121382] VALUES (1776,'(١٨٦) باب إيجاب الجنة بسقي الماء من لا يجد الماء إلا غبا','1193','1590','0');
INSERT INTO [t121382] VALUES (1777,'كتاب المناسك','1194','0','0');
INSERT INTO [t121382] VALUES (1778,'(١) باب فرض الحج على من استطاع إليه سبيلا. قال الله - عز وجل -: (ولله على الناس حج البيت من استطاع إليه سبيلا) [آل عمران: ٩٧] والبيان أن الحج على من استطاع إليه السبيل من الإسلام','1194','1777','0');
INSERT INTO [t121382] VALUES (1779,'(٢) باب ذكر الدليل على أن اسم الإسلام باسم المعرفة الألف واللام قد يقع على بعض شعب الإسلام، والدليل على أن النبي - صلى الله عليه وسلم - إنما أجاب جبريل في الخبر الذي ذكرنا عن أصل الإسلام وأساسه، إذ النبي - صلى الله عليه وسلم - أعلم أن الإسلام بني على هذه الخمس، وما بني من الإسلام على هذه الخمس سوى هذه الخمس، إذ البناء على الأساس سوى الأساس، وقد أوقع النبي - صلى الله عليه وسلم - اسم الإسلام باسم المعرفة بالألف واللام على أجزاء الإسلام التي هي سوى هذه الخمس التي أعلم في إجابته جبريل أنها الإسلام','1195','1777','0');
INSERT INTO [t121382] VALUES (1780,'(٣) باب الأمر بتعجيل الحج خوف فوته برفع الكعبة؛ إذ النبي - صلى الله عليه وسلم - أعلم أنها ترفع بعد هدم مرتين','1195','1777','0');
INSERT INTO [t121382] VALUES (1781,'(٤) باب ذكر الدليل على أن رفع البيت يكون بعد خروج يأجوج ومأجوج بعد مدة لا قبل خروجهما [٢٥٣ - ب] إذ النبي - صلى الله عليه وسلم - قد أعلم أنه يعتمر ويحج البيت بعد خروج يأجوج ومأجوج','1196','1777','0');
INSERT INTO [t121382] VALUES (1782,'(٥) باب ذكر بيان فرض الحج، وأن الفرض حجة واحدة على المرء لا أكثر منها','1196','1777','0');
INSERT INTO [t121382] VALUES (1783,'(٦) باب إباحة إعطاء الإمام إبل الصدقة من يحج عليها','1197','1777','0');
INSERT INTO [t121382] VALUES (1784,'(٧) باب الرخصة في الحج على الدواب المحبسة في سبيل الله','1197','1777','0');
INSERT INTO [t121382] VALUES (1785,'(٨) باب فضل الحج، إذ الحاج من وفد الله - عز وجل -','1197','1777','0');
INSERT INTO [t121382] VALUES (1786,'(٩) باب الأمر بالمتابعة بين الحج والعمرة، والبيان أن الفعل قد يضاف إلى الفعل، لا أن الفعل يفعل فعلا كما ادعى بعض أهل الجهل','1197','1777','0');
INSERT INTO [t121382] VALUES (1787,'(١٠) باب فضل الحج الذي لا رفث، ولا فسوق فيه، وتكفير الذنوب والخطايا به','1198','1777','0');
INSERT INTO [t121382] VALUES (1788,'(١١) باب ذكر البيان أن الحج يهدم ما كان قبله من الذنوب والخطايا [٢٥٤ - أ]','1198','1777','0');
INSERT INTO [t121382] VALUES (1789,'(١٢) باب استحباب دعاء الحاج، إذ النبي - صلى الله عليه وسلم - قد استغفر لهم ولمن استغفروا له','1199','1777','0');
INSERT INTO [t121382] VALUES (1790,'(١٣) باب استحباب الخروج إلى الحج يوم الخميس تبركا بفعل النبي - صلى الله عليه وسلم -، إذ كان - صلى الله عليه وسلم - قلما يخرج في سفر إلا يوم الخميس','1199','1777','0');
INSERT INTO [t121382] VALUES (1791,'(١٤) باب استحباب التزود للسفر اقتداء بالنبي - صلى الله عليه وسلم -، ومخالفة لبعض متصوفة أهل زماننا','1199','1777','0');
INSERT INTO [t121382] VALUES (1792,'(١٥) باب الزجر عن سفر المرأة مع غير ذي محرم، وغير زوجها، بذكر خبر في التأقيت غير دال توقيته، على أن ما كان أقل من ذلك التأقيت من السفر مباح سفر المرأة مع غير محرم، وغير زوجها، إذا كان سفرها أقل من ثلاث','1200','1777','0');
INSERT INTO [t121382] VALUES (1793,'(١٦) باب الزجر عن سفر المرأة يومين مع غير زوجها، وغير ذي رحمها، والدليل على صحة ما تأولت أن النبي - صلى الله عليه وسلم - لم يبح - بزجره عن سفرها ثلاثا، لها أن تسافر أقل من ثلاث مع غير زوجها، وغير ذي رحمها، بذكر لفظة في توقيت اليومين لم يرد النبي - صلى الله عليه وسلم - بتوقيته يومين إباحة لما هو أقل منها','1201','1777','0');
INSERT INTO [t121382] VALUES (1794,'(١٧) باب الزجر عن سفر المرأة يوما وليلة إلا مع ذي محرم، والدليل على أن النبي - صلى الله عليه وسلم - لم يبح بزجره إياها عن سفر يومين سفر ما هو أقل من يومين، إذ قد زجرها - صلى الله عليه وسلم - أن تسافر يوما وليلة إلا مع ذي محرم','1201','1777','0');
INSERT INTO [t121382] VALUES (1795,'(١٨) باب ذكر الدليل على أن النبي - صلى الله عليه وسلم - لم يبح بزجره عن سفرها مع غير ذوي محرم يوما وليلة السفر الذي هو أقل منه، إذ قد زجر - صلى الله عليه وسلم - أيضا أن تسافر ليلة واحدة مع غير ذي محرم اللهم إلا أن يكون هذا من الجنس الذي أعلمت في غير موضع من كتبنا أن العرب تذكر يوما تريد بليلته، وليلة تريد بيومها. قال الله - عز وجل - في سورة آل عمران [٤١]: (آيتك ألا تكلم الناس ثلاثة أيام إلا رمزا). وقال في سورة مريم [١٠]: (آيتك ألا تكلم الناس ثلاث ليال سويا)، فبان وثبت أنه أراد ثلاثة أيام بلياليها، وصح أنه أراد ثلاث ليال بأيامهن','1202','1777','0');
INSERT INTO [t121382] VALUES (1796,'(١٩) باب الزجر عن سفر المرأة بريدا مع غير ذي محرم، والدليل أن النبي - صلى الله عليه وسلم - أراد بزجره إياها عن سفر يوم وليلة أنه مباح لها سفر ما هو أقل من يوم وليلة','1202','1777','0');
INSERT INTO [t121382] VALUES (1797,'(٢٠) باب ذكر الدليل على أن زجر النبي - صلى الله عليه وسلم - عن سفرها بلا محرم زجر تحريم لا زجر تأديب','1203','1777','0');
INSERT INTO [t121382] VALUES (1798,'(٢١) باب إباحة سفر المرأة مع عبد زوجها أو مولاه، إذا كان العبد أو المولى يوثق بدينه وأمانته، وإن لم يكن العبد أو المولى بمحرم للمرأة إن كان حكم سائر النساء حكم أزواج النبي - صلى الله عليه وسلم - ولا أخال، لأن الله - عز وجل - أخبر أنهن أمهات المؤمنين، فجائز أن يكون العبد والأحرار محرما لأزواج النبي - صلى الله عليه وسلم -، فكان سفر ميمونة مع أبي رافع أن ميمونة أم أبي رافع، إذ كانت ميمونة زوجة النبي - صلى الله عليه وسلم -','1203','1777','0');
INSERT INTO [t121382] VALUES (1799,'(٢٢) باب ذكر خروج المرأة لأداء فرض الحج بغير محرم، وأمر الحاكم زوجها باللحاق بها للحج بها','1204','1777','0');
INSERT INTO [t121382] VALUES (1800,'(٢٣) باب توديع المسلم أخاه عند إرادة السفر','1204','1777','0');
INSERT INTO [t121382] VALUES (1801,'(٢٤) باب دعاء المرء لأخيه المسلم عند إرادته السفر','1205','1777','0');
INSERT INTO [t121382] VALUES (1802,'(٢٥) باب الدعاء عند الخروج إلى السفر','1205','1777','0');
INSERT INTO [t121382] VALUES (1803,'(٢٦) باب الرخصة في الخروج إلى الحج ماشيا لمن قدر على المشي، ولم يكن عيالا على رفقائه','1205','1777','0');
INSERT INTO [t121382] VALUES (1804,'(٢٧) باب استحباب ربط الأوساط بالأزر، وسرعة المشي إذا كان المرء ماشيا','1206','1777','0');
INSERT INTO [t121382] VALUES (1805,'(٢٨) باب استحباب النسل في المشي عند الإعياء من المشي ليخف الناسل ويذهب بعض الإعياء عنه','1206','1777','0');
INSERT INTO [t121382] VALUES (1806,'(٢٩) باب استحباب مصاحبة الأربعة في السفر','1207','1777','0');
INSERT INTO [t121382] VALUES (1807,'(٣٠) باب حسن المصاحبة في السفر، إذ خير الأصحاب خيرهم لصاحبه','1207','1777','0');
INSERT INTO [t121382] VALUES (1808,'(٣١) باب استحباب تأمير المسافرين أحدهم على أنفسهم، والبيان أن أحقهم بذلك أكثرهم جمعا للقرآن','1207','1777','0');
INSERT INTO [t121382] VALUES (1809,'(٣٢) باب التكبير والتسبيح والدعاء عند ركوب الدواب، عند إرادة المرء الخروج مسافرا','1208','1777','0');
INSERT INTO [t121382] VALUES (1810,'(٣٣) باب الأمر بتسمية الله - عز وجل - عند الركوب، وإباحة الحمل على الإبل في المسير قدر طاقتها','1209','1777','0');
INSERT INTO [t121382] VALUES (1811,'(٣٤) باب الزجر عن اتخاذ الدواب كراسي بوقفها والمرء راكبها، غير سائر عليها، ولا نازل عنها','1209','1777','0');
INSERT INTO [t121382] VALUES (1812,'(٣٥) باب استحباب الإحسان إلى الدواب المركوبة في العلف والسقي، وكراهية إجاعتها وإعطاشها، وركوبها والسير عليها جياعا عطاشا','1209','1777','0');
INSERT INTO [t121382] VALUES (1813,'(٣٦) باب إباحة الحمل على الدواب المركوبة في السير طلبا لقضاء الحوائج، إذا ذكر اسم الله عليها عند الركوب، بذكر خبر مختصر غير متقصى','1210','1777','0');
INSERT INTO [t121382] VALUES (1814,'(٣٧) باب الدليل على أن النبي - صلى الله عليه وسلم - إنما أباح الحمل على الدواب المركوبة، وأن لا تقصر على طلب حاجة، إذ الله - عز وجل - يراقبه، ورحمته تحمل الراكب بأن يقوي المركوب ليقضي الراكب حاجته','1210','1777','0');
INSERT INTO [t121382] VALUES (1815,'(٣٨) باب ذكر الدليل على أن النبي - صلى الله عليه وسلم - إنما أباح أن لا يقتصر عن حاجة إذا ركب الدواب من غير أن يجاوز السائر المنازل، إذا كانت الأرض مخصبة، والأمر بإمكان الركاب عن الرعي في الخصب إن صح الخبر، فإن في القلب من سماع الحسن من جابر','1211','1777','0');
INSERT INTO [t121382] VALUES (1816,'(٣٩) باب صفة السير في الخصب والجدب، والدليل على أن النبي - صلى الله عليه وسلم - إنما أمر بسرعة السير في الجدب كي يقطع الدواب المركوبة السفر بنقيها قبل تعجف، فيذهب نقي عظامها من الهزال والعجف','1212','1777','0');
INSERT INTO [t121382] VALUES (1817,'(٤٠) باب الزجر عن ضرب الدواب على الوجه، وفيه ما دل على أن الضرب على غير الوجه مباح','1212','1777','0');
INSERT INTO [t121382] VALUES (1818,'(٤١) باب الزجر عن ركوب الجلالة من الدواب المركوبة','1213','1777','0');
INSERT INTO [t121382] VALUES (1819,'(٤٢) باب الزجر عن صحبة الرفقة التي يكون فيها الكلب، أو الجرس، إذ الملائكة لا تصحبها','1213','1777','0');
INSERT INTO [t121382] VALUES (1820,'(٤٣) باب ذكر الدليل على أن الملائكة لا تصحب رفقة فيها جرس، إذ الجرس مزمار [٢٥٧ - أ] الشيطان','1213','1777','0');
INSERT INTO [t121382] VALUES (1821,'(٤٤) باب استحباب الدلجة بالليل، إذ الله - عز وجل - يطوي الأرض بالليل، فيكون السير بالليل أقطع للسفر','1214','1777','0');
INSERT INTO [t121382] VALUES (1822,'(٤٥) باب الزجر عن التعريس على جواد الطريق','1214','1777','0');
INSERT INTO [t121382] VALUES (1823,'(٤٦) باب صفة النوم في العرس','1214','1777','0');
INSERT INTO [t121382] VALUES (1824,'(٤٧) باب كراهية سير أول الليل','1215','1777','0');
INSERT INTO [t121382] VALUES (1825,'(٤٨) باب ذكر توقيت أول الليل الذي كره الانتشار والخروج فيه','1215','1777','0');
INSERT INTO [t121382] VALUES (1826,'(٤٩) باب وصية المسافر بالتكبير عند صعود الشرف، والتسبيح عند الهبوط','1215','1777','0');
INSERT INTO [t121382] VALUES (1827,'(٥٠) باب استحباب خفض الصوت بالتكبير عند صعود الشرف في الأسفار','1216','1777','0');
INSERT INTO [t121382] VALUES (1828,'(٥١) باب فضل الصلاة عند تعريس الناس بالليل','1216','1777','0');
INSERT INTO [t121382] VALUES (1829,'(٥٢) باب الدعاء عند رؤية القرى اللواتي يريد المرء دخولها','1217','1777','0');
INSERT INTO [t121382] VALUES (1830,'(٥٣) باب الاستعاذة عند نزول المنازل','1217','1777','0');
INSERT INTO [t121382] VALUES (1831,'(٥٤) باب توديع المنازل بالصلاة','1218','1777','0');
INSERT INTO [t121382] VALUES (1832,'(٥٥) باب النهي عن سير الوحدة بالليل','1218','1777','0');
INSERT INTO [t121382] VALUES (1833,'(٥٦) باب النهي عن سير الاثنين، والدليل على أن ما دون الثلاث من المسافرين فهم عصاة، إذ النبي - صلى الله عليه وسلم - قد أعلم أن الواحد شيطان، والاثنان شيطانان، ويشبه أن يكون معنى قوله شيطان أو عاصي، كقوله: (شياطين الإنس والجن) [الأنعام: ١١٢]، ومعناه: عصاة الجن والإنس','1218','1777','0');
INSERT INTO [t121382] VALUES (1834,'(٥٧) باب دعاء المسافر عند الصباح','1219','1777','0');
INSERT INTO [t121382] VALUES (1835,'(٥٨) باب صفة الدعاء بالليل في الأسفار','1219','1777','0');
INSERT INTO [t121382] VALUES (1836,'(٥٩) باب تقليد البدن وإشعارها عند السوق','1220','1777','0');
INSERT INTO [t121382] VALUES (1837,'(٦٠) باب إشعار البدن في شق السنام الأيمن، وسلت الدم عنها، ضد قول من زعم أن إشعار البدن مثلة، فسمى سنة النبي - صلى الله عليه وسلم - مثلة بجهله','1220','1777','0');
INSERT INTO [t121382] VALUES (1838,'(٦١) باب الهدي إذا عطب قبل أن يبلغ محله','1221','1777','0');
INSERT INTO [t121382] VALUES (1839,'(٦٢) باب الزجر عن أكل سائق البدن، وأهل رفقته، من لحمها إذا عطبت ونحرت','1221','1777','0');
INSERT INTO [t121382] VALUES (1840,'(٦٣) باب إيجاب إبدال الهدي الواجب إذا ضلت، إن صح الخبر، ولا أخال، فإن في القلب من عبد الله بن عامر الأسلمي','1222','1777','0');
INSERT INTO [t121382] VALUES (1841,'(٦٤) باب التطيب عند الإحرام ضد قول من كره ذلك، وخالف سنة النبي - صلى الله عليه وسلم -','1222','1777','0');
INSERT INTO [t121382] VALUES (1842,'(٦٥) باب الرخصة في التطيب عند الإحرام بالمسك، والدليل على أن المسك طاهر غير نجس، لا على ما زعم بعض التابعين أنه ميتة نجس، زعم أنه سقط من حي وهو ميت نجس','1223','1777','0');
INSERT INTO [t121382] VALUES (1843,'(٦٦) باب الرخصة في التطيب عند الإحرام بطيب يبقى أثره على المتطيب في الإحرام','1224','1777','0');
INSERT INTO [t121382] VALUES (1844,'(٦٧) باب استحباب الاغتسال بعد التطيب عند الإحرام مع استحباب جماع المرء امرأته إذا أراد الإحرام كي يكون أقل شهوة لجماع النساء في الإحرام إذا كان حديث عهد بجماعهن','1224','1777','0');
INSERT INTO [t121382] VALUES (1845,'(٦٨) باب ذكر مواقيت الإحرام بالحج والعمرة، أو بأحدهما لمن منازلهم وراء المواقيت','1225','1777','0');
INSERT INTO [t121382] VALUES (1846,'(٦٩) باب إحرام أهل المناهل التي هي أقرب إلى الحرم من هذه المواقيت التي وقتها النبي - صلى الله عليه وسلم - لمن منازلهم وراءها، والبيان أن مواقيت من منزله أقرب إلى الحرم من هذه المواقيت: منازلهم','1226','1777','0');
INSERT INTO [t121382] VALUES (1847,'(٧٠) باب ذكر البيان أن هذه المواقيت التي ذكرناها كل ميقات منها لأهله، ولمن مر به من غير أهله','1226','1777','0');
INSERT INTO [t121382] VALUES (1848,'(٧١) باب ذكر ميقات أهل العراق، إن ثبت الخبر مسندا','1227','1777','0');
INSERT INTO [t121382] VALUES (1849,'(٧٢) باب كراهية الإحرام وراء المواقيت التي وقت النبي - صلى الله عليه وسلم - لأهل الآفاق الذين منازلهم وراءها، إذ النبي - صلى الله عليه وسلم - وقت هذه المواقيت لأهلها ولمن أتى عليها من غير أهلها، والمصطفى - صلى الله عليه وسلم - وجميع من خرج [٢٥٩ - ب] من المدينة وقت إرادتهم الحج خرجوا فجلس حتى أتوا ذا الحليفة فأحرموا منه. ولو كان الإحرام وراء المواقيت أو من منازلهم وراء المواقيت سنة أو خيرا أو أفضل لأشبه أن يكون المصطفى - صلى الله عليه وسلم - يحرم من المدينة ويأمر أصحابه بالإحرام منها، واتباع سنة النبي - صلى الله عليه وسلم - أفضل مما سواها','1227','1777','0');
INSERT INTO [t121382] VALUES (1850,'(٧٣) باب أمر النفساء بالاغتسال والاستثفار إذا أرادت الإحرام، وإن كان الاغتسال لا يطهر ما يطهر غير النفساء وغير الحيض، إذ النفساء والحيض لا يطهرن بالاغتسال ما لم يطهرن بانقطاع دم النفاس والحيض، والبيان أن ليس في السنة إلا اتباعها، إذ لو كان من جهة العقل والرأي لم يكن لاغتسال النفساء والحيض قبل [أن] يطهرن معنى من جهة العقل والرأي، ولكن لما أمر النبي - صلى الله عليه وسلم - النفساء والحائض بالغسل وجب قبول أمره وترك الرأي والقياس','1228','1777','0');
INSERT INTO [t121382] VALUES (1851,'(٧٤) باب استحباب الاغتسال للإحرام','1228','1777','0');
INSERT INTO [t121382] VALUES (1852,'(٧٥) باب النهي عن الإحرام بالحج في غير أشهر الحج، إذ الله -جل وعلا- جعل الحج أشهرا معلومات، فغير جائز الدخول في الحج قبل وقته، كما لا يجوز الدخول في الصلوات قبل أوقاتها','1229','1777','0');
INSERT INTO [t121382] VALUES (1853,'(٧٦) باب ذكر الثياب الذي زجر المحرم عن لبسها في الإحرام','1229','1777','0');
INSERT INTO [t121382] VALUES (1854,'(٧٧) باب الزجر عن لبس الأقبية في الإحرام','1230','1777','0');
INSERT INTO [t121382] VALUES (1855,'(٧٨) باب الزجر عن انتقاب المرأة وعن التقفز في الإحرام','1230','1777','0');
INSERT INTO [t121382] VALUES (1856,'(٧٩) باب الإحرام في الأزر والأردية والنعال','1230','1777','0');
INSERT INTO [t121382] VALUES (1857,'(٨٠) باب اشتراط من به علة عند الإحرام أن محله حيث يحبس ضد قول من كره ذلك','1231','1777','0');
INSERT INTO [t121382] VALUES (1858,'(٨١) باب الاكتفاء بالنية عند الإحرام بالحج أو العمرة أو هما عند الإهلال عن النطق بذلك','1231','1777','0');
INSERT INTO [t121382] VALUES (1859,'(٨٢) باب إباحة القران بين الحج والعمرة، والإفراد، والتمتع، والبيان أن كل هذا جائز طلق مباح، والمرء مخير بين القران والإفراد وبين التمتع يهل بما شاء من ذلك','1232','1777','0');
INSERT INTO [t121382] VALUES (1860,'(٨٣) باب استحباب التمتع بالعمرة [٢٦٠ - ب] إلى الحج إذ النبي - صلى الله عليه وسلم - أعلم أصحابه أن لو استقبل من أمره ما استدبر لما ساق الهدي ولحل بعمرة، كما أمر من لم يسق الهدي بالإهلال بعمرة','1232','1777','0');
INSERT INTO [t121382] VALUES (1861,'(٨٤) باب أمر المهل بالعمرة الذي معه الهدي بالإهلال بالحج مع العمرة ليصير قارنا، إذ سائق الهدي المهل بالعمرة غير جائز له الإحلال منها قبل مبلغ الهدي محله','1233','1777','0');
INSERT INTO [t121382] VALUES (1862,'(٨٥) باب تقليد الغنم عند الإحرام إذا ساق الهدي','1233','1777','0');
INSERT INTO [t121382] VALUES (1863,'(٨٦) باب حديث الإحرام خلف الصلاة المكتوبة إذا حضرت','1234','1777','0');
INSERT INTO [t121382] VALUES (1864,'(٨٧) باب إباحة الإحرام من غير صلاة متقدمة من مكتوبة أو تطوع، والدليل أن غير المتطهر والجنب إن أحرم بالحج أو العمرة أو هما كان الإحرام جائزا، إذ النبي - صلى الله عليه وسلم - قد أمر النفساء والحائض بالإحرام وهما غير طاهرتين، غير جائز لهما الصلاة في الوقت الذي أمرهما النبي صلى الله عليه وسلم بالإحرام إذ النفساء والحائض لا تجزئهما الصلاة قبل [أن] تطهرا ولا تطهران بالاغتسال قبل [أن] تطهرا بانقطاع [٢٦١ - أ] دم الحيض والنفاس','1234','1777','0');
INSERT INTO [t121382] VALUES (1865,'(٨٨) باب الإهلال عند مسجد ذي الحليفة','1235','1777','0');
INSERT INTO [t121382] VALUES (1866,'(٨٩) باب الإهلال إذا استوت بالراكب ناقته عند مسجد ذي الحليفة، ضد قول من زعم أن النبي - صلى الله عليه وسلم - لم يهل حتى أتى البيداء، وهذا من الجنس الذي أعلمت في غير موضع في كتبنا أن الخبر الواجب قبوله هو خبر من يخبر بسماع الشيء ورؤيته دون من ينكر الشيء ويدفعه','1235','1777','0');
INSERT INTO [t121382] VALUES (1867,'(٩٠) باب استحباب الاستقبال بالراحلة القبلة إذا أراد الراكب الإهلال','1236','1777','0');
INSERT INTO [t121382] VALUES (1868,'(٩١) باب استحباب البيتوتة بذي الحليفة والغدو منها استنانا بالنبي - صلى الله عليه وسلم -','1236','1777','0');
INSERT INTO [t121382] VALUES (1869,'(٩٢) باب استحباب التعرس في بطن الوادي بذي الحليفة','1236','1777','0');
INSERT INTO [t121382] VALUES (1870,'(٩٣) باب استحباب الصلاة في ذلك الوادي','1237','1777','0');
INSERT INTO [t121382] VALUES (1871,'(٩٤) باب استحباب الإهلال بما يحرم به المهل من حج أو عمرة أو هما','1237','1777','0');
INSERT INTO [t121382] VALUES (1872,'(٩٥) باب إباحة الإحرام من غير تسمية حج ولا عمرة، ومن غير قصد نية واحد بعينه عند ابتداء الإحرام','1237','1777','0');
INSERT INTO [t121382] VALUES (1873,'(٩٦) باب صفة تلبية النبي - صلى الله عليه وسلم -','1238','1777','0');
INSERT INTO [t121382] VALUES (1874,'(٩٧) باب ذكر البيان أن الزيادة في التلبية على ما حفظ ابن عمر، عن النبي - صلى الله عليه وسلم - جائز، والدليل على أن بعض أصحاب النبي - صلى الله عليه وسلم - قد يحفظ عنه ما يغرب عن بعضهم، لأن أبا هريرة قد حفظ من النبي - صلى الله عليه وسلم - في تلبيته ما لم يحك عنه غيره','1239','1777','0');
INSERT INTO [t121382] VALUES (1875,'(٩٨) باب إباحة الزيادة في التلبية: "ذا المعارج" ونحوه، ضد قول من كره هذه الزيادة وذكر أنهم لم يقولوه مع النبي - صلى الله عليه وسلم-، مع الدليل على أن من تقدمت صحبته للنبي - صلى الله عليه وسلم - وكان أعلم قد كان يخفى عليه الشيء من','1239','1777','0');
INSERT INTO [t121382] VALUES (1876,'(٩٩) باب استحباب رفع الصوت بالتلبية','1240','1777','0');
INSERT INTO [t121382] VALUES (1877,'(١٠٠) باب البيان أن رفع الصوت بالإهلال من شعار الحج، وإنما أمر المهل برفع الصوت به إذ هو من شعار الحج','1241','1777','0');
INSERT INTO [t121382] VALUES (1878,'(١٠١) باب ذكر البيان أن رفع الصوت بالإهلال من أفضل الأعمال [٢٦٢ - ب]','1242','1777','0');
INSERT INTO [t121382] VALUES (1879,'(١٠٢) باب استحباب وضع الإصبعين في الأذنين عند رفع الصوت والتلبية، إذ وضع الإصبعين في الأذنين عند رفع الصوت يكون أرفع صوتا، وأمده','1242','1777','0');
INSERT INTO [t121382] VALUES (1880,'(١٠٣) باب ذكر تلبية الأشجار والأحجار اللواتي عن يمين الملبي وعن شماله عند تلبية الملبي','1243','1777','0');
INSERT INTO [t121382] VALUES (1881,'(١٠٤) باب الزجر عن معونة المحرم للحلال على الاصطياد بالإشارة ومناولة السلاح الذي يكون عونا للحلال على الاصطياد','1243','1777','0');
INSERT INTO [t121382] VALUES (1882,'(١٠٥) باب ذكر الدليل على أن المحرم إذا أشار للحلال الصيد فاصطاده الحلال، لم يجز أكله للمحرم','1244','1777','0');
INSERT INTO [t121382] VALUES (1883,'(١٠٦) باب كراهية قبول المحرم الصيد إذا أهدي له في إحرامه، والدليل على أن المحرم غير جائز له ملك الصيد في إحرامه','1244','1777','0');
INSERT INTO [t121382] VALUES (1884,'(١٠٧) باب ذكر خبر روي عن النبي - صلى الله عليه وسلم - في إباحة أكل لحم الصيد للمحرم، مجمل غير مفسر قد يحسب بعض من لا يميز بين الخبر المجمل والمفسر أن أكل لحم الصيد للمحرم إذا اصطاده الحلال طلق حلال بكل حال','1245','1777','0');
INSERT INTO [t121382] VALUES (1885,'(١٠٨) باب ذكر خبر روي عن النبي - صلى الله عليه وسلم - في رده لحم صيد أهدي له في إحرامه مجمل غير مفسر، وقد يحسب بعض من لم يتبحر العلم ولا يميز بين المجمل والمفسر من الأخبار أن لحم الصيد محرم على المحرم بكل حال وإن اصطاده الحلال','1246','1777','0');
INSERT INTO [t121382] VALUES (1886,'(١٠٩) باب ذكر الخبر المفسر للأخبار التي ذكرناها في البابين المتقدمين، والدليل على أن النبي - صلى الله عليه وسلم - إنما أباح أكل لحم الصيد للمحرم إذا اصطاده الحلال، إذا لم يكن الحلال اصطاده من أجل المحرم، وإنه إنما كره للمحرم أكل لحم الصيد الذي اصطاده الحلال من أجل الحرام','1247','1777','0');
INSERT INTO [t121382] VALUES (1887,'(١١٠) باب الزجر عن أكل المحرم بيض الصيد إذا [٢٦٤ - أ] أخذ البيض من أجل المحرم','1248','1777','0');
INSERT INTO [t121382] VALUES (1888,'(١١١) باب الزجر عن قتل الضبع في الإحرام إذ النبي - صلى الله عليه وسلم - المولى بيان ما أنزل الله عليه من الوحي إليه، قد أعلم أن الضبع صيد، والله - عز وجل - في محكم تنزيله قد نهى المحرم عن قتل الصيد فقال: (لا تقتلوا الصيد وأنتم حرم) [المائدة: ٩٥]','1249','1777','0');
INSERT INTO [t121382] VALUES (1889,'(١١٢) باب ذكر جزاء الضبع إذا قتله المحرم','1249','1777','0');
INSERT INTO [t121382] VALUES (1890,'(١١٣) باب الدليل على أن الكبش الذي قضى به جزاء للضبع هو المسن منه لا ما دون المسن، مع الدليل على أن الله - عز وجل - أراد بقوله: (فجزاء مثل ما قتل من النعم) [المائدة: ٩٥] أقرب الأشياء شبها بالبدن من النعم، لا مثله في القيمة كما قاله بعض العراقيين، إذ العلم محيط أن قيمة الضبع تختلف في الأزمان والبلدان، وكذلك قيمة الكبش قد تزيد وتنقص في بعض الأزمان والبلدان، ولو كان المثل في القيمة لم يجعل - صلى الله عليه وسلم - جزاء الضبع كبشا في كل وقت وزمان وفي كل بلد','1250','1777','0');
INSERT INTO [t121382] VALUES (1891,'(١١٤) باب الزجر عن تزويج المحرم وخطبته وإنكاحه','1250','1777','0');
INSERT INTO [t121382] VALUES (1892,'(١١٥) باب الرخصة في غسل المحرم رأسه','1251','1777','0');
INSERT INTO [t121382] VALUES (1893,'(١١٦) باب الرخصة في الحجامة للمحرم من غير قطع شعر ولا حلقه','1251','1777','0');
INSERT INTO [t121382] VALUES (1894,'(١١٧) باب الرخصة في ادهان المحرم بدهن غير مطيب إن جاز الاحتجاج بفرقد السبخي، وصحت هذه اللفظة في روايته أن النبي - صلى الله عليه وسلم - ادهن وهو محرم؛ لأن أصحاب حماد بن سلمة قد اختلفوا عنه في هذه اللفظة، أنا خائف أن يكون فرقد واهما في رفعه هذا الخبر','1252','1777','0');
INSERT INTO [t121382] VALUES (1895,'(١١٨) باب إباحة مداواة المحرم عينه -إذا أصابه رمد- بالصبر','1253','1777','0');
INSERT INTO [t121382] VALUES (1896,'(١١٩) باب الرخصة في السواك للمحرم','1253','1777','0');
INSERT INTO [t121382] VALUES (1897,'(١٢٠) باب الرخصة في تلبيد المحرم رأسه كي لا يتأذى بالقمل والصيبان في الإحرام [٢٦٥ - أ]','1253','1777','0');
INSERT INTO [t121382] VALUES (1898,'(١٢١) باب الرخصة في حجامة المحرم على الرأس وإن كان المحجوم ذا جمة أو وفرة بذكر خبر مختصر غير متقصى','1254','1777','0');
INSERT INTO [t121382] VALUES (1899,'(١٢٢) باب ذكر الدليل على أن النبي - صلى الله عليه وسلم - إنما احتجم على رأسه من وجع وجده برأسه','1254','1777','0');
INSERT INTO [t121382] VALUES (1900,'(١٢٣) باب إباحة الحجامة للمحرم على ظهر القدم، والدليل على أن النبي - صلى الله عليه وسلم - قد احتجم محرما غير مرة، مرة على الرأس، ومرة على ظهر القدم','1254','1777','0');
INSERT INTO [t121382] VALUES (1901,'(١٢٤) باب ذكر الدليل على أن الوجع الذي وجده النبي - صلى الله عليه وسلم - في إحرامه فاحتجم بسببه على ظهر القدم وجده بظهره أو بوركه لا بقدمه','1255','1777','0');
INSERT INTO [t121382] VALUES (1902,'(١٢٥) باب إباحة ركوب المحرم البدن إذا ساقه، بلفظ مجمل غير مفسر','1256','1777','0');
INSERT INTO [t121382] VALUES (1903,'(١٢٦) باب ذكر الخبر المفسر لبعض اللفظة المجملة التي ذكرتها، والدليل [على] أن النبي - صلى الله عليه وسلم - إنما أباح ركوب البدن إذا كان راكبها لا يجد ظهرا','1256','1777','0');
INSERT INTO [t121382] VALUES (1904,'(١٢٧) باب ذكر الدليل على أن النبي - صلى الله عليه وسلم - إنما أباح ركوب البدن عند الحاجة إلى ركوبها عند الإعواز من وجود الظهر ركوبا بالمعروف، ومن غير أن يشق الركوب على البدنة','1256','1777','0');
INSERT INTO [t121382] VALUES (1905,'(١٢٨) باب ذكر الدواب التي أبيح للمحرم قتلها في الإحرام بذكر لفظة مجملة في ذكر بعضهن بلفظ عام، مراده خاص على أصلنا','1257','1777','0');
INSERT INTO [t121382] VALUES (1906,'(١٢٩) باب إباحة قتل المحرم الحية وإن كان قاتلها في الحرم لا في الحل','1258','1777','0');
INSERT INTO [t121382] VALUES (1907,'(١٣٠) باب ذكر الخبر المفسر للفظة المجملة التي ذكرتها في بعض ما أبيح قتله للمحرم، والدليل على أن النبي - صلى الله عليه وسلم - إنما أباح للمحرم قتل بعض الغربان لا كلها، وإنه إنما أباح قتل الأبقع منها دون ما سواه من الغربان','1258','1777','0');
INSERT INTO [t121382] VALUES (1908,'(١٣١) باب ذكر تطيب المحرم ولبسه في الإحرام ما لا يجوز لبسه جاهلا','1259','1777','0');
INSERT INTO [t121382] VALUES (1909,'(١٣٢) باب ذكر اللفظة المفسرة للفظة المجملة التي ذكرتها في الطيب، والدليل على أن النبي - صلى الله عليه وسلم - لما أمر المحرم في الجبة بعد النضح بالطيب يغسل ذلك الطيب إذا كان ما تطيب به من طيب','1259','1777','0');
INSERT INTO [t121382] VALUES (1910,'(١٣٣) باب ذكر البيان أن النبي - صلى الله عليه وسلم - إنما أمر هذا المحرم الذي ذكرناه بغسل الطيب الذي كان عليه إذ الطيب الذي كان عليه خلوق فيه زعفران والتزعفر غير جائز للحلال أيضا وإن كان المحرم منهيا عنه، لا كما توهم بعض العراقيين أن النبي - صلى الله عليه وسلم - أمره بغسل ذلك الطيب لأن المحرم غير جائز أن يكون به أثر الطيب وهو محرم وإن كان تطيب به وهو حلال قبل أن يحرم','1260','1777','0');
INSERT INTO [t121382] VALUES (1911,'(١٣٤) باب ذكر زجر النبي - صلى الله عليه وسلم - عن تزعفر المحل والمحرم جميعا','1261','1777','0');
INSERT INTO [t121382] VALUES (1912,'(١٣٥) باب ذكر دليل ثان يدل على صحة ما تأولت أمر النبي - صلى الله عليه وسلم - في خبر يعلى بغسل الطيب الذي كان على المحرم إذ النبي - صلى الله عليه وسلم - قد أمر المحل أيضا بغسل الخلوق الذي كان قد تخلق به فسوى في الأمر بغسل الخلوق بين المحرم والمحل','1262','1777','0');
INSERT INTO [t121382] VALUES (1913,'(١٣٦) باب البيان ضد قول من زعم أن المحرم في الجبة عليه خرق الجبة وغير جائز له نزعها فوق رأسه','1263','1777','0');
INSERT INTO [t121382] VALUES (1914,'(١٣٧) باب الرخصة في حلق المحرم رأسه إذا مرض أو آذاه القمل أو الصيبان أو هما، وإيجاب الفدية على حالق الرأس وإن كان حلقه من مرض أو أذى برأسه','1263','1777','0');
INSERT INTO [t121382] VALUES (1915,'(١٣٨) باب ذكر الدليل على أن كعبا أمره النبي - صلى الله عليه وسلم - بحلق رأسه، و [أن] يفتدي بصيام، أو صدقة، أو نسك، قبل أن يبين لهم أنهم يحلقون بالحديبية، ويرجعون إلى المدينة، من غير وصول إلى مكة','1263','1777','0');
INSERT INTO [t121382] VALUES (1916,'(١٣٩) باب ذكر الدليل على أن قوله [تعالى]: (ولا تحلقوا رءوسكم حتى يبلغ الهدي محله فمن كان منكم مريضا أو به أذى من رأسه ففدية من صيام أو صدقة) [البقرة: ١٩٦] اختصار كلام معناه: فحلقتم ففدية من صيام أو صدقة أو نسك كقوله - جل وعلا: فقلنا (اضرب بعصاك البحر فانفلق) [الشعراء: ٦٣] أراد: فيهن جميعا فضرب فاختصر الكلام، وحذف فضرب، والعلم محيط أن انفجار الحجر وانبجاسه وانفلاق البحر إنما كان عن ضربات موسى - صلى الله عليه وسلم -، ولا شك ولا ارتياب أن موسى أطاع الله فيما أمر به من ضرب الحجر والبحر، فكان انفلاق البحر وانفجار الحجر وانبجاسه بعد ضربه مسارعة منه إلى طاعة خالقه','1264','1777','0');
INSERT INTO [t121382] VALUES (1917,'(١٤٠) باب الرخصة في أدب المحرم عبده إذا ضيع مال المولى فاستحق الأدب على ذلك','1266','1777','0');
INSERT INTO [t121382] VALUES (1918,'(١٤١) باب الرخصة في إنشاد المحرم الشعر والرجز','1267','1777','0');
INSERT INTO [t121382] VALUES (1919,'(١٤٢) باب الرخصة في لبس المحرم السراويل عند الإعواز من الإزار، والخفين عند عدم وجود النعلين، بلفظ مجمل غير مفسر في ذكر الخفين عند عدم وجود النعلين','1267','1777','0');
INSERT INTO [t121382] VALUES (1920,'(١٤٣) باب ذكر الخبر المفسر للفظة المجملة التي ذكرتها في إباحة لبس الخفين لمن لا يجد النعلين، والدليل [على] أن النبي - صلى الله عليه وسلم - إنما أباح للمحرم لبس الخفين المقطوع أسفل الكعبين، لا كل ما وقع عليه اسم خف وإن كان فوق الكعبين','1268','1777','0');
INSERT INTO [t121382] VALUES (1921,'(١٤٤) باب ذكر الدليل [على] أن النبي - صلى الله عليه وسلم - إنما أباح للمحرم لبس الخفين اللذين هما أسفل من الكعبين، لا أنه أباح له لبس الخفين اللذين لهما ساقان، وإن شق أسفل الكعبين من الخفين شقا وترك الساقان فلم يبانا مما أسفل من الكعبين على ما توهمه بعض الناس','1268','1777','0');
INSERT INTO [t121382] VALUES (1922,'(١٤٥) باب ذكر الدليل [على] أن النبي - صلى الله عليه وسلم - إنما رخص بالأمر بقطع الخفين للرجال دون النساء، إذ قد أباح للنساء الخفين وإن وجدن نعالا، فرخص للنساء في لبس الخفاف دون الرجال','1269','1777','0');
INSERT INTO [t121382] VALUES (1923,'(١٤٦) باب الرخصة في استظلال المحرم، وإن كان نازلا غير سائر، ضد قول من كرهه ونهى عنه','1270','1777','0');
INSERT INTO [t121382] VALUES (1924,'(١٤٧) باب إباحة استظلال المحرم وإن كان راكبا غير نازل.','1270','1777','0');
INSERT INTO [t121382] VALUES (1925,'(١٤٨) باب إباحة إبدال المحرم ثيابه في الإحرام والرخصة في لبس الممشق من الثياب وإن كان الممشق مصبوغا، غير أنه مصبوغ بالطين','1270','1777','0');
INSERT INTO [t121382] VALUES (1926,'(١٤٩) باب إباحة تغطية المحرمة وجهها من الرجال، بذكر خبر مجمل، أحسبه غير مفسر','1271','1777','0');
INSERT INTO [t121382] VALUES (1927,'(١٥٠) باب ذكر الخبر المفسر لهذه اللفظة التي حسبتها مجملة، والدليل [على] أن للمحرمة تغطية وجهها من غير انتقاب ولا إمساس الثوب، إذ الخمار الذي تستر به و','1271','1777','0');
INSERT INTO [t121382] VALUES (1928,'(١٥١) باب استحباب دخول مكة نهارا اقتداء برسول الله - صلى الله عليه وسلم- والبيتوتة قرب مكة إذا انتهى المرء بالليل إلى ذي طوى، ليكون دخوله مكة نهارا لا ليلا','1272','1777','0');
INSERT INTO [t121382] VALUES (1929,'(١٥٢) باب استحباب دخول مكة من الثنية العليا، استنانا بالنبي - صلى الله عليه وسلم- إذ في الاقتداء به الخير الذي لا يعتاض منه أحد ترك الاقتداء به','1272','1777','0');
INSERT INTO [t121382] VALUES (1930,'(١٥٣) باب استحباب الاغتسال لدخول مكة إذ النبي - صلى الله عليه وسلم - اغتسل عند إرادته دخول مكة','1273','1777','0');
INSERT INTO [t121382] VALUES (1931,'(١٥٤) باب قطع التلبية في الحج عند دخول الحرم إلى الفراغ من السعي بين الصفا والمروة','1273','1777','0');
INSERT INTO [t121382] VALUES (1932,'(١٥٥) باب استحباب تجديد الوضوء عند إرادة المرء الطواف بالبيت عند مقدمه مكة','1275','1777','0');
INSERT INTO [t121382] VALUES (1933,'(١٥٦) باب استحباب دخول المسجد من باب بني شيبة','1275','1777','0');
INSERT INTO [t121382] VALUES (1934,'(١٥٧) باب الأمر بالتزين عند إرادة الطواف بالبيت بلبس الثياب','1276','1777','0');
INSERT INTO [t121382] VALUES (1935,'(١٥٨) باب كراهة رفع اليدين عند رؤية البيت','1277','1777','0');
INSERT INTO [t121382] VALUES (1936,'(١٥٩) باب ذكر الخبر المفسر للفظة المجملة التي ذكرتها، والدليل [على] أن جابر بن عبد الله إنما أراد بقوله: لم يكن يفعل هذا، أي: لم نكن نرفع أيدينا عند الخروج من المسجد بعد الفراغ من الطواف والصلاة لم نكن نستقبل البيت فنرفع أيدينا بعد ذلك، لا أنا لم نكن نرفع أيدينا عند رؤية البيت [٢٧٠ - أ] أول ما نراه','1278','1777','0');
INSERT INTO [t121382] VALUES (1937,'(١٦٠) باب الدعاء عند دخول المسجد','1278','1777','0');
INSERT INTO [t121382] VALUES (1938,'(١٦١) باب الاضطباع بالرداء عند طواف الحج والعمرة أو أحدهما','1278','1777','0');
INSERT INTO [t121382] VALUES (1939,'(١٦٢) باب ذكر الدليل على أن السنة قد كان يسنها النبي - صلى الله عليه وسلم - لعلة حادثة فتزول العلة وتبقى السنة قائمة إلى الأبد. إذ النبي - صلى الله عليه وسلم - إنما رمل في الابتداء واضطبع ليري المشركين قوته وقوة أصحابه فبقي الاضطباع والرمل سنتان إلى آخر الأبد','1279','1777','0');
INSERT INTO [t121382] VALUES (1940,'(١٦٣) باب استلام الحجر الأسود عند ابتداء الطواف','1279','1777','0');
INSERT INTO [t121382] VALUES (1941,'(١٦٤) باب تقبيل الحجر الأسود إذا تم تقبيله من غير إيذاء المسلم','1280','1777','0');
INSERT INTO [t121382] VALUES (1942,'(١٦٥) باب البكاء عند تقبيل الحجر الأسود، وفي القلب من محمد بن عون هذا، ووضع اليدين على الحجر، ومسح الوجه بهما، ولكن خبر محمد بن علي ثابت','1280','1777','0');
INSERT INTO [t121382] VALUES (1943,'(١٦٦) باب السجود على الحجر الأسود إذا وجد الطائف السبيل إلى ذلك من غير إيذاء المسلم','1281','1777','0');
INSERT INTO [t121382] VALUES (1944,'(١٦٧) باب استلام الحجر باليد وتقبيل اليد إذا لم يكن تقبيل الحجر ولا السجود عليه','1281','1777','0');
INSERT INTO [t121382] VALUES (1945,'(١٦٨) باب التكبير عند استلام الحجر واستقباله في افتتاح الطواف','1282','1777','0');
INSERT INTO [t121382] VALUES (1946,'(١٦٩) باب الرمل في الأشواط الثلاثة، والمشي في الأربعة','1282','1777','0');
INSERT INTO [t121382] VALUES (1947,'(١٧٠) باب الرمل بالبيت من الحجر الأسود، إلى الحجر الأسود','1282','1777','0');
INSERT INTO [t121382] VALUES (1948,'(١٧١) باب ذكر العلة التي لها رمل النبي - صلى الله عليه وسلم - في الابتداء','1282','1777','0');
INSERT INTO [t121382] VALUES (1949,'(١٧٢) باب الدعاء بين الركن اليماني والحجر الأسود','1283','1777','0');
INSERT INTO [t121382] VALUES (1950,'(١٧٣) باب التكبير كلما انتهى إلى الحجر','1283','1777','0');
INSERT INTO [t121382] VALUES (1951,'(١٧٤) باب استلام الحجر والركن اليماني في كل طواف من السبع','1284','1777','0');
INSERT INTO [t121382] VALUES (1952,'(١٧٥) باب الإشارة إلى الركن عند الانتهاء إليه إذا لم يمكن استلامه','1284','1777','0');
INSERT INTO [t121382] VALUES (1953,'(١٧٦) باب استلام الركنين اللذين يليان الحجر، ركن الأسود والذي يليه، وهما الركنان اليمانيان','1284','1777','0');
INSERT INTO [t121382] VALUES (1954,'(١٧٧) باب ذكر العلة التي نرى أن النبي - صلى الله عليه وسلم - ترك استلام الركنين اللذين يليان الحجر لها','1285','1777','0');
INSERT INTO [t121382] VALUES (1955,'(١٧٨) باب وضع الخد على الركن اليماني عند تقبيله','1285','1777','0');
INSERT INTO [t121382] VALUES (1956,'(١٧٩) باب الدعاء بين الركنين أن يرزق الله الداعي القناعة بما رزق، ويبارك له فيه، ويخلف على كل غائبة له بخير','1285','1777','0');
INSERT INTO [t121382] VALUES (1957,'(١٨٠) باب فضل استلام الركنين وذكر حط الخطايا بمسحهما','1286','1777','0');
INSERT INTO [t121382] VALUES (1958,'(١٨١) باب صفة الركن والمقام والبيان أنهما ياقوتتان من يواقيت الجنة','1287','1777','0');
INSERT INTO [t121382] VALUES (1959,'(١٨٢) باب ذكر العلة التي من سببها اسود الحجر، وصفة نزوله من الجنة، والدليل [على] أنه إنما سودته خطايا بني آدم، إذ كان عند نزوله من الجنة أشد بياضا من الثلج','1288','1777','0');
INSERT INTO [t121382] VALUES (1960,'(١٨٣) باب ذكر الدليل على أن الحجر إنما سودته خطايا بني آدم المشركين دون خطايا المسلمين','1288','1777','0');
INSERT INTO [t121382] VALUES (1961,'(١٨٤) باب ذكر صفة الحجر يوم القيامة، وبعثة الله - عز وجل - إياه مع إعطائه إياه عينين يبصر بهما ولسانا ينطق به، يشهد لمن استلمه بحق جل ربنا وتعالى الذي هو فعال لما يريد','1288','1777','0');
INSERT INTO [t121382] VALUES (1962,'(١٨٥) باب ذكر الدليل على أن النبي - صلى الله عليه وسلم - إنما أراد بذكره الركن في هذا الخبر نفس الحجر الأسود لا غير، والدليل على أن النبي - صلى الله عليه وسلم - إنما أراد بقوله: "على من استلمه" أي لمن استلمه، في خبر فضيل بن سليمان "لمن استلمه بحق"، وفي حديث حماد بن سلمة أيضا: "لمن استلمه وقبله"','1289','1777','0');
INSERT INTO [t121382] VALUES (1963,'(١٨٦) باب ذكر الدليل على أن الحجر إنما يشهد لمن استلمه بالنية دون من استلمه ناويا باستلامه طاعة الله وتقربا إليه، إذ النبي - صلى الله عليه وسلم - قد أعلم أن للمرء ما نوى','1289','1777','0');
INSERT INTO [t121382] VALUES (1964,'(١٨٧) باب استحباب ذكر الله في الطواف، إذ الطواف بالبيت إنما جعل لإقامة ذكر الله، لا بحديث الناس والاشتغال بما لا يجري على الطائف نفعا في الآخرة، وإن كان التكلم بالخير في الطواف طلقا مباحا، وإن لم يكن ذلك الكلام ذكر الله','1290','1777','0');
INSERT INTO [t121382] VALUES (1965,'(١٨٨) باب الرخصة في التكلم بالخير في الطواف، والزجر عن الكلام السيئ فيه','1290','1777','0');
INSERT INTO [t121382] VALUES (1966,'(١٨٩) باب الطواف من وراء الحجر','1291','1777','0');
INSERT INTO [t121382] VALUES (1967,'(١٩٠) باب ذكر الدليل على صحة ما تأولت قول ابن عباس، والبيان أن بعض الحجر من البيت لا جميعه','1291','1777','0');
INSERT INTO [t121382] VALUES (1968,'(١٩١) باب ذكر العلة التي لها طاف النبي - صلى الله عليه وسلم - من وراء الحجر','1292','1777','0');
INSERT INTO [t121382] VALUES (1969,'(١٩٢) باب ذكر طواف القارن بين الحج والعمرة عند مقدمه مكة، والبيان أن الواجب عليه طواف واحد في الابتداء، ضد قول من زعم أن على القارن في الابتداء طوافين وسعيين','1293','1777','0');
INSERT INTO [t121382] VALUES (1970,'(١٩٣) باب إباحة الطواف والصلاة بمكة بعد الفجر وبعد العصر، والدليل على صحة مذهب المطلبي أن النبي - صلى الله عليه وسلم - إنما أراد بزجره عن الصلاة بعد الصبح حتى تطلع الشمس وبعد العصر حتى تغرب الشمس بعض الصلاة لا جميعها','1294','1777','0');
INSERT INTO [t121382] VALUES (1971,'(١٩٤) باب الرخصة في الشرب في الطواف إن ثبت الخبر، فإن في القلب من هذا الإسناد، وأنا خائف أن يكون عبد السلام أو من دونه وهم في هذه اللفظة أعني قوله: في الطواف','1295','1777','0');
INSERT INTO [t121382] VALUES (1972,'(١٩٥) باب الزجر عن قيادة الطائف بزمام أو خيط شبيها بقيادة البهائم','1295','1777','0');
INSERT INTO [t121382] VALUES (1973,'(١٩٦) باب فضل الطواف بالبيت وذكر كتبه حسنة، ورفع درجة، وحط خطيئة، عن الطائف بكل قدم يرفعها أو يضعها في طوافه، وإعطاء الطائف بإحصاء أسبوع من الطواف أجر معتق رقبة، إذ النبي - صلى الله عليه وسلم - جعل محصي الأسبوع الواحد من الطواف كعتق رقبة','1296','1777','0');
INSERT INTO [t121382] VALUES (1974,'(١٩٧) باب الصلاة بعد الفراغ من الطواف عند المقام. والدليل على أن الله - عز وجل - قد يأمر بالأمر أمر ندب وإرشاد وفضيلة، لا أن كل أمره أمر فرض وإيجاب. إذ الله - عز وجل - أمر باتخاذ [مقام] إبراهيم','1296','1777','0');
INSERT INTO [t121382] VALUES (1975,'(١٩٨) باب ذكر الدليل على أن النبي - صلى الله عليه وسلم - إنما صلى الركعتين حين عمد إلى مقام إبراهيم خلف المقام، جعل المقام بينه وبين الباب، لا أنه وقف بين يدي المقام ولا عن يمينه ولا عن يساره','1297','1777','0');
INSERT INTO [t121382] VALUES (1976,'(١٩٩) باب الرجوع إلى الحجر، واستلامه بعد الفراغ من ركعتي الطواف','1298','1777','0');
INSERT INTO [t121382] VALUES (1977,'(٢٠٠) باب الخروج إلى الصفا بعد استلام الركن، وصعود الصفا والمروة حتى يرى الصاعد البيت على الصفا والمروة، والبدء بالصفا قبل المروة، إذ الله - عز وجل - بدأ بذكر الصفا قبل ذكر المروة، وأمر المبين عن الله - عز وجل - النبي المصطفى بالبدء بما بدأ الله به في الذكر','1298','1777','0');
INSERT INTO [t121382] VALUES (1978,'(٢٠١) باب رفع اليدين عند الدعاء على الصفا','1299','1777','0');
INSERT INTO [t121382] VALUES (1979,'(٢٠٢) باب المشي بين الصفا والمروة خلا السعي في بطن الوادي فقط [٢٧٤ - أ]','1299','1777','0');
INSERT INTO [t121382] VALUES (1980,'(٢٠٣) باب ذكر خبر روي في السعي بين الصفا والمروة بلفظ عام مراده خاص، أنا خائف أن يخطر ببال بعض من لا يميز بين الخبر','1299','1777','0');
INSERT INTO [t121382] VALUES (1981,'(٢٠٤) باب ذكر الخبر المفسر للفظة المجملة التي ذكرت أن لفظها لفظ عام مرادها خاص، والدليل [على] أن النبي - صلى الله عليه وسلم - إنما سعى مما بين الصفا والمروة بطن المسيل فقط دون سائر ما بينهما، لا أنه سعى جميع ما بين الصفا والمروة','1300','1777','0');
INSERT INTO [t121382] VALUES (1982,'(٢٠٥) باب ذكر البيان أن السعي بين الصفا والمروة واجب','1301','1777','0');
INSERT INTO [t121382] VALUES (1983,'(٢٠٦) باب ذكر الدليل [على] أن الله - عز وجل - إنما أعلم أصحاب النبي - صلى الله عليه وسلم - أنه لا جناح عليهم في الطواف بين الصفا والمروة لأنهم تحرجوا م','1302','1777','0');
INSERT INTO [t121382] VALUES (1984,'(٢٠٧) باب ذكر الدليل على أن عائشة لم ترد بقولها: "هي سنة سنها رسول الله - صلى الله عليه وسلم - أن الطواف بينهما سنة يتم الحج بتركه','1304','1777','0');
INSERT INTO [t121382] VALUES (1985,'(٢٠٨) باب ذكر الدليل على أن السعي الذي ذكرت أنه واجب بين الصفا والمروة وسعيا كان أو مشيا بسكينة وتؤدة','1304','1777','0');
INSERT INTO [t121382] VALUES (1986,'(٢٠٩) باب ذكر إسقاط الحرج عن الساعي بين الصفا والمروة قبل الطواف بالبيت جهلا بأن الطواف بالبيت قبل السعي','1306','1777','0');
INSERT INTO [t121382] VALUES (1987,'(٢١٠) باب الدعاء على أهل الملل والأوثان على الصفا والمروة بأن يهزموا ويزلزلوا','1306','1777','0');
INSERT INTO [t121382] VALUES (1988,'(٢١١) باب الرخصة للمعذور في الركوب في الطواف بالبيت وبين الصفا والمروة','1307','1777','0');
INSERT INTO [t121382] VALUES (1989,'(٢١٢) باب ذكر بعض العلل التي لها سعى النبي - صلى الله عليه وسلم - بين الصفا والمروة. وهذا من الجنس الذي أعلمت قبل أن استنان السنة قد تكون في الابتداء لعلة فتزول العلة وتبقى السنة إلى آخر الأبد، إذ النبي - صلى الله عليه وسلم - إنما سعى بالبيت وبين الصفا والمروة ليرى المشركون قوته، فبقيت هذه السنة إلى آخر الأبد','1307','1777','0');
INSERT INTO [t121382] VALUES (1990,'(٢١٣) باب استحباب ركوب من بالناس إليه الحاجة والمسألة عن أمر دينهم، وبين الصفا والمروة إذا كثر الزحام على العالم، ولم يمكن سؤاله، إذا كان العالم ماشيا بين الصفا والمروة','1308','1777','0');
INSERT INTO [t121382] VALUES (1991,'(٢١٤) باب الرخصة في الركوب بين الصفا والمروة إذا أوذي الطائف بينهما بالازدحام عليه، والدليل على أن الركوب بينهما إباحة لا أنه سنة واجبة، ولا أنه سنة فضيلة بل هي سنة إباحة','1308','1777','0');
INSERT INTO [t121382] VALUES (1992,'(٢١٥) باب استلام الحجر بالمحجن للطائف الراكب','1309','1777','0');
INSERT INTO [t121382] VALUES (1993,'(٢١٦) باب تقبيل طرف المحجن إذا استلم به الركن إن صح الخبر، لأن في القلب من هذا الإسناد','1309','1777','0');
INSERT INTO [t121382] VALUES (1994,'(٢١٧) باب إحلال المعتمر عند الفراغ من السعي بين الصفا والمروة','1310','1777','0');
INSERT INTO [t121382] VALUES (1995,'(٢١٨) باب إباحة وطء المتمتع النساء ما بين الإحلال من العمرة إلى الإحرام بالحج، وإن كان بينهما قريب','1310','1777','0');
INSERT INTO [t121382] VALUES (1996,'(٢١٩) باب ذبح المعتمر ونحره هديه حيث شاء من مكة','1311','1777','0');
INSERT INTO [t121382] VALUES (1997,'(٢٢٠) باب المهلة بالعمرة تقدم مكة وهي حائض','1311','1777','0');
INSERT INTO [t121382] VALUES (1998,'(٢٢١) باب مقام القارن والمفرد بالحج على الإحرام إلى يوم النحر','1313','1777','0');
INSERT INTO [t121382] VALUES (1999,'(٢٢٢) باب فضل الحج ماشيا من مكة، إن صح الخبر، فإن في القلب من عيسى بن سوادة هذا','1313','1777','0');
INSERT INTO [t121382] VALUES (2000,'(٢٢٣) باب عدد حجج آدم صلوات الله عليه وصفة حجه إن صح الخبر فإن في القلب من القاسم بن عبد الرحمن هذا','1314','1777','0');
INSERT INTO [t121382] VALUES (2001,'(٢٢٤) باب خطبة الإمام يوم السابع من ذي الحجة ليعلم الناس مناسكهم','1314','1777','0');
INSERT INTO [t121382] VALUES (2002,'(٢٢٥) باب إهلال المتمتع بالحج يوم التروية من مكة','1314','1777','0');
INSERT INTO [t121382] VALUES (2003,'(٢٢٦) باب وقت الخروج يوم التروية من مكة إلى منى','1315','1777','0');
INSERT INTO [t121382] VALUES (2004,'(٢٢٧) باب ذكر عدد الصلوات التي يصلي الإمام والناس بمنى قبل الغدو إلى عرفة','1316','1777','0');
INSERT INTO [t121382] VALUES (2005,'(٢٢٨) باب وقت الغدو من منى إلى عرفة','1316','1777','0');
INSERT INTO [t121382] VALUES (2006,'(٢٢٩) باب ذكر البيان أن السنة الغدو من منى إلى عرفات بعد طلوع الشمس لا قبله','1317','1777','0');
INSERT INTO [t121382] VALUES (2007,'(٢٣٠) باب ذكر البيان أن النبي - صلى الله عليه وسلم - إنما اتبع خليل الله في غدوه من منى حين طلعت الشمس إذ قد أمر باتباعه','1317','1777','0');
INSERT INTO [t121382] VALUES (2008,'(٢٣١) باب ذكر العلة التي سميت لها عرفة عرفة مع الدليل على أن جبريل قد أرى النبي محمدا - صلى الله عليه وسلم - المناسك كما أرى إبراهيم خليل الرحمن','1318','1777','0');
INSERT INTO [t121382] VALUES (2009,'(٢٣٢) باب ذكر التخيير بين التلبية وبين التكبير في الغدو من منى إلى عرفة','1319','1777','0');
INSERT INTO [t121382] VALUES (2010,'(٢٣٣) باب التكبير والتهليل من التلبية في الغدو من منى إلى عرفة','1319','1777','0');
INSERT INTO [t121382] VALUES (2011,'(٢٣٤) باب ذكر خطبة الإمام بعرفة، ووقت الخطبة في ذلك اليوم','1319','1777','0');
INSERT INTO [t121382] VALUES (2012,'(٢٣٥) باب صفة الخطبة يوم عرفة','1320','1777','0');
INSERT INTO [t121382] VALUES (2013,'(٢٣٦) باب ذكر البيان أن النبي - صلى الله عليه وسلم - إنما خطب بعرفة راكبا لا نازلا بالأرض','1320','1777','0');
INSERT INTO [t121382] VALUES (2014,'(٢٣٧) باب قصر الخطبة يوم عرفة','1321','1777','0');
INSERT INTO [t121382] VALUES (2015,'(٢٣٨) باب الجمع بين الظهر والعصر بعرفة، والأذان والإقامة لهما','1322','1777','0');
INSERT INTO [t121382] VALUES (2016,'(٢٣٩) باب ترك التنفل بين الظهر والعصر إذا جمع بينهما بعرفة. ووقت الرواح إلى الموقف','1322','1777','0');
INSERT INTO [t121382] VALUES (2017,'(٢٤٠) باب التهجير بالصلاة يوم عرفة، وترك تأخير الصلاة بها','1322','1777','0');
INSERT INTO [t121382] VALUES (2018,'(٢٤١) باب تعجيل الوقوف بعرفة','1323','1777','0');
INSERT INTO [t121382] VALUES (2019,'(٢٤٢) باب الوقوف بعرفة، والرخصة للحاج أن يقفوا حيث شاءوا منه، إذ جميع عرفة موقف','1323','1777','0');
INSERT INTO [t121382] VALUES (2020,'(٢٤٣) باب الزجر عن الوقوف بعرنة','1323','1777','0');
INSERT INTO [t121382] VALUES (2021,'(٢٤٤) باب ذكر البيان أن الوقوف بعرفة من سنة إبراهيم خليل الرحمن وأنه إرث عنه، ورثتها أمة محمد النبي - صلى الله عليه وسلم -','1324','1777','0');
INSERT INTO [t121382] VALUES (2022,'(٢٤٥) باب ذكر وقت الوقوف بعرفة','1325','1777','0');
INSERT INTO [t121382] VALUES (2023,'(٢٤٦) باب ذكر البيان أن هذه الصلوات التي قال النبي - صلى الله عليه وسلم -: من صلى معنا هذه الصلاة، كانت صلاة الصبح لا غيرها','1326','1777','0');
INSERT INTO [t121382] VALUES (2024,'(٢٤٧) باب ذكر الدليل [على] أن الحاج إذا لم يدرك عرفة قبل طلوع الفجر من يوم النحر فهو فائت الحج غير مدركه','1326','1777','0');
INSERT INTO [t121382] VALUES (2025,'(٢٤٨) باب الوقوف بعرفة على الرواحل','1327','1777','0');
INSERT INTO [t121382] VALUES (2026,'(٢٤٩) باب رفع اليدين في الدعاء عند الوقوف بعرفة، وإباحة رفع إحدى اليدين إذا احتاج الراكب إلى حفظ العنان أو الخطام بإحدى اليدين','1327','1777','0');
INSERT INTO [t121382] VALUES (2027,'(٢٥٠) باب استقبال القبلة عند الوقوف بعرفة','1328','1777','0');
