INSERT INTO [t121382] VALUES (2029,'(٢٥٢) باب استحباب الفطر يوم عرفة بعرفات تقويا على الدعاء','1329','1777','0');
INSERT INTO [t121382] VALUES (2030,'(٢٥٣) باب استحباب التلبية بعرفات، وعلى الموقف، إحياء للسنة إذ بعض الناس قد كان تركه في بعض الأزمان','1329','1777','0');
INSERT INTO [t121382] VALUES (2031,'(٢٥٤) باب إباحة الزيادة على التلبية في الموقف بعرفة بأن الخير خير الآخرة','1330','1777','0');
INSERT INTO [t121382] VALUES (2032,'(٢٥٥) باب فضل حفظ البصر والسمع واللسان يوم عرفة','1330','1777','0');
INSERT INTO [t121382] VALUES (2033,'(٢٥٦) باب استحباب وقوف البدن بالموقف بعرفة','1331','1777','0');
INSERT INTO [t121382] VALUES (2034,'(٢٥٧) باب الاستعاذة في الموقف من الرياء والسمعة في الحج إن ثبت الخبر','1331','1777','0');
INSERT INTO [t121382] VALUES (2035,'(٢٥٨) باب وقت الدفعة من عرفة خلاف سنة أهل الكفر والأوثان كانت في الجاهلية','1332','1777','0');
INSERT INTO [t121382] VALUES (2036,'(٢٥٩) باب تباهي الله أهل السماء بأهل عرفات','1333','1777','0');
INSERT INTO [t121382] VALUES (2037,'(٢٦٠) باب ذكر الدعاء على الموقف عشية عرفة إن ثبت الخبر، ولا أخال إلا أنه ليس في الخبر حكم، وإنما هو دعاء، فخرجنا هذا الخبر وإن لم يكن ثابتا من جهة النقل إذ هذا الدعاء مباح أن يدعو به على الموقف وغيره','1333','1777','0');
INSERT INTO [t121382] VALUES (2038,'(٢٦١) باب ذكر العلة التي من أجلها سميت عرفة عرفة','1334','1777','0');
INSERT INTO [t121382] VALUES (2039,'(٢٦٢) باب صفة السير في الدفعة من عرفة، والأمر بالسكينة في السير، بلفظ عام مراده خاص','1334','1777','0');
INSERT INTO [t121382] VALUES (2040,'(٢٦٣) باب ذكر البيان أن إيجاف الخيل والإبل والإيضاع في السير في الدفعة من عرفة ليس البر، والدليل على أن البر السكينة في السير بمثل اللفظة التي ذكرت أن','1335','1777','0');
INSERT INTO [t121382] VALUES (2041,'(٢٦٤) باب ذكر الخبر الدال على أن اللفظة التي ذكرها في السكينة في السير في الدفعة من عرفة لفظ عام مراده خاص','1335','1777','0');
INSERT INTO [t121382] VALUES (2042,'(٢٦٥) باب ذكر الدعاء والذكر والتهليل في السير من عرفة إلى المزدلفة','1336','1777','0');
INSERT INTO [t121382] VALUES (2043,'(٢٦٦) باب إباحة النزول بين عرفات وجمع للحاجة تبدو للمرء','1336','1777','0');
INSERT INTO [t121382] VALUES (2044,'(٢٦٧) باب الجمع بين الصلاتين بين المغرب والعشاء بالمزدلفة','1337','1777','0');
INSERT INTO [t121382] VALUES (2045,'(٢٦٨) باب ترك التطوع بين الصلاتين إذا جمع بينهما بالمزدلفة مع البيان أن النبي - صلى الله عليه وسلم - صلى بالمزدلفة صلاة المسافر لا صلاة المقيم','1337','1777','0');
INSERT INTO [t121382] VALUES (2046,'(٢٦٩) باب الأذان للمغرب، والإقامة للعشاء من غير أذان، إذا جمع بينهما بالمزدلفة، خلاف قول من زعم أن الصلاتين إذا جمع بينهما في وقت الآخرة منهما جمع بينهما بإقامتين من غير أذان','1337','1777','0');
INSERT INTO [t121382] VALUES (2047,'(٢٧٠) باب إباحة الفصل بين المغرب والعشاء إذا جمع بينهما بفعل ليس من عمل الصلاة','1338','1777','0');
INSERT INTO [t121382] VALUES (2048,'(٢٧١) باب إباحة الأكل بين الصلاتين إذا جمع بينهما بالمزدلفة، إن ثبت الخبر، فإني لا أقف على سماع أبي إسحاق هذا الخبر من عبد الرحمن بن يزيد','1338','1777','0');
INSERT INTO [t121382] VALUES (2049,'(٢٧٢) باب البيتوتة بالمزدلفة ليلة النحر','1339','1777','0');
INSERT INTO [t121382] VALUES (2050,'(٢٧٣) باب التغليس بصلاة الفجر يوم النحر بالمزدلفة','1339','1777','0');
INSERT INTO [t121382] VALUES (2051,'(٢٧٤) باب الأذان والإقامة لصلاة الفجر بالمزدلفة','1340','1777','0');
INSERT INTO [t121382] VALUES (2052,'(٢٧٥) باب الوقوف عند المشعر الحرام [٢٨٢ - أ] والدعاء والذكر والتهليل والتمجيد والتعظيم لله في ذلك الموقف','1340','1777','0');
INSERT INTO [t121382] VALUES (2053,'(٢٧٦) باب إباحة الوقوف حيث شاء الحاج من المزدلفة إذ جميع المزدلفة موقف','1341','1777','0');
INSERT INTO [t121382] VALUES (2054,'(٢٧٧) باب الدفع من المشعر الحرام، ومخالفة أهل الشرك والأوثان في دفعهم منه','1341','1777','0');
INSERT INTO [t121382] VALUES (2055,'(٢٧٨) باب صفة السير في الإفاضة من جمع إلى منى بلفظ عام مراده خاص','1341','1777','0');
INSERT INTO [t121382] VALUES (2056,'(٢٧٩) باب ذكر الدليل على أن النبي - صلى الله عليه وسلم - إنما سار في الإفاضة من جمع إلى منى على السكينة خلا بطن وادي محسر، فإنه أوضع فيه','1342','1777','0');
