INSERT INTO [t121382] VALUES (2059,'(٢٨٢) باب فضل العمل في عشر ذي الحجة','1343','1777','0');
INSERT INTO [t121382] VALUES (2060,'(٢٨٣) باب فضل يوم النحر','1343','1777','0');
INSERT INTO [t121382] VALUES (2061,'(٢٨٤) باب التقاط الحصى لرمي الجمار من المزدلفة، والبيان أن كسر الحجارة لحصى الجمار بدعة. لما فيه من إيذاء الناس وإتعاب أبدان من يتكلف كسر الحجارة توهما أنه سنة','1344','1777','0');
INSERT INTO [t121382] VALUES (2062,'(٢٨٥) باب الرخصة في تقديم النساء من جمع إلى منى بالليل','1344','1777','0');
INSERT INTO [t121382] VALUES (2063,'(٢٨٦) باب الرخصة في تقديم الضعفاء من الرجال والولدان من جمع إلى منى بالليل','1345','1777','0');
INSERT INTO [t121382] VALUES (2064,'(٢٨٧) باب إباحة تقديم الثقل من جمع إلى منى بالليل','1345','1777','0');
INSERT INTO [t121382] VALUES (2065,'(٢٨٨) باب قدر الحصى الذي يرمى به الجمار، والدليل على أن الرمي بالحصى الكبار من الغلو في الدين، وتخويف الهلاك بالغلو في الدين','1346','1777','0');
