INSERT INTO [t121382] VALUES (2072,'(٢٩٥) باب الذكر عند رمي الجمار','1349','1777','0');
INSERT INTO [t121382] VALUES (2073,'(٢٩٦) باب الرخصة للنساء والضعفاء الذين رخص لهم في الإفاضة من جمع بليل في رمي الجمار قبل طلوع الشمس','1350','1777','0');
INSERT INTO [t121382] VALUES (2074,'(٢٩٧) باب الرخصة للنساء اللواتي رخص لهن في الإفاضة من جمع بليل في رمي الجمار قبل طلوع الفجر','1350','1777','0');
INSERT INTO [t121382] VALUES (2075,'(٢٩٨) باب قطع التلبية إذا رمى الحاج جمرة العقبة يوم النحر','1351','1777','0');
INSERT INTO [t121382] VALUES (2076,'(٢٩٩) باب ترك الوقوف عند جمرة العقبة بعد رميها يوم النحر','1353','1777','0');
INSERT INTO [t121382] VALUES (2077,'(٣٠٠) باب الرجوع من الجمرة إلى منى بعد رمي الجمرة للنحر والذبح','1354','1777','0');
INSERT INTO [t121382] VALUES (2078,'(٣٠١) باب الرخصة في النحر والذبح أين شاء المرء من منى','1354','1777','0');
INSERT INTO [t121382] VALUES (2079,'(٣٠٢) باب النهي عن احتضار المنازل بمنى إن ثبت الخبر، فإني لست أعرف مسيكة بعدالة ولا جرح، ولست أحفظ لها راويا إلا ابنها','1355','1777','0');
INSERT INTO [t121382] VALUES (2080,'(٣٠٣) باب استحباب ذبح الإنسان ونحر نسيكه بيده، مع إباحة دفع نسيكه إلى غيره ليذبحها أو ينحرها','1355','1777','0');
INSERT INTO [t121382] VALUES (2081,'(٣٠٤) باب نحر البدن قياما معقولة، ضد قول مذهب من كره ذلك، وجهل السنة وسمى السنة، بدعة بجهله بالسنة','1355','1777','0');
INSERT INTO [t121382] VALUES (2082,'(٣٠٥) باب التسمية والتكبير عند الذبح والنحر','1356','1777','0');
INSERT INTO [t121382] VALUES (2083,'(٣٠٦) باب إباحة الهدي من الذكران والإناث جميعا','1357','1777','0');
INSERT INTO [t121382] VALUES (2084,'(٣٠٧) باب استحباب إهداء ما قد غنم من أموال أهل الشرك والأوثان أهل الحرب منه مغايظة لهم','1357','1777','0');
INSERT INTO [t121382] VALUES (2085,'(٣٠٨) باب استحباب توجيهه الذبيحة للقبلة، والدعاء عند الذبح','1358','1777','0');
INSERT INTO [t121382] VALUES (2086,'(٣٠٩) باب إباحة اشتراك النفر في البدنة والبقرة الواحدة، وإن كان من يشترك في البقرة الواحدة أو البدنة الواحدة من قبائل شتى ليسوا من أهل بيت واحد، مع','1358','1777','0');
INSERT INTO [t121382] VALUES (2087,'(٣١٠) باب إباحة اشتراك سبعة من المتمتعين في البدنة الواحدة والبقرة الواحدة، والدليل على أن سبع بدنة وسبع بقرة مما استيسر من الهدي، إذ الله - عز وجل - أوجب على المتمتع ما استيسر من الهدي إذا وجده','1359','1777','0');
INSERT INTO [t121382] VALUES (2088,'(٣١١) باب إباحة اشتراك النساء المتمتعات في البقرة الواحدة','1359','1777','0');
INSERT INTO [t121382] VALUES (2089,'(٣١٢) باب إجازة الذبح والنحر عن المتمتعة بغير أمرها وعلمها','1360','1777','0');
INSERT INTO [t121382] VALUES (2090,'(٣١٣) باب ذكر الدليل على أن اسم الضحية قد يقع على الهدي الواجب، إذ نساء النبي - صلى الله عليه وسلم - في حجته كن متمتعات خلا عائشة التي صارت قارنة لإدخالها الحج على العمرة، لما لم يتمكنها الطواف والسعي لعلة الحيضة التي حاضت قبل [أن] تطوف وتسعى لعمرتها','1360','1777','0');
INSERT INTO [t121382] VALUES (2091,'(٣١٤) باب ذكر الدليل على أن لا حظر في أخبار جابر: نحرنا مع رسول الله - صلى الله عليه وسلم - البدنة عن سبعة، أن لا تجزئ البدنة عن أكثر من سبعة.','1360','1777','0');
INSERT INTO [t121382] VALUES (2092,'(٣١٥) باب استحباب المغالاة [٢٨٦ - ب] بثمن الهدي وكرائمه، إن كان شهم بن الجارود ممن يجوز الاحتجاج بخبره. وهذا من الجنس الذي قال المطلبي','1363','1777','0');
INSERT INTO [t121382] VALUES (2093,'(٣١٦) باب ذكر العيوب التي تكون في الأنعام فلا تجزئ هديا ولا ضحايا إذا كان بها بعض تلك العيوب','1363','1777','0');
INSERT INTO [t121382] VALUES (2094,'(٣١٧) باب الزجر عن ذبح العضباء في الهدي والأضاحي، زجر اختيار، أن صحيح القرن والأذن أفضل من العضباء، لا أن العضباء غير مجزية، إذ النبي - صلى الله عليه وسلم - لما أعلم أن أربعا لا تجزئ دلهم بهذا القول أن ما سوى ذلك الأربع جائز','1364','1777','0');
INSERT INTO [t121382] VALUES (2095,'(٣١٨) باب النهي عن ذبح ذوات النقص في العيون والآذان في الهدي والضحايا نهي ندب وإرشاد','1364','1777','0');
INSERT INTO [t121382] VALUES (2096,'(٣١٩) باب الرخصة في ذبح الجذعة من الضأن [٢٨٧ - أ] في الهدي والضحايا بلفظ مجمل غير مفسر','1365','1777','0');
INSERT INTO [t121382] VALUES (2097,'(٣٢٠) باب الرخصة في اقتطاع لحوم الهدي بإذن صاحبها','1366','1777','0');
INSERT INTO [t121382] VALUES (2098,'(٣٢١) باب الدليل على أن الجذعة إنما تجزئ عند الإعسار من المسن','1366','1777','0');
INSERT INTO [t121382] VALUES (2099,'(٣٢٢) باب الصدقة بلحوم الهدي، وجلودها، وجلال البدن، بذكر خبر مجمل غير مفسر','1366','1777','0');
INSERT INTO [t121382] VALUES (2100,'(٣٢٣) باب قسم لحوم الهدي وجلوده وجلاله في المساكين','1367','1777','0');
INSERT INTO [t121382] VALUES (2101,'(٣٢٤) باب ذكر الدليل على أن اسم الكل قد يقع على البعض، والدليل على أن علي بن أبي طالب إنما أراد بقوله: أمرني أن أقسم بدنه كلها أي خلا ما أمر من كل بدنه ببضعة فجعلت في قدر فحسيا من المرق، وأكلا من اللحم','1367','1777','0');
INSERT INTO [t121382] VALUES (2102,'(٣٢٥) باب النهي عن إعطاء الجازر أجره من الهدي بذكر خبر مجمل غير مفسر','1367','1777','0');
INSERT INTO [t121382] VALUES (2103,'(٣٢٦) باب ذكر الخبر المفسر للفظة المجملة التي ذكرتها، والدليل على أن النبي - صلى الله عليه وسلم - إنما زجر عن إعطاء الجازر من لحوم هديه على جزارتها شيئا، لا أن يتصدق من لحومها على الجازر، لو كان الجازر مسكينا','1368','1777','0');
INSERT INTO [t121382] VALUES (2104,'(٣٢٧) باب الأكل من لحم الهدي إذا كان تطوعا','1368','1777','0');
INSERT INTO [t121382] VALUES (2105,'(٣٢٨) باب الهدي يضل فينحر مكانه آخر، ثم يوجد الأول','1369','1777','0');
INSERT INTO [t121382] VALUES (2106,'(٣٢٩) باب صيام المتمتع إذا لم يجد الهدي','1370','1777','0');
INSERT INTO [t121382] VALUES (2107,'(٣٣٠) باب حلق الرأس بعد الفراغ من النحر أو الذبح، واستحباب التيامن في الحلق، مع الدليل على أن شعر بني آدم ليس بنجس بعد الحلق أو التقصير','1371','1777','0');
INSERT INTO [t121382] VALUES (2108,'(٣٣١) باب فضل الحلق في الحج والعمرة، واختيار الحلق على التقصير، وإن كان التقصير جائزا','1371','1777','0');
INSERT INTO [t121382] VALUES (2109,'(٣٣٢) باب تسمية من حلق النبي - صلى الله عليه وسلم - في حجته','1371','1777','0');
INSERT INTO [t121382] VALUES (2110,'(٣٣٣) باب استحباب تقليم الأظفار مع حلق الرأس، مع الدليل على أن الأظفار إذا قصت لم يكن حكمها حكم الميتة، ولا كانت نجسا كما توهم بعض أهل العلم أن ما قطع من الحي فهو ميت، وخبر أبي واقد الليثي إنما قال النبي - صلى الله عليه وسلم -: "ما قطع من البهيمة وهي حية فهو ميتة"، عند ذكر أهل الجاهلية في قطعهم إليات الغنم، وجبهم أسنمة الإبل، فكان قول النبي - صلى الله عليه وسلم - جوابا عن هذين الفعلين وما يشبههما، وهو في معانيهما والله أعلم','1372','1777','0');
INSERT INTO [t121382] VALUES (2111,'(٣٣٤) باب إباحة التطيب يوم النحر بعد الحلق وقبل زيارة البيت، ضد قول من زعم أن التطيب محظور حتى يزور البيت','1373','1777','0');
INSERT INTO [t121382] VALUES (2112,'(٣٣٥) باب إباحة التطيب يوم النحر قبل الزيارة بالطيب الذي فيه المسك','1373','1777','0');
INSERT INTO [t121382] VALUES (2113,'(٣٣٦) باب الرخصة للحائض أن تنسك المناسك كلها في حيضها خلا الطواف بالبيت والصلاة','1373','1777','0');
INSERT INTO [t121382] VALUES (2114,'(٣٣٧) باب الرخصة في الاصطياد وجميع ما حرم على المحرم بعد رمي الجمرة يوم النحر قبل زيارة البيت','1374','1777','0');
INSERT INTO [t121382] VALUES (2115,'(٣٣٨) باب ذكر الدليل على أن التطيب بعد رمي الجمار والنحر والذبح والحلاق إنما هو مباح عند بعض العلماء قبل زيارة البيت لمن قد طاف بالبيت قبل الوقوف بعرفة دون من لم يطف بالبيت قبل الوقوف بعرفة','1375','1777','0');
INSERT INTO [t121382] VALUES (2116,'(٣٣٩) باب استحباب طواف الزيارة يوم النحر استنانا بالنبي - صلى الله عليه وسلم - ومبادرة بقضاء الواجب عن الطواف الذي به يتم حج الحاج خوف أن يعرض للمرء ما لا يمكنه طواف الزيارة معه، وإن كان تأخير الإفاضة عن يوم النحر جائزا','1376','1777','0');
INSERT INTO [t121382] VALUES (2117,'(٣٤٠) باب ذكر الدليل على أن وطء النساء يحل بعد ركعتي طواف الزيارة، وإن كان الطائف بمكة قبل [أن] يرجع إلى منى','1377','1777','0');
INSERT INTO [t121382] VALUES (2118,'(٣٤١) باب ترك الرمل في طواف الزيارة للقارن وحكم المفرد [في] هذا كحكم القارن','1377','1777','0');
INSERT INTO [t121382] VALUES (2119,'(٣٤٢) باب استحباب الشرب من ماء زمزم بعد الفراغ من طواف الزيارة','1377','1777','0');
INSERT INTO [t121382] VALUES (2120,'(٣٤٣) باب استحباب الاستقاء من ماء زمزم إذ النبي - صلى الله عليه وسلم - قد أعلم أنه عمل صالح، وأعلم أن لولا أن يغلب المستقي منها على الاستقاء لنزع معهم','1378','1777','0');
INSERT INTO [t121382] VALUES (2121,'(٣٤٤) باب استحباب الشرب من نبيذ السقاية إذا لم يكن النبيذ مسكرا','1378','1777','0');
INSERT INTO [t121382] VALUES (2122,'(٣٤٥) باب السعي بين الصفا والمروة مع طواف الزيارة للمتمتع','1379','1777','0');
INSERT INTO [t121382] VALUES (2123,'(٣٤٦) باب ترك السعي بين الصفا والمروة مع طواف الزيارة للمفرد والقارن','1380','1777','0');
INSERT INTO [t121382] VALUES (2124,'(٣٤٧) باب ذكر من قدم نسكا قبل نسك جاهلا، بذكر خبر مختصر غير متقصي، والدليل على أن لا فدية له','1380','1777','0');
INSERT INTO [t121382] VALUES (2125,'(٣٤٨) باب خطبة الإمام بمنى يوم النحر بعد الظهر','1381','1777','0');
INSERT INTO [t121382] VALUES (2126,'(٣٤٩) باب خطبة الإمام على الراحلة','1382','1777','0');
INSERT INTO [t121382] VALUES (2127,'(٣٥٠) باب الرخصة في الجماع يوم النحر بعد الزيارة','1382','1777','0');
INSERT INTO [t121382] VALUES (2128,'(٣٥١) باب ذكر الناسي بعض نسكه يوم النحر ثم يذكره','1382','1777','0');
INSERT INTO [t121382] VALUES (2129,'(٣٥٢) باب البيتوتة بمنى ليالي أيام التشريق','1383','1777','0');
INSERT INTO [t121382] VALUES (2130,'(٣٥٣) باب الرخصة في البيتوتة لآل العباس بمكة أيام منى من أجل سقايتهم ليقوموا بإسقاء الناس منها','1383','1777','0');
INSERT INTO [t121382] VALUES (2131,'(٣٥٤) باب النهي عن الطيب واللباس إذا أمسى الحاج يوم النحر قبل أن يفيض، وكل ما زجر الحاج عنه قبل رمي الجمرة يوم النحر','1384','1777','0');
INSERT INTO [t121382] VALUES (2132,'(٣٥٥) باب النهي عن صوم يوم الفطر ويوم النحر','1384','1777','0');
INSERT INTO [t121382] VALUES (2133,'(٣٥٦) باب النهي عن صيام أيام التشريق بدلالة لا بتصريح','1385','1777','0');
INSERT INTO [t121382] VALUES (2134,'(٣٥٧) باب الزجر عن صوم أيام التشريق بتصريح لا بكناية، ولا بدلالة من غير تصريح','1385','1777','0');
INSERT INTO [t121382] VALUES (2135,'(٣٥٨) باب سنة الصلاة بمنى للحاج من غير أهل مكة','1386','1777','0');
INSERT INTO [t121382] VALUES (2136,'(٣٥٩) باب ذكر الدليل على أن النبي - صلى الله عليه وسلم - إنما صلى بها ركعتين لأنه كان مسافرا غير مقيم، إذ هو - صلى الله عليه وسلم - كان من أهل المدينة، وإنما قدم مكة حاجا، لم يقم بها إقامة [٢٩١ - ب] يجب عليه إتمام الصلاة','1386','1777','0');
INSERT INTO [t121382] VALUES (2137,'(٣٦٠) باب فضل يوم القر، وهو أول أيام التشريق','1387','1777','0');
INSERT INTO [t121382] VALUES (2138,'(٣٦١) باب بدء رمي النبي الجمار، والعلة التي رماها بدءا قبل عود','1388','1777','0');
INSERT INTO [t121382] VALUES (2139,'(٣٦٢) باب وقت رمي الجمار أيام التشريق','1388','1777','0');
INSERT INTO [t121382] VALUES (2140,'(٣٦٣) باب ذكر البيان أن رمي الجمار إنما أراد لإقامة ذكر الله لا للرمي فقط','1389','1777','0');
INSERT INTO [t121382] VALUES (2141,'(٣٦٤) باب التكبير مع كل حصاة يرمي بها رامي الجمار، والوقوف عند الجمرة الأولى والثانية مع تطويل القيام والتضرع [٢٩٢ - أ] وترك الوقوف عند جمرة العقبة بعد رميها أيام منى','1389','1777','0');
INSERT INTO [t121382] VALUES (2142,'(٣٦٥) باب الوقوف عند الجمرة الأولى والثانية بعد رميها، والدليل على أن الوقوف بعد رمي الأولى منهما أمامها لا خلفها، ولا عن يمينها، ولا عن شمالها، والوقوف عند الثانية ذات اليسار مما يلي الوادي مستقبل القبلة في الوقفين جميعا، ورفع اليدين في الوقفين جميعا','1390','1777','0');
INSERT INTO [t121382] VALUES (2143,'(٣٦٦) باب خطبة الإمام أوسط أيام التشريق','1390','1777','0');
INSERT INTO [t121382] VALUES (2144,'(٣٦٧) باب ذكر تعليم الإمام في خطبته يوم النفر الأول كيف ينفرون، كيف يرمون، ويعلمهم باقي مناسكهم','1391','1777','0');
INSERT INTO [t121382] VALUES (2145,'(٣٦٨) باب الرخصة [٢٩٢ - ب] للرعاء في رمي الجمار بالليل','1391','1777','0');
INSERT INTO [t121382] VALUES (2146,'(٣٦٩) باب الرخصة للرعاة أن يرموا يوما ويدعوا يوما','1392','1777','0');
INSERT INTO [t121382] VALUES (2147,'(٣٧٠) باب ذكر الدليل [على] أن النبي - صلى الله عليه وسلم - إنما رخص للرعاء في ترك رمي الجمار يوما ويرعوا يوما في يومين من أيام التشريق','1392','1777','0');
INSERT INTO [t121382] VALUES (2148,'(٣٧١) باب وقت النفر من منى آخر أيام التشريق','1393','1777','0');
INSERT INTO [t121382] VALUES (2149,'(٣٧٢) باب استحباب النزول بالمحصب استنانا بالنبي - صلى الله عليه وسلم -','1393','1777','0');
INSERT INTO [t121382] VALUES (2150,'(٣٧٣) باب ذكر الدليل على أن النبي - صلى الله عليه وسلم - قد كان أعلمهم وهو بمنى أن ينزل بالأبطح','1394','1777','0');
INSERT INTO [t121382] VALUES (2151,'(٣٧٤) باب ذكر الدليل على أن النبي - صلى الله عليه وسلم - إنما نزل بالأبطح ليكون أسمح لخروجه','1396','1777','0');
INSERT INTO [t121382] VALUES (2152,'(٣٧٥) باب ذكر الدليل على أن الاسم قد ينفى عن الشيء إذا لم يكن واجبا، وإن كان الفعل مباحا','1397','1777','0');
INSERT INTO [t121382] VALUES (2153,'(٣٧٦) باب استحباب النزول بالمحصب، وإن لم يكن ذلك واجبا إذ الخلفاء الراشدون المهديون الذين أمر النبي - صلى الله عليه وسلم - بالعض بالنواجذ على سنته وسنتهم -قد اقتدوا بالنبي - صلى الله عليه وسلم -- بالنزول به','1397','1777','0');
INSERT INTO [t121382] VALUES (2154,'(٣٧٧) باب استحباب الصلاة بالمحصب إذا نزله المرء','1398','1777','0');
INSERT INTO [t121382] VALUES (2155,'(٣٧٨) باب ذكر البيان أن رسول الله - صلى الله عليه وسلم - قصر الصلاة بالأبطح بعدما نفر من منى، ضد قول من يحكي لنا عنه من أهل عصرنا أن الحاج إذا قفل راجعا إلى بلده عليه إتمام الصلاة','1398','1777','0');
INSERT INTO [t121382] VALUES (2156,'(٣٧٩) باب استحباب الإدلاج بالارتحال من الحصبة، اقتداء بفعل المصطفى - عليه السلام -','1399','1777','0');
INSERT INTO [t121382] VALUES (2157,'(٣٨٠) باب الأمر بطواف الوداع بلفظ عام مراده خاص','1400','1777','0');
INSERT INTO [t121382] VALUES (2158,'(٣٨١) باب الدليل على أن اللفظة التي ذكرتها في خبر ابن عباس لفظ عام مراده خاص، والدليل [على] أن النبي - صلى الله عليه وسلم - إنما أراد بقوله: لا ينفرن أحد حتى يكون آخر عهده بالبيت، خلا الحيض، بذكر لفظة عام مرادها خاص في ذكر الحيض','1400','1777','0');
INSERT INTO [t121382] VALUES (2159,'(٣٨٢) باب ذكر الدليل على أن النبي - صلى الله عليه وسلم - إنما رخص للحيض في النفر بلا وداع إذا كن قد أفضن قبل ذلك، ثم حضن','1401','1777','0');
INSERT INTO [t121382] VALUES (2160,'(٣٨٣) باب استحباب دخول الكعبة والذكر والدعاء فيها','1401','1777','0');
INSERT INTO [t121382] VALUES (2161,'(٣٨٤) باب وضع الوجه والجبين على ما استقبل من الكعبة عند دخولها والذكر والاستغفار','1401','1777','0');
INSERT INTO [t121382] VALUES (2162,'(٣٨٥) باب التكبير والتحميد والتهليل والمسألة، والاستغفار عند [٢٩٤ - ب] كل ركن من أركان الكعبة','1402','1777','0');
INSERT INTO [t121382] VALUES (2163,'(٣٨٦) باب استحباب السجود بين العمودين عند دخول الكعبة، والجلوس بعد السجدة والدعاء','1402','1777','0');
INSERT INTO [t121382] VALUES (2164,'(٣٨٧) باب ذكر البيان أن النبي - صلى الله عليه وسلم - قد صلى في البيت، وهذا من الجنس الذي أعلمت في غير موضع من كتبنا أن الخبر الذي يجب قبوله هو خبر من ي','1403','1777','0');
INSERT INTO [t121382] VALUES (2165,'(٣٨٨) باب ذكر المكان الذي صلى فيه النبي - صلى الله عليه وسلم - من الكعبة','1403','1777','0');
INSERT INTO [t121382] VALUES (2166,'(٣٨٩) باب ذكر القدر الذي جعل النبي - صلى الله عليه وسلم - بين مقامه الذي صلى فيه بين الكعبة وبين الجدار','1404','1777','0');
INSERT INTO [t121382] VALUES (2167,'(٣٩٠) باب الخشوع في الكعبة إذا دخلها المرء، والنظر إلى موضع سجوده إلى الخروج منها','1405','1777','0');
INSERT INTO [t121382] VALUES (2168,'(٣٩١) باب استحباب دخول الكعبة إذ دخولها دخولا في حسنة، وخروجا من سيئة، مغفورا للداخل','1405','1777','0');
INSERT INTO [t121382] VALUES (2169,'(٣٩٢) باب ذكر الدليل [على] أن دخول الكعبة ليس بواجب، إذ النبي - صلى الله عليه وسلم - قد أعلم بعد دخوله إياها أنه ود أن لم يكن دخلها مخافة','1405','1777','0');
INSERT INTO [t121382] VALUES (2170,'(٣٩٣) باب استحباب الصلاة عند باب الكعبة بعد الخروج منها','1406','1777','0');
INSERT INTO [t121382] VALUES (2171,'(٣٩٤) باب ذكر الموضع الذي صلى فيه النبي - صلى الله عليه وسلم - الركعتين بعد خروجه من الكعبة','1406','1777','0');
INSERT INTO [t121382] VALUES (2172,'(٣٩٥) باب التزام البيت عند الخروج من الكعبة إن كان يزيد بن أبي زياد من الشرط الذي اشترطنا في أول الكتاب','1407','1777','0');
INSERT INTO [t121382] VALUES (2173,'(٣٩٦) باب استحباب الصلاة في الحجر إذا لم يمكن دخول الكعبة إذ بعض الحجر من البيت، بذكر خبر لفظه عام مراده خاص. أنا خائف أن يسمع بهذا الخبر الذي ذكرت أن لفظه لفظ عام مراده خاص، بعض الناس فيتوهم أن جميع الحجر من الكعبة لا بعضه','1408','1777','0');
INSERT INTO [t121382] VALUES (2174,'(٣٩٧) باب ذكر البيان أن بعض الحجر من البيت، لا جميعه، والدليل [على] أن النبي - صلى الله عليه وسلم - إنما أراد بقوله: وأخرجوا الحجر من البيت، بعضه لا جميعه، وهذا من الجنس الذي أعلمت في غير موضع من كتبنا أن الاسم باسم المعرفة بالألف واللام قد يقع على بعض الشيء','1408','1777','0');
INSERT INTO [t121382] VALUES (2175,'(٣٩٨) باب إباحة العمرة في ذي الحجة بعد مضي أيام التشريق','1411','1777','0');
INSERT INTO [t121382] VALUES (2176,'(٣٩٩) باب العمرة في ذي الحجة من التنعيم لمن قد حج ذلك العام، ضد قول من زعم أن العمرة غير جائزة إلا من المواقيت التي وقت النبي - صلى الله عليه وسلم - حين ذكر المواقيت، فقال: يهل أهل المدينة من ذي الحليفة، الأخبار بتمامها','1411','1777','0');
INSERT INTO [t121382] VALUES (2177,'(٤٠٠) باب ذكر الدليل على أن العمرة من الميقات أفضل منها من التنعيم، إذ هي أكثر نصبا وأفضل نفقة، وما كان أكثر نصبا وأفضل نفقة فالأجر على قدر النصب والنفقة','1412','1777','0');
INSERT INTO [t121382] VALUES (2178,'(٤٠١) باب إسقاط الهدي عن المعتمر بعد مضي أيام التشريق، وإن كان قد حج من عامه ذلك','1412','1777','0');
INSERT INTO [t121382] VALUES (2179,'(٤٠٢) باب إباحة الحج عمن لا يستطيع الحج عن نفسه من الكبر، والدليل على أن الله عز وجل ولى نبيه بيان ما أنزل عليه من الوحي خاصا وعاما. فبين النبي - صلى الله عليه وسلم - أن الله لم يرد بقوله: (وأن ليس للإنسان إلا ما سعى) [النجم: ٣٩] جميع الأعمال. وأن الله إنما أراد بعض السعي لا جميعه، إذ لو كان الله أراد جميع السعي لم يكن الحج [٢٩٧ - أ] إلا لمن حج بنفسه، لم يسقط فرض الحج عن المرء إذا حج عنه، ولم يكتب للمحجوج عنه سعي غيره إذ لم يسع هو بنفسه سعي العمل','1414','1777','0');
INSERT INTO [t121382] VALUES (2180,'(٤٠٣) باب ذكر الدليل على أن الشيخ الكبير إذا استفاد مالا بعد كبر السن وهو غني، أو استفاد مالا بعد الإسلام كان فرض الحج واجب عليه وإن كان غير مستطيع أن يح','1414','1777','0');
INSERT INTO [t121382] VALUES (2181,'(٤٠٤) باب حج المرأة عن الرجل','1416','1777','0');
INSERT INTO [t121382] VALUES (2182,'(٤٠٥) باب الحج عن الميت، بذكر خبر مجمل غير مفسر على أصلنا','1416','1777','0');
INSERT INTO [t121382] VALUES (2183,'(٤٠٦) باب الحج عمن يجب عليه الحج بالإسلام، أو ملك المال، أو هما وهو غير مستطيع للحج ببدنه من الكبر','1417','1777','0');
INSERT INTO [t121382] VALUES (2184,'(٤٠٧) باب حج الرجل عن المرأة التي لا تستطيع الحج من الكبر بمثل اللفظة التي ذكرت أنها مجملة غير مفسرة','1418','1777','0');
INSERT INTO [t121382] VALUES (2185,'(٤٠٨) باب النهي عن أن يحج عن الميت من لم يحج عن نفسه','1418','1777','0');
INSERT INTO [t121382] VALUES (2186,'(٤٠٩) باب العمرة عن الذي لا يستطيع العمرة من الكبر','1419','1777','0');
INSERT INTO [t121382] VALUES (2187,'(٤١٠) باب النذر بالحج ثم يحدث الموت قبل وفائه، والأمر بقضائه، والدليل على أنه من جميع المال لتشبيه النبي - صلى الله عليه وسلم - نذر الحج بالدين','1419','1777','0');
INSERT INTO [t121382] VALUES (2188,'(٤١١) باب الدليل على أن الحج الواجب من جميع المال لا من الثلث','1419','1777','0');
INSERT INTO [t121382] VALUES (2189,'(٤١٢) باب النذر بالحج ماشيا، فيعجز الناذر عن المشي بذكر خبر مختصر غير متقصى','1420','1777','0');
INSERT INTO [t121382] VALUES (2190,'(٤١٣) باب هدي الناذر بالحج ماشيا، فيعجز عن المشي، والدليل على أن الخبرين اللذين ذكرتهما في الباب قبل مختصرين على ما ذكرت','1420','1777','0');
INSERT INTO [t121382] VALUES (2191,'(٤١٤) باب اليمين بالمشي إلى الكعبة فيعجز الحالف عن المشي','1421','1777','0');
INSERT INTO [t121382] VALUES (2192,'(٤١٥) باب ذكر إسقاط فرض الحج عن الصبي قبل البلوغ، وعن المجنون حتى يفيق','1421','1777','0');
INSERT INTO [t121382] VALUES (2193,'(٤١٦) باب ذكر حج الصبيان قبل البلوغ على غير الوجوب، والدليل على أن قول النبي - صلى الله عليه وسلم -: "رفع القلم عن الصبي، أراد القلم مما يكون إثما ووزرا على البالغ إذا ارتكبه، لا أن القلم مرفوع عن كتابة الحسنات للصبي إذا عملها','1422','1777','0');
INSERT INTO [t121382] VALUES (2194,'(٤١٧) باب الصبي يحج قبل البلوغ ثم يبلغ','1422','1777','0');
INSERT INTO [t121382] VALUES (2195,'(٤١٨) باب حج الأكرياء، والدليل على أن أكر المرء نفسه في العمل طلق مباح، إذ هو من ابتغاء فضل الله لأخذه الأجرة على ذلك','1423','1777','0');
INSERT INTO [t121382] VALUES (2196,'(٤١٩) باب حج الأجراء، والدليل [٢٩٩ - أ] على أن الأجير إذا أجر نفسه بكذا، وحج عن نفسه كانت له الأجرة على مستأجره، وأداء الفرض عن نفسه جائز','1424','1777','0');
INSERT INTO [t121382] VALUES (2197,'(٤٢٠) باب إباحة التجارة في الحج، والدليل على أن الاشتغال بما أباح الله من طلب المال من حله أيام الموسم في غير الأوقات الذي يشتغل المرء عن أداء المناسك لا ينقص أجر الحاج، ولا يبطل الحج، ولا يوجب عليه هديا ولا صوما ولا صدقة','1424','1777','0');
INSERT INTO [t121382] VALUES (2198,'(٤٢١) باب ذكر عدد حجج النبي - صلى الله عليه وسلم -، والدليل على ضد ما توهمه العامة أن النبي - صلى الله عليه وسلم - لم يحج إلا حجة واحدة. والنبي - صلى الله عليه وسلم - إنما حج حجة واحدة بعد هجرته إلى المدينة. فأما ما قبل الهجرة فقد حج النبي - صلى الله عليه وسلم - غير تلك الحجة التي حجها من المدينة','1425','1777','0');
INSERT INTO [t121382] VALUES (2199,'(٤٢٢) باب ذكر الدليل على صحة هذا المتن، والبيان أن النبي - صلى الله عليه وسلم - قد حج قبل هجرته إلى المدينة لا كما من طعن في هذا الخبر، وادعى أن هذا الخبر لم يروه غير زيد بن الحباب','1426','1777','0');
INSERT INTO [t121382] VALUES (2200,'(٤٢٣) باب الرخصة في دخول مكة بغير إحرام عند العلم بحدث','1428','1777','0');
INSERT INTO [t121382] VALUES (2201,'(٤٢٤) باب ذكر البيان أن العمرة فرض وأنها من الإسلام كالحج سواء لا أنها [٣٠٠ - أ] تطوع غير فريضة على ما قال بعض العلماء','1429','1777','0');
INSERT INTO [t121382] VALUES (2202,'(٤٢٥) باب ذكر عدد عمر رسول الله - صلى الله عليه وسلم -','1432','1777','0');
INSERT INTO [t121382] VALUES (2203,'(٤٢٦) باب فضل العمرة، وتكفير الذنوب التي يرتكبها المعتمر بين العمرتين بهما','1432','1777','0');
INSERT INTO [t121382] VALUES (2204,'(٤٢٧) باب الدليل على أن جهاد النساء الحج والعمرة، وفي الخبر -علمي- دلالة على أن العمرة واجبة كالحج، إذ النبي - صلى الله عليه وسلم - أعلم أن عليهن العمرة كما أن عليهن الحج','1433','1777','0');
INSERT INTO [t121382] VALUES (2205,'(٤٢٨) باب الرخصة في العمرة على الدواب المحبسة في سبيل الله','1433','1777','0');
INSERT INTO [t121382] VALUES (2206,'(٤٢٩) باب الرخصة للحاج بعد الفراغ من الحج: والعمرة والإحرام بهما من أي الحل شاء','1434','1777','0');
INSERT INTO [t121382] VALUES (2207,'(٤٣٠) باب فضل العمرة في رمضان','1434','1777','0');
INSERT INTO [t121382] VALUES (2208,'(٤٣١) باب إباحة العمرة من الجعرانة','1435','1777','0');
INSERT INTO [t121382] VALUES (2209,'(٤٣٢) باب إباحة العمرة في أشهر الحج لمن لا يحج عامه ذلك','1436','1777','0');
INSERT INTO [t121382] VALUES (2210,'(٤٣٣) باب إباحة العمرة قبل الحج','1436','1777','0');
