INSERT INTO [t122394] VALUES (139,'١ - في ربيع الأول من هذه السنة: هاجر النبي - صلى الله عليه وسلم - من مكة إلى المدينة','129','138','0');
INSERT INTO [t122394] VALUES (140,'الشرح','129','139','0');
INSERT INTO [t122394] VALUES (141,'٢ - وفي هذه السنة: نزل - صلى الله عليه وسلم - بقباء, وبنى مسجد قباء','136','138','0');
INSERT INTO [t122394] VALUES (142,'الشرح','136','141','0');
