INSERT INTO [t122394] VALUES (782,'الشرح','522','781','0');
INSERT INTO [t122394] VALUES (783,'٤٩ - وفي هذه السنة: كانت سرية خالد بن سعيد بن العاص إلى اليمن.','523','675','0');
INSERT INTO [t122394] VALUES (784,'الشرح','523','783','0');
INSERT INTO [t122394] VALUES (785,'السنة العاشرة من الهجرة','525','0','0');
