INSERT INTO [t122394] VALUES (787,'١ - في ربيع الأول من هذه السنة: كانت سرية خالد بن الوليد - رضي الله عنه - إلى بني عبد المدان بنجران.','525','786','0');
INSERT INTO [t122394] VALUES (788,'الشرح','525','787','0');
INSERT INTO [t122394] VALUES (789,'٢ - وفي ربيع الأول من هذه السنة: جاء وفد الحارث بن كعب إلى رسول الله - صلى الله عليه وسلم.','525','786','0');
INSERT INTO [t122394] VALUES (790,'الشرح','525','789','0');
INSERT INTO [t122394] VALUES (791,'٣ - وفي شعبان من هذه السنة: قدم عدي بن حاتم الطائي على رسول الله -صلى الله عليه وسلم - فأسلم.','526','786','0');
INSERT INTO [t122394] VALUES (792,'الشرح','526','791','0');
INSERT INTO [t122394] VALUES (793,'٤ - وفي شعبان من هذه السنة: قدم وفد خولان على رسول الله - صلى الله عليه وسلم - مؤمنين مصدقين.','527','786','0');
INSERT INTO [t122394] VALUES (794,'الشرح','527','793','0');
INSERT INTO [t122394] VALUES (795,'٥ - وفي رمضان من هذه السنة: قدم وفد غامد على رسول الله - صلى الله عليه وسلم -.','528','786','0');
INSERT INTO [t122394] VALUES (796,'الشرح','528','795','0');
INSERT INTO [t122394] VALUES (797,'٦ - وفي رمضان من هذه السنة: اعتكف رسول الله - صلى الله عليه وسلم - عشرين يوما، وعارضه جبريل -عليه السلام- بالقرآن مرتين.','528','786','0');
INSERT INTO [t122394] VALUES (798,'الشرح','528','797','0');
INSERT INTO [t122394] VALUES (799,'٧ - وفي رمضان من هذه السنة: قدم وفد غسان على رسول الله - صلى الله عليه وسلم -.','528','786','0');
INSERT INTO [t122394] VALUES (800,'الشرح','528','799','0');
INSERT INTO [t122394] VALUES (801,'٨ - وفي رمضان من هذه السنة: كانت سرية علي بن أبي طالب - رضي الله عنه - إلى اليمن، فأسلمت على يده همدان كلها في يوم واحد.','529','786','0');
INSERT INTO [t122394] VALUES (802,'الشرح','529','801','0');
INSERT INTO [t122394] VALUES (803,'٩ - وفي رمضان من هذه السنة: قدم جرير بن عبد الله البجلي - رضي الله عنه - على رسول الله - صلى الله عليه وسلم - مسلما، فبعثه رسول الله - صلى الله عليه وسلم - إلى ذي الخلصة فهدمها.','530','786','0');
INSERT INTO [t122394] VALUES (804,'الشرح','530','803','0');
INSERT INTO [t122394] VALUES (805,'قال ابن حجر -رحمه الله-','530','804','0');
INSERT INTO [t122394] VALUES (806,'١٠ - وفي رمضان من هذه السنة: كانت سرية علي بن أبي طالب - رضي الله عنه - إلى اليمن المرة الثانية.','531','786','0');
INSERT INTO [t122394] VALUES (807,'الشرح','531','806','0');
INSERT INTO [t122394] VALUES (808,'قال ابن سعد -رحمه الله-','531','807','0');
INSERT INTO [t122394] VALUES (809,'١١ - وفي شوال من هذه السنة: قدم وفد سلامان على رسول الله - صلى الله عليه وسلم -.','532','786','0');
