INSERT INTO [t12293] VALUES (306,'(٢٨٣) وسئل فضيلته عن حكم القسم بقول: "وحياة الله"، وقول المرأة لزوجها: "حرام علي ربنا أن تفعل كذا"، وقولهم: "حد الله بيني وبينك "؟','498','301','0');
INSERT INTO [t12293] VALUES (307,'(٢٨٤) وسئل -حفظه الله تعالى-: عن حكم الحلف بالنبي صلى الله عليه وسلم والكعبة؟ والشرف والذمة؟ وقول الإنسان "بذمتي "؟','500','301','0');
INSERT INTO [t12293] VALUES (308,'(٢٨٥) وسئل: عن قول الإنسان: "والله وحياتك "؟','501','301','0');
