INSERT INTO [t12293] VALUES (312,'القبور','503','2','0');
INSERT INTO [t12293] VALUES (313,'(٢٨٩) سئل فضيلة الشيخ: عمن يعبد القبور بالطواف حولها ودعاء أصحابها والنذر لهم إلى غير ذلك من أنواع العبادة؟','504','312','0');
