INSERT INTO [t12293] VALUES (315,'(٢٩١) سئل فضيلة الشيخ: كيف نجيب عباد القبور الذين يحتجون بدفن النبي صلى الله عليه وسلم في المسجد النبوي؟','509','312','0');
INSERT INTO [t12293] VALUES (316,'(٢٩٢) سئل فضيلة الشيخ: عن رجل بنى مسجدا وأوصى أن يدفن فيه فدفن فما العمل الآن؟','510','312','0');
