INSERT INTO [t12293] VALUES (1873,'سئل فضيلة الشيخ: عن الصلاة في البيت المغصوب؟','4660','1857','0');
INSERT INTO [t12293] VALUES (1874,'وسئل فضيلة الشيخ: يوجد قبر خارج القرية، فنبتت على هذا القبر شجرة، فجاءت الإبل تأكل من هذه الشجرة وتدوس على هذا القبر، وحفاظا على هذا القبر وضعوا عليه سورا فهل هذا العمل جائز؟','4660','1857','0');
