INSERT INTO [t12293] VALUES (1877,'وسئل فضيلة الشيخ: عن حكم الصلاة على الإسفلت المرشوش بالماء؟','4662','1857','0');
INSERT INTO [t12293] VALUES (1878,'وسئل فضيلته: عن الأماكن التي لا تصح فيها الصلاة؟','4662','1857','0');
