INSERT INTO [t12293] VALUES (1920,'وسئل فضيلة الشيخ: عن رجل دخل مع الإمام بنية صلاة الوتر ثم تذكر وهو يصلي أنه لم يصل العشاء فقلب النية عشاء فهل يصح؟','4721','1917','0');
