INSERT INTO [t12293] VALUES (6490,'الهدي والأضحية','9858','4559','0');
INSERT INTO [t12293] VALUES (6491,'س١: سئل فضيلة الشيخ- رحمه الله-: عن الفرق بين الهدي والأضحية والفدية؟','9859','6490','0');
INSERT INTO [t12293] VALUES (6492,'س ٢: سئل فضيلة الشيخ- رحمه الله-: ما حكم الأضحية؟ وما حكم إفراد الميت بالأضحية؟','9860','6490','0');
INSERT INTO [t12293] VALUES (6493,'س٤: سئل فضيلة الشيخ- رحمه الله-: ما القول الصحيح في حكم الأضحية؟','9865','6490','0');
INSERT INTO [t12293] VALUES (6494,'س٥: سئل فضيلة الشيخ- رحمه الله-: هل يستحب الهدي في العمرة؟','9865','6490','0');
INSERT INTO [t12293] VALUES (6495,'س٦: سئل فضيلة الشيخ- رحمه الله-: ما سبب سياق النبي - صلى الله عليه وسلم - الهدي في عمرة الحديبية مع أنه كان ذاهبا للعمرة وليس للحج؟','9865','6490','0');
INSERT INTO [t12293] VALUES (6496,'س٧: سئل فضيلة الشيخ- رحمه الله-: رجل يتقرب إلى الله بذبيحة لكن في غير وقت الأضحية، فهل له أجر؟','9867','6490','0');
INSERT INTO [t12293] VALUES (6497,'س ٨: سئل فضيلة الشيخ- رحمه الله-: ما حكم الأضحية وهل يستدين الإنسان ليضحي؟','9867','6490','0');
INSERT INTO [t12293] VALUES (6498,'س٩: سئل فضيلة الشيخ- رحمه الله-: هل ذبح الأضحية يوم العيد واجب أو سنة؟','9868','6490','0');
INSERT INTO [t12293] VALUES (6499,'س١٠: سئل فضيلة الشيخ- رحمه الله-: كيف يجمع الإنسان بين الأضحية والحج؟ وهل هذا مشروع؟','9869','6490','0');
INSERT INTO [t12293] VALUES (6500,'س١١: سئل فضيلة الشيخ- رحمه الله-: كيف يجمع الإنسان بين الحج والأضحية؟ وماذا يصنع بالنسبة للأخذ من شعره وأظفاره؟','9869','6490','0');
INSERT INTO [t12293] VALUES (6501,'س١٢: سئل فضيلة الشيخ- رحمه الله-: هل على الحاج أضحية؟ وما الجواب عن أن النبي - صلى الله عليه وسلم - ضحى عن أزواجه','9870','6490','0');
INSERT INTO [t12293] VALUES (6502,'س١٣: سئل فضيلة الشيخ- رحمه الله-: هل على كل مسلم أن يضحي؟ وهل يجوز اشتراك خمسة أفراد في أضحية واحدة؟','9871','6490','0');
INSERT INTO [t12293] VALUES (6503,'س١٤: سئل فضيلة الشيخ- رحمه الله-: هل من السنة أن الإنسان إذا أهدى في الحج أن يضحي؟','9874','6490','0');
INSERT INTO [t12293] VALUES (6504,'س١٥: سئل فضيلة الشيخ- رحمه الله- عن رجل يضحي مع عائلته، وله أبناء في مدينة أخرى ليسوا متزوجين، ولم يضحوا فماذا عليهم؟','9874','6490','0');
INSERT INTO [t12293] VALUES (6505,'س١٦: سئل فضيلة الشيخ- رحمه الله-: عن شخص له والدة متوفاة، ويريد أن يضحي عنها من ماله، فهل يشركها في الأضحية','9874','6490','0');
INSERT INTO [t12293] VALUES (6506,'س١٧: سئل فضيلة الشيخ- رحمه الله-: يختلف الجاموس عن البقر في كثير من الصفات كاختلاف الماعز عن الضأن، وقد فصل الله في سورة الأنعام بين الضأن والماعز، ولم يفصل بين الجاموس والبقر، فهل يدخل في ضمن الأزواج الثمانية فيجوز الأضحية بها أم لا يجوز؟','9884','6490','0');
INSERT INTO [t12293] VALUES (6507,'س١٨: سئل فضيلة الشيخ- رحمه الله-: أيهما أفضل في الأضحية الكبش أو البقر؟','9884','6490','0');
INSERT INTO [t12293] VALUES (6508,'س١٩: سئل فضيلة الشيخ- رحمه الله-: أيهما أفضل في الأضحية كبيرة الحجم كثيرة الشحم واللحم أم غالية الثمن؟','9884','6490','0');
INSERT INTO [t12293] VALUES (6509,'س٢٠: سئل فضيلة الشيخ- رحمه الله-: ورد في كلامكم في فضل الأضحية أنها كلما كانت أكمل كلما كانت أفضل مع أن الثنية من الضأن أفضل لحما وأقل ثمنا عند الناس من التي أكبر منها فأيهما أفضل؟','9885','6490','0');
INSERT INTO [t12293] VALUES (6510,'س٢١: سئل فضيلة الشيخ- رحمه الله-: كيف أعرف الإبل التي تجوز الأضحية بها من جهة السن؟ لأن أصحاب الإبل يقولون إنها تضحي لكي يسوقون بضاعتهم، فما الحكم؟','9886','6490','0');
INSERT INTO [t12293] VALUES (6511,'س٢٢: سئل فضيلة الشيخ- رحمه الله-: هل تجوز أضحية واحدة لأخوين شقيقين في بيت واحد مع أولادهم أكلهم وشر بهم واحد؟','9886','6490','0');
INSERT INTO [t12293] VALUES (6512,'س٢٣: سئل فضيلة الشيخ- رحمه الله-: أهدى الرسول - صلى الله عليه وسلم - يوم النحر، في حجة الوداع مئة من الإبل، فهل هذه المئة له خاصة أو له ولأزواجه؟','9887','6490','0');
INSERT INTO [t12293] VALUES (6513,'س٢٤: سئل فضيلة الشيخ- رحمه الله-: عن أب يسكن معه في بيته ثلاثة أبناء متزوجون، ولكل واحد منهم جزء مستقل في البيت فهل تجزئ أضحية واحدة عنهم؟','9888','6490','0');
INSERT INTO [t12293] VALUES (6514,'س٢٥: سئل فضيلة الشيخ- رحمه الله-: عمن يسكن مع والده، وهو متزوج وله مال، فهل يكتفي بأضحية والده؟','9888','6490','0');
INSERT INTO [t12293] VALUES (6515,'س٢٦: سئل فضيلة الشيخ- رحمه الله-: هل يصح ذبح ذبحيتين: واحدة بنية الأضحية، والثانية بنية توزيع اللحم؟ وما حكم الأضحية بمقطوعة الأذن أو القرن؟','9889','6490','0');
INSERT INTO [t12293] VALUES (6516,'س٢٧: سئل فضيلة الشيخ- رحمه الله-: إذا كان الأب له أولاد وبعض الأولاد متزوج، فهل تكفي أضحية الأب عن الأبناء مع أن لهم زوجات؟ وهل يذبح الوالد عن نفسه والولد عن نفسه والزوجة عن نفسها، وكذلك كل من كان له مرتب؟','9890','6490','0');
INSERT INTO [t12293] VALUES (6517,'س٢٨: سئل فضيلة الشيخ- رحمه الله-: ثلاثة رجال كل واحد اشترى له هديا فقالوا: شاة نهديها، وشاة نتصدق بها، وشاة نأكلها فبهذا نكون قد أكلنا الثلث، وتصدقنا الثلث، وأهدينا الثلث، فما رأيك في هذا؟','9891','6490','0');
INSERT INTO [t12293] VALUES (6518,'س٢٩: سئل فضيلة الشيخ- رحمه الله-: عن ثلاثة أخوة في بيت، لهم رواتب، وكلهم متزوج، فهل تجزئهم أضحية واحدة أم لكل واحد أضحية؟','9891','6490','0');
INSERT INTO [t12293] VALUES (6519,'س٣٠: سئل فضيلة الشيخ- رحمه الله-: انتشر بين بعض العامة أنه لا يضحي الحاج إلا إذا كان قد توفي أحد والديه فهل هذا صحيح؟','9892','6490','0');
INSERT INTO [t12293] VALUES (6520,'س٣١: سئل فضيلة الشيخ- رحمه الله-: رجل له أولاد، وله ابن كبير متزوج يسكن معه وموظف، وأكلهما وشربهما واحد، فهل في حقهما أضحية واحدة؟','9893','6490','0');
INSERT INTO [t12293] VALUES (6521,'س٣٢: سئل فضيلة الشيخ- رحمه الله-: رجل متزوج بزوجتين الأولى عنده والأخرى عند أهلها هل يلزم أضحية أم أضحيتين؟','9893','6490','0');
INSERT INTO [t12293] VALUES (6522,'س٣٣: سئل فضيلة الشيخ- رحمه الله-: عن ثلاثة اخوة يسكنون في سكن واحد، ويضحون بأضحية واحدة فهل تجزئ عنهم؟','9893','6490','0');
INSERT INTO [t12293] VALUES (6523,'س٣٤: سئل فضيلة الشيخ- رحمه الله-: أشرك الرسول - صلى الله عليه وسلم - عليا رضي الله عنه معه في الهدي (٢) ، ولم يقل: لك هذا القدر، فهل للإنسان أن يشرك غيره بغير تحديد؟','9894','6490','0');
INSERT INTO [t12293] VALUES (6524,'س٣٥: سئل فضيلة الشيخ- رحمه الله-: هل يجوز للمقتدر أن يذبح أكثر من أضحية له، حيث ورد أن النبي - صلى الله عليه وسلم - ضحى بكبشين؟ وهل يمكن أن يشترك الرجل وزوجته في أضحية واحدة من هذا نصف ومن هذا نصف، وعلى أيهما يكون الإنسان؟','9895','6490','0');
INSERT INTO [t12293] VALUES (6525,'س٣٦: سئل فضيلة الشيخ- رحمه الله-: رجل يقول: عمتي قد أوصت بأضحية وعشاء في رمضان، وعندي لها ما يقرب من عشرين ألف رلال، وهذا المبلغ لا أستطيع أن أشتري به عقارا أنفذ الوصية من رلعه، فهل يمكن أن أشتري به أسهما من شركة الكهرباء مثلا، وأنفذ الوصية من ربح هذه الأسهم؟','9896','6490','0');
INSERT INTO [t12293] VALUES (6526,'س٣٧: سئل فضيلة الشيخ- رحمه الله-: إذا أوصى الميت بأضحية، ثم قال أحد الأقارب أو الأولاد أنا أضحي عن الميت من جيبي، فهل يجوز للوكيل مثلأ أن يقبل ولا يضحي من دراهم الميت، وإذا وقع ذلك فما الحكم؟','9897','6490','0');
INSERT INTO [t12293] VALUES (6527,'س٣٨: سئل فضيلة الشيخ- رحمه الله-: أيهما أفضل أن يدفع رب البيت قيمة الأضحية وحده، أم يضحي كلل مستطيع من أفراد الأسرة تطييبا لنفوسهم؟','9897','6490','0');
INSERT INTO [t12293] VALUES (6528,'س٣٩: سئل فضيلة الشيخ- رحمه الله-: إذا أوصى الرجل بأضحية، فهل الورثة مخيرون بين ذبح شاة، وبين الاشتراك في سبع بدنة أم لا؟','9898','6490','0');
INSERT INTO [t12293] VALUES (6529,'س٤٠: سئل فضيلة الشيخ- رحمه الله-: ما رأيكم في ذبح الهدي عن طريق البنك الاسلامي، وتوفيع لحمه خارج الحرم، خاصة إذا كان الحملة فيها عدد كبير من الناس، فهل الأفضل أن يذبحها','9898','6490','0');
INSERT INTO [t12293] VALUES (6530,'س٤١: سئل فضيلة الشيخ- رحمه الله-: هل يجوز ذبح الخميى في الأضحية؟','9899','6490','0');
INSERT INTO [t12293] VALUES (6531,'س٤٢: سئل فضيلة الشيخ- رحمه الله-: هل يجوز الأضحية بالمخصي؟','9900','6490','0');
INSERT INTO [t12293] VALUES (6532,'س٤٣: سئل فضيلة الشيخ- رحمه الله-: ما العيوب التي تكون مانعة من الاجزاء في الأضحية؟ وما أول وقت الذبح وآخره؟','9900','6490','0');
INSERT INTO [t12293] VALUES (6533,'س٤٤: سئل فضيلة الشيخ- رحمه الله-: ما حكم الأضحية بشاة فيها نوع من المرض يسمى بالطلوع وهل يعتبر عيبا؟','9902','6490','0');
INSERT INTO [t12293] VALUES (6534,'س٤٥: سئل فضيلة الشيخ- رحمه الله-: في مسالة الأضحية الحديث الوارد عن النبي - صلى الله عليه وسلم - ك- صلى الله عليه وسلم - في الأضحية التي لا تجزئ أربعة أشياء، وقاس- صلى الله عليه وسلم - عليها العلماء بعض الأشياء التي إما أن تكون أولى أو تكون ظاهرة القياس للعلة بينها، قد يقول قائل: نحن نقيس على بعض أركلان الإسلام، فما هو الضابط فيها؟','9902','6490','0');
INSERT INTO [t12293] VALUES (6535,'س٤٦: سئل فضيلة الشيخ- رحمه الله-: هل تصح الأضحية بالأغنام الموسومة في أذنيها؟','9904','6490','0');
INSERT INTO [t12293] VALUES (6536,'س٤٧: سئل فضيلة الشيخ- رحمه الله-: ما هي الكيفية الصحيحة لذبح الأضحية؟','9905','6490','0');
INSERT INTO [t12293] VALUES (6537,'س٤٨: سئل فضيلة الشيخ- رحمه الله-: ما وقت التسمية في الأضحية؟ وما صفتها؟ وهل ما يفعله الناس من المسح على ظهر الأضحية عند تسميتها له أصل من السنة أو من كلام أهل العلم؟','9906','6490','0');
INSERT INTO [t12293] VALUES (6538,'س٤٩: سئل فضيلة الشيخ- رحمه الله-: من نسي التسمية عند ذبح الأضاحي فماذا يلزمه؟ وهل هناك فرق بين صاحب الأضحية المتبرع أو الوكيل؟','9906','6490','0');
INSERT INTO [t12293] VALUES (6539,'س٥٠: سئل فضيلة الشيخ- رحمه الله-: هل يصح مسح ظهر الأضحية قبل ذبحها؟ وما الذكر المشروع؟','9908','6490','0');
INSERT INTO [t12293] VALUES (6540,'س٥١: سئل فضيلة الشيخ- رحمه الله-: هل يشترط أن يذكر عند ذبح الأضحية أنها عن فلان؟','9909','6490','0');
INSERT INTO [t12293] VALUES (6541,'س٥٢: سئل فضيلة الشيخ- رحمه الله-: هل يشترط أن يذبح الإنسان أضحيته بنفسه أو يحضر عند ذبح الوكيل للأضحية؟','9910','6490','0');
INSERT INTO [t12293] VALUES (6542,'س٥٣: سئل فضيلة الشيخ-رحمه الله-: عن رجل خارج بلده للعمل لمدة ثلاث سنوات، ولم يقم بالأضحية، ولم يوكل، فهل عليه كفارة؟','9913','6490','0');
INSERT INTO [t12293] VALUES (6543,'س ٥٤: سئل فضيلة الشيخ-رحمه الله-: عن رجل أراد أن يضحي وهو ناو الحج فكيف يعمل؟','9913','6490','0');
INSERT INTO [t12293] VALUES (6544,'س٥٥: سئل فضيلة الشيخ- رحمه الله-: هل يجوز للشخص','9913','6490','0');
INSERT INTO [t12293] VALUES (6545,'س٥٦: سئل فضيلة الشيخ- رحمه الله-: هل يجزئ الهدي عن طريق التوكيل بواسطة البنك الإسلامي ومتى يحل المحرم من إحرامه؛ لأنه لا يعلم وقت ذبح الهدي؟','9914','6490','0');
INSERT INTO [t12293] VALUES (6546,'س٥٧: سئل فضيلة الشيخ- رحمه الله-: من لم يستطع ذبح الهدي بنفسه ووكل جمعية، فهل يصح فعله؟','9915','6490','0');
INSERT INTO [t12293] VALUES (6547,'س٥٨: سئل فضيلة الشيخ- رحمه الله-: الحاج الذي عليه هدي أو فدية هل يوكل من يذبح عنه كشركة الراجحي؟','9925','6490','0');
INSERT INTO [t12293] VALUES (6548,'س٥٩: سئل فضيلة الشيخ- رحمه الله-: ذكرتم بأنه لا يدفع الإنسان أضحيته لمن في الخارج وأن عليه أن يذبحها بنفسه، فهل الهدي كذلك، فماذا تقولون لبعض الشركات التي تقبل الهدي والأضحية فهل ندفع لهم ذلك؟','9925','6490','0');
INSERT INTO [t12293] VALUES (6549,'س٦٠: سئل فضيلة الشيخ- رحمه الله-: عن رجل مسافر عن بلده إلى المملكة فهل يرسل ثمن الأضحية لبلده لأنهم أشد فقرا؟','9926','6490','0');
INSERT INTO [t12293] VALUES (6550,'س٦١: سئل فضيلة الشيخ- رحمه الله-: من كان مسافرا عن بلده فهل يرسل ثمن الأضحية لأهله ليضحوا في بيته؟','9926','6490','0');
INSERT INTO [t12293] VALUES (6551,'س٦٢: سئل فضيلة الشيخ- رحمه الله-: ما حكم ذبح الهدي في مكة عن طريق اللجنة وتوزيع لحمه خارج المملكة؟','9930','6490','0');
INSERT INTO [t12293] VALUES (6552,'س٦٣: سئل فضيلة الشيخ- رحمه الله تعالى-: بعض المخيمات في الحج تجمع من الحجاج الأموال لذبح الهدي فيؤكل البعض كاملا في المخيم، والبعض يتصدق به كاملا كما لو كان المجموع مئة من الغنم فيؤكل خمسين ويتصدق بخمسين؟','9930','6490','0');
INSERT INTO [t12293] VALUES (6553,'س٦٤: سئل فضيلة الشيخ-رحمه الله-: هل يجوز أن تقوم المرأة بذبح الأضحية؟','9931','6490','0');
INSERT INTO [t12293] VALUES (6554,'س٦٥: سئل فضيلة الشيخ- رحمه الله-: هل يجوز أن يذبح الإنسان الأضحية عن غيره؟','9932','6490','0');
INSERT INTO [t12293] VALUES (6555,'س٦٦: سئل فضيلة الشيخ- رحمه الله-: بعض الحجاج يدفع نقودا لبعض المؤسسات التي تتولى دفع هديه في أماكن المجاعة في شرق الأرض وغربها، فما حكم هذا العمل أثابكم الله؟','9932','6490','0');
INSERT INTO [t12293] VALUES (6556,'س٦٧: سئل فضيلة الشيخ- رحمه الله-: هل يجوز للشخص الحاج توكيل أهله في الأضحية؟','9933','6490','0');
INSERT INTO [t12293] VALUES (6557,'س٦٨: سئل فضيلة الشيخ- رحمه الله-: هل يجوز ذبح الهدي للحاج قبل يوم العيد؛ لأن الحجاج في حاجة حتى يتمكنوا من أعمال يوم العيد؟','9940','6490','0');
INSERT INTO [t12293] VALUES (6558,'س٦٩: سئل فضيلة الشيخ- رحمه الله-: من سافر ولم يترك أضحية عند أهله فهل يجوز له تأجيلها لليوم الثالث عشر أو يذبح عنهم في محل إقامته؟','9941','6490','0');
INSERT INTO [t12293] VALUES (6559,'س٧٠: سئل فضيلة الشيخ- رحمه الله-: هل يغرم الوكيل إذا ذبح الأضحية قبل صلاة العيد ناسيا أو جاهلا؟','9942','6490','0');
INSERT INTO [t12293] VALUES (6560,'س٧١: سئل فضيلة الشيخ- رحمه الله تعالى-: إذا نسي الوكيل','9942','6490','0');
INSERT INTO [t12293] VALUES (6561,'س٧٢: سئل فضيلة الشيخ- رحمه الله-: متى يبتدئ زمن ذبح هدي التمتع ومتى ينتهي؟ وهل هناك خلاف في تحديد الزمن؟','9943','6490','0');
INSERT INTO [t12293] VALUES (6562,'س٧٣: سئل فضيلة الشيخ- رحمه الله-: في يوم العيد تكثر اللحوم في منى فهل يجوز تأخير ذبح الهدي لليوم الثالث عشر؟','9943','6490','0');
INSERT INTO [t12293] VALUES (6563,'س٧٤: سئل فضيلة الشيخ- رحمه الله-: متى ينتهي وقت الأضحية؟','9944','6490','0');
INSERT INTO [t12293] VALUES (6564,'س٧٥: سئل فضيلة الشيخ- رحمه الله-: ما حكم ذبح الأضحية في مصلى العيد؟','9944','6490','0');
INSERT INTO [t12293] VALUES (6565,'س٧٦: سئل فضيلة الشيخ- رحمه الله-: بالنسبة للقارن هل يستطيع أن يشتري الهدي من مكة؟','9945','6490','0');
INSERT INTO [t12293] VALUES (6566,'س٧٧: سئل فضيلة الشيخ- رحمه الله-: إذا اشترى إنسان ذبيحتين يقصد إحداهما للأضحية والأخرى لحما فهل يشترط أن يعين التي سيضحي بها بعينها ولا يجوز له تبديلها بالأخرى؟','9945','6490','0');
INSERT INTO [t12293] VALUES (6567,'س٧٨: سئل فضيلة الشيخ- رحمه الله-: من اشترى الأضحية لتربيتها في الرعي، ثم مرضت أو انكسرت رجلها فهل يضحي بما؟','9949','6490','0');
INSERT INTO [t12293] VALUES (6568,'س٧٩: سئل فضيلة الشيخ- رحمه الله-: ما حكم تعليم الأضحية بالحناء وبالقلائد؟','9949','6490','0');
INSERT INTO [t12293] VALUES (6569,'س٨٠: سئل فضيلة الشيخ- رحمه الله-: هل يستحب للمتمتع بعد أن يحل ويشتري هديه أن يقلده ليعلم أنه هدي؟','9950','6490','0');
INSERT INTO [t12293] VALUES (6570,'س٨١: سئل فضيلة الشيخ- رحمه الله-: شخص نذر أن يذبح كبشا معينا فمات الكبش المعين دون تفريط منه فهل يلزمه إبداله؟','9950','6490','0');
INSERT INTO [t12293] VALUES (6571,'س٨٢: سئل فضيلة الشيخ- رحمه الله-: هل يلزم القارن أن يحمل الهدي معه من المكان الذي يحرم منه؟','9953','6490','0');
INSERT INTO [t12293] VALUES (6572,'س٨٣: سئل فضيلة الشيخ- رحمه الله-: عن رجل معه الهدي في سيارته فذهب لرمي جرة العقبة فلما رجع وجد هديه مذبوحا ومأخوذا إلا الرأس فاشترى هدئا آخر وذبحه، فما الحكم؟','9955','6490','0');
INSERT INTO [t12293] VALUES (6573,'س٨٤: سئل فضيلة الشيخ- رحمه الله-: هل يلزم في سوق الهدي أن يكون الإنسان قارنا؟ وهل يجوز للذي ساق الهدي أن يتمتع؟','9955','6490','0');
INSERT INTO [t12293] VALUES (6574,'س٨٥: سئل فضيلة الشيخ- رحمه الله-: إذا دفع الإنسان النقود للبنك الإسلامي لشراء الهدي وذبحه عنه فهل هذا من سوق الهدي، حيث إن الإنسان يدفع الدراهم وهو في بلده؟','9956','6490','0');
INSERT INTO [t12293] VALUES (6575,'س٨٦: سئل فضيلة الشيخ- رحمه الله-: حاج سرق منه مال الهدي فهل يقترض لشراء غيره؟','9959','6490','0');
INSERT INTO [t12293] VALUES (6576,'س٨٧: سئل فضيلة الشيخ- رحمه الله-: من اشترى هديه وضاع منه بمنى فما عليه؟','9959','6490','0');
INSERT INTO [t12293] VALUES (6577,'س٨٨: سئل فضيلة الشيخ-رحمه الله-: هل يشرع للفقير أن يستدين لكي يضحي؟','9959','6490','0');
INSERT INTO [t12293] VALUES (6578,'س٨٩: سئل فضيلة الشيخ- رحمه الله-: هل يجوز شراء الأضحية بالدين؟ وهل يعطى الجزار أجرة منها أو يهدى له منها؟','9959','6490','0');
INSERT INTO [t12293] VALUES (6579,'س٩٠: سئل فضيلة الشيخ- رحمه الله-: بالنسبة للمتمتع والقارن لهما هدي، فهل هذه تعتبر أضحية؟','9961','6490','0');
INSERT INTO [t12293] VALUES (6580,'س٩١: سئل فضيلة الشيخ- رحمه الله-: هل الأضحية مشروعة عن الأموات؟','9961','6490','0');
INSERT INTO [t12293] VALUES (6581,'س٩٢: سئل فضيلة الشيخ-رحمه الله-: هل تشرع الأضحية للحاج أم يكفي الهدي؟','9963','6490','0');
INSERT INTO [t12293] VALUES (6582,'س٩٣: سئل فضيلة الشيخ-رحمه الله-: هل يلزم الحاج المفرد أضحية في بلده؟','9963','6490','0');
INSERT INTO [t12293] VALUES (6583,'س٩٤: سئل فضيلة الشيخ- رحمه الله-: والدي متوفى ولم يوص بشيء، وله أولاد قصر، هل أضحي للأولاد القصر أم أضحي للوالد على حده؟','9968','6490','0');
INSERT INTO [t12293] VALUES (6584,'س٩٥: سئل فضيلة الشيخ- رحمه الله-: عن أم تقوم بذبح ضحايا متعددة لأبيها وأمها وغيرهما، مما يكلفها مبالغ كبيرة، فهل يصح فعلها؟','9969','6490','0');
INSERT INTO [t12293] VALUES (6585,'س٩٦: سئل فضيلة الشيخ- رحمه الله-: ما حكم الأضحية عن الميت؟ وهل نعيب على من يفعل ذلك؟','9972','6490','0');
INSERT INTO [t12293] VALUES (6586,'س٩٧: سئل فضيلة الشيخ- رحمه الله-: يقوم البعض بذبح الذبائح عند دخول شهر ذي الحجة، ويقولون: اللهم اجعلها','9972','6490','0');
INSERT INTO [t12293] VALUES (6587,'س٩٨: سئل فضيلة الشيخ- رحمه الله-: ما حكم الأضحية لمن كان عليه دين وهل لابد من استئذان صاحب الدين؟','9977','6490','0');
INSERT INTO [t12293] VALUES (6588,'س٩٩: سئل فضيلة الشيخ- رحمه الله-: شخص توفيت والدته وهو في سن أقل من السابعة وبلغ رشده الآن، يسأل هل يجوز أن يضحي عنها علما بأن والدته لم توصه بذلك؟ وإذا أوصته فما الحكم؟','9978','6490','0');
INSERT INTO [t12293] VALUES (6589,'س١٠٠: سئل فضيلة الشيخ- رحمه الله-: عن رجل يضحي في كل سنة عن والديه فما قولكم؟','9979','6490','0');
INSERT INTO [t12293] VALUES (6590,'س١٠١: سئل فضيلة الشيخ- رحمه الله-: هل تجزئ الأضحية عن الحي والميت إذا اشتركوا فيها؟','9980','6490','0');
INSERT INTO [t12293] VALUES (6591,'س١٠٢: سئل فضيلة الشيخ- رحمه الله-: يقول البعض بأن','9980','6490','0');
INSERT INTO [t12293] VALUES (6592,'س١٠٣: سئل فضيلة الشيخ- رحمه الله-: جاعة يضحون جميعا ثم جملون كامل أضاحيهم فهل يصح فعلهم؟','9982','6490','0');
INSERT INTO [t12293] VALUES (6593,'س١٠٤: سئل فضيلة الشيخ- رحمه الله-: من يقوم بطبخ كامل الأضاحي وجملها مع أقاربه بدون التصدق منها هل عملهم صحيح؟','9982','6490','0');
INSERT INTO [t12293] VALUES (6594,'س١٠٥: سئل فضيلة الشيخ-رحمه الله-: هل يجوز للمضحي أن يعطي الكافر من لحم أضحيته؟ وهل للمضحي أن يفطر من أضحيته؟','9983','6490','0');
INSERT INTO [t12293] VALUES (6595,'س١٠٦: سئل فضيلة الشيخ- رحمه الله-: بعض الحجاج يذبح هديه، ثم يتركه ولا يوزعه، فما حكم فعله؟','9984','6490','0');
INSERT INTO [t12293] VALUES (6596,'س١٠٧: سئل فضيلة الشيخ- رحمه الله-: ورد في مسند الإمام أحمد رحمه الله عن عبد الله بن عمر رضي الله عنهما أن النبي - صلى الله عليه وسلم - قال: " لا يدخر أحدكم من أضحيته فوق ثلاثة أيام" (٣) ، ما معنى هذا الحديث؟ وهل الآكل بعد ثلاثة أيام محرم أم لا؟','9984','6490','0');
INSERT INTO [t12293] VALUES (6597,'س١٠٨: سئل فضيلة الشيخ- رحمه الله-: ما الحكم فيمن ذبح الهدي ثم تركه، هل يجزئه ذلك أو لا؟','9985','6490','0');
INSERT INTO [t12293] VALUES (6598,'س١٠٩: سئل فضيلة الشيخ- رحمه الله-: ما حكم من ذبح هديه ثم ذهب، وتركه في المجزرة؟','9985','6490','0');
INSERT INTO [t12293] VALUES (6599,'س١١٠: سئل فضيلة الشيخ- رحمه الله-: يكون عمل بعض الناس في توزيع الأضاحي حسب المجاملات والمحاباة وإعطاء','9986','6490','0');
INSERT INTO [t12293] VALUES (6600,'س١١١: سئل فضيلة الشيخ- رحمه الله-: من فرق كامل الهدي على الفقراء ولم يهل منه، هل عليه شيء؟','9988','6490','0');
INSERT INTO [t12293] VALUES (6601,'س١١٢: سئل فضيلة الشيخ- رحمه الله-: عن قوله - صلى الله عليه وسلم -: (إذا دخلت العشر، وأراد أحدكم أن يضحي فليمسك عن شعره أظفاره - صلى الله عليه وسلم - هل هو صحيح؟ وهل النهي للتحريم؟ والحكمة منه؟ وهل يعم التحريم جميع أهل البيت؟ وهل يستوي في ذلك المقيم والحاج؟','9988','6490','0');
INSERT INTO [t12293] VALUES (6602,'س١١٣: سئل فضيلة الشيخ-رحمه الله-: هل من السنة ترك قص الشعر والأظافر في عشر ذي الحجة؟ وهل يشمل أسرة المضحي؟','9991','6490','0');
INSERT INTO [t12293] VALUES (6603,'س١١٤: سئل فضيلة الشيخ- رحمه الله-: ما الحكمة في عدم حلق الشعر وقص الأظفار للمضحي؟','9993','6490','0');
INSERT INTO [t12293] VALUES (6604,'س١١٥: سئل فضيلة الشيخ- رحمه الله-: إذا وكل من يريد أن يحج رجلا ليذبح أضحيته فعند الميقات أراد أن يقلم أظفاره ويقص شعره فهل يجوز ذلك؟','9993','6490','0');
INSERT INTO [t12293] VALUES (6605,'س١١٦: سئل فضيلة الشيخ- رحمه الله-: إذا كان الإنسان له أضحية عنه وأهل بيته فطرأ عليه الحج بعد دخول العشر، فهل يأخذ من شعره وأظفاره عند إحرامه وكيف يصنع بأضحيته؟ هل يذبحها في مكة أم يوكل عليها؟','9994','6490','0');
INSERT INTO [t12293] VALUES (6606,'س١١٧: سئل فضيلة الشيخ- رحمه الله-: هل يدخل أهل المضحي في النهي عن قص الشعر والأظافر، ومن تعمد القص هل تجزئ أضحيته؟','9995','6490','0');
INSERT INTO [t12293] VALUES (6607,'س١١٨: سئل فضيلة الشيخ- رحمه الله-: ما حكم مشط الشعر في شهر ذي الحجة قبل ذبح الأضحية؟','9996','6490','0');
INSERT INTO [t12293] VALUES (6608,'س١١٩: سئل فضيلة الشيخ-رحمه الله-: هل الزوجة تدخل في النهي عن قص الشعر أيام عشر ذي الحجة أم هو خاص بالزوج؟','9996','6490','0');
INSERT INTO [t12293] VALUES (6609,'س١٢٠: سئل فضيلة الشيخ-رحمه الله-: هل يجوز قص الأظفار وحلق الشعر في العشر من ذي الحجة؟','9997','6490','0');
INSERT INTO [t12293] VALUES (6610,'س١٢١: سئل فضيلة الشيخ- رحمه الله-: من يحلق لحيته هل تجزئ أضحيته؟','9998','6490','0');
INSERT INTO [t12293] VALUES (6611,'س١٢٢: سئل فضيلة الشيخ-رحمه الله-: كيف يعمل من أراد أن يضحي في بلده وهو حاج وما يترتب عليه؟','9999','6490','0');
INSERT INTO [t12293] VALUES (6612,'س١٢٣: سئل فضيلة الشيخ- رحمه الله-: من لم ينو الأضحية إلا بعد دخول العشر وقد أخذ من شعره فهل تجزئ أضحيته؟','10000','6490','0');
INSERT INTO [t12293] VALUES (6613,'س١٢٤: سئل فضيلة الشيخ-رحمه الله-: امرأة لا تستطيع ترك شعرها بدون تسريح فهل إذا تساقط شيء من شعرها يؤثر على الأضحية؟','10000','6490','0');
INSERT INTO [t12293] VALUES (6614,'س١٢٥: سئل فضيلة الشيخ- رحمه الله-: ما حكم من حلق يوم عيد الأضحى قبل ذهابه إلى الصلاة علما أنه نصح عن ذلك ولكنه أصر على الحلق قبل الصلاة؟','10001','6490','0');
INSERT INTO [t12293] VALUES (6615,'س١٢٦: سئل فضيلة الشيخ- رحمه الله-: هل يجوز للإنسان أن يمشط شعره في العشر من ذي الحجة؟ وأيهما أفضل في الأضحية الكبش أم البقرة؟','10002','6490','0');
INSERT INTO [t12293] VALUES (6616,'س١٢٧: سئل فضيلة الشيخ- رحمه الله-: إلى متى يمتد النهي عن الأخذ من الشعر والظفر والبشرة في عشر ذي الحجة؟','10002','6490','0');
INSERT INTO [t12293] VALUES (6617,'س١٢٨: سئل فضيلة الشيخ- رحمه الله-: الزوجة إذا أرادت أن تضحي ووكلت، فهل يحل لها تقليم الأظافر وقص الشعر؟','10002','6490','0');
INSERT INTO [t12293] VALUES (6618,'س١٢٩: سئل فضيلة الشيخ- رحمه الله-: إذا أخذ من أراد أن يضحي من شعره ناسيا أو جهلا، أو سقط الشعر بدون قصد فهل يمنعه ذلك من الأضحية؟','10003','6490','0');
INSERT INTO [t12293] VALUES (6619,'س١٣٠: سئل فضيلة الشيخ- رحمه الله-: المرأة إذا اغتسلت من الحيض أو النفاس لابد وأن يسقط شيء من شعرها، فما رأيكم لو','10003','6490','0');
INSERT INTO [t12293] VALUES (6620,'س١٣١: سئل فضيلة الشيخ- رحمه الله-: من يريد أن يحج ويضحي ويذبح الهدي بمكة وأهله يضحون له، هل يمكن أن يقص من شعره قبل أن يحرم؟','10004','6490','0');
INSERT INTO [t12293] VALUES (6621,'س١٣٢: سئل فضيلة الشيخ- رحمه الله-: هل يلزم الوكيل ما يلزم الموكل (صاحب الأضحية) من تجنب الأخذ من الشعر والظفر والبشرة؟','10005','6490','0');
INSERT INTO [t12293] VALUES (6622,'س١٣٣: سئل فضيلة الشيخ- رحمه الله-: عن الأخطاء التي يقع فيها الحجاج بالنسبة للهدي؟','10005','6490','0');
INSERT INTO [t12293] VALUES (6623,'العقيقة','10053','4559','0');
INSERT INTO [t12293] VALUES (6624,'س١٣٤: سئل فضيلة الشيخ- رحمه الله-: عن معنى العقيقة، وما السنة في توزيعها.','10054','6623','0');
INSERT INTO [t12293] VALUES (6625,'س١٣٥: سئل فضيلة الشيخ- رحمه الله-: ما معنى قوله - صلى الله عليه وسلم -: "كل غلام مرتهن بعقيقته؟','10056','6623','0');
INSERT INTO [t12293] VALUES (6626,'س١٣٦: سئل فضيلة الشيخ- رحمه الله-: عن العقيقة إذا ذبحها بمناسبة قدوم ضيف فهل تجزئ؟','10057','6623','0');
INSERT INTO [t12293] VALUES (6627,'س١٣٧: سئل فضيلة الشيخ- رحمه الله-: عن معنى العقيقة؟ وهل هي سنة مؤكدة أو مستحبة؟ ومن لم يفعلها هل هو آثم؟','10057','6623','0');
INSERT INTO [t12293] VALUES (6628,'س١٣٨: سئل فضيلة الشيخ- رحمه الله-: ما معنى عقيقة المولد؟ وهل هي فرض أم سنة؟','10058','6623','0');
INSERT INTO [t12293] VALUES (6629,'س١٣٩: سئل فضيلة الشيخ- رحمه الله-: عن رجل لم يذبح العقيقة لكونه لا يجد المال فهل عليه شيء؟','10059','6623','0');
INSERT INTO [t12293] VALUES (6630,'س١٤٠: سئل فضيلة الشيخ- رحمه الله-: الهدايا التي تقدم للأطفال حديثي الولادة هل للأم التصرف فيها بإهداء أو بيع أو نحوه؟','10060','6623','0');
INSERT INTO [t12293] VALUES (6631,'س١٤١: سئل فضيلة الشيخ- رحمه الله-: هل يستدين الإنسان لأجل العقيقة؟','10060','6623','0');
INSERT INTO [t12293] VALUES (6632,'س١٤٢: سئل فضيلة الشيخ- رحمه الله-: من لم يعق عن أحد من أولاده فماذا عليه الآن؟','10061','6623','0');
INSERT INTO [t12293] VALUES (6633,'س١٤٣: سئل فضيلة الشيخ- رحمه الله-: ما الحكمة في أن العقيقة تسن في السابع؟','10061','6623','0');
INSERT INTO [t12293] VALUES (6634,'س١٤٤: سئل فضيلة الشيخ- رحمه الله-: ما رأيك فيمن يؤخر العقيقة عن وقتها؟ وما معنى قول النبي ي: (كل غلام مرتهن بعقيقته) (١) ؟ حفظكم الله ورعاكم','10062','6623','0');
INSERT INTO [t12293] VALUES (6635,'س١٤٥: سئل فضيلة الشيخ- رحمه الله-: هل هناك زمن محدد لذبح العقيقة؟ ومتى يحلق شعر المولود؟ وهل هو خاص بالذكر؟','10064','6623','0');
INSERT INTO [t12293] VALUES (6636,'س١٤٦: سئل فضيلة الشيخ- رحمه الله-: هل تشرع العقيقة للميت؟','10064','6623','0');
INSERT INTO [t12293] VALUES (6637,'س١٤٧: سئل فضيلة الشيخ- رحمه الله-: رجل له بنات ولم يعق لهن، وهن الآن متزوجات فما على والدهن؟','10066','6623','0');
INSERT INTO [t12293] VALUES (6638,'س١٤٨: سئل فضيلة الشيخ- رحمه الله-: رجل أجل عقيقة ولده حتى ترجع زوجته إلى بيته بعد الولادة؟ لكي يجمع عليها الأقارب، فهل يجوز أم لا؟','10066','6623','0');
INSERT INTO [t12293] VALUES (6639,'س١٤٩: سئل فضيلة الشيخ- رحمه الله-: هل يجوز أن يشترك سبعة من الرجال في جل، ولكنها ليس في أضحية وإنما في عقيقة قياسا على الأضحية؟','10067','6623','0');
INSERT INTO [t12293] VALUES (6640,'س١٥٠: سئل فضيلة الشيخ- رحمه الله-: جماعة إذا ولد لهم مولود يوم الخميس قاموا بذبح خروف الخميس المقبل وتوزيعه لثلاثة أقسام، قسمان للأصهار وقسم للزوج، فهل يصح فعلهم، وإذا لم يعط الأصهار سبب شقاق ونزاع؟','10067','6623','0');
INSERT INTO [t12293] VALUES (6641,'س١٥١: سئل فضيلة الشيخ- رحمه الله- هل يجوز ذبح الماعز قي العقيقة أم لا يجوز؟ وهل يجوز ذبحها في العرس؟','10069','6623','0');
INSERT INTO [t12293] VALUES (6642,'س١٥٢: سئل فضيلة الشيخ- رحمه الله-: من لم يعق عن أولاده ثم توفي فهل تسقط العقيقة، أو يعق الأولاد عن أنفسهم؟','10070','6623','0');
INSERT INTO [t12293] VALUES (6643,'س١٥٣: سئل فضيلة الشيخ- رحمه الله-: من علم أن والده لم يعق عنه فهل يقوم بالعقيقة، ولو بلغ عمره أربعين سنة؟','10071','6623','0');
INSERT INTO [t12293] VALUES (6644,'س١٥٤: سئل فضيلة الشيخ- رحمه الله-: من لم يعق عن أولاده، وقد توفي بعضهم فماذا يلزمه؟','10071','6623','0');
INSERT INTO [t12293] VALUES (6645,'س١٥٥: سئل فضيلة الشيخ- رحمه الله-: هل الإنسان إذا لم يعق عنه وهو صغير هل تشرع له وهو كبير؟','10072','6623','0');
INSERT INTO [t12293] VALUES (6646,'س١٥٦: سئل فضيلة الشيخ- رحمه الله-: من له مجموعة من الأولاد، ولم يعق إلا عن واحد منهم فماذا يلزمه؟','10073','6623','0');
INSERT INTO [t12293] VALUES (6647,'س١٥٧: سئل فضيلة الشيخ- رحمه الله-: رجل لم يعق عن بناته حيث توفين وهن صغار فماذا يلزمه؟','10073','6623','0');
INSERT INTO [t12293] VALUES (6648,'س١٥٨: سئل فضيلة الشيخ- رحمه الله-: عن مولود توفي بعد الولادة مباشرة هل تجب له العقيقة؟','10074','6623','0');
INSERT INTO [t12293] VALUES (6649,'س١٥٩: سئل فضيلة الشيخ- رحمه الله-: السقط هل له عقيقة؟ وإذا مات بعد الولادة بيومين أو نفخت عليه شهر أو أكثر، فهل يعق عنه؟','10074','6623','0');
INSERT INTO [t12293] VALUES (6650,'س١٦٠: سئل فضيلة الشيخ رحمه الله-: الطفل الذي يولد ميتا، أو الذي لا يعيش إلا يومين هل يعق عنه؟ وهل يسمى؟','10075','6623','0');
INSERT INTO [t12293] VALUES (6651,'س١٦١: سئل فضيلة الشيخ- رحمه الله-: امرأة رزقت بستة أولاد ولم يعق زوجها إلا عن اثنين منهم، والباقي منهم ماتوا في أول أعمارهم، فهل تعق عنهم؟','10076','6623','0');
INSERT INTO [t12293] VALUES (6652,'س١٦٢: سئل فضيلة الشيخ- رحمه الله-: عن طفل مات قبل اليوم السابع بيوم واحد، ولم يسم ولم يعق عنه، ولم يصل عليه، وقد مضى عليه ثلاث سنوات فماذا يلزم والده الآن؟','10077','6623','0');
INSERT INTO [t12293] VALUES (6653,'س١٦٣: سئل فضيلة الشيخ- رحمه الله-: هل الولد الصغير الذي يسقط قبل أن يتم هل له عقيقة أم لا؟','10078','6623','0');
INSERT INTO [t12293] VALUES (6654,'س١٦٤: سئل فضيلة الشيخ- رحمه الله-: أم قد توفي بعض أطفالها قبل أن يعق عنهم فهل تلزمها العقيقة بعد وفاتهم؟','10078','6623','0');
INSERT INTO [t12293] VALUES (6655,'س١٦٥: سئل فضيلة الشيخ- رحمه الله-: عن سنة الرسول - صلى الله عليه وسلم - عند ولادة المولود؟','10081','6623','0');
INSERT INTO [t12293] VALUES (6656,'س١٦٦: سئل فضيلة الشيخ- رحمه الله-: ما السنة التي تفعل عندما يرزق المسلم بمولود؟','10082','6623','0');
INSERT INTO [t12293] VALUES (6657,'س١٦٧: سئل فضيلة الشيخ رحمه الله-: ما حكم الأذان في أذن المولود والإقامة، وهل التحنيك خاص بالرسول عليه الصلاة والسلام وما كيفيته؟','10085','6623','0');
INSERT INTO [t12293] VALUES (6658,'س١٦٨: سئل فضيلة الشيخ- رحمه الله-: بالنسبة للأذان في أذن المولود اليمنى والإقامة في اليسرى هل هو من السنة وله أصل؟','10086','6623','0');
INSERT INTO [t12293] VALUES (6659,'س١٦٩: سئل فضيلة الشيخ- رحمه الله-: هل من السنة أن الرجل إذا أراد أن يسمي المولود أن يأخذه إلى رجل صالح وتقي ليحنكه ويسميه؟','10087','6623','0');
INSERT INTO [t12293] VALUES (6660,'س١٧٠: سئل فضيلة الشيخ- رحمه الله-: من قال: إنه لا يجوز تسمية المولود إلا بعد أسبوع، فهل يصح قوله؟','10088','6623','0');
INSERT INTO [t12293] VALUES (6661,'س١٧١: سئل فضيلة الشيخ- رحمه الله-: هل الأذان في أذن المولود والإقامة في أذنه الأخرى سنة أم لا؟','10091','6623','0');
INSERT INTO [t12293] VALUES (6662,'س١٧٢: سئل فضيلة الشيخ- رحمه الله-: هل يصح الأذان في أذن المولود والإقامة في الأخرى؟ وما الحكم إذا لم يؤذن في أذنه عند ولادته؟','10091','6623','0');
INSERT INTO [t12293] VALUES (6663,'س١٧٣: سئل فضيلة الشيخ- رحمه الله-: هل ورد من أحكام المولود حلق شعره والتصدق بوزنه؟','10092','6623','0');
INSERT INTO [t12293] VALUES (6664,'س١٧٤: سئل فضيلة الشيخ- رحمه الله-: هل من السنة حلق شعر رأس المولود؟','10092','6623','0');
INSERT INTO [t12293] VALUES (6665,'س١٧٥: سئل فضيلة الشيخ- رحمه الله-: هل يسن حلق شعر البنت عند ولادتها من أجل جال الشعر ووفرته؟','10092','6623','0');
INSERT INTO [t12293] VALUES (6666,'س١٧٦: سئل فضيلة الشيخ- رحمه الله-: هل ورد بأنه يحلق شعر المولود في اليوم السابع، والتصدق بوزنه ذهبا؟','10093','6623','0');
