INSERT INTO [t12293] VALUES (6671,'س١٨١: سئل فضيلة الشيخ- رحمه الله-: عن شخص يلقب بأبي لهب؟','10097','6623','0');
INSERT INTO [t12293] VALUES (6672,'س١٨٢: سئل فضيلة الشيخ- رحمه الله-: ما حكم تسمية الأسر بمثل: الناصر، العلي، الماجد، الخالد، وهل يسمى بحجة الإسلام؟ وهل تسمى الأحياء بمثل الرحمانية؟','10098','6623','0');
