INSERT INTO [t12293] VALUES (6675,'س١٨٥: سئل فضيلة الشيخ- رحمه الله-: ما حكم تسمية البنت بمهاد؟','10100','6623','0');
INSERT INTO [t12293] VALUES (6676,'س١٨٦: سئل فضيلة الشيخ- رحمه الله-: ما حكم التسمي بهذه الأسماء شمس الدين، محيي الدين، قمر الدين وغير ذلك من الأسماء؟','10100','6623','0');
INSERT INTO [t12293] VALUES (6677,'س١٨٧: سئل فضيلة الشيخ رحمه الله-: ما خير الأسماء؟','10101','6623','0');
INSERT INTO [t12293] VALUES (6678,'س١٨٨: سئل فضيلة الشيخ- رحمه الله-: عن رجل عنده موظف اسمه عبد الرسول فقام بتعديل اسمه في سجلات','10101','6623','0');
INSERT INTO [t12293] VALUES (6679,'س١٨٩: سئل فضيلة الشيخ- رحمه الله-: عن التسمي بالإمام؟','10103','6623','0');
INSERT INTO [t12293] VALUES (6680,'س١٩٠: سئل فضيلة الشيخ- رحمه الله-: عن حكم التسمي بإيمان؟','10103','6623','0');
INSERT INTO [t12293] VALUES (6681,'س١٩١: سئل فضيلة الشيخ- رحمه الله-: عن التسمي بإيمان؟','10104','6623','0');
INSERT INTO [t12293] VALUES (6682,'س١٩٢: سئل فضيلة الشيخ- رحمه الله-: ما حكم هذه الألقاب "حجة الله" "حجة الإسلام" "آية الله"؟','10105','6623','0');
INSERT INTO [t12293] VALUES (6683,'س١٩٣: سئل فضيلة الشيخ- رحمه الله-: عن حكم التسمي بأسماء الله مثل كريم، وعزيز ونحوهما؟','10105','6623','0');
INSERT INTO [t12293] VALUES (6684,'س١٩٤: سئل فضيلة الشيخ- رحمه الله-: ما حكم التسمي بعزيز وشهيد؟','10107','6623','0');
INSERT INTO [t12293] VALUES (6685,'س١٩٥: سئل فضيلة الشيخ- رحمه الله-: عن حكم التسمي بأسماء الله تعالى مثل الرحيم والحكيم؟','10108','6623','0');
INSERT INTO [t12293] VALUES (6686,'س١٩٦: سئل فضيلة الشيخ- رحمه الله-: ما حكم التسمي','10109','6623','0');
INSERT INTO [t12293] VALUES (6687,'س١٩٧: سئل فضيلة الشيخ- رحمه الله-: عن لقب "شيخ الإسلام" هل يجوز؟','10110','6623','0');
INSERT INTO [t12293] VALUES (6688,'س١٩٨: وسئل فضيلة الشيخ- رحمه الله-: لماذا كان التسمي بعبد الحارث من الشرك مع أن الله هو الحارث؟','10111','6623','0');
INSERT INTO [t12293] VALUES (6689,'س١٩٩: سئل فضيلة الشيخ- رحمه الله-: عن حكم التسمي بقاضي القضاة؟','10112','6623','0');
INSERT INTO [t12293] VALUES (6690,'س٢٠٠: سئل فضيلة الشيخ- رحمه الله-: يكره بعض الناس اسم (علي) و (الحسين) ونحوه وينفر منها، وذلك لتعظيم الرافضة لتلك الأسماء، فما جوابكم حفظكم الله تعالى؟','10113','6623','0');
INSERT INTO [t12293] VALUES (6691,'س٢٠١: سئل فضيلة الشيخ- رحمه الله-: ما حكم التسمية بلينا أو رنا؟','10116','6623','0');
INSERT INTO [t12293] VALUES (6692,'س٢٠٢: سئل فضيلة الشيخ- رحمه الله-: عن رجل سمى ابنته (مارية) وقيل له: إن هذا الاسم مشتهر عند النصارى فهل يغير اسم ابنته؟','10117','6623','0');
INSERT INTO [t12293] VALUES (6693,'س٢٠٣: سئل فضيلة الشيخ- رحمه الله-: عن حكم التسمي بعبد المنان أو عبد المنتقم؟','10119','6623','0');
INSERT INTO [t12293] VALUES (6694,'س٢٠٤: سئل فضيلة الشيخ- رحمه الله-: عن التسمية بإسلام؟','10119','6623','0');
INSERT INTO [t12293] VALUES (6695,'س٢٠٥: سئل فضيلة الشيخ- رحمه الله-: امرأة اسمها موافق لإحدى أمهات المؤمنين -رضي الله عنهن- فيطلق عليها بعض الناس أم المؤمنين فما حكم ذلك؟ وهل يسمى بنوح؟','10120','6623','0');
INSERT INTO [t12293] VALUES (6696,'س٢٠٦: سئل فضيلة الشيخ- رحمه الله-: عن إطلاق بعض الأزواج على زوجاتهم وصف أم المؤمنين؟','10121','6623','0');
INSERT INTO [t12293] VALUES (6697,'س٢٠٧: سئل فضيلة الشيخ- رحمه الله-: ذكر بعض العلماء أن','10121','6623','0');
INSERT INTO [t12293] VALUES (6698,'س٢٠٨: سئل فضيلة الشيخ- رحمه الله-: عن التسمية بعبد النبي وعبد الرسول، مع أننا نعلم أن خير الأسماء ما عبد وحمد كما جاء في الحديث "أحب الأسماء الله ما عبد وحمد"؟','10123','6623','0');
INSERT INTO [t12293] VALUES (6699,'س٢٠٩: سئل فضيلة الشيخ- رحمه الله-: عن التسمية بمناف حيث إن رجلا سمى ابنه (مناف) وقيل له: إن هذا اسم إله في الجاهلية، فما رأيكم؟','10125','6623','0');
INSERT INTO [t12293] VALUES (6700,'س٢١٠: سئل فضيلة الشيخ- رحمه الله-: عن امرأة تريد أن تسمي بفريد وكريم؟','10127','6623','0');
INSERT INTO [t12293] VALUES (6701,'س٢١١: سئل فضيلة الشيخ- رحمه الله-: عن حكم التسمي بجورج ونحوه من الأسماء الغربية؟','10128','6623','0');
INSERT INTO [t12293] VALUES (6702,'س٢١٢: سئل فضيلة الشيخ- رحمه الله-: ما حكم تصغير الأسماء التي فيها تعبيد لله عز وجل مثل: عبود تصغير عبد الله؟','10129','6623','0');
INSERT INTO [t12293] VALUES (6703,'س٢١٣: سئل فضيلة الشيخ- رحمه الله-: ما حكم التسمي','10129','6623','0');
INSERT INTO [t12293] VALUES (6704,'س٢١٤: سئل فضيلة الشيخ- رحمه الله-: ما حكم التسمي باسم ملاك بالفتح، أو ملاك بالكسر للأنثى وما قولكم فيمن يقول: بأن هذا لا يجوز لأن هذه الكلمة مأخوذة من الملك وهم الملائكة ولا يجوز مشابهة اللائكة في ذلك؟ وهل ذلك من تسمية الملائكة تسمية الأنثى، كما ورد في القرآن وفقكم الله؟','10130','6623','0');
INSERT INTO [t12293] VALUES (6705,'س٢١٥: سئل فضيلة الشيخ- رحمه الله-: هل يلزم من يعتنق الإسلام أن يغير اسمه القديم إلى اسم إسلامي جديد؟','10131','6623','0');
INSERT INTO [t12293] VALUES (6706,'س٢١٦: سئل فضيلة الشيخ- رحمه الله-: عن مولود سماه أهله باسم مطابق لأخ له توفي قبله، وذكر بعضهم بأنه لا يجوز؛ لأنه يأخذ من أجره؟','10131','6623','0');
INSERT INTO [t12293] VALUES (6707,'س٢١٧: سئل فضيلة الشيخ- رحمه الله-: عدم تكسير عظام العقيقة هل هو مشروع؟','10132','6623','0');
INSERT INTO [t12293] VALUES (6708,'س٢١٨: سئل فضيلة الشيخ- رحمه الله-: العقيقة هل تأخذ حكم الأضحية بالنسبة للشروط؟','10133','6623','0');
INSERT INTO [t12293] VALUES (6709,'س٢١٩: سئل فضيلة الشيخ- رحمه الله-: ما هي أحكام العقيقة، تسميتها، وقتها، وهل يعطى الأغنياء منها؟ وهل يجوز إعطاء الكافر منها؟ وهل الأفضل توزيعها أو عمل وليمة؟','10133','6623','0');
INSERT INTO [t12293] VALUES (6710,'س٢٢٠: سئل فضيلة الشيخ- رحمه الله-: هل التسمية حق الزوج أو الزوجة؟','10134','6623','0');
INSERT INTO [t12293] VALUES (6711,'س٢٢١: سئل فضيلة الشيخ- رحمه الله-: عن تسمية الأولاد على الوالدين، خصوصا إذا كان الوالد يرى أن ذلك من البر وأنه','10135','6623','0');
INSERT INTO [t12293] VALUES (6712,'س ٢٢٢: سئل فضيلة الشيخ- رحمه الله-: عن حكم الجمع بين الأضحية والعقيقة إذا وافق يوم الأضحى أن يكون يوم السابع للمولود؟','10136','6623','0');
INSERT INTO [t12293] VALUES (6713,'س ٢٢٣: سئل فضيلة الشيخ- رحمه الله-: هل يجزئ سبع البقرة في عقيقة البنت قياسا على الأضحية؟','10137','6623','0');
INSERT INTO [t12293] VALUES (6714,'س ٢٢٤: سئل فضيلة الشيخ- رحمه الله-: هل يجوز لمن عليه فدية أن يذبحها في بلده إذا عاد إليه بعد الحج؛ ولتكون مناسبة لإظهار الفرح والسرور؟','10137','6623','0');
INSERT INTO [t12293] VALUES (6715,'س ٢٢٥: سئل فضيلة الشيخ- رحمه الله-: اعتاد بعض الناس إذا نزل منزلا جديدا أن يذبح شاة ويدعو من أحب فهل في ذلك شيء؟','10138','6623','0');
INSERT INTO [t12293] VALUES (6716,'س ٢٢٦: سئل فضيلة الشيخ- رحمه الله-: انتشر بين الناس أنه من نزل بيتا جديدا، أو اشترى سيارة جديدة أو توظف، أو ترقى في وظيفته أو نحو ذلك، فإنه يصنع وليمة ما حكم هذه الوليمة؟','10139','6623','0');
INSERT INTO [t12293] VALUES (6717,'س ٢٢٧: سئل فضيلة الشيخ- رحمه الله-: إذا نجح أحد الأبناء في الدراسة فهل يجوز لوالده أن يذبح ذبيحة احتفاء بنجاح الابن وشكرا لله عز وجل؟','10141','6623','0');
INSERT INTO [t12293] VALUES (6718,'كتاب الجهاد','10142','0','0');
INSERT INTO [t12293] VALUES (6719,'*','10143','6718','0');
INSERT INTO [t12293] VALUES (6720,'س ١: سئل فضيلة الشيخ- رحمه الله-: ما حكم الجهاد في سبيل الله وما أقسامه؟','10143','6719','0');
INSERT INTO [t12293] VALUES (6721,'س ٢: سئل فضيلة الشيخ- رحمه الله-: ما أقسام الجهاد عند الإطلاق؟','10146','6719','0');
INSERT INTO [t12293] VALUES (6722,'س ٣: سئل فضيلة الشيخ- رحمه الله-: هل الجهاد لإعلاء كلمة الله يدخل ضمن أقسام الجهاد في سبيل الله؟','10147','6719','0');
INSERT INTO [t12293] VALUES (6723,'س ٤: سئل فضيلة الشيخ- رحمه الله-: هل يعتبر التجنيد الإجباري داخلا في إعداد القوة للجهاد؟','10150','6719','0');
INSERT INTO [t12293] VALUES (6724,'س ٥: سئل فضيلة الشيخ- رحمه الله-: هل يجب الجهاد على النساء؟','10150','6719','0');
INSERT INTO [t12293] VALUES (6725,'س ٦: سئل فضيلة الشيخ- رحمه الله-: قلتم في الفتوى السابقة: إن الجهاد لا يجب على المرأة، ولكن قد تكون المرأة قائدة لطائرة','10150','6719','0');
INSERT INTO [t12293] VALUES (6726,'س ٧: سئل فضيلة الشيخ- رحمه الله-: هل يجوز للمرأة أن تخرج مع المجاهدين في المعركة؟','10151','6719','0');
INSERT INTO [t12293] VALUES (6727,'س ٨: سئل فضيلة الشيخ- رحمه الله-: هل طالب العلم يكون أعظم أجرا من الشهيد الذي قدم نفسه في سبيل الله عز وجل؟','10151','6719','0');
INSERT INTO [t12293] VALUES (6728,'س ٩: سئل فضيلة الشيخ- رحمه الله-: لماذا لا يقوم المسلمون بالجهاد ضد دول الكفر؟','10153','6719','0');
INSERT INTO [t12293] VALUES (6729,'س ١٠: سئل فضيلة الشيخ- رحمه الله-: ما حكم الدفاع عن الوطن؟','10156','6719','0');
INSERT INTO [t12293] VALUES (6730,'س ١١: سئل فضيلة الشيخ- رحمه الله-: بعض الشباب يرغب في الجهاد مع إخوانه في الكويت ضد عدوان العراق فهل يعد جهادا، وهل هذا الجهاد فرض عين أو فرض كفاية؟','10157','6719','0');
INSERT INTO [t12293] VALUES (6731,'س ١٢: سئل فضيلة الشيخ- رحمه الله-: نجد أن الله- عز وجل- في كثير من آيات الجهاد يقدم الجهاد بالمال على الجهاد بالنفس، فما الحكمة من ذلك؟','10158','6719','0');
INSERT INTO [t12293] VALUES (6732,'س ١٣: سئل فضيلة الشيخ- رحمه الله-: ما حكم الجهاد في هذا الزمان؟','10159','6719','0');
INSERT INTO [t12293] VALUES (6733,'س ١٤: سئل فضيلة الشيخ- رحمه الله-: هل يجوز الجهاد دون إذن إمام المسلمين؟ وهل هناك حالات يجوز فيها بدون إذن؟','10160','6719','0');
INSERT INTO [t12293] VALUES (6734,'س ١٥: سئل فضيلة الشيخ- رحمه الله- ما حكم الجهاد على المسلمين بعد سقوط الأندلس وكثير من الولايات الإسلامية، خاصة وقد أخذ اليهود في وقتنا الحاضر بيت المقدس الذي هو أول قبلة للمسلمين، ومن المساجد التي يشد إليها الرحال؟ وهل يسقط الجهاد عن المسلمين لعدم الاستطاعة؟ وإذا سقط الجهاد هل يتعين عليهم الإعداد؛ لأن ما لا يتم الواجب إلا به فهو واجب؟','10161','6719','0');
INSERT INTO [t12293] VALUES (6735,'س ١٦: سئل فضيلة الشيخ- رحمه الله-: ما حكم الجهاد في زماننا هذا؟ وهل هو فرض كفاية أم فرض عين؟ وإذا كان الجهاد فرض كفاية فمتى يكون فرض عين على هذه الأمة؟','10162','6719','0');
INSERT INTO [t12293] VALUES (6736,'س ١٧: سئل فضيلة الشيخ- رحمه الله-: أرجو أن توضح مدى حاجة المجتمع الإسلامي للجهاد في سبيل الله.','10163','6719','0');
INSERT INTO [t12293] VALUES (6737,'س ١٨: سئل فضيلة الشيخ- رحمه الله-: هل الإعداد للجهاد فرض عين أو فرض كفاية؟','10166','6719','0');
INSERT INTO [t12293] VALUES (6738,'س ١٩: سئل فضيلة الشيخ- رحمه الله-: هل الإنسان إذا توفرت له الفرصة للجهاد في بلد من البلدان، هل يصبح في حقه','10167','6719','0');
INSERT INTO [t12293] VALUES (6739,'س ٢٠: سئل فضيلة الشيخ- رحمه الله-: هل الجهاد ركن من أركان الإسلام؟','10169','6719','0');
INSERT INTO [t12293] VALUES (6740,'س ٢١: سئل فضيلة الشيخ- رحمه الله-: عن حكم الإعداد للجهاد في سبيل الله، ومن يخاطب به؟','10170','6719','0');
INSERT INTO [t12293] VALUES (6741,'س ٢٢: سئل فضيلة الشيخ- رحمه الله-: عن قتال الفلسطينيين لليهود هل هو جهاد شرعي؟','10170','6719','0');
INSERT INTO [t12293] VALUES (6742,'س ٢٣: سئل فضيلة الشيخ- رحمه الله- عن مسألتين الأولى: حكم الجهاد في أفغانستان هل هو فرض عين؟ وهل يلزم رضا الوالدين؟','10171','6719','0');
INSERT INTO [t12293] VALUES (6743,'س ٢٤: سئل فضيلة الشيخ- رحمه الله-: هل الجهاد في أفغانستان جهاد في سبيل الله بحق؟ فالبعض يطعن فيه، نرجو من فضيلتكم بيان ذلك بيانا وافيا؟','10174','6719','0');
INSERT INTO [t12293] VALUES (6744,'س ٢٥: سئل فضيلة الشيخ- رحمه الله-: راية الجهاد في كشمير هل هي راية صحيحة؟','10176','6719','0');
INSERT INTO [t12293] VALUES (6745,'س ٢٦: سئل فضيلة الشيخ- رحمه الله-: بلغنا أن لكم فتوى متداولة بين المجاهدين في كشمير، أي إنكم تنصحون بالجهاد في كشمير وأنها راية صحيحة؟','10177','6719','0');
INSERT INTO [t12293] VALUES (6746,'س ٢٧: سئل فضيلة الشيخ- رحمه الله-: سبق لك وأن أفتيت بالذهاب إلى بعض الأماكن كأفغانستان، ثم الآن لا تفتون بذلك، فلماذا؟','10177','6719','0');
INSERT INTO [t12293] VALUES (6747,'س ٢٨: سئل فضيلة الشيخ- رحمه الله-: عن واقع الجهاد في الشيشان؟','10178','6719','0');
INSERT INTO [t12293] VALUES (6748,'س ٢٩: سئل فضيلة الشيخ- رحمه الله-: ما حكم الذهاب للشيشان ويوغسلافيا للجهاد؟','10181','6719','0');
INSERT INTO [t12293] VALUES (6749,'س ٣٠: سئل فضيلة الشيخ- رحمه الله-: ما حكم من يذهب ليجاهد في أفغانستان بغير رضا والديه، حيث إن والدته متأثرة بدنيا ونفسيا. نرجو إسداء النصح له وجزاكم الله خيرا؟','10186','6719','0');
INSERT INTO [t12293] VALUES (6750,'س ٣١: سئل فضيلة الشيخ- رحمه الله-: يواجهنا عند الهجوم على مراكز العدو، أن بعض أفراد العدو يظهر لنا أنه مسلم إما بأن ينطق بالشهادتين، أو يقول: إنه يصلي أو غير ذلك من شعائر الإسلام، فهل يجوز لنا قتله، مع العلم أننا نتضرر بإبقائه بين أظهرنا على قيد الحياة كأن يغدر بنا؟ فما حكم قتله إذا كان مسلما ثم ارتد بقتاله مع الكفار، أو كان كافرا أو صليبيا؟','10189','6719','0');
INSERT INTO [t12293] VALUES (6751,'س ٣٢: سئل فضيلة الشيخ- رحمه الله-: والدي يحتاجني في عمله وأهلي كذلك، وأخي الأكبر في مدينة أخرى يطلب العلم، وأنا أريد أن أذهب إلى الجهاد، ولم يرض أحد من الوالدين، فهل يحق لي الذهاب مع العلم أن أخي يقدر أن يقوم مقامي بترك درا سته؟','10190','6719','0');
INSERT INTO [t12293] VALUES (6752,'س ٣٣: سئل فضيلة الشيخ- رحمه الله-: لي قريب ذهب إلى الجهاد في أرض أفغانستان من غير موافقة والده وبدون علمه، فما توجيهاتكم ونصيحتكم نحو الشباب الذين يفعلون ذلك علما بأن والد الشاب لا يزال غاضبا على ابنه؟','10192','6719','0');
INSERT INTO [t12293] VALUES (6753,'س ٣٤: سئل فضيلة الشيخ- رحمه الله-: أنا شاب أرغب الذهاب إلى الجهاد ولكن أبواي يمنعانني، فهل أذهب بدون أذنهما، علما بأنهما ليسا بحاجة لي، ولي عدد من الإخوان؟','10193','6719','0');
INSERT INTO [t12293] VALUES (6754,'س ٣٥: سئل فضيلة الشيخ- رحمه الله-: سمعنا من محدث أن أهل الأعراف هم أناس أو رجال خرجوا للجهاد في سبيل الله، ولم يستأذنوا أهلهم','10194','6719','0');
INSERT INTO [t12293] VALUES (6755,'س ٣٦: سئل فضيلة الشيخ- رحمه الله-: ذكرتم في أول إجابتكم أن الجهاد إذا كان تطوعا، فإنه يستلزم أن يستأذن والديه، وإذا كان واجبا لم يلزمه ذلك هل لنا أن نعرف الحالات التي يكون','10197','6719','0');
INSERT INTO [t12293] VALUES (6756,'س ٣٧: سئل فضيلة الشيخ- رحمه الله-: إنسان أجنبي في دولة إسلامية كان طالبا أو عاملا، ثم حصل استنفار في هذه الدولة للجهاد، فهل على هذا العامل أو الطالب الأجنبي أن يستنفر دون إذن والديه؟','10198','6719','0');
INSERT INTO [t12293] VALUES (6757,'س ٣٨: سئل فضيلة الشيخ- رحمه الله-: ما الحكم إذا أمر الأمير أن ينسحبوا من المعركة خشية الهلاك؛ لأن العدو باغتهم بأسلحة وعدد ورجال لا قبل لهم بها، وانسحبوا إلى أماكن أكثر تحصينا مثل أماكن الجبال لمقاتلة العدو فيها، هل يعتبر هذا فرارا من الزحف؟','10199','6719','0');
INSERT INTO [t12293] VALUES (6758,'س ٣٩: سئل فضيلة الشيخ- رحمه الله-: ما حكم اقتحام المجاهد بمفرده على العدو ومعه سلاحه بدون إذن أميره، وقد','10199','6719','0');
INSERT INTO [t12293] VALUES (6759,'س ٤٠: سئل فضيلة الشيخ- رحمه الله-: قام أحد الرجال في فلسطين بوضع متفجرات على نفسه، ودخل بين اليهود وقتل أكثر من عشرين يهوديا، وجرح قريبا من خمسين وبعضهم يعلل ويقول: إن لم يقتل اليوم قتل غدا، فهل هذا الفعل منه يعتبر انتحارا، أو يعتبر جهادا؟','10200','6719','0');
INSERT INTO [t12293] VALUES (6760,'س ٤١: سئل فضيلة الشيخ- رحمه الله-: ما رأي الشرع في الأعمال الانتحارية التي يؤمر بها الجندي في ميدان الحرب؟','10202','6719','0');
INSERT INTO [t12293] VALUES (6761,'س ٤٢: سئل فضيلة الشيخ- رحمه الله-: العمليات الانتحارية أفتى بعض العلماء بجوازها، فما رأي فضيلتكم؟','10203','6719','0');
INSERT INTO [t12293] VALUES (6762,'س ٤٣: سئل فضيلة الشيخ- رحمه الله-: استحل بعض الناس جواز قتل النفس، أو ما يسمونه بالعمليات الانتحارية بحديث ذكره مسلم في "صحيحه " في قصة الغلام (١) ، فهل استدلالهم هذا صحيح؟','10205','6719','0');
INSERT INTO [t12293] VALUES (6763,'س ٤٤: سئل فضيلة الشيخ- رحمه الله-: ما رأي سماحتكم في قول من قال عن العمليات الانتحارية ما يلي: لا فرق مؤثر في الحكم بين أن يقتحم المجاهد صف الأعداء بسلاحه وهو مقتول حتما لكن بسلاح العدو، وبين أن يقتحم صفهم بالمتفجرات فهو مقتول حتما، لكن مع الإثخان الشديد في صفوف العدو؟ والخلاصة أن العمليات هذه لا تعد انتحارا، لأن الانتحار هو إهلاك للنفس وهو محرم، أما هذه العمليات فهي إهلاك للنفس في سبيل الله وهو المرغب به؟','10209','6719','0');
INSERT INTO [t12293] VALUES (6764,'س ٤٥: سئل فضيلة الشيخ- رحمه الله-: ما رأي سماحتكم في الإضراب عن الطعام فكثيرا ما نسمع في الإذاعات ونقرأ في الصحف، أن أناسا يضربون عن الطعام احتجاجا على بعض الأحكام، وهؤلاء غالبا ما يكونون من المسجونين، فما حكم من توفى وهو مضرب عن الطعام؟','10211','6719','0');
INSERT INTO [t12293] VALUES (6765,'س ٤٦: سئل فضيلة الشيخ- رحمه الله-: هل يجوز قتل الكافر إذا اشتد القتال ونطق بالشهادتين؟','10212','6719','0');
INSERT INTO [t12293] VALUES (6766,'س ٤٧: سئل فضيلة الشيخ- رحمه الله-: أحيانا إذا اقتحم المجاهد العدو وبينه وبينه ألغام فربما يهلك أو ينجو ويصل إلى العدو. وأحيانا يأمر الأمير رجلا بالتقدم نحو العدو، وأمامه ألغام مزروعة، فربما يطأ على لغم فيقتل، ولكن يشق الطريق لبقية','10212','6719','0');
INSERT INTO [t12293] VALUES (6767,'س ٤٨: سئل فضيلة الشيخ- رحمه الله-: ما حكم القول بجواز الجهاد دون إذن من أحد لعدم وجود الإمام أو تخاذله أو تكاسله؟','10213','6719','0');
INSERT INTO [t12293] VALUES (6768,'س ٤٩: سئل فضيلة الشيخ- رحمه الله-: إمام عندنا خطب يوم الجمعة، فقال: لا يشترط في الجهاد الآن إذن الإمام لعدم وجود إمام عام للمسلمين، ومن قال غير هذا فقد جانب الصواب.','10214','6719','0');
INSERT INTO [t12293] VALUES (6769,'س ٥٠: سئل فضيلة الشيخ- رحمه الله-: هل تجوز الإغارة على العدو بدون سابق إنذار؟','10214','6719','0');
INSERT INTO [t12293] VALUES (6770,'س ٥١: سئل فضيلة الشيخ- رحمه الله-: ثبت في الحديث عنه - صلى الله عليه وسلم - أنه قال: "من مات وليس في عنقه بيعة لأحد، مات ميتة جاهلية ". (٢) ومعلوم أنه في أكثر بلاد المسلمين اليوم، لا يتحقق هذا الأمر، وأنه ليس في عنقهم بيعة لأسباب كثيرة منها: الاضطرابات السياسية، والانقلابات، وغيرها، فكيف يخرج المسلمون في تلك البلاد من هذا الإثم وهذا الوعيد؟ جزاكم الله خيرا.','10215','6719','0');
INSERT INTO [t12293] VALUES (6771,'س ٥٢: سئل فضيلة الشيخ- رحمه الله-: ما رأيكم فيمن يقول إن أحاديث السمع والطاعة لولاة الأمر تنصرف إلى القائد العام الذي يقود المسلمين جميعا؟','10219','6719','0');
INSERT INTO [t12293] VALUES (6772,'س ٥٣: سئل فضيلة الشيخ- رحمه الله-: هل يجوز وطئ المسبية قبل تقسيم الغنيمة؟ وهل إذا وطئت المسبية قبل التقسيم هل يعتبر واقعا في الزنا، وإن لم يكن كذلك، فما حكمه؟ وما الواجب على ولي الأمر تجاه من فعل ذلك؟','10221','6719','0');
INSERT INTO [t12293] VALUES (6773,'س ٥٤: سئل فضيلة الشيخ- رحمه الله-: ما حكم الغلول من الغنائم قبل التقسيم، ولو عرف الغال هل يخبر عنه أم لا؟ وإذا أخبر عنه ربما تحصل مفسدة بذلك؟','10221','6719','0');
INSERT INTO [t12293] VALUES (6774,'س ٥٥: سئل فضيلة الشيخ- رحمه الله-: ما الحكم إذا أخذ المجاهدون أخشاب البيوت المهجورة من السكان، والسكنى فيها، وقد يكون أهلها معروفين، ولكنهم هاجروا عنها بسبب الحروب علما بأن المجاهدين يحتاجون إلى ذلك؟','10222','6719','0');
INSERT INTO [t12293] VALUES (6775,'س ٥٦: سئل فضيلة الشيخ- رحمه الله-: نرجو الجواب بالتفصيل عن الغنائم وكيفية تقسيمها، مع الدليل إن أمكن.','10223','6719','0');
INSERT INTO [t12293] VALUES (6776,'س ٥٧: سئل فضيلة الشيخ- رحمه الله-: ما حكم أخذ الشظايا، وهى عبارة عن قطع حديد تكون غالبا صغيرة، وتوجد في أرض المعركة يجمعها المجاهدون والفقراء وغيرهم من عامة الناس، ويبيعونها في السوق، ما حكم أخذهم قطع الحديد وبيعها لأنفسهم؟ وما حكم جمعها، وأكل ثمنها لأصحاب المراكز التي رميت عليهم القذائف؟ علما أن هذه القطع لا تجمع مع الغنائم،','10223','6719','0');
INSERT INTO [t12293] VALUES (6777,'س ٥٨: سئل فضيلة الشيخ- رحمه الله-: أحيانا عندما نرجع من عملية اقتحام ويكون معنا أسرى العدو، لا نأمن من الطريق من وجود ألغام فنقدم الأسرى فربما يقتلون وينجو المجاهدون، فما حكم هذا العمل؟ وأحيانا نقدم المواشي في هذه الطرق فما الحكم؟','10224','6719','0');
INSERT INTO [t12293] VALUES (6778,'س ٥٩: سئل فضيلة الشيخ- رحمه الله-: المسبية، إذا كان معها أولادها وأسلمت، فما حكم أولادها بعد التقسيم؟','10225','6719','0');
INSERT INTO [t12293] VALUES (6779,'س ٦٠: سئل فضيلة الشيخ- رحمه الله-: هل كل ما يوجد على الكافر المقتول في أرض المعركة يجوز سلبه، أم من الأموال التي توجد عليه ما يرد إلى الغنائم؟','10225','6719','0');
INSERT INTO [t12293] VALUES (6780,'س ٦١: سئل فضيلة الشيخ- رحمه الله-: هل تعد مثلا الساعة والمحفظة التي فيها النقود من السلب؟','10225','6719','0');
INSERT INTO [t12293] VALUES (6781,'س ٦٢: سئل فضيلة الشيخ- رحمه الله-: ما هي مصارف الخمس في الغنيمة؟','10226','6719','0');
INSERT INTO [t12293] VALUES (6782,'س ٦٣: سئل فضيلة الشيخ- رحمه الله-: في زماننا الحالي كيف ينفق خمس الله تعالى ورسوله - صلى الله عليه وسلم -؟','10226','6719','0');
INSERT INTO [t12293] VALUES (6783,'س ٦٤: سئل فضيلة الشيخ- رحمه الله-: ما هي آداب الإسلام مع الأسير؟ نرجو الإجابة بالتفصيل مع الأدلة إن أمكن.','10226','6719','0');
INSERT INTO [t12293] VALUES (6784,'س ٦٥: سئل فضيلة الشيخ- رحمه الله-: عن المقاتلين من العدو هل يقتلون مطلقا أم يجوز أسرهم؟','10227','6719','0');
INSERT INTO [t12293] VALUES (6785,'س ٦٦: سئل فضيلة الشيخ- رحمه الله-: عمن يجوز سبيه من العدو، ومتى يكون المأسور رقيقا؟','10228','6719','0');
INSERT INTO [t12293] VALUES (6786,'س ٦٧: سئل فضيلة الشيخ- رحمه الله-: ماذا يفعل بالأسير من العدو؟','10230','6719','0');
INSERT INTO [t12293] VALUES (6787,'س ٦٨: سئل فضيلة الشيخ- رحمه الله-: ما حكم التمثيل بأسرى العدو، سواء قبل قتلهم أو بعده؟ نرجو الإجابة بالتفصيل','10231','6719','0');
INSERT INTO [t12293] VALUES (6788,'س ٦٩: سئل فضيلة الشيخ- رحمه الله-: قال الله تعالى: (وأعدوا لهم ما استطعتم من قوة ومن رباط الخيل) . (٣) إذا وجدت سلاحا ولم أتدرب عليه، هل أكون بذلك مخالفا لما أمر به سبحانه وتعالى في هذه الآية؟','10232','6719','0');
INSERT INTO [t12293] VALUES (6789,'س ٧٠: سئل فضيلة الشيخ- رحمه الله-: ما حكم الأكل من الطعام إذا دخلنا فاتحين مدينة من المدن؟','10233','6719','0');
INSERT INTO [t12293] VALUES (6790,'س ٧١: سئل فضيلة الشيخ- رحمه الله-: هل يجوز أن نقول فلان شهيد، وإذا كان لا يجوز، فماذا نقول؟','10233','6719','0');
INSERT INTO [t12293] VALUES (6791,'س ٧٢: سئل فضيلة الشيخ- رحمه الله-: إذا هدد السجين بأن تفعل الفاحشة معه أو مع أحد أقاربه، فهل يجوز له الانتحار، وهل يجوز له الإضراب عن الطعام إذا ضرب ضربا شديدا أو خشي إفشاء أسرار المسلمين، وهل يعد منتحرا إذا مات على هذه الحالة؟','10234','6719','0');
INSERT INTO [t12293] VALUES (6792,'س ٧٣: سئل فضيلة الشيخ- رحمه الله-: ما حكم قتل الجاسوس؟ وهل له توبة؟ وإذا تاب أيقتل أم لا؟ وإذا رأى الأمير المصلحة في عدم قتله، هل له أن يبقيه؟','10234','6719','0');
INSERT INTO [t12293] VALUES (6793,'س ٧٤: سئل فضيلة الشيخ- رحمه الله-: ما حكم معاقبة جميع المعسكر، إذا أخطأ واحد ولم يعرف؟','10236','6719','0');
INSERT INTO [t12293] VALUES (6794,'س ٧٥: سئل فضيلة الشيخ- رحمه الله-: ما حكم عمل غارة وهمية لأجل التدريب، ألا يدخل هذا في النهي عن إرهاب المسلم وحمل السلاح في وجهه؟','10236','6719','0');
INSERT INTO [t12293] VALUES (6795,'س ٧٦: سئل فضيلة الشيخ- رحمه الله-: بماذا تصير بلد الإسلام دار حرب، وهل الدول التي تحكم بالقانون الوضعي دار إسلام أم دار حرب، وما هو إظهار الدين في بلد الكفر؟','10237','6719','0');
INSERT INTO [t12293] VALUES (6796,'س ٧٧: سئل فضيلة الشيخ- رحمه الله-: ما حكم لبس الصليب في الحالات التالية (١) ؟','10238','6719','0');
INSERT INTO [t12293] VALUES (6797,'س ٧٨: سئل فضيلة الشيخ- رحمه الله-: هل يجوز معاقبة من أخطأ بالزحف على البطن؟','10239','6719','0');
INSERT INTO [t12293] VALUES (6798,'س ٧٩: سئل فضيلة الشيخ- رحمه الله-: ما حكم إخافة الجندي في التدريب أثناء الإعداد، ويعمل هذا على سبيل الإعداد؟ ومثال ذلك: أنه عندما يكون أحد الجنود في الحراسة في الليل يأتي بعض الجند فيأخذونه ويوثق على أساس أن الذي','10239','6719','0');
INSERT INTO [t12293] VALUES (6799,'س ٨٠: سئل فضيلة الشيخ- رحمه الله-: ما حكم تقليد الكفار في بعض نظام الجيش، كالمسير ولبس الخوذة والملابس الضيقة؟','10240','6719','0');
INSERT INTO [t12293] VALUES (6800,'س ٨١: سئل فضيلة الشيخ- رحمه الله-: إذا قتل الإنسان في المعركة، فهل يوصف بأنه شهيد؟','10241','6719','0');
INSERT INTO [t12293] VALUES (6801,'س ٨٢: سئل فضيلة الشيخ- رحمة الله-: هل تكفر الشهادة في سبيل الله الذنوب؟','10251','6719','0');
INSERT INTO [t12293] VALUES (6802,'س ٨٣: سئل فضيلة الشيخ- رحمه الله-: لا يخفى عليكم حادث التفجير الذي سبق وأنه وقع في العليا وحدث فيه أزهاق للأرواح من المعاهدين وغير ذلك، والذي حدث من أحداث الأسنان وسفهاء الأحلام، وأنكم تعلمون عظم هذا الفعل، وما فيه من مخالفة لأمر الله وأمر رسوله - صلى الله عليه وسلم -. وعدم الأخذ بالأدلة الشرعية، وتسفيه الآراء العلماء والراسخين في العلم، ومن مشاقة ومحاربة لولي الأمر، والآن وقد حدث تفجير جديد في الخبر. فهل من كلمة لتبيين دين الله تعالى في ذلك. والتحذير من هذا المنزلق الخطير الذي سلكه فئة من الشباب- وهم قلة ولله الحمد- وهو مستمد من فعل الخوارج، وهم قد لا يعلمون أن فعلهم فعل الخوارج، فهل من تبيين لدين الله سبحانه تعالى؟','10280','6719','0');
INSERT INTO [t12293] VALUES (6803,'س ٨٤: سئل فضيلة الشيخ رحمه الله: يذكر أن بعض القادة في المعارك الإسلامية كانوا أشاعرة، فهل يثنى عليهم؟','10284','6719','0');
INSERT INTO [t12293] VALUES (6804,'س ٨٥: سئل فضيلة الشيخ- رحمه الله-: عن معنى الحديث عن عائشة- رضي الله عنها- قالت: قال النبي - صلى الله عليه وسلم -: " لا هجرة بعد الفتح، ولكن جهاد، ونية وإذا استنفرتم فانفروا" (٢)','10285','6719','0');
INSERT INTO [t12293] VALUES (6805,'س ٨٦: سئل فضيلة الشيخ- رحمه الله-: هل الذي يخرج من البيت وهو ليس بمريض وبعد لحظات يحصل له حادث ويتوفى في حادث سيارة؛ هل يعتبر ذلك شهيدا؟ وهل هذا يعتبر مثل مرض الطاعون؛ لأن صاحب مرض الطاعون شهيد؟ أرجو الإفادة.','10286','6719','0');
INSERT INTO [t12293] VALUES (6806,'س ٨٧: سئل فضيلة الشيخ- رحمه الله-: هل من مات خارج بلاده يكون شهيدا؟ وهل يحاسب؟ وكيف يحاسب في القبر فقد سمعت بأن الشهيد لا يحاسب؟','10287','6719','0');
INSERT INTO [t12293] VALUES (6807,'س ٨٨: سئل فضيلة الشيخ- رحمه الله-: هل يدخل في الشهداء الغريق، والحريق، والمرأة التي ماتت في حالة الوضع؟ وما الدليل؟','10289','6719','0');
INSERT INTO [t12293] VALUES (6808,'س ٨٩: سئل فضيلة الشيخ- رحمه الله- قال - صلى الله عليه وسلم -: "يغفر الله','10291','6719','0');
INSERT INTO [t12293] VALUES (6809,'س ٩٠: سئل فضيلة الشيخ- رحمه الله-: إذا كان المسلم لا يؤدى فريضة الصلاة ولا الصوم وقتل في الجهاد. هل يموت شهيدا؟','10292','6719','0');
INSERT INTO [t12293] VALUES (6810,'س ٩١: سئل الشيخ- رحمه الله-: من مات من رجال الدفاع المدني، حيث احترق بالنار وهو يحاول إطفاءها هل يعتبر من الشهداء؟','10293','6719','0');
INSERT INTO [t12293] VALUES (6811,'س ٩٢: سئل فضيلة الشيخ- رحمه الله-: ما هي المواقف التي إذا مات فيها الشخص يكون شهيدا؟','10295','6719','0');
INSERT INTO [t12293] VALUES (6812,'س ٩٣: سئل فضيلة الشيخ- رحمه الله-: ما هو جزاء الشهيد ومكانته عند الله؟ وهل يغفر الله- عز وجل- الكبائر التي اقترفها ذلك الشهيد قبل أن يتوب منها؟ وهل يعتبر الشهيد من الستة الذين يظلهم الله في ظله؟','10296','6719','0');
INSERT INTO [t12293] VALUES (6813,'س ٩٤: سئل فضيلة الشيخ- رحمه الله-: لقد قرأت حديثا للصحابي الجليل أبى هريرة- رضي الله عنه- عن الرسول الكريم - صلى الله عليه وسلم - أنه قال: "ما تعدون الشهيد منكم" قالوا: يا رسول الله من قتل في سبيل الله فهو شهيد؟ قال: "إذا شهداء أمتي لقليل". قالوا: فمن هم يا رسول الله؟ قال: "من قتل في سبيل الله فهو شهيد، ومن مات في سبيل الله فهو شهيد، ومن مات في طاعون فهو شهيد، ومن مات بالطعن فهو شهيد، والغريق شهيد" (١) ٠ السؤال: هل من مات غريقا وهو سكران تكتب له الشهادة علما بأن الغريق يعد شهيدا حسب نص الحديث نرجو من فضيلتكم الإفادة؟','10297','6719','0');
INSERT INTO [t12293] VALUES (6814,'س ٩٥: سئل فضيلة الشيخ- رحمه الله-: ما جزاء من استشهد في سبيل الله، كمن قتل دفاعا عن عرضه؟','10302','6719','0');
INSERT INTO [t12293] VALUES (6815,'س ٩٦: سئل فضيلة الشيخ- رحمه الله-: ما جزاء الشهيد في الآخرة؟','10302','6719','0');
INSERT INTO [t12293] VALUES (6816,'س ٩٧: سئل فضيلة الشيخ- رحمه الله-: هل يصح أن يذهب الإنسان للجهاد وهو لم يحج بعد؟','10303','6719','0');
INSERT INTO [t12293] VALUES (6817,'س ٩٨: سئل فضيلة الشيخ- رحمه الله-: هذه الأحداث وغزو العراق للكويت لها أسباب لعل من أهمها كثرة المعاصي بجميع أنواعها، ولكن ألم يفتن المسلمون في عهد عثمان بن عفان رضي الله عنه فما الجواب عن ذلك؟','10303','6719','0');
INSERT INTO [t12293] VALUES (6818,'س ٩٩: سئل فضيلة الشيخ- رحمه الله-: يرفع بعض الجنود إصبعيه السبابة والوسطى على هيئة رقم سبعة رمزا للانتصار. ما حكم ذلك؟ أليس من الأولى أن نرفع السبابة إشارة للواحد القهار مع التكبير؟','10304','6719','0');
INSERT INTO [t12293] VALUES (6819,'س ١٠٠: سئل فضيلة الشيخ- رحمه الله-: مما لاشك فيه أنه منذ قتل عثمان- رضي الله عنه- الخليفة الثالث بعد الرسول - صلى الله عليه وسلم - وقعت حروب كثيرة في الدول الإسلامية، ثم اتسعت هذه الحروب وتعددت ألوانها وأشكالها بتعدد الممالك العربية والإسلامية، ولا شك أنه إذا قامت الحرب بين دولتين عربيتين','10304','6719','0');
INSERT INTO [t12293] VALUES (6820,'س ١٠١: سئل فضيلة الشيخ- رحمه الله-: ما ذنب الجندي في الطائفة الباغية إذا كان امتناعه عن الحرب يعتبر خروجا عن سلطانه؟','10306','6719','0');
INSERT INTO [t12293] VALUES (6821,'س ١٠٢: سئل فضيلة الشيخ- رحمه الله-: بعض الناس يقول ليت اليهود يقتلون جميع المسلمين ويظلوا وحدهم في فلسطين!','10307','6719','0');
INSERT INTO [t12293] VALUES (6822,'س ١٠٣: سئل فضيلة الشيخ- رحمه الله-: ما الأشهر الحرم؟ وهل يجوز القتال فيها؟','10308','6719','0');
INSERT INTO [t12293] VALUES (6823,'س ١٠٤: سئل فضيلة الشيخ- رحمه الله-: ما هو واجبنا تجاه إخواننا المسلمين في يوغسلافيا؟','10309','6719','0');
INSERT INTO [t12293] VALUES (6824,'س ١٠٥: سئل فضيلة الشيخ- رحمه الله-: قال رسول الله - صلى الله عليه وسلم -: " إذا التقى المسلمان بسيفيهما فالقاتل والمقتول في النار " (١) فما رأيكم بالحروب التي وقعت بعد وفاة الرسول - صلى الله عليه وسلم - والحروب التي وقعت في زماننا الحاضر؟','10310','6719','0');
INSERT INTO [t12293] VALUES (6825,'س ١٠٦: سئل فضيلة الشيخ- رحمه الله-: هل يلزم الكافر إذا أسلم في دار الكفر الانتقال إلى بلد الإسلام؟','10311','6719','0');
INSERT INTO [t12293] VALUES (6826,'باب عقد الذمة','10313','6718','0');
INSERT INTO [t12293] VALUES (6827,'س ١٠٧: سئل فضيلة الشيخ- رحمه الله-: ما حكم المعاهدة مع الكفار؟','10313','6826','0');
INSERT INTO [t12293] VALUES (6828,'س ١٠٨: سئل فضيلة الشيخ- رحمه الله-: هل أوجب الشارع كفارة للمعاهدة بين الآدميين وبين الإنسان؟ وما صيغة المعاهدة التي يلتزم بها الإمام؟','10314','6826','0');
INSERT INTO [t12293] VALUES (6829,'س ١٠٩: سئل فضيلة الشيخ- رحمه الله-: قال تعالى: (ما فرطنا في الكتاب من شيء) (١) . وقال تعالى: (ولو ردوه إلى الرسول وإلى أولي الأمر منهم لعلمه الذين يستنبطونه منهم) (٢) هناك شيء اشتهر واستفاض الآن وهو ما يسمى بالسلام والتطبيع والصلح والاعتراف باليهود فيريد الإنسان أن يعرف حكم ذلك؛ لأن الأحكام قد تتعارض ويريد الإنسان أن يكون على بينة وألا يعتقد بشيء يدين الله سبحانه وتعالى به، ولا يتكلم بشيء إلا وهو شيء صحيح سمعه من أولى العلم.','10315','6826','0');
INSERT INTO [t12293] VALUES (6830,'س ١١٠: سئل فضيلة الشيخ- رحمه الله-: عن الهدنة؟','10317','6826','0');
INSERT INTO [t12293] VALUES (6831,'س ١١١: سئل فضيلة الشيخ- رحمه الله-: هل للهدنة مع العدو مدة محددة؟','10319','6826','0');
INSERT INTO [t12293] VALUES (6832,'س ١١٢: سئل فضيلة الشيخ- رحمه الله-: ما حكم الهدنة مع الكفار مطلقا؟','10319','6826','0');
INSERT INTO [t12293] VALUES (6833,'س ١١٣: سئل فضيلة الشيخ- رحمه الله-: عن الجزية.','10320','6826','0');
INSERT INTO [t12293] VALUES (6834,'س ١١٤: سئل فضيلة الشيخ- رحمه الله-: لا يخفى على فضيلتكم أن في أخذ الجزية إقرار لليهودي والنصراني على دينه مع بقائه في جزيرة العرب، فكيف يجمع بين هذا وبين أمر النبي - صلى الله عليه وسلم - بإخراج اليهود والنصارى من جزيرة العرب؟','10321','6826','0');
INSERT INTO [t12293] VALUES (6835,'س ١١٥: سئل فضيلة الشيخ- رحمه الله-: عن مقدار مال الجزية؟','10322','6826','0');
INSERT INTO [t12293] VALUES (6836,'س ١١٦: سئل فضيلة الشيخ- رحمه الله-: ما الحكمة في أن النبي - صلى الله عليه وسلم - أخذ الجزية من مجوس هجر، ولم يأخذها من بقية المشركين؟','10323','6826','0');
INSERT INTO [t12293] VALUES (6837,'س ١١٧: سئل فضيلة الشيخ- رحمه الله-: لماذا عامل المسلمون المجوس معاملة أهل الكتاب مع أنهم وثنيون؟','10324','6826','0');
INSERT INTO [t12293] VALUES (6838,'س ١١٨: سئل فضيلة الشيخ- رحمه الله-: ما حقوق الوالدين الكافرين على الأبناء المسلمين، وكذلك الأشقاء والأقارب من حيث الزيارات والنفقة والصلة؟ ومتى تكون النفقة واجبة؟ ومتى تكون مستحبة؟','10324','6826','0');
INSERT INTO [t12293] VALUES (6839,'س ١١٩: سئل فضيلة الشيخ- رحمه الله-: ما حكم من يلقى عليه السلام في مجلس أو جماعة أو محلات تجارية ويرد السلام دون التأكد من ديانة الذي ألقى عليه السلام وبالعكس؟ ما حكم من يلقي السلام على هذه الجماعات المختلفة الأديان من غير المسلمين؟ وما حكم هذه الألفاظ (مع السلامة) (تصبحون على خير) أو (تمسون على خير) (ودعتكم الله) أو (في أمان الله) ؟','10325','6826','0');
INSERT INTO [t12293] VALUES (6840,'س ١٢٠: سئل فضيلة الشيخ- رحمه الله-: هل يجوز للحاكم المسلم أن يسوى بين المسلم والكتابي يهوديا كان أو نصرانيا دون أن يأخذ منهم الجزية وكيف تكون معاملتنا معهم؟','10327','6826','0');
INSERT INTO [t12293] VALUES (6841,'س ١٢١: سئل فضيلة الشيخ- رحمه الله-: هل يجوز أكل أموال غير المسلمين أم هي حرام محرمة كحرمة أموال المسلمين؟','10328','6826','0');
INSERT INTO [t12293] VALUES (6842,'س ١٢٢: سئل فضيلة الشيخ- رحمه الله-: أنا موظف في شركة ويرأسني شخص كافر وحين أذهب إلى العمل يكون قد وصل إلى العمل قبلي، فهل لي أن أسلم عليه وأبدأه بالسلام علما بأني إذا لم أسلم عليه قد يحصل لي ضرر؟','10329','6826','0');
INSERT INTO [t12293] VALUES (6843,'س ١٢٣: سئل فضيلة الشيخ- رحمه الله-: اعتاد الناس أنهم يقولون لصاحب المحلات من العمالة الوافدة يا محمد، أعطني','10329','6826','0');
INSERT INTO [t12293] VALUES (6844,'س ١٢٤: سئل فضيلة الشيخ- رحمه الله-: رجل متزوج ويعمل خارج بلده مع مجموعة من الأجانب النصارى ويكون بينه وبينهم الأكل والشرب هل يجوز معاملة هؤلاء؟','10330','6826','0');
INSERT INTO [t12293] VALUES (6845,'س ١٢٥: سئل فضيلة الشيخ- رحمه الله-: ما حكم مصافحة الكافر إذا كان هو البادئ بالمصافحة؟','10331','6826','0');
INSERT INTO [t12293] VALUES (6846,'س ١٢٦: سئل فضيلة الشيخ- رحمه الله-: عندنا في المسجد برادة للماء للشرب ويأتي عمال كفار ويأخذون من هذا الماء، فهل يحق لنا أن نمنعهم؟ وأيضا دورة المياه يأتون يستحمون يوميا في هذه الدورات هل لنا أن نمنع هؤلاء الكفار؟','10332','6826','0');
INSERT INTO [t12293] VALUES (6847,'س ١٢٧: سئل فضيلة الشيخ- رحمه الله-: نعمل في شركة وفيها نسبة من الكفار فهل يجوز الدعاء على أعيانهم؟','10333','6826','0');
INSERT INTO [t12293] VALUES (6848,'س ١٢٨: سئل فضيلة الشيخ- رحمه الله-: ما حكم الدعاء على هؤلاء الكفار الذين يعملون معنا؟','10334','6826','0');
INSERT INTO [t12293] VALUES (6849,'س ١٢٩: سئل فضيلة الشيخ- رحمه الله-: شخص مسلم تداين من شخص كافر، وأكل حقه، فهل يصح للمسلم أكل مال كافر بغير حق أفيدونا في ذلك؟','10334','6826','0');
INSERT INTO [t12293] VALUES (6850,'س ١٣٠: سئل فضيلة الشيخ- رحمه الله-: في هذه الأيام ونتيجة للاحتكاك مع الغرب والشرق وغالبهم من الكفار على اختلاف مللهم ونحلهم. أراهم يؤدون تحية الإسلام علينا حينما نتقابل معهم، فماذا يجب علينا تجاههم؟','10335','6826','0');
INSERT INTO [t12293] VALUES (6851,'س ١٣١: سئل فضيلة الشيخ- رحمه الله-: البعض يتأول في مسألة أهل الذمة بدعوى عدم وجود ولي الأمر العام أو الخلافة،','10336','6826','0');
INSERT INTO [t12293] VALUES (6852,'س ١٣٢: سئل فضيلة الشيخ- رحمه الله-: من يتجاوز هذه الضوابط يسمى مفتئتا عند أهل العلم، لكن وجد في الحقيقة ممن','10337','6826','0');
INSERT INTO [t12293] VALUES (6853,'س ١٣٣: سئل فضيلة الشيخ- رحمه الله-: كيف نخرج من القول الأول وهو النهى عن الاعتداء على الكافر المعاهد، ولو حدث وقتل كيف نجمع بينه وبين منع قود المسلم بكافر؟','10338','6826','0');
INSERT INTO [t12293] VALUES (6854,'س ١٣٤: سئل فضيلة الشيخ- رحمه الله-: العلاقة الطيبة بين المبتعث وأستاذه (مشرفه الدراسي) ضرورة تقتضيها عدة أمور من بينها مصلحة الطالب نفسه، غير أن هناك بعضا من المبتعثين يجدون حرجا في تقوية هذه العلاقة، وخاصة فيما يتعلق بالمناسبات الدينية كبعث بطاقات التهنئة بما يسمى الكريسمس أو عيد رأس السنة، فهل ترون غضاضة في أن يكتب الطالب تهنئة لأستاذه خاصة إذا كانت من باب المجاملة المطلوبة في مثل هذا المجتمع؟','10339','6826','0');
INSERT INTO [t12293] VALUES (6855,'س ١٣٥: سئل فضيلة الشيخ- رحمه الله-: كيف نعامل النصارى المشاركين لنا في العمل والمجاورين لنا في المنازل؟','10340','6826','0');
INSERT INTO [t12293] VALUES (6856,'كتاب العلم','10406','0','0');
INSERT INTO [t12293] VALUES (6857,'تعريف العلم','10407','6856','0');
INSERT INTO [t12293] VALUES (6858,'فضائل العلم','10412','6856','0');
INSERT INTO [t12293] VALUES (6859,'س ١: سئل فضيلة الشيخ- رحمه الله-: ما المقصود بقوله تعالى: (إنما يخشى الله من عباده العلماء) (١) ؟','10421','6858','0');
INSERT INTO [t12293] VALUES (6860,'س ٢: سئل فضيلة الشيخ- رحمه الله-: يقول الله تبارك وتعالى (إنما يخشى الله من عباده العلماء) (١) فكيف يعرف العالم؟','10422','6858','0');
INSERT INTO [t12293] VALUES (6861,'س ٣: سئل فضيلة الشيخ- رحمه الله-: ما المقصود بالعلماء في قوله تعالى (إنما يخشى الله من عباده العلماء) (٣) ؟','10422','6858','0');
INSERT INTO [t12293] VALUES (6862,'س ٤: سئل فضيلة الشيخ- رحمه الله-: يوجد من شباب اليوم من صرف اهتمامه في مذاكرة الحديث والتفسير والتوحيد والفقه فقط، مع إهمالهم لمواد أخرى مثل الرياضيات والعلوم، ونرى من هؤلاء من يتشددون في الدين تشددا عظيما، فما نصيحتكم؟','10425','6858','0');
INSERT INTO [t12293] VALUES (6863,'س ٥: سئل فضيلة الشيخ- رحمه الله-: عن أهمية العلم الشرعي بالنسبة لطالب العلم، وما هي الطريقة المثلى لطالب العلم','10427','6858','0');
INSERT INTO [t12293] VALUES (6864,'س ٦: سئل فضيلة الشيخ- رحمه الله-: جاء في الحديث " إذا مات ابن آدم انقطع عمله إلا من ثلاث: صدقة جارية، أو علم ينتفع به، أو ولد صالح يدعو له " (١) . فهل يدخل في ذلك العلم علوم الدنيا من الفيزياء والكيمياء أم هو مقيد بالشرعي؟','10436','6858','0');
INSERT INTO [t12293] VALUES (6865,'س ٧: سئل فضيلة الشيخ- رحمه الله-: ما مكانة وفضل أهل العلم في الإسلام؟','10436','6858','0');
INSERT INTO [t12293] VALUES (6866,'حكم طلب العلم','10438','6856','0');
INSERT INTO [t12293] VALUES (6867,'س ٨: سئل فضيلة الشيخ- رحمه الله-: أسكن مع والدي وأعمل في مدينة مجاورة، وأفكر في السكن قرب عملي لأجل أن أحضر حلقات العلم في ذلك البلد، أرجو توجيه النصح لي وجزاكم الله خيرا.','10439','6866','0');
INSERT INTO [t12293] VALUES (6868,'س ٩: سئل فضيلة الشيخ- رحمه الله-: أيهما أفضل الدراسة لكي ينال الشخص الشهادة أم التعليم الديني وحفظ القرآن ودروس','10439','6866','0');
INSERT INTO [t12293] VALUES (6869,'س ١٠: سئل فضيلة الشيخ- رحمه الله-: ما الأمور الشرعية التي يجب على المؤمن أن يتعلمها؟','10440','6866','0');
INSERT INTO [t12293] VALUES (6870,'س ١١: سئل فضيلة الشيخ- رحمه الله-: تعلم العلم الشرعي، هل يقتصر على المواد الشرعية فقط أم يدخل في ذلك المواد الأخرى؟','10442','6866','0');
INSERT INTO [t12293] VALUES (6871,'س ١٢: سئل فضيلة الشيخ- رحمه الله-: عن رأيه في تعليم البنات؟','10442','6866','0');
INSERT INTO [t12293] VALUES (6872,'س ١٣: سئل فضيلة الشيخ- رحمه الله-: هل العلوم كالطب والهندسة من التفقه في دين الله؟','10443','6866','0');
INSERT INTO [t12293] VALUES (6873,'س ١٤: سئل فضيلة الشيخ- رحمه الله-: أيهما أفضل قيام الليل، أم طلب العلم؟','10444','6866','0');
INSERT INTO [t12293] VALUES (6874,'س ١٥: سئل فضيلة الشيخ- رحمه الله-: ما الواجب على العامي ومن ليس له قدرة على طلب العلم؟','10445','6866','0');
INSERT INTO [t12293] VALUES (6875,'س ١٦: سئل فضيلة الشيخ- رحمه الله-: عن حكم تعلم اللغة الإنجليزية في الوقت الحاضر؟','10446','6866','0');
INSERT INTO [t12293] VALUES (6876,'س ١٧: سئل فضيلة الشيخ- رحمه الله-: إذا كانت الأمة أحوج إلى العلوم المادية كالطب والهندسة وغيرها، فهل الأفضل للإنسان أن يتخصص في العلوم المادية أم العلوم الشرعية؟','10447','6866','0');
INSERT INTO [t12293] VALUES (6877,'س ١٨: سئل فضيلة الشيخ- رحمه الله-: أنا طالب علم وأهلي لهم ظروف مادية، فقال لي والدي: اعمل علينا أفضل لك من طلب العلم، فهل أترك دراستي للعمل؟ وهل العمل على الأهل أفضل أم لا؟','10448','6866','0');
INSERT INTO [t12293] VALUES (6878,'س ١٩: سئل فضيلة الشيخ- رحمه الله-: أنا طالب في الجامعة وكل دراستي نظريات غربية تنافي تعاليم الشرع، فما رأيكم إذا علمت أنني أنوي نقد مثل هذه النظريات ونفع الأمة الإسلامية في دراستي الحالية وبعد تخرجي؟','10449','6866','0');
INSERT INTO [t12293] VALUES (6879,'س ٢٠: سئل فضيلة الشيخ- رحمه الله-: أنا متخصص في علم الكيمياء وأتابع البحوث والدراسات التي تصدر في هذا المجال لكي أستفيد وأفيد من ذلك في أي مجال أعمل به سواء مدرسة أو مصنعا، مع العلم بأن ذلك يشغلني عن طلب العلم الشرعي، فكيف أوفق بينهما؟','10450','6866','0');
INSERT INTO [t12293] VALUES (6880,'س ٢١: سئل فضيلة الشيخ- رحمه الله-: هل يعذر الشخص في عدم طلبه للعلم بسبب انشغاله بدراسته التي ليس بها طلب للعلم الشرعي، أو بسبب عمله أو غير ذلك؟','10451','6866','0');
INSERT INTO [t12293] VALUES (6881,'س ٢٢: سئل فضيلة الشيخ- رحمه الله-: هل تعليم الطالب الرياضيات إذا كان الشخص ينوي بها وجه الله له أجر أم لا؟','10451','6866','0');
INSERT INTO [t12293] VALUES (6882,'س ٢٣: سئل فضيلة الشيخ- رحمه الله-: عن طالب علم يريد أن يذهب مع إخوانه في الله لطلب العلم وكان الحائل بينه وبين الذهاب معهم هو أهله، والده وأمه، فما الحكم في خروج هذا الطالب؟','10452','6866','0');
INSERT INTO [t12293] VALUES (6883,'س ٢٤: سئل فضيلة الشيخ- رحمه الله-: هل يجوز للمرء أن يترك عمله ويتفرغ لطلب العلم، فيكون عالة على أبيه وأخيه؟','10453','6866','0');
INSERT INTO [t12293] VALUES (6884,'آداب طالب العلم','10456','6856','0');
INSERT INTO [t12293] VALUES (6885,'س ٢٥: سئل فضيلة الشيخ- رحمه الله-: هل صحيح أن للعلم زكاة، وهي بذله للناس وتعليمهم إياه؟','10491','6884','0');
INSERT INTO [t12293] VALUES (6886,'س ٢٦: سئل فضيلة الشيخ- رحمه الله-: بعض الناس يكتبون حرف (ص) بين قوسين، ويقصدون به رمز لجملة - صلى الله عليه وسلم - فهل يصح استعمال حرف (ص) رمزا لكلمة (- صلى الله عليه وسلم -)','10492','6884','0');
INSERT INTO [t12293] VALUES (6887,'س ٢٧: سئل فضيلة الشيخ- رحمه الله-: يتحرج بعض طلبة العلم الشرعي عند قصدهم العلم والشهادة، فكيف يتخلص','10492','6884','0');
INSERT INTO [t12293] VALUES (6888,'س ٢٨: سئل فضيلة الشيخ- رحمه الله-: بم يكون الإخلاص في طلب العلم؟','10494','6884','0');
INSERT INTO [t12293] VALUES (6889,'س ٢٩: سئل فضيلة الشيخ- رحمه الله-: يقول بعض الناس: إن إخلاص النية في عصرنا الحاضر صعب، أو قد يكون مستحيلا؛ لأن الذين يطلبون العلم ولاسيما الطلب النظامي يطلبون العلم لنيل الشهادة فحسب؟','10495','6884','0');
INSERT INTO [t12293] VALUES (6890,'س ٣٠: سئل فضيلة الشيخ- رحمه الله-: ما نصيحة فضيلتكم حول العمل بالعلم؟','10496','6884','0');
INSERT INTO [t12293] VALUES (6891,'س ٣١: سئل فضيلة الشيخ- رحمه الله-: أنا طالب أحب أن آخذ درجات عالية ومعدلا ممتازا، وأنا مع ذلك نيتي طيبة، فما رأيك في الفرح بالدرجات العالية والغضب من الدرجات الضعيفة؟ هل هذا خدش للإخلاص؟','10497','6884','0');
INSERT INTO [t12293] VALUES (6892,'س ٣٢: سئل فضيلة الشيخ- رحمه الله-: يلاحظ على بعض طلبة العلم قلة العبادة والذكر فما نصيحتكم؟','10498','6884','0');
INSERT INTO [t12293] VALUES (6893,'س ٣٣: سئل فضيلة الشيخ- رحمه الله-: يلاحظ التقصير في العمل بالعلم والدعوة، فما نصيحة فضيلتكم؟','10500','6884','0');
INSERT INTO [t12293] VALUES (6894,'س ٣٤: سئل فضيلة الشيخ- رحمه الله-: ما نصيحة فضيلتكم لطلبة العلم في محبة الخير لغيرهم؟','10502','6884','0');
INSERT INTO [t12293] VALUES (6895,'الأسباب المعينة على طلب العلم','10505','6856','0');
INSERT INTO [t12293] VALUES (6896,'س ٣٥: سئل فضيلة الشيخ- رحمه الله-: ما هي الأمور المعينة على طلب العلم؟','10515','6895','0');
INSERT INTO [t12293] VALUES (6897,'س ٣٦: سئل فضيلة الشيخ- رحمه الله-: يلاحظ ضعف الهمة والفتور في طلب العلم، فما الوسائل والطرق التي تدفع إلى علو الهمة والحرص على العلم؟','10515','6895','0');
INSERT INTO [t12293] VALUES (6898,'س ٣٧: سئل فضيلة الشيخ- رحمه الله-: ما توجيهكم حول استغلال الوقت وحفظه من الضياع؟','10517','6895','0');
INSERT INTO [t12293] VALUES (6899,'س ٣٨: سئل فضيلة الشيخ- رحمه الله-: كيف نرد على من قال: إن العلماء السابقين لم تكن لديهم المشاغل التي تؤثر على حفظهم كما هو حاصل لعلماء هذا الزمان، ومنهم من يكون ليس لديه إلا التفرغ لطلب العلم وحفظه والجلوس بلا مشاغل، أما الآن فكثرت المشاغل الدنيوية التي تأخذ كل الوقت، والإنسان قد لا يستطيع الاستغناء عن هذه المشاغل؟','10518','6895','0');
INSERT INTO [t12293] VALUES (6900,'س ٣٩: سئل فضيلة الشيخ- رحمه الله-: إذا كان آفة العلم النسيان فما الأمور أو الطرق التي تعين على ضبط وحفظ العلم؟','10519','6895','0');
INSERT INTO [t12293] VALUES (6901,'س ٤٠: سئل فضيلة الشيخ- رحمه الله-: ما توجيهكم- حفظكم الله- لطلاب العلم حيث يلاحظ الإهمال وعدم الجد مما له آثار سيئة في التحصيل العلمي؟','10520','6895','0');
INSERT INTO [t12293] VALUES (6902,'س ٤١: سئل فضيلة الشيخ- رحمه الله-: ما نصيحتكم لمن ينسى ما يقرأ ويتعلم؟','10521','6895','0');
INSERT INTO [t12293] VALUES (6903,'طرق تحصيل العلم','10523','6856','0');
INSERT INTO [t12293] VALUES (6904,'س ٤٢: سئل فضيلة الشيخ- رحمه الله-: ما هي أفضل طريقة لحفظ القرآن الكريم؟ وهل يجوز أن أقرأ جزا معينا مثل الجزء السادس والعشرون لكي أحفظ ذلك وأترك باقي القرآن أفيدوني مأجورين؟','10527','6903','0');
INSERT INTO [t12293] VALUES (6905,'س ٤٣: سئل فضيلة الشيخ- رحمه الله-: مجموعة من النساء لا يستطعن أن يحضرن إلى المساجد لسماع الندوات فتضطر إحداهن لشراء أشرطة المسجل لسماع هذه الندوات. فهل ثواب السامع من الشريط هو نفس ثواب الجالس في المسجد مباشرة من تنزل الملائكة عليهم وإحاطتهم بالرحمة؟','10528','6903','0');
INSERT INTO [t12293] VALUES (6906,'س ٤٤: سئل فضيلة الشيخ- رحمه الله-: هل تعتبر أشرطة التسجيل طريقة من طرق العلم؟ وما هي الطريقة المثلى للاستفادة منها؟','10529','6903','0');
INSERT INTO [t12293] VALUES (6907,'س ٤٥: سئل فضيلة الشيخ- رحمه الله-: عن المسجلات الصوتية التي يسجل فيها العلم، وهل هناك حرج من استعمالها؟','10530','6903','0');
INSERT INTO [t12293] VALUES (6908,'س ٤٦: سئل فضيلة الشيخ- رحمه الله-: بعض طلبة العلم يكتفون بسماع أشرطة العلماء من خلال دروسهم، فهل تكفي في تلقي العلم؟ وهل يعتبرون طلاب علم؟ وهل يؤثر في معتقدهم؟','10530','6903','0');
INSERT INTO [t12293] VALUES (6909,'س ٤٧: سئل فضيلة الشيخ- رحمه الله-: هل سماع الأشرطة والدروس العلمية تغني عن الرحلة في الطلب؟','10532','6903','0');
INSERT INTO [t12293] VALUES (6910,'س ٤٨: سئل فضيلة الشيخ- رحمه الله-: بماذا تنصح طالب العلم المبتدئ؟','10532','6903','0');
INSERT INTO [t12293] VALUES (6911,'س ٤٩: سئل فضيلة الشيخ- رحمه الله-: يقوم طلبة العلم بعمل بحوث دراسية في الجامعات فما توجيه فضيلتكم؟','10535','6903','0');
INSERT INTO [t12293] VALUES (6912,'س ٥٠: سئل فضيلة الشيخ- رحمه الله-: ما أحسن وسيلة لتلقي العلم النافع؟','10536','6903','0');
INSERT INTO [t12293] VALUES (6913,'س ٥١: سئل فضيلة الشيخ- رحمه الله-: لا يخفى على فضيلتكم ما أنعم الله به على عباده من تيسير طرق طلب العلم بخلاف ما كان عليه السابقون، فما نصيحة فضيلتكم لطلبة العلم في هذه الأزمنة لاستغلال هذه النعم والجد في طلب العلم؟','10537','6903','0');
INSERT INTO [t12293] VALUES (6914,'س ٥٢: سئل فضيلة الشيخ- رحمه الله-: هل ترى لطالب العلم أن يركز على حفظ القرآن حتى يحفظه، أو يطلب العلم ويحفظ المتون ولو ما حفظ من القرآن إلا قليل؟','10539','6903','0');
INSERT INTO [t12293] VALUES (6915,'س ٥٣: سئل فضيلة الشيخ- رحمه الله-: كيف يتفقه المسلم في دينه؟','10539','6903','0');
INSERT INTO [t12293] VALUES (6916,'س ٥٤: سئل فضيلة الشيخ- رحمه الله-: طالب العلم هل يبدأ بحفظ القرآن الكريم أم بقراءة كتب العلم؟','10540','6903','0');
INSERT INTO [t12293] VALUES (6917,'س ٥٥: سئل فضيلة الشيخ- رحمه الله-: يحصل من بعض طلبة العلم المبتدئين في دراسة العلم من إتعاب أنفسهم في دراسة علوم متنوعة على أيدي علماء متعددين، والقراءة في كتب أهل العلم بدون ضوابط، فأحيانا يقرأ في المطولات قبل المختصرات، ويحصل منهم إهمال لدروس الكليات الشرعية في فترة الصباح بحجة قلة بضاعة مدرسي الكليات وضعف المادة العلمية، فما نصيحتكم؟','10541','6903','0');
INSERT INTO [t12293] VALUES (6918,'س ٥٦: سئل فضيلة الشيخ- رحمه الله-: كيف يستطيع الشخص أن يوفق بين حفظ كتاب الله تعالى وبين حضور الدروس العلمية اليومية؛ لأنه يصعب عليه أن يوازن بينهما وأن يوفق بينهما إلى جانب أعماله وأعمال الأهل؟','10543','6903','0');
INSERT INTO [t12293] VALUES (6919,'س ٥٧: سئل فضيلة الشيخ- رحمه الله-: يلاحظ على بعض الطلبة في الكليات الشرعية التكاسل في السنة الأولى وعدم الجدية في الطلب فما نصيحتكم؟','10544','6903','0');
INSERT INTO [t12293] VALUES (6920,'س ٥٨: سئل فضيلة الشيخ- رحمه الله-: كان عندي رغبة شديدة في طلب العلم منذ سنوات عديدة ولكن حال بيني وبين هذه الرغبة طلب المعاش الذي كان يستغرق النهار كله، والآن لقد من الله علي بدخل يغنيني عن العمل، فهل لي أن أتفرغ للعلم مع أن عمري قد وصل الثلاثين سنة؟','10545','6903','0');
INSERT INTO [t12293] VALUES (6921,'س ٥٩: سئل فضيلة الشيخ- رحمه الله-: يحصل عند بعض طلبة العلم في الجامعات الإسلامية الاعتماد على مذكرات للمواد','10547','6903','0');
INSERT INTO [t12293] VALUES (6922,'س ٦٠: سئل فضيلة الشيخ- رحمه الله-: ما المراحل التي ينبغي لطالب العلم أن يسير عليها؟','10548','6903','0');
INSERT INTO [t12293] VALUES (6923,'س ٦١: سئل فضيلة الشيخ- رحمه الله-: كيف يجمع طالب العلم بين دروس الكليات الشرعية وبين دروس العلماء في المساجد؟','10549','6903','0');
INSERT INTO [t12293] VALUES (6924,'س ٦٢: سئل فضيلة الشيخ- رحمه الله-: عمن لا يحب دراسة العقيدة خصوصا مسألة القدر خوفا من الزلل؟','10550','6903','0');
INSERT INTO [t12293] VALUES (6925,'س ٦٣: سئل فضيلة الشيخ- رحمه الله-: ما أفضل الطرق المعينة على طلب العلم؟','10553','6903','0');
INSERT INTO [t12293] VALUES (6926,'س ٦٤: سئل فضيلة الشيخ- رحمه الله-: هل يجب التقليد لمذهب معين أم لا؟','10554','6903','0');
INSERT INTO [t12293] VALUES (6927,'س ٦٥: سئل فضيلة الشيخ- رحمه الله-: عن أقسام الناس في طلب علم الكتاب والسنة الصحيحة؟','10555','6903','0');
INSERT INTO [t12293] VALUES (6928,'س ٦٦: سئل فضيلة الشيخ- رحمه الله-: إذا أراد طالب العلم الفقه فهل له الاستغناء عن أصول الفقه؟','10557','6903','0');
INSERT INTO [t12293] VALUES (6929,'س ٦٧: سئل فضيلة الشيخ- رحمه الله-: ذكر الخطيب البغدادي رحمه الله جانبا من جوانب تعلم العلم وهو لزوم أحد العلماء أو أحد المشائخ، فما رأي فضيلتكم؟','10558','6903','0');
INSERT INTO [t12293] VALUES (6930,'س ٦٨: سئل فضيلة الشيخ- رحمه الله-: ذكر عن ابن الوزير رحمه الله- أن الصحابة أبا بكر، وعمر، وعثمان، وعلي- رضي الله عنهم- لم يحفظوا القرآن الكريم، وكذلك ما ورد عن الأئمة كعثمان ابن أبي شيبة على قدره أنه لم يحفظ القرآن، وهذه الأشياء تدعو بعض طلبة العلم لترك حفظ كتاب الله، هل هذا صحيح؟','10560','6903','0');
INSERT INTO [t12293] VALUES (6931,'س ٦٩: سئل فضيلة الشيخ- رحمه الله-: من الأصول التي يرجع إليها طالب العلم الشرعي أقوال الصحابة- رضي الله عنهم- فهل هي حجة يعمل بها؟','10560','6903','0');
INSERT INTO [t12293] VALUES (6932,'س ٧٠: سئل فضيلة الشيخ- رحمه الله-: أرجو من فضيلتكم- حفظكم الله تعالى- توضيح المنهج الصحيح في طلب العلم في مختلف العلوم الشرعية، جزاكم الله خيرا وكفر لكم.','10563','6903','0');
INSERT INTO [t12293] VALUES (6933,'س ٧١: سئل فضيلة الشيخ- رحمه الله-: إذا أراد الإنسان حفظ القرآن فبماذا تنصحونه؟','10571','6903','0');
INSERT INTO [t12293] VALUES (6934,'س ٧٢: سئل فضيلة الشيخ- رحمه الله-: هناك بعض طلبة العلم يبدأ طلب العلم بكتب الحديث ويعرض عن المتون الفقهية وحجتهم بأن المتون الفقهية خالية من أدلة الكتاب والسنة فهل هذا صحيح؟','10571','6903','0');
INSERT INTO [t12293] VALUES (6935,'س ٧٣: سئل فضيلة الشيخ- رحمه الله-: لكن هل الأولى أن يحفظ متنا من متون الفقه أو متنا مختصرا من الحديث؟','10573','6903','0');
INSERT INTO [t12293] VALUES (6936,'س ٧٤: سئل فضيلة الشيخ- رحمه الله-: ما هي نصيحتكم لمن ابتدأفي طلب العلم؟ باي شيء يبدأ؟','10573','6903','0');
INSERT INTO [t12293] VALUES (6937,'س٧٥: سئل فضيلة الشيخ- رحمه الله-: أنا طالب مبتدئ ولكن يشكل علي بعض الأحيان أن أقرأ حديئا صححه مثلا بعض المتقدمين، ويضعفه المتاخرون فماذا أفعل؟','10575','6903','0');
INSERT INTO [t12293] VALUES (6938,'س ٧٦: سئل فضيلة الشيخ- رحمه الله-: متى يكون طالب العلم متبعا لمذهب الإمام أحمد رحمه الله؟','10576','6903','0');
INSERT INTO [t12293] VALUES (6939,'س ٧٧: سئل فضيلة الشيخ- رحمه الله-: ما توجيه فضيلتكم- حفظكم الله تعالى- لطالب العلم المبتدئ هل يقلد إماما من أئمة','10576','6903','0');
INSERT INTO [t12293] VALUES (6940,'س ٧٨: سئل فضيلة الشيخ- رحمه الله-: ما طريقة طلب العلم باختصار؟ جزاكم الله خيرا.','10577','6903','0');
INSERT INTO [t12293] VALUES (6941,'س ٧٩: سئل فضيلة الشيخ- رحمه الله-: كثيرة الأسئلة عن كيفية الطلب، وباي شيء يبدأ من أراد أن يطلب العلم، وبأي المتون يبدأ حفظا، فما توجيهكم لهؤلاء الطلبة وجزاكم الله خيرا؟','10578','6903','0');
INSERT INTO [t12293] VALUES (6942,'س٨٠: سئل فضيلة الشيخ- رحمه الله-: كثر عند بعض الشباب الصالح القول بعدم التقليد مستندين إلى بعض أقوال ابن القيم عليه رحمة الله، فما قولكم؟','10582','6903','0');
INSERT INTO [t12293] VALUES (6943,'س٨١: سئل فضيلة الشيخ - رحمه الله -: ما فائدة تعلم طلاب العلم فرق المعتزلة والجهمية والخوارج مع عدم وجودها فى هذا العصر؟','10583','6903','0');
INSERT INTO [t12293] VALUES (6944,'س ٨٢: سئل فضيلة الشيخ- رحمه الله-: بماذا تنصح من يريد طلب العلم الشرعي ولكنه بعيد عن العلماء مع العلم بان لديه مجموعة كتب منها الأصول والمختصرات؟','10584','6903','0');
INSERT INTO [t12293] VALUES (6945,'س ٨٣: سئل فضيلة الشيخ- رحمه الله-: من الملاحظ انصراف كثير من طلاب العلم عن إتقان قواعد اللغة العربية مع أهميتها، فماتعليقكم؟','10584','6903','0');
INSERT INTO [t12293] VALUES (6946,'س ٨٤: سئل فضيلة الشيخ- رحمه الله-: هل يجوزتعلم العلم من الكتب فقط دون العلماء وخاصة إذا كان يصعب تعلم العلم من العلماء لندرتهم؟ وما رأيكم في القول القائل: من كان شيخه كتابه كان خطؤه أكثر من صوابه؟','10585','6903','0');
INSERT INTO [t12293] VALUES (6947,'س٨٥: سئل فضيلة الشيخ- رحمه الله-: ذكرتم- جزاكم الله خيرا- أن الاعتماد على أقوال الرجال خطأ يضر طالب العلم، فهل يفهم من هذا عدم التمذهب أو الرجوع إلى مذهب معين فيما يشكل من أحكام؟','10586','6903','0');
INSERT INTO [t12293] VALUES (6948,'س ٨٦: سئل فضيلة الشيخ- رحمه الله-: أمامي مجال لدخول كلية علمية، فهل أدخلها لنفع المسلمين أم أسلك المجال في كلية الشريعة؟ أفيدوني جز اكم الله خيرا.','10586','6903','0');
INSERT INTO [t12293] VALUES (6949,'س ٨٧: سئل فضيلة الشيخ- رحمه الله-: هل هناك دعاء لحفظ القرآن؟ وما طريقة حفظه؟','10587','6903','0');
INSERT INTO [t12293] VALUES (6950,'س ٨٨: سئل فضيلة الشيخ- رحمه الله-: أريد أن أتعلم العلم الشرعى وأبدأ في التعلم ولا أعرف كيف أبدأ، فبماذا تنصحوني في ذلك؟','10588','6903','0');
INSERT INTO [t12293] VALUES (6951,'س ٨٩: سئل فضيلة الشيخ- رحمه الله-: طالب العلم المبتدئ هل يبدأ في طلب العلم بالبحث عن الأدلة أم يقلد في ذلك أئمة أحد المذاهب؟ ما توجيه سماحتكم- حفظكم الله تعالى-؟','10589','6903','0');
INSERT INTO [t12293] VALUES (6952,'س٩٠: سئل فضيلة الشيخ- رحمه الله-: يقع بعض طلبة العلم المبتدئين في الكليات الشرعية في حيرة هل يكتفون بطلب العلم في الكليات أم يجمعون مع ذلك طلب العلم في المساجد فما نصيحة فضيلتكم؟','10590','6903','0');
INSERT INTO [t12293] VALUES (6953,'س ٩١: سئل فضيلة الشيخ- رحمه الله-: بماذا تنصح من بدأ في طلب العلم على كبر سنه؟ وإن لم يتيسر له شيخ يأخذ منه ودلازمه فهل ينفعه طلب العلم بلا شيخ؟','10591','6903','0');
INSERT INTO [t12293] VALUES (6954,'س ٩٢: سئل فضيلة الشيخ- رحمه الله-: ما هي الطريقة الصحيحة في طلب العلم؟ هل يكون بحفظ المتون في علوم الشريعة أم فهمها؟ نرجو التوضيح حفظكم الله تعالى.','10593','6903','0');
INSERT INTO [t12293] VALUES (6955,'س ٩٣: سئل فضيلة الشيخ- رحمه الله-: ما رأي فضيلتكم في تعلم التجويد والالتزام به؟','10594','6903','0');
INSERT INTO [t12293] VALUES (6956,'أخطاء يجب الحذر منها','10614','6856','0');
INSERT INTO [t12293] VALUES (6957,'س ٩٤: سئل فضيلة الشيخ- رحمه الله-: هل مساعدة طلاب العلم في حل ما استشكل عليهم من واجبات أو مساعدتهم وإعانتهم في عمل أبحاث لمجرد إعانتهم في إكمال مشوارهم، وتشجيعا لهم وأحيانا لضيق الوقت، هل يعتبر هذا من باب التعاون على البر والتقوى؟','10629','6956','0');
INSERT INTO [t12293] VALUES (6958,'س ٩٥: سئل فضيلة الشيخ- رحمه الله-: معلم أسند إليه تدريس أحد المواد التي قد لا يجيدها، ولكن لعدم وجود البديل وافق، فهل يأثم أم لا؟ أرجو الإفادة.','10630','6956','0');
INSERT INTO [t12293] VALUES (6959,'س ٩٦: سئل فضيلة الشيخ- رحمه الله-: عن طالب حريص على تعلم القرآن والسنة ولكنه ينشغل في أكثر أوقاته بنظم الشعر وقراءة كتب الشعراء، فما نصيحتكم؟','10631','6956','0');
INSERT INTO [t12293] VALUES (6960,'س ٩٧: سئل فضيلة الشيخ- رحمه الله-: هل لنا أن نسأل عن أمور لم تحدث مع فرض بعيد جدا لحدوثها نرجو الإفادة؟','10632','6956','0');
INSERT INTO [t12293] VALUES (6961,'س ٩٨: سئل فضيلة الشيخ- رحمه الله-: عن رجل حافظ لكتاب الله عز وجل لكنه لا يصلي آخر الليل، ويأتي إلى المسجد قرب الإقامة، ولا يظهر عليه أثر حفظ القرآن الكريم، ولا يختم إلا في الشهرين مرة واحدة، هل يأثم بذلك؟','10634','6956','0');
INSERT INTO [t12293] VALUES (6962,'س ٩٩: سئل فضيلة الشيخ- رحمه الله-: عن معلم يجد إحراجا من قبل طلابه في أسئلة كثيرة، فما حكم الإجابة على أسئلتهم إذا كانت خارج المنهج؟','10635','6956','0');
INSERT INTO [t12293] VALUES (6963,'س ١٠٠: سئل فضيلة الشيخ- رحمه الله-: هل يجوز لطلبة العلم الشرعي التغيب عن بعض المحاضرات بحجة الاستذكار للاختبار في مادة أخرى خاصة إذا كان الطالب يقصر في البداية إذا بدأت الاختبارات؟ أو يتغيب عن المحاضرات للمذاكرة؟ وهل من نصيحة لطالب العلم؟','10635','6956','0');
INSERT INTO [t12293] VALUES (6964,'س ١٠١: سئل فضيلة الشيخ- رحمه الله-: هل توجد فلسفة في الشريعة الإسلامية؟ وما الرد على من يدعي بذلك؟ وهل يجوز أن يدرس الطالب الفلسفة ويتعمق فيها؟','10637','6956','0');
INSERT INTO [t12293] VALUES (6965,'س ١٠٢: سئل فضيلة الشيخ- رحمه الله-: هل يجوز لي أن أتعلم القرآن عند رجل مبتاع؟','10638','6956','0');
INSERT INTO [t12293] VALUES (6966,'س ١٠٣: سئل فضيلة الشيخ- رحمه الله-: هل يعذر طلبة العلم الذين درسوا العقيدة على غير مذهب السلف الصالح محتجين بأن العالم الفلاني أو الإمام الفلاني يعتقد هذه العقيدة؟','10639','6956','0');
INSERT INTO [t12293] VALUES (6967,'س ١٠٤: سئل فضيلة الشيخ- رحمه الله-: من الملاحظ في الصحوة الإسلامية الاتجاه إلى العلم ولله الحمد والمنة، وخصوصا علم السنة النبوية على صاحبها أفضل الصلاة وأتم التسليم ومن الملاحظات: أ- التعرض للصحيحين (البخاري ومسلم) نقدا، تضعيفا وتصحيحا من قبل بعض طلبة العلم الذين لم ترسخ أقدامهم في هذا العلم، علما بأن هذين الكتابين من أصول أهل السنة والجماعة وقد تلقتهما الأمة بالقبول. ب- رواج مذهب الظاهرية عند غالبية الشباب، والأعراض عن كتب فقهاء الأمة. ب- انشغال بعض طلبة هذا العلم الشريف به عن العلوم الضرورية لطلبة العلم الشرعي مثل القرآن الكريم، واللغة العربية، والفقه والفرائض.. إلخ. د- شيوع ظاهرة التعالم والتصدر للتدريس والفتيا من قبل بعض طلبة العلم الذين لا يعرف لهم شيوخ ولا قدم ثابتة في العلم وإنما هي القراءة ومطالعة الكتب. فما توجيهكم حفظكم الله ورعاكم؟','10640','6956','0');
INSERT INTO [t12293] VALUES (6968,'س ١٠٥: سئل فضيلة الشيخ- رحمه الله-: ما قول فضيلتكم في بعض الطلاب الذين يدرسون من أجل الوظيفة والراتب، وكذلك ما يفعله البعض من استئجار من يكتب لهم البحوث، أو يعد لهم الرسائل، أو يحقق بعض الكتب فيحصلون به على شهادات علمية؟','10644','6956','0');
INSERT INTO [t12293] VALUES (6969,'س ١٠٦: سئل فضيلة الشيخ- رحمه الله-: بعض المبتدئين يبدؤون في القراءة من كتاب (المحلى) لابن حزم رحمه الله بحجة التمرن على المناظرة، وحينما تنصحهم بأن هذا سابق لأوانه يقولون: نريد التمرن، فهل هذا صحيح؟','10647','6956','0');
INSERT INTO [t12293] VALUES (6970,'س ١٠٧: سئل فضيلة الشيخ- رحمه الله-: ما نصيحة فضيلتكم لمن يجعل الولاء والبراء لإخوانه في موافقتهم له في مسألة أو عدم موافقتهم له، وكذلك ما يحصل من الحسد والبغض من طلاب العلم؟','10647','6956','0');
INSERT INTO [t12293] VALUES (6971,'س ١٠٨: سئل فضيلة الشيخ- رحمه الله-: نحن طلبة نتلقى العلم، وندرس العقيدة على معلمين يدرسونا العقيدة الأشعرية، ويفسرون يد الله تعالى بقدرته أو نعمته، واستواءه على عرشه بالاستيلاء عليه ونحو ذلك، فما حكم الدراسة على هؤلاء المعلمين؟','10649','6956','0');
INSERT INTO [t12293] VALUES (6972,'س ١٠٩: سئل فضيلة الشيخ- رحمه الله-: هناك بعض الناس يتعصبون لأقوالهم جدا، وينكرون على الآخرين الذين لم يأخذوا بأقوالهم، وقد يقعون في أصحاب هذه الأقوال الأخرى بالغيبة والسب، فما قولكم في هذا؟','10652','6956','0');
INSERT INTO [t12293] VALUES (6973,'س ١١٠: سئل فضيلة الشيخ- رحمه الله-: نحن طلاب العلم نحفظ الكثير من الآيات على سبيل الاستشهاد، وفي نهاية العام نكون قد نسينا الكثير منها، فهل ندخل في حكم من يعذبون بسبب نسيان ما حفظوه؟','10654','6956','0');
INSERT INTO [t12293] VALUES (6974,'س ١١١: سئل فضيلة الشيخ- رحمه الله-: قد يعلم الإنسان شيئا ويأمر غيره وهو نفسه لا يعمله سواء كان فرضا أو نفلا، فهل يحل له أن يأمر غيره بما لا يعمل؟ وهل يجب على المأمور امتثال أمره أم يحل له الاحتجاج عليه بعدم عمله ثم لا يعمل ما أمر به تبعا لذلك؟','10656','6956','0');
INSERT INTO [t12293] VALUES (6975,'س ١١٢: سئل فضيلة الشيخ- رحمه الله-: يقع من بعض طلبة العلم- هداهم الله- الاشتغال بالقيل والقال والكلام فيما لا ينفع مما يترتب عليه إضاعة الأوقات، فما نصيحتكم؟','10657','6956','0');
INSERT INTO [t12293] VALUES (6976,'موقف طالب العلم من العلماء','10660','6856','0');
INSERT INTO [t12293] VALUES (6977,'س ١١٣: سئل فضيلة الشيخ- رحمه الله-: يقع من بعض الناس- هداهم الله تعالى- التقليل من شأن العلماء بدعوى عدم فقه الواقع، فما توجيه سماحتكم جزاكم الله خيرا ووفقكم لما يحبه ويرضاه؟','10661','6976','0');
INSERT INTO [t12293] VALUES (6978,'س ١١٤: سئل فضيلة الشيخ- رحمه الله-: يذكر البعض: بأنه لا تجوز المذهبية ولا تقليد أحد المذاهب الأربعة وإنما العلم بالدليل، لكن ذلك يجعلني أتساءل دائما لقد كانت منابع هؤلاء الأئمة الكتاب والسنة والسؤال، فهل يجوز تقليد أي مذهب من المذاهب الأربعة دون التعصب لها؟','10663','6976','0');
INSERT INTO [t12293] VALUES (6979,'س ١١٥: سئل فضيلة الشيخ- رحمه الله-: نقرأ في بعض الكتب أن للإمام أحمد بن حنبل رحمه الله في المسألة الفلانية قولين أو ثلاثة، فلا أدري هل يعني ذلك أن هذه الأقوال هي عدة آراء رآها الإمام أحمد رحمه الله ولم يترجح عنده أحدها، أم أنها آراء قد نسخ اللاحق منها السابق أم ماذا؟ نرجو بيان ذلك.','10664','6976','0');
INSERT INTO [t12293] VALUES (6980,'س ١١٦: سئل فضيلة الشيخ- رحمه الله-: يقول: ما حكم تقليد مذهب من المذاهب الأربعة؟','10669','6976','0');
INSERT INTO [t12293] VALUES (6981,'س ١١٧: سئل فضيلة الشيخ- رحمه الله-: يذكر بعض الأخوة بأنه من الواجب علينا إذا مر ذكر صحابي أثناء قراءتنا أننا نقول: رضي الله عنه، ولكن إذا مر ذكر تابعي أو من السلف وقلنا: رضي الله عنه، هل في ذلك حرج؟','10671','6976','0');
INSERT INTO [t12293] VALUES (6982,'س ١١٨: سئل فضيلة الشيخ- رحمه الله-: بعض الناس يعتقد أن دور علماء المسلمين مقصور على الأحكام الشرعية، وأنه لا دخل لهم في العلوم الأخرى كالسياسة والاقتصاد ونحوهما، فما رأيكم في هذا الاعتقاد؟','10673','6976','0');
INSERT INTO [t12293] VALUES (6983,'س ١١٩: سئل فضيلة الشيخ- رحمه الله-: هناك بعض طلبة العلم يحرص على حضور دروس طلبة العلم دون أن يلقي اهتماما بدروس العلماء الذين جمعوا ما لم يجمعه طلبة العلم. فما توجيه فضيلتكم- حفظكم الله تعالى-؟','10674','6976','0');
INSERT INTO [t12293] VALUES (6984,'س ١٢٠: سئل فضيلة الشيخ- رحمه الله-: ما الأمور التي يجب توافرها فيمن يتلقى عنه العلم؟','10675','6976','0');
INSERT INTO [t12293] VALUES (6985,'س ١٢١: سئل فضيلة الشيخ- رحمه الله-: بعض طلبة العلم يأتي إلى مسألة من مسائل العلم فيبحثها ويحققها بأدلتها ومناقشتها مع العلماء، فإذا حضر مجلس عالم يشار إليه بالبنان، قال: ما تقول - أحسن الله إليك- في كذا وكذا، قال: هذا حرام مثلا، قال: كيف؟ بم تجيب عن قوله - صلى الله عليه وسلم - كذا؟ عن قول فلان كذا؟ ثم أتى بأدلة لا يعرفها العالم؛ لأن العالم ليس محيطا بكل شيء حتى يظهر نفسه أنه أعلم من هذا العالم، فما رأي فضيلتكم؟','10676','6976','0');
INSERT INTO [t12293] VALUES (6986,'س ١٢٢: سئل فضيلة الشيخ- رحمه الله-: إذا اجتهد العالم في مسألة من المسائل ولم يصب الحكم الصحيح، فبم يحكم عليه؟','10677','6976','0');
INSERT INTO [t12293] VALUES (6987,'س ١٢٣: سئل فضيلة الشيخ- رحمه الله-: ما قولكم فيمن يتخذ من أخطاء العلماء طريقا للقدح فيهم ورميهم بالبهتان؟ وما النصيحة التي توجهها لطلبة العلم في ذلك؟','10684','6976','0');
INSERT INTO [t12293] VALUES (6988,'س ١٢٤: سئل فضيلة الشيخ- رحمه الله-: عما يحصل من البعض من الوقوع في أعراض العلماء الربانيين، والقدح فيهم، وغيبتهم؟','10686','6976','0');
INSERT INTO [t12293] VALUES (6989,'س ١٢٥: سئل فضيلة الشيخ- رحمه الله-: متى ينكر على المخالف في المسائل الخلافية التي بين أهل العلم؟','10687','6976','0');
INSERT INTO [t12293] VALUES (6990,'س ١٢٦: سئل فضيلة الشيخ- رحمه الله-: قلتم: إن مذهب أهل السنة والجماعة هو مذهب الإمام أحمد رحمه الله، فكيف حكمنا على المذاهب الثلاثة الباقية؟','10687','6976','0');
INSERT INTO [t12293] VALUES (6991,'س ١٢٧: سئل فضيلة الشيخ- رحمه الله-: ما رأي فضيلتكم فيمن ينفر من قراءة كتب الدعاة المعاصرين ويرى الاقتصار على كتب السلف الأخيار، وأخذ المنهج منها؟ ثم ما هي النظرة الصحيحة أو الجامعة لكتب السلف- رحمهم الله- وكتب الدعاة المعاصرين والمفكرين؟','10688','6976','0');
INSERT INTO [t12293] VALUES (6992,'س ١٢٨: سئل فضيلة الشيخ- رحمه الله-: ما رأي فضيلتكم فيمن صار ديدنهم تجريح العلماء، وتنفير الناس عنهم والتحذير منهم؟ هل هذا عمل شرعي يثاب عليه أو يعاقب عليه؟','10689','6976','0');
INSERT INTO [t12293] VALUES (6993,'س ١٢٩: سئل فضيلة الشيخ- رحمه الله-: ماذا يجب علي تجاه أحد الأساتذة عندما يخطئ وخصوصا في المواد الدينية، وأنا متأكد من الجواب الصحيح؟','10692','6976','0');
INSERT INTO [t12293] VALUES (6994,'س ١٣٠: سئل فضيلة الشيخ- رحمه الله-: البعض - هداهم الله- يقلل من العلماء بحجة عدم معرفة الواقع ويقدم الدعاة وطلبة العلم عليهم؛ لأنهم على اطلاع واسع بخلاف العلماء فما قولكم؟','10692','6976','0');
INSERT INTO [t12293] VALUES (6995,'طالب العلم والدعوة إلى الله عز وجل','10696','6856','0');
INSERT INTO [t12293] VALUES (6996,'س ١٣١: سئل فضيلة الشيخ- رحمه الله-: يقول: شخص لديه علم شرعي وهو متخرج من أحد الكليات الشرعية، ويقوم بالتدريس للمرحلة الثانوي، ويطلب منه جماعة المسجد أو طلبة العلم أن يلقي كلمة أو محاضرة في المسجد أو في مناسبة، لكنه يمتنع ويصر على عدم المشاركة في أي درس في المسجد أو في قاعة أو في غيرها، ويعتذر ويقول: يكفي أنني أدرس المواد الشرعية في الثانوية، فهل يؤاخذ على ذلك؟','10697','6995','0');
INSERT INTO [t12293] VALUES (6997,'س ١٣٢: سئل فضيلة الشيخ- رحمه الله-: العلماء والدعاة المصلحون عليهم مسئولية عظيمة في بيان التوحيد والتحذير من الشرك والخرافات، فما نصيحتكم؟','10698','6995','0');
INSERT INTO [t12293] VALUES (6998,'س ١٣٣: سئل فضيلة الشيخ- رحمه الله-: يختلف الكثير من طلبة العلم في معاملة العاصي، فما التوجيه الصحيح جزاكم الله خيرا؟','10699','6995','0');
INSERT INTO [t12293] VALUES (6999,'س ١٣٤: سئل فضيلة الشيخ- رحمه الله-: هل من توجيه إلى طلبة العلم حتى يكونوا دعاة؟ حيث إنهم يحتجون بطلب العلم وأنه يشغلهم عن الدعوة والبعض يحتج بالدعوة عن طلب العلم؟','10702','6995','0');
INSERT INTO [t12293] VALUES (7000,'س ١٣٥: سئل فضيلة الشيخ- رحمه الله-: ما نصيحتكم لطلبة العلم حول دعوة الناس وتعليمهم العلم الشرعي، لأنه قد يوجد من بعضهم- هداهم الله تعالى- شيء من الغلظة والشدة في التعامل، نرجو التوجيه والإرشاد، سدد الله خطاكم ووفقكم لما يحبه ويرضاه؟','10704','6995','0');
INSERT INTO [t12293] VALUES (7001,'س ١٣٦: سئل فضيلة الشيخ- رحمه الله-: ما رأي فضيلتكم في طلبة العلم الذين قد جمعوا أسس العلم في العقيدة ومعرفة','10705','6995','0');
INSERT INTO [t12293] VALUES (7002,'س ١٣٧: سئل فضيلة الشيخ- رحمه الله-: ما الواجب على طالب العلم والعالم تجاه الدعوة إلى الله؟','10707','6995','0');
INSERT INTO [t12293] VALUES (7003,'س ١٣٨: سئل فضيلة الشيخ- رحمه الله-: ما رأي فضيلتكم في تعلم طالب العلم اللغة الإنجليزية، لاسيما في سبيل استخدامها في الدعوة إلى الله؟','10709','6995','0');
INSERT INTO [t12293] VALUES (7004,'س ١٣٩: سئل فضيلة الشيخ- رحمه الله-: أيهما أفضل: التفرغ للدعوة إلى الله- عز وجل- أم التفرغ لطلب العلم؟','10711','6995','0');
INSERT INTO [t12293] VALUES (7005,'س ١٤٠: سئل فضيلة الشيخ- رحمه الله-: أيهما أفضل: مخالطة الناس بعد العشاء لتعليمهم وإرشادهم ونصحهم بحيث لا يمكن قيام الليل أو اعتزالهم حتى يتم قيام الليل؟','10711','6995','0');
INSERT INTO [t12293] VALUES (7006,'س ١٤١: سئل فضيلة الشيخ- رحمه الله-: ما رأيكم بمن ترك الدعوة بحجة التفرغ لطلب العلم، وأنه لا يتمكن من الجمع بين الدعوة والعلم في بداية الطريق، لأنه يغلب على ظنه ترك العلم إذا اشتغل بالدعوة، ويرى أن يطلب العلم حتى إذا أخذ منه نصيبا اتجه لدعوة الناس وتعليمهم وإرشادهم؟','10712','6995','0');
INSERT INTO [t12293] VALUES (7007,'طالب العلم والكتب','10715','6856','0');
INSERT INTO [t12293] VALUES (7008,'س ١٤٢: سئل فضيلة الشيخ- رحمه الله-: عن الكتب المفيدة التي ينصح بها؟','10728','7007','0');
INSERT INTO [t12293] VALUES (7009,'س ١٤٣: سئل فضيلة الشيخ- رحمه الله-: طالب مبتدئ في العلم عنده كتاب رياض الصالحين وكتاب فقه السنة فبأيهما يبدأ؟','10730','7007','0');
INSERT INTO [t12293] VALUES (7010,'س ١٤٤: سئل فضيلة الشيخ- رحمه الله-: كيف يعرف الإنسان الأحاديث الصحيحة من الموضوعة؟ وهل هناك كتب توضح الأحاديث الموضوعة؟','10731','7007','0');
INSERT INTO [t12293] VALUES (7011,'س ١٤٥: سئل فضيلة الشيخ- رحمه الله-: ما حكم قراءة الكتب السماوية مع علمنا بتحريفها؟','10732','7007','0');
INSERT INTO [t12293] VALUES (7012,'س ١٤٦: سئل فضيلة الشيخ- رحمه الله-: عن كتاب "عقوبة أهل الكبائر" لمؤلفه أبي الليث السمرقندي؟','10733','7007','0');
INSERT INTO [t12293] VALUES (7013,'س ١٤٧: سئل فضيلة الشيخ- رحمه الله-: عن تفسير مختصر ابن كثير، وفقه السنة، ورياض الصالحين، والكبائر، وقصص الأنبياء؟','10735','7007','0');
INSERT INTO [t12293] VALUES (7014,'س ١٤٨: سئل فضيلة الشيخ- رحمه الله-: ما خير الكتب التي يجب على المسلم أن يقتنيها؟','10736','7007','0');
INSERT INTO [t12293] VALUES (7015,'س ١٤٩: سئل فضيلة الشيخ- رحمه الله-: ذكر عن فضيلتكم أن طريقة كتاب المعجم المفهرس لألفاظ القرآن الكريم غير جائزة، وأن طريقتهم التي استخدموها لا تجوز، هل هذا صحيح؟','10738','7007','0');
INSERT INTO [t12293] VALUES (7016,'س ١٥٠: سئل فضيلة الشيخ- رحمه الله-: ما رأي فضيلتكم حفظكم الله في كتاب (الروح) ، و (حادي الأرواح) لابن القيم رحمه الله تعالى؟','10740','7007','0');
INSERT INTO [t12293] VALUES (7017,'س ١٥١: سئل فضيلة الشيخ- رحمه الله-: ما أفضل الكتب المؤلفة في السيرة النبوية؟','10741','7007','0');
INSERT INTO [t12293] VALUES (7018,'س ١٥٢: سئل فضيلة الشيخ- رحمه الله-: عن مدى صحة كتب تفسير الأحلام، مثل كتاب تفسير الأحلام لابن سيرين رحمه الله؟','10741','7007','0');
INSERT INTO [t12293] VALUES (7019,'س ١٥٣: سئل فضيلة الشيخ- رحمه الله-: ما رأيكم في كتاب الروح لابن القيم؟ وهل القصص التي ذكرها عن أهل القبور صحيحة؟','10743','7007','0');
INSERT INTO [t12293] VALUES (7020,'س ١٥٤: سئل فضيلة الشيخ- رحمه الله-: عن كتاب: "درة الناصحين في الوعظ والإرشاد"؟','10743','7007','0');
INSERT INTO [t12293] VALUES (7021,'س ١٥٥: سئل فضيلة الشيخ- رحمه الله-: عن كتاب " الأذكار" وكتاب "الجواب الكافي لمن سال عن الدواء الشافي"','10744','7007','0');
INSERT INTO [t12293] VALUES (7022,'س ١٥٦: سئل فضيلة الشيخ- رحمه الله-: عن كتاب (بدائع الزهور) وكتاب (الروض الفائق) وكتاب (تنبيه الغافلين) ؟','10745','7007','0');
INSERT INTO [t12293] VALUES (7023,'س ١٥٧: سئل فضيلة الشيخ- رحمه الله-: عن طالب علم جع عدا كبيرا من الكتب الشرعية ولكن لم يقم بقراءتها، وعندما يطلب منه أحد الإعارة فإنه يعيره فهل عليه شيء؟ وهل صحيح','10746','7007','0');
INSERT INTO [t12293] VALUES (7024,'س ١٥٨: سئل فضيلة الشيخ- رحمه الله-: ما رأيكم في هذه الكتب: ا- (مروج الذهب) للمسعودي. ٢- (الحصن الحصين) . ٣- (المأثورات) . ٤- (الدعاء المستجاب) . ٥- (العواصم من القواصم) ؟ لأبي بكر ابن العربي وما الفرق بينه وبين ابن عربي؟','10748','7007','0');
INSERT INTO [t12293] VALUES (7025,'س ١٥٩: سئل فضيلة الشيخ- رحمه الله-: يعتقد البعض - وفقهم الله- بان كتب المتقدمين صماء وصعبة العبارة والمعاني، وأنها لا تصلح لوقتنا المعاصر الذي كثرت فيه المعاصي، وأن الحاجة لكتب الرقائق وغيرها أكثر من كتب العقيدة والفقه، فما قولكم؟','10749','7007','0');
INSERT INTO [t12293] VALUES (7026,'س ١٦٠: سئل فضيلة الشيخ- رحمه الله-: ما رأي فضيلتكم في (مجموع فتاوى ورسائل شيخ الإسلام ابن تيمية) رحمه الله تعالى، جمع الشيخ عبد الرحمن بن قاسم رحمه الله؟','10752','7007','0');
INSERT INTO [t12293] VALUES (7027,'س ١٦١: سئل فضيلة الشيخ- رحمه الله-: ما هو أفضل كتاب للحفظ في علم العقيدة، وأفضل شرح له، وعدة شروح أخرى؟','10752','7007','0');
INSERT INTO [t12293] VALUES (7028,'س ١٦٢: سئل فضيلة الشيخ- رحمه الله-: ما أفضل كتاب للحفظ في علم الحديث؟ وأفضل شرح له؟','10753','7007','0');
INSERT INTO [t12293] VALUES (7029,'س ١٦٣: سئل فضيلة الشيخ- رحمه الله-: عن أفضل متون في الفقه للحفظ؟','10753','7007','0');
INSERT INTO [t12293] VALUES (7030,'س ١٦٤: سئل فضيلة الشيخ- رحمه الله-: عن أفضل متون النحو للحفظ؟','10754','7007','0');
INSERT INTO [t12293] VALUES (7031,'س ١٦٥: سئل فضيلة الشيخ- رحمه الله-: عن كتاب رياض الصالحين؟','10754','7007','0');
INSERT INTO [t12293] VALUES (7032,'س ١٦٦: سئل فضيلة الشيخ- رحمه الله-: "حادي الأرواح "، وكتاب "الروح" لابن القيم. وعن كتاب "العقد الفريد" لابن عبد ربه الأندلسي، وكتاب "الفقه على المذاهب الأربعة"؟','10755','7007','0');
INSERT INTO [t12293] VALUES (7033,'س ١٦٧: سئل فضيلة الشيخ- رحمه الله-: ما الكتب التي تنصحون بها طالب العلم المتوسط؟','10756','7007','0');
INSERT INTO [t12293] VALUES (7034,'س ١٦٨: سئل فضيلة الشيخ- رحمه الله-: عن كتاب تفسير الأحلام لابن سيرين رحمه الله؟','10758','7007','0');
INSERT INTO [t12293] VALUES (7035,'س ١٦٩: سئل فضيلة الشيخ- رحمه الله-: عن شاب تعلم كثيرا من العلوم الدنيوية ثم وفقه الله للعلم الشرعي ويسأل عن أفضل التفاسير؟','10759','7007','0');
INSERT INTO [t12293] VALUES (7036,'س ١٧٠: سئل فضيلة الشيخ- رحمه الله-: إذا أراد طالب العلم أن ينقل الأحاديث التي زادت من بلوغ المرام على المحرر لابن عبد الهادي، فهل هذه الطريقة مفيدة؟','10762','7007','0');
INSERT INTO [t12293] VALUES (7037,'س ١٧١: سئل فضيلة الشيخ- رحمه الله-: أيهما أفضل كتاب المحرر لابن عبد الهادي رحمه الله، أو بلوغ المرام لابن حجر رحمه الله؟','10762','7007','0');
INSERT INTO [t12293] VALUES (7038,'س ١٧٢: سئل فضيلة الشيخ- رحمه الله-: أي كتب تفسير القرآن تنصح بقراءتها؟ وحفظ القرآن، إذا حفظ الإنسان ونسي،','10762','7007','0');
INSERT INTO [t12293] VALUES (7039,'س ١٧٣: سئل فضيلة الشيخ- رحمه الله-: عن كتاب فقه السنة؟','10765','7007','0');
INSERT INTO [t12293] VALUES (7040,'س ١٧٤: سئل فضيلة الشيخ- رحمه الله-: في هذا الزمن يجري تسمية بعض العلوم التجريبية بالعلم، حتى إن المدارس الثانوية سميت بعلمي وأدبي، فهل هذا صحيح؟ إضافة لذلك إن هذا التقسيم في المدارس يعلق بآذان الطلاب مما يؤثر عليهم مستقبلا.','10766','7007','0');
INSERT INTO [t12293] VALUES (7041,'س ١٧٥: سئل فضيلة الشيخ- رحمه الله-: ما هي الكتب التي تنصح بها؟ ونرجو توجيه نصيحة للطلاب جزاكم الله خيرا.','10766','7007','0');
INSERT INTO [t12293] VALUES (7042,'طالب العلم والفتوى','10768','6856','0');
INSERT INTO [t12293] VALUES (7043,'س ١٧٦: سئل فضيلة الشيخ- رحمه الله-: هل يجوز استفتاء أكثر من عالم؟ وفي حالة اختلاف الفتيا؟ وهل يأخذ المستفتي بالأيسر أم بالأحوط؟','10769','7042','0');
INSERT INTO [t12293] VALUES (7044,'س ١٧٧: سئل فضيلة الشيخ- رحمه الله-: هل يجوز لإنسان أن يجتهد في إفتاء بعض الناس إذا كان لا يوجد من يفتي أو لم يتيسر سؤال العلماء؟','10770','7042','0');
INSERT INTO [t12293] VALUES (7045,'س ١٧٨: سئل فضيلة الشيخ- رحمه الله-: كثيرا ما يشاع بأن الفتوى تتغير بتغير الزمان أو المكان، مثل: المذياع في أول ظهوره حرمه البعض، فنرجو من سماحتكم بيان الحق في هذه المسألة، والله يحفظكم ولرعاكم.','10771','7042','0');
INSERT INTO [t12293] VALUES (7046,'س ١٧٩: سئل فضيلة الشيخ- رحمه الله-: هناك من الناس من يفتي بغير علم، ما حكم ذلك؟','10773','7042','0');
INSERT INTO [t12293] VALUES (7047,'س ١٨٠: سئل فضيلة الشيخ- رحمه الله-: أنا طالب في السنوات الأولى من كلية الشريعة، وكثيرا ما يرد علينا مسائل مختلف فيها، وقد يكون الراجح في بعض هذه المسائل مخالفا لبعض أقوال العلماء الآن، أو نأخذ المسائل ولكن لا شيء يرجح بينها، فنصبح في حيرة من أمرنا، فماذا نفعل في حكم المسألة المختلف فيها أو عندما نسأل من عامة الناس؟ جزاكم الله خيرا؟','10774','7042','0');
INSERT INTO [t12293] VALUES (7048,'س ١٨١: سئل فضيلة الشيخ- رحمه الله-: ما سبب توقف العالم عن الفتوى؟','10782','7042','0');
INSERT INTO [t12293] VALUES (7049,'س ١٨٢: سئل فضيلة الشيخ- رحمه الله-: عن معنى "الاجتهاد" و"التقليد" وهل التقليد كان موجودا في زمن الصحابة والتابعين فيقلد بعضهم بعضا أم لا؟','10783','7042','0');
INSERT INTO [t12293] VALUES (7050,'س ١٨٣: سئل فضيلة الشيخ- رحمه الله-: هل هناك شروط للاجتهاد؟','10785','7042','0');
INSERT INTO [t12293] VALUES (7051,'س ١٨٤: سئل فضيلة الشيخ- رحمه الله-: عن امرأة سألت أخرى عن حكم مسألة شرعية فقالت: لا أدري، وكان إلى جانبهما امرأة تعرف الحكم الشرعي، فهل تخبر هذه السائلة مع أنها لم تسألها؟','10786','7042','0');
INSERT INTO [t12293] VALUES (7052,'س ١٨٥: سئل فضيلة الشيخ- رحمه الله-: إذا سئل المسلم عن شيء يعرفه في أمور الدين، وهو ليس متفقها في أمور الدين، فهل يجب عليه أن يخبره بهذا الشيء؟','10786','7042','0');
INSERT INTO [t12293] VALUES (7053,'س ١٨٦: سئل فضيلة الشيخ- رحمه الله-: إذا أفتى أحد العلماء بفتوى وبعد فترة تبين لهذا العالم أن ما أفتى به خطأ فماذا يلزمه؟','10787','7042','0');
INSERT INTO [t12293] VALUES (7054,'س ١٨٧: سئل فضيلة الشيخ- رحمه الله-: عن الشروط التي يجب أن تتوفر في المفتي.','10788','7042','0');
INSERT INTO [t12293] VALUES (7055,'س ١٨٨: سئل فضيلة الشيخ- رحمه الله-: ما حكم الإفتاء إذا علمت فتوى السؤال من شيخ من كبار العلماء؟','10789','7042','0');
INSERT INTO [t12293] VALUES (7056,'س ١٨٩: سئل فضيلة الشيخ- رحمه الله-: عن رجل يفتي زملاءه في كل صغيرة وكبيرة.','10789','7042','0');
INSERT INTO [t12293] VALUES (7057,'س ١٩٠: سئل فضيلة الشيخ- رحمه الله-: بعض الناس يتصدون للفتوى وليس عندهم علم شرعي يؤهلهم لذلك، فما نصيحتكم لمثل هؤلاء جزاكم الله خيرا؟','10791','7042','0');
INSERT INTO [t12293] VALUES (7058,'س ١٩١: سئل فضيلة الشيخ- رحمه الله-: إذا سئلت عن أمر من أمور الشرع، فهل أجيبه بما أعرف مما قرأته من الكتب الشرعية أو ما سمعته من الأشرطة الدينية أو ما سمعته من برنامج نور على الدرب أو أقول له لا أعلم؟','10792','7042','0');
INSERT INTO [t12293] VALUES (7059,'س ١٩٢: سئل فضيلة الشيخ- رحمه الله-: هل يجوز للعالم في العقيدة أن يفتي في الفقه؟','10793','7042','0');
INSERT INTO [t12293] VALUES (7060,'س ١٩٣: سئل فضيلة الشيخ- رحمه الله-: هناك ممن ينتسبون إلى العلم من يفتي بدون دليل، فن طولب بالدليل غضب وثار وقال: هل أفني عمري في البحث عن الأدلة، ومن العجب أنه علم تلاميذه عبارة غريبة فحواها بأن العالم لا يسال عن الدليل، ما الحكم في مقولة هذا الذي ينتسب إلى العلم؟ وما الحكم في فتواه؟ وما الحكم في مقولة تلاميذه ومريديه من أن العالم لا يسأل عن الدليل؟ وما الحكم في استفتاء من هذه حاله؟ أفيدونا في هذا الأمر الخطير جزاكم الله خيرا على أن تكون الإجابة مشفوعة بالأدلة؟','10794','7042','0');
INSERT INTO [t12293] VALUES (7061,'س ١٩٤: سئل فضيلة الشيخ- رحمه الله-: لقد شاع في هذا الزمان التسرع بالفتوى من غير علم ولا بصيرة، فما حكم الشرع في نظركم في مثل هذا الموضوع؟','10797','7042','0');
INSERT INTO [t12293] VALUES (7062,'س ١٩٥: سئل فضيلة الشيخ- رحمه الله-: يعلم فضيلتكم أن للإمام الشافعي- رحمه الله- فقها في العراق، فلما انتقل إلى مصر كان له فقه آخر اختلفت فيه بعض الأحكام الشرعية، ونحن بطبيعة الحال نجزم بأن العقائد من الثوابت التي لا تتغير بتغير الزمان ولا المكان. السؤال المطروح هو: إذا اختلف فقه الإمام الشافعي في بعض المسائل حين انتقل من بلد إسلامي إلى بلد إسلامي آخر تحتضنهما دولة واحدة، فكيف تكون مسائل الفقه لمن انتقل إلي بلاد يعتبر الإسلام فيها غريبا؟ ألا ترون أنه من الضروري معالجة كثير من الأمور التي تواجه المسلمين ولا تزال الآراء مختلفة بل متضاربة حولها؟','10803','7042','0');
INSERT INTO [t12293] VALUES (7063,'س ١٩٦: سئل فضيلة الشيخ- رحمه الله-: عندما يطرح سؤال شرعي يتسابق عامة الناس إذا كانوا في مجلس مثلا بالفتيا في تلك المسألة وبغير علم غالبا، فما تعليقكم على هذه الظاهرة؟','10804','7042','0');
INSERT INTO [t12293] VALUES (7064,'س ١٩٧: سئل فضيلة الشيخ- رحمه الله-: متى يكون الخلاف في الدين معتبرا؟ وهل يكون الخلاف في كل مسألة أم له مواضع معينة؟ نرجو بيان ذلك.','10805','7042','0');
INSERT INTO [t12293] VALUES (7065,'س ١٩٨: سئل فضيلة الشيخ- رحمه الله-: ما حكم الاجتهاد في الإسلام؟','10806','7042','0');
INSERT INTO [t12293] VALUES (7066,'س ١٩٩: سئل فضيلة الشيخ- رحمه الله-: هل يجوز لطالب العلم إذا كان في مجلس عام أن يقول لهم: من عنده مسألة أو مشكلة فليطرحها حتى أجيب عليها وتحصل الفائدة؟','10807','7042','0');
INSERT INTO [t12293] VALUES (7067,'س ٢٠٠: سئل فضيلة الشيخ- رحمه الله-: عما يحصل من اختلاف الفتيا من عالم لآخر في موضوع واحد. ما مرد ذلك؟ وما موقف متلقي الفتيا؟','10808','7042','0');
INSERT INTO [t12293] VALUES (7068,'س ٢٠١: سئل فضيلة الشيخ- رحمه الله-: انتشرت الفتوى حتى صار الصغير يفتي، فما تعليقكم- غفر الله لكم-؟','10809','7042','0');
INSERT INTO [t12293] VALUES (7069,'س ٢٠٢: سئل فضيلة الشيخ- رحمه الله-: هل يجوز لطالب العلم أن يرجح بعض الآراء الفقهية على بعض ثم يلزم بها غيره؟ وهل له أن يأخذ بالرأي المرجوح في بعض الأحوال وهو يعلم الراجح؟','10810','7042','0');
INSERT INTO [t12293] VALUES (7070,'نصائح عامة لطلبة العلم','10812','6856','0');
INSERT INTO [t12293] VALUES (7071,'س ٢٠٣: سئل فضيلة الشيخ- رحمه الله-: بعض الطلبة في الكليات الشرعية بقوم بإحراج المشايخ بطلب حذف بعض المنهج، وتجد بعض الطلبة أيضا يفرح إذا غاب أحد الأساتذة، لأنه ينقص من المنهج حينئذ، فما نصيحتكم؟','10817','7070','0');
INSERT INTO [t12293] VALUES (7072,'س ٢٠٤: سئل فضيلة الشيخ- رحمه الله-: ما توجيهكم حول ما يحصل من البعض من التفرق والتحزب؟','10820','7070','0');
INSERT INTO [t12293] VALUES (7073,'س ٢٠٥: سئل فضيلة الشيخ- رحمه الله-: عن حكم مشاهدة الأفلام التعليمية التي قد تكون فيها نساء وخصوصا أفلام تعلم اللغة الإنجليزية.','10827','7070','0');
INSERT INTO [t12293] VALUES (7074,'س ٢٠٦: سئل فضيلة الشيخ- رحمه الله-: هل يجوز للرجل أن يدرس في جامعة يختلط فيها الرجال والنساء في قاعة واحدة، علما بان الطالب له دور في الدعوة إلى الله تعالى؟ أفتونا جزاكم الله عنا كل خير.','10827','7070','0');
INSERT INTO [t12293] VALUES (7075,'س ٢٠٧: سئل فضيلة الشيخ- رحمه الله-: نلاحظ أن أكثر الشباب يهتم بقراءة الكتب الثقافية العامة متاثرا بها وغير مهتم بكتب الأصول، فما نصيحتكم وفقكم الله؟','10828','7070','0');
INSERT INTO [t12293] VALUES (7076,'س ٢٠٨: سئل فضيلة الشيخ- رحمه الله-: نرى كثيرا من الناس يعلم بعض الأحكام الشرعية كتحريم حلق اللحية، وشرب الدخان ومع ذلك لا يعمل بعلمه، فما أسباب ذلك؟ وكيف تعالج هذه الظاهرة الخطيرة؟','10829','7070','0');
INSERT INTO [t12293] VALUES (7077,'س ٢٠٩: سئل فضيلة الشيخ- رحمه الله-: لماذا لا يكون لكم درس أسبوعي في مدينة الرياض؟','10830','7070','0');
INSERT INTO [t12293] VALUES (7078,'س ٢١٠: سئل فضيلة الشيخ- رحمه الله-: بعض الشباب يريدون أن يتعلموا الطب وبعض العلوم الأخرى ولكن هناك عوائق مثل الاختلاط والسفر إلى بلاد الخارج، ما الحل؟ وما نصيحتكم لهؤلاء الشباب؟','10830','7070','0');
INSERT INTO [t12293] VALUES (7079,'س ٢١١: سئل فضيلة الشيخ- رحمه الله-: يحصل من بعض طلبة العلم في الكليات الشرعية قراءة بعض الكتب في العقيدة أو الحديث وذلك في أثناء محاضرات لبعض المواد التي يرون قلة الفائدة منها فما نصيحتكم؟','10831','7070','0');
INSERT INTO [t12293] VALUES (7080,'س ٢١٢: سئل فضيلة الشيخ- رحمه الله-: نرجو من سماحتكم- حفظكم الله تعالى- توجيه نصيحة لمن عمل في مجال التدريس، عسى الله أن ينفع بها وجزاكم الله خيرا.','10832','7070','0');
INSERT INTO [t12293] VALUES (7081,'س ٢١٣: سئل فضيلة الشيخ- رحمه الله-: ما هو قولكم فيمن يقول: نحن نحتاج إلى دعاة ولا نحتاج إلى علماء؟','10834','7070','0');
INSERT INTO [t12293] VALUES (7082,'س ٢١٤: سئل فضيلة الشيخ- رحمه الله-: لقد ظهرت دعوات كثيرة منها الدعوة إلى تجديد الفقه الإسلامي، فكيف','10835','7070','0');
INSERT INTO [t12293] VALUES (7083,'س ٢١٥: سئل فضيلة الشيخ- رحمه الله-: ما نصائحكم للطلبة في أيام الامتحانات والإجازات؟','10837','7070','0');
INSERT INTO [t12293] VALUES (7084,'س ٢١٦: سئل فضيلة الشيخ- رحمه الله-: عمن يقول بعدم الاجتهاد وخلو هذا العصر من المجتهدين.','10838','7070','0');
