INSERT INTO [t12416] VALUES (360,'أبو كاهل الأحمسي، واسمه قيس بن عائذ، صحب النبي صلى الله عليه وسلم وروى عنه. قال: " رأيت النبي صلى الله عليه وسلم يخطب على ناقة، وحبشي ممسك بخطامها "','824','0','0');
INSERT INTO [t12416] VALUES (361,'عبد الله بن عوسجة العرني من بجيلة، وهو كان رسول رسول الله صلى الله عليه وسلم بكتابه إلى بني حارثة بن عمرو بن قريط , يدعوهم إلى الإسلام، فأخذوا الصحيفة فغسلوها ورقعوا بها أسفل دلوهم، وأبوا أن يجيبوا، فقال رسول الله صلى الله عليه وسلم: " ما لهم أذهب','825','0','0');
