INSERT INTO [t12416] VALUES (393,'ومن سعد هذيم بن زيد بن ليث بن سود بن أسلم بن الحاف بن قضاعة وإنما قيل له سعد هذيم , لأنه حضنه عبد حبشي يقال له: هذيم , فغلب عليه، وسعد هو أبو عذرة وسلامان:','864','0','0');
