INSERT INTO [t12532] VALUES (1,'ستأتون غدا إن شاء الله عين تبوك، وإنكم لن تأتوها حتى يضحى النهار، فمن جاءها فلا يمسن من','3','0','0');
INSERT INTO [t12532] VALUES (2,'ما بين بيتي ومنبري روضة من رياض الجنة، ومنبري على حوضي» ومما قرئ عليها، وأنا أسمع بالروضة','19','0','0');
INSERT INTO [t12532] VALUES (3,'لا يدخل أحد ممن بايع تحت الشجرة النار»','20','0','0');
INSERT INTO [t12532] VALUES (4,'خير ما ركبت إليه الرواحل مسجدي هذا والبيت العتيق»','20','0','0');
INSERT INTO [t12532] VALUES (5,'صلاة فيه أفضل من ألف صلاة في ما سواه إلا مسجد الكعبة» . قلت: كذا سمعنا هذا الحديث على','21','0','0');
INSERT INTO [t12532] VALUES (6,'اقرءوها على موتاكم يعني يس» هذا أول حديث من الجزء','26','0','0');
INSERT INTO [t12532] VALUES (7,'لو أن أحدهم ينظر إلى قدميه لأبصرنا تحت قدميه، فقال: «يا أبا بكر ما ظنك باثنين الله','27','0','0');
INSERT INTO [t12532] VALUES (8,'من رآني بعد موتي وجبت له شفاعتي»','28','0','0');
INSERT INTO [t12532] VALUES (9,'من زار قبري وجبت له شفاعتي» وبه أنا شيخ الإسلام، أنا أبو الحسن علي بن أبي طالب الخوارزمي،','28','0','0');
INSERT INTO [t12532] VALUES (10,'نصلي مع رسول الله صلى الله عليه وسلم المغرب إذا توارت بالحجاب»','30','0','0');
INSERT INTO [t12532] VALUES (11,'أراك تتحرى الصلاة عند هذه الأسطوانة، قال: فإني رأيت النبي صلى الله عليه وسلم يتحرى الصلاة','30','0','0');
INSERT INTO [t12532] VALUES (12,'كان جدار المسجد عند المنبر ما كادت الشاة تجوزها» ، قلت: يعني الجدار القبلي، وكذلك كان أيضا','31','0','0');
INSERT INTO [t12532] VALUES (13,'ما بين بيتي ومنبري روضة من رياض الجنة، ومنبري على حوضي» فائدة في الاقتداء بشيوخ الاهتداء:','32','0','0');
INSERT INTO [t12532] VALUES (14,'نصلي مع رسول الله صلى الله عليه وسلم المغرب إذا توارت بالحجاب» قوله: إذا توارت يعني الشمس','35','0','0');
INSERT INTO [t12532] VALUES (15,'إذا جاء أحدكم إلى الصلاة فلا يسعى ولكن يمشي وعليه السكينة والوقار وليصل ما أدرك وليقض ما','39','0','0');
INSERT INTO [t12532] VALUES (16,'إذا توضأ العبد فمضمض واستنشق خرجت خطاياه من فيه وخرجت خطاياه من أنفه، فإذا غسل وجهه خرجت','40','0','0');
INSERT INTO [t12532] VALUES (17,'إذا توضأ العبد المؤمن فمضمض خرجت الخطايا من فيه، فإذا استنثر خرجت الخطايا من أنفه، فإذا','41','0','0');
INSERT INTO [t12532] VALUES (18,'الشمس تطلع من قرن الشيطان فإذا طلعت قارنها، وإذا ارتفعت فارقها» وذكر الحديث ثم قال: هؤلاء','49','0','0');
INSERT INTO [t12532] VALUES (19,'الشمس تطلع بقرني الشيطان» فذكر الحديث خرجه الدراقطني في اختلاف الموطآت فقال: أنا أحمد بن','53','0','0');
INSERT INTO [t12532] VALUES (20,'أنا فرطكم على الحوض، وأنا مكاثر بكم الأمم يوم القيامة فلا ترجعن بعدي كفارا يضرب بعضكم','55','0','0');
INSERT INTO [t12532] VALUES (21,'أراك تتحرى الصلاة عند هذه الأسطوانة، قال: «فإني رأيت النبي صلى الله عليه وسلم يتحرى الصلاة','61','0','0');
INSERT INTO [t12532] VALUES (22,'ما بين بيتي ومنبري روضة من رياض الجنة ومنبري على حوضي»','62','0','0');
INSERT INTO [t12532] VALUES (23,'كان جدار المسجد عند المنبر ما كادت الشاة تجوزها»','64','0','0');
INSERT INTO [t12532] VALUES (24,'ما بين بيتي ومنبري روضة من رياض الجنة ومنبري على حوضي» ذكر سفرنا من المدينة إلى مكة شرفها','64','0','0');
INSERT INTO [t12532] VALUES (25,'الراحمون يرحمهم الرحمن، ارحموا من في الأرض يرحمكم من في السماء» هذا الحديث وهو مخرج في','126','0','0');
INSERT INTO [t12532] VALUES (26,'رأى في بيتها جارية في وجهها سفعة فقال: استرقوا لها فإن بها النظرة "','128','0','0');
INSERT INTO [t12532] VALUES (27,'رأى في بيتها جارية في وجهها سفعة فقال: استرقوا لها فإن بها النظرة " فالأول من المشائخ','130','0','0');
INSERT INTO [t12532] VALUES (28,'الملتزم موضع يستجاب فيه الدعاء، وما دعا عبد الله تعالى فيه دعوة إلا استجابها» أو نحو ذلك','135','0','0');
INSERT INTO [t12532] VALUES (29,'ما من أيام أحب إليه العمل، أو أفضل، من أيام العشر، قيل: يا رسول الله ولا الجهاد في سبيل','141','0','0');
INSERT INTO [t12532] VALUES (30,'إذا دخل العشر وأراد أحدكم أن يضحي فليمسك عن شعره وأظفاره "','142','0','0');
INSERT INTO [t12532] VALUES (31,'من كان له ذبح يذبحه فإذا أهل هلال ذي الحجة فلا يأخذن من شعره ولا من أظفاره شيئا حتى يضحي»','143','0','0');
INSERT INTO [t12532] VALUES (32,'كل شيء بقدر حتى العجز والكيس أو الكيس والعجز» ، قال الحافظ أبو عبد الله بن خلفون: هكذا روى','147','0','0');
INSERT INTO [t12532] VALUES (33,'نحرنا مع رسول الله صلى الله عليه وسلم عام الحديبية البدنة عن سبعة والبقرة عن سبعة» ومنه','148','0','0');
INSERT INTO [t12532] VALUES (34,'شهدنا مع رسول الله صلى الله عليه وسلم فطرا أو أضحى، فلما صلى، قال: قد أصبتم خيرا، فمن أحب','153','0','0');
INSERT INTO [t12532] VALUES (35,'شهدنا مع رسول الله صلى الله عليه وسلم يومي عيدين فطر وأضحى، فلما فرغ من الصلاة أقبل علينا','154','0','0');
INSERT INTO [t12532] VALUES (36,'من يقل علي ما لم أقل فليتبوأ مقعده من النار» هذا أول حديث منها، ولنا منها نسخة، وهي عشرون','155','0','0');
INSERT INTO [t12532] VALUES (37,'بايعت النبي صلى الله عليه وسلم على إقام الصلاة» والحمد لله حق حمده وفيه أيضا سمع من البلاغ','170','0','0');
INSERT INTO [t12532] VALUES (38,'لكل شيء زكاة وزكاة الدار بيت الضيافة» هذا الحديث آخر ما في الجزء، وعندي منه نسخة. ومن','217','0','0');
INSERT INTO [t12532] VALUES (39,'كل أمر ذي بال لا يبدأ فيه بحمد الله فهو أقطع» بهذا الحديث افتتح خطبة الكتاب، وقفت على','219','0','0');
INSERT INTO [t12532] VALUES (40,'بخ بخ بخمس ما أثقلهن في الميزان» ! قال، قلت: وما هي يا رسول الله؟ قال: «سبحان الله والحمد','228','0','0');
INSERT INTO [t12532] VALUES (41,'بزق رسول الله صلى الله عليه وسلم في ثوبه وحك بعضه ببعض» . نا موسى، نا حماد، عن حميد، عن','233','0','0');
INSERT INTO [t12532] VALUES (42,'طبقات أمتي خمس طبقات، كل طبقة منها أربعون سنة، فطبقتي وطبقة أصحابي أهل العلم والإيمان،','234','0','0');
INSERT INTO [t12532] VALUES (43,'دخل النبي صلى الله عليه وسلم يوم فتح مكة، وعليه عمامة سوداء» قال محمد بن رشيد: لا نعلم في','246','0','0');
INSERT INTO [t12532] VALUES (44,'من أدرك ليلة عرفة التي بعد يوم عرفة قبل أن يطلع الفجر فقد أدرك الحج»','248','0','0');
INSERT INTO [t12532] VALUES (45,'{اليوم أكملت لكم دينكم وأتممت عليكم نعمتي ورضيت لكم الإسلام دينا} [المائدة: ٣] قال عمر:','249','0','0');
INSERT INTO [t12532] VALUES (46,'مر على رسول الله صلى الله عليه وسلم بجنازة، فقال: كم ترك؟ قالوا: ترك دينارين: قال: كيتين،','277','0','0');
INSERT INTO [t12532] VALUES (47,'الرقى والتمائم والتولة شرك» ، فقلنا: هذه الرقى والتمائم عرفناها، فما التولة؟ قال: شيء تجعله','279','0','0');
INSERT INTO [t12532] VALUES (48,'من أصدق امرأة صداقا، وهو مجمع أن لا يوفيها إياه ثم مات ولم يوفها إياه لقي الله عز وجل وهو','280','0','0');
INSERT INTO [t12532] VALUES (49,'من كنت مولاه علي مولاه اللهم وال من والاه وعاد من عاداه»','280','0','0');
INSERT INTO [t12532] VALUES (50,'اجتنبوا الخمر فإنها مفتاح كل شر، ولا يموتن أحدكم وعليه دين، فإنه ليس هناك دينار ولا درهم،','281','0','0');
INSERT INTO [t12532] VALUES (51,'عرضت على رسول الله صلى الله عليه وسلم يوم بدر، وأنا ابن ثلاث عشرة، فردني وعرضت عليه يوم','281','0','0');
INSERT INTO [t12532] VALUES (52,'قدم رجلان معي من قومي، قال: فأتينا إلى النبي صلى الله عليه وسلم فخطبا وتكلما فجعلا يعرضان','282','0','0');
INSERT INTO [t12532] VALUES (53,'أخذ سيفا وأصحابه حوله، فقال: من يأخذ هذا السيف؟ فبسطوا أيديهم، يقول هذا أنا وهذا أنا،','282','0','0');
INSERT INTO [t12532] VALUES (54,'أحب الأيام إلى الله يوم الجمعة، وأحب البقاع إلى الله المساجد، وأبغض البقاع إلى الله','283','0','0');
INSERT INTO [t12532] VALUES (55,'لو يعلم الناس من الوحدة ما أعلم ما أسرى راكب بليل وحده أبدا»','283','0','0');
INSERT INTO [t12532] VALUES (56,'الراحمون يرحمهم الرحمن، ارحموا أهل الأرض يرحمكم من في السماء» قال الحافظ أبو طاهر: قال لي','289','0','0');
INSERT INTO [t12532] VALUES (57,'التمسوا الرزق في خبايا الأرض»','291','0','0');
INSERT INTO [t12532] VALUES (58,'الولاء لمن أعتق»','291','0','0');
INSERT INTO [t12532] VALUES (59,'نهى عن بيع الولاء وعن هبته» تنبيه: ابن أبي شريح هذا شيخ مشهور صحيح السماع من البغوي وغيره،','291','0','0');
INSERT INTO [t12532] VALUES (60,'الراحمون يرحمهم الرحمن، ارحموا من في الأرض يرحمكم من في السماء»','294','0','0');
INSERT INTO [t12532] VALUES (61,'الراحمون يرحمهم الرحمن، ارحموا من في الأرض يرحمكم من في السماء»','295','0','0');
INSERT INTO [t12532] VALUES (62,'من أضاف مؤمنا فكأنما أضاف آدم، ومن أضاف اثنين فكأنما أضاف آدم وحواء، ومن أضاف ثلاثة','297','0','0');
INSERT INTO [t12532] VALUES (63,'في المحرم يدخل البستان؟ قال: نعم ويشم الريحان " انتهت الأحاديث ونقلتها من خطي الذي كنت','302','0','0');
INSERT INTO [t12532] VALUES (64,'كل ميت يختم على عمله إلا المرابط في سبيل الله تعالى فإنه ينمو له عمله إلى يوم القيامة','312','0','0');
INSERT INTO [t12532] VALUES (65,'الفطرة خمس: الاختتان، والاستحداد، وقص الشارب، وتقليم الأظفار، ونتف الإبط " أنشدنا شيخنا','314','0','0');
INSERT INTO [t12532] VALUES (66,'لو أنكم توكلتم على الله حق توكله لرزقكم كما ترزق الطير تغدو خماصا وتروح بطانا» روينا عن','321','0','0');
INSERT INTO [t12532] VALUES (67,'نهى عن ثمن الكلب ومهر البغي وحلوان الكاهن»','336','0','0');
INSERT INTO [t12532] VALUES (68,'لا تبيعوا الذهب بالذهب إلا مثلا بمثل، ولا تشفوا بعضها على بعض ولا تبيعوا منها غائبا بناجز»','336','0','0');
INSERT INTO [t12532] VALUES (69,'من شهد أن لا إله إلا الله مخلصا من قلبه، وأني محمد رسول الله دخل الجنة» قلت: أبا الطاهر','365','0','0');
INSERT INTO [t12532] VALUES (70,'لا يخافن أحدكم إلا ذنبه، ولا يرجون إلا ربه، ولا يستحي من لا يعلم أن يتعلم، ولا يستحي من','367','0','0');
INSERT INTO [t12532] VALUES (71,'سترون ربكم لا تضامون في رؤيته كما تنظرون إلى القمر ليلة البدر، فمن استطاع منكم أن لا يغلب','368','0','0');
INSERT INTO [t12532] VALUES (72,'لا إله إلا الله ثلاث مرات، ويل للعرب من شر قد اقترب، فتح من ردم يأجوج ومأجوج مثل هذه وحلق','369','0','0');
INSERT INTO [t12532] VALUES (73,'ينزل ربنا تبارك وتعالى، كل ليلة إلى السماء الدنيا، حين يبقي ثلث الليل فيقول: من يدعوني','371','0','0');
INSERT INTO [t12532] VALUES (74,'من قال حين يصبح: اللهم ما أصبح بي من نعمة أو بأحد من خلقك فمنك وحدك، لا شريك لك، فلك','372','0','0');
INSERT INTO [t12532] VALUES (75,'اللهم إني أسألك علما نافعا، وأعوذ بك من علم لا ينفع» .','373','0','0');
INSERT INTO [t12532] VALUES (76,'إذا حزبه أمر، قال: اللهم خر لي واختر لي "','373','0','0');
INSERT INTO [t12532] VALUES (77,'يكثر هذا الدعاء: «اللهم اجعل واسع رزقك علي عند كبر سني» وهذا الجزء العاشر معظمه في الدعاء','374','0','0');
