INSERT INTO [t12721] VALUES (1,'زوجها خرج في أعبد له أبقوا، حتى إذا كانوا بطرف القدوم لحقهم، فقتلوه، فسألت رسول الله صلى','4','0','0');
INSERT INTO [t12721] VALUES (2,'لم أصل إلا ركعتين؟ ، فقالوا: نعم، فقام فصلى بهم ركعتين أخريين، ثم سلم، ثم سجد سجدتي','7','0','0');
INSERT INTO [t12721] VALUES (3,'صلى إحدى صلاتي العشاء ركعتين، فقال ذو اليدين: أقصرت الصلاة أم نسيت؟، فقال: لم تقصر ولم','8','0','0');
INSERT INTO [t12721] VALUES (4,'هلا قعدت في بيت أبيك وأمك حتى يهدى لك؟ ، ثم قام بين ظهراني أصحابه، فحمد الله وأثنى عليه','9','0','0');
INSERT INTO [t12721] VALUES (5,'هدايا الأمراء غلول. هذا حديث غريب، من حديث أبي سعيد يحيى بن سعيد الأنصاري، عن أبي عبد','10','0','0');
INSERT INTO [t12721] VALUES (6,'وعليه عمامة سوداء. أخبرناه عاليا: أبو عمر محمد بن العباس بن محمد بن زكريا بن حيويه الخزاز','11','0','0');
INSERT INTO [t12721] VALUES (7,'ذلك لا يغني عني في الآخرة شيئا. أخبرناه أبو بكر محمد بن خلف بن محمد بن حيان الخلال','12','0','0');
INSERT INTO [t12721] VALUES (8,'دخل يوم فتح مكة وعليه عمامة سوداء. هذا حديث غريب، غريب من حديث الزهري، عن أنس، وهذا اللفظ','13','0','0');
INSERT INTO [t12721] VALUES (9,'وعلى رأسه مغفر، فقيل: هذا ابن خطل متعلق بأستار الكعبة، فقال: اقتلوه. وهذا لفظ موسى، وقال','14','0','0');
INSERT INTO [t12721] VALUES (10,'رحم الله عبدا كانت لأخيه عنده مظلمة في عرض أو مال فاستحلها منه قبل أن يؤخذ منه، وليس ثم','15','0','0');
INSERT INTO [t12721] VALUES (11,'من كانت بينه وبين أخيه مظلمة فليستحله قبل لا دينار ولا درهم','16','0','0');
INSERT INTO [t12721] VALUES (12,'من كانت لأخيه عنده مظلمة في عرضه، أو ماله، فليستحلله اليوم قبل أن يؤخذ منه يوم لا دينار','17','0','0');
INSERT INTO [t12721] VALUES (13,'ما من صلاة أثقل على المنافقين من صلاة العشاء، وصلاة الصبح، ولو يعلمون ما فيهما لأتوهما','18','0','0');
INSERT INTO [t12721] VALUES (14,'هاتين الصلاتين أثقل الصلوات على المنافقين، ولو يعلمون ما فيهما لأتوهما ولو حبوا، والصف','19','0','0');
INSERT INTO [t12721] VALUES (15,'صلاة الرجل في الجميع تفضل على صلاته وحده أربعا وعشرين. . . . وساق الحديث. كذا في الأصل،','20','0','0');
INSERT INTO [t12721] VALUES (16,'أثقل الصلوات على المنافقين صلاة العشاء وصلاة الفجر، ولو يعلمون ما فيهما لأتوهما ولو حبوا،','21','0','0');
INSERT INTO [t12721] VALUES (17,'إنه ليس صلاة أثقل على المنافقين من صلاة العشاء الآخرة، ومن صلاة الفجر، ولو يعلمون ما','22','0','0');
INSERT INTO [t12721] VALUES (18,'وما أنفقتم من شيء فهو يخلفه} [سبأ: ٣٩] ، في غير إسراف ولا تقتير','23','0','0');
INSERT INTO [t12721] VALUES (19,'زاهدكم راغب، ومجتهدكم مقصر، وعالمكم جاهل، وجاهلكم مغتر','24','0','0');
INSERT INTO [t12721] VALUES (20,'كفى به والله ذنبا أن يكون الله يزهدنا في الدنيا ونحن نرغب فيها، فزاهدكم راغب، وعالمكم','25','0','0');
INSERT INTO [t12721] VALUES (21,'إنكم لم تخلقوا للفناء - زاد سريج: وإنما خلقتم للبقاء -، ثم اتفقا فقالا: وإنما تنقلون من','26','0','0');
INSERT INTO [t12721] VALUES (22,'إنكم لم تخلقوا للفناء، وإنما تنقلون من دار إلى دار، كما نقلتم من الأصلاب إلى الأرحام، ومن','27','0','0');
INSERT INTO [t12721] VALUES (23,'أخ لك كلما لقيك ذكرك بحظك من الله خير لك من أخ لك كلما لقيك وضع في كفك','28','0','0');
