INSERT INTO [t12738] VALUES (101,'باب اللعب بالشطرنج والكعاب وبصورة البنت.','1506','13','0');
INSERT INTO [t12738] VALUES (102,'باب الأخلاق المحمودة كالنية والتوبة عن الذنب وأن العمل بها نافع سيما للمقربين عند الحسنات والتقوى سيما من الشاب عن مواضع التهم والشبهات والتثبت والتفكر والخوف والتوسط في الأمور والعبادة والإخلاص والصبر والرضا على القدر والتوكل فالعز مقسوم وللآنية آجال والزهد والحدة والحلم والشفقة وشكر النعم والتواضع وحسن العمل مع طول العمر.','1516','13','0');
